INSERT INTO `quran` VALUES (6349,2,1,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (6350,2,1,2,'<p><span>[a]</span><span> </span><span>Segala </span><a>[5]&nbsp; </a><span>puji </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=5a">5a</a><span>] </span><span>hanya </span><span> bagi </span>Allah <span>, Tuhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=6">6</a><span>] </span><span>semesta alam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=6a">6a</a><span>]</span></p><p><span>[a]</span><span> 6:2 ; 6:46 ; 10:11 ; 18 :2 ; 29:64 ; 30:19 ; 31:26 ; 34:2 ; 35:2 ; 37:183 ; 39:76 ; 45:37. </span></p>');
INSERT INTO `quran` VALUES (6351,2,1,3,'<p><span>[a]</span><span> </span><span>Maha Pemurah, </span><span>[b] </span><span>Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=7">7</a><span>]</span></p><p><span>[a] </span><span>25:62 ; 26:6 ; 41:3 ; 55:2 ; 59:23</span></p><p><span>[b]</span><span> </span><span>33:44 ; 36:59 </span></p>');
INSERT INTO `quran` VALUES (6352,2,1,4,'<p><span>[c] </span><span>Pemilik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=8">8</a><span>]</span><span> </span><span>[d] </span><span>Hari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=9">9</a><span>] </span><span>Pembalasan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=10">10</a><span>]</span></p><p><span>[c] </span><span>48:15</span></p><p><span>[d]</span><span> </span><span>51:13 ; 74:47; 82:18,19; 83:7</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6353,2,1,5,'<p><span>[a]</span><span> </span><span>Hanya Engkau kami sembah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=11">11</a><span>]</span><span> dan </span><span>[b]</span><span> </span><span>hanya kepada Engkau kami mohon pertolongan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=12">12</a><span>]</span></p><p><span>[a] </span><span>11:3; 12:41; 16:37; 17:24; 41:38</span></p><p><span>[b] </span><span>2:46, 154; 21:113</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6354,2,1,6,'<p><span>Tunjukilah kami pada </span><span>[a] </span><span>jalan yang lurus; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=13">13</a><span>]</span></p><p><span>[a] </span><span>19:37 ; 36:62 ; 42:53,54</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6355,2,1,7,'<p><span>Jalan </span><span>[a] </span><span>orang-orang yang telah Engkau beri nikmat atas mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=14">14</a><span>] </span><span>bukan </span><span>atas </span><span>mereka </span><span>[b]</span><span> </span><span> </span><span>yang dimurkai dan bukan pula</span><span> </span><span>[c]</span><span> </span><span>yang sesat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=15">15</a><span>]</span></p><p><span>[a] </span><span>4:70 ; 5:21 ; 19:59</span></p><p><span>[b] </span><span>2:62, 91 ; 3:113 ; 5:61,70</span></p><p><span>[c] </span><span>3:91 ; 5:78; 18:105</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6356,2,2,1,'<p><span>Aku baca</span><span> dengan nama Allah, Maha Pemurah, Maha Penyayang</span><span>.</span></p>');
INSERT INTO `quran` VALUES (6357,2,2,2,'<p><span>Aku Allah Yang Maha Mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=16">16</a><span>]</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6358,2,2,3,'<p><span>Inilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=17">17</a><span>] </span><span>Kitab </span><span>yang sempurna; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=17a">17a</a><span>] [a] </span><span>tiada keraguan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=18">18</a><span>]</span><span> di dalam-nya; </span><span>[b] </span><span>petunjuk bagi orang-orang yang bertakwa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=19">19</a><span>]</span></p><p><span>[a] </span><span>2:24; 10:38; 32:3; 41:43.</span></p><p><span>[b] </span><span>2:186; 3:139; 31:4. </span><span> </span></p>');
INSERT INTO `quran` VALUES (6359,2,2,4,'<p><span>Yaitu </span><span>mereka yang beriman kepada </span><span>[a] </span><span>yang gaib, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=20">20</a><span>] </span><span>dan tetap </span><span>[b] </span><span>mendirikan shalat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=21">21</a><span>] </span><span>dan dari apa-apa yang telah Kami rezekikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=22">22</a><span>] </span><span>kepada mereka, mereka </span><span>[c] </span><span>membelanjakan</span></p><p><span>[a] </span><span>5:95; 6:104; 21:50; 35:19; 36:12; 50:34; 57:26; 67:13. </span></p><p><span>[b] </span><span>2:44, 84, 111, 278; 5:56; 8:4; 9:71; 20:15; 27:4; 30:32; 31:5; 73:21.</span></p><p><span>[c] </span><span>2:196, 255, 263, 268; 3:93; 8:4; 9:34; 13:23; 14:32; 22:36; 28:55; 32:17; 42:39. </span></p>');
INSERT INTO `quran` VALUES (6360,2,2,5,'<p><span>Dan mereka yang beriman kepada engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=23">23</a><span>] </span><span>dan kepada apa yang telah </span><span>[a] </span><span>diturunkan sebelum engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=24">24</a><span>] </span><span>dan kepada </span><span>apa-apa</span><span>­ </span><span>[b] </span><span>yang </span><span>telah dijanjikan</span><span> akan datang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=25">25</a><span>] </span><span>mereka </span><span>pun </span><span>yakin.</span></p><p><span>[a] </span><span>2:137, 286; 3:200; 4:61, 137, 163; 5:60. </span></p><p><span>[b]</span><span> 6:93; 27:4; 31:5. </span><span> </span></p>');
INSERT INTO `quran` VALUES (6361,2,2,6,'<p><span>Mereka itulah yang tetap berada di atas </span><span>[c] </span><span>petunjuk dari Tuhan mereka dan mereka itulah </span><span>[d] </span><span>orang-orang yang menang</span></p><p><span>[c] </span><span>2:158; 31:6. </span></p><p><span>[d] </span><span>23:2; 28:68; 31:6; 87:15; 91:10. </span><span> </span></p>');
INSERT INTO `quran` VALUES (6362,2,2,7,'<p><span>Sesungguhnya orang-orang yang ingkar </span><span>[a] </span><span>sama saja bagi mereka, apakah mereka engkau peringatkan atau tidak engkau peringatkan, mereka tidak akan beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=26">26</a><span>]</span></p><p><span>[a] </span><span>26:137; 36 ; 11. </span></p>');
INSERT INTO `quran` VALUES (6363,2,2,8,'<p>Allah <span>[b] </span><span>telah mencap </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=27">27</a><span>] </span><span>hati mereka serta telinga mereka, sedang di atas mata mereka ada tutupan dan bagi mereka ada azab yang amat pedih</span></p><p><span>[b]</span><span> 4:156; 6:26, 47; 7:102, 180; 10:75; 16:109; 45:24; 83:15.</span></p>');
INSERT INTO `quran` VALUES (6364,2,2,9,'<p><span>Dan di antara manusia ada yang mengatakan, </span><span>[c] </span><span>”Kami beriman kepada </span>Allah<span>dan kepada Hari Kemudian,” padahal mereka bukan orang-orang yang beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=28">28</a><span>]</span></p><p><span>[c] </span><span>2:178; 3:115; 4:40, 60; 6:95; 58:23.</span></p>');
INSERT INTO `quran` VALUES (6365,2,2,10,'<p><span>Mereka </span><span>[a] </span><span>hendak menipu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=29">29</a><span>] </span>Allah <span>dan orang-orang beriman, padahal mereka tidak menipu selain diri mereka sendiri; dan mereka tidak menyadari</span></p><p><span>[a] </span><span>4:143.</span></p>');
INSERT INTO `quran` VALUES (6366,2,2,11,'<p><span>[b] </span><span>Dalam hati mereka ada penyakit, lalu </span>Allah <span>menambah penyakit mereka; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=30">30</a><span>] </span><span>dan bagi</span><span> </span><span>mereka ada azab yang pedih disebabkan mereka berdusta</span></p><p><span>[b] </span><span>5:53; 9:125; 74:32. </span></p>');
INSERT INTO `quran` VALUES (6367,2,2,12,'<p><span>Dan apabila dikatakan kepada mereka, </span><span>[c] </span><span>”Janganlah kamu berbuat kekacauan di bumi, ” berkata mereka, “Sesungguhnya kami hanya orang-orang yang mengadakan perbaikan.”</span></p><p><span>[c] </span><span>2:28, 221. </span></p>');
INSERT INTO `quran` VALUES (6368,2,2,13,'<p><span>Ingatlah, sesungguhnya mereka itu pembuat kekacauan tetapi mereka tidak menyadari</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6369,2,2,14,'<p><span>Dan apabila dikatakan kepada mereka, “Berimanlah kamu sebagaimana orang-orang telah beriman;” berkata. “Apakah kami harus beriman sebagaimana orang-orang bodoh telah beriman ?” Ingatlah, sesungguhnya mereka itu orang-orang bodoh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=31">31</a><span>]</span><span> tetapi mereka tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (6370,2,2,15,'<p><span>Dan </span><span>[a] </span><span>apabila mereka bertemu dengan orang-orang yang beriman, mereka berkata, “Kami telah beriman.” Tetapi, manakala mereka pergi kepada pemimpin-pemimpin mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=32">32</a><span>]</span><span> berkata mereka, “Sesungguhnya kami beserta kamu, </span><span>[b] </span><span>kami hanya berolok-olok.”</span></p><p><span>[a]</span><span> 2:77; 3:120; 5:62. </span></p><p><span>[b]</span><span> 9:64, 65. </span><span> </span></p>');
INSERT INTO `quran` VALUES (6371,2,2,16,'<p><span>[a] </span>Allah <span>akan menghukum </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=33">33</a><span>]</span><span> perolokan mereka dan akan </span><span>[b] </span><span>membiarkan mereka berkelana </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=33a">33a</a><span>]</span><span> binggung dalam kedurhakaan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=34">34</a><span>]</span></p><p><span>[a]</span><span> 9:79; 11:9; 21:42. </span></p><p><span>[b]</span><span> 6:111; 7:187; 10:12. </span></p>');
INSERT INTO `quran` VALUES (6372,2,2,17,'<p><span>Mereka itulah orang-orang yang </span><span>[c] </span><span>telah menukar kesesatan dengan petunjuk; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=35">35</a><span>]</span><span> karena itu tidak beruntung perniagaan mereka dan tidak pula mereka mendapat petunjuk.</span></p><p><span>[c] </span><span>2:87, 176; 3:178; 14:4; 16:108. </span></p>');
INSERT INTO `quran` VALUES (6373,2,2,18,'<p><span>Keadaan mereka seperti seorang yang menyalakan api, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=36">36</a><span>]</span><span> dan tatkala api itu telah menyinari apa yang ada di sekelilingnya, maka </span>Allah <span>melenyapkan cahaya mereka dan </span><span>[a] </span><span>membiarkan mereka dalam kegelapan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=37">37</a><span>]</span><span> mereka tidak dapat melihat.</span></p><p><span>[a]</span><span> 6:40, 123; 24:41. </span></p>');
INSERT INTO `quran` VALUES (6374,2,2,19,'<p><span>Mereka </span><span>[b] </span><span>tuli, bisu buta; maka mereka tidak akan kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=38">38</a><span>]</span></p><p><span>[b]</span><span> 2:172; 6:40; 7:180; 8:23; 10:43; 11:25; 17:98; 21:46; 27:81; 30:53, 54; 43:41. </span></p>');
INSERT INTO `quran` VALUES (6375,2,2,20,'<p><span>Atau </span><span>keadaan mereka </span><span>seperti hujan lebat dari langit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=39">39</a><span>]</span><span> </span><span>[c] </span><span>yang di dalamnya gelap-gulita, guruh, dan </span><span>[d] </span><span>kilat; mereka memasukan jari mereka ke dalam telinga mereka disebabkan petir, karena takut mati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=40">40</a><span>]</span><span> Dan </span>Allah <span>mengepung orang- orang kafir.</span></p><p><span>[c] </span><span>6:40, 123; 24:41. </span></p><p><span>[d] </span><span>13:13; 24:44; 30:25 </span></p>');
INSERT INTO `quran` VALUES (6376,2,2,21,'<p><span>Nyaris kilat itu menyambar penglihatan mereka; setiap kali kilat menyinari mereka, mereka berjalan di dalamnya; dan </span><span>[a] </span><span>apabila gelap meliputi mereka, berhentilah mereka. Dan jika </span>Allah<span> menghendaki, niscaya Dia menghilangkan pendengaran dan penglihatan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=41">41</a><span>]</span><span> Sesungguhnya </span>Allah <span>Maha Kuasa atas segala Sesuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=41a">41a</a><span>]</span></p><p><span>[a]</span><span> 4:73, 74. </span><span> </span></p>');
INSERT INTO `quran` VALUES (6377,2,2,22,'<p><span> </span><span>R. 3</span></p><p><span>Hai manusia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=42">42</a><span>] [a] </span><span>sembahlah Tuhan-mu Yang telah menjadikan kamu dan orang- orang yang sebelummu supaya kamu bertakwa.</span></p><p><span>[a] </span><span>4</span><span>:</span><span>2, 37; 5:73, 118; 16:37; 22:78; 51:57 </span></p>');
INSERT INTO `quran` VALUES (6378,2,2,23,'<p><span>Dia-lah,</span><span> </span><span>[b] </span><span>Yang menjadikan bumi bagimu sebagai hamparan, dan </span><span>[c] </span><span>langit sebagai atap, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=43">43</a><span>]</span><span> dan menurunkan air dari awan, maka dengan itu Dia mengeluarkan buah-buahan sebagai rezeki bagimu. Karena itu janganlah kamu mengadakan sekutu bagi </span>Allah <span>padahal kamu mengetahui.</span></p><p><span>[b] </span><span>20</span><span>:</span><span>54; 27:62; 43:11; 51:49; 71:20; 78:7. </span></p><p><span>[c] </span><span>51</span><span>:</span><span>48; 78:13; 79:28, 29 </span></p>');
INSERT INTO `quran` VALUES (6379,2,2,24,'<p><span>Dan jika kamu dalam keraguan tentang apa yang telah Kami turunkan kepada hamba Kami, maka </span><span>[d] </span><span>buatlah satu Surah yang semisalnya, dan ajaklah pembantu-pembantumu selain </span>Allah<span>, sekiranya kamu memang orang-orang yang benar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=44">44</a><span>]</span></p><p><span>[d] </span><span>10</span><span>:</span><span>39; 11:14; 17:89; 52:35.</span></p>');
INSERT INTO `quran` VALUES (6380,2,2,25,'<p><span>Tetapi, jika kamu tidak dapat membuat</span><span>nya,</span><span> dan sekali-kali tidak akan dapat membuat</span><span>nya</span><span>, maka takutlah kamu akan Api </span><span>[a] </span><span> </span><span>yang bahan bakarnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=45">45</a><span>]</span><span> manusia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=46">46</a><span>]</span><span> dan batu, disediakan untuk orang-orang kafir.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>11; 66:7. </span></p>');
INSERT INTO `quran` VALUES (6381,2,2,26,'<p><span>Dan berilah kabar suka kepada orang-orang yang beriman dan beramal saleh, sesungguhnya </span><span>[b] </span><span>untuk mereka ada kebun-kebun yang dibawahnya mengalir sungai-sungai. Setiap kali diberikan kepada mereka buah-buahan dari kebun itu sebagai rezeki, berkata mereka, “Inilah rezeki yang telah diberikan kepada kami dahulu,” dan akan diberikan kepada mereka yang serupa. Dan, bagi mereka di dalamnya ada </span><span>[a] </span><span>jodoh-jodoh yang suci, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=46a">46a</a><span>]</span><span> dan mereka kekal di dalamnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=47">47</a><span>]</span></p><p><span>[b] </span><span>3</span><span>:</span><span>16, 134, 196, 199; 4 :14, 58, 123; 5:13, 86; 7:44; 9:72, 89, 100; 10:10; 13:36, 22</span><span>:</span><span>15, 24; 25:11; 32:18; 47:16; 58:23; 61:13; 64:10.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>16; 4:58. </span></p>');
INSERT INTO `quran` VALUES (6382,2,2,27,'<p><span>Sesungguhnya </span>Allah <span>[a]</span><span> </span><span>tidak segan </span><span>[b] </span><span>mengemukakan suatu perumpamaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=48">48</a><span>]</span><span> sekecil nyamuk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=48a">48a</a><span>]</span><span> atau yang lebih dari itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=48b">48b</a><span>]</span><span> Adapun orang-orang yang beriman mengetahui bahwa </span><span>perumpamaan</span><span> itu benar dari Tuhan mereka; dan adapun orang-orang yang ingkar berkata, “Apakah yang dikehendaki </span>Allah<span> dengan perumpamaan ini ?” </span><span>Sebenarnya </span><span>dengan ini banyak yang </span><span>[c] </span><span>Dia nyatakan </span><span>sesat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=49">49</a><span>]</span><span> dan dengan ini juga banyak yang Dia beri petunjuk dan tiada yang Dia nyatakan sesat dengan itu kecuali orang-orang durhaka.</span></p><p><span>[a] </span><span>33</span><span>:</span><span>54.</span></p><p><span>[b] </span><span>14</span><span>:</span><span>25; 16:76, 113; 47:4; 66:12.</span></p><p><span>[c] </span><span>6</span><span>:</span><span>118; 7:187; 13:28; 16:94; 40:35.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6383,2,2,28,'<p><span>Orang-orang yang </span><span>[b] </span><span>melanggar janji kepada </span>Allah <span>sesudah meneguhkannya dan memutuskan apa yang diperintahkan </span>Allah <span>untuk menghubungkannya, dan mereka membuat kekacauan di bumi; mereka itulah orang- orang yang rugi.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>101; 4:156; 5:14; 13:26.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6384,2,2,29,'<p><span>Bagaimanakah kamu dapat menginkari </span>Allah <span>? Padahal dahulu kamu tidak bernyawa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=50">50</a><span>] </span><span>lalu </span><span>[c] </span><span>Dia menghidupkan kamu, kemudian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=51">51</a><span>] </span><span> Dia akan mematikan kamu, kemudian Dia akan menghidupkan kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=52">52</a><span>]</span><span> kemudian kepada-Nya kamu akan dikembalikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=53">53</a><span>]</span></p><p><span>[c] </span><span>19</span><span>:</span><span>34; 22:67; 30:41; 40:12; 45:27.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6385,2,2,30,'<p><span>[a] </span><span>Dia-lah Yang menciptakan untukmu segala yang ada di bumi; kemudian Dia </span><span>[b] </span><span>mengarah ke </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=54">54</a><span>]</span><span> langit lalu Dia menyempurnakan-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=55">55</a><span>]</span><a>&nbsp; </a><span>tujuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=56">56</a><span>]</span><span> langit; dan Dia Maha Mengetahui segala sesuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=56a">56a</a><span>]</span></p><p><span>[a] </span><span>22</span><span>:</span><span>66, 31:21; 45:14.</span></p><p><span>[b]</span><span> </span><span>7</span><span>:</span><span>55; 10:4; 41:10-13.</span></p>');
INSERT INTO `quran` VALUES (6386,2,2,31,'<p><span>Dan ketika Tuhan engkau berkata </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=57">57</a><span>]</span><span> kepada para malaikat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=57a">57a</a><span>] </span><span>“Sesungguhnya Aku hendak menjadikan seorang </span><span>[a] </span><span>khalifah di bumi;” berkata mereka, “Apakah Engkau akan menjadikan di dalamnya orang yang akan membuat kekacauan di dalamnya dan akan menumpahkan darah? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=58">58</a><span>]</span><span> Padahal kami bertasbih dengan pujian Engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=59">59</a><span>]</span><span> dan kami mensucikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=60">60</a><span>]</span><span> Engkau.” Berfirman Dia, “Sesungguhnya Aku mengetahui apa yang tidak kamu ketahui.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=61">61</a><span>]</span></p><p><span>[a] </span><span>7</span><span>:</span><span>130; 10:15; 15:29; 24:56; 38:27</span></p>');
INSERT INTO `quran` VALUES (6387,2,2,32,'<p><span>Dan, Dia mengajarkan kepada Adam nama-nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=62">62</a><span>] [a] </span><span>semuanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=62a">62a</a><span>]</span><span> kemudian Dia mengemukakannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=62b">62b</a><span>]</span><span> kepada para malaikat dan berfirman, “Beritahukanlah kepada-Ku nama-nama ini jika kamu berkata benar.</span></p><p><span>[a] </span><span>7</span><span>:</span><span>181; 17:111; 20:9; 59:24, 25.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6388,2,2,33,'<p><span>Berkata mereka, “Mahasuci Engkau ! Kami tidak mempunyai ilmu selain apa yang telah Engkau ajarkan kepada kami. Sesungguhnya Engkau Yang Maha Mengetahui, Maha- bijaksana.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=63">63</a><span>]</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6389,2,2,34,'<p><span>Dia berfirman, “Hai Adam Sebutkanlah kepada mereka nama-nama itu ,” maka tatkala disebut kannya kepada mereka nama-nama itu, berfirman Dia, “Bukanlah telah Aku katakan kepadamu, sesungguhnya Aku mengetahui rahasia seluruh langit dan bumi, dan mengetahui apa yang kamu zahirkan dan apa yang kamu sembunyikan?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=64">64</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (6390,2,2,35,'<p><span>Dan </span><span>ingatlah </span><span>[a] </span><span>ketika Kamu berkata para malaikat, “ Tunduklah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=65">65</a><span>]</span><span> kamu kepada Adam,” maka mereka tunduk, kecuali </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=66">66</a><span>]</span><span> iblis. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=67">67</a><span>] </span><span>Ia menolak dan takabur; dan memang ia termasuk orang- orang kafir.</span></p><p><span>[a] </span><span>7</span><span>:</span><span>12, 13; 15:29, 33; 17:62, 18:51; 20:117; 38:72 </span><span>–</span><span> 77 </span><span> </span><span> </span></p>');
INSERT INTO `quran` VALUES (6391,2,2,36,'<p><span>Dan Kami berfirman, “Hai Adam, </span><span>[a] </span><span>tinggallah engkau dan isterimu dalam kebun ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=68">68</a><span>]</span><span> dan makanlah darinya sepuas hati di manapun kamu berdua suka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=68a">68a</a><span>]</span><span> tetapi janganlah kamu berdua mendekati pohon ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=69">69</a><span>]</span><span> jangan-jangan kamu berdua termasuk orang-orang aniaya.</span></p><p><span>[a] </span><span>7</span><span>:</span><span>20 , 23, 20:117, 118.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6392,2,2,37,'<p><span>Akan tetapi </span><span>[a] </span><span>syaitan </span><span>[70]</span><span> telah menggelincirkan keduanya dari </span><span>tempat</span><span> itu dan ia mengeluarkan keduanya dari keadaan mereka semula. Dan Kami berfirman, </span><span>[b] </span><span>”Pergilah kamu </span><span>dari sini;</span><span> sebagian dari kamu adalah musuh bagi yang lain, dan </span><span>[c] </span><span>bagimu di bumi ini </span><span>ada </span><span>tempat kediaman </span><span>[71]</span><span> dan bekal hidup sampai suatu masa tertentu.</span></p><p><span>[a] </span><span>7</span><span>:</span><span>21, 28; 20 121.</span></p><p><span>[b] </span><span>7</span><span>:</span><span>25, 20:124.</span></p><p><span>[c] </span><span>7</span><span>:</span><span>25, 26; 20:56; 77:26,27.</span></p>');
INSERT INTO `quran` VALUES (6393,2,2,38,'<p><span>Kemudian Adam menerima “ </span><span>[a] </span><span>kalimat-kalimat </span><span>doa </span><span>dari Tuhan-nya, lalu </span><span>[b] </span><span>Dia menerima tobatnya. Sesungguhnya, Dia Maha Penerima tobat, Maha Penyayang</span></p><p><span>[a] </span><span>7</span><span>:</span><span>24.</span></p><p><span>[b] </span><span>20</span><span>:</span><span>123.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6394,2,2,39,'<p><span>Kami berkata, “ Pergilah kamu sekalian dari sini. Kemudian, jika </span><span>[c] </span><span>datang kepadamu suatu petunjuk dari Aku, maka barangsiapa mengikuti petunjuk-Ku niscaya tak akan ada ketakutan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=72">72</a><span>]</span><span> menimpa mereka dan tidak pula mereka akan bersedih.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=73">73</a><span>]</span></p><p><span>[c] </span><span>7</span><span>:</span><span>36; 20:124</span><span> </span></p>');
INSERT INTO `quran` VALUES (6395,2,2,40,'<p><span>Dan </span><span>[d] </span><span>orang-orang yang ingkar serta mendustakan Tanda-tanda Kami, mereka adalah penghuni Api, mereka tinggal lama di dalamnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=74">74</a><span>]</span></p><p><span>[d] </span><span>7</span><span>:</span><span>37.</span><span> </span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6396,2,2,41,'<p><span>Hai Bani Israil ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=75">75</a><span>]</span><span> Ingatlah </span><span>[e]</span><span> </span><span>nikmat-Ku yang telah Aku anugerahkan kepadamu dan tepatilah janji-Ku, </span><span>niscaya</span><span> Aku tepati janji-mu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=76">76</a><span>]</span><span> dan hanya Aku-lah yang harus kamu takuti.</span></p><p><span>[e] </span><span>2</span><span>:</span><span>48, 123; 5:21; 14:7</span><span> </span></p>');
INSERT INTO `quran` VALUES (6397,2,2,42,'<p><span>Dan, berimanlah kamu kepada apa yang telah Aku turunkan yang </span><span>[a] </span><span>menggenapi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=77">77</a><span>]</span><span> apa yang ada padamu, janganlah kamu menjadi orang-orang </span><span>[b] </span><span>yang pertama-tama ingkar kepadanya, dan </span><span>[c] </span><span>janganlah kamu menjual ayat-ayat-Ku dengan harga yang rendah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=77a">77a</a><span>]</span><span> dan hanya kepada Aku-lah kamu harus bertakwa</span></p><p><span>[a] </span><span>2</span><span>:</span><span>90, 98, 102; 3:4, 82; 4:48; 5:49.</span></p><p><span>[b] </span><span>7</span><span>:</span><span>102; 10:75.</span></p><p><span>[c] </span><span>2</span><span>:</span><span>80, 175; 3:200; 5:45; 9:9; 16:96.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6398,2,2,43,'<p><span>Dan, janganlah kamu </span><span>[a] </span><span>memcampuradukkan yang hak dengan yang batil, dan </span><span>jangan pula</span><span> kamu menyembunyikan yang hak itu padahal kamu mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=78">78</a><span>]</span></p><p><span>[d] </span><span>3</span><span>:</span><span>72.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6399,2,2,44,'<p><span>Dan, </span><span>[a] </span><span>dirikanlah shalat dan </span><span>[b] </span><span>bayarlah zakat dan rukuklah bersama orang-orang yang rukuk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=79">79</a><span>]</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span>4.</span><span> </span></p><p><span>[b] </span><span>2</span><span>:</span><span>84:111. 178; 4:163; 5:56; 9:11; 21:74; 23:5.</span></p>');
INSERT INTO `quran` VALUES (6400,2,2,45,'<p><span>[c] </span><span>Apakah kamu menyuruh orang berbuat kebaikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=80">80</a><span>]</span><span> dan kamu melupakan dirimu sendiri, padahal kamu membaca AlKitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=81">81</a><span>]</span><span> itu ? Apakah kamu tidak menggunakan akal ?</span></p><p><span>[c] </span><span>26</span><span>:</span><span> 227; 61:3, 4.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6401,2,2,46,'<p><span>Dan, </span><span>[d] </span><span>mohonlah pertolongan dengan sabar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=82">82</a><span>]</span><span> dan doa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=83">83</a><span>]</span><span> Dan </span><span>[c] </span><span>Sesungguhnya hal itu sungguh berat, kecuali atas orang-orang yang merendahkan diri.</span></p><p><span>[d] </span><span>2</span><span>:</span><span>154; 7:120.</span></p><p><span>[e] </span><span>4</span><span>:</span><span>143; 9:54.</span></p>');
INSERT INTO `quran` VALUES (6402,2,2,47,'<p><span>Orang-orang yang yakin bahwa </span><span>[a] </span><span>mereka akan bertemu dengan Tuhan mereka dan bahwa kepada-Nya juga mereka akan kembali</span></p><p><span>[a] </span><span>2</span><span>:</span><span>224, 250; 11:30; 18:111; 29:6, 84:7.</span></p>');
INSERT INTO `quran` VALUES (6403,2,2,48,'<p><span>R. 6</span></p><p><span>Hai Bani Israil, </span><span>[b] </span><span>ingatlah nikmat-Ku yang telah Aku anegerahkan kepadamu dan bahwa telah </span><span>[c] </span><span>Aku muliakan kamu di atas seluruh alam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=84">84</a><span>]</span></p><p><span>[b] </span><span>Lihat 2 </span><span>:</span><span> 41.</span></p><p><span>[c] </span><span>2</span><span>:</span><span>123; 3:34; 5:31; 6:87; 7:141, 45:17.</span></p>');
INSERT INTO `quran` VALUES (6404,2,2,49,'<p><span>Dan takutlah hari itu </span><span>[d] </span><span>bilamana suatu jiwa tak akan dapat menggantikan jiwa yang lain sedikitpun, dan </span><span>[e] </span><span>tidak akan diterima untuknya suatu syafaat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=85">85</a><span>]</span><span> dan tidak akan diambil suatu tebusan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=86">86</a><span>]</span><span> darinya dan tidak pula mereka akan ditolong.</span></p><p><span>[d] </span><span>2</span><span>:</span><span>124; 31:34;82:20.</span></p><p><span>[e] </span><span>2</span><span>:</span><span>124; 256; 19:88, 20:110; 21:29; 34:24; 39:45; 43:87; 53:27; 74:49</span><span> </span></p>');
INSERT INTO `quran` VALUES (6405,2,2,50,'<p><span>Dan, </span><span>ingatlah</span><span> ketika Kami </span><span>[a] </span><span>membebaskan kamu dari kaum </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=87">87</a><span>]</span><span> Firaun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=88">88</a><span>]</span><span> yang menimpahkan azab yang pedih </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=88a">88a</a><span>]</span><span> kepadamu </span><span>[b] </span><span>dengan membunuh anak-anak lelakimu dan membiarkan hidup wanita-wanitamu, dan dalam hal itu adalah suatu cobaan besar dari Tuhan-mu.</span></p><p><span>[a] </span><span>14</span><span>:</span><span>7; 20:81; 44:31, 43.</span></p><p><span>[b] </span><span>7</span><span>:</span><span>128, 142; 28:5.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6406,2,2,51,'<p><span>Dan, </span><span>ingatlah</span><span> ketika Kami membela laut </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=89">89</a><span>]</span><span> untukmu lalu Kami menyelamatkan kamu dan </span><span>[a] </span><span>Kami menenggelamkan kaum Firaun, sedang kamu menyaksikannya.</span></p><p><span>[a] </span><span>7</span><span>:</span><span>137; 8:55 ; 20:78, 81; 26:64-67; 28:41; 44:25</span><span> </span><span> </span></p>');
INSERT INTO `quran` VALUES (6407,2,2,52,'<p><span>Dan, </span><span>ingatlah</span><span> Kami </span><span>[a] </span><span>berjanji kepada Musa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=90">90</a><span>] </span><span>empat puluh malam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=91">91</a><span>]</span><span> kemudian sepeninggalnya, kamu </span><span>[b] </span><span>menjadikan anak lembu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=92">92</a><span>]</span><span> </span><span>sebagai sembahan</span><span> dan kamu orang-orang aniaya.</span></p><p><span>[a] </span><span>7</span><span>:</span><span>143.</span><span> </span></p><p><span>[b] </span><span>2</span><span>:</span><span>55, 93; 4:154; 7:149, 153; 20:89.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6408,2,2,53,'<p><span>Lalu, </span><span>[c] </span><span>Kami mengampuni kamu sesudah itu, supaya kamu bersyukur.</span></p><p><span>[c] </span><span>4</span><span>:</span><span>154</span><span>.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6409,2,2,54,'<p><span>Dan, </span><span>ingatlah</span><span> ketika </span><span>[d] </span><span>Kami memberikan kepada Musa AlKitab </span><span>[93] </span><span>dan </span><span>[e] </span><span>Furqam </span><span>[94]</span><span> supaya kamu mendapat petunjuk.</span></p><p><span>[d] </span><span>2</span><span>: </span><span>88; 23:50; 32:24; 37:118; 40:54.</span></p><p><span>[e] </span><span>21</span><span>:</span><span>49.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6410,2,2,55,'<p><span>Dan, </span><span>ingatlah </span><span>ketika Musa berkata kepada kaumnya, “ Hai kaumku, sesungguhnya kamu telah menganiaya dirimu dengan menjadikan anak lembu </span><span> sebagai sembahan</span><span>; karena itu kembalilah kepada Pencipta-mu, kemudian bunuhlah hawa-nafsumu; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=95">95</a><span>]</span><span> yang demikian itu amat baik bagimu pada sisi Pencipta-mu. “ Lalu, Dia menerima tobatmu. Sesungguhnya Dia Maha Penerima Tobat, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (6411,2,2,56,'<p><span>Dan, </span><span>ingatlah</span><span> ketika kamu berkata, “ Hai Musa, sekali-kali kami tidak akan percaya kepada engkau </span><span>[a] </span><span>sebelum kami melihat Allah bermuka-muka “ lalu kamu ditimpah azab yang memati kan, sedang kamu menyaksikan.</span></p><p><span>[a] </span><span>4 </span><span>:</span><span>154.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6412,2,2,57,'<p><span>Kemudian, </span><span>[a] </span><span>Kami bangkitkan kamu sesudah kematian </span><span>rohani</span><span>-</span><span>mu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=96">96</a><span>]</span><span> supaya kamu bersyukur.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>260; 6:123.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6413,2,2,58,'<p><span>Dan, Kami jadikan awan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=97">97</a><span>]</span><span> menaungimu dan Kami </span><span>[a] </span><span>turunkan manna, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=98">98</a><span>]</span><span> dan salwa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=99">99</a><span>]</span><span> untukmu. </span><span>[b] </span><span>Makanlah makanan yang baik-baik yang telah Kami rezekikan kepadamu. Dan tidaklah mereka merugikan Kami, akan tetapi mereka </span><span>hanya</span><span> merugikan diri sendiri.</span></p><p><span>[a] </span><span>7</span><span>:</span><span>161</span><span> </span></p><p><span>[b] </span><span>7</span><span>:</span><span>161; 20:81</span><span> </span></p>');
INSERT INTO `quran` VALUES (6414,2,2,59,'<p><span>Dan, </span><span>ingatlah</span><span> </span><span>[a] </span><span>ketika Kami berfirman, “Masuklah ke kota </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=100">100</a><span>]</span><span> ini dan makanlah darinya di mana kamu sukai sepuas hati; dan masukilah pintu-</span><span>Nya</span><span> dengan tunduk sambil mengucapkan, “</span><span>Tuhan</span><span>, bebaskanlah </span><span>kami</span><span> dari dosa kami,” Kami akan ampuni kesalahan -kesalahanmu, dan akan Kami lipat gandakan </span><span>anugerah</span><span> kepada orang-orang yang berbuat baik.”</span></p><p><span>[a] </span><span>7</span><span>:</span><span>162.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6415,2,2,60,'<p><span>Maka, </span><span>[b] </span><span>orang-orang aniaya menukar ucapan itu dengan apa yang tidak dikatakan kepada mereka, lalu Kami menurunkan kepada orang-orang aniaya itu azab dari langit, disebabkan mereka durhaka.</span></p><p><span>[b] </span><span>7</span><span>:</span><span>163.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6416,2,2,61,'<p><span>R. 7</span></p><p><span>Dan, </span><span>ingatlah</span><span> </span><span>[a] </span><span>ketika Musa memohonkan air untuk kaumnya, dan Kami berfirman, “Pukullah batu itu dengan tongkat engkau;” maka memancarlah darinya dua belas mata air,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=101">101</a><span>]</span><span> sehingga tiap-tiap suku mengetahui tempat minum mereka. Makan dan minumlah dari rezeki Allah, dan janganlah kamu berlaku sewenang-wenang di muka bumi dengan menimbulkan kekacauan.”</span></p><p><span>[c] </span><span>7</span><span>:</span><span>161</span><span> </span></p>');
INSERT INTO `quran` VALUES (6417,2,2,62,'<p><span>Dan, </span><span>ingatlah</span><span> ketika kamu berkata, “Hai Musa, sungguh kami tak sabar dengan satu macam makanan saja; karena itu mohonkanlah untuk kami kepada Tuhan engkau supaya Dia mengadakan untuk kami dari apa-apa yang ditumbuhkan bumi, sayur- mayurnya, mentimunnya, bawang putih nya/gandumnya, kacang-kacangan dan bawang merahnya. “Dia berfirman, “Apakah kamu mau meminta yang lebih buruk sebagai ganti yang lebih baik? Pergilah ke suatu kota, niscaya akan kamu peroleh apa yang kamu minta.”</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=102">102</a><span>]</span><span> Maka </span><span>[a] </span><span>ditimpahkan kepada mereka kehinaan dan kemiskinan, dan </span><span>[b] </span><span>mereka kembali dengan mendapat kemurkaan dari Allah; yang demikian itu karena mereka ingkar kepada Ayat-ayat Allah dan mereka membunuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=103">103</a><span>] [c] </span><span>nabi-nabi tampa hak. Itu mereka selalu berbuat durhaka dan melampui batas.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>112.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>91; 3:113; 5:61.</span></p><p><span>[c] </span><span>2</span><span>:</span><span>88; 3:22, 113, 184; 5:71.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6418,2,2,63,'<p><span>R. 8</span></p><p><span>[a] </span><span>Sesungguhnya orang-orang yang beriman dan orang-orang Yahudi dan orang-orang Nasrani dan orang-orang Shabi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=104">104</a><span>]</span><span> barangsiapa </span><span>di antara mereka</span><span> </span><span>[b] </span><span>beriman kepada Allah dan Hari Kemudian serta mengerjakan amal saleh, maka untuk mereka ada ganjaran pada sisi Tuhan mereka, dan </span><span>[c] </span><span>tidak ada ketakutan menimpa mereka dan tidak pula mereka akan bersedih.</span></p><p><span>[a] </span><span>5</span><span>:</span><span>70; 22:18.</span></p><p><span>[b] </span><span>4</span><span>:</span><span>137; 6:93.</span></p><p><span>[c] </span><span>2</span><span>:</span><span>113, 278; 6:49, 10:63.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6419,2,2,64,'<p><span>Dan, </span><span>ingatlah</span><span> ketika </span><span>[a] </span><span>Kami mengambil janji yang teguh dari kamu dan </span><span>[b] </span><span>Kami menjulang tinggikan Gunung Thur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=105">105</a><span>]</span><span> di atas kamu. Pegang teguhlah apa yang Kami berikan kepadamu dan camkanlah apa yang ada di dalamnya supaya kamu menjadi orang yang bertakwa.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>84, 94; 4:155.</span></p><p><span>[b] </span><span>7</span><span>:</span><span>172</span><span> </span></p>');
INSERT INTO `quran` VALUES (6420,2,2,65,'<p><span>kemudian kamu berpaling sesudah itu; maka sekiranya tidak ada karunia Allah dan Rahmat- Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=106">106</a><span>]</span><span> atasmu, niscaya kamu termasuk orang-orang yang rugi.</span></p>');
INSERT INTO `quran` VALUES (6421,2,2,66,'<p><span>Dan sesungguhnya, kamu telah mengetahui orang-orang di antaramu yang </span><span>[a] </span><span>melanggar mengenai </span><span>Hari</span><span> Sabat. Maka Kami berfirman kepada mereka, </span><span>[b] </span><span>” Jadilah kamu sekalian kera yang hina.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=107">107</a><span>]</span></p><p><span>[a] </span><span>4</span><span>:</span><span>48, 155; 7:164; 16:125.</span></p><p><span>[b] </span><span>5</span><span>:</span><span> 61; 7:167.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6422,2,2,67,'<p><span>Maka Kami menjadikannya </span><span>[c] </span><span>peringatan bagi orang-orang yang ada pada masanya dan bagi orang-orang yang </span><span>datang</span><span> di belakangnya, dan nasihat bagi orang-orang yang bertakwa.</span></p><p><span>[c] </span><span>5</span><span>:</span><span>39.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6423,2,2,68,'<p><span>Dan, </span><span>ingatlah</span><span> ketika Musa berkata kepada kaumnya, “ Sesungguhnya Allah menyuruh kamu menyembeli seekor lembu betina, berkata mereka , </span><span>‘</span><span>Apakah engkau menjadikan kami sasaran olok</span><span>–</span><span>olok?</span><span>’</span><span> Berkata ia, “Aku berlindung kepada Allah dari menjadi seorang di antara orang-orang jahil.”</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6424,2,2,69,'<p><span>Mereka berkata, “Mohonkanlah untuk kami kepada Tuhan engkau supaya Dia menjelaskan bagi kami apakah itu.” Ia, berkata, “Sesungguhnya Dia berfirman bahwa itu adalah lembu betina, tidak tua dan tidak muda, pertengahan di antara itu, maka lakukanlah apa yang diperintahkan kepadamu.”</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6425,2,2,70,'<p><span>Mereka berkata, “Mohonkanlah untuk kami kepada Tuhan engkau supaya dia menjelaskan kepada kami apa warnanya.” Ia menjawab , ”Sesungguhnya Dia berfirman bahwa lembu betina itu kuning, cemerlang warnanya, menyenangkan orang-orang yang memandangnya.”</span><span> </span></p>');
INSERT INTO `quran` VALUES (6426,2,2,71,'<p><span>Mereka berkata, “Mohonkanlah </span><span>lagi</span><span> untuk kami kepada Tuhan engkau supaya Dia menjelaskan kepada kami bagaimanakah itu, karena lembu </span><span>macam </span><span> itu serupa saja bagi kami; dan jika Allah menghendaki, kami sesungguhnya akan menerima petunjuk.</span></p>');
INSERT INTO `quran` VALUES (6427,2,2,72,'<p><span>Ia berkata, “ Sesungguhnya Dia berfirman bahwa lembu itu lembu betina yang </span><span>[a] </span><span>belum dijinakkan untuk mambajak tanah dan tidak pula mengairi sawah; mulus tak ada cacatnya.” Kata mereka , “Sekarang baru engkau mengemukakan hal sebenarnya” maka mereka menyembelih, dan hampir mereka tidak mau mengerjakannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=108">108</a><span>]</span></p><p><span>[a] </span><span>67</span><span>:</span><span>16</span><span> </span></p>');
INSERT INTO `quran` VALUES (6428,2,2,73,'<p><span>R. 9</span></p><p><span>Dan, </span><span>ingatlah</span><span> ketika kamu </span><span>berusaha</span><span> membunuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=109">109</a><span>]</span><span> seseorang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=109a">109a</a><span>]</span><span> lalu kamu berselisih mengenainya. Dan Allah menyingkapkan apa yang kamu sembunyikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=109b">109b</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (6429,2,2,74,'<p><span>Maka Kami berfirman, “Bandingkanlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=110">110</a><span>]</span><span> </span><span>peristiwa</span><span> ini dengan beberapa peristiwa </span><span>semacamnya, baru akan kamu ketahui kebenarannya.</span><span>”</span><span> </span><span>Demikian </span><span>[a] </span><span>Allah menghidupkan yang mati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=110a">110a</a><span>]</span><span> dan memperlilhatkan Tanda-tanda-Nya kepadamu supaya kamu menggunakan akal.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>180.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6430,2,2,75,'<p><span>Lalu, menjadi keras </span><span>[a] </span><span>hatimu sesudah itu hingga ia seperti batu atau lebih keras </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=111">111</a><span>]</span><span> lagi; dan sesungguhnya di antara batu-batu pun ada yang mengalir darinya sungai-sungai, dan sesungguhnya di antaranya ada yang jatuh tersungkur karena takut kepada Allah . Dan, Allah sekali-kali tidak lalai terhadap apa yang kamu kerjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=112">112</a><span>]</span></p><p><span>[a] </span><span>5</span><span>:</span><span>14; 6:44; 57:17 </span></p>');
INSERT INTO `quran` VALUES (6431,2,2,76,'<p><span>Apakah kamu mengharapkan bahwa mereka, akan percaya kepada</span><span>–</span><span>mu ? Padahal ada satu golongan di antara mereka yang mendengar firman Allah lalu mereka </span><span>[a] </span><span>mengubahnya sesudah memahaminya, padahal mereka mengetahui </span><span>akibat perbuatan itu.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>4:47; 5:14, 42.</span></p>');
INSERT INTO `quran` VALUES (6432,2,2,77,'<p><span>Dan, </span><span>[b] </span><span>apabila mereka bertemu dengan orang-orang beriman, berkata mereka, “ Kami telah beriman,” dan apabila mereka bertemu satu sama lain berkata mereka, “Apakah kamu memberitahukan kepada mereka, apa yang telah diterangkan Allah kepadamu, karena dengan itu nanti mereka dapat berbantah dengan kamu di hadapan Tuhan-mu. Apakah kamu tidak berakal ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=113">113</a><span>]</span></p><p><span>[b] </span><span>2</span><span>:</span><span>15; 3:120, 5:62.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6433,2,2,78,'<p><span>Apakah mereka tidak mengetahui bahwa </span><span>[c] </span><span>Allah mengetahui apa yang mereka sembunyikan dan apa yang mereka nyatakan.</span></p><p><span>[c] </span><span>11:6; 35:39.</span></p>');
INSERT INTO `quran` VALUES (6434,2,2,79,'<p><span>Dan di Antara mereka ada yang buta huruf, </span><span>[113a]</span><span> mereka tidak mengetahui AlKitab kecuali beberapa khayalan palsu, bahkan mereka hanya menduga-duga.</span></p>');
INSERT INTO `quran` VALUES (6435,2,2,80,'<p><span>Maka, celakalah orang-orang yang menulis Alkitab dengan tangan mereka sendiri, kemudian berkata mereka, “Ini dari sisi Allah ,” supaya mereka memperoleh </span><span>[a] </span><span>sedikit keuntungan dengan itu. Celakalah mereka disebabkan apa yang dituliskan oleh tangan mereka dan celaka </span><span>pulalah</span><span> mereka karena apa yang diusahakan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=114">114</a><span>]</span></p><p><span>[a] </span><span>2</span><span>:</span><span>175; 3:200.</span></p>');
INSERT INTO `quran` VALUES (6436,2,2,81,'<p><span>Dan mereka berkata: “Api </span><span>[a] </span><span>tidak akan menyentuh kami kecuali beberapa hari saja. “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=115">115</a><span>]</span><span> Katakanlah, </span><span>[b] </span><span>”Adakah kamu telah menerima suatu janji dari Allah ? </span><span>Kalau demikian,</span><span> maka Allah sekali-kali tidak akan menyalahi janji-Nya. Atau kamu berkata terhadap Allah apa yang tidak kamu ketahui ?”</span></p><p><span>[a] </span><span>3</span><span>:</span><span>25</span></p><p><span>[b] </span><span>54</span><span>:</span><span>44</span><span>.</span></p>');
INSERT INTO `quran` VALUES (6437,2,2,82,'<p><span>Memang, barangsiapa mengerjakan kejahatan, dosanya meliputinya, maka mereka akan tinggal lama di dalamnya.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6438,2,2,83,'<p><span>Dan orang-orang yang beriman serta mengerjakan amal saleh mereka itulah penghuni sorga; mereka akan menetap di dalamnya</span></p>');
INSERT INTO `quran` VALUES (6439,2,2,84,'<p><span>Dan,</span><span> ingatlah </span><span> ketika </span><span>[a] </span><span>Kami mengambil janji teguh dari Bani Israil, bahwa “Janganlah kamu menyembah </span><span>sesuatu</span><span> selain Allah dan berbuatlah kebaikan terhadap ibu-bapak, kaum kerabat, anak-anak yatim dan orang-orang miskin, dan ucapkanlah kata-kata baik kepada manusia dan </span><span>[a] </span><span>dawamlah mendirikan shalat dan bayarlah zakat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=116">116</a><span>]</span><span> kemudian kamu berpaling, kecuali sedikit di antara kamu dan kamu selalu berpaling.</span></p><p><span>[a] </span><span>4</span><span>:</span><span>155; 5:13.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>44, 111; 4:78; 6:73, 22:79, 24:57; 30:32.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6440,2,2,85,'<p><span>Dan, </span><span>ingatlah</span><span> ketika Kami mengambil janji teguh dari kamu, bahwa,“Janganlah kamu menumpahkan darah </span><span>sesama</span><span>-mu dan janganlah kamu mengusir kaummu dari kampung halamanmu,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=117">117</a><span>]</span><span> kemudian kamu mengikrarkannya; dan kamu </span><span>selalu</span><span> menjadi saksi </span><span>atasnya</span><span>.</span></p>');
INSERT INTO `quran` VALUES (6441,2,2,86,'<p><span>Kemudian kamulah orang-orang yang membunuh satu sama lain dan mengusir segolongan dari kamu dari kampung-halaman mereka, sambil membantu </span><span>musuh-musuh</span><span> mereka dalam dosa dan pelanggaran. Dan, jika mereka datang kepadamu selaku tawanan, kamu menebus mereka, padahal pengusiran mereka telah diharamkan bagimu. Adakah kamu beriman kepada sebagian Alkitab dan ingkar kepada sebagian lainnya? Maka tak ada balasan bagi orang yang berbuat demikian diantaramu kecuali kehinaan di dalam kehidupan dunia; dan pada hari Kiamat mereka akan dikembalikan kepada azab yang sangat keras; dan sesungguhnya Allah tidak lengah terhadap apa yang kamu kerjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=118">118</a><span>]</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6442,2,2,87,'<p><span>Mereka inilah orang-orang yang telah membeli kehidupan dunia dengan akhirat; karena itu tidak diringankan dari mereka azab, dan mereka tidak akan ditolong.</span></p>');
INSERT INTO `quran` VALUES (6443,2,2,88,'<p><span>R. 11</span></p><p><span>Dan sesungguhnya </span><span>[a] </span><span>Kami memberikan Alkitab kepada Musa dan Kami </span><span>[b] </span><span>mengikutkan rasul-rasul di belakangnya, dan </span><span>[c] </span><span>Kami memberikan kepada Isa Ibnu Maryam Tanda-tanda yang nyata, dan Kami memperkuatnya dengan </span><span>[d] </span><span>Ruhul Kudus. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=119">119</a><span>]</span><span> Maka apakah setiap datang kepadamu seorang Rasul yang tidak disukai oleh dirimu, kamu menyombongkan dan sebagian dari kamu dustakan dan sebagian lainnya kamu bunuh ?</span></p><p><span>[a] </span><span>Lihat 2 </span><span>:</span><span> 54</span></p><p><span>[b] </span><span>5</span><span>:</span><span>47; 57:28.</span></p><p><span>[c] </span><span>2</span><span>:</span><span>254; 3:185:111; 43:64</span></p><p><span>[d] </span><span>16</span><span>:</span><span>103.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6444,2,2,89,'<p><span>Dan mereka berkata, </span><span>[e] </span><span>”Hati kami tertutup.” Bukan demikian, tetapi Allah telah mengutuk mereka; karena kekufuran mereka ;maka sedikit sekali mereka yang beriman.</span></p><p><span>[e] </span><span>4</span><span>:</span><span>156; 41:6.</span></p>');
INSERT INTO `quran` VALUES (6445,2,2,90,'<p><span>Dan, ketika datang kepada mereka sebuah Kitab dari Allah </span><span>[a] </span><span>menggenapi yang ada pada mereka dan sebelum itu mereka telah memohon kemenangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=120">120</a><span>]</span><span> atas orang-orang ingkar, maka ketika datang </span><span>[b] </span><span>apa yang mereka ketahui, mereka mengingkarinya Maka laknat Allah atas orang-orang kafir</span></p><p><span>[a] </span><span>2</span><span>:</span><span>42, 92, 98, 102; 3:82, 4:48:36:32; 46:13.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>147</span><span> </span></p>');
INSERT INTO `quran` VALUES (6446,2,2,91,'<p><span>Alangkah buruknya hal demikian itu, mereka telah menjual diri mereka, yakni mereka ingkar kepada apa yang diturunkan Allah , </span><span>disebabkan</span><span> iri hati karena Allah menurunkan karunia-Nya kepada siapa yang Dia kehendaki di antara hamba-hamba Nya. Maka </span><span>[c] </span><span>mereka ditimpa kemurkaan demi kemurkaan; dan bagi orang-orang kafir ada azab yang menghinakan.</span></p><p><span>[c] </span><span>3</span><span>:</span><span>113; 5:61.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6447,2,2,92,'<p><span>Dan, </span><span>[a] </span><span>apabila dikatakan kepada mereka, “ Berimanlah kepada apa yang diturunkan Allah ,” berkata mereka, “ Kami beriman kepada apa yang diturunkan kepada kami,” dan mereka ingkar kepada yang </span><span>diturunkan</span><span> sesudahnya, padahal itulah kebenaran yang menggenapi yang ada pada mereka. Katakanlah </span><span>[b] </span><span>”Jika demikian, mengapakah kamu membunuh nabi-nabi Allah sebelum ini, jika kamu orang-orang yang beriman ?”</span></p><p><span>[a] </span><span>2</span><span>:</span><span>171.</span></p><p><span>[b] </span><span>3</span><span>:</span><span>113, 182</span></p>');
INSERT INTO `quran` VALUES (6448,2,2,93,'<p><span>Dan, sesungguhnya telah datang kepadamu Musa dengan Tanda</span><span>–</span><span>tanda nyata, kemudian sepeninggalnya</span><span>[c]</span><span> </span><span> </span><span>kamu menjadikan anak lembu </span><span>sebagai sembahan </span><span>dan kamu orang-orang aniaya.</span></p><p><span>[c] </span><span>2</span><span>:</span><span>52; 4:154; 7:149, 153; 20:98.</span></p>');
INSERT INTO `quran` VALUES (6449,2,2,94,'<p><span>Dan, </span><span>ingatlah</span><span> </span><span>[d] </span><span>ketika Kami mengambil janji teguh dari kamu dan Kami menjulang tinggikan Gunung Thur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=121">121</a><span>]</span><span> di atas kamu. Pegang teguh apa yang telah Kami berikan kepadamu dan dengarkanlah. Berkata mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=121a">121a</a><span>]</span><span> “Kami dengar, dan kami tidak taat; dan hati mereka diresapkan dengan </span><span>kecintaan kepada </span><span>anak lembu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=122">122</a><span>]</span><span> karena kekufuran mereka. Katakanlah, “Alangkah buruk apa yang diperintahkan imanmu kepadamu, jika kamu orang-orang beriman.”</span></p><p><span>[d] </span><span>2</span><span>:</span><span>64; 4:155; 7:172</span></p>');
INSERT INTO `quran` VALUES (6450,2,2,95,'<p><span> </span><span>Katakanlah, </span><span>[a]</span><span> </span><span>”Jika tempat kediaman akhirat di sisi Allah itu semata-mata untukmu, bukan untuk orang lain, maka </span><span>cobalah </span><span>kamu mengharap mati, sekiranya kamu orang benar.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=122a">122a</a><span>]</span></p><p><span>[a] </span><span>2:112; 62:7.</span></p>');
INSERT INTO `quran` VALUES (6451,2,2,96,'<p><span>Dan, mereka </span><span>[b] </span><span>sekali-kali tidak akan mengingini </span><span>maut</span><span> itu selama-lamanya, disebabkan apa yang telah dikerjakan tangan mereka. Dan Allah Maha Mengatahui orang-orang aniaya.</span></p><p><span>[b] </span><span>62:8</span></p>');
INSERT INTO `quran` VALUES (6452,2,2,97,'<p><span>Dan, pasti akan engkau dapati mereka manusia paling serakah </span><span>pada</span><span> kehidupan </span><span>dunia</span><span> dan </span><span>bahkan lebih</span><span> daripada orang-orang musyrik; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=122b">122b</a><span>]</span><span> masing-masing mereka ingin diberi umur seribu tahun, padahal diberi umur </span><span>selama itu</span><span> </span><span>[a] </span><span>tidak dapat menjauhkannya dari azab; dan Allah Maha Melihat apa yang mereka kerjakan.</span></p><p><span>[a] </span><span>62:9.</span></p>');
INSERT INTO `quran` VALUES (6453,2,2,98,'<p><span>Katakanlah, “Barangsiapa menjadi musuh bagi Jibril, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=123">123</a><span>]</span><span> karena sesungguhnya dialah yang </span><span>[b ]</span><span>menurunkan Kitab ke dalam hati engkau dengan seizin Allah </span><span>[c] </span><span>menggenapi </span><span>kalam</span><span> yang ada sebelumnya; dan merupakan petunjuk dan khabar suka bagi orang-orang mukmin.</span></p><p><span>[b] </span><span>26:194, 195.</span></p><p><span>[c] </span><span>Lihat 2:90.</span></p>');
INSERT INTO `quran` VALUES (6454,2,2,99,'<p><span>[a] </span><span>”Barangsiapa menjadi musuh Allah dan malaikat-malaikat-Nya dan rasul-rasul-Nya dan Jibril dan Mikail, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=124">124</a><span>]</span><span> maka sesungguhnya Allah itu musuh bagi orang-orang kafir.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=125">125</a><span>]</span></p><p><span>[a] </span><span>58:6.</span></p>');
INSERT INTO `quran` VALUES (6455,2,2,100,'<p><span>Dan, sesungguhnya, Kami telah menurunkan kepada engkau Tanda</span><span>–</span><span>tanda yang nyata, dan tidak lah seorang pun ingkar kepada-Nya kecuali orang- orang durhaka.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6456,2,2,101,'<p><span>Adakah </span><span>[b] </span><span>setiap kali mereka membuat janji, segolongan dari mereka membuangnya ? Bahkan, kebanyakan dari mereka tidak beriman.</span></p><p><span>[b] </span><span>3:188.</span></p>');
INSERT INTO `quran` VALUES (6457,2,2,102,'<p><span>Dan tatkala datang kepada mereka seorang rasul dari Allah , </span><span>[c] </span><span>menggenapi apa yang ada pada mereka, segolongan dari orang-orang yang diberi Alkitab </span><span>[d] </span><span>membuang Kitab Allah ke belakang punggung mereka, seolah-olah mereka tidak mengetahui.</span></p><p><span>[c] </span><span>Lihat 2:90.    </span></p><p><span>[d] </span><span>3:188.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6458,2,2,103,'<p><span>Dan mereka mengikuti apa yang diikuti </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=126">126</a><span>]</span><span> oleh pemberontak-pemberontak di masa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=127">127</a><span>]</span><span> kerajaan Sulaiman, dan bukanlah Sulaiman yang ingkar melainkan pemberontak-pemberontaklah yang ingkar; mereka mengajarkan sihir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=128">128</a><span>]</span><span> kepada manusia. Dan </span><span>mereka mengaku mengikuti</span><span> apa yang telah diturunkan kepada dua malaikat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=129">129</a><span>]</span><span> di Babil, Harut dan Marut. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=130">130</a><span>]</span><span> Dan keduanya tidaklah mengajar seorangpun sebelum mereka mengatakan, “Sesungguhnya hanya kami satu cobaan karena itu janganlah kamu ingkar, “ Maka orang-orang belajar dari keduanya hal dengan itu mereka membuat perbedaan di antara laki-laki dan istrinya, dan mereka tidak mendatangkan mudarat kepada seorang pun dengan itu kecuali dengan seizin Allah; dan mereka ini belajar hal yang mendatangkan mudarat kepada mereka dan tidak bermanfaat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=130a">130a</a><span>]</span><span> bagi mereka. Dan sesungguhnya mereka mengetahui bahwa barangsiapa berniaga dengan cara ini tiada baginya suatu bagian </span><span>keuntungan</span><span> di akhirat. Dan sungguh amat buruk hal yang untuk itu mereka menjual diri mereka; sekiranya mereka mengatahui.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6459,2,2,104,'<p><span>Dan </span><span>[a] </span><span>sekiranya mereka beriman dan bertakwa, tentu ganjaran yang terbaik adalah di sisi Allah, sekiranya mereka mengatahui.</span></p><p><span>[a] </span><span>3</span><span>:</span><span> 180; 5:55, 67</span></p>');
INSERT INTO `quran` VALUES (6460,2,2,105,'<p><span>Hai orang-orang yang beriman, </span><span>[b] </span><span>jangan kamu katakan </span><span>kepada Nabi,</span><span> “Ra</span><span>’</span><span>ina”, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=131">131</a><span>]</span><span> tetapi katakanlah, “Unzhurna” dan dengarlah. Dan bagi orang-orang kafir ada azab yang pedih.</span></p><p><span>[a] </span><span>4</span><span>:</span><span>47</span><span> </span></p>');
INSERT INTO `quran` VALUES (6461,2,2,106,'<p><span>Orang-orang yang ingkar di antara Ahlikitab dan orang-orang musyrik tidak suka suatu kebaikan dari Tuhan-mu diturunkan kepadamu, dan Allah </span><span>[a] </span><span>mengkhususkan rahmat-Nya bagi siapa yang Dia kehendaki. Dan Allah Yang Empunya karunia yang besar.</span></p><p><span>[a] </span><span>3</span><span>:</span><span> 75.</span></p>');
INSERT INTO `quran` VALUES (6462,2,2,107,'<p><span> </span><span>[b] </span><span>Ayat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=131a">131a</a><span>]</span><span> mana pun yang Kami mansukhkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=132">132</a><span>]</span><span> atau Kami biarkan terlupa , Kami mendatangkan yang lebih baik darinya atau yang semisalnya. Tidak tahukah engkau bahwa Allah Maha Kuasa atas segala sesuatu ?</span></p><p><span>[b] </span><span>16</span><span>:</span><span> 102.</span></p>');
INSERT INTO `quran` VALUES (6463,2,2,108,'<p><span>Tidak tahukah engkau bahwa kepunyaan-Nya </span><span>[a] </span><span>Kerajaan langit dan bumi ? Dan tiada bagimu selain Allah pelindung dan penolong.</span></p><p><span>[a] </span><span>3</span><span>:</span><span> 190; 5:41; 7:159; 9:116; 43:86; 57:6.</span></p>');
INSERT INTO `quran` VALUES (6464,2,2,109,'<p><span>[b] </span><span>Adakah kamu hendak menanyai </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=133">133</a><span>]</span><span> Rasulmu sebagaimana Musa telah ditanya dahulu ? Dan barang siapa menukar iman dengan kekufuran, maka sesungguhnya ia sesat dari jalan lurus.</span></p><p><span>[b] </span><span>4</span><span>:</span><span> 154.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6465,2,2,110,'<p><span>Kebanyakan dari Ahlikitab menginginkan </span><span>[a] </span><span>supaya mereka dapat mengembalikan kamu menjadi kafir sesudah kamu beriman, karena dengki yang timbul dari diri mereka sendiri setelah nyata bagi mereka kebenaran itu. Maka </span><span>[b] </span><span>maafkanlah dan biarkanlah mereka hingga </span><span>[c] </span><span>Allah mendatangkan keputusan-Nya. Sesungguhnya Allah Maha Kuasa atas segala sesuatu</span><span>.</span></p><p><span>[a] </span><span>3</span><span>:</span><span> 101; 150; 4:90. </span></p><p><span>[b] </span><span>5</span><span>:</span><span> 14. </span></p><p><span>[c] </span><span>5</span><span>:</span><span> 53; 16:34.</span></p>');
INSERT INTO `quran` VALUES (6466,2,2,111,'<p><span>Dan </span><span>[d] </span><span>dirikanlah shalat dan bayarlah zakat; dan </span><span>[e] </span><span>kebaikan apa saja yang kamu dahulu kerjakan untuk dirimu, kamu akan memperolehnya di sisi Allah , Sesungguhnya Allah Maha Melihat segala yang kamu kerjakan.</span></p><p><span>[d] </span><span>Lihat 2</span><span>:</span><span> 4. </span></p><p><span>[e]</span><span> </span><span>73</span><span>:</span><span> 21</span></p>');
INSERT INTO `quran` VALUES (6467,2,2,112,'<p><span>Dan mereka berkata, </span><span>[a] </span><span>”Tidak ada yang masuk sorga. Kecuali orang- orang Yahudi atau Nasrani.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=134">134</a><span>]</span><span> Ini hanya angan-angan mereka. Katakanlah, “Kemukakanlah bukti-bukti-mu, jika kamu orang-orang benar.”</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 95; 62:7</span></p>');
INSERT INTO `quran` VALUES (6468,2,2,113,'<p><span>Mengapa tidak, </span><span>[a] </span><span>barangsiapa menyerahkan dirinya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=135">135</a><span>]</span><span> kepada Allah dan ia berbuat kebaikan maka bagi dia ada ganjarannya di sisi Tuhan-nya. Dan, </span><span>[b] </span><span>tak akan ada ketakutan menimpa mereka dan tidak pula mereka akan bersedih.</span></p><p><span>[a] </span><span>4</span><span>:</span><span> 126. </span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span> 63.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6469,2,2,114,'<p><span>Dan orang-orang Yahudi mengatakan, </span><span>[c] </span><span>”Orang-orang Nasrani tidak </span><span>berdiri </span><span> di atas sesuatu </span><span>kebenaran.</span><span> “ Dan orang-orang Nasrani mengatakan, </span><span>[d] </span><span>”Orang-orang Yahudi tidak berdiri di atas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=136">136</a><span>]</span><span> sesuatu </span><span>kebenaran</span><span>.” Padahal mereka membaca Alkitab yang </span><span>sama</span><span>. Demikian pula orang-orang yang tidak mengatahui mengatakan seperti perkataan mereka itu, Maka Allah akan menghakimi di antara mereka pada Hari Kiamat tentang apa yang mereka perselisihkan.</span></p><p><span>[c] </span><span>5</span><span>:</span><span> 69.    </span></p><p><span>[d] </span><span>5</span><span>:</span><span> 69.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6470,2,2,115,'<p><span>Dan siapakah yang lebih aniaya dari orang yang menghalangi menyebut nama-Nya </span><span>[a] </span><span>di dalam masjid-masjid Allah dan berupaya merusaknya? </span><span>[137]</span><span> Mereka itu tidak layak masuk ke dalamnya kecuali dengan rasa takut. Bagi mereka di dunia ada kehinaan dan bagi mereka di akhirat </span><span>tersedia </span><span>azab yang besar.</span></p><p><span>[a] </span><span>9</span><span>:</span><span> 17, 18; 22:26; 72:19. 20</span></p>');
INSERT INTO `quran` VALUES (6471,2,2,116,'<p><span>Dan, </span><span>[a] </span><span>kepunyaan Allah timur dan barat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=138">138</a><span>]</span><span> jadi kemana pun kamu menghadap, di sanalah perhatian Allah Sesungguhnya, Allah Maha Luas </span><span>perberian-Nya,</span><span> Maha Mengatahui.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 143; 26:29, 55:18.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6472,2,2,117,'<p><span>Dan, mereka berkata </span><span>[b] </span><span>”Allah mengambil seorang anak.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=139">139</a><span>]</span><span> Maha Suci Dia. Bahkan, Dia lah Yang Empunya segala sesuatu di langit dan di bumi. </span><span>[c] </span><span>Semuanya tunduk kepada-Nya.</span></p><p><span>[b] </span><span>4</span><span>:</span><span> 172; 6:101, 102; 10:69; 17:112; 18:5; 19:36, 89, 90; 21:27; 25:3; 39:5; 43:82</span></p><p><span>[c] </span><span>30</span><span>:</span><span> 27</span><span> </span></p>');
INSERT INTO `quran` VALUES (6473,2,2,118,'<p><span>Dia-lah </span><span>[d] </span><span>Pencipta </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=140">140</a><span>]</span><span> langit dan bumi. Dan apabila Dia menghendaki sesuatu, maka </span><span>[e] </span><span>Dia hanya berfirman kepadanya “Jadilah !” Maka terjadilah ia.</span></p><p><span>[d] </span><span>6</span><span>:</span><span> 102.     </span></p><p><span>[e] </span><span>3</span><span>:</span><span> 48; 6:74; 16:41; 36:83; 40:69.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6474,2,2,119,'<p><span>Dan, berkatalah orang-orang yang tidak berpengetahuan “Mengapakah Allah tidak berfirman dengan kami, atau mendatangkan </span><span>[a] </span><span>satu Tanda </span><span>[141]</span><span> kepada kami ?” Demikian pula orang-orang sebelum mereka mengatakan seperti perkataan mereka itu. Hati mereka serupa. Sesungguhnya Kami telah menjelaskan Tanda-tanda kepada suatu kaum yang yakin.</span></p><p><span>[a] </span><span>6</span><span>:</span><span> 38; 20:135; 21:6; 43:54</span></p>');
INSERT INTO `quran` VALUES (6475,2,2,120,'<p><span>Sesungguhnya, Kami mengutus engkau dengan hak, </span><span>[b] </span><span>pembawa khabar suka dan pemberi ingat. Dan, engkau tidak akan ditanyai tentang penghuni neraka.</span></p><p><span>[b] </span><span>5</span><span>:</span><span>20; 6:49; 17:106; 33:46.</span></p>');
INSERT INTO `quran` VALUES (6476,2,2,121,'<p><span>Dan orang Yahudi sekali-kali tidak akan senang kepada engkau dan tidak pula orang Nasrani hingga engkau mengikuti agama mereka. Katakanlah, “Sesungguhnya petunjuk Allah itu pentunjuk </span><span>yang benar.” </span><span>[a] </span><span>Dan jika engkau menuruti kemauan mereka setelah ilmu datang kapada engkau, niscaya engkau tidak akan mempunyai dari Allah seorang teman pun dan tidak pula penolong.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 146; 13:38.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6477,2,2,122,'<p><span>[b] </span><span>Orang-orang yang kepada mereka Kami berikan Alkitab </span><span>dan</span><span> mereka membacanya dengan bacaan yang sebenarnya </span><span>[142]</span><span> mereka itulah yang beriman kepadanya. Dan barangsiapa ingkar kepadanya, maka mereka itulah orang-orang yang rugi.</span></p><p><span>[b] </span><span>3</span><span>:</span><span> 114</span><span> </span></p>');
INSERT INTO `quran` VALUES (6478,2,2,123,'<p><span>R. 15</span></p><p><span>Hai Bani Israil! </span><span>[c] </span><span>Ingatlah nikmat-Ku yang Aku anugerahkan kepadamu dan telah </span><span>[d] </span><span>Aku mulia kan kamu di atas semesta alam.</span></p><p><span>[c] </span><span>Lihat 2</span><span>:</span><span>41.    </span></p><p><span>[d] </span><span>Lihat 2</span><span>:</span><span> 48.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6479,2,2,124,'<p><span>Dan, takutlah Hari itu </span><span>[a] </span><span>suatu jiwa tidak akan dapat menggantikan jiwa yang lain sedikit pun, dan tidak akan diterima darinya tebusan, </span><span>[b] </span><span>dan tidak akan bermanfaat baginya syafaat, dan tidak pula mereka akan ditolong.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span> 49.    </span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span> 49</span><span>.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6480,2,2,125,'<p><span>Dan </span><span>ingatlah </span><span>ketika Ibrahim diuji </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=142a">142a</a><span>]</span><span> oleh Tuhan-nya dengan beberapa perintah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=142b">142b</a><span>]</span><span> lalu dipenuhinya. Dia berfirman, “Sesungguhnya </span><span>[c] </span><span>Aku menjadikan engkau imam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=143">143</a><span>]</span><span> bagi manusia. ” Ia, </span><span>Ibrahim</span><span> memohon, “Dan dari antara keturunanku.” Berfirman Dia, “Janji-Ku tidak akan sampai kepada orang-orang aniaya.”</span></p><p><span> </span><span>[c] </span><span>2</span><span>:</span><span> 131; 16:121, 122; 60:5. </span><span> </span></p>');
INSERT INTO `quran` VALUES (6481,2,2,126,'<p><span>Dan </span><span>ingatlah </span><span>ketika Kami jadikan Rumah itu tempat berkumpul </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=144">144</a><span>]</span><span> bagi manusia dan tempat aman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=145">145</a><span>]</span><span> Dan </span><span>[a] </span><span>jadikanlah tempat berdiri Ibrahim itu tempat shalat. Dan Kami perintahkan kepada Ibrahim dan Ismail, “Sucikanlah rumah-Ku itu untuk orang-orang yang tawaf, yang I</span><span>’</span><span>tikaf, yang rukuk dan yang sujud.”</span></p><p><span>[a] </span><span>3</span><span>:</span><span> 98; 22:27</span><span> </span></p>');
INSERT INTO `quran` VALUES (6482,2,2,127,'<p><span>Dan </span><span>ingatlah</span><span> ketika Ibrahim berkata, “Ya Tuhan-ku,</span><span>[b] </span><span>jadikanlah </span><span>tempat</span><span> ini kota yang aman dan berilah rezeki buah-buahan kepada penduduknya di antara mereka yang beriman kepada Allah dan Hari Kemudian.” </span>Allah <span>berfirman, “Dan siapa yang ingkar, maka akan Aku beri sedikit kesenangan kepadanya; kemudian Aku akan memaksanya ke dalam azab Api, dan seburuk-buruk tempat kembali.”</span></p><p><span>[b] </span><span>3</span><span>:</span><span> 98; 14:36; 27:92; 28:58.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6483,2,2,128,'<p><span>Dan </span><span>ingatlah</span><span> ketika Ibrahim dan Ismail meninggikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=146">146</a><span>]</span><span> pondasi-pondasi Rumah itu </span><span>dan berdoa,</span><span> “Ya Tuhan kami; </span><span>[a] </span><span>terimalah </span><span>ini</span><span> dari kami; sesungguhnya Engkau-lah Maha Mendengar, Maha Mengetahui.”</span></p><p><span>[a] </span><span>14</span><span>:</span><span> 41.</span></p>');
INSERT INTO `quran` VALUES (6484,2,2,129,'<p><span>“Ya Tuhan kami. Jadikanlah kami berdua orang yang menyerahkan diri kepada Engkau, dan </span><span>jadikanlah</span><span> dari kamu antara keturunan kami satu umat yang menyerahkan diri kepada Engkau. Dan tunjukkanlah kepada kami cara-cara ibadah dan terima tobat kami; sesungguhnya Engkau-lah Penerima Tobat, Maha Penyayang.”</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6485,2,2,130,'<p><span>“Ya Tuhan kami, bangkitkanlah di tengah-tengah mereka </span><span>[a] </span><span>seorang rasul dari antara mereka yang akan membacakan Ayat-ayat Engkau kepada mereka dan yang mengajarkan Kitab dan hikmah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=147">147</a><span>]</span><span> kepada mereka dan akan mensucikan mereka. Sesungguhnya Engkau-lah Maha Perkasa, Maha Bijaksana.”</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 152; 3:165; 62:3.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6486,2,2,131,'<p><span>R. 16</span></p><p><span>Dan, Siapakah yang berpaling dari </span><span>[a] </span><span>agama Ibrahim selain orang yang berlaku bodoh atas dirinya? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=148">148</a><span>]</span><span> Dan, sesungguhnya telah </span><span>[b] </span><span>Kami pilih dia di dunia dan sesungguhnya di akhirat pun dia termasuk antara orang-orang saleh.</span></p><p><span>[a] </span><span>3</span><span>:</span><span> 96; 4:126; 6:162.    </span></p><p><span>[b] </span><span>2</span><span>:</span><span> 125; 3:34; 16:121, 122; 60:5.</span><span> </span><span> </span></p>');
INSERT INTO `quran` VALUES (6487,2,2,132,'<p><span>Ketika Tuhan-nya berfirman kepadanya, “Berserah dirilah,” ia berkata </span><span>[c] </span><span>”Aku telah berserah diri kepadamu Tuhan semesta alam.”</span></p><p><span>[c] </span><span>3</span><span>:</span><span> 68; 4:126.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6488,2,2,133,'<p><span>Dan, Ibrahim mewasiatkan demikian kepada anak-anaknya dan Ya</span><span>’</span><span>kub, </span><span>berkata,</span><span> “Hai anak-anakku, sesungguhnya </span>Allah <span>memilih agama ini bagimu; </span><span>[d] </span><span>maka janganlah kamu mati kecuali dalam keadaan menyerahkan diri.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=149">149</a><span>]</span></p><p><span>[d] </span><span>3</span><span>:</span><span> 103</span><span> </span></p>');
INSERT INTO `quran` VALUES (6489,2,2,134,'<p><span>Apakah kamu hadir di saat kematian menjelang Ya</span><span>’</span><span>kub, ketika ia berkata kepada anak-anaknya, “ Apakah yang akan kamu sembah sepeninggalanku?” Mereka menjawab, “Kami akan menyembah Tuhan engkau dan Tuhan bapak-bapak engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=150">150</a><span>] </span><span>Ibrahim, Ismail. Dan Ishak, yaitu Tuhan Yang Maha Esa; dan kepada-Nya kami menyerahkan diri.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=151">151</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (6490,2,2,135,'<p><span>[a] </span><span>Itulah umat yang berlalu, bagi mereka apa yang diusahakan mereka dan bagimu apa yang kamu usahakan; dan kamu tidak akan ditanya mengenai apa yang mereka telah kerjakan.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 142</span><span> </span></p>');
INSERT INTO `quran` VALUES (6491,2,2,136,'<p><span>Dan mereka berkata, </span><span>[b] </span><span>”Jadilah kamu Yahudi atau Nasrani, </span><span>barulah</span><span> kamu akan mendapat petunjuk.” Katakanlah, “Tidak, bahkan </span><span>turutilah</span><span> agama Ibrahim </span><span>[a] </span><span>yang hatinya selalu condong </span><span>kepada </span>Allah<span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=152">152</a><span>]</span><span> dan ia bukan dari antara orang-orang musyrik.”</span></p><p><span>[b] </span><span>2</span><span>:</span><span> 112.</span><span>     </span></p><p><span>[a] </span><span>3</span><span>:</span><span> 68; 6:80; 16:124; 22:32.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6492,2,2,137,'<p><span>Katakanlah olehmu, “Kami </span><span>[b]</span><span> </span><span>beriman kepada Allah dan kepada apa yang diturunkan kepada kami dan kepada apa yang diturunkan kepada Ibrahim dan Ismail dan Ishak dan Ya</span><span>’</span><span>kub dan Keturunan</span><span>nya</span><span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=153">153</a><span>]</span><span> dan kepada yang diberikan kepada Musa dan Isa, dan kepada apa yang diberikan kepada sekalian nabi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=154">154</a><span>]</span><span> dari Tuhan mereka; </span><span>[c] </span><span>kami tidak membedakan seorang pun di antara mereka, dan hanya kepada-Nya kami menyerahkan diri</span></p><p><span>[b] </span><span>3</span><span>:</span><span> 85. </span></p><p><span>[c] </span><span>2</span><span>:</span><span> 286; 3:85; 4:153</span><span> </span></p>');
INSERT INTO `quran` VALUES (6493,2,2,138,'<p><span>Maka, </span><span>[a] </span><span>jika mereka beriman sebagaimana kamu beriman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=155">155</a><span>]</span><span> kepada </span><span>ajaran</span><span> ini, niscaya mereka mendapat petunjuk; dan jika mereka berpaling, maka sesungguhnya mereka menimbulkan perpecahan, dan tentu </span>Allah <span> akan memadai bagi engkau menghadapi mereka; dan Dia Maha Mendengar, Maha Mengetahui.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>21</span><span> </span></p>');
INSERT INTO `quran` VALUES (6494,2,2,139,'<p><span>Katakanlah, </span><span> </span><span>“Kami menganut agama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=156">156</a><span>]</span><span> </span>Allah <span>;dan siapakah yang lebih baik dari </span>Allah<span>dalam mengajarkan </span><span>agama; dan kepada-Nya kami menyembah.”</span></p>');
INSERT INTO `quran` VALUES (6495,2,2,140,'<p><span>Katakanlah, “Apakah kamu berbantah dengan kami tentang </span>Allah<span>, padahal Dia Tuhan kami dan Tuhan kamu ? Dan </span><span>[a] </span><span>bagi kami amal kami dan bagi kamu amal kamu; dan </span><span>hanya</span><span> kepada-Nya kami mengikhlaskan diri.”</span></p><p><span>[a] </span><span>28</span><span>:</span><span> 56; 42:16; 109:7.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6496,2,2,141,'<p><span>Adakah kamu berkata, “Sesungguhnya </span><span>[b] </span><span>Ibrahim dan Ismail dan Ishak dan Ya</span><span>’</span><span>kub dan Keturunannya adalah Yahudi atau Nasrani.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=157">157</a><span>]</span><span> Katakanlah, “Apakah kamu yang lebih tahu ataukah </span>Allah <span>?” dan </span><span>[c] </span><span>siapakah yang lebih aniaya daripada orang yang menyembunyikan kesaksian yang dimilikinya dari </span>Allah <span>? Dan, </span>Allah <span>tidak lengah terhadap apa yang kamu kerjakan.</span></p><p><span>[b] </span><span>3</span><span>:</span><span>85; 4:164.    </span></p><p><span>[c] </span><span>2</span><span>:</span><span> 284.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6497,2,2,142,'<p><span>[a] </span><span>Itulah umat yang telah berlalu; bagi mereka apa yang diusahakan mereka dan bagimu apa yang kamu usahakan; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=158">158</a><span>]</span><span> </span><span> dan kamu tidak akan ditanya mengenai apa-apa yang mereka kerja kan.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 135.</span></p>');
INSERT INTO `quran` VALUES (6498,2,2,143,'<p><span>R. 17</span></p><p><span>Orang-orang bodoh di antara manusia akan berkata “Apakah yang menyebabkan mereka berpaling dari kiblat mereka, yang mereka telah berada di atasnya?” Katakanlah, </span><span>[b] </span><span>”Timur dan Barat kepunyaan Allah ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=159">159</a><span>]</span><span> Dia memberi petunjuk kepada siapa yang Dia kehendaki ke jalan yang lurus.”</span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span> 116</span></p>');
INSERT INTO `quran` VALUES (6499,2,2,144,'<p><span>Dan, demikianlah </span><span>[a] </span><span>Kami menjadikan kamu satu umat yang mulia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=160">160</a><span>] [b] </span><span>supaya kamu menjadi penjaga manusia dan agar Rasul itu menjadi penjaga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=161">161</a><span>]</span><span> kamu. Dan, tidak Kami jadikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=162">162</a><span>]</span><span> kiblat yang kepadanya dahulu engkau berkiblat melainkan supaya Kami mengetahui orang yang mengikuti Rasul dari orang yang berpaling di atas kedua tumitnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=163">163</a><span>] </span><span>Dan, sesungguhnya Allah Maha Pengasih, Maha Penyayang terhadap manusia.</span></p><p><span>[a] </span><span>3</span><span>:</span><span> 111. </span></p><p><span>[b] </span><span>22</span><span>:</span><span> 79</span><span> </span></p>');
INSERT INTO `quran` VALUES (6500,2,2,145,'<p><span>Sesungguhnya, Kami </span><span>sering</span><span> melihat wajah engkau menengadah ke langit; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=164">164</a><span>]</span><span> maka niscaya akan Kami palingkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=165">165</a><span>]</span><span> engkau ke arah Kiblat yang engkau menyukainya. Maka </span><span>[a] </span><span>palingkanlah wajah engkau ke arah Masjidilharam; dan di mana saja kamu berada, hadapkanlah wajahmu ke arahnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=166">166</a><span>]</span><span> Dan sesungguhnya orang-orang yang di beri Alkitab tentu mereka mengetahui bahwa ini kebenaran dari Tuhan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=167">167</a><span>]</span><span> Dan Allah tidak lengah terhadap apa yang mereka kerjakan.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 150, 151.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6501,2,2,146,'<p><span>Dan sekalipun engkau membawa segala Tanda kepada orang-orang yang diberi Alkitab niscaya </span><span>[a ]</span><span>mereka tidak akan mengikuti Kiblat engkau dan engkau pun tidak akan menjadi pengikut Kiblat mereka; dan sebagian mereka tidak akan menjadi pengikut Kiblat sebagian yang lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=168">168</a><span>] [b] </span><span>Dan jika sesudah ilmu datang kepada engkau, engkau menuruti juga keinginan mereka, niscaya engkau akan termasuk orang-orang aniaya.</span></p><p><span>[a] </span><span>109</span><span>:</span><span> 3,7.    </span></p><p><span>[b] </span><span>6</span><span>:</span><span> 57; 13:38.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6502,2,2,147,'<p><span>[a] </span><span>Orang-orang yang telah Kami beri Alkitab, mereka mengenalnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=169">169</a><span>]</span><span> sebagaimana mereka mengenal </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=170">170</a><span>]</span><span> anak-anak mereka. Dan sesungguhnya segolongan dari mereka </span><span>[b] </span><span>menyembunyikan kebenaran, padahal mereka mengetahui.</span></p><p><span>[a] </span><span>6</span><span>:</span><span> 21.    </span></p><p><span>[b] </span><span>2</span><span>:</span><span> 175; 5:16; 6:92.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6503,2,2,148,'<p><span>[c] </span><span>Kebenaran ini dari Tuhan engkau; maka janganlah engkau termasuk orang-orang yang ragu.</span></p><p><span>[c] </span><span>3</span><span>:</span><span> 61; 6:115; 10:95</span><span> </span></p>');
INSERT INTO `quran` VALUES (6504,2,2,149,'<p><span>Dan, bagi tiap orang ada suatu tujuan yang kepadanya ia menghadapkan </span><span>perhatiannya;</span><span> maka </span><span>[d]</span><span>berlomba-lombalah dalam kebaikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=171">171</a><span>]</span><span> Di mana pun kamu berada, Allah akan mengumpulkan kamu semua. Sesungguhnya Allah berkuasa atas segala sesuatu.</span></p><p><span>[d] </span><span>3</span><span>:</span><span> 134; 5:49; 35:33; 57:22</span><span> </span></p>');
INSERT INTO `quran` VALUES (6505,2,2,150,'<p><span>Dan dari mana pun engkau keluar, </span><span>[a] </span><span>hadapkanlah perhatianmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=172">172</a><span>]</span><span> ke arah Masjidilharam, dan sesungguhnya ini adalah kebenaran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=173">173</a><span>]</span><span> dari Tuhan-mu. Dan Allah tidak lengah terhadap apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span> 145.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6506,2,2,151,'<p><span>Dan </span><span>[b] </span><span>dari mana pun engkau keluar, hadapkankanlah perhatian engkau ke arah Masjidil haram; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=174">174</a><span>]</span><span> dan dimana pun kamu sekalian berada, hadapkanlah ke arahnya, supaya orang-orang jangan mempunyai alasan terhadap kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=175">175</a><span>]</span><span> kecuali orang-orang yang aniaya di antara mereka, maka janganlah kamu </span><span>[c] </span><span>takut kepada mereka dan takutlah kepada-Ku, dan </span><span>[d] </span><span>supaya aku menyempurnakan nikmat-Ku atasmu; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=176">176</a><span>]</span><span> dan supaya kamu mendapat petunjuk.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>145, 150.    </span></p><p><span>[c] </span><span>5</span><span>:</span><span>4.    </span></p><p><span>[d] </span><span>5</span><span>:</span><span>4; 12:7</span><span> </span></p>');
INSERT INTO `quran` VALUES (6507,2,2,152,'<p><span>Sebagaimana telah </span><span>[a] </span><span>Kami utus kepadamu seorang Rasul dari antara kamu yang membacakan Ayat-ayat Kami kepadamu dan mensucikan kamu dan mengajar kamu Kitab dan hikmah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=177">177</a><span>]</span><span> dan mengajar kamu apa yang belum kamu ketahui.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span>130.</span></p>');
INSERT INTO `quran` VALUES (6508,2,2,153,'<p><span>Maka </span><span>[b] </span><span>ingatlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=178">178</a><span>]</span><span> kepada-Ku, aku pun akan ingat kepadamu; dan bersyukurlah kepada-Ku dan janganlah engkau tidak bersyukur kepada-Ku.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>204; 8:46; 62:11</span><span> </span></p>');
INSERT INTO `quran` VALUES (6509,2,2,154,'<p><span>R. 19</span></p><p><span>Hai orang-orang yang beriman, </span><span>[c] </span><span>mohonlah pertolongan dengan sabar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=179">179</a><span>]</span><span> dan shalat; sesungguhnya Allah bersama orang-orang yang sabar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=180">180</a><span>]</span></p><p><span>[c] </span><span>Lihat 2</span><span>:</span><span> 46</span><span> </span></p>');
INSERT INTO `quran` VALUES (6510,2,2,155,'<p><span>Dan, </span><span>[a] </span><span>janganlah mengatakan tentang orang-orang yang terbunuh di jalan Allah </span><span>itu</span><span> mereka mati; tidak, bahkan mereka hidup, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=181">181</a><span>]</span><span> tetapi kamu tidak akan menyadari</span></p><p><span>[a] </span><span>3</span><span>:</span><span>170</span><span> </span></p>');
INSERT INTO `quran` VALUES (6511,2,2,156,'<p><span>Dan pasti akan </span><span>[b] </span><span>K</span><span>ami menguji kamu dengan sesuatu ketakutan dan kelaparan, dan kekurangan dalam harta dan jiwa dan buah-buahan; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=182">182</a><span>]</span><span> dan berikanlah kabar suka kepada orang-orang yang sabar.</span></p><p><span>[b] </span><span>3</span><span>:</span><span>187</span><span> </span></p>');
INSERT INTO `quran` VALUES (6512,2,2,157,'<p><span>Orang-orang yang </span><span>[a] </span><span>apabila suatu musibah menimpa mereka, mereka berkata </span><span>[b] </span><span>”Sesungguhnya kami kepunyaan Allah dan sesungguhnya kepada-Nya kami akan kembali.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=183">183</a><span>]</span></p><p><span>[a] </span><span>22</span><span>:</span><span>36. </span></p><p><span>[b] </span><span>7</span><span>:</span><span>126; 26:51</span><span> </span></p>');
INSERT INTO `quran` VALUES (6513,2,2,158,'<p><span>Mereka inilah yang dilimpahi berkat-berkat dan rahmat dari Tuhan mereka dan mereka itulah yang mendapat petunjuk.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6514,2,2,159,'<p><span>Sesungguhnya, Shafa dan Marwah </span><span>[184]</span><span> adalah di antara </span><span>[c] </span><span>Tanda-tanda Allah , maka barangsiapa menunaikan haji ke Rumah itu, atau umrah, maka tiada dosa baginya jika ia tawaf di antara keduanya. Dan, barangsiapa berbuat kebaikan dengan kerelaan hati, </span><span>[185]</span><span> maka sesungguhnya Allah Maha Menghargai </span><span>amal-amal baik, </span><span>Maha Mengetahui.</span></p><p><span>[c] </span><span>22</span><span>:</span><span>33</span></p>');
INSERT INTO `quran` VALUES (6515,2,2,160,'<p><span>Sesungguhnya orang-orang yang menyembunyikan </span><span>[186]</span><span> apa yang Kami turunkan berupa Tanda-tanda kebenaran dan petunjuk sesudah Kami membuat jelas bagi manusia di dalam Alkitab, </span><span>[a] </span><span>merekalah yang dilaknat oleh Allah dan dilaknat </span><span>pula</span><span> oleh orang-orang yang melaknat.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>175</span></p>');
INSERT INTO `quran` VALUES (6516,2,2,161,'<p><span>Kecuali </span><span>[a] </span><span>mereka yang bertobat, memperbaiki diri, dan menyatakan </span><span>kebenaran </span><span>dengan tegas, maka kepada mereka itulah Aku kembali dengan ampunan dan Aku Penerima tobat, Maha Penyayang.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>90; 147; 5:40; 24:6</span><span> </span></p>');
INSERT INTO `quran` VALUES (6517,2,2,162,'<p><span>Sesungguhnya, orang-orang yang ingkar dan mati dalam keadaan mereka kafir, itulah orang- orang yang </span><span>[b] </span><span>atas mereka laknat Allah dan malaikat dan manusia semuanya.</span></p><p><span>[b] </span><span>3</span><span>:</span><span> 88</span></p>');
INSERT INTO `quran` VALUES (6518,2,2,163,'<p><span>[c] </span><span>Mereka </span><span>akan tinggal lama di dalamnya. Azab tidak akan diringankan dari mereka, dan mereka tidak akan diberi tangguh.</span><span> </span></p><p><span>[c] </span><span>3</span><span>:</span><span> 89</span><span> </span></p>');
INSERT INTO `quran` VALUES (6519,2,2,164,'<p><span>Dan, </span><span>[d] </span><span>Tuhan-mu ialah Tuhan Yang Maha Esa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=187">187</a><span>]</span><span> tiada Tuhan melainkan Dia Yang Maha Pemurah, Maha Penyayang.</span><span> </span></p><p><span>[d] </span><span>2</span><span>:</span><span> 256; 16:23; 22:35; 37:5; 59:23, 24; 112:2</span></p>');
INSERT INTO `quran` VALUES (6520,2,2,165,'<p><span>R. 20</span></p><p><span>[e] </span><span>Sesungguhnya dalam penciptaan seluruh langit dan bumi dan pertukaran malam dan siang, dan kapal-kapal yang berlayar di lautan dengan </span><span>membawa</span><span> apa yang bermanfaat bagi manusia, dan dari air yang diturunkan Allah dari langit, lalu dengan itu Dia menghidupkan bumi sesudah matinya dan Dia menyebarkan di dalamnya segala macam binatang, dan perkisaran angin dan awan yang ditugaskan di antara seluruh langit dan bumi, sesungguhnya ada Tanda-tanda bagi kaum yang mempergunakan akal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=188">188</a><span>]</span></p><p><span>[e] </span><span>3</span><span>:</span><span> 191; 10:7; 30:23; 45:6.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6521,2,2,166,'<p><span>Dan di antara manusia ada yang mengambil sekutu-sekutu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=189">189</a><span>] </span><span>selain dari Allah , mencintai mereka itu seperti mencintai Allah. Tetapi, orang yang beriman lebih kuat kecintaannya kepada Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=190">190</a><span>]</span><span> Dan sekiranya orang-orang aniaya dapat melihat ketika mereka akan menyaksikan azab, </span><span>mereka akan mengetahui</span><span> bahwa segala kekuatan itu kepunyaan Allah dan sesungguhnya azab Allah sangat keras.</span></p>');
INSERT INTO `quran` VALUES (6522,2,2,167,'<p><span>[a] </span><span>Ketika orang-orang yang diikuti akan berlepas diri dari orang-orang yang pernah mengikutinya dan mereka akan menyaksikan azab dan putuslah </span><span>[191]</span><span> segala hubungan dengan mereka.</span></p><p><span>[a] </span><span>28</span><span>:</span><span> 64, 65; 34:33, 34.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6523,2,2,168,'<p><span>Dan orang-orang yang telah mengikuti </span><span>mereka</span><span> berkata, </span><span>[a] </span><span>”Seandainya kami dapat kembali, niscaya kamu pun akan berlepas diri dari mereka sebagaimana mereka berlepas diri dari kami.” Demikianlah Allah akan memperlihatkan kepada mereka, perbuatan-perbuatan mereka menjadi </span><span>dasar</span><span> penyesalan bagi mereka, dan tidaklah mereka akan keluar dari Api</span></p><p><span>[a] </span><span>23</span><span>:</span><span> 100; 26:103</span></p>');
INSERT INTO `quran` VALUES (6524,2,2,169,'<p><span>R. 21</span></p><p><span>Hai manusia, makanlah dari apa </span><span>[b] </span><span>yang halal </span><span>dan</span><span> baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=192">192</a><span>]</span><span> di bumi; dan </span><span>[c] </span><span>janganlah mengikuti langkah-langkah syaitan; </span><span>[193]</span><span> sesungguhnya, </span><span>[d] </span><span>ia bagimu musuh yang nyata.</span></p><p><span>[b] </span><span>5</span><span>:</span><span> 89; 8:70; 16:115        </span></p><p><span>[c] </span><span>2</span><span>:</span><span> 209; 6:143; 24:22</span><span> </span></p><p><span>[d] </span><span>7</span><span>:</span><span> 23, 12:6; 28:16; 35:7; 36:61</span></p>');
INSERT INTO `quran` VALUES (6525,2,2,170,'<p><span>Sesungguhnya, ia hanya </span><span>[a] </span><span>menyuruh kamu berbuat jahat dan keji. </span><span>[194]</span><span> dan kamu mengatakan terhadap Allah apa yang tidak kamu ketahui.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 269; 24:22</span><span> </span></p>');
INSERT INTO `quran` VALUES (6526,2,2,171,'<p><span>Dan </span><span>[b] </span><span>apabila dikatakan kepada mereka, “Ikutilah apa yang diturunkan Allah;” berkata mereka “Tidak, bahkan kami hanya mengikuti yang kami dapati pada bapak-bapak kami,” </span><span>[195]</span><span> Apa ! Walaupun bapak-bapak mereka tidak mengerti suatu apa pun, dan tidak </span><span>pula</span><span> mereka mendapat petunjuk ?</span></p><p><span>[b] </span><span>5</span><span>:</span><span> 105; 10:79; 21:53, 54; 31:22.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6527,2,2,172,'<p><span>Dan perumpamaan orang-orang yang ingkar itu seperti keadaan seseorang yang berteriak kepada sesuatu yang tidak dapat mendengar selain panggilan dan seruan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=196">196</a><span>] [c] </span><span>Mereka </span><span>tuli, bisu, dan buta, karena mereka itu tidak dapat menggunakan akal.</span></p><p><span>[c] </span><span>Lihat 2</span><span>:</span><span> 19</span><span> </span></p>');
INSERT INTO `quran` VALUES (6528,2,2,173,'<p><span>[a] </span><span>Hai orang-orang yang beriman, makanlah dari antara barang-barang baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=197">197</a><span>]</span><span> yang Kami rezekikan kepadamu, dan bersyukurlah kepada Allah, jika benar-benar hanya kepada-Nya kamu menyembah.</span></p><p><span>[a] </span><span>5</span><span>:</span><span> 6; 16:115; 23:52; 40:65</span><span> </span></p>');
INSERT INTO `quran` VALUES (6529,2,2,174,'<p><span>[b] </span><span>Sesungguhnya, yang diharamkan bagimu hanya bangkai, darah, dan daging babi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=198">198</a><span>]</span><span> dan apa yang disembelih dengan </span><span>menyebut</span><span> selain Allah. Tetapi, barangsiapa terpaksa, bukan melanggar peraturan dan tidak melampaui batas, maka tiada dosa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=199">199</a><span>]</span><span> atasnya. Sesungguhnya, Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[b] </span><span>5</span><span>:</span><span>4; 6:146; 16:116</span><span> </span></p>');
INSERT INTO `quran` VALUES (6530,2,2,175,'<p><span>[a] </span><span>Sesungguhnya, mereka yang menyembunyikan apa yang diturunkan Allah dalam Alkitab dan </span><span>[b] </span><span>menukarnya dengan harga sedikit, mereka tidak memakan dalam perut mereka kecuali api. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=200">200</a><span>] [c] </span><span>Dan Allah tidak akan berbicara dengan mereka pada Hari Kiamat dan tidak akan mensucikan mereka. Dan bagi mereka siksaan yang pedih.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span> 147.    </span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span> 42.     </span></p><p><span>[c] </span><span>2</span><span>:</span><span> 160</span><span> </span></p>');
INSERT INTO `quran` VALUES (6531,2,2,176,'<p><span>Mereka itulah </span><span>[a] </span><span>yang telah menukar kesesatan dengan petunjuk, dan azab dengan ampunan. Maka, alangkah sabarnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=201">201</a><span>]</span><span> mereka terhadap Api.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>17; 3:178; 4:45</span><span> </span></p>');
INSERT INTO `quran` VALUES (6532,2,2,177,'<p><span>Azab</span><span> itu adalah karena </span><span>[a] </span><span>Allah telah menurunkan Kitab dengan hak; dan sesungguhnya orang-orang yang berselisih mengenai Kitab itu </span><span>bertindak</span><span> terlalu jauh dalam permusuhan</span></p><p><span>[b] </span><span>17</span><span>:</span><span>106</span></p>');
INSERT INTO `quran` VALUES (6533,2,2,178,'<p><span>R. 22</span></p><p><span>[a] </span><span>Bukanlah kebaikan bahwa kamu menghadapkan wajahmu ke arah Timur dan Barat, tetapi yang </span><span>sebenarnya</span><span> kebaikan ialah yang beriman kepada Allah dan Hari Kemudian dan malaikat-malaikat dan Kitab dan nabi-nabi, dan </span><span>[b] </span><span>memberikan harta atas kecintaan kepada-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=202">202</a><span>]</span><span> kepada kaum kerabat, dan anak-anak yatim, dan orang-orang miskin, dan orang musafir, dan mereka yang meminta </span><span>sedekah </span><span>dan untuk </span><span>memerdekakan </span><span>hamba sahaya; dan orang-orang yang mendirikan shalat dan membayar zakat; dan </span><span>[c] </span><span>orang-orang yang menepati janji mereka bila mereka berjanji, dan mereka yang sabar dalam </span><span>[d] </span><span>kesusahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=202a">202a</a><span>]</span><span> dan kesengsaraan, dan </span><span>tabah</span><span> dalam masa perang; </span><span>[e] </span><span>merekalah orang-orang yang benar dan merekalah orang-orang yang bertakwa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=203">203</a><span>]</span><span> </span></p><p><span>[a] </span><span>2</span><span>:</span><span>190.    </span></p><p><span>[b] </span><span>76</span><span>:</span><span>9.     </span></p><p><span>[c] </span><span>9</span><span>:</span><span>4; 13:21.     </span></p><p><span>[d] </span><span>2</span><span>:</span><span>215; 6:43; 7:95. </span></p><p><span>[e] </span><span>49</span><span>:</span><span>16</span></p>');
INSERT INTO `quran` VALUES (6534,2,2,179,'<p><span>Hai orang-orang yang beriman, </span><span>[a] </span><span>diwajibkan atasmu pembalasan </span><span>yang setimpal</span><span> mengenai orang-orang yang dibunuh; orang merdeka dengan orang merdeka; dan hamba sahaya dengan hamba sahaya, dan perempuan dengan perempuan. Tetapi, barangsiapa mendapat sesuatu ampunan dari pihak saudaranya </span><span>yang dibunuh</span><span> maka hendaklah </span><span>ahli waris yang dibunuh</span><span> menuntut </span><span>uang darah</span><span> dengan cara yang layak, dan pembayaran </span><span>oleh si pembunuh </span><span>hendaklah dilakukan kepadanya dengan cara sebaik-baiknya. Yang demikian itu adalah satu keringanan dan rahmat dari Tuhan-mu. Dan barangsiapa melampaui batas sesudah ini maka baginya siksaan yang pedih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=204">204</a><span>]</span></p><p><span>[a] </span><span>2</span><span>:</span><span>195; 5:46</span><span> </span></p>');
INSERT INTO `quran` VALUES (6535,2,2,180,'<p><span>Dan dalam </span><span>hukum </span><span>pembalasan ini adalah kehidupan bagimu, hai orang-orang yang berakal supaya kamu terpelihara. </span><span>[204a]</span><span> </span></p>');
INSERT INTO `quran` VALUES (6536,2,2,181,'<p><span>[a] </span><span>Diwajibkan atasmu, apabila maut menjelang seseorang di antaramu, jika ia meninggalkan harta, </span><span>hendaklah ia </span><span>berwasiat untuk ibu-bapak dan kaum kerabat dengan cara yang wajar. </span><span>[205]</span><span> </span><span>Inilah</span><span> kewajiban bagi orang-orang yang bertakwa.</span></p><p><span>[a] </span><span>4</span><span>:</span><span> 12, 13, 177; 5:107.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6537,2,2,182,'<p><span>Tetapi barangsiapa mengubahnya sesudah didengarnya, maka sesungguhnya dosanya hanya atas mereka yang mengubahnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=205a">205a</a><span>]</span><span> Sesungguhnya Allah Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (6538,2,2,183,'<p><span>Tetapi barang siapa mengkhawatirkan orang yang berwasiat akan berat-sebelah atau berbuat dosa, lalu mengadakan perdamaian di antara mereka, maka tak ada dosa atasnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=205b">205b</a><span>]</span><span> Sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (6539,2,2,184,'<p><span>Hai orang-orang yang beriman, puasa diwajibkan atasmu sebagaimana telah diwajibkan atas orang-orang sebelummu, </span><span>[206]</span><span> supaya kamu terpelihara </span><span>dari keburukan rohani dan jasmani.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6540,2,2,185,'<p><span>Hari-hari yang telah </span><span>[a] </span><span>ditentukan bilangnya, maka barangsiapa di antara kamu sakit atau dalam perjalanan, maka </span><span>hendaknya berpuasa </span><span>sebanyak itu pada hari-hari lain; dan bagi mereka yang tidak sanggup </span><span>berpuasa</span><span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=207">207</a><span>]</span><span> membayar fidyah, memberi makan kepada seorang miskin. Dan, barangsiapa berbuat kebaikan dengan rela hati maka hal itu lebih baik baginya. Dan,berpuasa itu lebih baik bagimu jika kamu mengetahui</span><span>.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 204</span><span> </span></p>');
INSERT INTO `quran` VALUES (6541,2,2,186,'<p><span>Bulan Ramadhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=207a">207a</a><span>]</span><span> ialah </span><span>bulan</span><span> yang di dalamnya Al-quran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=207b">207b</a><span>]</span><span> diturunkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=208">208</a><span>]</span><span> sebagai petunjuk bagi manusia dan keterangan-keterangan yang nyata mengenai petunjuk dan </span><span>[a] </span><span>furqan. Maka, barangsiapa sakit atau dalam perjalanan, maka </span><span>hendaklah berpuasa </span><span>sebanyak bilangan itu pada hari-hari lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=209">209</a><span>]</span><span> </span><span>[b] </span>Allah <span>menghendaki keringanan bagimu dan tidak menghendaki kesukaran bagimu, dan </span><span>Dia menghendaki</span><span> supaya kamu menyempurnakan bilangan itu dan supaya </span><span>[c] </span><span>kamu mengagung kan </span>Allah<span>, karena Dia memberi petunjuk kepadamu dan supaya kamu bersyukur.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 54; 3:4; 8:42; 21:49; 25:2.    </span></p><p><span>[b] </span><span>2</span><span>:</span><span> 287; 5:7; 22:79. </span></p><p><span>[c] </span><span>22</span><span>:</span><span> 38</span></p>');
INSERT INTO `quran` VALUES (6542,2,2,187,'<p><span>Dan, apabila hamba-hamba-Ku bertanya kepada engkau tentang Aku, </span><span>katakanlah</span><span> </span><span>[a]</span><span>”Sesungguh nya Aku dekat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=210">210</a><span>]</span><span> </span><span>[b] </span><span>Aku mengabulkan doa orang yang memohon apabila ia berdoa kepada-Ku. Maka, hendaklah mereka menyambut seruan-Ku dan beriman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=211">211</a><span>]</span><span> kepada-Ku supaya mereka mendapat petunjuk.</span></p><p><span>[a] </span><span>11</span><span>:</span><span> 62; 34:51; 50:17.    </span></p><p><span>[b] </span><span>27</span><span>:</span><span> 63</span><span> </span></p>');
INSERT INTO `quran` VALUES (6543,2,2,188,'<p><span>Dihalalkan bagimu pada malam hari puasa bercampur dengan istri-istrimu; mereka </span><span>adalah</span><span> pakaian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=212">212</a><span>]</span><span> bagimu, dan kamu </span><span>adalah</span><span> pakaian bagi mereka. Allah mengetahui sesungguhnya kamu </span><span>telah</span><span> mengkhianati dirimu sendiri, maka Dia kembali </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=213">213</a><span>]</span><span> kepadamu dengan kasih sayang dan Dia memperbaiki kesalahanmu. Maka, sekarang campurilah mereka dan carilah apa yang ditentukan Allah bagimu; dan makanlah dan minumlah hingga tampak jelas kepadamu benang putih dari benang hitam fajar. Kemudian, sempurnakanlah puasa sampai malam hari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=214">214</a><span>]</span><span> dan janganlah kamu mencampuri mereka ketika kamu ber i</span><span>’</span><span>tikaf dalam </span><span>masjid-masjid</span><span>. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=215">215</a><span>]</span><span> Inilah batas-batas </span><span>ketentuan</span><span> Allah maka janganlah kamu mendekatinya. Demikianlah Allah menjelaskan hukum-hukum-Nya bagi manusia supaya mereka terpelihara </span><span>dari keburukan rohani dan jasmani.</span><span> </span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6544,2,2,189,'<p><span>Dan, </span><span>[a] </span><span>janganlah makan hartamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=215a">215a</a><span>] </span><span>di antara kamu dengan jalan batil, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=216">216</a><span>]</span><span> dan </span><span>jangan pula</span><span> kamu serahkan harta itu </span><span>sebagai suapan</span><span> kepada para penguasa supaya kamu dapat memakan sebagian harta dengan cara berbuat dosa, padahal kamu mengetahui.</span></p><p><span>[a] </span><span>4</span><span>:</span><span>30, 162; 9:34.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6545,2,2,190,'<p><span>R. 24</span></p><p><span>Mereka bertanya kepada engkau mengenai bulan sabit. Katakanlah, </span><span>[b] </span><span>”Itu adalah sarana penentuan waktu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=217">217</a><span>]</span><span> bagi manusia dan untuk ibadah haji.” Dan bukanlah kebaikan kalau kamu memasuki rumah-rumah dari belakangnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=218">218</a><span>]</span><span> akan tetapi </span><span>[c] </span><span>kebaikan ialah orang yang bertakwa. Dan masukilah rumah-rumah dari pintu-pintunya dan bertakwalah kepada Allah supaya kamu berjaya.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>198; 9:36.    </span></p><p><span>[c] </span><span>2</span><span>:</span><span>178.</span><span> </span><span> </span></p>');
INSERT INTO `quran` VALUES (6546,2,2,191,'<p><span>Dan </span><span>[a] </span><span>perangilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=219">219</a><span>]</span><span> di jalan Allah, orang-orang yang memerangimu, namun jangan kamu melampaui batas, Sesungguhnya Allah tidak mencintai orang-orang yang melampaui batas.</span></p><p><span>[a] </span><span>4</span><span>:</span><span> 76; 8:40; 9:13; 22:40; 60:9, 10.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6547,2,2,192,'<p><span>Dan bunuhlah mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=220">220</a><span>]</span><span> di mana pun mereka kamu dapati, dan usirlah mereka dari tempat mereka telah mengusirmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=221">221</a><span>]</span><span> dan </span><span>[a] </span><span>fitnah itu lebih buruk daripada pembunuhan. Dan, janganlah kamu memerangi mereka di dekat Masjidilharam sebelum mereka memerangimu disana. Tetapi, jika mereka memerangimu, maka bunuhlah mereka. Demikianlah balasan bagi orang-orang kafir.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 218</span><span> </span></p>');
INSERT INTO `quran` VALUES (6548,2,2,193,'<p><span>Tetapi </span><span>[b] </span><span>jika mereka berhenti, maka sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a] </span><span>8</span><span>:</span><span>40.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6549,2,2,194,'<p><span>Dan </span><span>[a] </span><span>perangilah mereka sehingga tak ada fitnah lagi, dan agama itu </span><span>hanya</span><span> untuk Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=222">222</a><span>]</span><span> Tetapi, jika mereka berhenti, maka tidak </span><span>ada</span><span> permusuhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=223">223</a><span>]</span><span> kecuali terhadap orang-orang aniaya.</span></p><p><span>[a] </span><span>8</span><span>:</span><span>40</span></p>');
INSERT INTO `quran` VALUES (6550,2,2,195,'<p><span>Bulan Suci </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=224">224</a><span>]</span><span> </span><span>dibalas</span><span> dengan Bulan Suci, dan untuk segala barang suci itu ada </span><span>hukum</span><span> pembalasan,</span><span>[a]</span><span> Maka, barangsiapa menyerang kamu, seranglah dia sepadan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=225">225</a><span>]</span><span> dengan serangannya kepadamu; dan bertakwalah kepada Allah, dan ketahuilah bahwa Allah s.w.t. beserta orang-orang bertakwa.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span> 179.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6551,2,2,196,'<p><span>Dan, </span><span>[a] </span><span>belanjakanlah </span><span>harta</span><span> pada jalan Allah, dan janganlah kamu menjerumuskan </span><span>dirimu</span><span> dengan tanganmu ke dalam kebinasaan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=226">226</a><span>]</span><span> dan berbuat baiklah, sesungguhnya Allah mencintai orang- orang yang berbuat kebaikan.</span></p><p><span>[b] </span><span>2</span><span>:</span><span> 225; 14:32; 47:39; 57:11; 63:11</span><span> </span></p>');
INSERT INTO `quran` VALUES (6552,2,2,197,'<p><span>Dan sempurnakanlah ibadah haji </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=227">227</a><span>]</span><span> dan umrah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=228">228</a><span>]</span><span> karena Allah ; tetapi </span><span>[a] </span><span>jika kamu terhalang,</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=229">229</a><span>]</span><span> maka </span><span>sembelilah hewan</span><span> kurban yang mudah di dapat; dan janganlah mencukur kepalamu sebelum hewan kurban sampai ke tempat penyelembelihannya. Dan, barangsiapa di antaramu sakit atau ada gangguan sakit di kepala, maka ia harus membayar fidyah dengan puasa, atau sedekah atau kurban. Maka, apabila kamu telah aman, kemudian siapa yang ingin mengambil faedah mengerjakan umrah bersama-sama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=230">230</a><span>]</span><span> dengan ibadah haji, hendaklah ia berkurban yang mudah didapat. Dan barangsiapa tidak mendapatkannya, hendaklah ia berpuasa tiga hari di </span><span>musim</span><span> haji, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=231">231</a><span>]</span><span> dan tujuh hari setelah kamu kembali. Inilah sepuluh hari yang sempurna. Yang demikian itu bagi orang yang keluarganya tidak tinggal dekat Masjidilharam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=232">232</a><span>]</span><span> Dan, bertakwalah kepada Allah dan ketahuilah bahwa Allah keras dalam menghukum.</span></p><p><span>[a] </span><span>48</span><span>:</span><span>26</span></p>');
INSERT INTO `quran` VALUES (6553,2,2,198,'<p><span>R. 25</span></p><p><span>[a] </span><span>Ibadah haji dilakukan dalam bulan-bulan yang dikenal; maka </span><span>[a] </span><span>barangsiapa telah bertekad akan menunaikan ibadah haji dalam </span><span>bulan-bulan</span><span> itu, maka janganlah membicarakan hal yang tidak senonoh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=233">233</a><span>]</span><span> jangan melanggar peraturan dan jangan bertengkar pada </span><span>musim</span><span> haji. Dan kebaikan apa pun yang kamu kerjakan, tentu Allah mengetahuinya, Dan sediakanlah perbekalan sesungguhnya sebaik-baik perbekalan ialah takwa; dan bertakwalah kepada</span><span>–</span><span>Ku, hai orang-orang yang berakal.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>190; 9:36.    </span></p><p><span>[b] </span><span>3</span><span>:</span><span>98; 22:28</span><span> </span></p>');
INSERT INTO `quran` VALUES (6554,2,2,199,'<p><span>Tiada dosa atasmu </span><span>[c] </span><span>mencari karunia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=234">234</a><span>]</span><span> dari Tuhan-mu. Tetapi, apabila kamu bertolak dari Arafah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=235">235</a><span>]</span><span> berzikirlah kepada Allah dekat Al Masy</span><span>’</span><span>arulharam; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=236">236</a><span>]</span><span> dan </span><span>[a] </span><span>berzikirlah kepada-Nya sebagaimana Dia memberi petunjuk kepadamu; meskipun sebelumnya kamu sesungguhnya termasuk orang-orang sesat.</span></p><p><span>[c] </span><span>62</span><span>:</span><span>11.    </span></p><p><span>[a] </span><span>2</span><span>:</span><span>153, 204; 8:46; 62:11</span><span> </span></p>');
INSERT INTO `quran` VALUES (6555,2,2,200,'<p><span>Kemudian, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=237">237</a><span>] </span><span>bertolaklah kamu dari tempat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=238">238</a><span>]</span><span> bertolaknya orang-orang dan mohonlah ampunan kepada Allah. Sesungguhnya, Allah Maha Pengampun, Maha Penyayang.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6556,2,2,201,'<p><span>Maka setelah kamu menunaikan </span><span>[a] </span><span>cara-cara ibadah </span><span>hajimu</span><span>, </span><span>[b] </span><span>berzikirlah kepada Allah sebagaimana kamu mengingat bapak-bapakmu atau berzikirlah lebih banyak lagi. Dan </span><span>[a] </span><span>di antara manusia ada yang berkata, “Ya Tuhan kami, anugerahilah kami </span><span>kesenangan</span><span> di dunia ini dan tiada bagi orang itu </span><span>mendapati</span><span> bagian di akhirat.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>129.    </span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span> 153.    </span></p><p><span>[c] </span><span>4</span><span>:</span><span>135; 42:21.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6557,2,2,202,'<p><span>Dan </span><span>[d] </span><span>di antara mereka ada yang mengatakan, “Ya Tuhan kami, berilah kami segala yang baik di dunia dan segala yang baik di akhirat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=239">239</a><span>]</span><span> dan hindarkanlah kami dari azab Api.”</span></p><p><span>[d] </span><span>42</span><span>:</span><span>21</span><span> </span></p>');
INSERT INTO `quran` VALUES (6558,2,2,203,'<p><span>Mereka inilah yang akan memperoleh bagian </span><span>sebagai pahala</span><span> dari apa yang mereka usahakan. Dan Allah Mahacepat dalam menghisab.</span></p>');
INSERT INTO `quran` VALUES (6559,2,2,204,'<p><span>Dan, </span><span>[a] </span><span>berzikirlah kepada Allah dalam hari-hari yang ditentukan bilangannya; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=240">240</a><span>]</span><span> maka barangsiapa segera pulang dalam dua hari, maka tak ada dosa baginya; dan barangsiapa terlambat, maka tidak </span><span>pula</span><span> ada dosa baginya. </span><span>Petunjuk</span><span> ini bagi orang yang bertakwa. Dan bertakwalah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=241">241</a><span>]</span><span> kamu kepada Allah, dan ketahuilah bahwa kamu semua akan dikumpulkan kepada-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=242">242</a><span>]</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span> 153</span><span> </span></p>');
INSERT INTO `quran` VALUES (6560,2,2,205,'<p><span>Dan, </span><span>[a] </span><span>di antara manusia ada orang yang ucapannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=243">243</a><span>]</span><span> mengagumkan engkau mengenai kehidupan dunia dan ia menjadikan Allah sebagai saksi atas apa yang ada dalam hati nya; padahal ia petengkar dan sekeras-keras-nya.</span></p><p><span>[a] </span><span>63</span><span>:</span><span>5</span><span> </span></p>');
INSERT INTO `quran` VALUES (6561,2,2,206,'<p><span>Dan apabila ia berkuasa, berkeliaranlah ia di muka bumi untuk membuat kekacauan di dalamnya dan membinasakan sawah-ladang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=244">244</a><span>]</span><span> dan keturunan, dan Allah tidak menyukai kekacauan.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6562,2,2,207,'<p><span>Dan apabila dikatakan kepadanya, “Takutlah kepada Allah, ” rasa sombong mendorongnya untuk </span><span>berbuat</span><span> dosa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=245">245</a><span>]</span><span> maka cukuplah baginya Jahannam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=246">246</a><span>]</span><span> Dan sungguh buruk tempat kediaman itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=247">247</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6563,2,2,208,'<p><span>Dan diantara manusia ada yang menjual </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=248">248</a><span>]</span><span> dirinya untuk mencari keridhaan Allah; dan </span><span>[a] </span><span>Allah Maha Penyantun terhadap hamba-hamba.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>31; 9:117; 57:10.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6564,2,2,209,'<p><span>Hai orang-orang yang beriman, masuklah kamu sekalian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=249">249</a><span>]</span><span> ke dalam kepatuhan seutuh nya dan </span><span>[b] </span><span>janganlah mengikuti langkah-langkah syaitan; sesungguhnya bagimu, ia musuh yang nyata.</span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span>169</span><span> </span></p>');
INSERT INTO `quran` VALUES (6565,2,2,210,'<p><span>Tetapi, jika kamu tergelincir sesudah datang kepadamu Tanda-tanda nyata, maka ketahuilah bahwa Allah Maha Perkasa, Maha Bijaksana</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6566,2,2,211,'<p><span>[a] </span><span>Hal apakah yang mereka tunggu kecuali agar Allah datang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=250">250</a><span>]</span><span> kepada mereka dalam naungan dari awan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=251">251</a><span>]</span><span> bersama malaikat-malaikat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=252">252</a><span>]</span><span> dan agar diputuskan perkara itu ? Dan kepada Allah dikembalikan segala perkara.</span></p><p><span>[a] </span><span>6</span><span>:</span><span>159; 16:34; 89:23.</span></p>');
INSERT INTO `quran` VALUES (6567,2,2,212,'<p><span>R. 26</span></p><p><span>Tanyakanlah kepada Bani Israil </span><span>[a] </span><span>berapa banyak Tanda-tanda nyata yang telah Kami berikan kepada mereka. Dan barangsiapa mengubah nikmah Allah setelah datang kepadanya, maka sesungguhnya Allah sangat keras dalam menghukum. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=253">253</a><span>]</span></p><p><span>[a] </span><span>17</span><span>:</span><span>102; 28 ; 37</span><span> </span></p>');
INSERT INTO `quran` VALUES (6568,2,2,213,'<p><span>Ditampakkan indah bagi orang-orang ingkar </span><span>[b] </span><span>kehidupan dunia ini dan mereka mencemoohkan orang-orang yang beriman, dan orang-orang bertakwa berada di atas mereka pada Hari Kiamat. Dan </span><span>[c] </span><span>Allah memberi rezeki orang-orang yang Dia kehendaki tanpa perhitungan.</span></p><p><span>[b] </span><span>3</span><span>:</span><span>15; 18:47; 57:21.    </span></p><p><span>[c] </span><span>3</span><span>:</span><span>38; 24:39; 35:4; 40:41.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6569,2,2,214,'<p><span>Tadinya manusia merupakan satu umat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=254">254</a><span>]</span><span> lalu Allah mengutus nabi-nabi sebagai </span><span>[a] </span><span>pembawa kabar suka dan pemberi ingat; dan Dia menurunkan beserta Alkitab dengan benar supaya Dia menghakimi di antara manusia dalam hal-hal yang diperselisihkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=255">255</a><span>]</span><span> hal itu kecuali orang-orang yang diberi Alkitab itu sesudah Tanda-tanda yang nyata datang kepada mereka, karena kedengkian di antara mereka. Maka, Allah dengan perintah-Nya telah menunjuki orang-orang yang beriman kepada kebenaran yamg diperselisihkan oleh mereka itu, Dan Allah memberi petunjuk kepada siapa yang Dia kehendaki ke jalan yang lurus.</span></p><p><span>[a] </span><span>4</span><span>:</span><span>166; 6:49; 18:57</span><span> </span></p>');
INSERT INTO `quran` VALUES (6570,2,2,215,'<p><span>[a] </span><span>Apakah kamu menyangka bahwa kamu akan masuk surga padahal belum datang kepadamu seperti keadaan orang-orang yang dahulu sebelum kamu? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=256">256</a><span>]</span><span> </span><span>[b] </span><span>Kesusahan dan kesengsaraan menimpa mereka dan mereka digoncang dengan hebatnya </span><span>[c] </span><span>sehingga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=256a">256a</a><span>]</span><span> rasul itu dan orang-orang yang beriman besertanya berkata, “Kapankah pertolongan Allah ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=257">257</a><span>]</span><span> Ketahuilah, Sesungguhnya pertolongan Allah itu dekat.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>143; 9:16.    </span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span>178.    </span></p><p><span>[c] </span><span>12</span><span>:</span><span>111</span><span> </span></p>');
INSERT INTO `quran` VALUES (6571,2,2,216,'<p><span>mereka bertanya kepada engkau, apa yang harus mereka belanjakan. Katakanlah, </span><span>[a] </span><span>”Apa saja yang kamu belanjakan dari harta yang baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=258">258</a><span>]</span><span> hendaklah untuk ibu-bapak, kaum kerabat, anak yatim, orang-orang miskin, dan orang-orang musafir. Dan kebaikan apa saja yang kamu perbuat, maka sesungguhnya Allah Maha Mengetahui tentang itu.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>178; 4:37.</span></p>');
INSERT INTO `quran` VALUES (6572,2,2,217,'<p><span>Diwajibkan atasmu </span><span>[b] </span><span>berperang, dan itu sesuatu yang kamu tidak sukai, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=259">259</a><span>]</span><span> dan boleh jadi kamu tidak menyukai sesuatu, padahal hal itu baik bagimu, dan boleh jadi kamu menyukai sesuatu, padahal hal itu sangat buruk bagimu. Dan Allah mengetahui dan kamu tidak mengatahui.</span></p><p><span>[b] </span><span>8</span><span>:</span><span>6.</span></p>');
INSERT INTO `quran` VALUES (6573,2,2,218,'<p><span>Mereka bertanya kepada engkau tentang berperang di dalam Bulan Suci. Katakanlah, “Berperang di dalam </span><span>bulan ini </span><span>adalah </span><span>dosa</span><span> besar” tetapi menghalangi orang dari jalan Allah dan ingkar kepada-Nya dan </span><span>kepada</span><span> Masjidilharam dan mengusir penghuninya dari </span><span> tempat</span><span> itu adalah </span><span>dosa</span><span> yang lebih besar lagi di sisi Allah; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=260">260</a><span>]</span><span> dan </span><span>[a] </span><span>fitnah itu lebih besar </span><span>dosanya</span><span> daripada pembunuhan. Dan mereka tidak akan berhenti memerangi kamu hingga mereka memalingkan kamu dari agamamu jika mereka sanggup. Dan, </span><span>[b] </span><span>barangsiapa di antaramu berpaling dari agamanya dan ia mati sedang ia masih dalam keadaan kufur, maka mereka itulah yang </span><span>[c] </span><span>amalannya akan menjadi sia-sia di dunia dan akhirat, Mereka itulah penghuni api dan mereka akan tinggal lama di dalamnya.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>192.    </span></p><p><span>[b] </span><span>3</span><span>:</span><span> 87, 91; 4:138; 5:55; 47:26.    </span></p><p><span>[c] </span><span>3</span><span>:</span><span> 23; 7:148; 18:106.</span></p>');
INSERT INTO `quran` VALUES (6574,2,2,219,'<p><span>Sesungguhnya orang-orang yang beriman dan </span><span>[a] </span><span>orang-orang yang berhijrah dan berjihad di jalan Allah , mereka itulah yang mengharapkan rahmat Allah, Dan Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a] </span><span>8</span><span>:</span><span>75; 9:20.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6575,2,2,220,'<p><span>Mereka bertanya kepada engkau tentang </span><span>[b] </span><span>arak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=261">261</a><span>]</span><span> dan judi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=262">262</a><span>]</span><span> Katakanlah, “Di dalam keduanya ada dosa </span><span>dan kerugian</span><span> besar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=263">263</a><span>]</span><span> dan </span><span>juga </span><span>manfaat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=264">264</a><span>]</span><span> bagi manusia, dan dosanya </span><span>serta kerugiannya </span><span>lebih besar daripada manfaatnya.” Dan, mereka bertanya kepada engkau tentang apa yang harus mereka belanjakan. Katakanlah, “Apa yang tidak mendatangkan kesusahan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=265">265</a><span>]</span><span> Demikian Allah menjelaskan hukum-hukum-</span><span>Nya</span><span> bagimu supaya kamu berpikir.</span></p><p><span>[b] </span><span>5</span><span>:</span><span>91, 92</span><span> </span></p>');
INSERT INTO `quran` VALUES (6576,2,2,221,'<p><span>Tentang dunia ini dan akhirat. Dan mereka bertanya kepada engkau mengenai </span><span>[a] </span><span>anak yatim. Katakanlah, “Memperbaiki mereka adalah sangat baik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=266">266</a><span>]</span><span> dan jika kamu bergaul dengan mereka, maka mereka itu saudara-saudaramu. Dan Allah mengatahui yang berbuat perbaikan. Dan sekiranya Allah menghendaki niscaya Dia akan menyusahkan kamu, Sesungguhnya Allah itu Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a] </span><span>4</span><span>:</span><span>128; 89:18; 93:10; 107:3.</span></p>');
INSERT INTO `quran` VALUES (6577,2,2,222,'<p><span>Dan </span><span>[a] </span><span>janganlah kamu menikahi perempuan-perempuan musyrik sebelum mereka beriman; dan sebenarnya hamba-sahaya perempuan yang beriman itu lebih baik daripada perempuan musyrik meskipun ia menawan hatimu. Dan, janganlah kamu menikahkan </span><span>perempuan beriman</span><span> dengan laki-laki musyrik sebelum mereka beriman, dan sebenarnya hamba-sahaya laki-laki yang beriman lebih baik daripada laki-laki musyrik, meskipun ia menawan hatimu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=267">267</a><span>]</span><span> Mereka mengajak ke Api, dan Allah mengajak ke sorga dan ampunan dengan izin-Nya. Dan, Dia menjelaskan Tanda-tanda-Nya kepada manusia supaya mereka dapat meraih nasihat.</span></p><p><span>[a] </span><span>60</span><span>:</span><span>11</span><span> </span></p>');
INSERT INTO `quran` VALUES (6578,2,2,223,'<p><span>Dan mereka itu bertanya kepada engkau tentang haid. Katakanlah, “Itu adalah hal </span><span>yang</span><span> memudaratkan, maka jauhilah perempuan-perempuan di waktu haid, dan janganlah kamu menghampiri mereka sebelum mereka suci. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=268">268</a><span>]</span><span> Dan apabila mereka telah mensucikan diri, maka datangilah mereka sebagaimana Allah telah memerintahkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=269">269</a><span>]</span><span> kepadamu. Sesungguhnya Allah mencintai mereka yang banyak bertobat, dan Dia mencintai orang-orang yang menjaga kebersihan dirinya.</span></p>');
INSERT INTO `quran` VALUES (6579,2,2,224,'<p><span>Istri-istrimu </span><span>bagaikan</span><span> sawah-ladang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=270">270</a><span>]</span><span> bagimu, maka datangilah sawah-ladangmu bilamana </span><a>[271</a><span>]</span><span> kamu sukai; dan kirimkanlah lebih dahulu </span><span>kebaikan</span><span> untuk dirimu dan bertakwahlah kepada Allah,dan ketahuilah bahwa kamu akan bertemu dengan-Nya; dan berilah kabar suka kepada orang-orang mukmin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=272">272</a><span>]</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6580,2,2,225,'<p><span>Dan janganlah kamu menjadikan Allah sasaran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=273">273</a><span>]</span><span> bagi sumpah-sumpahmu untuk berbuat kebaikan dan bertakwa, dan memperbaiki di antara manusia. Dan Allah Maha Mendengar, Maha Mengetahui.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6581,2,2,226,'<p><span>[a] </span><span>Allah tidak akan menuntut kamu atas sumpah-sumpahmu yang sia-sia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=274">274</a><span>]</span><span> akan tetapi Dia akan menuntut kamu atas apa yang </span><span>sengaja</span><span> diusahakan oleh hatimu, Dan AllahMaha Pengampun, Maha Penyantun.</span></p><p><span>[a] </span><span>5</span><span>:</span><span>90</span><span> </span></p>');
INSERT INTO `quran` VALUES (6582,2,2,227,'<p><span>Bagi mereka yang bersumpah </span><span>memisahkan diri</span><span> dari istri-istri mereka, </span><span>hendaknya</span><span> menunggu empat bulan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=275">275</a><span>]</span><span> kemudian jika mereka kembali </span><span>untuk berdamai,</span><span> maka sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6583,2,2,228,'<p><span>Dan jika mereka telah bertekad untuk </span><span>[a] </span><span>talak, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=276">276</a><span>]</span><span> maka sesungguh Allah Maha Mendengar, Maha Mengatahui.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>230; 33:50; 65:2.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6584,2,2,229,'<p><span>Dan </span><span>[b] </span><span>perempuan-perempuan yang ditalak harus menahan diri mereka tidak kali masa haid, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=277">277</a><span>]</span><span> dan tidak boleh bagi mereka menyembunyikan apa yang telah diciptakan Allah dalam kandungan mereka, jika mereka beriman kepada Allah dan Hari Kemudian, Dan suami-suami mereka lebih berhak kembali rujuk dalam masa itu, jika mereka menghendaki perbaikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=278">278</a><span>] </span><span>Dan perempuan-perempuan mempunyai hak yang sama dengan kewajibannya menurut cara yang ma</span><span>’</span><span>ruf; tetapi </span><span>[c] </span><span>laki-laki mempunyai satu derajat </span><span>lebih </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=279">279</a><span>]</span><span> atas mereka. Dan Allah Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>235; 65:5.    </span></p><p><span>[c] </span><span>4</span><span>:</span><span>35</span></p>');
INSERT INTO `quran` VALUES (6585,2,2,230,'<p><span>[a] </span><span>Talak itu dua kali; kemudian </span><span>[a] </span><span>boleh menahan </span><span>perempuan-perempuan</span><span> itu dengan ma</span><span>’</span><span>ruf atau lepaskan </span><span>mereka</span><span> dengan baik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=280">280</a><span>]</span><span> Dan tidak dihalalkan bagimu mengambil kembali sesuatu dari apa yang telah kamu berikan kepada mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=281">281</a><span>]</span><span> kecuali kalau ke dua mereka itu khawatir tidak akan dapat menegakkan pereturan</span><span>–</span><span>peraturan Allah, maka tidak ada dosa atas keduanya di dalam apa yang diberikan oleh perempuan itu untuk penebus dirinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=282">282</a><span>]</span><span> Demikianlah peraturan-peraturan Allah, maka janganlah kamu melanggar-Nya dan barangsiapa melanggar peraturan-peraturan Allah, maka mereka itulah orang-orang aniaya.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span> 228.    </span></p><p><span>[b] </span><span>2</span><span>:</span><span>232; 4:130; 65:3.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6586,2,2,231,'<p><span>Dan jika ia menjatuhkan talak kepadanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=283">283</a><span>]</span><span> </span><span>ketiga kalinya,</span><span> maka sesudah itu tidak halal baginya </span><span>perempuan</span><span> itu hingga ia menikah dengan suami lain. Dan jika ia, </span><span>suami kedua itu</span><span>, menjatuhkan talak kepadanya, maka tak ada dosa atas mereka berdua untuk kembali kepada satu sama lain jika mereka berdua yakin akan dapat menegakkan peraturan-peraturan Allah. Dan, demikianlah peraturan- peraturan Allah yang dijelaskan-Nya kepada kaum yang </span><span>mau</span><span> mengatahui.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6587,2,2,232,'<p><span>Dan apabila kamu menjatuhkan talak kepada perempuan- perempuan lalu </span><span>[a] </span><span>mereka mendekati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=283a">283a</a><span>]</span><span> akhir masa </span><span>‘</span><span>idahnya, maka </span><span>[b] </span><span>tahanlah mereka secara ma</span><span>’</span><span>ruf, atau lepaskanlah mereka secara ma</span><span>’</span><span>ruf; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=284">284</a><span>]</span><span> tetapi, janganlah kamu menahan mereka sehingga menyusahkan dengan menlanggar </span><span>hak mereka</span><span>. Dan , barangsiapa berbuat demikian, maka sesungguhnya ia menganiaya dirinya sendiri. Dan janganlah kamu menjadikan hukum-hukum Allah sebagai perolokan, dan ingatlah nikmat Allah kepadamu, dan apa yang diturunkan kepadamu yakni Kitab dan Hikmah yang dengan itu Dia menasehati kamu. Dan bertakwalah kepada Allah dan ketahuilah bahwa Allah Maha Mengatahui segala sesuatu.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>229; 65:5.    </span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span>230    </span></p><p><span>[c] </span><span>3</span><span>:</span><span>104</span></p>');
INSERT INTO `quran` VALUES (6588,2,2,233,'<p><span>R. 30</span></p><p><span>Dan, apabila kamu menalak perempuan-perempuan itu lalu mereka menyempurnakan waktu </span><span>‘</span><span>idah mereka, maka janganlah kamu menghalangi mereka untuk menikah dengan suami-suami mereka yang </span><span>lama</span><span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=285">285</a><span>]</span><span> jika antara mereka ada persetujuan yang ma</span><span>’</span><span>ruf. Yang demikian itu nasehat bagi siapa di antaramu yang beriman kepada Allah dan Hari Kemudian. Inilah yang lebih berberkat bagimu dan lebih suci. Dan Allah mengatahui sedang kamu tidak mengatahui.</span><span> </span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6589,2,2,234,'<p><span>Dan, </span><span>[a] </span><span>ibu-ibu hendaknya menyusui anak-anak mereka dua tahun genap; bagi </span><span>[b] </span><span>siapa yang hendak menyempurnakan masa menyusui. Dan atas laki-laki yang punya anaklah tanggung-jawab memberi mereka makan dan pakaian dengan cara ma</span><span>’</span><span>ruf </span><span>[c] </span><span>”Tiada seseorang dibebani kecuali menurut kadar kemampuannya. Janganlah seorang ibu </span><span>disusahkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=286">286</a><span>]</span><span> karena anaknya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=287">287</a><span>]</span><span> Dan demikian pula wajib atas ahli waris dari </span><span>pihak bapak </span><span> seperti itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=288">288</a><span>]</span><span> Dan, jika mereka berdua menghendaki penyapihan </span><span>anak itu </span><span> atas kerelaan dan perundingan bersama-sama, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=288a">288a</a><span>]</span><span> maka tak ada dosa atas mereka berdua </span><span> dalam hal</span><span> itu. Dan, jika kamu berkehendak anak-anakmu disusui </span><span>perempuan lain,</span><span> maka tidak ada dosa atasmu asal kamu membayar apa yang telah kamu sepakati secara layak. Dan, bertakwalah kepada Allah dan ketahuilah bahwa Allah melihat segala apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>31</span><span>:</span><span>15; 46:16.    </span></p><p><span>[b] </span><span>65</span><span>:</span><span>7.    </span></p><p><span>[c] </span><span>2</span><span>:</span><span>287; 6:153; 7:43; 23:63; 65:8.</span></p>');
INSERT INTO `quran` VALUES (6590,2,2,235,'<p><span>Dan, mengenai </span><span>[a] </span><span>orang-orang yang wafat di antaramu dan meninggalkan istri-istri, </span><span>[b] </span><span>istri-istri</span><span> mereka itu harus menahan diri mereka empat bulan sepuluh hari. Dan, apabila mereka telah mencapai jangka waktu </span><span>‘</span><span>idah mereka yang ditentukan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=289">289</a><span>]</span><span> maka tak ada dosa terhadapmu atas apa yang diperbuat mereka mengenai diri mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=290">290</a><span>]</span><span> secara patut. Dan, Allah Maha Mengetahui segala apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>241.    </span></p><p><span>[b] </span><span>2</span><span>:</span><span>229.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6591,2,2,236,'<p><span>Dan, tidak ada dosa atasmu dalam apa yang kamu isyaratkan untuk meminang perempuan-perempuan ini atau dalam </span><span>keinginan</span><span> yang kamu sembunyikan dalam hatimu.Allah mengatahui bahwa kamu tentu akan mengingat mereka </span><span>dalam hubungan ini, </span><span>akan tetapi, jangan kamu membuat perjanjian dengan mereka secara rahasia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=291">291</a><span>]</span><span> kecuali kamu mengucapkan </span><span>kepada mereka </span><span>perkataan yang ma</span><span>’</span><span>ruf. Dan janganlah kamu bertekad untuk mengikat pernikahan sebelum </span><span>‘</span><span>idah</span><span> itu mencapai jangka waktunya yang di tetapkan. Dan, ketahuilah bahwa Allah mengatahui segala sesuatu yang ada di dalam hatimu, karena itu takutlah kepada-Nya. Dan, ketahuilah sesungguhnya Allah Maha Pengampun, Maha Penyantun.</span><span> </span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6592,2,2,237,'<p><span>Tak ada dosa atasmu, jika kamu menalak perempuan-perempuan yang belum kamu sentuh atau belum kamu memastikan maskawin bagi mereka. Akan tetapi, berikanlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=292">292</a><span>]</span><span> kepada mereka, yang kaya menurut kadar kemampuannya, dan bagi yang berkekurangan menurut kadar kemampuannya, suatu pemberian dengan cara yang ma</span><span>’</span><span>ruf. </span><span>Inilah</span><span> kewajiban atas orang-orang yang berbuat baik.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6593,2,2,238,'<p><span>Dan, jika kamu menjatuhkan talak kepada mereka sebelum kamu menyentuh mereka, sedangkan telah kamu tetapkan maskawin bagi mereka, maka </span><span>kamu berikan</span><span> seperdua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=293">293</a><span>]</span><span> dari apa yang telah kamu tetapkan, kecuali jika mereka memaafkan atau dimaafkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=294">294</a><span>]</span><span> oleh orang yang ditangannya ada ikatan perkawinan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=294a">294a</a><span>]</span><span> Dan, bahwa kamu memaafkan adalah lebih dekat kepada takwa. Dan janganlah kamu lupa berbuat kebaikan di antaramu. Sesungguhnya </span>Allah <span>melihat segala sesuatu yang kamu kerjakan, </span></p>');
INSERT INTO `quran` VALUES (6594,2,2,239,'<p><span>[a] </span><span>Peliharalah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=295">295</a><span>]</span><span> semua shalat dan </span><span>khususnya</span><span> shalat tengah-tengah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=296">296</a><span>]</span><span> dan berdirilah di hadapan Allah dengan patuh.</span></p><p><span>[a] </span><span>23</span><span>:</span><span>10; 70:35</span></p>');
INSERT INTO `quran` VALUES (6595,2,2,240,'<p><span>Dan, </span><span>[a] </span><span>jika kamu </span><span>dalam keadaan</span><span> takut, maka </span><span>shalatlah</span><span> sambil berjalan kaki atau berkendaraan; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=297">297</a><span>]</span><span> dan </span><span>[b] </span><span>apabila kamu telah aman, maka ingatlah Allah sebagaimana Dia telah mengajarkan kepada mu apa yang belum ketahui.</span></p><p><span>[a] </span><span>4</span><span>:</span><span>102.     </span></p><p><span>[b] </span><span>4</span><span>:</span><span>104</span></p>');
INSERT INTO `quran` VALUES (6596,2,2,241,'<p><span>Dan, </span><span>[c] </span><span>orang-orang yang meninggal dunia di antaramu dan meninggalkan istri-istri, hendaknya mewasiatkan untuk istri-istri mereka perbekalan untuk setahun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=298">298</a><span>]</span><span> tampa menyuruh mereka keluar </span><span> dari rumah.</span><span> Tetapi jika mereka keluar </span><span>sendiri</span><span>, maka tak ada dosa atasmu tentang apa yang wajar mereka lakukan terhadap diri mereka. Dan, Allah Maha Perkasa, Maha Bijaksana.</span></p><p><span>[c] </span><span>2</span><span>:</span><span>235</span><span> </span></p>');
INSERT INTO `quran` VALUES (6597,2,2,242,'<p><span>Dan, bagi perempuan-perempuan yang ditalak </span><span>juga harus disediakan </span><span>[a] </span><span>perbekalan yang ma</span><span>’</span><span>ruf , </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=299">299</a><span>] </span><span>kewajiban atas mereka bertakwa.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>242; 65:8.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6598,2,2,243,'<p><span>Demikianlah Allah menjelaskan Hukum-hukum-Nya bagimu supaya kamu mengerti.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6599,2,2,244,'<p><span>R. 32</span></p><p><span>Tidaklah engkau mendengar ihwal orang-orang yang keluar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=300">300</a><span>]</span><span> dari kampung halaman mereka, dan mereka itu beribu-ribu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=301">301</a><span>]</span><span> </span><span>karena</span><span> takut mati ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=302">302</a><span>]</span><span> Lalu, Allah berfirman kepada mereka. </span><span>[b] </span><span>”Matilah!” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=303">303</a><span>]</span><span> kemudian Dia menghidupkan mereka. Sesungguhnya, Allah mempunyai karunia terhadap manusia, tetapi kebanyakan manusia tidak bersyukur.</span></p><p><span>[b] </span><span>5</span><span>:</span><span>27</span></p>');
INSERT INTO `quran` VALUES (6600,2,2,245,'<p><span>Dan </span><span>[a] </span><span>berpeganglah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=304">304</a><span>]</span><span> kamu di jalan Allah, dan ketahuilah bahwa Allah Maha Mendengar, Maha Mengetahui.</span></p><p><span>[a] </span><span>2</span><span>:</span><span> 191; 4:85</span><span> </span></p>');
INSERT INTO `quran` VALUES (6601,2,2,246,'<p><span>[b] </span><span>Siapakah yang mau memberi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=305">305</a><span>]</span><span> pinjaman yang baik kepada Allah agar Dia melipat-lipat ganda ? Dan, Allah mengambil dan memperbanyak </span><span>harta,</span><span> dan kepada-Nya kamu akan dikembalikan.</span></p><p><span>[b] </span><span>57</span><span>:</span><span> 12, 19; 64:18</span><span> </span></p>');
INSERT INTO `quran` VALUES (6602,2,2,247,'<p><span>Tidaklah engkau memperhatikan </span><span>ihwal</span><span> para pemuka Bani Israil sesudah Musa, ketika mereka, “Angkatlah bagi kami seorang raja , supaya kami dapat berperang di jalan Allah. ” Berkata ia, </span><span>[a] </span><span>”Apakah barangkali kamu tidak akan berperang jika berperang diwajibkan atasmu ?” Berkata mereka, “Mengapakah kami tidak akan berperang </span><span>[306]</span><span> di jalan Allah jika kami telah diusir dari rumah-rumah kami dan </span><span>dipisahkan dari </span><span>anak- anak kami ?” Tetapi tatkala diwajibkan atas mereka berperang, berpalinglah mereka. Dan Allah Maha Mengatahui orang-orang aniaya.</span></p><p><span>[a] </span><span>4</span><span>:</span><span>78.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6603,2,2,248,'<p><span>Dan, berkata nabi mereka kepada mereka, “Sesungguhnya Allah telah mengangkat Thalut </span><a>[307</a><span>]</span><span> menjadi raja bagimu.” Barkata mereka, “Bagaimana ia bisa mempunyai kerajaan atas kami, padahal kami lebih berhak mempunyai kerajaan daripadanya, dan ia tidak diberi berlimpah-limpah harta ?” Berkata ia, “ Sesungguhnya Allah telah melihatnya atasmu dan melebihkannya dengan keluasan ilmu dan kekuatan badan.” Dan </span><span>[a] </span><span>Allah memberikan kerajaan-Nya kepada siapa yang Dia kehendaki dan Allah Maha Luas, Maha Mengetahui.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>27</span><span> </span></p>');
INSERT INTO `quran` VALUES (6604,2,2,249,'<p><span>Dan, berkata nabi mereka kepada mereka. “Sesungguhnya tanda kerajaannya ialah, akan datang kepadamu suatu Tabut, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=308">308</a><span>]</span><span> yang di dalamnya mengandung ketenteraman dari Tuhan-mu dan </span><span>harta </span><span>pusaka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=309">309</a><span>]</span><span> yang ditinggalkan oleh keluarga Musa dan keluarga Harun yang dipikul oleh Malaikat-malaikat. Sesungguhnya, dalam hal ini ada suatu Tanda bagimu, jika kamu orang-orang mukmin.”</span><span> </span></p>');
INSERT INTO `quran` VALUES (6605,2,2,250,'<p><span>Kemudian, tatkala Thalut berangkat dengan balatentara</span><span>nya</span><span>, berkata </span><span>ia</span><span>, “Sesungguhnya Allah akan mencobaimu dengan sebuah sungai. Maka, barangsiapa minum darinya ia bukan dariku; dan barangsiapa tidak mencicipinya, maka sesungguhnya ia dariku, kecuali orang yang menciduk seciduk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=310">310</a><span>]</span><span> dengan tangannya.” Tetapi, mereka minum darinya kecuali sebagian kecil dari mereka. Maka, tatkala ia dan orang-orang yang beriman besertanya telah menyeberanginya, berkata mereka, “Tak ada kekuatan pada kamu hari ini untuk </span><span>menghadapi</span><span> Jalut </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=310a">310a</a><span>]</span><span> dan bala-tentaranya.” </span><span>Tetapi</span><span>, mereka yang meyakini bahwa mereka akan menemui Allah </span><span>[a] </span><span>berkata, “Banyak golongan yang kecil telah mengalahkan golongan yang besar dengan izin Allah. Dan Allah beserta orang-orang sabar.”</span></p><p><span>[a] </span><span>3</span><span>:</span><span>124; 8:66</span><span> </span></p>');
INSERT INTO `quran` VALUES (6606,2,2,251,'<p><span>Dan ketika mereka maju untuk </span><span>menghadapi</span><span> Jalut </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=311">311</a><span>]</span><span> dan bala tentaranya, berkata mereka, "Ya Tuhan kami </span><span>[a] </span><span>anugerakan ketabahan atas kami dan </span><span>[b] </span><span>tolonglah kami terhadap kaum kafir.”</span></p><p><span>[a] </span><span>3</span><span>:</span><span>148, 201; 7:127.        </span></p><p><span>[b] </span><span>2</span><span>:</span><span>287; 3:148</span><span> </span></p>');
INSERT INTO `quran` VALUES (6607,2,2,252,'<p><span>Maka, mereka mengalahkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=312">312</a><span>]</span><span> mereka itu dengan izin Allah; dan Dawud membunuh Jalut dan Allah memberinya kerajaan dan kebijakan dan mengajarkan kepadanya apa yang Dia kehendaki. Dan, </span><span>[a] </span><span>sekiranya Allah tidak menyingkirkan </span><span>kejahatan</span><span> sebagian manusia oleh sebagian lainnya, niscaya bumi akan penuh dengan kerusuhan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=313">313</a><span>]</span><span> tetapi Allah mempunyai limpahan karunia atas sekalian alam.</span></p><p><span>[a] </span><span>22</span><span>:</span><span>41</span><span> </span></p>');
INSERT INTO `quran` VALUES (6608,2,2,253,'<p><span>Inilah Ayat-ayat Allah. Kami membacakannya kepada engkau dengan hak. Dan, sesungguhnya engkau seorang dari rasul-rasul.</span></p>');
INSERT INTO `quran` VALUES (6609,2,2,254,'<p><span>[b] </span><span>Inilah rasul-rasul yang telah Kami lebih kan sebagian dari mereka di atas yang lain; </span><span>[c] </span><span>di antara mereka ada yang </span><span>kepada mereka</span><span> Allah bercakap-cakap dan </span><span>[d] </span><span>Dia meninggikan sebagian dari mereka dalam derajat-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=314">314</a><span>]</span><span> Dan </span><span>[e] </span><span>Kami memberi Isa ibnu Maryam keterangan-keterangan nyata dan Kami memperkuat dia dengan Ruhulkudus.</span></p><p><span>[b] </span><span>17</span><span>:</span><span>56.    </span></p><p><span>[c] </span><span>4</span><span>:</span><span>165.    </span></p><p><span>[d] </span><span>4</span><span>:</span><span>159; 19:58.    </span></p><p><span>[e] </span><span>2</span><span>:</span><span>88.    </span></p><p><span>[a] </span><span>4</span><span>:</span><span>56, 10:41</span></p>');
INSERT INTO `quran` VALUES (6610,2,2,255,'<p><span>Hai orang-orang yang beriman. </span><span>[b] </span><span>belanjakanlah apa yang telah Kami rezekikan kepadamu sebelum datang hari yang tak ada jual-beli </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=315">315</a><span>] </span><span>di dalamnya, dan </span><span>[c] </span><span>tidak pula persahabatan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=316">316</a><span>]</span><span> dan </span><span>[d] </span><span>tidak pula syafaat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=317">317</a><span>]</span><span> dan orang-orang kafir itulah mereka yang aniaya.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>196; 14:32; 47:39; 57:11; 63:11. </span></p><p><span>[c] </span><span>14</span><span>:</span><span>32; 43:68. </span></p><p><span>[d] </span><span>Lihat 2</span><span>:</span><span>49</span><span> </span></p>');
INSERT INTO `quran` VALUES (6611,2,2,256,'<p><span>Allah, tiada Tuhan selain Dia, </span><span>[a] </span><span>Yang Maha Hidup, Yang Tegak atas Dzat-Nya sendiri dan Penegak </span><span>segala sesuatu.</span><span> Kantuk tidak menyerang-Nya dan tidak pula tidur. Kepunyaan Dia-lah apa yang </span><span>di seluruh langit dan apa yang</span><span> </span><span>ada di bumi. </span><span>[b] </span><span>Siapakah yang dapat memberi syafaat di hadirat-Nya kecuali dengan izin-Nya ? </span><span>[c] </span><span>Dia mengetahui apa yang ada di hadapan mereka dan di belakang mereka; dan mereka tidak meliputi barang sesuatu dari Ilmu-Nya kecuali apa yang Dia kehendaki. Ilmu-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=318">318</a><span>]</span><span> meliputi seluruh langit dan bumi; dan tidaklah memberatkan-Nya menjaga keduanya; dan Dia Maha Tinggi, Maha Besar.</span></p><p><span>[a] </span><span>3</span><span>:</span><span>3;20:112; 25:59.        </span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span> 49.    </span></p><p><span>[c] </span><span>20</span><span>:</span><span>111.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6612,2,2,257,'<p><span>[d] </span><span>Tidak ada paksaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=319">319</a><span>]</span><span> dalam agama. Sesungguhnya jalan benar itu nyata </span><span>bedanya </span><span>dari kesesatan; dan barang siapa menolak</span><span> ajakan</span><span> orang-orang sesat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=320">320</a><span>]</span><span> dan beriman kepada Allah , maka sesungguhnya ia </span><span>[e] </span><span>telah berpegang kepada suatu pegangan yang kuat dan tak kenal putus. Dan Allah Maha Mendengar, Maha Mengetahui.</span></p><p><span>[d] </span><span>10</span><span>:</span><span>100; 11:119; 18:30; 76:4. </span></p><p><span>[e] </span><span>31</span><span>:</span><span>23</span><span> </span></p>');
INSERT INTO `quran` VALUES (6613,2,2,258,'<p><span>[a] </span><span>Allah itu sahabat orang-orang beriman; </span><span>[b] </span><span>Dia mengeluarkan mereka dari kegelapan kepada cahaya. Dan, orang-orang kafir, </span><span>[c] </span><span>sahabat mereka adalah orang-orang sesat yang mengeluarkan mereka dari cahaya kepada kegelapan. Mereka tinggal lama di dalamnya.</span></p><p><span>[a] </span><span>45</span><span>:</span><span>20.    </span></p><p><span>[b] </span><span>5</span><span>:</span><span>17; 65:12.    </span></p><p><span>[c] </span><span>7</span><span>:</span><span>28; 16:101</span><span> </span></p>');
INSERT INTO `quran` VALUES (6614,2,2,259,'<p><span> </span><span>R. 35</span></p><p><span>Tidaklah engkau mendengar orang yang berbantah dengan Ibrahim tentang Tuhan-nya karena Allah telah memberi kerajaan kepadanya ? Ketika berkata Ibrahim, </span><span>[d] </span><span>”Tuhan-ku Yang menghidupkan dan mematikan,” berkata ia, “Aku pun menghidupkan dan mematikan.” Berkata Ibrahim, “ Sesungguhnya Allah mendatangkan </span><span>matahari</span><span> itu dari timur; maka datanglah </span><span>matahari</span><span> itu dari barat !” Maka terdiamlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=321">321</a><span>]</span><span> orang yang ingkar itu. Dan, Allah tidak memberi petunjuk kepada kaum aniaya.</span></p><p><span>[d] </span><span>3</span><span>:</span><span>157; 9:116; 40:69; 57:3.</span></p>');
INSERT INTO `quran` VALUES (6615,2,2,260,'<p><span>Atau, </span><span>tidaklah engkau mendengar perumpamaan</span><span> seperti orang yang melalui suatu kota </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=322">322</a><span>]</span><span> yang telah runtuh atas atap-atapnya, </span><span>kemudian</span><span> ia berkata, “Bilakah Allah akan menghidupkan kembali </span><span>kota</span><span> ini sesudah matinya ?” Maka, Allah mematikannya seratus tahun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=323">323</a><span>]</span><span> </span><span>lamanya;</span><span> kemudian Dia membangkitkannya </span><span>lagi </span><span>dan berfirman,</span><span> “</span><span>Berapa lamakah engkau tinggal </span><span>dalam keadaan seperti ini ?”</span><span> Berkata ia, “ Aku tinggal sehari atau sebagian hari, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=323a">323a</a><span>]</span><span> Berfirman Dia, </span><span>“Sungguh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=323b">323b</a><span>]</span><span> akan tetapi engkau </span><span> pun</span><span> telah tinggal seratus tahun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=324">324</a><span>]</span><span> lamanya </span><span>dalam keadaan seperti ini.</span><span> Maka, lihatlah makanan engkau dan minuman engkau; </span><span>benda-benda </span><span>itu tidak membusuk. Dan, lihatlah keledai engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=325">325</a><span>]</span><span> Maka, </span><span>Kami melakukan itu</span><span> supaya Kami menjadikan engkau Tanda bagi manusia. Dan, </span><span>[a] </span><span>lihatlah tulang-belulang itu, betapa Kami menatanya kembali, kemudian Kami membalutnya dengan daging.” Maka, setelah </span><span>kenyataan</span><span> ini menjadi terang baginya, berkatalah ia, “Aku mengetahui bahwa Allah berkuasa atas segala sesuatu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=326">326</a><span>]</span></p><p><span>[a] </span><span>23</span><span>:</span><span>15</span></p>');
INSERT INTO `quran` VALUES (6616,2,2,261,'<p><span>Dan, </span><span>ingatlah,</span><span> ketika berkata Ibrahim, “Ya Tuhan-ku, perlihatkanlah kepadaku bagaimana Engkau menghidupkan yang mati.” Dia berfirman, “ Tidaklah engkau percaya ?” Berkata ia, “Ya, tetapi </span><span>kutanyakan hal ini </span><span>supaya tenteram hatiku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=327">327</a><span>]</span><span> Berfiman Dia, “Maka, </span><span>jika demikian,</span><span> ambillah empat ekor burung dan jinakkan-lah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=328">328</a><span>]</span><span> mereka kepada engkau, kemudian letakkanlah setiap </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=329">329</a><span>]</span><span> burung itu di atas tiap-tiap gunung; kemudian panggillah mereka, niscaya mereka akan datang kepada engkau dengan segera. Dan Ketahuilah, bahwa Allah Maha Perkasa, Maha Bijaksana.”</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6617,2,2,262,'<p><span>R. 36</span></p><p><span>[a] </span><span>Tamsil orang-orang yang membelanjakan harta mereka di jalan Allah, adalah seumpama sebuah biji menumbuhkan tujuh butir, pada setiap butir </span><span>terdapat</span><span> seratus biji. Dan Allah melipatgandakan bagi siapa yang Dia kehendaki dan Allah Maha Luas, Maha Mengatahui.. </span><a>[330</a><span>]</span></p><p><span>[a] </span><span>3</span><span>:</span><span>266; 30:40</span><span> </span><span> </span></p>');
INSERT INTO `quran` VALUES (6618,2,2,263,'<p><span>Orang-orang yang membelanjakan harta mereka di jalan Allah lalu mereka tidak mengiringi </span><span>[b] </span><span>apa yang dibelanjakan mereka dengan menyebut-nyebut kebaikan dan tidak pula menyakiti, </span><a>[331</a><span>]</span><span> bagi mereka ada ganjaran mereka di sisi Tuhan mereka, dan tak ada ketakutan pada mereka dan tidak pula mereka akan bersedih.</span></p><p><span>[b] </span><span>2</span><span>:</span><span>265; 74:7</span><span> </span></p>');
INSERT INTO `quran` VALUES (6619,2,2,264,'<p><span>[a] </span><span>Tutur kata yang baik dan ampunan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=332">332</a><span>]</span><span> adalah lebih baik daripada sedekah yang diiringi menyakiti. Dan Allah Maha Kaya, Maha Penyantun.</span></p><p><span>[a] </span><span>47</span><span>:</span><span>22</span><span> </span></p>');
INSERT INTO `quran` VALUES (6620,2,2,265,'<p><span>Hai orang-orang yang beriman, </span><span>[a] </span><span>janganlah kamu menjadikan sedekah-sedekahmu sia-sia dengan menyebut-nyebut jasa baik dan menyakiti seperti halnya orang </span><span>[b] </span><span>yang membelanjakan hartanya untuk di lihat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=333">333</a><span>]</span><span> manusia, dan ia tidak beriman kepada Allah dari Hari Kemudian. Maka, keadaannya adalah semisal batu licin yang di atasnya </span><span>tertutup</span><span> tanah, lalu hujan lebat menimpanya dan meninggalkannya licin. </span><span>[c] </span><span>Mereka tidak akan memperoleh sesuatu dari apa yang mereka usahakan. Dan Allah tidak memberi petunjuk kepada kaum kafir.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span>263.    </span></p><p><span>[b] </span><span>4</span><span>:</span><span>39, 8:48.    </span></p><p><span>[c] </span><span>14</span><span>:</span><span>19.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6621,2,2,266,'<p><span>Dan, misal orang-orang yang membelanjakan harta mereka demi mencari keridhaan Allah dan memperteguh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=334">334</a><span>]</span><span> jiwa mereka adalah semisal sebidang kebun yang terletak di tempat tinggi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=335">335</a><span>] </span><span>Hujan lebat menimpanya dan ia menghasilkan </span><span>[a] </span><span>buahnya dua kali lipat. Dan, jika hujan lebat tidak menimpanya, maka gerimis </span><span>pun memadai.</span><span> Dan, Allah Maha melihat apa-apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span>262</span><span> </span></p>');
INSERT INTO `quran` VALUES (6622,2,2,267,'<p><span>Adakah seorang dari antara kamu ingin mempunyai sebidang kebun korma dan anggur, yang dibawahnya mengalir sungai-sungai ? Baginya di dalam </span><span>kebun</span><span> itu ada segala macam buah-buahan, sedangkan hari tua telah menjelangnya dan ia mempunyai keturunan yang tidak berdaya, lalu </span><span>kebun itu</span><span> ditimpa angin puyuh yang mengandung api, dan terbakarlah </span><span>kebun</span><span> itu. </span><a>[336</a><span>]</span><span> Demikianlah Allah menjelaskan Tanda-Tanda-Nya bagimu supaya kamu berfikir.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6623,2,2,268,'<p><span>Hai orang-orang yang beriman, belanjakanlah barang-barang baik yang kamu usaha kan dan segala sesuatu yang Kamu keluarkan bagimu dari bumi; dan janganlah kamu memilih yang buruk darinya </span><span>lalu</span><span> kamu membelanjakanya; padahal kamu sendiri tidak mau mengambilnya kecuali dengan memicingkan mata terhadapnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=337">337</a><span>]</span><span> Dan, ketahuilah bahwa Allah Maha Kaya, Maha Terpuji.</span><span> </span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6624,2,2,269,'<p><span>[a] </span><span>Syaitan menakut-nakuti kamu dengan kemiskinan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=338">338</a><span>] </span><span>dan menyuruh kamu berbuat kekejian, </span><a>[339</a><span>]</span><span> dan Allah menjanjikan kepadamu ampunan dari-Nya dan karunia. Dan Allah Maha Luas Karunia- Nya, Maha Mengetahui.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span>170; 24:22</span><span> </span></p>');
INSERT INTO `quran` VALUES (6625,2,2,270,'<p><span>[a] </span><span>Dia memberi kebijakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=340">340</a><span>]</span><span> kepada siapa yang Dia kehendaki dan barangsiapa diberi kebijakan, maka sungguh ia telah diberi berlimpah-limpah kebaikan; dan tiada yang dapat menarik pelajaran kecuali orang-orang berakal.</span></p><p><span>[a] </span><span>17</span><span>:</span><span>40</span></p>');
INSERT INTO `quran` VALUES (6626,2,2,271,'<p><span>[b] </span><span>Dan belanja apa pun yang kamu belanjakan atau nazar apa pun yang kamu nazarkan </span><span>di jalan </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=341">341</a><span>]</span><span>.</span><span> maka sesungguhnya Allah mengetahuinya; dan bagi orang-orang aniaya tak ada seorang penolong.</span></p><p><span>[b] </span><span>22</span><span>:</span><span>30; 76:8.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6627,2,2,272,'<p><span>Jika kamu memberikan </span><span>[c] </span><span>sedekah-sedekah dengan terang-terangan, maka hal itu baik; dan jika kamu sembunyikan itu, dan kamu memberikannya kepada fakir miskin, maka hal itu lebih baik </span><a>[342</a><span>]</span><span> bagimu dan </span><span>[d] </span><span>Dia akan menghapuskan dari kesalahan-kesalahanmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=343">343</a><span>]</span><span> Dan Allah Maha Mengetahui apa yang kamu kerjakan.</span></p><p><span>[c] </span><span>9</span><span>:</span><span> 60, 103, 104.    </span></p><p><span>[d] </span><span>4</span><span>:</span><span> 32; 8:30; 29:8; 64:10; 66:9.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6628,2,2,273,'<p><span>[a] </span><span>Bukanlah tanggungjawab engkau memberi petunjuk kepada mereka, akan tetapi Allah lah yang memberi petunjuk kepada siapa yang Dia kehendaki. Dan harta </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=344">344</a><span>]</span><span> apapun yang kamu belanjakan maka </span><span>manfaatnya</span><span> adalah untuk dirimu, dan </span><span>sebenarnya</span><span> tidaklah kamu membelanjakan melainkan untuk mencari keridhaan Allah .</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=345">345</a><span>]</span><span> Dan harta apa pun yang kamu </span><span>[b] </span><span>belanjakan niscaya akan dikembalikan kepadamu dengan penuh dan kamu tidak akan dianiaya.</span></p><p><span>[a] </span><span>28</span><span>:</span><span> 57; 92:13.    </span></p><p><span>[b] </span><span>2</span><span>:</span><span>282; 4:174; 8:61; 39:11</span><span> </span></p>');
INSERT INTO `quran` VALUES (6629,2,2,274,'<p><span>Infak tersebut</span><span> bagi orang-orang fakir yang terikat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=346">346</a><span>]</span><span> di jalan Allah, mereka tidak mampu bergerak bebas di muka bumi. Orang yang tidak tahu menganggap mereka kaya, </span><span>disebabkan mereka</span><span> </span><span>menghindarkan diri </span><span>dari meminta-minta. </span><span>[a] </span><span>Engkau dapat mengenali mereka dari raut muka mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=347">347</a><span>]</span><span> dan mereka tidak suka meminta kepada manusia dengan mendesak-desak. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=348">348</a><span>]</span><span> Dan harta </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=348a">348a</a><span>]</span><span> apa pun yang kamu belanjakan maka sesungguhnya Allah Maha Mengetahui hal itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=349">349</a><span>]</span></p><p><span>[a] </span><span>48</span><span>:</span><span>30.</span></p>');
INSERT INTO `quran` VALUES (6630,2,2,275,'<p><span>[a] </span><span>Orang-orang yang membelanjakan harta mereka pada malam dan siang dengan sembunyi-sembunyi dan terang-terangan, bagi mereka ada ganjaran mereka di sisi Tuhan mereka; dan tak ada ketakutan pada mereka dan tidak pula mereka akan bersedih.</span></p><p><span>[a] </span><span>13</span><span>:</span><span>23; 14:32; 16:76; 35:30.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6631,2,2,276,'<p><span>Orang-orang </span><span>[b] </span><span>yang memakan riba </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=350">350</a><span>]</span><span> tidak berdiri melainkan seperti berdiri orang yang syaitan merasuknya dengan penyakit gila. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=351">351</a><span>]</span><span> Hal demikian adalah karena mereka berkata, “Sesungguhnya jual-beli itu serupa riba,” padahal Allah menghalalkan jual-beli dan mengharamkan riba. Maka siapa yang kepadanya telah sampai peringatan dari Tuhan-nya lalu berhenti </span><span>dari pelanggaran itu,</span><span> maka untuknyalah apa yang </span><span>diterimanya</span><span> di masa lalu; dan urusannya </span><span>terserah </span><span>kepada Allah. Dan barangsiapa kembali lagi </span><span>makan riba,</span><span> maka mereka adalah penghuni Api, mereka akan tinggal lama di dalamnya.</span></p><p><span>[b] </span><span>3</span><span>:</span><span>131; 30:40.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6632,2,2,277,'<p><span>Allah akan menghapuskan </span><span>[352] </span><span>dan </span><span>[a] </span><span>mengembangkan sedekah-sedekah. Dan, Allah tidak menyukai setiap orang kafir yang pekat, banyak berbuat dosa.</span></p><p><span>[a] </span><span>30</span><span>:</span><span>40</span></p>');
INSERT INTO `quran` VALUES (6633,2,2,278,'<p><span>Sesungguhnya orang-orang yang beriman dan beramal saleh dan </span><span>[b] </span><span>mendirikan shalat, dan membayar zakat, bagi mereka ada ganjaran mereka di sisi Tuhan mereka, dan tak ada ketakutan pada mereka dan tidak pula mereka akan bersedih.</span></p><p><span>[b] </span><span>Lihat 2</span><span>:</span><span>4</span><span> </span></p>');
INSERT INTO `quran` VALUES (6634,2,2,279,'<p><span>Hai orang-orang yang beriman, takutlah kepada Allah dan tinggalkanlah yang masih tersisa dari riba, jika kamu adalah orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (6635,2,2,280,'<p><span>Dan, jika kamu tidak berbuat </span><span>demikian,</span><span> maka waspadalah terhadap perang dari Allah dan Rasul-Nya; dan jika kamu bertobat maka untuk kamu asal hartamu; </span><span>dengan demikian</span><span> kamu tidak akan menganiaya dan tidak pula kamu dianiaya.</span></p><p><span> </span></p>');
INSERT INTO `quran` VALUES (6636,2,2,281,'<p><span>Dan, jika orang </span><span>yang berhutang</span><span> itu dalam kesempitan, maka </span><span>berilah</span><span> dia tangguh sampai ia merasa lapang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=353">353</a><span>]</span><span> Dan, jika kamu menyedekahkannya maka kamu akan lebih baik bagimu, jika kamu mengetahui.</span><span> </span></p>');
INSERT INTO `quran` VALUES (6637,2,2,282,'<p><span>Dan, takutlah kamu terhadap hari itu ketika kamu akan dikembalikan kepada Allah </span><span>[a] </span><span>kemudian, setiap jiwa akan diganjar sepenuhnya untuk apa yang telah diusahakannya, dan mereka tidak akan dianiaya.</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span>273</span></p>');
INSERT INTO `quran` VALUES (6638,2,2,283,'<p><span>Hai orang-orang yang beriman, apabila kamu berhutang pada sesamamu untuk masa tertentu, hendaklah menuliskannya. Dan hendaklah seorang juru tulis di antaramu menuliskan dengan jujur; dan janganlah juru tulis itu menolak untuk menuliskan, seperti </span><span>[b] </span><span>Allah telah mengajarkan kepadanya. maka hendaklah ia menuliskan. Dan, hendaklah orang yang berhak itu mendiktekan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=354">354</a><span>]</span><span> dan ia harus takut kepada Allah, Tuhan-Nya dan janganlah ia mengurangi darinya sedikit pun. Maka, jika orang yang berhak itu kurang berakal atau lemah, atau ia tidak mampu mendiktekan, maka walinya harus mendiktekan dengan jujur. Dan carilah saksi dua orang di antara laki-lakimu; tetapi, jika tak ada dua orang laki-laki, maka seorang laki-laki dan dua orang wanita dari antara saksi-saksi yang kamu sukai; supaya jika seorang dari kedua </span><span>wanita</span><span> keliru, maka seorang lagi dapat mengingatkan yang lain. Dan janganlah kamu enggan menuliskannya, baik kecil maupun besar, beserta batas waktu </span><span>pembayarannya.</span><span> Hal demikian adalah lebih adil di sisi Allah dan lebih menegakkan kesaksian dan lebih dekat supaya kamu tidak ragu; </span><span>[a] </span><span>kecuali jika perdagangan tunai yang kamu lakukan di antaramu, maka tak ada dosa atasmu jika kamu tidak menuliskannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=354a">354a</a><span>]</span><span> Dan, adakanlah saksi apabila kamu berjual-beli,</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=354b">354b</a><span>]</span><span> dan janganlah disusahkan juru tulis maupun saksi. Dan, jika kamu mengerjakan demikian, maka sesungguhnya itu suatu kefasikan dari diri kamu. Dan bertakwalah kepada Allah. Dan Allah akan mengajarmu, karena Allah Maha Mengetahui segala sesuatu.</span></p><p><span>[b] </span><span>96</span><span>:</span><span>5.    </span></p><p><span>[c] </span><span>4</span><span>:</span><span>30.</span></p>');
INSERT INTO `quran` VALUES (6639,2,2,284,'<p><span>Dan, jika kamu dalam perjalanan, dan kamu tidak memperoleh seorang juru tulis maka hendaklah ada barang jaminan sebagai pengangan. </span><span>[355]</span><span> Jika seorang di antaramu mempercayai kepada yang lain, maka orang yang dipercaya itu hendaklah menyerahkan kembali amanatnya, dan hendaklah ia bertakwa kepada Allah. Tuhan-nya. Dan </span><span>[a] </span><span>janganlah kamu menyembunyikan kesaksian; dan barangsiapa menyembunyikannya, maka hatinya pasti berdosa. Dan Allah Maha Mengetahui segala apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>2</span><span>:</span><span>141; 5:107</span><span> </span></p>');
INSERT INTO `quran` VALUES (6640,2,2,285,'<p><span>R. 40    </span></p><p><span>Kepunyaan Allah segala apa yang ada di seluruh langit dan yang di bumi; dan jika kamu menzahirkan apa yang terdapat di dalam hatimu atau kamu menyembunyikan, Allah akan menghisabmu mengenainya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=356">356</a><span>]</span><span> </span><span>[a] </span><span>Kemudian Dia akan mengampuni siapa yang Dia kehendaki dan akan menjatuhkan siksaan kepada siapa yang Dia kehendai; dan </span>Allah <span>Maha Kuasa atas segala sesuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=357">357</a><span>]</span></p><p><span>[a] </span><span>5</span><span>:</span><span>19, 41; 48:15</span></p>');
INSERT INTO `quran` VALUES (6641,2,2,286,'<p><span>Rasul ini beriman kepada apa yang diturunkan kepadanya dari Tuhan-nya, dan </span><span>begitu pula</span><span> orang-orang mukmin; semuanya beriman kepada </span>Allah <span>dan Malaikat-malaikat-Nya, dan Kitab-kitab-Nya, Dan Rasul-rasul-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=358">358</a><span>]</span><span> </span><span>mereka mengatakan,</span><span> </span><span>[b] </span><span>”Kami tidak membeda- bedakan di antara seorang pun dari Rasul-rasul-Nya yang satu terhadap yang lainnya;” dan mereka berkata,”Kami dengar dan kami taat. Ya Tuhan kami, </span><span>[c] </span><span>kami mohon ampunan Engkau dan kepada Engkau</span><span> kami</span><span> akan kembali.”</span></p><p><span>[b] </span><span>Lihat 2 </span><span>:</span><span> 137.    </span></p><p><span>[c] </span><span>3</span><span>:</span><span>148, 194; 60:6.</span></p>');
INSERT INTO `quran` VALUES (6642,2,2,287,'<p><span>[a] </span><span>Allah tidak membebani seorang kecuali sesuai dengan kemampuannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=359">359</a><span>] </span><span>Baginya </span><span>ganjaran untuk </span><span>apa yang diusahakannya, dan ia akan mendapat </span><span>siksaan</span><span> untuk apa yang diusahakannya. </span><span>Dan mereka berkata,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=360">360</a><span>]</span><span> “Ya Tuhan kami, janganlah engkau menghukum kami jika kami lupa atau kami berbuat salah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=361">361</a><span>] </span><span>Ya Tuhan kami, janganlah Engkau membebani </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=362">362</a><span>]</span><span> kami tanggung jawab seperti telah Engkau bebankan atas orang-orang sebelum kami. Ya Tuhan kami, janganlah Engkau membebani kami apa yang kami tidak kuat menanggungnya; dan maafkanlah kami dan ampunilah kami serta kasihanilah kami </span><span>karena</span><span> Engkau-lah Pelindung kami, </span><span>[b] </span><span>maka tolonglah kami terhadap kaum kafir.”</span></p><p><span>[a] </span><span>Lihat 2</span><span>:</span><span>234.    </span></p><p><span>[b] </span><span>3</span><span>:</span><span>148</span><span> </span></p>');
INSERT INTO `quran` VALUES (6643,2,3,1,'<p><span>Aku baca</span><span> dengan nama Allah, Maha Pemurah, Maha Penyayang</span></p>');
INSERT INTO `quran` VALUES (6644,2,3,2,'<p><span>[a] </span><span>Aku Allah Yang lebih Mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=362a">362a</a><span>]</span></p><p><span>[a] </span><span>Lihat 2:2.</span></p>');
INSERT INTO `quran` VALUES (6645,2,3,3,'<p><span>[a] </span>Allah<span>, tiada tuhan selain Dia, Yang Maha hidup Yang Tegak atas Dzat-Nya sendiri dan Penegak </span><span>segala sesuatu</span><span>. </span></p><p><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=363">363</a><span>]</span></p><p><span>[b] </span><span>Lihat 2:256.</span></p>');
INSERT INTO `quran` VALUES (6646,2,3,4,'<p><span>[a] </span><span>Dia menurunkan kepada engkau Kitab yang hak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=364">364</a><span>]</span><span> dan menggenapi yang ada sebelumnya dan , Dia menurunkan Taurat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=365">365</a><span>]</span><span> dan Injil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=366">366</a><span>]</span></p><p><span>[a] </span><span>4:106; 5:49; 29:52; 39:3.</span></p>');
INSERT INTO `quran` VALUES (6647,2,3,5,'<p><span>Hal itu</span><span> sebelum ini sebagai petunjuk bagi manusia; dan Dia menurunkan </span><span>[b] </span><span>Pembeda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=367">367</a><span>]</span><span> </span><span>yang hak dari yang bathil</span><span>. Sesungguhnya orang-orang yang ingkar kepada Tanda-tanda </span>Allah<span>, bagi mereka ada azab sangat keras. Dan </span><span>[c] </span>Allah <span>Maha Perkasa, Yang Empunya Pembalasan.</span></p><p><span>[b] </span><span>2:54, 156; 8:42; 21:49; 25:2.</span></p><p><span>[c] </span><span>5:96; 14:48; 39:38.</span></p>');
INSERT INTO `quran` VALUES (6648,2,3,6,'<p><span>Sesungguhnya bagi </span>Allah <span> </span><span>[a] </span><span>tiada sesuatu pun yang tersembunyi baik di bumi maupun di langit.</span></p><p><span>[a] </span><span>14:39; 40:17; 64:5; 86:6.</span></p>');
INSERT INTO `quran` VALUES (6649,2,3,7,'<p><span>[b] </span><span>Dia-lah Yang membentuk kamu di dalam rahim </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=368">368</a><span>]</span><span> sebagaimana Dia kehendaki; tiada Tuhan selain Dia, Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b] </span><span>22:6; 23:12 </span><span>–</span><span> 15; 39:7; 40:65; 64:4.</span></p>');
INSERT INTO `quran` VALUES (6650,2,3,8,'<p><span>Dia-lah yang menurunkan Kitab kepada engkau, </span><span>[a] </span><span>diantaranya ada ayat-ayat yang muhkamat, </span><a>[369</a><span>]</span><span> itulah dasar-dasar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=370">370</a><span>]</span><span> Kitab, dan </span><span>[b] </span><span>yang lain adalah </span><span>ayat-ayat </span><span> mutasyabihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=371">371</a><span>]</span><span> Adapun orang-orang yang di dalam hati mereka ada kebengkokan, mereka mengikut apa yang mutasyabihat di dalamnya karena ingin menimbulkan fitnah dan ingin mencari tafsirannya </span><span>yang salah</span><span>. Dan, tak ada yang mengetahui tafsirannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=372">372</a><span>]</span><span> </span><span>[c] </span><span>kecuali </span>Allah<span>. </span><span>[d] </span><span>Dan mereka yang matang dalam ilmu, mereka berkata, “Kami beriman kepadanya; semuanya dari Tuhan kami.” Dan, tiada yang meraih nasihat kecuali orang-orang berakal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=373">373</a><span>]</span></p><p><span>[a] </span><span>11:2.</span></p><p><span>[b] </span><span>39:24</span></p><p><span>[c] </span><span>7:54; 18:79.</span></p><p><span>[d] </span><span>4:163</span></p>');
INSERT INTO `quran` VALUES (6651,2,3,9,'<p><span>Ya Tuhan kami, janganlah Engkau bengkokkan hati kami sesudah Engkau memberi petunjuk kepada kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=374">374</a><span>]</span><span> dan berilah kami rahmat dari sisi Engkau; sesungguhnya Engkau Maha Pemberi;</span></p>');
INSERT INTO `quran` VALUES (6652,2,3,10,'<p><span>Ya Tuhan kami, </span><span>[a] </span><span>sesungguhnya Engkau akan menghimpun manusia pada Hari yang tiada keraguan tentangnya; sesungguhnya </span>Allah <span>tidak akan menyalahi janji.</span></p><p><span>[a] </span><span>3:26; 4:88; 45:27.</span></p>');
INSERT INTO `quran` VALUES (6653,2,3,11,'<p><span>Sesungguhnya orang-orang yang ingkar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=375">375</a><span>]</span><span> tidak akan berguna sedikit pun bagi mereka </span><span>[a] </span><span>harta mereka dan anak-anak mereka melawan </span>Allah<span>; dan mereka itulah bahan bakar Api.</span></p><p><span>[a] </span><span>3:117; 58:18; 92:12; 101:3.</span></p>');
INSERT INTO `quran` VALUES (6654,2,3,12,'<p><span>[b] </span><span>Peristiwa mereka</span><span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=376">376</a><span>]</span><span> seperti keadaan kaum Firaun dan orang-orang sebelum mereka; mereka mendustakan Tanda-tanda Kami, maka Allah menghukum mereka disebabkan dosa-dosa mereka, dan Allah sangat keras dalam menghukum.</span></p><p><span>[b] </span><span>8:53, 55.</span></p>');
INSERT INTO `quran` VALUES (6655,2,3,13,'<p><span>Katakanlah kepada orang-orang yang ingkar, </span><span>[c] </span><span>” Niscaya kamu akan dikalahkan dan dihimpun ke Jahannam; dan alangkah buruknya tempat kediaman itu.”</span></p><p><span>[c] </span><span>8:37; 54:46.</span></p>');
INSERT INTO `quran` VALUES (6656,2,3,14,'<p><span>Sesungguhnya telah ada satu Tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=377">377</a><span>]</span><span> bagimu dalam </span><span>[a] </span><span>dua golongan yang saling berhadapan, segolongan berperang di jalan </span>Allah <span>dan yang lain ingkar, mereka melihat kepada mereka itu dua kali jumlah mereka dalam pandangan mata </span><span>zahir</span><span>. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=378">378</a><span>]</span><span> Dan, </span><span>demikianlah</span><span> </span><span>[b] </span>Allah <span> memperkuat dengan pertolongan-Nya siapa yang Dia kehendaki. Sesungguhnya dalam </span><span>hal</span><span> ini ada pelajaran bagi mereka yang bermata.</span></p><p><span>[a] </span><span>8:42, 43.</span></p><p><span>[b] </span><span>8:27.</span></p>');
INSERT INTO `quran` VALUES (6657,2,3,15,'<p><span>[c] </span><span>Ditampakkan indah bagi manusia kecintaan terhadap segala yang menarik, yakni, perempuan dan anak laki-laki dan kekayaan yang berlimpah berupa emas dan perak, dan kuda pilihan dan ternak dan sawah ladang. </span><span>[d] </span><span>Yang demikian adalah perlengkapan hidup </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=379">379</a><span>]</span><span> di dunia, dan </span>Allah<span>, di sisi-Nya tempat kembali yang baik.</span></p><p><span>[c] </span><span>18:47; 57:21.</span></p><p><span>[d] </span><span>3:186; 9:38; 10:71</span></p>');
INSERT INTO `quran` VALUES (6658,2,3,16,'<p><span>Katakanlah, “Maukah kamu Aku kabarkan </span><span>[a] </span><span>yang lebih baik dari yang demikian itu ?” Bagi orang-orang yang bertakwa, di sisi Tuhan mereka ada kebun-kebun yang di bawahnya mengalir sungai-sungai; mereka akan menetap di dalamnya, dan </span><span>[b] </span><span>jodoh-jodoh suci dan </span><span>[c] </span><span>keridhaan dari </span>Allah <span>. dan, </span>Allah <span>Maha Melihat akan hamba-hamba-</span><span>Nya</span><span>.</span></p><p><span>[a] </span><span>8:47; 19:77.</span></p><p><span>[b] </span><span>LIhat 2:26</span></p><p><span>[c] </span><span>3:163; 5:3; 9:72; 48:30; 59:9.</span></p>');
INSERT INTO `quran` VALUES (6659,2,3,17,'<p><span>Orang-orang yang mengatakan, “Ya Tuhan kami, sesungguhnya kami telah beriman; maka </span><span>[d] </span><span>ampunilah bagi kami dosa-dosa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=380">380</a><span>]</span><span> kami dan peliharalah kami dari azab Api.”</span></p><p><span>[d] </span><span>3:194; 7:156; 23:110; 60:6.</span></p>');
INSERT INTO `quran` VALUES (6660,2,3,18,'<p><span>[a] </span><span>Orang-orang yang sabar dan yang benar, dan yang taat, dan yang membelanjakan </span><span>di jalan </span>Allah<span>dan </span><span>[b] </span><span>orang-orang yang memohon ampunan di akhir malam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=381">381</a><span>]</span></p><p><span>[a] </span><span>33:36.</span></p><p><span>[b] </span><span>51:18, 19.</span></p>');
INSERT INTO `quran` VALUES (6661,2,3,19,'<p>Allah <span> memberi kesaksian bahwa sesungguhnya tiada Tuhan selain Dia dan </span><span>demikian pula</span><span> malaikat-malaikat dan orang-orang berilmu, yang </span><span>[a] </span><span>berpegang teguh pada keadilan;</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=381a">381a</a><span>]</span><span> tiada Tuhan selain Dia, Yang Maha Perkasa, Maha Bijaksana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=382">382</a><span>]</span></p><p><span>[a] </span><span>5:9; 7:30.</span></p>');
INSERT INTO `quran` VALUES (6662,2,3,20,'<p><span>Sesungguhnya agama </span><span>[b] </span><span>yang benar</span><span> di sisi </span>Allah <span>ialah Islam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=383">383</a><span>]</span><span> dan tiada berselisih orang-orang yang diberi Kitab melainkan setelah datang kepada mereka ilmu, karena kedengkian di antara mereka. dan, barangsiapa ingkar kepada Tanda-tanda </span>Allah<span>, maka sesungguhnya </span>Allah <span>cepat dalam menghisap.</span></p><p><span>[b] </span><span>3:86</span></p>');
INSERT INTO `quran` VALUES (6663,2,3,21,'<p><span>Kemudian jika mereka berselisih dengan engkau, maka katakanlah, </span><span>[a] </span><span>”Telah kuserahkan seluruh diriku kepada </span>Allah<span>, dan </span><span>demikian juga </span><span>orang-orang yang mengikutiku.” Dan, katakanlah kepada orang-orang yang diberi Alkitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=384">384</a><span>]</span><span> dan orang Ummi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=385">385</a><span>]</span><span> “sudahkah kamu menyerah kan diri ?” Maka, jika mereka telah menyerahkan diri, pasti mereka akan mendapat petunjuk; </span><a>[386</a><span>]</span><span> dan jika mereka berpaling, maka sesungguhnya kewajiban </span><span>[b] </span><span>engkau hanya menyampaikan. Dan, </span>Allah <span>Maha Melihat kepada hamba-hamba-</span><span>Nya</span><span>.</span></p><p><span>[a] </span><span>4:126.</span></p><p><span>[b] </span><span>5:93; 100; 13:41; 16:83.</span></p>');
INSERT INTO `quran` VALUES (6664,2,3,22,'<p><span>Sesungguhnya orang-orang yang ingkar kepada Tanda-tanda </span>Allah<span>, dan </span><span>[c] </span><span>membunuh nabi-nabi tanpa hak, dan membunuh orang-orang yang menyuruh berbuat adil di antara manusia, maka berilah mereka kabar tentang azab yang pedih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=387">387</a><span>]</span></p><p><span>[c] </span><span>Lihat 2:62.</span></p>');
INSERT INTO `quran` VALUES (6665,2,3,23,'<p><span>Mereka itulah orang-orang </span><span>[a] </span><span>yang sia-sia amalannya di dunia dan di akhirat, dan bagi mereka tidak ada penolong-penolong. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=388">388</a><span>]</span></p><p><span>[a] </span><span>2:218; 7:148; 18:106.</span></p>');
INSERT INTO `quran` VALUES (6666,2,3,24,'<p><span>Tidaklah engkau melihat orang-orang yang diberi sebagian dari Alkitab ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=389">389</a><span>]</span><span> </span><span>[b] </span><span>mereka diseru kepada Kitab </span>Allah <span>supaya </span><span>Kitab</span><span> itu menghakimi di antara mereka, kemudian segolongan dari mereka berpaling dan mereka membelakangi.</span></p><p><span>[b] </span><span>24:49.</span></p>');
INSERT INTO `quran` VALUES (6667,2,3,25,'<p><span>Yang demikian itu disebabkan mereka berkata, “Sekali-kali </span><span>[a] </span><span>Api tidak akan menyentuh kami kecuali terbatas beberapa hari saja.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=390">390</a><span>]</span><span> Dan, telah memperdayai mereka apa yang pernah diada-adakan oleh mereka tentang agama mereka.</span></p><p><span>[a] </span><span>2:81; 5:19.</span></p>');
INSERT INTO `quran` VALUES (6668,2,3,26,'<p><span>Maka, bagaimanakah </span><span>keadaan mereka</span><span> </span><span>[b] </span><span>apabila Kami himpun mereka pada Hari yang tak ada keraguan di dalamnya; dan tiap-tiap jiwa akan diganjar sepenuhnya untuk apa yang telah diusahakannya dan mereka tidak akan dianiaya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=391">391</a><span>]</span></p><p><span>[b] </span><span>3:10; 4:88; 45:27</span></p>');
INSERT INTO `quran` VALUES (6669,2,3,27,'<p><span>Katakanlah, Ya </span><span>[c] </span>Allah<span>, Pemilik kerajaan, Engkau memberikan kerajaan kepada siapa yang Engkau kehendaki, dan Engkau mencabut kerajaan dari siapa yang Engkau kehendaki. Dan, Engkau memuliakan siapa yang Engkau kehendaki, dan Engkau merendahkan siapa yang Engkau kehendaki. Di tangan Engkau-lah segala kebaikan. Sesungguhnya, Engkau Maha Kuasa atas segala susuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=392">392</a><span>]</span></p><p><span>[c] </span><span>2:285; 5:19, 41; 35:14; 40:17; 48:15.</span></p>');
INSERT INTO `quran` VALUES (6670,2,3,28,'<p><span>[a] </span><span>”Engkau memasukkan malam ke dalam siang dan Engkau memasukkan siang ke dalam malam. </span><a>[393</a><span>]</span><span> dan, </span><span>[b] </span><span>Engkau mengeluarkan yang hidup dari yang mati dan Engkau mengeluarkan yang mati dari yang hidup. Dan, Engkau memberi rezeki kepada siapa yang Engkau kehendaki tanpa perhitungan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=394">394</a><span>]</span></p><p><span>[a] </span><span>7:55; 13:4; 22:62; 35:14; 39:6; 57:7.</span></p><p><span>[b] </span><span>6:96, 10:32; 30:20.</span></p>');
INSERT INTO `quran` VALUES (6671,2,3,29,'<p><span>[c] </span><span>Janganlah orang-orang mukmin mengambil orang-orang kafir menjadi sahabat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=395">395</a><span>]</span><span> dengan mengesam-pingkan orang-orang mukmin, dan barangsiapa berbuat demikian, maka tak ada </span><span>hubungan</span><span> dengan </span>Allah <span>sedikit pun, kecuali bila kamu menjaga diri dari mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=396">396</a><span>]</span><span> dengan suatu penjagaan sebaik-baiknya, Dan, </span>Allah <span>memperingatkan kamu terhadap hukuman-Nya; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=397">397</a><span>]</span><span> dan kepada </span>Allah <span>kamu</span><span> akan kembali.</span></p><p><span>[c] </span><span>3:119; 4:140, 145.</span></p>');
INSERT INTO `quran` VALUES (6672,2,3,30,'<p><span>Katakanlah, </span><span>[a] </span><span>”Baik kamu sembunyikan apa yang ada di dalam dadamu atau pun kamu menzahirkannya, </span><span>niscaya</span><span> </span>Allah <span>mengetahuinya; dan Dia mengetahui apa yang ada di seluruh langit dan apa yang ada di bumi. Dan, </span>Allah <span>Maha Kuasa atas segala sesuatu.”</span></p><p><span>[a] </span><span>27:75; 28:70</span></p>');
INSERT INTO `quran` VALUES (6673,2,3,31,'<p><span>Waspadalah terhadap </span><span> Hari </span><span>[a] </span><span>ketika setiap orang akan mendapati dihadapannya segala kebaikan yang telah dikerjakan dan segala kejahatan yang telah dikerjakannya, Ia menginginkan, alangkah baiknya jika di antara dia dan </span><span>kejahatan</span><span> itu ada jarak jauh. Dan, </span>Allah <span>Maya Penyantun terhadap hamba- hamba-</span><span>Nya</span><span>.</span></p><p><span>[a] </span><span>18:50</span></p>');
INSERT INTO `quran` VALUES (6674,2,3,32,'<p><span>Katakanlah, </span><span>[b] </span><span>”Jika kamu mencintai </span>Allah<span>, maka ikutilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=398">398</a><span>]</span><span> aku, </span><span>kemudian</span><span> </span>Allah <span>akan mencintai dan akan mengampuni dosa-dosamu. Dan, </span>Allah <span>Maha Pengampun, Maha Penyayang.”</span></p><p><span>[b] </span><span>4:70,</span></p>');
INSERT INTO `quran` VALUES (6675,2,3,33,'<p><span>Katakanlah, </span><span>[c] </span><span>”Taatilah </span>Allah <span>dan Rasul ini, kemudian, jika mereka berpaling, maka sesungguhnya </span>Allah <span>tidak mencintai orang-orang kafir.</span></p><p><span>[c] </span><span>4:60; 5:93; 8:47; 24:55; 58:14.</span></p>');
INSERT INTO `quran` VALUES (6676,2,3,34,'<p><span>Sesungguhnya, </span>Allah <span>telah memilih Adam dan Nuh dan keluarga Ibrahim dan keluarga Ibrahim dan keluarga </span><span>‘</span><span>Imran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=399">399</a><span>]</span><span> di atas seluruh alam </span><span>pada zamannya</span><span>.</span></p>');
INSERT INTO `quran` VALUES (6677,2,3,35,'<p><span>[a] </span><span>Mereka adalah </span><span> keturunan yang sebagiannya dari sebagian yang lain. Dan, </span>Allah <span>Maha mendengar, Maha mengetahui.</span></p><p><span>[a] </span><span>6:88; 19:59.</span></p>');
INSERT INTO `quran` VALUES (6678,2,3,36,'<p><span>Ingatlah</span><span> ketika perempuan </span><span>‘</span><span>Imran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=400">400</a><span>]</span><span> berkata, “Ya Tuhan-ku sesungguhnya aku telah menazarkan kepada Engkau apa yang ada dalam kandunganku untuk berkhidmat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=401">401</a><span>]</span><span> Maka, terimalah itu dari aku; sesungguhnya, hanya Engkau-lah Yang Maha Mendengar, Maha Mengetahui.”</span></p>');
INSERT INTO `quran` VALUES (6679,2,3,37,'<p><span>Maka tatkala ia telah melahirkannya, berkatalah ia, “Ya Tuhan-ku, sesungguhnya yang kulahirkan itu seorang perempuan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=402">402</a><span>]</span><span> Dan </span>Allah <span>lebih mengetahui apa yang dilahirkannya. </span><a>[402a</a><span>]</span><span> Dan anak lelaki itu tidaklah sama seperti anak perempuan, dan bahwa aku menamainya Maryam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=402b">402b</a><span>]</span><span> dan aku memohonkan dia dan keturunannya dalam perlindungan Engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=402c">402c</a><span>]</span><span> dari syaitan yang terkutuk.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=402d">402d</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6680,2,3,38,'<p><span>Maka Tuhannya telah menerimanya dengan penerimaan yang baik, dan menumbuhkannya dengan pertumbuhan yang baik dan menyerahkan pemeliharaannya kepada Zakaria. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=403">403</a><span>]</span><span> Setiap kali Zakaria datang menemuinya di mihrab didapatinya ada rezeki padanya. Berkatalah ia, “Hai Maryam, dari manakah engkau dapat ini ?” Jawabnya, “Itu dari sisi </span>Allah<span>.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=404">404</a><span>]</span><span> Sesungguhnya </span>Allah <span>memberi rezeki kepada siapa yang Dia kehendaki tanpa perhitungan.</span></p>');
INSERT INTO `quran` VALUES (6681,2,3,39,'<p><span>Di sanalah Zakaria berdoa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=405">405</a><span>]</span><span> kepada Tuhan-nya, dia berkata, </span><span>[a] </span><span>”Ya Tuhan-ku, anugerahilah aku dari sisi Engkau keturunan yang suci; sesungguhnya, Engkau Maha Mendengar doa.”</span></p><p><span>[a] </span><span>19:6, 7; 2190, 91.</span></p>');
INSERT INTO `quran` VALUES (6682,2,3,40,'<p><span>Maka malaikat menyerunya ketika ia </span><span>[b] </span><span>sedang berdiri shalat di tempat yang baik di </span><span>rumah,</span><span> “Sesungguhnya </span><span>[c] </span>Allah <span>memberi kabar suka tentang Yahya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=406">406</a><span>]</span><span> yang akan menggenapi </span><span>[d] </span><span>kalimat dari </span>Allah <span>, dan ia seorang pemimpin, pengekang </span><span>hawa nafsu</span><span>, dan seorang nabi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=407">407</a><span>]</span><span> dari antara orang-orang saleh.”</span></p><p><span>[b] </span><span>19:12.</span></p><p><span>[c] </span><span>19:8; 21:91.</span></p><p><span>[d] </span><span>3:46; 4:172.</span></p>');
INSERT INTO `quran` VALUES (6683,2,3,41,'<p><span>Berkata ia, </span><span>[a] </span><span>”Ya Tuhan ku, bagaimanakah aku akan mendapat anak laki-laki, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=408">408</a><span>]</span><span> sedang masa tua telah menjelangku dan istriku mandul ?” Dia berfirman, “Demikianlah </span><span>kekuasaan </span>Allah<span>, Dia berbuat apa yang Dia kehendaki.”</span></p><p><span>[a] </span><span>19:9, 10</span></p>');
INSERT INTO `quran` VALUES (6684,2,3,42,'<p><span>[b] </span><span>Berkata ia, “Ya Tuhan ku, berikanlah kepadaku suatu Tanda. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=409">409</a><span>]</span><span> Dia berfirman, “Tanda bagi engkau ialah, engkau tidak boleh berbicara dengan manusia selama tiga hari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=410">410</a><span>]</span><span> </span><span>[c] </span><span>kecuali dengan isyarat. Dan, berzikirlah kepada Tuhan engkau sebanyak-banyaknya dan bertasbihlah petang dan pagi.”</span></p><p><span>[b] </span><span>19:11.</span></p><p><span>[c] </span><span>19:12.</span></p>');
INSERT INTO `quran` VALUES (6685,2,3,43,'<p><span>Dan, </span><span>ingatlah</span><span>, ketika malaikat-malaikat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=411">411</a><span>]</span><span> berkata, “Hai Maryam, sesungguhnya </span>Allah <span>telah memilih </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=412">412</a><span>]</span><span> engkau dan mensucikan engkau, dan telah </span><span>[a] </span><span>memilih engkau di atas wanita-wanita semesta alam. “</span></p><p><span>[a] </span><span>3:34.</span></p>');
INSERT INTO `quran` VALUES (6686,2,3,44,'<p><span>“Hai Maryam, patuhilah Tuhan engkau dan sujudlah dan ruku</span><span>’</span><span>lah </span><span>kepada</span><span> </span><span>Tuhan</span><span> bersama orang- orang yang ruku</span><span>’</span><span>.”</span></p>');
INSERT INTO `quran` VALUES (6687,2,3,45,'<p><span>[a] </span><span>Yang demikian itu sebagian dari kabar-kabar gaib </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=413">413</a><span>]</span><span> yang Kami wahyukan kepada engkau. Dan, engkau tidak bersama mereka ketika mereka melemparkan panah-panah mereka </span><span>untuk mengundi</span><span> </span><span>[b] </span><span>siapakah di antara mereka yang akan memelihara Maryam, dan tidak pula engkau bersama mereka ketika mereka berbantah.</span></p><p><span>[a] </span><span>11:50; 12:103.</span></p><p><span>[b] </span><span>3:38.</span></p>');
INSERT INTO `quran` VALUES (6688,2,3,46,'<p><span>Ketika berkata malaikat-malaikat, “Hai Maryam, sesungguhnya </span><span>[c] </span><span>Allah memberi engkau kabar suka dengan </span><span>[d] </span><span>satu kalimat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=414">414</a><span>]</span><span> dari-Nya </span><span>tentang seorang anak laki-laki;</span><span> namanya Al-Masih </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=415">415</a><span>]</span><span> Isa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=416">416</a><span>]</span><span> Ibnu Maryam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=417">417</a><span>]</span><span> yang dimuliakan di dunia dan di akhirat, dan ia adalah dari antara orang-orang dekat </span><span>kepada Allah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=418">418</a><span>]</span></p><p><span>[c] </span><span>19:30.</span></p><p><span>[d] </span><span>3:10; 4:172</span></p>');
INSERT INTO `quran` VALUES (6689,2,3,47,'<p><span>Dan, </span><span>[a] </span><span>ia akan berkata-kata dengan manusia dalam buian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=418a">418a</a><span>]</span><span> dan ketika sudah setengah umur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=418b">418b</a><span>]</span><span> dan ia dari antara orang-orang saleh.</span></p><p><span>[a]</span><span> 5:111.</span></p>');
INSERT INTO `quran` VALUES (6690,2,3,48,'<p><span>Berkata dia, “Ya Tuhan ku, </span><span>[b] </span><span>bagaimanakah aku akan mempunyai anak laki-laki padahal belum pernah aku disentuh seorang laki-laki ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=419">419</a><span>]</span><span> Dia berfirman, “Demikianlah </span><span>kekuasaan</span><span> </span>Allah<span>, Dia menciptakan apa yang Dia kehendaki. </span><span>[a] </span><span>Apabila Dia memutuskan sesuatu hal, maka Dia berfirman tentang itu, `Jadilah ! maka jadilah itu.”</span></p><p><span>[a] </span><span>Lihat 2:118.</span></p><p><span>[b] </span><span>19:21.</span></p>');
INSERT INTO `quran` VALUES (6691,2,3,49,'<p><span>“Dan </span><span>[a] </span><span>Dia akan mengajarkan kepadanya Alkitab, Hikmah, Taurat dan Injil”</span></p><p><span>[a] </span><span>5:111.</span></p>');
INSERT INTO `quran` VALUES (6692,2,3,50,'<p><span>[b] </span><span>”Dan </span><span>sebagai</span><span> rasul kepada Bani Israil, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=419a">419a</a><span>]</span><span> </span><span>dengan pesan,</span><span> `Sesungguhnya </span><span>[c] </span><span>aku datang kepadamu membawa Tanda dari Tuhan-mu, ialah, aku menciptakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=420">420</a><span>]</span><span> untukmu </span><span>suatu makhluk</span><span> yang bersifat tanah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=420a">420a</a><span>]</span><span> seperti bentuk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=420b">420b</a><span>]</span><span> burung, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=420c">420c</a><span>]</span><span> </span><span>kemudian aku tiupkan ke dalamnya </span><span>jiwa baru</span><span>, maka jadilah ia burung dengan izin </span>Allah<span>; dan aku menyembuhkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=420d">420d</a><span>]</span><span> orang buta </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=420e">420e</a><span>]</span><span> dan orang kusta, dan aku menghidupkan orang mati, sengan izin Allah; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=420f">420f</a><span>]</span><span> dan aku akan memberitahukan kepadamu tentang apa-apa yang kamu makan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=420g">420g</a><span>]</span><span> dan apa-apa yang kamu simpan di rumah-rumahmu. Sesungguhnya dalam hal ini ada suatu Tanda bagimu, jika kamu orang-orang mukmin.</span></p><p><span>[b] </span><span>43:60; 61:7.</span></p><p><span>[c] </span><span>5:111.</span></p>');
INSERT INTO `quran` VALUES (6693,2,3,51,'<p><span>“Dan aku </span><span>datang</span><span> untuk </span><span>[a] </span><span>menggenapi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=421">421</a><span>]</span><span> apa yang </span><span>telah ada</span><span> sebelumku, yakni Taurat; dan aku menghalalkan bagimu sebagian dari yang telah diharamkan atasmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=421a">421a</a><span>]</span><span> dan aku datang kepadamu </span><span>membawa suatu Tanda dari Tuhan mu. Maka, bertakwalah kepada </span>Allah <span>dan taatilah aku.</span></p><p><span>[a] </span><span>43:60; 61:7.</span></p>');
INSERT INTO `quran` VALUES (6694,2,3,52,'<p><span>“Sesungguhnya, </span><span>[b] </span>Allah <span>itu</span><span> Tuhan-ku dan Tuhan-mu; maka sembahlah Dia; inilah jalan yang lurus.”</span></p><p><span>[b] </span><span>5:73, 118, 19:37; 43:65.</span></p>');
INSERT INTO `quran` VALUES (6695,2,3,53,'<p><span>Maka ketika Isa menyadari adanya kekufuran pada mereka, berkatalah ia, </span><span>[a] </span><span>”Siapakah yang akan menjadi penolong-penolongku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=422">422</a><span>]</span><span> di jalan </span>Allah <span>?” Berkata para hawari, “Kamilah penolong-penolong </span>Allah<span>. Kami beriman kepada </span>Allah<span>. dan, saksikanlah bahwa kami adalah orang-orang yang taat.”</span></p><p><span>[a] </span><span>5:112; 61:15.</span></p>');
INSERT INTO `quran` VALUES (6696,2,3,54,'<p><span>“Ya Tuhan kami, kami beriman kepada apa yang telah Engkau turunkan dan kami mengikuti Rasul ini; maka catatlah kami bersama orang-orang yang menjadi saksi.”</span></p>');
INSERT INTO `quran` VALUES (6697,2,3,55,'<p><span>Dan mereka, </span><span>[b] </span><span>yakni </span><span>musuh Al-Masih</span><span>, membuat rencana dan </span>Allah <span>pun</span><span> membuat rencana; dan </span>Allah <span>adalah sebaik-baik Perencana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=423">423</a><span>]</span></p><p><span>[b] </span><span>8:31; 27:51.</span></p>');
INSERT INTO `quran` VALUES (6698,2,3,56,'<p><span>Ingatlah</span><span> ketika </span>Allah <span>berfirman, “Hal Isa, sesungguhnya </span><span>[a] </span><span>Aku akan mematikan engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=424">424</a><span>]</span><span> </span><span>secara wajar</span><span> dan </span><span>[b] </span><span>akan meninggikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=424a">424a</a><span>]</span><span> derajat engkau di sisi-Ku dan akan membersihkan engkau dari </span><span>tuduhan</span><span> orang-orang yang ingkar dan akan menjadikan orang-orang yang mengikut engkau di atas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=424b">424b</a><span>]</span><span> orang-orang yang ingkar hingga Hari Kiamat; </span><span>[c] </span><span>kemudian kepada Aku-lah kamu kembali, lalu Aku akan menghakimi di antaramu tentang apa yang kamu perselisikan.”</span></p><p><span>[a] </span><span>3:194; 4:16; 7:127; 8:51; 10:47, 105; 12:102; 13:41; 16:29, 33; 22:6; 39:43; 40:68, 78; 47:28.</span></p><p><span>[b] </span><span>4:159; 7:177; 19:58.</span></p><p><span>[c] </span><span>5:49; 6:165; 11:24; 31:16; 39:8.</span></p>');
INSERT INTO `quran` VALUES (6699,2,3,57,'<p><span>“Adapun orang-orang yang ingkar, akan Aku azab mereka dengan azab yang keras di dunia dan di akhirat, dan tiada bagi mereka seorang penolong pun.”</span></p>');
INSERT INTO `quran` VALUES (6700,2,3,58,'<p><span>“Adapun orang-orang yang beriman dan beramal saleh. maka </span><span>[a] </span><span>Dia akan memberikan penuh kepada mereka ganjaran mereka. Dan Allah tidak mencintai orang-orang aniaya.”</span></p><p><span>[a] </span><span>4:174; 35:31; 39:11,70.</span></p>');
INSERT INTO `quran` VALUES (6701,2,3,59,'<p><span>Demikianlah Kami membacakannya kepada engkau sebagian dari Tanda-tanda dan Al-Zikr yang penuh hikmah.</span></p>');
INSERT INTO `quran` VALUES (6702,2,3,60,'<p><span>Sesungguhnya misal Isa di sisi </span>Allah <span>adalah seperti misal Adam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=425">425</a><span>]</span><span> Dia menjadikannya dari debu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=425a">425a</a><span>] </span><span>kemudian Dia berfirman kepadanya, “Jadilah !” , maka jadilah ia.</span></p>');
INSERT INTO `quran` VALUES (6703,2,3,61,'<p><span>[a] </span><span>Ini adalah</span><span> hak dari Tuhan engkau, maka janganlah engkau termasuk orang-orang yang ragu.</span></p><p><span>[a] </span><span>2:148 ; 6:115, 10:95.</span></p>');
INSERT INTO `quran` VALUES (6704,2,3,62,'<p><span>Maka, barangsiapa berbantah dengan engkau tentang dia setelah datang kepada engkau ilmu, maka katakalah, “Marilah kita memanggil anak-anak laki-laki kami dan anak-anak laki-laki kamu dan perempuan-perempuan kami, dan perempuan-perempuan kamu dan orang-orang kami dan orang-orang kamu; kemudian kita </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=426">426</a><span>]</span><span> </span><span>[b] </span><span>berdoa supaya laknat Allah ditimpakan atas orang-orang yang berdusta.”</span></p><p><span>[b] </span><span>62:7, 8.</span></p>');
INSERT INTO `quran` VALUES (6705,2,3,63,'<p><span>Sesungguhnya ini adalah kisah yang benar. Dan tak ada Tuhan selain </span>Allah<span>; dan sesungguhnya </span>Allah<span>, Dia-lah Yang Maha Perkasa, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (6706,2,3,64,'<p><span>Dan, jika mereka berpaling, maka </span><span>ingatlah bahwa </span><span>sesungguhnya </span>Allah <span>Maha Mengetahui perusuh-perusuh.</span></p>');
INSERT INTO `quran` VALUES (6707,2,3,65,'<p><span>Katakanlah, “Hai Ahli-kitab, marilah kepada satu kalimat yang sama di antara kami dan kamu, bahwa kita tidak menyembah kecuali kepada </span>Allah<span>, dan tidak pula kita mempersekutukan-Nya dengan sesuatu apa pun, dan </span><span>[a] </span><span>sebagian yang lain sebagai Tuhan selalin </span>Allah<span>.” Tetapi, jika mereka berpaling, maka katakanlah, “Jadilah saksi bahwa kami orang-orang yang menyerahkan diri </span><span>kepada Tuhan”</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=426a">426a</a><span>]</span></p><p><span>[a] </span><span>9:31</span></p>');
INSERT INTO `quran` VALUES (6708,2,3,66,'<p><span>Hai Ahlikitab, </span><span>[b] </span><span>mengapa kamu berbantah mengenai Ibrahim, padahal tidaklah diturunkan Taurat dan Injil melainkan sesudahnya ? Maka, tidaklah kamu mau mengerti ?</span></p><p><span>[b] </span><span>2:140.</span></p>');
INSERT INTO `quran` VALUES (6709,2,3,67,'<p><span>Perhatikanlah, kamu adalah orang-orang yang telah berbantah mengenai apa yang kamu mempunyai </span><span>sedikit</span><span> pengetahuan. Maka, mengapa kamu berbantah pula mengenai apa yang kamu tidak mempunyai pengetahuan </span><span>sama sekali ?</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=427">427</a><span>] </span><span>Sedangkan </span>Allah <span>mengetahui dan kamu tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (6710,2,3,68,'<p><span>Ibrahim bukan seorang Yahudi dan </span><span>[a] </span><span>bukan pula seorang Nasrani, akan tetapi dia seorang yang selalu </span><span>[b] </span><span>cenderung </span><span>kepada Tuhan</span><span> dan menyerahkan diri </span><span>kepada-Nya</span><span>, dan bukan dari antara orang-orang musyrik.</span></p><p><span>[a] </span><span>2:141.</span></p><p><span>[b] </span><span>3:96; 4:126; 6:162; 16:121, 124.</span></p>');
INSERT INTO `quran` VALUES (6711,2,3,69,'<p><span>Sesungguhnya, manusia yang paling dekat kepada Ibrahim adalah orang-orang yang mengikutinya, dan </span><span>[c] </span><span>Nabi ini dan orang-orang yang beriman </span><span>kepadanya</span><span>. Dan </span>Allah <span>adalah sahabat orang-orang mukmin.</span></p><p><span>[c] </span><span>16:124.</span></p>');
INSERT INTO `quran` VALUES (6712,2,3,70,'<p><span>[d] </span><span>Segolongan dari Ahlikitab menginginkan agar mereka menyesatkan kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=427a">427a</a><span>]</span><span> tetapi, mereka tidak menyesatkan kecuali diri mereka sendiri, namun mereka tidak menyadari.</span></p><p><span>[d] </span><span>4:90.</span></p>');
INSERT INTO `quran` VALUES (6713,2,3,71,'<p><span>[e] </span><span>Hai Ahlikitab, mengapa kalian mengingkari ayat-ayat </span>Allah<span>, padahal kalian menyaksikan? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=427b">427b</a><span>]</span></p><p><span>[e] </span><span>3:99.</span></p>');
INSERT INTO `quran` VALUES (6714,2,3,72,'<p><span>Hai Ahlikitab, </span><span>[a] </span><span>mengapa kamu mencampur-adukan yang hak dengan yang batil dan </span><span>[b] </span><span>menyembunyikan yang hak padahal kalian mengetahui ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=427c">427c</a><span>]</span></p><p><span>[a] </span><span>2:43.</span></p><p><span>[a] </span><span>Lihat 2:43.</span></p>');
INSERT INTO `quran` VALUES (6715,2,3,73,'<p><span>Dan segolongan Ahlikitab berkata, “Percayalah kepada apa yang diturunkan kepada orang-orang yang beriman di waktu permulaan hari dan ingkarlah di waktu akhirnya, barangkali mereka akan kembali.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=428">428</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6716,2,3,74,'<p><span>“Dan </span><span>[a] </span><span>janganlah kamu percaya kecuali kepada orang yang mengikuti agamamu.” Katakanlah “Sesungguhnya petunjuk </span><span>yang benar</span><span> ialah petunjuk dari </span>Allah<span>, bahwa seseorang diberi seperti apa yang telah di berikan kepadamu, atau </span><span>[a] </span><span>mereka akan bertengkar dengan kamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=428a">428a</a><span>]</span><span> dihadapan Tuhan-mu. “Katakanlah, “Sesungguhnya karunia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=428b">428b</a><span>]</span><span> itu </span><span>[c] </span><span>di tangan </span>Allah<span>. dia memberikannya kepada siapa yang Dia kehendaki, dan </span>Allah <span>Mahaluas </span><span>pemberiannya</span><span>, Maha Mengetahui.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=428c">428c</a><span>]</span></p><p><span>[a] </span><span>2:121.</span></p><p><span>[b] </span><span>2:77.</span></p><p><span>[c] </span><span>57:30.</span></p>');
INSERT INTO `quran` VALUES (6717,2,3,75,'<p><span>“Dia </span><span>[a] </span><span>mengkhususkan rahmat-Nya bagi siapa yang Dia kehendaki. Dan,</span>Allah<span> adalah Yang Empunya karunia yang sangat besar.”</span></p><p><span>[a] </span><span>2:106.</span></p>');
INSERT INTO `quran` VALUES (6718,2,3,76,'<p><span>Dan, di antara para Ahlikitab ada orang yang jika engkau mengamatkan kepadanya setumpuk </span><span>harta</span><span>, niscaya akan dikembalikan kepada engkau; dan, di antara mereka ada </span><span>pula</span><span> orang yang jika engkau mengamanatkan kepadanya satu dinar, tidak akan dikembalikannya kepada engkau, kecuali jika engkau tetap berdiri </span><span>menagih</span><span> atasnya. Hal demikian itu disebabkan mereka berkata, “Tak ada tuntutan atas kami mengenai orang-orang ummi.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=429">429</a><span>]</span><span> Dan mereka berkata dusta terhadap Allah pada hal mereka mengetahui.</span></p>');
INSERT INTO `quran` VALUES (6719,2,3,77,'<p><span>Tidak, bahkan </span><span>[b] </span><span>barangsiapa memenuhi janjinya dan bertakwa, maka sesungguhnya </span>Allah <span>mencintai orang-orang yang bertakwa.</span></p><p><span>[b] </span><span>5:2; 6:153; 13:21; 16:92; 17:35.</span></p>');
INSERT INTO `quran` VALUES (6720,2,3,78,'<p><span>Sesungguhnya, orang-orang yang menukar janji </span><span>mereka</span><span> kepada </span>Allah <span>dan sumpah-sumpah mereka dengan </span><span>[a] </span><span>harga yang rendah, mereka inilah yang tiada satu bagian pun bagi mereka di akhirat dan </span><span>[b]</span><span> </span>Allah <span>tidak akan bercakap-cakap dengan mereka dan tidak akan memandang mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=430">430</a><span>]</span><span> pada Hari Kiamat, dan tidak pula akan mensucikan mereka; dan bagi mereka ada azab yang pedih.</span></p><p><span>[a] </span><span>Lihat 2:42.</span></p><p><span>[b] </span><span>2:175; 23:109</span></p>');
INSERT INTO `quran` VALUES (6721,2,3,79,'<p><span>Dan, sesunguhnya di kalangan mereka ada segolongan </span><span>[c] </span><span>yang memutar lidahnya dari Alkitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=431">431</a><span>]</span><span> supaya kamu menyangka </span><span>hal</span><span> itu dari Alkitab, padahal itu bukan dari Alkitab. Dan, mereka mengatakan, “Itu adalah dari </span>Allah<span>,” padahal itu bukan dari </span>Allah<span>, dan mereka berkata dusta terhadap </span>Allah<span>, padahal mereka mengetahui.</span></p><p><span>[c] </span><span>2:76; 3:47; 5:42.</span></p>');
INSERT INTO `quran` VALUES (6722,2,3,80,'<p><span>Tidak layak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=432">432</a><span>]</span><span> bagi seorang manusia yang kepadanya </span>Allah <span>memberi Alkitab dan kekuasaan serta kenabian, dan kemudian ia </span><span>[a] </span><span>berkata kepada manusia, “Jadilah hamba-hambaku dan bukan hamba-hamba </span>Allah<span>;” bahkan </span><span>hendaklah ia berkata,</span><span> “Jadilah kamu orang berkhidmat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=432a">432a</a><span>]</span><span> hanya kepada Tuhan, karena kamu senantiasa mengajarkan Alkitab dan senantiasa mempelajari</span><span>nya.”</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=432b">432b</a><span>]</span></p><p><span>[a] </span><span>5:117, 118.</span></p>');
INSERT INTO `quran` VALUES (6723,2,3,81,'<p><span>Dan tidak </span><span>pula</span><span> ia menyuruhmu supaya kamu mengambil malaikat-malaikat dan nabi-nabi menjadi tuhan-tuhan. Adakah ia akan menyuruhmu menjadi kufur setelah kamu menjadi muslim ?</span></p>');
INSERT INTO `quran` VALUES (6724,2,3,82,'<p><span>Dan </span><span>ingatlah</span><span> </span><span>[a] </span><span>ketika </span>Allah <span>mengambil perjanjian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=433">433</a><span>]</span><span> </span><span>dari manusia melalui </span><span> nabi-nabi, “Apa saja yang Aku berikan kepadamu berupa Kitab dan Hikmah </span><span>dan</span><span> kemudian datang kepadamu seorang rasul yang menggenapi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=433a">433a</a><span>]</span><span> apa yang ada padamu, maka haruslah kamu beriman kepadanya dan haruslah kamu membantunya.” Dia berfirman, “Adakah kamu mengakui dan menerima tanggung-jawab yang </span><span>Aku bebankan </span><span> kepadamu mengenai itu ?” Mereka menjawab, “Kami mengakui.” Dia berfirman, “Maka kamu hendaknya menjadi saksi dan Aku pun besertamu termasuk orang-orang yang menjadi saksi.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=433b">433b</a><span>]</span></p><p><span>[a] </span><span>5:13.</span></p>');
INSERT INTO `quran` VALUES (6725,2,3,83,'<p><span>Dan </span><span>[a] </span><span>barangsiapa berpaling sesudah itu, maka merekalah orang-orang fasik.</span></p><p><span>[a] </span><span>5:48; 24:56.</span></p>');
INSERT INTO `quran` VALUES (6726,2,3,84,'<p><span>Adakah mereka mencari suatu agama selain agama </span>Allah <span>padahal kepada Dia-lah tunduk segala yang ada di seluruh langit dan bumi, dengan rela atau terpaksa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=434">434</a><span>]</span><span> dan kepada-Nya mereka akan dikembali kan ?</span></p>');
INSERT INTO `quran` VALUES (6727,2,3,85,'<p><span>Katakanlah, </span><span>[b] </span><span>”Kami beriman kepada </span>Allah <span>dan kepada apa yang diturunkan kepada kami dan kepada apa yang diturunkan kepada Ibrahim dan Ismail dan Ishak dan Ya</span><span>’</span><span>kub dan keturunannya dan kepada apa yang diberikan kepada Musa dan Isa dan sekalian nabi dari Tuhan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=435">435</a><span>]</span><span> Kami tidak membedakan salah seorang di antara mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=435a">435a</a><span>]</span><span> dan kepada-Nya kami menyerahkan diri.”</span></p><p><span>[b] </span><span>2:137, 286.</span></p>');
INSERT INTO `quran` VALUES (6728,2,3,86,'<p><span>Dan, </span><span>[a] </span><span>barangsiapa mencari agama selain Islam, maka tidak akan diterima darinya, dan ia di akhirat termasuk orang-orang rugi.</span></p><p><span>[a] </span><span>3:20; 5:4.</span></p>');
INSERT INTO `quran` VALUES (6729,2,3,87,'<p><span>Bagaimanakah </span>Allah <span>akan memberi petunjuk kepada suatu kaum yang ingkar setelah mereka beriman, dan mereka telah menjadi saksi bahwa rasul itu benar, dan telah datang kepada mereka dalil-dalil yang nyata ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=436">436</a><span>]</span><span> Dan </span>Allah <span>tidak memberi petunjuk kepada kaum aniaya.</span></p>');
INSERT INTO `quran` VALUES (6730,2,3,88,'<p><span>Mereka itu </span><span>orang-orang</span><span> yang balasan mereka, bahwa atas mereka ada </span><span>[a] </span><span>laknat </span>Allah<span>, malaikat dan manusa seluruhnya.</span></p><p><span>[a] </span><span>2:162; 4:53; 5:79.</span></p>');
INSERT INTO `quran` VALUES (6731,2,3,89,'<p><span>[b] </span><span>Mereka akan tinggal lama di dalamnya. Tidak akan diringankan azab mereka dan tidak pula mereka akan diberi tangguh.</span></p><p><span>[b] </span><span>2:163.</span></p>');
INSERT INTO `quran` VALUES (6732,2,3,90,'<p><span>[c] </span><span>Kecuali orang-orang yang bertaubat setelah itu dan memperbaiki </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=436a">436a</a><span>]</span><span> </span><span>diri</span><span>. Maka, sesungguhnya </span>Allah <span> Maha Pengampun, Maha Penyayang.</span></p><p><span>[c] </span><span>2:161; 4:147; 5:40; 24:6.</span></p>');
INSERT INTO `quran` VALUES (6733,2,3,91,'<p><span>[d] </span><span>Sesungguhnya, orang-orang yang ingkar sesudah mereka beriman, kemudian mereka bertambah kekufuran mereka, sekali-kali tidak akan diterima tobat mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=437">437</a><span>]</span><span> dan mereka itulah orang-orang sesat.</span></p><p><span>[d] </span><span>4:138; 63:4.</span></p>');
INSERT INTO `quran` VALUES (6734,2,3,92,'<p><span>Sesungguhnya </span><span>[a] </span><span>orang-orang yang ingkar dan mati dalam keadaan kufur, maka sekali-kali tidak akan diterima dari seorangpun dari mereka </span><span>sekalipun</span><span> emas sepenuh bumi, walaupun ia menebus dirinya dengan itu. Itulah orang-orang yang bagi mereka ada azab yang pedih, dan tiada bagi mereka penolong-penolong.</span></p><p><span>[a] </span><span>2:162; 4:19; 47:35.</span></p>');
INSERT INTO `quran` VALUES (6735,2,3,93,'<p><span>[b] </span><span>Sekali-kali kamu tidak akan mencapai kebaikan </span><span>yang sempurna</span><span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=438">438</a><span>]</span><span> sebelum kamu membelanjakan sebagian dari apa yang kamu cintai; dan apa pun yang kamu belanjakan, maka sesungguhnya tentang itu Allah Maha Mengetahui.</span></p><p><span>[b] </span><span>9:34, 111; 63:11.</span></p>');
INSERT INTO `quran` VALUES (6736,2,3,94,'<p><span>Segala makanan dahulunya halal </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=439">439</a><span>]</span><span> bagi Bani Israil, kecuali apa yang diharamkan oleh Israil, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=440">440</a><span>]</span><span> atas dirinya sebelum Taurat diturunkan. Katakanlah, “Bawalah Taurat, maka bacalah itu, jika kamu orang-orang benar.”</span></p>');
INSERT INTO `quran` VALUES (6737,2,3,95,'<p><span>Dan, barangsiapa mengada-ada kedustaan terhadap Allah sesusah itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=441">441</a><span>]</span><span> maka mereka itulah orang- orang aniaya.</span></p>');
INSERT INTO `quran` VALUES (6738,2,3,96,'<p><span>Katakanlah, “Allah telah menyatakan yang benar; maka, ikutilah agama Ibrahim </span><span>[a] </span><span>yang selalu cenderung </span><span>kepada Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=442">442</a><span>]</span><span> dan ia bukanlah dari orang-orang musyrik.”</span></p><p><span>[a] </span><span>Lihat 3:68.</span></p>');
INSERT INTO `quran` VALUES (6739,2,3,97,'<p><span>Sesungguhnya </span><span>[a] </span><span>Rumah pertama yang didirikan untuk manusia, ialah yang </span><span>ada</span><span> di Bakkah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=443">443</a><span>]</span><span> yang penuh dengan berkat dan petunjuk bagi seluruh alam.</span></p><p><span>[a] </span><span>4:98; 27:92; 28:58; 29:68; 106:4,5.</span></p>');
INSERT INTO `quran` VALUES (6740,2,3,98,'<p><span>Di dalamnya ada Tanda-tanda yang nyata; </span><span>[b] </span><span>tempat berdiri Ibrahim; dan </span><span>[c] </span><span>barangsiapa memasukinya maka amanlah ia. Dan, </span><span>[d] </span><span>berziarah ke Rumah itu </span><span>merupakan kewajiban</span><span> atas menusia karena Allah,</span><span>bagi</span><span> orang-orang yang mampu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=444">444</a><span>]</span><span> menempuh jalan ke sana. Dan, barangsiapa ingkar, maka sesungguhnya Allah Maha Kaya, dari sekalian alam.</span></p><p><span>[b] </span><span>2:126.</span></p><p><span>[c] </span><span>14:36; 28:58; 29:68.</span></p><p><span>[d] </span><span>22:28.</span></p>');
INSERT INTO `quran` VALUES (6741,2,3,99,'<p><span>Katakanlah, </span><span>[a] </span><span>”Hai Ahlikitab, mengapa kamu mengingkari Tanda-tanda Allah padahal Allah menjadi saksi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=445">445</a><span>]</span><span> atas apa yang kamu kerjakan?”</span></p><p><span>[a] </span><span>3:71.</span></p>');
INSERT INTO `quran` VALUES (6742,2,3,100,'<p><span>Katakanlah, “Hai Ahlikitab, </span><span>[b] </span><span>mengapa kamu menghalangi orang-orang beriman dari jalan Allah; kamu menghendakinya bengkok, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=446">446</a><span>]</span><span> padahal kamu menjadi saksi </span><span>tentang itu</span><span>. Dan, Allah tidak lengah terhadap apa yang kamu kerjakan.”</span></p><p><span>[b] </span><span>7:46, 87; 8:48; 9:34; 14:4; 22:26.</span></p>');
INSERT INTO `quran` VALUES (6743,2,3,101,'<p><span>[c] </span><span>Hai orang-orang yang beriman, jika kamu menuruti suatu golongan dari antara orang-orang yang diberi Alkitab, mereka akan mengembalikan lagi kamu setelah kamu beriman menjadi orang-orang kafir.</span></p><p><span>[c] </span><span>2:110; 3:150.</span></p>');
INSERT INTO `quran` VALUES (6744,2,3,102,'<p><span>Dan, bagaimana kamu akan ingkar padahal dibacakan kepadamu Ayat-ayat Allah, dan hadir di tengah-tengahmu Rasul-Nya ? </span><span>[a] </span><span>Dan barangsiapa berpegang teguh kepada Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=447">447</a><span>]</span><span> maka sesungguhnya ia diberi petunjuk kepada jalan yang lurus.</span></p><p><span>[a] </span><span>4:147, 176.</span></p>');
INSERT INTO `quran` VALUES (6745,2,3,103,'<p><span>Hai orang-orang yang beriman, bertakwalah kepada Allah dengan ketakwaan yang sebenarnya, dan </span><span>[b] </span><span>janganlah kamu mati kecuali dalam keadaan menyerahkan diri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=448">448</a><span>]</span></p><p><span>[b] </span><span>2:133.</span></p>');
INSERT INTO `quran` VALUES (6746,2,3,104,'<p><span>Dan, </span><span>[c] </span><span>berpegangteguhlah kamu sekalian kepada tali </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=449">449</a><span>]</span><span> Allah dan janganlah bercerai-berai dan </span><span>[d] </span><span>ingatlah akan nikmat Allah atasmu ketika kamu dahulu bermusuh- musuhan, lalu </span><span>[e] </span><span>Dia menyatukan hatimu dengan kecintaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=450">450</a><span>]</span><span> antara satu sama lain sehingga dengan nikmat-Nya kamu menjadi bersaudara; dan kamu dahulu berada di tepi jurang Api, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=451">451</a><span>]</span><span> kemudian Dia menyelamatkan kamu darinya. Demikianlah Allah menjelaskan Ayat-ayat-Nya kepadamu supaya kamu mendapat petunjuk.</span></p><p><span>[c] </span><span>3:106; 6:160; 8:47</span></p><p><span>[d] </span><span>2:232.</span></p><p><span>[e] </span><span>8:64.</span></p>');
INSERT INTO `quran` VALUES (6747,2,3,105,'<p><span>Dan hendaklah ada di antaramu segolongan </span><span>[a] </span><span>yang mengajak </span><span>manusia</span><span> kepada kebajikan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=452">452</a><span>]</span><span> dan menyuruh kepada kebaikan dan melarang terhadap keburukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=453">453</a><span>]</span><span> Dan mereka itulah orang-orang berjaya.</span></p><p><span>[a] </span><span>3:111, 115; 7:158; 9:711; 31:18.</span></p>');
INSERT INTO `quran` VALUES (6748,2,3,106,'<p><span>Dan, janganlah kamu menjadi seperti orang-orang yang </span><span>[a] </span><span>berpecah-belah dan berselisih </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=454">454</a><span>]</span><span> sesudah Tanda-tanda yang nyata datang kepada mereka. Dan, mereka itulah yang bagi mereka ada azab besar.</span></p><p><span>[a] </span><span>3:104; 6:160; 8:47.</span></p>');
INSERT INTO `quran` VALUES (6749,2,3,107,'<p><span>Pada hari </span><span>[b] </span><span>ketika </span><span>beberapa</span><span> muka akan menjadi putih, dan </span><span>beberapa</span><span> muka akan menjadi hitam. </span><a>[455</a><span>]</span><span> Ada pun orang-orang yang muka mereka akan menjadi hitam, </span><span>dikatakan kepada mereka</span><span>, “Adakah kamu ingkar sesudah beriman ?” Maka, rasakanlah azab ini disebabkan keingkaranmu.</span></p><p><span>[b] </span><span>10:27, 28; 39:61; 80:39 </span><span>–</span><span> 43.</span></p>');
INSERT INTO `quran` VALUES (6750,2,3,108,'<p><span>Dan, </span><span>[c] </span><span>ada pun orang-orang yang putih muka mereka, maka mereka akan berada di dalam rahmat Allah; mereka akan menetap di dalamnya.</span></p><p><span>[c] </span><span>10:27.</span></p>');
INSERT INTO `quran` VALUES (6751,2,3,109,'<p><span>Inilah Ayat-ayat Allah yang mengandung kebenaran, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=456">456</a><span>]</span><span> Kami membacakan kepada engkau dan Allah tidak menghendaki suatu keaniayaan atas sekalian alam.</span></p>');
INSERT INTO `quran` VALUES (6752,2,3,110,'<p><span>Dan, </span><span>[a] </span><span>kepunyaan Allah apa-apa yang ada di langit dan apa-apa yang ada di bumi; dan kepada Allah segala perkara dikembalikan.</span></p><p><span>[a] </span><span>3:130, 190; 4:132; 57:11.</span></p>');
INSERT INTO `quran` VALUES (6753,2,3,111,'<p><span>[b] </span><span>Kamu adalah umat terbaik, dibangkitkan demi </span><span>kebaikan</span><span> umat manusia; </span><span>[c] </span><span>kamu menyuruh berbuat kebaikan dan melarang berbuat keburukan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=457">457</a><span>]</span><span> dan beriman kepada Allah. Dan, sekiranya Ahlikitab beriman, niscaya akan lebih baik bagi mereka. Di antara mereka ada yang beriman dan kebanyakan mereka orang-orang fasik.</span></p><p><span>[b] </span><span>2:144.</span></p><p><span>[c] </span><span>3:105, 115; 7:158; 9:71; 31:18.</span></p>');
INSERT INTO `quran` VALUES (6754,2,3,112,'<p><span>Mereka sekali-kali tidak akan dapat memudaratkan kamu kecuali </span><span>menyebabkan</span><span> sedikit sakit hati. Dan </span><span>[a] </span><span>jika mereka memerangi kamu, niscaya mereka akan akan membalikkan punggung </span><span>mereka</span><span> kepadamu. Kemudian mereka tidak akan ditolong.</span></p><p><span>[a] </span><span>59:13.</span></p>');
INSERT INTO `quran` VALUES (6755,2,3,113,'<p><span>[b] </span><span>Ditimpakan kepada mereka kehinaan di mana saja mereka ditemukan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=458">458</a><span>] </span><span> </span><span>kecuali kalau </span><span>mereka dilindungi</span><span> dengan suatu janji dari Allah atau suatu janji dari manusia. Dan, mereka kembali dengan kemurkaan dari Allah dan ditimpakan kepada mereka kesengsaraan. </span><span>[c] </span><span>Yang demikian itu ialah karena mereka mengingkari Ayat-ayat Allah, dan mereka membunuh nabi-nabi tanpa hak. Yang demikian itu ialah disebabkan mereka telah durhaka dan senantiasa melampaui batas.</span></p><p><span>[b] </span><span>2:62, 91; 5:61; 7:160.</span></p><p><span>[c] </span><span>2:62, 92; 3:22.</span></p>');
INSERT INTO `quran` VALUES (6756,2,3,114,'<p><span>[a] </span><span>Tidaklah mereka itu sama. Di antara Ahlikitab ada satu golongan yang berdiri sendiri teguh </span><span>atas janji, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=459">459</a><span>]</span><span> mereka membaca ayat-ayat Allah di waktu malam dan mereka bersujud </span><span>di hadapan-Nya.</span></p><p><span>[a] </span><span>4:163.</span></p>');
INSERT INTO `quran` VALUES (6757,2,3,115,'<p><span>Mereka beriman kepada Allah dan Hari Akhir dan mereka </span><span>[b] </span><span>menyuruh berbuat kebaikan dan melarang berbuat keburukan dan mereka </span><span>[c] </span><span>berlomba-lomba dalam perbagai kebajikan. Dan, mereka termasuk orang-orang saleh.</span></p><p><span>[b] </span><span>3:105, 111; 9:71.</span></p><p><span>[c] </span><span>21:91; 23:62; 35:33.</span></p>');
INSERT INTO `quran` VALUES (6758,2,3,116,'<p><span>Dan </span><span>[d] </span><span>kebaikan apa pun yang mereka kerjakan, maka sekali-kali mereka tidak dihalangi </span><span>menerima ganjarannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=460">460</a><span>]</span><span> dan Allah Maha Mengetahui orang-orang yang bertakwa.</span></p><p><span>[d] </span><span>28:85; 99:8.</span></p>');
INSERT INTO `quran` VALUES (6759,2,3,117,'<p><span>[a] </span><span>Sesungguhnya orang-orang yang ingkar, harta mereka dan anak keturunan mereka tidak akan dapat menyelamatkan mereka sedikit pun dari azab Allah dan mereka itulah penghuni Api; mereka akan tinggal lama di dalamnya.</span></p><p><span>[a] </span><span>3:11; 58:18.</span></p>');
INSERT INTO `quran` VALUES (6760,2,3,118,'<p><span>[b] </span><span>Perumpamaan apa yang mereka belanjakan di dalam kehidupan dunia ini adalah seumpama angin yang di dalamnya mengandung suhu amat dingin menimpa ladang suatu kaum yang berlaku aniaya terhadap diri mereka, lalu angin itu menghancurkannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=461">461</a><span>]</span><span> Dan tidaklah Allah menganiaya mereka tetapi merekalah yang menganiaya diri mereka sendiri.</span></p><p><span>[b] </span><span>10:25; 68:18 </span><span>–</span><span>21.</span></p>');
INSERT INTO `quran` VALUES (6761,2,3,119,'<p><span>Hai orang-orang-orang yang beriman, </span><span>[c]</span><span> jangalah kamu mengambil teman kepercayaan di luar </span><span>golongan</span><span> kamu; </span><span>[d]</span><span> mereka tidak henti-hentinya </span><span>menimbulkan </span><span> kemudaratan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=462">462</a><span>]</span><span> bagimu. Mereka senang </span><span>melihat</span><span> kamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=463">463</a><span>]</span><span> dalam kesusahan. Kebencian sesungguhnya telah zahir dari mulut mereka, dan apa yang disembunyikan dada mereka lebih besar lagi. Sesungguhnya telah Kami jelaskan kepadamu Ayat-ayat </span><span>Kami</span><span>, seandainya Kamu menggunakan akal.</span></p><p><span>[c] </span><span>3:21; 4:140, 145.</span></p><p><span>[d] </span><span>9:47.</span></p>');
INSERT INTO `quran` VALUES (6762,2,3,120,'<p><span>Ingatlah, kamu orang-orang yang mencintai mereka, padahal mereka tidak mencintaimu. </span><span>[a]</span><span> Dan, kamu beriman kepada Kitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=464">464</a><span>]</span><span> seluruhnya. Dan, Apabila mereka bertemu dengan kamu, mereka berkata, “Kami beriman;” dan, apabila mereka menyendiri, mereka mengigit</span><span>–</span><span>gigit jari karena sangat marah terhadap kamu. Katakanlah, “Matilah kamu karena kemarahanmu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=465">465</a><span>]</span><span> Sesungguhnya Allah Maha Mengatahui rahasia-rahasia dalam dada.</span></p><p><span>[a] </span><span>2:15, 77; 5:62.</span></p>');
INSERT INTO `quran` VALUES (6763,2,3,121,'<p><span>[a] </span><span>Jika kamu mendapat keberhasilan, mereka bersedih hati, dan jika kamu mendapat kesusahan, mereka bersenang hati. Dan jika kamu bersabar dan bertakwa, maka tipu muslihat mereka tidak akan dapat memudaratkan kamu sedikit pun. Sesungguhnya Allah menghancurkan apa-apa yang dikerjakan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=466">466</a><span>]</span></p><p><span>[a] </span><span>9:50.</span></p>');
INSERT INTO `quran` VALUES (6764,2,3,122,'<p><span>Dan, ingatlah ketika engkau berangkat pagi hari dari keluarga engkau guna menetapkan orang-orang mukmin tempat kedudukan mereka untuk berperang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=467">467</a><span>]</span><span> Dan, Allah Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (6765,2,3,123,'<p><span>Ketika Dua golongan dari antaramu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=468">468</a><span>]</span><span> memperlihatkan </span><span>sifat pengecut</span><span> karena takutnya, padahal Allah adalah Pelindung mereka. Dan hanya kepada Allah orang-orang mukmin harus bertawakal.</span></p>');
INSERT INTO `quran` VALUES (6766,2,3,124,'<p><span>[a]</span><span> Dan, sesungguhnya Allah telah menolongmu di </span><span>perang Badar</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=469">469</a><span>]</span><span> </span><span>[b]</span><span> ketika kamu masih lemah. Maka, bertakwalah kepada Allah supaya kamu menjadi orang-orang bersyukur.</span></p><p><span>[a] </span><span>8:8, 11; 9:25.</span></p><p><span>[b] </span><span>2:250.</span></p>');
INSERT INTO `quran` VALUES (6767,2,3,125,'<p><span>Ketika engkau berkata kepada orang-orang mukmin, </span><span>[c]</span><span> “Tidaklah cukup bagimu bahwa Tuhan-mu menolongmu dengan tiga ribu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=470">470</a><span>]</span><span> malaikat yang diturunkan </span><span>dari langit?” </span></p><p><span>[c] </span><span>8:10.</span></p>');
INSERT INTO `quran` VALUES (6768,2,3,126,'<p><span>Mengapa tidak ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=471">471</a><span>]</span><span> Jika kamu bersabar dan bertakwa, dan mereka menyerang kepadamu dengan seketika itu juga, tentu Tuhan-mu akan menolongmu dengan lima ribu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=472">472</a><span>]</span><span> malaikat yang menggempur dengan dahsyatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=473">473</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6769,2,3,127,'<p><span>Dan, </span><span>[a] </span><span>Allah tidak menjadikan yang demikian itu melainkan sebagai kabar suka bagimu dan supaya hatimu tenteram </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=474">474</a><span>]</span><span> karenanya, dan tiada pertolongan kecuali dari Allah Yang Maha Perkasa, Maha bijaksana.</span></p><p><span>[a] </span><span>8:11.</span></p>');
INSERT INTO `quran` VALUES (6770,2,3,128,'<p><span>Karena itu ditetapkan </span><span> supaya Dia memotong sebagian dari orang-orang ingkar atau Dia menghinakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=475">475</a><span>]</span><span> mereka supaya mereka kembali dengan kegagalan.</span></p>');
INSERT INTO `quran` VALUES (6771,2,3,129,'<p><span>Engkau tidak punya kepentingan sedikit pun dalam urusan ini, baik Dia menerima taubat mereka atau menyiksa mereka, karena sesungguhnya mereka adalah orang-orang yang aniaya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=476">476</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6772,2,3,130,'<p><span>[a]</span><span> Dan, kepunyaan Allah apa-apa yang ada di langit dan apa-apa yang ada di bumi. Dia mengampuni siapa yang Dia kehendaki dan Dia mengazab siapa yang Dia kehendaki. Dan Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a] </span><span>3:110, 190; 4:132; 56:11.</span></p>');
INSERT INTO `quran` VALUES (6773,2,3,131,'<p><span>Hai orang-orang yang beriman, </span><span>[b] </span><span>janganlah kamu memakan riba yang berlipatganda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=477">477</a><span>]</span><span> dan bertakwalah kepada Allah supaya kamu memperoleh keberhasilan.</span></p><p><span>[b] </span><span>2:276; 30:40.</span></p>');
INSERT INTO `quran` VALUES (6774,2,3,132,'<p><span>Dan </span><span>[a]</span><span> </span><span>takutlah kepada Api </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=478">478</a><span>]</span><span> yang disediakan bagi orang-orang kafir.</span></p><p><span>[a] </span><span>2:25; 66:7.</span></p>');
INSERT INTO `quran` VALUES (6775,2,3,133,'<p><span>Dan, </span><span>[b] </span><span>taatlah kepada Allah dan Rasul ini supaya kamu dikasihani.</span></p><p><span>[b] </span><span>Lihat 3:33.</span></p>');
INSERT INTO `quran` VALUES (6776,2,3,134,'<p><span>[c] </span><span>Dan, berlomba-lambalah ke arah ampunan dari Tuhan-mu dan sorga yang nilainya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=479">479</a><span>]</span><span> seluruh langit dan bumi, disediakan bagi orang-orang muttaqi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=479a">479a</a><span>]</span></p><p><span>[c] </span><span>57:22; Lihat juga 2:26.</span></p>');
INSERT INTO `quran` VALUES (6777,2,3,135,'<p><span>Ialah</span><span>, orang-orang yang membelanjakan </span><span>harta</span><span> di waktu lapang dan di waktu sempit, dan yang menahan marah dan yang memaafkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=480">480</a><span>]</span><span> manusia. Dan Allah mencintai orang-orang yang berbuat kebajikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=481">481</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6778,2,3,136,'<p><span>Dan, orang-orang yang </span><span>[a] </span><span>apabila mereka melakukan suatu perbuatan keji atau menganiaya diri mereka sendiri, mereka ingat kepada Allah, lalu mereka memohon ampunan bagi dosa mereka dan </span><span>[a] </span><span>siapakah yang dapat mengampuni dosa-dosa selain Allah ? dan mereka tidak bersikeras pada apa yang telah dikerjakan mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=482">482</a><span>]</span><span> sedang mereka mengetahui.</span></p><p><span>[a] </span><span>7:202.</span></p><p><span>[b] </span><span>14:11; 39:54;</span></p>');
INSERT INTO `quran` VALUES (6779,2,3,137,'<p><span>[c] </span><span>Orang-orang itulah, ganjaran mereka ialah ampunan dari Tuhan mereka dan kebun-kebun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=483">483</a><span>]</span><span> yang di bawahnya mengalir sungai-sungai, mereka akan menetap di dalamnya, dan alangkah baiknya ganjaran orang-orang beramal.</span></p><p><span>[c] </span><span>39:75.</span></p>');
INSERT INTO `quran` VALUES (6780,2,3,138,'<p><span>Sesungguhnya </span><span>[b] </span><span>telah berlalu sebelummu banyak tata tertib perilaku, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=484">484</a><span>]</span><span> maka </span><span>[b] </span><span>berjalanlah di muka bumi dan perhatikanlah berapa </span><span>buruk</span><span> akibat orang-orang yang mendustakan.</span></p><p><span>[a] </span><span>7:39; 13:31; 41:26; 46:19.</span></p><p><span>[b] </span><span>6:12; 12:110; 27:70</span></p>');
INSERT INTO `quran` VALUES (6781,2,3,139,'<p><span>Ini </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=485">485</a><span>]</span><span> </span><span> </span><span>[c] </span><span>suatu penjelasan bagi manusia dan </span><span>[d] </span><span>suatu petunjuk dan </span><span>[e] </span><span>nasihat bagi orang-orang muttaqi</span></p><p><span>[c] </span><span>5:16, 36:70</span></p><p><span>[d] </span><span>2:3, 186; 31:4.</span></p><p><span>[e] </span><span>24:35.</span></p>');
INSERT INTO `quran` VALUES (6782,2,3,140,'<p><span>Dan </span><span>[a] </span><span>janganlah kamu lesu dan jangan pula bersedih dan kamu pasti unggul, jika </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=486">486</a><span>]</span><span> kamu orang-orang mukmin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=487">487</a><span>]</span></p><p><span>[a] </span><span>4:105; 47:36.</span></p>');
INSERT INTO `quran` VALUES (6783,2,3,141,'<p><span>[a] </span><span>Jika kamu mendapat luka, maka sesungguhnya kaum </span><span>kufar</span><span> itu pun mendapat luka seperti itu. </span><a>[488</a><span>]</span><span> Dan, hari-hari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=488a">488a</a><span>]</span><span> itu Kami pergilirkan di antara manusia </span><span>supaya mereka mendapat nasihat</span><span>, dan supaya Allah menzahirkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=489">489</a><span>]</span><span> orang-orang yang beriman, dan Dia mengambil saksi-saksi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=490">490</a><span>]</span><span> dari antara mu. Dan Allah tidak mencintai orang-orang aniaya.</span></p><p><span>[a] </span><span>4:105.</span></p>');
INSERT INTO `quran` VALUES (6784,2,3,142,'<p><span>Dan, supaya Allah mensucikan orang-orang yang beriman, dan membinasakan orang-orang kafir. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=491">491</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6785,2,3,143,'<p><span>[a] </span><span>Adakah kamu menyangka bahwa kamu akan masuk sorga padahal Allah belum menzahirkan siapa-siapa yang berjihad di antaramu dan belum pula menzahirkan orang-orang yang sabar? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=492">492</a><span>]</span></p><p><span>[a] </span><span>2:215; 9:16.</span></p>');
INSERT INTO `quran` VALUES (6786,2,3,144,'<p><span>Dan, sesungguhnya kamu pernah menginginkan maut </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=493">493</a><span>]</span><span> itu sebelum kamu menemuinya; maka sesungguhnya kamu </span><span>sekarang</span><span> telah melihatnya dan kamu sedang menyaksikan.</span></p>');
INSERT INTO `quran` VALUES (6787,2,3,145,'<p><span>Dan, </span><span>[a] </span><span>Muhammad tidak lain melainkan seorang rasul. Sesungguhnya telah berlalu rasul-rasul sebelum nya. Jadi, jika ia mati atau terbunuh, akan berpalingkah kamu atas tumitmu? </span><span>[b] </span><span>Dan, barangsiapa berpaling atas tumitnya maka ia tidak akan memudaratkan Allah sedikit pun. </span><a>[494</a><span>]</span><span> Dan, Allah pasti akan memberi ganjaran kepada orang-orang yang bersyukur.</span></p><p><span>[a] </span><span>5:76.</span></p><p><span>[b] </span><span>2:144, 218; 5:55; 47:39.</span></p>');
INSERT INTO `quran` VALUES (6788,2,3,146,'<p><span>Dan, tiada jiwa akan mati kecuali dengan izin Allah, sebagai keputusan yang ditetapkan waktunya. </span><span>[a] </span><span>Dan, barangsiapa menghendaki ganjaran dunia, akan Kami beri dia darinya dan barangsiapa menghendaki pahala ukhrawi akan Kami beri dia darinya </span><span>pula</span><span>, dan niscaya akan Kami beri imbalan kepada orang-orang yang bersyukur.</span></p><p><span>[a] </span><span>3:149; 4:135; 42:21.</span></p>');
INSERT INTO `quran` VALUES (6789,2,3,147,'<p><span>Dan, betapa banyaknya nabi telah berperang besertanya sejumlah besar pengikutnya, </span><span>[495]</span><span> maka </span><span>[b] </span><span>mereka tidak merasa lesu disebabkan </span><span>kesusahan</span><span> yang menimpa mereka di jalan Allah, dan mereka tidak lemah dan tidak pula mereka merendahkan diri </span><span>di hadapan musuh</span><span>. Dan Allah mencintai orang-orang yang sabar.</span></p><p><span>[b] </span><span>4:105.</span></p>');
INSERT INTO `quran` VALUES (6790,2,3,148,'<p><span>Dan, tiada ucapan mereka selain mereka berkata, </span><span>[a] </span><span>”Ya Tuhan kami, ampunilah dosa-dosa kami dan </span><span>perbuatan</span><span> kami yang berlebih-lebihan dalam urusan kami, dan teguhkanlah langkah-langkah kami dan tolonglah kami terhadap kaum kafir.”</span></p><p><span>[a] </span><span>2:251, 287.</span></p>');
INSERT INTO `quran` VALUES (6791,2,3,149,'<p><span>[b] </span><span>Karena itu Allah memberi mereka pahala duniawi dan </span><span>juga</span><span> sebaik-baik pahala akhirat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=496">496</a><span>]</span><span> dan Allah mencintai orang-orang yang berbuat kebaikan.</span></p><p><span>[b] </span><span>3:146.</span></p>');
INSERT INTO `quran` VALUES (6792,2,3,150,'<p><span>[c] </span><span>Hai orang-orang yang beriman, jika kamu menaati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=497">497</a><span>]</span><span> orang-orang yang ingkar, </span><span>niscaya</span><span> mereka akan membalikkan kamu atas tumit-tumitmu, maka kamu akan kembali menjadi orang- orang yang rugi.</span></p><p><span>[c] </span><span>2:110; 3:101.</span></p>');
INSERT INTO `quran` VALUES (6793,2,3,151,'<p><span>Bahkan </span><span>[a] </span><span>Allah pelindung-mu, dan Dia sebaik-baik Penolong.</span></p><p><span>[a] </span><span>8:41; 9:51; 22:79.</span></p>');
INSERT INTO `quran` VALUES (6794,2,3,152,'<p><span>[a] </span><span>Kami akan memasukkan rasa takut ke dalam hati orang-orang ingkar disebabkan dengan apa yang mereka sekutukan dengan Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=498">498</a><span>]</span><span> sesuatu yang tidak Dia turunkan dalil tentangnya, dan tempat tinggal mereka ialah Api. Dan alangkah buruknya kediaman orang-orang aniaya.</span></p><p><span>[a] </span><span>8:13; 59:3.</span></p>');
INSERT INTO `quran` VALUES (6795,2,3,153,'<p><span>Dan sesungguhnya Allah telah memenuhi janji-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=499">499</a><span>]</span><span> kepadamu ketika kamu membunuh dan memusnahkan mereka dengan izin-Nya, hingga apabila </span><span>kamu</span><span> telah menampakkan kelemahan </span><a>[500</a><span>]</span><span> dan bertengkar mengenai perintah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=501">501</a><span>]</span><span> </span><span>Rasul</span><span> itu, dan kamu durhaka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=502">502</a><span>]</span><span> sesudah Dia memperlihatkan kepadamu apa yang kamu sukai, </span><span>lalu Dia menarik kembali pertolongan-Nya</span><span>. Di antaramu ada yang menghendaki dunia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=503">503</a><span>]</span><span> dan di antaramu ada yang menghendaki akhirat. kemudian Dia memalingkan kamu dari mereka supaya Dia menguji kamu dan sesungguhnya Dia telah memaafkan kamu. Dan Allah Yang Empunya karunia besar atas orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (6796,2,3,154,'<p><span>Ketika kamu melarikan diri dan tidak menoleh ke belakang kepada seorang jua pun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=504">504</a><span>]</span><span> padahal Rasul memanggil kamu dari paling belakangmu, kemudian Dia memberi kamu kesedihan atas kesedihan; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=505">505</a><span>]</span><span> </span><span>[a] </span><span>supaya kamu jangan berdukacita tentang apa yang telah luput darimu dan jangan pula </span><span>bersedih</span><span> tentang apa yang telah menimpamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=505a">505a</a><span>] </span><span> Dan, Allah Mengetahui segala yang kamu kerjakan.</span></p><p><span>[a] </span><span>57:24</span></p>');
INSERT INTO `quran` VALUES (6797,2,3,155,'<p><span>Kemudian </span><span>[a] </span><span>Dia menurunkan kepadamu rasa aman, setelah kesedihan itu, suatu kantuk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=506">506</a><span>]</span><span> yang meliputi segolongan di antaramu, sedang segolongan lagi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=506a">506a</a><span>]</span><span> mengkhawatirkan diri mereka sendiri. Mereka menyangka yang tidak benar mengenai Allah, </span><span>seperti</span><span> sangkaan jahiliah. Berkata mereka, “Adakah bagi kami sesuatu bagian dalam urusan itu ?” Katakanlah, “Sesungguhnya, urusan itu seluruhnya kepunyaan Allah,” Mereka menyembunyikan dalam hati mereka kepada engkau. Berkata mereka, </span><span>[b]</span><span>”Sekiranya kami mempunyai sesuatu bagian dalam urusan itu, niscaya kami tidak terbunuh di sini.” Katakan lah, “Walaupun kamu </span><span>tetap</span><span> di rumah-rumahmu, niscaya orang-orang, yang terhadap mereka berperang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=506b">506b</a><span>]</span><span> telah diwajibkan, akan keluar juga ke tempat-tempat </span><span>kematian </span><span>mereka,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=506c">506c</a><span>]</span><span> </span><span>supaya </span><span> </span><span>Allah melaksanakan keputusan-Nya</span><span>, dan supaya Allah menguji apa-apa yang ada di dalam dadamu dan supaya Dia mensucikan apa-apa yang ada di dalam hatimu. Dan Allah Maha Mengetahui segala apa yang ada di dalam dada.</span></p><p><span>[a] </span><span>8:12.</span></p><p><span>[b] </span><span>3:169.</span></p>');
INSERT INTO `quran` VALUES (6798,2,3,156,'<p><span>Sesungguhnya orang-orang yang berpaling di antaramu pada hari </span><span>ketika</span><span> dua pasukan saling berhadapan , </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=507">507</a><span>]</span><span> sesungguhnya syaitanlah yang menggelincirkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=507a">507a</a><span>] </span><span>mereka disebabkan sebagian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=508">508</a><span>]</span><span> perbuatan mereka, dan sesungguhnya Allah mengampuni mereka. Sesungguh nya Allah Maha Pengampun, Maha Penyantun.</span></p>');
INSERT INTO `quran` VALUES (6799,2,3,157,'<p><span>Hai orang-orang yang beriman, janganlah kamu menjadi seperti orang-orang yang ingkar dan yang berkata mengenai saudara-saudara mereka apabila mereka mengadakan perjalanan di muka bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=509">509</a><span>]</span><span> atau berada dalam peperangan, “Sekiranya mereka tetap di samping kami, tidaklah mereka akan mati dan tidak pula akan terbunuh.” </span><span>Ucapan mereka itu</span><span> supaya dijadikan oleh Allah suatu penyesalan dalam hati mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=510">510</a><span>]</span><span> Dan, Allah yang menghidupkan dan yang mematikan, dan Allah Maha Melihat apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (6800,2,3,158,'<p><span>Dan, jika kamu terbunuh di jalan Allah atau kamu mati, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=511">511</a><span>]</span><span> </span><span>[a] </span><span>tentu ampunan dan rahmat dari Allah adalah lebih baik daripada apa yang mereka kumpulkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=512">512</a><span>]</span></p><p><span>[a] </span><span>10:59; 43:33.</span></p>');
INSERT INTO `quran` VALUES (6801,2,3,159,'<p><span>Dan, jika kamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=513">513</a><span>]</span><span> mati atau kamu terbunuh, </span><span>[b] </span><span>niscaya kepada Allah kamu akan dihimpun.</span></p><p><span>[b] </span><span>5:97; 6:73; 8:25; 23:80.</span></p>');
INSERT INTO `quran` VALUES (6802,2,3,160,'<p><span>Dan, oleh karena rahmat dari Allah maka engkau bersikap lemah-lembut </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=514">514</a><span>]</span><span> terhadap mereka, dan jika engkau kasar dan keras hati, niscaya mereka akan cerai-berai dari sekitar engkau. Maka, maafkanlah mereka dan mintalah ampunan </span><span>dari Allah</span><span> bagi mereka, dan </span><span>[a] </span><span>bermusyawarahlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=515">515</a><span>]</span><span> dengan mereka dalam urusan </span><span>yang penting</span><span> dan , apabila engkau telah mengambil suatu ketetapan, maka bertawakallah kepada Allah. Sesungguhnya, Allah mencintai orang-orang yang bertawakal.</span></p><p><span>[a] </span><span>42:39</span></p>');
INSERT INTO `quran` VALUES (6803,2,3,161,'<p><span>Jika Allah menolong kamu, maka tak ada yang akan dapat mengalahkanmu; dan jika Dia meninggalkan kamu, maka siapakah yang akan menolongmu selain Dia ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=516">516</a><span>]</span><span> Dan kepada Allah hendaknya bertawakal orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (6804,2,3,162,'<p><span>Dan Tidaklah </span><span>mungkin</span><span> bagi seorang nabi berkhianat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=517">517</a><span>]</span><span> dan barangsiapa berkhianat niscaya ia akan membawa apa-apa yang dikhianatinya itu pada Hari Kiamat. </span><span>[a] </span><span>Kemudian tiap-tiap jiwa akan diberi balasan sempurna untuk apa-apa yang diusahakannya; dan mereka tidak akan dianiaya.</span></p><p><span>[a] </span><span>3:26; 14:52; 40:18.</span></p>');
INSERT INTO `quran` VALUES (6805,2,3,163,'<p><span>[a] </span><span>Adakah orang yang mengikuti keridhaan Allah serupa dengan orang yang kembali dengan membawa kemurkaan dari Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=518">518</a><span>]</span><span> dan tempat kediamannya adalah Jahannam ? Dan alangkah buruknya tempat-kembali itu.</span></p><p><span>[a] </span><span>2:208, 266; 3:16; 5:3, 17; 19:72.</span></p>');
INSERT INTO `quran` VALUES (6806,2,3,164,'<p><span>Mereka </span><span>mempunyai </span><span> derajat-derajat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=519">519</a><span>]</span><span> di sisi Allah; dan Allah Maha Melihat apa yang mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (6807,2,3,165,'<p><span>Sesungguhnya Allah telah memberi karunia kepada orang-orang mukmin ketika </span><span>[a] </span><span>membangkitkan kepada mereka seorang Rasul dari antara mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=520">520</a><span>]</span><span> yang membacakan ayat-ayat-Nya kepada mereka, dan mensucikan mereka dan mengajarkan kepada mereka Kitab dan Hikmah; dan walaupun sebelum itu mereka sesungguhnya ada di dalam kesesatan yang nyata.</span></p><p><span>[a] </span><span>2:130, 152; 9:128; 63:3; 65:12.</span></p>');
INSERT INTO `quran` VALUES (6808,2,3,166,'<p><span>Mengapa, </span><span>[b] </span><span>ketika suatu musibah menimpamu, padahal telah ditimpakan kepada kamu dua kali </span><span>seperti</span><span> </span><span>itu</span><span>. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=521">521</a><span>] </span><span>kamu berkata,, dari manakah ini ? Katakanlah, “Itu adalah dari dirimu sendiri.”</span><a>[522</a><span>]</span><span> Sesungguhnya Allah Maha Kuasa atas segala sesuatu.</span></p><p><span>[b] </span><span>4:80</span></p>');
INSERT INTO `quran` VALUES (6809,2,3,167,'<p><span>Dan, apa yang menimpa kamu pada hari </span><span>ketika</span><span> dua pasukan berhadap-hadapan, maka </span><span>hal demikian adalah</span><span> dengan izin Allah; dan supaya Dia mengetahui orang-orang yang beriman.</span></p>');
INSERT INTO `quran` VALUES (6810,2,3,168,'<p><span>Dan, supaya Dia mengetahui orang-orang yang munafik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=523">523</a><span>]</span><span> </span><span>Dan, dikatakan kepada mereka, “Marilah berperang di jalan Allah dan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=524">524</a><span>]</span><span> tangkislah </span><span>serangan musuh</span><span>;” mereka berkata, “Jika kami mengetahui cara berperang, niscaya kami akan mengikutimu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=525">525</a><span>]</span><span> Mereka pada hari itu lebih dekat kepada kufur daripada kepada iman. </span><span>[a] </span><span>Mereka mengatakan dengan mulut mereka apa yang tidak ada dalam hati mereka. Dan, Allah Maha Mengetahui apa yang disembunyikan mereka.</span></p><p><span>[a] </span><span>48:12</span></p>');
INSERT INTO `quran` VALUES (6811,2,3,169,'<p><span>Mereka inilah</span><span> orang-orang yang berkata tentang saudara-saudaranya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=526">526</a><span>]</span><span> sedang mereka duduk saja </span><span>di garis belakang</span><span>, “Sekiranya mereka menaati kami, </span><span>[a] </span><span>tidaklah mereka akan terbunuh.” Katakanlah, “Maka </span><span>cobalah</span><span> hindarkan </span><span>[b] </span><span>maut dari dirimu, jika kamu orang-orang yang benar.”</span></p><p><span>[a] </span><span>3:155.</span></p><p><span>[b] </span><span>4:79.</span></p>');
INSERT INTO `quran` VALUES (6812,2,3,170,'<p><span>[c]</span><span> </span><span>Dan janganlah kamu mengira tentang orang-orang yang terbunuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=527">527</a><span>]</span><span> di jalan Allah itu mati, bahkan mereka itu hidup di sisi Tuhan mereka, mereka diberi rezeki.</span></p><p><span>[c] </span><span>2:155.</span></p>');
INSERT INTO `quran` VALUES (6813,2,3,171,'<p><span>Mereka gembira </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=528">528</a><span>]</span><span> dengan apa yang diberikan Allah kepada mereka dari karunia-Nya; dan mereka bergirang hati terhadap orang-orang yang masih di belakang mereka dan belum bergabung dengan mereka; karena tak ada ketakutan </span><span>akan datang </span><span> </span><span>[a] </span><span>terhadap mereka, dan tidak pula mereka akan bersedih.</span></p><p><span>[a] </span><span>2:63; 6:49; 7:50; 46:14.</span></p>');
INSERT INTO `quran` VALUES (6814,2,3,172,'<p><span>Mereka bergirang hati dengan nikmat dari Allah dan karunia, dan sesungguhnya </span><span>[b] </span><span>Allah tidak menyia-nyiakan ganjaran orang-orang mukmin.</span></p><p><span>[b] </span><span>7:171; 9:129; 11:116.</span></p>');
INSERT INTO `quran` VALUES (6815,2,3,173,'<p><span>Orang-orang yang telah </span><span>[c] </span><span>mengabulkan perintah Allah dan Rasul sesudah luka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=529">529</a><span>]</span><span> menimpa mereka. Bagi orang-orang yang berbuat kebaikan di antara mereka dan bertakwa tersedia ganjaran yang besar;</span></p><p><span>[c] </span><span>8:25.</span></p>');
INSERT INTO `quran` VALUES (6816,2,3,174,'<p><span>Orang-orang yang kepada mereka manusia berkata, “Sesungguhnya manusia telah mengumpulkan </span><span>lasykar </span><span>untuk </span><span>menyerang</span><span> kamu, maka takutilah mereka,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=530">530</a><span>]</span><span> tetapi </span><span>hal itu</span><span> menambah keimanan mereka dan mereka berkata “Cukuplah Allah bagi kami, dan Dia sebaik-baik Pelindung.”</span></p>');
INSERT INTO `quran` VALUES (6817,2,3,175,'<p><span>Maka, kembalilah mereka dengan nikmat dan karunia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=531">531</a><span>]</span><span> dari Allah; keburukan tidak menyentuh mereka dan mereka mengikuti </span><span>[a] </span><span>keridhaan Allah. Dan Allah adalah Yang Empunya karunia besar.</span></p><p><span>[a] </span><span>2:208, 266; 3:16, 163; 5:3, 17; 9:72; 57:21, 28.</span></p>');
INSERT INTO `quran` VALUES (6818,2,3,176,'<p><span>Sesungguhnya itu tak lain hanyalah syaitan yang menakuti-nakuti </span><span>[a] </span><span>kawan-kawan-nya; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=532">532</a><span>]</span><span> karena itu, janganlah kamu takut kepada mereka, dan takutlah kepada-Ku, jika kamu orang-orang mukmin.</span></p><p><span>[b] </span><span>7:28; 16:101; 35:7.</span></p>');
INSERT INTO `quran` VALUES (6819,2,3,177,'<p><span>[c] </span><span>Dan, janganlah menyedihkan engkau oleh orang-orang yang cepat-cepat masuk ke dalam kekufuran; sesungguhnya mereka sekali-kali tidak memudaratkan Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=533">533</a><span>]</span><span> sedikit pun. Allah berkehendak tidak akan memberi sesuatu bagian kepada mereka di akhirat; dan bagi mereka azab yang besar.</span></p><p><span>[c] </span><span>5:42.</span></p>');
INSERT INTO `quran` VALUES (6820,2,3,178,'<p><span>Sesungguhnya </span><span>[a] </span><span>orang-orang yang menukar iman dengan kekufuran sekali-kali tidak akan memudaratkan Allah sedikit pun; dan bagi mereka azab yang pedih.</span></p><p><span>[a] </span><span>2:17, 87; 14:29.</span></p>');
INSERT INTO `quran` VALUES (6821,2,3,179,'<p><span>Dan, janganlah sekali-kali orang-orang yang ingkar menyangka bahwa </span><span>[a] </span><span>Kami memberi tangguh kepada mereka itu, baik bagi diri mereka; sesungguhnya Kami memberi tangguh kepada mereka itu supaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=534">534</a><span>]</span><span> mereka bertambah dalam dosa; dan bagi mereka azab yang menghinakan.</span></p><p><span>[b] </span><span>22:45.</span></p>');
INSERT INTO `quran` VALUES (6822,2,3,180,'<p><span>[c] </span><span>Allah tidak mungkin membiarkan orang-orang mukmin di dalam keadaan kamu sekarang </span><a>[535</a><span>]</span><span> sampai Dia memisahkan yang buruk dari yang baik. Dan Allah tidak akan </span><span>[d] </span><span>memberitahukan yang gaib kepadamu, tetapi Allah memilih </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=536">536</a><span>]</span><span> di antara rasul-rasul-Nya siapa yang Dia kehendaki. Maka berimanlah kamu kepada Allah dan rasul-rasul-Nya. Dan jika kamu beriman dan bertakwa, maka bagimu ganjaran yang besar.</span></p><p><span>[c] </span><span>8:38; 29:3, 4.</span></p><p><span>[d] </span><span>72:27, 28</span></p>');
INSERT INTO `quran` VALUES (6823,2,3,181,'<p><span>Dan janganlah sekali-kali orang-orang </span><span>[a] </span><span>yang bakhil </span><span>dalam membelanjakan </span><span>apa-apa yang telah diberikan Allah kepada mereka dari karunia-Nya menyangka bahwa itu baik bagi mereka, bahkan itu buruk bagi mereka. Akan dikalungkan kepada mereka apa-apa yang mereka telah bakhilkan pada Hari Kiamat. Dan kepunyaan Allah warisan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=537">537</a><span>]</span><span> seluruh langit dan bumi, dan Allah Maha Mengetahui apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>4:38; 17:30; 25:68.</span></p>');
INSERT INTO `quran` VALUES (6824,2,3,182,'<p><span>Sesungguhnya Allah telah mendengar ucapan orang-orang yang mengatakan, “Sesungguhnya </span><span>[a] </span><span>Allah miskin dan kami kaya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=538">538</a><span>]</span><span> Kami akan mencatat apa yang mereka katakan dan mereka </span><span>terus </span><span>[a] </span><span>berusaha </span><span> membunuh nabi-nabi tanpa hak dan Kami akan mengatakan, “Kamu rasakanlah azab yang membakar,”</span></p><p><span>[a] </span><span>4:156.</span></p><p><span>[b] </span><span>5:65</span></p>');
INSERT INTO `quran` VALUES (6825,2,3,183,'<p><span>“Itu disebabkan oleh apa yang telah dikerjakan tanganmu sendiri,” dan sesungguhnya </span><span>[b] </span><span>Allah tidak aniaya terhadap hamba-hamba.</span></p><p><span>[b] </span><span>8:52; 41:47; 50:30.</span></p>');
INSERT INTO `quran` VALUES (6826,2,3,184,'<p><span>Orang-orang yang mengatakan, “Sesungguhnya Allah telah mengamanatkan kepada kami supaya kami tidak beriman kepada seseorang rasul sebelum ia mendatangkan kepada kami kurban yang api memakannya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=539">539</a><span>]</span><span> Katakanlah, “Sesungguhnya telah datang kepadamu rasul-rasul sebelumku dengan </span><span>[c] </span><span>”Tanda-tanda yang nyata, dan </span><span>juga</span><span> apa-apa yang telah kamu katakan. Maka, mengapa kamu membunuh mereka jika kamu adalah orang-orang benar ?”</span></p><p><span>[c] </span><span>5:33; 14:10; 40:84.</span></p>');
INSERT INTO `quran` VALUES (6827,2,3,185,'<p><span> </span><span>[a]</span><span> Dan, jika mereka mendustakan engkau, maka sesungguhnya telah didustakan pula rasul-rasul sebelum engkau yang datang dengan Tanda-tanda yang nyata dan Zabur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=540">540</a><span>]</span><span> </span><span>dan Kitab Syariat yang menerangi.</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=541">541</a><span>]</span></p><p><span>[a] </span><span>35:5, 26.</span></p>');
INSERT INTO `quran` VALUES (6828,2,3,186,'<p><span>[b] </span><span>Tiap-tiap jiwa akan merasai maut. Dan sesungguhnya akan disempurnakan </span><span>[c] </span><span>ganjaranmu pada Hari Kiamat. Maka, barangsiapa dijauhkan dari Api dan dimasukkan ke dalam sorga, sesungguhnya ia telah berhasil. Dan, kehidupan dunia ini tidak lain melainkan suatu kesenangan yang memperdayakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=542">542</a><span>]</span></p><p><span>[b] </span><span>21:36; 29:58.</span></p><p><span>[c] </span><span>4:174; 35:31; 39:36.</span></p>');
INSERT INTO `quran` VALUES (6829,2,3,187,'<p><span>[a] </span><span>Kamu pasti akan di uji </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=543">543</a><span>]</span><span> dalam hartamu dan jiwamu, dan pasti </span><span>[b] </span><span>kamu akan mendengar banyak </span><span>hal</span><span> yang menyakitkan hati dari orang-orang yang telah diberi Alkitab sebelummu dan dari orang-orang musyrik. Dan jika kamu bersabar dan bertakwa, maka hal demikian sungguh merupakan urusan keteguhan hati.</span></p><p><span>[a] </span><span>2:156; 8:29; 64:16.</span></p><p><span>[b] </span><span>5:83.</span></p>');
INSERT INTO `quran` VALUES (6830,2,3,188,'<p><span>Dan, </span><span>ingatlah</span><span> ketika Allah mengambil janji </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=544">544</a><span>]</span><span> dari orang-orang yang telah diberi Alkitab </span><span>dan berfirman</span><span> , “Kamu harus menerangkannya kapada manusia dan jangan menyembunyikan.” Tetapi, </span><span>[c] </span><span>mereka mencampakkannya ke belakang punggung mereka dan menukarkannya dengan harga rendah. Maka, alangkah buruknya apa yang mereka beli.</span></p><p><span>[c] </span><span>2:102.</span></p>');
INSERT INTO `quran` VALUES (6831,2,3,189,'<p><span>Janganlah sekali-kali engkau menyangka bahwa </span><span>[a] </span><span>orang-orang yang sangat berbesar hati dengan apa-apa yang mereka perbuat dan mereka menyukai supaya mereka dipuji dengan apa-apa yang mereka tidak pernah kerjakan, maka janganlah sekali-kali engkau menyangka </span><span>bahwa </span><span> mereka akan terpelihara </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=545">545</a><span>]</span><span> dari azab, dan bagi mereka azab yang pedih.</span></p><p><span>[a] </span><span>61:3 </span><span>–</span><span> 4.</span></p>');
INSERT INTO `quran` VALUES (6832,2,3,190,'<p><span>[b] </span><span>Dan kepunyaan Allah kerajaan seluruh langit dan bumi; dan Allah Maha kuasa atas segala sesuatu.</span></p><p><span>[b] </span><span>5:18 </span><span>–</span><span> 19, 121; 24:43; 42:50.</span></p>');
INSERT INTO `quran` VALUES (6833,2,3,191,'<p><span>[c] </span><span>Sesungguhnya dalam penciptaan seluruh langit dan bumi, dan pertukaran malam dan siang, pasti ada Tanda-tanda bagi orang-orang yang berakal;</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=546">546</a><span>]</span></p><p><span>[c] </span><span>2:165; 3:28; 45:4, 6.</span></p>');
INSERT INTO `quran` VALUES (6834,2,3,192,'<p><span>Yaitu, </span><span>[d] </span><span>orang-orang yang selalu mengingat Allah, ketika berdiri dan duduk dan ketika </span><span>berbaring</span><span> atas rusuk mereka, dan mereka merenungkan tentang penciptaan seluruh langit dan bumi </span><span>berkata,</span><span> “Ya Tuhan kami, tidaklah Engkau menjadikan ini </span><span>[a] </span><span>sia-sia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=547">547</a><span>]</span><span> Maha Suci Engkau, maka perliharalah kami dari azab Api.”</span></p><p><span>[a] </span><span>38:28.</span></p><p><span>[d] </span><span>4:104; 10:13; 39:10; 62:11.</span></p>');
INSERT INTO `quran` VALUES (6835,2,3,193,'<p><span>“Wahai Tuhan kami, sesungguhnya barangsiapa yang Engkau masukkan ke dalam Api, maka sungguh Engkau telah menghinakannya. Dan tak ada bagi orang-orang aniaya seorang penolong pun.</span></p>');
INSERT INTO `quran` VALUES (6836,2,3,194,'<p><span>“Wahai Tuhan kami sesungguhnya kami telah mendengar seorang penyeru memanggil kepada keimanan, bahwa, </span><span>‘</span><span>Berimanlah kepada Tuhan-mu,</span><span>’</span><span> maka kami telah beriman. Wahai Tuhan kami, ampunilah bagi kami, dosa-dosa kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=548">548</a><span>]</span><span> dan hapuskanlah dari kami kesalahan-kesalahan kami dan wafatkanlah kami dalam golongan orang-orang baik.”</span></p>');
INSERT INTO `quran` VALUES (6837,2,3,195,'<p><span>“Wahai Tuhan kami, berikanlah kepada kami apa yang telah Engkau janjikan kepada kami dengan perantaraan rasul-rasul Engkau, dan janganlah Engkau hinakan kami pada Hari Kiamat. Sesungguhnya Engkau tidak menyalahi janji.”</span></p>');
INSERT INTO `quran` VALUES (6838,2,3,196,'<p><span>Maka Tuhan mereka telah mengabulkan </span><span>doa</span><span> mereka, “Sesungguhnya Aku tidak akan menyia-nyiakan amalan orang yang beramal dari antaramu baik laki-laki maupun perempuan. Sebagian kamu </span><span>adalah</span><span> dari sebagian lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=549">549</a><span>]</span><span> </span><span>[b] </span><span>Maka orang-orang yang telah berhijrah dan diusir dari rumah-rumah mereka dan disiksa pada jalan-Ku, dan mereka berperang dan terbunuh, niscaya Aku akan menghapuskan dari mereka keburukan-keburukan mereka dan pasti Aku </span><span>[a] </span><span>akan memasukkan mereka ke dalam kebun-kebun yang di bawahnya mengalir sungai-sungai, sebagai ganjaran dari sisi Allah, dan Allah, di sisi-Nya ada sebaik-baik ganjaran.</span></p><p><span>[b] </span><span>16:42; 22:59, 60.</span></p><p><span>[a] </span><span>Lihat 2:26.</span></p>');
INSERT INTO `quran` VALUES (6839,2,3,197,'<p><span>[b] </span><span>Janganlah sekali-kali engkau terpedaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=550">550</a><span>]</span><span> oleh lalu-lalang orang-orang ingkar di dalam kota.</span></p><p><span>[b] </span><span>40:5.</span></p>');
INSERT INTO `quran` VALUES (6840,2,3,198,'<p><span>Ini adalah</span><span> kesenangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=551">551</a><span>]</span><span> sementara yang sedikit, kemudian, tempat kediaman mereka adalah Jahannam. Dan, alangkah buruknya tempat itu.</span></p>');
INSERT INTO `quran` VALUES (6841,2,3,199,'<p><span>Tetapi, orang-orang yang bertakwa kepada Tuhan mereka, bagi mereka ada kebun-kebun yang di bawahnya mengalir sungai-sungai; mereka akan menetap di dalamnya, suatu hidangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=552">552</a><span>]</span><span> dari sisi Allah. Dan, apa yang ada di sisi Allah adalah lebih baik bagi orang-orang saleh.</span></p>');
INSERT INTO `quran` VALUES (6842,2,3,200,'<p><span>Dan, sesungguhnya </span><span>[a] </span><span>di antara Ahlikitab ada yang beriman kepada Allah, dan kepada apa yang diturunkan kepadamu, dan kepada apa yang diturunkan kepada mereka, mereka berendah diri di hadapan Allah. Mereka tidak menjual ayat-ayat Allah dengan harga rendah. Orang-orang inilah yang bagi mereka ada ganjaran mereka di sisi Tuhan mereka. Sesungguhnya Allah sangat cepat dalam menghisab. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=553">553</a><span>]</span></p><p><span>[a] </span><span>3:11.</span></p>');
INSERT INTO `quran` VALUES (6843,2,3,201,'<p><span>Wahai orang-orang yang beriman, bersabarlah dan tingkatkanlah kesabaran dan </span><span>[b] </span><span>berjaga-jagalah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=554">554</a><span>]</span><span> di perbatasan serta bertakwalah kepada Allah supaya kamu memperoleh keberhasilan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=555">555</a><span>]</span></p><p><span>[b] </span><span>8:61</span></p>');
INSERT INTO `quran` VALUES (6844,2,4,1,'<p><span>Aku baca </span><span> dengan nama Allah , Maha Pemurah, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (6845,2,4,2,'<p><span>Hai manusia, </span><span>[a] </span><span>bertakwalah kepada Tuhan-mu </span><span>[b] </span><span>Yang menciptakan kamu dari satu jiwa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=556">556</a><span>]</span><span> dan darinya Dia menciptakan jodohnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=557">557</a><span>]</span><span> dan mengembang-biakan dari keduanya banyak laki-laki dan perempuan; dan bertakwalah kepada Allah yang dengan </span><span>nama</span><span>-Nya kamu saling bertanya, terutama </span><span>mengenai hubungan</span><span> tali kekerabatan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=558">558</a><span>]</span><span> Sesungguhnya Allah adalah pengawas atas kamu.</span></p><p><span>[a] </span><span>33:71; 59:19.</span></p><p><span>[b] </span><span>7:190; 16:73; 30:22; 39:7.</span></p>');
INSERT INTO `quran` VALUES (6846,2,4,3,'<p><span>Dan, </span><span>[a] </span><span>berikanlah kepada anak-anak yatim harta mereka; dan jangalah kamu mempertukarkan yang buruk dengan yang baik, dan janganlah kamu memakan harta mereka mencampurkannya dengan hartamu. Sesungguhnya itu adalah dosa besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=559">559</a><span>]</span></p><p><span>[a] </span><span>4:11, 128; 6:153; 17:35.</span></p>');
INSERT INTO `quran` VALUES (6847,2,4,4,'<p><span>Dan, Jika kamu khawatir bahwa kamu tak akan dapat berlaku adil terhadap anak-anak yatim, maka kawinilah perempuan-perempuan </span><span>lainnya</span><span> yang kamu sukai; dua, tiga, atau empat; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=560">560</a><span>]</span><span> akan tetapi, </span><span>[b] </span><span>jika kamu khawatir kamu tak akan dapat berlaku adil, maka </span><span>kawinilah</span><span> seorang perempuan saja, atau </span><span>kawinilah</span><span> yang dimiliki tangan kananmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=561">561</a><span>]</span><span> </span><span>Cara demikian itu lebih dekat untuk kamu supaya tidak berbuat aniaya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=562">562</a><span>]</span></p><p><span>[b] </span><span>4:130.</span></p>');
INSERT INTO `quran` VALUES (6848,2,4,5,'<p><span>Dan, </span><span>[a] </span><span>berikanlah kepada perempuan-perempuan maskawin mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=563">563</a><span>]</span><span> dengan suka hati. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=564">564</a><span>]</span><span> Akan tetapi, jika mereka sendiri merelakan untukmu sebagaian darinya, maka makanlah pemberian itu dengan lezat dan nikmat.</span></p><p><span>[a] </span><span>4:25, 26; 60:11</span></p>');
INSERT INTO `quran` VALUES (6849,2,4,6,'<p><span>Dan, janganlah kamu menyerahkan kepada orang-orang yang masih kurang pengertian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=565">565</a><span>]</span><span> hartamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=566">566</a><span>]</span><span> yang telah dijadikan Allah sebagai sandaran bagimu, dan berilah mereka makanan dan pakaian darinya dan ucapkanlah kepada mereka nasihat yang baik.</span></p>');
INSERT INTO `quran` VALUES (6850,2,4,7,'<p><span>Dan, ujilah </span><span>daya pikir</span><span> anak-anak yatim hingga mereka mencapai </span><span>usia untuk</span><span> nikah; maka, jika kamu melihat pada diri mereka ada kematangan dalam pertimbangan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=567">567</a><span>]</span><span> serahkanlah kepada mereka harta mereka; dan janganlah kamu memakan </span><span>harta </span><span> itu secara boros dan tergesa-gesa karena </span><span>takut</span><span> mereka akan lekas dewasa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=568">568</a><span>]</span><span> Dan, barangsiapa kaya hendaklah ia menahan diri dan siapa yang kurang mampu bolehlah ia makan </span><span>dari harta itu </span><span> secara patut. Dan apabila kamu menyerahkan kepada mereka harta mereka maka datangkanlah saksi-saksi di hadapan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=569">569</a><span>]</span><span> Dan cukuplah Allah sebagai Penghisab.</span></p>');
INSERT INTO `quran` VALUES (6851,2,4,8,'<p><span>[a] </span><span>Bagi laki-laki ada bagian dari </span><span>harta</span><span> yang ditinggalkan oleh kedua orang tua dan kerabatnya; dan bagi perempuan-perempuan pun ada bagian dari </span><span>harta</span><span> yang ditinggalkan oleh kedua orang tua dan kerabatnya, baik sedikit atau banyak darinya, suatu bagian yang telah ditetapkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=570">570</a><span>]</span></p><p><span>[a] </span><span>4:34.</span></p>');
INSERT INTO `quran` VALUES (6852,2,4,9,'<p><span>Dan, apabila hadir pada waktu pembagian </span><span>warisan</span><span> itu kaum kerabat </span><span>yang lain</span><span> dan anak-anak yatim dan orang-orang miskin, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=571">571</a><span>]</span><span> maka berikanlah kepada mereka </span><span>sesuatu</span><span> darinya, dan ucapkanlah kepada </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=571a">571a</a><span>]</span><span> mereka perkataan yang patut.</span></p>');
INSERT INTO `quran` VALUES (6853,2,4,10,'<p><span>Dan, hendaklah merasa takut </span><span>akan Allah </span><span>orang-orang yang kalau mereka meninggalkan di belakang mereka keturunan yang lemah, khawatir terhadap mereka </span><span>akan sia-sia</span><span>, maka hendaklah mereka takut kepada Allah dan hendaklah mereka mengucapkan perkataan yang lurus. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=572">572</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6854,2,4,11,'<p><span>Sesungguhnya, mereka yang memakan harta anak-anak yatim dengan aniaya, mereka sebenarnya tak lain hanya menelan api ke dalam perut mereka, dan mereka pasti akan terbakar dalam api yang bernyala-nyala.</span></p><p><span>[a] </span><span>Lihat 4:3.</span></p>');
INSERT INTO `quran` VALUES (6855,2,4,12,'<p><span>Allah memerintahkan kepadamu mengenai anak-anakmu; untuk </span><span>[b] </span><span>seorang anak laki-laki seperti bagian dua orang anak perempuan; tetapi jika mereka hanya </span><span>perempuan saja</span><span>, lebih dari dua orang, maka untuk mereka dua pertiga dari apa yang ditinggalkan; dan jika ia </span><span>hanya</span><span> seorang perempuan saja, maka bagiannya seperdua. Dan, untuk kedua orang tuanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=573">573</a><span>]</span><span> masing-masing seperenam dari harta peninggalan itu jika ia mempunyai anak; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=574">574</a><span>]</span><span> tetapi jika ia tidak mempunyai anak dan </span><span>hanya</span><span> kedua orang tuanya menjadi ahli warisnya, maka untuk ibunya sepertiga, tetapi, jika ia mempunyai saudara-saudara, maka untuk ibunya seperenam, sesudah </span><span>melunasi</span><span> wasiatnya yang telah diwasiatkannya atau </span><span>melunasi </span><span> utang-utangnya. Bapak-bapakmu dan anak-anakmu, tidaklah kamu ketahui siapa di antara mereka yang lebih dekat kepadamu dalam manfaat. Penetapan</span><a> </a><a>[574a]</a><span> </span><span>bagian ini </span><span> dari Allah. Sesungguhnya Allah Maha Mengetahui, Maha Bijaksana.</span></p><p><span>[b] </span><span>4:177.</span></p>');
INSERT INTO `quran` VALUES (6856,2,4,13,'<p><span>Dan, untukmu seperdua dari yang ditinggalkan oleh istri-istrimu jika mereka tidak mempunyai anak; tetapi, jika mereka mempunyai anak, maka untukmu seperempat dari apa yang ditinggalkan mereka, sesudah </span><span>melunasi</span><span> wasiat yang diwariskan mereka atau </span><span>melunasi</span><span> utang-utang. Dan, untuk mereka itu seperempat dari yang kamu tinggalkan jika kamu tidak mempunyai anak; namun , jika kamu mempunyai anak, maka untuk mereka seperdelapan dari yang kamu tinggalkan sesudah </span><span>melunasi </span><span> wasiat yang kamu wasiatkan atau</span><span> melunasi </span><span> utang-utangmu. </span><span>[a] </span><span>Dan, jika ada seorang laki-laki atau perempuan yang sudah tidak mempunyai bapak dan anak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=575">575</a><span>]</span><span> harta warisnya akan dibagikan, tetapi ia mempunyai seorang saudara laki-laki atau perempuan, maka tiap- tiap dari mereka akan mendapat seperenam. Namun, jika mereka lebih banyak dari itu maka mereka mendapat bersama-sama sepertiga sesudah </span><span>melunasi</span><span> wasiat yang diwasiatkannya atau </span><span>melunasi</span><span> utang-utangnya. </span><span>Dalam pembagian ini hendaklah </span><span> tanpa </span><span>maksud mendatangkan </span><span> kemudaratan </span><span>kepada siapa pun.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=575a">575a</a><span>]</span><span> Ini adalah perintah dari Allah, dan Allah Maha Mengetahui, Maha Penyantun.</span></p><p><span>[a] </span><span>4:177</span></p>');
INSERT INTO `quran` VALUES (6857,2,4,14,'<p><span>Ini adalah batas-batas yang </span><span>ditetapkan</span><span> Allah, dan </span><span>[a] </span><span>barangsiapa taat kepada Allah dan Rasul-Nya, </span><span>[b] </span><span>Dia akan memasukkannya ke dalam kebun-kebun yang di bawahnya mengalir sungai-sungai; mereka akan menetap di dalamnya; dan inilah kemenangan besar.</span></p><p><span>[a] </span><span>3:133; 8:21; 33:73.</span></p><p><span>[b] </span><span>Lihat 2:26.</span></p>');
INSERT INTO `quran` VALUES (6858,2,4,15,'<p><span>[c] </span><span>Dan, barangsiapa durhaka kepada Allah dan Rasul-Nya, dan melanggar batas-batas Nya, niscaya Dia akan memasukkannya ke dalam Api; ia akan tinggal lama di dalamnya; dan bagi nya azab yang menghinakan.</span></p><p><span>[c] </span><span>72:24.</span></p>');
INSERT INTO `quran` VALUES (6859,2,4,16,'<p><span>[d] </span><span>Dan, tentang siapa-siapa di antara perempuan-perempuanmu yang melakukan perbuatan keji,</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=576">576</a><span>]</span><span> maka carilah empat orang saksi terhadap mereka dari antaramu, jika mereka memberi kesaksian, maka tahanlah perempuan-perempuan itu di dalam rumah-rumah hingga datang kematian kepada mereka, atau Allah membukakan suatu jalan </span><span>lain</span><span> untuk mereka.</span></p><p><span>[d] </span><span>4:20, 26; 24:20.</span></p>');
INSERT INTO `quran` VALUES (6860,2,4,17,'<p><span>Dan, jika dua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=577">577</a><span>]</span><span> orang laki-laki di antaramu melakukan perbuatan keji, maka hukumlah keduanya. Jika kedua mereka itu bertobat dan memperbaiki diri, maka biarkanlah kedua mereka itu. Sesungguhnya Allah Maha Penerima taubat, Maha Penyanyang.</span></p>');
INSERT INTO `quran` VALUES (6861,2,4,18,'<p><span>Sesungguhnya tobat terhadap </span><span>[a]</span><span> Allah </span><span>dikabulkan </span><span> hanya bagi orang-orang yang berbuat keburukan karena kejahilan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=578">578</a><span>]</span><span> kemudian mereka bertobat dengan segera. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=579">579</a><span>]</span><span> Maka, mereka itulah yang Allah kembali kepadanya dengan kasih, dan Allah Maha Mengetahui Maha Bijaksana.</span></p><p><span>[a] </span><span>6:55; 16:120; 24:6.</span></p>');
INSERT INTO `quran` VALUES (6862,2,4,19,'<p><span>Dan tidaklah tobat itu </span><span>dikabulkan</span><span> bagi orang-orang yang </span><span>terus menerus</span><span> mengerjakan keburukan, sehingga apabila maut hadir kepada salah seorang dari antara mereka, ia berkata, “Sungguh-sungguh aku sekarang bertobat,” dan </span><span>[a] </span><span>tidak pula </span><span>diterima</span><span> bagi mereka yang mati dalam keadaan masih ingkar. Orang-orang inilah yang bagi mereka Kami sediakan azab yang pedih.</span></p><p><span>[a] </span><span>2:62; 3:92; 47:43.</span></p>');
INSERT INTO `quran` VALUES (6863,2,4,20,'<p><span>Hai orang-orang yang beriman, tidak halal bagimu mewarisi perempuan-perempuan dengan paksa; dan janganlah menahan mereka </span><span>dengan aniaya</span><span> agar kamu dapat mengambil kembali sebagian dari apa yang telah kamu berikan kepada mereka, </span><span>[b] </span><span>kecuali jika mereka melakukan perbuatan keji yang nyata; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=580">580</a><span>]</span><span> dan bergaullah dengan mereka secara baik-baik ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=581">581</a><span>]</span><span> </span><span>[a] </span><span>jika kamu tidak menyukai mereka, maka </span><span>ingatlah </span><span> bahwa boleh jadi kamu tidak menyukai sesuatu padahal Allah menjadikan di dalamnya banyak kebaikan.</span></p><p><span>[a] </span><span>2:217</span></p><p><span>[b] </span><span>Lihat 4:16</span></p>');
INSERT INTO `quran` VALUES (6864,2,4,21,'<p><span>Dan jika kamu hendak mengambil seorang istri untuk ganti yang lain dan kamu telah memberikan harta yang banyak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=582">582</a><span>]</span><span> kepada salah seorang dari mereka, maka janganlah mengambil </span><span>kembali</span><span> darinya walupun sedikit. Akan kamu ambiikan harta itu kembali dengan dusta dan dosa yang nyata?</span></p>');
INSERT INTO `quran` VALUES (6865,2,4,22,'<p><span>Dan, betapa kamu dapat mengambilnya </span><span>kembali</span><span> padahal sebagian kamu telah bercampur satu sama lain, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=583">583</a><span>]</span><span> sedangkan mereka telah mengambil dari kamu satu janji yang teguh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=584">584</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6866,2,4,23,'<p><span>Dan, janganlah kamu mengawini perempuan-perempuan yang pernah diwawini bapak-bapak mu, kecuali apa yang sudah lampau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=585">585</a><span>]</span><span> Sesungguhnya </span><span>hal</span><span> itu keji dan dibenci dan suatu jalan yang buruk.</span></p>');
INSERT INTO `quran` VALUES (6867,2,4,24,'<p><span>Telah diharamkan atasmu ibu-ibumu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=586">586</a><span>]</span><span> dan anak-anak perempuanmu dan saudara-saudara perempuanmu dan saudara-saudara perempuan bapakmu dan saudara-saudara perempuan ibumu, dan anak-anak perempuan saudara-saudara laki-lakimu dan anak-anak perempuan saudara perempuanmu dan ibu-ibumu yang menyusui kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=587">587</a><span>]</span><span> dan saudara-saudara perempuan sepesusuan denganmu, dan ibu-ibu istri-istrimu yang telah kamu campuri; tetapi jika kamu belum bercampur dengan mereka, maka tiada dosa bagimu </span><span>mengawini anaknya itu, dan diharamkan </span><span> istri-istri anak-anak lelakimu yang </span><span>lahir</span><span> dari sulbimu ( anak kandung ); dan </span><span>juga diharamkan bagi </span><span> kamu mengumpulkan dua orang perempuan bersaudara </span><span>sebagai istri-istrimu dalam satu waktu</span><span> kecuali apa yang telah lampau. Sesungguhnya, Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (6868,2,4,25,'<p><span>Juz 5</span></p><p><span>Dan, </span><span>diharamkan juga</span><span> perempuan-perempuan yang bersuami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=588">588</a><span>]</span><span> kecuali yang dimiliki tangan kananmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=589">589</a><span>]</span><span> </span><span> Allah telah menerapkan hukum ini atasmu. Dan, dihalalkan bagimu selain itu kalau kamu mencari </span><span>mereka</span><span> dengan hartamu </span><span>[a] </span><span>untuk dinikahi dan bukan untuk zina. Maka, untuk manfaat yang telah kamu ambil dari mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=590">590</a><span>] </span><span> </span><span>[b] </span><span>berikanlah kepada mereka maskawin mereka sebagaimana telah ditetapkan, dan tiada dosa bagimu tentang apa yang kamu telah saling merelakan setelah penetapan </span><span> maskawin</span><span> itu. Sesungguhnya Allah Maha Mengetahui, Maha Bijaksana.</span></p><p><span>[a] </span><span>4:26; 5:6.</span></p><p><span>[b] </span><span>4:5; 60:11.</span></p>');
INSERT INTO `quran` VALUES (6869,2,4,26,'<p><span>Dan, barangsiapa si antaramu tidak mampu membiayai pernikahan </span><span>dengan</span><span> perempuan-perempuan merdeka yang mukmin, maka </span><span>boleh ia menikah</span><span> dengan apa yang dimiliki tangan kananmu dari antara hamba-hamba sahaya perempuan mukmin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=591">591</a><span>]</span><span> Dan, Allah lebih mengetahui tentang keimananmu; sebagian di antara kamu dari yang lain </span><span>mempunyai pertalian</span><span>, maka nikahilah mereka dengan izin majikan-majikan mereka, dan berikanlah kepada mereka maskawin mereka dengan cara yan patut sebagai perempuan-perempuan yang memelihara kehormatannya, bukan pezina dan bukan pula suka mengambil kekasih-kekasih rahasia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=591a">591a</a><span>]</span><span> Dan, apabila perempuan-perempuan itu telah kawin lalu </span><span>[a]</span><span> mereka melakukan perbuatan keji, maka bagi mereka seperdua dari hukuman yang telah ditetapkan bagi perempuan-perempuan merdeka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=592">592</a><span>]</span><span> Ini adalah bagi orang di antaramu yang takut berbuat dosa. Dan, jika kamu bersabar, itu adalah lebih baik bagimu. Dan Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a] </span><span>4:16, 20; 24:20.</span></p>');
INSERT INTO `quran` VALUES (6870,2,4,27,'<p><span>[a]</span><span> Allah menghendaki untuk menjelaskan kepadamu cara-cara orang-orang sebelum kamu dan memberi petunjuk kepadamu dan berlaku kasih-sayang kepadamu. Dan, Allah Maha Mengetahui, Maha Bijaksana.</span></p><p><span>[a] </span><span>4:177.</span></p>');
INSERT INTO `quran` VALUES (6871,2,4,28,'<p><span>[b] </span><span>Dan, Allah menghendaki berlaku kasih-sayang kepadamu dan orang-orang yang menuruti hawa-nafsu menghendaki kamu bener-benar cenderung </span><span>kepada kejahatan</span><span>.</span></p><p><span>[b] </span><span>9:104; 33:74; 42:26.</span></p>');
INSERT INTO `quran` VALUES (6872,2,4,29,'<p><span>Allah menghendaki untuk meringankan </span><span>beban</span><span> dari kamu, dan karena manusia telah diciptakan lemah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=593">593</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6873,2,4,30,'<p><span>Hai orang-orang yang beriman, </span><span>[a] </span><span>janganlah kamu memakan hartamu sesamamu dengan batil, kecuali dengan perniagaan berdasar atas kerelaan dari antaramu. Dan, janganlah kamu membunuh dirimu. Sesungguhnya Allah Maha Penyayang terhadapmu.</span></p><p><span>[a] </span><span>2:189.</span></p>');
INSERT INTO `quran` VALUES (6874,2,4,31,'<p><span>Dan, barangsiapa berbuat demikian dengan melakukan pelanggaran dan keaniayaan, niscaya akan Kami lemparkan dia ke dalam Api. Dan hal demikian itu mudah bagi Allah.</span></p>');
INSERT INTO `quran` VALUES (6875,2,4,32,'<p><span>Jika kamu </span><span>[b] </span><span>menjauhi dosa-dosa besar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=594">594</a><span>]</span><span> yang kamu dilarang darinya, Kami akan hapuskan dari mu dosa-dosa dan kamu akan Kami masukkan ke tempat yang mulia.</span></p><p><span>[b] </span><span>42:38; 53:33.</span></p>');
INSERT INTO `quran` VALUES (6876,2,4,33,'<p><span>Dan, janganlah kamu berhasrat sesuatu yang oleh karenanya </span><span>[a]</span><span> Allah melebihkan sebagianmu dari yang lain apa yang diusahakan mereka. Dan bagi perempuan-perempuan ada bagian dari apa yang diusahakan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=595">595</a><span>]</span><span> Dan mohonlah kepada Allah bagian dari karunia-Nya. Sesungguhnya Allah Maha Mengetahui segala sesuatu.</span></p><p><span>[a] </span><span>4:35.</span></p>');
INSERT INTO `quran` VALUES (6877,2,4,34,'<p><span>Dan, bagi tiap-tiap </span><span>orang</span><span> telah Kami jadikan ahli waris </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=596">596</a><span>]</span><span> dari apa yang ditinggalkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=597">597</a><span>]</span><span> kedua orang tua dan kaum kerabat dan orang-orang yang diikat oleh janjimu maka, berikanlah kepada mereka bagian mereka. Sesungguhnya Allah Maha Penyaksi atas segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (6878,2,4,35,'<p><span>[a] </span><span>Laki-laki itu pelindung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=598">598</a><span>]</span><span> bagi perempuan-perempuan, karena </span><span>[b]</span><span> Allah</span><span> </span><span> telah melebihkan sebagian mereka di atas sebagian </span><span>yang lain</span><span>, dan disebabkan mereka membelanjakan sebagian dari harta mereka, Maka perempuan-perempuan saleh ialah yang taat dan menjaga rahasia- rahasia </span><span>suami mereka </span><span> dari apa-apa yang telah dilindungi Allah. Dan, perempuan- perempuan yang kamu khawatirkan kedurhakaan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=599">599</a><span>]</span><span> maka nasihatilah mereka, dan jauhilah mereka di tempat tidur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=600">600</a><span>]</span><span> dan pukullah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=601">601</a><span>]</span><span> mereka. Kemudian jika mereka taat kepadamu, maka janganlah kamu mencari jalan </span><span>menyusahkan</span><span> mereka. Sesungguhnya Allah</span><span> </span><span> Maha Tinggi, Maha Besar.</span></p><p><span>[a] </span><span>2:229.</span></p><p><span>[b] </span><span>2:238; 4:44.</span></p>');
INSERT INTO `quran` VALUES (6879,2,4,36,'<p><span>Dan, </span><span>[a] </span><span>jika kamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=602">602</a><span>]</span><span> mengkhawatirkan terjadi perpecahan di antara mereka, maka angkatlah seorang juru damai dari keluarga laki-laki dan seorang juru-damai dari keluarga perempuan; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=603">603</a><span>]</span><span> jika kedua </span><span>juru-damai</span><span> itu menghendaki perdamaian, niscaya Allah</span><span> </span><span> akan memberi persesuaian di antara kedua suami-istri. Sesungguhnya Allah</span><span> </span><span> Maha Mengetahui, Maha Waspada.</span></p><p><span>[a] </span><span>4:129</span></p>');
INSERT INTO `quran` VALUES (6880,2,4,37,'<p><span>Dan, </span><span>[a] </span><span>sembahlah Allah</span><span> </span><span> dan jangan kamu mempersekutukan sesuatu dengan-Nya; dan </span><span>berbuat</span><span> baiklah terhadap kedua orang tua, dan kaum kerabat, dan anak-anak yatim, dan orang-orang miskin, dan tetangga yang sesanak-saudara dan tetangga yang bukan kerabat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=604">604</a><span>]</span><span> dan handai taulan, dan orang musafir, dan yang dimiliki oleh tangan kananmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=605">605</a><span>]</span><span> Sesungguhnya, Allah tidak menyukai orang sombong, membanggakan diri.</span></p><p><span>[a] </span><span>6:152; 7:34; 17:24, 25; 23:60.</span></p>');
INSERT INTO `quran` VALUES (6881,2,4,38,'<p><span>[b] </span><span>Orang-orang yang bakhil dan menyuruh manusia supaya bakhil, dan menyembunyikan apa yang diberikan Allah kepada mereka dari karunia-Nya. Dan, Kami telah menyediakan bagi orang- orang kafir, azab yang menghinakan.</span></p><p><span>[b] </span><span>3:181; 17:30; 25:68.</span></p>');
INSERT INTO `quran` VALUES (6882,2,4,39,'<p><span>Dan, </span><span>[a] </span><span>orang-orang yang membelanjakan harta mereka untuk dilihat manusia, dan mereka tidak beriman kepada Allah dan tidak pula kepada Hari Kemudian. Dan, </span><span>[b] </span><span>siapa yang menjadikan syaitan sebagai kawannya, maka itulah seburuk-buruknya kawan.</span></p><p><span>[a] </span><span>2:265.</span></p><p><span>[b] </span><span>43:37, 39.</span></p>');
INSERT INTO `quran` VALUES (6883,2,4,40,'<p><span>Dan, apakah </span><span>ruginya</span><span> atas mereka seandainya mereka beriman kepada Allah dan Hari Kemudian, dan membelanjakan apa-apa yang telah direzekikan Allah kepada mereka ? Dan, Allah Maha Mengetahui tentang mereka.</span></p>');
INSERT INTO `quran` VALUES (6884,2,4,41,'<p><span>Sesungguhnya </span><span>[a]</span><span> Allah tidak akan menganiaya </span><span>seseorang</span><span> walaupun sebesar zarah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=606">606</a><span>]</span><span> Dan, jika ada sesuatu kebaikan, Dia melipatgandakan dan memberi ganjaran besar dari sisi-Nya.</span></p><p><span>[a] </span><span>10:45; 18:50; 28:85.</span></p>');
INSERT INTO `quran` VALUES (6885,2,4,42,'<p><span>Maka, bagaimana </span><span>keadaan mereka </span><span> </span><span>[a] </span><span>ketika Kami akan mendatangkan seorang saksi dari setiap umat, dan Kami akan mendatangkan engkau sebagai saksi terhadap mereka ini ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=607">607</a><span>]</span></p><p><span>[a] </span><span>16:90.</span></p>');
INSERT INTO `quran` VALUES (6886,2,4,43,'<p><span>Pada hari itu </span><span>[a] </span><span>orang-orang yang ingkar dan mendurhakai Rasul akan menghendaki supaya mereka disamaratakan dengan bumi, dan mereka tak akan </span><span>dapat</span><span> menyembunyikan sesuatu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=608">608</a><span>]</span><span> dari Allah.</span></p><p><span>[a] </span><span>78:41</span></p>');
INSERT INTO `quran` VALUES (6887,2,4,44,'<p><span>Hai orang-orang beriman, janganlah kamu mendekati shalat bila kamu tidak berada dalam keadaan sadar </span><span>sepenuhnya,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=609">609</a><span>]</span><span> sampai kamu mengetahui apa yang kamu ucapkan, dan jangan pula ketika kamu dalam keadaan junub </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=610">610</a><span>]</span><span> hingga kamu telah mandi kecuali kali kamu sedang bepergian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=611">611</a><span>]</span><span> Dan, jika kamu sakit atau dalam perjalanan atau seseorang di antaramu datang setelah buang air atau kamu campur dengan istri-istrimu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=612">612</a><span>]</span><span> dan kamu tidak mendapat air, maka hendaklah kamu tayammum dengan tanah suci, kemudian sapulah mukamu dan tanganmu. Sesungguhnya Allah Maha Pemaaf, Maha Pengampun.</span></p>');
INSERT INTO `quran` VALUES (6888,2,4,45,'<p><span>Tidaklah engkau memperhatikan orang-orang yang diberi sebagian dari Kitab ? </span><span>[a] </span><span>Mereka membeli kesesatan dan menghendaki supaya kamu sesat dari jalan itu.</span></p><p><span>[a] </span><span>4:90.</span></p>');
INSERT INTO `quran` VALUES (6889,2,4,46,'<p><span>Dan, Allah Maha Mengetahui benar musuh-musuhmu. Dan, cukuplah Allah sebagai </span><span>[b] </span><span>Pelindung, dan cukuplah Allah sebagai Penolong.</span></p><p><span>[b] </span><span>4:174; 33:18.</span></p>');
INSERT INTO `quran` VALUES (6890,2,4,47,'<p><span>Di antara orang-orang Yahudi ada yang </span><span>[c] </span><span>mengubah-ubah kalimat-kalimat Allah dari tempat-nya. Dan mereka berkata, “Kami dengar dan kami tolak dan dengarlah </span><span>kami</span><span> semoga </span><span>firman Tuhan</span><span> tidak diperdengarkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=613">613</a><span>]</span><span> kepada </span><span>engkau</span><span>, “ dan mereka berkata, </span><span>[d] </span><span>”Ra</span><span>’</span><span>ina,” dengan memutarbalikkan lidah mereka dan mencela agama. Dan, jika sekiranya mereka berkata, “Kami dengar dan taat” dan, “dengarlah,” dan, </span><span>[e] </span><span>”unzhurnaa” niscaya hal ini lebih baik bagi mereka karena kekufuran mereka; maka, tidaklah mereka beriman malinkan sedikit.</span></p><p><span>[c] </span><span>2:76; 3:79; 5:42.</span></p><p><span>[d] </span><span>2:105.</span></p><p><span>[e] </span><span>2:105</span></p>');
INSERT INTO `quran` VALUES (6891,2,4,48,'<p><span>Hai orang-orang yang telah diberi Kitab, berimanlah kepada apa yang telah Kami turunkan, menggenapi apa yang telah ada padamu, </span><span>[a] </span><span>sebelum Kami membinasakan pemuka-pemuka </span><span>di antaramu</span><span> dan Kami membalikkan mereka ke belakang mereka, atau Kami melaknat mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=614">614</a><span>]</span><span> seperti </span><span>[b] </span><span>Kami telah melaknat orang-orang Sabat. Dan, perintah Allah </span><span>pasti</span><span> akan terlaksana.</span></p><p><span>[a] </span><span>10:89.</span></p><p><span>[b] </span><span>2:66; 4:155; 7:164; 16:125.</span></p>');
INSERT INTO `quran` VALUES (6892,2,4,49,'<p><span>Sesungguhnya </span><span>[c]</span><span> Allah tidak akan mengampuni jika sesuatu dipersekutukan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=615">615</a><span>]</span><span> dengan-Nya dan Dia akan mengampuni selain dari itu bagi siapa yang Dia kehendaki. Dan, barangsiapa mempersekutukan Allah, maka sesungguhnya ia telah berbuat dosa besar.</span></p><p><span>[e] </span><span>4:117.</span></p>');
INSERT INTO `quran` VALUES (6893,2,4,50,'<p><span>Tidaklah engkau memperhatikan orang-orang yang menganggap diri mereka suci ? Bahkan hanya Allah yang mensucikan siapa yang Dia kehendaki dan </span><span>[a] </span><span>mereka tidak akan dianiaya sebesar alur biji korma.</span></p><p><span>[a] </span><span>4:78, 125; 17:72.</span></p>');
INSERT INTO `quran` VALUES (6894,2,4,51,'<p><span>Lihatlah, betapa mereka </span><span>[a] </span><span>mengada-adakan dusta terhadap Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=616">616</a><span>]</span><span> Dan, dengan itu cukuplah sebagai dosa yang nyata.</span></p><p><span>[a] </span><span>5:104; 10:70; 16:117.</span></p>');
INSERT INTO `quran` VALUES (6895,2,4,52,'<p><span>Tidaklah engkau memperhatikan orang-orang yang diberi sebagian dari Kitab ? Mereka mempercayai sesuatu yang sia-sia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=617">617</a><span>]</span><span> dan </span><span>mengikuti</span><span> orang-orang yang melanggar batas, dan mereka mengatakan tentang orang-orang yang ingkar, “Inilah orang-orang yang lebih mendapat jalan petunjuk daripada orang-orang yang beriman.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=618">618</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6896,2,4,53,'<p><span>Inilah orang-orang </span><span>[a] </span><span>yang mereka dilaknat Allah, dan barangsiapa dilaknat Allah maka engkau tidak akan memperoleh baginya seorang penolong.</span></p><p><span>[a] </span><span>2:160; 3:87, 88.</span></p>');
INSERT INTO `quran` VALUES (6897,2,4,54,'<p><span>Ataukah mereka mempunyai bagian dalam kerajaan ? Andaikan ada, maka mereka tidak akan memberi </span><span>faedah</span><span> kepada manusia sekecil lobang yang ada pada biji korma.</span></p>');
INSERT INTO `quran` VALUES (6898,2,4,55,'<p><span>Ataukah mereka menaruh dengki terhadap manusia tentang apa yang telah diberikan Allah kepada mereka dari karunia-Nya ? Maka sesungguhnya telah Kami berikan Kitab dan Hikmah kepada keturunan Ibrahim, dan telah Kami berikan </span><span>juga</span><span> kepada mereka suatu kerajaan besar.</span></p>');
INSERT INTO `quran` VALUES (6899,2,4,56,'<p><span>[a] </span><span>Maka, di antara mereka ada yang beriman kepadanya, dan diantara mereka ada yang berpaling darinya. Dan, cukuplah kobaran api Jahannam </span><span>bagi mereka</span><span>.</span></p><p><span>[a] </span><span>2:254; 10:41; 61:15.</span></p>');
INSERT INTO `quran` VALUES (6900,2,4,57,'<p><span>Sesungguhnya orang-orang yang telah ingkar kepada Ayat-ayat Kami niscaya segera akan Kami masukkan mereka ke dalam Api. Dan setiap kali kulit mereka hangus, Kami menggantikannya dengan kulit lainnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=619">619</a><span>]</span><span> supaya mereka merasakan azab itu. Sesungguhnya adalah, Allah Maha Perkasa, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (6901,2,4,58,'<p><span>Dan </span><span>[a] </span><span>orang-orang yang beriman dan beramal saleh, kelak akan Kami masukkan ke dalam kebun-kebun yang di bawahnya mengalir sungai-sungai; mereka akan tinggal di dalamnya untuk selama-lamanya; bagi mereka di dalamnya ada jodoh-jodoh suci, dan akan Kami masukkan mereka ke </span><span>tempat </span><span> yang </span><span>[b] </span><span>teduh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=620">620</a><span>]</span><span> lagi nyaman.</span></p><p><span>[a] </span><span>4:123; 13:30; 14:24; 22:24; Lihat juga 2:26.</span></p><p><span>[b] </span><span>13:36; 56:31.</span></p>');
INSERT INTO `quran` VALUES (6902,2,4,59,'<p><span>Sesungguhnya, Allah memerintahkan kamu supaya </span><span>[a] </span><span>menyerahkan amanat-amanat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=621">621</a><span>]</span><span> kepada yang berhak menerimanya, dan apabila kamu menghakimi di antara manusia hendaklah kamu memutuskan dengan adil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=622">622</a><span>] </span><span>Sesungguhnya Allah menasihatimu sebaik-baiknya dengan cara itu. Sesungguhnya Allah Maha Mendengar, Maha Melihat.</span></p><p><span>[a] </span><span>8:28</span></p>');
INSERT INTO `quran` VALUES (6903,2,4,60,'<p><span>Hai orang-orang yang beriman,taatlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=623">623</a><span>]</span><span> kepada Allah, dan taatlah kepada Rasul-Nya, dan kepada </span><span>[a] </span><span>orang-orang yang memegang kekuasaan di antaramu. Dan, </span><span>[b] </span><span>jika kamu berselisih mengenai sesuatu, maka kembalikanlah hal itu kepada Allah dan Rasul-</span><span>Nya</span><span>, jika kamu memang beriman kepada Allah dan Hari Kemudian. Hal demikian itu paling baik dan paling bagus akibatnya.</span></p><p><span>[a] </span><span>4:84.</span></p><p><span>[b] </span><span>4:66.</span></p>');
INSERT INTO `quran` VALUES (6904,2,4,61,'<p><span>Tidaklah engkau memperhatikan orang-orang yang mengaku bahwa mereka telah beriman kepada apa yang diturunkan kepada engkau dan kepada apa yang diturunkan sebelum engkau ? Mereka ingin berhakim kepada orang durhaka, padahal mereka telah diperintahkan supaya menolaknya. Dan, syaitan ingin menyesatkan mereka </span><span>menuju</span><span> kesesatan yang sejauh-jauhnya.</span></p>');
INSERT INTO `quran` VALUES (6905,2,4,62,'<p><span>Dan, </span><span>[a] </span><span>apabila dikatakan kepada mereka, “Marilah kepada apa yang diturunkan Allah dan kepada Rasul-</span><span>Nya</span><span>, “ engkau melihat orang-orang munafik benar-benar berpaling dari engkau.</span></p><p><span>[a] </span><span>63:6</span></p>');
INSERT INTO `quran` VALUES (6906,2,4,63,'<p><span>Maka, bagaimanakah apabila suatu musibah menimpa mereka disebabkan oleh apa yang telah dibuat tangan mereka, lalu mereka datang kepada engkau dengan bersumpah, “Demi Allah kami tidak bermaksud kecuali kebaikan dan perdamaian.”</span></p>');
INSERT INTO `quran` VALUES (6907,2,4,64,'<p><span>Mereka itulah orang-orang yang Allah mengetahui apa-apa yang ada dalam hati mereka. Maka, berpalinglah dari mereka dan nasihatilah mereka dan katakanlah kepada mereka perkataan yang berguna bagi diri mereka. </span></p><p><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=624">624</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6908,2,4,65,'<p><span>Dan, tidak Kami utus seorang rasul melainkan supaya ia ditaati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=625">625</a><span>]</span><span> dengan izin Allah. Dan, jika mereka datang kepada engkau ketika mereka telah menganiaya </span><span>[a] </span><span>diri mereka sendiri, lalu mereka memohon ampun kepada Allah, dan Rasul </span><span>juga</span><span> memintakan ampun bagi mereka, niscaya akan mereka dapati Allah Maha Penerima taubat, Maha Penyayang.</span></p><p><span>[a] </span><span>4:111.</span></p>');
INSERT INTO `quran` VALUES (6909,2,4,66,'<p><span>Tidak, demi Tuhan engkau, </span><span>[a] </span><span>mereka tidak beriman sebelum mereka menjadikan engkau sebagai hakim dalam segala apa yang menjadi perselisihan di antara mereka, kemudian mereka tidak mendapati suatu keberatan dalam hati mereka tentang apa yang telah engkau putuskan serta mereka menerima dengan sepenuh penerimaan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=626">626</a><span>]</span></p><p><span>[a] </span><span>4:60.</span></p>');
INSERT INTO `quran` VALUES (6910,2,4,67,'<p><span>Dan, jika Kami memerintahkan mereka, </span><span>[a] </span><span>”Bunuhlah dirimu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=627">627</a><span>]</span><span> atau keluarlah dari kampung-halamanmu,” tidaklah mereka akan mengerjakannya kecuali sebagian kecil dari antara mereka; dan jika mereka mengerjakan apa yang dinasihatkan tentang hal itu, niscaya akan lebih baik bagi mereka dan lebih meneguhkan.</span></p><p><span>[a] </span><span>6:78.</span></p>');
INSERT INTO `quran` VALUES (6911,2,4,68,'<p><span>Dan, jika demikian, tentu akan Kami berikan kepada mereka ganjaran besar dari sisi Kami.</span></p>');
INSERT INTO `quran` VALUES (6912,2,4,69,'<p><span>Dan, pasti akan </span><span>[a] </span><span>Kami bimbing mereka ke jalan lurus.</span></p><p><span>[a] </span><span>19:37; 36:62; 42:53, 54.</span></p>');
INSERT INTO `quran` VALUES (6913,2,4,70,'<p><span>Dan, </span><span>[a] </span><span>barangsiapa taat kepada Allah dan Rasul ini maka mereka akan termasuk di antara </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=628">628</a><span>] </span><span>orang-orang </span><span>[b] </span><span>yang kepada mereka Allah memberikan nikmat, yakni : nabi-nabi, shiddiq-shiddiq, syahid-syahid, dan orang-orang saleh. Dan, mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=629">629</a><span>]</span><span> itulah sahabat yang sejati.</span></p><p><span>[a] </span><span>4:14; 8:25.</span></p><p><span>[b] </span><span>1:7; 5:21; 19:59; 57:20.</span></p>');
INSERT INTO `quran` VALUES (6914,2,4,71,'<p><span>Ini karunia dari Allah dan cukuplah Allah Yang Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (6915,2,4,72,'<p><span>Hai orang-orang yang beriman, ambillah peralatan-peralatanmu; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=630">630</a><span>]</span><span> kemudian keluarlah beregu- regu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=631">631</a><span>]</span><span> atau keluarlah bersama-sama.</span></p>');
INSERT INTO `quran` VALUES (6916,2,4,73,'<p><span>Dan, sesungguhnya di antaramu ada yang suka menunggu di belakang, maka jika suatu musibah menimpamu, berkata ia, “Sesungguhnya Allah memberi nikmat kepadaku, karena aku tidak hadir bersama mereka.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=632">632</a><span>]</span></p>');
INSERT INTO `quran` VALUES (6917,2,4,74,'<p><span>Tetapi, jika sampai kepadamu suatu karunia dari Allah, tentulah ia mengatakan, seolah-olah tak ada kasih-sayang antaramu dengan dia, “Alangkah baiknya jika aku bersama mereka sehingga aku memperoleh kemenangan besar !”</span></p>');
INSERT INTO `quran` VALUES (6918,2,4,75,'<p><span>Maka hendaklah mereka berperang di jalan Allah, yaitu </span><span>[a] </span><span>orang-orang yang menukar kehidupan dunia dengan akhirat. Dan barangsiapa berperang di jalan Allah, lalu ia </span><span>[b] </span><span>terbunuh atau ia memperoleh kemenangan maka Kami segera akan memberinya ganjaran besar.</span></p><p><span>[a] </span><span>9:111.</span></p><p><span>[b] </span><span>9:52.</span></p>');
INSERT INTO `quran` VALUES (6919,2,4,76,'<p><span>Dan, mengapakah kamu tidak mau berperang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=632a">632a</a><span>]</span><span> di jalan Allah dan </span><span>demi membela</span><span> </span><span>[a] </span><span>orang-orang lemah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=633">633</a><span>]</span><span> laki-laki, perempuan-perempuan dan anak-anak, yang berkata, “Hai Tuhan kami keluarkanlah kami dari kota yang penduduknya kejam ini dan jadikanlah bagi kami seorang sahabat dari sisi Engkau, dan jadikanlah bagi kami seorang penolong dari sisi Engkau.”</span></p><p><span>[a] </span><span>4:99.</span></p>');
INSERT INTO `quran` VALUES (6920,2,4,77,'<p><span>Orang-orang yang beriman berperang di jalan Allah, sedangkan orang-orang yang ingkar berperang di jalan syaitan. Maka, perangilah olehmu kawan-kawan syaitan; sesungguhnya tipu daya syaitan itu lemah.</span></p>');
INSERT INTO `quran` VALUES (6921,2,4,78,'<p><span>Tidaklah engkau memperhatikan orang-orang yang telah dikatakan kepada mereka, “Tahanlah tanganmu dan dirikanlah shalat dan bayarlah zakat.” Akan tetapi, </span><span>[a] </span><span>ketika perang diwajibkan atas mereka, tiba-tiba segolongan dari mereka takut kepada manusia seperti takut kepada Allah atau lebih takut lagi; dan mereka berkata, “Ya Tuhan kami mengapa Engkau mewajibkan atas diri kami berperang ? </span><span>[b] </span><span>Mangapa tidak Engkau beri kami tenggang waktu sedikit lagi ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=634">634</a><span>]</span><span> Katakanlah, </span><span>[c] </span><span>”Keuntungan di dunia ini hanya sedikit dan </span><span>kehidupan</span><span> akhirat itu lebih baik bagi orang yang bertakwa; dan, </span><span>[d] </span><span>kamu tidak akan dianiaya sebesar alur biji korma.</span></p><p><span>[a] </span><span>2:247; 4:67.</span></p><p><span>[b] </span><span>14:45; 63:11.</span></p><p><span>[c] </span><span>9:38; 57:21.</span></p><p><span>[d] </span><span>Lihat 4:50.</span></p>');
INSERT INTO `quran` VALUES (6922,2,4,79,'<p><span>Di mana saja kamu berada </span><span>[a] </span><span>maut akan mendatangi kamu sekali pun kamu ada di dalam benteng yang kokoh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=635">635</a><span>]</span><span> Dan, jika mereka memperoleh suatu kebaikan, mereka berkata, “Ini adalah dari sisi Allah;” dan jika mereka ditimpa suatu keburukan, mereka berkata, “Ini adalah dari engkau.” Katakanlah, “Segala sesuatu adalah dari Allah.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=636">636</a><span>]</span><span> Maka, apakah yang telah terjadi dengan kaum ini sehingga mereka hampir-hampir tidak memahani perkataan ?</span></p><p><span>[a] </span><span>62:9.</span></p>');
INSERT INTO `quran` VALUES (6923,2,4,80,'<p><span>Kebaikan apa saja yang sampai kepada engkau itu adalah dari Allah; dan keburukan apa saja yang menimpa engkau, itu adalah dari engkau sendiri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=637">637</a><span>]</span><span> Dan,telah Kami utus engkau sebagai Rasul kepada manusia. Dan, cukuplah Allah sebagai Saksi.</span></p>');
INSERT INTO `quran` VALUES (6924,2,4,81,'<p><span>Barangsiapa taat kepada Rasul, maka sebenarnya ia taat kepada Allah; dan barangsiapa berpaling, maka Kami tidak mengutus engkau sebagai penjaga atas mereka.</span></p>');
INSERT INTO `quran` VALUES (6925,2,4,82,'<p><span>Dan mereka mengatakan “</span><span>Kami</span><span> taat,” tetapi, ketika mereka telah berangkat dari sisi engkau, segolongan dari mereka </span><span>[a] </span><span>pada malam hari merencanakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=638">638</a><span>]</span><span> yang lain dari yang engkau katakan. Dan, Allah mencatat apa yang direncanakan mereka pada malam hari itu. Maka, jauhilah mereka dan tawakallah kepada Allah. Dan, cukuplah Allah sebagai Pengelola segala urusan.</span></p><p><span>[a] </span><span>4:100.</span></p>');
INSERT INTO `quran` VALUES (6926,2,4,83,'<p><span>Maka, </span><span>[a] </span><span>tidakkah mereka ingin merenungkan Alquran ? Dan, andaikata </span><span>Alquran</span><span> ini bukan dari sisi Allah, niscaya mereka akan mendapati di dalamnya banyak pertentangan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=639">639</a><span>]</span></p><p><span>[a] </span><span>47:25.</span></p>');
INSERT INTO `quran` VALUES (6927,2,4,84,'<p><span>Dan, apabila datang kepada mereka suatu berita mengenai keamanan atau ketakutan, mereka menyebarkannya; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=640">640</a><span>]</span><span> padahal, jika mereka menyerahkan hal itu kepada Rasul dan kepada </span><span>[a] </span><span>orang-orang yang memegang, kekuasaan di antara mereka, niscacya akan diketahuinya oleh orang-orang dari mereka yang dapat menyelidikinya. Dan, sekiranya bukan karena karunia Allah atasmu dan rahmat-Nya, niscaya kamu akan mengikut syaitan, kecuali sebagian kecil.</span></p><p><span>[a] </span><span>4:60.</span></p>');
INSERT INTO `quran` VALUES (6928,2,4,85,'<p><span>Oleh karena itu, berperanglah di jalan Allah, tidaklah engkau dibebani tanggungjawab kecuali mengenai diri engkau sendiri, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=641">641</a><span>]</span><span> dan </span><span>[a] </span><span>kobarkanlah semangat orang-orang mukmin. Mudah-mudahan Allah akan menahan serangan orang-orang ingkar; dan Allah lebih keras serangan-Nya dan lebih berat pula hukuman-Nya.</span></p><p><span>[a] </span><span>8:66.</span></p>');
INSERT INTO `quran` VALUES (6929,2,4,86,'<p><span>Barangsiapa memberi syafaat yang baik, tentu untuknya ada bagian darinya, dan barangsiapa memberi syafaat yang buruk, tentu untuknya ada bagian darinya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=642">642</a><span>]</span><span> dan Allah Maha Menguasai segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (6930,2,4,87,'<p><span>Dan, apabila kamu diberi ucapan salam, maka ucapkanlah salam yang lebih baik dari itu atau balaslah sebandingnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=643">643</a><span>]</span><span> Sesungguhnya Allah senantiasa membuat perhitungan atas segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (6931,2,4,88,'<p><span>Allah, tidak ada Tuhan selain Dia. Dia pasti akan mengumpulkan kamu sampai Hari Kiamat, tiada keraguan di dalamnya. Dan, siapakah yang lebih benar perkataannya dari Allah ?</span></p>');
INSERT INTO `quran` VALUES (6932,2,4,89,'<p><span>Maka, apakah yang terjadi dengan dirimu sehingga kamu menjadi dua golongan mengenai orang- orang munafik ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=644">644</a><span>]</span><span> Padahal Allah telah menjerumuskan mereka disebabkan oleh apa yang telah diusahakan mereka. Inginkah kamu memberi petunjuk orang yang telah disesatkan Allah ? Dan, barangsiapa yang dibinasakan Allah, maka engkau tak akan mendapat baginya suatu jalan.</span></p>');
INSERT INTO `quran` VALUES (6933,2,4,90,'<p><span>[a] </span><span>M</span><span>ereka ingin kali kamu ingkar seperti mereka telah ingkar sehingga kamu menjadi sama. Maka janganlah kamu mengabil dari antara mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=645">645</a><span>]</span><span> menjadi sahabat-sahabat sebelum mereka berhijrah di jalan Allah. Maka, jika mereka berpaling, tangkaplah mereka dan bunuhlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=646">646</a><span>]</span><span> mereka di mana saja mereka kamu jumpai; dan janganlah kamu mengambil seorang di antara mereka menjadi sahabat dan tidak </span><span>pula</span><span> mengambil seorang menjadi penolong.</span></p><p><span>[a] </span><span>2:110; 4:45; 14:4.</span></p>');
INSERT INTO `quran` VALUES (6934,2,4,91,'<p><span>Kecuali orang-orang yang mempunyai hubungan dengan suatu kaum yang di antara kamu dengan mereka ada suatu perjanjian, atau mereka datang kepadamu sementara hati mereka kecut bila hendak memerangi kamu atau memerangi kaum mereka </span><span>sendiri</span><span>. Dan, jika Allah menghendaki niscaya Dia akan memberi kekuasaan kepada mereka atasmu, maka pasti mereka memerangi kamu. Tetapi, jika mereka menjauhkan diri dari kamu dan tidak memerangimu dan menawarkan kedamaian kepadamu, maka tidaklah Allah mengadakan jalan bagimu untuk </span><span>menyerang</span><span> mereka.</span></p>');
INSERT INTO `quran` VALUES (6935,2,4,92,'<p><span>Tentulah kamu akan menjumpai golongan lain yang menghendaki supaya mendapat keamanan dari kamu dan mendapat keamanan </span><span>pula</span><span> dari kaum mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=647">647</a><span>]</span><span> Bila saja mereka dikembalikan kepada fitnah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=648">648</a><span>]</span><span> </span><span>[a] </span><span>maka mereka terjun ke dalamnya. Maka jika mereka tidak menjauhkan diri dari kamu dan tidak menawarkan perdamaian kepadamu dan tidak pula menahan tangan mereka, maka </span><span>[b] </span><span>tangkaplah mereka dan bunuhlah mereka di mana saja kamu dapati mereka. Dan untuk melawan mereka, Kami beri kamu kekuasaan nyata.</span></p><p><span>[a] </span><span>33:15.</span></p><p><span>[b] </span><span>9:5.</span></p>');
INSERT INTO `quran` VALUES (6936,2,4,93,'<p><span>Dan tidak layak bagi seorang mukmin membunuh seorang mukmin kecuali jika tidak sengaja. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=649">649</a><span>]</span><span> Dan, barangsiapa membunuh seorang mukmin tidak dengan sengaja, maka </span><span>wajiblah</span><span> ia memerdekakan seorang budak yang mukmin dan </span><span>membayar</span><span> tebusan untuk diserahkan kepada ahli waris si terbunuh, kecuali jika mereka merelakan sebagai sedekah. Tetapi, jika ia </span><span>yang terbunuh</span><span> itu dari kaum yang bermusuhan denganmu, dan ia seorang mukmin, maka </span><span>cukuplah</span><span> memerdekakan seorang budak mukmin; dan jika ia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=650">650</a><span>]</span><span> dari kaum yang di antara kamu dan mereka ada suatu perjanjian persekutuan, maka </span><span>bayarlah</span><span> tebusan untuk diserahkan kepada ahli warisnya dan memerdekakan pula seorang budak mukmin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=651">651</a><span>]</span><span> Maka barangsiapa tidak memperoleh budak maka </span><span>[a] </span><span>wajiblah</span><span> ia berpuasa dua bulan berturut-turut, </span><span>keringanan </span><span> ini suatu kasih-sayang dari Allah. Dan Allah Maha Mengetahui, Maha Bijaksana.</span></p><p><span>[a] </span><span>58:5.</span></p>');
INSERT INTO `quran` VALUES (6937,2,4,94,'<p><span>Dan, </span><span>[a] </span><span>barangsiapa membunuh seorang mukmin dengan sengaja, maka balasannya ialah Jahannam; ia akan tinggal lama di dalamnya, dan Allah murka kepadanya dan melaknatnya dan akan menyediakan baginya azab yang besar.</span></p><p><span>[a] </span><span>25:69, 70.</span></p>');
INSERT INTO `quran` VALUES (6938,2,4,95,'<p><span>Hai orang-orang yang beriman, apabila kamu pergi </span><span>berjihad</span><span> di jalan Allah, </span><span>[a] </span><span>maka selidikilah sebaik-baiknya dan janganlah kamu mengatakan kepada orang yang memberi salam kepadamu, “Engkau bukan mukmin.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=652">652</a><span>]</span><span> Kamu hendak mencari harta kehidupan di dunia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=653">653</a><span>] </span><span>padahal di sisi Allah banyak harta kekayaan. Demikianlah keadaanmu dahulu, lalu Allah memberi karunia kepadamu; oleh sebab itu selidikilah. Sesungguhnya, Allah itu Maha Mengetahui mengenai apa-apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>49:7.</span></p>');
INSERT INTO `quran` VALUES (6939,2,4,96,'<p><span>Tidak sama </span><span>[a] </span><span>orang-orang mukmin yang duduk </span><span>di rumah,</span><span> selain orang-orang uzur, dengan mereka yang berjihad di jalan Allah dengan harta mereka dan diri mereka. Allah melebihkan derajat orang-orang yang berjihad dengan harta mereka dan diri mereka daripada orang-orang yang duduk </span><span>di rumah</span><span>. Dan untuk masing-masing Allah telah menjanjikan kebaikan. Dan Allah melebihkan orang-orang yang berjihad atas mereka yang duduk </span><span>di rumah</span><span> dengan ganjaran yang besar; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=654">654</a><span>]</span></p><p><span>[a] </span><span>9:19, 20; 57:11.</span></p>');
INSERT INTO `quran` VALUES (6940,2,4,97,'<p><span>Dengan anugerah</span><span> beberapa derajat dari-Nya, dan ampunan serta rahmat. Dan Allah itu Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (6941,2,4,98,'<p><span>Sesungguhnya </span><span>[a] </span><span>orang-orang yang malaikat mewafatkan mereka dalam keadaan aniaya terhadap diri mereka akan ditanya </span><span>oleh malaikat</span><span>, “Bagaimana keadaanmu dahulu ?” Mereka akan menjawab, “Kami dahulu di pandang lemah di muka bumi,” Berkata </span><span>malaikat</span><span>, “Tidaklah bumi Allah itu luas untuk kamu berhijrah di dalamnya ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=655">655</a><span>]</span><span> Maka, mereka inilah yang tempat tinggalnya Jahannam dan alangkah buruknya tempat kembali itu.</span></p><p><span>[a] </span><span>16:20.</span></p>');
INSERT INTO `quran` VALUES (6942,2,4,99,'<p><span>Kecuali </span><span>[a] </span><span>orang-orang lemah di antara laki-laki dan perempuan dan anak-anak yang tidak mampu berdaya-upaya dan tidak pula mendapatkan suatu jalan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=656">656</a><span>]</span></p><p><span>[a] </span><span>4:76.</span></p>');
INSERT INTO `quran` VALUES (6943,2,4,100,'<p><span>Tentang mereka ini mudah-mudahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=657">657</a><span>]</span><span> Allah akan memaafkan mereka; dan Allah itu Maha Pemaaf, Maha Pengampun.</span></p>');
INSERT INTO `quran` VALUES (6944,2,4,101,'<p><span>Dan, barangsiapa berhijrah di jalan Allah, niscaya di bumi akan memperoleh banyak tempat perlindungan dan kelapangan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=658">658</a><span>]</span><span> Dan, barangsiapa keluar dari rumahnya hendak berhijrah kepada Allah dan Rasul-Nya, kemudian sampailah maut kepadanya, maka sesungguhnya telah tersedia ganjarannya pada Allah dan Allah itu Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (6945,2,4,102,'<p><span>Dan, apabila kamu bepergian di muka bumi,</span><span>[a] </span><span>maka tak ada dosa bagimu mengqashar shalat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=659">659</a><span>] </span><span>jika kamu takut orang-orang akan menyusahkan kamu. Sesungguhnya, orang-orang kafir bagimu musuh yang nyata.</span></p><p><span>[a] </span><span>2:240.</span></p>');
INSERT INTO `quran` VALUES (6946,2,4,103,'<p><span>Dan, apabila engkau berada di antara mereka lalu engkau mengimani shalat bagi mereka, maka hendaklah segolongan dari mereka berdiri bersama engkau, dan hendaklah mereka memegang senjata mereka. Dan, apabila mereka telah selesai sujud, maka hendaklah mereka pergi kebelakangmu, dan hendaklah maju pula </span><span>ke muka</span><span> golongan lain yang belum shalat, dan hendaklah mereka shalat, bersama engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=660">660</a><span>]</span><span> dan hendaklah mereka </span><span>terus</span><span> memegang peralatan- peralatan dan senjata mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=661">661</a><span>]</span><span> Orang-orang ingkar ingin supaya kamu lengah dari senjatamu dan perlengkapanmu. lalu mereka menyergapmu sekaligus. Dan, tiada dosa atasmu jika kamu berada dalam kesusahan yang disebabkan oleh hujan atau jika kamu sakit, lalu kamu meletakkan senjata mu, tetapi kamu harus waspada juga. Sesungguhnya Allah menyediakan bagi orang-orang kafir azab yang menghinakan.</span></p>');
INSERT INTO `quran` VALUES (6947,2,4,104,'<p><span>Dan, apabila kamu telah selesai mengerjakan shlalat itu maka </span><span>[a] </span><span>ingatlah kepada Allah sambil berdiri dan sambil duduk dan </span><span>sambil berbaring</span><span> atas rusukmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=662">662</a><span>]</span><span> dan </span><span>[b] </span><span>apabila kamu telah merasa aman dari </span><span>bahaya</span><span>, dirikanlah shalat </span><span>sebagaimana seharusnya</span><span>. Sesungguhnya shalat itu adalah fardhu yang telah ditentukan waktunya bagi orang-orang mukmin.</span></p><p><span>[a] </span><span>3:192.</span></p><p><span>[b] </span><span>2:240.</span></p>');
INSERT INTO `quran` VALUES (6948,2,4,105,'<p><span>Dan, janganlah kamu </span><span>[a] </span><span>malas dalam mencari kaum </span><span>musuh</span><span> itu. Jika kamu menderita, maka sesungguhnya mereka pun menderita seperti kamu menderita; sedang kamu mengharapkan dari Allah apa yang tidak diharapkan oleh mereka. Dan, Allah itu Maha Mengetahui, Maha Bijakasana.</span></p><p><span>[a] </span><span>3:147</span></p>');
INSERT INTO `quran` VALUES (6949,2,4,106,'<p><span>Sesungguhnya, Kami telah menurunkan kepada engkau Kitab dengan hak </span><span>[a] </span><span>supaya engkau menghakimi di antara manusia dengan apa yang diperlihatkan Allah kepada engkau. Dan, jangalah engkau menjadi seorang petengkar untuk </span><span>membela</span><span> orang-orang yang berkhinat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=663">663</a><span>]</span></p><p><span>[a] </span><span>5:49.</span></p>');
INSERT INTO `quran` VALUES (6950,2,4,107,'<p><span>Dan mohonlah ampun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=664">664</a><span>]</span><span> kepada Allah Sesungguhnya Allah itu Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (6951,2,4,108,'<p><span>Dan, janganlah engkau berbantah untuk </span><span>membela</span><span> orang-orang yang mengkhianati diri mereka</span><span>. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=665">665</a><span>]</span><span> Sesungguhnya Allah tidak menyukai orang yang senantiasa berkhianat, bergelimang dosa.</span></p>');
INSERT INTO `quran` VALUES (6952,2,4,109,'<p><span>Mereka berupaya menyembunyikan </span><span>rencana mereka</span><span> dari manusia, tetapi mereka tak dapat menyembunyikannya dari Allah, dan </span><span>[a] </span><span>Dia bersama mereka ketika mereka di waktu malam merencanakan hal-hal yang tidak Dia sukai. Dan, Allah akan mengepung apa yang dikerjakan mereka.</span></p><p><span>[a] </span><span>4:82.</span></p>');
INSERT INTO `quran` VALUES (6953,2,4,110,'<p><span>Ketahuilah, kamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=666">666</a><span>]</span><span> adalah orang-orang yang berbantah untuk </span><span>membela</span><span> mereka dalam kehidupan di dunia ini. Maka. siapakah yang akan berbantah untuk </span><span>membela</span><span> mereka di hadapan Allah pada Hari Kiamat, atau siapakah yang akan menjadi pelindung mereka ?</span></p>');
INSERT INTO `quran` VALUES (6954,2,4,111,'<p><span>Dan, barangsiapa berbuat keburukan atau menganiaya dirinya, kemudian ia </span><span>[a] </span><span>meminta ampun kepada Allah, dia akan mendapatkan Allah itu Maha Pengampun, Maha Penyayang.</span></p><p><span>[a] </span><span>4:65.</span></p>');
INSERT INTO `quran` VALUES (6955,2,4,112,'<p><span>Dan, </span><span>[a] </span><span>barangsiapa berbuat keburukan atau menganiaya dirinya, maka sesungguhnya apa yang diperbuatnya itu akan menimpa dirinya sendiri. Dan Allah itu Maha Mengetahui, Maha Bijaksana.</span></p><p><span>[a] </span><span>2:287; 99:9.</span></p>');
INSERT INTO `quran` VALUES (6956,2,4,113,'<p><span>Dan, barangsiapa berbuat suatu kesalahan atau dosa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=667">667</a><span>]</span><span> kemudian </span><span>[a] </span><span>melemparkannya kepada orang yang tidak bersalah, maka sesungguhnya ia memikul </span><span>beban</span><span> kebohongan dan dosa yang nyata.</span></p><p><span>[a] </span><span>24:2, 24; 33:59.</span></p>');
INSERT INTO `quran` VALUES (6957,2,4,114,'<p><span>Dan, andaikata tidak ada karunia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=668">668</a><span>]</span><span> Allah dan rahmatNya atas engkau, sesungguhnya telah bertekad </span><span>[a] </span><span>segolongan dari mereka hendak membinasakan engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=669">669</a><span>]</span><span> Dan, tidaklah mereka membinasakan melainan diri mereka sendiri dan mereka tidak dapat memudaratkan engkau sedikit pun. Dan, Allah telah menurunkan kepada engkau Kitab dan Hikmah dan </span><span>[b] </span><span>Dia telah mengajarkan kepada engkau apa yang tadinya engkau tidak mengetahui. Dan karunia Allah atas engkau </span><span>sangat</span><span> besar.</span></p><p><span>[a] </span><span>17:74.</span></p><p><span>[b] </span><span>42:52; 96:6.</span></p>');
INSERT INTO `quran` VALUES (6958,2,4,115,'<p><span>Tak ada kebaikan dalam kebanyakan permupakatan </span><span>rahasia</span><span> mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=670">670</a><span>]</span><span> kecuali </span><span>permupakatan</span><span> orang yang menyuruh bersedekah atau </span><span>menyuruh</span><span> berbuat baik atau </span><span>[a] </span><span>perdamaian di antara manusia. Dan, barangsiapa berbuat demikian untuk mencari keridhaan Allah, maka Kami akan segera memberikan kepadanya ganjaran besar.</span></p><p><span>[a] </span><span>2:225.</span></p>');
INSERT INTO `quran` VALUES (6959,2,4,116,'<p><span>Dan,barangsiapa menentang Rasul setelah nyata baginya petunjuk dan </span><span>[a] </span><span>mengikuti bukan jalan orang-orang mukmin, akan Kami palingkan dia ke jalan yang ditujunya, dan akan Kami masukkan dia ke dalam Jahannam. Dan alagkah buruknya tempat kembali itu.</span></p><p><span>[a] </span><span>7:4.</span></p>');
INSERT INTO `quran` VALUES (6960,2,4,117,'<p><span>Sesungguhnya </span><span>[a]</span><span> Allah tidak akan mengampuni jika dia dipersekutukan, dan Dia akan mengampuni selain dari itu bagi siapa yang Dia kehendaki. Dan, </span><span>[b] </span><span>barangsiapa mempersekutukan Allah, maka sesungguhnya sesatlah ia dengan kesesatan yang sangat jauh.</span></p><p><span>[a] </span><span>4:49.</span></p><p><span>[b] </span><span>4:137.</span></p>');
INSERT INTO `quran` VALUES (6961,2,4,118,'<p><span>Tiada yang mereka seru selain Dia, melainkan benda-benda mati, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=671">671</a><span>]</span><span> dan tiada yang mereka seru melainkan syaitan yang durhaka.</span></p>');
INSERT INTO `quran` VALUES (6962,2,4,119,'<p><span>Yang Allah telah melaknatnya dan mereka berkata, </span><span>[a] </span><span>”Tidak akan aku mengambil dari hamba-hamba Engkau; bagian tertentu.”</span></p><p><span>[a] </span><span>14:23; 17:65.</span></p>');
INSERT INTO `quran` VALUES (6963,2,4,120,'<p><span>“Dan niscaya akan kusesatkan mereka, dan niscaya akan kujanjikan kepada mereka harapan- harapan kosong, dan niscaya akan kusuruh mereka supaya memotong telinga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=672">672</a><span>]</span><span> binatang- binatang ternak; dan niscaya akan kusuruh mereka mengubah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=673">673</a><span>]</span><span> makhluk Allah.” Dan, barangsiapa mengambil syaitan menjadi sahabat selain Allah, maka sesungguhnya ia menderita kerugian yang nyata.</span></p>');
INSERT INTO `quran` VALUES (6964,2,4,121,'<p><span>[a] </span><span>Ia, </span><span>syaitan</span><span>, membuat janji-janji kepada mereka dan menimbulkan harapan kosong kepada mereka. Dan tak ada yang dijanjikan syaitan kepada mereka melainkan tipuan belaka.</span></p><p><span>[a] </span><span>14:23; 17:75.</span></p>');
INSERT INTO `quran` VALUES (6965,2,4,122,'<p><span>Itulah orang-orang yang tempat tinggal mereka Jahannam, dan </span><span>[a] </span><span>mereka tidak akan mendapatkan tempat lari darinya.</span></p><p><span>[a] </span><span>14:22.</span></p>');
INSERT INTO `quran` VALUES (6966,2,4,123,'<p><span>Dan, </span><span>[a] </span><span>orang-orang yang beriman dan beramal saleh, akan Kami masukkan mereka ke dalam kebun-kebun yang dibawahnya mengalir sungai-sungai; mereka akan tinggal di dalamnya selama-lamanya. </span><span>Inilah</span><span> janji Allah yang hak. Dan siapakah yang lebih benar perkataannya daripada Allah ?</span></p><p><span>[a] </span><span>Lihat 2:26</span></p>');
INSERT INTO `quran` VALUES (6967,2,4,124,'<p><span>Tidaklah akan sesuai dengan angan-anganmu dan tidak pula dengan angan-angan Ahlikitab. Barangsiapa berbuat jahat, ia akan dibalas dengan </span><span>kejahatan</span><span> itu pula; dan </span><span>[a] </span><span>ia tidak akan memperoleh baginya selain Allah seorang sahabat dan tidak pula seorang penolong.</span></p><p><span>[a] </span><span>4:46; 33:18,66.</span></p>');
INSERT INTO `quran` VALUES (6968,2,4,125,'<p><span>Dan, </span><span>[a] </span><span>barangsiapa mengerjakan amal-amal saleh, baik laki-laki atau pun perempuan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=674">674</a><span>]</span><span> sedang ia mukmin, maka mereka itu akan masuk sorga, dan mereka </span><span>sedikit pun</span><span> tidak akan dianiaya biar sebesar lubang kecil biji korma.</span></p><p><span>[a] </span><span>40:41.</span></p>');
INSERT INTO `quran` VALUES (6969,2,4,126,'<p><span>Dan, siapakah yang lebih baik agamanya dari </span><span>[a] </span><span>orang yang sepenuhnya menyerahkan dirinya kepada Allah, dan ia seorang pelaku kebaikan, dan mengikuti agama Ibrahim yang tulus ikhlas ? Dan, Allah telah menjadikan Ibrahim </span><span>sebagai</span><span> sahabat karib. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=675">675</a><span>]</span></p><p><span>[a] </span><span>2:132.</span></p>');
INSERT INTO `quran` VALUES (6970,2,4,127,'<p><span>Dan, </span><span>[a] </span><span>kepunyaan Allah segala apa yang di langit dan apa yang ada di bumi. Dan </span><span>[b]</span><span> Allah melingkupi segala sesuatu.</span></p><p><span>[a] </span><span>2:285; 4:132; 10:56; 16:53; 24:65.</span></p><p><span>[b] </span><span>41:55; 85:21.</span></p>');
INSERT INTO `quran` VALUES (6971,2,4,128,'<p><span>Dan, mereka meminta fatwa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=676">676</a><span>] </span><span>dari engkau mengenai </span><span>peraturan perkawinan dengan</span><span> perempuan-perempuan. Katakanlah, Allah memberi fatwa kepadamu mengenai mereka; dan </span><span>[a] </span><span>apa yang dibacakan kepadamu dalam Kitab ini </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=677">677</a><span>]</span><span> mengenai perempuan yatim yang tidak kamu berikan kepada mereka </span><span>mahar</span><span> yang telah ditetapkan bagi mereka, padahal kamu ingin mengawini mereka dan </span><span>mengenai</span><span> anak-anak perempuan yang lemah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=677a">677a</a><span>]</span><span> Dan hendaknya kamu senantiasa berlaku adil terhadap anak-anak yatim. Dan apa-apa yang kamu kerjakan dari kebaikan, maka sesungguhnya Allah mengetahui hal itu.</span></p><p><span>[a] </span><span>4:4.</span></p>');
INSERT INTO `quran` VALUES (6972,2,4,129,'<p><span>Dan, </span><span>[a] </span><span>jika seorang perempuan merasa khawatir akan mendapat perlakuan tidak baik atau kurang mendapat perhatian dari suaminya, maka tiada dosa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=678">678</a><span>]</span><span> bagi keduanya mengadakan perdamaian di antara mereka berdua dengan sungguh-sungguh. Dan perdamaian itu paling baik. Dan, manusia cenderung kepada sifat bakhil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=679">679</a><span>]</span><span> Dan, jika kamu berbuat baik dan bertakwa, maka sesungguhnya Allah mengetahui segala apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>4:35.</span></p>');
INSERT INTO `quran` VALUES (6973,2,4,130,'<p><span>Dan, </span><span>[b] </span><span>kamu sekali-kali tidak akan dapat berlaku adil </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=680">680</a><span>]</span><span> di antara istri-istri, betapa pun kamu menginginkannya. Maka janganlah kamu mencondongkan seluruh kecondongan </span><span>kepada seorang</span><span> </span><span>[a] </span><span>sehingga kamu meninggalkan </span><span>yang lain</span><span> sebagai barang terkatung-katung. Dan, jika kamu saling memperbaiki diri dan bertakwa, maka sesungguhnya Allah itu Maha Pengampun, Maha Penya yang.</span></p><p><span>[a] </span><span>2:232.</span></p><p><span>[b] </span><span>4:4.</span></p>');
INSERT INTO `quran` VALUES (6974,2,4,131,'<p><span>Dan, jika kedua mereka itu bercerai, maka Allah akan memberi kecukupan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=681">681</a><span>]</span><span> kepada masing-masing dengan kelimpahan karunia-Nya. Dan Allah itu Mahaluas, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (6975,2,4,132,'<p><span>Dan </span><span>[a] </span><span>kepunyaan Allah segala sesuatu yang ada di langit dan segala sesuatu yang ada di bumi. Dan, sungguh </span><span>[b] </span><span>Kami telah mewasiatkan kepada orang-orang yang diberi Kitab sebelummu dan juga kepadamu supaya kamu bertakwa kepada Allah. Dan, jika kamu ingkar, maka sesungguhnya kepunyaan Allah segala sesuatu yang ada di langit dan segala sesuatu yang ada di bumi. Dan Allah itu Mahakaya, Maha Terpuji.</span></p><p><span>[a] </span><span>Lihat 4:127.</span></p><p><span>[b] </span><span>42:14.</span></p>');
INSERT INTO `quran` VALUES (6976,2,4,133,'<p><span>Dan </span><span>[a] </span><span>kepunyaan Allah segala seuatu yang ada dilangit dan segala sesuatu yang ada di bumi, dan cukuplah Allah sebagai Pelindung.</span></p><p><span>[a] </span><span>Lihat 4:127.</span></p>');
INSERT INTO `quran` VALUES (6977,2,4,134,'<p><span>Jika Dia menghendaki, niscaya Dia dapat melenyapkan kamu hai manusia, dan mendatangkan umat lain </span><span>sebagai penggantimu</span><span>. Dan, Allah berkuasa untuk berbuat hal serupa itu.</span></p>');
INSERT INTO `quran` VALUES (6978,2,4,135,'<p><span>[a] </span><span>Barangsiapa menghendaki ganjaran duniawi, maka </span><span>ketahuilah</span><span> di sisi Allah ada ganjaran duniawi dan ukhrawi. Dan Allah itu Maha Mendengar, Maha Melihat.</span></p><p><span>[a] </span><span>2:201, 202; 42:21.</span></p>');
INSERT INTO `quran` VALUES (6979,2,4,136,'<p><span>Hai orang-orang yang beriman, </span><span>[a] </span><span>jadilah kamu orang-orang yang menjadi penegak keadilan dan </span><span>jadilah</span><span> saksi karena Allah walaupun bertentangan dengan dirimu sendiri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=682">682</a><span>]</span><span> atau ibu-bapak dan kaum kerabat. Baik ia orang kaya atau miskin, maka Allah lebih memperhatikan kepada keduanya. Karena itu janganlah kamu menuruti hawa nafsu agar kamu dapat berlaku adil . </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=682a">682a</a><span>]</span><span> </span><span> Dan, jika kamu menyembunyikan </span><span>kebenaran</span><span> atau mengelakkan diri, maka sesungguhnya Allah itu Maha Mengetahui segala sesuatu yang kamu kerjakan.</span></p><p><span>[a] </span><span>5:9.</span></p>');
INSERT INTO `quran` VALUES (6980,2,4,137,'<p><span>Hai orang-orang yang beriman, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=683">683</a><span>]</span><span> berimanlah kepada Allah dan Rasul-Nya dan Kitab yang diturunkan kepada Rasul-Nya dan </span><span>[a] </span><span>Kitab yang telah diturunkan sebelum</span><span>nya</span><span>. Dan </span><span>[b] </span><span>siapa yang ingkar kepada Allah dan malaikat-malaikat-Nya dan Kitab-kitab-Nya, dan Rasul-rasul -Nya dan Hari Kemudian, maka sesungguhnya sesatlah </span><span>[c] </span><span>ia dengan kesesatan yang sangat jauh.</span></p><p><span>[a] </span><span>2:5, 137; 4:163; 5:60.</span></p><p><span>[b] </span><span>4:151. </span></p><p><span>[c] </span><span>4:117.</span></p>');
INSERT INTO `quran` VALUES (6981,2,4,138,'<p><span>[a] </span><span>Sesungguhnya orang-orang yang beriman, kemudian ingkar, kemudian beriman </span><span>lagi</span><span>, kemudian ingkar </span><span>lagi</span><span>, kemudian kian bertambah dalam kekufuran, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=684">684</a><span>]</span><span> sekali-kali Allah tidak akan mengampuni mereka dan tidak pula akan menunjukkan jalan </span><span>lurus</span><span> kepada mereka.</span></p><p><span>[a] </span><span>3:91; 63:4.</span></p>');
INSERT INTO `quran` VALUES (6982,2,4,139,'<p><span>[b] </span><span>Beritahulah orang-orang munafik bahwa sesungguhnya bagi mereka ada azab pedih.</span></p><p><span>[b] </span><span>9:3.</span></p>');
INSERT INTO `quran` VALUES (6983,2,4,140,'<p><span>[a] </span><span>Orang-orang yang menjadikan orang-orang kafir sebagai sahabat-sahabat selain orang-orang mukmin. Apakah mereka mencari kehormatan di sisi mereka ? Maka, sesungguhnya </span><span>[b] </span><span>segala kehormatan itu kepunyaan </span>Allah<span>.</span></p><p><span>[a] </span><span>3:29, 119; 4:145.</span></p><p><span>[b] </span><span>10:66; 35:11.</span></p>');
INSERT INTO `quran` VALUES (6984,2,4,141,'<p><span>Dan, sesungguhnya Dia telah menurunkan kepadamu di dalam Kitab ini </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=685">685</a><span>]</span><span> bahwa apabila kamu mendengar Ayat-ayat </span>Allah<span> diingkarnya dan dicemoohkannya, maka janganlah kamu duduk bersama mereka </span><span>[a] </span><span>sebelum mereka beralih ke dalam percakapan lainnya. Jika demikian, sesungguhnya kamu niscaya semisal mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=686">686</a><span>] </span><span>Sesungguhnya </span>Allah<span> akan menghimpun orang-orang munafik dan orang-orang kafir semua di dalam Jahannam.</span></p><p><span>[a] </span><span>6:69.</span></p>');
INSERT INTO `quran` VALUES (6985,2,4,142,'<p><span>[a] </span><span>Orang-orang </span><span>munafik</span><span> yang menunggu-nunggu </span><span>berita</span><span> mengenai kamu. Maka, jika bagimu ada kemenangan dari </span>Allah<span>, mereka berkata, “Bukankah kami beserta kamu ?” Dan, jika bagi orang-orang kafir ada bagian </span><span>dari kemenangan,</span><span> berkata mereka, ”Bukankah kami pada peristiwa </span><span>sebelumnya</span><span> lebih cerdik dari kamu dan menyelamatkan kamu dari orang-orang mukmin ?” Maka </span>Allah<span> akan menghakimi di antaramu pada Hari Kiamat. Dan sekali-kali </span>Allah<span> tidak akan memberi kepada orang-orang kafir jalan </span><span>untuk menang</span><span> atas orang-orang mukmin.</span></p><p><span>[a] </span><span>9:98; 57:15.</span></p>');
INSERT INTO `quran` VALUES (6986,2,4,143,'<p><span>[a] </span><span>Sesungguhnya orang-orang munafik hendak menipu </span>Allah<span>, dan Dia pun akan membalas tipuan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=687">687</a><span>]</span><span> Dan, apabila mereka berdiri untuk mengerjakan shalat, </span><span>[b] </span><span>mereka berdiri dengan malas, dan untuk diperlihatkan kepada manusia, dan tidak mereka ingat kepada </span>Allah<span> melainkan hanya sedikit.</span></p><p><span>[a] </span><span>2:10.</span></p><p><span>[b] </span><span>9:54.</span></p>');
INSERT INTO `quran` VALUES (6987,2,4,144,'<p><span>Keadaan mereka bimbang di antara itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=688">688</a><span>]</span><span> tidak berpihak kepada mereka ini, dan tidak pula kepada mereka itu. Dan, siapa yang dibinasakan </span>Allah<span> maka engkau tidak akan mendapatkan jalan </span><span>keselamatan</span><span> baginya.</span></p>');
INSERT INTO `quran` VALUES (6988,2,4,145,'<p><span>Hai orang-orang yang beriman, </span><span>[a] </span><span>janganlah kamu mengambil orang-orang kafir menjadi sahabat-sahabat selain orang-orang mukmin. Inginkah kamu mengadakan bukti yang nyata bagi </span>Allah<span> untuk </span><span>memberatkan</span><span> kamu ?</span></p><p><span>[a] </span><span>3:29, 119; 4:140.</span></p>');
INSERT INTO `quran` VALUES (6989,2,4,146,'<p><span>Sesungguhnya orang-orang munafik berada di bagian paling bawah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=689">689</a><span>]</span><span> dalam Api; dan engkau tidak akan mendapatkan penolong bagi mereka.</span></p>');
INSERT INTO `quran` VALUES (6990,2,4,147,'<p><span>[a] </span><span>Kecuali orang-orang yang bertobat dan memperbaiki diri dan </span><span>[b] </span><span>berpegang teguh kepada </span>Allah<span>, serta mereka ikhlas dalam ibadah mereka kepada </span>Allah<span>. Dan mereka ini termasuk golongan orang-orang mukmin. Dan, kelak </span>Allah<span> akan memberi kepada orang-orang mukmin ganjaran besar.</span></p><p><span>[a] </span><span>Lihat 2:161.</span></p><p><span>[b] </span><span>3:102.</span></p>');
INSERT INTO `quran` VALUES (6991,2,4,148,'<p><span>Mengapa </span>Allah<span> akan mengazab kamu jika kamu bersyukur dan beriman ? Dan, </span><span>[a]</span><span> Allah</span><span> itu Maha Menghargai, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=690">690</a><span>]</span><span> Maha Mengatahui.</span></p><p><span>[a] </span><span>2:159</span></p>');
INSERT INTO `quran` VALUES (6992,2,4,149,'<p><span>JUZ VI</span></p><p>Allah<span> tidak menyukai perzahiran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=691">691</a><span>]</span><span> perkataan buruk kecuali </span><span>yang diucapkan</span><span> orang yang teraniaya. Sesungguhnya </span>Allah<span> itu Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (6993,2,4,150,'<p><span>Jika kamu menzahirkan kebaikan atau menyembunyikannya, atau memaafkan suatu keburukan, maka sesungguhnya </span>Allah<span> itu Maha Pemaaf, Maha Kuasa.</span></p>');
INSERT INTO `quran` VALUES (6994,2,4,151,'<p><span>Sesungguhnya </span><span>[a] </span><span>orang-orang yang ingkar kepada </span>Allah<span> dan Rasul-rasul-Nya dan mereka ingin membedakan antara </span>Allah<span> dan Rasul-rasul-Nya, dan mereka mengatakan, “Kami beriman kepada sebagian dan kami ingkar kepada sebagian </span><span>lain,</span><span>” dan mereka ingin mengambil jalan tengah di antara hal demikian itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=692">692</a><span>]</span></p><p><span>[a] </span><span>4:137</span></p>');
INSERT INTO `quran` VALUES (6995,2,4,152,'<p><span>Mereka itulah orang-orang kafir yang sebenar-benarnya, dan Kami telah menyediakan bagi orang-orang kafir azab yang hina.</span></p>');
INSERT INTO `quran` VALUES (6996,2,4,153,'<p><span>Dan, </span><span>[a] </span><span>orang-orang yang beriman kepada </span>Allah<span> dan Rasul-rasul-Nya dan tidak membedakan seorang pun di antara mereka, kepada mereka inilah </span>Allah<span> segera akan memberikan ganjaran mereka. Dan, </span>Allah<span> itu Maha Pengampun, Maha Penyayang.</span></p><p><span>[a] </span><span>2:137; 2:286; 3:85.</span></p>');
INSERT INTO `quran` VALUES (6997,2,4,154,'<p><span>Ahlikitab meminta kepada engkau supaya engkau menurunkan atas mereka sebuah Kitab dari langit, maka sesungguhnya </span><span>[a] </span><span>mereka pun pernah miminta kepada Musa lebih besar dari itu. Mereka berkata, </span><span>[b] </span><span>”Perlihatkanlah </span>Allah<span> kepada kami secara zahir.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=693">693</a><span>]</span><span> Maka, mereka disergap oleh siksaan yang memusnahkan disebabkan keaniayaan mereka. Kemudian </span><span>[c] </span><span>mereka mengambil anak sapi </span><span>sebagai sembahan</span><span> setelah datang kepada mereka Tanda-tanda nyata, namun Kami memaafkan hal itu. Dan, Kami memberi Musa kemenangan yang nyata.</span></p><p><span>[a] </span><span>2:109.</span></p><p><span>[b] </span><span>2:56.</span></p><p><span>[c] </span><span>2:52, 93; 7:149, 153.</span></p>');
INSERT INTO `quran` VALUES (6998,2,4,155,'<p><span>Dan, </span><span>[a] </span><span>Kami menjulang tinggikan di atas mereka gunung Thur ketika membuat perjanjian dengan mereka, dan Kami berkata kepada mereka, </span><span>[b] </span><span>”Masukilah pintu ini dengan patuh,” dan Kami ber-kata kepada mereka, </span><span>[c] </span><span>”Janganlah kamu membuat pelanggaran dalam </span><span>hari</span><span> Sabat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=694">694</a><span>]</span><span> Dan, Kami mengambil dari mereka suatu perjanjian teguh.</span></p><p><span>[a] </span><span>2:64,94.</span></p><p><span>[b] </span><span>2:59; 7:162.</span></p><p><span>[c] </span><span>2:66; 4:48; 7:164; 16:125.</span></p>');
INSERT INTO `quran` VALUES (6999,2,4,156,'<p><span>Maka, disebabkan pelanggaran mereka atas perjanjian mereka dan </span><span>[a] </span><span>kekufuran mereka kepada Tanda-tanda </span>Allah<span> dan mereka </span><span>[b] </span><span>membunuh nabi-nabi tanpa hak dan karena ucapan mereka, </span><span>[c]</span><span>”Hati kami terselubung,” bahkan </span><span>[d]</span><span> Allah</span><span> telah mencap </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=695">695</a><span>]</span><span> mereka disebabkan kekufuran mereka, maka tidaklah mereka beriman melainkan sedikit.</span></p><p><span>[a] </span><span>5:14.</span></p><p><span>[b] </span><span>3:182.</span></p><p><span>[c] </span><span>2:89.</span></p><p><span>[d] </span><span>2:89; 16:109; 83:15.</span></p>');
INSERT INTO `quran` VALUES (7000,2,4,157,'<p><span>Dan disebabkan kekufuran mereka dan ucapan mereka terhadap Maryam </span><span>berupa </span><span> tuduhan palsu yang besar; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=696">696</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7001,2,4,158,'<p><span>Dan ucapan mereka, “Sesungguhnya kami telah membunuh Al-Masih, Isa Ibnu Maryam, Rasul </span>Allah<span>,” padahal mereka tidak membunuhnya dan tidak pula mematikannya di atas salib, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=697">697</a><span>] </span><span>akan tetapi ia disamarkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=698">698</a><span>]</span><span> kepada mereka </span><span>seperti telah mati di atas salib</span><span>. Dan, sesungguhnya orang-orang yang berselisih dalam hal ini niscaya ada dalam keraguan tentang ini; </span><span>[a] </span><span>mereka tidak mumpunyai pengetahuan </span><span>yang pasti</span><span> tentang ini melainkan menuruti dugaan; dan mereka tidak membunuhnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=699">699</a><span>]</span><span> dengan yakin.</span></p><p><span>[a] </span><span>10:37; 53:29.</span></p>');
INSERT INTO `quran` VALUES (7002,2,4,159,'<p><span>[a] </span><span>Bahkan </span>Allah<span> telah mengangkatnya kepada-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=700">700</a><span>]</span><span> dan </span>Allah<span> itu Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a] </span><span>2:254; 3:56; 7:177; 58:12.</span></p>');
INSERT INTO `quran` VALUES (7003,2,4,160,'<p><span>Dan, tidak ada seorang pun dari ahlikitab melainkan akan tetap beriman kepada hal ini sebelum ajalnya; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=701">701</a><span>]</span><span> dan pada Hari Kiamat </span><span>[a] </span><span>ia, </span><span>Nabi Isa</span><span>, akan menjadi saksi terhadap mereka.</span></p><p><span>[a] </span><span>5:118.</span></p>');
INSERT INTO `quran` VALUES (7004,2,4,161,'<p><span>Maka, disebabkan kezaliman orang-orang Yahudi, Kami mengharamkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=702">702</a><span>]</span><span> atas mereka barang -barang baik yang pernah dihalalkan bagi mereka, dan karena rintangan mereka kepada banyak </span><span>orang</span><span> dari jalan </span>Allah<span>.</span></p>');
INSERT INTO `quran` VALUES (7005,2,4,162,'<p><span>Dan </span><span>karena</span><span> mereka </span><span>[a] </span><span>mengambil riba sungguhpun mereka telah dilarang terhadapnya, dan mereka </span><span>[b] </span><span>memakan harta orang </span><span>lain</span><span> dengan jalan batil. Dan, Kami menyediakan bagi orang- orang kafir dari antara mereka azab yang pedih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=703">703</a><span>]</span></p><p><span>[a] </span><span>2:276, 277; 3:131; 30:40.</span></p><p><span>[b] </span><span>9:34.</span></p>');
INSERT INTO `quran` VALUES (7006,2,4,163,'<p><span>Akan tetapi, </span><span>[a] </span><span>orang-orang yang matang ilmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=704">704</a><span>]</span><span> di antara mereka, dan </span><span>juga</span><span> </span><span>[b] </span><span>orang-orang mukmin, mereka percaya kepada apa-apa yang diturunkan sebelum engkau, dan </span><span>terutama</span><span> orang-orang yang dawan mendirikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=705">705</a><span>]</span><span> shalat, dan membayar zakat, dan beriman kepada </span>Allah<span> dan Hari Kemudian. Itulah orang-orang yang kepada mereka Kami pasti memberikan ganjaran yang besar.</span></p><p><span>[a] </span><span>3:8.</span></p><p><span>[b] </span><span>2:5, 137; 3:200; 4:137; 5:60.</span></p>');
INSERT INTO `quran` VALUES (7007,2,4,164,'<p><span>Sesungguhnya, </span><span>[a] </span><span>Kami telah mewahyukan kepada engkau sebagaimana Kami telah mewahyukan kepada Nuh dan nobi-nabi yang sesudahnya; dan telah Kami Wahyukan kepada Ibrahim dan Ismail dan Ishak dan Ya</span><span>’</span><span>kub dan keturunan-</span><span>nya</span><span> dan Isa dan Ayyub dan Yunus dan Harun dan Sulaiman, dan telah </span><span>[b] </span><span>Kami berikan Zabur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=706">706</a><span>]</span><span> kepada Daud.</span></p><p><span>[a] </span><span>2:137; 3; 6:85 </span><span>–</span><span> 88.</span></p><p><span>[b] </span><span>17:56.</span></p>');
INSERT INTO `quran` VALUES (7008,2,4,165,'<p><span>Dan </span><span>ada</span><span> </span><span>[a] </span><span>rasul-rasul yang telah Kami beritahukan kepada engkau sebelum </span><span>ini</span><span>, dan </span><span>ada</span><span> rasul- rasul yang tidak Kami beritahukan kepada engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=707">707</a><span>]</span><span> Dan </span>Allah<span> telah berfirman kepada Musa dengan firman-</span><span>Nya</span><span>. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=707a">707a</a><span>]</span></p><p><span>[a] </span><span>40:79.</span></p>');
INSERT INTO `quran` VALUES (7009,2,4,166,'<p><span>Rasul-rasul </span><span>[a] </span><span>pembawa kabar suka dan pemberi ingat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=708">708</a><span>] </span><span>supaya jangan ada alasan bagi manusia untuk menyalahkan </span>Allah<span> setelah </span><span>kedatangan </span><span> rasul-rasul </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=709">709</a><span>]</span><span> itu. Dan </span>Allah<span> itu Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a] </span><span>2:214; 6:49; 17:106; 18:57.</span></p>');
INSERT INTO `quran` VALUES (7010,2,4,167,'<p><span>Tetapi, </span><span>[a]</span><span> Allah</span><span> memberi kesaksian dengan apa yang diturunkan kepada engkau </span><span>bahwa</span><span> Dia menurunkannya dengan ilmu-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=710">710</a><span>]</span><span> dan para malaikat </span><span>pun</span><span> memberi kesaksian. Dan cukuplah </span>Allah<span> sebagai saksi.</span></p><p><span>[a] </span><span>3:19; 11:15.</span></p>');
INSERT INTO `quran` VALUES (7011,2,4,168,'<p><span>Sesungguhnya </span><span>[a] </span><span>orang-orang yang ingkar dan menghalangi dari jalan </span>Allah<span>, sesungguhnya mereka telah sesat, sesat sejauh-jauhnya.</span></p><p><span>[a] </span><span>4:138.</span></p>');
INSERT INTO `quran` VALUES (7012,2,4,169,'<p><span>Sesungguhnya, </span><span>[a] </span><span>orang-orang ingkar dan berbuat aniaya, </span>Allah<span> sekali-kali tidak akan mengampuni mereka dan tidak pula akan menunjukkan kepada mereka suatu jalan.</span></p><p><span>[a] </span><span>4:138.</span></p>');
INSERT INTO `quran` VALUES (7013,2,4,170,'<p><span>Kecuali jalan Jahannam; mereka akan tinggal lama di dalamnya. Dan hal demikian itu </span><span>[a] </span><span>mudah bagi </span>Allah<span>.</span></p><p><span>[a] </span><span>33:31; 64.</span></p>');
INSERT INTO `quran` VALUES (7014,2,4,171,'<p><span>Hai manusia, sesungguhnya telah datang kepadamu Rasul ini dengan hak dari Tuhan-mu; maka kamu berimanlah, </span><span>itu</span><span> baik bagimu. Dan, jika kamu ingkar, maka sesungguhnya kepunyaan </span>Allah<span> segala apa yang ada di seluruh langit dan di bumi. Dan, </span>Allah<span> itu Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (7015,2,4,172,'<p><span>[e] </span><span>Hai Ahlikitab, janganlah kamu melampaui batas dalam </span><span>urusan </span><span> agamamu, dan janganlah kamu berkata mengenai </span>Allah<span> kecuali yang benar. Sesungguhnya Al-Masih, Isa Ibnu Maryam hanya </span><span>seorang </span><span> rasul </span>Allah<span> dan suatu khabar suka dari-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=711">711</a><span>]</span><span> yang diturunkan kepada Maryam, dan </span><span>[a] </span><span>rahmat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=712">712</a><span>]</span><span> dari-Nya, maka berimanlah kepada </span>Allah<span> dan Rasul-rasul-Nya, dan janganlah kamu </span><span>[b] </span><span>mengatakan, “</span><span>Tuhan itu</span><span> tiga,” Berhentilah </span><span>dari ucapan itu,</span><span> ini lebih baik bagimu. Sesungguhnya </span>Allah<span> itu Tuhan Yang Maha Esa. </span><span>[c] </span><span>Maha Suci Dia dari mempunyai </span><span>[d] </span><span>anak. Kepunyaan-Nya segala apa yang ada di seluruh langit dan segala apa yang ada di bumi. Dan cukuplah </span>Allah<span> sebagai Pelindung.</span></p><p><span>[a] </span><span>58:23.</span></p><p><span>[b] </span><span>5:74.</span></p><p><span>[c] </span><span>2:117; 10:69.</span></p><p><span>[d] </span><span>17:112; 18:5; 112:4,5.</span></p><p><span>[e] </span><span>5:78.</span></p>');
INSERT INTO `quran` VALUES (7016,2,4,173,'<p><span>[a] </span><span>Al-Masih sekali-kali tidak akan merasa hina menjadi hamba bagi </span>Allah<span>, dan tidak juga malaikat yang dekat </span><span>kepada-Nya</span><span>. Dan barangsiapa merasa hina karena beribadat kepada-Nya dan berlaku sombong, maka Dia akan mengumpulkan mereka semua kepada-Nya.</span></p><p><span>[a] </span><span>5:117; 118.</span></p>');
INSERT INTO `quran` VALUES (7017,2,4,174,'<p><span>Ada pun orang-orang yang beriman dan beramal saleh, maka </span><span>[a] </span><span>Dia akan menyempurnakan ganjaran mereka dan akan menambah karunia-Nya bagi mereka. Dan orang-orang yang merasa hina </span><span>beribadah</span><span> dan berlaku sombong, maka Dia akan menyiksa mereka dengan azab yang pedih. Dan </span><span>[b] </span><span>mereka tidak akan mendapatkan bagi mereka sahabat dan penolong selain </span>Allah<span>.</span></p><p><span>[a] </span><span>3:58; 16:97; 39:11.</span></p><p><span>[b] </span><span>4:46; 33:18, 66.</span></p>');
INSERT INTO `quran` VALUES (7018,2,4,175,'<p><span>Hai Manusia, sesungguhnya telah datang kepadamu dalil nyata </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=713">713</a><span>]</span><span> dari Tuhan-mu, dan telah Kami turunkan kepadamu </span><span>[a] </span><span>Nur yang terang-benderang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=714">714</a><span>]</span></p><p><span>[a] </span><span>7:158; 64:9.</span></p>');
INSERT INTO `quran` VALUES (7019,2,4,176,'<p><span>Maka, mengenai orang-orang yang beriman kepada </span>Allah<span> dan </span><span>[a] </span><span>berpegang teguh kepada-Nya niscaya Dia akan memasukkan mereka ke dalam rahmat dari-Nya dan karunia-</span><span>Nya</span><span>, dan Dia akan menunjuki mereka kepada-Nya pada jalan lurus.</span></p><p><span>[a] </span><span>3:102; 4:147.</span></p>');
INSERT INTO `quran` VALUES (7020,2,4,177,'<p><span>Mereka meminta fatwa dari engkau.</span><span>[a] </span><span>Katakanlah, </span>Allah<span> memberi fatwa kepadamu mengenai kalalah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=715">715</a><span>]</span><span> demikian : “ Jika seorang laki-laki wafat yang tidak mempunyai anak, dan ia mempunyai seorang saudara perempuan, maka baginya, seperdua dari apa yang ditinggalkan; dan ia akan menjadi ahli-warisnya jika ia </span><span>perempuan</span><span> tidak mempunyai anak. Dan seandainya saudara perempuannya itu ada dua orang, maka bagi keduanya dua pertiga dari apa yang ditinggalkan. Dan jika </span><span>ahli warisnya</span><span> terdiri atas beberapa orang saudara laki-laki dan saudara perempuan, </span><span>[b] </span><span>maka untuk yang laki-laki sebanyak bagian dua orang perempuan. </span><span>[c]</span><span> Allah</span><span> menjelaskan kepadamu supaya kamu jangan tersesat dan </span>Allah<span> itu Maha Mengetahui segala sesuatu.</span></p><p><span>[a] </span><span>4:13.</span></p><p><span>[b] </span><span>4:12.</span></p><p><span>[c] </span><span>4:27.</span></p>');
INSERT INTO `quran` VALUES (7021,2,5,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (7022,2,5,2,'<p><span>Hai orang-orang yang beriman, penuhilah segala perjanjianmu. Dihalalkan bagimu binatang-binatang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=716">716</a><span>]</span><span> berkaki empat, selain </span><span>[a] </span><span>apa-apa yang kamu diberitahukan kepadamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=717">717</a><span>]</span><span> jangan kamu menganggap binatang buruan halal sedang kamu dalam keadaan ihram; sesungguhnya, Allah menetapkan hukum mengenai apa yang Dia kehendaki.</span></p><p><span>[a]</span><span> 2:174; 5:4; 6:146.</span></p>');
INSERT INTO `quran` VALUES (7023,2,5,3,'<p><span>Hai orang-orang beriman, jangan tidak menghormati Syiar-syiar Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=718">718</a><span>]</span><span> dan jangan tidak </span><span>menghormati</span><span> bulan Haram, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=719">719</a><span>]</span><span> dan jangan </span><span>tidak menghormati</span><span> binatang-binatang korban dan jangan </span><span>tidak menghormati</span><span> yang ditandai kalung, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=720">720</a><span>]</span><span> dan jangan </span><span>tidak menghormati</span><span> orang-orang yang berniat menziarahi Baitul Haram untuk </span><span>[a] </span><span>mencari karunia dan keridhaan dari Tuhan mereka. Dan apabila kamu telah melepas pakaian ihram </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=720a">720a</a><span>]</span><span> maka boleh kamu berburu. Dan </span><span>[b] </span><span>janganlah kebencian sesuatu kaum mendorongmu melampui batas karena mereka mencegah kamu dari Masjidil Haram. Dan, tolong-menolonglah kamu dalam kebaikan dan takwa; dan janganlah kamu tolong-menolong dalam dosa dan permusuhan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=720b">720b</a><span>]</span><span> Dan bertakwalah kepada Allah; sesungguhnya Allah sangat keras hukuman-Nya.</span></p><p><span>[a] </span><span>59:9</span></p><p><span>[b] </span><span>5:9; 11:90</span></p>');
INSERT INTO `quran` VALUES (7024,2,5,4,'<p><span>[a] </span><span>Diharamkan bagimu bangkai dan darah dan daging babi; dan </span><span>hewan</span><span> yang disembelih dengan tidak menyebut nama Allah; dan yang </span><span>mati</span><span> dicekik; dan yang </span><span>mati</span><span> dipukul; dan yang </span><span>mati</span><span> terjatuh; dan yang </span><span>mati </span><span> ditanduk; dan yang telah dimakan oleh binatang buas, kecuali yang telah kamu sembelih; dan yang disembelih di tempat pemujaan berhala-berhala. Dan </span><span>juga </span><span>[b] </span><span>diharankan,</span><span> mengadu nasib dengan mengundi anak panah. Hal demikian itu suatu kedurhakaan. Pada hari ini orang-orang yang ingkar telah putus asa untuk </span><span>merusak</span><span> agamamu. Maka, janganlah takut kepada mereka, dan takutlah kepada-Ku. Hari ini telah kusempurnakan agamamu bagimu dan telah Ku-lengkapkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=721">721</a><span>]</span><span> nikmat-Ku atasmu, dan </span><span>[c] </span><span>telah Ku-sukai bagimu Islam sebagai agama. Tetapi, </span><span>[d] </span><span>barangsiapa terpaksa karena lapar, dan bukan sengaja cenderung kepada dosa, maka sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a] </span><span>2:174; 6:146.    </span></p><p><span>[b] </span><span>5:91.    </span></p><p><span>[c] </span><span>3:20, 86.    </span></p><p><span>[d] </span><span>2:174; 6:146; 16:116.</span></p>');
INSERT INTO `quran` VALUES (7025,2,5,5,'<p><span>Mereka bertanya kepada engkau apa saja yang dihalalkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=722">722</a><span>]</span><span> bagi mereka. Katakanlah, "Dihalalkan bagimu segala yang baik; dan apa yang telah kamu ajarkan kepada hewan pemburu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=722a">722a</a><span>]</span><span> dengan melatih mereka berburu </span><span>dan </span><span> mengajari mereka apa yang telah diajarkan Allah kepadamu. Maka, makanlah </span><span>buruan</span><span> apa yang telah ditangkap mereka untukmu, dan </span><span>[a] </span><span>sebutlah nama Allah atasnya. Dan bertakwalah kepada Allah Sesungguhnya Allahcepat dalam menghisap.</span></p><p><span>[a] </span><span>6:119</span></p>');
INSERT INTO `quran` VALUES (7026,2,5,6,'<p><span>Hari ini telah dihalalkan bagimu segala barang yang baik. Dan makanan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=723">723</a><span>]</span><span> orang-orang yang diberi Kitab halal bagimu dan makananmu halal bagi mereka. Dan </span><span>dihalalkan bagimu</span><span> wanita-wanita yang memelihara kehormatan dari antara wanita-wanita mukmin dan wanita-wanita yang memelihara kehormatan dari antara orang-orang yang diberi Kitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=724">724</a><span>]</span><span> sebelum kamu, apabila kamu memberikan kepada mereka maskawin mereka untuk nikah dengan sah dan bukan untuk berbuat zina, dan tidak pula untuk menjadikan gundik-gundik. Dan barangsiapa menjadi ingkar sesudah beriman, maka sesungguhnya hapuslah amalannya, dan di akhirat ia di antara orang-orang yang merugi.</span></p>');
INSERT INTO `quran` VALUES (7027,2,5,7,'<p><span>Hai orang-orang yang beriman, apabila kamu hendak mendirikan shalat, maka basuhlah mukamu dan kedua tanganmu hingga siku, dan usaplah kepalamu, dan </span><span>basuhlah</span><span> kedua kakimu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=725">725</a><span>]</span><span> sampai mata kaki. Dan, jika kamu dalam keadaan junub, maka bersucilah kamu. Dan jika kamu sakit atau waktu bepergian atau salah seorang dari kamu datang dari jamban, atau kamu telah menyentuh perempuan kemudian kamu tidak mendapat air, maka bertayamumlah kamu dengan debu yang suci dan usaplah mukamu dan kedua tanganmu dengan itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=725a">725a</a><span>]</span><span> Allah tidak menghendaki kamu dalam </span><span>[a] </span><span>kesukaran, akan tetapi Dia berkehendak supaya Dia mensucikan kamu dan akan melengkapkan nikmat-Nya atasmu supaya kamu bersyukur.</span></p><p><span>[a] </span><span>2:186; 2:287.</span></p>');
INSERT INTO `quran` VALUES (7028,2,5,8,'<p><span>Dan, ingatlah nikmat Allahkepadamu dan perjanjian-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=726">726</a><span>]</span><span> yang Dia telah ikatkan dengan kamu, ketika kamu berkata, </span><span>[a] </span><span>"Kami telah dengar dan kami taat." Dan, bertakwalah kepada Allah Sesungguhnya, Allah Maha Mengetahui apa-apa yang ada dalam dada..</span></p><p><span>[a] </span><span>2:286.</span></p>');
INSERT INTO `quran` VALUES (7029,2,5,9,'<p><span>Hai orang-orang yang beriman, </span><span>[a] </span><span>hendaklah kamu berdiri teguh karena Allah, menjadi saksi dengan adil; dan </span><span>[a] </span><span>janganlah kebencian sesuatu kaum mendorong kamu bertindak tidak adil. Berlakulah adil; itu lebih dekat kepada takwa. Dan bertakwalah kepada Allah Sesungguhnya, Allah Maha Mengetahui apa-apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>4:136    </span></p><p><span>[b] </span><span>5:3; 11:90</span></p>');
INSERT INTO `quran` VALUES (7030,2,5,10,'<p><span>[a] </span><span>Allah berjanji kepada orang-orang beriman dan beramal shaleh bagi mereka ada ampunan dan ganjaran besar.</span></p><p><span>[a] </span><span>24:56; 48:30</span></p>');
INSERT INTO `quran` VALUES (7031,2,5,11,'<p><span>Dan </span><span>[a] </span><span>orang-orang yang ingkar dan yang mendustakan Ayat-ayat Kami, mereka itulah penghuni Jahannam.</span></p><p><span>[a] </span><span>5:87; 6:50; 7:37, 41; 22:58.</span></p>');
INSERT INTO `quran` VALUES (7032,2,5,12,'<p><span>Hai orang-orang yang beriman, ingatlah nikmat Allah atasmu, ketika satu kaum bermaksud menjangkaukan tangan mereka terhadap kamu, tetapi </span><span>[a] </span><span>Dia telah menahan tangan mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=727">727</a><span>]</span><span> dari kamu; dan bertakwalah kepada Allah . Dan kepada Allah hendaknya orang-orang beriman bertawakkal.</span></p><p><span>[a] </span><span>5:111.</span></p>');
INSERT INTO `quran` VALUES (7033,2,5,13,'<p><span>Dan sesungguhnya </span><span>[a] </span><span>Allah telah mengambil perjanjian yang teguh dari Bani Israil; dan </span><span>[b] </span><span>Kami membangkitkan di antara mereka dua belas pemimpin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=727a">727a</a><span>]</span><span> Dan Allah berfirman, "Sesungguhnya, Aku beserta kamu. Jika kamu dawam mendirikan shalat, dan membayar zakat, dan beriman kepada rasul-rasul-Ku, dan membantu mereka, dan meminjamkan kepada Allah pinjaman yang baik, niscaya akan Ku-hapuskan dari kamu keburukanmu, dan pasti </span><span>[c] </span><span>Ku-masukkan kamu ke dalam kebun-kebun yang di bawahnya mengalir sungai-sungai. Kemudian barang siapa di antara kamu ingkar sesudah itu, maka sesungguhnya sesatlah ia dari jalan lurus."</span></p><p><span>[a] </span><span>2:41, 84.</span></p><p><span>[b] </span><span>2:61; 7:161.</span></p><p><span>[c] </span><span>Lihat 2:26.</span></p>');
INSERT INTO `quran` VALUES (7034,2,5,14,'<p><span>Maka, oleh sebab mereka melanggar janji mereka, Kami laknat mereka, dan Kami jadikan hati mereka keras. Mereka menukar perkataan-perkataan dari tempat-tempatnya, dan melupakan sebagian dari apa yang telah dinasihatkan kepada mereka. Dan senantiasa engkau akan menemukan pengkhianatan dari mereka. Kecuali sedikit dari mereka. Maka maafkanlah mereka dan biarkanlah. Sesungguhnya Allah mencintai orang-orang yang berbuat kebajikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=727b">727b</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7035,2,5,15,'<p><span>Dan, dari orang-orang yang berkata, "Kami orang Nasrani; Kami telah ambil janji dari mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=727c">727c</a><span>]</span><span> tetapi mereka telah melupakan sebagian dari apa-apa yang dengannya mereka telah dinasihatkan. Maka, Kami timbulkan di antara mereka permusuhan dan kebencian hingga Hari Kiamat. Dan, Allah akan segera memberitahu mereka mengenai apa yang telah dikerjakan mereka.</span></p>');
INSERT INTO `quran` VALUES (7036,2,5,16,'<p><span>Hai Ahlikitab, sesungguhnya telah datang kepada kamu Rasul Kami yang menjelaskan kepada kamu banyak dari apa yang telah kamu sembunyikan dari Kitab, dan ia memaafkan banyak dari </span><span>kesalahanmu</span><span>. Sesungguhnya telah datang kepadamu Nur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=727d">727d</a><span>]</span><span> dari Allah dan Kitab yang nyata.</span></p>');
INSERT INTO `quran` VALUES (7037,2,5,17,'<p><span>Dengan itu Allah menuntun orang-orang yang mengikuti keridhaan-Nya pada jalan-jalan keselamatan, dan </span><span>[a] </span><span>mengeluarkan mereka dari kegelapan kepada cahaya dengan izin-Nya, dan menuntun mereka kepada jalan lurus.</span></p><p><span>[a] </span><span>2:258; 14:2; 33:44; 57:10; 65:12.</span></p>');
INSERT INTO `quran` VALUES (7038,2,5,18,'<p><span>[a] </span><span>Sesungguhnya telah ingkar orang-orang yang berkata, "Sesungguhnya Allah itu ialah Al-Masih ibnu Maryam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=728">728</a><span>]</span><span> dan ibunya serta semua orang yang ada di bumi ini ?" Dan, </span><span>[b] </span><span>kepunyaan Allah kerajaan seluruh langit dan bumi dan apa-apa yang ada di antara keduanya. Dia menciptakan apa yang Dia kehendaki. Dan Allah Maha Kuasa atas segala sesuatu.</span></p><p><span>[a] </span><span>5:73; 74.    </span></p><p><span>[b] </span><span>Lihat 3:190.</span></p>');
INSERT INTO `quran` VALUES (7039,2,5,19,'<p><span>Dan, orang-orang Yahudi dan Nasrani berkata, </span><span>[a] </span><span>"Kami anak-anak Allah dan kekasih- kekasih-Nya." Katakanlah, "Mengapakah Dia mengazab kamu karena dosa-dosamu ? Bahkan kamu adalah manusia-manusia biasa dari antara mereka yang telah Dia ciptakan." </span><span>[b] </span><span>Dia mengampuni siapa yang Dia kehendaki dan Dia menghukum siapa yang Dia kehendaki. Dan kepunyaan Allah kerajaan langit dan bumi dan apa-apa yang ada di antara keduanya, dan kepada-Nya tempat kembali.</span></p><p><span>[a] </span><span>62:7.</span></p><p><span>[b] </span><span>2:285; 3:130; 5:41.</span></p>');
INSERT INTO `quran` VALUES (7040,2,5,20,'<p><span>Hai Ahlikitab, sungguh telah datang kepadamu Rasul Kami, </span><span>[a] </span><span>yang menjelaskan kepadamu sesudah terhentinya rasul-rasul supaya kamu tidak mengatakan, "Kepada kami tidak pernah datang seorang pemberi kabar-suka dan tidak pula seorang pemberi ingat." </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=729">729</a><span>]</span><span> Padahal, sesungguhnya telah datang kepadamu seorang pembawa kabar suka dan pemberi ingat. Dan Allah Maha Kuasa atas segala sesuatu.</span></p><p><span>[a] </span><span>5:16.</span></p>');
INSERT INTO `quran` VALUES (7041,2,5,21,'<p><span>Dan, tatkala Musa berkata kepada kaumnya, "Hai kaumku, ingatlah </span><span>[a] </span><span>nikmat Allah atasmu, ketika Dia menjadikan nabi-nabi di antaramu dan menjadikan kamu raja-raja </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=730">730</a><span>]</span><span> dan Dia memberikan kepadamu apa yang tidak diberikan kepada kaum lain di antara bangsa-bangsa.</span></p><p><span>[a] </span><span>1:7; 4:70; 19:59.</span></p>');
INSERT INTO `quran` VALUES (7042,2,5,22,'<p><span>"Hai kaumku, masukilah Tanah Suci yang telah ditetapkan Allah bagimu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=731">731</a><span>]</span><span> dan janganlah kamu berpaling ke belakang sehingga kamu kembali menjadi orang-orang yang rugi."</span></p>');
INSERT INTO `quran` VALUES (7043,2,5,23,'<p><span>Berkata mereka, "Ya Musa, sesungguhnya di dalam </span><span>negeri</span><span> itu ada suatu kaum liar yang kuat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=732">732</a><span>]</span><span> dan tidaklah kami akan memasukinya sebelum mereka keluar dari situ. Maka jika mereka keluar dari situ, maka kami akan memasukinya." </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=733">733</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7044,2,5,24,'<p><span>Berkatalah dua orang laki-laki </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=734">734</a><span>]</span><span> dari antara mereka yang takut </span><span>kepada </span><span>Allah , Allah telah memberi nikmat kepada keduanya, "Masukilah melalui pintu gerbang </span><span>dan serbulah</span><span> mereka; maka jika kamu memasuki negeri itu niscaya kamu akan menang, Dan, kepada Allah hendaknya kamu </span><span>[a] </span><span>bertawakal, jika kamu benar-benar mukmin."</span></p><p><span>[a] </span><span>3:161; 5:12; 9:51.</span></p>');
INSERT INTO `quran` VALUES (7045,2,5,25,'<p><span>Mereka berkata, "Hai Musa, sesungguhnya kami sekali-kali tidak akan memasuki </span><span>negeri</span><span> itu, selama mereka masih ada di dalamnya. Karena itu pergilah engkau bersama Tuhan engkau dan berperanglah engkau berdua sesungguhnya kami hendak duduk-duduk saja di sini !"</span></p>');
INSERT INTO `quran` VALUES (7046,2,5,26,'<p><span>Musa</span><span> berkata, "Ya Tuhan-ku, sesungguhnya aku tidak berkuasa selain terhadap diriku dan saudara laki-lakiku; maka bedakanlah di antara kami dan kaum yang durhaka itu."</span></p>');
INSERT INTO `quran` VALUES (7047,2,5,27,'<p><span>Allah berfirman, "Maka </span><span>[a] </span><span>sesungguhnya </span><span>negeri</span><span> itu diharamkan bagi mereka selama empat puluh tahun; mereka akan bertualang kebingungan di muka bumi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=735">735</a><span>]</span><span> Maka janganlah engkau bersedih atas kaum yang durhaka."</span></p>');
INSERT INTO `quran` VALUES (7048,2,5,28,'<p><span>Dan, ceriterakanlah kepada mereka kisah kedua anak Adam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=736">736</a><span>] </span><span>dengan hak, ketika kedua mereka itu mempersembahkan korban, maka salah seorang dari kedua mereka itu dikabulkan dan dari yang lain tidak dikabulkan </span><span>lalu</span><span> ia berkata, "Pasti akan kubunuh engkau." Berkata </span><span>yang lain,</span><span> “Sesunguhnya Allah hanya mengabulkan dari orang-orang yang bertakwa.</span></p>');
INSERT INTO `quran` VALUES (7049,2,5,29,'<p><span>"Jika engkau menjangkaukan tangan engkau terhadapku untuk membunuhku, aku tidak akan menjangkaukan tanganku terhadap engkau untuk membunuh engkau. Sesungguhnya, aku takut kepada Allah, Tuhan sekalian Alam."</span></p>');
INSERT INTO `quran` VALUES (7050,2,5,30,'<p><span>"Sesungguhnya aku menginginkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=737">737</a><span>]</span><span> supaya engkau menanggung dosaku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=738">738</a><span>]</span><span> dan dosa engkau sendiri, maka engkau akan menjadi penghuni Api, dan demikianlah balasan bagi orang-orang aniaya."</span></p>');
INSERT INTO `quran` VALUES (7051,2,5,31,'<p><span>Tetapi nafsunya telah membujuknya supaya membunuh saudaranya. Maka, ia membunuhnya, dan ia pun menjadi di antara orang-orang yang rugi.</span></p>');
INSERT INTO `quran` VALUES (7052,2,5,32,'<p><span>Maka Allah mengirim seekor burung gagak yang menggaruk-garuk di tanah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=739">739</a><span>]</span><span> supaya Dia memperlihatkan kepadanya, bagaimana ia harus menyembunyikan mayat saudaranya. Berkata ia, "Celaka aku ! tak sanggupkah aku berbuat seperti gagak ini sehingga dapat kusembunyikan mayat saudaraku ?" Maka ia menjadi di antara orang-orang yang malu.</span></p>');
INSERT INTO `quran` VALUES (7053,2,5,33,'<p><span>Oleh sebab itu Kami menetapkan bagi Bani Israil bahwa barangsiapa yang membunuh seseorang. padahal orang itu </span><span>tidak pernah membunuh</span><span> orang lain atau telah mengadakan kerusuhan di bumi, maka seoleh-olah ia membunuh sekalian manusia. Dan barangsiapa menyelamatkan nyawa seseorang, maka ia seolah-olah menghidupkan sekalian manusia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=740">740</a><span>]</span><span> Dan sesungguhnya telah datang kepada mereka </span><span>[a] </span><span>rasul-rasul Kami dengan Tanda-tanda nyata; kemudian sesungguhnya kebanyakan dari mereka sesudah itu melampuai batas di bumi ini.</span></p><p><span>[a] </span><span>7:102; 9:70; 14:10; 40:23.</span></p>');
INSERT INTO `quran` VALUES (7054,2,5,34,'<p><span>Sesungguhnya balasan bagi orang-orang </span><span>[a] </span><span>yang memerangi Allah dan Rasul-Nya dan berdaya upaya mengadakan kekacauan di bumi ini ialah mereka dibunuh atau disalib atau pun di potong tangan dan kaki mereka disebabkan oleh permusuhan </span><span>mereka</span><span>, atau mereka diusir dari negeri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=741">741</a><span>]</span><span> Hal demikian adalah penghinaan bagi mereka di dunia ini; dan di akhirat pun mereka akan mendapat azab yang besar.</span></p><p><span>[a] </span><span>9:107.</span></p>');
INSERT INTO `quran` VALUES (7055,2,5,35,'<p><span>Kecuali mereka </span><span>[a] </span><span>yang bertobat sebelum kamu berkuasa atas mereka. Maka, ketahuilah bahwa Allah itu Maha Pengampun, Maha Penyayang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=742">742</a><span>]</span></p><p><span>[a] </span><span>4:18</span></p>');
INSERT INTO `quran` VALUES (7056,2,5,36,'<p><span>Hai orang-orang yang beriman, bertakwalah kepada Allah dan </span><span>[a] </span><span>carilah jalan pendekatan diri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=743">743</a><span>]</span><span> kepada-Nya, dan </span><span>[b] </span><span>berjuanglah di jalan-Nya supaya kamu berjaya.</span></p><p><span>[a] </span><span>17:58.    </span></p><p><span>[b] </span><span>9:41; 22:79.</span></p>');
INSERT INTO `quran` VALUES (7057,2,5,37,'<p><span>Sesungguhnya, orang-orang yang ingkar jika mereka mempunyai apa yang ada di bumi seluruhnya dan semisal itu besertanya supaya mereka </span><span>[a] </span><span>menebus diri dengannya dari azab Hari Kiamat , niscaya tidak akan dapat diterima dari mereka; dan bagi mereka azab yang pedih.</span></p><p><span>[a] </span><span>13:19; 39:48.</span></p>');
INSERT INTO `quran` VALUES (7058,2,5,38,'<p><span>Mereka ingin supaya keluar dari Api, namun mereka tidak akan dapat keluar darinya, dan bagi mereka azab abadi.</span></p>');
INSERT INTO `quran` VALUES (7059,2,5,39,'<p><span>Dan, laki-laki pencuri dan perempuan pencuri, maka potonglah tangan keduanya </span><span>sebagai</span><span> pembalasan atas apa yang telah diusahakan mereka, </span><span>inilah</span><span> sebagai hukuman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=744">744</a><span>]</span><span> dari Allah Dan Allah Maha Perkasa, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (7060,2,5,40,'<p><span>Tetapi, </span><span>[a] </span><span>barangsiapa bertobat sesudah melakukan kezalimannya dan memperbaiki diri, maka sesungguhnya Allah akan kembali kepadanya dengan penuh kasih. Sesungguhnya Allah itu Maha Pengampun, Maha Penyayang.</span></p><p><span>[a] </span><span>6:55; 20:83; 25:72.</span></p>');
INSERT INTO `quran` VALUES (7061,2,5,41,'<p><span>Tidaklah engkau mengetahui bahwa Allah yang mempunyai </span><span>[a] </span><span>kerajaan seluruh langit dan bumi ? Dia menghukum siapa yang Dia kehendaki dan Dia mengampuni bagi siapa yang Dia kehendaki. Dan Allah itu Maha Kuasa atas segala sesuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=745">745</a><span>]</span></p><p><span>[a] </span><span>5:19; 48:15. </span></p>');
INSERT INTO `quran` VALUES (7062,2,5,42,'<p><span>Hai Rasul, janganlah mereka menyedihkan engkau, yaitu orang-orang yang cepat sekali dalam kekufuran yang berkata dengan mulut mereka, "Kami telah beriman," padahal hati mereka tidak beriman. Dan di antara orang-orang Yahudi ada yang gemar </span><span>[a] </span><span>mendengar kedustaan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=746">746</a><span>]</span><span> mereka mendengar untuk </span><span>meneruskan kepada </span><span> kaum lain yang belum pernah datang kepada engkau. Mereka </span><span>[b] </span><span>memutar-balikkan kalimat Allah itu yang sudah terletak </span><span>benar</span><span> pada tempat-tempatnya. Mereka berkata, "Jika ini diberikan kepadamu, maka terimalah; dan jika hal itu tidak diberikan kepadamu, maka menghindarlah !" Dan, barangsiapa yang Allah ingin mengujinya, maka engkau tidak akan sekali-kali mampu menolong dia terhadap Allah sedikit pun. Mereka inilah orang-orang yang Allah tidak berkenan mencusikan hati mereka. Bagi mereka ada kehinaan di dunia ini; dan bagi mereka di akhirat juga ada azab yang besar.</span></p><p><span>[a] </span><span>9:47.    </span></p><p><span>[b] </span><span>2:76; 3:79; 4:47.</span></p>');
INSERT INTO `quran` VALUES (7063,2,5,43,'<p><span>Mereka gemar mendengarkan berita-berita dusta, </span><span>[a] </span><span>memakan barang-barang terlarang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=747">747</a><span>]</span><span> Jika mereka datang kepada engkau </span><span>untuk memperoleh keputusan</span><span>, maka hakimilah di antara mereka atau berpalinglah dari mereka. Dan, jika engkau berpaling dari mereka, mereka sekali-kali tidak akan mendatangkan kemudaratan kepada engkau sedikit pun. Dan, jika engkau menghakimi, maka hakimilah di antara mereka dengan adil. Sesungguhnya Allah mencintai orang-orang yang adil.</span></p><p><span>[a] </span><span>5:63, 64.</span></p>');
INSERT INTO `quran` VALUES (7064,2,5,44,'<p><span>Dan, bagaimanakah mereka akan menjadikan engkau hakim,padahal mereka memiliki Taurat yang di dalamnya terdapat hukum Allah ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=748">748</a><span>]</span><span> Kemudian mereka berpaling sesudah itu. Dan sungguh mereka bukan orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (7065,2,5,45,'<p><span>Sesungguhnya,telah Kami turunkan </span><span>[a]</span><span>Taurat yang di dalamnya ada petunjuk dan cahaya. Dengan itulah para nabi yang patuh </span><span>kepada Kami</span><span> berhakim bagi orang-orang Yahudi, sebagaimana </span><span>dilakukan pula oleh</span><span> para arif-akan-Tuhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=749">749</a><span>]</span><span> dan para ulama; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=750">750</a><span>]</span><span> karena mereka diharapkan menjaga Kitab Allah dan </span><span>disebabkan</span><span> mereka menjadi pengawas atasnya. Maka janganlah takut kepada manusia, melainkan takutlah kepada-Ku; dan janganlah kamu </span><span>[c] </span><span>memperjual-belikan Ayat-ayat-Ku dengan harga rendah. Dan, </span><span>[b] </span><span>barangsiapa tidak berhakim dengan apa-apa yang telah diturunkan Allah , maka mereka itulah orang-orang kafir</span></p><p><span>[a] </span><span>6:92; 7:155.    </span></p><p><span>[b] </span><span>5:46, 48.    </span></p><p><span>[c] </span><span>2:42.     </span></p>');
INSERT INTO `quran` VALUES (7066,2,5,46,'<p><span>Dan Kami wajibkan bagi mereka di dalam </span><span>Taurat</span><span> itu bahwa, "Jiwa </span><span>dibalas</span><span> dengan jiwa, dan mata dengan mata, dan hidung dengan hidung, dan telinga dengan telinga, dan gigi dengan gigi, dan untuk luka-luka ada pembalasannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=751">751</a><span>]</span><span> Dan barangsiapa melepaskan hak untuk membalas, maka hal demikian itu akan menjadi penebus dosa baginya, dan </span><span>[a] </span><span>barangsiapa tidak berhakim menurut apa-apa yang telah diturunkan Allah , mereka itulah orang-orang aniaya.</span></p><p><span>[a] </span><span>5:45, 48.</span></p>');
INSERT INTO `quran` VALUES (7067,2,5,47,'<p><span>Dan, Kami </span><span>[a] </span><span>menyebabkan Isa ibnu Maryam mengikuti jejak mereka, </span><span>para nabi pendahulu</span><span>, </span><span>[b]</span><span> menggenapi apa yang telah ada sebelumnya di dalam Taurat; dan Kami berikan kepadanya Injil yang di dalamnya </span><span>terdapat</span><span> petunjuk dan cahaya, dan menggenapi apa yang telah </span><span>diwahyukan</span><span> sebelumnya di dalam Taurat dan sebagai petunjuk dan nasihat bagi orang-orang bertakwa.</span></p><p><span>[a] </span><span>2:88; 57:28.    </span></p><p><span>[b] </span><span>3:51; 61:7.</span></p>');
INSERT INTO `quran` VALUES (7068,2,5,48,'<p><span>Dan hendaklah para pengikut Injil berhakim menurut apa-apa yang telah diturunkan Allah di dalamnya, dan </span><span>[a] </span><span>barang siapa tidak berhakim dengan apa-apa yang diturunkan Allah, mereka itulah orang-orang durhaka.</span></p><p><span>[a] </span><span>5:45, 46.</span></p>');
INSERT INTO `quran` VALUES (7069,2,5,49,'<p><span>Dan, Kami </span><span>[d] </span><span>menurunkan kepada engkau Kitab yang mengandung kebenaran dan menggenapi apa yang telah </span><span>diwahyukan</span><span> sebelumnya di dalam Alkitab dan sebagai penjaga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=752">752</a><span>]</span><span> atasnya. Maka, hendaklah engkau </span><span>[e] </span><span>berhakim di antara mereka dengan apa-apa yang diturunkan Allah dan janganlah mengikuti hawa nafsu mereka </span><span>dengan berpaling</span><span> dari kebenaran yang telah datang kepada engkau. Bagi setiap orang di antaramu Kami menetapkan jalan kecil dan besar menuju air </span><span>rohani. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=753">753</a><span>] </span><span> Dan </span><span>[a] </span><span>jika Allah menghendaki niscaya Dia akan menjadikan kamu satu umat, akan tetapi Dia hendak menguji kamu tentang apa yang diberikan-Nya kepadamu. Maka </span><span>[b]</span><span> berlomba-lombalah kamu dalam kebaikan Kepada Allah , kamu semua akan kembali, maka Dia akan memberitahukan kepadamu tentang apa-apa yang di dalamnya kamu berselisih.</span></p><p><span>[d] </span><span>6:106; 39:3.     </span></p><p><span>[e] </span><span>5:50.</span></p><p><span>[a] </span><span>10:100; 11:119; 16:10,    </span></p><p><span>[b] </span><span>3:134; 35:33; 57:22.</span></p>');
INSERT INTO `quran` VALUES (7070,2,5,50,'<p><span>Dan </span><span>hai Rasul</span><span>, hendaklah engkau </span><span>[a] </span><span>menghakimi di antara mereka dengan apa yang diturunkan Allah </span><span>kepada engkau</span><span> dan janganlah engkau menuruti hawa nafsu mereka, dan hindarilah mereka , </span><span>[b] </span><span>jangan-jangan mereka melibatkan engkau ke dalam fitnah dikarenakan oleh sebagian yang telah diturunkan Allah kepada engkau. Tetapi jika mereka berpaling, maka ketahuilah bahwa Allah berkehendak menghukum mereka karena sebagian dosa mereka. Dan sesungguhnya kebanyakan di antara manusia adalah orang-orang fasik.</span></p><p><span>[a] </span><span>5:49.    </span></p><p><span>[b] </span><span>17:74.</span></p>');
INSERT INTO `quran` VALUES (7071,2,5,51,'<p><span>Adakah yang mereka inginkan itu hukum </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=754">754</a><span>]</span><span> Jahiliah ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=755">755</a><span>]</span><span> Dan, siapakah yang lebih baik dari Allah dalam menghakimi bagi kaum yang berkeyakinan ?</span></p>');
INSERT INTO `quran` VALUES (7072,2,5,52,'<p><span>Hai orang-orang yang beriman ! </span><span>[a]</span><span> Janganlah kamu menjadikan orang-orang Yahudi dan orang-orang Nasrani sebagai penolong. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=756">756</a><span>]</span><span> Sebagian dari mereka penolong sebagiang lainnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=757">757</a><span>]</span><span> Dan, barangsiapa di antara kamu menjadikan mereka penolong-penolong, maka sesungguhnya ia adalah dari mereka. Sesungguhnya Allah tidak memberi petunjuk kepada kaum yang aniaya.</span></p><p><span>[a] </span><span>3:29, 119; 4:145; 5:58; 60:10.</span></p>');
INSERT INTO `quran` VALUES (7073,2,5,53,'<p><span>Maka, engkau akan melihat orang-orang yang di dalam hati mereka ada penyakit bergegas-gegas kepada mereka </span><span>yang kafir</span><span> seraya berkata, "Kami takut kalau-kalau bencana </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=758">758</a><span>]</span><span> menimpa kami ," Boleh jadi Allah akan mendatangkan </span><span>[a] </span><span>kemenangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=759">759</a><span>]</span><span> atau suatu peristiwa </span><span>lain</span><span> dari sisi-Nya. Maka, mereka akan merasa malu atas apa yang telah disembunyikan mereka di dalam diri mereka.</span></p><p><span>[a] </span><span>32:30.</span></p>');
INSERT INTO `quran` VALUES (7074,2,5,54,'<p><span>Dan akan berkata orang-orang yang beriman, "Inikah orang-orang yang bersumpah dengan nama Allah dengan dengan sumpah mereka yang sesungguh-sungguhnya bahwa mereka benar-benar bersama kamu ?" Sia-sialah segala amal mereka, kemudian mereka menjadi orang-orang yang rugi.</span></p>');
INSERT INTO `quran` VALUES (7075,2,5,55,'<p><span>Hai orang-orang yang beriman, barangsiapa di antaramu </span><span>[a] </span><span>murtad dari agamanya, maka segera Allah akan mendatangkan suatu kaum, Dia akan mencintai mereka dan mereka pun akan mencintai-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=760">760</a><span>]</span><span> mereka akan bersikap lemah-lembut terhadap orang-orang mukmin dan keras terhadap orang-orang kafir. Mereka akan berjuang di jalan Allah dan tidak takut celaan seorang pencela. Itulah karunia Allah ; Dia memberikannya kepada siapa yang Dia kehendaki dan Allah itu Maha Luas, Maha Mengetahui.</span></p><p><span>[a] </span><span>3:145.</span></p>');
INSERT INTO `quran` VALUES (7076,2,5,56,'<p><span>[a] </span><span>Sesungguhnya penolongmu hanyalah Allah , Rasul-Nya dan orang-orang beriman yang dawam mendirikan shalat dan membayar zakat dan mereka taat </span><span>kepada </span>Allah<span>.</span></p><p><span>[a] </span><span>2:258; 3:69.</span></p>');
INSERT INTO `quran` VALUES (7077,2,5,57,'<p><span>Dan barangsiapa menjadikan Allah , Rasul-Nya dan mereka yang beriman sebagai penolong, maka sesungguhnya </span><span>[a] </span><span>jemaat Allah pasti menang.</span></p><p><span>[a] </span><span>58:23.</span></p>');
INSERT INTO `quran` VALUES (7078,2,5,58,'<p><span>Hai orang-orang beriman, </span><span>[a] </span><span>janganlah kamu mengambil </span><span>[b] </span><span>mereka yang menjadikan agamamu perolokan dan permainan, </span><span>yaitu</span><span> dari antara orang-orang yang telah diberi Kitab sebelummu dan orang-orang kafir sebagai penolong. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=761">761</a><span>]</span><span> Dan bertakwalah kepada Allah jika kamu orang-orang mukmin.</span></p><p><span>[a] </span><span>3:29, 119; 4:145; 5:52; 60:10    </span></p><p><span>[v] </span><span>6:71; 7:52.</span></p>');
INSERT INTO `quran` VALUES (7079,2,5,59,'<p><span>Dan, apabila kamu menyeru </span><span>orang-orang</span><span> untuk shalat, mereka menjadikan itu sebagai olok-olok dan permainan. Hal itu karena mereka kaum yang tidak menggunakan akal.</span></p>');
INSERT INTO `quran` VALUES (7080,2,5,60,'<p><span>Katakanlah, "Hai Ahlikitab, apakah kamu </span><span>[a] </span><span>menganggap kami salah hanya karena kami beriman kepada Allah dan kepada apa-apa yang diturunkan kepada kami dan kepada apa-apa yang diturunkan sebelum ini ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=762">762</a><span>]</span><span> Padahal sesungguhnya kebanyakan kamu orang-orang durhaka."</span></p><p><span>[a] </span><span>7:127; 60:2.</span></p>');
INSERT INTO `quran` VALUES (7081,2,5,61,'<p><span>Katakanlah, "Apakah kuberitahukan kepadamu yang lebih buruk daripada itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=763">763</a><span>]</span><span> tentang pembalasan dari sisi Allah ? </span><span>Yaitu</span><span> Orang-orang yang dilaknati Allah dan kepadanya Dia </span><span>[b] </span><span>murka dan menjadikan sebagian dari mereka kera-kera dan babi-babi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=764">764</a><span>]</span><span> dan yang menyembah </span><span>[c] </span><span>syaitan. </span><span>[d] </span><span>Mereka itu berada di tempat yang buruk dan jauh tersesat dari jalan lurus.</span></p><p><span>[b] </span><span>2:66; 7:167.</span></p><p><span>[c] </span><span>2:258; 4:52.</span></p><p><span>[d] </span><span>12:78; 25:35.</span></p>');
INSERT INTO `quran` VALUES (7082,2,5,62,'<p><span>Dan, apabila mereka datang kepada kamu, mereka berkata, "Kami beriman." </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=765">765</a><span>]</span><span> padahal mereka masuk dengan kekufuran dan keluar dengannya </span><span>juga</span><span>. Dan Allah lebih mengetahui apa-apa yang mereka sembunyikan.</span></p>');
INSERT INTO `quran` VALUES (7083,2,5,63,'<p><span>Dan, engkau menyaksikan kebanyakan mereka bergegas dalam dosa dan permusuhan dan </span><span>[a] </span><span>makanan mereka haram. Sungguh amat jahatlah yang mereka kerjakan.</span></p><p><span>[a] </span><span>5:43.</span></p>');
INSERT INTO `quran` VALUES (7084,2,5,64,'<p><span>Mengapa orang-orang arif-akan-Tuhan dan para ulama tidak melarang mereka dari ucapan dosa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=766">766</a><span>]</span><span> dan </span><span>dari</span><span> memakan makanan mereka yang haram ? Sungguh amat </span><span>[a] </span><span>jahatlah apa yang telah mereka kerjakan</span></p><p><span>[a] </span><span>5:80.</span></p>');
INSERT INTO `quran` VALUES (7085,2,5,65,'<p><span>Dan, berkata </span><span>[a] </span><span>orang Yahudi, "Tangan Allah terbelenggu. </span><span>[b] </span><span>Tangan mereka </span><span>sendirilah</span><span> yang akan dibelenggu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=767">767</a><span>]</span><span> dan mereka akan dilaknat disebabkan apa yang dikatakan oleh mereka. </span><span>tidak benar,</span><span> bahkan kedua tangan-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=768">768</a><span>]</span><span> terbuka lebar; Dia membelanjakan sebagaimana Dia kehendaki. Dan, </span><span>[c]</span><span> apa yang diturunkan kepada engkau dari Tuhan engkau niscaya akan menyebabkan kebanyakan mereka bertambah durhaka dan kufur. Dan, </span><span>[d] </span><span>Kami melontarkan permusuhan dan kebencian di antara mereka sampai Hari Kiamat. Setiap kali mereka </span><span>[a] </span><span>menyalakan api untuk perang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=769">769</a><span>]</span><span> Allah memadamkannya. Dan, mereka berusaha membuat kerusuhan di muka bumi, dan Allah tidak menyukai perusuh-perusuh.</span></p><p><span>[a] </span><span>2:18.</span></p><p><span>[b] </span><span>3:182; 36:48.</span></p><p><span>[c] </span><span>5:69.</span></p><p><span>[d] </span><span>3:56; 5:15.</span></p>');
INSERT INTO `quran` VALUES (7086,2,5,66,'<p><span>Dan, </span><span>[b] </span><span>andaikan para Ahlikitab beriman dan bertakwa niscaya Kami hapuskan dari mereka keburukan mereka dan pasti Kami masukkan mereka ke dalam kebun-kebun menikmatan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=770">770</a><span>]</span></p><p><span>[b] </span><span>7:97.</span></p>');
INSERT INTO `quran` VALUES (7087,2,5,67,'<p><span>Dan, andaikata </span><span>[a] </span><span>mereka benar-benar menegakkan </span><span>ajaran</span><span> Taurat dan Injil dan apa-apa yang diturunkan kepada mereka dari Tuhan mereka, niscaya mereka akan memakan </span><span>barang-barang</span><span> dari atas mereka dan dari bawah kaki mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=771">771</a><span>]</span><span> Di antara mereka ada umat yang mengambil jalan tengah, dan alangkah buruknya apa yang dikerjakan oleh kebanyakan mereka.</span></p><p><span>[a] </span><span>5:48</span></p>');
INSERT INTO `quran` VALUES (7088,2,5,68,'<p><span>Hai Rasul, </span><span>[a] </span><span>sampaikanlah apa-apa yang diturunkan kepada engkau dari Tuhan engkau. Dan jika engkau tidak melakukan </span><span>hal itu</span><span> maka engkau tidak menyampaikan amanat-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=772">772</a><span>]</span><span> Dan, Allah akan melindungi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=773">773</a><span>]</span><span> engkau dari manusia. Sesungguhnya Allah tidak akan memberi petunjuk kepada kaum kafir.</span></p><p><span>[a] </span><span>6:20. </span></p>');
INSERT INTO `quran` VALUES (7089,2,5,69,'<p><span>Katakanlah, "Hai Ahlikitab, kamu tidak </span><span>berdiri</span><span> di atas apa pun, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=774">774</a><span>]</span><span> sebelum kamu menegakkan </span><span>ajaran</span><span> Taurat dan Injil dan apa-apa yang diturunkan kepadamu dari Tuhan-mu." Dan sesungguhnya, apa-apa yang diturunkan kepada kamu dari Tuhan-mu itu akan meyebabkan kebanyakan mereka bertambah </span><span>[a] </span><span>durhaka dan kufur, maka janganlah engkau bersedih hati terhadap kaum kafir.</span></p><p><span>[a]</span><span> 5:65.</span></p>');
INSERT INTO `quran` VALUES (7090,2,5,70,'<p><span>Sesungguhnya, </span><span>[a] </span><span>orang-orang beriman dan orang-orang Yahudi dan orang-orang Shabi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=775">775</a><span>]</span><span> dan orang-orang Nasrani barangsiapa beriman kepada Allah dan Hari Kemudian serta beramal saleh, maka </span><span>[b] </span><span>tidak akan ada ketakutan atas mereka dan tidak pula mereka bersedih.</span></p><p><span>[a]</span><span> 2:63; 22:18.    </span></p><p><span>[b]</span><span> Lihat 2:63.</span></p>');
INSERT INTO `quran` VALUES (7091,2,5,71,'<p><span>Sesungguhnya Kami telah mengambil perjanjian dari kaum Bani Israil dan Kami mengutus kepada mereka rasul-rasul. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=776">776</a><span>]</span><span> </span><span>[a] </span><span>Setiap kali datang kepada mereka seorang rasul dengan apa-apa yang tidak berkenan di hati mereka, mereka mendustakan sebagian dan mereka membunuh sebagian.</span></p><p><span>[a]</span><span> 2:88.</span></p>');
INSERT INTO `quran` VALUES (7092,2,5,72,'<p><span>Dan, mereka menyangka bahwa tidak akan ada fitnah </span><span>akibat perbuatan mereka</span><span>, maka mereka menjadi buta dan tuli. Kemudian Allah kembali dengan penuh kasih kepada mereka; namun kebanyakan dari mereka masih buta dan tuli. Dan Allah Maha Melihat apa yang mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (7093,2,5,73,'<p><span>Sesungguhnya ingkarlah </span><span>[a] </span><span>orang-orang yang berkata, "Sesungguhnya Allah itu adalah Al-Masih ibnu Maryam," padahal Al-Masih berkata, "Hai Bani Israil, </span><span>[b] </span><span>beribadahlah kepada Allah Tuhan-ku dan Tuhan-mu." </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=777">777</a><span>]</span><span> Sesungguhnya, barangsiapa mempersekutukan </span><span>sesuatu</span><span> dengan Allah ,maka sesungguhnya Allah mengharamkan surga baginya dan tempat tinggalnya ialah Api. Dan tak ada bagi orang-orang zalim seorang penolong pun.</span></p><p><span>[a] </span><span>4:172; 5:18; 9:30.</span></p><p><span>[b]</span><span> 5:118; 19:37.</span></p>');
INSERT INTO `quran` VALUES (7094,2,5,74,'<p><span>[a] </span><span>Sungguh ingkarlah orang-orang yang berkata, "Sesungguhnya Allah itu ketiga dari yang tiga;" </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=778">778</a><span>]</span><span> padahal tiada Tuhan melainkan Tuhan Yang Esa. Dan jika mereka tidak berhenti dari apa yang dikatakan mereka, maka pasti azab yang pedih akan menimpa orang-orang yang ingkar di antara mereka.</span></p><p><span>[a] </span><span>4:172.</span></p>');
INSERT INTO `quran` VALUES (7095,2,5,75,'<p><span>Apakah mereka tidak akan bertobat kepada Allah dan meminta ampun kepada-Nya, sedang kan Allah itu Maha Pengampun, Maha Penyayang ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=779">779</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7096,2,5,76,'<p><span>Al-Masih ibnu Maryam itu tidak lain melainkan seorang rasul; sesungguhnya telah wafat rasul-rasul sebelumnya. Dan ibunya adalah seorang yang benar. </span><span>[a] </span><span>Keduanya dahulu makan makanan.</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=780">780</a><span>]</span><span> Perhatikanlah, betapa Kami menjelaskan Tanda-tanda bagi mereka, kemudian perhatikanlah bagaimana mereka berpaling.</span></p><p><span>[a]</span><span> 21:9.</span></p>');
INSERT INTO `quran` VALUES (7097,2,5,77,'<p><span>Katakanlah, </span><span>[a] </span><span>"Apakah kamu menyembah selain Allah yang tak kuasa mendatangkan kemudaratan kepada kamu dan tidak pula keuntungan ?" </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=781">781</a><span>]</span><span> Dan, Allah adalah Dia Yang Maha Mendengar, Maha Mengetahui.</span></p><p><span>[a]</span><span> 6:72; 10:107; 21:67; 22:13.</span></p>');
INSERT INTO `quran` VALUES (7098,2,5,78,'<p><span>Katakanlah, </span><span>[a] </span><span>"Hai Ahlikitab, janganlah kamu melampaui batas dalam </span><span>urusan</span><span> agamamu dengan cara tanpa hak, dan janganlah kamu mengikuti keinginan kaum yang telah sesat sebelum ini dan menyesatkan pula banyak orang, dan mereka telah sesat dari jalan lurus."</span></p><p><span>[a] </span><span>4:172</span></p>');
INSERT INTO `quran` VALUES (7099,2,5,79,'<p><span>[a] </span><span>Dilaknat orang-orang yang ingkar dari antara Bani Israil oleh lidah Daud dan Isa ibnu Maryam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=782">782</a><span>]</span><span> Hal demikian itu disebabkan mereka durhaka dan melampaui batas.</span></p><p><span>[a] </span><span>3:88; 4:48.</span></p>');
INSERT INTO `quran` VALUES (7100,2,5,80,'<p><span>Mereka tidak </span><span>[a]</span><span> saling mencegah dari kemungkaran yang dikerjakan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=783">783</a><span>]</span><span> Sungguh amat buruk apa yang biasa mereka kerjakan.</span></p><p><span>[a]</span><span> 5:43, 64</span></p>');
INSERT INTO `quran` VALUES (7101,2,5,81,'<p><span>Engkau akan melihat kebanyakan dari mereka menjadikan sebagai </span><span>penolong</span><span> orang-orang ingkar. Sungguh buruk apa-apa yang telah dikirimkan oleh mereka lebih dahulu bagi diri mereka sehingga Allah </span><span>[a]</span><span> murka kepada mereka, dan di dalam azab inilah mereka akan tinggal lama.</span></p><p><span>[a]</span><span> 3:163.</span></p>');
INSERT INTO `quran` VALUES (7102,2,5,82,'<p><span>Dan, andaikata mereka beriman kepada Allah dan Nabi ini </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=784">784</a><span>]</span><span> dan kepada apa yang diturunkan kepadanya, niscaya mereka tidak akan mengambil orang-orang itu sebagai penolong-penolong mereka, akan tetapi kebanyakan mereka adalah orang-orang durhaka.</span></p>');
INSERT INTO `quran` VALUES (7103,2,5,83,'<p><span>Pasti engkau akan mendapati manusia yang paling keras permusuhannya terhadap orang-orang yang beriman, ialah orang-orang Yahudi dan orang-orang yang mempersekutukan </span><span>Allah/span><span>. Dan, pasti engkau akan mendapati orang yang paling dekat kecintaannya terhadap orang-orang beriman ialah mereka yang mengatakan, "Sesungguhnya kami orang-orang Nasrani." Hal demikian itu disebabkan di antara mereka ada pendeta-pendeta </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=785">785</a><span>]</span><span> dan rahib-rahib </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=786">786</a><span>]</span><span> dan juga mereka tidak sombong. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=787">787</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7104,2,5,84,'<p><span>Dan apabila mereka mendengar apa yang diturunkan kepada Rasul ini, engkau melihat mata mereka meneteskan air mata </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=788">788</a><span>]</span><span> disebabkan mereka telah mengenal kebenaran. Mereka berkata, </span><span>[a]</span><span> "Ya Tuhan kami, kami beriman, maka catatlah kami di antara orang-orang yang menjadi saksi."</span></p><p><span>[a]</span><span> 3:54, 194.</span></p>');
INSERT INTO `quran` VALUES (7105,2,5,85,'<p><span>Dan mengapa kamu tidak akan beriman kepada Allah dan kepada kebenaran yang telah datang kepada kami, sedangkan kami </span><span>[a]</span><span> mendambakan sekali supaya Tuhan memasukkan kami ke dalam golongan orang-orang shaleh ?</span></p><p><span>[a] </span><span>26:52.</span></p>');
INSERT INTO `quran` VALUES (7106,2,5,86,'<p><span>Maka disebabkan ucapan mereka, Allah </span><span>[a]</span><span> memberi ganjaran kepada mereka dengan kebun-kebun yang di bawahnya mengalir sungai-sungai, mereka kekal di dalamnya. Dan itulah ganjaran orang-orang yang berbuat kebaikan.</span></p><p><span>[a] </span><span>Lihat 2:26.</span></p>');
INSERT INTO `quran` VALUES (7107,2,5,87,'<p><span>Dan, </span><span>[a]</span><span> orang-orang yang ingkar dan mendustakan Ayat-ayat Kami mereka itulah penghuni neraka Jahim.</span></p><p><span>[a] </span><span>5:87; 6:50; 7:37; 22:58.</span></p>');
INSERT INTO `quran` VALUES (7108,2,5,88,'<p><span>Hai orang-orang yang beriman, </span><span>[a]</span><span> janganlah kamu mengharamkan barang-barang baik yang Allah telah menghalalkan bagimu dan janganlah kamu melampui batas. Sesungguhnya Allah tidak mencintai orang-orang yang melampui batas.</span></p><p><span>[a]</span><span> 10:60.</span></p>');
INSERT INTO `quran` VALUES (7109,2,5,89,'<p><span>Dan, </span><span>[a]</span><span> makanlah dari apa-apa yang Allah telah merezekikan kepadamu yang halal dan baik. Dan bertakwalah kepada AllahYang kepada-Nya kamu beriman,</span></p><p><span>[a]</span><span> 2:169; 8:70; 16:115.</span></p>');
INSERT INTO `quran` VALUES (7110,2,5,90,'<p><span>[a]</span><span> Allah tidak akan menuntut kamu atas sumpah-sumpahmu yang sia-sia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=789">789</a><span>]</span><span> akan tetapi Dia akan menuntut kamu atas sumpah-sumpahmu yang sungguh-sungguh; maka tebusannya ialah memberi makan sepuluh orang miskin dengan </span><span>makanan yang </span><span> rata-rata apa yang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=790">790</a><span>]</span><span> kamu beri makan kepada keluargamu, atau memberi pakaian kepada mereka, atau memerdekakan seorang sahaya. Tetapi, barangsiapa tidak memperoleh</span><span>nya</span><span> maka </span><span>hendaklah </span><span> ia berpuasa tiga hari. Hal demikian itu penebusan sumpah-sumpahmu apabila kamu telah bersumpah. Dan jagalah sumpah-sumpahmu. Demikian Allah menerangkan bagimu Ayat-ayat-Nya supaya kamu bersyukur.</span></p><p><span>[a]</span><span> 2:226.</span></p>');
INSERT INTO `quran` VALUES (7111,2,5,91,'<p><span>Hai orang-orang yang beriman, sesungguhnya </span><span>[a]</span><span> arak dan judi dan berhala-berhala dan </span><span>[b]</span><span> panah-panah undi adalah suatu kekejian dari perbuatan syaitan. Maka, jauhilah semua itu supaya kamu berhasil.</span></p><p><span>[a] </span><span>2:220; 5:92.</span></p><p><span>[b]</span><span> 5:4.</span></p>');
INSERT INTO `quran` VALUES (7112,2,5,92,'<p><span>Sesungguhnya syaitan hanya ingin menimbulkan permusuhan dan kebencian di antaramu melalui arak dan judi, dan hendak menghalangimu dari berzikir kepada Allah dan dari shalat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=790a">790a</a><span>]</span><span> Maka, apakah kamu akan berhenti ?</span></p>');
INSERT INTO `quran` VALUES (7113,2,5,93,'<p><span>Dan, </span><span>[c]</span><span> taatlah kepada Allah dan taatlah kepada Rasul ini dan berhati-hatilah. Dan, jika kamu berpaling, maka ketahuilah bahwa </span><span>[d]</span><span> kewajiban Rasul Kami hanya menyampaikan dengan jelas.</span></p><p><span>[c]</span><span> 3:133; 4:70; 64:13.</span></p><p><span>[d] </span><span>5:100; 16:83; 36:18; 64:13.</span></p>');
INSERT INTO `quran` VALUES (7114,2,5,94,'<p><span>Tiada dosa bagi orang-orang yang beriman dan beramal saleh dalam apa yang telah dimakan mereka </span><span>dahulu</span><span> asalkan mereka bertakwa dan beriman dan beramal saleh, kemudian mereka </span><span>semakin</span><span> bertakwa dan berbuat kebaikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=791">791</a><span>]</span><span> Dan, Allah mencintai orang-orang yang berbuat kebaikan.</span></p>');
INSERT INTO `quran` VALUES (7115,2,5,95,'<p><span>Hai orang-orang yang beriman, Allah pasti akan mencobai kamu dengan sesuatu berupa binatang-buruan yang dapat dicapai oleh tanganmu dan tombak-tombakmu supaya Allah </span><span>[a]</span><span> mengetahui siapa yang takut kepada-Nya dalam keadaan tersendiri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=792">792</a><span>]</span><span> Dan, barangsiapa melampaui batas sesudah itu maka baginya azab yang pedih.</span></p><p><span>[a]</span><span> 57:26</span></p>');
INSERT INTO `quran` VALUES (7116,2,5,96,'<p><span>Hai orang-orang beriman, </span><span>[a]</span><span> janganlah membunuh binatang buruan selagi kamu dalam keadaan ihram. Dan barangsiapa di antara kamu membunuhnya dengan sengaja maka gantinya ialah yang serupa dengan binatang yang dibunuhnya, yang diputuskan oleh dua laki-laki yang adil di antara mu, sebagai binatang korban yang harus disampaikan ke Ka''bah atau tebusan </span><span>dengan</span><span> memberi makan beberapa orang miskin atau berpuasa beberapa hari sebanding dengan itu, supaya ia merasakan akibat buruk perbuatannya. Allah memaafkan dari apa-apa </span><span>[b]</span><span> yang telah berlalu. Dan barangsiapa mengulangi lagi, maka Allah akan menghukumnya. Dan Allah Maha Perkasa. Pemberi hukuman.</span></p><p><span>[a]</span><span> 5:2, 97.</span></p><p><span>[b]</span><span> 2:276.</span></p>');
INSERT INTO `quran` VALUES (7117,2,5,97,'<p><span>Dihalalkan bagimu binatang-buruan laut </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=793">793</a><span>]</span><span> dan makanannya, sebagai perbekalan bagimu dan bagi orang-orang yang berpergian; dan, </span><span>[a]</span><span> diharamkan atasmu binatang buruan darat selama kamu dalam keadaan ihram. Dan bertakwalah kepada Allah Yang kepada-Nya kamu akan dihimpunkan.</span></p><p><span>[a]</span><span> 5:2, 96.</span></p>');
INSERT INTO `quran` VALUES (7118,2,5,98,'<p><span>Allah menjadikan Ka''bah </span><span>[a]</span><span> Rumah Suci itu sebagai sarana kemajuan yang kekal bagi manusia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=794">794</a><span>]</span><span> dan </span><span>juga</span><span> Bulan Suci dan </span><span>[b]</span><span> binatang-binatang korban dan binatang yang ditandai kalung; hal demikian itu supaya kamu mengetahui bahwa Allah mengetahui apa yang ada di seluruh langit dan apa yang ada di bumi, dan sesungguhnya Allah Maha Mengetahui segala sesuatu.</span></p><p><span>[a]</span><span> 2:126; 3:97, 98</span></p><p><span>[b] </span><span>5:3.</span></p>');
INSERT INTO `quran` VALUES (7119,2,5,99,'<p><span>[a]</span><span> Ketahuilah bahwa sesungguhnya Allah sangat keras dalam menghukum, dan sesunguhnya Allah Maha Pengampun Maha Penyayang.</span></p><p><span>[a]</span><span> 15:50, 51.</span></p>');
INSERT INTO `quran` VALUES (7120,2,5,100,'<p><span>Kewajiban Rasul tidak lain hanya </span><span>[a]</span><span> menyampaikan. Dan, Allah mengetahui apa-apa yang kamu </span><span>[b] </span><span>zahirkan dan apa-apa yang kamu sembunyikan.</span></p><p><span>[a]</span><span> 16:83; 36:18; 64:13.</span></p><p><span>[b]</span><span> 2:78; 6:4; 11:6; 16:20.</span></p>');
INSERT INTO `quran` VALUES (7121,2,5,101,'<p><span>Katakanlah, " Tidak sama </span><span>[a]</span><span> yang buruk yang yang baik walaupun kebanyakan yang buruk itu menyenangkan engkau." </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=795">795</a><span>]</span><span> Maka, bertakwalah kepada Allah , hai orang-orang yang berakal, supaya kamu berhasil.</span></p><p><span>[a] </span><span>2:268.</span></p>');
INSERT INTO `quran` VALUES (7122,2,5,102,'<p><span>Hai orang-orang yang beriman, </span><span>[a] </span><span>janganlah kamu menanyakan hal-hal yang apabila diterangakan kepadamu, hal itu akan menyusahkan kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=796">796</a><span>]</span><span> dan jika kamu menanyakan mengenai itu ketika Alquran sedang diturunkan niscaya akan diterangkan kepadamu. Allah menahan diri </span><span>untuk menerangkan</span><span> hal itu. Dan Allah Maha Pengampun.</span></p><p><span>[a] </span><span>2:109.</span></p>');
INSERT INTO `quran` VALUES (7123,2,5,103,'<p><span>Sesungguhnya satu kaum sebelummu telah </span><span>[a]</span><span> menanyakan perihal itu, kemudian mereka menjadi kufur karenanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=797">797</a><span>]</span></p><p><span>[a] </span><span>2:109</span></p>');
INSERT INTO `quran` VALUES (7124,2,5,104,'<p>Allah<span> </span><span> </span><span>[a]</span><span> tidak menetapkan aturan tentang suatu "Bahirah" </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=798">798</a><span>]</span><span> dan tidak </span><span>pula</span><span> "Saibah" </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=798a">798a</a><span>]</span><span> dan tidak </span><span>pula</span><span> "Washilah" </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=798b">798b</a><span>]</span><span> dan tidak </span><span>pula</span><span> "Ham", </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=798c">798c</a><span>]</span><span> akan tetapi orang-orang ingkar mengada-ada dusta terhadapa Allah , dan kebanyakan mereka tidak menggunakan akal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=798d">798d</a><span>]</span></p><p><span>[a] </span><span>6:137.</span></p>');
INSERT INTO `quran` VALUES (7125,2,5,105,'<p><span>Dan, apabila dikatakan kepada mereka, "Marilah </span><span>taat</span><span> kepada apa-apa yang diturunkan Allah dan kepada Rasul, " Berkata mereka, "Cukuplah bagi kami apa yang kami dapati pada bapak-bapak kami." Apakah meskipun bapak-bapak mereka tidak mengetahui sesuatu dan tidak pula mendapat petunjuk ?</span></p>');
INSERT INTO `quran` VALUES (7126,2,5,106,'<p><span>Hai orang-orang yang beriman, jagalah dirimu ! Tidak akan mendatangkan mudararat kepadamu orang yang </span><span>[a]</span><span> sesat bila kamu telah memperoleh petunjuk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=799">799</a><span>]</span><span> Kepada Allah kamu sekalian akan kembali; kemudian Dia akan memberitahukan kepadamu apa yang telah kamu kerjakan.</span></p><p><span>[a]</span><span> 2:138</span></p>');
INSERT INTO `quran` VALUES (7127,2,5,107,'<p><span>Hai orang-orang yang beriman, apabila maut hadir kepada seorang di antaramu. </span><span>cara</span><span> kesaksian di antaramu di waktu berwasiat, </span><span>ialah</span><span> , dua orang laki-laki yang adil dari antaramu, atau dua orang lain bukan dari antara kamu jika kamu sedang bepergian di muka bumi, lalu kamu ditimpa musibah maut. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=800">800</a><span>]</span><span> Tahanlah kedua saksi itu sesudah shalat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=801">801</a><span>]</span><span> </span><span>untuk memberikan</span><span> </span><span>kesaksian</span><span> ; dan jika kamu menaruh syak-wasangka </span><span>mengenai kesaksian mereka</span><span> maka hendaknya keduanya bersumpah dengan nama Allah bahwa, "Kami tidak akan mengambil keuntungan dengan </span><span>sumpah kami</span><span> ini, walaupun ia </span><span>yang untuknya kami beri persaksian itu,</span><span> dari kaum kerabat </span><span>kami</span><span> dan kami tidak </span><span>[a] </span><span>menyembunyikan kesaksian yang di perintahkan oleh Allah; sesungguhnya jika berbuat demikian, niscaya kami termasuk orang-orang yang berdosa."</span></p><p><span>[a]</span><span> 2:141. 284.</span></p>');
INSERT INTO `quran` VALUES (7128,2,5,108,'<p><span>Kemudian kalau sudah diketahui bahwa kedua orang itu telah nyata berdosa, maka dua orang lain </span><span>dari kerabat yang meninggal</span><span> berdiri</span><span> memberikan kesaksian</span><span> untuk menentang kedua orang terdahulu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=802">802</a><span>]</span><span> Maka keduanya bersumpah dengan nama Allah , "Sesungguhnya kesaksian kami lebih benar daripada kesaksian kedua orang itu, dan kami tidak berlaku curang. Sesungguhnya jika kami berbuat demikian, niscaya akan termasuk orang-orang yang aniaya."</span></p>');
INSERT INTO `quran` VALUES (7129,2,5,109,'<p><span>Hal demikian itu lebih dekat supaya mereka itu memberi kesaksian menurut kenyataan sebenarnya atau </span><span>supaya</span><span> mereka itu takut bahwa sesudah sumpah mereka akan diambil sumpah-sumpah lain. Dan, bertakwalah kepada Allah dan dengarkanlah. Dan, Allah tidak memberi petunjuk kepada kaum yang durhaka.</span></p>');
INSERT INTO `quran` VALUES (7130,2,5,110,'<p><span>Ingatlah </span><span> hari ketika Allah akan mengumpulkan para rasul lalu Dia berfirman, "Apakah </span><span>[a] </span><span>jawaban yang telah diberikan kepadamu ?" Mereka akan berkata, "Tak ada pengetahuan pada kami. Sesungguhnya hanya Engkau-lah Yang Maha Mengetahui segala yang gaib." </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=803">803</a><span>]</span></p><p><span>[a]</span><span> 7:7; 28:66.</span></p>');
INSERT INTO `quran` VALUES (7131,2,5,111,'<p><span>Ketika Allah berfirman, "Hai Isa ibnu Maryam, ingatlah nikmat-Ku kepada engkau dan kepada ibu engkau ketika </span><span>[b]</span><span> Aku memperkuat engkau dengan Ruhulkudus, </span><span>[c]</span><span> engkau bertutur kata kepada orang-orang </span><span>ketika engkau masih </span><span> kanak-kanak dan ketika usia lanjut; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=804">804</a><span>]</span><span> dan </span><span>ingatlah</span><span> ketika </span><span>[d] </span><span>Aku mengajari engkau Alkitab dan Hikmah dan Taurat dan Injil; dan ketika engkau </span><span>[e] </span><span>menciptakan </span><span>sesuatu</span><span> dari tanah seperti burung dengan izin-Ku; lalu engkau meniup kan </span><span>jiwa baru</span><span> ke dalamnya, maka jadilah ia </span><span>sesuatu</span><span> yang dapat terbang dengan izin-Ku; dan engkau menyembuhkan orang-orang buta dan yang berpenyakit kusta dengan perintah-Ku; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=805">805</a><span>]</span><span> dan ketika engkau membangkitkan yang telah mati </span><span>rohani</span><span> dengan izin-Ku. Dan tatkala </span><span>[a]</span><span> Aku menghalangi Bani Israil dari </span><span>membunuh</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=806">806</a><span>]</span><span> engkau ketika engkau datang kepada mereka dengan Tanda-tanda yang nyata, maka berkata orang-orang yang ingkar di antara mereka, "Ini tidak lain melainkan sihir yang nyata."</span></p><p><span>[a]</span><span> 5:12.</span></p><p><span>[b]</span><span> 2:88; 2:254.</span></p><p><span>[c]</span><span> 3:47.</span></p><p><span>[d]</span><span> 3:49.</span></p><p><span>[e]</span><span> 3:50</span></p>');
INSERT INTO `quran` VALUES (7132,2,5,112,'<p><span>Dan, </span><span>ingatlah</span><span> ketika </span><span>[b]</span><span> Aku mewahyukan kepada hawari, "Berimanlah kepada-Ku dan kepada rasul-Ku." Berkata mereka, "Kami telah beriman, dan saksikanlah bahwa kami orang-orang yang berserah diri."</span></p><p><span>[b]</span><span> 3:53, 54; 61:15.</span></p>');
INSERT INTO `quran` VALUES (7133,2,5,113,'<p><span>Ingarlah</span><span> ketika para hawari berkata, "Hai Isa ibnu Maryam adakah Tuhan engkau mampu menurunkan kepada kami hidangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=807">807</a><span>]</span><span> dari langit ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=808">808</a><span>]</span><span> " Berkata ia, "Bertakwalah kepada Allah jika kamu orang-orang yang beriman."</span></p>');
INSERT INTO `quran` VALUES (7134,2,5,114,'<p><span>Mereka berkata, "Kami ingin makah hidangan itu dan supaya hati kami tenteram dan </span><span>supaya</span><span> kami yakin bahwa engkau telah berkata benar kepada kami dan </span><span>supaya</span><span> kami dapat menjadi saksi terhadapnya."</span></p>');
INSERT INTO `quran` VALUES (7135,2,5,115,'<p><span>Berkata Isa ibnu Maryam, "Ya Allah, Tuhan kami, turunkanlah kapada kami hidangan dari langit supaya menjadi suatu hari raya bagi kami, bagi orang-orang yang awal dari kami dan yang datang di belakang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=808a">808a</a><span>]</span><span> kami, dan sebagai Tanda kebenaran dari Engkau, dan berilah kami rezeki dan Engkau-lah sebaik-baik Pemberi rezeki. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=809">809</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7136,2,5,116,'<p><span>Allah berfirman, "Sesungguhnya Aku akan menurunkannya kepadamu, maka barangsiapa di antaramu ingkar sesudah itu, niscaya Aku akan mengazabnya dengan azab yang tidak </span><span>pernah</span><span> Aku mengazab kepada seorang pun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=810">810</a><span>]</span><span> di seluruh alam."</span></p>');
INSERT INTO `quran` VALUES (7137,2,5,117,'<p><span>Dan, ketika Allah berfirman, "Hai Isa ibnu Maryam, adakah engkau berkata kepada manusia, "Jadikanlah aku dan ibuku sebagai dua tuhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=811">811</a><span>]</span><span> selain Allah ?" Ia menjawab, "Maha Suci Engkau. Tidak layak bagiku mengatakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=812">812</a><span>]</span><span> apa yang bukan hakku; sekiranya aku telah mengatakannya tentu Engkau mengetahuinya. Engkau mengetahui apa yang ada dalam diriku, dan aku tidak mengetahui apa yang ada dalam diri Engkau. </span><span>[a]</span><span> Sesungguhnya Engkau-lah Yang Maha Mengetahui segala yang gaib.</span></p><p><span>[a]</span><span> 5:110; 9:78; 34:49.</span></p>');
INSERT INTO `quran` VALUES (7138,2,5,118,'<p><span>Tidak pernah aku mengatakan kepada mereka selain apa yang telah Engkau perintahkan kepada ku, yaitu, </span><span>[a] </span><span>"Beribadahlah kepada Allah , Tuhan-ku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=813">813</a><span>]</span><span> dan Tuhan-mu." Dan aku menjadi saksi atas mereka selama aku berada di antara mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=814">814</a><span>]</span><span> akan tetapi, setelah </span><span>[b] </span><span>Engkau mewafatkan aku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=815">815</a><span>]</span><span> maka Engkau-lah Yang menjadi Pengawas atas mereka dan Engkau adalah Saksi atas segala sesuatu;</span></p><p><span>[a]</span><span> 5:73; 19:37.</span></p><p><span>[b]</span><span> 3:57.</span></p>');
INSERT INTO `quran` VALUES (7139,2,5,119,'<p><span>"Jika Engkau mengazab mereka, maka sesungguhnya mereka hamba-hamba Engkau; dan jika Engkau mengampuni mereka, maka sesungguhnya Engkau Maha Perkasa, Maha Bijaksana."</span></p>');
INSERT INTO `quran` VALUES (7140,2,5,120,'<p><span>Allah berfirman, "Inilah hari ketika </span><span>hanya</span><span> orang-orang benar yang akan beruntung karena kebenaran mereka. Bagi mereka ada kebun-kebun yang di bawahnya mengalir sungai-sungai; mereka akan tinggal kekal di dalamnya. Allah </span><span>[a]</span><span> ridha kepada mereka dan mereka pun ridha kepada-Nya. Itulah kemenangan yang besar."</span></p><p><span>[a]</span><span> 9:100; 58:23; 98:9.</span></p>');
INSERT INTO `quran` VALUES (7141,2,5,121,'<p><span>[a] </span><span>Kepunyaan Allah kerajaan seluruh langit dan bumi dan apa-apa yang ada di dalamnya. Dan Dia Maha Kuasa atas segala sesuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=816">816</a><span>]</span></p><p><span>[a]</span><span> 5:18, 41 ; 42:50 ; 48:15.</span></p>');
INSERT INTO `quran` VALUES (7142,2,6,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (7143,2,6,2,'<p><span>Segala puji bagi Allah Yang telah menciptakan seluruh langit dan bumi dan mewujudkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=817">817</a><span>]</span><span> kegelapan dan cahaya. Kemudian </span><span>[b]</span><span> orang-orang yang ingkar mempersekutukan Tuhan mereka.</span></p><p><span>[b]</span><span> </span><span>6:151 ; 27:61.</span></p>');
INSERT INTO `quran` VALUES (7144,2,6,3,'<p><span>Dia-lah </span><span>[c]</span><span> Yang telah menciptakan kamu dari tanah liat, kemudian Dia menetapkan suatu jangka waktu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=818">818</a><span>]</span><span> Dan ada </span><span>[d]</span><span> suatu jangka waktu lagi si sisi-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=819">819</a><span>]</span><span> namun demikian kamu ragu-ragu.</span></p><p><span>[c]</span><span> 15:27 ; 23:13 ; 32:8 ; 37:12 ; 38:72.</span></p><p><span>[d]</span><span> </span><span>71:5</span></p>');
INSERT INTO `quran` VALUES (7145,2,6,4,'<p><span>Dan </span><span>[a]</span><span> Dia-lah Allah, </span><span>Tuhan</span><span> di seluruh langit dan di bumi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=820">820</a><span>]</span><span> Dia mengetahui apa yang kamu sembunyikan dan apa yang kamu zahirkan. Dan Dia mengetahui apa-apa yang kamu usahakan.</span></p><p><span>[a]</span><span> 43:85.</span></p>');
INSERT INTO `quran` VALUES (7146,2,6,5,'<p><span>Dan </span><span>[b] </span><span>tidak datang kepada mereka suatu Tanda pun dari Tanda-tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=821">821</a><span>]</span><span> Tuhan mereka melainkan darinya mereka berpaling.</span></p><p><span>[b]</span><span> 21:3 ; 26:6 ; 36:47.</span></p>');
INSERT INTO `quran` VALUES (7147,2,6,6,'<p><span>Maka sesungguhnya </span><span>[c]</span><span> mereka telah mendustakan yang hak ketika datang kepada mereka maka, akan segera datang kepada mereka kabar-kabar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=822">822</a><span>]</span><span> tentang apa yang telah diperolok-olokan oleh mereka.</span></p><p><span>[c]</span><span> 26:7.</span></p>');
INSERT INTO `quran` VALUES (7148,2,6,7,'<p><span>Tidaklah mereka memaklumi berapa banyak generasi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=823">823</a><span>]</span><span> yang telah Kami binasakan sebelum mereka ? </span><span>[a]</span><span> Kami telah memberikan keteguhan kepada mereka di bumi yang belum pernah keteguhan itu Kami berikan kepadamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=824">824</a><span>]</span><span> dan </span><span>[b]</span><span> Kami kirimkan kepada mereka awan yang mencurahkan hujan lebat ; dan Kami jadikan sungai-sungai mengalir di bawah mereka ; lalu Kami binasakan mereka disebabkan dosa-dosa mereka dan Kami bangkitkan suatu generasi yang lain sesudah mereka.</span></p><p><span>[a]</span><span> 46:27.</span></p><p><span>[b]</span><span> 11:53 ; 71:12.</span></p>');
INSERT INTO `quran` VALUES (7149,2,6,8,'<p><span>Dan, andaikan Kami menurunkan kepada engkau suatu Kitab </span><span>yang ditulis</span><span> di atas kertas, lalu mereka merabanya dengan tangan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=825">825</a><span>]</span><span> niscaya orang-orang yang ingkar akan berkata, “ Ini tidak lain melainkan sihir yang nyata. “</span></p>');
INSERT INTO `quran` VALUES (7150,2,6,9,'<p><span>Dan, mereka berkata, </span><span>[a]</span><span> “Mengapa tidak titurunkan kepadanya seorang malaikat ? “ Dan, jika Kami menurunkan seorang malaikat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=826">826</a><span>]</span><span> tentu perkara menjadi selesai, kemudian mereka tidak akan diberi tenggang waktu.</span></p><p><span>[a]</span><span> 2:211 ; 25:8.</span></p>');
INSERT INTO `quran` VALUES (7151,2,6,10,'<p><span>Dan, jika Kami menjadikan seorang malikat </span><span>sebagai rasul</span><span> tentu Kami jadikan dia </span><span>seperti</span><span> seorang laki-laki ; dan niscaya akan Kami ragukan kepada mereka apa-apa yang mereka ragukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=827">827</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7152,2,6,11,'<p><span>Dan, sungguh rasul-rasul telah </span><span>[b]</span><span> diperolok-olokan sebelum engkau, maka orang-orang yang mencemoohkan di antara mereka, azab itulah yang telah mengepung mereka akibat dari apa yang mereka perolok-olokan.</span></p><p><span>[b]</span><span> 21:42.</span></p>');
INSERT INTO `quran` VALUES (7153,2,6,12,'<p><span>Katakanlah, </span><span>[c]</span><span> “ Bepergianlah di muka bumi dan perhatikanlah betapa akibat orang-orang yang mendustakan.” </span></p><p><span>[c]</span><span> 3:138 ; 22:47 ; 27:70.</span></p>');
INSERT INTO `quran` VALUES (7154,2,6,13,'<p><span>Katakanlah, “ Kepunyaan siapakah apa yang ada di seluruh langit dan bumi ? “ Katakanlah, “Kepunyaan Allah,” </span><span>[a]</span><span> Dia telah mewajibkan atas diri-Nya </span><span>memberi</span><span> rahmat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=828">828</a><span>]</span><span> Sudah pasti </span><span>[b]</span><span> Dia senantiasa akan mengumpulkan kamu hingga Hari Kiamat ; tiada keraguan di dalamnya. Orang-orang yang merugikan diri mereka sendiri, maka mereka tidak akan ber iman.</span></p><p><span>[a]</span><span> 6:55 ; 7:157.</span></p><p><span>[b]</span><span> 3:10 ; 10:4 : 88 ; 45:27.</span></p>');
INSERT INTO `quran` VALUES (7155,2,6,14,'<p><span>Dan, kepunyaan-Nya segala apa yang ada di dalam malam dan siang. Dan, Dia Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (7156,2,6,15,'<p><span>Katakanlah, “ Apakah akan aku jadikan yang lain sebagai pelindung selain Allah, </span><span>[c]</span><span> Yang menciptakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=829">829</a><span>]</span><span> seluruh langit dan bumi padahal Dia Yang </span><span>[d]</span><span> memberi makan dan Dia tidak diberi makan, “ Katakanlah, “ Sesungguhnya </span><span>[e]</span><span> aku diperintahkan supaya menjadi orang yang pertama menyerahkan diri.” Dan jangan sekali-kali engkau termasuk orang-orang musyrik.</span></p><p><span>[c]</span><span> 12:102 ; 14:11 ; 35:2 ; 39:47.</span></p><p><span>[d]</span><span> 20:133 ; 51:58, 59.</span></p><p><span>[e]</span><span> 6:164 ; 39:13.</span></p>');
INSERT INTO `quran` VALUES (7157,2,6,16,'<p><span>Katakanlah, “ Sesungguhnya </span><span>[a]</span><span> aku takut jika aku mendurhakai </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=830">830</a><span>]</span><span> Tuhan-ku pada azab Hari yang besar. “</span></p><p><span>[a]</span><span> 10:16 ; 39:14.</span></p>');
INSERT INTO `quran` VALUES (7158,2,6,17,'<p><span>Barangsiapa dihindarkan dari </span><span>azab</span><span> pada Hari itu maka sungguh Dia telah mengasihaninya. Dan itulah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=831">831</a><span>]</span><span> kejayaan yang nyata.</span></p>');
INSERT INTO `quran` VALUES (7159,2,6,18,'<p><span>Dan, </span><span>[b]</span><span> jika Allah menimpakan suatu kemudaratan kepada engkau, maka tak ada yang akan menjauhkan kemudaratan itu baginya selain Dia. Dan jika Dia mendatangkan suatu kebaikan kepada engkau, maka Dia Maha Kuasa atas segala sesuatu.</span></p><p><span>[b]</span><span> 10:108.</span></p>');
INSERT INTO `quran` VALUES (7160,2,6,19,'<p><span>Dan, </span><span>[c]</span><span> Dia Maha Unggul </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=832">832</a><span>]</span><span> di atas hamba-hamba-Nya ; dan Dia Maha Bijaksana, Maha Mengetahui.</span></p><p><span>[c]</span><span> 6:62.</span></p>');
INSERT INTO `quran` VALUES (7161,2,6,20,'<p><span>Katakanlah, “ Wujud manakah yang lebih benar dalam kesaksian ? “ Katakanlah, </span><span>[a]</span><span> “ Allah, Dia-lah Saksi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=833">833</a><span>]</span><span> di antara aku dan kamu. Dan diwahyukan kepadaku Alquran ini supaya dengannya aku memberi peringatan kepada kamu </span><span>tentang azab yang akan datang</span><span> dan kepada siapa-siapa </span><span>yang kepadanya Alquran</span><span> telah sampai. Apakah kamu sungguh-sungguh memberi kesaksian bahwa di samping Allah ada tuhan-tuhan lain ? “ Katakanlah, “ Aku tidak memberi kesaksian. “ Kemudian katakanlah, “ Sesungguhnya Dia Tuhan Yang Tunggal dan sesungguhnya aku terlepas diri dari segala apa yang kamu persekutukan.”</span></p><p><span>[a]</span><span> 4:167 ; 13:44 ; 29:53.</span></p>');
INSERT INTO `quran` VALUES (7162,2,6,21,'<p><span>[b]</span><span> Orang-orang yang telah Kami beri kepada mereka Kitab, mereka mengenalnya sebagaimana mereka mengenal </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=834">834</a><span>]</span><span> anak-anak mereka sendiri. Orang-orang yang telah merugikan diri mereka sendiri, mereka itu tidak akan beriman.</span></p><p><span>[b]</span><span> 2:147.</span></p>');
INSERT INTO `quran` VALUES (7163,2,6,22,'<p><span>Dan, </span><span>[a]</span><span> siapakah yang lebih aniaya daripada orang yang mengada-ada kedustaan terhadap Allah atau mendutakan Tanda-tanda-Nya ? Sesungguhnya orang-orang aniaya itu tidak akan berhasil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=835">835</a><span>]</span></p><p><span>[a]</span><span> 6:94 ; 7:38 ; 10:18 ; 11:19 ; 61:8.</span></p>');
INSERT INTO `quran` VALUES (7164,2,6,23,'<p><span>Dan, </span><span>[b]</span><span> ingatlah hari ketika Kami akan menghimpun mereka semuanya ; kemudian Kami akan berkata kepada orang-orang yang telah mempersekutukan </span>Allah<span> , “Di manakah sekutu-sekutu kamu yang kamu dahulu katakan ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=836">836</a><span>]</span></p><p><span>[b]</span><span> 10:39.</span></p>');
INSERT INTO `quran` VALUES (7165,2,6,24,'<p><span>Kemudian tiada lagi fitnah mereka kecuali mereka akan berkata, “ Demi Allah, Tuhan Kami, dahulu kami bukan orang-orang musyrik.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=837">837</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7166,2,6,25,'<p><span>Lihatlah bagaimana mereka akan berdusta terhadap diri mereka sendiri, dan akan lupa dari mereka </span><span>[a]</span><span> apa-apa yang dahulu mereka ada-adakan.</span></p><p><span>[a]</span><span> 7:54 ; 11:22.</span></p>');
INSERT INTO `quran` VALUES (7167,2,6,26,'<p><span>Dan, di antara mereka ada orang-orang yang </span><span>[b]</span><span> mendengarkan engkau ; padahal </span><span>[c]</span><span> Kami telah jadikan tutupan atas hati mereka supaya mereka tidak dapat memahaminya dan kepekaan di dalam telinga mereka. Dan meskipun mereka melihat segala Tanda, mereka tidak akan beriman kepadanya ; sehingga apabila mereka datang kepada engkau mereka berbantah dengan engkau, berkatalah orang-orang yang ingkar, “ Ini tiada lain melainkan hanya hikayat-hikayat orang-orang dahulu.”</span></p><p><span>[b]</span><span> 10:43 ; 17:48.</span></p><p><span>[c]</span><span> 17:47 ; 41:6.</span></p>');
INSERT INTO `quran` VALUES (7168,2,6,27,'<p><span>Dan, mereka melarang </span><span>untuk percaya</span><span> terhadapnya, dan mereka </span><span>sendiri</span><span> menjauhkan diri darinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=838">838</a><span>]</span><span> Dan, tiada lain yang mereka binasakan melainkan diri mereka sendiri, dan mereka tidak sadar.</span></p>');
INSERT INTO `quran` VALUES (7169,2,6,28,'<p><span>Dan, andaikan engkau melihat </span><span>[a]</span><span> ketika mereka disuruh berdiri di hadapan Api, </span><span>[b]</span><span> maka mereka akan berkata, “ Sekiranya jika kami dikembalikan, dan niscaya kami tidak akan mendustakan Tanda-tanda Tuhan kami, dan kami akan termasuk orang-orang mukmin.</span></p><p><span>[a]</span><span> 46:35.</span></p><p><span>[b]</span><span> 2:168 ; 23:100, 101 : 26:103 ; 39:59.</span></p>');
INSERT INTO `quran` VALUES (7170,2,6,29,'<p><span>Bahkan sebenarnya telah nyata bagi mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=839">839</a><span>]</span><span> apa yang biasa dahulu mereka sembunyikan. Dan, jika mereka dikembalikan lagi, niscaya mereka akan kembali kepada apa yang telah dilarang darinya, dan sesungguhnya mereka itu pendusta-pendusta.</span></p>');
INSERT INTO `quran` VALUES (7171,2,6,30,'<p><span>Dan mereka berkata, </span><span>[c]</span><span> “ Tiada yang lain selain kehidupan kita di dunia dan kami tidak akan dibangkitkan.”</span></p><p><span>[c]</span><span> 23:38 ; 44:36; 45:25.</span></p>');
INSERT INTO `quran` VALUES (7172,2,6,31,'<p><span>Dan, andaikan engkau melihat ketika mereka disuruh berdiri dihadapan Tuhan mereka. </span><span>[a]</span><span> Dia akan berfirman, “ Bukankah </span><span>kehidupan di akhirat</span><span> ini benar ? “ Mereka akan berkata, “ Ya, benar, demi Tuhan kami. “ Berfirman Dia, “ Maka rasakanlah azab ini disebabkan kamu dahulu mengingkari.”</span></p><p><span>[a]</span><span> 46:35.</span></p>');
INSERT INTO `quran` VALUES (7173,2,6,32,'<p><span>Sungguh rugilah </span><span>[b]</span><span> orang-orang yang mendustakan pertemuan dengan Allah sehingga apabila saat itu menjelang mereka dengan tiba-tiba, mereka akan berkata, </span><span>[c]</span><span> “ Alangkah menyesalnya kami atas apa yang telah kami abaikan mengenai </span><span>Saat</span><span> ini. “ Dan, mereka akan memikul beban mereka di atas punggung mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=840">840</a><span>]</span><span> Sungguh buruk apa yang di pikul mereka.</span></p><p><span>[b]</span><span> 10:46.</span></p><p><span>[c]</span><span> 2:168.</span></p>');
INSERT INTO `quran` VALUES (7174,2,6,33,'<p><span>Dan, </span><span>[d]</span><span> kehidupan dunia tidak lain melainkan permainan dan hiburan. Dan, sesungguhnya </span><span>[e]</span><span> rumah akhirat itu lebih baik bagi orang-orang yang bertakwa. Apakah kamu tidak menggunakan akal ? </span></p><p><span>[d]</span><span> 29:65 ; 47:37 ; 57:21.</span></p><p><span>[e]</span><span> 7:170 ; 12:110.</span></p>');
INSERT INTO `quran` VALUES (7175,2,6,34,'<p><span>Sesungguhnya Kami mengetahui bahwa </span><span>[a]</span><span> yang dikatakan mereka itu tentu menyedihkan engkau ; karena sesungguhnya mereka bukan mendustakan engkau melainkan orang-orang zalim itu dengan Tanda-tanda Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=841">841</a><span>]</span><span> mereka menolak.</span></p><p><span>[a]</span><span> 15:98 ; 16:104.</span></p>');
INSERT INTO `quran` VALUES (7176,2,6,35,'<p><span>Dan, sesungguhnya telah didustakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=842">842</a><span>]</span><span> rasul-rasul sebelum engkau, maka mereka terus bersabar meskipun mereka di dustakan dan disakiti, hingga datang kepada mereka </span><span>[b]</span><span> pertolongan Kami. </span><span>[c]</span><span> Dan, tiada yang dapat mengubah Kalimat-kalimat Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=843">843</a><span>]</span><span> Dan, sesungguhnya telah datang kepada engkau sebagian dari kabar-kabar tentang rasul-rasul.</span></p><p><span>[b]</span><span> 2:215 ; 40:52.</span></p><p><span>[c]</span><span> 6:116.</span></p>');
INSERT INTO `quran` VALUES (7177,2,6,36,'<p><span>Dan, jika berpalingnya mereka terasa berat bagi engkau maka kalau engkau sanggup mencari lubang ke dalam bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=844">844</a><span>]</span><span> atau tangga ke langit, lalu engkau mendatangkan kepada mereka suatu Tanda. Dan, </span><span>[a]</span><span> jika Allah menghendaki niscaya mereka akan dihimpun-Nya kepada petunjuk. Maka janganlah sekali-kali engkau menjadi orang-orang yang jahil.</span></p><p><span>[a]</span><span> 5:49 ; 6:150 ; 11:119 ; 13:32 ; 16:10.</span></p>');
INSERT INTO `quran` VALUES (7178,2,6,37,'<p><span>Sesungguhnya orang-orang yang menerima </span><span>kebenaran</span><span> hanyalah orang-orang yang mendengar. Dan orang-orang mati, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=844a">844a</a><span>]</span><span> Allah akan membangkitkan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=845">845</a><span>]</span><span> kemudian kepada-Nya mereka akan dikembalikan.</span></p>');
INSERT INTO `quran` VALUES (7179,2,6,38,'<p><span>Dan, mereka berkata, </span><span>[a]</span><span> “ Mengapa tidak diturunkan kepadanya suatu Tanda dari Tuhan nya ?” Katakanlah, “ Sesungguhnya Allah berkuasa menurunkan Tanda, akan tetapi kebanyakan mereka tidak mengetahui.”</span></p><p><span>[a]</span><span> 10:21 ; 29:51.</span></p>');
INSERT INTO `quran` VALUES (7180,2,6,39,'<p><span>Dan, </span><span>[b]</span><span> tiada binatang yang berjalan di atas bumi dan tidak pula burung yang terbang dengan kedua sayapnya, melainkan mereka adalah umat-umat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=846">846</a><span>]</span><span> seperti kamu. Tiada sesuatu yang Kami </span><span>[c]</span><span> kurangi dalam Kitab ini. Kemudian mereka akan dihimpun kepada Tuhan mereka.</span></p><p><span>[b]</span><span> 11:7, 57.</span></p><p><span>[c]</span><span> 16:90.</span></p>');
INSERT INTO `quran` VALUES (7181,2,6,40,'<p><span>Dan, </span><span>[a]</span><span> orang-orang yang mendustakan Tanda-tanda Kami adalah tuli dan bisu, berada dalam gelap-gulita. Barangsiapa Allah menghendaki, akan disesatkan-Nya, dan barang siapa yang Dia menghendaki, Dia akan menempatkan pada jalan lurus.</span></p><p><span>[a]</span><span> 2:19, 172 ; 27:81, 82 ; 30:53, 54.</span></p>');
INSERT INTO `quran` VALUES (7182,2,6,41,'<p><span>Katakanlah, </span><span>[b]</span><span> “ Bagaimana pendapatmu jika azab Allah datang kepadamu atau jika Saat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=847">847</a><span>]</span><span> itu datang kepadamu ; apakah kamu akan menyeru </span><span>yang lain</span><span> selain Allah , jika kamu orang yang benar ? “</span></p><p><span>[b]</span><span> 6:48 ; 12:108 ; 43:67.</span></p>');
INSERT INTO `quran` VALUES (7183,2,6,42,'<p><span>Tidak, </span><span>[c]</span><span> bahkan Dia yang kamu akan seru,maka Dia akan menghilangkan </span><span>bahaya</span><span> apa yang kamu seru kepada-Nya jika Dia menghendaki ; dan kamu akan melupakan apa yang kamu sekutukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=848">848</a><span>]</span></p><p><span>[c]</span><span> 10:23, 24.</span></p>');
INSERT INTO `quran` VALUES (7184,2,6,43,'<p><span>Dan sesungguhnya Kami telah mengutus </span><span>rasul-rasul</span><span> kepada umat-umat sebelum engkau; dan kemudian </span><span>[a]</span><span> Kami menghukum mereka dengan kemiskinan dan kesusahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=849">849</a><span>]</span><span> supaya mereka merendahkan diri.</span></p><p><span>[a]</span><span> 7:95.</span></p>');
INSERT INTO `quran` VALUES (7185,2,6,44,'<p><span>Kemudian, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=850">850</a><span>]</span><span> mengapa mereka tidak merendahkan diri ketika datang kepada mereka hukuman Kami ; bahkan </span><span>[b]</span><span> hati mereka </span><span>semakin keras</span><span> dan </span><span>[c]</span><span> syaitan menampakkan indah kepada mereka apa yang dikerjakan mereka.</span></p><p><span>[b]</span><span> 2:75 ; 57:17.</span></p><p><span>[c]</span><span> 6:123 ; 8:49 ; 16:64 ; 29:39.</span></p>');
INSERT INTO `quran` VALUES (7186,2,6,45,'<p><span>Kemudian, ketika </span><span>[d]</span><span> mereka melupakan dengan apa yang telah diperingatkan kepada mereka, Kami membukakan untuk mereka pintu-pintu segala sesuatu, sehingga bila mereka bergembira dengan apa yang diberikan kepada mereka, </span><span>[e]</span><span> Kami menghukum mereka dengan tiba-tiba, maka seketika itu juga mereka berputus-asa.</span></p><p><span>[d]</span><span> 5:14 ; 7:116.</span></p><p><span>[e]</span><span> 7:96 ; 39:56.</span></p>');
INSERT INTO `quran` VALUES (7187,2,6,46,'<p><span>[a]</span><span> Maka dipotonglah sampai ke akar-akar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=851">851</a><span>]</span><span> dari kaum yang aniaya itu dan segala puji bagi Allah , Tuhan semesta alam.</span></p><p><span>[a]</span><span> 7:73 ; 15:67.</span></p>');
INSERT INTO `quran` VALUES (7188,2,6,47,'<p><span>Katakanlah, “ Bagaimana pendapatmu </span><span>[b]</span><span> jika Allah menghilangkan pendengaranmu dan penglihatanmu dan mencap hatimu, siapakah tuhan selain Allah yang akan mengembalikannya kepadamu ? “ Lihatlah bagaimana Kami berulang-ulang membentangkan Tanda-tanda, kemudian mereka tetap memalingkan muka.</span></p><p><span>[b]</span><span> 2:8 ; 16:109 ; 45:24.</span></p>');
INSERT INTO `quran` VALUES (7189,2,6,48,'<p><span>Katakanlah, “ Bagaimana pendapatmu </span><span>[c]</span><span> jika azab Allah datang kepadamu dengan tiba-tiba atau terang-terangan, maka adakah lainnya yang akan dibinasakan kecuali kaum yang aniaya ? “</span></p><p><span>[c]</span><span> 6:41 ; 10:51 ; 12:108 ; 43:67.</span></p>');
INSERT INTO `quran` VALUES (7190,2,6,49,'<p><span>Dan, tidaklah </span><span>[a]</span><span> Kami mengutus rasul-rasul kecuali sebagai pembawa kabar suka dan pemberi peringatan. Lalu </span><span>[b]</span><span> barangsiapa beriman dan memperbaiki diri, maka tak ada ketakutan menimpa diri mereka dan tidak pula mereka akan bersedih.</span></p><p><span>[a]</span><span> 4:166 ; 5:20 ;18:57.</span></p><p><span>[b]</span><span> 5:70 ; 7:36.</span></p>');
INSERT INTO `quran` VALUES (7191,2,6,50,'<p><span>Dan, </span><span>[c]</span><span> orang-orang yang mendustakan Ayat-ayat Kami, azab akan menyentuh mereka karena mereka durhaka.</span></p><p><span>[c]</span><span> 3:12 ; 5:11 ; 7:37 ; 10:74 ; 22:58.</span></p>');
INSERT INTO `quran` VALUES (7192,2,6,51,'<p><span>Katakanlah, </span><span>[d]</span><span> “Aku tidak berkata kepadamu bahwa padaku ada khazanah-khazanah Allah , dan aku tidak mengetahui ilmu ghaib, dan aku tidak mengatakan kepadamu, bahwa aku malaikat, </span><span>[e]</span><span> aku mengikuti hanya apa yang diwahyukan kepadaku.” Katakanlah, “ Samakah orang yang buta dengan orang yang melihat ? “ Tidaklah kamu berpikir ?</span></p><p><span>[d]</span><span> 11:32.</span></p><p><span>[e</span><span>] 10:16 ; 46:10.</span></p>');
INSERT INTO `quran` VALUES (7193,2,6,52,'<p><span>Dan, berilah peringatan dengannya kepada orang-orang yang takut bahwa mereka akan dihimpun kepada Tuhan mereka, tak ada bagi mereka seorang penolong selain Dia dan tidak pula seorang pemberi syafaat, mudah-mudahan mereka bertakwa.</span></p>');
INSERT INTO `quran` VALUES (7194,2,6,53,'<p><span>Dan, </span><span>[a]</span><span> janganlah engkau mengusir orang-orang yang selalu menyeru Tuhan mereka di pagi hari dan di petang hari </span><span>[b]</span><span> untuk menginginkan perhatian-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=852">852</a><span>]</span><span> Engkau sedikit pun tidak bertanggungjawab mengenai mereka, dan mereka sedikit pun tidak bertanggung jawab mengenai engkau. Maka, </span><span>jika</span><span> engkau mengusir mereka tentu engkau termasuk orang-orang aniaya.</span></p><p><span>[a]</span><span> 11:30.</span></p><p><span>[b]</span><span> 18:29.</span></p>');
INSERT INTO `quran` VALUES (7195,2,6,54,'<p><span>Dan, dengan cara demikian Kami telah menguji sebagian mereka dengan sebagian yang lain sehingga mereka berkata, </span><span>[c]</span><span> “Orang-orang inikah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=853">853</a><span>]</span><span> yang Allah telah memberi karunia atas mereka di antara kita ? “ Bukankah Allah lebih mengetahui orang-orang yang bersyukur ?</span></p><p><span>[c]</span><span> 11:28.</span></p>');
INSERT INTO `quran` VALUES (7196,2,6,55,'<p><span>Dan, apabila datang kepada engkau orang-orang yang beriman kepada Tanda-tanda Kami maka ketakanlah, “ Selamat sejahtera atasmu ! </span><span>[d]</span><span> Tuhan-mu telah menetapkan atas diri-Nya </span><span>memberi</span><span> rahmat sehingga </span><span>[a]</span><span> barangsiapa di antaramu berbuat keburukan karena kejahilan, lalu ia bertobat sesudah itu dan memperbaiki diri, maka sesungguhnya Dia Maha Pengampun, Maha Penyayang.”</span></p><p><span>[d]</span><span> 6:13 ; 7:157.</span></p><p><span>[a]</span><span> 4:18 ; 16:120.</span></p>');
INSERT INTO `quran` VALUES (7197,2,6,56,'<p><span>Dan, demikianlah Kami membentangkan Tanda-tanda, dan supaya jalan orang-orang yang bersalah menjadi jelas.</span></p>');
INSERT INTO `quran` VALUES (7198,2,6,57,'<p><span>R. 7</span></p><p><span>Katakanlah, “ Sesungguhnya aku dilarang menyembah wujud-wujud yang kamu seru selain Allah “ Katakanlah, </span><span>[b]</span><span> “ Aku tidak akan mengikuti hawa nafsumu. Jika demikian, niscaya aku sesat dan aku bukan termasuk orang-orang yang mendapat petunjuk.”</span></p><p><span>[b]</span><span> 5:50 ; 42:16.</span></p>');
INSERT INTO `quran` VALUES (7199,2,6,58,'<p><span>Katakanlah, </span><span>[c]</span><span> “ Sesungguhnya aku </span><span>berdiri</span><span> di atas dalil yang terang dari Tuhan-ku, dan kamu mendustakannya. Tiada padaku </span><span>kekuasaan mengenai</span><span> apa yang ingin kamu segerakan. </span><span>[d]</span><span> Tiada keputusan kecuali pada Allah Dia menerangkan kebenaran dan Dia sebaik-baik Pemberi keputusan.”</span></p><p><span>[c]</span><span> 11:64 ; 12:109.</span></p><p><span>[d]</span><span> 12:41, 68.</span></p>');
INSERT INTO `quran` VALUES (7200,2,6,59,'<p><span>Katakanlah, </span><span>[a]</span><span> “ Sekiranya ada padaku kekuasaan mengenai apa yang ingin kamu segerakan itu, niscaya telah diputuskan perkara di antara aku dengan kamu. Dan Allah lebih mengetahui tentang orang-orang aniaya.”</span></p><p><span>[a]</span><span> 6:9 ; 10:12.</span></p>');
INSERT INTO `quran` VALUES (7201,2,6,60,'<p><span>Dan, pada sisi-Nya kunci-kunci segala yang gaib ; tiada yang mengetahuinya kecuali Dia. Dan, Dia mengetahui apa yang ada di daratan dan di lautan. Dan tidaklah gugur sehelai daun pun melainkan Dia mengetahuinya ; dan tiada sebutir biji pun dalam kegelapan bumi dan tidak pula sesuatu yang basah atau yang kering melainkan </span><span>tertulis</span><span> dalam Kitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=854">854</a><span>]</span><span> yang terang.</span></p>');
INSERT INTO `quran` VALUES (7202,2,6,61,'<p><span>Dan, </span><span>[a]</span><span> Dia-lah Yang mewafatkan kamu di waktu malam dan Dia mengetahui apa yang kamu kerjakan di waktu siang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=865">865</a><span>]</span><span> kemudian Dia membangunkan kamu di dalamnya, supaya jangka waktu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=856">856</a><span>]</span><span> yang telah ditetapkan dapat digenapi. Kemudian kepada-Nya kamu akan kembali. Kemudian Dia akan memberitahukan kepadamu segala sesuatu yang dahulu kamu kerjakan.</span></p><p><span>[a]</span><span> 39:43.</span></p>');
INSERT INTO `quran` VALUES (7203,2,6,62,'<p><span>Dan, </span><span>[a]</span><span> Dia Maha Unggul </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=857">857</a><span>]</span><span> di atas hamba-hamba-Nya dan </span><span>[b]</span><span> Dia mengutus penjaga-pernjaga bagimu hingga apabila maut menjelang salah seorang di antaramu, utusan-utusan Kamu, mewafatkannya. Dan mereka tidak melalaikannya.</span></p><p><span>[a]</span><span> 6:19 ;13:17.</span></p><p><span>[b]</span><span> 13:12 ; 82:11.</span></p>');
INSERT INTO `quran` VALUES (7204,2,6,63,'<p><span>Kemudian, mereka dikembalikan kepada Allah , Pelindung mereka yang hak. Ketahuilah segala keputusan kepunyaan-Nya. Dan Dia Penghisab Yang secepat- cepatnya.</span></p>');
INSERT INTO `quran` VALUES (7205,2,6,64,'<p><span>Katakanlah, </span><span>[c]</span><span> “ Siapakah yang menyelamatkan kamu dari bencana-bencana </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=858">858</a><span>]</span><span> di darat dan di laut, </span><span>ketika</span><span> kamu berseru kepada-Nya dengan merendahkan diri dan diam-diam </span><span>mengatakan</span><span> `Jika Dia menyelamatkan kami dari </span><span>bencana</span><span> ini niscaya kamu akan menjadi orang-orang yang bersyukur.”</span></p><p><span>[c]</span><span> 10:23 ; 17:68 ; 29:66 ; 31:33.</span></p>');
INSERT INTO `quran` VALUES (7206,2,6,65,'<p><span>Katakanlah, “Allah Yang menyelamatkan kamu dari </span><span>bencana</span><span> itu dan dari segala kemalangan, kemudian kamu masih juga mempersekutukan. “</span></p>');
INSERT INTO `quran` VALUES (7207,2,6,66,'<p><span>Katakanlah, “ Dia-lah Yang berkuasa mengirimkan azab kepadamu dari atasmu atau dari bawah kakimu atau mencampur-baurkan kamu menjadi golongan-golongan </span><span>saling berselisih</span><span> dan membuat sebagian kamu merasakan kesusahan sebagian yang lain. “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=859">859</a><span>]</span><span> Lihatlah bagaimana Kami membentangkan Tanda-tanda supaya mereka mengerti.</span></p>');
INSERT INTO `quran` VALUES (7208,2,6,67,'<p><span>Dan, </span><span>[a]</span><span> kaum engkau telah mendustakannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=860">860</a><span>]</span><span> padahal itu adalah hak. Katakanlah, </span><span>[b]</span><span> “ Aku bukan orang yang bertanggungjawab mengurusmu.”</span></p><p><span>[a]</span><span> 6:6.</span></p><p><span>[b]</span><span> 39:42 ; 42:7.</span></p>');
INSERT INTO `quran` VALUES (7209,2,6,68,'<p><span>Bagi tiap kabar gaib ada masa yang tertentu ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=861">861</a><span>]</span><span> dan kamu segera akan mengetahui.</span></p>');
INSERT INTO `quran` VALUES (7210,2,6,69,'<p><span>Dan, </span><span>[a]</span><span> apabila engkau melihat orang-orang yang sedang mempercakapkan </span><span>hal-hal yang dusta</span><span> tentang Tanda-tanda Kami, maka menghindarlah dari mereka hingga mereka mempercakapkan percakapan lain. dan jika syaitan menyebabkan engkau lupa, maka setelah ingat janganlah engkau duduk bersama kaum aniaya.</span></p><p><span>[a]</span><span> 4:141.</span></p>');
INSERT INTO `quran` VALUES (7211,2,6,70,'<p><span>Dan tidaklah </span><span>[b]</span><span> orang-orang yang bertakwa sedikit pun bertanggungjawab mengenai mereka itu, akan tetapi </span><span>kewajibannya hanyalah</span><span> memberi nasihat supaya mereka bertakwa.</span></p><p><span>[b]</span><span> 6:53.</span></p>');
INSERT INTO `quran` VALUES (7212,2,6,71,'<p><span>Dan, tinggalkanlah </span><span>[c]</span><span> mereka yang menjadikan agama mereka permainan dan hiburan dan mereka telah terperdaya oleh kehidupan dunia. Dan nasihatilah </span><span>mereka</span><span> dengannya supaya jangan sampai suatu jiwa dijerumskan oleh apa yang telah diperbuatnya. Tiada baginya seorang pelindung selain Allah , dan tidak pula seorang pemberi syafaat ; dan jika ia hendak menebus dengan segala macam tebusan, tidak akan diterima darinya. Inilah orang-orang akan dijerumuskan karena perbuatan-perbuatan mereka. </span><span>[a]</span><span> Mereka akan mendapati minuman air panas mendidih dan azab yang pedih, karena mereka dahulu mengingkari.</span></p><p><span>[c]</span><span> 5:58; 7:52 ; 8:36.</span></p><p><span>[a]</span><span> 10:5.</span></p>');
INSERT INTO `quran` VALUES (7213,2,6,72,'<p><span>R. 9</span></p><p><span>Katankanlah, </span><span>[b]</span><span> “Apakah kita akan menyeru selian Allah yang tidak memberi manfaat kepada kita dan tidak pula mendatangkan mudarat kepada kita, dan adakah kita akan dikembalikan atas tumit kita, setelah Allah memberi petunjuk kepada kita ? Seperti seorang yang telah diperdayakan oleh syaitan di muka bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=862">862</a><span>]</span><span> </span><span>dan meniggalkannya</span><span> dalam kebingungan, </span><span>dan</span><span> mempunyai kawan-kawan yang memanggilnya kepada petunjuk, </span><span>mengatakan,</span><span> “ Datanglah kepada kami ! “ Katakanlah, “ Sesungguhnya petunjuk Allah itulah petunjuk </span><span>yang benar</span><span> dan kami diperintahkan supaya tunduk kepada Tuhan semesta alam.”</span></p><p><span>[b]</span><span> 21:67 ; 22:74.</span></p>');
INSERT INTO `quran` VALUES (7214,2,6,73,'<p><span>“ Dan, bahwa </span><span>kami diperintahkan,</span><span> </span><span>[a]</span><span> </span><span>‘</span><span> Tetap mendirikan shalat dan bertakwa kepada-Nya ,</span><span>‘</span><span> dan Dia-lah Yang kepada-Nya kamu sekalian akan dihimpun.”</span></p><p><span>[a]</span><span> 4:78 ; 22:79 ; 24:57.</span></p>');
INSERT INTO `quran` VALUES (7215,2,6,74,'<p><span>“ Dan, </span><span>[b]</span><span> Dia-lah Yang telah menciptakan seluruh langit dan bumi dengan hak, dan pada hari itu Dia menitahkan, `jadilah,` maka jadila ia. Firman-nya itu benar. Dan, kepunyaan-Nya kerajaan pada hari ketika </span><span>[c]</span><span> nafiri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=863">863</a><span>]</span><span> ditiup. </span><span>Dia</span><span> </span><span>[d]</span><span> Mengetahui yang gaib dan yang nyata, dan Dia Maha Bijaksana, Maha Mengetahui.</span></p><p><span>[b]</span><span> 14:20 ; 16:4 ; 29:45.</span></p><p><span>[c]</span><span> 27:88 ; 39:69.</span></p><p><span>[d]</span><span> 9:94 ; 13:10 ; 23:93 ; 39:47 ; 59:23.</span></p>');
INSERT INTO `quran` VALUES (7216,2,6,75,'<p><span>Dan </span><span>ingatlah</span><span> </span><span>[a]</span><span> ketika Ibrahim berkata kepada bapaknya, Azar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=864">864</a><span>]</span><span> “ Adakah engkau mengambil berhala-berhala itu sebagaia Tuhan ? Sesungguhnya, aku melihat engkau dan kaum engkau dalam kesesatan yang nyata.”</span></p><p><span>[a]</span><span> 19:43.</span></p>');
INSERT INTO `quran` VALUES (7217,2,6,76,'<p><span>Dan, demikianlah Kami memperlihatkan kepada Ibrahim kerajaan seluruh langit dan bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=865">865</a><span>]</span><span> dan supaya ia termasuk orang-orang yang berkeyakinan.</span></p>');
INSERT INTO `quran` VALUES (7218,2,6,77,'<p><span>Maka, ketika kegelapan malam menyelimutunya ia melihat sebuah bintang. Ia berkata, “Inikah Tuhan-ku ?” Kemudian ketika bintang itu terbenam ia berkata “ Aku tidak suka kepada yang terbenam. “</span></p>');
INSERT INTO `quran` VALUES (7219,2,6,78,'<p><span>Kemudian tatkala ia melihat bulan terbit dengan memancarkan cahaya ia berkata, “Inikah Tuhan-ku ? “ Tetapi tatkala terbenam ia berkata, “ Seandainya Tuhan-ku tidak memberi petunjuk kepadaku niscaya aku akan menjadi di antara kaum yang sesat.”</span></p>');
INSERT INTO `quran` VALUES (7220,2,6,79,'<p><span>Maka, tatkala ia melihat matahari bersinar ia berkata, “Inikah Tuhan-ku ? Ini paling besar ! “ Tetapi, ketika terbenam ia berkata, “ Hai kaumku, sesungguhnya aku terlepas dari apa-apa yang kamu persekutukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=866">866</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (7221,2,6,80,'<p><span>“Sesunguhnya </span><span>[a]</span><span> aku menghadapkan perhatianku kepada Dzat Yang menciptakan seluruh langit dan bumi dengan hati condong </span><span>kepada Allah</span><span> dan aku bukanlah dari antara orang-orang musyrik.”</span></p><p><span>[a]</span><span> 3:21.</span></p>');
INSERT INTO `quran` VALUES (7222,2,6,81,'<p><span>Dan, kaumnya berbantah dengannya. Ia berkata, “ Apakah kamu berbantah dengan aku mengenai Allah padahal Dia telah memberi perunjuk kepadaku ? Dan aku tidak takut terhadap apa yang kamu persekutukan dengan-Nya, kecuali jika Tuhan-ku menghendaki sesuatu. </span><span>[b]</span><span> Tuhan-ku meliputi segala sesuatu ilmu. Tidakkah kamu hendak mengambil nasihat ?</span></p><p><span>[b]</span><span> 7:90.</span></p>');
INSERT INTO `quran` VALUES (7223,2,6,82,'<p><span>“Dan, mengapakah harus aku takut kepada apa yang kamu persekutukan bila kamu tidak takut </span><span>[c]</span><span> mempersekutukan sesuatu dengan Allah yang Dia tidak menurunkan kepada mu dalil ? “ Maka, manakah di antara dua pihak itu yang lebih berhak atas keamanan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=867">867</a><span>]</span><span> jika memang kamu mengetahui ?</span></p><p><span>[c]</span><span> 7:34 ; 22:72.</span></p>');
INSERT INTO `quran` VALUES (7224,2,6,83,'<p><span>Orang-orang yang beriman dan tidak </span><span>[a]</span><span> mencampurbaurkan keimanan mereka dengan kezaliman, merekalah yang akan memperoleh keamanan dan merekalah yang mendapat petunjuk.</span></p><p><span>[a]</span><span> 31:14.</span></p>');
INSERT INTO `quran` VALUES (7225,2,6,84,'<p><span>R.10</span></p><p><span>Dan, itulah hujjah Kami yang Kami memberikannya kepada Ibrahim terhadap kaumnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=868">868</a><span>]</span><span> </span><span>[b]</span><span> Kami meninggikan derajat orang yang Kami kehendaki. Sesungguhnya Tuhan engkau Maha Bijaksana, Maha Mengetahui.</span></p><p><span>[b]</span><span> 12:77</span></p>');
INSERT INTO `quran` VALUES (7226,2,6,85,'<p><span>Dan, </span><span>[a]</span><span> Kami menganugerahkan kepadanya, Ishak dan Ya</span><span>’</span><span>kub ; masing-masing Kami beri petunjuk ; dan Nuh Kami beri petunjuk sebelumnya, dan dari keturunannya, Daud dan Sulaiman dan Ayyub </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=869">869</a><span>]</span><span> dan Yusuf dan Musa dan Harun. Dan, demikianlah Kami memberi ganjaran kepada mereka yang berbuat kebajikan.</span></p><p><span>[a]</span><span> 29:28.</span></p>');
INSERT INTO `quran` VALUES (7227,2,6,86,'<p><span>Dan, kepada Zakaria dan Yahya dan Isa dan Ilyas ; semuanya mereka dari orang-orang shaleh.</span></p>');
INSERT INTO `quran` VALUES (7228,2,6,87,'<p><span>Dan, Ismail dan Al-Yasa</span><span>’</span><span> dan Yunus dan Luth, dan </span><span>[b]</span><span> masing-masing Kamu lebihkan derajatnya di atas seluruh alam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=870">870</a><span>]</span></p><p><span>[b]</span><span> 2:49 ; 3:34, 35 ; 45:17</span></p>');
INSERT INTO `quran` VALUES (7229,2,6,88,'<p><span>Dan, dari bapak-bapak mereka dan keturunan mereka dan saudara-saudara mereka ; dan Kami telah memilih mereka serta menunjuki mereka kepada jalan yang lurus.</span></p>');
INSERT INTO `quran` VALUES (7230,2,6,89,'<p><span>Itulah petunjuk Allah Dengan itu Dia memberi petunjuk kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya. Dan, </span><span>[a]</span><span> jika mereka berbuat syirik, pasti lenyap dari mereka apa yang telah mereka kerjakan.</span></p><p><span>[a]</span><span> 2:49 ; 3:34, 35 ; 45:17.</span></p>');
INSERT INTO `quran` VALUES (7231,2,6,90,'<p><span>Mereka itulah </span><span>[a]</span><span> orang-orang yang telah Kami anugerahkan kepada mereka Kitab </span><span>[ </span><a href="web+quran://quran.ibrohim.me/tafsir?id=871">871</a><span>]</span><span> dan kekuasaan, dan kenabian. Tetapi, jika mereka mengingkarinya maka sesungguhnya Kami telah menyerahkannya kepada satu kaum yang tidak akan menolaknya.</span></p><p><span>[a]</span><span> 45:17.</span></p>');
INSERT INTO `quran` VALUES (7232,2,6,91,'<p><span>Mereka itulah orang-orang yang Allah telah memberi perunjuk, maka ikutilah petunjuk mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=872">872</a><span>]</span><span> Katakanlah, “ Aku tidak meminta kepadamu upah untuk ini. Ini tidak lain melainkan suatu nasihat untuk sekalian alam.</span></p>');
INSERT INTO `quran` VALUES (7233,2,6,92,'<p><span>Dan, tidaklah </span><span>[a]</span><span> mereka menghargai Allah dengan penghargaan sebenar-benarnya ketika </span><span>[b]</span><span> mereka berkata, “ Allah tidak menurunkan sesuatu kepada seorang manusia pun.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=873">873</a><span>]</span><span> Katakanlah, “Siapakah yang telah menurunkan Kitab yang dibawa oleh Musa sebagai cahaya dan petunjuk bagi segenap manusia, walaupun kamu menganggapnya sebagai lembaran-lembaran kertas </span><span>biasa, yang sebagaian </span><span> kamu perlihatkan , sedang sebagian besarnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=874">874</a><span>]</span><span> kamu sembunyikan ; dan telah diajarkan kepadamu apa-apa yang tidak kamu ketahui dan tidak pula bapak-bapakmu ? “ Katakanlah, “ Allah ! “ Biarkanlah mereka bermain-main dalam percakapan dusta mereka.</span></p><p><span>[a]</span><span> 22:75 ; 39:68.</span></p><p><span>[b]</span><span> 36:16 ; 67:10.</span></p>');
INSERT INTO `quran` VALUES (7234,2,6,93,'<p><span>[a]</span><span> Dan, inilah sebuah Kitab yang Kami telah menurunkannya, penuh oleh keberkatan, menggenapi yang sebelumnya dan supaya engkau </span><span>[b]</span><span> memberi peringatan kepada Ummul Qura </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=875">875</a><span>]</span><span> dan orang-orang sekitaranya yang beriman kepada akhirat, beriman pula kepada </span><span>Alquran</span><span> ini </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=876">876</a><span>]</span><span> dab </span><span>[c]</span><span> mereka senantiasa memelihara shalat mereka.</span></p><p><span>[a]</span><span> 6:156 ; 21:51 ; 38:30.</span></p><p><span>[b]</span><span> 42:8.</span></p><p><span>[c]</span><span> 23:10 ; 70:24.</span></p>');
INSERT INTO `quran` VALUES (7235,2,6,94,'<p><span>Dan, </span><span>[a]</span><span> siapakah yang lebih aniaya daripoda orang-orang yang mengada-ada kedustaan terhadap Allah atau yang mengatakan, “ Telah diwahyukan kepadaku,” padahal tiada sesuatu diwahyukan kepadanya; dan barangsiapa yang mengatakan, “ Aku akan menurunkan seperti yang telah diturunkan Allah “ Dan andaikan engkau melihat ketika orang-orang yang aniaya berada dalam sakaratul maut, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=877">877</a><span>]</span><span> dan malaikat-malaikat merentangkan tangan mereka </span><span>sambil berkata, </span><span>“ Keluarkanlah nyawamu. </span><span>[b]</span><span> Hari ini kamu akan dibalas dengan azab yang menghinakan disebabkan oleh apa yang kamu katakan dengan tidak benar terhadap allah dan </span><span>karena</span><span> kamu takabur terhadap Tanda-tanda-Nya.</span></p><p><span>[a]</span><span> 6:22 ; 7:38 ; 10:18 ; 11:19 ; 61:8.</span></p><p><span>[b]</span><span> 46:21.</span></p>');
INSERT INTO `quran` VALUES (7236,2,6,95,'<p><span>Dan, sesungguhnya </span><span>[c]</span><span> kamu datang kepada Kami sendiri-sendiri sebagaimana Kami ciptakan kamu pertama kali dan kamu telah meninggalkan di belakang punggungmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=878">878</a><span>]</span><span> apa yang Kami anugerakan kepadamu dan Kami tidak melihat besertamu pemberi-pemberi syafaatmu yang kamu anggap bahwa mereka itu dalam urusan kamu sekutu-sekutu </span>Allah<span> Sesungguhnya putuslah hubungan di antaramu dan lenyaplah dari kamu segala apa yang kamu anggap itu.</span></p><p><span>[c]</span><span> 18:49.</span></p>');
INSERT INTO `quran` VALUES (7237,2,6,96,'<p><span>Sesungguhnya Allah yang menumbuhkan tumbuh-tumbahan dan buah-buahan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=879">879</a><span>]</span><span> </span><span>[a]</span><span> Dia mengeluarkan yang hidup dari yang mati, dan mengeluarkan yang mati dari yang hidup. Itulah Allah kamu ; maka kemana kamu dikembalikan ?</span></p><p><span>[a]</span><span> 2:28 ; 10:32 ; 30:20.</span></p>');
INSERT INTO `quran` VALUES (7238,2,6,97,'<p><span>Dia-lah Yang merekahkan </span><span>[b]</span><span> fajar pagi dan menjadikan </span><span>[c]</span><span> malam untuk istirahat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=880">880</a><span>]</span><span> dan </span><span>[d]</span><span> matahari serta bulan untuk perhitungan </span><span>waktu</span><span> . </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=881">881</a><span>]</span><span> Inilah takdir Tuhan Yang Maha Perkasa, Maha Mengetahui.</span></p><p><span>[b]</span><span> 113:2.</span></p><p><span>[c]</span><span> 25:48 ; 78:11.</span></p><p><span>[d]</span><span> 36:39, 40 ; 55:6.</span></p>');
INSERT INTO `quran` VALUES (7239,2,6,98,'<p><span>Dan, Dia-lah </span><span>[a]</span><span> Yang telah menjadikan bagimu bintang-bintang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=882">882</a><span>]</span><span> supaya kamu dapat mengikuti arah yang benar dengannya dalam kegelapan daratan dan lautan. Begitulah Kami telah menjelaskan secara rinci Tanda-tanda bagi orang-orang yang berilmu.</span></p><p><span>[a]</span><span> 16:17.</span></p>');
INSERT INTO `quran` VALUES (7240,2,6,99,'<p><span>Dan, Dia-lah </span><span>[b]</span><span> Yang telah menciptakan kamu dari satu jiwa kemudian </span><span>ada bagimu</span><span> </span><span>[c]</span><span> tempat tinggal sementara dan tempat tinggal abadi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=883">883</a><span>]</span><span> Sesungguhnya telah Kami jelas kan Tanda-tanda bagi orang-orang yang mengerti.</span></p><p><span>[b]</span><span> 4:3 ; 7:190 ; 39:7.</span></p><p><span>[c]</span><span> 11:7.</span></p>');
INSERT INTO `quran` VALUES (7241,2,6,100,'<p><span>Dan, </span><span>[a]</span><span> Dia-lah Yang telah menurunkan air dari langit ; kemudian Kami mengeluarkan dengan itu segala macam tumbuh-tumbuhan ; lalu Kami mengeluarkan dari itu dedaunan hijau yang darinya Kami mengeluarkan biji-biji yang bersusun-susun. Dan, dari pohon kurma, dari mayangnya </span><span>keluarlah</span><span> tandan-tandan yang berjuntai. Dan, </span><span>Kami jadikan pula</span><span> </span><span>[b]</span><span> kebun-kebun anggur dan zaitun dan delima, yang serupa dan yang tidak serupa. Lihat lah kepada buahnya, apabila ia berbuah dan mematangnya. Sesungguhnya, dalam hal demikian itu ada Tanda-tanda bagi kaum yang beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=884">884</a><span>]</span></p><p><span>[a]</span><span> 14:33 ; 16:11 ; 22:64 ; 35:28.</span></p><p><span>[b]</span><span> 6:142 ; 13:5.</span></p>');
INSERT INTO `quran` VALUES (7242,2,6,101,'<p><span>Dan, </span><span>[a]</span><span> mereka menjadikan jin-jin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=885">885</a><span>]</span><span> sebagai sekutu bagi Allah padahal Dia menciptakan mereka ; dan mereka telah mengada-adakan beberapa anak laki-laki dan anak perempuan bagi-Nya tanpa ilmu. Mahasuci Dia dan Mahaluhur dari apa yang mereka jelas kan.</span></p><p><span>[a]</span><span> 2:117 ; 9:31 ; 10:19.</span></p>');
INSERT INTO `quran` VALUES (7243,2,6,102,'<p><span>Dia-lah</span><span> </span><span>[b]</span><span> Yang menjadikan seluruh langit dan bumi. Bagaimana mungkin Dia mempunyai anak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=886">886</a><span>]</span><span> padahal Dia tidak mempunyai istri. Sedang Dia-lah Yang menciptakan segala sesuatu dan Dia Maha Mengetahui segala sesuatu.</span></p><p><span>[b]</span><span> 2:118.</span></p>');
INSERT INTO `quran` VALUES (7244,2,6,103,'<p><span>[a]</span><span> Inilah Allah , Tuhan-mu. Tidak ada Tuhan selain Dia, </span><span>[b]</span><span> Pencipta segala sesuatu ; maka sembahlah Dia. Dan, Dia Pemelihara atas segala sesuatu.</span></p><p><span>[a]</span><span> 40:63.</span></p><p><span>[b]</span><span> 13:17 ; 39:63.</span></p>');
INSERT INTO `quran` VALUES (7245,2,6,104,'<p><span>Penglihatan mata tidak sampai kepada-Nya tetapi Dia mencapai penglihatan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=887">887</a><span>]</span><span> Dan, </span><span>[c]</span><span> Dia Mahahalus, Maha Mengatahui.</span></p><p><span>[c]</span><span> 22:64 ; 67:15.</span></p>');
INSERT INTO `quran` VALUES (7246,2,6,105,'<p><span>[a]</span><span> Sesungguhnya telah datang kepadamu bukti-bukti </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=888">888</a><span>]</span><span> yang terang dari Tuhan-mu ; maka barangsiapa melihat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=889">889</a><span>]</span><span> maka </span><span>faedahnya</span><span> untuk dirinya ; dan barangsiapa buta </span><a>[890]</a><span> maka ia sendiri menanggungnya. Dan, aku bukanlah pemeliharamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=891">891</a><span>]</span></p><p><span>[a]</span><span> 7:204.</span></p>');
INSERT INTO `quran` VALUES (7247,2,6,106,'<p><span>Dan, </span><span>[b]</span><span> demikianlah Kami membentangkan tanda-tanda dengan berbagai cara dan sehingga mereka berkata, “Engkau telah membacanya dan supaya Kami menjelaskannya untuk orang-orang yang mengetahui.</span></p><p><span>[b]</span><span> 7:59.</span></p>');
INSERT INTO `quran` VALUES (7248,2,6,107,'<p><span>[c]</span><span> Ikutilah apa yang di wahyukan kepada engkau dari Tuhan engkau. Tidak ada Tuhan selain Dia ; dan menghindarlah dari orang-orang musyrik.</span></p><p><span>[c]</span><span> 10:110 ; 33:3.</span></p>');
INSERT INTO `quran` VALUES (7249,2,6,108,'<p><span>Dan, jika Allah menghendaki </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=892">892</a><span>]</span><span> niscaya mereka tidak akan berbuat syirik. Dan, tidak </span><span>[a]</span><span> Kami jadikan engkau sebagai penjaga bagi mereka, dan tidak pula engkau menjadi pelindung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=893">893</a><span>]</span><span> bagi mereka.</span></p><p><span>[a]</span><span> 39:42 ; 42:7 ; 88:23.</span></p>');
INSERT INTO `quran` VALUES (7250,2,6,109,'<p><span>Dan, janganlah kalian memaki </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=894">894</a><span>]</span><span> apa yang diseru mereka selain Allah, maka mereka memaki Allah karena rasa permusuhan, tanpa ilmu. Demikianlah Kami menampakkan indah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=895">895</a><span>]</span><span> </span><span>[b]</span><span> kepada tiap-tiap umat amalan mereka. Kemudian kepada Tuhan merekalah kembali mereka, maka Dia akan memberitahukan kepada mereka apa-apa yang dahulu mereka kerjakan.</span></p><p><span>[b]</span><span> 6:123 ; 9:37 ; 10:13 ; 27:5 40:38 ; 49:8.</span></p>');
INSERT INTO `quran` VALUES (7251,2,6,110,'<p><span>Dan, mereka bersumpah dengan nama Allah dengan sumpahnya yang sungguh-sungguh , bahwa jika datang kepada mereka suatu Tanda, niscaya mereka akan beriman kepada-Nya. Katakanlah “ Sesungguhnya, Tanda-tanda itu di sisi Allah Dan, apakah yang menjadikan kamu sadar bahwa apabila </span><span>Tanda-tanda</span><span> </span><span>itu</span><span> datang, mereka tidak akan beriman ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=896">896</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7252,2,6,111,'<p><span>Dan Kami akan memalingkan hati mereka dan mata mereka karena mereka tidak beriman kepadanya pertama kali dan </span><span>[a]</span><span> Kami akan membiarkan mereka melantur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=897">897</a><span>]</span><span> dalam ke durhakaan mereka.</span></p><p><span>[a]</span><span> 2:16</span></p>');
INSERT INTO `quran` VALUES (7253,2,6,112,'<p><span>JUZ VIII</span></p><p><span>Dan, sekalipun jika Kami menurunkan malaikat-malaikat kepada mereka dan </span><span>[a]</span><span> orang-orang yang telah mati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=898">898</a><span>]</span><span> berbicara dengan mereka dan Kami mengumpulkan di hadapan mereka segala sesuatu berhadap-hadapan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=899">899</a><span>]</span><span> niscaya mereka tidak akan beriman, kecuali jika Allah menghendaki. Akan tetapi, kebanyakan mereka tidak berpengatahuan.</span></p><p><span>[a]</span><span> 13:32</span></p>');
INSERT INTO `quran` VALUES (7254,2,6,113,'<p><span>Dan, </span><span>[a]</span><span> dengan cara demikian Kami telah menjadikan musuh bagi setiap nabi, syaitan-syaitan di antara manusia dan jin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=900">900</a><span>]</span><span> Sebagian mereka membisikkan kepada sebagian lainnya kata-kata indah untuk mengelabui . Dan, jika Tuhan engkau menghendaki, mereka tidak akan mengerjakannya ; maka biarkanlah mereka dengan apa yang mereka ada-adakan.</span></p><p><span>[a]</span><span> 25:32.</span></p>');
INSERT INTO `quran` VALUES (7255,2,6,114,'<p><span>Dan, supaya hati orang-orang yang tidak percaya kepada akhirat cenderung kepada bisikan itu, mereka menyukainya dan supaya mereka mengusahakan apa-apa yang sedang merekaa usahakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=900a">900a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7256,2,6,115,'<p><span>Katakanlah,</span><span> “Apakah aku harus mencari hakum selain Allah padahal </span><span>[a]</span><span> Dia-lah Yang telah menurunkan kepadamu Kitab dengan penjelasan rinci ? Dan </span><span>[b]</span><span> orang-orang yang telah Kami beri mereka Kitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=901">901</a><span>]</span><span> mengetahui bahwa Kitab itu diturunkan dari Tuhan engkau dengan hak, maka janganlah engkau termasuk orang-orang yang ragu.</span></p><p><span>[a]</span><span> 7:53 ; 12:112 ; 16:90.</span></p><p><span>[b]</span><span> 2:147 ; 6:21.</span></p>');
INSERT INTO `quran` VALUES (7257,2,6,116,'<p><span>Dan, sempurnalah Kalimat Tuhan engkau dengan benar dan adil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=901a">901a</a><span>]</span><span> Tiada yang dapat mengubah </span><span>[c]</span><span> Kalimat-kalimat-Nya ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=902">902</a><span>]</span><span> dan Dia-lah Yang Maha Mendengar, Maha Mengetahui.</span></p><p><span>[c]</span><span> 6:35.</span></p>');
INSERT INTO `quran` VALUES (7258,2,6,117,'<p><span>Dan, jika engkau mengikuti kebanyakan orang di bumi, mereka akan menyesatkan engkau dari jalan Allah. </span><span>[a]</span><span> Tiada lain yang mereka ikuti melainkan prasangka dan mereka tiada lain kecuali berdusta.</span></p><p><span>[a]</span><span> 10:37 ; 53:29.</span></p>');
INSERT INTO `quran` VALUES (7259,2,6,118,'<p><span>Sesungguhnya, </span><span>[b]</span><span> Tuhan engkau adalah Dia Yang Maha Mengetahui siapa yang sesat dari jalan-Nya dan Dia Maha Mengetahui orang-orang yang mendapat petunjuk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=903">903</a><span>]</span></p><p><span>[b]</span><span> 16:126.</span></p>');
INSERT INTO `quran` VALUES (7260,2,6,119,'<p><span>Maka, </span><span>[c]</span><span> makanlah apa yang nama Allah telah diucapkan atasnya, jika kamu kepada Tanda-tanda-Nya beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=904">904</a><span>]</span></p><p><span>[c]</span><span> 5:5.</span></p>');
INSERT INTO `quran` VALUES (7261,2,6,120,'<p><span>Dan, apakah sebabnya maka kamu tidak mau memakan apa yang nama Allah telah di ucapkan atasnya dan sungguh </span><span>[d]</span><span> Dia telah menjelaskan bagimu apa yang telah Dia haramkan atasmu, kecuali kamu dalam keadaan terpaksa terhadapnya. Dan, sesungguhnya kebanyakan orang benar-benar menyesatkan </span><span>orang lain</span><span> dengan hawa nafsu mereka tanpa ilmu. Sesungguhnya Tuhan engkau Dia-lah Yang Maha Mengetahui orang-orang yang melampui batas.</span></p><p><span>[d]</span><span> 2:174 ; 5:4,5; 6:146 ; 16:116.</span></p>');
INSERT INTO `quran` VALUES (7262,2,6,121,'<p><span>Dan, </span><span>[a]</span><span> tinggalkanlah dosa yang lahir dan bathinnya. Sesungguhnya orang-orang yang berbuat dosa akan dibalas sesuai apa yang mereka usahakan.</span></p><p><span>[a]</span><span> 5:152 ; 7:34.</span></p>');
INSERT INTO `quran` VALUES (7263,2,6,122,'<p><span>Dan, </span><span>[b]</span><span> janganlah kalian memakan apa yang nama Allah tidak diucapkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=905">905</a><span>]</span><span> atasnya dan sesungguhnya </span><span>perbuatan</span><span> itu suatu kedurhakaan. Dan, sesungguhnya syaitan-syaitan membisikkan </span><span>demikian</span><span> kepada kawan-kawan mereka supaya mereka bertengkar dengan kamu ; dan jika kalian menaati mereka niscaya kalian akan menjadi orang-orang musyrik.</span></p><p><span>[b]</span><span> 5:4 ; 6:146.</span></p>');
INSERT INTO `quran` VALUES (7264,2,6,123,'<p><span>Dan, apakah </span><span>[a]</span><span> orang yang tadinya mati, lalu Kami hidupkan dia dan Kami jadikan cahaya baginya ia berjalan dengan cahaya itu, di tengah-tengah manusia, seperti keadaannya orang yang berada di dalam gelap-gulita, tak dapat keluar darinya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=906">906</a><span>]</span><span> </span><span>[b]</span><span> Demikianlah telah ditampakkan indah bagi orang-orang kafir apa yang telah dikerjakan mereka.</span></p><p><span>[a]</span><span> 8:52.</span></p><p><span>[b]</span><span> 6:109 ; 10:13 ; 27:5.</span></p>');
INSERT INTO `quran` VALUES (7265,2,6,124,'<p><span>Dan, demikianlah Kami </span><span>[c]</span><span> menjadikan di dalam tiap negeri penjahat-penjahatnya yang besar sehingga akubatnya mereka mengadakan tipu-daya di dalam negeri itu dan mereka tidak menperdayakan melainkan diri mereka sendiri ; dan tidak mereka menyadari.</span></p><p><span>[c]</span><span> 17:17.</span></p>');
INSERT INTO `quran` VALUES (7266,2,6,125,'<p><span>Dan, apabila datang kepada mereka suatu Tanda, berkata mereka, </span><span>[a]</span><span> “Kami sekali-kali tidak akan beriman sebelum kami diberi seperti apa yang telah diberikan kepada rasul-rasul Allah.” Allah Maha Mengetahui di mana Dia akan menempatkan risalat-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=906a">906a</a><span>]</span><span> Akan ditimpakan kehinaan kepada orang-orang yang berdosa di sisi Allah , dan azab yang keras disebabkan mereka telah mengerjakan tipu-daya.</span></p>');
INSERT INTO `quran` VALUES (7267,2,6,126,'<p><span>Maka, barangsiapa yang Allah menghendaki supaya diberi petunjuk kepadanya, Dia membukakan dadanya untuk Islam. Dan barangsiapa yang Dia menghendaki supaya Dia membiarkannya sesat, Dia menjadikan dadanya sangat sempit seakan-akan ia sedang naik ke langit, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=907">907</a><span>]</span><span> Seperti itulah </span><span>[b]</span><span> Allah menimpakan azab kepada orang-orang yang tidak </span></p><p><span>[b]</span><span> 10:101.</span></p>');
INSERT INTO `quran` VALUES (7268,2,6,127,'<p><span>Dan, </span><span>[a]</span><span> inilah jalan Tuhan engkau yang lurus. Sesungguhnya, telah Kami jelaskan Tanda-tanda bagi kaum yang suka mengambil pelajaran.</span></p><p><span>[a]</span><span> 6:154.</span></p>');
INSERT INTO `quran` VALUES (7269,2,6,128,'<p><span>Bagi mereka </span><span>[b]</span><span> rumah keselamatan di sisi Tuhan mereka dan Dia Pelindung mereka di sebabkan apa yang mereka kerjakan.</span></p><p><span>[b]</span><span> 10:26</span></p>');
INSERT INTO `quran` VALUES (7270,2,6,129,'<p><span>Dan, </span><span>ingatlah</span><span> </span><span>[c]</span><span> hari ketika Dia akan menghimpun mereka semua, </span><span>Dia berfirman</span><span> , “Hai golongan jin, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=908">908</a><span>]</span><span> sesungguhnya kamu telah menarik banyak manusia ! “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=909">909</a><span>]</span><span> Dan teman-teman mereka dari antara manusia akan berkata, “Hai Tuhan kami, sebagian kami telah mengambil keuntungan dari sebagian lainnya, tetapi kami telah sampai kepada jangka waktu kami yang telah Engkau tetapkan bagi kami. “ Dia berfirman, “ Api itulah tempat tinggalmu, kamu akan tinggal lama di dalamnya, kecuali apa yang dihendaki Allah.” Sesungguhnya Tuhan engkau Maha Bijaksana, Maha Mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=910">910</a><span>]</span></p><p><span>[c]</span><span> 7:39 </span><span>–</span><span> 40 ; 10:29 ; 34:32.</span></p>');
INSERT INTO `quran` VALUES (7271,2,6,130,'<p><span>Dan demikianlah Kami jadikan teman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=910a">910a</a><span>]</span><span> sebagian orang-orang yang aniaya </span><span>bagi</span><span> sebagian yang lain disebabkan apa yang telah mereka usahakan.</span></p>');
INSERT INTO `quran` VALUES (7272,2,6,131,'<p><span>[a]</span><span> “Hai golongan jin dan manusia. tidaklah telah datang kepadamu rasul-rasul dari antara mu yang menceriterakan kepadamu Tanda-tanda-Ku dan memperingatkan kamu mengenai pertemuan pada harimu ini ? “ Mereka akan berkata, “Kami menjadi saksi atas diri kami. “ Dan kehidupan dunia telah memperdayakan mereka. Dan </span><span>[b]</span><span> mereka telah menjadi saksi atas diri mereka sendiri bahwa mereka dahulunya orang-orang kafir.</span></p><p><span>[a]</span><span> 39:72 ; 40:51 ; 67:9 </span><span>–</span><span> 10.</span></p><p><span>[b] </span><span>7:38.</span></p>');
INSERT INTO `quran` VALUES (7273,2,6,132,'<p><span>Yang demikian itu karena </span><span>[b]</span><span> Tuhan engkau tidak pernah membinasakan negeri-negeri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=911">911</a><span>]</span><span> secara aniaya sedang penduduknya dalam keadaan lengah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=912">912</a><span>]</span></p><p><span>[b]</span><span> 11:118 ; 20:135 ; 26:209 ; 28:60.</span></p>');
INSERT INTO `quran` VALUES (7274,2,6,133,'<p><span>Dan, bagi masing-masing ada derajat-derajat menurut apa yang telah mereka amalkan dan Tuhan engkau tidak lengah terhadap apa yang mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (7275,2,6,134,'<p><span>Dan Tuhan engkau Maha Kaya, </span><span>[c]</span><span> Yang Empunya rahmat. Jika Dia menghendaki niscaya </span><span>[d]</span><span> Dia dapat membinasakan kamu dan menjadikan pengganti sesudah kamu siapa yang Dia kehendaki sebagaimana kamu telah Dia bangkitkan dari keturunan kaum lain.</span></p><p><span>[c]</span><span> 6:148 ; 18:59.</span></p><p><span>[d]</span><span> 4:134 ; 14:20 ; 35:17.</span></p>');
INSERT INTO `quran` VALUES (7276,2,6,135,'<p><span>Sesungguhnya, </span><span>[a]</span><span> apa yang dijanjikan kepadamu pasti akan datang dan kamu tak akan dapat mengagalkannya.</span></p><p><span>[a]</span><span> 11:34 ; 42:32.</span></p>');
INSERT INTO `quran` VALUES (7277,2,6,136,'<p><span>Katakanlah, </span><span>[b]</span><span> “Hai kaum-ku, beramallah menurut kemampuanmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=913">913</a><span>]</span><span> sesungguhnya akupun beramal. Maka, kamu akan mengetahui siapa yang akan memperoleh ganjaran di tempat kediaman ini.” Sesungguhnya orang-orang aniaya tidak akan menang.</span></p><p><span>[b]</span><span> 11:9 , 122 ; 39:40 </span><span>–</span><span> 41.</span></p>');
INSERT INTO `quran` VALUES (7278,2,6,137,'<p><span>Dan, </span><span>[c]</span><span> mereka menentukan bagi Allah sebagian yang trelah Dia ciptakan dari ladang dan binatang-binatang ternak ; maka mereka berkata, “ Ini bagi Allah, “ demikianlah anggapan mereka, dan ini bagi berhala-berhala kami. Namun, apa yang bagi berhala-berhala mereka itu, maka tidak akan sampai kepada Allah, dan apa yang bagi Allah itu akan sampai kepada berhala-berhala mereka. Alangkah buruknya apa yang mereka putus kan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=914">914</a><span>]</span></p><p><span>[c]</span><span> 16:57.</span></p>');
INSERT INTO `quran` VALUES (7279,2,6,138,'<p><span>Dan, demikianlah berhala-berhala mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=915">915</a><span>]</span><span> menampakkan indah kepada kebanyakan orang-orang musyrik, pembunuhan anak-anak mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=916">916</a><span>]</span><span> untuk membinasakan mereka dan mengacaukan agama mereka. Dan jika Allah menghendaki, mereka tidak akan berbuat demikian ; maka biarkanlah mereka dengan apa yang mereka ada-adakan.</span></p>');
INSERT INTO `quran` VALUES (7280,2,6,139,'<p><span>Dan mereka berkata, “ Inilah binatang-binatang dan tanaman-tanaman yang dilarang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=917">917</a><span>]</span><span> tidak boleh memakannya selain orang yang kamu kehendaki, demikianlah anggapan mereka, dan ada beberapa binatang ternak yang punggungnya diharamkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=918">918</a><span>]</span><span> dan ada binatang ternak yang mereka tidak menyebutkan nama Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=919">919</a><span>]</span><span> atasnya, semata-mata membuat-buat kebohongan terhadap-Nya. Dia segera akan membalas mereka untuk apa yang telah mereka ada-adakan.</span></p>');
INSERT INTO `quran` VALUES (7281,2,6,140,'<p><span>Dan mereka berkata “ Apa yang ada dalam perut binatang-binatang ternak ini khusus bagi kaum laki-laki kami dan diharamkan atas istri-istri kami ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=920">920</a><span>]</span><span> tetapi jika ia mati </span><span>waktu melahirkan</span><span> maka mereka dalam hal ini sama-sama </span><span>boleh memakannya.</span><span> Dia niscaya akan menghukum mereka atas pernyataan mereka. Sesungguhnya Dia Maha Bijaksana, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (7282,2,6,141,'<p><span>Sesungguhnya merugilah orang-orang yang membunuh anak-anak mereka karena kebodohan tanpa ilmu, dan mereka mengharamkan apa-apa yang telah direzekikan Allah kepada mereka, karena mengada-ada dusta terhadapa Allah Sungguh sesatlah mereka dan mereka tidak mendapat petunjuk.</span></p>');
INSERT INTO `quran` VALUES (7283,2,6,142,'<p><span>Dan </span><span>[a]</span><span> Dia-lah Yang menjadikan kebun-kebun yang berperambat dan tidak berperambat dan pohon-pohon kurma, dan tanam-tanaman yang beraneka rasanya dan zaitun dan delima, yang serupa. Makanlah buah-buahan apabila ia berbuah dan berilah hak-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=921">921</a><span>]</span><span> pada hari panennya dan janganlah kamu melampaui batas. Sesungguhnya, Dia tidak menyukai orang-orang yang melampui batas.</span></p><p><span>[a]</span><span> 6:100 ; 13:5 : 16:12 ; 35:28 ; 36:35 </span><span>–</span><span> 36.</span></p>');
INSERT INTO `quran` VALUES (7284,2,6,143,'<p><span>Dan, di antara binatang-binatang ternak itu ada untuk pemikul beban dan ada untuk sembelihan. Makanlah dari apa yang telah direzekikan Allah kepadamu dan </span><span>[a]</span><span> janganlah mengikuti langkah-langkah syaitan. Sesungguhnya bagi kamu ia musuh yang nyata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=922">922</a><span>]</span></p><p><span>[a]</span><span> Lihat 2:209.</span></p>');
INSERT INTO `quran` VALUES (7285,2,6,144,'<p><span>Tentang binatang ternak Dia ciptakan</span><span> </span><span>[b]</span><span> delapan pasang, dari domba dua dan dari kambing dua. Katakanlah, “Dua yang jantankah yang Dia telah haramkan, atau dua yang betinakah, ataukan yang ada dalam kandungan kedua betina itu ? Beritahukanlah kepadaku berdasarkan ilmu jika kamu orang-orang benar.”</span></p><p><span>[b]</span><span> 39:7.</span></p>');
INSERT INTO `quran` VALUES (7286,2,6,145,'<p><span>“Dan dari unta dua, dan dari lembu dua. “ Katakanlah, “Dua yang jantankah yang dia telah haramkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=923">923</a><span>]</span><span> ataukah dua yang betina, ataukah yang ada dalam kandungan kedua betina itu ? “ </span><span>Katakanlah</span><span> , “Adakah kamu hadir ketika Allah memerintahkan kepadamu tentang hal ini ? “ </span><span>kalau tidak, </span><span> maka </span><span>[a]</span><span> siapakah yang lebih aniaya daripada orang yang membuat-buat kebohongan terhadap Allah supaya ia menyesatkan manusia tanpa ilmu ? Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang aniaya.</span></p><p><span>[a]</span><span> 6:22 ; 7:38 ; 11:19</span></p>');
INSERT INTO `quran` VALUES (7287,2,6,146,'<p><span>Katakanlah, </span><span>[b]</span><span> “ Aku tidak mendapatkan di dalam apa yang diwahyukan kepadaku sesuatu yang diharamkan bagi orang yang memakannya kecuali jika makanan itu bangkai atau darah yang ditumpahkan, atau daging babi, karena sesungguhnya itu najis, atau </span><span>[c]</span><span> yang bersifat fasik yakni yang disembelih dengan menyebut nama lain selain Alla swt. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=924">924</a><span>]</span><span> atasnya. Tetapi, barangsiapa terpaksa </span><span>memakannya,</span><span> tanpa maksud melanggar hukum dan tidak pula melampaui batas maka sesungguhnya Tuhan engkau Maha Pengampun, Maha Penyayang.</span></p><p><span>[b]</span><span> 2:174 ; 5:4 ; 16:116.</span></p><p><span>[c]</span><span> 6:122.</span></p>');
INSERT INTO `quran` VALUES (7288,2,6,147,'<p><span>Dan, </span><span>[a]</span><span> bagi orang-orang Yahudi, telah Kami haramkan segala binatang bercakar ; dan tentang lembu dan domba, Kami haramkan atas mereka lemak keduanya itu kecuali </span><span>lemak</span><span> yang melekat pada punggungnya atau pada usus-usus atau yang bercampur dengan tulang-tulang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=925">925</a><span>]</span><span> Demikianlah Kami membalas mereka atas kedurhakaan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=926">926</a><span>]</span><span> Dan, sesungguhnya Kami Maha Benar.</span></p><p><span>[a]</span><span> 3:134 ; 7:157.</span></p>');
INSERT INTO `quran` VALUES (7289,2,6,148,'<p><span>Tetapi, jika mereka mendustakan engkau, maka katakanlah, </span><span>[a]</span><span> “Tuhan-ku adalah Yang mempunyai rahmat yang luas, dan siksaan-Nya tak dapat dielakkan dari kaum yang berdosa.”</span></p><p><span>[a]</span><span> 3:134 ; 7:157.</span></p>');
INSERT INTO `quran` VALUES (7290,2,6,149,'<p><span>Pasti akan berkata </span><span>[b]</span><span> orang-orang yang berbuat syirik, “Jika dikehendaki Allah tentu kami tidak akan berbuat syirik dan tidak pula bapak-bapak kami ; dan tidak akan kami haramkan sesuatu pun.” Begitu pula orang-orang yang sebelum mereka telah mendustakan </span><span>nabi-nabi Allah</span><span> hingga mereka merasakan siksaan Kami. Katakanlah, “Adakah padamu ilmu, maka kemukakanlah itu kepada Kami. Tak lain yang kamu ikuti melainkan prasangka. Dan kamu tidak lain hanya menerka-nerka.</span></p><p><span>[b]</span><span> 16:36 ; 43:21.</span></p>');
INSERT INTO `quran` VALUES (7291,2,6,150,'<p><span>Katakanlah, “Allah mempunyai dalil yang meyakinkan. Dan </span><span>[c]</span><span> jika Dia menghendaki niscaya Dia memberi perunjuk kepada kamu sekalian.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=927">927</a><span>]</span></p><p><span>[c]</span><span> 5:49 ; 11:119 ; 13:32 ; 16:10.</span></p>');
INSERT INTO `quran` VALUES (7292,2,6,151,'<p><span>Katakanlah, “Kemukankanlah saksi-saksimu yang mereka akan menjadi saksi bahwa Allah telah mengharamkan </span><span>barang-barang</span><span> ini. “ Dan jika mereka memberi kesaksian maka janganlah engkau memberi kesaksian bersama mereka dan </span><span>[a]</span><span> janganlah mengikuti hawa nafsu orang-orang yang mendustakan Tanda-tanda Kami, dan orang-orang yang tidak beriman kepada akhirat dan </span><span>[b]</span><span> mereka juga mempersekutukan </span><span>sesuatu</span><span> dengan Tuhan mereka.</span></p><p><span>[a]</span><span> 5:49 ; 45:19.</span></p><p><span>[b]</span><span> 6:2 ; 27:61.</span></p>');
INSERT INTO `quran` VALUES (7293,2,6,152,'<p><span>Katakan, “Marilah kubacakan apa yang diharamkan Tuhan-mu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=928">928</a><span>]</span><span> atasmu, janganlah kamu </span><span>[c]</span><span> mempersekutukan sesuatu pun dengan-Nya ; dan berbuat baiklah terhadap kedua orang-tua, dan </span><span>[d]</span><span> janganlah kamu membunuh anak-anakmu karena </span><span>takut</span><span> miskin, Kami Yang memberi rezeki kepadamu dan kepada mereka, dan </span><span>[e]</span><span> jangan kamu mendekati perbuatan keji, baik itu zahir ataupun yang tersembunyi ; dan janganlah kamu membunuh suatu jiwa yang Allah mengharamkannya kecuali dengan hak. Demikianlah Dia telah mewasiatkan kepadamu mengenai hal itu supaya kamu memahami.</span></p><p><span>[c]</span><span> 4:37 ; 17:24.</span></p><p><span>[d]</span><span> 17:32.</span></p><p><span>[e]</span><span> 6:121 ; 7:34</span></p>');
INSERT INTO `quran` VALUES (7294,2,6,153,'<p><span>Dan, </span><span>[a]</span><span> janganlah kamu mendekati harta anak yatim kecuali dengan </span><span>cara </span><span> yang terbaik sampai ia mencapai kedewasaannya. Dan </span><span>[b]</span><span> penuhilah ukuran dan timbangan dengan adil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=929">929</a><span>] [c]</span><span> Kami tidak memberi beban kepada suatu jiwa kecuali menurut kemampuannya. Dan apabila kamu berkata, maka hendaklah berlaku adil walaupun itu </span><span>terhadap</span><span> seorang kerabat ; dan </span><span>[d]</span><span> sempurnakanlah janji dengan Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=930">930</a><span>]</span><span> Demikianlah Dia telah memerintahkan kepadamu mengenai hal itu supaya kamu mendapat nasihat.”</span></p><p><span>[a]</span><span> 4:11 ; 17:35.</span></p><p><span>[b]</span><span> 17:36 ; 26:182 </span><span>–</span><span> 183 ; 55:10.</span></p><p><span>[c]</span><span> 2:287 ; 7:43.</span></p><p><span>[d]</span><span> 5:2 ; 16:92 ; 17:35.</span></p>');
INSERT INTO `quran` VALUES (7295,2,6,154,'<p><span>Dan </span><span>katakanlah</span><span> , </span><span>[a]</span><span> “Inilah jalan-Ku yang lurus. Maka, ikutilah </span><span>jalan</span><span> ini ; dan janganlah kamu mengikuti jalan-jalan </span><span>yang lain.</span><span> karena </span><span>jalan itu</span><span> akan menjauhkan kamu dari jalan-Nya. Demikianlah Dia telah memerintahkan kepadamu mengenai hal itu supaya kamu bertakwa.”</span></p><p><span>[a]</span><span> 6:127</span></p>');
INSERT INTO `quran` VALUES (7296,2,6,155,'<p><span>Kemudian, </span><span>[b]</span><span> Kami memberi Kitab kepada Musa, untuk penyempurnaan </span><span>nikmat</span><span> kepada orang yang berbuat kebaikan, dan </span><span>[c]</span><span> penjelasan mengenai segala sesuatu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=931">931</a><span>]</span><span> dan sebagai petunjuk serta rahmat, supaya mereka percaya akan pertemuan dengan Tuhan mereka.</span></p><p><span>[b]</span><span> 2:54 ; 5:45.</span></p><p><span>[c]</span><span> 7:146.</span></p>');
INSERT INTO `quran` VALUES (7297,2,6,156,'<p><span>Dan, </span><span>[d]</span><span> inilah Kitab </span><span>Alquran</span><span> yang Kami telah menurunkannya dengan penuh berkat, maka ikutilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=932">932</a><span>]</span><span> dia dan bertakwalah supaya kamu dikasihani.</span></p><p><span>[d]</span><span> 6:93 ; 21:51.</span></p>');
INSERT INTO `quran` VALUES (7298,2,6,157,'<p><span>Supaya kamu </span><span>jangan</span><span> berkata, “Kitab itu diturunkan hanya kepada dua golongan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=933">933</a><span>]</span><span> sebelum kami, dan kami benar-benar lengah terhadap apa yang dibaca oleh mereka;</span></p>');
INSERT INTO `quran` VALUES (7299,2,6,158,'<p><span>[a]</span><span> Atau </span><span>supaya</span><span> kamu </span><span>jangan</span><span> berkata, “Jika Kitab itu diturunkan kepada kami tentu kami akan lebih mendapat petunjuk daripada mereka.” </span><span>Sekarang</span><span> telah datang kepadamu bukti yang jelas dari Tuhan-mu dan petunjuk serta rahmat. Maka </span><span>[b]</span><span> siapakah yang lebih aniaya dari orang yang mendustakan Tanda-tanda Allah dan berpaling darinya ? Tentu akan Kami balas orang-orang yang berpaling dari Tanda-tanda Kami dengan azab yang amat buruk disebabkan mereka senantiasa berpaling.</span></p><p><span>[a]</span><span> 35:43.</span></p><p><span>[b]</span><span> 6:22 ; 7:38 ; 10:18.</span></p>');
INSERT INTO `quran` VALUES (7300,2,6,159,'<p><span>Tak lain yang </span><span>[c]</span><span> mereka nanti-nantikan kecuali kedatangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=934">934</a><span>]</span><span> malaikat-malaikat kepada mereka atau kedatangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=935">935</a><span>]</span><span> Tuhan engkau atau kedatangan sebagian dari Tanda-tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=936">936</a><span>]</span><span> Tuhan engkau. Pada hari ketika sebagian Tanda Tuhan engkau datang, </span><span>Tanda itu</span><span> tidak akan memberi manfaat kepada seseorang untuk mempercayainya, baik yang tidak beriman lebih dahulu atau yang </span><span>belum </span><span> mengusahakan kebaikan dalam keimanan nya. Katakanlah, “Tunggulah olehmu, sesungguhnya kami </span><span>pun</span><span> menunggu.”</span></p><p><span>[c]</span><span> 2:211 ; 16:34.</span></p>');
INSERT INTO `quran` VALUES (7301,2,6,160,'<p><span>Sesungguhnya </span><span>[a]</span><span> orang-orang yang memecah-belah agama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=937">937</a><span>]</span><span> dan mereka menjadi golongan-golongan, engkau tidak berkepentingan apa pun dengan mereka. Sesungguhnya, urusan mereka terserah kepada Allah, kemudian Dia akan memberitahukan kepada mereka apa yang telah mereka kerjakan.</span></p><p><span>[a]</span><span> 39:33.</span></p>');
INSERT INTO `quran` VALUES (7302,2,6,161,'<p><span>[a]</span><span> Barangsiapa berbuat kebaikan maka baginya ada </span><span>ganjaran</span><span> sepuluh kali </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=938">938</a><span>]</span><span> semisal itu, tetapi siapa yang berbuat keburukan maka ia tidak akan dibalas melainkan hanya semisal itu dan mereka tidak akan dianiaya..</span></p><p><span>[a]</span><span> 4:41 ; 27:90 28:85.</span></p>');
INSERT INTO `quran` VALUES (7303,2,6,162,'<p><span>Katakanlah, “ Sesungguhnya aku telah diberi petunjuk oleh Tuhan-ku kepada jalan lurus, agama yang teguh, </span><span>[b]</span><span> agama Ibrahim yang benar dan dia bukanlah dari orang-orang musyrik.”</span></p><p><span>[b]</span><span> 3:96 ; 16:124.</span></p>');
INSERT INTO `quran` VALUES (7304,2,6,163,'<p><span>Katakanlah, “ Sesungguhnya shalatku dan pengorbananku dan kehidupanku serta kematian ku hanyalah untuk Allah, Tuhan semesta alam ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=939">939</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7305,2,6,164,'<p><span>[a]</span><span> Tidak ada sekutu bagi-Nya dan untuk itulah aku diperintah dan akulah orang pertama yang menyerahkan diri.</span></p><p><span>[a]</span><span> 6:15 ; 39:12, 13.</span></p>');
INSERT INTO `quran` VALUES (7306,2,6,165,'<p><span>Katakanlah, </span><span>[b]</span><span> “ Apakah aku akan mencari Tuhan selain Allah sedangkan Dia Tuhan segala sesuatu ? “ Dan tiada jiwa mengupayakan </span><span>sesuatu</span><span> melainkan akan menimpa dirinya ; dan </span><span>[c]</span><span> tidak pula seorang pemikul beban memikul beban orang lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=940">940</a><span>]</span><span> Kemudian kepada Tuhan-mu tempat kembalimu, maka Dia akan memberitahu kamu apa-apa yang mengenainya kamu berselisih.</span></p><p><span>[b]</span><span> 7:141.</span></p><p><span>[c]</span><span> 17:16 ; 35:19 ; 53:39.</span></p>');
INSERT INTO `quran` VALUES (7307,2,6,166,'<p><span>Dan, Dia-lah Yang menjadikan kamu penerus-penerus di bumi dan Dia meninggikan sebagian kamu dari sebagian yang lain dalam derajat </span><span>[d]</span><span> supaya Dia menguji kamu dengan apa-apa yang telah Dia berikan kepadamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=941">941</a><span>]</span><span> Sesungguhnya, Tuhan engkau sangat cepat dalam menghukum. Dan sesungguhnya, Dia Maha Pengampun, Maha Penyayang.</span></p><p><span>[d]</span><span> 5:49 ; 11:8 ; 67:3.</span></p>');
INSERT INTO `quran` VALUES (7308,2,7,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (7309,2,7,2,'<p><span>[a]</span><span> Aku Allah, Yang Maha Mengetahui dan Maha Besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=942">942</a><span>]</span></p><p><span>[a]</span><span> 2:2 ; 3:2 ; 29:2 ; 30:2 ; 31:2 ; 32:2.</span></p>');
INSERT INTO `quran` VALUES (7310,2,7,3,'<p><span>[b]</span><span>Inilah </span><span>Kitab yang diturunkan kepada engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=943">943</a><span>]</span><span> maka janganlah ada kesempitan di dalam dada engkau mengenainya supaya engkau memberi peringatan dengannya dan nasihat bagi orang-orang Mukmin.</span></p><p><span>[b]</span><span> 6:52 ; 19:98 ; 25:2.</span></p>');
INSERT INTO `quran` VALUES (7311,2,7,4,'<p><span>[c]</span><span> Ikutilah apa yang telah diturunkan kepadamu dari Tuhan-mu dan janganlah kamu mengikuti pelindung-pelindung selain dari-Nya. </span><span>Tetapi,</span><span> tidak sedikit pun kamu mengambil nasihat.</span></p><p><span>[c]</span><span> 33:3 ; 39:56.</span></p>');
INSERT INTO `quran` VALUES (7312,2,7,5,'<p><span>Dan, </span><span>[a]</span><span> beberapa banyak negeri telah Kami binasakan ! Maka, siksaan Kami menimpa mereka ketika tidur di waktu malam atau ketika mereka sedang istirahat siang hari. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=944">944</a><span>]</span></p><p><span>[a]</span><span> 7:98 ; 21:12 ; 28:59.</span></p>');
INSERT INTO `quran` VALUES (7313,2,7,6,'<p><span>Maka, tiada lain seruan mereka ketika siksaan Kami menimpa mereka melainkan mereka mengatakan, “Sesungguhnya </span><span>[b]</span><span> kami orang-orang aniaya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=945">945</a><span>]</span></p><p><span>[b]</span><span> 21:15.</span></p>');
INSERT INTO `quran` VALUES (7314,2,7,7,'<p><span>Maka, pasti akan </span><span>[c]</span><span> Kami tanyai orang-orang yang kepada mereka </span><span>rasul-rasul </span><span> telah diutus dan pasti akan </span><span>[d]</span><span> Kami tanyai </span><span>pula</span><span> rasul-rasul itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=946">946</a><span>]</span></p><p><span>[c]</span><span> 28:66.</span></p><p><span>[d]</span><span> 5:110.</span></p>');
INSERT INTO `quran` VALUES (7315,2,7,8,'<p><span>Maka, pasti akan Kami ceritakan kepada mereka </span><span>keadaan mereka</span><span> dengan sepengetahuan </span><span>Kami</span><span> dan tidak pernah Kami tidak hadir.</span></p>');
INSERT INTO `quran` VALUES (7316,2,7,9,'<p><span>Dan, </span><span>[a]</span><span> timbangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=947">947</a><span>]</span><span> pada Hari itu benar. Maka, barangsiapa berat timbangannya, merekalah orang-orang yang berhasil.</span></p><p><span>[a]</span><span> 21:48 ; 23:103 ; 101:9 </span><span>–</span><span> 10.</span></p>');
INSERT INTO `quran` VALUES (7317,2,7,10,'<p><span>Dan, </span><span>[b]</span><span> barangsiapa ringan timbangannya, maka merekalah yang merugikan diri mereka sendiri disebabkan mereka telah berbuat aniaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=948">948</a><span>]</span><span> terhadap Tanda-tanda Kami.</span></p><p><span>[b]</span><span> 23:104 ; 101:9 </span><span>–</span><span> 10.</span></p>');
INSERT INTO `quran` VALUES (7318,2,7,11,'<p><span>Dan, sesungguhnya </span><span>[c]</span><span> Kami telah mengokohkan kamu di atas bumi dan Kami menjadikan bagimu sumber kehidupan di dalamnya. </span><span>Tetapi</span><span> sedikit sekali kamu bersyukur.</span></p><p><span>[c]</span><span> 15:21 ; 46:27.</span></p>');
INSERT INTO `quran` VALUES (7319,2,7,12,'<p><span>Dan, sesungguhnya Kami telah menciptakan kamu, kemudian </span><span>[a]</span><span> Kami beri kamu bentuk, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=949">949</a><span>]</span><span> lalu Kami berfirman kepada para malaikat, </span><span>[b]</span><span> tunduklah kepada Adam ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=950">950</a><span>]</span><span> maka tunduklah mereka kecuali iblis, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=951">951</a><span>]</span><span> ia tidak termasuk di antara orang-orang yang tunduk.</span></p><p><span>[a]</span><span> 23:15 ; 39:7 ; 40:65.</span></p><p><span>[b]</span><span> 2:35 ; 15:30, 31 ; 17:62 ; 18:51 ; 20:117 ; 38:73, 75.</span></p>');
INSERT INTO `quran` VALUES (7320,2,7,13,'<p><span>Tuhan</span><span> </span><span>[c]</span><span> berfirman, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=952">952</a><span>]</span><span> “Apa yang telah menghalangi engkau sehingga engkau tidak tunduk ketika Aku memberi perintah kepada engkau ? “ Ia berkata, “ Aku lebih baik dari nya. Engkau jadikan aku dari api dan dia Engkau jadikan dari tanah.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=953">953</a><span>]</span></p><p><span>[c]</span><span> 15:33, 34 ; 38:76, 77.</span></p>');
INSERT INTO `quran` VALUES (7321,2,7,14,'<p><span>Tuhan </span><span>berfirman, </span><span>[a]</span><span> “Pergilah engkau darinya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=954">954</a><span>]</span><span> karena tidak patut bagi engkau berlaku sombang di dalamnya. Keluarlah ! Sesungguhnya engkau termasuk di antara orang-orang hina ?</span></p><p><span>[a]</span><span> 15:35 ; 38:78.</span></p>');
INSERT INTO `quran` VALUES (7322,2,7,15,'<p><span>Ia </span><span>[b]</span><span> berkata, “tangguhkanlah aku sampai hari itu, bila mereka akan dibangkitkan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=954a">954a</a><span>]</span></p><p><span>[b]</span><span> 15:37 ; 38:80.</span></p>');
INSERT INTO `quran` VALUES (7323,2,7,16,'<p><span>Tuhan </span><span> berfirman, “Sesungguhnya engkau termasuk orang-orang yang diberi tangguh.”</span></p>');
INSERT INTO `quran` VALUES (7324,2,7,17,'<p><span>[a]</span><span> Ia berkata, “Disebabkan Engkau telah menyesatkan aku maka pasti aku akan duduk menghadang mereka di jalan Engkau yang lurus ; </span></p><p><span>[a]</span><span> 15:40 ; 38:83.</span></p>');
INSERT INTO `quran` VALUES (7325,2,7,18,'<p><span>Kemudian, pasti akan kudatangi mereka dari muka mereka dan dari belakang mereka dan dari kanan mereka dan dari kiri mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=955">955</a><span>]</span><span> Dan Engkau tidak akan mendapatkan kebanyakan mereka yang bersyukur.”</span></p>');
INSERT INTO `quran` VALUES (7326,2,7,19,'<p><span>Tuhan</span><span> berfirman, “ Keluarlah darinya, </span><span>dalam keadaan </span><span> terhina dan terusir. Sesungguhnya, </span><span>[b]</span><span> barangsiapa dari antara mereka akan mengikuti engkau, tentu akan Aku penuhi Jahannam dengan kamu sekalian.”</span></p><p><span>[b]</span><span> 11:20 ; 15:43, 44 ; 32:14 ; 38:86.</span></p>');
INSERT INTO `quran` VALUES (7327,2,7,20,'<p><span>Dan </span><span>[c]</span><span> hai Adam, tinggallah engkau dan istri engkau di dalam kebun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=955a">955a</a><span>]</span><span> ini, maka makanlah </span><span>dan minumlah</span><span> dari mana saja kamu berdua sukai, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=956">956</a><span>]</span><span> tetapi janganlah kamu berdua mendekati pohon ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=957">957</a><span>]</span><span> jangan-jangan kamu berdua termasuk orang-orang aniaya.”</span></p><p><span>[c]</span><span> 2:38 ; 20:118.</span></p>');
INSERT INTO `quran` VALUES (7328,2,7,21,'<p><span>Tetapi, </span><span>[a]</span><span> syaitan membisikkan </span><span>pikiran jahat</span><span> kepada kedua mereka itu agar ia dapat menampakkan kepada kedua mereka itu apa yang tersembunyi dari kedua mereka itu, aurat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=957a">957a</a><span>]</span><span> mereka dan ia berkata, “Tidak lain Tuhan-mu melarang kamu berdua dari pohon ini agar kamu berdua jangan menjadi malaikat atau menjadi di antara orang-orang yang </span><span>hidup</span><span> kekal.”</span></p><p><span>[a]</span><span> 2:37 ; 20:121.</span></p>');
INSERT INTO `quran` VALUES (7329,2,7,22,'<p><span>Dan, ia bersumpah kepada kedua mereka itu, “Sesungguhnya aku penasihat bagi kamu berdua.” </span></p>');
INSERT INTO `quran` VALUES (7330,2,7,23,'<p><span>Maka, ia menjerumuskan kedua mereka itu dengan tipu daya. Maka, tatkala kedua mereka itu </span><span>[a]</span><span> mencicipi </span><span>buah</span><span> pohon itu tampaklah kepada mereka aurat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=958">958</a><span>]</span><span> mereka dan mulailah mereka menutupi diri mereka dengan daun-daun surga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=959">959</a><span>]</span><span>. Dan, kedua mereka itu diseru oleh Tuhan mereka, “Bukankah Aku telah melarang kamu berdua dari pohon itu dan Aku katakan kepadamu berdua, sesungguhnya </span><span>[b]</span><span> syaitan itu musuh yang nyata bagi kamu berdua ?”</span></p><p><span>[a]</span><span> 2:37 ; 20:122.</span></p><p><span>[b]</span><span> 2:169, 209 ; 6:143 ; 12:6 ; 20:118 ; 28:16 ; 35:7 ; 36:61.</span></p>');
INSERT INTO `quran` VALUES (7331,2,7,24,'<p><span>Mereka berdua berkata, </span><span>[a]</span><span> “wahai Tuhan kami ! kami telah berlaku aniaya terhadap diri kami ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=960">960</a><span>]</span><span> dan, jika Engkau tidak mengampuni kami dan tidak mengasihani kami, pasti kami akan termasuk orang-orang merugi.</span></p><p><span>[a]</span><span> 2:38.</span></p>');
INSERT INTO `quran` VALUES (7332,2,7,25,'<p><span>Tuhan berfirman, </span><span>[b] </span><span>”Pergilah kamu sekalian </span><span>dari sini</span><span> ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=961">961</a><span>]</span><span> sebagian kamu adalah musuh bagi sebagian lain. Dan bagimu di bumi ini terdapat tempat kediaman dan bekal hidup sampai masa tertentu.”</span></p><p><span>[b]</span><span> 2:37, 39 ; 20:124</span></p>');
INSERT INTO `quran` VALUES (7333,2,7,26,'<p><span>Dia berfirman, </span><span>[a]</span><span> “ Di situlah kamu sekalian akan hidup dan di situlah kamu akan mati, dan darinya kamu akan dikeluarkan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=962">962</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7334,2,7,27,'<p><span>Wahai Bani Adam, sesungguhnya Kami telah menurunkan kepadamu pakaian penutup aurat mu sebagai perhiasan. Dan pakaian takwa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=963">963</a><span>]</span><span> itulah yang terbaik. Hal demikian itu sebagian dari Tanda-tanda Allah mudah-mudahan mereka mendapat nasihat.</span></p>');
INSERT INTO `quran` VALUES (7335,2,7,28,'<p><span>Wahai Bani Adam, janganlah kamu biarkan syaitan menggoda kamu biarkan syaitan menggoda kamu sebagaimana ia telah mengeluarkan kedua orang tuamu dari surga, ia menanggalkan pakaan kedua mereka itu untuk menampakkan kepada kedua mereka itu aurat mereka. Sesungguhnya ia dan suku bangsanya melihat kamu dari tempat yang kamu tidak dapat melihat mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=964">964</a><span>]</span><span> Sesungguhnya </span><span>[a]</span><span> Kami jadikan syaitan-syaitan itu sahabat-sahabat bagi orang-orang yang tidak beriman.</span></p><p><span>[a]</span><span> 2:258 ; 3:176 ; 16:101.</span></p>');
INSERT INTO `quran` VALUES (7336,2,7,29,'<p><span>Dan, apabila mereka mengerjakan suatu kekejian, mereka berkata, “Kami mendapati bapak-bapak kami demikian dan Allah Memerintahkan kami seperti itu pula.” Katakanlah, “Sesunguhnya Allah sekali-kali tidak memerintahkan berbuat </span><span>[b]</span><span> keji. Apakah kamu mengatakan terhadap Allah apa yang tidak kamu ketahui ?”</span></p><p><span>[b]</span><span> 16:91.</span></p>');
INSERT INTO `quran` VALUES (7337,2,7,30,'<p><span>Katakanlah, “Tuhan-ku memerintahkan berbuat </span><span>[a]</span><span> adil. Dan, pusatkanlah perhatianmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=965">965</a><span>]</span><span> di setiap tempat ibadah, dan serulah Dia dengan mengikhlaskan keita</span><span>’</span><span>atan kepada-Nya. Sebagaimana Dia mendiptakan kamu permulaan kali, demikian pula kamu akan kembali </span><span>kepada-Nya.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=966">966</a><span>]</span><span>”.</span></p><p><span>[a]</span><span> 4:59 ; 16:91 ; 57:26.</span></p>');
INSERT INTO `quran` VALUES (7338,2,7,31,'<p><span>[b]</span><span> Satu golongan telah Dia beri petunjuk dan segolongan lain telah pasti atas mereka kesesatan. Sesungguhnya mereka itu mengambil syaitan-syaitan menjadi sahabat-sahabat selain Allah, dan mereka menduga bahwa mereka telah mendapat petunjuk.</span></p><p><span>[b]</span><span> 16:37 ; 22:19.</span></p>');
INSERT INTO `quran` VALUES (7339,2,7,32,'<p><span>Wahai Bani Adam, pakailah perhiasanmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=967">967</a><span>]</span><span> di setiap tempat ibadah dan makanlah serta minumlah tetapi </span><span>[a]</span><span> jangan berlebihan, sesungguhnya, Dia tidak mencintai orang-orang yang berlebih-lebihan.</span></p><p><span>[a]</span><span> 17:28 ; 25:68.</span></p>');
INSERT INTO `quran` VALUES (7340,2,7,33,'<p><span>Katakanlah, “Siapakah yang mengharamkan perhiasan Allah yang dikeluarkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=968">968</a><span>]</span><span> untuk hamba-hamba-Nya dan </span><span>[b]</span><span> barang-barang yang baik dari rezeki ? “ Katakanlah, “Barang-barang itu bagi orang-orang yang beriman di dalam kehidupan dunia ini </span><span>akan</span><span> dikhususkan pada Hari Kiamat. Demikian Kami jelaskan Tanda-tanda bagi kaum yang berpengetahuan.”</span></p><p><span>[b]</span><span> 2:169, 173 ; 23:52.</span></p>');
INSERT INTO `quran` VALUES (7341,2,7,34,'<p><span>Katakanlah, </span><span>[c]</span><span> “Tuhan-ku hanya mengharamkan perbuatan-perbuatan keji, apa-apa yang nyata darinya dan yang tersembunyi, dan dosa dan pelanggaran tanpa hak, dan bahwa </span><span>[d]</span><span> kamu mempersekutukan sesuatu dengan Allah; Dia tidak menurunkan suatu dalil pun, dan kamu mengatakan terhadap Allah apa yang tidak kamu ketahui.”</span></p><p><span>[c]</span><span> 6:152.</span></p><p><span>[d]</span><span> 3:152 ; 7:72 ; 22:72.</span></p>');
INSERT INTO `quran` VALUES (7342,2,7,35,'<p><span>Dan bagi </span><span>[a]</span><span> tiap-tiap umat ada batas waktu ; maka, apabila telah datang batas waktunya, tidak dapat mereka mengundurkan barang sesaat pun dan tidak pula dapat memajukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=969">969</a><span>]</span></p><p><span>[a]</span><span> 10:50 ; 15:6 ; 16:62 ; 35:46.</span></p>');
INSERT INTO `quran` VALUES (7343,2,7,36,'<p><span>Wahai Bani Adam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=970">970</a><span>]</span><span> jika datang kepadamu </span><span>[b]</span><span> rasul-rasul dari antaramu yang memperdengarkan Ayat-ayat-Ku kepadamu, maka barangsiapa bertakwa dan memperbaiki diri, tidak akan ada ketakutan menimpa mereka dan tidak pula mereka akan bersedih hati.</span></p><p><span>[b]</span><span> 2:39 ; 20:124.</span></p>');
INSERT INTO `quran` VALUES (7344,2,7,37,'<p><span>Tetapi, </span><span>[c]</span><span> orang-orang yang mendustakan Ayat-ayat Kami dan dengan sombong berpaling darinya, mereka itu penghuni Api ; mereka akan tinggal lama di dalamnya.</span></p><p><span>[c]</span><span> 2:40 ; 5:11, 87 ; 6:50 ; 7:41 ; 22:58.</span></p>');
INSERT INTO `quran` VALUES (7345,2,7,38,'<p><span>Maka, </span><span>[a]</span><span> siapakah yang lebih aniaya dari orang yang mengada-ada dusta terhadap Allah atau mendustakan Ayat-ayat-Nya ? Mereka ini akan memperoleh bagian mereka sebagaimana telah ditetapkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=971">971</a><span>]</span><span> Hingga apabila datang kepada mereka utusan-utusan Kami untuk mencabut nyawa mereka </span><span>seraya </span><span> berkata, </span><span>[b]</span><span> “Di manakah yang pernah kamu seru selain Allah ? “ Mereka menjawab, “Mereka telah hilang dari kami.” Dan mereka akan </span><span>[c]</span><span> memberi kesaksian terhadap diri mereka sendiri bahwa mereka dahulu orang-orang kafir.</span></p><p><span>[a]</span><span> 6:22 ; 10:18 ; 11:19 ; 61:8.</span></p><p><span>[b]</span><span> 6:23 ; 40:74 </span><span>–</span><span> 75.</span></p><p><span>[c]</span><span> 6:131.</span></p>');
INSERT INTO `quran` VALUES (7346,2,7,39,'<p><span>Dia berfirman, “Masuklah kamu ke dalam Api bersama umat-umat jin dan manusia yang telah berlalu sebelummu.” Setiap kali suatu umat masuk, umat itu akan mengutuk saudara-saudaranya, hingga apabila mereka semua telah tiba di dalamnya, maka berkatalah mereka yang terakhir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=972">972</a><span>]</span><span> kepada mereka yang terdahulu, “Ya Tuhan kami, mereka ini telah menyesatkan kami, karena itu berilah mereka </span><span>[a]</span><span> azab Api berlipat ganda.” Berfirman Dia, “Bagi masing-masing </span><span>mendapat azab </span><span> berlipat ganda, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=973">973</a><span>]</span><span> akan tetapi kamu tidak mengetahui.”</span></p><p><span>[a]</span><span> 38:62.</span></p>');
INSERT INTO `quran` VALUES (7347,2,7,40,'<p><span>Dan, berkata mereka yang terdahulu kepada mereka yang terakhir, “Tak ada bagimu suatu kelebihan di atas kami ; maka, rasakanlah azab itu disebabkan oleh apa yang telah kamu lakukan.”</span></p>');
INSERT INTO `quran` VALUES (7348,2,7,41,'<p><span>Sesungguhnya </span><span>[b]</span><span> orang-orang yang mendustakan Ayat-ayat Kami dan dengan sombongnya berpaling darinya, tidak akan dibukakan bagi mereka pintu-pintu langit </span><span>rohani</span><span> dan tidak pula mereka akan masuk surga sebelum unta masuk ke lubang jarum. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=974">974</a><span>]</span><span> Dan, demikianlah Kami memberi balasan kepada orang-orang berdosa.</span></p><p><span>[b]</span><span> Lihat 7:37.</span></p>');
INSERT INTO `quran` VALUES (7349,2,7,42,'<p><span>[a]</span><span> Bagi mereka ada tempat tidur di Jahannam dan di atas mereka ada selimut, Dan, demikianlah Kami memberi balasan kepada orang-orang aniaya.</span></p><p><span>[a]</span><span> 39:17.</span></p>');
INSERT INTO `quran` VALUES (7350,2,7,43,'<p><span>Dan orang-orang yang beriman dan beramal shaleh, </span><span>[b]</span><span> Kami tidak membebankan seseorang kecuali sesuai dengan kemampuannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=975">975</a><span>]</span><span> mereka inilah penghuni surga ; mereka akan kekal di dalamnya.</span></p><p><span>[b]</span><span> 2:234, 287 ; 6:153 ; 7:43 ; 23:63.</span></p>');
INSERT INTO `quran` VALUES (7351,2,7,44,'<p><span>Dan Kami akan </span><span>[c]</span><span> mencabut segala dendam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=976">976</a><span>]</span><span> yang ada di dalam dada mereka. </span><span>[d]</span><span> di bawah mereka akan mengalir sungai-sungai dan mereka akan berkata, </span><span>[e]</span><span> “Segala puji bagi Allah Yang telah menunjuki kami kepada </span><span>surga</span><span> ini. Dan kami tidak akan mendapat petunjuk andaikata Allah tidak memberi petunjuk kepada kami. Sesungguhnya telah datang rasul-rasul Tuhan kami membawa kebenaran.” Dan, akan diserukan kepada mereka, “Inlah surga yang diwariskan kepadamu </span><span>sebagai ganjaran</span><span> atas apa-apa yang telah kamu kerjakan.”</span></p><p><span>[c]</span><span> 15:48.</span></p><p><span>[d]</span><span> Lihat 2:26.</span></p><p><span>[e]</span><span> 10:11 ; 39:75.</span></p>');
INSERT INTO `quran` VALUES (7352,2,7,45,'<p><span>Dan, penghuni surga akan berseru kepada penghuni neraka, “Sesungguhnya telah kamu dapati apa yang Tuhan kami telah janjikan kepada kami itu benar. Maka, adakah kamu mendapati pula apa yang telah dijanjikan tuhanmu itu benar ? “ Berkata mereka, “Ya !” Lalu berserulah seorang penyeru di antara mereka, “ Laknat Allah atas orang-orang aniaya.</span></p>');
INSERT INTO `quran` VALUES (7353,2,7,46,'<p><span>[a]</span><span> Yang menghalangi orang-orang dari jalan Allah dan mereka menghendaki jalan itu bengkok, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=977">977</a><span>]</span><span> dan mereka itu orang-orang yang ingkar kepada akhirat.”</span></p><p><span>[a]</span><span> 7:87 ; 11:20 ; 14:4 ; 16:89.</span></p>');
INSERT INTO `quran` VALUES (7354,2,7,47,'<p><span>Dan di antara keduanya, ada tabir. Dan di atas Tempat-tempat Tinggi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=978">978</a><span>]</span><span> </span><span>di surga</span><span> ada orang-orang laki-laki yang akan mengenal semuanya dengan tanda-tanda di wajah mereka. Dan mereka akan berseru kepada penghuni surga, “Selamat sejatera bagimu.” Mereka itu belum lagi masuk ke dalamnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=979">979</a><span>]</span><span> namun mereka sangat berhasrat </span><span>memasukinya.</span><span> </span></p>');
INSERT INTO `quran` VALUES (7355,2,7,48,'<p><span>Dan, apabila pandangan mata mereka dialihkan ke arah penghuni Api, mereka akan berkata, “Hai Tuhan kami, </span><span>[a]</span><span> janganlah Engkau menjadikan kami termasuk orang-orang aniaya.”</span></p><p><span>[a]</span><span> 23:95.</span></p>');
INSERT INTO `quran` VALUES (7356,2,7,49,'<p><span>Dan, penghuni A</span><span>’</span><span>raf berseru kepada beberapa orang laki-laki yang dikenal mereka dengan tanda-tanda di wajah mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=980">980</a><span>]</span><span> </span><span>sambil</span><span> berkata, “Tidak ada faedahnya bagimu bilangan mu dan tidak pula apa yang kamu sombongkan itu.”</span></p>');
INSERT INTO `quran` VALUES (7357,2,7,50,'<p><span>[a]</span><span> “Orang-orang inikah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=981">981</a><span>]</span><span> yang kamu bersumpah bahwa Allah tidak akan menyampaikan rahmat kepada mereka ?” </span><span>Allah berfirman</span><span>, “Masuklah kamu ke dalam surga ; tak akan ada ketakutan atas diri kamu dan tidak pula kamu akan bersedih hati.”</span></p><p><span>[a]</span><span> 23:111.</span></p>');
INSERT INTO `quran` VALUES (7358,2,7,51,'<p><span>Dan, penghuni neraka akan berseru kepada penghuni surga, “Tuangkanlah kepada kami sedikit air atau sedikit dari apa yang Allah telah rezekikan kepadamu.” Mereka akan menjawab,“ Sesungguhnya, Allah mengharamkan kedua-duanya atas orang-orang kafir”</span></p>');
INSERT INTO `quran` VALUES (7359,2,7,52,'<p><span>[a]</span><span> “Orang-orang yang telah menjadikan agamanya sebagai olok-olok dan main-main, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=982">982</a><span>]</span><span> dan telah memperdayakan mereka kehidupan dunia.” Maka pada hari ini </span><span>[b]</span><span> Kami akan melupakan mereka, sebagaimana mereka, telah melupakan pertemuan pada hari mereka ini dan karena mereka selalu membantah Ayat-ayat Kami.</span></p><p><span>[a]</span><span> 5:58 ; 6:71. </span></p><p><span>[b]</span><span> 45:35.</span></p>');
INSERT INTO `quran` VALUES (7360,2,7,53,'<p><span>Dan, sesungguhnya </span><span>[c]</span><span> Kami telah memberikan kepada mereka sebuah Kitab yang telah Kami jelaskan sesuai dengan pengetahuan Kami, sebagai petunjuk dan rahmat bagi kaum beriman.</span></p><p><span>[c]</span><span> 6:115 ; 10:58 ; 12:112 ; 16:90 ; 29:52.</span></p>');
INSERT INTO `quran` VALUES (7361,2,7,54,'<p><span>[a]</span><span> Apakah mereka hanya menantikan penyempurnaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=983">983</a><span>]</span><span> ta</span><span>’</span><span>wilnya ? Pada hari penyem-purnaan itu tiba, akan berkata orang-orang yang dahulu melupakannya, “Sesungguhnya rasul-rasul kami telah datang membawa kebenaran. Maka, adakah bagi kami pemberi-pemberi syafaat supaya mereka dapat menyampaikan syafaat untuk kami ? Atau, </span><span>[b]</span><span> dapat kah kami dikembalikan supaya kami beramal yang lain selain apa yang telah kami amalkan ? “ Sebenarnya mereka telah merugikan diri mereka sendiri, dan lenyaplah dari mereka apa yang telah mereka ada-adakan.</span></p><p><span>[a]</span><span> 2:211 ; 6:159.</span></p><p><span>[b]</span><span> 26:103 ; 35:38 ; 39:59.</span></p>');
INSERT INTO `quran` VALUES (7362,2,7,55,'<p><span>Sesungguhnya, Tuhan-mu ialah Allah, Yang </span><span>[c]</span><span> menciptakan seluruh langit dan bumi dalam enam masa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=984">984</a><span>]</span><span> kemudian Dia bersemayam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=985">985</a><span>]</span><span> di atas singgasana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=986">986</a><span>]</span><span> Dia </span><span>menjadi kan </span><span> malam </span><span>[d]</span><span> menutupi siang yang mengejarnya dengan cepat. Dan, </span><span>Dia menciptakan </span><span> matahari dan bulan dan bintang-bintang, untuk tunduk kepada perintah-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=987">987</a><span>]</span><span> Ingatlah , penciptaan dan perintah adalah wewenang-Nya. Maha Berberkatlah Allah Tuhan sekalian alam.</span></p><p><span>[c]</span><span> 10:4 ; 11:8 ; 25:60 ; 32:5 ; 41:10, 13 ; 50:39 ; 57:5.</span></p><p><span>[d]</span><span> 13:4 , 36 , 38.</span></p>');
INSERT INTO `quran` VALUES (7363,2,7,56,'<p><span>[a]</span><span> Berdoalah kepada Tuhan-mu dengan rendah hati dan dengan suara rendah . Sesungguhnya, Dia tidak mencintai orang-orang yang melampui batas.</span></p>');
INSERT INTO `quran` VALUES (7364,2,7,57,'<p><span>Dan, janganlah kamu berbuat kerusuhan di muka bumi sesudah perbaikannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=988">988</a><span>]</span><span> dan </span><span>[a]</span><span> berdoalah kepada-Nya dengan </span><span>rasa </span><span>takut dan </span><span>penuh</span><span> harap. Sesungguhnya, rahmat Allah dekat kepada orang-orang yang berbuat kebaikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=989">989</a><span>]</span></p><p><span>[a]</span><span> 21:91; 32:17.</span></p>');
INSERT INTO `quran` VALUES (7365,2,7,58,'<p><span>Dan, </span><span>[a]</span><span> Dia-lah Yang mengirimkan angin sebagai pembawa kabar suka sebelum datang rahmat-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=990">990</a><span>]</span><span> hingga apabila </span><span>angin</span><span> itu membawa awan yang berat, Kami menghalaunya ke suatu daerah yang mati. Maka Kami menurunkan air darinya, lalu dengan itu Kami mengeluarkan segala macam buah-buahan. Demikianlah Kami mengeluarkan orang-orang mati </span><span>rohani</span><span> supaya kamu mengambil pelajaran.</span></p><p><span>[a]</span><span> 15:23; 24:44; 25:49; 27:64; 30:47; 35:10.</span></p>');
INSERT INTO `quran` VALUES (7366,2,7,59,'<p><span>Dan, negeri yang baik, menumbuhkan tumbuh-tumbuhan atas perintah Tuhan-nya ; dan </span><span>negeri</span><span> yang buruk, tidak menumbuhkan kecuali yang jelek. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=991">991</a><span>]</span><span> Demikianlah Kami berulang</span><span>–</span><span>ulang menjelaskan Tanda-tanda bagi kaum yang bersyukur.</span></p>');
INSERT INTO `quran` VALUES (7367,2,7,60,'<p><span>Sesungguhnya Kami mengutus </span><span>[a]</span><span> Nuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=992">992</a><span>]</span><span> kepada kaumnya dan ia berkata, “Hai kaumku sembahlah Allah, tiada tuhan bagimu selain Dia. Sesungguhnya, aku khawatir atas dirimu akan azab Hari yang besar.</span></p><p><span>[a]</span><span> 11:26 </span><span>–</span><span> 27 ; 23:24.</span></p>');
INSERT INTO `quran` VALUES (7368,2,7,61,'<p><span>Berkatalah </span><span>[a]</span><span> pemuka-pemuka kaumnya, “Sesungguhnya, kami melihat engkau berada di dalam kesesatan yang nyata.”</span></p><p><span>[a]</span><span> 11:28 ; 23:25 </span><span>–</span><span> 26.</span></p>');
INSERT INTO `quran` VALUES (7369,2,7,62,'<p><span>Ia berkata, “Hai kaumku, </span><span>[b]</span><span> tak ada kesesatan padaku melainkan aku seorang rasul dari Tuhan sekalian alam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=993">993</a><span>]</span></p><p><span>[b]</span><span> 7:69, 80 ; 46:24.</span></p>');
INSERT INTO `quran` VALUES (7370,2,7,63,'<p><span>[c]</span><span> “Aku menyampaikan kepadamu Amanat-amanat Tuhan-ku dan aku menasehati kamu, dan aku mengetahui dari Allah apa yang tidak kamu ketahui.</span></p><p><span>[c]</span><span> 7:69, 80 ; 46:24.</span></p>');
INSERT INTO `quran` VALUES (7371,2,7,64,'<p><span>[d]</span><span> “Apakah kamu merasa heran bahwa telah datang kepadamu suatu Kalam penuh nasihat dari Tuhan-mu dengan perantaraan seorang laki-laki di antara kamu agar dia memperingatkanmu dan supaya kamu bertakwa dan mudah-mudahan kamu diberi rahmat ?”</span></p><p><span>[d]</span><span> 7:70 ; 10:3 ; 38:5 ; 50:3.</span></p>');
INSERT INTO `quran` VALUES (7372,2,7,65,'<p><span>Tetapi </span><span>[a]</span><span> mereka masih mendustakannya, maka Kami menyelamatkannya dan orang-orang yang besertanya dalam bahtera ; dan Kami menenggelamkan orang-orang yang mendustakan Ayat-ayat Kami. Sesungguhnya, mereka adalah kaum yang buta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=994">994</a><span>]</span></p><p><span>[a]</span><span> 7:73 ; 26:120 </span><span>–</span><span> 121.</span></p>');
INSERT INTO `quran` VALUES (7373,2,7,66,'<p><span>Dan </span><span>[b]</span><span> kepada </span><span>‘</span><span>Ad </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=995">995</a><span>]</span><span> </span><span>Kami utus</span><span> saudara mereka, Hud. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=996">996</a><span>]</span><span> Berkatalah ia, “Hai kaumku, sembahlah Allah tiada tuhan bagimu selain Dia. Tidakkah kamu bertakwa ?”</span></p><p><span>[b]</span><span> 11:51 ; 46:22.</span></p>');
INSERT INTO `quran` VALUES (7374,2,7,67,'<p><span>Berkata pemuka-pemuka yang ingkar dari kaumnya, </span><span>[c]</span><span> “Sesungguhnya kami melihat engkau berada di dalam kebodohan, dan sesungguhnya kami menganggap engkau termasuk di antara para pendusta.”</span></p><p><span>[c]</span><span> 41:16.</span></p>');
INSERT INTO `quran` VALUES (7375,2,7,68,'<p><span>Berkata ia, </span><span>[a]</span><span> “Hak kaumku, tidak ada padaku kebodohan melainkan aku ini seorang Rasul dari Tuhan semesta alam;</span></p><p><span>[a]</span><span> 7:62</span></p>');
INSERT INTO `quran` VALUES (7376,2,7,69,'<p><span>[b]</span><span> “Aku menyampaikan kepadamu amanat-amanat Tuhan-ku, dan aku bagimu seorang penasihat yang terpercaya.</span></p><p><span>[b]</span><span> 7:63, 80 ; 46:24.</span></p>');
INSERT INTO `quran` VALUES (7377,2,7,70,'<p><span>[c]</span><span> “Apakah kamu merasa heran bahwa telah datang kepadamu suatu nasihat dari Tuhan-mu dengan perantaraan seorang laki-laki dari antara kamu supaya ia memperingatkan kamu ? Dan, ingatlah ketika </span><span>[d]</span><span> Dia menjadikan kamu pengganti-pengganti </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=997">997</a><span>]</span><span> sesudah kaum Nuh ,dan melebihkan kamu dalam kekuatan jasmani. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=997a">997a</a><span>]</span><span> Maka, ingatlah nikmat-nikmat Allah supaya kamu berjaya.”</span></p><p><span>[c]</span><span> 7:64 ; 10:3 ; 38:5 ; 50:3.</span></p><p><span>[d]</span><span> 6:166 ; 7:75, 130 ; 10:15.</span></p>');
INSERT INTO `quran` VALUES (7378,2,7,71,'<p><span>Mereka berkata, </span><span>[e]</span><span> “Adakah engkau datang kepada kami supaya kami hanya menyembah Allah saja, dan </span><span>supaya</span><span> kami meninggalkan apa yang disembah oleh bapak-bapak kami ? Maka, datangkanlah kepada kami apa yang engkau ancamkan kepada kami, jika engkau termasuk orang-orang benar.”</span></p><p><span>[e]</span><span> 10:79 ; 11:63, 88.</span></p>');
INSERT INTO `quran` VALUES (7379,2,7,72,'<p><span>Berkata ia, “Sesungguhnya telah menimpa atasmu dari Tuhan-mu siksaan dan kemurkaan. </span><span>[a]</span><span> Apakah kamu berbantah dengan-ku tentang nama-nama yang kamu berikan, kamu dan bapak-bapakmu, sedangkan Allah tidak menurunkan sebuah dalil pun. Maka tungguhlah, sesungguhnya </span><span>[b]</span><span> aku pun besertamu termasuk orang-orang yang menunggu.”</span></p><p><span>[a]</span><span> 3:152 ; 7:34 ; 22:72 ; 53:24.</span></p><p><span>[b]</span><span> 10:21, 103 ; 11:123.</span></p>');
INSERT INTO `quran` VALUES (7380,2,7,73,'<p><span>Maka, </span><span>[c]</span><span> Kami menyelamatkan dia dan orang-orang yang besertanya dengan rahmat dari Kami, dan Kami potong sampai ke akarnya orang-orang yang mendustakan Ayat-ayat Kami. Dan mereka bukanlah orang-orang beriman.</span></p><p><span>[c]</span><span> 7:165 ; 26:120 </span><span>–</span><span> 121.</span></p>');
INSERT INTO `quran` VALUES (7381,2,7,74,'<p><span>Dan, kepada </span><span>[a]</span><span> Tsamud </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=998">998</a><span>]</span><span> </span><span>Kami utus</span><span> saudara mereka, Shaleh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=999">999</a><span>]</span><span> Ia berkata, “Hai kaumku, sembahlah Allah, tiada tuhan bagimu selain Dia. Sesungguhnya telah datang kepadamu satu dalil yang nyata dari Tuhan-mu, </span><span>[b]</span><span> ini adalah unta-betina Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1000">1000</a><span>]</span><span> suatu Tanda bagimu. Maka, biarkanlah dia makan di bumi Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1001">1001</a><span>]</span><span> dan janganlah kamu menyentuhnya dengan menyakiti, maka akan menimpa kamu azab pedih.</span></p><p><span>[a]</span><span> 11:62 ; 27:46.</span></p><p><span>[b]</span><span> 7:78 ; 11:65 ; 17:60 ; 26:156 ; 24:28 ; 91:14.</span></p>');
INSERT INTO `quran` VALUES (7382,2,7,75,'<p><span>“Dan, ingatlah saat </span><span>[a]</span><span> ketika Dia menjadikanmu pengganti sesudah kaum </span><span>‘</span><span>Ad, dan Dia menempatkanmu di bumi ; kamu mendirikan istana-istana di atas tanah-tanah datarnya dan </span><span>[b]</span><span> kamu memahat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1002">1002</a><span>]</span><span> gunung-gunung </span><span>untuk dibuat </span><span> rumah-rumah. Maka, ingatlah nikmat-nikmat Allah dan janganlah kamu merajalela di bumi dengan membuat kerusuhan.”</span></p><p><span>[a]</span><span> 6:166 ; 7:70, 130 ; 10:15</span></p><p><span>[b]</span><span> 15:83 ; 26:150.</span></p>');
INSERT INTO `quran` VALUES (7383,2,7,76,'<p><span>Berkata pemuka-pemuka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1003">1003</a><span>]</span><span> kaumnya yang sombong kepada orang-orang yang dianggap mereka lemah, </span><span>yakni</span><span> orang-orang beriman di antara mereka, “Adakah kalian mengetahui bahwa Shaleh itu orang yang diutus oleh Tuhannya ?” Berkata mereka, “Sesungguhnya, kepada apa yang dia diutus untuk menyampaikannya kami beriman.”</span></p>');
INSERT INTO `quran` VALUES (7384,2,7,77,'<p><span>Berkata orang-orang yang sombong itu, “Sesungguhnya kepada apa yang kamu imani dengannya, kami tidak percaya.”</span></p>');
INSERT INTO `quran` VALUES (7385,2,7,78,'<p><span>Lalu, </span><span>[a]</span><span> mereka </span><span>karena marahnya,</span><span> memotong urat keting kaki unta betina itu dan mendurhakai perintah Tuhan mereka serta berkata, “Hai Shaleh ! Datangkanlah kepada kami apa yang engkau ancamkan kepada kami, jika engkau sungguh di antara orang-orang yang diutus.”</span></p><p><span>[a]</span><span> Lihat 7:74.</span></p>');
INSERT INTO `quran` VALUES (7386,2,7,79,'<p><span>Karena itu mereka ditimpa </span><span>[b]</span><span> gempa bumi. Mereka berjatuhan tersungkur di dalam rumah-rumah mereka.</span></p><p><span>[b]</span><span> 7:92 ; 11:68 ; 15:84 ; 26:159.</span></p>');
INSERT INTO `quran` VALUES (7387,2,7,80,'<p><span>Lalu, </span><span>Shaleh</span><span> berpaling dari mereka dan berkata, </span><span>[a]</span><span> “Hai kaumku, sesungguhnya aku telah menyampaikan kepadamu Amanat Tuhan-ku, dan aku telah memberi nasehat kepadamu, tetapi kamu tidak menyukai orang-orang pemberi nasihat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1004">1004</a><span>]</span></p><p><span>[a]</span><span> 7:63, 69 ; 46:24.</span></p>');
INSERT INTO `quran` VALUES (7388,2,7,81,'<p><span>Dan, </span><span>[b]</span><span> </span><span>Kami mengutus pula </span><span> Luth. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1005">1005</a><span>]</span><span> Ketika ia berkata kepada kaumnya, “Mengapa kamu melakukan perbuatan keji yang tidak pernah dikerjakan sebelummu oleh seorang pun di seluruh alam ini ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1006">1006</a><span>]</span></p><p><span>[b]</span><span> 27:55 ; 29:29.</span></p>');
INSERT INTO `quran` VALUES (7389,2,7,82,'<p><span>“Sesungguhnya, </span><span>[c]</span><span> kamu mendatangi orang-orang laki-laki selain dari perempuan-perempuan </span><span>guna melampiaskan</span><span> hawa nafsumu. Benar-benar kamu kaum yang melewati batas.”</span></p><p><span>[c]</span><span> 26:166 ; 27:56 ; 29:30.</span></p>');
INSERT INTO `quran` VALUES (7390,2,7,83,'<p><span>Dan </span><span>[a]</span><span> jawaban kaumnya tiada lain melainkan berkata, “Usirlah mereka dari kotamu ; karena sesungguhnya mereka itu manusia-manusia yang membanggakan diri suci.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1007">1007</a><span>]</span></p><p><span>[a]</span><span> 27:57.</span></p>');
INSERT INTO `quran` VALUES (7391,2,7,84,'<p><span>Maka, </span><span>[b]</span><span> Kami menyelamatkan dia serta keluarganya, kecuali istrinya ; ia termasuk orang-orang yang tertinggal di belakang.</span></p><p><span>[b]</span><span> 26:171 </span><span>–</span><span> 172 ; 27:58 ; 29:34 ; 37:135 </span><span>–</span><span> 136.</span></p>');
INSERT INTO `quran` VALUES (7392,2,7,85,'<p><span>Dan, </span><span>[c]</span><span> Kami mencurahkan di atas mereka hujan </span><span>batu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1008">1008</a><span>]</span><span> Maka, lihatlah betapa akibatnya orang-orang berdosa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1009">1009</a><span>]</span></p><p><span>[c]</span><span> 26:174 ; 27:59.</span></p>');
INSERT INTO `quran` VALUES (7393,2,7,86,'<p><span>[d]</span><span> Dan, </span><span>Kami utus pula</span><span> kepada Madyan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1010">1010</a><span>]</span><span> saudara mereka Syu</span><span>’</span><span>aib. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1011">1011</a><span>]</span><span> Ia berkata, “Hai kaumku, sembahlah Allah bagimu tidak ada tuhan selain Dia. Sesungguhnya, telah datang kepadamu Tanda yang nyata dari Tuhan-mu. Maka, </span><span>[a]</span><span> penuhilah ukuran dan timbangan dan janganlah kamu merugikan manusia atas barang-barang mereka ; dan janganlah kamu menimbulkan kerusuhan di muka bumi setelah diperbaiki keadaannya. Hal demikian itu lebih baik bagimu, jika kamu orang-orang mukmin.</span></p><p><span>[d]</span><span> 11:85 ; 29:37.</span></p><p><span>[a]</span><span> 6:153 ; 11:86.</span></p>');
INSERT INTO `quran` VALUES (7394,2,7,87,'<p><span>“Dan, janganlah kamu duduk di tiap-tiap jalan, mengancam dan </span><span>[b]</span><span> menghalangi dari jalan Allah orang-orang beriman kepada-Nya, dan kamu menginginkannya bengkok. Dan, </span><span>[c]</span><span> ingatlah ketika kamu dahulu sedikit, lalu Dia memperbanyak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1012">1012</a><span>]</span><span> kamu. Dan, lihatlah bagaimana akibat orang-orang yang berbuat kerusuhan itu !</span></p><p><span>[b]</span><span> 7:46 ; 11:20 ; 14:4 ; 16:89.</span></p><p><span>[c]</span><span> 3:124 ; 7:27.</span></p>');
INSERT INTO `quran` VALUES (7395,2,7,88,'<p><span>“Dan, jika ada segolongan di antaramu beriman kepada apa yang dengannya aku diutus, sedangkan segolongan </span><span>lain</span><span> tidak beriman, maka bersabarlah kamu hingga Allah memberi keputusan di antara kita dan Dia-lah sebaik-baik hakim.”</span></p>');
INSERT INTO `quran` VALUES (7396,2,7,89,'<p><span>Juz IX</span></p><p><span>Berkata </span><span>[a]</span><span> pemuka-pemuka kaumnya yang sombong, “Pasti akan kami usir engkau, hai Syu</span><span>’</span><span>aib, dan </span><span>juga</span><span> orang-orang yang telah beriman beserta engkau dari kota kamu ; atau, kamu harus kembali ke dalam agama kami.” Berkata ia, “Walaupun kami tidak suka ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1013">1013</a><span>]</span></p><p><span>[a]</span><span> 14:14.</span></p>');
INSERT INTO `quran` VALUES (7397,2,7,90,'<p><span>“Sesungguhnya kami telah mengada-ada dusta terhadap Allah, andaikata kami kembali ke dalam agamamu setelah Allah menyelamatkan kami darinya. Dan, tidaklah pantas bagi kami kembali kepadanya kecuali Allah, Tuhan kami menghendaki. Ilmu </span><span>[a]</span><span> Tuhan kami meliputi segala sesuatu. Kepada Allah kami bertawakal. Ya Tuhan kami, tetapkanlah keputusan di antara kami dan kaum kami dengan hak dan Engkau-lah Pemberi Keputusan yang sebaik-baiknya.</span></p><p><span>[a]</span><span> 2:256 ; 40:8.</span></p>');
INSERT INTO `quran` VALUES (7398,2,7,91,'<p><span>Dan, pemuka-pemuka yang ingkar dari kaumnya berkata, “Jika kamu mengikuti Syu</span><span>’</span><span>aib, niscaya kamu akan menjadi orang-orang yang rugi.”</span></p>');
INSERT INTO `quran` VALUES (7399,2,7,92,'<p><span>Maka, mereka ditimpa </span><span>[b]</span><span> gempa bumi, Maka mereka berjatuhan tersungkur di dalam rumah-rumah mereka.</span></p><p><span>[b]</span><span> 7:79 ; 11:68 ; 15:84 ; 26:159.</span></p>');
INSERT INTO `quran` VALUES (7400,2,7,93,'<p><span>Orang-orang yang mendustakan Syu</span><span>’</span><span>aib, seolah-olah mereka tidak pernah tinggal di dalamnya. Orang-orang yang mendustakan Syu</span><span>’</span><span>aib, mereka itu orang-orang yang rugi.</span></p>');
INSERT INTO `quran` VALUES (7401,2,7,94,'<p><span>Maka, ia berpaling dari mereka seraya berkata, </span><span>[a]</span><span> “Hai kaumku ! Sesungguhnya telah kesampaikan kepadamu Amanat-amanat Tuhan-ku, dan sudah kuberikan nasihat kepadamu. Maka, betapa aku bersedih hati terhadap kaum kafir.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1014">1014</a><span>]</span></p><p><span>[a]</span><span> 7:69 , 80 ; 46:24. </span></p>');
INSERT INTO `quran` VALUES (7402,2,7,95,'<p><span>R. 12</span></p><p><span>Dan, Kami tidak pernah mengutus seorang nabi pun kepada suatu negeri, kecuali </span><span>[b]</span><span> Kami timpakan kepada pendduknya penderitaan dan kesengsaraan supaya mereka merendahkan diri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1015">1015</a><span>]</span></p><p><span>[b]</span><span> 6:43.</span></p>');
INSERT INTO `quran` VALUES (7403,2,7,96,'<p><span>Kemudian, Kami mengubah </span><span>keadaan buruk mereka</span><span> menjadi baik sehingga mereka mendapat kemajuan ; dan mereka berkata, “Sesungguhnya kesengsaraan maupun kesenangan pernah dialami bapak-bapak kami. “ Lalu, Kami timpakan </span><span>azab</span><span> kepada mereka dengan tiba-tiba, sedangkan mereka tidak menyadari.</span></p>');
INSERT INTO `quran` VALUES (7404,2,7,97,'<p><span>Dan, </span><span>[a]</span><span> jika sekiranya penduduk kota-kota beriman dan bertakwa, niscaya akan Kami bukakan pintu keberkatan dari langit dan bumi bagi mereka ; akan tetapi, mereka telah mendustakan. Maka, Kami timpakan </span><span>azab</span><span> kepada mereka diseabkan oleh apa-apa yang mereka usahakan.</span></p><p><span>[a]</span><span> 2:104 ; 5:66.</span></p>');
INSERT INTO `quran` VALUES (7405,2,7,98,'<p><span>Apakah penduduk negeri-negeri merasa aman dari </span><span>[b]</span><span> kedatangan siksaan Kami kepada mereka di malam hari selagi mereka tidur ? </span></p><p><span>[b]</span><span> 7:5.</span></p>');
INSERT INTO `quran` VALUES (7406,2,7,99,'<p><span>Ataukah penduduk negeri-negeri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1016">1016</a><span>]</span><span> merasa aman dari </span><span>[c]</span><span> kedatangan siksaan Kami kepada mereka, waktu matahari naik sepenggalah sementara mereka sedang bermain-main?</span></p><p><span>[c]</span><span> 7:5</span></p>');
INSERT INTO `quran` VALUES (7407,2,7,100,'<p><span>Adakah mereka merasa aman dari rencana Allah ? Maka, tak ada yang merasa dirinya aman dari rencana Allah kecuali kaum yang rugi.</span></p>');
INSERT INTO `quran` VALUES (7408,2,7,101,'<p><span>[a]</span><span> Apakah tidak menjadi petunjuk bagi orang-orang yang mewarisi bumi sesudah penduduknya </span><span>dihancurkan, </span><span> bahwa jika Kami menghandaki, niscaya Kami siksa mereka karena dosa-dosa mereka, dan Kami </span><span>[b]</span><span> memeterai hati mereka sehingga mereka tidak dapat mendengar </span><span>petunjuk yang benar.</span></p><p><span>[a]</span><span> 20:129 ; 32:27.</span></p><p><span>[b]</span><span> 10:75 ; 16:109 ; 45:24.</span></p>');
INSERT INTO `quran` VALUES (7409,2,7,102,'<p><span>Inilah negeri-negeri yang Kami ceritakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1017">1017</a><span>]</span><span> kepada engkau sebagian dari kisah-kisahnya. Dan, sesungguhnya, telah datang kepada mereka </span><span>[c]</span><span> rasul-rasul mereka dengan Tanda-tanda yang nyata. Akan tetapi, mereka tidak juga beriman kepada apa-apa yang telah mereka dustakan sebelumnya. Demikianlah Allah memeterai </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1018">1018</a><span>]</span><span> hati orang-orang kafir.</span></p><p><span>[c]</span><span> 3:185 ; 5:33.</span></p>');
INSERT INTO `quran` VALUES (7410,2,7,103,'<p><span>Dan, Kami tidak mendapati kebanyakan mereka </span><span>menepati </span><span> janji, bahkan sesungguhnya Kami mendapati kebanyakan mereka orang-orang fasik.</span></p>');
INSERT INTO `quran` VALUES (7411,2,7,104,'<p><span>Kemudian, </span><span>[a]</span><span> Kami mengutus sesudah mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1019">1019</a><span>]</span><span> Musa dengan Tanda-tanda Kami kepada Firaun dan pemuka-pemukanya ; tetapi mereka berlaku aniaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1020">1020</a><span>]</span><span> dengannya. Maka, lihatlah bagaimana akibat orang-orang yang berbuat kerusuhan.</span></p><p><span>[a]</span><span> 17:102 ; 28:37 ; 43:47.</span></p>');
INSERT INTO `quran` VALUES (7412,2,7,105,'<p><span>Dan, </span><span>[b]</span><span> Musa berkata, “Hai Firaun, sesungguhnya aku seorang rasul dari Tuhan semesta alam “;</span></p><p><span>[b]</span><span> 26:17 ; 20:48 ; 43:47.</span></p>');
INSERT INTO `quran` VALUES (7413,2,7,106,'<p><span>“Sebenarnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1021">1021</a><span>]</span><span> aku tidak mengatakan sesuatu terhadap Allah kecuali yang hak. Sesungguhnya, aku datang kepadamu dengan Tanda-kebenaran yang nyata dari Tuhan-mu ; maka, </span><span>[c]</span><span> biarkanlah Bani Israil </span><span>pergi</span><span> bersamaku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1022">1022</a><span>]</span></p><p><span>[c]</span><span> 20:48 ; 26:18.</span></p>');
INSERT INTO `quran` VALUES (7414,2,7,107,'<p><span>Berkatalah </span><span>Firaun,</span><span> </span><span>[a]</span><span> “Jika sungguh engkau datang dengan suatu Tanda, maka kemukakanlah itu, jika engkau sungguh termasuk orang-orang yang benar.”</span></p><p><span>[a]</span><span> 26:32.</span></p>');
INSERT INTO `quran` VALUES (7415,2,7,108,'<p><span>[b]</span><span> Maka </span><span>Musa</span><span> melemparkan tongkatnya ; lalu tiba-tiba</span><span> tongkat</span><span> itu menjadi seekor ular yang nyata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1023">1023</a><span>]</span></p><p><span>[b]</span><span> 20:21 ; 26:33 ; 27:11 ; 28:32.</span></p>');
INSERT INTO `quran` VALUES (7416,2,7,109,'<p><span>Dan, </span><span>[a]</span><span> ia mengerluarkan tangannya maka tiba-tiba </span><span>tangan</span><span> itu </span><span>nampak</span><span> putih bagi orang-orang yang menyaksikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1024">1024</a><span>]</span></p><p><span>[a]</span><span> 26:34 ; 27:13 ; 28:33.</span></p>');
INSERT INTO `quran` VALUES (7417,2,7,110,'<p><span>Berkata </span><span>[a]</span><span> pemuka-pemuka kaum Fir</span><span>’</span><span>aun, “Sesungguhnya </span><span>orang </span><span> ini tukang sihir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1025">1025</a><span>]</span><span> yang pintar” ;</span></p><p><span>[a]</span><span> 20:64 ; 26:35.</span></p>');
INSERT INTO `quran` VALUES (7418,2,7,111,'<p><span>[b]</span><span> “Ia bermaksud mengeluarkan kamu dari negerimu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1026">1026</a><span>]</span><span> maka bagaimana pendapatmu ?”</span></p><p><span>[b]</span><span> 20:64 ; 26:36.</span></p>');
INSERT INTO `quran` VALUES (7419,2,7,112,'<p><span>[c]</span><span> Mereka berkata, “Tahanlah dia dan saudaranya dan kirimlah ke kota-kota beberapa pencanang.</span></p><p><span>[c]</span><span> 26:37.</span></p>');
INSERT INTO `quran` VALUES (7420,2,7,113,'<p><span>[d]</span><span> “Yang harus membawa kepada engkau setiap tukang sihir yang pintar.”</span></p><p><span>[d]</span><span> 26:38.</span></p>');
INSERT INTO `quran` VALUES (7421,2,7,114,'<p><span>Dan, </span><span>[a]</span><span> datanglah sejumlah tukang sihir kepada Fir</span><span>’</span><span>aun, mereka berkata, “Sudah tentu kami akan diberi imbalan jika kami menang.”</span></p><p><span>[a]</span><span> 26:42.</span></p>');
INSERT INTO `quran` VALUES (7422,2,7,115,'<p><span>Berkatalah </span><span>[b]</span><span> ia, “Ya ! Malahan kamu pun tentu akan termasuk orang-orang yang dekat </span><span>kepadaku.</span><span> “</span></p><p><span>[b]</span><span> 26:43.</span></p>');
INSERT INTO `quran` VALUES (7423,2,7,116,'<p><span>Berkatalah </span><span>[c]</span><span> mereka, “Hai Musa, apakah engkau yang akan </span><span>pertama</span><span> melempar atau kami yang harus menjadi pelempar ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1027">1027</a><span>]</span></p><p><span>[c]</span><span> 20:66.</span></p>');
INSERT INTO `quran` VALUES (7424,2,7,117,'<p><span>Berkata ia, “Lemparkanlah olehmu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1028">1028</a><span>]</span><span> Maka tatkala mereka </span><span>[d]</span><span> melemparkan, mereka menyihir mata orang-orang dan membuat mereka itu takut dan mereka menampilkan sihir yang hebat.</span></p><p><span>[d]</span><span> 20:67 ; 26:45.</span></p>');
INSERT INTO `quran` VALUES (7425,2,7,118,'<p><span>Dan, Kami mewahyukan kepada Musa, </span><span>[e]</span><span> Lemparkanlah tongkat engkau ! “ Maka tiba-tiba tongkat itu menelan apa-apa yang disihir mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1029">1029</a><span>]</span></p><p><span>[e]</span><span> 20:70 ; 26:46.</span></p>');
INSERT INTO `quran` VALUES (7426,2,7,119,'<p><span>Maka tegaklah hak dan batallah apa yang telah mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (7427,2,7,120,'<p><span>Dengan demikian mereka dikalahkan di situ dan kembalilah mereka terhina. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1030">1030</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7428,2,7,121,'<p><span>Dan, [a] tukang-tukang sihir itu jatuh bersujud. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1031">1031</a><span>]</span></p><p><span>[a]</span><span> 20:71 ; 26:47.</span></p>');
INSERT INTO `quran` VALUES (7429,2,7,122,'<p><span>[a]</span><span> Mereka berkata, “Kami beriman kepada Tuhan semesta alam.”</span></p><p><span>[a]</span><span> 20:71 ; 26:48.</span></p>');
INSERT INTO `quran` VALUES (7430,2,7,123,'<p><span>[b]</span><span> “Tuhan-nya Musa dan Harun.”</span></p><p><span>[b]</span><span> 20:71 ; 26:49.</span></p>');
INSERT INTO `quran` VALUES (7431,2,7,124,'<p><span>Berkata </span><span>[c]</span><span> Fir</span><span>’</span><span>aun, “Kalin sudah beriman kepadanya sebelum kalian aku beri izin. Sungguh, aku kira ini tentu tipu-daya yang telah kalian rencanakan alam kota supaya kalian dapat mengeluarkan penduduknya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1032">1032</a><span>] </span><span>dari kota, maka segera akan kaian ketahui.</span></p><p><span>[c]</span><span> 20:72 ; 26:50</span></p>');
INSERT INTO `quran` VALUES (7432,2,7,125,'<p><span>[d]</span><span> “Pasti akan aku potong tangan kalian dan kaki kalian karena pembangkangan. Kemudian, pasti akan aku salib kalian semuanya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1033">1033</a><span>]</span></p><p><span>[d]</span><span> 20:72 ; 26:50.</span></p>');
INSERT INTO `quran` VALUES (7433,2,7,126,'<p><span>[e]</span><span> Mereka menjawab, “Sesungguhnya, kepada Tuhan kami-lah kami akan kembali;</span></p><p><span>[e]</span><span> 20:73 ; 26:51.</span></p>');
INSERT INTO `quran` VALUES (7434,2,7,127,'<p><span>“Dan, tidaklah </span><span>[a]</span><span> engkau menuntut balas dari kamu melainkan karena kami telah beriman kepada Tanda-tanda Tuhan kami tatkala Tanda-tanda itu datang kepada kami. Ya Tuhan kami, limpahkanlah kesabaran kepada kami dan wafatkanlah kami </span><span>dalam keadaan </span><span> menyerah kan diri.”</span></p><p><span>[a]</span><span> 20:74.</span></p>');
INSERT INTO `quran` VALUES (7435,2,7,128,'<p><span>Dan, berkata pemuka-pemuka Firaun, “Akan engkau biarkankah Musa dan kaumnya membuat kekacauan di bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1034">1034</a><span>]</span><span> dan meninggalkan engkau dan tuhan-tuhan engkau ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1035">1035</a><span>]</span><span> Ia menjawab, </span><span>[b]</span><span> “Kami akan membunuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1036">1036</a><span>]</span><span> anak-anak lelaki mereka dan akan membiarkan hidup perempuan-perempuan mereka. Dan sesungguhnya kami berkuasa atas mereka.”</span></p><p><span>[b]</span><span> 2:50 ; 7:142 ; 14:7 ; 28:5.</span></p>');
INSERT INTO `quran` VALUES (7436,2,7,129,'<p><span>Berkata </span><span>[a]</span><span> Musa kepada kaumnya, “Mohonlah pertolongan kepada Allah dan bersabarlah. Sesungguhnya, bumi ini kepunyaan Allah Dia mewariskannya kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya dan kesudahan itu bagi orang-orang yang bertakwa.”</span></p><p><span>[a]</span><span> 2:46. 154.</span></p>');
INSERT INTO `quran` VALUES (7437,2,7,130,'<p><span>Mereka berkata, “Kami disusahkan sebelum engkau datang kepada kami dan sesudah engkau datang kepada kami.” Ia menjawab, </span><span>[b]</span><span> “Mudah-mudahan Tuhan-mu akan membinasakan musuhmu dan menjadikan kamu pengganti di atas bumi, maka Dia akan melihat bagaimana kamu berbuat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1037">1037</a><span>]</span></p><p><span>[b]</span><span> 10:14, 15.</span></p>');
INSERT INTO `quran` VALUES (7438,2,7,131,'<p><span>Dan sesungguhnya, </span><span>[c]</span><span> Kami telah menghukum kaum Fir</span><span>’</span><span>aun dengan tahun-tahun paceklik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1038">1038</a><span>]</span><span> dan kekurangan buah-buahan, supaya mereka mengambil pelajaran.</span></p><p><span>[c]</span><span> 17:102.</span></p>');
INSERT INTO `quran` VALUES (7439,2,7,132,'<p><span>Maka, apabila datang kepada mereka kemakmuran, mereka berkata, “Ini bagi kami.” Dan, </span><span>[a]</span><span> jika mereka ditimpa kesusahan, mereka menuduhkan keburukan itu kepada Musa dan orang-orang yang menyertainya. Ingatlah, sesungguhnya keburukan mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1039">1039</a><span>]</span><span> ada di sisi Allah Akan tetapi, kebanyakan mereka tidak mengetahui.</span></p><p><span>[a]</span><span> 27:48 ; 36:19.</span></p>');
INSERT INTO `quran` VALUES (7440,2,7,133,'<p><span>Dan, mereka berkata, “Tanda apa pun yang engkau bawa kepada kami untuk menyihir kami dengannya, maka </span><span>[b]</span><span> kami sekali-kali tidak akan beriman kepada engkau.”</span></p><p><span>[b]</span><span> 10:79.</span></p>');
INSERT INTO `quran` VALUES (7441,2,7,134,'<p><span>Lalu, Kami </span><span>[a]</span><span> mengirimkan kepada mereka taufan, belalang, kutu, katak, dan darah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1040">1040</a><span>]</span><span> </span><span>sebagai</span><span> Tanda-tanda yang jelas ; tetapi, mereka berlaku sombong dan jadilah mereka kaum yang berdosa.</span></p><p><span>[a]</span><span> 17:102 ; 43:49.</span></p>');
INSERT INTO `quran` VALUES (7442,2,7,135,'<p><span>Dan, </span><span>[b]</span><span> bilamana siksaan menimpa mereka, mereka berkata, “Hai Musa, mohonkanlah kepada Tuhan engkau untuk kami sesuai dengan apa yang Dia telah janjikan kepada engkau. Jika engkau dapat menyingkirkan siksaan ini dari kami, pasti kami akan beriman kepada engkau dan tentu kami akan mengirimkan bersama engkau kaum Bani Israil.”</span></p><p><span>[b]</span><span> 43:50.</span></p>');
INSERT INTO `quran` VALUES (7443,2,7,136,'<p><span>Maka </span><span>[c]</span><span> ketika Kami telah menyingkirkan siksaan itu dari mereka hingga suatu jangka waktu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1041">1041</a><span>]</span><span> yang mereka akan sampai kepadanya, tiba-tiba mereka mengingkari janji.</span></p><p><span>[c]</span><span> 43:50.</span></p>');
INSERT INTO `quran` VALUES (7444,2,7,137,'<p><span>Maka, Kami </span><span>[a]</span><span> menuntut balas dari mereka dan Kami menenggelamkan mereka ke dalam laut, karena mereka dahulu mendutakan Tanda-tanda Kami dan terhadapnya mereka lalai.</span></p><p><span>[a]</span><span> 43:56.</span></p>');
INSERT INTO `quran` VALUES (7445,2,7,138,'<p><span>Dan, </span><span>[b]</span><span> Kami menjadikan kaum yang dipandang lemah itu ahli waris bumi sebelah timur dan sebelah baratnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1042">1042</a><span>]</span><span> yang Kami berkati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1043">1043</a><span>]</span><span> di dalamnya. Dan, sempurnalah firman Tuhan engkau yang </span><span>[c]</span><span> baik terhadap Bani Israil karena mereka bersabar, Dan, Kami hancur kan segala apa yang telah dibangun oleh Fir</span><span>’</span><span>aun serta kaumnya, begitu pula segala apa yang telah mereka dirikan.</span></p><p><span>[b]</span><span> 28:6</span></p><p><span>[c]</span><span> 32:25.</span></p>');
INSERT INTO `quran` VALUES (7446,2,7,139,'<p><span>Dan, Kami bawa Bani Israil menyeberangi laut ; lalu, sampailah mereka kepada suatu kaum yang menyembah berhala-berhala mereka. Berkata mereka, “Hai Musa, buatkanlah untuk kami sembahan seperti mereka mempunyai sembahan-sembahan.” Berkata ia, “Sungguh, kamu kaum yang bodoh.”</span></p>');
INSERT INTO `quran` VALUES (7447,2,7,140,'<p><span>“Sesungguhnya, pekerjaan yang mereka sibuk di dalamnya itu akan dihancurkan dan akan sia-sialah apa yang mereka kerjakan.”</span></p>');
INSERT INTO `quran` VALUES (7448,2,7,141,'<p><span>[a]</span><span> Ia berkata, “Haruskah aku mencarikan bagimu suatu sembahan selain Allah padahal Dia telah </span><span>[b]</span><span> melebihkan kamu di atas sekalian alam ?”</span></p><p><span>[a]</span><span> 6:15, 165.</span></p><p><span>[b]</span><span> 2:48 ; 3:34.</span></p>');
INSERT INTO `quran` VALUES (7449,2,7,142,'<p><span>Dan, </span><span>[c]</span><span> </span><span>Ingatlah</span><span> ketika Kami menyelamatkan kamu dari kaum Fir</span><span>’</span><span>aun yang menimpakan azab yang buruk kepadamu. Mereka membunuh anak-anak lelakimu dan membiarkan hidup wanita-wanita-mu. Dan di dalam yang demikian ada suatu cobaan besar bagimu dari Tuhan-mu.</span></p><p><span>[c]</span><span> 2:50 ; 7:128 ; 14:7 ; 28:5.</span></p>');
INSERT INTO `quran` VALUES (7450,2,7,143,'<p><span>Dan, </span><span>[d]</span><span> Kami menjanjikan kepada Musa tiga puluh malam dan Kami menggenapkan malam itu dengan sepuluh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1044">1044</a><span>]</span><span> maka sempurnalah waktu </span><span>yang dijanjikan</span><span> Tuhan-nya empat puluh malam. Dan, berkata Musa kepada sauranya, Harun, “Wakililah aku di kalangan kaumku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1045">1045</a><span>]</span><span> dan perbaikilah </span><span>mereka</span><span> dan janganlah engkau mengikuti jalan orang-orang yang menimbulkan kekacauan.”</span></p><p><span>[d]</span><span> 2:52.</span></p>');
INSERT INTO `quran` VALUES (7451,2,7,144,'<p><span>Dan </span><span>[a]</span><span> ketika Musa datang pada waktu yang Kami tetapkan dan Tuhan-nya bercakap-cakap dengannya, berkatalah ia, “Ya Tuhan-ku, tampakkanlah kepadaku supaya aku dapat melihat Engkau.” Berfirman </span><span>Dia</span><span>, “Sekali-kali engkau tidak akan dapat melihat Aku. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1046">1046</a><span>]</span><span> Tetapi, pandanglah gunung itu ; maka, jika ia tetap ada pada tempatnya, maka engkau akan dapat melihat Aku.” Maka, tatkala Tuhan-nya menampakkan Diri-Nya diatas gunung itu, Dia menjadikannya hancur lebur, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1047">1047</a><span>]</span><span> dan Musa pun jatuh pingsan. Maka, ketika ia sadar kembali, berkatalah ia “Mahasuci Engkau, aku tobat kepada Engkau dan aku orang pertama di antara orang-orang beriman.”</span></p><p><span>[a]</span><span> 2:254 ; 4:165.</span></p>');
INSERT INTO `quran` VALUES (7452,2,7,145,'<p><span>Dia berfirman, “Hai Musa, sesungguhnya Aku telah memilih engkau di atas umat manusia dengan Risalat-Ku dan Firman-Ku. Maka pegang-teguhlah apa yang telah Aku berikan kepada engkau dan jadilah engkau termasuk orang-orang yang bersyukur.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1048">1048</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7453,2,7,146,'<p><span>Dan, Kami menuliskan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1049">1049</a><span>]</span><span> baginya dia atas alwah</span><span>*)</span><span> segala sesuatu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1050">1050</a><span>]</span><span> berupa nasihat dan </span><span>[a]</span><span> penjelasan mengenai segala sesuatu. “Maka, peganglah segala hal itu dengan teguh dan suruhlah kaum engkau mengambil yang terbaik darinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1051">1051</a><span>]</span><span> Segera akan Aku perlihatkan kepadamu tempat kediaman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1052">1052</a><span>]</span><span> orang- orang durhaka.”</span></p><p><span>[a]</span><span> 6:155.</span></p>');
INSERT INTO `quran` VALUES (7454,2,7,147,'<p><span>Segera Aku akan memalingkan dari Ayat-ayat-Ku orang-orang yang menyombongkan diri di bumi tanpa hak ; dan, </span><span>[a]</span><span> jika mereka melihat setiap Tanda, mereka tidak akan beriman kepadanya. Dan, jika mereka melihat jalan petunjuk, mereka tidak akan mengambilnya sebagai jalan. Dan, jika mereka melihat jalan kesesatan, mereka akan mengambilnya sebagai jalan. Hal demikian itu disebabkan mereka mendustakan Tanda-tanda Kami dan mereka itu lalai darinya.</span></p><p><span>[a]</span><span> 6:26.</span></p>');
INSERT INTO `quran` VALUES (7455,2,7,148,'<p><span>Dan, </span><span>[b]</span><span> orang-orang yang telah mendustakan Tanda-tanda Kami dan pertemuan akhirat, sia-sialah amalan mereka. Mereka </span><span>hanya</span><span> mendapatkan ganjaran sesuai amal mereka.</span></p><p><span>[b]</span><span> 3:12 ; 5:11 ; 7:37 ; 21:78.</span></p>');
INSERT INTO `quran` VALUES (7456,2,7,149,'<p><span>Dan, sepeninggalnya, </span><span>[a]</span><span> kaum Musa telah membuat dari barang-barang perhiasan mereka anak sapi, jasad </span><span>tanpa nyawa</span><span> yang bersuara tanpa arti. </span><span>[b]</span><span> Tidakkah mereka memperlihatkan bahwa </span><span>jasad</span><span> itu tidak dapat berkata-kata </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1053">1053</a><span>]</span><span> dengan mereka dan tidak memberi petunjuk mereka </span><span>kepada</span><span> suatu jalan ? Mereka menjadikannya </span><span>sebagai sembahan</span><span> dan mereka itu orang-orang aniaya.</span></p><p><span>[a]</span><span> 2:52, 93 ; 4:154 ; 7:153 ; 20:89.</span></p><p><span>[b]</span><span> 20:90.</span></p>');
INSERT INTO `quran` VALUES (7457,2,7,150,'<p><span>Dan, tatkala mereka menyesal </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1054">1054</a><span>]</span><span> dan melihat bahwa mereka sungguh telah sesat, mereka berkata, “Andaikata Tuhan kami tidak mengasihani kami dan tidak mengampuni kami, tentulah kami akan menjadi di antara orang-orang yang rugi.</span></p>');
INSERT INTO `quran` VALUES (7458,2,7,151,'<p><span>Dan, ketika </span><span>[a]</span><span> kembali Musa kepada kaumnya dengan marah dan sedih, ia berkata, “Alangkah buruknya apa yang kamu kerjakan sepeninggalku sebagai wakilku. Apakah kamu hendak mendahului perintah Tuhan-mu ? “ Lalu, ia meletakkan alwah itu dan merenggut kepada saudaranya seraya menariknya kepada dirinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1055">1055</a><span>]</span><span> Berkata </span><span>Harun</span><span>, </span><span>[b]</span><span>”Hai anak ibuku ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1056">1056</a><span>]</span><span> Sesungguhnya kaum ini memandang aku lemah dan hampir mereka membunuhku. Maka, janganlah engkau membiarkan musuh-musuhku mengejek aku, dan janganlah engkau menganggap aku termasuk kaum aniaya.”</span></p><p><span>[a]</span><span> 20:87.</span></p><p><span>[b]</span><span> 20:95.</span></p>');
INSERT INTO `quran` VALUES (7459,2,7,152,'<p><span>Berkata </span><span>Musa</span><span>, “Ya Tuhan-ku, ampunilah diriku dan saudaraku, dan masukkanlah kami ke dalam rahmat Engkau, dan Engkau-lah Yang Maha Penyayang di antara segala penyayang.”</span></p>');
INSERT INTO `quran` VALUES (7460,2,7,153,'<p><span>Sesungguhnya, </span><span>[a]</span><span> orang-orang yang telah menjadikan anak sapi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1057">1057</a><span>]</span><span> </span><span>untuk sembahan,</span><span> kelak akan menimpa mereka kemurkaan dari Tuhan mereka dan kehinaan di dalam kehidupan dunia. Dan, demikianlah Kami mengganjar orang-orang yang mengada-ada kedustaan.</span></p><p><span>[a]</span><span> 2:52, 93 ; 4:154 ; 7:149 ; 20:89.</span></p>');
INSERT INTO `quran` VALUES (7461,2,7,154,'<p><span>Tetapi, </span><span>[b]</span><span> orang-orang yang mengerjakan keburukan lalu mereka bertobat sesudah itu dan mereka beriman , sesungguhnya sesudah itu Tuhan Engkau adalah Maha Pengampun, Maha Penyayang.</span></p><p><span>[b]</span><span> 5:40 ; 16:120.</span></p>');
INSERT INTO `quran` VALUES (7462,2,7,155,'<p><span>Dan, tatkala kemarahan Musa sudah reda, ia mengambil alwah itu, dan </span><span>[c]</span><span> di dalam tulisannya ada petunjuk dan rahmat bagi mereka yang takut kepada Allah Tuhan mereka.</span></p><p><span>[c]</span><span> 5:45 ; 6:92.</span></p>');
INSERT INTO `quran` VALUES (7463,2,7,156,'<p><span>Dan, Musa memilih dari antara kaumnya tujuh puluh orang laki-laki untuk waktu yang Kami tetapkan. Tetapi, tatkala gempa bumi mengguncang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1057a">1057a</a><span>]</span><span> mereka, berkata </span><span>Musa,</span><span> “Ya Tuhan-ku, jika Engkau menghendaki, </span><span>[a]</span><span> Engkau dapat membinasakan mereka dan diriku sebelum</span><span>nya</span><span>. Apakah Engkau akan membinasakan kami disebabkan oleh apa yang diperbuat oleh orang-orang bodoh di antara kami ? Ini tiada lain melainkan suatu cobaan dari Engkau. Engkau menyesatkan dengan ini siapa yang Engkau kehendaki, dan Engkau memberi petunjuk kepada siapa yang Engkau kehendaki. Engkau-lah Pelindung kami. Maka, ampunilah kami dan kasihanilah kami dan Engkau-lah Yang sebaik-baik di antara para pengampun;</span></p><p><span>[a]</span><span> 13:28.</span></p>');
INSERT INTO `quran` VALUES (7464,2,7,157,'<p><span>“Dan, </span><span>[b]</span><span> tuliskanlah bagi kami kami kebaikan di dunia ini dan di akhirat. Sesungguhnya kami telah mendapat petunjuk untuk </span><span>kembali</span><span> kepada Engkau,” Berfirman Dia, “ </span><span>[c]</span><span> Aku akan timpakan azab-Ku kepada siapa yang Aku kehendaki, dan </span><span>[a]</span><span> rahmat-Ku meliputi segala sesuatu, Maka, Aku akan menuliskannya bagi orang-orang yang bertakwa dan mereka yang membayar zakat dan mereka yang beriman kepada Tanda-tanda Kami.”</span></p><p><span>[b]</span><span> 2:202.</span></p><p><span>[c]</span><span> 2:285 ; 5:41.</span></p><p><span>[a]</span><span> 40:8.</span></p>');
INSERT INTO `quran` VALUES (7465,2,7,158,'<p><span>“</span><span>Yaitu</span><span> orang-orang yang mengikuti Rasul. </span><span>[b]</span><span> Nabi Ummi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1058">1058</a><span>] [c]</span><span> yang mereka dapati tercantum di dalam Taurat dan Injil </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1059">1059</a><span>]</span><span> yang ada pada mereka. </span><span>[d]</span><span> Ia menyuruh mereka kapada yang ma</span><span>’</span><span>ruf dan melarang mereka dari yang mungkar dan menghalalkan bagi mereka segala yang baik dan mengharamkan bagi mereka segala yang buruk dan menyingkirkan dari mereka beban mereka dan belenggu-belenggu yang ada pada mereka. Maka, orang-orang yang telah beriman kepadanya, dan mendukungnya serta menolongnya dan mengikuti cahaya yang telah diturunkan besertanya, mereka itulah orang-orang yang berbahagia.”</span></p><p><span>[b]</span><span> 29:49 ; 42:53 ; 62:3.</span></p><p><span>[c]</span><span> 48:30.</span></p><p><span>[d]</span><span> Lihat 3:105.</span></p>');
INSERT INTO `quran` VALUES (7466,2,7,159,'<p><span>Katakanlah, “Hai manusia, </span><span>[a]</span><span> sesungguhnya aku Rasul Allah kepada kamu sekalian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1060">1060</a><span>]</span><span> dari Yang mempunyai kerajaan seluruh langit dan bumi. Tidak ada Tuhan selain Dia. </span><span>[b]</span><span> Dia menghidupkan dan mematikan. Maka berimanlah kepada Allah dan Rasul-Nya, Nabi Ummi yang beriman kepada Allah dan Kalimat-kalimat-Nya. Maka ikutilah dia supaya kamu mendapat petunjuk.”</span></p><p><span>[a]</span><span> 21:108 ; 25:2 ; 34:29.</span></p><p><span>[b]</span><span> 2:259 ; 23:81 ; 44:9. 57:3.</span></p>');
INSERT INTO `quran` VALUES (7467,2,7,160,'<p><span>Dan, </span><span>[a]</span><span> dari kaum Musa ada satu golongan yang memberi petunjuk dengan hak dan dengan itu mereka menegakkan keadilan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1061">1061</a><span>]</span></p><p><span>[a]</span><span> 7:82.</span></p>');
INSERT INTO `quran` VALUES (7468,2,7,161,'<p><span>Dan, </span><span>[b]</span><span> Kami membagi mereka ke dalam dua belas suku </span><span>berkembang menjadi</span><span> bangsa-bangsa. Dan, Kami mewahyukan kepada Musa ketika kaumnya </span><span>[c]</span><span> meminta air kepadanya, “Pukullah batu itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1601a">1601a</a><span>]</span><span> dengan tongkat engkau ;” lalu memancarlah darinya dia belas mata air ; tiap-tiap suku mengetahui tempat minumnya. Dan, </span><span>[d]</span><span> Kami menaungkan awan di atas mereka dan Kami menurunkan kepada mereka manna dan salwa. *) </span><span>Dan, Kami berkata</span><span> , “Makanlah segala yang baik yang telah Kami rezekikan kepadamu.” Dan mereka tidaklah menganiaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1062">1062</a><span>]</span><span> Kami, melainkan mereka menganiaya diri mereka sendiri.</span></p><p><span>[b]</span><span> 5:13.</span></p><p><span>[c]</span><span> 2:61.</span></p><p><span>[d]</span><span> 2:58 ; 20:81.</span></p><p><span>*) </span><span>manna </span><span> artinya cendawan dan </span><span>salwa</span><span> artinya burung puyuh ( Tafsir Shagir hal. 212 th. 1971 ).</span></p>');
INSERT INTO `quran` VALUES (7469,2,7,162,'<p><span>Dan, </span><span>ingatlah </span><span> </span><span>[a]</span><span> ketika dikatakan kepada mereka, “Tinggallah kamu di dalam kota ini dan makanlah darinya di mana saja kamu sukai dan katakanlah, </span><span>‘</span><span> </span><span>Ya Tuhan</span><span>, ringankanlah beban kami,</span><span>’</span><span> dan masukilah pintu</span><span>nya</span><span> dengan merendahkan diri, Kami akan mengampuni kesalahan-kesalahanmu, Kami kelak akan menambah </span><span>ganjaran</span><span> kepada orang-orang yang berbuat kebaikan.”</span></p><p><span>[a]</span><span> 2:59.</span></p>');
INSERT INTO `quran` VALUES (7470,2,7,163,'<p><span>Akan tetapi, </span><span>[b]</span><span> orang-orang aniaya di antara mereka, menukar ucapan itu dengan apa yang tidak dikatakan kepada mereka, lalu Kami mengirimkan kepada mereka azab dari langit di sebabkan mereka telah berbuat aniaya.</span></p><p><span>[b]</span><span> 2:60.</span></p>');
INSERT INTO `quran` VALUES (7471,2,7,164,'<p><span>Dan, tanyakanlah kepada mereka mengenai kota </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1063">1063</a><span>]</span><span> yang terletak di dekat laut. Tatkala mereka melanggar pada hari </span><span>[c]</span><span> Sabat, ketika datang kepada mereka ikan-ikan mereka pada hari Sabat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1064">1064</a><span>]</span><span> mereka bermunculan di permukaan air </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1064a">1064a</a><span>]</span><span> dan pada hari ketika mereka tidak merayakan Sabat, </span><span>ikan-ikan itu </span><span> tidak mendatangi mereka. Demikianlah Kami </span><span>[a]</span><span> mencobai mereka sebab mereka telah durhaka.</span></p><p><span>[c]</span><span> 2:66 ; 4:155.</span></p><p><span>[a]</span><span> 7:169.</span></p>');
INSERT INTO `quran` VALUES (7472,2,7,165,'<p><span>Dan, ketika segolongan di antara mereka berkata </span><span>kepada golongan lain</span><span> , “ Mengapa kamu menasihati kaum yang Allah akan membinasakan mereka atau mengazab mereka dengan azab yang hebat ?” Berkata mereka, “</span><span>Agar kami </span><span> mempunyai dalih di hadapan Tuhan-mu, dan supaya mereka bertakwa.”</span></p>');
INSERT INTO `quran` VALUES (7473,2,7,166,'<p><span>Maka, </span><span>[b]</span><span> tatkala mereka melupakan yang telah dinasihatkan kepada mereka, Kami menyelamatkan orang-orang yang melarang berbuat keburukan dan Kami menghukum orang-orang aniaya dengan azab yang mengerikan karena mereka telah durhaka.</span></p><p><span>[b]</span><span> 6:45</span></p>');
INSERT INTO `quran` VALUES (7474,2,7,167,'<p><span>Maka, ketika mereka melanggar apa yang telah dilarang bagi mereka melakukannya, Kami berkata kepada mereka, </span><span>[a]</span><span> “Jadilah kamu kera-kera yang hina ! “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1065">1065</a><span>]</span></p><p><span>[a]</span><span> 2:66 ; 5:61.</span></p>');
INSERT INTO `quran` VALUES (7475,2,7,168,'<p><span>Dan, </span><span>ingatlah</span><span> ketika Tuhan engkau mengumumkan bahwa niscaya </span><span>[b]</span><span> Dia pasti akan mengutus </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1066">1066</a><span>]</span><span> kepada mereka hingga Hari Kiamat, orang-orang yang akan menimpakan kepada mereka azab yang mengerikan. Sesungguhnya Tuhan engkau sangat cepat dalam menghukum </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1066a">1066a</a><span>]</span><span> dan sesungguhnya Dia, Maha Pengampun, Maha Penyayang.</span></p><p><span>[b]</span><span> 2:62 ; 3:113.</span></p>');
INSERT INTO `quran` VALUES (7476,2,7,169,'<p><span>Dan, Kami membagi-bagi mereka di bumi menjadi bangsa-bangsa. Di antara mereka ada orang-orang yang shaleh, dan di antara mereka ada yang tidak demikian, Dan, </span><span>[a]</span><span> Kami menguji mereka dengan rupa-rupa kebaikan dan keburukan supaya mereka kembali </span><span>kepada yang hak.</span><span> </span></p><p><span>[a]</span><span> 7:164.</span></p>');
INSERT INTO `quran` VALUES (7477,2,7,170,'<p><span>[b]</span><span> Kemudian, datang sesudah mereka suatu generasi </span><span>yang jahat</span><span> mewarisi Kitab itu. Mereka mengambil harta dunia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1067">1067</a><span>]</span><span> yang hina ini, dan mereka berkata, “Tentu kami akan diampuni.” Tetapi, jika datang kepada mereka harta semacam itu mereka akan mengambilnya. Bukankah telah diambil dari mereka perjanjian </span><span>dalam</span><span> Kitab bahwa mereka tidak akan mengatakan </span><span>sesuatu</span><span> terhadap Allah kecuali yang hak ? Dan, mereka telah memperlajari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1068">1068</a><span>]</span><span> apa yang tercantum di dalamnya. Dan </span><span>[c]</span><span> rumah akhirat adalah lebih baik bagi orang-orang yang bertakwa. Apakah kamu tidak mau mengerti ?</span></p><p><span>[b]</span><span> 19:60.</span></p><p><span>[c]</span><span> 6:33; 12:110.</span></p>');
INSERT INTO `quran` VALUES (7478,2,7,171,'<p><span>Dan, orang-orang yang </span><span>[a]</span><span> berpegang teguh pada Kitab itu dan dengan tetapnya mendirikan shalat. Sesungguhnya Kami tidak menyia-nyiakan ganjaran orang-orang yang mengadakan perbaikan.</span></p><p><span>[a]</span><span> 31:23.</span></p>');
INSERT INTO `quran` VALUES (7479,2,7,172,'<p><span>Dan, ketika Kami </span><span>[b]</span><span> mengangkat gunung di atas mereka seolah-olah menjadi naungan dan mereka menyangka bahwa gunung itu akan jatuh di atas mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1069">1069</a><span>]</span><span> </span><span>Kami berkata, </span><span>“Peganglah apa yang telah Kami berikan kepadamu dengan teguh dan ingatlah apa-apa yang ada di dalamnya, mudah-mudahan kamu bertakwa.”</span></p><p><span>[b]</span><span> 2:64.</span></p>');
INSERT INTO `quran` VALUES (7480,2,7,173,'<p><span>Dan, </span><span>ingatlah</span><span> ketika Tuhan engkau mengambil dari tulang sulbi bani Adam keturunan mereka dan menjadikan mereka saksi atas diri mereka sendiri, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1070">1070</a><span>]</span><span> </span><span>sambil berfirman,</span><span> </span><span>[c]</span><span> “Bukankah Aku Tuhan-mu ?” Mereka berkata, “Ya, benar, kami menjadi saksi,” supaya </span><span>jangan</span><span> kamu mengatakan pada Hari Kiamat, “Sesungguhnya kami dari hal ini lalai.”</span></p><p><span>[c]</span><span> 43:38.</span></p>');
INSERT INTO `quran` VALUES (7481,2,7,174,'<p><span>Atau , </span><span>jangan</span><span> kamu berkata, </span><span>[a]</span><span> “Sesungguhnya bapak-bapak kami dahulu yang berbuat syirik, sedangkan kami </span><span>hanyalah</span><span> keturunan sesudah mereka. Adakah Engkau akan membinasakan kami karena apa yang diperbuat oleh orang-orang pembohong itu ?”</span></p><p><span>[a]</span><span> 7:39.</span></p>');
INSERT INTO `quran` VALUES (7482,2,7,175,'<p><span>Dan, demikianlah Kami menjelaskan Tanda-tanda itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1071">1071</a><span>]</span><span> dan supaya mereka kembali </span><span>kepada yang hak.</span></p>');
INSERT INTO `quran` VALUES (7483,2,7,176,'<p><span>Dan, ceritakanlah kepada mereka kisah orang-orang yang telah Kami berikan kepadanya Tanda-tanda Kami, lalu ia melepaskan diri darinya ; maka syaitan mengikutinya dan jadilah ia di antara orang-orang yang sesat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1072">1072</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7484,2,7,177,'<p><span>Dan, jika Kami menghendaki, tentulah Kami meninggikannya dengan itu ; akan tetapi, ia cenderung ke bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1073">1073</a><span>]</span><span> dan mengikuti hawa nafsunya. Maka, keadaannya seperti seekor anjing </span><span>kehausan </span><span>; jika engkau menghalaunya ia menjulurkan lidahnya dan jika engkau membiarkannya ia menjulurkan juga lidahnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1074">1074</a><span>]</span><span> Demikianlah tamsilan orang-orang yang mendustakan Tanda-tanda Kami. Maka, kisahkanlah kisah </span><span>ini</span><span> supaya mereka merenungkan.</span></p>');
INSERT INTO `quran` VALUES (7485,2,7,178,'<p><span>Amanat buruk keadaan </span><span>[a]</span><span> orang-orang yang mendustakan Tanda-tanda Kami. Dan kepada diri mereka sendirilah mereka aniaya.</span></p><p><span>[a]</span><span> 2:12 ; 7:183 ; 8:55.</span></p>');
INSERT INTO `quran` VALUES (7486,2,7,179,'<p><span>[b]</span><span> Barangsiapa kepadanya Allah memberi petunjuk, maka dialah yang mendapat petunjuk. Dan barangsiapa Dia sesatkan, maka mereka itulah orang-orang yang rugi.</span></p><p><span>[b]</span><span> 17:98 ; 18:18.</span></p>');
INSERT INTO `quran` VALUES (7487,2,7,180,'<p><span>Dan, sesungguhnya Kami telah menciptakan untuk Jahannam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1075">1075</a><span>]</span><span> banyak di antara jin dan manusia. </span><span>[c]</span><span> Mereka mempunyai hati, </span><span>tetapi</span><span> dengan itu mereka tidak mengerti ; dan mereka mempunyai mata, tetapi dengan itu mereka tidak melihat ; dan mereka mempunyai telinga, tetapi dengan itu mereka tidak mendengar. </span><span>[d]</span><span> Mereka itu seperti binatang ; bahkan mereka lebih sesat. Mereka itulah orang-orang yang lalai.</span></p><p><span>[c]</span><span> 2:8 ; 22:47 ; 45:24.</span></p><p><span>[d]</span><span> 25:45.</span></p>');
INSERT INTO `quran` VALUES (7488,2,7,181,'<p><span>[a]</span><span> Dan kepunyaan Allah nama-nama sifat yang baik. Maka, serulah Dia dengan itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1076">1076</a><span>]</span><span> Dan, tinggalkanlah orang-orang yang menyimpang dari nama-nama sifat-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1077">1077</a><span>]</span><span> Mereka kelak akan dibalas menurut apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 17:111.</span></p>');
INSERT INTO `quran` VALUES (7489,2,7,182,'<p><span>Dan,</span><span>[b] </span><span>di antara </span><span>manusia</span><span> yang telah Kami ciptakan ada umat yang memberi petunjuk dengan hak dan dengan itu pula mereka menegakkan keadilan.</span></p><p><span>[b]</span><span> 7:160.</span></p>');
INSERT INTO `quran` VALUES (7490,2,7,183,'<p><span>Dan, </span><span>[c]</span><span> orang-orang yang mendustakan Tanda-tanda Kami, kalak akan Kami tarik mereka berangsur-angsur </span><span>kepada kebinasaan</span><span> dari tempat yang mereka tidak ketahui.</span></p><p><span>[c]</span><span> 3:12 ; 7:183 ; 8:55.</span></p>');
INSERT INTO `quran` VALUES (7491,2,7,184,'<p><span>Dan, </span><span>[d]</span><span> Aku memberi tenggang waktu kepada mereka ; sesungguhnya rencana-Ku amat teguh.</span></p><p><span>[d]</span><span> 3:179 ; 68:46.</span></p>');
INSERT INTO `quran` VALUES (7492,2,7,185,'<p><span>Apakah mereka tidak memikirkan </span><span>bahwa</span><span> sahabat mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1078">1078</a><span>]</span><span> itu </span><span>[a]</span><span> bukanlah orang gila ? Ia tiada lain melainkan seorang pemberi peringatan yang jelas;</span></p><p><span>[a]</span><span> 23:26 ; 34:47 ; 52:30 ; 81:23.</span></p>');
INSERT INTO `quran` VALUES (7493,2,7,186,'<p><span>[b]</span><span> Apakah mereka tidak memperhatikan kerajaan langit dan bumi dan segala sesuatu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1079">1079</a><span>]</span><span> yang telah diciptakan Allah ? Dan bahwa mungkin saat kehancuran mereka telah dekat ? </span><span>[c]</span><span> Maka, kepada perkataan apa lagi mereka akan percaya sesudah itu ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1080">1080</a><span>]</span></p><p><span>[b]</span><span> 6:76 ; 10:102.</span></p><p><span>[c]</span><span> 45:7 ; 77:51</span></p>');
INSERT INTO `quran` VALUES (7494,2,7,187,'<p><span>[d]</span><span> Barangsiapa yang telah disesatkan Allah, maka tak ada yang dapat memberi petunjuk kepadanya. Dan, </span><span>[e]</span><span> Dia akan membiarkan mereka kebingungan dalam kedurhakaan mereka.</span></p><p><span>[d]</span><span> 7:179 ; 17:98 ; 18:16.</span></p><p><span>[e]</span><span> 2:16 ; 6:111.</span></p>');
INSERT INTO `quran` VALUES (7495,2,7,188,'<p><span>[a]</span><span> Mereka bertanya kepada engkau tentang Kiamat, “Bilakah itu akan terjadi ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1081">1081</a><span>]</span><span> Katakanlah, </span><span>[b]</span><span> “Pengetahuan mengenai itu hanya ada pada sisi Tuhan-ku. Tak ada yang dapat menampakkannya pada waktunya kecuali Dia. Sangat berat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1082">1082</a><span>]</span><span> </span><span>Kiamat itu</span><span> di seluruh langit dan bumi. </span><span>[c]</span><span> Tidak akan datang kepadamu melainkan dengan tiba-tiba.” Mereka bertanya kepada engkau seolah-olah engkau benar-benar mengetahuinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1083">1083</a><span>]</span><span> katakanlah, “Pengetahuan mengenai itu hanya ada pada sisi Allah ; akan tetapi, kebanyakan manusia tidak mengetahui.”</span></p><p><span>[a]</span><span> 33:64 ; 78:2 ; 79:43.</span></p><p><span>[b]</span><span> 31:35 ; 43:86.</span></p><p><span>[c]</span><span> 16:78 ; 54:51.</span></p>');
INSERT INTO `quran` VALUES (7496,2,7,189,'<p><span>[d]</span><span> Katakanlah, “Aku tidak memiliki kekuasaan meraih kemanfaatan bagi diriku dan tidak pula kemudaratan, kecuali apa yang dikehendaki Allah Dan, sekiranya aku mengetahui hal gaib, niscaya aku telah meraih banyak kebaikan, dan keburukan tidak akan menjamahku. </span><span>[a]</span><span> Aku tidak lain hanyalah pemberi peringatan dan pemberi kabar suka kepada kaum yang beriman.”</span></p><p><span>[d]</span><span> 10:50 ; 72:22.</span></p><p><span>[a]</span><span> 2:120 ; 5:20 ; 11:3.</span></p>');
INSERT INTO `quran` VALUES (7497,2,7,190,'<p><span>[b]</span><span> Dia-lah yang telah menciptakan kamu dari satu jiwa dan darinya Dia menjadikan pasangannya </span><span>[c]</span><span> supaya ia mendapat ketenteraman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1084">1084</a><span>]</span><span> di dalam dirinya. Maka setelah dipergaulinya mengandunglah ia, suatu kandungan yang ringan lalu ia berjalan kian kemari dengan </span><span>kandungan</span><span> itu. Maka, tatkala </span><span>kandungannya </span><span> berat, kedua mereka itu berdoa kepada Allah, Tuhan mereka, “Andaikata Engkau memberi kami seirang </span><span>anak</span><span> yang shaleh niscaya kami akan menjadi di antara orang-orang yang bersyukur.”</span></p><p><span>[b]</span><span> 4:2 ; 16:73 ; 39:7.</span></p><p><span>[c]</span><span> 30:22.</span></p>');
INSERT INTO `quran` VALUES (7498,2,7,191,'<p><span>Akan tetapi, ketika Dia menganugerahkan kepada kedua mereka itu seorang </span><span>anak</span><span> yang shaleh, keduanya menjadikan sekutu-sekutu bagi-Nya berkenaan dengan apa yang telah dianugerahkan-Nya kepada kedua mereka itu. Mka Mahaluhur Allah di atas apa yang mereka persekutukan.</span></p>');
INSERT INTO `quran` VALUES (7499,2,7,192,'<p><span>Apakah </span><span>[a]</span><span> mereka mempersekutukan </span>Allah<span> dengan apa-apa yang tidak menciptakan sesuatu dan </span><span>bahkan</span><span> merekalah yang menciptakan ?</span></p><p><span>[a]</span><span> 16:21 ; 25:4.</span></p>');
INSERT INTO `quran` VALUES (7500,2,7,193,'<p><span>Dan, </span><span>[b]</span><span> mereka tidak mampu memberikan pertolongan kepada mereka dan tidak pula dapat menolong diri mereka sendiri.</span></p><p><span>[b]</span><span> 7:198 ; 21:44 ; 36:76.</span></p>');
INSERT INTO `quran` VALUES (7501,2,7,194,'<p><span>Dan, jika kamu </span><span>[c]</span><span> menyeru mereka kepada petunjuk, mereka tidak akan mengikuti kamu. Sama saja bagimu, baik kamu mengajak mereka atau kamu berdiam diri.</span></p><p><span>[c]</span><span> 7:199.</span></p>');
INSERT INTO `quran` VALUES (7502,2,7,195,'<p><span>Sesungguhnya, yang kamu seru selain Allah adalah hamba-hamba Allah seperti kamu juga. Maka, </span><span>[d]</span><span> serulah mereka agar mereka menjawab kepadamu, jika kamu memang orang-orang yang benar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1085">1085</a><span>]</span></p><p><span>[d]</span><span> 35:15.</span></p>');
INSERT INTO `quran` VALUES (7503,2,7,196,'<p><span>[a]</span><span> Apakah mereka berkaki yang dengan itu mereka dapat berjalan ; atau, apakah mereka bertangan yang dengan itu mereka dapat memegang ; atau, apakah mereka bermata yang dengan itu mereka dapat melihat ; atau, apakah mereka bertelinga yang dengan itu mereka dapat mendengar ? Katakanlah, “Panggillah sekutu- sekutumu, kemudian </span><span>[b]</span><span> rancanglah tipu-daya melawanku dan janganlah memberi tenggang waktu kepadaku. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1086">1086</a><span>]</span></p><p><span>[a]</span><span> 2:8 ; 22:47 ; 45:24.</span></p><p><span>[b]</span><span> 10:72 ; 11:56.</span></p>');
INSERT INTO `quran` VALUES (7504,2,7,197,'<p><span>“Sesungguhnya, </span><span>[c]</span><span> Pelindungan ialah Allah Yang telah menurunkan Kitab ini dan Dia melindungi orang-orang shaleh.</span></p><p><span>[c]</span><span> 45:20.</span></p>');
INSERT INTO `quran` VALUES (7505,2,7,198,'<p><span>“Dan, </span><span>[d] </span><span>yang kamu seru selain dari-Nya itu tidak akan mampu menolongmu dan tidak pula mereka dapat menolong diri mereka sendiri;”</span></p><p><span>[d]</span><span> 7:193 ; 21:44 ; 36:76.</span></p>');
INSERT INTO `quran` VALUES (7506,2,7,199,'<p><span>Dan, </span><span>[a]</span><span> jika kamu menyeru mereka kepada petunjuk, mereka tidak akan mendengar. Dan, </span><span>[b]</span><span> engkau melihat mereka memandang engkau padahal mereka tidak melihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1087">1087</a><span>]</span></p><p><span>[a]</span><span> 7:194.</span></p><p><span>[b]</span><span> 10:44.</span></p>');
INSERT INTO `quran` VALUES (7507,2,7,200,'<p><span>Hai Nabi</span><span>, </span><span>[c]</span><span> jadilah engkau pemaaf dan suruhlah orang beramal baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1087a">1087a</a><span>]</span><span> dan berpalinglah dari orang-orang jahil.</span></p><p><span>[c]</span><span> 3:160 ; 31:18.</span></p>');
INSERT INTO `quran` VALUES (7508,2,7,201,'<p><span>Dan, </span><span>[d]</span><span> jika suatu godaan syaitan menggoda engkau, maka mohonlah perlindungan kepada Allah Sesungguhnya Dia Maha Mendengar, Maha Mengetahui.</span></p><p><span>[d]</span><span> 41:37.</span></p>');
INSERT INTO `quran` VALUES (7509,2,7,202,'<p><span>Sesungguhnya, </span><span>[e]</span><span> orang-orang yang bertakwa apabila mereka digoda oleh suatu godaan daro syaitan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1088">1088</a><span>]</span><span> mereka ingat </span><span>kepada Allah</span><span> ; maka mereka mulai melihat </span><span>yang benar.</span></p><p><span>[e]</span><span> 3:136.</span></p>');
INSERT INTO `quran` VALUES (7510,2,7,203,'<p><span>Dan, saudara-saudara mereka yang </span><span>kafir</span><span> membiarkan mereka dalam kesesatan, lalu mereka tidak mengurangi </span><span>dalam usaha itu</span><span>.</span></p>');
INSERT INTO `quran` VALUES (7511,2,7,204,'<p><span>Dan, apabila engkau tidak membawa suatu Tanda kepada mereka, berkatalah mereka, “Mangapa engkau tidak mengada-ada sendiri </span><span>Tanda itu</span><span> ?” Katakanlah, </span><span>[a]</span><span> “Aku hanya mengikuti apa yang diwahyukan kepadaku dari Tuhan-ku. </span><span>[b]</span><span> Ini adalah dalil-dalil yang nyata </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1089">1089</a><span>] dari Tuhan-mu dan petunjuk serta rahmat bagi kaum yang beriman.”</span></p><p><span>[a]</span><span> 6:51.</span></p><p><span>[b]</span><span> 6:105 ; 17:103.</span></p>');
INSERT INTO `quran` VALUES (7512,2,7,205,'<p><span>Dan, </span><span>[c]</span><span> apabila Alquran dibacakan maka hendaklah kamu mendengarkannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1090">1090</a><span>]</span><span> dan diamlah agar kamu dikasihi.</span></p><p><span>[c]</span><span> 17:107.</span></p>');
INSERT INTO `quran` VALUES (7513,2,7,206,'<p><span>Dan, </span><span>[a]</span><span> ingatlah Tuhan engkau di dalam hati engkau dengan merendahkan diri dan rasa takut dan tanpa suara keras pada waktu pagi dan petang ; dan janganlah engkau termasuk orang-orang yang lalai.</span></p><p><span>[a]</span><span> 6:64 ; 7:56.</span></p>');
INSERT INTO `quran` VALUES (7514,2,7,207,'<p><span>Sesungguhnya, </span><span>[b]</span><span> orang-orang yang dekat kepada Tuhan engkau tidak berpaling dengan angkuhnya dari beribadah kepada-Nya, melainkan mereka </span><span>selalu</span><span> menyanjung kesucian-Nya dan kepada-Nya mereka bersujud. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1091">1091</a><span>]</span></p><p><span>[b]</span><span> 21:20 </span><span>–</span><span> 21 ; 41:39.</span></p>');
INSERT INTO `quran` VALUES (7515,2,8,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (7516,2,8,2,'<p><span>Mereka bertanya kepada engkau mengenai harta rampasan perang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1092">1092</a><span>]</span><span> Katakanlah, “Harta rampasan perang itu kepunyaan Allah dan Rasul-Nya. Maka bertakwalah kepada Allah dan perbaikilah hubungan di antara sesamamu dan </span><span>[b]</span><span> taatlah kepada Allah dan Rasulnya jika kamu orang-orang beriman.”</span></p><p><span>[b]</span><span> 3:3; 4:60 ; 8:47 ; 9:71 ; 24:55.</span></p>');
INSERT INTO `quran` VALUES (7517,2,8,3,'<p><span>[c]</span><span> Orang-orang mukmin ialah mereka yang apabila disebut </span><span>nama</span><span> Allah, gemetarlah hati mereka, dan </span><span>[d]</span><span> apabila Ayat-ayat-Nya ditilawatkan kepada mereka, bertambahlah keimanan mereka, dan kepada Tuhan merekalah, mereka bertawakal.</span></p><p><span>[c]</span><span> 22:36.</span></p><p><span>[d]</span><span> 9:124.</span></p>');
INSERT INTO `quran` VALUES (7518,2,8,4,'<p><span>[e]</span><span> Orang-orang yang tetap mendirikan shalat dan </span><span>[f]</span><span> membelanjakan </span><span>sebagian</span><span> dari apa yang Kami rezekikan kepada mereka.</span></p><p><span>[e]</span><span> 55:56 ; 9:71 ; 72:4 ; 31:5 ; 73:21.</span></p><p><span>[f]</span><span> Lihat 2:4.</span></p>');
INSERT INTO `quran` VALUES (7519,2,8,5,'<p><span>[a]</span><span> Mereka inilah orang-orang mukmin yang sebenarnya. Bagi mereka ada derajat-derajat </span><span>yang tinggi</span><span> di sisi Tuhan mereka dan ampunan serta rezeki yang mulia.</span></p><p><span>[a]</span><span> 8:75</span></p>');
INSERT INTO `quran` VALUES (7520,2,8,6,'<p><span>Hal itu</span><span> disebabkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1093">1093</a><span>]</span><span> Tuhan engkau telah mengeluarkan engkau dari rumah engkau untuk tujuan yang benar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1094">1094</a><span>]</span><span> padahal sesungguhnya segolongan dari orang-orang mukmin tidak menyukai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1095">1095</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7521,2,8,7,'<p><span>Mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1096">1096</a><span>]</span><span> berbantah dengan engkau mengenai hak setalah </span><span>hak</span><span> itu menjadi nyata, seolah-olah mereka digiring menuju maut dan mereka benar-benar melihatnya.</span></p>');
INSERT INTO `quran` VALUES (7522,2,8,8,'<p><span>Dan, </span><span>ingatlah</span><span> ketika Allah </span><span>[a]</span><span> menjanjikan kepadamu bahwa salah satu dari kedua golongan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1097">1097</a><span>]</span><span> itu untukmu dan kamu menginginkan supaya golongan yang tidak bersenjata itu untuk kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1098">1098</a><span>]</span><span> sedang Allah menghendaki untuk menegakkan yang hak dengan firman-firman-Nya dan melumpuhkan orang-orang kafir </span><span>sampai </span><span>keakar-akarnya.</span></p><p><span>[a]</span><span> 8:43.</span></p>');
INSERT INTO `quran` VALUES (7523,2,8,9,'<p><span>[b]</span><span> Supaya Dia menegakkan yang hak dan menghapuskan yang bathil, meskipun orang-orang yang berdosa tidak menyukainya.</span></p><p><span>[b]</span><span> 10:83.</span></p>');
INSERT INTO `quran` VALUES (7524,2,8,10,'<p><span>Dan ingatlah</span><span> </span><span>[c]</span><span> ketika kamu memohon pertolongan kepada Tuhan-mu, lalu Dia mengabulkan doamu, “Sesungguhnya Aku akan menolong kamu dengan seribu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1099">1099</a><span>]</span><span> malaikat berlapis-lapis.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1099a">1099a</a><span>]</span></p><p><span>[c]</span><span> 31:124.</span></p>');
INSERT INTO `quran` VALUES (7525,2,8,11,'<p><span>Dan, Allah </span><span>[a]</span><span> tidak menjadikan hal itu melainkan sebagai kabar suka dan supaya karenanya hati kamu sekalian tenteram. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1100">1100</a><span>]</span><span> Dan tiada pertolongan kecuali dari sisi Allah ; sesungguhnya Allah Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a]</span><span> 3:127.</span></p>');
INSERT INTO `quran` VALUES (7526,2,8,12,'<p><span>Tanda ini terjadi </span><span> </span><span>[b]</span><span> ketika Dia mendatangkan kantuk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1101">1101</a><span>]</span><span> kepadamu sebagai rasa aman dari-Nya, dan Dia menurunkan air untukmu dari awan supaya Dia mensucikan kamu dengannya dan menghilangkan dari kamu kekotoran syaitan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1102">1102</a><span>]</span><span> dan supaya Dia menguatkan hatimu dan meneguhkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1103">1103</a><span>]</span><span> dengannya langkah-langkamu.</span></p><p><span>[b]</span><span> 3:155.</span></p>');
INSERT INTO `quran` VALUES (7527,2,8,13,'<p><span>Ketika Tuhan engkau mewahyukan kepada malaikat-malaikat, “Sesungguhnya Aku beserta kamu ; maka teguhkanlah orang-orang yang beriman. Aku akan memasukkan rasa takut ke dalam hati orang-orang yang ingkar. Maka pukullah pada leher </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1104">1104</a><span>]</span><span> </span><span>mereka</span><span> dan pukullah pada tiap ruas jari mereka.”</span></p>');
INSERT INTO `quran` VALUES (7528,2,8,14,'<p><span>[a]</span><span> Hal demikian itu disebabkan mereka telah menentang Allah dan Rasul-Nya. Dan, barangsiapa menentang Allah dan Rasul-Nya, maka sesungguhnya Allah sangat keras dalam pembalasan.</span></p><p><span>[a]</span><span> 6:16 ; 47:33, 59:5.</span></p>');
INSERT INTO `quran` VALUES (7529,2,8,15,'<p><span>[b]</span><span> Itulah </span><span>hukuman</span><span> bagimu, maka rasakanlah itu ; dan sesungguhnya bagi orang-orang ingkar tersedia azab api.</span></p><p><span>[b]</span><span> 22:23 ; 34:42.</span></p>');
INSERT INTO `quran` VALUES (7530,2,8,16,'<p><span>Hai orang-orang yang beriman, </span><span>[c]</span><span> apabila kamu bertemu dengan orang-orang ingkar yang sedang bergerak dalam pasukan, maka janganlah kamu membalikkan punggung kepada mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1105">1105</a><span>]</span></p><p><span>[c]</span><span> 8:46 ; 47:5.</span></p>');
INSERT INTO `quran` VALUES (7531,2,8,17,'<p><span>Dan, barangsiapa membalikkan punggungnya kepada mereka pada hari semacam itu, kecuali beralih tempat untuk perang atau hendak bergabung kepada pasukan lain, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1106">1106</a><span>]</span><span> maka sesungguhnya ia kembali dengan kemurkaan dari Allah , dan tempat tinggalnya Jahannam. Dan, alangkah buruknya tempat kembali.</span></p>');
INSERT INTO `quran` VALUES (7532,2,8,18,'<p><span>Maka, bukanlah kamu yang membunuh mereka melainkan Allah yang telah membunuh mereka. Dan bukan engkau yang melempar </span><span>pasir</span><span> ketika engkau melempar, melainkan Allah yang telah melempar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1107">1107</a><span>]</span><span> dan supaya Dia </span><span>[a]</span><span> menganugerahi orang-orang mukmin anugerah yang baik dari-Nya. Sesungguhnya Allah Maha Mendengar, Maha Mengetahui.</span></p><p><span>[a]</span><span> 33:12.</span></p>');
INSERT INTO `quran` VALUES (7533,2,8,19,'<p><span>Inilah hal yang terjadi, dan sesungguhnya Allah melemahkan tipu-daya orang-orang kafir.</span></p>');
INSERT INTO `quran` VALUES (7534,2,8,20,'<p><span>Hai orang-orang kafir,</span><span> </span><span>[a]</span><span> jika kamu meminta </span><span>tanda</span><span> kemenangan maka sesungguhnya telah datang kepadamu kemenangan itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1108">1108</a><span>]</span><span> Dan, jika kamu menahan diri maka lebih baik bagimu ; dan jika kamu kembali </span><span>berbuat kejahatan,</span><span> Kami pun akan kembali </span><span>menghukum.</span><span> Dan Tidak akan berfaedah sedikit pun bagimu golonganmu walaupun banyak. Dan </span><span>ketahuilah</span><span> bahwa sesungguhnya Allah beserta orang-orang mukmin.</span></p><p><span>[a]</span><span> 32:29</span></p>');
INSERT INTO `quran` VALUES (7535,2,8,21,'<p><span>Hai orang-orang yang beriman, </span><span>[a]</span><span> taatilah Allah dan Rasul-Nya, dan janganlah kamu berpaling darinya padahal kamu mendengar </span><span>perintahnya.</span><span> </span></p><p><span>[a]</span><span> 3:33 ; 4:60 ; 8:47 ; 24:55.</span></p>');
INSERT INTO `quran` VALUES (7536,2,8,22,'<p><span>Dan, janganlah kamu menjadi seperti orang-orang </span><span>[b] </span><span>yang berkata, “Kami telah mendengar” padahal mereka tidak mendengar.</span></p><p><span>[b]</span><span> 2:94 ; 4:47.</span></p>');
INSERT INTO `quran` VALUES (7537,2,8,23,'<p><span>Sesungguhnya, binatang </span><span>[c]</span><span> yang paling buruk di sisi Allah ialah orang-orang yang pekak, bisu, </span><span>dan</span><span> tidak berakal.</span></p><p><span>[c]</span><span> 8:56 ; 98:7.</span></p>');
INSERT INTO `quran` VALUES (7538,2,8,24,'<p><span>Dan, andaikata Allah mengetahui ada suatu kebaikan dalam diri mereka, niscaya Dia akan menjadikan mereka mendengar </span><span>Alquran</span><span>. Dan, andaikata Dia menjadikan mereka mendengar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1109">1109</a><span>]</span><span> niscaya mereka berpaling </span><span>dari Alquran</span><span> dan merekalah orang-orang yang memalingkan diri.</span></p>');
INSERT INTO `quran` VALUES (7539,2,8,25,'<p><span>Hai orang-orang yang beriman, </span><span>[d]</span><span> sambutlah </span><span>seruan</span><span> Allah dan Rasul-rasul-Nya apabila ia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1109a">1109a</a><span>]</span><span> menyeru kamu supaya ia menghidupkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1110">1110</a><span>]</span><span> kamu, dan ketahuilah bahwa Allah menjadi pembatas di antara manusia dan hatinya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1110a">1110a</a><span>]</span><span> dan bahwa kepada-Nya kamu akan dihimpun.</span></p><p><span>[d]</span><span> 4:60 ; 8:47 ; 24:55.</span></p>');
INSERT INTO `quran` VALUES (7540,2,8,26,'<p><span>Dan </span><span>[a]</span><span> takutilah fitnah yang tidak hanya menimpa khusus orang-orang aniaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1111">1111</a><span>]</span><span> saja di antaramu. Dan ketahuilah sesungguhnya Allah sangat keras hukuman-Nya.</span></p><p><span>[a]</span><span> 11:114.</span></p>');
INSERT INTO `quran` VALUES (7541,2,8,27,'<p><span>Dan, ingatlah </span><span>saat</span><span> ketika kamu masih sedikit, dipandang lemah di muka bumi, </span><span>dan</span><span> kamu merasa takut manusia akan merenggut kamu, namun Dia memberikan tempat berlindung kepadamu </span><span>di kota Medinah</span><span> dan memperkuat kamu dengan pertolongan-Nya, dan Dia memberi kamu rezeki dari yang baik-baik, supaya kamu bersyukur. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1112">1112</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7542,2,8,28,'<p><span>Hai orang-orang yang beriman, janganlah kamu berkhianat kepada Allah dan Rasul dan </span><span>jangan</span><span> berkhianat terhadap amanat-amanat kamu padahal kamu mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1113">1113</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7543,2,8,29,'<p><span>Dan, ketahuilah bahwa </span><span>[a]</span><span> hartamu dan anak-anakmu adalah suatu fitnah, dan bahwa Allah itu di sisi-Nya ganjaran yang sangat besar.</span></p><p><span>[a]</span><span> 7:87 ; 3:124 ; 64:16.</span></p>');
INSERT INTO `quran` VALUES (7544,2,8,30,'<p><span>Hai orang-orang yang beriman, </span><span>[a]</span><span> jika kamu bertakwa kepada Allah , Dia akan mengadakan bagimu suatu pembeda, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1114">1114</a><span>]</span><span> dan Dia akan menghapuskan dari kamu keburukan-keburukan dan Dia akan mengampuni kamu ; dan Allah adalah Yang Empunya karunia yang besar.</span></p><p><span>[a]</span><span> 18:6 ; 63:10 ; 66:9.</span></p>');
INSERT INTO `quran` VALUES (7545,2,8,31,'<p><span>Dan, </span><span>ingatlah </span><span> ketika orang-orang ingkar merencanakan tipu-daya terhadap engkau, supaya mereka dapat menangkap engkau atau membunuh engkau atau mengusir engkau. Dan, </span><span>[b]</span><span> mereka membuat rencana, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1115">1115</a><span>]</span><span> dan Allah itu Perencana Yang sebaik-baiknya.</span></p><p><span>[b]</span><span> 3:55 ; 27:51</span></p>');
INSERT INTO `quran` VALUES (7546,2,8,32,'<p><span>Dan, </span><span>[a]</span><span> apabila Ayat-ayat Kami ditilawatkan kepada mereka, berkatalah mereka, “Kami telah mendengar. Jika kami ingin, niscaya kami pun pasti dapat mengatakan serupa itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1116">1116</a><span>]</span><span> </span><span>Alquran</span><span> ini tiada lain hanya dongeng-dongeng orang-orang dahulu.”</span></p><p><span>[a]</span><span> 6:26 ; 68:16 ; 83:41.</span></p>');
INSERT INTO `quran` VALUES (7547,2,8,33,'<p><span>Dan, </span><span>ingatlah</span><span> ketika mereka berkata, “Ya Allah, jika ini sungguh-sungguh hak dari Engkau, maka hujanilah kami </span><span>dengan</span><span> batu dari langit atau datangkanlah kepada kami azab yang pedih.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1117">1117</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7548,2,8,34,'<p><span>Namun, Allah sekali-kali tidak akan mengazab mereka selama engkau berada di tengah-tengah mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1118">1118</a><span>]</span><span> dan </span><span>[b]</span><span> Allah tidak akan mengazab mereka selama mereka meminta ampun.</span></p><p><span>[b]</span><span> 11:4.</span></p>');
INSERT INTO `quran` VALUES (7549,2,8,35,'<p><span>Dan, </span><span>[a]</span><span> alasan apa yang mereka miliki sehingga Allah tidak mengazab mereka, tatkala mereka menghalangi </span><span>orang-orang</span><span> dari Masjidilharam dan mereka bukanlah orang-orang yang berhak menjaganya. </span><span>[b]</span><span> Karena orang-orang yang berhak menjaganya hanyalah orang-orang muttaqi, akan tetapi kebanyakan mereka tidak mengetahui.</span></p><p><span>[a]</span><span> 22:26.</span></p><p><span>[b]</span><span> 10:63, 64;</span></p>');
INSERT INTO `quran` VALUES (7550,2,8,36,'<p><span>Dan shalat mereka di Baitullah itu tak lain, kecuali siul dan tepuk tangan belaka. Maka rasakanlah azab itu sebab kamu telah ingkar.</span></p>');
INSERT INTO `quran` VALUES (7551,2,8,37,'<p><span>Sesungguhnya orang-orang yang ingkar membelanjakan harta mereka guna menghalangi </span><span>orang-orang </span><span> dari jalan Allah Maka mereka akan senantiasa membelanjakannya ; kemudian hal itu menjadi sesalan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1119">1119</a><span>]</span><span> bagi mereka, </span><span>dan</span><span> </span><span>[c]</span><span> sesudah itu mereka akan ditaklukkan. Dan, orang-orang yang ingkar akan dihimpun ke Jahannam.</span></p><p><span>[c]</span><span> 3:13.</span></p>');
INSERT INTO `quran` VALUES (7552,2,8,38,'<p><span>[d]</span><span> Supaya Allah membedakan yang buruk dari yang baik, dan Dia menjadikan yang buruk itu yang sebagian di atas sebagian yang lain, lalu Dia menumpukkan semuanya, kemudian melemparkannya ke dalam Jahannam. Mereka itulah orang-orang yang rugi.</span></p><p><span>[d]</span><span> 3:180.</span></p>');
INSERT INTO `quran` VALUES (7553,2,8,39,'<p><span>Katakanlah kepada orang-orang yang ingkar, “Jika mereka berhenti dari apa-apa yang telah lampau, mereka akan diampuni ; dan, jika mereka kembali </span><span>kepada perbuatan salah,</span><span> maka sesungguhnya telah berlaku sunnah Allah </span><span>terhadap</span><span> orang-orang terdahulu.</span></p>');
INSERT INTO `quran` VALUES (7554,2,8,40,'<p><span>Dan, </span><span>[a]</span><span> perangilah mereka itu, sehingga tak ada lagi fitnah dan supaya agama menjadi seutuhnya bagi Allah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1120">1120</a><span>]</span><span> Tetapi, jika mereka berhenti, maka sesungguhnya Allah Maha Melihat apa-apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 2:194.</span></p>');
INSERT INTO `quran` VALUES (7555,2,8,41,'<p><span>[b]</span><span> Dan, jika mereka berpaling </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1121">1121</a><span>]</span><span> maka ketahuilah bahwa sesungguhnya Allah adalah Pelindung kamu, sebaik-baik Pelindung dan sebaik-baik Penolong !</span></p><p><span>[b]</span><span> 3:151 ; 22:79 ; 47:12.</span></p>');
INSERT INTO `quran` VALUES (7556,2,8,42,'<p><span>Juz X</span></p><p><span>Dan, ketahuilah bahwa </span><span>[a]</span><span> apa pun yang kamu peroleh sebagai harta rampasan </span><span>dalam peperangan,</span><span> maka sesungguhnya bagi Allah seperlimanya dan bagi Rasul-</span><span>Nya</span><span> dan bagi kaum kerabat dan anak-anak yatim dan orang-orang miskin dan orang musafir, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1122">1122</a><span>]</span><span> jika memang kamu beriman kepada Allah dan kepada apa yang Kami turunkan kepada hamba Kami pada Hari Pembedaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1123">1123</a><span>]</span><span> </span><span>di antara hak dan batil,</span><span> </span><span>[b]</span><span> hari ketika dua pasukan bertemu dan Allah berkuasa atas segala sesuatu.</span></p><p><span>[a]</span><span> 8:70.</span></p><p><span>[b]</span><span> 3:14, 167.</span></p>');
INSERT INTO `quran` VALUES (7557,2,8,43,'<p><span>Ketika kamu berada di tepi </span><span>lembah</span><span> yang dekat dan mereka di tepi yang jauh, sedang kafilah itu </span><span>berada</span><span> </span><span>di tempat yang </span><span> lebih rendah dari kamu. Dan, sekiranya kamu bersepakat di antaramu, niscaya kamu akan berselisih tentang penetapan waktu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1124">1124</a><span>]</span><span> </span><span>perang.</span><span> Akan tetapi, Allah pasti melaksanakan apa yang telah diputuskan-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1125">1125</a><span>]</span><span> supaya binasa-lah dia yang </span><span>telah</span><span> binasa dengan keterangan yang jelas, dan supaya hiduplah dia yang </span><span>telah</span><span> hidup dengan keterangan yang jelas. Dan, sesungguhnya Allah Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (7558,2,8,44,'<p><span>[a]</span><span> Ketika Allah memperlihatkan kepada engkau dalam mimpi engkau, mereka itu sedikit ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1126">1126</a><span>]</span><span> dan andaikan Dia memperlihatkan kepada engkau mereka itu banyak, niscaya kamu akan gentar dan akan berselisih dalam urusan itu ; akan tetapi, Allah menyelamatkan kamu. Sesungguhnya Dia Maha Mengetahui isi hati.</span></p><p><span>[a]</span><span> 3:14.</span></p>');
INSERT INTO `quran` VALUES (7559,2,8,45,'<p><span>Dan, </span><span>ingatlah</span><span> </span><span>[b]</span><span> ketika Dia menampakkan kepada kamu mereka itu lemah dalam penglihatan kamu di waktu kamu berperang, dan Dia membuat kamu </span><span>nampak</span><span> lemah dalam penglihatan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1127">1127</a><span>]</span><span> supaya Allah menyempurnakan hal yang telah diputuskan, dan </span><span>[c]</span><span> kepada Allah segala hal dikembalikan.</span></p><p><span>[b]</span><span> 3:14.</span></p><p><span>[c]</span><span> 2:211, 3:110 ; 35:5.</span></p>');
INSERT INTO `quran` VALUES (7560,2,8,46,'<p><span>Wahai </span><span>[a]</span><span> orang-orang yang beriman, apabila kamu berhadapan dengan lasykar </span><span>musuh,</span><span> maka bersiteguhlah kamu dan </span><span>[b]</span><span> ingatlah Allah sebanyak-banyaknya supaya kamu berjaya.</span></p><p><span>[a]</span><span> 8:16 ; 47:5.</span></p><p><span>[b]</span><span> 33:42 ; 62:11.</span></p>');
INSERT INTO `quran` VALUES (7561,2,8,47,'<p><span>Dan, </span><span>[c]</span><span> taatilah Allah dan Rasul-Nya serta janganlah kamu berselisih, maka kamu akan gentar dan kekautanmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1128">1128</a><span>]</span><span> akan hilang, dan bersabarlah. Sesungguhnya Allah beserta orang-orang yang sabar.</span></p><p><span>[c]</span><span> 3:33 ; 4:60 ; 8:21 ; 24:55.</span></p>');
INSERT INTO `quran` VALUES (7562,2,8,48,'<p><span>Dan, janganlah kamu menjadi seperti orang-orang yang keluar dari rumah mereka dengan sombong dan ingin dilihat orang serta menghalangi dari jalan Allah Dan, Allah menghancurkan apa-apa yang mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (7563,2,8,49,'<p><span>Dan </span><span>ingatlah </span><span> ketika </span><span>[a]</span><span> syaitan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1129">1129</a><span>]</span><span> menampakkan indah kepada mereka amal-amal mereka dan berkata, </span><span>[b]</span><span> “Tak seorang pun di antara manusia yang dapat mengalahkanmu pada hari ini, dan sesungguhnya aku pelindungmu. “ Tetapi, ketika kedua pasukan itu berhadapan kepada satu sama lain, berbaliklah ia atas tumitnya sambil berkata, “Sesungguhnya aku berlepas diri dari kamu ; sesungguhnya, aku melihat apa yang tidak kamu lihat. Sesungguhnya, aku takut kepada Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1130">1130</a><span>]</span><span> dan sangat keras siksaan Allah</span></p><p><span>[a]</span><span> 6:44 ; 16:64 ; 27:25 ; 29:49.</span></p><p><span>[b]</span><span> 14:23 ; 59:17.</span></p>');
INSERT INTO `quran` VALUES (7564,2,8,50,'<p><span>Ingatlah</span><span> </span><span>[a]</span><span> ketika berkata orang-orang munafik dan orang-orang yang di dalam hatinya ada penyakit, “Telah menipu mereka agama mereka.” Dan </span><span>[b]</span><span> barangsiapa bertawakal kepada Allah, maka sesungguhnya Allah Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a]</span><span> 33:13.</span></p><p><span>[b]</span><span> 9:51 ; 12:68 ; 14:12 ; 33:4 ; 65:4.</span></p>');
INSERT INTO `quran` VALUES (7565,2,8,51,'<p><span>Dan, sekiranya engkau dapat melihat </span><span>[c]</span><span> ketika malaikat mencabut nyawa orang-orang ingkar, mereka memukuli wajah-wajah dan punggung-punggung mereka dan </span><span>berkata</span><span>, </span><span>[d]</span><span> “Rasakanlah olehmu azab yang membakar.</span></p><p><span>[c]</span><span> 47:28.</span></p><p><span>[d]</span><span> 3:182 ; 22:10.</span></p>');
INSERT INTO `quran` VALUES (7566,2,8,52,'<p><span>“</span><span>Azab </span><span>itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1131">1131</a><span>]</span><span> disebabkan oleh apa yang telah diperbuat oleh tangamu dan </span><span>[e]</span><span> Allah sekali-kali tidak aniaya terhadap hamba-hamba-</span><span>Nya.</span><span>”</span></p><p><span>[e]</span><span> 3:183 ; 22:11 ; 41:47.</span></p>');
INSERT INTO `quran` VALUES (7567,2,8,53,'<p><span>[f]</span><span> Seperti keadaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1131a">1131a</a><span>]</span><span> kaum Fir</span><span>’</span><span>aun dan orang-orang sebelum mereka. Mereka meng ingkari Tanda-tanda Allah, maka Allah menghukum mereka disebabkan dosa-dosa mereka. Sesungguhnya, Allah Mahakuat, Maha Keras dalam menghukum.</span></p><p><span>[f]</span><span> 3:12 ; 8:55</span></p>');
INSERT INTO `quran` VALUES (7568,2,8,54,'<p><span>Yang demikian itu adalah karena </span><span>[a]</span><span> Allah tidak akan mengubah suatu nikmat yang telah Dia anugerahkan kepada suatu kaum, sebelum mereka mengubah keadaan diri mereka sendiri, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1132">1132</a><span>]</span><span> dan sesungguhnya Allah Maha Mendengar, Maha Mengetahui.</span></p><p><span>[a]</span><span> 13:12.</span></p>');
INSERT INTO `quran` VALUES (7569,2,8,55,'<p><span>Seperti </span><span>[b]</span><span> keadaan kaum Fir</span><span>’</span><span>aun dan orang-orang sebelum mereka. Mereka telah mendustakan Tanda-tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1133">1133</a><span>]</span><span> Tuhan mereka, maka Kami binasakan mereka disebabkan dosa-dosa mereka dan Kami tenggelamkan kaum Fir</span><span>’</span><span>aun dan ; semua mereka itu adalah orang-orang aniaya.</span></p><p><span>[b]</span><span> 3:12 ; 8:53.</span></p>');
INSERT INTO `quran` VALUES (7570,2,8,56,'<p><span>Sesungguhnya, </span><span>[c]</span><span> yang seburuk-buruk binatang di sisi Allah ialah orang-orang yang ingkar, karena mereka tidak mau beriman.</span></p><p><span>[c]</span><span> 8:23 ; 98:7.</span></p>');
INSERT INTO `quran` VALUES (7571,2,8,57,'<p><span>Yaitu,</span><span> orang-orang yang engkau telah mengadakan perjanjian dengan mereka, kemudian </span><span>[a]</span><span> mereka mengingkari janji mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1134">1134</a><span>]</span><span> dalam setiap kali </span><span>berjanji</span><span> dan mereka tidak bertakwa.</span></p><p><span>[a]</span><span> 2:28.</span></p>');
INSERT INTO `quran` VALUES (7572,2,8,58,'<p><span>Maka, jika engkau menjumpai mereka dalam peperangan, maka kucar-kacirkanlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1135">1135</a><span>]</span><span> mereka dengan perantaraan orang-orang yang di belakang mereka, supaya mereka mengambil pelajaran.</span></p>');
INSERT INTO `quran` VALUES (7573,2,8,59,'<p><span>Dan, jika engkau khawatir terhadap pengkhianatan dari suatu kaum, maka kembalikanlah </span><span>perjanjian itu</span><span> kepada mereka dengan cara yang sama. Sesungguhnya, </span><span>[b]</span><span> Allah tidak mencintai orang-orang yang berkhianat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1136">1136</a><span>]</span></p><p><span>[b]</span><span> 4:108.</span></p>');
INSERT INTO `quran` VALUES (7574,2,8,60,'<p><span>Dan, </span><span>[a]</span><span> janganlah sekali-kali orang-orang ingkar menyangka bahwa </span><span>melalui tipu-daya</span><span> mereka bisa maju ke depan. Sesungguhnya, mereka tidak akan dapat menggagalkan </span><span>orang-orang mukmin.</span><span> </span></p><p><span>[a]</span><span> 3:179.</span></p>');
INSERT INTO `quran` VALUES (7575,2,8,61,'<p><span>Dan, </span><span>[b]</span><span> siapkanlah untuk </span><span>menghadapi </span><span> mereka sejauh kesanggupanmu berupa kekuatan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1137">1137</a><span>]</span><span> dan kuda-kuda yang diikat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1138">1138</a><span>]</span><span> di garis depan untuk berperang;</span><span> dengan itu kamu dapat menggentarkan musuh Allah dan musuhmu dan </span><span>musuh</span><span> yang lain di samping mereka yang tidak kamu ketahui, </span><span>tetapi</span><span> Allah mengetahui mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1139">1139</a><span>]</span><span> Dan, </span><span>[c]</span><span> apa pun yang kamu belanjakan di jalan Allah, itu akan dibayar penuh kepadamu dan kamu tidak akan diperlakukan dengan aniaya.</span></p><p><span>[b]</span><span> 3:201.</span></p><p><span>[c]</span><span> 2:273 ; 9:121 ; 64:18 ; 65:8.</span></p>');
INSERT INTO `quran` VALUES (7576,2,8,62,'<p><span>Dan, jika mereka condong kepada perdamaian, maka condong pulalah engkau kepadanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1140">1140</a><span>]</span><span> dan berwakallah kepada Allah. Sesungguhnya, Dia-lah Yang Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (7577,2,8,63,'<p><span>Dan, jika mereka berkeinginan menipu engkau, maka sesungguhnya </span><span>[a]</span><span> Allah cukup bagi engkau ; Dia-lah Yang telah menguatkan engkau dengan pertolongan-Nya dan dengan orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (7578,2,8,64,'<p><span>Dan, </span><span>[b]</span><span> Dia telah menanamkan kecintaan di antara hati mereka. Andaikata engkau membelanjakan yang ada di bumi ini seluruhnya, engkau tidak akan dapat menanamkan kecintaan di antara hati mereka, tetapi Allah yang telah menanamkan kecintaan di antara mereka. Sesungguhnya Dia Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b]</span><span> 3:104.</span></p>');
INSERT INTO `quran` VALUES (7579,2,8,65,'<p><span>Hai Nabi, </span><span>[a]</span><span> Allah cukup bagi engkau dan orang-orang yang mengikuti engkau di antara orang-orang mukmin.</span></p><p><span>[a]</span><span> 8:63.</span></p>');
INSERT INTO `quran` VALUES (7580,2,8,66,'<p><span>Hai Nabi, </span><span>[b]</span><span> kobarkanlah semangat orang-orang mukmin untuk berperang. Jika ada di antaramu dua puluh orang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1141">1141</a><span>]</span><span> yang sabar, mereka akan dapat mengalahkan dua ratus </span><span>orang kafir;</span><span> dan jika ada di antaramu seratus orang, mereka akan mengalahkan seribu orang kafir, disebabkan mereka itu kaum tyang tidak mengerti. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1142">1142</a><span>]</span></p><p><span>[b]</span><span> 4:85</span></p>');
INSERT INTO `quran` VALUES (7581,2,8,67,'<p><span>Sekarang Allah telah meringankan beban dari kamu dan Dia mengetahui bahwa di dalam dirimu ada kelemahan. Maka, jika ada di antaramu seratus orang yang sabar, mereka akan mengalahkan dua ratus ; dan jika ada di antaramu seribu, mereka akan mengalahkan dua ribu orang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1143">1143</a><span>]</span><span> dengan izin Allah dan Allah berserta orang-orang yang sabar.</span></p>');
INSERT INTO `quran` VALUES (7582,2,8,68,'<p><span>[a]</span><span> Tidak layak bagi seorang Nabi bahwa ia mempunyai tawanan sebelum ia menumpahkan darah </span><span>di waktu</span><span> perang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1144">1144</a><span>]</span><span> di bumi. Kamu menginginkan </span><span>[b]</span><span> harta dunia, padahal Allah menghendaki akhirat </span><span>bagimu;</span><span> dan Allah Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a]</span><span> 47:5.</span></p><p><span>[b]</span><span> 4:95.</span></p>');
INSERT INTO `quran` VALUES (7583,2,8,69,'<p><span>Sekiranya tidak ada suatu ketetapan dari Allah sejak dahulu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1145">1145</a><span>]</span><span> niscaya akan menimpakamu azab yang besar karena apa-apa yang kamu ambil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1145a">1145a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7584,2,8,70,'<p><span>Maka </span><span>[a]</span><span> makanlah dari apa-apa yang kamu peroleh dari rampasan perang yang halal dan baik dan bertakwalah kepada Allah Sesungguhnya, Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a]</span><span> 8:42.</span></p>');
INSERT INTO `quran` VALUES (7585,2,8,71,'<p><span>Hai Nabi, katakanlah kepada orang-orang yang ada di tanganmu dari tawanan-tawanan, “ Jika Allah mengetahui ada kebaikan dalam hatimu, Dia akan memberikan kepadamu lebih baik dari apa yang diambil darimu ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1146">1146</a><span>]</span><span> dan Dia akan mengampuni kamu. Dan Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (7586,2,8,72,'<p><span>Dan jika mereka bermaksud khianat kepada engkau, maka sesungguhnya mereka pernah berkhianat kepada Allah sebelumnya, tetapi Dia memberi kekuasaan kepada engkau di atas mereka. Dan Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (7587,2,8,73,'<p><span>Sesungguhnya, </span><span>[a]</span><span> orang-orang yang beriman dan berhijrah dan berjihad dengan harta mereka dan jiwa mereka di jalan Allah, dan orang-orang yang memberikan tempat perlindungan dan pertolongan, mereka itulah sebagian mereka sahabat dari sebagian yang lain. Dan orang-orang yang beriman tetapi tidak berhijrah, tidaklah kamu berkewajiban melindungi mereka sedikit pun, sebelum mereka meminta pertolongan kepadamu dalam </span><span>urusan</span><span> agama, maka wajiblah bagi kamu menolong </span><span>mereka,</span><span> kecuali terhadap suatu kaum yang antara kamu dengan mereka telah mengikat perjanjian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1147">1147</a><span>]</span><span> Dan, Allah melihat apa yang kamu kerjakan.</span></p><p><span>[a]</span><span> 2:219 ; 9:20 ; 61:12.</span></p>');
INSERT INTO `quran` VALUES (7588,2,8,74,'<p><span>Dan, orang-orang yang ingkar sebagian mereka adalah sahabat sebahagian yang lain. Jika kamu tidak mengerjakannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1148">1148</a><span>]</span><span> </span><span>apa yang Kami perintahkan,</span><span> niscaya akan timbul fitnah dan kerusuhan besar di bumi.</span></p>');
INSERT INTO `quran` VALUES (7589,2,8,75,'<p><span>Dan, </span><span>[a]</span><span> orang-orang yang beriman dan berhijrah dan berjihad di jalan Allah, dan orang-orang yang memberi tempat perlindungan dan pertolongan, mereka itulah orang-orang mukmin hakiki. Bagi mereka ada pengampunan dan rezeki mulia.</span></p><p><span>[a]</span><span> 2:219 ; 9:20 ; 61:12.</span></p>');
INSERT INTO `quran` VALUES (7590,2,8,76,'<p><span>Dan, orang-orang yang beriman sesudah </span><span>waktu ini</span><span> dan berhijrah dan berjihad bersama kamu, mereka itu termasuk antara kamu ; dan </span><span>[a]</span><span> orang-orang yang bertalian darah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1149">1149</a><span>]</span><span> sebagaian mereka lebih dekat kepada sebagian yang lain menurut Kitab Allah Sesungguhnya, Allah Maha Mengetahui segala sesuatu.</span></p><p><span>[a]</span><span> 33:7.</span></p>');
INSERT INTO `quran` VALUES (7591,2,9,1,'<p><span>Inilah</span><span> suatu pernyataan bebas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1150">1150</a><span>]</span><span> </span><span>tuduhan </span><span> dari Allah dan Rasul-Nya kepada orang-orang musyrik yang kamu berjanji </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1151">1151</a><span>]</span><span> dengan mereka </span><span>bahwa kamu akan mendapat kemenangan.</span><span> </span></p>');
INSERT INTO `quran` VALUES (7592,2,9,2,'<p><span>Maka jelajahilah bumi ini </span><span>selama</span><span> empat bulan dan ketahuilah bahwa kamu tidak dapat </span><span>[a]</span><span> menggagalkan </span><span>rencana</span><span> Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1152">1152</a><span>]</span><span> dan bahwa Allah akan menghinakan orang-orang kafir.</span></p><p><span>[a]</span><span> 6:135 ; 11:21.</span></p>');
INSERT INTO `quran` VALUES (7593,2,9,3,'<p><span>Dan </span><span>ini adalah </span><span> satu pengumuman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1153">1153</a><span>]</span><span> dari Allah dan Rasul-Nya kepada manusia pada hari Haj Akbar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1153a">1153a</a><span>]</span><span> bahwa Allah dan Rasul-Nya bebas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1154">1154</a><span>]</span><span> dari </span><span>tuduhan</span><span> orang-orang musyrik. Maka jika kamu bertobat, itulah lebih baik bagimu ; dan jika kamu berpaling, maka ketahuilah bahwa kamu tidak dapat </span><span>[a]</span><span> menggagalkan </span><span>rencana </span><span> Allah Dan </span><span>[b]</span><span> beritakanlah kepada orang-orang yang ingkar mengenai azab yang pedih.</span></p><p><span>[a]</span><span> Lihat 9:2.</span></p><p><span>[b]</span><span> 4:139</span></p>');
INSERT INTO `quran` VALUES (7594,2,9,4,'<p><span>[a]</span><span> Kecuali orang-orang musyrik yang kamu telah mengadakan perjanjian, kemudian mereka tidak melanggar </span><span>janji dengan </span><span> kamu sedikit pun dan tidak pula membantu seseorang melawan kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1155">1155</a><span>]</span><span> Maka, penuhilah sampai batas waktunya. Sesungguhnya Allah mencintai orang-orang yang bertakwa.</span></p><p><span>[a]</span><span> 9:7.</span></p>');
INSERT INTO `quran` VALUES (7595,2,9,5,'<p><span>Maka, apabila bulan-bulan haram </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1155a">1155a</a><span>]</span><span> itu lewat, maka bunuhlah orang-orang musyrik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1156">1156</a><span>]</span><span> itu di mana saja kamu jumpai mereka, dan lawanlah mereka dan kepunglah mereka </span><span>dalam benteng mereka,</span><span> dan dudukilah setiap tempat pengintaian untuk </span><span>mengintai</span><span> mereka. Tetapi, </span><span>[a]</span><span> jika mereka bertobat serta dawan mendirikan shalat dan membayar zakat, maka bukalah jalan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1157">1157</a><span>]</span><span> Sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a]</span><span> 7:157 ; 9:11.</span></p>');
INSERT INTO `quran` VALUES (7596,2,9,6,'<p><span>Dan, jika salah seorang di antara orang-orang musyrik miminta perlindungan kepada engkau, berilah dia perlindungan sehingga gia dapat mendengar firman Allah ; kemudian sampaikanlah dia ke tempatnya yang aman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1158">1158</a><span>]</span><span> Hal itu karena mereka kaum yang tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (7597,2,9,7,'<p><span>Bagaimana bisa ada perjanjian bagi orang-orang musyrik dengan Allah dan dengan Rasul-Nya, </span><span>[a]</span><span> kecuali orang-orang yang kamu telah mengadakan perjanjian dengan mereka di dekat Masjidil Haram ? Maka, selama berpegang teguh </span><span>dalam perjanjian</span><span> dengan kamu, maka berpegang teguhlah kamu terhadap mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1159">1159</a><span>]</span><span> Sesungguhnya Allah mencintai orang-orang muttaqi.</span></p><p><span>[a]</span><span> 9:4.</span></p>');
INSERT INTO `quran` VALUES (7598,2,9,8,'<p><span>Bagaimana </span><span>mungkin</span><span> jika mereka menang terhadapmu, </span><span>[b]</span><span> mereka tidak akan menghiraukan tali kekeluargaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1160">1160</a><span>]</span><span> dan tidak pula perjanjian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1161">1161</a><span>]</span><span> Mereka membuatmu senang dengan mulut mereka, sedang hati mereka menolak dan kebanyakan mereka orang-orang durhaka.</span></p><p><span>[b]</span><span> 9:10.</span></p>');
INSERT INTO `quran` VALUES (7599,2,9,9,'<p><span>[a]</span><span> Mereka menukar Ayat-ayat Allah dengan harga yang rendah, lalu mereka menghalangi </span><span>orang-orang</span><span> dari jalan-Nya. Sesungguhnya buruklah apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 2:175 ; 3:78, 188 ; 16:96. </span></p>');
INSERT INTO `quran` VALUES (7600,2,9,10,'<p><span>[b]</span><span> Mereka tidak memelihara ikatan kekeluargaan terhadap seorang mukmin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1162">1162</a><span>]</span><span> dan tidak pula perjanjian. Dan, mereka itulah orang-orang yang melampui batas.</span></p><p><span>[b]</span><span> 9:8.</span></p>');
INSERT INTO `quran` VALUES (7601,2,9,11,'<p><span>Akan tetapi, </span><span>[a]</span><span> jika mereka bertobat dan tetap mendirikan shalat dan membayar zakat, maka </span><span>mereka</span><span> saudara-saudaramu se-agama. Dan Kami menjelaskan Ayat-ayat bagi kaum yang mengetahui.</span></p><p><span>[a]</span><span> 7:154 ; 9:5.</span></p>');
INSERT INTO `quran` VALUES (7602,2,9,12,'<p><span>Dan, jika mereka melanggar sumpah mereka setelah perjanjian mereka dan mereka menyerang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1163">1163</a><span>]</span><span> agamamu, maka </span><span>[b]</span><span> perangilah pemimpin-pemimpin kafir, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1164">1164</a><span>]</span><span> sesungguhnya mereka tidak mengindahkan sumpah mereka agar mereka berhenti </span><span>dari amal buruk.</span></p><p><span>[b]</span><span> 2:191 ; 4:92.</span></p>');
INSERT INTO `quran` VALUES (7603,2,9,13,'<p><span>Tidaklah kamu akan memerangi suatu kaum yang telah melanggar sumpah mereka dan yang telah bertekad mengusir Rasul, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1165">1165</a><span>]</span><span> serta mereka yang pertama-tama memulai </span><span>memerangi </span><span> kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1166">1166</a><span>]</span><span> Takutlah kamu kepada mereka ? Padahal, Allah yang lebih berhak agar kamu takut kepada-Nya, jika kamu sungguh orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (7604,2,9,14,'<p><span>Perangilah mereka, supaya Allah mengazab mereka dengan tangan-tanganmu dan akan memberi kehinaan kepada mereka dan akan menolong kamu atas mereka dan dengan jalan itu Dia menyelamatkan hati orang-orang mukmin dari </span><span>kesedihan dan ketakutan.</span><span> </span></p>');
INSERT INTO `quran` VALUES (7605,2,9,15,'<p><span>Dan supaya Dia akan menghilangkan kegusaran hati mereka. Dan, Allah akan menerima tobat siapa yang Dia kehendaki. Dan Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (7606,2,9,16,'<p><span>[a]</span><span> Apakah kamu mengira bahwa kamu akan dibiarkan sedangkan Allah belum menzahirkan siapa di antaramu yang telah berjihad, dan </span><span>[b]</span><span> mereka tiadk mengambil sahabat rahasia </span><span>dari orang kafir</span><span> untuk melawan Allah dan Rasul-Nya dan orang-orang mukmin ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1167">1167</a><span>] Dan, Allah Maha Mengetahui apa yang kamu kerjakan.</span></p><p><span>[a]</span><span> 3:143, 180 ; 20:3 </span><span>–</span><span> 4.</span></p><p><span>[b]</span><span> 3:29 ; 4:140, 145 ; 9:23.</span></p>');
INSERT INTO `quran` VALUES (7607,2,9,17,'<p><span>Tidaklah layak bagi orang-orang musyrik memakmurkan masjid-masjid Allah, sedang mereka menjadi saksi atas kekufuran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1168">1168</a><span>]</span><span> diri mereka sendiri. Mereka itulah orang-orang yang sia-sia amalnya dan di dalam Api mereka akan tinggal lama.</span></p>');
INSERT INTO `quran` VALUES (7608,2,9,18,'<p><span>Sesungguhnya yang memakmurkan masjid-masjid Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1169">1169</a><span>]</span><span> hanyalah orang yang beriman kepada Allah dan Hari kemudian dan tetap mendirikan shalat dan membawar zakat serta ia tidak takut kecuali kepada Allah maka mudah-mudahan mereka itu termasuk golongan orang-orang yang mendapat petunjuk.</span></p>');
INSERT INTO `quran` VALUES (7609,2,9,19,'<p><span>Apakah kamu anggap memberi minum kepada orang-orang yang melaksanakan haj dan yang memelihara Masjidilharam itu seperti orang yang beriman kepada Allah dan Hari Kemudian serta yang telah berjihad pada jalan Allah ? Mereka </span><span>sekali-kali</span><span> tidak sama di sisi Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1170">1170</a><span>]</span><span> dan Allah tidak memberi petunjuk kepada kaum aniaya.</span></p>');
INSERT INTO `quran` VALUES (7610,2,9,20,'<p><span>[a]</span><span> Orang-orang yang beriman dan berhijrah dan berjihad di jalan Allah dengan harta mereka dan jiwa mereka, memiliki derajat yang tertinggi di sisi Allah Dan merekalah orang-orang yang berjaya.</span></p><p><span>[a]</span><span> 4:96 ; 57:11.</span></p>');
INSERT INTO `quran` VALUES (7611,2,9,21,'<p><span>[a]</span><span> Tuhan mereka memberi kabar suka kepada mereka mengenai rahmat dari-Nya dan keridhaan-</span><span>Nya</span><span> dan surga-surga bagi mereka yang di dalamnya terdapat nikmat yang kekal.</span></p><p><span>[a]</span><span> 3:16 ; 5:13 ; 9:72 ; 10:10 ; 57:21.</span></p>');
INSERT INTO `quran` VALUES (7612,2,9,22,'<p><span>Mereka akan menetap di dalamnya untuk selama-lamanya. Sesungguhnya Allah di sisi-Nya mempunyai ganjaran yang besar.</span></p>');
INSERT INTO `quran` VALUES (7613,2,9,23,'<p><span>Hai orang-orang yang beriman, </span><span>[b]</span><span> janganlah kamu mengambil bapak-bapakmu dan saudara-saudaramu laki-lakimu menjadi sahabat jika mereka lebih mencintai kekufuran daripada keimanan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1171">1171</a><span>]</span><span> Dan, barangsiapa di antaramu bersahabat dengan mereka, maka mereka itu orang-orang aniaya.</span></p><p><span>[b]</span><span> 3:29 ; 4:140, 145 ; 9:16 ; 58:23.</span></p>');
INSERT INTO `quran` VALUES (7614,2,9,24,'<p><span>Katakanlah, “Jika bapak-bapakmu dan anak-anak laki-lakimu dan saudara-saudara laki-laki mu dan istri-istrimu dan kerabatmu dan harta yang kamu telah mengupayakan dan perniagaan yang kamu khawatirkan kerugiannya dan tempat tinggal yang kamu menyukainya, </span><span>ke semuanya </span><span> lebih kamu cintai daripada Allah dan Rasul-Nya dan berjihad di jalan-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1172">1172</a><span>]</span><span> maka tungguhlah sampai Allah mendatangkan keputusan-Nya. Dan Allah tidak memberi petunjuk kepada kaum yang durhaka.</span></p>');
INSERT INTO `quran` VALUES (7615,2,9,25,'<p><span>Sesungguhnya, </span><span>[a]</span><span> Allah telah menolong kamu di sekian banyak peperangan dan pada hari Hunian, ketika bilangan banyakmu itu menjadikan kamu sombong, tetapi itu tidak memberi manfaat kepadamu sedikit pun ; dan bumi dengan keluasannya itu menjadi sempit, kemudian kamu berbalik mundur. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1173">1173</a><span>]</span></p><p><span>[a]</span><span> 3:124.</span></p>');
INSERT INTO `quran` VALUES (7616,2,9,26,'<p><span>Kemudian, </span><span>[a]</span><span> Allah menurunkan ketenteraman-Nya kepada Rasul-Nya dan kepada orang-orang mukmin, dan Dia menurunkan lasykar-lasykar yang kamu tidak melihatnya dan Dia mengazab orang-orang yang ingkar. Dan itulah balasan </span><span>bagi</span><span> orang-orang kafir.</span></p><p><span>[a]</span><span> 9:40 ; 48:27.</span></p>');
INSERT INTO `quran` VALUES (7617,2,9,27,'<p><span>Lalu, Allah memberi tobat setelah itu kepada siapa yang Dia kehendaki. Dan Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (7618,2,9,28,'<p><span>Wahai orang-orang yang beriman, sesungguhnya orang-orang musyrik itu najis, karena itu janganlah mereka mendekati Masjidilharam sesudah tahun mereka ini. Dan, jika kamu khawatir menjadi miskin, maka Allah akan segera memperkaya kamu dengan karunai-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1174">1174</a><span>]</span><span> jika Dia menghendaki. Sesungguhnya Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (7619,2,9,29,'<p><span>[a]</span><span> Perangilah orang-orang yang tidak beriman kepada Allah dan tidak pula kepada Hari Kemudian dan tidak mengharamkan apa yang diharamkan oleh Allah dan Rasul-Nya dan tidak mengikuti agama yang hak, di antara orang-orang yang telah di beri Kitab, hingga mereka membayar pajak dengan senang hati dan mereka takluk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1175">1175</a><span>]</span></p><p><span>[a]</span><span> 2:191.</span></p>');
INSERT INTO `quran` VALUES (7620,2,9,30,'<p><span>Dan berkata </span><span>[a]</span><span> orang-orang Yahudi, “Uzair </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1176">1176</a><span>]</span><span> itu anak Allah ;” dan orang-orang Nasrani berkata, “Al-Masih itu anak Allah.” Demikian itulah perkataan mereka dengan mulut mereka. Mereka hanya meniru-niru perkataan orang-orang ingkar yang terdahulu, Allah membinasakan mereka. Betapa jauh mereka berpaling.</span></p><p><span>[a]</span><span> 2:117 ; 5:18 ; 10:69.</span></p>');
INSERT INTO `quran` VALUES (7621,2,9,31,'<p><span>Mereka telah menjadikan ulama-ulama mereka dan rahib-rahib mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1177">1177</a><span>]</span><span> sebagai tuhan-tuhan selain Allah ; dan </span><span>begitu juga </span><span> Al-Masih ibnu Maryam. Padahal </span><span>[a]</span><span> mereka tidak diperintahkan, kecuali supaya mereka menyembah Tuhan Yang Maha Esa. Tidak ada Tuhan selain Dia. Mahasuci Dia dari apa yang mereka sekutukan.</span></p><p><span>[a]</span><span> 12:41 ; 17:24 ; 98:6.</span></p>');
INSERT INTO `quran` VALUES (7622,2,9,32,'<p><span>[b]</span><span> Mereka berkehendak memadamkan Nur Ilahi dengan mulut mereka, tetapi Allah menolak bahkan menyempurnakan Nur-Nya, walaupun orang-orang kafir tidak menyukai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1178">1178</a><span>]</span></p><p><span>[b]</span><span> 61:9.</span></p>');
INSERT INTO `quran` VALUES (7623,2,9,33,'<p><span>[c]</span><span> Dia-lah Yang telah mengutus Rasul-Nya dengan petunjuk dan agama yang hak supaya Dia mengunggulkannya di atas semua agama walaupun orang-orang musyrik tidak menyukai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1179">1179</a><span>]</span></p><p><span>[c]</span><span> 48:29 ; 61:10</span></p>');
INSERT INTO `quran` VALUES (7624,2,9,34,'<p><span>Wahai orang-orang yang beriman, sesungguhnya </span><span>[a]</span><span> kebanyakan ulama dan rahib itu, makan harta orang dengan cara tidak benar dan mereka </span><span>[b]</span><span> menghalangi dari jalan Allah Dan orang-orang yang menimbun emas dan perak dan tidak membelanjakannya di jalan Allah, maka kabarkanlah kepada mereka tentang azab yang pedih.</span></p><p><span>[a]</span><span> 4:162.</span></p><p><span>[b]</span><span> 4:161.</span></p>');
INSERT INTO `quran` VALUES (7625,2,9,35,'<p><span>Pada hari itu </span><span>emas dan perak</span><span> akan dipanaskan di dalam Api Jahannam, lalu dengannya dahi mereka dan lambung mereka dan punggung mereka dicap bakar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1180">1180</a><span>]</span><span> </span><span>dikatakan kepada mereka, </span><span>“Inilah apa yang telah kamu timbun untuk diri kamu ; oleh karena itu rasakanlah apa yang telah kamu timbun.”</span></p>');
INSERT INTO `quran` VALUES (7626,2,9,36,'<p><span>Sesungguhnya bilangan bulan pada sisi Allah ialah dua belas bulan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1181">1181</a><span>]</span><span> menurut ketetapan Allah sejak hari Dia menciptakan langit dan bumi ; di antaranya ada empat yang suci. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1182">1182</a><span>]</span><span> Itulah agama yang teguh. Karena itu, janganlah kamu menganiaya dirimu sendiri di dalamnya. Dan, perangilah orang-orang musyrik itu semua sebagaimana mereka semua memerangimu. Dan ketahuilah, sesungguhnya Allah beserta orang-orang bertakwa.</span></p>');
INSERT INTO `quran` VALUES (7627,2,9,37,'<p><span>Sesungguhnya penangguhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1183">1183</a><span>]</span><span> </span><span>bulan suci itu</span><span> hanyalah tambahan dalam kekafiran ; dengan itu orang-orang yang ingkar disesatkan. Setahun mereka menghalalkan dan di tahun yang lain mereka mengharamkannya, supaya dapat mereka melaraskan dengan bilangan </span><span>bulan</span><span> yang dinyatakan suci oleh Allah Dengan demikian mereka menghalalkan apa yang diharamkan Allah. </span><span>[a]</span><span> Perbuatan mereka yang buruk itu ditampakkan indah kepada mereka. Dan Allah tidak memberi petunjuk kepada kaum kafir.</span></p><p><span>[a]</span><span> Lihat 6:44 ; 13:34 ; 16:64 ; 27:25 ; 29:49 ; 35:9.</span></p>');
INSERT INTO `quran` VALUES (7628,2,9,38,'<p><span>Wahai orang-orang yang beriman, apa yang terjadi atas dirimu bila dikatakan kepadamu, “Berangkatlah di jalan Allah, kamu lebih berat </span><span>condong</span><span> mencintai bumi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1184">1184</a><span>]</span><span> Adakah kamu </span><span>[b]</span><span> lebih menyukai kehidupan duniawi daripada ukhrawi ? Padahal, </span><span>[c]</span><span> kesenangan hidup di dunia ini dibandingkan dengan di akhirat, itu hanya sedikit.</span></p><p><span>[b]</span><span> 13:27.</span></p><p><span>[c]</span><span> Lihat 3:15.</span></p>');
INSERT INTO `quran` VALUES (7629,2,9,39,'<p><span>Jika kamu tidak berangkat </span><span>untuk berjihad,</span><span> Dia akan mengazabmu dengan azab yang pedih dan akan mengganti kamu dengan kaum yang lain, dan kamu tak akan merugikan Dia sedikit pun. Dan Allah berkuasa atas segala sesuatu</span></p>');
INSERT INTO `quran` VALUES (7630,2,9,40,'<p><span>Jika kamu tidak menolongnya, maka sesungguhnya Allah telah menolongnya ketika ia di usir oleh orang-orang ingkar, sedangkan ia kedua dari yang dua ketika keduanya berada dalam gua, maka ia berkata kepada temannya, “Janganlah engkau bersedih, sesungguhnya Allah beserta kita,” lalu </span><span>[a]</span><span> Allah menurunkan ketenteraman-Nya kepadanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1185">1185</a><span>]</span><span> dan menolongnya dengan lasykar-lasykar yang tidak kamu lihat, dan Dia menjadikan perkataan orang-orang yang ingkar itu rendah dan Kalimah Allah itulah yang tertinggi. Dan Allah Maha Perkasa, Maha Bijaksana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1186">1186</a><span>]</span></p><p><span>[a]</span><span> 9:26 ; 48:27.</span></p>');
INSERT INTO `quran` VALUES (7631,2,9,41,'<p><span>Berangkatlah, </span><span>baik</span><span> ringan maupun berat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1187">1187</a><span>]</span><span> dan </span><span>[a]</span><span> berjihadlah dengan harta kamu dan jiwa kamu di jalan Allah Yang demikian itu lebih baik bagi kamu, sekiranya kamu mengetahui.</span></p><p><span>[a]</span><span> 8:75 ; 9:88, 111 ; 61:12.</span></p>');
INSERT INTO `quran` VALUES (7632,2,9,42,'<p><span>Sekiranya merupakan keuntungan yang dekat dan perjalanan yang pendek, niscaya, mereka akan mengikuti engkau, akan tetapi perjalanan berat itu terasa amat jauh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1188">1188</a><span>]</span><span> bagi mereka. Dan Mereka akan bersumpah dengan </span><span>nama </span>Allah <span>dengan berkata, </span><span>“Jika kami mampu, niscaya kami akan berangkat bersama kamu.” Mereka membinasakan diri sendiri, dan Allah mengetahui bahwa sesungguhnya mereka itu pendusta.</span></p>');
INSERT INTO `quran` VALUES (7633,2,9,43,'<p><span>Allah menghilangkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1189">1189</a><span>]</span><span> </span><span>akibat buruk</span><span> kesalahan engkau. Mengapa engkau memberi izin kepada mereka </span><span>tinggal di belakang</span><span> sebelum nyata kepada engkau orang-orang yang benar dan </span><span>juga</span><span> engkau mengetahui orang-orang yang dusta ?</span></p>');
INSERT INTO `quran` VALUES (7634,2,9,44,'<p><span>Orang-orang yang beriman kepada Allah dan Hari Kemudian tidak akan meminta izin kepada engkau </span><span>agar dikecualikan dari </span><span> berjihad dengan harta mereka dan jiwa mereka. Dan Allah Maha Mengetahui orang-orang yang bertakwa.</span></p>');
INSERT INTO `quran` VALUES (7635,2,9,45,'<p><span>Sesungguhnya yang meminta izin kepada engkau </span><span>agar dikecualikan,</span><span> hanyalah orang-orang yang tidak beriman kepada Allah dan Hari Kemudian, dan hati mereka ragu-ragu sehingga mereka bimbang dalam keraguan mereka.</span></p>');
INSERT INTO `quran` VALUES (7636,2,9,46,'<p><span>Dan sekiranya mereka bermaksud pergi </span><span>untuk berperang,</span><span> niscaya mereka akan menyiapakan perlengkapan untuk itu ; akan tetapi Allah tidak menyukai keberangkatan mereka. Karena itu Dia menahan mereka dan dikatakan </span><span>kepada mereka, </span><span>“Duduklah </span><span>dirumah</span><span> bersama </span><span>–</span><span>sama orang-orang yang duduk.”</span></p>');
INSERT INTO `quran` VALUES (7637,2,9,47,'<p><span>Andaikata mereka keluar bersama kamu, </span><span>[a]</span><span> mereka tidak akan menambah bagimu selain kerusakan, dan niscaya mereka akan bergegas mundar-mandir di tengah-tengahmu </span><span>dan</span><span> menginginkan </span><span>timbulnya </span><span> fitnah di antaramu. Dan, di kalangan kamu pun ada yang suka mendengar-dengar </span><span>berita dan menyampaikannya </span><span> kepada mereka. Dan, Allah Maha Mengetahui orang-orang yang aniaya.</span></p><p><span>[a]</span><span> 3:119</span></p>');
INSERT INTO `quran` VALUES (7638,2,9,48,'<p><span>Sesungguhnya mereka telah menghendaki </span><span>timbulnya</span><span> fitnah sebelumnya dan mereka merancang tipu-daya terhadap engkau, hingga datanglah kebenaran dan menanglah keputusan Allah, sedang mereka tidak menyukai.</span></p>');
INSERT INTO `quran` VALUES (7639,2,9,49,'<p><span>Dan di antara mereka ada orang yang berkata, “Izinkanlah aku </span><span>tinggal di belakang</span><span> dan janganlah engkau mencobai aku.” Perhatikanlah, mereka telah jatuh ke dalam cobaan. Dan sesungguhnya Jahannam itu mengepung orang-orang kafir.</span></p>');
INSERT INTO `quran` VALUES (7640,2,9,50,'<p><span>Jika suatu kebaikan menimpa engkau, hal itu menyedihkan mereka ; tetapi, jika engkau ditimpa suatu musibah, mereka berkata, “Kami telah mengambil tindakan pencegahan kami lebih dahulu ; “ dan mereka berpaling seraya bergirang hati.</span></p>');
INSERT INTO `quran` VALUES (7641,2,9,51,'<p><span>Katakanlah, “Sekali-kali tidak akan menimpa diri kami selain apa yang telah ditetapkan Allah bagi kami. Dia Pelindung kami. Dan hanya kepada Allah hendaknya orang-orang mukmin bertawakkal.”</span></p>');
INSERT INTO `quran` VALUES (7642,2,9,52,'<p><span>Katakanlah, “Tiada </span><span>sesuatu</span><span> yang kamu nantikan untuk kami kecuali salah satu dari dua kebaikan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1190">1190</a><span>]</span><span> dan kamu pun menantikan untukmu agar Allah akan menumpakan azab kepadamu dari sisi-Nya atau dengan tangan kami. Maka tunggulah, sesungguhnya kami pun menunggu besertamu.”</span></p>');
INSERT INTO `quran` VALUES (7643,2,9,53,'<p><span>Katakanlah. “belanjakanlah dengan rela atau pun dengan terpaksa ; ini sekali-kali tidak akan di terima </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1191">1191</a><span>]</span><span> dari kamu. Sesungguhnya kamu kaum yang durhaka.”</span></p>');
INSERT INTO `quran` VALUES (7644,2,9,54,'<p><span>Dan tidak ada yang menghalangi mereka agar diterima dari mereka sumbangan mereka, kecuali hanya karena mereka tidak beriman kepada Allah dan Rasul-Nya, dan </span><span>[a]</span><span> mereka tidak mendirikan shalat kecuali dengan malas dan mereka tidak membelanjakan harta </span><span>di jalan Allah</span><span> kecuali dengan enggan.</span></p><p><span>[a]</span><span> 4:143.</span></p>');
INSERT INTO `quran` VALUES (7645,2,9,55,'<p><span>Maka </span><span>[b]</span><span> janganlah menakjubkan engkau harta mereka dan jangan pula anak-anak mereka. Sesungguhnya Allah hanya berkehendak mengazab mereka dengan itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1192">1192</a><span>]</span><span> dalam kehidupan di dunia ini dan supaya jiwa mereka dicabut dalam keadaan mereka kafir.</span></p><p><span>[b]</span><span> 9:85.</span></p>');
INSERT INTO `quran` VALUES (7646,2,9,56,'<p><span>Dan, mereka bersumpah dengan </span><span>nama</span><span> Allah bahwa sesungguhnya mereka dari </span><span>golongan </span><span>kamu, padahal mereka bukan dari </span><span>golongan</span><span> kamu, akan tetapi mereka itu kaum pengecut.</span></p>');
INSERT INTO `quran` VALUES (7647,2,9,57,'<p><span>Sekiranya mereka menemukan suatu tempat berlindung atau gua-gua atau lubang-lubang yang dapat dimasuki, niscaya mereka akan berpaling kepadanya dan mereka berlari tunggang-langgang.</span></p>');
INSERT INTO `quran` VALUES (7648,2,9,58,'<p><span>Dan di antara mereka ada </span><span>[a]</span><span> yang mencela engkau tentang </span><span>pembagian</span><span> sedekah-sedekah. Jika diberikan kepada mereka </span><span>sebagian</span><span> darinya mereka bergembira, tetapi jika mereka tidak diberi </span><span>bagian</span><span> darinya, serta-merta mereka marah.</span></p><p><span>[a]</span><span> 9:79.</span></p>');
INSERT INTO `quran` VALUES (7649,2,9,59,'<p><span>Dan jika mereka bersengang hati dengan apa yang telah diberikan Allah dan Rasul-Nya kepada mereka dan berkata, “Cukuplah Allah bagi kami ; Allah akan memberikan kepada kami sebagian dari karunia-Nya, dan </span><span>demikian pula </span><span> Rasul-Nya ; sesungguhnya hanya kepada Allah kami berharap.”</span></p>');
INSERT INTO `quran` VALUES (7650,2,9,60,'<p><span>Sesungguhnya sedekah-sedekah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1193">1193</a><span>]</span><span> itu untuk orang-orang fakir dan orang-orang miskin dan petugas-petugas dalam urusan itu dan orang-orang yang dipikat hatinya dan untuk </span><span>membebaskan</span><span> tawanan dan untuk mereka yang berhutang dan untuk </span><span>mujahid-mujahid</span><span> di jalan Allah dan orang-orang musafir, </span><span>yang demikian</span><span> itu ketetapan dari Allah Dan Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (7651,2,9,61,'<p><span>Dan, di antara mreka ada yang menyakiti Nabi sambil mengatakan, “Ia </span><span>hanyalah </span><span>kuping.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1194">1194</a><span>]</span><span> Katakanlah, “</span><span>Ia</span><span> kuping untuk kebaikan kamu ; ia beriman kepada Allah dan mempercayai orang-orang mukmin, dan </span><span>[a]</span><span> rahmat bagi orang-orang yang telah beriman di antaramu .” Dan orang-orang yang menyakiti Rasul Allah, bagi mereka azab yang pedih.</span></p><p><span>[a]</span><span> 9:128 ; 21:108.</span></p>');
INSERT INTO `quran` VALUES (7652,2,9,62,'<p><span>[b]</span><span> Mereka bersumpah kepada kamu dengan </span><span>nama</span><span> Allah untuk menyenangkanmu, padahal Allah dan Rasul-Nya lebih berhak agar mereka benar-benar orang-orang mukmin.</span></p><p><span>[b]</span><span> 9:96.</span></p>');
INSERT INTO `quran` VALUES (7653,2,9,63,'<p><span>Tidaklah mereka mengetahui bahwa </span><span>[c]</span><span> barangsiapa menentang Allah dan Rasul-Nya, maka baginya ada Api Jahannam yang mereka akan tinggal lama di dalamnya ? Itulah kehinaan yang besar.</span></p><p><span>[c]</span><span> 58:6, 21.</span></p>');
INSERT INTO `quran` VALUES (7654,2,9,64,'<p><span>Orang-orang munafik itu khawatir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1195">1195</a><span>]</span><span> kalau-kalau sebuah Surah diturunkan tentang mereka, mengabarkan kepada mereka apa yang ada dalam hati mereka. Katakanlah, “Teruskan olok-olokkan kamu ! Sesungguhnya Allah akan menzahirkan apa-apa yang kamu khawatirkan itu. “</span></p>');
INSERT INTO `quran` VALUES (7655,2,9,65,'<p><span>Dan, Kalau engkau menanyakan kepada mereka, tentu </span><span>[a]</span><span> mereka akan menjawab, “Sesunggunya kami hanya bersenda gurau dan bermain-main.” Katakanlah, “Apakah Allah dan Ayat-ayat-Nya dan Rasul-Nya yang kamu perolok-olokan itu ?”</span></p><p><span>[a]</span><span> 2:45.</span></p>');
INSERT INTO `quran` VALUES (7656,2,9,66,'<p><span>[b]</span><span> Janganlah kamu mengemukakan dalih, sesungguhnya kamu ingkar setelah kamu beriman. Jika Kami memaafkan suatu golongan dari antaramu, Kami akan mengazab golongan </span><span>lain</span><span> karena mereka orang-orang yang berdosa.</span></p><p><span>[b]</span><span> 56:8.</span></p>');
INSERT INTO `quran` VALUES (7657,2,9,67,'<p><span>Orang-orang munafik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1196">1196</a><span>]</span><span> laki-laki dan orang-orang munafik perempuan itu sebagian dengan sebagian lainnya </span><span>sama,</span><span> mereka menyuruh </span><span>mengerjakan</span><span> keburukan dan melarang kebaikan, dan mereka menahan tangan mereka </span><span>dari membelanjakan harta di jalan Allah</span><span> </span><span>[a]</span><span> Mereka melupakan Allah karena itu Dia pun melupakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1197">1197</a><span>]</span><span> mereka. Sesungguhnya, orang-orang munafik itu merekalah orang-orang durhaka.</span></p><p><span>[a]</span><span> 59:20.</span></p>');
INSERT INTO `quran` VALUES (7658,2,9,68,'<p><span>[b]</span><span> Allah menjanjikan kepada orang-orang munafik laki-laki dan munafik perempuan, dan kepada orang-orang ingkar, Api Jahannam yang di dalamnya mereka akan tinggal lama. Itu memadai mereka dan Allah melaknat mereka, dan bagi mereka azab yang sangat lama.</span></p><p><span>[b]</span><span> 4:146.</span></p>');
INSERT INTO `quran` VALUES (7659,2,9,69,'<p><span>Seperti orang-orang sebelum kamu, mereka lebih kuat dari kamu dan lebih banyak harta dan anak-anak. Mereka telah menikmati bagian mereka, dan kamu pun telah menikmati bagian mereka. Dan kamu asyik bercakap kosong sebagaimana mereka asyik bercakap kosong. </span><span>[c]</span><span> Mereka itulah yang telah dia-sia amal mereka di dunia dan akhirat. Dan mereka itulah orang-orang yang rugi.</span></p><p><span>[c]</span><span> 15:106.</span></p>');
INSERT INTO `quran` VALUES (7660,2,9,70,'<p><span>[a]</span><span> Tidak sampaikah kepada mereka berita mengenai orang-orang yang sebelum mereka, kaum Nuh dan </span><span>‘</span><span>Ad dan Tsamud dan kaum Ibrahim dan penduduk Madyan dan negeri-negeri yang telah dibinasakan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1198">1198</a><span>]</span><span> Telah datang kepada mereka Rasul-rasul mereka dengan Tanda-tanda yang nyata. Maka, </span><span>[b]</span><span> bukan Allah Yang berkehendak menganiaya mereka, namun merekalah yang menganiaya diri mereka sendiri.</span></p><p><span>[a]</span><span> 14:10 ; 50:13 </span><span>–</span><span> 15.</span></p><p><span>[b]</span><span> 10:45 ; 29:41 ; 30:10.</span></p>');
INSERT INTO `quran` VALUES (7661,2,9,71,'<p><span>Dan, orang-orang mukmin laki-laki dan perempuan itu satu sama lain bersahabat. </span><span>[c]</span><span> Mereka menyuruh kepada kebaikan dan mencegah dari keburukan dan </span><span>[d]</span><span> mendirikan shalat dan </span><span>[e]</span><span> membayar zakat serta </span><span>[f]</span><span> menaati Allah dan Rasul-Nya. Mereka itulah yang akan dikasih Allah Sesungguhnya Allah Maha Perkasa, Maha Bijaksana.</span></p><p><span>[c]</span><span> 3:105, 111 ; 7:158 ; 9:112 ; 31:18</span></p><p><span>[d]</span><span> Lihat 2:4.</span></p><p><span>[e]</span><span> Lihat 2:44</span></p><p><span>[f]</span><span> Lihat 8:2.</span></p>');
INSERT INTO `quran` VALUES (7662,2,9,72,'<p><span>[a]</span><span> Allah telah menjanjikan kepada orang-orang mukmin laki-laki dan mukmin perem puan, kebun-kebun yang di bawahnya mengalir sungai-sungai </span><span>dan</span><span> di dalamnya mereka akan menetap ; dan tempat-tempat tinggal yang baik di dalam kebun-kebun abadi. Dan, </span><span>[b]</span><span> keridhaan Allah yang laing besar. Hal demikian itu kejayaan yag besar.</span></p><p><span>[a]</span><span> Lihat 2:26.</span></p><p><span>[b]</span><span> 3:16 ; 5:3 ; 9:22 ; 57:21.</span></p>');
INSERT INTO `quran` VALUES (7663,2,9,73,'<p><span>[c]</span><span> Wahai Nabi, berjihadlah terhadap </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1199">1199</a><span>]</span><span> orang-orang ingkar dan orang-orang munafik, dan bersikap keraslah terhadap mereka. Tempat tinggal mereka Jahannam. Dan seburuk-buruk tempat kembali.</span></p><p><span>[c]</span><span> 66:10.</span></p>');
INSERT INTO `quran` VALUES (7664,2,9,74,'<p><span>Mereka bersumpah dengan </span><span>nama</span><span> Allah bahwa mereka tidak mengatakan sesuatu padahal mereka sebenarnya telah mengucapkan perkataan kufur dan telah ingkar sesudah mereka </span><span>memeluk</span><span> Islam, dan mereka merencanakan sesuatu yang tidak dapat dicapai oleh mereka. Dan tidaklah mereka menaruh dendam kecuali karena Allah dan Rasul-Nya telah memperkaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1200">1200</a><span>]</span><span> mereka dari karunia-Nya. Maka jika mereka bertaubat, itu lebih baik bagi mereka, dan jika mereka berpaling, Allah akan mengazab mereka dengan azab yang pedih di dunia maupun di akhirat dan tidak akan ada bagi mereka di bumi ini seorang sahabat dan tidak </span><span>pula </span><span> penolong.</span></p>');
INSERT INTO `quran` VALUES (7665,2,9,75,'<p><span>Dan, di antara mereka ada yang berjanji kepada Allah </span><span>dengan berkata,</span><span> “Andaikata Dia memberikan kepada kami sebagian karunia-Nya, pastilah kami akan bersedekah dan pastilah kami akan menjadi orang-orang yang shaleh.”</span></p>');
INSERT INTO `quran` VALUES (7666,2,9,76,'<p><span>Maka, tatkala Dia memberikan kepada mereka dari karunia-Nya, mereka menjadi bakhil dengannya, dan mereka berpaling dan mereka menghindar dengan benci.</span></p>');
INSERT INTO `quran` VALUES (7667,2,9,77,'<p><span>Maka, Dia mengakibatkan mereka </span><span>mengidap</span><span> kemunafikan di dalam hati mereka sampai hari ketika mereka bertemu dengan Dia, karena mereka telah menyalahi janji kepada Allah dengan apa-apa yang mereka janjikan kepada-Nya dan karena mereka telah berdusta.</span></p>');
INSERT INTO `quran` VALUES (7668,2,9,78,'<p><span>Tidaklah mereka mengetahui bahwa </span><span>[a]</span><span> Allah mengetahui rahasia mereka dan perundingan rahasia mereka dan bahwa Allah Maha Mengetahui segala yang gaib ?</span></p><p><span>[a]</span><span> 6:4 ; 11:6 ; 25:7 ; 28:70.</span></p>');
INSERT INTO `quran` VALUES (7669,2,9,79,'<p><span>Orang-orang </span><span>munafik inilah</span><span> </span><span>[b]</span><span> yang mencela orang-orang mukmin yang memberi sedekah dengan rela, dan </span><span>mencela juga</span><span> orang-orang yang tidak mendapatkan </span><span>apa pun untuk diberikan</span><span> selain </span><span>hasil</span><span> jerih-payah mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1201">1201</a><span>]</span><span> Lalu, mereka memperolok-olokkan mereka itu. Allah akan membalas olok-olok mereka, dan untuk mereka azab yang pedih.</span></p><p><span>[b]</span><span> 9:58.</span></p>');
INSERT INTO `quran` VALUES (7670,2,9,80,'<p><span>Engkau </span><span>[a]</span><span> memintakan ampunan bagi mereka atau engkau tidak memintakan ampunan bagi mereka ; sekalipun engkau meminta ampunan bagi mereka tujuh puluh kali, Allah sekali-kali tidak akan mengampuni mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1202">1202</a><span>]</span><span> Hal demikian itu karena mereka ingkar kepada Allah dan Rasul-Nya. Dan. Allah tidak memberi petunjuk kepada kaum yang durhaka.</span></p><p><span>[a]</span><span> 63:7.</span></p>');
INSERT INTO `quran` VALUES (7671,2,9,81,'<p><span>[b]</span><span> Orang-orang yang dibiarkan tinggal di belakang duduk-duduk di rumah mereka, merasa gembira, bertentangan dengan perintah Rasul Allah, karena mereka tidak suka berjihad dengan harta mereka dan jiwa mereka di jalan Allah. Dan, mereka berkata, “Janganlah kamu berangkat dalam panas terik.” Katakan, “Api Jahannam lebih hebat panasnya.” Seandainya mereka mengerti !</span></p><p><span>[b]</span><span> 9:67, 93.</span></p>');
INSERT INTO `quran` VALUES (7672,2,9,82,'<p><span>Maka, hendaklah mereka sedikit tertawa dan banyak menangis, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1203">1203</a><span>]</span><span> sebagai balasan atas apa yang telah mereka usahakan.</span></p>');
INSERT INTO `quran` VALUES (7673,2,9,83,'<p><span>Maka, jika Allah mengembalikan engkau kepada segolongan di antara mereka dan mereka miminta izin kepada engkau untuk keluar </span><span>berperang,</span><span> maka katakanlah, “Kalian sekali-kali tidak boleh keluar bersamaku dan kalian sekali-kalli tidak boleh memerangi musuh bersamaku. Sesungguhnya kalian menyukai duduk </span><span>di rumah</span><span> sejak pertama kali, karena itu duduklah kalian bersama-sama orang-orang yang tinggal di belakang.”</span></p>');
INSERT INTO `quran` VALUES (7674,2,9,84,'<p><span>Dan, janganlah sekali-kali engkau menyembahyangkan </span><span>jenazah</span><span> seorang yang mati di antara mereka dan jangan pula engkau berdiri </span><span>mendoa</span><span> di atas kuburannya. Sesungguhnya mereka ingkar kepada Allah dan Rasul-Nya dan mereka mati dalam keadaan durhaka.</span></p>');
INSERT INTO `quran` VALUES (7675,2,9,85,'<p><span>[a]</span><span> Dan, janganlah engkau takjub harta mereka dan anak-anak mereka. Allah hanya berkehendak mengazab mereka dengan itu di dunia dan supaya jiwa mereka melayang sedang mereka dalam keadaan kafir.</span></p><p><span>[a]</span><span> 9:55.</span></p>');
INSERT INTO `quran` VALUES (7676,2,9,86,'<p><span>Dan, apabila diturunkan suatu surah bahwa, “Berimanlah kamu kepada Allah dan berjihadlah beserta Rasul-Nya, “ maka orang-orang yang kaya di antara mereka meminta izin kepada engkau dan berkata, “Tinggalkanlah kami bersama orang-orang yang duduk </span><span>di belakang.</span><span>” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1204">1204</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7677,2,9,87,'<p><span>[a]</span><span> Mereka menyukai berada bersama suku-suku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1205">1205</a><span>]</span><span> yang tinggal di belakang dan </span><span>[b]</span><span> hati mereka dimaterai </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1206">1206</a><span>]</span><span> sehingga mereka tidak mengerti.</span></p><p><span>[a]</span><span> 9:81, 93.</span></p><p><span>[b]</span><span> 6:26 ; 63:4.</span></p>');
INSERT INTO `quran` VALUES (7678,2,9,88,'<p><span>[c]</span><span> Akan tetapi, Rasul itu dan orang-orang yang beriman bersamanya telah berjihad </span><span>di jalan Allah</span><span> dengan harta mereka dan jiwa mereka ; dan mereka itulah orang-orang yang akan memperoleh bermacam-macam kebaikan, dan mereka itulah orang-orang yang akan menang.</span></p><p><span>[c]</span><span> 8:75 ; 9:41, 111 ; 61:12.</span></p>');
INSERT INTO `quran` VALUES (7679,2,9,89,'<p><span>[a]</span><span> Allah telah menyediakan bagi mereka kebun-kebun yang mengalir di bawahnya sungai-sungai, mereka akan tinggal tetap di dalamnya. Inilah kemenangan yang besar.</span></p><p><span>[a]</span><span> Lihat 2:26.</span></p>');
INSERT INTO `quran` VALUES (7680,2,9,90,'<p><span>Dan, datanglah orang-orang yang mengemukakan alasan-alasan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1207">1207</a><span>]</span><span> dari antara orang-orang Arab gurun, </span><span>seraya memohon</span><span> supaya mereka diizinkan </span><span>tinggal di belakang;</span><span> dan orang-orang yang mendustai Allah dan Rasul-Nya duduk </span><span>di rumah</span><span>. Akan menimpa orang </span><span>–</span><span>orang yang ingkar di antara mereka azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (7681,2,9,91,'<p><span>[b]</span><span> Tak ada celaan terhadap orang-orang yang lemah, dan tidak pula terhadap orang-orang yang sakit, dan tidak pula terhadap orang-orang yang tidak memperoleh sesuatu yang dapat mereka belanjakan, apabila mereka berlaku ikhlas terhadap Allah dan Rasul-Nya. Tidak ada jalan untuk </span><span>mencela</span><span> orang-orang yang berbuat baik, dan Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[b]</span><span> 48:18.</span></p>');
INSERT INTO `quran` VALUES (7682,2,9,92,'<p><span>Dan, tidak pula </span><span>ada celaan</span><span> terhadap orang-orang yang ketika mereka datang kepada engkau supaya engkau menyediakan kendaraan bagi mereka, engkau berkata, “Aku tidak memperoleh sesuatu yang dapat mengangkut kamu ;” mereka kembali dengan mata mereka berlinang oleh air mata karena sedih, disebabkan mereka tiak memperoleh apa-apa yang dapat mereka belanjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1208">1208</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7683,2,9,93,'<p><span>Sesungguhnya jalan </span><span>untuk mencela</span><span> itu hanya terhadap orang-orang yang meminta izin kepada engkau, padahal mereka orang-orang kaya. </span><span>[a]</span><span> Mereka </span><span>lebih</span><span> suka berada bersama-sama suku-suku yang tinggal di belakang. Dan </span><span>[b]</span><span> Allah telah memeterai hati mereka sehingga mereka tidak mengetahui.</span></p><p><span>[a]</span><span> 9:19, 87.</span></p><p><span>[b]</span><span> 6:26 ; 9:87 ; 63:4.</span></p>');
INSERT INTO `quran` VALUES (7684,2,9,94,'<p><span>Juz XI</span></p><p><span>Mereka akan mengemukakan alasan-alasan kepada kamu apabila kamu telah kembali kepada mereka. Katakanlah, “Janganlah kamu membuat alasan-alasan ; kami tidak mempercayai kamu. Sesungguhnya Allah telah memberitahukan kepada kami tentang hal-ihwalmu. Allah dan Rasul-Nya pasti akan melihat amalmu ; kemudian kamu akan dikembalikan kepada Yang Mengetahui yang gaib dan yang nyata, maka Dia akan memberitahukan kepadamu apa-apa yang telah kamu kerjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1209">1209</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7685,2,9,95,'<p><span>Mereka akan bersumpah kepadamu dengan </span><span>nama </span><span> Allah apabila kamu kembali kepada mereka, supaya kamu berpaling dari mereka. Maka, berpalinglah dari mereka. Sesungguhnya mereka itu kotor dan tempat tinggal mereka Jahannam, sebagai balasan terhadap apa yang mereka usahakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1210">1210</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7686,2,9,96,'<p><span>[a]</span><span> Mereka akan bersumpah kepadamu supaya kamu ridha kepada mereka. Tetapi bila kamu ridha kepada mereka, maka sesungguhnya Allah tidak ridha kepada kaum yang durhaka.</span></p><p><span>[a]</span><span> 9:62.</span></p>');
INSERT INTO `quran` VALUES (7687,2,9,97,'<p><span>Orang-orang Arab gurun itu amat keras dalam kekufuran dan kemunafikan dan lebih pantas mereka tidak mengetahui batas-batas </span><span>hukum</span><span> yang telah diturunkan Allah kepada Rasul-Nya, dan Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (7688,2,9,98,'<p><span>Dan di antara orang-orang Arab gurun itu ada yang menganggap apa yang dibelanjakan </span><span>di jalan Allah</span><span> itu sebagai denda dan ia menanti-nantikan bencana-bencana atas dirimu. </span><span>[b]</span><span> Atas merekalah bencana yang buruk. Dan Allah Maha Mendengar, Maha Mengetahui.</span></p><p><span>[b]</span><span> 48:7.</span></p>');
INSERT INTO `quran` VALUES (7689,2,9,99,'<p><span>Dan, di antara orang-orang Arab gurun itu ada yang beriman kepada Allah dan Hari Kemudian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1211">1211</a><span>]</span><span> dan menganggap apa yang dibelanjakannya itu untuk mendekatkan diri kepada Allah dan </span><span>sebagai penarik</span><span> doa-doa Rasul. Ketahuilah, sesungguhnya itulah </span><span>sarana</span><span> bagi mereka untuk mendekatkan diri </span><span>kepada Allah.</span><span> Allah pasti akan memasukkan mereka ke dalam rahmat-Nya. Sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (7690,2,9,100,'<p><span>R. 13</span></p><p><span>Dan, orang-orang terdahulu yang pertama-tama di antara kaum Muhajirin dan Anshar dan orang-orang yang mengikuti mereka dalam kebaikan, </span><span>[a]</span><span> Allah ridha kepada mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1212">1212</a><span>]</span><span> dan mereka pun ridha kepada-Nya dan Dia telah menyediakan bagi mereka kebun-kebun yang mengalir dibawahnya sungai-sungai, mereka akan menetap di dalamnya untuk selama-lamanya. Itulah kemenangan yang besar.</span></p><p><span>[a]</span><span> 58:23 ; 98:9.</span></p>');
INSERT INTO `quran` VALUES (7691,2,9,101,'<p><span>Dan, di antara orang-orang Arab gurun di sekitarmu ada orang-orang munafik dan </span><span>juga</span><span> di antara penduduk-penduduk Medinah, mereka itu gigih dalam kemunafikan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1213">1213</a><span>]</span><span> Engkau tidak mengetahui mereka. </span><span>tetapi</span><span> Kami mengetahui mereka. Kami segera akan mengazab mereka dua kali ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1214">1214</a><span>]</span><span> kemudian mereka akan dikembalikan kepada azab yang besar.</span></p>');
INSERT INTO `quran` VALUES (7692,2,9,102,'<p><span>Dan, ada orang-orang lain yang mengakui dosa-dosa mereka, telah mencampur-baurkan amal baik dengan </span><span>amal</span><span> lain yang buruk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1215">1215</a><span>]</span><span> Mudah-mudahan Allah akan menerima tobat mereka. Sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (7693,2,9,103,'<p><span>Ambillah dari harta mereka sedekah supaya engkau membersihkan mereka dan mensucikan mereka dengannya. Dan berdoalah untuk mereka ; sesungguhnya doa engkau adalah ketenteraman bagi mereka. Dan, Allah Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (7694,2,9,104,'<p><span>Apakah mereka tidak mengetahui bahwa </span><span>[a]</span><span> Allah Yang menerima tobat dari hamba-hamba-Nya dan mengambil sedekah-sedekah dan sesungguhnya Allah Dia-lah Maha Penerima tobat, Maha Penyayang ?</span></p><p><span>[a]</span><span> 42:26.</span></p>');
INSERT INTO `quran` VALUES (7695,2,9,105,'<p><span>Dan katakanlah, “Beramallah, maka </span><span>[b]</span><span> Allah dan Rasul-nya serta orang-orang yang ber iman pasti akan melihat amalmu. Dan kamu akan dikembalikan kepada Yang Mengetahui yang gaib dan yang nyata ; maka Dia akan memberitahukan kepadamu tentang apa-apa yang telah kamu kerjakan.”</span></p><p><span>[b]</span><span> 9:94</span></p>');
INSERT INTO `quran` VALUES (7696,2,9,106,'<p><span>Dan, </span><span>ada pula </span><span>[a]</span><span> orang-orang lain yang ditunda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1216">1216</a><span>]</span><span> </span><span>perkaranya sampai ada keputusan Allah</span><span>. Boleh jadi Dia akan mengazab mereka dan boleh jadi akan menerima tobat mereka. Dan Allah Maha Mengetahui, Maha Bijaksana.</span></p><p><span>[a]</span><span> 9:118.</span></p>');
INSERT INTO `quran` VALUES (7697,2,9,107,'<p><span>Dan, </span><span>di antara orang-orang munafik ada </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1217">1217</a><span>]</span><span> yang telah membuat masjid untuk kemudaratan </span><span>Islam</span><span> dan </span><span>membantu</span><span> kekufuran dan </span><span>menyebabkan</span><span> perpecahan di kalangan orang-orang yang beriman, dan membuat tempat untuk memata-matai orang-orang yang memerangi Allah dan Rasul-Nya sebelum ini. Dan, mereka pasti akan bersumpah, “Kami bermaksud tiada lain kecuali kebaikan.” Dan </span><span>[b]</span><span> Allah menyaksikan, sesungguhnya mereka itu pendusta- pendusta.</span></p><p><span>[b]</span><span> 63:2</span></p>');
INSERT INTO `quran` VALUES (7698,2,9,108,'<p><span>Janganlah emgkau berdiri </span><span>shalat</span><span> di dalamnya untuk selama-lamanya. Sesungguhnya masjid yang pondasinya diletakkan atas takwa semenjak hari permulaan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1218">1218</a><span>]</span><span> engkau lebih berhak berdiri </span><span>untuk shalat</span><span> di dalamnya. Di dalamnya ada orang-orang yang berkeinginan mensucikan diri, dan Allah mencintai orang- orang yang mensucikan diri.</span></p>');
INSERT INTO `quran` VALUES (7699,2,9,109,'<p><span>Maka, apakah orang yang telah mendirikan bangunannya atas dasar takwa kepada Allah dan keridhaan-Nya itu yang baik ataukah orang yang mendirikan bangunannya di atas tebing yang terkikis air dan mau runtuh, lalu jatuh besertanya ke dalam Api Jahannam ? Dan, Allah tidak memberi petunjuk kepada kaum yang aniaya.</span></p>');
INSERT INTO `quran` VALUES (7700,2,9,110,'<p><span>Bangunan mereka yang telah mereka dirikan senantiasa akan menjadi keraguan dalam hari mereka, kecuali bila hati mereka itu tersayat-sayat, dan Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (7701,2,9,111,'<p><span>Sesungguhnya, </span><span>[a]</span><span> Allah telah membeli dari orang-orang mukmin jiwa mereka dan harta mereka bahwa bagi mereka tersedia surga. </span><span>[b]</span><span> Mereka berperang di jalan Allah, dan mereka membunuh atau terbunuh, janji yang pasti dari-Nya dalam Taurat dan Injil </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1219">1219</a><span>]</span><span> dan Alquran. Dan siapakah yang lebih menepati janjinya daripada Allah? Maka bergembiralah dengan jual-belimu yang kamu telah lakukan dengan-Nya, dan itulah kemenangan yang besar.</span></p><p><span>[a]</span><span> 4:75 ; 61:11, 12.</span></p><p><span>[b]</span><span> 3:196 ; 61:5.</span></p>');
INSERT INTO `quran` VALUES (7702,2,9,112,'<p><span>Yaitu,</span><span> orang-orang yang bertobat, </span><span>[a]</span><span> yang beribadah, yang memuji </span><span>Allah, </span><span>yang bepergian </span><span>pada jalan Allah,</span><span> yang ruku</span><span>’</span><span>, yang sujud, </span><span>[b]</span><span> yang menyuruh kepada kebaikan dan melarang keburukan dan yang menjaga batas-batas </span><span>hukum</span><span> Allah. Dan sampaikanlah kabar suka, kepada orang-orang yang beriman.</span></p><p><span>[a]</span><span> 33:36.</span></p><p><span>[b]</span><span> 3:105, 111, 115 ; 7:158 ; 9:71 ; 31:18.</span></p>');
INSERT INTO `quran` VALUES (7703,2,9,113,'<p><span>Tidaklah patut bagi Nabi dan orang-orang yang beriman memohonkan ampunan </span><span>dari Allah</span><span> untuk orang-orang musyrik, meskipun mereka kaum kerabat, </span><span>yaitu</span><span> setelah jelas kepada mereka bahwa mereka itu penghuni Jahannam.</span></p>');
INSERT INTO `quran` VALUES (7704,2,9,114,'<p><span>Dan tidaklah permohonan ampunan Ibrahim untuk bapaknya kecuali karena perjanjian yang telah dijanjikan kepadanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1220">1220</a><span>]</span><span> maka ketika telah jelas baginya bahwa </span><span>bapaknya</span><span> itu musuh Allah, maka ia berlepas dari darinya. </span><span>[c]</span><span> Sesungguhnya Ibrahim sangat lembut hati, penyantun.</span></p><p><span>[c]</span><span> 19:48 ; 26:87 ; 60:5.</span></p>');
INSERT INTO `quran` VALUES (7705,2,9,115,'<p><span>Dan bukanlah </span><span>sifat</span><span> Allah untuk menyesatkan suatu kaum setelah Dia memberi petunjuk kepada mereka, sebelum Dia menjelaskan kepada mereka apa-apa yang harus mereka hindari. Sesungguhnya, Allah Maha Mengetahui segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (7706,2,9,116,'<p><span>Sesungguhnya </span><span>[a]</span><span> Allah, bagi-Nya kerajaan seluruh langit dan bumi. Dia menghidupkan dan mematikan. Dan tidak ada bagimu selain Allah seorang pun sahabat dan penolong.</span></p><p><span>[a]</span><span> 11:76 ; 39:45 ; 57:3.</span></p>');
INSERT INTO `quran` VALUES (7707,2,9,117,'<p><span>Sesungguhnya Allah telah kembali dengan kasih-sayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1221">1221</a><span>]</span><span> Kepada Nabi dan para Muhajirin dan para Anshar yang telah mengikutinya di saat kesusahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1222">1222</a><span>]</span><span> setelah hati segolongan di antara mereka hampir berpaling </span><span>dari tugas.</span><span> Lalu Dia kembali lagi dengan kasih-sayang kepada mereka. Sesungguhnya, kepada mereka dia Maha Pengasih, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (7708,2,9,118,'<p><span>Dan kepada tiga orang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1223">1223</a><span>]</span><span> yang tertinggal </span><span>[a]</span><span> dibelakang. Sehingga ketika sempit bagi mereka bumi dengan </span><span>segala</span><span> keluasannya dan sempit bagi mereka jiwa mereka sendiri dan mereka menyangka bahwa tidak ada tempat berlindung dari </span><span>kemurkaan</span><span> Allah kecuali kepada-Nya. Kemudian Dia kembali dengan kasih-sayang kepada mereka supaya mereka bertobat. Sesungguhnya, Allah Maha Penerima tobat, Maha Penyayang.</span></p><p><span>[a]</span><span> 9:106.</span></p>');
INSERT INTO `quran` VALUES (7709,2,9,119,'<p><span>Hai orang-orang yang beriman, </span><span>[b]</span><span> bertakwalah kamu kepada Allah dan hendaklah kamu bersama orang-orang yang benar.</span></p><p><span>[b]</span><span> 3:103 ; 5:36 ; 39:11 ; 57:29.</span></p>');
INSERT INTO `quran` VALUES (7710,2,9,120,'<p><span>Tidaklah patut bagi penduduk Medinah dan orang-orang Arab gurun di sekitarnya tertinggal di belakang Rasul Allah </span><span>dalam peperangan</span><span> dan tidak mementingkan diri mereka sendiri di atas dirinya </span><span>Rasul.</span><span> Hal demikian karena mereka tidak akan ditimpah dahaga dan tidak kelelahan dan tidak kelaparan di jalan Allah, dan tidak mereka akan memijak tempat berpijak yang menjadikan marah orang-orang kafir dan tidaklah mereka meraih dari musuh suatu kemenangan, melainkan akan ditulis bagi mereka karena hal itu suatu amal shaleh. Sesungguhnya, Allah tidak akan menyia-nyiakan ganjaran orang-orang yang ber buat baik,</span></p>');
INSERT INTO `quran` VALUES (7711,2,9,121,'<p><span>Dan, tidaklah mereka membelanjakan suatu nafkah yang kecil maupun besar, dan tidak pula mereka melintasi suatu lembah melainkan dituliskan bagi mereka, supaya </span><span>[a]</span><span> Allah memberi mereka ganjaran yang terbaik untuk apa-apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 16:97, 98 ; 24:39 ; 39:36.</span></p>');
INSERT INTO `quran` VALUES (7712,2,9,122,'<p><span>Dan, </span><span>[a]</span><span> tidak mungkin bagi orang-orang mukmin keluar semuanya. Maka mengapa tidak keluar dari setiap golongan mereka satu rombongan supaya mereka memperdalam ilmu agama, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1224">1224</a><span>]</span><span> dan agar mereka memperingatkan kaum mereka, apabila kembali kepada mereka supaya mereka takut </span><span>dari kesesatan.</span><span> </span></p><p><span>[a]</span><span> 3:105.</span></p>');
INSERT INTO `quran` VALUES (7713,2,9,123,'<p><span>R. 16</span></p><p><span>Hai orang-orang yang beriman, </span><span>[b]</span><span> perangilah orang-orang kafir yang tinggal di dekatmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1225">1225</a><span>]</span><span> dan </span><span>[c]</span><span> supaya mereka mendapatkan dalam dirimu keteguhan. Dan ketahuilah bahwa Allah beserta orang-orang yang bertakwa.</span></p><p><span>[b]</span><span> 2:191.</span></p><p><span>[c]</span><span> 48:30.</span></p>');
INSERT INTO `quran` VALUES (7714,2,9,124,'<p><span>Dan, bilamana suatu surah diturunkan, maka di antara mereka ada yang berkata, “Siapakah di antaramu yang dengan ini bertambah keimanannya ? “ Tetapi bagi </span><span>[a]</span><span> orang-orang yang beriman telah menambah kepada mereka keimanan dan mereka merasa gembira.</span></p><p><span>[a]</span><span> 8:3.</span></p>');
INSERT INTO `quran` VALUES (7715,2,9,125,'<p><span>Dan adapun </span><span>[b]</span><span> orang-orang yang dalam hati mereka ada penyakit, maka </span><span>surah itu </span><span> menambah mereka kekotaran kepada kekotoran mereka, dan mereka mati dalam keadaan kafir.</span></p><p><span>[b]</span><span> 2:11.</span></p>');
INSERT INTO `quran` VALUES (7716,2,9,126,'<p><span>Apakah mereka tidak melihat bahwa mereka diuji setiap tahun sekali atau dua kali ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1226">1226</a><span>]</span><span> Namun demikian mereka tidak bertobat dan tidak pula mengambil pelajaran.</span></p>');
INSERT INTO `quran` VALUES (7717,2,9,127,'<p><span>Dan apabila suatu surah diturunkan, </span><span>[c]</span><span> sebagian mereka memandang kepada sebagian lain nya </span><span>berkata,</span><span> “Adakah seseorang melihat kamu ? “ Kemudian mereka berpaling. </span><span>[d]</span><span> Allah telah memalingkan hati mereka karena sesungguhnya mereka suatu kaum yang tidak mau mengerti.</span></p><p><span>[c]</span><span> 24:64.</span></p><p><span>[d]</span><span> 61:6.</span></p>');
INSERT INTO `quran` VALUES (7718,2,9,128,'<p><span>Sesungguhnya telah datang kepadamu kamu seorang Rasul dari antaramu ; berat terasa olehnya apa yang menyusahkan kamu, </span><span>ia</span><span> sangat mengharapkan </span><span>kesejahteraan </span><span> bagimu </span><span>dan</span><span> </span><span>[a]</span><span> terhadap orang-orang mukmin </span><span>ia </span><span> sangat berbelas kasih, penyayang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1227">1227</a><span>]</span></p><p><span>[a]</span><span> 9:61</span></p>');
INSERT INTO `quran` VALUES (7719,2,9,129,'<p><span>Maka jika mereka berpaling, maka katakanlah, “Cukuplah bagiku </span><span>[b]</span><span> Allah, tiada tuhan selain Dia. Kepada-Nya aku bertawakkal, dan Dia adalah Tuhan </span><span>‘</span><span>Arasy yang besar.</span></p><p><span>[b]</span><span> 21:23 ; 23:117 ; 27:27 ; 39:39 ; 40:16.</span></p>');
INSERT INTO `quran` VALUES (7720,2,10,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (7721,2,10,2,'<p><span>[b]</span><span> Aku Allah Yang Maha Melihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1228">1228</a><span>]</span><span> </span><span>[c]</span><span> Inilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1229">1229</a><span>]</span><span> Ayat-ayat Kitab yang penuh hikmah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1230">1230</a><span>]</span></p><p><span>[b]</span><span> 11:2 ; 12:2 ; 13:2 :14:2 ; 15:2.</span></p><p><span>[c]</span><span> 26:3 ; 27:2 ; 31:3.</span></p>');
INSERT INTO `quran` VALUES (7722,2,10,3,'<p><span>[d]</span><span> Apakah </span><span>hal ini</span><span> bagi manusia suatu keajaiban bahwa Kami telah mewahyukan kepada seorang lelaki di antara mereka bahwa, “Peringatkanlah manusia dan sampaikanlah khabar suka, kepada orang-orang yang beriman, bahwa sesungguhnya untuk mereka ada martabat yang sempurna </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1231">1231</a><span>]</span><span> di sisi Tuhan mereka ? “ Berkata orang-orang kafir, “ Sesungguhnya ini tukang sihir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1231a">1231a</a><span>]</span><span> yang nyata.”</span></p><p><span>[d]</span><span> 7:64, 70 ; 50:3.</span></p>');
INSERT INTO `quran` VALUES (7723,2,10,4,'<p><span>Sesungguhnya Tuhan-mu ialah Allah Yang telah menciptakan </span><span>[a]</span><span> seluruh langit dan bumi dalam enam masa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1232">1232</a><span>]</span><span> kemudian </span><span>[b]</span><span> Dia bersemayam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1232a">1232a</a><span>]</span><span> teguh di atas </span><span>‘</span><span>Arsy. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1233">1233</a><span>] [c]</span><span> Dia mengatur segala sesuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1234">1234</a><span>]</span><span> Tak ada seorang pun </span><span>[d]</span><span> pemberi syafaat kecuali setelah izin-Nya. Demikian Allah Tuhan-mu ; maka sembahlah Dia. Apakah kamu tidak akan mengambil pelajaran ?</span></p><p><span>[a]</span><span> 7:55 ; 11:8 ; 25:60 ; 32:5.</span></p><p><span>[b]</span><span> 13:3 ; 20:6 ; 32:5.</span></p><p><span>[c]</span><span> 32:6.</span></p><p><span>[d]</span><span> 2:256 ; 32:5.</span></p>');
INSERT INTO `quran` VALUES (7724,2,10,5,'<p><span>[e]</span><span> Kepada-Nya-lah tempat kembali kamu semua. Janji Allah itu benar. Sesungguhnya </span><span>[f]</span><span> Dia Yang memulai penciptaan, kemudian Dia mengulangnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1235">1235</a><span>]</span><span> supaya Dia memberi ganjaran kepada orang-orang yang beriman dan beramal shaleh dengan adil. Dan orang-orang yang ingkar unutk mereka ada minuman air mendidih dan azab yang pedih disebebkan mereka senantiasa ingkar.</span></p><p><span>[e]</span><span> 6:165 ; 11:5 ; 39:8.</span></p><p><span>[f]</span><span> 10:35 ; 27:65 ; 29:20 ; 30:12, 28.</span></p>');
INSERT INTO `quran` VALUES (7725,2,10,6,'<p><span>[a]</span><span> Dia Yang menjadikan matahari mempunyai cahaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1236">1236</a><span>]</span><span> dan bulan </span><span>memantulkan</span><span> sinar ; dan Dia tetapkan baginya tempat-tempat </span><span>peredaran,</span><span> supaya </span><span>[b]</span><span> kamu dapat mengetahui bilangan tahun dan perhitungan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1237">1237</a><span>]</span><span> </span><span>waktu.</span><span> Allah tidak menjadikan yang demikian itu melainkan dengan hak ; Dia menjelaskan Tanda-tanda itu bagi kaum yang mengetahui.</span></p><p><span>[a]</span><span> 25:62 ; 71;17.</span></p><p><span>[b]</span><span> 17:13.</span></p>');
INSERT INTO `quran` VALUES (7726,2,10,7,'<p><span>Sesungguhnya </span><span>[a]</span><span> dalam pertukaran malam dan siang dan apa-apa yang diciptakan Allah di seluruh langit dan bumi pasti ada Tanda-tanda bagi kaum yang bertakwa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1238">1238</a><span>]</span></p><p><span>[a]</span><span> 2:165 ; 3:191 ; 23:81.</span></p>');
INSERT INTO `quran` VALUES (7727,2,10,8,'<p><span>Sesungguhnya </span><span>[a]</span><span> orang-orang yang tidak mengharapkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1239">1239</a><span>]</span><span> pertemuan dengan Kami dan telah merasa senang dengan kehidupan dunia dan merasa puas dengannya ; dan orang-orang yang terhadap Tanda-tanda Kami lalai.</span></p><p><span>[a]</span><span> 10:12, 46 ; 25:22.</span></p>');
INSERT INTO `quran` VALUES (7728,2,10,9,'<p><span>Mereka itulah yang tempat tinggalnya Api, disebabkan apa-apa yang telah mereka usahakan</span></p>');
INSERT INTO `quran` VALUES (7729,2,10,10,'<p><span>Sesungguhnya orang-orang </span><span>[b]</span><span> yang beriman dan beramal shaleh, mereka akan diberi petunjuk oleh Tuhan mereka disebabkan iman mereka. Mengalir di bawah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1240">1240</a><span>]</span><span> mereka sungai-sungai dalam kebun-kebun kenikmatan.</span></p><p><span>[b]</span><span> 2:278 ; 4:176 ; 13:30 ; 14:24 ; 20:15, 24.</span></p>');
INSERT INTO `quran` VALUES (7730,2,10,11,'<p><span>Seruan mereka di dalamnya, “Mahasuci Engkau, ya Allah ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1241">1241</a><span>]</span><span> Dan ucapan </span><span>[c]</span><span> do</span><span>’</span><span>a mereka di dalamnya “Selamat Sejahtera.” Dan Akhir seruan mereka, “Segala puji bagi Allah, Tuhan semesta alam.”</span></p><p><span>[c]</span><span> 14:24 ; 36:59.</span></p>');
INSERT INTO `quran` VALUES (7731,2,10,12,'<p><span>Dan </span><span>[a]</span><span> jika sekiranya Allah mempercepat keburukan bagi manusia seperti keinginan mereka untuk segera </span><span>memperoleh </span><span> yang baik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1242">1242</a><span>]</span><span> pastilah kepada mereka diakhiri ajal mereka. Oleh karena itu </span><span>[b]</span><span> Kami biarkan orang-orang yang tidak mengharapkan pertemuan dengan Kami, mereka bingung dalam kedurhakaannya.</span></p><p><span>[a]</span><span> 17:12.</span></p><p><span>[b</span><span>] 10:8.</span></p>');
INSERT INTO `quran` VALUES (7732,2,10,13,'<p><span>Dan </span><span>[c]</span><span> apabila menimpa manusia kesusahan, ia berseru kepada Kami dalam keadaan </span><span>berbaring</span><span> pada sisinya, atau duduk atau berdiri, tetapi setelah Kami singkirkan darinya kesusahan itu, maka berlalulah ia seolah-olah tidak pernah berdo</span><span>’</span><span>a kepada Kami untuk </span><span>menjauhkan</span><span> kesusahan yang telah menimpanya. Demikianlah di tampakkan indah bagi orang-orang yang melampui batas apa yang mereka kerjakan.</span></p><p><span>[c]</span><span> 30:34 ; 39:9, 50.</span></p>');
INSERT INTO `quran` VALUES (7733,2,10,14,'<p><span>Dan sesungguhnya telah </span><span>[a]</span><span> Kami binasakan generasi demi generasi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1243">1243</a><span>]</span><span> sebelum kamu, ketika mereka berbuat aniaya, padahal telah datang kepada mereka rasul-rasul mereka dengan keterangan-keterangan yang nyata, tetapi mereka tidak mau beriman. Demikian lah Kami memberi pembalasan kepada kaum yang berdosa.</span></p><p><span>[a]</span><span> 6:7 ; 20:129 ; 32:27.</span></p>');
INSERT INTO `quran` VALUES (7734,2,10,15,'<p><span>Kemudian, </span><span>[b]</span><span> Kami jadikan kamu pengganti-pengganti di bumi sesudah mereka, supaya Kami menyaksikan bagaimana kamu beramal.</span></p><p><span>[b]</span><span> 2:31 ; 7:130.</span></p>');
INSERT INTO `quran` VALUES (7735,2,10,16,'<p><span>Dan apabila dibacakan kepada mereka Ayat-ayat Kami yang nyata, maka berkatalah </span><span>[c]</span><span> orang-orang yang tidak mengharapkan pertemuan dengan Kami, </span><span>[d]</span><span> “Datangkanlah Alquran yang lain selain ini, atau rubahlah dia.” Katakanlah, “Tidak patut bagiku untuk merubahnya dari diriku sendiri, aku hanya </span><span>[e]</span><span> mengikuti apa yang diwahyukan kepadaku ; sesungguhnya aku takut jika aku mendurhakai Tuhan-ku pada azab Hari yang besar.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1244">1244</a><span>]</span></p><p><span>[c]</span><span> 10:8.</span></p><p><span>[d]</span><span> 17:74. </span></p><p><span>[e]</span><span> 6:51 ; 7:204 ; 46:10.</span></p>');
INSERT INTO `quran` VALUES (7736,2,10,17,'<p><span>Katakanlah, “Jika Allah menghendaki tentu tidak kubacakan ini kepadamu dan tidak pula dia memberitahukannya kepada kamu tentang itu. Sesungguhnya aku telah tinggal bersamamu dalam masa yang panjang sebelum ini ; tidakkah kamu menggunakan akal ?“ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1245">1245</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7737,2,10,18,'<p><span>Maka </span><span>[a]</span><span> siapakah yang lebih aniaya dari orang yang mengada-ada dusta terhadap Allah atau mendustakan Tanda-tanda-Nya ? Sesungguhnya tidak akan sukses orang-orang yang berdosa.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1246">1246</a><span>]</span></p><p><span>[a]</span><span> 6:22 ; 11:19 ; 61:8.</span></p>');
INSERT INTO `quran` VALUES (7738,2,10,19,'<p><span>Dan </span><span>[a]</span><span> mereka menyembah selain Allah apa yang tidak merugikan mereka dan tidak menguntungkan mereka dan mereka berkata, “Mereka inilah pemberi syafaat kepada kami di sisi Allah.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1247">1247</a><span>]</span><span> katakanlah, </span><span>[b]</span><span> “Apakah kamu memberitahukan kepada Allah apa yang Dia tidak ketahui di seluruh langit dan di bumi ? Mahasuci Dia dan Mahatinggi dari apa yang mereka sekutukan.”</span></p><p><span>[a]</span><span> 16:74 ; 22:72 ; 29:18.</span></p><p><span>[b]</span><span> 49:17.</span></p>');
INSERT INTO `quran` VALUES (7739,2,10,20,'<p><span>Dan tidaklah dahulunya </span><span>[c]</span><span> manusia kecuali satu umat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1247a">1247a</a><span>]</span><span> lalu mereka berselisih, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1248">1248</a><span>]</span><span> </span><span>[d] </span><span>Dan jika tidak karena sebuah Kalimat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1248a">1248a</a><span>]</span><span> yang telah ada dari Tuhan engkau, niscaya telah diberi keputusan di antara mereka tentang apa yang mereka perselisihkan.</span></p><p><span>[c]</span><span> 2:214.</span></p><p><span>[d]</span><span> 11:111 ; 20:130 ; 41:46.</span></p>');
INSERT INTO `quran` VALUES (7740,2,10,21,'<p><span>Dan mereka berkata </span><span>[a]</span><span> “Mengapa tidak diturunkan kepadanya suatu Tanda dari Tuhan nya ? ” Maka katakanlah, “Sesungguhnya yang gaib itu hanya kepunyaan Allah ; maka kamu tunggulah dan aku pun bersamamu termasuk orang-orang yang menunggu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1249">1249</a><span>]</span></p><p><span>[a]</span><span> 6:38.</span></p>');
INSERT INTO `quran` VALUES (7741,2,10,22,'<p><span>Dan </span><span>[b]</span><span> apabila Kami merasakan kepada manusia suatu rahmat setelah kesusahan menimpa mereka, tiba-tiba </span><span>[c] </span><span>mereka membuat tipu-daya terhadap Tanda-tanda Kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1250">1250</a><span>]</span><span> Katakanlah, “Allah lebih cepat dalam membuat rencana. Sesungguhnya utusan-utusan Kami menuliskan apa yang kamu rencanakan.”</span></p><p><span>[b]</span><span> 30:37 ; 41:51, 52 ; 42:49.</span></p><p><span>[c]</span><span> 8:31 ; 35:44.</span></p>');
INSERT INTO `quran` VALUES (7742,2,10,23,'<p><span>Dia Yang memperjalankan kamu di daratan dan lautan. Sehingga </span><span>[a]</span><span> apabila kamu telah </span><span>ada</span><span> di kapal-kapal, dan berlayar kapal-kapal itu membawa mereka dengan angin yang baik dan mereka bergembira karenanya, maka datanglah angin badai dan datang juga kepada mereka ombak dari segala penjuru dan mereka menduga, bahwa mereka telah terkepung oleh itu, mereka berseru kepada Allah dengan mengikhlaskan ketaatan kepada-Nya </span><span>dan berkata</span><span> “Jika sekiranya Engkau selamatkan kami dari </span><span>bahaya</span><span> ini, niscaya kami akan termasuk orang-orang yang bersyukur.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1251">1251</a><span>]</span></p><p><span>[a]</span><span> 17:67 ; 29:66 ; 31:33.</span></p>');
INSERT INTO `quran` VALUES (7743,2,10,24,'<p><span>Tetapi </span><span>[b]</span><span> setelah Dia selamatkan mereka, tiba-tiba mereka berbuat durhaka di bumi tanpa hak. Hai manusia, </span><span>[c]</span><span> kedurhakaanmu itu akan menimpa dirimu, </span><span>akibat</span><span> kesenangan hidup di dunia, kemudian kepada Kami tempat kembalimu, lalu Kami beritahukan kepadamu apa-apa yang telah kamu kerjakan.</span></p><p><span>[b]</span><span> 17:68 ; 31:33.</span></p><p><span>[c]</span><span> 35:44.</span></p>');
INSERT INTO `quran` VALUES (7744,2,10,25,'<p><span>Sesungguhnya </span><span>[a]</span><span> misal kehidupan dunia adalah seperti air yang Kami turunkan dari awan, lalu bercampur dengannya tumbuh-tumbuhan bumi, diantaranya dimakan manusia dan binatang ternak. Sehingga apabila bumi telah memakai perhiasannya dan indah kelihatannya, dan pemilik-pemiliknya menduga bahwa mereka berkuasa atasnya, </span><span>[b]</span><span> maka datanglah kepadanya keputusan Kami di waktu malam atau siang, dan Kami menjadikannya ladang yang telah disabit seolah-olah </span><span>[c]</span><span> tidak pernah ada kemarin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1252">1252</a><span>]</span><span> Demikianlah Kami jelaskan Tanda-tanda Kami bagi kaum yang berpikir.</span></p><p><span>[a]</span><span> 18:46.</span></p><p><span>[b]</span><span> 3:118.</span></p><p><span>[c]</span><span> 11:69.</span></p>');
INSERT INTO `quran` VALUES (7745,2,10,26,'<p><span>Dan </span><span>[d]</span><span> Allah menyeru ketempat keselamatan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1253">1253</a><span>]</span><span> Dan memberi petunjuk kepada siapa yag Dia kehendaki ke jalan yang lurus.</span></p><p><span>[d]</span><span> 6:128.</span></p>');
INSERT INTO `quran` VALUES (7746,2,10,27,'<p><span>[e]</span><span> Bagi orang-orang yang berbuat baik ada </span><span>balasan</span><span> yang lebih baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1254">1254</a><span>]</span><span> serta tambahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1255">1255</a><span>]</span><span> </span><span>berkat yang lain.</span><span> Dan </span><span>[f]</span><span> muka mereka tidak akan ditutupi debu hitam dan tidak pula kehinaan. Mereka itu penghuni surga, mereka akan tetap di dalamnya.</span></p><p><span>[e]</span><span> 50:36.</span></p><p><span>[f]</span><span> 75:23, 24.</span></p>');
INSERT INTO `quran` VALUES (7747,2,10,28,'<p><span>Dan orang-orang yang mengerjakan keburukan, </span><span>[a]</span><span> balasan keburukannya adalah semisal dengan itu ; dan </span><span>[b]</span><span> kehinaan akan menutupi mereka. Tidak ada pelindung bagi mereka dari Allah, seolah-olah telah ditutupi muka mereka dengan lapisan malam yang gelap. </span><span>{</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1256">1256</a><span>]</span><span> Mereka itulah penghuni Api, mereka akan tinggal lama di dalamnya.</span></p><p><span>[a]</span><span> 42:41.</span></p><p><span>[b]</span><span> 68:44 ; 75:25 ; 80:41, 42 ; 88:3, 4.</span></p>');
INSERT INTO `quran` VALUES (7748,2,10,29,'<p><span>Dan </span><span>ingatlah</span><span> Hari itu </span><span>[c]</span><span> ketika Kami akan mengumpulkan mereka semuanya, lalu Kami akan berkata kepada orang-orang yang mempersekutukan </span><span>dengan Tuhan,</span><span> “Tetaplah di tempatmu, kamu dan sekutu-sekutumu.” Lalu Kami akan memisahkan di antara mereka, maka berkatalah sekutu-sekutu mereka, </span><span>[d]</span><span> “Kamu </span><span>sekali-kali</span><span> tidak pernah penyembah kami,”</span></p><p><span>[c]</span><span> 6:23 ; 46;7.</span></p><p><span>[d]</span><span> 16:87 ; 28:64.</span></p>');
INSERT INTO `quran` VALUES (7749,2,10,30,'<p><span>“Maka cukuplah Allah sebagai saksi di antara kami dan kamu, bahwa </span><span>[e]</span><span> kami tentang penyembahan kamu sesungguhnya tidak tahu-menahu,”</span></p><p><span>[e]</span><span> 46:6.</span></p>');
INSERT INTO `quran` VALUES (7750,2,10,31,'<p><span>[a]</span><span> Di sanalah tiap-tiap jiwa akan merasakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1257">1257</a><span>]</span><span> apa yang telah dikerjakannya dahulu dan mereka akan dikembalikan kepada Allah Pelindung Mereka sebenarnya, dan akan lenyaplah dari mereka apa yang mereka telah ada-adakan.</span></p><p><span>[a]</span><span> 86:10.</span></p>');
INSERT INTO `quran` VALUES (7751,2,10,32,'<p><span>Katakanlah, </span><span>[b]</span><span> “Siapakah yang memberimu rezeki dari langit dan bumi ? Atau siapakah yang menguasai telinga dan mata ? Dan </span><span>[c]</span><span> siapakah yang mengeluarkan yang hidup dari yang mati, dan yang mengeluarkan yang mati dari yang hidup ? Dan </span><span>[d]</span><span> siapakah yang mengatur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1258">1258</a><span>]</span><span> urusan ? Mereka akan berkata, “Allah” Maka katakanlah, “Apakah kamu tidak bertakwa ?”</span></p><p><span>[b]</span><span> 27:65 ;34:25 ; 35:4.</span></p><p><span>[c]</span><span> 3:28 ; 6:96.</span></p><p><span>[d]</span><span> 10:4.</span></p>');
INSERT INTO `quran` VALUES (7752,2,10,33,'<p><span>Maka demikianlah Allah, Tuhan-mu Yang benar ; maka tidak ada sesudah kebenaran itu kecuali kesesatan. Maka kemanakah kamu di palingkan ?</span></p>');
INSERT INTO `quran` VALUES (7753,2,10,34,'<p><span>[a]</span><span> Demikianlah telah terbukti kebenaran firman Tuhan engkau mengenai orang-orang yang durhaka, bahwa mereka tidak akan beriman.</span></p><p><span>[a]</span><span> 10:97 ; 40:7</span></p>');
INSERT INTO `quran` VALUES (7754,2,10,35,'<p><span>Katakanlah, “Apakah di antara sekutu-sekutumu itu </span><span>[b]</span><span> yang memulai penciptaan, kemudian mengulanginya kembali ? “ Katakanlah, “Allah Yang memulai penciptaan, lalu mengulanginya kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1259">1259</a><span>]</span><span> Maka kemanakah kamu dipalingkan ?”</span></p><p><span>[b]</span><span> 10:5.</span></p>');
INSERT INTO `quran` VALUES (7755,2,10,36,'<p><span>Katakanlah, “Apakah di antara sekutu-sekutumu itu ada yang memberi petunjuk kepada kebenaran ? “ Katakanlah, “ Allah yang memberi petunjuk kepada kebenaran.” Apakah Yang dapat memberi petunjuk kepada kebenaran itu lebih berhak diikuti, ataukah yang tak dapat memberi petunjuk kecuali jika ia diberi petunjuk. Apakah yang terjadi dengan kamu ? Bagaimanakah kamu mengambil keputusan ?</span></p>');
INSERT INTO `quran` VALUES (7756,2,10,37,'<p><span>[a]</span><span> Dan kebanyakan mereka tidak mengikuti melainkan prasangka. Sesungguhnya prasangka itu tidak berguna menghadapi kebenaran sedikit pun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1260">1260</a><span>]</span><span> Sesungguhnya Allah Maha Mengetahui apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 6:117 ; 10:67 ; 53:29.</span></p>');
INSERT INTO `quran` VALUES (7757,2,10,38,'<p><span>Dan tidaklah mungkin Alquran ini diada-adakan oleh selain Allah ; tetapi </span><span>[b]</span><span> membenarkan yang ada sebelumnya dan menjelaskan Kitab yang tidak ada keraguan di dalamnya dari Tuhan semesta alam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1261">1261</a><span>]</span></p><p><span>[b]</span><span> 12:112 ; 16:90.</span></p>');
INSERT INTO `quran` VALUES (7758,2,10,39,'<p><span>Apakah mereka mengatakan, “Ia telah mengada-adakannya ? ” Katakanlah, </span><span>[c]</span><span> Bawalah sebuah Surah yang semisalnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1262">1262</a><span>]</span><span> dan panggillah siapa saja yang kamu mampu selain Allah jika memang kamu orang-orang benar.</span></p><p><span>[c]</span><span> 2:24 ; 11:14 ; 17:89 ; 52:34, 35.</span></p>');
INSERT INTO `quran` VALUES (7759,2,10,40,'<p><span>[a]</span><span> Bahkan mereka telah mendustakan tentang apa-apa yang mereka belum menguasai ilmunya, dan sebenarnya belum datang kepada mereka penjelasannya. Demikianlah telah mendustakan orang-orang yang sebelum mereka, maka perhatikanlah bagaimana akibat orang-orang zalim.</span></p><p><span>[a]</span><span> 27:85.</span></p>');
INSERT INTO `quran` VALUES (7760,2,10,41,'<p><span>Dan di antara mereka </span><span>[b]</span><span> ada yang percaya kepada </span><span>Alquran</span><span> itu dan di antara mereka ada yang tidak percaya kepadanya. Dan Tuhan engkau lebih mengetahui tentang orang-orang yang berbuat kerusakan.</span></p><p><span>[b]</span><span> 2:254 ; 4:56.</span></p>');
INSERT INTO `quran` VALUES (7761,2,10,42,'<p><span>Dan jika mereka mendustakan engkau, maka katakanlah, </span><span>[c]</span><span> “Bagiku amalanku dan bagimu amalanmu. Kamu tidak bertanggungjawab terhadap apa yang aku kerjakan, dan aku tidak bertanggungjawab terhadap apa yang kamu kerjakan.”</span></p><p><span>[c]</span><span> 2:140 ; 109:7.</span></p>');
INSERT INTO `quran` VALUES (7762,2,10,43,'<p><span>Dan di antara mereka ada </span><span>[a]</span><span> orang yang mendengarkan kepada engkau. </span><span>[b]</span><span> Tetapi dapatkah engkau membuat orang-orang tuli mendengar, walaupun mereka tidak menggunakan akal ?</span></p><p><span>[a]</span><span> 6:26 ; 17:48.</span></p><p><span>[b]</span><span> 27:81.</span></p>');
INSERT INTO `quran` VALUES (7763,2,10,44,'<p><span>Dan </span><span>[c]</span><span> di antara mereka ada orang yang memperhatikan kepada engkau. Tetapi dapatkah engkau memberi petunjuk orang-orang buta, walaupun mereka tidak melihat ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1263">1263</a><span>]</span></p><p><span>[c]</span><span> 7:199.</span></p>');
INSERT INTO `quran` VALUES (7764,2,10,45,'<p><span>Sesungguhnya </span><span>[d]</span><span> Allah tidak menganiaya manusia sedikit pun, akan tetapi manusia itulah yang menganiaya dirinya sendiri.</span></p><p><span>[d]</span><span> 4:41 ; 9:70 ; 18:50 ; 30:10.</span></p>');
INSERT INTO `quran` VALUES (7765,2,10,46,'<p><span>Dan pada hari Dia mengumpulkan mereka, </span><span>[e]</span><span> seolah-olah mereka tidak pernah tinggal </span><span>di dunia</span><span> kecuali sesaat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1264">1264</a><span>]</span><span> saja di siang hari. Mereka akan saling mengenal diantara mereka. Sesungguhnya merugilah </span><span>[f]</span><span> orang-orang yang mendustakan pertemuan dengan Allah dan mereka tidak akan mendapat petunjuk.</span></p>');
INSERT INTO `quran` VALUES (7766,2,10,47,'<p><span>Dan </span><span>[a]</span><span> jika Kami perlihatkan kepada engkau sebagian dari yang telah Kami ancamkan kepada mereka, atau jika Kami wafatkan engkau, maka kepada Kami juga tempat kembali mereka ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1265">1265</a><span>]</span><span> kemudian Allah menjadi saksi atas apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 13:41 ; 40:78.</span></p>');
INSERT INTO `quran` VALUES (7767,2,10,48,'<p><span>Dan </span><span>[b]</span><span> untuk setiap umat ada rasul. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1266">1266</a><span>]</span><span> Maka apabila rasul mereka datang, diputuskan di antara mereka dengan adil, dan mereka tidak di aniaya.</span></p><p><span>[b]</span><span> 16:37 ; 35:25.</span></p>');
INSERT INTO `quran` VALUES (7768,2,10,49,'<p><span>Dan </span><span>[c]</span><span> mereka berkata, “Bilakah janji ini </span><span>sempurna,</span><span> jika memang kamu orang yang benar ?”</span></p><p><span>[c]</span><span> 21:39 ; 27:72 ; 34:30 ; 36:49.</span></p>');
INSERT INTO `quran` VALUES (7769,2,10,50,'<p><span>Katakanlah, </span><span>[d]</span><span> “Aku tidak berkuasa untuk diriku suatu kerugian dan tidak pula suatu manfaat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1267">1267</a><span>]</span><span> kecuali apa yang dikehendaki Allah </span><span>[e]</span><span> Bagi setiap umat ada batas waktu </span><span>yang tertentu.</span><span> Apabila datang batas waktu mereka, maka mereka tidak dapat menangguhkan sesaat pun dan tidak pula mendahulukannya.</span></p><p><span>[d]</span><span> 7:189.</span></p><p><span>[e]</span><span> 7:35 ; 16:62 ; 35:46.</span></p>');
INSERT INTO `quran` VALUES (7770,2,10,51,'<p><span>Katakanlah. “Bagaimanakah pendapatmu, </span><span>[a]</span><span> jika datang kepadamu kamu azab-Nya di waktu malam atau siang hari ; bagaimana orang-orang yang berdosa itu dapat melarikan diri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1268">1268</a><span>]</span><span> darinya ?</span></p><p><span>[a]</span><span> 6:48 ; 7:98, 99.</span></p>');
INSERT INTO `quran` VALUES (7771,2,10,52,'<p><span>Apakah kemudian bila </span><span>azab</span><span> itu telah terjadi, barulah pada waktu itu kamu akan mempercayainya ?“ </span><span>[b]</span><span> Apakah sekarang </span><span>kamu percaya.</span><span> padahal kamu dahulu memintanya dipercepat.”</span></p><p><span>[b]</span><span> 10:92.</span></p>');
INSERT INTO `quran` VALUES (7772,2,10,53,'<p><span>[c]</span><span> Kemudian akan dikatakan kepada orang-orang yang aniaya. “Rasakanlah olehmu azab yang lama itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1269">1269</a><span>]</span><span> kamu tidak dibalas melainkan oleh apa yang telah kamu usahakan.”</span></p><p><span>[c]</span><span> 34:43.</span></p>');
INSERT INTO `quran` VALUES (7773,2,10,54,'<p><span>Dan mereka bertanya kepada engkau, “Apakah itu benar ? “ </span><span>[a]</span><span> Katakanlah, “Memang, demi Tuhan-ku, itu sungguh benar, dan kamu tidak dapat menggagalkannya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1269a">1269a</a><span>]</span></p><p><span>[a]</span><span> 11:18.</span></p>');
INSERT INTO `quran` VALUES (7774,2,10,55,'<p><span>R. 6</span></p><p><span>Dan </span><span>[b]</span><span> jika sekiranya setiap jiwa yang berbuat aniaya memiliki segala yang ada di bumi, niscaya ia akan menebus dirinya dengan itu. </span><span>[c]</span><span> Dan mereka akan menyembunyikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1270">1270</a><span>]</span><span> penyesalannya, di waktu mereka melihat azab, dan diputuskan di antara mereka dengan adil, dan mereka tidak akan dianiaya.</span></p><p><span>[b]</span><span> 39:48.</span></p><p><span>[c]</span><span> 34:34.</span></p>');
INSERT INTO `quran` VALUES (7775,2,10,56,'<p><span>Ingatlah ! Sesungguhnya </span><span>[d]</span><span> kepunyaan Allah apa yang ada di seluruh langit dan bumi. Ingatlah ! Sesungguhnya janji Allah memang benar, akan tetapi kebanyakan mereka tidak mengetahui.</span></p><p><span>[d]</span><span> 2:285 ; 10:67 ; 31:27.</span></p>');
INSERT INTO `quran` VALUES (7776,2,10,57,'<p><span>[e]</span><span> Dia Yang menghidupkan dan mematikan, dan kepada-Nya kamu akan dikembalikan.</span></p><p><span>[e]</span><span> 3:157 ; 7:159 ; 44:9 ; 57;3.</span></p>');
INSERT INTO `quran` VALUES (7777,2,10,58,'<p><span>Hai manusia ! Sesungguhnya telah datang kepadamu suatu nasihat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1271">1271</a><span>]</span><span> dari Tuhan-mu, dan penyembuh bagi apa yang ada dalam dada, dan </span><span>[f]</span><span> petunjuk serta rahmat bagi orang-orang mukmin.</span></p><p><span>[f]</span><span> 12:112 ; 27:3.</span></p>');
INSERT INTO `quran` VALUES (7778,2,10,59,'<p><span>Katakanlah, “</span><span>Kesemuanya itu </span><span> dengan karunai Allah dan dengan rahmat-Nya, maka karena itu mereka hendaknya bergembira. </span><span>[a]</span><span> Yang demikian itu lebih baik dari apa yang mereka kumpulkan ! “</span></p><p><span>[a]</span><span> 43:33.</span></p>');
INSERT INTO `quran` VALUES (7779,2,10,60,'<p><span>Katakanlah, “Apakah kamu memperhatikan, bahwa Allah telah menurunkan rezeki untukmu, </span><span>[b]</span><span> lalu darinya kamu jadikan haram dan </span><span>sebagian</span><span> lagi halal ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1272">1272</a><span>]</span><span> Katakanlah, “Apakah Allah telah memberi izin kepadamu, atau kamu mengada-ada dusta terhadap Allah ? “</span></p><p><span>[b]</span><span> 5:104.</span></p>');
INSERT INTO `quran` VALUES (7780,2,10,61,'<p><span>Dan apakah dugaan orang-orang yang mengada-adakan dusta terhadap Allah tentang Hari Kiamat ? </span><span>[c]</span><span> Sesungguhnya Allah pasti mempunyai karunia atas manusia, akan tetapi kebanyakan mereka tidak bersyukur.</span></p><p><span>[c]</span><span> 27:74 ; 40:62.</span></p>');
INSERT INTO `quran` VALUES (7781,2,10,62,'<p><span>Dan tidaklah engkau </span><span>sibuk</span><span> dalam suatu urusan dan tidak pula engkau membaca darinya sebagian dari Alquran dan tidak pula </span><span>[a]</span><span> kamu melakukan suatu pekerjaan, melainkan Kami menjadi saksi atasmu ketika kamu tekun di dalamnya. Dan </span><span>[b]</span><span> tidaklah tersembunyi dari Tuhan engkau sebesar zarah pun di bumi dan tidak di langit, dan tidak pula yang lebih kecil </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1273">1273</a><span>]</span><span> dari itu dan tidak yang lebih besar, melainkan itu ada dalam kitab yang nyata.</span></p><p><span>[a]</span><span> 57:5 ; 58;8.</span></p><p><span>[b]</span><span> 34:4.</span></p>');
INSERT INTO `quran` VALUES (7782,2,10,63,'<p><span>Ingatlah ! Sesungguhnya wali-wali Allah itu </span><span>[c]</span><span> tidak ada ketakutan atas mereka dan tidak pula mereka bersedih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1274">1274</a><span>]</span></p><p><span>[c]</span><span> 2:63.</span></p>');
INSERT INTO `quran` VALUES (7783,2,10,64,'<p><span>Yaitu</span><span> orang-orang yang beriman dan bertakwa.</span></p>');
INSERT INTO `quran` VALUES (7784,2,10,65,'<p><span>[d]</span><span> Bagi mereka ada khabar suka dalam kehidupan di dunia dan di akhirat. Tidak ada perubahan pada firman-firman Allah Itulah kemenangan yang besar.</span></p><p><span>[d]</span><span> 41:31.</span></p>');
INSERT INTO `quran` VALUES (7785,2,10,66,'<p><span>Dan </span><span>[a]</span><span> janganlah menyedihkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1275">1275</a><span>]</span><span> engkau perkataan mereka. Sesungguhnya kekuasaan itu seluruhnya kepunyaan Allah Dia Maha Mendengar, Maha Mengetahui.</span></p><p><span>[a]</span><span> 36:77.</span></p>');
INSERT INTO `quran` VALUES (7786,2,10,67,'<p><span>Ingatlah ! </span><span>[b]</span><span> Sesungguhnya kepunyaan Allah semua yang ada di seluruh langit dan semua yang ada di bumi. Dan mereka tidak mengikuti sekutu-sekutu yang menyeru selain Allah. </span><span>[c]</span><span> Mereka hanya mengikuti prasangka dan mereka hanya menduga-duga.</span></p><p><span>[b]</span><span> 10:56.</span></p><p><span>[c]</span><span> 10:37.</span></p>');
INSERT INTO `quran` VALUES (7787,2,10,68,'<p><span>[d]</span><span> Dia-lah yang telah menjadikan malam untukmu, supaya kamu dapat beristirahat di dalamnya, dan siang hari terang-benderang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1276">1276</a><span>]</span><span> Sesungguhnya dalam hal itu Tanda-tanda bagi kaum yang mau mendengar.</span></p><p><span>[d]</span><span> 17:13 ; 27:87 ; 28:74 ; 30:24.</span></p>');
INSERT INTO `quran` VALUES (7788,2,10,69,'<p><span>[a]</span><span> Mereka berkata, “Allah mengangkat anak,” Mahasuci Dia. Dia Mahakaya. Kepunyaan-Nya apa yang ada di seluruh langit dan apa yang ada di bumi. Tidak ada pada kamu suatu kekuasaan tentang hal ini. Patutlah kamu mengatakan terhadap Allah apa yang tidak kamu ketahui ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1277">1277</a><span>]</span></p><p><span>[a]</span><span> 2:117 ; 4:172 ; 9:31 ; 17:112 ; 18:5, 6.</span></p>');
INSERT INTO `quran` VALUES (7789,2,10,70,'<p><span>Katakanlah, “Sesungguhnya </span><span>[b]</span><span> orang-orang yang mengada-adakan dusta terhadap Allah tidak akan menang.”</span></p><p><span>[b]</span><span> 4;51 ; 16:117.</span></p>');
INSERT INTO `quran` VALUES (7790,2,10,71,'<p><span>[c]</span><span> </span><span>Mereka memperoleh sedikit</span><span> kesenangan sementara di dunia. Kemudian kepada Kami tempat kembali mereka ; kemudian Kami merasakan kepada mereka azab yang keras, disebabkan mereka senantiasa ingkar.</span></p><p><span>[c]</span><span> 3;15 , 198 ; 9:38 ; 16:118 ; 28:61 ; 40:40.</span></p>');
INSERT INTO `quran` VALUES (7791,2,10,72,'<p><span>R. 8</span></p><p><span>Dan bacakanlah kepada mereka kisah Nuh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1278">1278</a><span>]</span><span> ketika ia berkata kepada kaumnya, “Hai kaumku ! </span><span>[d]</span><span> Jika kamu keberatan terhadap kedudukanku dan peringatanku melalui Tanda-tanda Allah, maka hanya kepada Allah aku bertawakkal ; maka himpunlah </span><span>semua </span><span> rencanamu dan sekutu-sekutumu, sehingga urusanmu tidak menjadi kabur bagimu, kemudian laksanakanlah terhadapku dan janganlah aku diberi tangguh.”</span></p><p><span>[d]</span><span> 71:8.</span></p>');
INSERT INTO `quran` VALUES (7792,2,10,73,'<p><span>“Dan jika kamu berpaling, </span><span>[a]</span><span> maka aku tidak meminta kepada kamu suatu ganjaran. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1279">1279</a><span>]</span><span> Tidaklah ganjaranku kecuali pada Allah dan aku disuruh supaya aku termasuk orang-orang yang menyerahkan diri.”</span></p><p><span>[a]</span><span> 6:91 ; 11:30.</span></p>');
INSERT INTO `quran` VALUES (7793,2,10,74,'<p><span>Tetapi mereka telah mendutakannya, lalu </span><span>[b]</span><span> Kami selamatkan dia dan orang-orang yang besertanya dalam bahtera, dan Kami jadikan mereka sebagai pengganti-pengganti dan Kami tenggelamkan orang-orang yang mendustakan Tanda-tanda Kami. maka lihatlah, bagaimana akibat orang-orang yang diberi peringatan.</span></p><p><span>[b]</span><span> 29:16.</span></p>');
INSERT INTO `quran` VALUES (7794,2,10,75,'<p><span>Kemudian Kami utus sesudahnya rasul-rasul kepada kaum mereka, </span><span>[a]</span><span> yang datang kepada mereka dengan bukti-bukti nyata. Akan tetapi mereka tidak mau beriman kepadanya disebabkan mereka telah mendustakannya sebelum itu. Demikianlah Kami mencap </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1280">1280</a><span>]</span><span> hati orang-orang yang melampui batas.</span></p><p><span>[a]</span><span> 30:48 ; 40:24.</span></p>');
INSERT INTO `quran` VALUES (7795,2,10,76,'<p><span>[b]</span><span> Kemudian sesudah mereka, Kami utus Musa dan Harun kepada Firaun dan pemuka-pemuka mereka dengan Tanda-tanda Kami, tetapi mereka berlaku sombong dan mereka itu kaum yang berdosa.</span></p><p><span>[b]</span><span> 7:104.</span></p>');
INSERT INTO `quran` VALUES (7796,2,10,77,'<p><span>Maka </span><span>[c]</span><span> tatkala datang kepada mereka kebenaran dari sisi Kami, mereka berkata, “Sesungsuhnya ini adalah sihir yang nyata.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1281">1281</a><span>]</span></p><p><span>[c]</span><span> 40:26</span></p>');
INSERT INTO `quran` VALUES (7797,2,10,78,'<p><span>Musa berkata, “Apakah kamu berkata mengenai hak, ketika ia telah datang kepadamu ? Sihirkah ini ? Padahal </span><span>[a]</span><span> tukang-tukang sihir itu tidak akan mendapat kemenangan. “</span></p><p><span>[a]</span><span> 20:70.</span></p>');
INSERT INTO `quran` VALUES (7798,2,10,79,'<p><span>Mereka berkata, “Apakah engkau datang kepada kami supaya engkau membelokkan kami dari apa yang telah kami dapati pada bapak-bapak kami dan agar kamu berdua memperoleh kebesaran di bumi ? Tetapi </span><span>[b]</span><span> kami tidak akan percaya kepada kamu berdua.”</span></p><p><span>[b]</span><span> 7:133.</span></p>');
INSERT INTO `quran` VALUES (7799,2,10,80,'<p><span>[c]</span><span> Dan berkata Firaun, “Datangkanlah kepadaku setiap tukang sihir yang mahir.”</span></p><p><span>[c]</span><span> 7:113 ; 20:67 ; 26:44.</span></p>');
INSERT INTO `quran` VALUES (7800,2,10,81,'<p><span>Maka setelah datang tukang-tukang sihir itu, berkatalah Musa kepada mereka, </span><span>[d]</span><span> “Lemparkanlah apa yang hendak kamu lemparkan.”</span></p><p><span>[d]</span><span> 7:117 ; 20:67 ; 26:44.</span></p>');
INSERT INTO `quran` VALUES (7801,2,10,82,'<p><span>Maka ketika mereka telah melemparkan, berkatalah </span><span>[e]</span><span> Musa, “Apa yang telah kamu bawa itu adalah sihir. Niscaya Allah akan segera membatalkannya. Sesungguhnya Allah tidak akan memperbaiki amal orang-orang yang berbuat kerusakan.</span></p><p><span>[e]</span><span> 7:119 ; 20:70.</span></p>');
INSERT INTO `quran` VALUES (7802,2,10,83,'<p><span>Dan </span><span>[a]</span><span> Allah akan menegakkan yang hak itu dengan firman-firman-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1281a">1281a</a><span>]</span><span> walaupun orang-orang yang berdosa tidak menyukai.</span></p><p><span>[a]</span><span> 8:9.</span></p>');
INSERT INTO `quran` VALUES (7803,2,10,84,'<p><span>R. 9</span></p><p><span>Maka tidak ada yang beriman kepada Musa kecuali beberapa pemuda dari kaumnya, karena takut kepada </span><span>[b]</span><span> Firaun dan pemuka-pemuka mereka, bahwa ia akan memfitnah mereka. Dan sesungguhnya Firaun berbuat sewenang-wenang di bumi. Dan sesungguhnya ia termasuk orang-orang yang melampui batas.</span></p><p><span>[b]</span><span> 28:5.</span></p>');
INSERT INTO `quran` VALUES (7804,2,10,85,'<p><span>Dan Musa berkata, “Hai kaumku ! Jika kamu telah beriman kepada Allah, maka kepada-Nya kamu bertawakkal, jika kamu benar-benar menyerahkan diri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1282">1282</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7805,2,10,86,'<p><span>Maka mereka berkata, “Kepada Allah kami bertawakkal. Ya Tuhan kami ! Janganlah Engkau jadikan kami sebagai fitnah bagi kaum yang aniaya.</span></p>');
INSERT INTO `quran` VALUES (7806,2,10,87,'<p><span>Dan selamatkanlah kami dengan rahmat Engkau dari kaum yang kafir”</span></p>');
INSERT INTO `quran` VALUES (7807,2,10,88,'<p><span>Dan Kami wahyukan kepada Musa dan saudaranya bahwa, “Ambillah untuk kaum kamu ber dua rumah-rumah di Mesir, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1283">1283</a><span>]</span><span> dan jadikanlah rumah-rumahmu supaya berhadap-hadapan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1284">1284</a><span>]</span><span> dan dirikanlah shalat, dan sampaikanlah khabar suka kepada orang-orang beriman.</span></p>');
INSERT INTO `quran` VALUES (7808,2,10,89,'<p><span>Dan Musa berkata, “Ya Tuhan kami, sesungguhnya Engkau telah memberikan kepada Firaun dan pemuka-pemukanya perhiasan dan kekayaan dalam kehidupan dunia. Ya Tuhan kami, akibatnya mereka menyesatkan dari jalan Engkau. Ya Tuhan kami, musnahkanlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1284a">1284a</a><span>]</span><span> harta mereka dan keraskanlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1284b">1284b</a><span>]</span><span> hati mereka ; </span><span>[a]</span><span> mereka tidak akan beriman sebelum mereka melihat azab yang pedih.”</span></p><p><span>[a]</span><span> 10:97, 98.</span></p>');
INSERT INTO `quran` VALUES (7809,2,10,90,'<p><span>Dia berfirman, “Sesungguhnya telah dikabulkan doa kamu berdua, maka teguhlah kamu ber dua dan janganlah kamu ikuti jalan orang-orang yang tidak mengetahui.”</span></p>');
INSERT INTO `quran` VALUES (7810,2,10,91,'<p><span>Dan </span><span>[a]</span><span> Kami telah membuat Bani Israil menyeberangi laut, lalu </span><span>[b]</span><span> Firaun dan lasykar-lasykarnya mengejar mereka secara durhaka dan aniaya, sehingga ketika ia hampir tenggelam, ia berkata, “Aku percaya, sesungguhnya Dia tiada Tuhan selain yang dipercayai oleh Bani Israil, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1285">1285</a><span>]</span><span> dan aku termasuk orang-orang yang menyerahkan diri,”</span></p><p><span>[a]</span><span> 7:139 ; 20:78.</span></p><p><span>[b]</span><span> 20:79 ; 26:61 ; 44:25.</span></p>');
INSERT INTO `quran` VALUES (7811,2,10,92,'<p><span>[c]</span><span> “Apa, </span><span>baru</span><span> sekarang ! ? Padahal engkau telah membangkang sebelum ini, dan telah termasuk orang-orang yang berbuat kerusakan.</span></p><p><span>[c]</span><span> 10:52.</span></p>');
INSERT INTO `quran` VALUES (7812,2,10,93,'<p><span>“Maka pada hari ini Kami akan menyelamatkan engkau menjadi suatu Tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1286">1286</a><span>]</span><span> bagi orang-orang sesudah engkau. Dan sesungguhnya kebanyakan dari manusia sangat lengah terhadap Tanda-tanda Kami.”</span></p>');
INSERT INTO `quran` VALUES (7813,2,10,94,'<p><span>Dan sesungguhnya telah Kami tempatkan Bani Israil di tempat yang bagus dan </span><span>[a]</span><span> Kami rezekikan mereka barang-barang yang baik ; maka mereka tidak berselisih sebelum datang kepada mereka ilmu. Sesungguhnya Tuhan engkau </span><span>[b]</span><span> akan memberi keputusan di antara mereka pada Hari Kiamat tentang apa yang mereka perselisihkan.</span></p><p><span>[a]</span><span> 45:17.</span></p><p><span>[b]</span><span> 45:18.</span></p>');
INSERT INTO `quran` VALUES (7814,2,10,95,'<p><span>Maka sekiranya engkau ada dalam keraguan tentang apa yang telah Kami turunkan kepada engkau, maka tanyalah orang-orang yang membaca Kitab sebelum engkau. Sesungguhnya telah datang kepada engkau </span><span>[c]</span><span> hak dari Tuhan engkau, maka sekali-kali janganlah engkau menjadi orang-orang ragu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1287">1287</a><span>]</span></p><p><span>[c]</span><span> 2:148 ; 10:95 ; 11:18.</span></p>');
INSERT INTO `quran` VALUES (7815,2,10,96,'<p><span>Dan sekali-kali janganlah engkau termasuk di antara orang-orang yang telah mendustakan Tanda-tanda Allah, maka engkau akan menjadi di antara orang-orang yang rugi.</span></p>');
INSERT INTO `quran` VALUES (7816,2,10,97,'<p><span>[a]</span><span> Sesungguhnya orang-orang yang telah sempurna atas mereka firman Tuhan engkau, mereka tidak akan beriman.</span></p><p><span>[a]</span><span> 10:34 ; 40:7.</span></p>');
INSERT INTO `quran` VALUES (7817,2,10,98,'<p><span>[b]</span><span> Walaupun datang kepada mereka setiap Tanda, </span><span>mereka tidak akan beriman</span><span> hingga mereka menyaksikan azab yang pedih.</span></p><p><span>[b]</span><span> 10:89.</span></p>');
INSERT INTO `quran` VALUES (7818,2,10,99,'<p><span>Dan mengapa tidak ada suatu kota </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1287a">1287a</a><span>]</span><span> yang beriman, padahal memberi manfaat kepadanya imannya </span><span>[c]</span><span> kecuali kaum Yunus ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1288">1288</a><span>]</span><span> Ketika mereka beriman, Kami singkirkan dari mereka azab yang menghinakan dalam kehidupan dunia, dan Kami memberi mereka perbekalan untuk sementara waktu.</span></p><p><span>[c]</span><span> 37:149.</span></p>');
INSERT INTO `quran` VALUES (7819,2,10,100,'<p><span>Dan </span><span>[a]</span><span> sekiranya Tuhan engkau menghendaki, niscaya orang yang ada di bumi akan beriman semuanya. </span><span>[b]</span><span> Apakah engkau akan memaksa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1289">1289</a><span>]</span><span> manusia hingga mereka menjadi orang beriman ? “</span></p><p><span>[a]</span><span> 6:150 ; 16:10.</span></p><p><span>[b]</span><span> 2:257 ; 18:30.</span></p>');
INSERT INTO `quran` VALUES (7820,2,10,101,'<p><span>Dan tidak ada seorang pun akan beriman kecuali dengan izin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1290">1290</a><span>]</span><span> Allah Dan </span><span>[c]</span><span> Dia menimpakan kemurkaan atas orang-orang yang tidak menggunakan akal.</span></p><p><span>[c]</span><span> 6:126.</span></p>');
INSERT INTO `quran` VALUES (7821,2,10,102,'<p><span>Katakanlah, </span><span>[d]</span><span> “Perhatikanlah apa yang ada di seluruh langit dan bumi ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1291">1291</a><span>] [e]</span><span> Dan tidak berfaedah Tanda-tanda dan peringatan-peringatan bagi kaum yang tidak beriman.</span></p><p><span>[d]</span><span> 7:186.</span></p><p><span>[e]</span><span> 54:6.</span></p>');
INSERT INTO `quran` VALUES (7822,2,10,103,'<p><span>Maka </span><span>[f]</span><span> apakah yang mereka tunggu selain seperti hari-hari orang-orang yang telah berlalu sebelum mereka ? Katakanlah, “Maka </span><span>[g]</span><span> tunggulah, sesungguhnya aku pun bersamamu termasuk orang-orang yang menunggu.</span></p><p><span>[f]</span><span> 35:44.</span></p><p><span>[g]</span><span> 11:123.</span></p>');
INSERT INTO `quran` VALUES (7823,2,10,104,'<p><span>Kemudian Kami akan menyelamatkan rasul-rasul Kami dan orang-orang yang beriman. </span><span>[a]</span><span> Demikianlah menjadi hak atas Kami untuk menyelamatkan orang-orang yang beriman.</span></p><p><span>[a]</span><span> 30:48 ; 40:52 ; 58:22.</span></p>');
INSERT INTO `quran` VALUES (7824,2,10,105,'<p><span>R. 11</span></p><p><span>Katakanlah, “Hai manusia, jika kamu masih dalam keraguan tentang agamaku, </span><span>[b]</span><span> maka </span><span>ketahuilah, bahwa </span><span> aku tidak menyembah yang kamu sembah selain Allah ; akan tetapi aku hanya menyembah Allah, Yang akan mematikan kamu, dan </span><span>[c]</span><span> aku diperintahkan supaya termasuk di antara orang-orang yang beriman.”</span></p><p><span>[b]</span><span> 109:3.</span></p><p><span>[c]</span><span> 6:164.</span></p>');
INSERT INTO `quran` VALUES (7825,2,10,106,'<p><span>Dan akan diperintahkan, </span><span>[d]</span><span> “Hadapkanlah wajahmu untuk agama dengan selurus-lurusnya </span><span>[e]</span><span> dan janganlah engkau termasuk di antara orang-orang musyrik.</span></p><p><span>[d]</span><span> 30:31, 44.</span></p><p><span>[e]</span><span> 28:88.</span></p>');
INSERT INTO `quran` VALUES (7826,2,10,107,'<p><span>[f]</span><span> Dan janganlah engkau seru selain Allah apa yang tidak memberi engkau manfaat dan tidak </span><span>pula</span><span> memberi engkau mudarat. Dan jika engkau berbuat demikian, maka sesunggunya engkau pasti akan termasuk di antara orang-orang aniaya.</span></p><p><span>[f]</span><span> 28:89.</span></p>');
INSERT INTO `quran` VALUES (7827,2,10,108,'<p><span>[a]</span><span> dan jika Allah menimpakan suatu mudarat kepada engkau, maka tidak ada yang dapat menghilangkannya selain Dia. Dan jika Dia menghendaki suatu kebaikan bagi engkau, maka tidak ada yang dapat menolak karunia-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1292">1292</a><span>]</span><span> Dia memberikan kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya. Dan Dia Maha Pengampun, Maha Penyayang.</span></p><p><span>[a]</span><span> 6:18 ; 39:39.</span></p>');
INSERT INTO `quran` VALUES (7828,2,10,109,'<p><span>Katakanlah, “Hai menusia, sesungguhnya telah datang kepada kamu kebenaran dari Tuhan mu. Maka </span><span>[b]</span><span> barangsiapa mengikuti petunjuk itu bagi dirinya sendiri. Dan barangsiapa sesat, maka sesungguhnya kesesatan itu akan menimpa dirinya. Dan aku bukanlah penjaga atas kamu.”</span></p><p><span>[b]</span><span> 27:93 ;39;42.</span></p>');
INSERT INTO `quran` VALUES (7829,2,10,110,'<p><span>Dan </span><span>[c]</span><span> ikutilah apa yang diwahyukan kepada engkau dan bersabarlah, hingga Allah memberi keputusan. Dan Dia adalah sebaik-baik Hakim.</span></p><p><span>[c]</span><span> 7:204.</span></p>');
INSERT INTO `quran` VALUES (7830,2,11,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (7831,2,11,2,'<p><span>[b]</span><span> Aku Allah Yang Maha Melihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1293">1293</a><span>]</span><span> </span><span>[c</span><span>] </span><span>Ini adalah </span><span> Kitab yang Ayat-ayatnya telah dibuat kokoh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1293a">1293a</a><span>]</span><span> kemudian telah diuraikan dengan rinci </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1294">1294</a><span>]</span><span> dari </span><span>Tuhan</span><span> Yang Maha Bijaksana, Mahatahu.</span></p><p><span>[b]</span><span> 10:2 ; 12:2 ; 13:2 ;14:2 ;15:2.</span></p><p><span>[c]</span><span> 3:8 ; 10:2.</span></p>');
INSERT INTO `quran` VALUES (7832,2,11,3,'<p><span>Supaya kamu jangan menyembah selain Allah </span><span>[d]</span><span> Sesungguhnya aku bagi kamu pemberi peringatan dan pembawa khabar suka dari-Nya.</span></p><p><span>[d]</span><span> 2:120 ; 5:20 ; 7:189 ; 25:57 ; 34:29 ; 35:25.</span></p>');
INSERT INTO `quran` VALUES (7833,2,11,4,'<p><span>Dan supaya kamu </span><span>[e]</span><span> minta ampunan kepada Tuhan-mu, kemudian kembalilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1295">1295</a><span>]</span><span> kepada-Nya. Dia akan menganugerahkan barang-barang perbekalan yang baik kepada kamu sampai saat yang ditentukan dan Dia akan memberikan karunia-Nya kepada setiap orang yang berhak </span><span>menerima </span><span>karunia. Dan jika kamu berpaling, maka sesungguhnya aku takut atas kamu terhadap azab Hari yang dahsyat.</span></p><p><span>[e]</span><span> 11:53, 62 ; 71:11.</span></p>');
INSERT INTO `quran` VALUES (7834,2,11,5,'<p><span>[a]</span><span> Kepada Allah tempat kembalimu, dan Dia Mahakuasa atas segala sesuatu.</span></p><p><span>[a]</span><span> 10:5.</span></p>');
INSERT INTO `quran` VALUES (7835,2,11,6,'<p><span>Ingatlah ! Sesungguhnya mereka memalingkan dada mereka supaya mereka menyembunyikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1296">1296</a><span>] </span><span>pikiran buruk</span><span> dari-Nya. Ingatlah, ketika mereka menyelimuti dirinya dengan pakaiannya, </span><span>[b]</span><span> Dia mengetahui apa yang mereka sembunyikan dan yang mereka nyatakan. Sesungguhnya Dia Maha Mengetahui apa yang terkandung dalam dada.</span></p><p><span>[b]</span><span> 2:78 ; 16:24 ; 27:75 ; 28:70 ; 36:77.</span></p>');
INSERT INTO `quran` VALUES (7836,2,11,7,'<p><span>JUZ XII</span></p><p><span>Dan </span><span>[a]</span><span> tidak ada suatu makhluk bernyawa di bumi, melainkan Allah yang menanggung rezekinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1297">1297</a><span>]</span><span> Dan Dia mengetahui tempat tinggalnya yang sementara dan tempat tinggalnya yang tetap. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1298">1298</a><span>]</span><span> Semuanya </span><span>tercatat</span><span> dalam Kitab yang nyata.</span></p><p><span>[a]</span><span> 11:57.</span></p>');
INSERT INTO `quran` VALUES (7837,2,11,8,'<p><span>Dan </span><span>[b]</span><span> Dia yang telah menciptakan seluruh langit dan bumi dalam enam masa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1299">1299</a><span>]</span><span> dan </span><span>‘</span><span>Arasy-Nya diatas air </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1300">1300</a><span>] [c]</span><span> supaya Dia mengujimu siapakah di antara kamu yang lebih baik amalnya. Dan jika engkau mengatakan, “Sesungguhnya kamu akan dibangkitkan setelah mati,” niscaya orang-orang yang ingkar itu akan berkata, “Ini tiada lain melainkan sihir yang nyata.”</span></p><p><span>[b]</span><span> 7:55 ; 10:4 ; 25:60.</span></p><p><span>[c]</span><span> 5:49 ; 6:166 ; 67:3.</span></p>');
INSERT INTO `quran` VALUES (7838,2,11,9,'<p><span>[a]</span><span> Dan jika Kami tangguhkan azab dari mereka sampai suatu waktu yang telah ditentukan, pasti mereka akan berkata, “Apakah yang menahannya ?” Ingatlah, pada hari </span><span>azab</span><span> itu akan datang kepada mereka, tidak ada yang dapat dielakkan dari mereka, dan akan mengepung mereka apa yang mereka perolok-olokan itu.</span></p><p><span>[a]</span><span> 17:74.</span></p>');
INSERT INTO `quran` VALUES (7839,2,11,10,'<p><span>Dan </span><span>[b]</span><span> jika Kami merasakan kepada manusia rahmat dari Kami. Kemudian Kami tarik kembali darinya, sesungguhnya ia pasti putus asa, tidak bersyukur.</span></p><p><span>[b]</span><span> 41:52.</span></p>');
INSERT INTO `quran` VALUES (7840,2,11,11,'<p><span>Dan jika Kami membuat dia merasakan kebahagian </span><span>[c]</span><span> setelah musibah menimpanya, pasti ia akan berkata, “Lenyaplah segala kesusahan dariku. “ Sesungguhnya ia pasti gembira, bangga,.</span></p><p><span>[c]</span><span> 41:51.</span></p>');
INSERT INTO `quran` VALUES (7841,2,11,12,'<p><span>[d]</span><span> Kecuali orang-orang yang bersabar dan beramal shaleh. Mereka itulah yang bagi mereka ampunan dan ganjaran yang besar.</span></p><p><span>[d]</span><span> 41:9 ; 84:26 ; 95:7.</span></p>');
INSERT INTO `quran` VALUES (7842,2,11,13,'<p><span>Maka </span><span>[a]</span><span> boleh jadi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1301">1301</a><span>]</span><span> </span><span>orang kafir mengharap</span><span> engkau meninggalkan sebahagian dari apa yang diwahyukan kepada engkau, supaya dada engkau menjadi sempit karenanya, bahwa mereka berkata, </span><span>[b]</span><span> “Mengapa tidak diturunkan kepadanya suatu khazanah atau datang bersamanya seorang malaikat ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1302">1302</a><span>] [c]</span><span> Sesungguhnya engkau hanya seorang pemberi ingat. Dan Allah adalah Pemelihara atas segala sesuatu.</span></p><p><span>[a]</span><span> 17:74.</span></p><p><span>[b]</span><span> 17:94 ; 25:9.</span></p><p><span>[c]</span><span> 13:8.</span></p>');
INSERT INTO `quran` VALUES (7843,2,11,14,'<p><span>Apakah mereka berkata, </span><span>[d]</span><span> “ Ia telah membuat-buatnya ? “ Katakanlah, “Bawalah sepuluh surah semisal itu yang dibuat-buat ; dan panggillah siapa saja yang dapat kamu </span><span>panggil</span><span> selain Allah, jika memang kamu orang yang benar.”</span></p><p><span>[d]</span><span> 2:24 ; 10:39 ; 17:89 ; 52:34, 35.</span></p>');
INSERT INTO `quran` VALUES (7844,2,11,15,'<p><span>Maka jika mereka tidak menerima </span><span>tantangan</span><span> kamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1303">1303</a><span>]</span><span> </span><span>[a]</span><span> maka ketahuilah, bahwa sesungguhnya itu diturunkan dengan ilmu Allah dan bahwa tiada Tuhan selain Dia. Maka apakah kamu orang yang menyerahkan diri ?</span></p><p><span>[a]</span><span> 4:167.</span></p>');
INSERT INTO `quran` VALUES (7845,2,11,16,'<p><span>[b]</span><span> Barangsiapa menghendaki kehidupan dunia dan perhiasannya, akan Kami balas sepenuhnya, kepada mereka amal-amal mereka di dalamnya dan mereka di dalamnya tidak akan dirugikan.</span></p><p><span>[b]</span><span> 2:201 ; 17:19.</span></p>');
INSERT INTO `quran` VALUES (7846,2,11,17,'<p><span>[c]</span><span> Mereka itulah orang-orang yang tidak ada bagi mereka diakhirat kecuali Api dan sia-sialah apa yang mereka perbuat di dalamnya, dan sia-sia apa yang telah mereka amalkan.</span></p><p><span>[c]</span><span> 17:19.</span></p>');
INSERT INTO `quran` VALUES (7847,2,11,18,'<p><span>Maka </span><span>[a]</span><span> apakah orang yang </span><span>berdiri</span><span> atas dalil yang nyata dari Tuhan-nya dan </span><span>[b]</span><span> ia akan di susul oleh seorang saksi dari-Nya dan yang sebelumnya telah didahului oleh Kitab Musa, sebagai imam dan rahmat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1304">1304</a><span>]</span><span> Mereka itu beriman kepadanya. Dan barangsiapa yang ingkar kepadanya diantara golongan-golongan, maka Api akan menjadi tempat yang dijanjikan baginya. </span><span>[c]</span><span> Maka, janganlah engkau ragu-ragu mengenainya, sesungguhnya itu adalah kebenaran dari Tuhan engkau, akan tetapi kebanyakan manusia tidak beriman.</span></p><p><span>[a]</span><span> 47:15.</span></p><p><span>[b]</span><span> 46:11 ; 61:7.</span></p><p><span>[c]</span><span> 2:148 ; 10:95.</span></p>');
INSERT INTO `quran` VALUES (7848,2,11,19,'<p><span>Dan </span><span>[d]</span><span> siapakah yang lebih aniaya daripada orang yang mengada-adakan dusta terhadap Allah? Mereka itu akan dihadapkan kepada Tuhan mereka, dan akan berkata para saksi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1305">1305</a><span>]</span><span> </span><span>[e]</span><span> “Inilah orang-orang yang telah berdusta terhadap Tuhan-nya.” Ingatlah, laknat Allah atas orang yang aniaya ;</span></p><p><span>[d]</span><span> 6:22 ; 10:18 ; 61:8.</span></p><p><span>[e]</span><span> 39:61.</span></p>');
INSERT INTO `quran` VALUES (7849,2,11,20,'<p><span>Yaitu,</span><span> orang-orang </span><span>[a]</span><span> yang menghalangi dari jalan Allah dan mereka mengingingkan bengkok. Dan mereka itulah yang ingkar kepada akhirat.</span></p><p><span>[a]</span><span> 3:100 ; 7:46 ; 14:4 ; 16:89.</span></p>');
INSERT INTO `quran` VALUES (7850,2,11,21,'<p><span>Mareka itu tidak dapat menggagalkan </span><span>rencana Allah</span><span> di bumi, dan tiada kawan bagi mereka selain Allah Akan dilipatgandakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1306">1306</a><span>]</span><span> bagi mereka azab itu. </span><span>[b]</span><span> Mereka tidak akan sanggup mendengar dan tidak pula mereka dapat melihat.</span></p><p><span>[b]</span><span> 26:213.</span></p>');
INSERT INTO `quran` VALUES (7851,2,11,22,'<p><span>[c]</span><span> Mereka itulah orang-orang yang telah merugikan diri mereka sendiri, dan akan lenyaplah dari mereka apa yang selalu mereka ada-adakan.</span></p><p><span>[c]</span><span> 7:54 ; 10:31.</span></p>');
INSERT INTO `quran` VALUES (7852,2,11,23,'<p><span>[d]</span><span> Tidak syak lagi sesungguhnya mereka di akhirat yang paling rugi.</span></p><p><span>[d]</span><span> 16:110.</span></p>');
INSERT INTO `quran` VALUES (7853,2,11,24,'<p><span>Sesungguhnya </span><span>[e]</span><span> orang-orang yang telah beriman dan beramal shaleh dan merendahkan diri di hadapan Tuhan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1307">1307</a><span>]</span><span> mereka itulah penghuni surga mereka di dalamnya tinggal selama-lamanya.</span></p><p><span>[e]</span><span> 2:83 ; 3:58 ; 4:58 ; 13:30 ; 22:57 ; 29:8 ; 30:16 ; 42:23.</span></p>');
INSERT INTO `quran` VALUES (7854,2,11,25,'<p><span>[a]</span><span> Misal kedua golongan itu adalah seperti orang buta dan tuli, dan orang melihat dan mendengar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1308">1308</a><span>]</span><span> Apakah misal kedua golongan itu sama ? Apakah kamu tidak juga mengerti ?</span></p><p><span>[a]</span><span> 13:17 ; 35:20, 21.</span></p>');
INSERT INTO `quran` VALUES (7855,2,11,26,'<p><span>R. 3</span></p><p><span>Dan sesungguhnya telah </span><span>[b]</span><span> Kami utus Nuh kepada kaumnya, </span><span>ia berkata,</span><span> “Sesungguhnya aku bagimu pemberi ingat yang nyata,</span></p><p><span>[b]</span><span> 7:60 ; 23:24 ; 71:3.</span></p>');
INSERT INTO `quran` VALUES (7856,2,11,27,'<p><span>[c]</span><span> “Bahwa janganlah kamu menyembah selain Allah Sesungguhnya aku takut atas kamu terhadap azab Hari yang pedih.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1309">1309</a><span>]</span></p><p><span>[c]</span><span> 7:60 ; 71:4.</span></p>');
INSERT INTO `quran` VALUES (7857,2,11,28,'<p><span>Maka berkata </span><span>[a]</span><span> pemuka-pemuka yang ingkar dari antara kaumnya, “Kami tidak melihat engkau kecuali seorang manusia seperti kami, dan </span><span>[b]</span><span> Kami tidak melihat mereka yang mengikuti engkau melainkan orang-orang yang keadaan lahirnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1310">1310</a><span>]</span><span> paling hina di antara kami. Dan tidak kami lihat pada kamu suatu pun kelebihan atas kami ; bahkan kami yakin bahwa kamu adalah pendusta.”</span></p><p><span>[a]</span><span> 23:25.</span></p><p><span>[b]</span><span> 26:112.</span></p>');
INSERT INTO `quran` VALUES (7858,2,11,29,'<p><span>[c]</span><span> Ia berkata, “Hai kaumku, bagaimana pandanganmu, jika aku </span><span>berdiri</span><span> atas suatu Tanda yang nyata dari Tuhan-ku, dan telah Dia anugerahkan kepadaku rahmat dari sisi-Nya, tetapi itu dikaburkan bagimu. Apakah akan kami paksakan itu kepadamu, padahal kamu tidak menyukai nya ?</span></p><p><span>[c]</span><span> 11:64 ;47:15.</span></p>');
INSERT INTO `quran` VALUES (7859,2,11,30,'<p><span>[d]</span><span> “Dan hai kaumku, aku tidak minta kepadamu harta atas itu. Tidaklah ganjaranku hanyalah pada Allah Dan </span><span>[e]</span><span> aku tidak akan mengusir orang yang telah beriman. Sesungguhnya mereka akan bertemu dengan Tuhan mereka. Tetapi aku memandang kamu sebagai kaum yang berbuat bodoh.</span></p><p><span>[d]</span><span> 10:73 ; 26:110.</span></p><p><span>[e</span><span>] 26:115.</span></p>');
INSERT INTO `quran` VALUES (7860,2,11,31,'<p><span>“Dan hai kaumku, siapakah yang dapat menolongku dari Allah jika aku mengusir mereka ? Apakah kamu tidak juga mau mengambil pelajaran ?</span></p>');
INSERT INTO `quran` VALUES (7861,2,11,32,'<p><span>“Dan </span><span>[a]</span><span> tidak aku berkata kepadamu, “Padaku ada perbendaharaan Allah dan aku tidak mengetahui yang gaib dan tidak pula aku berkata, </span><span>‘</span><span>Sesungguhnya aku adalah malaikat.</span><span>’</span><span> dan tidak aku berkata mengenai orang-orang yang dipandang hina oleh matamu, </span><span>‘</span><span>Sekali-kali Allah tidak akan memberikan kebaikan apa pun kepada mereka.</span><span>’</span><span> Allah lebih mengetahui yang ada dalam diri mereka. Sesungguhnya jika demikian niscaya aku termasuk orang-orang yang aniaya.”</span></p><p><span>[a]</span><span> 6:51.</span></p>');
INSERT INTO `quran` VALUES (7862,2,11,33,'<p><span>Berkata mereka, “Hai Nuh, </span><span>[b]</span><span> sesungguhnya engkau telah berbantah dengan kami dan memperpanjang bantahanmu terhadap kami ; maka datangkanlah kepada kami </span><span>azab</span><span> yang telah engkau ancamkan kepada kami, jika engkau termasuk orang-orang yang benar.”</span></p><p><span>[b]</span><span> 46:23.</span></p>');
INSERT INTO `quran` VALUES (7863,2,11,34,'<p><span>Berkata ia, “Sesungguhnya, hanya </span><span>[a]</span><span> Allah yang akan mendatangkannya kepadamu, jika Dia kehendaki, dan kamu tidak dapat menggagalkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1311">1311</a><span>]</span></p><p><span>[a]</span><span> 46:24.</span></p>');
INSERT INTO `quran` VALUES (7864,2,11,35,'<p><span>“Dan tidak akan bermanfaat kepadamu, jika aku berkehendak memberi kamu nasihat, seandainya Allah berkehendak , seandainya Allah berkehendak membinasa kan kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1312">1312</a><span>]</span><span> Dia adalah Tuhan-mu, dan kepada-Nya kamu akan dikembalikan.”</span></p>');
INSERT INTO `quran` VALUES (7865,2,11,36,'<p><span>[b]</span><span> Apakah mereka mengatakan, “Ia telah mengada-adakan itu ? “ Katakanlah, “Seandainya aku telah mengada-adakannya, maka akulah yang akan menanggung dosaku, dan aku berlepas diri dari dosa yang kamu perbuat.”</span></p><p><span>[b]</span><span> 46:9.</span></p>');
INSERT INTO `quran` VALUES (7866,2,11,37,'<p><span>R. 4</span></p><p><span>Dan telah diwahyukan kepada Nuh, “Bahwa sekali-kali tidak akan beriman seorang pun dari kaummu selain orang yang telah beriman </span><span>sebelumnya</span><span> ; maka janganlah engkau bersedih mengenai apa yang selama ini mereka kerjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1313">1313</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7867,2,11,38,'<p><span>“Dan </span><span>[a]</span><span> buatlah bahtera di hadapan mata </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1314">1314</a><span>]</span><span> Kami dan </span><span>sesuai dengan wahyu</span><span> Kami. Dan janganlah engkau bicarakan dengan Aku mengenai orang yang berlaku aniaya. Sesungguhnya mereka akan di tenggelamkan.”</span></p><p><span>[a]</span><span> 23:28.</span></p>');
INSERT INTO `quran` VALUES (7868,2,11,39,'<p><span>Dan mulailah ia membuat bahtera itu ; dan apabila lewat padanya pemuka-pemuka dari kaumnya, mereka itu memperolok-olokannya. Berkatalah ia, “Jika kamu memperolok-olokkan kami, maka sesungguhnya kami pun akan memperolok-olokan kamu, seperti kamu memperolok-olokkan.</span></p>');
INSERT INTO `quran` VALUES (7869,2,11,40,'<p><span>[b]</span><span> “Kemudian kamu segera akan mengetahui siapa yang kepadanya akan datang azab yang akan menghinakannya, dan aikan menimpa atasnya azab yang tetap.”</span></p><p><span>[b]</span><span> 11:94 ; 39:40, 41.</span></p>');
INSERT INTO `quran` VALUES (7870,2,11,41,'<p><span>Hingga </span><span>[a]</span><span> ketika datang perintah Kami dan memancarlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1315">1315</a><span>]</span><span> sumber mata air ; Kami berkata, </span><span>[b]</span><span> “Naikkanlah ke dalam </span><span>bahtera</span><span> itu masing-masing dari jenis </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1316">1316</a><span>]</span><span> satu pasang, dan keluarga engkau, kecuali mereka yang keputusannya telah ditetapkan, dan mereka yang telah beriman. Dan tiada yang beriman bersamanya malainkan sedikit.</span></p><p><span>[a]</span><span> 23:28 ; 54:13.</span></p><p><span>[b]</span><span> 23:28.</span></p>');
INSERT INTO `quran` VALUES (7871,2,11,42,'<p><span>Dan ia berkata, “Naiklah ke atasnya. Dengan nama Allah berlayarnya dan berlabuhnya. Sesungguhnya Tuhan-ku Maha Pengampun, Maha Penyayang.”</span></p>');
INSERT INTO `quran` VALUES (7872,2,11,43,'<p><span>Dan berlayarlah </span><span>bahtera</span><span> itu membawa mereka di tengah ombak laksana gunung. Dan Nuh memanggil anaknya, yang berada di tempat terpisah, “Hai anakku, naiklah beserta kami dan janganlah engkau termasuk orang-orang kafir.”</span></p>');
INSERT INTO `quran` VALUES (7873,2,11,44,'<p><span>Dia menjawab, “Aku akan segera mencari perlindungan ke gunung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1317">1317</a><span>]</span><span> yang akan menyelamatkan dari air.” Berkatalah ia, </span><span>Nuh,</span><span> “Tidak ada yang menyelamatkan pada hari ini dari perintah Allah, kecuali bagi orang yang Dia kasihani.” Lalu gelombang menjadi penghalang di antara keduanya ; maka jadilah ia termasuk orang-orang yang ditenggelamkan.</span></p>');
INSERT INTO `quran` VALUES (7874,2,11,45,'<p><span>Dan difirmankan, “Hai bumi, telanlah airmu, dan hai langit, hentikanlah </span><span>hujan.</span><span>” Maka disurutkanlah air, dan selesailah perintah itu. Dan bahtera itu pun berlabuh di atas Al Judi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1317a">1317a</a><span>]</span><span> dan dikatakan, “Kebinasaanlah bagi kaum aniaya.”</span></p>');
INSERT INTO `quran` VALUES (7875,2,11,46,'<p><span>Dan Nuh berseru kepada Tuhan-nya dan berkata, “Ya Tuhan-ku, sesungguhnya anakku termasuk keluargaku, dan sesungguhnya janji Engkau benar, dan Engkau adalah Hakim yang paling adil di antara sekalian hakim.”</span></p>');
INSERT INTO `quran` VALUES (7876,2,11,47,'<p>Allah<span> berfirman, “Hai Nuh, sesungguhnya ia bukanlah dari keluarga engkau ; ia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1318">1318</a><span>]</span><span> beramal yang tidak baik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1319">1319</a><span>]</span><span> Sekali-kali janganlah meminta kepada-Ku sesuatu yang engkau tidak diberi mengenainya ilmu. Aku nasihatkan kepada engkau, supaya engkau jangan termasuk orang-orang yang bodoh.”</span></p>');
INSERT INTO `quran` VALUES (7877,2,11,48,'<p><span>Berkatalah ia, </span><span>Nuh,</span><span> “Ya Tuhan-ku, sesungguhnya aku berlindung kepada Engkau dari memohon kepada Engkau sesuatu yang aku tidak mengetahuinya. Dan </span><span>[a]</span><span> sekiranya tidak Engkau mengampuniku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1320">1320</a><span>]</span><span> dan tidak mengasihiniku, niscaya aku akan termasuk orang-orang yang rugi.</span></p><p><span>[a]</span><span> 7:24.</span></p>');
INSERT INTO `quran` VALUES (7878,2,11,49,'<p><span>Difirmankan, “Hai Nuh, turunlah dengan keselamatan dan keberkatan dari Kami atas umat-umat dari orang-orang yang beserta engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1321">1321</a><span>] </span><span>Dan umat-umat </span><span>lain</span><span> yang niscaya akan Kami beri perbekalan untuk sementara ; kemudian akan menimpa mereka azab yang pedih dari Kami.”</span></p>');
INSERT INTO `quran` VALUES (7879,2,11,50,'<p><span>Inilah di antara khabar-khabar gaib </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1322">1322</a><span>]</span><span> yang Kami mewahyukannya kepada engkau. Tidaklah engkau mengetahuinya sebelum ini dan tidak pula kaum engkau. Maka bersabarlah. Sesungguhnya kesudahan yang </span><span>baik</span><span> itu bagi orang-orang yang bertakwa.</span></p>');
INSERT INTO `quran` VALUES (7880,2,11,51,'<p><span>Dan </span><span>[a]</span><span> kepada </span><span>‘</span><span>Ad </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1233">1233</a><span>]</span><span> </span><span>telah Kami utus</span><span> saudara mereka Hud. Ia berkata, “Hai kaumku, sembahlah Allah, tiada tuhan bagimu selain Dia. Tidaklah kamu melainkan mengada-adakan dusta;</span></p><p><span>[a]</span><span> 7:66.</span></p>');
INSERT INTO `quran` VALUES (7881,2,11,52,'<p><span>“Hai kaumku, </span><span>[a]</span><span> aku tidak minta upah kepadamu untuk itu. Tidaklah balasanku melainkan atas Dzat Yang telah menciptakanku. Apakah kamu tidak menggunakan akal ? </span></p><p><span>[a]</span><span> 26:128.</span></p>');
INSERT INTO `quran` VALUES (7882,2,11,53,'<p><span>“Dan hai kaumku, </span><span>[a]</span><span> mohonlah ampunan dari Tuhan-mu, kemudian bertobatlah kepada-Nya, niscaya Dia akan mengirimkan atasmu awan yang menurunkan hujan lebat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1324">1324</a><span>]</span><span> dan akan menambahkan kekuatan kepada kekuatanmu. Dan janganlah berpaling </span><span>dari Dia</span><span> sebagai orang-orang yang berdosa.”</span></p><p><span>[a]</span><span> 11:4, 62 ; 71:11.</span></p>');
INSERT INTO `quran` VALUES (7883,2,11,54,'<p><span>Mereka berkata, “Hai Hud, engkau tidak mendatangkan kepada kami suatu bukti yang nyata, dan </span><span>[b]</span><span> kami tidak akan meninggalkan tuhan-tuhan kami karena perkataan engkau dan sekali-kali kami tidak percaya kepada engkau ;</span></p><p><span>[b]</span><span> 71:24.</span></p>');
INSERT INTO `quran` VALUES (7884,2,11,55,'<p><span>“Tidaklah kami mengatakan melainkan sebagian tuhan-tuhan kami menimpakan kepada engkau keburukan. “ Ia menjawab, “Sesungguhnya aku bersaksi kepada Allah dan kamu pun bersaksilah, bahwa aku berlepas dari apa yang kamu sekutukan,</span></p>');
INSERT INTO `quran` VALUES (7885,2,11,56,'<p><span>Selain Dia, </span><span>[a]</span><span> meka lancarkanlah tipu-daya kamu semua terhadap ku, kemudian janganlah aku di beri tangguh.</span></p><p><span>[a]</span><span> 7:196 ; 10:72.</span></p>');
INSERT INTO `quran` VALUES (7886,2,11,57,'<p><span>“Sesungguhnya aku bertawakkal kepada Allah, Tuhan-ku dan Tuhan-mu. </span><span>[b]</span><span> Tiada suatu pun makhluk yang hidup melainkan Dia genggam rambut ubun-ubunnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1325">1325</a><span>]</span><span> Sesungguhnya Tuhan-ku berdiri di atas jalan yang lurus.</span></p><p><span>[b]</span><span> 11:7.</span></p>');
INSERT INTO `quran` VALUES (7887,2,11,58,'<p><span>“Tetapi jika kamu berpaling, maka sesungguhnya telah </span><span>[c]</span><span> kesampaikan kepadamu apa-apa yang dengannya aku diutus kepadamu. Dan </span><span>[d]</span><span> Tuhan-ku akan mengganti suatu kaum selain kamu. Dan kamu tidak akan dapat memudaratkan-Nya sedikit pun. Sesungguhnya Tuhan-ku penjaga atas segala sesuatu.”</span></p><p><span>[c]</span><span> 7:69 ; 46:24.</span></p><p><span>[d]</span><span> 4:134 ; 6:134.</span></p>');
INSERT INTO `quran` VALUES (7888,2,11,59,'<p><span>Dan ketika datang keputusan Kami, Kami selamatkan Hud dan orang-orang yang telah beriman bersamanya dengan rahmat dari Kami. Dan </span><span>[e]</span><span> Kami selamatkan mereka dari azab yang dahsyat.</span></p><p><span>[e]</span><span> 7:73.</span></p>');
INSERT INTO `quran` VALUES (7889,2,11,60,'<p><span>Dan demikianlah </span><span>‘</span><span>Ad. Mereka telah mengingkari Tanda-tanda Tuhan mereka dan menduhakai rasul-rasul-Nya dan mengikuti perintah setiap orang yang sewenang-wenang yang menentang kebenaran.</span></p>');
INSERT INTO `quran` VALUES (7890,2,11,61,'<p><span>[a]</span><span> Dan mereka diikuti oleh laknat di dunia ini dan pada Hari Kiamat. Ketahuilah, sesungguhnya kaum </span><span>‘</span><span>Ad tidak mensyukuri </span><span>kabaikan</span><span> Tuhan mereka. Ketahuilah, kebinasaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1325a">1325a</a><span>]</span><span> bagi </span><span>‘</span><span>Ad, kaum Hud.</span></p><p><span>[a]</span><span> 28:43.</span></p>');
INSERT INTO `quran` VALUES (7891,2,11,62,'<p><span>R. 6</span></p><p><span>[b]</span><span> Dan kepada kaum Tsamud </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1326">1326</a><span>]</span><span> </span><span>Kami utus</span><span> saudara mereka Shaleh. Berkata ia, “Hai kaumku, sembahlah Allah, tiada bagimu tuhan selain Dia. Dia-lah yang telah membangkitkan kamu dari bumi, dan memakmurkan kamu di dalamnya, maka mohonlah ampunan kepada-Nya. Sesungguhnya Tuhan-ku itu dekat, mengabulkan </span><span>doa.</span></p><p><span>[b]</span><span> 7:74.</span></p>');
INSERT INTO `quran` VALUES (7892,2,11,63,'<p><span>Mereka berkata, “Hai Shaleh, sesungguhnya engkau adalah seorang di antara kami yang menjadi tumpuan harapan kami sebelum ini. Apakah engkau melarang kami menyembah apa yang bapak-bapak kami sembah ? Dan sesungguhnya kami benar-benar dalam keraguan yang menggelisahkan mengenai apa yang engkau serukan kepada kami.”</span></p>');
INSERT INTO `quran` VALUES (7893,2,11,64,'<p><span>Berkata ia, </span><span>[a]</span><span> “Hai kaumku, apakah kamu melihat, jika aku berada di atas bukit yang nyata menganugerahkan kepadaku rahmat dari-Nya, maka siapakah yang dapat menolong melawan Allah, jika aku mendurhakai-Nya ? Maka tidaklah kamu menambah apa pun kepadaku selain kerugian.</span></p><p><span>[a]</span><span> 11:29, 89.</span></p>');
INSERT INTO `quran` VALUES (7894,2,11,65,'<p><span>“Dan hai kaumku, </span><span>[b]</span><span> inilah unta betina dari Allah sebagai Tanda bagimu, maka biarkanlah dia makan di bumi Allah dan janganlah kamu sentuh dia dengan menyakitinya, kalau tidak, azab yang dekat menimpamu.”</span></p><p><span>[b]</span><span> 7:74 ; 17:60 ; 26:156 ; 54:28 ; 91:14.</span></p>');
INSERT INTO `quran` VALUES (7895,2,11,66,'<p><span>[a]</span><span> Tetapi mereka telah memotong kakinya. Maka ia berkata, “Bersenang-senanglah kamu di rumahmu selama tiga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1327">1327</a><span>]</span><span> hari. Itulah suatu janji yang tidak dapat didustakan.”</span></p><p><span>[a]</span><span> 7:78; 26:158 ; 54:30 ; 91:15.</span></p>');
INSERT INTO `quran` VALUES (7896,2,11,67,'<p><span>Maka ketika datang perintah Kami, Kami selamatkah Shaleh dan orang-orang yang beriman bersamanya dengan rahmat dari Kami, dan pula dari kehinaan pada hari itu. Sesungguhnya Tuhan engkau Dia-lah Yang Maha Kuat, Maha Perkasa.</span></p>');
INSERT INTO `quran` VALUES (7897,2,11,68,'<p><span>Dan </span><span>[a]</span><span> azab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1328">1328</a><span>]</span><span> menyergap orang yang aniaya itu, maka mereka tertelungkup di rumah mereka masing-masing.</span></p><p><span>[a]</span><span> 7:79 ; 26:159 ; 54:32.</span></p>');
INSERT INTO `quran` VALUES (7898,2,11,69,'<p><span>[b]</span><span> Seolah-olah mereka itu tidak pernah tinggal di dalamnya. Ketahuilah, sesungguhnya </span><span>kaum</span><span> Tsamud telah mengingkari Tuhan-nya. Ketahuilah, kebinasaan bagi </span><span>kaum </span><span> Tsamud. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1329">1329</a><span>]</span></p><p><span>[b]</span><span> 10:25.</span></p>');
INSERT INTO `quran` VALUES (7899,2,11,70,'<p><span>R. 7</span></p><p><span>Dan </span><span>[a]</span><span> sesungguhnya telah datang utusan-utusan Kami </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1330">1330</a><span>]</span><span> kepada Ibrahim </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1331">1331</a><span>]</span><span> dengan </span><span>membawa</span><span> kabar suka. </span><span>[b]</span><span> Mereka berkata, “Selamat sejahtera,” maka tidak lama kemudian datanglah ia dengan membawa anak sapi yang dipanggang.</span></p><p><span>[a]</span><span> 15:52 ; 51:25.</span></p><p><span>[b]</span><span> 15:53 ; 51:26.</span></p>');
INSERT INTO `quran` VALUES (7900,2,11,71,'<p><span>Maka </span><span>[c]</span><span> ketika ia melihat tangan mereka tidak menjamahnya, ia menganggap perbuatan mereka itu aneh dan merasa khawatir terhadap mereka. Berkatalah mereka itu, “Jangan engkau takut ; sesungguhnya kami adalah yang pernah diutus kepada kaum Luth. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1332">1332</a><span>]</span></p><p><span>[c]</span><span> 51:28, 29.</span></p>');
INSERT INTO `quran` VALUES (7901,2,11,72,'<p><span>Dan istrinya sedang berdiri </span><span>di dekatnya,</span><span> maka ia terkejut, lalu </span><span>[a]</span><span> Kami berikan khabar suka kepadanya </span><span>tentang kalhiran </span><span> Ishak, dan sesudah Ishak, Ya</span><span>’</span><span>kub.</span></p><p><span>[a]</span><span> 21:73 ; 5129.</span></p>');
INSERT INTO `quran` VALUES (7902,2,11,73,'<p><span>[b]</span><span> Berkatalah ia, “Aduhai celakalah aku ! Apakah aku akan melahirkan anak ? Sedangkan aku seorang perempuan tua dan suamiku ini orang yang sudah tua. Sesungguhnya ini benar-benar suatu hal yang ajaib.”</span></p><p><span>[b]</span><span> 51:30.</span></p>');
INSERT INTO `quran` VALUES (7903,2,11,74,'<p><span>[c]</span><span> Mereka berkata, “Apakah engkau merasa heran akan keputusan Allah ? Rahmat Allah dan berkat-Nya atas kamu, hai ahli rumah ini. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1333">1333</a><span>]</span><span> Sesungguhnya Dia Maha Terpuji, Maha Mulia.”</span></p><p><span>[c]</span><span> 51:31.</span></p>');
INSERT INTO `quran` VALUES (7904,2,11,75,'<p><span>Maka setalah rasa takut hilang dari Ibrahim, dan telah sampai pula khabar suka kepadanya, maka mulailah ia berbahas dengan Kami </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1334">1334</a><span>]</span><span> mengenai kaum Luth.</span></p>');
INSERT INTO `quran` VALUES (7905,2,11,76,'<p><span>[a]</span><span> Sesungguhnya Ibrahim itu sangat penyantun, pengiba, yang berulang kali kembali </span><span>kepada Kami.</span><span> </span></p><p><span>[a]</span><span> 9:114.</span></p>');
INSERT INTO `quran` VALUES (7906,2,11,77,'<p><span>“Hai Ibrahim, berhentilah dari </span><span>berbahas</span><span> ini. Sesungguhnya telah datang keputusan Tuhan engkau, dan sesungguhnya akan datang kepada mereka azab yang tak dapat dielakkan.”</span></p>');
INSERT INTO `quran` VALUES (7907,2,11,78,'<p><span>Dan </span><span>[b]</span><span> ketika utusan-utusan Kami datang kepada Luth, ia merasa susah karena mereka itu dan merasa tak berdaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1335">1335</a><span>]</span><span> terhadap mereka dan berkatalah ia, “Ini adalah hari yang amat sulit.”</span></p><p><span>[b]</span><span> 29:34.</span></p>');
INSERT INTO `quran` VALUES (7908,2,11,79,'<p><span>Dan datanglah kaumnya berlari-lari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1336">1336</a><span>]</span><span> kepadanya. Dan sebelum ini </span><span>pun</span><span> </span><span>[c]</span><span> mereka telah biasa berbuat keburukan. Ia berkata, “Hai kaumku, </span><span>[a]</span><span> inilah anak-anak perempuanku, mereka lebih suci untukmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1337">1337</a><span>]</span><span> Maka takutlah kepada Allah dan janganlah menghinakan aku di </span><span>hadapan </span><span> tamuku, Tidak adakah seorang </span><span>pun</span><span> di antaramu yang cerdik ?”</span></p><p><span>[c]</span><span> 7:81 ; 29:29.</span></p><p><span>[a]</span><span> 15:72</span></p>');
INSERT INTO `quran` VALUES (7909,2,11,80,'<p><span>Mereka berkata, “Sesungguhnya telah engkau ketahui, bahwa kami tidak mempunyai hak apa </span><span>pun</span><span> atas anak-anak perempuan engkau, dan sesungguhnya engkau mengetahui apa yang kami inginkan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1338">1338</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7910,2,11,81,'<p><span>Berkata ia, “Sekiranya aku mempunyai kekuatan untuk melawan kamu atau aku berlindung pada suatu perlindungan yang kuat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1338a">1338a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7911,2,11,82,'<p><span>Mereka berkata, “Hai Luth, sesungguhnya kami ini utusan-utusan Tuhan engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1339">1339</a><span>]</span><span> Mereka itu sekali-kali tak akan sampai kepada engkau, maka </span><span>[a]</span><span> berangkatlah dengan keluarga engkau </span><span>[b]</span><span> pada bagian malam dan janganlah seorangpun di antaramu menoleh kebelakang, kecuali istri engkau. Sesungguhnya apa yang akan menimpa mereka akan menimpanya </span><span>juga</span><span>. Sesungguhnya </span><span>[c]</span><span> waktu yang telah dijanjikan bagi mereka ialah waktu subuh. Bukankah subuh itu sudah dekat ?”</span></p><p><span>[a]</span><span> 15:66.</span></p><p><span>[b]</span><span> 7:84 ; 15:61 ; 29:34.</span></p><p><span>[c]</span><span> 15:67.</span></p>');
INSERT INTO `quran` VALUES (7912,2,11,83,'<p><span>[a]</span><span> Maka setelah datang keputusan Kami, </span><span>[b]</span><span> Kami jadikan </span><span>kota</span><span> itu jungkir-balik dan Kami hujani atasnya batu-batu dari tanah liat, bertubi-tubi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1339a">1339a</a><span>]</span></p><p><span>[a]</span><span> 15:75.</span></p><p><span>[b]</span><span> 51:34.</span></p>');
INSERT INTO `quran` VALUES (7913,2,11,84,'<p><span>[c]</span><span> Yang ditandai dari sisi Tuhan engkau, Dan </span><span>azab</span><span> seperti itu tidak jauh dari orang-orang yang aniaya.</span></p><p><span>[c]</span><span> 51;35.</span></p>');
INSERT INTO `quran` VALUES (7914,2,11,85,'<p><span>R. 8</span></p><p><span>[d]</span><span> Dan kepada Madyan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1340">1340</a><span>]</span><span> </span><span>Kami utus</span><span> saudara mereka Syu</span><span>’</span><span>aib. Ia berkata, “Hai kaumku, sembahlah Allah Tiada tuhan bagimu selain Dia. Dan </span><span>[e]</span><span> janganlah kamu mengurangi sukatan dan timbangan. Sesungguhnya aku melihat kamu dalam keadaan baik, dan sesungguhnya aku khawatir atas kamu azab Hari yang membinasakan.</span></p><p><span>[d]</span><span> 7:86 ; 29:37.</span></p><p><span>[e]</span><span> 26:182, 183.</span></p>');
INSERT INTO `quran` VALUES (7915,2,11,86,'<p><span>[f]</span><span> “Dan hai kaumku, cukupkanlah sukatan dan timbangan dengan adil ; janganlah kamu merugikan menusia atas barang-barang mereka, dan janganlah kamu mengacau di bumi dengan membuat kerusakan.</span></p><p><span>[f]</span><span> 7:86 ; 26:184.</span></p>');
INSERT INTO `quran` VALUES (7916,2,11,87,'<p><span>“Apa yang disisakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1341">1341</a><span>]</span><span> Allah itu lebih baik bagimu, jika kamu orang-orang yang beriman. Dan aku bukanlah penjaga atasmu.”</span></p>');
INSERT INTO `quran` VALUES (7917,2,11,88,'<p><span>Mereka berkata, “Hai Syu</span><span>’</span><span>aib, apakah shalatmu menyuruh engkau supaya kami meninggalkan apa yang disembah oleh bapak-bapak kami atau kami </span><span>meninggalkan</span><span> untuk melakukan di dalam harta kami apa yang kami kehendaki ? Kamu sesungguhnya </span><span>menganggap dirimu </span><span> seorang penyantun, cerdik.”</span></p>');
INSERT INTO `quran` VALUES (7918,2,11,89,'<p><span>Ia berkata, </span><span>[a]</span><span> “Hai kaumku, bagaimana pendanganmu jika aku </span><span>berdiri</span><span> atas dalil yang nyata dari Tuhan-ku, dan telah Dia berikan kepadaku dari sisi-Nya rezeki yang baik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1342">1342</a><span>]</span><span> Dan aku tidak menghendaki menentang kamu tentang apa yang aku melarang kamu terhadapnya. Aku tidak menghendaki kecuali perbaikan sesuai </span><span>[b]</span><span> kemampuanku. Dan tiada taufik padaku kecuali dengan </span><span>karunai</span><span> Allah Kepada-Nya aku kembali.</span></p><p><span>[a]</span><span> 11:64.</span></p><p><span>[b]</span><span> 7:94.</span></p>');
INSERT INTO `quran` VALUES (7919,2,11,90,'<p><span>“Dan hai kaumku, janganlah mendorong kamu berbuat dosa </span><span>karena </span><span> permusuhan terhadapku sehingga menimpa kamu seperti yang telah menimpa kaum Nuh atau kaum Hud atau kaum Shaleh. Sedang kaum Luth tidaklah jauh dari kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1343">1343</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7920,2,11,91,'<p><span>“Dan </span><span>[b]</span><span> mohonlah ampunan kepada Tuhan-mu ; kemudian tertobatlah kepada-Nya. Sesungguhnya Tuhan-ku Maha Penyayang, Maha Pencinta.”</span></p><p><span>[b]</span><span> 11:4.</span></p>');
INSERT INTO `quran` VALUES (7921,2,11,92,'<p><span>Mereka berkata, “Hai Syu</span><span>’</span><span>aib, kami tidak mengerti banyak tentang apa yang engkau katakan, dan </span><span>[c]</span><span> sesungguhnya kami melihat engkau di antara kami seorang yang lemah. Dan jika sekiranya tidak karena kaum kerabat engkau, niscaya kami telah merajam engkau. Dan engkau bukanlah seorang yang terhormat di tengah-tengah kami.”</span></p><p><span>[c]</span><span> 7:89.</span></p>');
INSERT INTO `quran` VALUES (7922,2,11,93,'<p><span>Ia berkata, “Hai kaumku, apakah kaum kerabatku itu lebih mulia atas kamu daripada Allah ? Dan telah kamu campakkan perintah Dia di belakang punggungmu. sesungguhnya Tuhan-ku meliputi apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (7923,2,11,94,'<p><span>“Dan </span><span>[a]</span><span> hai kaumku, beramallah kamu menurut kemampuanmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1343a">1343a</a><span>]</span><span> sesungguhnya aku pun beramal. Segera kamu akan mengetahui, siapa yang ditimpa azab yang menistakannya dan siapa yang pendusta. Dan tunggulah, sesungguhnya aku pun menunggu bersama kamu.”</span></p><p><span>[a]</span><span> 39:40.</span></p>');
INSERT INTO `quran` VALUES (7924,2,11,95,'<p><span>Dan ketika datang keputusan Kami, Kami selamatkan Syu</span><span>’</span><span>aib dan orang-orang yang beriman bersamanya dengan rahmat dari Kami, dan </span><span>[b]</span><span> azab itu menyergap orang-orang yang telah aniaya itu ; maka mereka tertelungkup di rumah-rumah mereka.</span></p><p><span>[b]</span><span> 7:92 ; 26:190 ; 29:38.</span></p>');
INSERT INTO `quran` VALUES (7925,2,11,96,'<p><span>[c]</span><span> Seolah-olah mereka tidak pernah tinggal di dalamnya. Ingatlah, kaum Madyan pun telah dibinasakan seperti kaum Tsamud telah dibinasakan.</span></p><p><span>[c] </span><span>7:93.</span></p>');
INSERT INTO `quran` VALUES (7926,2,11,97,'<p><span>Dan sesungguhnya telah </span><span>[a]</span><span> Kami utus Musa dengan Tanda-tanda Kami dan keterangan-keterangan yang nyata.</span></p><p><span>[a]</span><span> 14;6 ; 40:24.</span></p>');
INSERT INTO `quran` VALUES (7927,2,11,98,'<p><span>[b]</span><span> Kepada Firaun dan pemuka-pemukanya ; tetapi mereka itu mengikuti perintah Firaun, dan tidaklah sekali-kali perintah Firaun itu benar.</span></p><p><span>[b]</span><span> 23:47 ; 40:25.</span></p>');
INSERT INTO `quran` VALUES (7928,2,11,99,'<p><span>Ia akan berjalan di muka kaumnya pada Hari Kiamat dan akan membawa mereka masuk ke dalam Api. Dan alangkah buruknya tempat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1343b">1343b</a><span>]</span><span> yang didatangi itu.</span></p>');
INSERT INTO `quran` VALUES (7929,2,11,100,'<p><span>Dan </span><span>[c]</span><span> mereka diikuti oleh laknat di dunia ini dan </span><span>pada</span><span> Hari Kiamat. Alangkah buruknya pemberian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1344">1344</a><span>]</span><span> yang akan diberikan itu.</span></p><p><span>[c]</span><span> 28:43.</span></p>');
INSERT INTO `quran` VALUES (7930,2,11,101,'<p><span>[d]</span><span> Itu adalah </span><span>sebagian</span><span> dari berita-berita kota-kota yang Kami ceriterakan kepada engkau. Di antaranya ada yang masih berdiri dan ada yang telah musnah.</span></p><p><span>[d]</span><span> 20:100.</span></p>');
INSERT INTO `quran` VALUES (7931,2,11,102,'<p><span>Dan bukanlah </span><span>[a]</span><span> Kami yang menganiaya mereka, melainkan merekalah yang menganiaya diri sendiri, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1345">1345</a><span>]</span><span> maka tidaklah berfaedah sedikit pun kepada mereka tuhan-tuhan mereka yang mereka seru selain Allah, apabila datang perintah </span><span>azab</span><span> Tuhan engkau. dan itu tidak menambahkan kepada mereka selain kebinasaan.</span></p><p><span>[a]</span><span> 3:118 ; 16:34.</span></p>');
INSERT INTO `quran` VALUES (7932,2,11,103,'<p><span>[b]</span><span> Dan demikianlah cengkeraman Tuhan-mu, apabila Dia mencengkeram kota-kota yang aniaya. Sesungguhnya cengkeraman -Nya itu pedih lagi keras.</span></p><p><span>[b]</span><span> 54:43 ; 85:13.</span></p>');
INSERT INTO `quran` VALUES (7933,2,11,104,'<p><span>Sesungguhnya </span><span>[c]</span><span> pada yang demikian itu adalah satu Tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1346">1346</a><span>]</span><span> bagi siapa yang takut akan azab akhirat. Itulah suatu hari ketika semua manusia akan dihimpunkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1347">1347</a><span>]</span><span> dan itulah hari yang akan disaksikan.</span></p><p><span>[c]</span><span> 14:15.</span></p>');
INSERT INTO `quran` VALUES (7934,2,11,105,'<p><span>Dan tidaklah Kami undurkan itu melainkan untuk suatu jangka waktu yang telah ditentukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1348">1348</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7935,2,11,106,'<p><span>[a]</span><span> Tatkala datang hari itu, tiada seorang pun yang berbicara kecuali dengan izin-Nya ; maka di antara mereka akan ada yang bernasib buruk dan ada yang bernasib baik.</span></p><p><span>[a]</span><span> 78:39.</span></p>');
INSERT INTO `quran` VALUES (7936,2,11,107,'<p><span>Adapun orang yang buruk nasibnya, </span><span>[b]</span><span> mereka itu akan ada dalam Api ; di dalamnya mereka akan menarik nafas panjang dan tersendat-sendat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1349">1349</a><span>]</span></p><p><span>[b]</span><span> 21:101.</span></p>');
INSERT INTO `quran` VALUES (7937,2,11,108,'<p><span>Mereka </span><span>[a]</span><span> akan kekal di dalamnya selama langit dan bumi ada, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1350">1350</a><span>]</span><span> kecuali apa yang Tuhan engkau kehendaki. Sesungguhnya Tuhan engkau Maha Pelaksana apa yang Dia kehendaki.</span></p><p><span>[a]</span><span> 78:24.</span></p>');
INSERT INTO `quran` VALUES (7938,2,11,109,'<p><span>[b]</span><span> Tetapi mengenai orang yang bernasib naik, mereka ada dalam surga ; mereka akan kekal di dalamnya selama langit dan bumi ada, kecuali apa yang Tuhan engkau kehendaki, pemberian yang tiada putus-putusnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1351">1351</a><span>]</span></p><p><span>[b]</span><span> 15:49.</span></p>');
INSERT INTO `quran` VALUES (7939,2,11,110,'<p><span>Maka janganlah engkau dalam keraguan tentang yang mereka sembah. Mereka tidak menyembah melainkan seperti bapak-bapak mereka menyembah dahulu-kala ; dan sesungguhnya Kami pasti akan membayar penuh kepada mereka bagian mereka, tanpa dikurangi.</span></p>');
INSERT INTO `quran` VALUES (7940,2,11,111,'<p><span>Dan sesungguhnya telah </span><span>[a]</span><span> Kami berikan Kitab kepada Musa, tetapi telah ditimbulkan perselisihan di dalamnya. Dan jika sekiranya tiada kalimat yang telah terdahulu dari Tuhan engkau, tentu telah diadakan keputusan di antara mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1352">1352</a><span>]</span><span> Dan sesungguhnya mereka tentang itu dalam keraguan yang menggelisahkan.</span></p><p><span>[a]</span><span> 41:46.</span></p>');
INSERT INTO `quran` VALUES (7941,2,11,112,'<p><span>Dan sesungguhnya kepada mereka semua, Tuhan engkau pasti </span><span>[b]</span><span> akan menyempurnakan amal mereka. Sesunggunya Dia Maha Mengetahui apa yang mereka kerjakan.</span></p><p><span>[b]</span><span> 3:58 ; 16:97 ; 39:11.</span></p>');
INSERT INTO `quran` VALUES (7942,2,11,113,'<p><span>[a]</span><span> Maka tetaplah engkau pada jalan yang lurus sebagaimana yang diperintahkan kepada engkau ; dan </span><span>juga</span><span> kepada orang yang telah bertobat beserta engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1353">1353</a><span>]</span><span> dan janganlah kamu melampaui batas. Sesungguhnya Dia Maha Melihat apa yang kamu kerjakan.</span></p><p><span>[a]</span><span> 42:16.</span></p>');
INSERT INTO `quran` VALUES (7943,2,11,114,'<p><span>Dan janganlah kamu cenderung kepada orang aniaya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1354">1354</a><span>]</span><span> supaya jangan kamu disentuh Api. Dan tiada bagimu kawan selain Allah, kemudian kamu tidak akan diberi pertolongan.</span></p>');
INSERT INTO `quran` VALUES (7944,2,11,115,'<p><span>Dan </span><span>[b]</span><span> dirikanlah shalat pada kedua ujung siang dan pada beberapa bagian malam. Sesungguhnya kebaikan-kabaikan menghapuskan keburukan-keburukan. Ini adalah suatu peringatan bagi orang-orang yang penuh perhatian.</span></p><p><span>[b]</span><span> 17:79.</span></p>');
INSERT INTO `quran` VALUES (7945,2,11,116,'<p><span>Dan </span><span>[c]</span><span> bersabarlah, karena sesungguhnya Allah sekali-kali tidak menyia-nyiakan ganjaran orang-orang yang berbuat baik.</span></p><p><span>[c]</span><span> 12:91.</span></p>');
INSERT INTO `quran` VALUES (7946,2,11,117,'<p><span>Maka mengapakah tidak ada di antara umat sebelummu orang berpengertian, yang akan melarang kerusakan di bumi, kecuali sedikit dari antara mereka yang telah Kami selamatkan ? Tetapi </span><span>[a]</span><span> orang yang aniaya mereka tenggelam dalam </span><span>kelezatan</span><span> harta berlimpah-limpah, dan mereka itu menjadi orang-orang yang berdosa.</span></p><p><span>[a]</span><span> 13:34.</span></p>');
INSERT INTO `quran` VALUES (7947,2,11,118,'<p><span>Dan </span><span>[b]</span><span> Tuhan engkau sekali-kali tidak akan membinasakan kota-kota secara aniaya sedang penduduknya berbuat kebaikan.</span></p><p><span>[b]</span><span> 6:132 ; 20:135 ; 26:209 ; 28:60</span></p>');
INSERT INTO `quran` VALUES (7948,2,11,119,'<p><span>Dan </span><span>[c]</span><span> jika Tuhan engkau menghendaki, niscaya Dia telah menjadikan semua manusia satu umat ; tetapi mereka senantiasa berselisih.</span></p><p><span>[c]</span><span> 2:214 ; 10:20 ; 42:9.</span></p>');
INSERT INTO `quran` VALUES (7949,2,11,120,'<p><span>Kecuali kepada orang yang Tuhan engkau melimpahkan rahmat ; dan untuk itulah Dia mencipta kan mereka. </span><span>[d]</span><span> Tetapi perkataan Tuhan engkau akan menjadi sempurna, “Sesungguhnya akan Aku penuhi Jahannam dengan jin dan manusia semuanya.”</span></p><p><span>[d]</span><span> 15:44 ; 32:14 ; 38:85, 86.</span></p>');
INSERT INTO `quran` VALUES (7950,2,11,121,'<p><span>Dan Kami ceriterakan kepada engkau </span><span>[a]</span><span> semua khabar mengenai rasul-rasul, yang dengan itu Kami teguhkan hati engkau. Dan di dalam ini telah datang kepada engkau yang hak dan nasihat serta peringatan bagi orang-orang mukmin.</span></p><p><span>[a]</span><span> 25:33.</span></p>');
INSERT INTO `quran` VALUES (7951,2,11,122,'<p><span>Dan </span><span>[b]</span><span> katakanlah kepada orang yang tidak beriman, “Beramallah menurut kemampuanmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1355">1355</a><span>]</span><span> sesungguhnya kami </span><span>pun</span><span> beramal.</span></p><p><span>[b]</span><span> 6:136 ; 11:94 ; 39:40.</span></p>');
INSERT INTO `quran` VALUES (7952,2,11,123,'<p><span>“Dan kamu </span><span>[c]</span><span> tunggulah, kami </span><span>pun</span><span> sedang menunggu.”</span></p><p><span>[c]</span><span> 10:103 ; 32:31.</span></p>');
INSERT INTO `quran` VALUES (7953,2,11,124,'<p><span>Dan </span><span>[d]</span><span> kepunyaan Allah yang gaib di seluruh langit dan bumi, dan kepada Dia akan dikembalikan segala urusan. Maka sembahlah Dia dan bertawakkallah kepada-Nya. Dan Tuhan engkau tidak lengah mengenai apa yang kamu kerjakan.</span></p><p><span>[d]</span><span> 16:78 ; 27:66 ; 35:39.</span></p>');
INSERT INTO `quran` VALUES (7954,2,12,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (7955,2,12,2,'<p><span>[b]</span><span> Aku Allah Yang Maha Melihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1356">1356</a><span>]</span><span> </span><span>[c]</span><span> Inilah ayat-ayat Kitab yang terang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1356a">1356a</a><span>]</span></p><p><span>[b]</span><span> 10:2 ; 13:2 ; 11:2 ; 14:2 ; 15:2.</span></p><p><span>[c]</span><span> 15:2 ; 26:3 ; 27:2 ; 28:3 ;.</span></p>');
INSERT INTO `quran` VALUES (7956,2,12,3,'<p><span>Sesungguhnya </span><span>[d]</span><span> Kami telah menurunkannya Alquran dalam bahsa Arab, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1357">1357</a><span>]</span><span> supaya kamu memahami.</span></p><p><span>[d]</span><span> 42:8 ; 43:4 ; 46:13.</span></p>');
INSERT INTO `quran` VALUES (7957,2,12,4,'<p><span>Kami ceritakan kepada engkau sebaik-baiknya kisah dengan mewahyukan kepada engkau Alquran ini ; Walaupun engkau sebelumnya termasuk orang-orang lalai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1358">1358</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7958,2,12,5,'<p><span>Ingatlah </span><span> </span><span>[a]</span><span> ketika berkata Yusuf </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1359">1359</a><span>]</span><span> kepada ayahnya, “Ya ayahku, sesungguhnya aku lihat </span><span>dalam mimpi</span><span> sebelas bintang dan matahari dan bulan, aku lihat mereka bersujud kepadaku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1360">1360</a><span>]</span></p><p><span>[a]</span><span> 12:101.</span></p>');
INSERT INTO `quran` VALUES (7959,2,12,6,'<p><span>Ia berkata, “Hai anak-ku, janganlah engkau ceriterakan mimpimu kepada saudara-saudaramu, maka nanti mereka mengadakan makar untuk memperdayakan engkau, </span><span>[a]</span><span> sesungguhnya syaitan bagi manusia musuh yang nyata.</span></p><p><span>[a]</span><span> 2:169 ; 18:51 ; 35:7.</span></p>');
INSERT INTO `quran` VALUES (7960,2,12,7,'<p><span>“Dan demikianlah Tuhan engkau akan memilih engkau dan </span><span>[b]</span><span> akan mengajar engkau dari ta</span><span>’</span><span>bir mimpi-mimpi dan akan menyempurnakan nikmat-Nya atas engkau dan atas keturunan Ya</span><span>’</span><span>kub, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1361">1361</a><span>]</span><span> seperti Dia telah menyempurnakannya atas kedua bapak engkau dahulu, Ibrahim dan Ishak. Sesungguhnya Tuhan engkau Maha Mengetahui, Maha Bijaksana.”</span></p><p><span>[b]</span><span> 12:22, 102.</span></p>');
INSERT INTO `quran` VALUES (7961,2,12,8,'<p><span>R. 2</span></p><p><span>Sesungguhnya di dalam </span><span>peristiwa</span><span> Yusuf dan saudara-saudaranya </span><span>merupakan </span><span> Tanda-tanda bagi orang-orang yang bertanya.</span></p>');
INSERT INTO `quran` VALUES (7962,2,12,9,'<p><span>Ketika mereka berkata, “Sesungguhnya Yusuf dan saudaranya lebih dicintai oleh ayah kita dari pada kita, padahal kita kelompok yang kuat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1362">1362</a><span>] [a]</span><span> Sesungguhnya ayah kita pasti dalam kekeliruan yang nyata.</span></p><p><span>[a]</span><span> 12:96.</span></p>');
INSERT INTO `quran` VALUES (7963,2,12,10,'<p><span>“</span><span>Karena itu</span><span> bunuhlah Yusuf </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1363">1363</a><span>]</span><span> atau buanglah dia ke negeri lain, supaya perhatian ayah mu kepada kamu saja ; dan sesudah itu kamu menjadi kaum yang shaleh.”</span></p>');
INSERT INTO `quran` VALUES (7964,2,12,11,'<p><span>Berkatalah seorang pembicara </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1364">1364</a><span>]</span><span> di antara mereka, “Janganlah kamu bunuh Yusuf, dan lemparkanlah dia ke dasar sumur yang dalam ; maka beberapa orang musafir akan memungutnya, seandai nya kamu mau berbuat sesuatu.”</span></p>');
INSERT INTO `quran` VALUES (7965,2,12,12,'<p><span>Mereka berkata, “Wahai ayah kami, mengapakah engkau tidak percaya kepada kami mengenai Yusuf, padahal kami sungguh- sungguh berkemauan baik terhadapnya ?</span></p>');
INSERT INTO `quran` VALUES (7966,2,12,13,'<p><span>“Kirimkan dia esok hari beserta kami, supaya dia bersenang-senang dan bermain- main, dan sesungguhnya kami pasti akan menjaganya.”</span></p>');
INSERT INTO `quran` VALUES (7967,2,12,14,'<p><span>Ia berkata, “Sesungguhnya menyedihkanku kalau kamu membawanya, dan aku khawatir serigala akan memakannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1365">1365</a><span>]</span><span> sedang kamu lengah terhadapnya.”</span></p>');
INSERT INTO `quran` VALUES (7968,2,12,15,'<p><span>Mereka berkata, “Jika dia dimakan serigala, padahal kami sekelompok yang kuat, sungguh kami akan menjadi orang-orang yang rugi.”</span></p>');
INSERT INTO `quran` VALUES (7969,2,12,16,'<p><span>Maka tatkala mereka membawa dia pergi dan telah sepakat untuk memasukkannya ke dasar sumur yang dalam, dan Kami wahyukan kepadanya, “Niscaya engkau akan memberitahukan kepada mereka tentang perbuatan mereka ini ; sedangkan mereka tidak sadar.”</span></p>');
INSERT INTO `quran` VALUES (7970,2,12,17,'<p><span>Dan mereka datang kepada ayah mereka di waktu Isya sambil menangis.</span></p>');
INSERT INTO `quran` VALUES (7971,2,12,18,'<p><span>Mereka berkata, “Aduhai ayah kami, sesungguhnya kami pergi berlomba, dan kami tinggalkan Yusuf bersama barang-barang kami, lalu ia dimakan serigala; tetapi engkau tidak akan mempercayai kami, walaupun kami benar.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1366">1366</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7972,2,12,19,'<p><span>Dan mereka datang dengan darah palsu pada kemejanya. </span><span>[a]</span><span> Ia bekata, “</span><span>Apa yang kamu katakan itu tidak benar,</span><span> bahkan nafsumu telah membuat perkara itu nampak indah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1367">1367</a><span>]</span><span> Maka bersabarlah yang terbaik </span><span>bagiku.</span><span> Dan hanya Allah yang dapat dimohon pertolongan-Nya mengenai apa-apa yang kamu ceriterakan itu.”</span></p><p><span>[a]</span><span> 12:84.</span></p>');
INSERT INTO `quran` VALUES (7973,2,12,20,'<p><span>Dan datanglah suatu kafilah, lalu mereka mengirim pengambil air mereka. Maka ia menurunkan timbanya </span><span>ke dalam sumur itu. </span><span>Ia berkata, “Wahai. ada khabar suka ! ini seorang anak laki-laki ! “ Dan mereka menyembunyikannya sebagai barang dagangan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1368">1368</a><span>]</span><span> Dan Allah Maha Mengetahui apa-apa yang mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (7974,2,12,21,'<p><span>Dan mereka menjualnya dengan harga murah, yaitu beberapa dirham, dan mereka tidak tertarik mengenai itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1368a">1368a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7975,2,12,22,'<p><span>Dan berkata orang yang membelinya dari Mesir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1369">1369</a><span>]</span><span> kepada istrinya, “Berilah dia tempat tingal yang terhormat. Boleh jadi dia akan bermanfaat bagi kita atau kita akan angkat dia sebagai anak.” </span><span>[a]</span><span> Dan demikianlah Kami memberi Yusuf kedudukan di negeri itu, dan supaya Kami mengajarkan kepadanya ta</span><span>’</span><span>bir mimpi-mimpi. Dan Allah berkuasa penuh atas keputusan-Nya, akan tetapi kebanyakan manusia tidak mengetahui.</span></p><p><span>[a]</span><span> 12:57. </span></p>');
INSERT INTO `quran` VALUES (7976,2,12,23,'<p><span>[b]</span><span> Dan setelah ia sampai kepada kedewasaannya, Kami berikan kepadanya kebijaksanaan dan ilmu. Dan demikianlah Kami memberi ganjaran kepada orang-orang yang berbuat kebaik an.</span></p><p><span>[b]</span><span> 28:15.</span></p>');
INSERT INTO `quran` VALUES (7977,2,12,24,'<p><span>Dan perempuan yang di rumahnya ia tinggal. berikhtiar menggodanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1370">1370</a><span>]</span><span> </span><span>untuk berbuat </span><span> yang berlawanan dengan kehendaknya. Dan perempuan itu menutup semua pintu </span><span>rumahnya</span><span> dan berkata “Marilah kepadaku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1371">1371</a><span>]</span><span> Berkatalah ia, “Aku mohon perlindungan kepada Allah Sesungguhnya Dia-lah Tuhan-ku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1372">1372</a><span>]</span><span> Yang telah memberiku tempat tinggal yang baik. sesungguhnya tidak akan berhasil orang-orang aniaya.”</span></p>');
INSERT INTO `quran` VALUES (7978,2,12,25,'<p><span>Dan sesungguhnya perempuan itu telah bertekad keras terhadapnya </span><span>untuk menggodanya,</span><span> dan ia pun telah bertekad keras untuk menolak </span><span>keinginan </span><span> perempuan itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1373">1373</a><span>]</span><span> Jika sekiranya ia tidak melihat Tanda yang nyata dari Tuhan-nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1374">1374</a><span>]</span><span> Demikianlah supaya Kami jauhkan dia dari keburukan dan kerendahan budi. Sesungguhnya ia termasuk hamba-hamba Kami yang terpilih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1375">1375</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7979,2,12,26,'<p><span>Keduanya berlomba lari menuju pintu, dan perempuan itu telah merobek kemeja dia dari belakang; dan mereka keduanya dapati suami perempuan itu sudah ada di muka pintu. Berkatalah perempuan, “Apakah hukumannya bagi seseorang yang hendak berbuat keburukan terhadap istri engkau kecuali dipenjarakan atau diberi azab yang pedih ?”</span></p>');
INSERT INTO `quran` VALUES (7980,2,12,27,'<p><span>Berkatalah ia,</span><span> Yusuf,</span><span> “Dialah yang telah berusaha menggodaku, berlawanan dengan kehendakku.” Dan seorang saksi dari keluarga perempuan itu memberi kesaksian, “Jika kemeja nya sobek di bagian depan, maka perempuan itu berkata benar dan ia termasuk orang-orang yang dusta ;</span></p>');
INSERT INTO `quran` VALUES (7981,2,12,28,'<p><span>“Tetapi jika kemejanya sobek di bagian belakang, maka perempuan itu berdusta dan ia termasuk orang-orang yang benar.”</span></p>');
INSERT INTO `quran` VALUES (7982,2,12,29,'<p><span>Maka tatkala ia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1376">1376</a><span>]</span><span> melihat kemejanya telah sobek di bagian belakang, berkatalah ia, “Sesungguhnya ini adalah tipu-daya kamu perempuan. Sesungguhnya tipu-daya perempuan sangat besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1377">1377</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7983,2,12,30,'<p><span>“Hai Yusuf, berpalinglah dari ini dan engkau, </span><span>perempuan,</span><span> mohonlah ampunan atas dosamu ; sesungguhnya engkau termasuk orang-orang yang bersalah.”</span></p>');
INSERT INTO `quran` VALUES (7984,2,12,31,'<p><span>Dan perempuan-perempuan di kota itu berkata, “Istri Aziz </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1378">1378</a><span>]</span><span> berusaha menggoda bujangnya bertentangan dengan kehendaknya. Sesungguhnya cinta telah menguasai hatinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1379">1379</a><span>]</span><span> Sesungguhnya kami memandangnya dalam kesesatan yang nyata.”</span></p>');
INSERT INTO `quran` VALUES (7985,2,12,32,'<p><span>Maka setelah ia mendengar pergunjungan mereka, ia memanggil mereka dan menyediakan bagi mereka tempat duduk, dan memberikan kepada setiap orang dari antara mereka itu sebilah pisau, </span><span>lalu</span><span> ia berkata </span><span>kepada Yusuf,</span><span> “Keluarlah di hadapan mereka.” Maka ketika melihatnya, mereka mendapatkan </span><span>seorang yang </span><span> sangat mulia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1379a">1379a</a><span>]</span><span> mereka </span><span>[a]</span><span> mengerat tangan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1380">1380</a><span>]</span><span> dan </span><span>[b]</span><span> berkata, “Maha Suci Allah ! Ini bukan manusia. Ini tidak lain hanyalah malaikat yang mulia.”</span></p><p><span>[a]</span><span> 12:51.</span></p><p><span>[b]</span><span> 12:52.</span></p>');
INSERT INTO `quran` VALUES (7986,2,12,33,'<p><span>Ia, </span><span>perempuan itu,</span><span> berkata, “Inilah dia yang karenanya kamu mencelaku. Dan memang aku telah berusaha menggoda dia bertentangan dengan kehendaknya ; akan tetapi dia telah memelihara dirinya. Dan sekiranya dia tidak mengerjakan apa yang keprintahkan kepadanya, tentu dia akan dipenjarakan dan akan termasuk orang-orang yang hina.”</span></p>');
INSERT INTO `quran` VALUES (7987,2,12,34,'<p><span>Berkata </span><span>Yusuf,</span><span> “Ya Tuhan-ku penjara lebih kusakai bagiku daripada apa yang mereka mengajakku kepadanya ; dan jika Engkau tidak mengelakkan dari diriku tipudaya mereka, tentu aku akan cenderung kepada mereka itu dan aku akan termasuk orang-orang yang bodoh.”</span></p>');
INSERT INTO `quran` VALUES (7988,2,12,35,'<p><span>Maka Tuhan-nya mengabulkan doanya, dan mengelakkannya dari tipu-daya mereka. Sesungguhnya Dia Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (7989,2,12,36,'<p><span>Kemudian timbul pikiran pada mereka, </span><span>pembesar-pembesar itu, </span><span>setelah mereka melihat tanda-tanda </span><span>kesucian kesucian Yusuf </span><span>itu, bahwa </span><span>untuk menjaga nama baik mereka,</span><span> harus lah mereka memenjarakannya hingga beberapa waktu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1381">1381</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7990,2,12,37,'<p><span>R. 5</span></p><p><span>Dan masuk bersamanya ke dalam penjara dua orang pemuda. Berkata seorang di antara keduanya, “Sesungguhnya kulihat </span><span>dalam mimpi,</span><span> aku sedang memeras air anggur.” Dan yang lain berkata, “Sesungguhnya kulihat diriku </span><span>dalam mimpi</span><span> membawa di atas kepadaku roti yang sebagiannya dimakan burung-burung. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1382">1382</a><span>]</span><span> Beritahukanlah kepada kami ta</span><span>’</span><span>wilnya ; sesungguhnya kami memandang engkau termasuk orang-orang yang berbuat baik.”</span></p>');
INSERT INTO `quran` VALUES (7991,2,12,38,'<p><span>Ia berkata, “Tidak akan datang kepadamu berdua makanan yang akan diberikan kepadamu, tetapi akan keberitahukan kepada-mu ta</span><span>’</span><span>wilnya sebelum makanan itu sampai kepadamu ; yang demikian itu karena Tuhan-ku telah mengajarku. Sesungguhnya aku telah meninggalkan agama kaum yang tidak percaya kepada Allah dan mereka ingkar kepada akhirat.</span></p>');
INSERT INTO `quran` VALUES (7992,2,12,39,'<p><span>[a]</span><span> “Dan aku mengikuti agama bapak-bapak Ibrahim, Ishak, dan Ya</span><span>’</span><span>kub. Kami tidak berhak mempersekutukan apa pun dengan Allah Itulah dari karunia Allah kepada kami dan kepada seluruh manusia ; akan tetapi kebanyakan manusia tidak bersyukur.</span></p><p><span>[a]</span><span> 2:134.</span></p>');
INSERT INTO `quran` VALUES (7993,2,12,40,'<p><span>“Hai kawanku sepenjara, apakah tuhan-tuhan yang saling berselisih itu lebih baik ataukah Allah Yang Maha Esa, Maha Gagah ?</span></p>');
INSERT INTO `quran` VALUES (7994,2,12,41,'<p><span>[a]</span><span> “Tiada sesuatu yang kamu sembah selain dari-Nya, melainkan nama-nama yang dibuat-buat oleh kamu dan bapak-bapakmu, yang Allah tidak menurunkan satu dalil pun mengenai itu. </span><span>[b]</span><span> Keputusan itu bagi Allah saja. </span><span>[c]</span><span> Dia telah memerintahkan bahwa janganlah kamu sembah selain Dia. </span><span>[d]</span><span> Itulah agama yang benar, tetapi kebanyakan manusia tidak mengetahui ;</span></p><p><span>[a]</span><span> 7:72 ; 53:54.</span></p><p><span>[b]</span><span> 6:58 ; 12:68.</span></p><p><span>[c]</span><span> 2:84 ; 17:24 ; 41:15.</span></p><p><span>[d]</span><span> 30:31 ; 98:6.</span></p>');
INSERT INTO `quran` VALUES (7995,2,12,42,'<p><span>“Hai kedua kawanku sepenjara ! Seorang di antaramu akan menuangkan minuman anggur kepada tuannya ; dan ada pun yang lain, maka ia akan disalibkan, lalu burung-burung akan memakan dari kepalanya. Perkara yang kamu berdua tanyakan itu telah diputuskan.”</span></p>');
INSERT INTO `quran` VALUES (7996,2,12,43,'<p><span>Dan dia berkata kepada orang yang diduganya akan dibebaskan dari antara kedua orang itu, “Ceritakanlah tentang diriku kepada majikan engkau.” Tetapi syaitan menyebabkan ia lupa menyebutnya kepada majikannya, maka tinggallah dia dalam penjara beberapa tahun lamanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1383">1383</a><span>]</span></p>');
INSERT INTO `quran` VALUES (7997,2,12,44,'<p><span>R. 6</span></p><p><span>Dan raja berkata, “Sesungguhnya aku lihat </span><span>dalam mimpi</span><span> tujuh ekor sapi betina yang gemuk dimakan oleh tujuh ekor sapi kurus, dan tujuh bulir yang hijau dan yang lainnya kering. Hai pembesar- pembesar, terangkanlah kepadaku tentang mimpiku itu jika kamu dapat mena</span><span>’</span><span>wilkan mimpi.”</span></p>');
INSERT INTO `quran` VALUES (7998,2,12,45,'<p><span>Mereka berkata, “Mimpi-mimpi </span><span>ini</span><span> kacau-balau dan kami tidak mengetahui ta</span><span>’</span><span>wil mimpi-mimpi itu.”</span></p>');
INSERT INTO `quran` VALUES (7999,2,12,46,'<p><span>Dan berkatalah seorang yang telah selamat dari kedua orang itu, kemudian sesudah beberapa lama ingat, “Aku akan beritahukan kepada kamu ta</span><span>’</span><span>wilnya, maka utuslah aku.”</span></p>');
INSERT INTO `quran` VALUES (8000,2,12,47,'<p><span>Dan dia berkata,</span><span> “Yusuf, wahai engkau orang yang benar ! Terangkanlah kepada kami </span><span>arti mimpi</span><span> tujuh ekor sapi betina gemuk dimakan oleh tujuh ekor </span><span>sapi</span><span> kurus dan tujuh bulir hijau dan lainnya kering, supaya aku dapat kembali kepada orang-orang itu, agar mereka dapat mengetahui.”</span></p>');
INSERT INTO `quran` VALUES (8001,2,12,48,'<p><span>Ia, </span><span>Yusuf,</span><span> berkata, “Kamu hendaknya bercocok tanam tujuh tahun lamanya terus-menerus, dan apa yang kamu ketam biarlah itu dalam bulirnya, kecuali sedikit darinya yang kamu makan ; </span></p>');
INSERT INTO `quran` VALUES (8002,2,12,49,'<p><span>“Kemudian akan datang sesudah itu tujuh tahun paceklik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1384">1384</a><span>]</span><span> yang akan menelan apa yang telah kamu sediakan </span><span>untuk</span><span> itu sebelumnya, kecuali sedikit dari apa yang kamu simpan.</span></p>');
INSERT INTO `quran` VALUES (8003,2,12,50,'<p><span>“Kemudian akan datang sesudah itu tahun di mana akan dikabulkan permohonan-permohonan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1385">1385</a><span>]</span><span> manusia dan dalam keadaan itu mereka akan dikabulkan dan dalam keadaan itu mereka akan </span><span>saling</span><span> memberi hadiah.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1386">1386</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8004,2,12,51,'<p><span>R. 7</span></p><p><span>Dan raja itu berkata, “Bawalah dia kepadaku.” Ketika utusan itu datang kepadanya, ia berkata, “Kembalilah kepada majikan engkau dan </span><span>[a]</span><span> tanyakanlah kepadanya, bagaiamana keadaan para wanita yang telah mengerat tangan mereka sendiri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1387">1387</a><span>]</span><span> Sesungguhnya Tuhan-ku amat mengetahui rencana </span><span>tipu daya </span><span> mereka.”</span></p><p><span>[a]</span><span> 12:32.</span></p>');
INSERT INTO `quran` VALUES (8005,2,12,52,'<p><span>Ia, </span><span>raja itu,</span><span> berkata </span><span>kepada para wanita,</span><span> “Apakah perkaramu </span><span>sebenarnya,</span><span> ketika kamu menggoda Yusuf, bertentangan dengan kehendaknya ? “ </span><span>[a]</span><span> Mereka berkata, Maha Suci Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1388">1388</a><span>]</span><span> kami tidak mengetahui adanya sesuatu keburukan padanya.” Istri Aziz itu berkata, “Sekarang telah menjadi nyata kebenaran itu. Akulah yang telah menggodanya, bertentangan dengan kehendaknya, dan Sesungguhnya ia termasuk orang-orang yang benar”</span></p><p><span>[a]</span><span> 12:32</span></p>');
INSERT INTO `quran` VALUES (8006,2,12,53,'<p><span>Yusuf berkata,</span><span> “Yang demikian itu supaya ia, </span><span>Aziz,</span><span> mengetahui bahwa sesungguhnya aku tidak mengkhianatinya dibelakangnya, dan sesungguhnya Allah tidak memberi keberhasilan tipu daya orang-orang yang khianat.”</span></p>');
INSERT INTO `quran` VALUES (8007,2,12,54,'<p><span>JUZ XIII</span></p><p><span>“Dan aku tidak menganggap diriku bebas </span><span>dari kelemahan,</span><span> sesungguhnya nafsu itu senantiasa menyuruh kepada keburukan, kecuali yang dikasihani oleh Tuhan-ku. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1389">1389</a><span>]</span><span> Sesungguhnya Tuhan- ku Maha Pengampun, Maha Penyayang.”</span></p>');
INSERT INTO `quran` VALUES (8008,2,12,55,'<p><span>Dan raja berkata, “Bawalah dia kepadaku, supaya aku pilih dia untuk </span><span>tugas</span><span> pribadiku.” Maka ketika ia berbicara dengan dia, berkatalah </span><span>raja,</span><span> “Sesungguhnya engkau hari ini </span><span>seseorang </span><span>yang berkedudukan tinggi di sisi kami </span><span>lagi</span><span> terpercaya.”</span></p>');
INSERT INTO `quran` VALUES (8009,2,12,56,'<p><span>Berkata ia, </span><span>Yusuf</span><span>, “Jadikanlah aku bendahara negeri ini, karena aku seorang penjaga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1390">1390</a><span>]</span><span> yang baik </span><span>serta</span><span> sangat memahami.”</span></p>');
INSERT INTO `quran` VALUES (8010,2,12,57,'<p><span>[a]</span><span> Dan demikianlah telah Kami berikan kedudukan kepada Yusuf di negeri itu. Ia tinggal dimana saja yang ia kehendaki. </span><span>[b]</span><span> Kami limpahkan rahmat Kami kepada siapa yang Kami kehendaki, dan tidaklah Kami menyia-nyiakan ganjaran orang-orang yang berbuat baik.</span></p><p><span>[a]</span><span> 12:22.</span></p><p><span>[b]</span><span> 2:106 ; 3.</span></p>');
INSERT INTO `quran` VALUES (8011,2,12,58,'<p><span>Dan sesungguhnya ganjaran akhirat itu lebih baik bagi orang-orang yang beriman dan bertakwa.</span></p>');
INSERT INTO `quran` VALUES (8012,2,12,59,'<p><span>R. 8</span></p><p><span>Dan datanglah saudara-saudara Yusuf, lalu menghadap kepadanya ; ia mengenal mereka, </span><span>[c]</span><span> tetapi mereka tidak mengenalnya.</span></p><p><span>[c]</span><span> 12:16.</span></p>');
INSERT INTO `quran` VALUES (8013,2,12,60,'<p><span>Dan ketika ia telah menyiapkan mereka dengan bahan makanan mereka ; berkatalah ia, “Bawalah kepadaku saudara kamu yang seayah dengan kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1390a">1390a</a><span>]</span><span> tidakkah kamu lihat, bahwa sesungguhnya aku memenuhi sukatan dan aku sebaik-baik penerima tamu ?</span></p>');
INSERT INTO `quran` VALUES (8014,2,12,61,'<p><span>“Tetapi jika kamu tidak membawanya kepadaku, maka tidak ada lagi sukatan bagimu dariku, dan janganlah kamu mendekatiku.”</span></p>');
INSERT INTO `quran` VALUES (8015,2,12,62,'<p><span>Mereka berkata, “Kami akan membujuk ayahnya mengenai dia dan sesungguhnya kami pasti lakukan.”</span></p>');
INSERT INTO `quran` VALUES (8016,2,12,63,'<p><span>Dan ia berkata kepada pelayan-pelayannya, “Masukkanlah uang pembayaran mereka ke dalam kantung pelananya, supaya mereka mengenalnya apabila mereka telah kembali kepada keluarganya, mudah-mudahan mereka akan kembali.”</span></p>');
INSERT INTO `quran` VALUES (8017,2,12,64,'<p><span>Dan ketika mereka pulang kepada ayah mereka, berkatalah mereka : “Wahai ayah kami, dilarang kepada kami sukatan, maka kirimkanlah beserta kami saudara kami, supaya kami mendapat sukatan, dan kami pasti akan menjaganya.”</span></p>');
INSERT INTO `quran` VALUES (8018,2,12,65,'<p><span>Ia berkata, “Bagaimana aku akan mempercayakan kepadamu tentang dia, kecuali seperti aku telah mempercayakan kepadamu tentang saudaranya dahulu ? Namun Allah sebaik-baik penjaga, dan Dia-lah Yang Paling Penyayang dari semua penyayang.”</span></p>');
INSERT INTO `quran` VALUES (8019,2,12,66,'<p><span>Dan ketika mereka membuka barang-barangnya, mereka mendapatkan barang-barang mereka telah dikembalikan kepada mereka. Mereka berkata, “Wahai ayah kami, apa </span><span>lagi</span><span> yang kita inginkan ? </span><span>Lihatlah</span><span> ini, barang-barang kita telah dikembalikan kepada kita. Dan akan kami bawa bahan makanan untuk keluarga kita, dan akan kami jaga saudara kami dan kita akan mendapat tambahan sukatan sepemuat seekor unta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1391">1391</a><span>]</span><span> Itulah sukatan yang sedikit.”</span></p>');
INSERT INTO `quran` VALUES (8020,2,12,67,'<p><span>Ia berkata, “Sekali-kali aku tidak akan mengirimkannya bersama kamu, sebelum kamu berjanji teguh kepadaku atas nama Allah bahwa kamu pasti akan membawanya kembali kepadaku, kecuali jika kamu dikepung. Maka setelah mereka memberikan kepadanya janji mereka yang teguh ia berkata, “Allah menjadi pengawas atas apa yang kita katakan.”</span></p>');
INSERT INTO `quran` VALUES (8021,2,12,68,'<p><span>Dan ia berkata, “Hai anak-anakku, janganlah kamu masuk dari satu pintu, tetapi masuklah dari pintu-pintu yang berlainan. Dan aku tidak berguna sedikit pun bagimu melawan </span><span>keputusan</span><span> Allah </span><span>[a]</span><span> Kepada-Nya aku bertawakkal dan kepada-Nya hendaknya bertawakkal orang-orang yang bertawakkal.”</span></p><p><span>[a]</span><span> 11:57, 89 ; 14:12.</span></p>');
INSERT INTO `quran` VALUES (8022,2,12,69,'<p><span>Dan ketika mereka masuk dari mana ayah mereka memerintahkan mereka. Itu tidak berguna bagi mereka sedikit pun melawan </span><span>keputusan</span><span> Allah, kecuali ada keinginan dalam diri Ya</span><span>’</span><span>kub yang ia telah menyempurnakannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1392">1392</a><span>]</span><span> Dan sesungguhnya ia memiliki ilmu yang telah Kami ajarkan kepadanya, tetapi kebanyakan manusia tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (8023,2,12,70,'<p><span>R. 9</span></p><p><span>Dan ketika mereka menghadapkan Yusuf, ia memberikan tempat tinggal kepada saudaranya itu bersama dia, ia berkata, “Sesungguhnya aku saudara engkau ; maka janganlah engkau bersedih atas apa yang mereka telah perbuat.”</span></p>');
INSERT INTO `quran` VALUES (8024,2,12,71,'<p><span>Maka tatkala ia telah menyiapkan mereka dengan bahan makanan mereka, ia meletakkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1393">1393</a><span>]</span><span> piala takaran ke dalam kantong pelana saudaranya. Kemudian berserulah seorang penyeru, “Hai orang- orang kafilah, sesungguhnya kamu pencuri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1394">1394</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8025,2,12,72,'<p><span>Mereka berkata sambil menghadap kepada mereka “Kehilangan barang apakah kamu ?”</span></p>');
INSERT INTO `quran` VALUES (8026,2,12,73,'<p><span>Mereka berkata, “Kami kehilangan piala takaran raja, dan barangsiapa menemukannya kembali, akan mendapat </span><span>barang </span><span> sepemuatan unta, dan aku menjaminnya.”</span></p>');
INSERT INTO `quran` VALUES (8027,2,12,74,'<p><span>Mereka berkata, “Demi Allah, sesungguhnya kamu tahu, kami tidak datang untuk membuat kerusuhan di negeri ini, dan kami bukanlah pencuri.”</span></p>');
INSERT INTO `quran` VALUES (8028,2,12,75,'<p><span>Mereka berkata, “Maka apakah hukumannya, jika kamu ternyata pendusta ?”</span></p>');
INSERT INTO `quran` VALUES (8029,2,12,76,'<p><span>Meraka berkata, “Hukumannya orang yang di dalam kantung pelananya terdapat </span><span>piala takaran</span><span> itu, maka dialah balasannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1395">1395</a><span>]</span><span> Demikianlah kami menghukum orang-orang aniaya.”</span></p>');
INSERT INTO `quran` VALUES (8030,2,12,77,'<p><span>Maka ia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1396">1396</a><span>]</span><span> mulailah </span><span>menggeladah</span><span> karung-karung mereka, sebelum karung saudaranya </span><span>Yusuf,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1396a">1396a</a><span>]</span><span> kemudian ia mengeluarkannya dari karung saudaranya. Demikianlah telah Kami rencanakan untuk Yusuf. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1397">1397</a><span>]</span><span> Ia tidak dapat menahan saudaranya menurut undang-undang kerajaan, kecuali jika Allah menghendaki. </span><span>[a]</span><span> Kami tinggikan derajat siapa yang Kami kehendaki. Dan di atas setiap orang yang berilmu ada Dzat Yang Maha Mengetahui.</span></p><p><span>[a]</span><span> 6:84.</span></p>');
INSERT INTO `quran` VALUES (8031,2,12,78,'<p><span>Mereka berkata, “Jika ia mencuri, maka saudaranya pun sebelumnya telah mencuri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1398">1398</a><span>]</span><span> Tetapi Yusuf telah merahasiakan dalam hatinya dan tidak membukakannya kepada mereka. Ia berkata </span><span>dalam hatinya,</span><span> “Kamu berada dalam keadaan buruk ; dan Allah lebih mengetahui apa yang kamu katakan.”</span></p>');
INSERT INTO `quran` VALUES (8032,2,12,79,'<p><span>Mereka berkata, “Wahai yang mulia, sesungguhnya ia mempunyai ayah yang sudah tua renta , </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1399">1399</a><span>]</span><span> maka ambillah salah seorang dari antara kami sebagai gantinya ; sesungguhnya kami lihat engkau termasuk orang-orang yang berbuat baik.</span></p>');
INSERT INTO `quran` VALUES (8033,2,12,80,'<p><span>Ia berkata, “Kami berlindung kepada Allah dari menahan orang </span><span>lain</span><span> kecuali siapa yang kami dapatkan barang kamu padanya ; sesungguhnya jika kami berbuat demikian sungguh kami orang aniaya.”</span></p>');
INSERT INTO `quran` VALUES (8034,2,12,81,'<p><span>Maka ketika mereka telah putus harapan tentang dia, mereka memisahkan diri berunding secara rahasia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1399a">1399a</a><span>]</span><span> Berkatalah yang tertua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1400">1400</a><span>]</span><span> dari mereka, “Apakah kamu tidak mengetahui, bahwa ayah kamu telah mengambil dari kamu perjanjian yang teguh atas nama Allah, dan bagaimana sebelum ini kamu telah mengabaikan tugasmu berkenaan dengan Yusuf ? Maka, tidaklah sekali-kali akan aku tinggalkan negeri ini sampai ayahku memberikan izin kepadaku atau Allah menetapkan keputusan bagiku. Dan Dia-lah Hakim yang terbaik dari semua hakim ;</span></p>');
INSERT INTO `quran` VALUES (8035,2,12,82,'<p><span>“Kembalilah kamu kepada ayahmu, dan katakanlah, `Ya ayah kami, sesungguhnya anak engkau telah mencuri, dan tidaklah kami memberikan kesaksian kecuali apa yang kami ketahui, dan kami bukanlah penjaga atas hal-hal yang gaib ;</span></p>');
INSERT INTO `quran` VALUES (8036,2,12,83,'<p><span>`Dan tanyakanlah kepada </span><span>penduduk </span><span> kota </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1401">1401</a><span>]</span><span> yang kami berada di sana dan kepada kafilah yang kami datang bersamanya. Dan sesungguhnya kami orang-orang benar.”</span></p>');
INSERT INTO `quran` VALUES (8037,2,12,84,'<p><span>[a]</span><span> Ia berkata, “Bahkan nafsumu telah menampakkan indah bagimu perbuatan itu, Maka </span><span>tidak ada lagi bagiku kecuali</span><span> kesabaran yang baik. Mudah-mudahan Allah akan membawa mereka itu semua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1402">1402</a><span>]</span><span> kepadaku. Sesungguhnya Dia Maha Mengetahui, Maha Bijaksana.”</span></p><p><span>[a]</span><span> 12:19.</span></p>');
INSERT INTO `quran` VALUES (8038,2,12,85,'<p><span>Dan ia berpaling dari mereka dan berkata, “Alangkah sedihnya aku terhadap Yusuf ! “ Dan berlinanglah kedua matanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1403">1403</a><span>]</span><span> karena sedih, dan ia menahan kesedihan.</span></p>');
INSERT INTO `quran` VALUES (8039,2,12,86,'<p><span>Mereka berkata, “Demi Allah, engkau tidak akan berhenti menyebut-nyebut Yusuf sebelum engkau jatuh sakit atau sebelum engkau menjadi diantara orang-orang yang binasa.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1404">1404</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8040,2,12,87,'<p><span>Ia berkata, “Sesungguhnya aku mengadukan kesusahanku dan kesedihanku kepada Allah, dan aku mengetahui dari Allah apa yang kamu tidak ketahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1404a">1404a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8041,2,12,88,'<p><span>“Hai anak-anakku, pergi dan selidikilah tentang Yusuf dan saudaranya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1405">1405</a><span>]</span><span> dan </span><span>[a]</span><span> janganlah kamu putus asa akan rahmat Allah Sebenarnya tiada yang putus asa akan rahmat Allah kecuali orang-orang kafir.”</span></p>');
INSERT INTO `quran` VALUES (8042,2,12,89,'<p><span>Maka, </span><span> ketika mereka datang kepadanya, mereka berkata, “Ya yang mulia, telah menimpa kami dan keluarga kami kesengsaraan, dan telah kami bawa barang-barang sedikit, maka penuhilah bagi kami sukatan dan bersedekahlah kepada kami. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1405a">1405a</a><span>]</span><span> Sesungguhnya Allah memberi balasan kepada orang-orang yang bersedekah.”</span></p>');
INSERT INTO `quran` VALUES (8043,2,12,90,'<p><span>Ia berkata, “Apakah kamu tahu apa yang telah kamu lakukan terhadap Yusuf dan saudaranya, ketika kamu tidak mengetahui </span><span>akibat perbuatanmu ?</span><span> “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1406">1406</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8044,2,12,91,'<p><span>Mereka berkata, “Apakah sesungguhnya engkau Yusuf ? “ Ia berkata, “Ya, akulah Yusuf dan ini saudaraku. Sesungguhnya Allah telah melimpahkan karunia atas kami. Sesungguhnya barangsiapa bertakwa dan bersabar, maka </span><span>[a]</span><span> Allah pasti tidak akan menyia-nyiakan ganjaran bagi orang-orang yang berbuat baik.”</span></p><p><span>[a]</span><span> 12:57.</span></p>');
INSERT INTO `quran` VALUES (8045,2,12,92,'<p><span>Mereka berkata, “Demi Allah Sesungguhnya Allah telah melebihkan engkau di atas kami dan sesungguhnya kami adalah orang-orang yang bersalah.”</span></p>');
INSERT INTO `quran` VALUES (8046,2,12,93,'<p><span>Ia berkata, “Tiada celaan bagi kamu pada hari ini. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1407">1407</a><span>]</span><span> </span><span>Semoga</span><span> Allah mengampuni kamu ! Dan Dia-lah Yang Paling Penyayang diantara para penyayang.</span></p>');
INSERT INTO `quran` VALUES (8047,2,12,94,'<p><span>“Pergilah dengan kemejaku ini dan letakkanlah di hadapan ayahku ; ia akan mengetahui segala sesuatu. Dan bawalah kepadaku keluargamu semua.”</span></p>');
INSERT INTO `quran` VALUES (8048,2,12,95,'<p><span>Dan tatkala kafilah itu telah bertolak, berkatalah ayah mereka, “Sesungguhnya aku men cium bau Yusuf, meskipun kamu menganggap diriku pikun.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1408">1408</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8049,2,12,96,'<p><span>[a]</span><span> Mereka berkata, “Demi Allah, sesungguhnya engkau masih di dalam kekeliruan engkau yang lama.”</span></p><p><span>[a]</span><span> 12:9.</span></p>');
INSERT INTO `quran` VALUES (8050,2,12,97,'<p><span>Maka ketika pembawa khabar suka itu telah datang, ia meletakkan </span><span>kemeja</span><span> itu di hadapannya, maka ia menjadi mengerti. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1409">1409</a><span>]</span><span> Ia berkata, “Tidaklah telah kekatakan kepada kamu, sesungguhnya telah kuketahui dari Allah apa yang kamu tidak ketahui ?”</span></p>');
INSERT INTO `quran` VALUES (8051,2,12,98,'<p><span>Mereka berkata, “Ya, ayah kami, mohonkan ampunan bagi kami atas dosa-dosa kami ; sesungguhnya kami adalah orang-orang yang salah.”</span></p>');
INSERT INTO `quran` VALUES (8052,2,12,99,'<p><span>Ia berkata, “Tentu akan kumohonkan pengampunan bagimu kepada Tuhan-ku ; Sesungguhnya Dia adalah Maha Pengampun, Maha Penyayang.”</span></p>');
INSERT INTO `quran` VALUES (8053,2,12,100,'<p><span>Dan ketika mereka datang ke hadapan Yusuf, ia menempatkan ibu-bapaknya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1410">1410</a><span>]</span><span> di sampingnya dan ia berkata, “Masuklah ke Mesir dengan aman jika Allah menghendaki.”</span></p>');
INSERT INTO `quran` VALUES (8054,2,12,101,'<p><span>Dan ia menaikkan ibu bapaknya di atas singgasana, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1411">1411</a><span>]</span><span> dan mereka merebahkan diri bersujud karena dia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1412">1412</a><span>]</span><span> </span><span>ke hadirat Allah</span><span> Dan ia berkata, “Wahai ayahku, inilah ta</span><span>’</span><span>wil mimpiku dahulu. Sungguh Tuhan-ku telah menjadikannya benar. Dan sesungguhnya Dia telah bermurah hati kepadaku ketika Dia mengeluarkan aku dari penjara </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1413">1413</a><span>]</span><span> dan membawa kamu sekalian dari padang pasir setelah syaitan menimbulkan perpecahan antara aku dan saudara-saudaraku. Sesungguhnya Tuhan-ku Maha Lembut kepada siapa yang Dia kehendaki. Sesungguhnya Dia-lah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (8055,2,12,102,'<p><span>“Ya Tuhan-ku, Engkau telah menganugerahkan kepadaku, sebagian kerajaan dan </span><span>[a]</span><span> mengajarkan kepadaku ta</span><span>’</span><span>wil mimpi-mimpi. </span><span>[b]</span><span> Pencipta seluruh langit dan bumi, Engkau-lah Penolong-ku di dunia dan akhirat. Wafatkanlah aku dalam keadaan taat dan gabungkanlah aku dengan orang-orang yang shaleh.”</span></p><p><span>[a]</span><span> 12:7, 22.</span></p><p><span>[b]</span><span> 6:15 ; 14:11 ; 35:2 ; 39:47.</span></p>');
INSERT INTO `quran` VALUES (8056,2,12,103,'<p><span>[c]</span><span> Itulah dari khabar-khabar gaib </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1414">1414</a><span>]</span><span> yang Kami mewahyukannya kepada engkau. Dan engkau tidak beserta mereka, ketika mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1415">1415</a><span>]</span><span> bersepakat tentang rencana mereka, ketika mereka membuat makar.</span></p><p><span>[c]</span><span> 3:45 ; 11:50.</span></p>');
INSERT INTO `quran` VALUES (8057,2,12,104,'<p><span>[d]</span><span> Dan kebanyakan manusia tidak mau beriman, walaupun engkau menginginkan.</span></p><p><span>[d]</span><span> 18:7.</span></p>');
INSERT INTO `quran` VALUES (8058,2,12,105,'<p><span>Dan engkau tidak meminta kepada mereka ganjaran untuk itu. </span><span>[a]</span><span> Itu tiada lain hanya nasihat bagi semesta alam.</span></p><p><span>[a]</span><span> 38:88 ; 81:28.</span></p>');
INSERT INTO `quran` VALUES (8059,2,12,106,'<p><span>[b]</span><span> Dan alangkah banyaknya Tanda-tanda di seluruh langit dan bumi yang mereka melaluinya . sedang mereka itu berpaling darinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1416">1416</a><span>]</span></p><p><span>[b]</span><span> 21:33 ; 23:67.</span></p>');
INSERT INTO `quran` VALUES (8060,2,12,107,'<p><span>Dan kebanyakan dari mereka tidak percaya kepada Allah, melainkan mereka dalam keadaan syirik.</span></p>');
INSERT INTO `quran` VALUES (8061,2,12,108,'<p><span>Apakah mereka aman jika datang kepada mereka azab Allah yang dahsyat </span><span>[c]</span><span> atau saat itu datang kepada mereka tiba-tiba, sedang mereka tidak menyadari ?</span></p><p><span>[c]</span><span> 10:51 ; 22:56 ; 43:67.</span></p>');
INSERT INTO `quran` VALUES (8062,2,12,109,'<p><span>Katakanlah, </span><span>[d]</span><span> “Inilah jalanku ; aku memanggil kepada Allah dengan hujjah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1417">1417</a><span>]</span><span> yang nyata, aku dan orang yang mengikutiku. Dan Maha Suci Allah, aku tidaklah termasuk di antara orang-orang musyrik.</span></p><p><span>[d]</span><span> 6:58.</span></p>');
INSERT INTO `quran` VALUES (8063,2,12,110,'<p><span>“Dan </span><span>[a]</span><span> tidaklah Kami utus sebelum engkau </span><span>rasul-rasul,</span><span> melainkan orang-orang laki-laki, yang kepadanya Kami turunkan wahyu, dari antara penduduk kota-kota. Maka apakah tidak mereka bepergian di bumi lalu mereka melihat bagaimana akibatnya orang-orang yang sebelum mereka ? Dan sesungguhnya rumah di akhirat lebih baik bagi orang-orang yang bertakwa. Maka apakah kamu tidak menggunakan akal ?”</span></p><p><span>[a]</span><span> 16:44 ; 21:8.</span></p>');
INSERT INTO `quran` VALUES (8064,2,12,111,'<p><span>Dan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1417a">1417a</a><span>] [b]</span><span> ketika berputus asa rasul-rasul, dan orang-orang </span><span>kafir</span><span> menyangka bahwa mereka telah dibohongi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1418">1418</a><span>]</span><span> datanglah kepada mereka pertolongan Kami </span><span>para rasul,</span><span> kemudian Kami menyelamatkan siapa yang Kami kehendaki. Dan sekali-kali tidak dapat dihindarkan siksaan Kami dari kaum yang berdosa.</span></p><p><span>[b]</span><span> 2:215.</span></p>');
INSERT INTO `quran` VALUES (8065,2,12,112,'<p><span>Sesungguhnya dalam riwayat mereka itu ada pelajaran bagi orang-orang yang berakal. </span><span>[a]</span><span> Ini bukanlah suatu hal yang telah dibuat-buat, melainkan suatu penyempurnaan apa yang telah ada sebelumnya dan penjelasan terrinci untuk segala sesuatu, dan </span><span>[b]</span><span> suatu petunjuk dan rahmat bagi kaum yang beriman.</span></p><p><span>[a]</span><span> 10:38.</span></p><p><span>[b]</span><span> 16:90.</span></p>');
INSERT INTO `quran` VALUES (8066,2,13,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8067,2,13,2,'<p><span>[b]</span><span> Aku Allah Yang Maha Mengetahui Maha Melihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1419">1419</a><span>] [c]</span><span> Inilah ayat-ayat Kitab </span><span>yang sempurna.</span><span> Dan yang telah diturunkan kepada engkau dari Tuhan engkau adalah hak, akan tetapi kebanyakan manusia tidak beriman.</span></p><p><span>[b]</span><span> 2:2.</span></p><p><span>[c]</span><span> 13:20 ; 32:3, 4.</span></p>');
INSERT INTO `quran` VALUES (8068,2,13,3,'<p><span>[d]</span><span> Allah , Dia-lah Yang telah meninggikan seluruh langit tanpa tiang yang kamu melihatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1420">1420</a><span>]</span><span> Kemudian Dia bersemayam di atas </span><span>‘</span><span>Arasy. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1420a">1420a</a><span>] [e]</span><span> Dan Dia tetapkan matahari dan bulan berkhidmat </span><span>bagi kamu,</span><span> masing-masing beredar menurut arah perjalannya, hingga suatu masa yang telah ditetapkan. </span><span>[f]</span><span> Dia mengatur segala urusan </span><span>dan</span><span> Dia menjelaskan Tanda-tanda itu, supaya kamu dengan pertemuan Tuhan-mu yakin.</span></p><p><span>[d]</span><span> 31:11.</span></p><p><span>[e]</span><span> 7:55 ; 16:13 ; 29:62 ; 31:30 ; 35:14 ; 39:6.</span></p><p><span>[f]</span><span> 32:6.</span></p>');
INSERT INTO `quran` VALUES (8069,2,13,4,'<p><span>Dan </span><span>[a]</span><span> Dia-lah Yang telah membentangkan bumi dan menjadikan bumi dan menjadikan padanya gunung-gunung dan sungai-sungai. Dan dari setiap macam </span><span>[b]</span><span> buah-buahan Dia jadikan di dalamnya dua jenis berpasang-pasang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1421">1421</a><span>] [c]</span><span> Dia membuat malam menutupi siang. Sesungguhnya dalam hal ini ada Tanda-tanda bagi kaum yang berpikir.</span></p><p><span>[a]</span><span> 15:20 ; 16:16 ; 21:32.</span></p><p><span>[b]</span><span> 36:37 ; 51:50.</span></p><p><span>[c]</span><span> 7:55 ; 39:6.</span></p>');
INSERT INTO `quran` VALUES (8070,2,13,5,'<p><span>[d]</span><span> Dan di bumi ini ada potongan-potongan tanah yang berdampingan, dan kebun-kebun dari anggur dan sawah-ladang, dan pohon-pohon kurma berumpun yang tumbuh dari satu akar dan yang tidak berumpun ; </span><span>semuanya</span><span> itu disirami dengan air yang sama, </span><span>[e]</span><span> dan Kami melebihkan sebagian dari sebagian yang lain dalam buah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1422">1422</a><span>]</span><span> Sesungguhnya dalam yang demikian itu ada tanda- tanda bagi kaum yang menggunakan akal.</span></p><p><span>[d]</span><span> 6:100 ; 16:12.</span></p><p><span>[e]</span><span> 16:14 ; 39:22.</span></p>');
INSERT INTO `quran` VALUES (8071,2,13,6,'<p><span>[a]</span><span> Dan jika engkau takjub, maka yang mengherankan adalah ucapan mereka, “Apakah jika kami sudah menjadi tanah, apakah kami akan dijadikan dalam ciptaan yang baru ?” Itulah orang-orang yang ingkar kepada Tuhan mereka ; dan </span><span>[b]</span><span> mereka itu yang ada belenggu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1423">1423</a><span>]</span><span> di leher mereka, dan mereka itulah penghuni Api ; mereka akan tinggal lama di dalam nya.</span></p><p><span>[a]</span><span> 27:68 ; 37:17 ; 50:4.</span></p><p><span>[b]</span><span> 36:9 ; 76:5.</span></p>');
INSERT INTO `quran` VALUES (8072,2,13,7,'<p><span>[c]</span><span> Dan mereka itu meminta engkau mempercepat datangnya keburukan lebih dahulu dari pada kebaikan, padahal sesungguhnya telah berlalu sebelum mereka siksaan sebagai contoh. Dan sesungguhnya Tuhan engkau mempunyai pengampunan bagi manusia, meskipun kezaliman mereka dan </span><span>[d]</span><span> sesungguhnya Tuhan engkau sangat keras memberi hukuman.</span></p><p><span>[c]</span><span> 22:48 ; 29:54, 55.</span></p><p><span>[d]</span><span> 41:44 ; 53:33.</span></p>');
INSERT INTO `quran` VALUES (8073,2,13,8,'<p><span>[a]</span><span> Dan berkatalah orang-orang ingkar, “Mengapa tidak diturunkan kepada orang itu suatu Tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1424">1424</a><span>]</span><span> dari Tuhan-nya ? “ </span><span>[b]</span><span> Sesungguhnya engkau adalah seorang pemberi peringatan, dan bagi setiap kaum ada seorang pemberi petunjuk.</span></p><p><span>[a]</span><span> 6:38 ; 10:21.</span></p><p><span>[b]</span><span> 11:13 ; 35:24.</span></p>');
INSERT INTO `quran` VALUES (8074,2,13,9,'<p><span>R. 2</span></p><p><span>[c]</span><span> Allah mengetahui apa yang dikandung oleh setiap perempuan, dan apa yang kurang sempurna dalam rahim, dan apa yang dikembangkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1425">1425</a><span>] [d]</span><span> Dan segala sesuatu di sisi-Nya mempunyai ukuran.</span></p><p><span>[c]</span><span> 35:12 ; 41:48.</span></p><p><span>[d]</span><span> 15:22.</span></p>');
INSERT INTO `quran` VALUES (8075,2,13,10,'<p><span>Dia-lah</span><span> </span><span>[e]</span><span> Yang mengetahui yang tersembunyi dan yang nampak ; Maha Besar, Maha Luhur.</span></p><p><span>[e]</span><span> 6:74 ; 9:94 ; 59:23 ; 64:19.</span></p>');
INSERT INTO `quran` VALUES (8076,2,13,11,'<p><span>Sama saja </span><span>dalam ilmu Allah, </span><span>siapa di antara kamu yang merahasiakan perkataan dan siapa yang menzahirkannya ; dan siapa yang bersembunyi pada waktu malam, dan orang yang berjalan pada waktu siang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1426">1426</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8077,2,13,12,'<p><span>Untuk dia, </span><span>rasul itu,</span><span> ada pergiliran </span><span>malaikat-malaikat</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1427">1427</a><span>]</span><span> dihadapannya dan di belakangnya ; mereka menjaganya atas perintah Allah Sesungguhnya </span><span>[a]</span><span> Allah tidak mengubah keadaan sesuatu kaum sebelum mereka sendiri mengubah apa yang ada pada diri mereka. Dan apabila Allah menghendaki keburukan pada suatu kaum, maka tiada yang dapat menghindarkannya, dan tiada bagi mereka penolong selain dari Dia.</span></p><p><span>[a]</span><span> 8:54.</span></p>');
INSERT INTO `quran` VALUES (8078,2,13,13,'<p><span>[b]</span><span> Dia-lah Yang memperlihatkan kepadamu kilat untuk ketakutan dan harapan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1428">1428</a><span>]</span><span> dan Dia menimbulkan awan-awan yang tebal.</span></p><p><span>[b]</span><span> 30:25.</span></p>');
INSERT INTO `quran` VALUES (8079,2,13,14,'<p><span>Dan petir itu bertasbih dengan pujian-Nya </span><span>[a]</span><span> dan malaikat- malaikat </span><span>juga,</span><span> karena takut kepada-Nya ; dan </span><span>[b]</span><span> Dia mengirimkan halilintar menimpakannya atas barangsiapa yang Dia kehendaki, namun mereka itu bertengkar tentang Allah, dan Dia sangat keras dalam menyiksa.</span></p><p><span>[a]</span><span> 16:51 ; 42:6.</span></p><p><span>[b]</span><span> 24:44.</span></p>');
INSERT INTO `quran` VALUES (8080,2,13,15,'<p><span>Hanya</span><span> bagi Dia-lah doa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1429">1429</a><span>]</span><span> yang benar. </span><span>[c]</span><span> Dan mereka yang diseru oleh orang-orang itu selain Dia, tidak menjawab bagi mereka sedikit pun, kecuali seperti orang yang mengulurkan kedua tangannya ke air, supaya sampai ke mulutnya, tetapi itu tidak akan sampai </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1430">1430</a><span>]</span><span> kepadanya. </span><span>[d]</span><span> Dan tidaklah doa orang-orang kafir itu kecuali sia-sia belaka.</span></p><p><span>[c]</span><span> 35:14 ; 40:21.</span></p><p><span>[d]</span><span> 40:51.</span></p>');
INSERT INTO `quran` VALUES (8081,2,13,16,'<p><span>Dan siapa pun yang ada di seluruh langit dan bumi bersujud kepada Allah dengan senang atau tidak senang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1431">1431</a><span>]</span><span> dan juga bayangan-bayangan mereka pada setiap pagi dan petang.</span></p>');
INSERT INTO `quran` VALUES (8082,2,13,17,'<p><span>[a]</span><span> Katakanlah, “Siapakah Tuhan seluruh langit dan bumi ?” Katakanlah. “Allah!” Katakanlah, “Apakah kamu mengambil selain dia penolong-penolong </span><span>[b]</span><span> yang tidak mempunyai kekuasaan untuk kemanfaatan ataupun kemudaratan, bagi dirinya sendiri ? “ Katakanlah, </span><span>[c]</span><span> Apakah sama keadaan orang-orang buta dan orang-orang yang melihat ? Atau samakah gelap dan terang ? Atau, apakah mereka itu menjadikan bagi Allah sekutu-sekutu yang telah menciptakan seperti ciptaan-Nya, sehingga </span><span>kedua</span><span> ciptaan itu nampak serupa saja bagi mereka ? “ Katakanlah, “</span><span>Hanya </span><span>Allah yang telah menciptakan segala sesuatu, dan Dia-lah Yang Maha Esa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1432">1432</a><span>]</span><span> Maha Perkasa.”</span></p><p><span>[a]</span><span> 23:87.</span></p><p><span>[b]</span><span> 25:4.</span></p><p><span>[c]</span><span> 11:25 ; 35:20.</span></p>');
INSERT INTO `quran` VALUES (8083,2,13,18,'<p><span>[a]</span><span> Dia menurunkan air dari langit, maka lembah-lembah mengalir menurut ukurannya, dan air bah itu membawa buih yang menggelembung. Dan dari apa yang mereka bakar dalam api untuk berusaha </span><span>membuat</span><span> perhiasan atau perkakas-perkakas, ada buih semacam itu. Demikianlah Allah menjelaskan yang benar dan yang batil. Adapun buih itu akan hilang, sia-sia, tetapi apa yang bermanfaat bagi manusia akan tinggal tetap di bumi. Demikianlah Allah menjelaskan tamsil-tamsil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1433">1433</a><span>]</span></p><p><span>[a]</span><span> 39:22.</span></p>');
INSERT INTO `quran` VALUES (8084,2,13,19,'<p><span>Bagi orang-orang yang memenuhi seruan Tuhan mereka, ada pembalasan yang baik. Dan orang-orang yang tidak memenuhi seruan-Nya, </span><span>[b]</span><span> sekiranya mereka mempunyai yang ada di bumi semuanya dan sebanyak itu tambahannya, niscaya mereka itu akan menebus dirinya dengan itu. Mereka itulah yang baginya ada perhitungan yang buruk, dan tempat tinggal mereka adalah Jahannam. Dan alangkah buruknya tempat tinggal itu !</span></p><p><span>[b]</span><span> 5:37 ; 39:48.</span></p>');
INSERT INTO `quran` VALUES (8085,2,13,20,'<p><span>Apakah orang yang mengetahui, bahwa yang telah diturunkan kepada engkau dari Tuhan engkau itu benar, sama dengan orang buta ? Sesungguhnya yang menerima nasihat hanyalah orang-orang berakal ;</span></p>');
INSERT INTO `quran` VALUES (8086,2,13,21,'<p><span>[a]</span><span> Yaitu orang-orang yang menepati janji Allah, dan tidak melanggar janji itu ;</span></p><p><span>[a]</span><span> 6:152 ; 16:92 ; 17:35.</span></p>');
INSERT INTO `quran` VALUES (8087,2,13,22,'<p><span>Dan mereka yang menghubungkan apa yang Allah telah memerintahkan hal itu supaya dihubungkan dan takut kepada Tuhan mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1434">1434</a><span>]</span><span> dan takut kepada perhitungan yang buruk ;</span></p>');
INSERT INTO `quran` VALUES (8088,2,13,23,'<p><span>Dan orang-orang yang sabar mencari keridhaan Tuhan mereka, dan </span><span>[b]</span><span> mendirikan shalat dan membelanjakan sebagian dari apa yang telah Kami rezekikan kepada mereka secara sembunyi-sembunyi dan secara terang-terangan, </span><span>[c]</span><span> dan menolak keburukan dengan kebaikan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1435">1435</a><span>]</span><span> bagi mereka itulah ada tempat kesudahan </span><span>yang baik,</span></p><p><span>[b]</span><span> 2:4 ; 8:4 ; 14:32 ; 27:4.</span></p><p><span>[c]</span><span> 41:35.</span></p>');
INSERT INTO `quran` VALUES (8089,2,13,24,'<p><span>[a]</span><span> Kebun-kebun yang abadi. Mereka akan masuk ke dalamnya dan barangsiapa yang shaleh dari antara bapak-bapak mereka, dan istri-istri mereka dan keturunan mareka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1436">1436</a><span>]</span><span> Dan malaikat- malaikat akan masuk kepada mereka dari setiap pintu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1437">1437</a><span>]</span></p><p><span>[a]</span><span> 40:9.</span></p>');
INSERT INTO `quran` VALUES (8090,2,13,25,'<p><span>[b]</span><span> “Selamat sejahtera atas kamu, sebab kamu telah bersabar maka </span><span>lihatlah</span><span> betapa baiknya tempat kesudahan itu ! “</span></p><p><span>[b]</span><span> 39:74.</span></p>');
INSERT INTO `quran` VALUES (8091,2,13,26,'<p><span>Dan orang-orang </span><span>[c]</span><span> yang melanggar janji Allah setelah diteguhkan dan memutuskan apa yang telah diperintahkan Allah hal itu supaya dihubungkan, dan mereka membuat kerusuhan di bumi, bagi mereka itulah laknat </span>Allah <span>dan bagi mereka tempat yang buruk.</span></p><p><span>[c]</span><span> 2:28.</span></p>');
INSERT INTO `quran` VALUES (8092,2,13,27,'<p><span>[a]</span><span> Allah melapangkan rezeki bagi siapa yang Dia kehendaki dan menyempitkan. Dan </span><span>[b]</span><span> mereka itu gembira dengan kehidupan dunia. Dan tidaklah kehidupan dunia dibanding </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1438">1438</a><span>]</span><span> akhirat, kecuali kesenangan sementara.</span></p><p><span>[a]</span><span> 29:63 ; 30:38 ; 39:53.</span></p><p><span>[b]</span><span> 10:8.</span></p>');
INSERT INTO `quran` VALUES (8093,2,13,28,'<p><span>Dan berkatalah orang-orang yang ingkar, </span><span>[c]</span><span> “Mengapakah tidak diturunkan kepadanya suatu Tanda dari Tuhannya ? “ Katakanlah, “Sesungguhnya Allah membiarkan sesat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1439">1439</a><span>]</span><span> siapa yang Dia kehendaki dan </span><span>[d]</span><span> memberi petunjuk kepada siapa yang kembali kepada-Nya.</span></p><p><span>[c] </span><span>6:38 ; 10:21 ; 29:51.</span></p><p><span>[d]</span><span> 14:5 ; 74:32.</span></p>');
INSERT INTO `quran` VALUES (8094,2,13,29,'<p><span>“Yaitu orang-orang yang beriman, dan merasa tenteram hati mereka dengan mengingat Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1440">1440</a><span>]</span><span> Ketahuilah, dengan mengingat Allah, hati menjadi tenteram.</span></p>');
INSERT INTO `quran` VALUES (8095,2,13,30,'<p><span>[e]</span><span> “Orang-orang yang beriman dan beramal shaleh, kebahagiaan bagi mereka, dan sebaik-baik tempat kembali.”</span></p><p><span>[e]</span><span> 3:16 ; 18:31, 108 ; 68:35 ; 98:8, 9.</span></p>');
INSERT INTO `quran` VALUES (8096,2,13,31,'<p><span>Demikianlah telah Kami utus engkau kepada kaum suatu umat, yang sebelumnya telah berlalu umat-umat lain, supaya dapat engkau bacakan kepada mereka apa yang telah Kami wahyu kan kepada engkau, sebab </span><span>[a]</span><span> mereka itu ingkar kepada Yang Maha Pemurah. Katakanlah, “Dia-lah Tuhan-ku ; tiada tuhan selain Dia, kepada-Nya aku bertawakkal dan kepada-Nya aku bertobat.”</span></p><p><span>[a]</span><span> 25:61.</span></p>');
INSERT INTO `quran` VALUES (8097,2,13,32,'<p><span>Dan sekiranya ada sebuah Alquran yang dengan itu gunung-gunung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1441">1441</a><span>]</span><span> dapat dijalankan atau dengan itu bumi dapat dibelah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1442">1442</a><span>]</span><span> atau dengan itu dapat berbicara </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1443">1443</a><span>]</span><span> orang-orang yang telah mati, </span><span>mereka tetap tidak akan percaya.</span><span> </span><span>[b]</span><span> Tetapi bagi Allah urusan semuanya. Apakah orang-orang yang telah beriman belum mengetahui, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1443a">1443a</a><span>]</span><span> bahwa sekiranya Allah menghendaki niscaya Dia memberi petunjuk kepada manusia semua ? Dan selalu </span><span>bagi </span><span> orang-orang yang ingkar </span><span>[c]</span><span> bencana menimpa mereka disebabkan apa yang telah mereka perbuat, atau </span><span>bencana</span><span> itu akan turun dekat rumah mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1444">1444</a><span>]</span><span> hingga datanglah janji Allah Sesungguhnya Allah tidak menyalahi janji.</span></p><p><span>[b]</span><span> 3:155 ; 30:5.</span></p><p><span>[c]</span><span> 22:56.</span></p>');
INSERT INTO `quran` VALUES (8098,2,13,33,'<p><span>R. 5</span></p><p><span>Dan sesungguhnya dicemoohkan rasul-rasul sebelum engkau, tetapi Aku </span><span>[a]</span><span> menangguhkan kepada orang-orang yang ingkar, kemudian Aku binasakan mereka maka alangkah </span><span>dahsyatnya</span><span> pembalasan-Ku !</span></p><p><span>[a]</span><span> 22:45.</span></p>');
INSERT INTO `quran` VALUES (8099,2,13,34,'<p><span>Apakah Dia, Yang mengawasi atas setiap jiwa tentang apa yang telah dikerjakannya, dan </span><span>[b]</span><span> mereka telah menjadikan sekutu-sekutu bagi Allah, Katakanlah, “Sebutlah nama mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1445">1445</a><span>]</span><span> Apakah kamu akan memberitahukan kepada-Nya apa yang Dia tidak ketahui di bumi ? Atau, apakah ini </span><span>hanya</span><span> merupakan ucapan kosong ? “ </span><span>Tidak,</span><span> bahkan ditampakkan indah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1446">1446</a><span>]</span><span> bagi orang-orang ingkar tipu daya mereka, dan mereka telah dihalangi dari jalan </span><span>yang benar.</span><span> Dan </span><span>[a]</span><span> barangsiapa dibiarkan sesat oleh Allah, maka tidak ada baginya pemberi petunjuk.</span></p><p><span>[b]</span><span> 6:101 ; 10:67 ; 13:17.</span></p><p><span>[a]</span><span> 17:98 ; 39:24, 37.</span></p>');
INSERT INTO `quran` VALUES (8100,2,13,35,'<p><span>[b]</span><span> Bagi mereka ada azab dalam kehidupan dunia ; dan sesungguhnya azab di akhirat lebih keras, dan tidak ada bagi mereka seorang pelindung pun dari </span><span>azab</span><span> Allah</span></p><p><span>[b]</span><span> 39:27 ; 68:34.</span></p>');
INSERT INTO `quran` VALUES (8101,2,13,36,'<p><span>[c]</span><span> Perumpamaan kebun yang telah dijanjikan kepada orang-orang yang bertakwa. Mengalir di bawahnya sungai-sungai ; buahnya kekal </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1447">1447</a><span>]</span><span> dan juga bayangannya. Itulah tempat kesudahan bagi orang-orang yang bertakwa ; dan tempat kesudahan bagi orang- orang ingkar adalah Api.</span></p><p><span>[c]</span><span> 2:26 ; 4:58 ; 47:16.</span></p>');
INSERT INTO `quran` VALUES (8102,2,13,37,'<p><span>Dan </span><span>[a] </span><span>orang-orang yang kepada mereka telah Kami berikan Kitab mereka telah Kami berikan Kitab, mereka gembira dengan apa-apa yang diturunkan kepada engkau. Dan </span><span>[b]</span><span> dari </span><span>beberapa</span><span> golongan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1448">1448</a><span>]</span><span> ada yang ingkar kepada sebagainnya. Katakanlah, </span><span>[c]</span><span> Aku diperintahkan untuk menyembah Allah dan aku tidak mempersekutukan-Nya. Kepada-Nya aku berseru, dan kepada-Nya tempat kembaliku.”</span></p><p><span>[a]</span><span> 28:53.</span></p><p><span>[b]</span><span> 2:86.</span></p><p><span>[c]</span><span> 18:111 ; 39:12 ; 72:21.</span></p>');
INSERT INTO `quran` VALUES (8103,2,13,38,'<p><span>Dan demikianlah </span><span>[d]</span><span> telah Kami menurunkannya sebagai peraturan yang jelas. Dan </span><span>[e]</span><span> jika engkau mengikuti hawa nafsu mereka sesudah datang kepada engkau ilmu, tidak ada bagi engkau sahabat melawan Allah dan tidak </span><span>pula</span><span> penyelamat.</span></p><p><span>[d]</span><span> 12:3 ; 20:114 ; 43:4.</span></p><p><span>[e]</span><span> 2:121, 146 ; 42:16.</span></p>');
INSERT INTO `quran` VALUES (8104,2,13,39,'<p><span>R. 6</span></p><p><span>Dan sesungguhnya telah Kami utus rasul-rasul sebelum engkau, dan Kami jadikan bagi mereka itu istri-istri dan keturunan. Dan </span><span>[f]</span><span> tidaklah mungkin bagi seorang rasul untuk mendatangkan suatu Tanda, melainkan dengan izin Allah Untuk setiap jangka waktu telah ditetapkan.</span></p><p><span>[f]</span><span> 14:12 ; 40:79.</span></p>');
INSERT INTO `quran` VALUES (8105,2,13,40,'<p><span>[a]</span><span> Allah menghapus apa yang Dia kehendaki, dan menegakkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1449">1449</a><span>]</span><span> dan pada-Nya </span><span>[b]</span><span> sumber segala hukum. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1450">1450</a><span>]</span></p><p><span>[a]</span><span> 42:25.</span></p><p><span>[b]</span><span> 43:5.</span></p>');
INSERT INTO `quran` VALUES (8106,2,13,41,'<p><span>Dan </span><span>[c]</span><span> jika Kami perlihatkan kepada engkau sebagian dari hal-hal yang telah Kami ancamkan kepada mereka ataupun Kami wafatkan engkau, </span><span>akan sedikit saja bedanya ; </span><span> maka sesungguhnya kewajiban </span><span>[d]</span><span> engkau hanya menyampaikan dan atas Kami pelaksanaan perhitungan.</span></p><p><span>[c]</span><span> 10:47 ; 40:78.</span></p><p><span>[d]</span><span> 3:21 ; 5:93 ; 16:83.</span></p>');
INSERT INTO `quran` VALUES (8107,2,13,42,'<p><span>Tidaklah mereka melihat, bahwa </span><span>[e]</span><span> Kami mendatangi tanah itu, sambil mengurangi batas-batas-nya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1451">1451</a><span>]</span><span> Dan Allah yang menetapkan keputusan ; dan tiada seorangpun yang dapat menolak keputusan-Nya. Dan Dia sangat cepat dalam menghisab.</span></p><p><span>[e]</span><span> 21:45.</span></p>');
INSERT INTO `quran` VALUES (8108,2,13,43,'<p><span>Dan sesungguhnya </span><span>[a]</span><span> telah membuat rencana orang-orang sebelum mereka, tetapi bagi Allah seluruh rencana. </span><span>{</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1451a">1451a</a><span>]</span><span> Dia mengetahui apa yang telah diusahakan oleh tiap-tiap jiwa. Dan akan segera mengetahui orang-orang kafir </span><span>[b]</span><span> bagi siapa tempat kesudahan itu.</span></p><p><span>[a]</span><span> 3:55 ; 8:31 ; 14:47 ; 27:51.</span></p><p><span>[b]</span><span> 28:38.</span></p>');
INSERT INTO `quran` VALUES (8109,2,13,44,'<p><span>Dan berkata orang-orang yang telah ingkar, </span><span>[c]</span><span> “Engkau bukanlah seorang rasul ! “ Katakanlah, </span><span>[d]</span><span> “ Cukuplah Allah sebagai saksi antara aku dengan kamu, dan </span><span>juga menjadi saksi </span><span>orang yang memiliki ilmu Alkitab.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1451b">1451b</a><span>]</span></p><p><span>[c]</span><span> 25:42.</span></p><p><span>[d]</span><span> 4:167 ; 6:20 ; 29:53 ; 48:29.</span></p>');
INSERT INTO `quran` VALUES (8110,2,14,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8111,2,14,2,'<p><span>Aku </span><span>[b]</span><span> Allah Yang Maha Melihat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1452">1452</a><span>]</span><span> </span><span>Inilah </span><span> suatu Kitab yang telah Kami turunkan kepada engkau, supaya </span><span>[c]</span><span> engkau dapat mengeluarkan manusia dari kegelapan kepada cahaya dengan izin Tuhan mereka kepada jalan Tuhan Yang Maha Perkasa, Maha Terpuji,</span></p><p><span>[b]</span><span> 10:2 ; 11:2 ; 12:2 ; 13:2 ; 2:15.</span></p><p><span>[c]</span><span> 2:256 ; 5:17 ; 14:6 ; 65:12.</span></p>');
INSERT INTO `quran` VALUES (8112,2,14,3,'<p><span>Jalan</span><span> Allah, Yang mempunyai segala sesuatu di seluruh langit dan segala sesuatu di bumi. Dan </span><span>[d]</span><span> celaka bagi orang-orang kafir, dari azab yang keras,</span></p><p><span>[d]</span><span> 19:38 ; 38:28 ; 51:61.</span></p>');
INSERT INTO `quran` VALUES (8113,2,14,4,'<p><span>[e]</span><span> Orang-orang yang lebih menyukai kehidupan dunia daripada akhirat, dan </span><span>[f]</span><span> menghalangi dari jalan Allah, dan berusaha membuatnya bengkok. Mereka itu berada dalam kesesatan yang jauh.</span></p><p><span>[e]</span><span> 16:108.</span></p><p><span>[f]</span><span> 3:100 ; 7:46 ; 11:20.</span></p>');
INSERT INTO `quran` VALUES (8114,2,14,5,'<p><span>Dan tidaklah Kami utus seorang rasul, melainkan dengan bahasa kaumnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1453">1453</a><span>]</span><span> supaya ia dapat menjelaskan kepada mereka. </span><span>[a]</span><span> Maka Allah membiarkan sesat siapa yang Dia kehendaki, dan memberi petunjuk kepada siapa yang Dia kehendaki. Dan Dia-lah Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a]</span><span> 13:28 ; 74:32.</span></p>');
INSERT INTO `quran` VALUES (8115,2,14,6,'<p><span>Dan sesungguhnya telah Kami utus Musa dengan Tanda-tanda Kami, </span><span>[b]</span><span> “Keluarkanlah kaum engkau dari kegelapan kepada cahaya, dan peringatkanlah mereka tentang Hari-hari Allah” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1454">1454</a><span>]</span><span> Sesungguhnya dalam hal itu ada Tanda-tanda bagi tiap-tiap orang yang bersabar </span><span>dan</span><span> bersyukur.</span></p><p><span>[b]</span><span> 14:2.</span></p>');
INSERT INTO `quran` VALUES (8116,2,14,7,'<p><span>Dan </span><span>ingatlah </span><span> ketika Musa berkata kepada kaumnya, </span><span>[c]</span><span> “Ingatlah kamu sekalian akan nikmat Allah atasmu, tatkala Dia menyelamatkan kamu dari kaum Firaun, yang menimpakan kepada mu azab yang buruk, dan membiarkan hidup wanita-wanitamu. Dan dalam hal itu bagimu ada cobaan yang besar dari Tuhan-mu.”</span></p><p><span>[c]</span><span> 2:50 ; 7:142 ; 28:5.</span></p>');
INSERT INTO `quran` VALUES (8117,2,14,8,'<p><span>R. 2</span></p><p><span>Dan ketika Tuhan engkau mengumumkan, </span><span>[a]</span><span> “Jika kamu bersyukur, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1455">1455</a><span>]</span><span> pasti Aku akan menambahkan lebih banyak padamu ; dan jika kamu tidak mensyukuri, sesungguhnya azab-Ku amat keras.”</span></p><p><span>[a]</span><span> 2:116 ; 4:148.</span></p>');
INSERT INTO `quran` VALUES (8118,2,14,9,'<p><span>Dan berkata Musa, “Jika </span><span>[b]</span><span> kamu ingkar, kamu dan orang-orang yang ada di bumi semuanya , </span><span>tidak akan dapat memudaratkan Allah, </span><span>maka sesungguhnya Allah Maha Kaya, Maha Terpuji.”</span></p><p><span>[b]</span><span> 31:13.</span></p>');
INSERT INTO `quran` VALUES (8119,2,14,10,'<p><span>[c]</span><span> Bukanlah telah datang kepadamu berita mengenai orang-orang yang sebelummu, kaum Nuh, </span><span>‘</span><span>Ad dan Tsamud, dan orang-orang yang sesudah mereka ? </span><span>Sekarang </span><span> tiada yang mengetahui mereka kecuali Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1456">1456</a><span>]</span><span> Telah datang kepada mereka rasul-rasul mereka dengan Tanda-tanda yang nyata, tetapi mereka meletakkan tangan mereka pada mulut mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1457">1457</a><span>]</span><span> dan berkata, “Sesungguhnya kami ingkar kepada apa yang dengan itu kamu telah diutus, dan sesungguhnya kami ada dalam keraguan tentang apa yang kamu seru kami kepadanya yang menggelisahkan.</span></p><p><span>[c]</span><span> 9:70 ; 40:32 ; 50:13 </span><span>–</span><span> 15.</span></p>');
INSERT INTO `quran` VALUES (8120,2,14,11,'<p><span>Berkatalah rasul-rasul mereka, “Apakah kamu dalam keraguan mengenai Allah, </span><span>[a]</span><span> Pencipta seluruh langit dan bumi ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1458">1458</a><span>]</span><span> Dia panggil kamu, supaya Dia mengampuni dosa-dosamu, dan memberi kamu tangguh hingga suatu masa yang ditetapkan.” Mereka berkata, </span><span>[b]</span><span> “Tiadalah kamu melainkan manusia seperti kami juga. Kamu menghendaki menghalangi kami dari apa yang bapak-bapak kami sembah, maka datangkanlah kepada kami bukti yang nyata.”</span></p><p><span>[a]</span><span> 6:15 ; 12:102 ; 35:2 ; 39:47.</span></p><p><span>[b]</span><span> 11:28 ; 23:25.</span></p>');
INSERT INTO `quran` VALUES (8121,2,14,12,'<p><span>Rasul-rasul mereka berkata kepada mereka, </span><span>[a]</span><span> “Tiadalah kami melainkan manusia seperti kamu juga, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1459">1459</a><span>]</span><span> akan tetapi </span><span>[b]</span><span> Allah limpahkan karunia kepada siapa yang Dia kehendaki diantara hamba-hamba-Nya. Dan tidak layak bagi kami mendatangkan sesuatu bukti bagimu, melainkan dengan izin Allah Dan hanya kepada Allah sajalah orang-orang mukmin bertawakkal.”</span></p><p><span>[a]</span><span> 18:111 ; 41:7.</span></p><p><span>[b]</span><span> 3:165 ; 6:125.</span></p>');
INSERT INTO `quran` VALUES (8122,2,14,13,'<p><span>“Dan </span><span>[c]</span><span> mengapa kami tidak akan bertakwakal kepada Allah, padahal sesungguhnya Dia telah menunjuki kami jalan kami ? dan pasti kami akan bersabar terhadap segala penderitaan yang kamu timpakan kepada kami. Dan hanya kepada Allah sajalah bertawakkal orang-orang yang bertawakkal.”</span></p><p><span>[c] </span><span>11:57, 89 ; 12:68.</span></p>');
INSERT INTO `quran` VALUES (8123,2,14,14,'<p><span>R. 3</span></p><p><span>Dan berkatalah, </span><span>[d]</span><span> orang-orang yang ingkar kepada rasul-rasul mereka, “Niscaya akan kami usir kamu dari bumi kami, atau kamu harus kembali kepada agama kami.” Maka Tuhan mereka mewahyukan kepada mereka, “Pasti akan Kami binasakan orang-orang yang aniaya.”</span></p><p><span>[d]</span><span> 7:89.</span></p>');
INSERT INTO `quran` VALUES (8124,2,14,15,'<p><span>“Dan </span><span>[a]</span><span> tentu akan Kami tempatkan kamu di bumi ini setelah mereka. Inilah </span><span>janji</span><span> bagi siapa yang takut kepada kedudukan-Ku dan takut kepada ancaman-Ku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1460">1460</a><span>]</span></p><p><span>[a]</span><span> 21:106.</span></p>');
INSERT INTO `quran` VALUES (8125,2,14,16,'<p><span>Dan mereka itu berdoa untuk kemenangan, dan gagal setiap orang yang berlaku sewenang-wenang, musuh kebenaran,</span></p>');
INSERT INTO `quran` VALUES (8126,2,14,17,'<p><span>Sesudah dari </span><span>azab duniawi </span><span> ada Jahannam, dan dia akan diberi minum dari </span><span>[b]</span><span> air mendidih.</span></p><p><span>[b]</span><span> 69:37 ; 78:25, 26.</span></p>');
INSERT INTO `quran` VALUES (8127,2,14,18,'<p><span>Ia akan meminumnya seteguk demi seteguk dan tidak dapat menelannya dengan mudah. Dan </span><span>[c]</span><span> maut akan datang kepadanya dari setiap penjuru, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1461">1461</a><span>]</span><span> </span><span>namun</span><span> dia tidak akan mati. Dan selain itu ada azab yang keras.</span></p><p><span>[c]</span><span> 20:75 ; 87:14.</span></p>');
INSERT INTO `quran` VALUES (8128,2,14,19,'<p><span>[a]</span><span> Misal orang-orang yang ingkar kepada Tuhan mereka, amal mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1462">1462</a><span>]</span><span> seperti abu yang dihembus angin keras pada hari berangin kencang. </span><span>[b]</span><span> Mereka tidak akan mempunyai kekuasaan sedikit pun atas apa yang telah mereka usahakan. Itulah sebenarnya kebinasaan yang hebat.</span></p><p><span>[a]</span><span> 24:40.</span></p><p><span>[b]</span><span> 2:265.</span></p>');
INSERT INTO `quran` VALUES (8129,2,14,20,'<p><span>Tidaklah engkau lihat, bahwa </span><span>[c]</span><span> Allah menjadikan seluruh langit dan bumi ini dengan hak ? </span><span>[d]</span><span> Jika Dia kehendaki, Dia dapat memusnakan kamu dan mendatangkan makhluk baru.</span></p><p><span>[c]</span><span> 6:74 ; 16:4 ; 29:45 ; 39:6.</span></p><p><span>[d]</span><span> 4:134 ; 6:134</span></p>');
INSERT INTO `quran` VALUES (8130,2,14,21,'<p><span>Dan tidaklah </span><span>[e]</span><span> yang demikian itu sulit bagi Allah</span></p><p><span>[e]</span><span> 35:18.</span></p>');
INSERT INTO `quran` VALUES (8131,2,14,22,'<p><span>Dan mereka itu semuanya akan tampil dihadapan Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1463">1463</a><span>] [f]</span><span> maka akan berkata orang-orang yang lemah kepada orang-orang yang sombong, “Sesungguhnya kami dahulu pengikut-pengikutmu ; maka apakah kamu dapat menghindarkan kami dari azab Allah sedikit pun ? Mereka berkata, “Seandainya Allah memberi petunjuk kepada kami, pasti kami telah memberi petunjuk kepadamu. Sama saja bagi kami, apakah kami berkeluh-kesah atau kami bersabar ; tiada bagi kami jalan untuk melepaskan diri.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1464">1464</a><span>]</span></p><p><span>[f]</span><span> 6:129 ; 7:39, 40 ; 28:64 ; 33:68, 69 ; 34:32 ; 40:48, 49.</span></p>');
INSERT INTO `quran` VALUES (8132,2,14,23,'<p><span>R. 4</span></p><p><span>Dan berkatalah syaitan, ketika perkara itu telah diputuskan, “Sesungguhnya Allah telah menjanjikan kepadamu janji yang benar, dan aku menjanjikan kepadamu, tetapi aku telah menyalahinya. Dan </span><span>[a]</span><span> tidaklah aku mempunyai kekuasaan apa pun atasmu, melainkan aku telah mengajak kamu, lalu kamu telah menerimaku. Maka janganlah kamu mencela aku, tetapi celalah dirimu sendiri. Tiadalah aku dapat menolongmu dan kamu pun tidak dapat menolongku. Sesungguhnya aku telah mengingkari apa-apa yang kamu persekutukan dengan aku sejak dahulu. Sesungguhnya orang-orang aniaya itu bagi mereka ada azab yang pedih.”</span></p><p><span>[a]</span><span> 15:43 ; 16:100 ; 17:66.</span></p>');
INSERT INTO `quran` VALUES (8133,2,14,24,'<p><span>Dan </span><span>[b]</span><span> orang-orang yang beriman dan beramal shaleh akan dimasukkan ke dalam kebun-kebun yang di bawahnya mengalir sungai-sungai ; mereka kekal di dalamnya dengan izin Tuhan mereka. </span><span>[c]</span><span> Salam mereka di dalamnya ialah, “Selamat sejahtera.”</span></p><p><span>[b]</span><span> 10:10 ; 22:24.</span></p><p><span>[c]</span><span> 10:11 ; 15:47 ; 36:59 ; 50:35.</span></p>');
INSERT INTO `quran` VALUES (8134,2,14,25,'<p><span>Tidakkah engkau lihat, bagaimana Allah membuat perumpamaan satu kalimah yang baik seperti sebatang pohon yang baik, yang akarnya kokoh kuat dan cabang-cabangnya </span><span>menjulang</span><span> ke langit ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1465">1465</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8135,2,14,26,'<p><span>Ia memberikan buahnya pada setiap waktu dengan izin Tuhan-Nya. Dan </span><span>[a]</span><span> Allah membuat perumpamaan-perumpamaannya itu bagi manusia supaya mereka mendapat nasihat.</span></p><p><span>[a]</span><span> 13:18 ; 29:44.</span></p>');
INSERT INTO `quran` VALUES (8136,2,14,27,'<p><span>Dan perumpamaan kalimah yang buruk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1466">1466</a><span>]</span><span> adalah seperti halnya pohon buruk, yang telah dicabut dengan akar-akarnya dari permukaan bumi, tidaklah baginya </span><span>dapat </span><span> tegak.</span></p>');
INSERT INTO `quran` VALUES (8137,2,14,28,'<p><span>Allah meneguhkan orang-orang beriman dengan firman yang kokoh dalam kehidupan di dunia dan di akhirat ; dan Allah membiarkan sesat orang-orang aniaya. Dan Allah berbuat apa yang Dia kehendaki.</span></p>');
INSERT INTO `quran` VALUES (8138,2,14,29,'<p><span>R. 5</span></p><p><span>[a]</span><span> Apakah engkau tidak melihat kepada orang-orang yang telah menukar nikmat Allah </span><span>dengan</span><span> kekufuran dan menjatuhkan kaum mereka ke tempat kebinasaan.</span></p><p><span>[a]</span><span> 2:212.</span></p>');
INSERT INTO `quran` VALUES (8139,2,14,30,'<p><span>Yakni</span><span> Jahannam. mereka akan dibakar di dalamnya ; dan tempat tinggal yang seburuk-buruknya.</span></p>');
INSERT INTO `quran` VALUES (8140,2,14,31,'<p><span>Dan </span><span>[b]</span><span> mereka menjadikan bagi Allah sekutu-sekutu, untuk menyesatkan dari jalan-Nya. Katakanlah, </span><span>[c]</span><span> “Bersenang-senanglah kamu sementara, maka sesungguhnya tempat kembali kamu kepada Api.”</span></p><p><span>[b]</span><span> 2:23 ; 13:34.</span></p><p><span>[c]</span><span> 47:13 ; 77:47.</span></p>');
INSERT INTO `quran` VALUES (8141,2,14,32,'<p><span>Katakanlah kepada hamba-hamba-Ku yang beriman, mereka hendaknya mendirikan shalat dan </span><span>[d]</span><span> membelanjakan sebagian dari apa yang telah Kami rezekikan kepada mereka dengan sembunyi-sembunyi dan terang-terangan, sebelum </span><span>[e]</span><span> datang Hari yang tidak ada jual-beli di dalamnya dan tidak </span><span>pula</span><span> persahabatan.</span></p><p><span>[d]</span><span> 2:275 ; 13:23 ; 16:76.</span></p><p><span>[e]</span><span> 2:256 ; 43:68.</span></p>');
INSERT INTO `quran` VALUES (8142,2,14,33,'<p><span>Allah yang telah menciptakan seluruh langit dan bumi dan </span><span>[a]</span><span> menurunkan air dari awan, lalu Dia mengeluarkan dengan itu buah-buahan sebagai rezeki bagimu ; dan </span><span>[b]</span><span> Dia telah menjadikan bahtera agar berkhidmat kepadamu, supaya berlayar di lautan dengan perintah-Nya, dan Dia telah menjadikan sungai-sungai agar berkhidmat kepadamu.</span></p><p><span>[a]</span><span> 2:23 ; 20:54 ; 22:64 ; 35:28.</span></p><p><span>[b]</span><span> 22:66 ; 43:14 ; 45:13.</span></p>');
INSERT INTO `quran` VALUES (8143,2,14,34,'<p><span>Dan </span><span>[c]</span><span> Dia menjadikan matahari dan bulan agar berkhidmat kepadamu, kedua-duanya menjalankan tugasnya dengan dawan. Dan Dia telah menjadikan malam dan siang agar berkhidmat kepadamu.</span></p><p><span>[c]</span><span> 7:55 ; 13:3 ; 16:13 ; 39:6.</span></p>');
INSERT INTO `quran` VALUES (8144,2,14,35,'<p><span>Dan Dia berikan kepadamu segala sesuatu apa yang kamu minta kepada-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1467">1467</a><span>]</span><span> Dan </span><span>[d]</span><span> sekiranya kamu menghitung nikmat-nikmat Allah, kamu tidak akan dapat menghitungnya. Sesungguhnya manusia sangat aniaya, sangat tidak berterimakasih.</span></p><p><span>[d]</span><span> 16:19.</span></p>');
INSERT INTO `quran` VALUES (8145,2,14,36,'<p><span>R. 6</span></p><p><span>Dan </span><span>ingatlah</span><span> </span><span>[e]</span><span> ketika berkata Ibrahim, “Ya Tuhan-ku, jadikanlah kota ini </span><span>tempat</span><span> yang aman, dan </span><span>[f]</span><span> lindungilah aku dan anak-anakku dari menyembah berhala-berhala. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1468">1468</a><span>]</span></p><p><span>[e]</span><span> 2:127.</span></p><p><span>[f]</span><span> 2:129.</span></p>');
INSERT INTO `quran` VALUES (8146,2,14,37,'<p><span>“Ya Tuhan-ku </span><span>[a]</span><span> sesungguhnya berhala-berhala itu telah menyesatkan banyak dari antara manusia. Maka barangsiapa mengikutiku, maka sesungguhnya ia dari aku ; dan barangsiapa yang durhaka kepadaku, maka sesungguhnya Engkau Maha Pengampun, Maha Penyayang.</span></p><p><span>[a]</span><span> 71:25.</span></p>');
INSERT INTO `quran` VALUES (8147,2,14,38,'<p><span>[b]</span><span> “Ya Tuhan kami, sesungguhnya aku telah menempatkan sebagian keturunanku di lembah yang tandus dekat rumah Engkau yang dihormati. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1469">1469</a><span>]</span><span> Ya Tuhan kami, supaya mereka dapat mendirikan shalat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1470">1470</a><span>]</span><span> Maka jadikanlah hati manusia cenderung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1471">1471</a><span>]</span><span> kepada mereka dan </span><span>[c]</span><span> rezekikanlah mereka berupa buah-buahan, supaya mereka </span><span>selalu</span><span> bersyukur.</span></p><p><span>[b]</span><span> 22:27.</span></p><p><span>[c]</span><span> 2:127 ; 28:58.</span></p>');
INSERT INTO `quran` VALUES (8148,2,14,39,'<p><span>“Ya Tuhan kami, sesungguhnya </span><span>[a]</span><span> Engkau mengetahui apa yang kami sembunyikan dan apa yang kami nyatakan. Dan tiada yang tersembunyi bagi Allah sesuatu pun di bumi dan tidak </span><span>pula</span><span> di langit ; </span></p><p><span>[a]</span><span> 2:78 ; 3:6 ; 27:66.</span></p>');
INSERT INTO `quran` VALUES (8149,2,14,40,'<p><span>“Segala puji bagi Allah Yang telah menganugerahkan kepadaku walaupun usiaku telah lanjut, Ismail dan Ishak. Sesungguhnya Tuhan-ku Maha Mendengar doa.”</span></p>');
INSERT INTO `quran` VALUES (8150,2,14,41,'<p><span>[b]</span><span> “Ya Tuhan-ku, jadikanlah aku orang yang </span><span>tetap</span><span> mendirikan shalat, dan dari keturunanku. Ya Tuhan kami, </span><span>[c]</span><span> </span><span>karunialah kami dengan rahmat Engkau </span><span> dan kabulkanlah doaku.”</span></p><p><span>[b]</span><span> 2:129.</span></p><p><span>[c]</span><span> 2:128.</span></p>');
INSERT INTO `quran` VALUES (8151,2,14,42,'<p><span>[d]</span><span> “Ya Tuhan kami, ampunilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1472">1472</a><span>]</span><span> aku dan kedua orangtuaku dan orang-orang mukmin pada Hari diadakannya perhitungan.”</span></p><p><span>[d]</span><span> 71:29.</span></p>');
INSERT INTO `quran` VALUES (8152,2,14,43,'<p><span>R. 7</span></p><p><span>Dan janganlah sekali-kali engkau menyangka, bahwa Allah lengah terhadap apa yang dikerjakan oleh orang-orang aniaya. Sesungguhnya Dia hanya memberi tangguh kepada mereka hingga Hari, yang pada waktu ini mata </span><span>mereka</span><span> akan terbelalak </span><span>karena ngerinya.</span><span> </span></p>');
INSERT INTO `quran` VALUES (8153,2,14,44,'<p><span>Mereka terburu-buru lari ketakutan, dengan menengadakan kepadanya, pandangan mereka tidak berpaling, dan hati mereka kosong. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1473">1473</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8154,2,14,45,'<p><span>Dan peringatkanlah manusia tentang Hari, ketika azab itu akan datang kepada mereka, maka akan berkatalah </span><span>[a]</span><span> orang-orang yang aniaya, “Ya Tuhan kami, berilah kami tangguh hingga masa yang dekat. Kami akan sambut seruan Engkau dan akan mengikuti para rasul.” </span><span>Dia akan berkata,</span><span> “Bukanlah kamu dahulu telah bersumpah, bahwa kamu tidak akan jatuh ?</span></p><p><span>[a]</span><span> 63:11.</span></p>');
INSERT INTO `quran` VALUES (8155,2,14,46,'<p><span>“Dan kamu menetap di tempat tinggal orang-orang yang telah aniaya terhadap diri mereka, dan telah nyata bagimu, bagaimana Kami berlaku terhadap mereka ; dan Kami telah jelaskan kepadamu perumpamaan-perumpamaan.”</span></p>');
INSERT INTO `quran` VALUES (8156,2,14,47,'<p><span>Dan </span><span>[a]</span><span> sesungguhnya mereka telah melakukan makar mereka ; tetapi makar mereka ada di sisi Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1474">1474</a><span>]</span><span> dan sekalipun makar mereka dapat memindahkan gunung-gunung.</span></p><p><span>[a]</span><span> 3:55 ; 8:31 ; 13:43 ; 27:51.</span></p>');
INSERT INTO `quran` VALUES (8157,2,14,48,'<p><span>Maka janganlah engkau sekali-kali menyangka, bahwa </span><span>[b]</span><span> Allah akan menyalahi janji-Nya kepada rasul-rasul-Nya, Sesungguhnya, Allah Maha Perkasa, Yang mempunyai pembalasan.</span></p><p><span>[b]</span><span> 3:195 ; 10:104 ; 58:22.</span></p>');
INSERT INTO `quran` VALUES (8158,2,14,49,'<p><span>Pada Hari, ketika diganti bumi ini dengan bumi yang lain, dan begitu </span><span>pula</span><span> seluruh langit ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1475">1475</a><span>]</span><span> dan mereka akan tampil menghadap Allah Yang Maha Esa, Maha Perkasa.</span></p>');
INSERT INTO `quran` VALUES (8159,2,14,50,'<p><span>Dan </span><span>[c]</span><span> engkau akan melihat orang-orang yang berdosa pada Hari itu diikat dengan rantai.</span></p><p><span>[c]</span><span> 38:39.</span></p>');
INSERT INTO `quran` VALUES (8160,2,14,51,'<p><span>Baju mereka dari pelangkin (ter), dan muka mereka akan tertutup </span><span>[a]</span><span> api.</span></p><p><span>[a]</span><span> 10:28 ; 23:105 ; 54:49.</span></p>');
INSERT INTO `quran` VALUES (8161,2,14,52,'<p><span>[b]</span><span> </span><span>Yang demikian itu,</span><span> supaya Allah membalas setiap jiwa, apa yang telah diusahakannya. Sesungguhnya, Allah sangat cepat dalam perhitungan. </span></p><p><span>[b]</span><span> 40:18 ; 45:23 74:39.</span></p>');
INSERT INTO `quran` VALUES (8162,2,14,53,'<p><span>[c]</span><span> Ini adalah penjelasan yang cukup bagi manusia, </span><span>supaya mereka mendapat manfaat,</span><span> dan supaya mereka diberi peringatan dengan itu dan supaya mereka mengetahui bahwa Dia-lah Tuhan Yang Maha Esa, dan supaya mengambil nasihat orang-orang yang berakal.</span></p><p><span>[c]</span><span> 5:68 ; 6:20.</span></p>');
INSERT INTO `quran` VALUES (8163,2,15,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8164,2,15,2,'<p><span>[b]</span><span> Aku Allah Yang Maha Melihat. </span><span>[c]</span><span> Inilah ayat-ayat Kitab yang </span><span>sempurna</span><span> dan Alquran yang memberi penerangan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1476">1476</a><span>]</span></p><p><span>[b]</span><span> 10:2 ; 11:2 ; 12:2 ; 13:2 ; 14:2.</span></p><p><span>[c]</span><span> 27:2 ; 31:3.</span></p>');
INSERT INTO `quran` VALUES (8165,2,15,3,'<p><span>JUZ XIV</span></p><p><span>Acapkali orang-orang yang ingkar ingin kiranya dahulu mereka menjadi Muslim. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1477">1477</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8166,2,15,4,'<p><span>[d]</span><span> Biarlah mereka makan dan bersenang-senang, dan mereka dilalaikan oleh angan-angan kosong ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1478">1478</a><span>]</span><span> maka mereka segera akan mengetahui.</span></p><p><span>[d]</span><span> 47:13.</span></p>');
INSERT INTO `quran` VALUES (8167,2,15,5,'<p><span>Dan tidak Kami menbinasakan suatu kota, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1479">1479</a><span>]</span><span> melainkan baginya telah ada suatu kepurusan yang telah diketahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1479a">1479a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8168,2,15,6,'<p><span>[a]</span><span> Tiada suatu umat </span><span>dapat</span><span> mendahului ajalnya dan tidak pula mereka dapat mengundurkannya.</span></p><p><span>[a]</span><span> 7:35 ; 10:50 ; 16:62</span></p>');
INSERT INTO `quran` VALUES (8169,2,15,7,'<p><span>Dan mereka berkata, </span><span>[b]</span><span> “Hai orang yang telah diturunkan kepadanya peringatan ini, sesungguhnya engkau adalah orang gila ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1480">1480</a><span>]</span></p><p><span>[b]</span><span> 37:37 ; 44:15 ; 68:52.</span></p>');
INSERT INTO `quran` VALUES (8170,2,15,8,'<p><span>[c]</span><span> “Mengapa tidak engkau datangkan kepada kami malaikat-malaikat jika engkau termasuk orang-orang yang benar.”</span></p><p><span>[c]</span><span> 6:9 ; 11:13 ; 13: 25:8.</span></p>');
INSERT INTO `quran` VALUES (8171,2,15,9,'<p><span>[d]</span><span> Kami tidak menurunkan malaikat-malaikat melainkan diberi tangguh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1481">1481</a><span>]</span></p><p><span>[d]</span><span> 6:9.</span></p>');
INSERT INTO `quran` VALUES (8172,2,15,10,'<p><span>Sesungguhnya, </span><span>[a]</span><span> Kami Yang telah menurunkan Peringatan </span><span>Alquran</span><span> ini, dan sesungguhnya Kami baginya adala Pemelihara. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1482">1482</a><span>]</span></p><p><span>[a]</span><span> 36:70 ; 65:11.</span></p>');
INSERT INTO `quran` VALUES (8173,2,15,11,'<p><span>Dan sesungguhnya Kami telah mengutus </span><span>rasul-rasul</span><span> sebelum engkau kepada golongan-golongan terdahulu.</span></p>');
INSERT INTO `quran` VALUES (8174,2,15,12,'<p><span>Dan </span><span>[b]</span><span> tidak pernah datang kepada mereka seorang rasul pun, melainkan kepadanya mereka memperolok-olokkan.</span></p><p><span>[b]</span><span> 36:31 ; 43:8.</span></p>');
INSERT INTO `quran` VALUES (8175,2,15,13,'<p><span>[a]</span><span> Demikian Kami memasukkannya ini </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1483">1483</a><span>]</span><span> ke dalam hati orang-orang yang berdosa ;</span></p><p><span>[a]</span><span> 26:201.</span></p>');
INSERT INTO `quran` VALUES (8176,2,15,14,'<p><span>[b]</span><span> Tidaklah mereka itu beriman kepada </span><span>Alquran</span><span> ini, sekalipun telah berlalu contoh orang-orang terdahulu.</span></p><p><span>[b]</span><span> 26:202.</span></p>');
INSERT INTO `quran` VALUES (8177,2,15,15,'<p><span>Dan sekirannya Kami bukakan bagi mereka sebuah pintu dari langit, maka mereka terus saja naik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1484">1484</a><span>]</span><span> melaluinya.</span></p>');
INSERT INTO `quran` VALUES (8178,2,15,16,'<p><span>Pasti mereka berkata, “Penglihatan kami saja yang dikaburkan, bahkan kami sebenarnya kaum yang kena sihir.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1485">1485</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8179,2,15,17,'<p><span>R. 2</span></p><p><span>Dan sesungguhnya Kami </span><span>[a]</span><span> telah jadikan di langit gugusan-gugusan bintang dan Kami telah menghiasnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1486">1486</a><span>] </span><span>untuk orang-orang yang melihat,</span></p><p><span>[a]</span><span> 37:7 ; 41:13 ; 67:6.</span></p>');
INSERT INTO `quran` VALUES (8180,2,15,18,'<p><span>Dan </span><span>[b]</span><span> Kami telah memeliharanya dari setiap syaitan yang terkutuk, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1487">1487</a><span>]</span></p><p><span>[b]</span><span> 37:8 ; 41:13.</span></p>');
INSERT INTO `quran` VALUES (8181,2,15,19,'<p><span>Kecuali </span><span>[c]</span><span> jika ada orang yang mencuri-curi dengar </span><span>wahyu Ilahi dan memutarbalikkannya,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1488">1488</a><span>]</span><span> maka ia diikuti nyata api yang terang-benderang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1488a">1488a</a><span>]</span></p><p><span>[c]</span><span> 37:11 ; 67:6.</span></p>');
INSERT INTO `quran` VALUES (8182,2,15,20,'<p><span>[a]</span><span> Dan bumi Kami telah membentangkannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1489">1489</a><span>]</span><span> dan Kami </span><span>[b]</span><span> tegakkan di dalamnya gunung-gunung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1489a">1489a</a><span>]</span><span> dan Kami tumbuhkan di dalamnya segala sesuatu dengan perimbangan yang tepat.</span></p><p><span>[a]</span><span> 13:4.</span></p><p><span>[b]</span><span> 15:16 ; 21:32.</span></p>');
INSERT INTO `quran` VALUES (8183,2,15,21,'<p><span>Dan telah </span><span>[c]</span><span> Kami jadikan bagimu di dalamnya segala keperluan hidup, dan untuk yang kamu tidak memberi rezeki kepadanya.</span></p><p><span>[c]</span><span> 7:11.</span></p>');
INSERT INTO `quran` VALUES (8184,2,15,22,'<p><span>Dan </span><span>[d]</span><span> tiada suatu pun benda melainkan pada Kami ada khazanah-khazanahnya dan tidaklah Kami menurunkannya melainkan dalam ukuran yang tertentu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1490">1490</a><span>]</span></p><p><span>[d]</span><span> 40:14.</span></p>');
INSERT INTO `quran` VALUES (8185,2,15,23,'<p><span>Dan telah </span><span>[a]</span><span> Kami tiupkan angin yang mengangkat uap, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1491">1491</a><span>]</span><span> maka Kami turunkan air dari awan, lalu Kami memberikannya kepadamu untuk minum ; dan kamu sendiri tidak dapat menyimpannya.</span></p><p><span>[a]</span><span> 7:58 ; 24:44 ; 25:49.</span></p>');
INSERT INTO `quran` VALUES (8186,2,15,24,'<p><span>Dan sesungguhnya </span><span>[b]</span><span> Kami Yang menghidupkan, dan Kami Yang mematikan ; dan </span><span>[c]</span><span> Kami </span><span>pula</span><span> yang menjadi pewaris. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1492">1492</a><span>]</span></p><p><span>[b]</span><span> 50:44.</span></p><p><span>[c]</span><span> 19:41.</span></p>');
INSERT INTO `quran` VALUES (8187,2,15,25,'<p><span>Dan sesungguhnya Kami mengetahui orang-orang yang terdepan dari antaramu dan sesungguhnya Kami mengetahu </span><span>pula</span><span> orang-orang yang tertinggal dibelakang.</span></p>');
INSERT INTO `quran` VALUES (8188,2,15,26,'<p><span>Dan sesungguhnya </span><span>[d]</span><span> Tuhan engkau Dia-lah yang menghimpun mereka. Sesungguhnya Dia Maha Bijaksana, Maha Mengetahui.</span></p><p><span>[d]</span><span> 6:129 ; 25:18 ; 34:41 .</span></p>');
INSERT INTO `quran` VALUES (8189,2,15,27,'<p><span>R. 3</span></p><p><span>Dan sesungguhnya, telah </span><span>[e]</span><span> Kami jadikan manusia dari tanah liat kering yang berdenting, dari lumpur hitam yang telah diberi bentuk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1493">1493</a><span>]</span></p><p><span>[e]</span><span> 6:3 ; 15:29, 34 ; 55:15.</span></p>');
INSERT INTO `quran` VALUES (8190,2,15,28,'<p><span>Dan </span><span>[a]</span><span> jin Kami telah menciptakan sebelum itu dari api angin yang panas. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1494">1494</a><span>]</span></p><p><span>[a]</span><span> 7:13 ; 38:77 ; 55:16.</span></p>');
INSERT INTO `quran` VALUES (8191,2,15,29,'<p><span>Dan ingatlah ketika Tuhan engkau berfirman kepada para malaikat, </span><span>[a]</span><span> “Sesungguhnya Aku hendak menciptakan manusia dari tanah liat kering yang berdenting, dari lumpur hitam yang telah diberi bentuk ;</span></p><p><span>[a]</span><span> 7:13 ; 77:55.</span></p>');
INSERT INTO `quran` VALUES (8192,2,15,30,'<p><span>“Maka </span><span>[b]</span><span> Ketika Aku memberinya bentuk yang </span><span>sempurna</span><span> dan telah Aku tiupkan wahyu-Ku ke dalam </span><span>hati</span><span> nya, maka </span><span>[c]</span><span> jatuhkanlah dirimu bersamanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1495">1495</a><span>]</span><span> sujud </span><span>kepada Allah.</span><span> “</span></p><p><span>[b]</span><span> 32:10 ; 38:73.</span></p><p><span>[c]</span><span> 2:35 ; 7:12 ; 17:62 ; 18:51 ; 20:117.</span></p>');
INSERT INTO `quran` VALUES (8193,2,15,31,'<p><span>[d]</span><span> Maka bersujudlah malaikat semua </span><span>bersamanya kepada Allah,</span></p><p><span>[d]</span><span> 2:35 ; 7:12 ; 17:62 ; 18:51 ; 20:177.</span></p>');
INSERT INTO `quran` VALUES (8194,2,15,32,'<p><span>[e]</span><span> Kecuali iblis. Ia menolak bersama-sama mereka untuk bersujud. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1496">1496</a><span>]</span></p><p><span>[e]</span><span> 2:35 ; 7:12 ; 17:62 ; 18:51 ; 20:117.</span></p>');
INSERT INTO `quran` VALUES (8195,2,15,33,'<p><span>[a]</span><span> </span><span>Allah</span><span> berfirman, “Hai iblis, apakah yang telah terjadi dengan engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1496a">1496a</a><span>]</span><span> bahwa engkau tidak bersama-sama dengan mereka yang sujud.</span></p><p><span>[a]</span><span> 7:13 ; 38:76.</span></p>');
INSERT INTO `quran` VALUES (8196,2,15,34,'<p><span>[b]</span><span> Ia berkata, “Aku tidak mau bersujud bersama manusia yang telah Engkau jadikan dari tanah liat kering yang berdenting, dari lumpur yang telah diberi bentuk.</span></p><p><span>[b]</span><span> 7:13 ; 17:62 ; 18:51.</span></p>');
INSERT INTO `quran` VALUES (8197,2,15,35,'<p><span>[c]</span><span> </span><span>Allah</span><span> berfirman, “Maka keluarlah dari sini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1497">1497</a><span>]</span><span> karena sesungguhnya engkau terkutuk,</span></p><p><span>[c]</span><span> 7:14, 19 ; 38:78.</span></p>');
INSERT INTO `quran` VALUES (8198,2,15,36,'<p><span>“Dan </span><span>[d]</span><span> sesungguhnya atasmu ada kutukan hingga Hari Pembalasan.”</span></p><p><span>[d]</span><span> 38:79.</span></p>');
INSERT INTO `quran` VALUES (8199,2,15,37,'<p><span>Ia </span><span>[e]</span><span> berkata, “Ya Tuhan-ku, maka berilah aku tangguh hingga hari mereka akan dibangkitkan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1498">1498</a><span>]</span></p><p><span>[e]</span><span> 7:15 ; 17:63 ; 38:80.</span></p>');
INSERT INTO `quran` VALUES (8200,2,15,38,'<p><span>[a]</span><span> </span><span>Allah</span><span> berfirman, “Maka sesungguhnya engkau termasuk orang-orang yang diberi tangguh, </span></p><p><span>[a] </span><span>7:16 ; 38:81.</span></p>');
INSERT INTO `quran` VALUES (8201,2,15,39,'<p><span>[b]</span><span> “Hingga hari yang waktunya telah ditetapkan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1499">1499</a><span>]</span></p><p><span>[b]</span><span> 38:82.</span></p>');
INSERT INTO `quran` VALUES (8202,2,15,40,'<p><span>[c]</span><span> Ia berkata. “Ya Tuhan-ku, karena Engkau telah memutuskan aku sesat, tentulah aku akan jadikan </span><span>kesesatan </span><span> ditampakkan indah bagi mereka di bumi, dan niscaya akan kusesatkan mereka itu semua,</span></p><p><span>[c]</span><span> 7:17, 18 ; 38:83.</span></p>');
INSERT INTO `quran` VALUES (8203,2,15,41,'<p><span>[d]</span><span> “Kecuali hamba-hamba Engkau yang mukhlis dari antara mereka.”</span></p><p><span>[d]</span><span> 38:84.</span></p>');
INSERT INTO `quran` VALUES (8204,2,15,42,'<p><span>Allah</span><span> berfirman, “Inilah jalan kepada-Ku yang lurus.</span></p>');
INSERT INTO `quran` VALUES (8205,2,15,43,'<p><span>“Sesungguhnya, hamba-hamba-Ku, tidak ada bagi </span><span>[e]</span><span> engkau atas mereka kekuasaan, kecuali yang mengikuti engkau diantara orang-orang </span><span>[f]</span><span> sesat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1500">1500</a><span>]</span></p><p><span>[e]</span><span> 17:66 ; 34:22.</span></p><p><span>[f]</span><span> 7:19 ; 17:64 ; 38:86.</span></p>');
INSERT INTO `quran` VALUES (8206,2,15,44,'<p><span>Dan sesungguhnya </span><span>[a]</span><span> Jahanam adalah tempat yang telah dijanjikan bagi mereka semua.</span></p><p><span>[a]</span><span> 17:64 ; 38:86.</span></p>');
INSERT INTO `quran` VALUES (8207,2,15,45,'<p><span>Baginya ada tujuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1501">1501</a><span>]</span><span> pintu, untuk setiap pintu dari mereka ada bagian yang ditentukan.</span></p>');
INSERT INTO `quran` VALUES (8208,2,15,46,'<p><span>R. 4</span></p><p><span>Sesungguhnya </span><span>[b]</span><span> orang-orang yang bertakwa akan masuk ke dalam kebun-kebun dan mata air-mata air.</span></p><p><span>[b]</span><span> 51:16 ; 52:18 ; 68:35 ; 77:42 ; 78:33.</span></p>');
INSERT INTO `quran` VALUES (8209,2,15,47,'<p><span>“Masuklah kamu kedalamnya dengan selamat dan aman.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1502">1502</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8210,2,15,48,'<p><span>Dan akan </span><span>[c]</span><span> Kami lenyapkan segala dendam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1503">1503</a><span>]</span><span> yang ada dalam dada mereka, sehingga mereka </span><span>merasa</span><span> bersaudara, </span><span>duduk</span><span> berhadap-hadapan di atas dipan-dipan.</span></p><p><span>[c]</span><span> 7:44.</span></p>');
INSERT INTO `quran` VALUES (8211,2,15,49,'<p><span>Tidak akan menyentuh mereka di dalamnya </span><span>[a]</span><span> keletihan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1504">1504</a><span>]</span><span> dan </span><span>[b]</span><span> mereka tidak akan dikeluarkan darinya.</span></p><p><span>[a]</span><span> 35:36.</span></p><p><span>[b]</span><span> 11:109 ; 18:109.</span></p>');
INSERT INTO `quran` VALUES (8212,2,15,50,'<p><span>Ya Rasul,</span><span> </span><span>[c]</span><span> beritahulah hamba-hamba-Ku, bahwa Aku sesungguh nya Maha Pengampun, Maha Penyayang ;</span></p><p><span>[c]</span><span> 5:99.</span></p>');
INSERT INTO `quran` VALUES (8213,2,15,51,'<p><span>Dan </span><span>[d]</span><span> bahwa azab-Ku itu azab yang sangat pedih.</span></p><p><span>[d]</span><span> 5:99.</span></p>');
INSERT INTO `quran` VALUES (8214,2,15,52,'<p><span>Dan </span><span>[e]</span><span> bertahulah mereka tentang tamu Ibrahim.</span></p><p><span>[e]</span><span> 51:25.</span></p>');
INSERT INTO `quran` VALUES (8215,2,15,53,'<p><span>[f]</span><span> Ketika mereka itu masuk kepadanya dan berkata, “Selamat </span><span>atasmu,</span><span>” Ia berkata, </span><span>[g]</span><span> “Kami sungguh takut dari kamu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1505">1505</a><span>]</span></p><p><span>[f]</span><span> 11:70 ; 51:26.</span></p><p><span>[g]</span><span> 11:71 ; 51:29.</span></p>');
INSERT INTO `quran` VALUES (8216,2,15,54,'<p><span>[h]</span><span> Mereka berkata, “Janganlah engkau takut, sesungguhnya kami memberi engkau khabar suka tentang seorang anak laki-laki yang banyak ilmu.”</span></p><p><span>[h]</span><span> 11:71 ; 51:29.</span></p>');
INSERT INTO `quran` VALUES (8217,2,15,55,'<p><span>[a]</span><span> Ia berkata, “Apakah kamu memberiku khabar suka padahal saya telah jadi tua ? Maka atas dasar apa kamu memberi khabar suka ? “</span></p><p><span>[a]</span><span> 11:73.</span></p>');
INSERT INTO `quran` VALUES (8218,2,15,56,'<p><span>Mereka berkata, “Kami telah memberi khabar suka kepada engkau dengan benar ; maka janganlah engkau termasuk orang-orang yang putus-asa.”</span></p>');
INSERT INTO `quran` VALUES (8219,2,15,57,'<p><span>Ia berkata, “Dan </span><span>[b]</span><span> siapakah yang putus-asa tentang rahmat dari Tuhan-nya, kecuali orang-orang yang sesat ? “</span></p><p><span>[b]</span><span> 12:88.</span></p>');
INSERT INTO `quran` VALUES (8220,2,15,58,'<p><span>Ia berkata, </span><span>[c]</span><span> “Apakah urusan penting kamu wahai utusan-utusan ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1506">1506</a><span>]</span></p><p><span>[c]</span><span> 51:32.</span></p>');
INSERT INTO `quran` VALUES (8221,2,15,59,'<p><span>Mereka berkata, </span><span>[d]</span><span> “Sesungguhnya kami telah diutus kepada kaum yang berdosa,</span></p><p><span>[d]</span><span> 51:33.</span></p>');
INSERT INTO `quran` VALUES (8222,2,15,60,'<p><span>[e]</span><span> “Kecuali pengikut-pengikut Luth. Sesungguhnya pasti akan Kami selamatkan mereka itu semuanya,</span></p><p><span>[e]</span><span> 29:33 ; 51:36.</span></p>');
INSERT INTO `quran` VALUES (8223,2,15,61,'<p><span>[f]</span><span> “Kecuali isterinya. Kami telah memutuskan, ia sesungguhnya termasuk orang-orang yang akan tertinggal dibelakang.”</span></p><p><span>[f]</span><span> 7:84 ; 11:82 ; 26:172 ; 27:58.</span></p>');
INSERT INTO `quran` VALUES (8224,2,15,62,'<p><span>R. 5</span></p><p><span>Dan </span><span>[a]</span><span> tatkala utusan-utusan datang kepada pengikut Luth,</span></p><p><span>[a]</span><span> 11:78 ; 29:34.</span></p>');
INSERT INTO `quran` VALUES (8225,2,15,63,'<p><span>Ia berkata, </span><span>[b]</span><span> “Sesungguhnya kamu adalah kaum yang tidak dikenal.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1507">1507</a><span>]</span></p><p><span>[b]</span><span> 51:26.</span></p>');
INSERT INTO `quran` VALUES (8226,2,15,64,'<p><span>Mereka berkata, “Bahkan kami datang kepada engkau dengan membawa berita </span><span>azab</span><span> yang tentang itu mereka ragukan ;</span></p>');
INSERT INTO `quran` VALUES (8227,2,15,65,'<p><span>“Dan kami telah membawa kepada engkau berita yang pasti, dan sesungguhnya kami adalah orang-orang yang benar ;</span></p>');
INSERT INTO `quran` VALUES (8228,2,15,66,'<p><span>[c]</span><span> “Maka berangkatlah engkau dengan keluargamu di sebagian malam, engkau ikutlah di belakang mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1508">1508</a><span>]</span><span> Dan janganlah seorang pun dari kamu menoleh kebelakang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1509">1509</a><span>]</span><span> dan teruskanlah perjalanan kamu di mana kamu diperintahkan.”</span></p><p><span>[c]</span><span> 11:82.</span></p>');
INSERT INTO `quran` VALUES (8229,2,15,67,'<p><span>Dan </span><span>[a]</span><span> telah Kami beritahukan kepadanya keputusan ini, bahwa akar orang-orang ini akan ditumpas habis pada waktu subuh.</span></p><p><span>[a]</span><span> 6:46 ; 7:73, 85.</span></p>');
INSERT INTO `quran` VALUES (8230,2,15,68,'<p><span>Dan datanglah </span><span>[b]</span><span> penduduk kota dalam keadaan bergembira. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1510">1510</a><span>]</span></p><p><span>[b]</span><span> 11:79.</span></p>');
INSERT INTO `quran` VALUES (8231,2,15,69,'<p><span>Ia berkata, </span><span>[c]</span><span> “Sesungguhnya orang-orang ini tamuku, maka janganlah membuat aku malu,</span></p><p><span>[c]</span><span> 11:79.</span></p>');
INSERT INTO `quran` VALUES (8232,2,15,70,'<p><span>“Dan bertakwalah kepada Allah dan janganlah kamu menghinaku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1511">1511</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8233,2,15,71,'<p><span>Mereka berkata, “Tidakkah kami telah melarang engkau </span><span>melayani</span><span> orang-orang lain ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1512">1512</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8234,2,15,72,'<p><span>[a]</span><span> Ia berkata, “Mereka ini anak-anak perempuanku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1513">1513</a><span>]</span><span> </span><span>yang menjadi jaminan</span><span> jika kamu berbuat </span><span>hendak menentangku.</span><span>”</span></p><p><span>[a]</span><span> 11:79.</span></p>');
INSERT INTO `quran` VALUES (8235,2,15,73,'<p><span>Demi usia engkau, sesungguhnya mereka, dalam kemabukan mereka, berkelana kebingungan.</span></p>');
INSERT INTO `quran` VALUES (8236,2,15,74,'<p><span>[b]</span><span> Maka azab itu menimpa mereka pada saat matahari terbit.</span></p><p><span>[b]</span><span> 11:82.</span></p>');
INSERT INTO `quran` VALUES (8237,2,15,75,'<p><span>Maka, </span><span>[c]</span><span> Kami jadikan </span><span>kota mereka </span><span> bagian atasnya terbalik ke bawah, dan Kami hujankan atas mereka batu-batu dari tanah liat.</span></p><p><span>[c]</span><span> 11:83.</span></p>');
INSERT INTO `quran` VALUES (8238,2,15,76,'<p><span>[d]</span><span> Sesungguhnya dalam yang demikian itu ada tanda-tanda bagi orang-orang yang menggunakan firasat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1514">1514</a><span>]</span></p><p><span>[d]</span><span> 29:36 ; 51:38.</span></p>');
INSERT INTO `quran` VALUES (8239,2,15,77,'<p><span>Dan sesungguhnya </span><span>kota </span><span> itu </span><span>[e]</span><span> terletak pada sebuah jalan yang </span><span>masih</span><span> tetap dipakai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1515">1515</a><span>]</span></p><p><span>[e]</span><span> 37:138.</span></p>');
INSERT INTO `quran` VALUES (8240,2,15,78,'<p><span>Sesungguhnya </span><span>[a]</span><span> dalam yang demikian itu ada Tanda bagi orang-orang yang beriman.</span></p><p><span>[a]</span><span> 26:9.</span></p>');
INSERT INTO `quran` VALUES (8241,2,15,79,'<p><span>Dan sesungguhnya </span><span>[b]</span><span> penghuni Rimba Aikah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1516">1516</a><span>]</span><span> juga orang-orang aniaya.</span></p><p><span>[b]</span><span> 26:177 ; 38:14 ; 50:15.</span></p>');
INSERT INTO `quran` VALUES (8242,2,15,80,'<p><span>Maka </span><span>[c]</span><span> Kami timpakan hukuman keras diantara mereka. Dan sesungguhnya kedua tempat itu </span><span>terletak</span><span> di jalan raya yang terbuka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1517">1517</a><span>]</span></p><p><span>[c]</span><span> 26:190 ; 38:15 ; 50:15.</span></p>');
INSERT INTO `quran` VALUES (8243,2,15,81,'<p><span>R. 6</span></p><p><span>Dan sesungguhnya telah mendustakan para penghuni Hijr </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1518">1518</a><span>]</span><span> terhadap rasul-rasul.</span></p>');
INSERT INTO `quran` VALUES (8244,2,15,82,'<p><span>Dan Kami berikan kepada mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1519">1519</a><span>]</span><span> Tanda-tanda Kami, tetapi mereka darinya telah berpaling.</span></p>');
INSERT INTO `quran` VALUES (8245,2,15,83,'<p><span>Dan </span><span>[a]</span><span> mereka biasa memahat gunung-gunung untuk rumah-rumah yang aman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1520">1520</a><span>]</span></p><p><span>[a]</span><span> 7:75 ; 26:150.</span></p>');
INSERT INTO `quran` VALUES (8246,2,15,84,'<p><span>[b]</span><span> Maka mereka disergap oleh azab pada waktu subuh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1521">1521</a><span>]</span></p><p><span>[b]</span><span> 7:79 ; 11:68.</span></p>');
INSERT INTO `quran` VALUES (8247,2,15,85,'<p><span>Maka tidak berguna bagi mereka apa yang telah mereka usahakan.</span></p>');
INSERT INTO `quran` VALUES (8248,2,15,86,'<p><span>Dan tidaklah </span><span>[a]</span><span> Kami ciptakan seluruh langit dan bumi serta apa yang ada di antara keduanya melainkan dengan hak. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1521a">1521a</a><span>]</span><span> Dan sesungguhnya </span><span>[b]</span><span> saat itu pasti akan datang. Maka maafkanlah dengan cara maaf yang baik.</span></p><p><span>[a]</span><span> 3:192 ; 16:4 ; 38:28.</span></p><p><span>[b]</span><span> 20:16 ; 40:60.</span></p>');
INSERT INTO `quran` VALUES (8249,2,15,87,'<p><span>Sesungguhnya, Tuhan engkau Dia-lah Yang Maha Pencipta, Yang Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (8250,2,15,88,'<p><span>Dan Sesungguhnya </span><span>[c]</span><span> telah Kami berikan kepada engkau tujuh </span><span>ayat</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1522">1522</a><span>]</span><span> yang selalu diulang-ulang, dan Alquran yang agung.</span></p><p><span>[c]</span><span> 39:24.</span></p>');
INSERT INTO `quran` VALUES (8251,2,15,89,'<p><span>[a]</span><span> Janganlah engkau tujukan pandangan kedua matamu ke arah apa yang telah Kami berikan sebagai bahan kesenangan sementara kepada beberapa golongan di antara mereka, dan janganlah engkau bersedih hati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1523">1523</a><span>]</span><span> terhadap mereka, dan rendahkanlah sayap </span><span>kasih sayang</span><span> bagi orang-orang yang beriman.</span></p><p><span>[a]</span><span> 20:132.</span></p>');
INSERT INTO `quran` VALUES (8252,2,15,90,'<p><span>Dan katakanlah, </span><span>[b]</span><span> “Sesungguhnya aku ini pemberi ingat yang nyata.”</span></p><p><span>[b]</span><span> 22:50 ; 29:51 ; 51:51, 52 ; 67:27.</span></p>');
INSERT INTO `quran` VALUES (8253,2,15,91,'<p><span>Sebagaimana Kami telah menurunkan </span><span>azab</span><span> atas orang-orang yang telah membagi tugas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1524">1524</a><span>]</span><span> </span><span>melawan engkau ;</span></p>');
INSERT INTO `quran` VALUES (8254,2,15,92,'<p><span>Orang-orang yang telah menjadikan Alquran sebagai </span><span>himpunan </span><span> kebohongan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1525">1525</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8255,2,15,93,'<p><span>Maka demi Tuhan engkau, tentulah Kami akan minta tanggung jawab dari mereka semua.</span></p>');
INSERT INTO `quran` VALUES (8256,2,15,94,'<p><span>Mengenai apa-apa yang telah mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (8257,2,15,95,'<p><span>Maka </span><span>[a]</span><span> sampaikanlah dengan terang-terangan, apa yang diperintahkan kepada engkau, dan berpalinglah dari orang-orang musyrik.</span></p><p><span>[a]</span><span> 5:68.</span></p>');
INSERT INTO `quran` VALUES (8258,2,15,96,'<p><span>Sesungguhnya Kami </span><span>[b]</span><span> memelihara engkau terhadap orang-orang yang berolok-olok.</span></p><p><span>[b]</span><span> 2:138.</span></p>');
INSERT INTO `quran` VALUES (8259,2,15,97,'<p><span>Orang-orang yang menjadikan tuhan lain di samping Allah, maka mereka segera akan mengetahui.</span></p>');
INSERT INTO `quran` VALUES (8260,2,15,98,'<p><span>Dan sesungguhnya </span><span>[c]</span><span> Kami mengetahui, bahwa dada engkau menjadi sempit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1526">1526</a><span>]</span><span> disebabkan apa yang mereka katakan.</span></p><p><span>[c]</span><span> 6:34 ; 11:13.</span></p>');
INSERT INTO `quran` VALUES (8261,2,15,99,'<p><span>Maka </span><span>[d]</span><span> bertasbihlah dengan memuji Tuhan engkau dan jadilah engkau termasuk orang-orang yang bersujud </span><span>kepada-Nya.</span></p><p><span>[d]</span><span> 20:131 ; 50:40 ; 110:4.</span></p>');
INSERT INTO `quran` VALUES (8262,2,15,100,'<p><span>Dan teruslah menyembah kepada Tuhan engkau, hingga maut datang kepada engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1527">1527</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8263,2,16,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8264,2,16,2,'<p><span>Segera datang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1528">1528</a><span>] [b]</span><span> Keputusan Allah, maka janganlah kamu memintanya dipercepat. Maha Suci Dia dan Meha Tinggi, dari apa yang mereka persekutukan.</span></p><p><span>[b]</span><span> 5:53.</span></p>');
INSERT INTO `quran` VALUES (8265,2,16,3,'<p><span>Dia menurunkan malaikat-malaikat dengan wahyu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1529">1529</a><span>]</span><span> atas perintah-Nya kepada siapa yang Dia kehendaki diantara hamba-hamba-Nya, supaya kalian peringatkan </span><span>manusia,</span><span> bahwa tidak ada tuhan selain Aku ; maka bertakwalah kepada-Ku.</span></p>');
INSERT INTO `quran` VALUES (8266,2,16,4,'<p><span>[c]</span><span> Dia telah menciptakan seluruh langit dan bumi dengan hak, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1530">1530</a><span>]</span><span> Maha Tinggi Dia dari apa yang mereka persekutukan.</span></p><p><span>[c]</span><span> 3:192 ; 14:20 ; 15:86 ; 29:45 ; 39:6 ; 64:4.</span></p>');
INSERT INTO `quran` VALUES (8267,2,16,5,'<p><span>[a]</span><span> Dia telah menciptakan manusia dari mani, maka tiba-tiba ia menjadi pembantah yang nyata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1531">1531</a><span>]</span></p><p><span>[a]</span><span> 18:38 ; 22:6 ; 23:13, 14 ; 35:12 ; 36:78 ; 40:68.</span></p>');
INSERT INTO `quran` VALUES (8268,2,16,6,'<p><span>Dan </span><span>[b]</span><span> binatang ternak pun telah Dia ciptakan, bagi kamu padanya ada sarana kehangatan serta manfaat-manfaat </span><span>lainnya ; </span><span> dan dari sebagiannya kamu makan.</span></p><p><span>[b]</span><span> 6:143 ; 23:22 ; 36:72, 74 ; 40:80, 81.</span></p>');
INSERT INTO `quran` VALUES (8269,2,16,7,'<p><span>Dan bagimu padanya ada sarana keindahan, ketika kamu menggiring pulang di waktu petang, dan ketika kamu melepaskan di waktu pagi </span><span>ke tempat penggembalaan.</span></p>');
INSERT INTO `quran` VALUES (8270,2,16,8,'<p><span>Dan </span><span>[c]</span><span> binatang itu mengangkut muatan kamu ke suatu negeri yang tidak dapat kamu capai kecuali dengan banyak penderitaan bagi diri</span><span>mu.</span><span> Sesungguhnya Tuhan-mu Maha Penyantun, Maha Penyayang.</span></p><p><span>[c]</span><span> 6:143 ; 36:73 ; 40:81 ; 43:13.</span></p>');
INSERT INTO `quran` VALUES (8271,2,16,9,'<p><span>Dan kuda-kuda, bagal-bagal, dan keledai-keledai, supaya kamu dapat </span><span>[d]</span><span> menungganginya, dan juga </span><span>sebagai</span><span> sarana keindahan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1532">1532</a><span>]</span><span> Dan Dia akan menciptakan apa yang kamu tidak ketahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1532a">1532a</a><span>]</span></p><p><span>[d]</span><span> 36:73 ; 40:81 ; 43:13.</span></p>');
INSERT INTO `quran` VALUES (8272,2,16,10,'<p><span>Dan tanggung jawab Allah menunjukkan jalan yang benar ; dan diantaranya ada yang menyimpang. Dan </span><span>[a]</span><span> sekiranya Dia menghendaki, niscaya Dia telah memberi petunjuk kepada kamu sekalian.</span></p><p><span>[a]</span><span> 6:150 ; 10:100 ; 11:119.</span></p>');
INSERT INTO `quran` VALUES (8273,2,16,11,'<p><span>[b]</span><span> Dia-lah Dzat Yang menurunkan dari awan air bagi kamu dari nya kamu peroleh air minum , dan darinya </span><span>tumbuh</span><span> pohon-pohonan yang padanya kamu menggembala.</span></p><p><span>[b]</span><span> 2:23 ; 6:100 ; 13:18 ; 16:66 ; 22:64.</span></p>');
INSERT INTO `quran` VALUES (8274,2,16,12,'<p><span>Dia tumbuhkan bagimu </span><span>[c]</span><span> dengan </span><span>air</span><span> itu tanam-tanaman dan zaitun, dan kurma, dan anggur , dan segala macam buah-buahan. Sesungguhnya dalam yang demikian itu ada Tanda-tanda bagi orang-orang yang berfikir. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1533">1533</a><span>]</span></p><p><span>[c]</span><span> 6:100 ; 13:5.</span></p>');
INSERT INTO `quran` VALUES (8275,2,16,13,'<p><span>Dan </span><span>[a]</span><span> Dia mengkhidmatkan bagimu malam dan siang dan matahari dan bulan. dan bintang-bintang dikhidmatkan dengan perintah-Nya. Sesungguhnya dalam hal itu ada Tanda-tanda bagi orang-orang yang menggunakan akal.</span></p><p><span>[a]</span><span> 7:55 ; 13:3 ; 14:34 ; 35:14 ; 39:6.</span></p>');
INSERT INTO `quran` VALUES (8276,2,16,14,'<p><span>Dan apa-apa yang Dia telah ciptakan bagi kamu </span><span>[b]</span><span> di bumi berbagai macam jenisnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1534">1534</a><span>]</span><span> Sesungguhnya dalam hal itu adalah Tanda bagi orang-orang yang mengambil pelajaran. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1535">1535</a><span>]</span></p><p><span>[b]</span><span> 13:5 ; 39:22.</span></p>');
INSERT INTO `quran` VALUES (8277,2,16,15,'<p><span>Dan </span><span>[a]</span><span> Dia-lah Dzat, Yang telah mengkhidmatkan laut, supaya kamu dapat memakan daging </span><span>ikan</span><span> yang segar darinya, dan kamu mengeluarkan darinya </span><span>benda-benda</span><span> perhiasan yang kamu memakainya. Dan engkau lihat kapal membelah lautan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1536">1536</a><span>]</span><span> dan supaya kamu mencari karunia-Nya, dan agar kamu bersyukur.</span></p><p><span>[a]</span><span> 35:13 ; 45:13.</span></p>');
INSERT INTO `quran` VALUES (8278,2,16,16,'<p><span>Dan </span><span>[b]</span><span> Dia telah menancapkan di bumi gunung-gunung, supaya jangan sampai berguncang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1537">1537</a><span>]</span><span> bersama kamu, dan sungai-sungai serta jalan-jalan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1538">1538</a><span>]</span><span> supaya kamu dapat menemukan jalan </span><span>ke tempat yang dituju,</span></p><p><span>[b]</span><span> 13:4 ; 21:32.</span></p>');
INSERT INTO `quran` VALUES (8279,2,16,17,'<p><span>Dan </span><span>Dia jadikan</span><span> tanda-tanda </span><span>batas. </span><span>Dan dengan bintang-bintang itu mereka mendapat petunjuk </span><span>arah.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1539">1539</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8280,2,16,18,'<p><span>Apakah Dia, Yang menciptakan sama dengan yang tidak menciptakan ? Tidaklah kamu mau mengambil pelajaran ?</span></p>');
INSERT INTO `quran` VALUES (8281,2,16,19,'<p><span>Dan </span><span>[a]</span><span> sekiranya kamu menghitung nikmat-nikmat Allah, sekali-kali kamu tidak dapat menghitungnya. Sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a]</span><span> 14:35.</span></p>');
INSERT INTO `quran` VALUES (8282,2,16,20,'<p><span>Dan, </span><span>[b]</span><span> Allah mengetahui apa yang kamu sembunyikan dan apa yang kamu zahirkan.</span></p><p><span>[b]</span><span> 2:78 ; 27:26 ; 64:5.</span></p>');
INSERT INTO `quran` VALUES (8283,2,16,21,'<p><span>Dan </span><span>[c]</span><span> orang-orang yang menyeru selain Allah, mereka itu tidak menjadikan sesuatu pun, bahkan mereka sendiri yang telah diciptakan.</span></p><p><span>[c]</span><span> 7:192 ; 25:4.</span></p>');
INSERT INTO `quran` VALUES (8284,2,16,22,'<p><span>Mereka itu</span><span> mati, tidak hidup ; dan mereka tidak menyadari kapan mereka akan dibangkitkan.</span></p>');
INSERT INTO `quran` VALUES (8285,2,16,23,'<p><span>[d]</span><span> Tuhan-mu adalah Tuhan Yang Maha Esa. Dan adapun tentang mereka yang tidak beriman kepada akhirat, hati mereka ingkar, dan mereka itu sombong.</span></p><p><span>[d]</span><span> 2:164 ; 5:74 ; 22:35 ; 37:5.</span></p>');
INSERT INTO `quran` VALUES (8286,2,16,24,'<p><span>Tidak ragu lagi, bahwa </span><span>[a]</span><span> Allah mengetahui yang mereka sembunyikan dan yang mereka zahirkan. Sesungguhnya Dia tidak suka kepada orang-orang yang sombong.</span></p><p><span>[a]</span><span> 16:20.</span></p>');
INSERT INTO `quran` VALUES (8287,2,16,25,'<p><span>Dan apabila dikatakan kepada mereka, “</span><span>Betapa indahnya wahyu </span><span> yang telah diturunkan oleh Tuhan-mu” </span><span>[b]</span><span> Mereka berkata, “Itu hanyalah dongengan orang-orang dahulu,”</span></p><p><span>[b]</span><span> 8:32 ; 68:16 ; 83:14.</span></p>');
INSERT INTO `quran` VALUES (8288,2,16,26,'<p><span>[c]</span><span> Supaya mereka memikul beban mereka sepenuhnya pada Hari Kiamat, dan beban orang-orang yang mereka sesatkan tanpa ilmu. Ketahuilah, sangat buruk beban yang mereka pikul.</span></p><p><span>[c]</span><span> 29:14.</span></p>');
INSERT INTO `quran` VALUES (8289,2,16,27,'<p><span>R. 4</span></p><p><span>Sesungguhnya telah membuat rencana </span><span>jahat</span><span> orang-orang yang sebelum mereka maka </span><span>[d]</span><span> Allah menghancurkan bangunan-bangunan mereka sampai pondasinya, maka runtuhlah atas mereka atap dari atas mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1540">1540</a><span>]</span><span> Dan datanglah kepada mereka azab dari arah yang tidak mereka sadari.</span></p><p><span>[d]</span><span> 39:26 ; 59:3.</span></p>');
INSERT INTO `quran` VALUES (8290,2,16,28,'<p><span>Kemudian pada Hari Kiamat, Dia akan menghinakan mereka dan Dia akan berfirman, </span><span>[a]</span><span> “Manakah sekutu-sekutu-Ku, yang kamu gunakan untuk menentang mereka, </span><span>rasul-rasul-Ku </span><span>? Berkata orang-orang yang telah diberi ilmu “Sesungguhnya kehinaan pada hari ini dan musibah atas orang-orang kafir.”</span></p><p><span>[a]</span><span> 28:63, 75.</span></p>');
INSERT INTO `quran` VALUES (8291,2,16,29,'<p><span>[b]</span><span> Orang-orang yang mereka diwafatkan oleh malaikat sedangkan mereka aniaya terhadap dirinya , </span><span>[c]</span><span> lalu menyerahkan diri </span><span>berkata,</span><span> “Tidak pernah kami berbuat keburukan apapun.” Tidak benar, sesungguhnya Allah, Maha Mengetahui apa yang dahulu kamu kerjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1541">1541</a><span>]</span></p><p><span>[b]</span><span> 4:98 ; 8:51 ; 47:28.</span></p><p><span>[c]</span><span> 16:88.</span></p>');
INSERT INTO `quran` VALUES (8292,2,16,30,'<p><span>[d]</span><span> “Maka masukilah pintu-pintu Jahannam untuk tinggal lama di dalamnya. Maka sesungguhnya sangat buruk tempat tinggal orang-orang sombong.”</span></p><p><span>[d]</span><span> 39:73 ; 40:77.</span></p>');
INSERT INTO `quran` VALUES (8293,2,16,31,'<p><span>Dan dikatakan kepada orang-orang yang bertakwa, “Betapa </span><span>mulianya wahyu</span><span> yang telah diturunkan Tuhan-mu.” Mereka berkata, “Paling baik.” </span><span>[a]</span><span> Bagi orang-orang yang berbuat kebaikan di dunia ini ada </span><span>kehidupan</span><span> baik dan </span><span>[b]</span><span> tempat tinggal di akhirat itu adalah lebih baik. Dan sesungguhnya sebaik-baik tempat tinggal orang-orang yang bertakwa.</span></p><p><span>[a]</span><span> 39:11.</span></p><p><span>[b]</span><span> 6:33 ; 12:110.</span></p>');
INSERT INTO `quran` VALUES (8294,2,16,32,'<p><span>[c]</span><span> Kebun-kebun abadi yang akan mereka memasukinya, mengalir di bawahnya sungai-sungai, bagi mereka di dalamnya apa yang mereka kehendaki. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1542">1542</a><span>]</span><span> Demikianlah Allah memberi ganjaran kepada orang-orang yang bertakwa.</span></p><p><span>[c]</span><span> 9:72 ; 13:24 ; 35:34 ; 61:13 ; 98:9.</span></p>');
INSERT INTO `quran` VALUES (8295,2,16,33,'<p><span>Orang-orang yang diwafatkan oleh malaikat dalam keadaan suci, mereka </span><span>[d]</span><span> berkata, “Selamat sejahtera atas kamu ! Masukilah surga disebabkan apa yang kamu kerjakan.”</span></p><p><span>[d]</span><span> 10:11 ; 13:25 ; 36:59 ; 39:74.</span></p>');
INSERT INTO `quran` VALUES (8296,2,16,34,'<p><span>[e]</span><span> Apakah yang mereka nanti-nantikan selain bahwa akan datang kepada mereka malaikat-malaikat </span><span>dengan azab</span><span> atau datang keputusan Tuhan engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1543">1543</a><span>]</span><span> Demikianlah telah diamalkan oleh orang-orang sebelum mereka. </span><span>[f]</span><span> Dan Allah tidak aniaya terhadap mereka, akan tetapi mereka sendiri menganiaya diri mereka.</span></p><p><span>[e]</span><span> 2:211 ; 6:159 ; 7:54.</span></p><p><span>[f]</span><span> 9:70 ; 16:119 ; 29:41 ; 30:10.</span></p>');
INSERT INTO `quran` VALUES (8297,2,16,35,'<p><span>Maka telah menimpa mereka keburukan-keburukan akibat perbuatan mereka, dan </span><span>azab</span><span> telah mengepung mereka apa </span><span>[a]</span><span> yang senantiasa meeka perolokkan mengenai itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1544">1544</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8298,2,16,36,'<p><span>R. 5</span></p><p><span>[b]</span><span> Dan berkata orang-orang musyrik, “Sekiranya Allah menghendaki, kami tentu tidak akan menyembah apa pun selain-Nya, baik kami maupun bapak-bapak kami, dan kami tidak mengharamkan sesuatu tanpa </span><span>perintah</span><span> dari-Nya.” Demikianlah yang telah dikerjakan oleh orang-orang sebelum mereka. Maka </span><span>[c]</span><span> apakah atas rasul-rasul itu </span><span>ada kewajiban lain </span><span>selain menyampaikan dengan jelas ?</span></p><p><span>[b]</span><span> 6:149 ; 43:21.</span></p><p><span>[c]</span><span> 5:93, 100 ; 24:55 ; 29:19 ; 36:18.</span></p>');
INSERT INTO `quran` VALUES (8299,2,16,37,'<p><span>Dan sesungguhnya </span><span>[d]</span><span> Kami mengutus dalam setiap umat seorang rasul, supaya kamu menyembah Allah dan jauhilah orang yang melampaui batas. </span><span>[e]</span><span> Maka, sebagian dari mereka ada yang diberi petunjuk oleh Allah dan sebagian dari mereka ada yang telah pasti atas mereka kesesatan. </span><span>[f]</span><span> Maka berjalanlah kamu di bumi, lalu lihatlah betapa akibatnya orang-orang yang telah mendustakan </span><span>rasul-rasul.</span><span> </span></p><p><span>[d]</span><span> 10:48 ; 13:8 ; 35:25.</span></p><p><span>[e]</span><span> 7:31.</span></p><p><span>[f] </span><span>3:138 ; 6:12.</span></p>');
INSERT INTO `quran` VALUES (8300,2,16,38,'<p><span>[a]</span><span> Jika engkau sangat berhasrat, supaya mereka mendapat petunjuk, maka sesungguhnya Allah tidak akan memberi petunjuk kepada orang-orang yang menyesatkan, dan bagi mereka itu tidak ada penolong.</span></p><p><span>[a]</span><span> 12:104 ; 28:57.</span></p>');
INSERT INTO `quran` VALUES (8301,2,16,39,'<p><span>Dan mereka bersumpah dengan </span><span>nama</span><span> Allah sekuat-kuat sumpah mereka, </span><span>[b]</span><span> </span><span>bahwa</span><span> Allah tidak akan membangkitkan orang-orang mati. </span><span>[c]</span><span> Tidak demikian, </span><span>inilah </span><span> janji yang </span><span>untuk memenuhinya</span><span> menjadi hak kewajiban-Nya, akan tetapi kebanyakan manusia tidak mengetahui.</span></p><p><span>[b]</span><span> 23:38 ; 45:25.</span></p><p><span>[c]</span><span> 10:5 ; 21:105.</span></p>');
INSERT INTO `quran` VALUES (8302,2,16,40,'<p><span>Dia akan membangkitkan mereka</span><span> agar Dia dapat menjelaskan kepada mereka tentang apa yang diperselisihkannya, dan supaya mengetahui orang-orang yang ingkar, bahwa meeka itu pendusta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1545">1545</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8303,2,16,41,'<p><span>Sesungguhnya </span><span>[d]</span><span> ucapan Kami berkenaan dengan sesuatu, apabila Kami menghendakinya, Kami hanya berkata kepadanya, “Jadilah,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1546">1546</a><span>]</span><span> maka jadilah ia.</span></p><p><span>[d]</span><span> 2:118 ; 3:48 ; 36:83 ; 40:69.</span></p>');
INSERT INTO `quran` VALUES (8304,2,16,42,'<p><span>R. 6</span></p><p><span>Dan </span><span>[a]</span><span> orang-orang yang berhijrah karena Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1547">1547</a><span>]</span><span> setelah mereka dianiaya, tentu akan Kami berikan kepada mereka di dunia tempat tinggal yang baik. Dan sesungguhnya ganjaran akhirat itu lebih besar ; sekiranya mereka mengetahui,</span></p><p><span>[a]</span><span> 2:219 ; 4 :101 ; 22:59.</span></p>');
INSERT INTO `quran` VALUES (8305,2,16,43,'<p><span>[b]</span><span> </span><span>Yaitu</span><span> orang-orang yang bersabar dan bertawakkal kepada Tuhan mereka.</span></p><p><span>[b]</span><span> 29:60.</span></p>');
INSERT INTO `quran` VALUES (8306,2,16,44,'<p><span>[c]</span><span> Dan Kami tidak mengutus </span><span>rasul-rasul</span><span> sebelum engkau melainkan laki-laki yang telah Kami berikan wahyu kepada mereka, maka tanyakanlah kepada orang-orang ahli Zikir, </span><span>Alquran,</span><span> jika kamu tidak tahu,</span></p><p><span>[c]</span><span> 12:110 ; 21:8.</span></p>');
INSERT INTO `quran` VALUES (8307,2,16,45,'<p><span>[d]</span><span> Dengan Tanda-tanda yang nyata dan lembaran-lembaran berisikan wahyu. Dan </span><span>[e]</span><span> Kami telah menurunkan kepada engkau Zikir, </span><span>Alquran</span><span>, supaya engkau dapat menerangkan kepada manusia, apa yang telah diturunkan kepada mereka, agar mereka berfikir.</span></p><p><span>[d]</span><span> 35:26.</span></p><p><span>[e]</span><span> 3:59 ; 15:7, 10 ; 20:100.</span></p>');
INSERT INTO `quran` VALUES (8308,2,16,46,'<p><span>Lalu, </span><span>[f]</span><span> apakah merasa aman orang-orang yang membuat rencana jahat, bahwa Allah akan menghinakan mereka di negeri-nya atau akan datang kepada mereka azab dari arah yang sama sekali mereka tidak sadari ?</span></p><p><span>[f]</span><span> 6:66 ; 17;69 ; 34:10 ; 67:17, 18.</span></p>');
INSERT INTO `quran` VALUES (8309,2,16,47,'<p><span>Atau Dia akan membinasakan mereka dalam perjalanan kian kemari mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1548">1548</a><span>]</span><span> maka mereka tidak akan dapat terhindar.</span></p>');
INSERT INTO `quran` VALUES (8310,2,16,48,'<p><span>Atau Dia akan membinasakan mereka dengan berangsur-angsur ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1549">1549</a><span>]</span><span> Maka sesungguh nya Tuhan kamu itu Maha Penyantun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (8311,2,16,49,'<p><span>Apakah mereka tidak memperhatikan segala sesuatu yang telah diciptakan Allah, yang bayangan-bayangannya bergeser dari kanan dan kiri dalam keadaan sujud kepada Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1550">1550</a><span>] </span><span>sedangkan mereka dalam keadaan hina.</span></p>');
INSERT INTO `quran` VALUES (8312,2,16,50,'<p><span>Dan kepada Allah bersujud </span><span>[a]</span><span> apa yang ada di seluruh langit dan apa yang ada di bumi, dari binatang melata, dan para malaikat, dan mereka tidak berlaku sombong.</span></p><p><span>[a]</span><span> 13:16 ; 22:19.</span></p>');
INSERT INTO `quran` VALUES (8313,2,16,51,'<p><span>Mereka takut kepada Tuhan mereka yang ada di atas mereka, dan mereka </span><span>[b]</span><span> mengerjakan apa yang diperintahkan kepada mereka.</span></p><p><span>[b]</span><span> 66:7.</span></p>');
INSERT INTO `quran` VALUES (8314,2,16,52,'<p><span>R. 7</span></p><p><span>Dan Allah berfirman, “Janganlah kamu jadikan dua tuhan. </span><span>[c]</span><span> Sesungguhnya hanya Dia-lah Tuhan Yang Esa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1551">1551</a><span>]</span><span> Maka hanya kepada Aku kamu harus takut.”</span></p><p><span>[c]</span><span> 16:23.</span></p>');
INSERT INTO `quran` VALUES (8315,2,16,53,'<p><span>Dan kepunyaan-Nya segala apa yang ada di seluruh langit dan bumi dan </span><span>[d]</span><span> bagi Dia ketaatan itu untuk selama-lamanya. Apakah kepada selain Allah kamu bertakwa ?</span></p><p><span>[d]</span><span> 39:4.</span></p>');
INSERT INTO `quran` VALUES (8316,2,16,54,'<p><span>Dan </span><span>[e]</span><span> kenikmatan apa pun yang ada padamu, itu adalah dari Allah Kemudian apabila kamu ditimpa kemudaratan, maka hanya kepada-Nya kamu berseru minta pertolongan ; </span></p><p><span>[e]</span><span> 4:80 ; 10:13, 23 ; 23:65 ; 30:34 ; 39:9.</span></p>');
INSERT INTO `quran` VALUES (8317,2,16,55,'<p><span>[a]</span><span> Kemudian, apabila Dia menghilangkan kemudararan itu dari kamu, tiba-tiba segolongan dari kamu mulai mempersekutukan Tuhan-nya.</span></p><p><span>[a]</span><span> 10:13, 24 ; 29:66 ; 30:34 ; 39:9.</span></p>');
INSERT INTO `quran` VALUES (8318,2,16,56,'<p><span>[b]</span><span> Lalu mereka mengingkari apa yang telah Kami berikan kepada mereka. Maka bersenang-senanglah kamu sejenak, segera kamu akan mengetahui.</span></p><p><span>[b]</span><span> 29:67 ; 30:35.</span></p>');
INSERT INTO `quran` VALUES (8319,2,16,57,'<p><span>Dan </span><span>[c]</span><span> mereka menjadikan apa yang mereka tidak mengetahui sebagian bagi </span><span>tuhan-tuhan palsu</span><span> mereka dari apa yang telah Kami rezekikan kepada mereka. Demi Allah, kamu pasti akan ditanyai tentang apa yang telah kamu ada-adakan.</span></p><p><span>[c]</span><span> 6:137.</span></p>');
INSERT INTO `quran` VALUES (8320,2,16,58,'<p><span>Dan </span><span>[d]</span><span> mereka menjadikan bagi Allah anak-anak perempuan, Maha Suci Dia, sedang bagi mereka apa yang mereka inginkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1552">1552</a><span>]</span><span> </span><span>anak laki-laki.</span></p><p><span>[d]</span><span> 6:101 ; 37:153, 154 ; 43:17 ; 52:40 ; 53:22.</span></p>');
INSERT INTO `quran` VALUES (8321,2,16,59,'<p><span>Dan </span><span>[e]</span><span> apabila diberi khabar suka kepada salah seorang di antara mereka </span><span>mengenai kelahiran </span><span> seorang anak perempuan, maka mukanya menjadi hitam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1552a">1552a</a><span>]</span><span> dan dia menahan marah.</span></p><p><span>[e]</span><span> 43:18.</span></p>');
INSERT INTO `quran` VALUES (8322,2,16,60,'<p><span>Diam menyembunyikan diri dari orang-orang disebabkan khabar buruk yang telah di sampaikan kepadanya. Apakah ia akan memeliharanya meskipun dengan menanggung kehinaan, atau kah ia akan menguburnya di dalam tanah ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1553">1553</a><span>]</span><span> Ketahuilah, sangat buruk apa yang mereka putuskan.</span></p>');
INSERT INTO `quran` VALUES (8323,2,16,61,'<p><span>Bagi orang-orang yang tidak beriman kepada akhirat keadaannya buruk, dan bagi Allah </span><span>[a]</span><span> segala sifat yang agung. Dan Dia Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a]</span><span> 30:28.</span></p>');
INSERT INTO `quran` VALUES (8324,2,16,62,'<p><span>R. 8</span></p><p><span>Dan </span><span>[b]</span><span> jika Allah hendak menghukum manusia disebabkan kezaliman mereka, niscaya tidak akan Dia tinggalkan di atasnya sesuatu makhluk yang bernyawa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1554">1554</a><span>]</span><span> akan tetapi Dia menangguhkan mereka, hingga waktu yang ditentukan. Maka </span><span>[c]</span><span> apabila batas waktu mereka itu datang, maka mereka tidak dapat mengundurkan sesaat pun dan tidak pula mendahulukan.</span></p><p><span>[b]</span><span> 10:12 ; 18:59 ; 35:46.</span></p><p><span>[c]</span><span> 7:35 ; 10:50.</span></p>');
INSERT INTO `quran` VALUES (8325,2,16,63,'<p><span>Dan mereka menjadikan bagi Allah apa yang tidak mereka sukai dan lidah mereka mengucapkan kedustaan, bahwa mereka akan memperoleh yang terbaik. Tidak ada keraguan bahwa bagi merekalah Api dan sesungguhnya mereka akan dibiarkan </span><span>di dalamnya.</span></p>');
INSERT INTO `quran` VALUES (8326,2,16,64,'<p><span>Demi Allah, </span><span>[a]</span><span> sesungguhnya Kami telah mengirimkan </span><span>rasul-rasul</span><span> kepada umat-umat sebelum engkau ; </span><span>[b]</span><span> tetapi syaitan </span><span>menampakkan</span><span> perbuatan mereka indah bagi mereka. Maka ia menjadi pemimpin bagi mereka pada hari itu dan bagi merekalah azab yang pedih.</span></p><p><span>[a]</span><span> 6:43 ; 22:53.</span></p><p><span>[b]</span><span> 6:44 ; 6:49.</span></p>');
INSERT INTO `quran` VALUES (8327,2,16,65,'<p><span>Dan Kami tidak menurunkan kepada engkau Kitab ini, melainkan supaya engkau dapat menjelaskan kepada mereka mengenai apa yang mereka berselisih di dalamnya, dan supaya </span><span>[c]</span><span> menjadi petunjuk dan rahmat bagi kaum yang beriman.</span></p><p><span>[c]</span><span> 6:158 ; 12:112 ; 16:90.</span></p>');
INSERT INTO `quran` VALUES (8328,2,16,66,'<p><span>Dan </span><span>[a]</span><span> Allah telah menurunkan air dari langit, lalu Dia menghidupkan dengan itu bumi setelah matinya. Sesungguhnya dalam yang demikian itu ada Tanda bagi kaum yang mau mendengar.</span></p><p><span>[a]</span><span> 2:165 ; 13:18.</span></p>');
INSERT INTO `quran` VALUES (8329,2,16,67,'<p><span>67</span></p><p><span>R. 9</span></p><p><span>Dan </span><span>[b]</span><span> sesungguhnya bagi kamu pada binatang ternak ada sarana pelajaran. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1555">1555</a><span>]</span><span> Kami membari minum kamu dari apa yang ada dalam perutnya, </span><span>yang terdapat</span><span> dari antara kotoran dan darah, susu yang murni </span><span>dan</span><span> sedap untuk orang-orang yang minum.</span></p><p><span>[b]</span><span> 23:22.</span></p>');
INSERT INTO `quran` VALUES (8330,2,16,68,'<p><span>Dan </span><span>[c]</span><span> dari buah kurma dan anggur kamu jadikan minuman yang memabukkan dan rezeki yang baik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1555a">1555a</a><span>]</span><span> Sesungguhnya dalam hal itu adalah Tanda bagi kaum yang menggunakan akal.</span></p><p><span>[c]</span><span> 13:5 ; 16:12 ; 23:20 ; 36:35.</span></p>');
INSERT INTO `quran` VALUES (8331,2,16,69,'<p><span>Dan telah mewahyukan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1556">1556</a><span>]</span><span> Tuhan engkau kepada lebah, “Buatlah sarang-sarang di gunung-gunung, dan di pohon-pohon dan di tempat-tempat apa yang </span><span>manusia </span><span> bangun ;</span></p>');
INSERT INTO `quran` VALUES (8332,2,16,70,'<p><span>“Kemudian makanlah dari segala buah-buahan, dan tempuhlah jalan Tuhan engkau yang dimudahkan </span><span>bagimu.</span><span> “ Keluar dari perutnya minuman beraneka warnanya, di dalamnya ada obat penyembuh bagi manusia. Sesungguhnya dalam yang demikian itu ada Tanda bagi orang-orang yang berpikir. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1557">1557</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8333,2,16,71,'<p><span>Dan Allah menciptakan kamu, kemudian Dia mewafatkan kamu ; dan </span><span>[a]</span><span> dari antaramu ada yang dikembalikan kepada usia yang terlemah sehingga mereka tidak mengetahui sesuatu yang tadinya mereka mengetahuinya. Sesungguhnya, Allah Maha Mengetahui , Maha Kuasa.</span></p><p><span>[a]</span><span> 22:6.</span></p>');
INSERT INTO `quran` VALUES (8334,2,16,72,'<p><span>Dan </span><span>[b]</span><span> Allah telah melebihkan sebagian dari antara kamu di atas sebagian yang lain dalam rezeki. Tetapi, orang-orang yang dilebihkan itu tidak mau mengembalikan </span><span>sebagian</span><span> rezeki mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1558">1558</a><span>]</span><span> kepada orang-orang yang dimiliki oleh tangan kanan mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1559">1559</a><span>]</span><span> supaya mereka di dalamnya </span><span>memperoleh bagian</span><span> yang sama. Apakah mereka mengingkari nikmat Allah ? </span></p><p><span>[b]</span><span> 24:23 ; 30:29.</span></p>');
INSERT INTO `quran` VALUES (8335,2,16,73,'<p><span>Dan </span><span>[a]</span><span> Allah telah menjadikan bagi kamu isteri-isteri dari jenis kamu sendiri, dan menjadikan bagimu dari isteri-isterimu itu anak-anak dan cucu-cucu, dan telah memberikan rezeki kepadamu yang baik-baik. </span><span>[b]</span><span> Apakah mereka akan beriman kepada yang batil dan mengingkari nikmat Allah ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1560">1560</a><span>]</span></p><p><span>[a]</span><span> 4:2 ; 7:190 ; 30:22 ; 39:7.</span></p><p><span>[b]</span><span> 29:68.</span></p>');
INSERT INTO `quran` VALUES (8336,2,16,74,'<p><span>Dan </span><span>[c]</span><span> mereka menyembah selain Allah, yang tidak mempunyai kekuasaan bagi mereka untuk memberi rezeki dari seluruh langit dan bumi sedikit pun, dan mereka tidak akan mampu.</span></p><p><span>[c]</span><span> 10:19 ; 22:72 ; 29:18.</span></p>');
INSERT INTO `quran` VALUES (8337,2,16,75,'<p><span>Maka jangan kamu membuat persamaan-persamaan bagi Allah Sesungguhnya, Allah mengetahui sedangkan kamu tidak mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1561">1561</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8338,2,16,76,'<p><span>Allah mengadakan misal ; seorang hamba sayaha, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1562">1562</a><span>]</span><span> ia tidak mempunyai kekuasaan atas sesuatu pun, dan seorang yang telah Kami beri rezeki yang baik dari Kami, maka </span><span>[a]</span><span> ia membelanjakan darinya dengan sembunyi dan terang-terangan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1563">1563</a><span>]</span><span> Apakah mereka itu sama ? Segala puji bagi Allah Akan tetapi, kebanyakan dari mereka tidak mengetahui.</span></p><p><span>[a]</span><span> 2:275 ; 13:23.</span></p>');
INSERT INTO `quran` VALUES (8339,2,16,77,'<p><span>Dan Allah mengadakan perumpamaan </span><span>lain</span><span> tentang dua orang laki-laki, salah seorang dari antara mereka bisu, ia tidak berkuasa atas sesuatu pun, dan ia menjadi beban bagi tuannya ; kemana saja ia disuruh, ia tidak mendatangkan suatu kebaikan. Apakah orang itu sama dengan dia yang menyuruh berbuat adil sedang dia berada di atas jalan yang lurus ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1564">1564</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8340,2,16,78,'<p><span>R. 11</span></p><p><span>[a]</span><span> Dan kepunyaan Allah</span><span> ilmu</span><span> yang gaib </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1565">1565</a><span>]</span><span> di seluruh langit dan bumi. Dan </span><span>[b] </span><span>tidaklah urusan </span><span>kedatangan</span><span> saat yang </span><span>dijanjikan</span><span> itu melainkan bagaikan sekejap mata, atau malahan lebih dekat lagi. Sesungguhnya Allah Maha Kuasa atas segala sesuatu.</span></p><p><span>[a]</span><span> 11:124 ; 18:27 ; 35:39.</span></p><p><span>[b] </span><span>7:188 ; 54:51.</span></p>');
INSERT INTO `quran` VALUES (8341,2,16,79,'<p><span>Dan </span><span>[c]</span><span> Allah telah mengeluarkan kamu dari perut ibu-ibumu </span><span>dalam keadaan </span><span> kamu tidak mengetahui sedikit pun dan Dia </span><span>[d]</span><span> menjadikan bagi kamu telinga, mata, dan hati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1566">1566</a><span>]</span><span> supaya kamu bersyukur.</span></p><p><span>[c] </span><span>39:7.</span></p><p><span>[d]</span><span> 23:79 ; 67:24.</span></p>');
INSERT INTO `quran` VALUES (8342,2,16,80,'<p><span>[e]</span><span> Apakah mereka tidak memperhatikan kepada burung-burung yang ditugaskan di angkasa bebas ? Tiada yang menahannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1567">1567</a><span>]</span><span> kecuali Allah Sesungguhnya dalam yang demikian itu ada Tanda-tanda bagi kaum yang beriman.</span></p><p><span>[e]</span><span> 67:20.</span></p>');
INSERT INTO `quran` VALUES (8343,2,16,81,'<p><span>Dan Allah telah menjadikan bagimu dari rumah-rumahmu tempat tinggal dan juga telah menjadikan bagimu rumah-rumah dari kulit-kulit binatang ternak, yang kamu rasakan ringan ketika kamu dalam perjalanan dan </span><span>berguna</span><span> pada waktu kamu mukim ; dan juga dari bulunya yang halus dan bulu yang tebal dan rambutnya sebagai perlengkapan rumah tangga dan alat-alat keperluan sementara sampai suatu waktu .</span></p>');
INSERT INTO `quran` VALUES (8344,2,16,82,'<p><span>Dan Allah telah mengadakan bagimu dari apa yang telah Dia ciptakan sebagai naungan ; dan Dia telah menjadikan bagimu gunung-gunung sebagai tempat perlindungan ; dan Dia jadi kan bagimu baju yang melindungi kamu terhadap panas, dan baju besi yang melindungi kamu dalam peperangan. Demikianlah Dia menyempurnakan nikmat-Nya atasmu supaya kamu menyerahkan diri.</span></p>');
INSERT INTO `quran` VALUES (8345,2,16,83,'<p><span>[a]</span><span> Tetapi jika mereka itu berpaling maka kewajibanmu hanyalah menyampaikan </span><span>amanat</span><span> dengan jelas.</span></p><p><span>[a]</span><span> 3:21 ; 5:93.</span></p>');
INSERT INTO `quran` VALUES (8346,2,16,84,'<p><span>Mereka mengenal nikmat-nikmat Allah, kemudian mereka mengingkarinya ; dan kebanyakan mereka adalah orang-orang kafir.</span></p>');
INSERT INTO `quran` VALUES (8347,2,16,85,'<p><span>R. 12</span></p><p><span>[b]</span><span> Dan </span><span>ingatlah</span><span> hari bila Kami bangkitkan dari setiap umat seorang saksi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1568">1568</a><span>]</span><span> kemudian tidak akan diizinkan bagi orang-orang yang ingkar </span><span>untuk membela diri,</span><span> dan </span><span>[c]</span><span> alasan mereka tidak akan dikabulkan.</span></p><p><span>[b]</span><span> 4:42 ; 16:90.</span></p><p><span>[c]</span><span> 30:58 ; 41:25.</span></p>');
INSERT INTO `quran` VALUES (8348,2,16,86,'<p><span>[d]</span><span> Dan apabila orang-orang yang aniaya melihat azab, maka tidak akan diringankan bagi mereka dan mereka tidak akan diberi tangguh.</span></p><p><span>[d]</span><span> 2:166.</span></p>');
INSERT INTO `quran` VALUES (8349,2,16,87,'<p><span>Dan </span><span>[e]</span><span> apabila orang-orang yang mempersekutukan </span>Allah<span> itu melihat tuhan-tuhan sekutu mereka, mereka akan berkata, “Ya Tuhan kami, inilah tuhan-tuhan sekutu kamu yang kami biasa seru selain Engkau.” Maka sekutu-sekutunya akan berkata kepada mereka dengan mengatakan, “Sesungguhnya kamu adalah pendusta. ” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1569">1569</a><span>]</span></p><p><span>[e]</span><span> 30:14</span></p>');
INSERT INTO `quran` VALUES (8350,2,16,88,'<p><span>Dan </span><span>[a]</span><span> mereka menyatakan ketaatannya kepada Allah pada hari itu, dan akan hilanglah dari mereka apa yang dahulu mereka ada-adakan.</span></p><p><span>[a]</span><span> 16:29.</span></p>');
INSERT INTO `quran` VALUES (8351,2,16,89,'<p><span>[b]</span><span> </span><span>Adapun</span><span> orang-orang yang ingkar dan yang menghalang-halangi dari jalan Allah, akan Kami tambahi mereka azab di atas azab disebabkan mereka </span><span>selalu</span><span> membuat kerusuhan.</span></p><p><span>[b]</span><span> 7:46 ; 11:20 ; 14:4.</span></p>');
INSERT INTO `quran` VALUES (8352,2,16,90,'<p><span>[c]</span><span> Dan </span><span>ingatlah</span><span> hari itu, </span><span>ketika</span><span> Kami akan membangkitkan dalam setiap umat seorang saksi terhadap mereka dari antara mereka sendiri, dan Kami akan mendatangkan engkau sebagai saksi atas mereka semuanya. Dan </span><span>[d]</span><span> telah Kami turunkan kepada engkau Kitab untuk menjelaskan segala sesuatu dan petunjuk dan rahmat dan khabar suka bagi orang-orang yang menyerahkan diri.</span></p><p><span>[c]</span><span> 4:42 ; 16:85.</span></p><p><span>[d]</span><span> 10:38 ; 12:112.</span></p>');
INSERT INTO `quran` VALUES (8353,2,16,91,'<p><span>R. 13</span></p><p><span>Sesungguhnya Allah menyuruh berlaku adil dan berbuat kebaikan dan memberi kepada kaum kerabat ; dan melarang dari perbuatan keji, dan hal yang tidak disenangi, dan memberontak. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1570">1570</a><span>]</span><span> Dia memberi kamu nasihat supaya kamu mengambil pelajaran.</span></p>');
INSERT INTO `quran` VALUES (8354,2,16,92,'<p><span>Dan </span><span>[a]</span><span> sempurnakanlah perjanjian dengan Allah apabila kamu telah berjanji, dan janganlah kamu melanggar sumpah-sumpah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1571">1571</a><span>]</span><span> setelah diteguhkannya, padahal telah kamu jadikan Allah sebagai jaminan atas kamu. Sesungguhnya Allah mengetahui apa yang kamu kerjakan.</span></p><p><span>[a]</span><span> 6:153 ; 13:21 ; 17:35.</span></p>');
INSERT INTO `quran` VALUES (8355,2,16,93,'<p><span>Dan janganlah kamu menjadikan seperti seorang perempuan, yang memutus-mutuskan benangnya sesudah kuat menjadi berpotong- potong, kamu </span><span>[a]</span><span> jadikan sumpah-sumpahmu sebagai penipuan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1572">1572</a><span>]</span><span> di antaramu </span><span>karena takut</span><span> jangan-jangan suatu kaum akan menjadi lebih kuat dari kaum yang lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1573">1573</a><span>]</span><span> Sesungguhnya, Allah menguji kamu dengan itu dan pada Hari Kiamat Dia akan menjadikan jelas kepadamu apa yang kamu telah berselisih di dalamnya.</span></p><p><span>[a]</span><span> 16:95.</span></p>');
INSERT INTO `quran` VALUES (8356,2,16,94,'<p><span>[a]</span><span> Dan jika Allah menghendaki, Dia pasti akan menjadikan kamu </span><span>sekalian</span><span> satu umat, akan tetapi Dia membiarkan orang yang menghendaki kesesatan itu tersesat, dan memberi petunjuk kepada orang yang Dia kehendaki dan niscaya kamu akan ditanyai tentang apa yang telah kamu kerjakan.</span></p><p><span>[a]</span><span> 5:49 ; 11:119</span></p>');
INSERT INTO `quran` VALUES (8357,2,16,95,'<p><span>Dan janganlah kamu menjadikan sumpah-sumpahmu sebagai cara penipuan di antara kamu ; maka kakimu akan tergelincir setelah berdiri tegak sesudah kokohnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1574">1574</a><span>]</span><span> dan akan kamu rasakan keburukan itu, karena kamu telah menghalangi </span><span>orang-orang</span><span> dari jalan Allah, dan bagimu ada azab yang sangat besar.</span></p>');
INSERT INTO `quran` VALUES (8358,2,16,96,'<p><span>[b]</span><span> Dan janganlah kamu menukar janji Allah dengan harga sedikit, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1575">1575</a><span>]</span><span> Sesungguh nya apa yang di sisi Allah itulah yang lebih baik bagimu jika kamu mengetahui.</span></p><p><span>[b]</span><span> 3:78.</span></p>');
INSERT INTO `quran` VALUES (8359,2,16,97,'<p><span>Apa yang ada padamu akan punah, dan apa yang ada si sisi Allah kekal. </span><span>[a]</span><span> Dan niscaya Kami akan memberi kepada orang-orang yang bersabar ganjaran mereka lebih baik sesuai apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 11:12 ; 39:11.</span></p>');
INSERT INTO `quran` VALUES (8360,2,16,98,'<p><span>[b]</span><span> Barangsiapa berbuat amal shaleh dari antara laki-laki maupun perempuan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1576">1576</a><span>]</span><span> dan ia adalah orang beriman, tentulah Kami akan memberikan kehidupan yang suci ; dan niscaya Kami akan melimpahkan kepada mereka ganjaran mereka lebih baik, sesuai apa yang mereka kerjakan.</span></p><p><span>[b]</span><span> 3:196 ; 4:125 ; 20:113.</span></p>');
INSERT INTO `quran` VALUES (8361,2,16,99,'<p><span>Maka apabila engkau hendak membaca Alquran maka mohonlah perlindungan Allah dari syaitan yang terkutuk.</span></p>');
INSERT INTO `quran` VALUES (8362,2,16,100,'<p><span>[c]</span><span> Sesungguhnya ia tidak mempunyai kekuasaan atas orang-orang yang beriman dan yang bertawakkal kepada Tuhan mereka.</span></p><p><span>[c]</span><span> 15:43 ; 17:66 ; 34:22.</span></p>');
INSERT INTO `quran` VALUES (8363,2,16,101,'<p><span>[d]</span><span> Sesungguhnya kekuasaannya atas orang-orang yang bersahabat dengannya dan orang-orang yang dengannya mereka persekutukan.</span></p><p><span>[d]</span><span> 2:258 ; 3:176 ; 7:28.</span></p>');
INSERT INTO `quran` VALUES (8364,2,16,102,'<p><span>R. 14</span></p><p><span>Dan </span><span>[e]</span><span> apabila Kami mengganti suatu Ayat ke tempat Ayat yang lain, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1577">1577</a><span>]</span><span> dan Allah lebih mengetahui </span><span>tujuan dari </span><span> apa yang Dia turunkan, mereka berkata, “Sesungguhnya engkau hanyalah seorang yang mengada-ada.” Tidak, bahkan kebanyakan mereka tidak mengetahui.</span></p><p><span>[e]</span><span> 2:107.</span></p>');
INSERT INTO `quran` VALUES (8365,2,16,103,'<p><span>Katakanlah, “Telah menurunkannya </span><span>[a]</span><span> Ruhulqudus dari Tuhan engkau dengan hak, supaya Dia mengokohkan orang-orang yang beriman ; dan juga </span><span>[b]</span><span> sebagai petunjuk dan khabar suka bagi orang-orang yang menyerahkan diri.”</span></p><p><span>[a]</span><span> 2:98 ; 26:194.</span></p><p><span>[b]</span><span> 12:112.</span></p>');
INSERT INTO `quran` VALUES (8366,2,16,104,'<p><span>Dan sesungguhnya Kami mengetahui bahwa mereka berkata, “Sesungguhnya hanya seorang manusia yang mengajarnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1578">1578</a><span>]</span><span> </span><span>Padahal</span><span> bahasa orang yang mereka tuduhkan mengajarkannya tidak fasih, dan </span><span>Alquran</span><span> ini adalah </span><span>bahasa </span><span> Arab yang jelas.</span></p>');
INSERT INTO `quran` VALUES (8367,2,16,105,'<p><span>Sesungguhnya, orang-orang yang tidak beriman kepada Ayat-ayat Allah, Allah tidak akan memberi petunjuk kepada mereka, dan bagi mereka azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (8368,2,16,106,'<p><span>Sesungguhnya mereka yang mengada-adakan dusta hanyalah orang -orang yang tidak beriman kepada Ayat-ayat Allah ; dan mereka itulah pendusta.</span></p>');
INSERT INTO `quran` VALUES (8369,2,16,107,'<p><span>[a]</span><span> Barangsiapa yang ingkar kepada Allah setelah ia beriman, kecuali orang yang telah dipaksa sedang hatinya tetap tenteram dalam keimanan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1579">1579</a><span>]</span><span> akan tetapi orang yang membukakan dadanya untuk kekufuran, maka atas mereka kemurkaan dari Allah dan bagi mereka azab yang besar.</span></p><p><span>[a]</span><span> 3:91 ; 4:138 ; 63:4.</span></p>');
INSERT INTO `quran` VALUES (8370,2,16,108,'<p><span>[b]</span><span> Yang demikian itu disebabkan mereka lebih mencintai kehidupan dunia dari pada akhirat, dan sesungguhnya Allah tidak memberi petunjuk kepada kaum kafir.</span></p>');
INSERT INTO `quran` VALUES (8371,2,16,109,'<p><span>[c]</span><span> Mereka itulah orang-orang yang Allah telah mencap hatinya, telinganya, dan mata nya. Dan mereka itulah orang-orang yang lalai.</span></p><p><span>[c]</span><span> 2:8 ; 4:156 ; 7:180.</span></p>');
INSERT INTO `quran` VALUES (8372,2,16,110,'<p><span>[d]</span><span> Tiada syak lagi bahwa mereka di akhirat adalah orang-orang yang rugi.</span></p><p><span>[d]</span><span> 11:23.</span></p>');
INSERT INTO `quran` VALUES (8373,2,16,111,'<p><span>[e]</span><span> Kemudian sesungguhnya Tuhan engkau, terhadap orang-orang yang berhijrah sesudah mereka menderita cobaan, kemudian mereka itu berjuang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1580">1580</a><span>]</span><span> keras </span><span>di jalan Allah</span><span> dan tetap bersabar, sesungguhnya Tuhan engkau sesudah itu adalah Maha Pengampun, Maha Penyayang.</span></p><p><span>[e]</span><span> 2:219.</span></p>');
INSERT INTO `quran` VALUES (8374,2,16,112,'<p><span>R. 15</span></p><p><span>Pada hati </span><span>ketika</span><span> setiap jiwa akan datang untuk membela dirinya, dan akan diberi </span><span>balasan</span><span> sempurna </span><span>[a]</span><span> setiap jiwa atas apa yang telah dikerjakan dan mereka tidak dianiaya.</span></p><p><span>[a]</span><span> 2:282.</span></p>');
INSERT INTO `quran` VALUES (8375,2,16,113,'<p><span>[b]</span><span> Dan Allah mengemukakan perumpamaan sebuah kota </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1581">1581</a><span>]</span><span> yang aman sentosa ; rezekinya datang kepadanya berlimpah-limpah dari segala jurusan ; tetapi tidak bersyukur atas nikmat Allah ; maka Allah mencicipkan kepadanya rasa pakaian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1582">1582</a><span>]</span><span> kelaparan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1583">1583</a><span>]</span><span> dan ketakutan disebabkan oleh apa yang selalu mereka kerjakan.</span></p><p><span>[b]</span><span> 34:16, 17.</span></p>');
INSERT INTO `quran` VALUES (8376,2,16,114,'<p><span>Dan sesungguhnya telah datang kepada mereka seorang rasul dari antara mereka, tetapi mereka mendustakannya, maka azab telah menyergap mereka ketika mereka berbuat aniaya.</span></p>');
INSERT INTO `quran` VALUES (8377,2,16,115,'<p><span>[a]</span><span> Maka makanlah dari apa-apa yang Allah telah rezekikan kepadamu yang halal dan baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1584">1584</a><span>]</span><span> dan syukurilah nikmat Allah, jika hanya kepada-Nya kamu menyembah.</span></p><p><span>[a]</span><span> 2:169 ; 5:89 ; 8:70.</span></p>');
INSERT INTO `quran` VALUES (8378,2,16,116,'<p><span>[b]</span><span> Sesungguhnya, Dia hanya mengharamkan atas kamu bangkai dan darah, daging babi, dan apa yang disembelih dengan menyebut nama selain Allah Tetapi, barangsiapa terpaksa </span><span>memakannya</span><span> bukan melanggar peraturan dan tidak melampaui batas, maka sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[b]</span><span> 2:174 ; 5:4 ; 6:146.</span></p>');
INSERT INTO `quran` VALUES (8379,2,16,117,'<p><span>[c]</span><span> Dan janganlah kamu berkata disebabkan oleh kedustaan yang diucapkan oleh lidahmu, “Ini halal dan ini haram,” supaya </span><span>jangan</span><span> kamu mengada-ada dusta terhadap Allah, Sesungguhnya, orang-orang yang mengada-ada dusta terhadap Allah tidak akan memperoleh kemenangan.</span></p><p><span>[c]</span><span> 4:18 ; 6:55.</span></p>');
INSERT INTO `quran` VALUES (8380,2,16,118,'<p><span>[a]</span><span> </span><span>Kehidupan ini hanya </span><span> kesenangan yang sedikit dan bagi mereka azab yang pedih.</span></p><p><span>[a]</span><span> 3:198 ; 4:78.</span></p>');
INSERT INTO `quran` VALUES (8381,2,16,119,'<p><span>Dan kepada orang-orang Yahudi telah Kami haramkan segala yang telah Kami ceritakan kepada engkau sebelum ini. Dan </span><span>[b]</span><span> Kami tidak menganiaya mereka, akan tetapi merekalah yang telah menganiaya diri mereka sendiri.</span></p><p><span>[b]</span><span> 11:102 ; 16:34.</span></p>');
INSERT INTO `quran` VALUES (8382,2,16,120,'<p><span>Kemudian, sesungguhnya </span><span>[c]</span><span> Tuhan engkau kepada orang-orang yang berbuat keburukan karena kebodohan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1585">1585</a><span>]</span><span> kemudian mereka bertobat sesudah itu dan memperbaiki diri. Sesungguhnya Tuhan engkau sesudah itu adalah Maha Pengampun, Maha Penyayang.</span></p><p><span>[c]</span><span> 4:18. 6:55.</span></p>');
INSERT INTO `quran` VALUES (8383,2,16,121,'<p><span>R. 16</span></p><p><span>Sesungguhnya, Ibrahim adalah seorang yang memiliki semua sifat yang baik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1586">1586</a><span>] [d]</span><span> taat kepada Allah, hatinya selalu condong kepada-Nya. Dan bukanlah ia termasuk orang-orang musyrik.</span></p><p><span>[d]</span><span> 2:136 ; 3:68 ; 6:80.</span></p>');
INSERT INTO `quran` VALUES (8384,2,16,122,'<p><span>Senantiasa</span><span> bersyukur atas nikmat-nikmat-Nya. </span><span>[e]</span><span> Dia telah memilihnya dan membimbing ke jalan yang lurus.</span></p><p><span>[e]</span><span> 2:131.</span></p>');
INSERT INTO `quran` VALUES (8385,2,16,123,'<p><span>Dan telah </span><span>[a]</span><span> Kami berikan kepadanya kebaikan di dunia. Dan sesungguhnya ia di akhirat akan termasuk orang-orang shaleh.</span></p><p><span>[a]</span><span> 2:131 ; 29:28</span></p>');
INSERT INTO `quran` VALUES (8386,2,16,124,'<p><span>Kemudian Kami wahyukan kepada engkau, </span><span>[b]</span><span> “Agar ikutilah agama Ibrahim yang senantiasa condong kepada Allah ; dan dia tidak termasuk orang-orang musyrik.”</span></p><p><span>[b]</span><span> 2:136 ; 4:125 ; 22:79.</span></p>');
INSERT INTO `quran` VALUES (8387,2,16,125,'<p><span>[c]</span><span> Sesungguhnya diwajibkan </span><span>hukuman</span><span> Sabat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1587">1587</a><span>]</span><span> atas orang- orang yang berselisih di dalamnya. Dan sesungguhnya </span><span>[d]</span><span> Tuhan engkau pasti akan mengadili antara mereka pada Hari Kiamat mengenai apa yang di dalamnya mereka perselisikan.</span></p><p><span>[c] </span><span>2:66 ; 4:48. 155.</span></p><p><span>[d]</span><span> 3:56 ; 22:70.</span></p>');
INSERT INTO `quran` VALUES (8388,2,16,126,'<p><span>Panggilah kepada jalan Tuhan engkau dengan bijaksana </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1588">1588</a><span>]</span><span> dan nasihat yang baik, dan </span><span>[e]</span><span> bertukar-pikiranlah dengan mereka, dengan cara yang sebaik-baiknya. Sesungguhnya </span><span>[f]</span><span> Tuhan engkau Dia lebih mengetahui siapa yang telah sesat dari jalan-Nya ; dan Dia Maha Mengetahui siapa yang mendapat petunjuk.</span></p><p><span>[e]</span><span> 41:35.</span></p><p><span>[f]</span><span> 6:118.</span></p>');
INSERT INTO `quran` VALUES (8389,2,16,127,'<p><span>[a]</span><span> Dan jika kamu memutuskan akan menghukum, maka hukumlah mereka setimpal dengan kesalahan yang dilakukan terhadap kamu. </span><span>[b]</span><span> Tetapi, jika kamu bersabar maka sesungguhnya itulah yang lebih baik bagi orang-orang sabar.</span></p><p><span>[a]</span><span> 42:41.</span></p><p><span>[b]</span><span> 42:44.</span></p>');
INSERT INTO `quran` VALUES (8390,2,16,128,'<p><span>Dan </span><span>ya Rasul,</span><span> bersabarlah engkau, dan tidaklah kesabaran engkau kecuali dengan </span><span>pertolongan </span>Allah <span>[c]</span><span> Dan janganlah engkau bersedih atas mereka dan janganlah engkau bersempit dada disebabkan apa yang mereka rencanakan.</span></p><p><span>[c]</span><span> 15:89, 98 ; 27:71.</span></p>');
INSERT INTO `quran` VALUES (8391,2,16,129,'<p><span>[d]</span><span> Sesungguhnya Allah beserta orang-orang yang bertakwa dan orang-orang yang berbuat kebaikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1589">1589</a><span>]</span></p><p><span>[d]</span><span> 45:20.</span></p>');
INSERT INTO `quran` VALUES (8392,2,17,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8393,2,17,2,'<p><span>Maha Suci Dia, Yang telah menjalankan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1590">1590</a><span>]</span><span> hamba-Nya pada waktu malam dari Masjid Haram ke Masjid Aqsha, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1591">1591</a><span>]</span><span> yang telah Kami berkati, </span><span>[b]</span><span> sekelilingnya supaya Kami perlihatkan kepadanya sebagaian dari Tanda-tanda Kami. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1591a">1591a</a><span>]</span><span> Sesungguhnya Dia, Yang Maha Mendengar, Maha Melihat.</span></p><p><span>[b]</span><span> 5:22 ; 7:138.</span></p>');
INSERT INTO `quran` VALUES (8394,2,17,3,'<p><span>Dan </span><span>[a]</span><span> Kamu telah memberi kepada Musa kitab, dan Kami jadikan itu petunjuk bagi Bani Israil, </span><span>[b]</span><span> “Janganlah kamu mengambil pelindung selain Aku.</span></p><p><span>[a]</span><span> 2:54, 88 ; 23:50 ; 32:24 ; 40:54.</span></p><p><span>[b]</span><span> 17:69.</span></p>');
INSERT INTO `quran` VALUES (8395,2,17,4,'<p><span>“Hai, keturunan orang-orang </span><span>[a]</span><span> yang Kami naikkan </span><span>kedalam bahtera</span><span> beserta Nuh !” Sesungguhnya ia seorang hamba yang banyak bersyukur.</span></p><p><span>[a]</span><span> 19:59 ; 23:28.</span></p>');
INSERT INTO `quran` VALUES (8396,2,17,5,'<p><span>Dan Kami telah tetapkan kepada Bani Israil dalam kitab itu, “Pasti kamu akan melakukan kerusakan di bumi dua kali, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1592">1592</a><span>]</span><span> dan niscaya kamu akan menyombongkan diri dengan kesombongan yang sangat besar.”</span></p>');
INSERT INTO `quran` VALUES (8397,2,17,6,'<p><span>Maka apabila datang janji pertama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1593">1593</a><span>]</span><span> dari kedua itu, Kami bangkitkan untuk menghadapimu hamba-hamba Kami yang mempunyai kekuatan tempur yang dahsyat, maka mereka menerobos jauh ke dalam rumah-rumah. Dan itu suatu janji yang pasti akan terjadi.</span></p>');
INSERT INTO `quran` VALUES (8398,2,17,7,'<p><span>Kemudian Kami kembalikan kepadamu kekuatan untuk mengalahkan mereka, dan Kami bantu kamu dengan harta dan anak-anak, dan Kami jadikan kamu kelompok yang lebih besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1594">1594</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8399,2,17,8,'<p><span>[a]</span><span> Jika kamu berbuat baik kamu berbuat baik bagi dirimu sendiri ; dan jika kamu berbuat buruk, maka itu untuk dirimu sendiri. Maka bila datang janji kedua itu, supaya mereka mendatangkan kesusahan kepada pemimpin-pemimpin terhormat kamu ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1594a">1594a</a><span>]</span><span> dan supaya mereka memasuki masjid seperti pernah mereka memasukinya pada kali pertama ; dan supaya mereka menghancurlulukan segala yang telah mereka kuasai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1595">1595</a><span>]</span></p><p><span>[a]</span><span> 4:124, 125 ; 6:161 ; 28:85 ; 41:47 ; 99:8, 9.</span></p>');
INSERT INTO `quran` VALUES (8400,2,17,9,'<p><span>Boleh jadi Tuhan-mu akan menaruh kasihan kepadamu ; tetapi jika kamu kembali </span><span>kepada perbuatan buruk,</span><span> Kami pun akan kembali </span><span>menimpakan hukuman ;</span><span> dan Kami jadikan Jahannam , bagi orang-orang kafir sebagai penjara.</span></p>');
INSERT INTO `quran` VALUES (8401,2,17,10,'<p><span>Sesungguhnya, </span><span>[a]</span><span> Alquran ini memberi petunjuk kepada apa yang paling benar ; dan memberi khabar suka kepada orang-orang mukmin yag berbuat amal shaleh bahwa bagi mereka ada ganjaran yang besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1596">1596</a><span>]</span></p><p><span>[a]</span><span> 12:112 ; 16:103 ; 18:3.</span></p>');
INSERT INTO `quran` VALUES (8402,2,17,11,'<p><span>Dan bahwa </span><span>[b]</span><span> orang-orang yang tidak beriman kepada akhirat Kami telah sediakan bagi mereka azab yang sangat pedih.</span></p><p><span>[b]</span><span> 16:23 ; 27:5 ; 34:9.</span></p>');
INSERT INTO `quran` VALUES (8403,2,17,12,'<p><span>Dan </span><span>[c]</span><span> manusia memanggil kepada keburukan seperti panggilannya kepada kebaikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1597">1597</a><span>]</span><span> Dan manusia itu sangat tergesa-gesa.</span></p><p><span>[c]</span><span> 10:12.</span></p>');
INSERT INTO `quran` VALUES (8404,2,17,13,'<p><span>Dan </span><span>[a]</span><span> Kami menjadikan malam dan siang dua Tanda, lalu Kami hapuskan Tanda malam, dan Kami jadikan Tanda siang terang supaya kamu dapat mencari karunia dari Tuhan-mu, dan </span><span>[b]</span><span> supaya kamu mengetahui bilangan tahun dan </span><span>ilmu</span><span> hisab. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1598">1598</a><span>]</span><span> Dan segala sesuatu telah Kami terangkan dengan keterangan yang rinci.</span></p><p><span>[a]</span><span> 36:38 ; 40:62 ; 41:38.</span></p><p><span>[b]</span><span> 10:6.</span></p>');
INSERT INTO `quran` VALUES (8405,2,17,14,'<p><span>Dan setiap manusia, Kami </span><span>[c]</span><span> gantungkan amal-amalnya pada lehernya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1599">1599</a><span>]</span><span> Dan akan Kami keluarkan baginya pada Hari Kiamat sebuah buku yang akan didapatinya terbuka lebar.</span></p><p><span>[c]</span><span> 45:29 ; 83:7 </span><span>–</span><span> 10.</span></p>');
INSERT INTO `quran` VALUES (8406,2,17,15,'<p><span>[d]</span><span> Bacalah bukumu Cukuplah dirimu sendiri pada hari ini sebagai penghisap bagimu.</span></p><p><span>[d]</span><span> 17:72 ; 45:30 ; 69:20, 26, 27.</span></p>');
INSERT INTO `quran` VALUES (8407,2,17,16,'<p><span>[a]</span><span> Barangsiapa telah menerima petunjuk, maka sesungguhnya petunjuk itu untuk dirinya, dan barangsiapa sesat, maka kesesatan itu hanya atas dirinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1600">1600</a><span>]</span><span> Dan </span><span>[b]</span><span> tiada pemikul beban akan memikul beban orang lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1601">1601</a><span>]</span><span> Dan </span><span>[c]</span><span> Kami tidak akan mengazab sebelum Kami mengirimkan seorang rasul. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1602">1602</a><span>]</span></p><p><span>[a]</span><span> 10:109 ; 39:42.</span></p><p><span>[b]</span><span> 6:165 ; 35:19 ; 39:8 ; 53:39.</span></p><p><span>[c]</span><span> 28:60.</span></p>');
INSERT INTO `quran` VALUES (8408,2,17,17,'<p><span>Dan </span><span>[d]</span><span> apabila Kami hendak membinasakan suatu kota, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1603">1603</a><span>]</span><span> Kami memerintahkan </span><span>berbuat baik</span><span> kepada warganya yang hidup mewah, tetapi mereka durhaka di dalamnya;maka sempurnalah atasnya keputusan </span><span>Kami,</span><span> maka Kami menghancurkannya.</span></p><p><span>[d]</span><span> 22:46 ; 28:59.</span></p>');
INSERT INTO `quran` VALUES (8409,2,17,18,'<p><span>Dan </span><span>[a]</span><span> berapa banyaknya keturunan yang telah Kami binasakan sesudah Nuh ! Dan cukuplah Tuhan engkau Maha Mengetahui Maha Melihat dosa-dosa hamba-hamba-Nya.</span></p><p><span>[a]</span><span> 21:12 ; 65:9.</span></p>');
INSERT INTO `quran` VALUES (8410,2,17,19,'<p><span>[b]</span><span> Barangsiapa menghendaki </span><span>kehidupan</span><span> dunia, Kami segerakan baginya di dalamnya </span><span>perbekalan </span><span>yang Kami kehendaki bagi orang yang Kami sukai, kemudian Kami menetapkan baginya Jahannam ; ia akan memasukinya </span><span>dalam keadaan </span><span> tercela, terusir.</span></p><p><span>[b]</span><span> 3:146 ; 42:21.</span></p>');
INSERT INTO `quran` VALUES (8411,2,17,20,'<p><span>[c]</span><span> Dan barangsiapa menghendaki akhirat dan berusaha untuk itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1604">1604</a><span>]</span><span> dengan usahanya yang sungguh-sungguh, dan ia seorang mukmin, maka mereka itulah yang usaha-usahanya akan dihargai.</span></p><p><span>[c]</span><span> 3:146 ; 42:21.</span></p>');
INSERT INTO `quran` VALUES (8412,2,17,21,'<p><span>Semuanya Kami beri pertolongan, mereka itu maupun mereka ini, suatu anugerah dari Tuhan engkau. Dan anugerah Tuhan engkau tidak dapat dihalangi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1605">1605</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8413,2,17,22,'<p><span>Lihatlah, betapa Kamu telah memuliakan sebagian mereka di atas sebagian yang lain, </span><span>[a]</span><span> dan sesungguhnya </span><span>kehidupan </span><span> akhirat lebih besar derajatnya dan lebih besar kemuliannya.</span></p><p><span>[a]</span><span> 6:33 ; 12:58 ; 16:42.</span></p>');
INSERT INTO `quran` VALUES (8414,2,17,23,'<p><span>[b]</span><span> Janganlah engkau jadikan bersama Allah tuhan yang lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1606">1606</a><span>]</span><span> supaya engkau tidak duduk tercela, ditinggalkan.</span></p><p><span>[b]</span><span> 17:40 ; 26:214 ; 28:89.</span></p>');
INSERT INTO `quran` VALUES (8415,2,17,24,'<p><span>[c]</span><span> Dan Tuhan engkau telah memerintahkan supaya jangan menyembah selain kepada-Nya. </span><span>[d]</span><span> dan berbuat baiklah terhadap ibu-bapak. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1607">1607</a><span>]</span><span> Jika salah seorang dari mereka atau kedua-duanya mencapai usia lanjut dalam kehidupan engkau, maka janganlah engkau mengatakan “ah” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1608">1608</a><span>]</span><span> terhadap keduanya dan kanganlah engkau hardik keduanya, dan berkatalah kepada keduanya dengan perkataan yang hormat.</span></p><p><span>[c]</span><span> 2:84 ; 4:37 ; 12:41 ; 41:15.</span></p><p><span>[d]</span><span> 6:152 ; 29:9, 31:15 ; 46:16.</span></p>');
INSERT INTO `quran` VALUES (8416,2,17,25,'<p><span>“Berlaku rendah hatilah untuk mereka berdua dengan kasih sayang, dan katakanlah, </span><span>[a]</span><span> “Ya Tuhan, kasihanilah mereka berdua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1609">1609</a><span>]</span><span> sebgaimana mereka berdua telah memeliharaku semasa </span><span>aku</span><span> kecil.”</span></p><p><span>[a]</span><span> 14:42 ; 46:16 ; 71:29.</span></p>');
INSERT INTO `quran` VALUES (8417,2,17,26,'<p><span>Tuhan-mu lebih mengetahui apa yang ada dalam jiwamu. Jika kamu orang shaleh, maka sesungguhnya Dia Maha Pengampun terhadap orang-orang yang selalu kembali.</span></p>');
INSERT INTO `quran` VALUES (8418,2,17,27,'<p><span>[a]</span><span> Dan berikanlah kepada kaum kerabat haknya, dan kepada fakir-miskin dan orang musafir, dan janganlah engkau menghambur-hamburkan secara boros.</span></p><p><span>[a]</span><span> 16:91 ; 30:39.</span></p>');
INSERT INTO `quran` VALUES (8419,2,17,28,'<p><span>[b]</span><span> Sesungguhnya pemboros-pemboros itu adalah saudara-saudara syaitan, dan syaitan kepada Tuhannya tidak berterima kasih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1610">1610</a><span>]</span></p><p><span>[b]</span><span> 6:142 ; 7:32 ; 25:68.</span></p>');
INSERT INTO `quran` VALUES (8420,2,17,29,'<p><span>Dan jika engkau harus berpaling dari mereka untuk mencari rahmat dari Tuhan engkau yang engkau dambakan, </span><span>maka</span><span> </span><span>[c]</span><span> katakanlah kepada mereka dengan perkataan yang lemah lembut. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1611">1611</a><span>]</span></p><p><span>[c]</span><span> 93:10, 11.</span></p>');
INSERT INTO `quran` VALUES (8421,2,17,30,'<p><span>[a]</span><span> Dan janganlah engkau menjadikan tangan engkau terbelenggu pada leher engkau, dan jangan engkau membentangkannya terlalu terbuka, kalau tidak engkau akan duduk tercela dan letih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1612">1612</a><span>]</span><span> </span></p><p><span>[a]</span><span> 9:34 ; 25:68.</span></p>');
INSERT INTO `quran` VALUES (8422,2,17,31,'<p><span>Sesungguhnya, </span><span>[b]</span><span> Tuhan engkau melapangkan rezeki bagi siapa yang Dia kehendaki, dan menyempitkannya. Sesungguhnya Dia kepada hamba-hamba-Nya Maha Mengetahui, Maha Melihat.</span></p><p><span>[b]</span><span> 13:27 ; 29:63 ; 30:38 ; 39:53.</span></p>');
INSERT INTO `quran` VALUES (8423,2,17,32,'<p><span>Dan </span><span>[c]</span><span> janganlah kamu membunuh anak-anakmu karena takut kemiskinan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1613">1613</a><span>]</span><span> Kami-lah yang memberi rezeki kepada mereka dan kepada kamu. Sesungguhnya membunuh mereka itu adalah kesalahan besar</span><span>. [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1614">1614</a><span>]</span></p><p><span>[c]</span><span> 6:152.</span></p>');
INSERT INTO `quran` VALUES (8424,2,17,33,'<p><span>Dan </span><span>[a]</span><span> janganlah kamu mendekati zinah ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1615">1615</a><span>]</span><span> sesungguhnya itu adalah keji dan jalan yang sangat buruk.</span></p><p><span>[a]</span><span> 25:69.</span></p>');
INSERT INTO `quran` VALUES (8425,2,17,34,'<p><span>[b]</span><span> Dan janganlah kamu membunuh jiwa yang telah diharamkan Allah, kecuali dengan hak. Dan barangsiapa dibunuh dengan aniaya, sesungguhnya Kami telah memberi wewenang kepada ahli-warisnya, tetapi janganlah ia melampuai batas dalam pembunuhan itu. Sesungguhnya dia akan ditolong. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1616">1616</a><span>]</span></p><p><span>[b]</span><span> 6:152 ; 25;69.</span></p>');
INSERT INTO `quran` VALUES (8426,2,17,35,'<p><span>[a]</span><span> Dan janganlah kamu mendekati harta anak yatim, kecuali dengan cara yang terbaik hingga ia mencapai kedewasaannya, dan </span><span>[b]</span><span> tepatilah janji, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1617">1617</a><span>]</span><span> sesungguhnya janji itu akan ditanyakan.</span></p><p><span>[a]</span><span> 4:7, 11 ; 6:153.</span></p><p><span>[b]</span><span> 5:2 ; 16:92.</span></p>');
INSERT INTO `quran` VALUES (8427,2,17,36,'<p><span>[a]</span><span> Dan sempurnakanlah sukatan bila kamu menyukat, dan timbanglah dengan timbangan yang benar. Yang demikian itu baik dan lebih baik akibatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1618">1618</a><span>]</span></p><p><span>[a]</span><span> 7:86 ; 11:85, 86 ; 26:182, 183 ; 53:10.</span></p>');
INSERT INTO `quran` VALUES (8428,2,17,37,'<p><span>[b]</span><span> Dan janganlah engkau ikuti apa yang tentang itu engkau tidak mempunyai ilmu. </span><span>[c]</span><span> Sesungguhnya telinga dan mata dan hari, semuanya akan ditanya mengenai itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1619">1619</a><span>]</span></p><p><span>[b]</span><span> 11:47.</span></p><p><span>[c]</span><span> 24:25 ; 36:66 ; 41:21-23.</span></p>');
INSERT INTO `quran` VALUES (8429,2,17,38,'<p><span>[d]</span><span> Dan janganlah engkau berjalan di bumi dengan sombong, karena sesungguhnya engkau sekali-kali tidak dapat membelah bumi dan tidak pula engkau dapat mencapai ketinggian gunung-gunung. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1620">1620</a><span>]</span></p><p><span>[d]</span><span> 31:19</span></p>');
INSERT INTO `quran` VALUES (8430,2,17,39,'<p><span>Semua itu adalah keburukannya di sisi Tuhan engkau amat dibenci.</span></p>');
INSERT INTO `quran` VALUES (8431,2,17,40,'<p><span>Itulah </span><span>ajaran</span><span> dari apa-apa yang telah diwahyukan Tuhan-mu kepada engkau dari sebagian hikmah. </span><span>[a]</span><span> Dan janganlah engkau jadikan bersama Allah, tuhan lain supaya engkau </span><span>jangan</span><span> dilemparkan ke dalam Janannam, tercela, terusir.</span></p><p><span>[a]</span><span> 17:23 ; 26:214 ; 28:89.</span></p>');
INSERT INTO `quran` VALUES (8432,2,17,41,'<p><span>[b]</span><span> Apakah Tuhan kamu memilih untuk kamu anak-anak lelaki dan Dia mengambil dari malaikat anak-anak perempuan ? Sesungguhnya kamu mengucapkan kata-kata yang besar </span><span>dosanya</span><span>.</span></p><p><span>[b]</span><span> 377:151 ; 43:20 ; 52:40.</span></p>');
INSERT INTO `quran` VALUES (8433,2,17,42,'<p><span>Dan susungguhnya </span><span>[c]</span><span> Kami telah menerangkan berulang-ulang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1621">1621</a><span>]</span><span> dalam Alquran ini supaya mereka mengambil nasihat. Dan tidaklah </span><span>Alquran itu</span><span> menambah bagi mereka, kecuali kebencian. </span></p><p><span>[c]</span><span> 17:90 ; 18:55.</span></p>');
INSERT INTO `quran` VALUES (8434,2,17,43,'<p><span>Katakanlah, “Jika ada beserta-Nya tuhan-tuhan lain sebagaimana mereka katakan, niscaya mereka mampu mencari jalan kepada Dzat Yang mempunya Arasy.” </span></p>');
INSERT INTO `quran` VALUES (8435,2,17,44,'<p><span>[a]</span><span> Maha Suci Dia, dan Maha Luhur, seluhur-luhurnya dari apa yang mereka katakan.</span></p><p><span>[a]</span><span> 6:101 ; 39:68.</span></p>');
INSERT INTO `quran` VALUES (8436,2,17,45,'<p><span>Kepada-Nya bertasbih ketujuh langit dan bumi dan apa yang ada di dalamnya. Dan tiada suatu benda pun melainkan </span><span>[b]</span><span> bertasbih dengan puji-pujian-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1622">1622</a><span>]</span><span> akan tetapi kamu tidak memahami tasbih mereka itu. Sesungguhnya Dia Maha Penyantun, Maha Pengampun. </span></p><p><span>[b]</span><span> 24:42 ; 59:25 ; 61:2 ; 64:2.</span></p>');
INSERT INTO `quran` VALUES (8437,2,17,46,'<p><span>Dan apabila engkau membaca Alquran, Kami jadikan antara engkau dan mereka yang tidak beriman pada akhirat suatu penghalang yang tersembunyi, </span></p>');
INSERT INTO `quran` VALUES (8438,2,17,47,'<p><span>[a]</span><span> Dan Kami meletakkan tutupan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1623">1623</a><span>]</span><span> di atas hati mereka supaya mereka tidak memahami nya dalam telinga mereka ada ketulian. </span><span>[b]</span><span> Dan apabila engkau menyebutkan Tuhan engkau Yang Tunggal dalam Alquran, mereka membalikkan punggung mereka karena benci. </span></p><p><span>[a]</span><span> 6:26 ; 18:58 ; 41:6.</span></p><p><span>[b]</span><span> 17:49.</span></p>');
INSERT INTO `quran` VALUES (8439,2,17,48,'<p><span>Kami lebih mengetahui untuk apa mereka mendengarkan, ketika mereka mendengarkan engkau dan ketka mereka sedang berunding secara rahasia, bila orang-orang aniaya itu berkata </span><span>satu sama lain,</span><span> </span><span>[c]</span><span> “Kamu tidak mengikuti kecuali seorang laki-laki yang kena sihir.” </span></p><p><span>[c]</span><span> 25:9.</span></p>');
INSERT INTO `quran` VALUES (8440,2,17,49,'<p><span>[d]</span><span> Lihatlah, bagaimana mereka mengada-adakan tamsil-tamsil mengenai diri engkau, maka </span><span>akibatnya</span><span> mereka menjadi sesat, lalu mereka tidak dapat menemukan jalan. </span></p><p><span>[d]</span><span> 25:10.</span></p>');
INSERT INTO `quran` VALUES (8441,2,17,50,'<p><span>Dan mereka berkata, </span><span>[c]</span><span> “Apabila kami telah menjadi tulang dan benda yang hancur, apakah kami sungguh-sungguh akan dibangkitkan </span><span>kembali</span><span> sebagai makhluk yang baru “ ? </span></p><p><span>[e]</span><span> 17:99 ; 23:83 ; 37:17 ; 56:48.</span></p>');
INSERT INTO `quran` VALUES (8442,2,17,51,'<p><span>Katakanlah, “Jadilah kamu batu atau besi,</span></p>');
INSERT INTO `quran` VALUES (8443,2,17,52,'<p><span>“Atau makhluk yang nampaknya keras </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1624">1624</a><span>]</span><span> dalam hatimu.” </span><span>[a]</span><span> Maka segera mereka akan berkata, “Siapakah yang akan mengembalikan kami ? “ Katakanlah, “Dia Yang telah menciptakan kamu pertama kali.” Masih juga mereka akan menggelengkan kepadanya terhadap engkau dan berkata, </span><span>[b]</span><span> “ Kapankah itu ? “ Katakanlah, “Boleh jadi itu </span><span>sudah</span><span> dekat.” </span></p><p><span>[a]</span><span> 36:79, 80.</span></p><p><span>[b]</span><span> 34:30 ; 36:49 ; 67:26.</span></p>');
INSERT INTO `quran` VALUES (8444,2,17,53,'<p><span>“</span><span>Itu akan terjadi </span><span> pada hari ketika Dia akan memanggilmu, lalu kamu akan menyambut dengan memuji-Nya dan kamu akan beranggapan bahwa </span><span>[c]</span><span> kamu tinggal </span><span>di dunia </span><span> hanya sebentar.” </span></p><p><span>[c]</span><span> 20:105 ; 23:114, 115.</span></p>');
INSERT INTO `quran` VALUES (8445,2,17,54,'<p><span>R. 6</span></p><p><span>[d]</span><span> Dan katakanlah kepada hamba-hamba-Ku supaya mereka mengucapkan </span><span>perkataan</span><span> yang sebaik-baiknya. </span><span>[e]</span><span> Sesungguhnya syaitan menimbulkan perpecahan di antara mereka. Sesungguhnya syaitan bagi manusia adalah musuh yang nyata. </span></p><p><span>[d]</span><span> 16:126 ; 23:97 ; 41:35.</span></p><p><span>[e]</span><span> 7:201 ; 12:101 ; 41:37.</span></p>');
INSERT INTO `quran` VALUES (8446,2,17,55,'<p><span>Tuhan-mu lebih mengetahui tentang kamu. </span><span>[a]</span><span> Jika dia menghendaki, Dia tentu akan mengasihanimu ; atau jika Dia menghendaki, Dia akan mengazab kamu. </span><span>[b]</span><span> Dan Kami tidak mengutus engkau atas mereka </span><span>sebagai</span><span> penanggung jawab. </span></p><p><span>[a]</span><span> 2:285 ; 3:129 ; 5:41 ; 29:22.</span></p><p><span>[b]</span><span> 6:108 ; 39:42 ; 42:7.</span></p>');
INSERT INTO `quran` VALUES (8447,2,17,56,'<p><span>Dan Tuhan engkau lebih mengetahui tentang mereka yang ada di seluruh langit dan bumi. Dan sesungguhnya telah </span><span>[c]</span><span> Kami muliakan sebagian nabi-nabi di atas sebagian yang lain, dan telah Kami berikan kepada Daud Zabur. </span></p><p><span>[c]</span><span> 2:254 ; 27:16.</span></p>');
INSERT INTO `quran` VALUES (8448,2,17,57,'<p><span>[d]</span><span> Katakanlah, “Serulah orang-orang yang kamu anggap </span><span>tuhan</span><span> selain Dia ; padahal mereka tidak mempunyai kekuasaan menghilangkan kesusahan dari kamu dan tidak pula mengubah </span><span>keadaanmu.</span><span>” </span></p><p><span>[d]</span><span> 22:74 ; 25:4 ; 34:23.</span></p>');
INSERT INTO `quran` VALUES (8449,2,17,58,'<p><span>Mereka yang diseru </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1625">1625</a><span>]</span><span> oleh orang-orang itu, mereka </span><span>juga</span><span> mencari sarana </span><span>kedekatan</span><span> kepada Tuhan mereka, siapa di antara mereka yang lebih dekat kepada </span><span>Tuhan</span><span> dan mengharapkan rahmat-Nya, dan takut kepada azab-Nya. Sesungguhnya azab Tuhan engkau adalah sesuatu yang ditakuti. </span></p>');
INSERT INTO `quran` VALUES (8450,2,17,59,'<p><span>[a]</span><span> Dan tiada suatu negeri pun melainkan Kami menghancurkannya sebelum Hari Kiamat, atau mengazabnya dengan azab yang sangat keras. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1626">1626</a><span>]</span><span> Adalah hal itu telah tertulis dalam Kitab. </span></p><p><span>[a]</span><span> 21:12 ; 22:46 ; 28:59.</span></p>');
INSERT INTO `quran` VALUES (8451,2,17,60,'<p><span>[b]</span><span> Dan tiada yang dapat menghalangi Kami untuk mengirimkan Tanda-tanda melainkan bahwa orang-orang dahulu telah mendustakannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1627">1627</a><span>]</span><span> Dan Kami berikan kepada Tsamud unta betina sebagai suatu Tanda yang nyata, tetapi mereka menganiayanya. Dan Kami tidak mengirimkan Tanda-tanda melainkan untuk menakuti. </span></p><p><span>[b]</span><span> 17:95 ; 18:56.</span></p>');
INSERT INTO `quran` VALUES (8452,2,17,61,'<p><span>Dan </span><span>ingatlah</span><span> ketika Kami mengatakan kepada engkau, “Sesungguhnya Tuhan engkau telah mengepung orang-orang ini </span><span>dengan kebinasaan.</span><span>” </span><span>[c]</span><span> Dan tidaklah Kami jadikan ru</span><span>’</span><span>ya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1627a">1627a</a><span>]</span><span> yang telah Kami perlihatkan kepada engkau melainkan sebagai ujian bagi manusia, dan begitu pula pohon terkutuk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1628">1628</a><span>]</span><span> dalam Alquran. Dan Kami menakuti-nakuti kepada mereka, kecuali kedurhakaan amat besar. </span></p><p><span>[c]</span><span> 17:2.</span></p>');
INSERT INTO `quran` VALUES (8453,2,17,62,'<p><span>[a]</span><span> Dan </span><span>ingatlah</span><span> ketika Kami berkata kepada para malaikat, “Bersujudlah bersama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1629">1629</a><span>]</span><span> Adam,” maka bersujudlah mereka, kecuali iblis. Ia berkata, “Apakah aku harus sujud bersama orang yang Engkau jadikan dari tanah liat ?” </span></p><p><span>[a]</span><span> 2:35 ; 7:12 ; 15:30, 31 ; 18:51 ; 20:117 ; 38:73 </span><span>–</span><span> 75.</span></p>');
INSERT INTO `quran` VALUES (8454,2,17,63,'<p><span>Ia berkata, “Bagaimanakah pendapat Engkau ? Apakah </span><span>[a]</span><span> orang ini, yang Engkau telah muliakan atasku </span><span>menjadi majikan-ku</span><span> ? Jika Engkau akan memberi tangguh kepadaku hingga Hari Kiamat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1630">1630</a><span>] [b]</span><span> tentulah akan aku kuasai semua anak-keturunannya, kecuali sedikit.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1631">1631</a><span>]</span><span> </span></p><p><span>[a]</span><span> 7:13 ; 15:34 ; 38:77.</span></p><p><span>[b]</span><span> 7:17, 18 ; 15:40.</span></p>');
INSERT INTO `quran` VALUES (8455,2,17,64,'<p><span>[c]</span><span> Dia berfirman, “Pergilah ! Maka barangsiapa mengikuti engkau dari antara mereka, niscaya Jahannamlah balasan bagi kamu sekalian, suatu balasan yang penuh. </span></p><p><span>[c]</span><span> 7:19 ; 15:43, 44 ; 38:86.</span></p>');
INSERT INTO `quran` VALUES (8456,2,17,65,'<p><span>[d]</span><span> “Dan bujuklah siapa dari antara mereka yang engkau sanggup </span><span>membujuk</span><span> dengan suara engkau, dan kerahkanlah terhadap mereka pasukan berkuda engkau dan pasukan berjalan-kaki engkau dan berserikatlah dengan mereka dalam harta, dan anak-anak, dan berjanjilah kepada mereka.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1632">1632</a><span>] [e]</span><span> Dan syaitan tidak menjanjikan kepada mereka selain tipu-daya. </span></p><p><span>[d]</span><span> 7:18.</span></p><p><span>[e]</span><span> 4:121 ; 14:23.</span></p>');
INSERT INTO `quran` VALUES (8457,2,17,66,'<p><span>[a]</span><span> Sesungguhnya hamba-hamba-Ku, tidak ada bagi engkau kekuasaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1633">1633</a><span>]</span><span> atas mereka. Dan cukuplah Tuhan engkau sebagai Pelindung. </span></p><p><span>[a]</span><span> 15:41 ; 38:84.</span></p>');
INSERT INTO `quran` VALUES (8458,2,17,67,'<p><span>[b]</span><span> Tuhan-mu, Dia-lah Yang melayarkan bahtera-bahtera bagimu di lautan, supaya kamu dapat mencari karunia-Nya. Sesungguhnya Dia terhadapmu, Maha Penyayang. </span></p><p><span>[b]</span><span> 14:33 ; 22:66 ; 45:13.</span></p>');
INSERT INTO `quran` VALUES (8459,2,17,68,'<p><span>[c]</span><span> Dan apabila kemudaratan menimpamu di lautan, sia-sialah siapa saja yang kamu seru selain Dia. Tetapi apabila Dia menyelamtkan kamu sampai ke daratan, kamu berpaling. Dan manusia sangat tidak tahu berterima-kasih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1634">1634</a><span>]</span><span> </span></p><p><span>[c]</span><span> 10:13 ; 11:10 ; 23:65 ; 30:34 ; 39:9 ; 41:50-52 ; 70:21, 22.</span></p>');
INSERT INTO `quran` VALUES (8460,2,17,69,'<p><span>[a]</span><span> Apakah kamu merasa aman, bahwa Dia akan membenamkan kamu di tepi daratan atau mengirimkan atasmu taufan pasir yang dahsyat, kemudian kamu tidak akan memperoleh bagimu pelindung ? </span></p><p><span>[a]</span><span> 67:17, 18.</span></p>');
INSERT INTO `quran` VALUES (8461,2,17,70,'<p><span>Atau, apakah kamu merasa aman bahwa Dia akan mengembalikan kamu ke dalamnya untuk kedua kalinya, </span><span>[b]</span><span> lalu Dia mengirimkan atasmu hembusan angin taufan yang kencang, dan menenggelamkan kamu, karena kamu ingkar ? Kemudian kamu tidak akan memperoleh bagimu seorang penolong melawan Kami dalam hal ini. </span></p><p><span>[b] </span><span>67:18.</span></p>');
INSERT INTO `quran` VALUES (8462,2,17,71,'<p><span>Dan sesungguhnya telah Kami muliakan keturunan Adam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1635">1635</a><span>]</span><span> dan Kami angkut mereka di daratan dan di lautan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1635a">1635a</a><span>]</span><span> dan Kami beri mereka rezeki yang baik dan Kami lebihkan mereka dengan kelebihan di atas kebanyakan dari yang telah Kami ciptakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1635b">1635b</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8463,2,17,72,'<p><span>Ingatlah</span><span> hari itu, ketika Kami akan memanggil semua orang beserta pemimpin mereka, </span><span>[a]</span><span> maka barangsiapa akan diberikan kitabnya di tangan kanannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1636">1636</a><span>]</span><span> maka mereka itu akan membaca kitab mereka dan mereka tidak akan dianiaya sedikit pun. </span></p><p><span>[a]</span><span> 69:20 ; 84:8, 9.</span></p>');
INSERT INTO `quran` VALUES (8464,2,17,73,'<p><span>[b]</span><span> Dan barangsiapa buta di </span><span>dunia</span><span> ini, maka di akhirat pun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1637">1637</a><span>]</span><span> ia akan buta juga, dan bahkan lebih tersesat dari jalan. </span></p><p><span>[b]</span><span> 20:125.</span></p>');
INSERT INTO `quran` VALUES (8465,2,17,74,'<p><span>[a]</span><span> Dan hampir-hampir mereka menimpakan fitnah kepada engkau disebabkan oleh apa yang telah Kami wahyukan kepada engkau, supaya engkau mengada-adakan terhadap Kami selain itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1638">1638</a><span>]</span><span> dan sesudah itu mereka niscaya akan menjadikan engkau teman. </span></p><p><span>[a]</span><span> 10:16 ; 68:10.</span></p>');
INSERT INTO `quran` VALUES (8466,2,17,75,'<p><span>[b]</span><span> Dan sekiranya engkau tidak Kami perkuat </span><span>dengan Alquran,</span><span> niscaya engkau akan sedikit cenderung kepada mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1639">1639</a><span>]</span><span> </span></p><p><span>[b]</span><span> 25:33.</span></p>');
INSERT INTO `quran` VALUES (8467,2,17,76,'<p><span>Jika demikian Kami pasti merasakan kepada engkau </span><span>siksaan</span><span> berlipat-ganda sesudah mati, kemudian tidaklah engkau akan memperoleh bagi diri engkau seorang penolong melawan Kami. </span></p>');
INSERT INTO `quran` VALUES (8468,2,17,77,'<p><span>Dan sesungguhnya hampir mereka akan menakut-nakuti engkau di negeri ini, </span><span>[a]</span><span> supaya mereka mengeluarkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1640">1640</a><span>]</span><span> engkau darinya, dan jika demikian mereka tidak akan tinggal dibelakang engkau, melainkan hanya sebentar. </span></p><p><span>[a]</span><span> 8:31 ; 60:2.</span></p>');
INSERT INTO `quran` VALUES (8469,2,17,78,'<p><span>[b]</span><span> </span><span>Demikian</span><span> cara </span><span>Kami</span><span> kepada yang Kami utus sebelum engkau dari rasul-rasul Kami dan tidak akan engkau dapatkan perubahan dalam cara Kami. </span></p><p><span>[b]</span><span> 33:63 ; 35:44 ; 48:24.</span></p>');
INSERT INTO `quran` VALUES (8470,2,17,79,'<p><span>R. 9</span></p><p><span>[c]</span><span> Dirikanlah shalat sejak matahari condong hingga kegelapan malam. dan bacalah </span><span>Alquran</span><span> pada waktu subuh. Sesungguhnya pembacaan </span><span>Alquran</span><span> pada waktu subuh diterima </span><span>secara istimewa oleh Allah</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1641">1641</a><span>]</span><span> </span></p><p><span>[c]</span><span> 11:115 ;30:131 ;30:18 ; 19:50 50:40.</span></p>');
INSERT INTO `quran` VALUES (8471,2,17,80,'<p><span>[a]</span><span> Dan pada sebagian malam, maka tahajudlah engkau dengan </span><span>membaca</span><span>-nya, suatu ibadah tambahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1642">1642</a><span>]</span><span> bagi engkau. Semoga Tuhan engkau akan mengangkat engkau ke tempat yang sangat terpuji. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1643">1643</a><span>]</span><span> </span></p><p><span>[a]</span><span> 50:41 ; 52:50 ; 73:3 </span><span>–</span><span> 5 ; 76:27.</span></p>');
INSERT INTO `quran` VALUES (8472,2,17,81,'<p><span>Dan katakanlah, “Ya Tuhan-ku, masukkanlah aku </span><span>dengan cara</span><span> masuk yang baik dan keluarkanlah aku </span><span>dengan cara </span><span> keluar yang baik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1644">1644</a><span>]</span><span> Dan jadikanlah bagiku dari hadirat Engkau kekuatan yang menolong.” </span></p>');
INSERT INTO `quran` VALUES (8473,2,17,82,'<p><span>Dan katakanlah, </span><span>[b]</span><span> “Kebenaran telah datang dan kebatilan telah lenyap. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1645">1645</a><span>]</span><span> Sesungguhnya kebatilan itu pasti akan lenyap.” </span></p><p><span>[b]</span><span> 21:19 ; 34:50.</span></p>');
INSERT INTO `quran` VALUES (8474,2,17,83,'<p><span>Dan </span><span>[a]</span><span> Kami </span><span>berangsur-angsur</span><span> turunkan Alquran suatu yang merupakan menyembuh dan rahmat bagi orang-orang yang beriman ; tetapi tidaklah itu menambah kepada orang-orang yang aniaya melainkan kerugian. </span></p><p><span>[a]</span><span> 10:58 ; 12:112 ; 16:90.</span></p>');
INSERT INTO `quran` VALUES (8475,2,17,84,'<p><span>[b]</span><span> Dan apabila Kami berikan nikmat kepada manusia, berpalinglah ia dan menjauhkan diri, dan bila keburukan menjamahnya berputus-asalah ia. </span></p><p><span>[b]</span><span> 17:68.</span></p>');
INSERT INTO `quran` VALUES (8476,2,17,85,'<p><span>Katakanlah, “Tiap orang beramal menurut caranya sendiri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1646">1646</a><span>] [c]</span><span> Tetapi Tuhan-mu lebih mengetahui siapa yang lebih terpimpin pada jalan-</span><span>Nya.</span><span>” </span></p><p><span>[c]</span><span> 28:86.</span></p>');
INSERT INTO `quran` VALUES (8477,2,17,86,'<p><span>Dan mereka bertanya kepada engkau mengenai ruh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1647">1647</a><span>]</span><span> Katakanlah, “Ruh </span><span>diciptakan</span><span> atas perintah Tuhan-ku; dan tidak kamu diberi ilmu melainkan sedikit.” </span></p>');
INSERT INTO `quran` VALUES (8478,2,17,87,'<p><span>Dan sekiranya Kami menghendaki, tentulah Kami mengambil kembali </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1648">1648</a><span>]</span><span> apa yang telah Kami wahyukan kepada engkau, kemudian engkau tidak akan memperoleh penjaga bagi engkau dalam hal ini melawan Kami. </span></p>');
INSERT INTO `quran` VALUES (8479,2,17,88,'<p><span>[a]</span><span> Kecuali ada rahmat dari Tuhan engkau. Sesungguhnya karunia-Nya kepada engkau sangat besar. </span></p><p><span>[a]</span><span> 28:87.</span></p>');
INSERT INTO `quran` VALUES (8480,2,17,89,'<p><span>[b]</span><span> Katakanlah, “Seandainya berhimpun manusia dan jin untuk mendatangkan yang semisal Alquran ini, tidaklah mereka akan sanggup mendatangkan yang sama seperti ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1649">1649</a><span>]</span><span> walaupun sebagian mereka kepada sebagian yang lain sebagai penolong.” </span></p><p><span>[b]</span><span> 2:24 ; 10:39 ; 11:14 ; 52:35.</span></p>');
INSERT INTO `quran` VALUES (8481,2,17,90,'<p><span>Dan sesungguhnya telah </span><span>[c]</span><span> Kami uraikan bagi manusia di dalam Alquran ini, berbagai misal, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1650">1650</a><span>]</span><span> tetapi kebanyakan manusia menolaknya, kecuali </span><span>memilih</span><span> keingkaran. </span></p><p><span>[c]</span><span> 17:42 ; 18:55.</span></p>');
INSERT INTO `quran` VALUES (8482,2,17,91,'<p><span>Dan mereka berkata, “Sekali-kali kami tidak akan beriman kepada engkau sebelum engkau pancarkan untuk kami dari bumi mata air;</span></p>');
INSERT INTO `quran` VALUES (8483,2,17,92,'<p><span>[d]</span><span> “Atau engkau mempunyai kebun kurma dan anggur, lalu engkau alirkan sungai-sungai yang berlimpah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1651">1651</a><span>]</span><span> di tengahnya. </span></p><p><span>[d]</span><span> 25:11.</span></p>');
INSERT INTO `quran` VALUES (8484,2,17,93,'<p><span>“Atau engkau jatuhkan langit sebagaimana engkau da</span><span>’</span><span>wakan atas kami berkeping-keping atau engkau datangkan Allah dan para malaikat berhadap-hadapan </span><span>dengan kami,</span><span> </span></p>');
INSERT INTO `quran` VALUES (8485,2,17,94,'<p><span>“Atau engkau mempunyai sebuah rumah dari emas atau engkau naik ke langit. Dan sekali-kali kami tidak akan percaya kenaikan engkau </span><span>ke langit,</span><span> hingga engkau turunkan kepada kami sebuah kitab yang dapat kami membacanya.” Katakanlah, “Maha Suci Tuhan-ku, aku tidak lain melainkan seorang manusia </span><span>sebagai seorang rasul” </span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1652">1652</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8486,2,17,95,'<p><span>R. 11</span></p><p><span>[a]</span><span> Dan tiada sesuatu yang menghalangi manusia untuk beriman ketika datang kepada mereka petunjuk, kecuali mereka berkata, “Apakah Allah telah mengutus seorang manusia sebagai rasul ? “</span></p><p><span>[a]</span><span> 17:60 ; 23:25 ; 34:44.</span></p>');
INSERT INTO `quran` VALUES (8487,2,17,96,'<p><span>Katakanlah, “Sekiranya di bumi ini ada malaikat yang berjalan-jalan dengan aman tenteram, niscaya </span><span>[a]</span><span> Kami turunkan kepada mereka dari langit seorang malaikat </span><span>sebagai </span><span>rasul.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1653">1653</a><span>]</span><span> </span></p><p><span>[a]</span><span> 23:25 ; 25:22 ; 43:61.</span></p>');
INSERT INTO `quran` VALUES (8488,2,17,97,'<p><span>Katakanlah, </span><span>[b]</span><span> “Cukuplah Allah sebagai saksi di antara aku dan kamu. Sesungguh- nya Dia terhadap hamba-hamba-Nya Maha Mengetahui, Maha Melihat.” </span></p><p><span>[b]</span><span> 10:30 ; 13:44 ; 29:53 ; 46:9.</span></p>');
INSERT INTO `quran` VALUES (8489,2,17,98,'<p><span>[c]</span><span> Dan barangsiapa Allah memberi petunjuk, maka dialah yang mendapat petunjuk, dan barangsiapa yang Dia Sesatkan, maka sekali-kali engkau tidak akan mendapatkan bagi mereka penolong-penolong selain Dia. Dan akan Kami himpun mereka </span><span>[d]</span><span> pada Hari Kiamat, sesuai maksud mereka dalam keadaan buta, bisu, dan tuli. Tempat tinggal mereka adalah Jahannam. Setiap kali </span><span>api</span><span> itu berkurang Kami tambahkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1654">1654</a><span>]</span><span> bagi mereka nyala api. </span></p><p><span>[c]</span><span> 7:179 ; 18:16 ; 39:37, 38.</span></p><p><span>[d]</span><span> 6:129 ; 19:69.</span></p>');
INSERT INTO `quran` VALUES (8490,2,17,99,'<p><span>[a]</span><span> Itulah balasan bagi mereka disebabkan mereka mengingkari Tanda-tanda Kami dan berkata, “Apakah </span><span>[b]</span><span> kami </span><span>telah menjadi </span><span> tulang-belulang dan telah hancur, apakah benar-benar kami akan dibangkitkan sebagai makhluk baru ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1655">1655</a><span>]</span><span> </span></p><p><span>[a]</span><span> 18:107 ; 34:18.</span></p><p><span>[b]</span><span> 17:50 ; 23:83 ; 36:79 ; 37:17 ; 56:48.</span></p>');
INSERT INTO `quran` VALUES (8491,2,17,100,'<p><span>Apakah mereka tidak melihat bahwa </span><span>[c]</span><span> Allah, Yang telah menciptakan seluruh langit dan bumi, Dia berkuasa pula untuk menciptakan yang sama seperti mereka ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1656">1656</a><span>]</span><span> Dan Dia telah menetapkan bagi mereka suatu jangka waktu, tidak ada keraguan di dalamnya. Tetapi orang-orang yang aniaya mengingkari segala sesuatu kecuali </span><span>memilih</span><span> keingkaran. </span></p><p><span>[c]</span><span> 36:82 ; 46:34 ; 86:9.</span></p>');
INSERT INTO `quran` VALUES (8492,2,17,101,'<p><span>R. 12</span></p><p><span>Katakanlah, “Seandainya kamu memiliki khazanah-khazanah rahmat Tuhan-ku, niscaya kamu akan menahan karena takut membelanjakan-</span><span>nya.</span><span> Dan manusia itu sangat kikir. </span></p>');
INSERT INTO `quran` VALUES (8493,2,17,102,'<p><span>Dan sesungguhnya </span><span>[a]</span><span> Kami telah memberi Musa sembilan Tanda yang terang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1657">1657</a><span>]</span><span> maka tanyakanlah kepada Bani Israil. Ketika ia datang kepada mereka, maka berkatalah </span><span>[b] </span><span>Firaun kepadanya, “Sesungguhnya aku anggap engkau, hai Musa, seorang yang disihir.” </span></p><p><span>[a]</span><span> 7:134 ; 27:13.</span></p><p><span>[b]</span><span> 27:14 ; 28:37 ; 40:25.</span></p>');
INSERT INTO `quran` VALUES (8494,2,17,103,'<p><span>Ia berkata, “Sesungguhnya engkau telah mengetahui, bahwa tiada yang menurunkan Tanda-tanda ini, melainkan Tuhan seluruh langit dan bumi, sebagai bukti-bukti nyata ; dan sesungguhnya aku anggap engkau, hai Firaun orang yang akan binasa. “</span></p>');
INSERT INTO `quran` VALUES (8495,2,17,104,'<p><span>Maka dia menghendaki untuk mengusir mereka dari negeri ini, </span><span>[c]</span><span> tetapi Kami tenggelamkan dia dan orang-orang yang beserta dia, semuanya. </span></p><p><span>[c]</span><span> 2:51 ; 7:137 ; 8:55 ; 20:79 ; 26:67 ; 28:41.</span></p>');
INSERT INTO `quran` VALUES (8496,2,17,105,'<p><span>Dan Kami berkata sesudah dia </span><span>tenggelam</span><span> kepada Bani Israil, </span><span>[a]</span><span> “Tinggallah di negeri </span><span>yang dijanjikan</span><span> itu; dan apabila akan datang janji akhir untuk </span><span>kaum Musliman tentang azab</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1658">1658</a><span>]</span><span> akan Kami himpun kamu semuanya.” </span></p><p><span>[a]</span><span> 7:138.</span></p>');
INSERT INTO `quran` VALUES (8497,2,17,106,'<p><span>[b]</span><span> Dan sesuai dengan hak telah Kami menurunkannya. Dan dengan hak ia telah turun. Dan tidak Kami mengutus engkau, melainkan sebagai pembawa khabar suka dan pemberi peringatan. </span></p><p><span>[b]</span><span> 4:106 ; 5:49 ; 39:3.</span></p>');
INSERT INTO `quran` VALUES (8498,2,17,107,'<p><span>Dan Alquran yang telah </span><span>[c]</span><span> Kami membaginya dalam bagian-bagian supaya engkau dapat membacakannya kepada manusia dengan cara berangsur, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1659">1659</a><span>]</span><span> dan Kami telah menurun- kannya bagian demi bagian. </span></p><p><span>[c]</span><span> 25:33 ; 73:5.</span></p>');
INSERT INTO `quran` VALUES (8499,2,17,108,'<p><span>Katakanlah, “Berimanlah kepadanya atau kamu tidak beriman. Sesungguhnya orang-orang yang telah diberi ilmu sebelumnya apabila dibacakan kepada mereka, mereka </span><span>[a]</span><span> merebahkan mukanya untuk bersujud. </span></p><p><span>[a]</span><span> 19:59 ; 32:16 ; 38:25.</span></p>');
INSERT INTO `quran` VALUES (8500,2,17,109,'<p><span>Dan mereka berkata, “Maha Suci Tuhan kami, </span><span>[b]</span><span> sesungguhnya janji Tuhan kami pasti akan terlaksana.” </span></p><p><span>[b]</span><span> 18:99 ; 19:62 ; 46:17 ; 73:19.</span></p>');
INSERT INTO `quran` VALUES (8501,2,17,110,'<p><span>Dan mereka jatuh atas dahi mereka sambil menangis, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1660">1660</a><span>]</span><span> dan itu menambah mereka khusyu.</span><span>’</span><span> </span></p>');
INSERT INTO `quran` VALUES (8502,2,17,111,'<p><span>[a]</span><span> Katakanlah, “Serulah Allah atau serulah Ar-Rahman ; </span><span>dengan</span><span> nama apa saja kamu berseru </span><span>kepada Dia ; </span><span> kepunyaan-Nya semua nama yang terbaik.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1661">1661</a><span>] [b]</span><span> Dan janganlah kamu ucapkan doa-doamu keras-keras, dan jangan pula kamu mengucapkannya terlalu </span><span>lemah</span><span>, tetapi carilah jalan di antara itu. </span></p><p><span>[a]</span><span> 7:181 ; 20:9 ; 59:25.</span></p><p><span>[b]</span><span> 7:56, 206.</span></p>');
INSERT INTO `quran` VALUES (8503,2,17,112,'<p><span>[c]</span><span> Dan katakanlah, “Segala puji bagi Allah Yang tidak mempunyai anak, dan tidak ada sekutu bagi-Nya penolong karena kelemahan.” Dan sanjunglah keagungan-Nya dengan pengagungan yang sebesar-besarnya. </span></p><p><span>[c]</span><span> 18:5 ; 19:36, 43 ; 25:3 ; 72:4.</span></p>');
INSERT INTO `quran` VALUES (8504,2,18,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8505,2,18,2,'<p><span>[b]</span><span> Segala puji bagi Allah, Yang telah menurunkan atas hamba-Nya Kitab ini dan tidaklah Dia menjadikan baginya kebengkokan.</span></p><p><span>[b]</span><span> 25:2 ; 57:10.</span></p>');
INSERT INTO `quran` VALUES (8506,2,18,3,'<p><span>Dia sebagai penjaga, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1662">1662</a><span>] [c]</span><span> untuk memberi peringatan tentang siksaan yang dahsyat dari sisi-Nya, dan memberikan khabar suka kepada orang-orang mukmin, yang beramal shaleh bahwa bagi mereka ada ganjaran yang baik,</span></p><p><span>[c]</span><span> 17:10, 11.</span></p>');
INSERT INTO `quran` VALUES (8507,2,18,4,'<p><span>Mereka tetap di dalamnya selama-lamanya,</span></p>');
INSERT INTO `quran` VALUES (8508,2,18,5,'<p><span>Dan supaya memperingatkan orang-orang </span><span>[d]</span><span> yang mengatakan, “Allah telah mengambil seorang anak lelaki.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1663">1663</a><span>]</span></p><p><span>[d]</span><span> 17:112 ; 19:36 ; 21:27 ; 25:3 ; 39:5 ; 72:4.</span></p>');
INSERT INTO `quran` VALUES (8509,2,18,6,'<p><span>Tidak ada bagi </span><span>[a]</span><span> mereka ilmu mengenainya, dan tidak </span><span>pula</span><span> bagi bapak-bapak mereka. Alangkah </span><span>[b]</span><span> besar </span><span>bahaya </span><span> perkataan yang keluar dari mulut mereka. Mereka tidak mengucapkan selain dusta.</span></p><p><span>[a]</span><span> 22:72 ; 40:43.</span></p><p><span>[b]</span><span> 19:91, 92.</span></p>');
INSERT INTO `quran` VALUES (8510,2,18,7,'<p><span>Maka sangat mungkin engkau akan </span><span>[c]</span><span> membinasakan dirimu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1664">1664</a><span>]</span><span> karena sangat sedih sesudah mereka </span><span>berpaling</span><span>, jika mereka tidak beriman kepada keterangan ini.</span></p><p><span>[c]</span><span> 26:4.</span></p>');
INSERT INTO `quran` VALUES (8511,2,18,8,'<p><span>Sesungguhnya Kami telah menjadikan yang ada di bumi perhiasan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1665">1665</a><span>]</span><span> baginya, </span><span>[d]</span><span> supaya Kami menguji mereka siapakah di antara mereka yang terbaik amalnya.</span></p><p><span> [d]</span><span> 5:49 ; 6:166 ; 11:8 ; 67:3.</span></p>');
INSERT INTO `quran` VALUES (8512,2,18,9,'<p><span>[a]</span><span> Dan sesungguhnya akan Kami jadikan segala yang ada di atasnya </span><span>menjadi</span><span> tanah-rata yang tandus. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1666">1666</a><span>]</span></p><p><span>[a]</span><span> 18:41.</span></p>');
INSERT INTO `quran` VALUES (8513,2,18,10,'<p><span>Apakah engkau menyangka bahwa penghuni gua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1666a">1666a</a><span>]</span><span> dan penulis prasasti itu adalah di antara Tanda-tanda Kami yang menakjubkan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1667">1667</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8514,2,18,11,'<p><span>Ketika beberapa pemuda mencari perlindungan dalam gua, maka mereka berkata, “Ya Tuhan kami, anugerahulah kami rahmat dari sisi Engkau ; dan lengkapilah kami dengan petunjuk yang benar dalam urusan kami.”</span></p>');
INSERT INTO `quran` VALUES (8515,2,18,12,'<p><span>Maka Kami cegah mereka dari mendengar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1668">1668</a><span>]</span><span> dalam gua beberapa tahun lamanya.</span></p>');
INSERT INTO `quran` VALUES (8516,2,18,13,'<p><span>Kemudian Kami bangkitkan mereka supaya Kami mengetahui manakah di antara dua golongan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1669">1669</a><span>]</span><span> yang lebih tepat membuat perhitungan mengenai lamanya mereka tinggal.</span></p>');
INSERT INTO `quran` VALUES (8517,2,18,14,'<p><span>Kami ceriterakan kepada engkau kisah mereka dengan benar. Sesungguhnya mereka itu pemuda-pemuda yang beriman kepada Tuhan mereka, dan </span><span>[a]</span><span> Kami menambah kepada mereka petunjuk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1670">1670</a><span>]</span></p><p><span>[a]</span><span> 8:3 ; 47:18.</span></p>');
INSERT INTO `quran` VALUES (8518,2,18,15,'<p><span>Dan Kami kuatkan hati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1671">1671</a><span>]</span><span> mereka, ketika mereka berdiri dan berkata, “Tuhan kami adalah Rabb seluruh langit dan bumi. Sekali-kali kami tidak menyeru tuhan selain Dia. Sesungguhnya jika kami mengatakan demikian, niscaya sudah jauh dari kebenaran.</span></p>');
INSERT INTO `quran` VALUES (8519,2,18,16,'<p><span>[a]</span><span> “Mereka itu kaum kami yang telah mengambil tuhan-tuhan lain selain Dia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1672">1672</a><span>]</span><span> Mengapa mereka tidak mengemukakan suatu dalil yang terang tentang mereka itu ? Maka </span><span>[b]</span><span> siapakah yang lebih aniaya dari orang yang mengada-adakan dusta terhadap Allah?</span></p><p><span>[a]</span><span> 21:25 ; 25:4.</span></p><p><span>[b]</span><span> 6:145 ; 7:38 ; 10:18 ; 11:19.</span></p>');
INSERT INTO `quran` VALUES (8520,2,18,17,'<p><span>“Dan ketika kamu meninggalkan mereka dan dari apa yang mereka sembah selain Allah, maka carilah perlindungan dalam gua ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1673">1673</a><span>]</span><span> Tuhan-mu akan melapangkan bagimu rahmat-Nya, dan akan menyediakan untukmu sarana kemudahan bagi urusanmu.</span></p>');
INSERT INTO `quran` VALUES (8521,2,18,18,'<p><span>Dan engkau lihat matahari, bila ia terbit, menjauh lewat dari gua mereka ke sebelah kanan dan apabila ia terbenam, meninggalkan mereka di sebelah kiri, sedang mereka ada dalam rongga yang luas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1674">1674</a><span>]</span><span> di gua itu. Yang demikian itu dari Tanda-tanda Allah, </span><span>[a]</span><span> Barangsiapa diberi petunjuk oleh Allah, maka dia mendapat petunjuk ; dan barangsiapa Dia sesatkan, maka sekali-kali engkau tidak akan menemukan baginya penolong, penunjuk jalan. </span></p><p><span>[a]</span><span> 7:179 ; 17:98 ; 39:37, 38.</span></p>');
INSERT INTO `quran` VALUES (8522,2,18,19,'<p><span>R. 3</span></p><p><span>Dan engkau menyangka mereka itu bangun, padahal mereka itu tidur, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1675">1675</a><span>]</span><span> dan Kami akan membalik-balikan mereka ke kanan dan ke kiri, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1675a">1675a</a><span>]</span><span> dan anjing mereka sedang menjulurkan kedua kaki-depannya di halaman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1676">1676</a><span>]</span><span> Sekiranya engkau menyaksikan mereka, tentulah engkau akan berbalik dari mereka untuk melarikan diri dan tentulah engkau akan dipenuhi oleh rasa takut terhadap mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1677">1677</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8523,2,18,20,'<p><span>Dan demikianlah Kami membangkitkan mereka supaya mereka saling bertanya diantara mereka. Salah seorang dari mereka berkata, </span><span>[a]</span><span> “Sudah berapa lamakah kamu tinggal ?” Mereka berkata, “Kami telah tinggal sehari atau sebagian dari hari.” </span><span>Yang lain</span><span> berkata, “Hanya Tuhan-mu yang lebih mengetahui lamanya kamu tinggal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1678">1678</a><span>]</span><span> Maka suruhlah sekarang salah seorang dari antaramu dengan mata uangmu ini ke kota dan hendaklah ia meneliti, siapa dari antara </span><span>penghuni</span><span> kota mempunyai bahan makanan terbaik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1679">1679</a><span>]</span><span> dan hendaklah ia membawa bahan makanan bagimu darinya. Dan hendaklah ia bersikap lemah-lembut</span><span>,[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1680">1680</a><span>]</span><span> dan jangan sekali-kali dia memberitahukan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1681">1681</a><span>]</span><span> tentang kamu kepada siapa pun. </span></p><p><span> </span></p><p><span>[a]</span><span> 2:260 ; 23:113, 114.</span></p>');
INSERT INTO `quran` VALUES (8524,2,18,21,'<p><span>“karena jika mereka berkuasa atas kamu, tentulah mereka akan merajammu, atau akan memaksamu kembali ke dalam agama mereka; dan sekali-kali kamu tidak akan berhasil selama-lamanya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1682">1682</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8525,2,18,22,'<p><span>Dan demikianlah Kami memberitahukan tentang mereka supaya mereka mengetahui </span><span>[a]</span><span> sesungguhnya janji Allah itu benar, dan bahwa </span><span>[b]</span><span> saat itu tidak ada keraguan di dalamnya, </span><span>ingatlah saat itu</span><span> ketika mereka bertengkar di antara mereka mengenai urusan mereka, dan mereka berkata </span><span>satu sama lain</span><span>, “Dirikanlah di atas mereka itu suatu bangunan. ” Tuhan mereka lebih mengetahui tentang mereka. Berkata orang-orang yang unggul atas perkara mereka, “Kami pasti akan membangun di atas </span><span>tempat tinggal</span><span> mereka rumah peribadatan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1683">1683</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 31:34 ; 35:6.</span></p><p><span>[b]</span><span> 15:86 ; 20:16 ; 22:8.</span></p>');
INSERT INTO `quran` VALUES (8526,2,18,23,'<p><span>Mereka akan berkata, “</span><span>Mereka itu </span><span> tiga, yang keempatnya adalah anjing mereka,” dan mereka </span><span>yang lain </span><span>berkata, “</span><span>Mereka itu </span><span>lima, yang keenamnya adalah anjing mereka,” Dan mereka menerka secara gaib. Dan mereka </span><span>yang lainnya lagi </span><span>berkata, “</span><span>Mereka itu</span><span> tujuh, yang kedelapannya adalah anjing mereka.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1684">1684</a><span>]</span><span> Katakanlah, “Tuhan-ku lebih mengetahui bilangan mereka. Tiada orang yang mengetahui mereka kecuali sedikit.” Maka janganlah engkau bertengkar mengenai mereka kecuali dengan dalil yang tak dapat dibantah ; dan jangan pula engkau mencari keterangan tentang mereka dari salah seorang di antara mereka. </span></p>');
INSERT INTO `quran` VALUES (8527,2,18,24,'<p><span>R. 4</span></p><p><span>Dan jangan engkau sekali-kali berkata tentang sesuatu, “Aku pasti akan mengerjakannya esok hari, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1685">1685</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8528,2,18,25,'<p><span>[a]</span><span> “Kecuali bila Allah menghendaki.” Dan ingatlah kepada Tuhan engkau bila engkau lupa dan katakanlah, “Mudah-mudahan Tuhan-ku akan menunjuki aku kepada yang lebih dekat kebenarannya dari ini.” </span></p><p><span> </span></p><p><span>[a]</span><span> 18:40 ; 74:57 ; 76:31 ; 81:30.</span></p>');
INSERT INTO `quran` VALUES (8529,2,18,26,'<p><span>Dan mereka tinggal dalam gua mereka tiga ratus tahun, dan mereka tambah sembilan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1686">1686</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8530,2,18,27,'<p><span>Katakanlah, “Allah Yang lebih mengetahui berapa lamanya mereka tinggal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1687">1687</a><span>] [b]</span><span> Kepunyaan Dia rahasia seluruh langit dan bumi. </span><span>[c]</span><span> Alangkah terang penglihatan-Nya dan alangkah tajam pendengaran-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1687a">1687a</a><span>]</span><span> Mereka tidak mempunyai penolong selain Dia dan Dia tidak mengambil sekutu seorang pun dalam keputusan-Nya. </span></p><p><span> </span></p><p><span>[b]</span><span> 11:124 ; 16:78 ; 35:39.</span></p><p><span>[c]</span><span> 19:39 ; 29:46.</span></p>');
INSERT INTO `quran` VALUES (8531,2,18,28,'<p><span>Dan baacakanlah apa yang telah diwahyukan kepada engkau dari Kitab Tuhan engkau. </span><span>[a]</span><span> Tidak ada yang dapat mengubah perkataan-Nya, dan engkau tidak akan dapat selain dari Dia tempat berlindung. </span></p><p><span> </span></p><p><span>[a]</span><span> 6:35, 116 ; 10:65.</span></p>');
INSERT INTO `quran` VALUES (8532,2,18,29,'<p><span>[b]</span><span> Dan bersabarlah diri engkau bersama orang-orang yang menyeru Tuhan mereka pagi dan petang hari untuk mencari keridhaan-Nya ; dan janganlah engkau melewatkan pandangan kedua mata engkau dari mereka, </span><span>karena</span><span> engkau menghendaki perhiasan kehidupan dunia, dan janganlah engkau mengikuti orang yang Kami telah lalaikan hatinya dari mengingat Kami dan mengikuti hawa nafsunya dan urusannya telah melampaui batas. </span></p><p><span> </span></p><p><span>[b]</span><span> 6:53 ; 7:206.</span></p>');
INSERT INTO `quran` VALUES (8533,2,18,30,'<p><span>Dan katakanlah, </span><span>[c]</span><span> “</span><span>Inilah </span><span>hak dari Tuhan-mu ; maka barangsiapa menghendaki, maka berimanlah, dan barangsiapa menghendaki, maka ingkarlah.” </span><span>[d]</span><span> Sesungguhnya Kami telah menyediakan bagi orang-orang yang aniaya itu api yang dinding-dindingnya mengepung mereka. Dan jika mereka berteriak minta tolong, mereka akan ditolong dengan air laksana leburan timah, yang akan menghanguskan wajah-wajah. Sangat buruk minuman itu. Dan sangat buruk tempat tinggal itu ! </span></p><p><span> </span></p><p><span>[c]</span><span> 2:257 ; 10:100.</span></p><p><span>[d]</span><span> 25:38 ; 42:46.</span></p>');
INSERT INTO `quran` VALUES (8534,2,18,31,'<p><span>Sesungguhnya orang-orang yang beriman dan beramal shaleh, sesungguhnya </span><span>[a]</span><span> Kami tidak akan menyia-nyiakan ganjaran bagi orang-orang yang mengerjakan amalan baik. </span></p><p><span> </span></p><p><span>[a]</span><span> 7:171 ; 9:120 ; 12:57.</span></p>');
INSERT INTO `quran` VALUES (8535,2,18,32,'<p><span>[b]</span><span> Mereka itulah yang bagi mereka ada kebun-kebun abadi yang di bawahnya mengalir sungai-sungai. Mereka, di dalamnya akan dihiasi dengan gelang-gelang emas dan mereka akan mengenakan pakaian dari sutera halus </span><span>berwarna</span><span> </span><span>hijau</span><span> dan sutera tebal, mereka di dalamnya </span><span>[c]</span><span> duduk bersandar pada dipan-dipan yang indah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1688">1688</a><span>]</span><span> Alangkah baiknya ganjaran itu, dan alangkah indahnya tempat tinggal itu ! </span></p><p><span> </span></p><p><span>[b]</span><span> 9:72 ; 13:24 ; 19:62 ; 20:77 ; 35:34 ; 38:51 ; 61:13 ; 98:9.</span></p><p><span>[c]</span><span> 15:48 ; 36:57 ; 83:24.</span></p>');
INSERT INTO `quran` VALUES (8536,2,18,33,'<p><span>R. 5</span></p><p><span>Dan jelaskanlah kepada mereka misal dua orang laki-laki, Kami jadikan bagi seorang di antara keduanya dua kebun anggur, dan Kami kelilingi kedua kebun itu dengan pohon- pohon kurma, dan di antara keduanya itu Kami jadikan ladang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1689">1689</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8537,2,18,34,'<p><span>Kedua kebun itu menghasilkan buah-buahan dan tidak dikurangi darinya sedikitpun, dan di antara keduanya Kami alirkan sebuah sungai, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1690">1690</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8538,2,18,35,'<p><span>Dan ia mempunyai buah yang banyak. Maka ia berkata kepada kawannya, ketika ia bercakap-cakap dengan dia, “</span><span>Lihatlah, </span><span>aku lebih banyak dalam harta darimu dan mempunyai golongan yang lebih mulia.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1690a">1690a</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8539,2,18,36,'<p><span>Dan ia masuk ke kebunnya dalam keadaan aniaya terhadap dirinya. Ia berkata, “Aku kira </span><span>kebun</span><span> ini tidak akan binasa selama-lamanya. “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1691">1691</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8540,2,18,37,'<p><span>“Dan aku tidak mengira bahwa Saat </span><span>yang dijanjikan </span><span>itu akan datang. Dan seandainya aku dikembalikan kepada Tuhan-ku, aku pasti akan mendapat tempat kembali yang lebih baik darinya.” </span></p>');
INSERT INTO `quran` VALUES (8541,2,18,38,'<p><span>Kawannya berkata kepadanya sedang ia bercakap-cakap dengannya, “Apakah engkau ingkar kepada </span><span>[a]</span><span> Yang menciptakan engkau dari tanah, kemudian dari air mani, lalu membentuk engkau seorang laki-laki sempurna. </span></p><p><span> </span></p><p><span>[a]</span><span> 22:6 ; 23:13 ; 35:12 ; 36:78 ; 40:68.</span></p>');
INSERT INTO `quran` VALUES (8542,2,18,39,'<p><span>“Akan tetapi </span><span>[b]</span><span> Dia-lah Allah Tuhan-ku, dan aku tidak akan mempersekutukan dengan Tuhan-ku siapa pun. </span></p><p><span> </span></p><p><span>[b]</span><span> 13:37 ; 72:21.</span></p>');
INSERT INTO `quran` VALUES (8543,2,18,40,'<p><span>Dan mengapa ketika engkau memasuki kebun engkau, engkau tidak mengatakan, “Apa yang dikehendaki Allah, tiada kekuatan melainkan dengan </span><span>pertolongan </span><span> Allah, jika engkau memandang aku lebih kurang dari engkau dalam harta dan anak-anak. </span></p>');
INSERT INTO `quran` VALUES (8544,2,18,41,'<p><span>[a]</span><span> Maka Mungkin Tuhan-ku akan menganugerahkan kepadaku sesuatu yang lebih baik daripada kebun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1692">1692</a><span>]</span><span> engkau dan mengirimkan atasnya petir dari langit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1693">1693</a><span>]</span><span> sehingga menjadi dataran yang licin tandas. </span></p><p><span> </span></p><p><span>[a]</span><span> 68:33. </span></p>');
INSERT INTO `quran` VALUES (8545,2,18,42,'<p><span>“Atau dijadikan airnya kering, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1694">1694</a><span>]</span><span> sehingga engkau tidak akan mampu mencarinya.” </span></p>');
INSERT INTO `quran` VALUES (8546,2,18,43,'<p><span>[b]</span><span> Dan dihancurkan semua buahnya, lalu ia mulai membolak-balikkan kedua tangannya </span><span>menyesali </span><span> atas apa yang ia telah belanjakan untuk itu, sedang seluruhnya telah roboh atas para-paranya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1695">1695</a><span>]</span><span> Dan ia berkata, </span><span>[c]</span><span> Alangkah baiknya jika aku tidak memperse- kutukan siapa pun dengan Tuhan-ku.” </span></p><p><span> </span></p><p><span>[b]</span><span> 68:20.</span></p><p><span>[c]</span><span> 68:32.</span></p>');
INSERT INTO `quran` VALUES (8547,2,18,44,'<p><span>[a]</span><span> Dan tiada baginya satu golongan untuk menolongnya selain Allah, dan tidak pula ia dapat membela diri. </span></p><p><span> </span></p><p><span>[a]</span><span> 28:82.</span></p>');
INSERT INTO `quran` VALUES (8548,2,18,45,'<p><span>[b]</span><span> Dalam keadaan yang demikian pertolongan itu </span><span>hanya</span><span> dari Allah, Yang Maha Benar. Dia sebaik-baik pemberi ganjaran dan sebaik-baik pemberi balasan. </span></p><p><span> </span></p><p><span>[b]</span><span> 40:17 ; 82:20.</span></p>');
INSERT INTO `quran` VALUES (8549,2,18,46,'<p><span>R. 6</span></p><p><span>[c]</span><span> </span><span>Dan jelaskanlah kepada mereka misal kehidupan dunia ; seperti air yang Kami turunkan dari awan, lalu tumbuh-tumbuhan bumi bercampur dengannya ; kemudian ia menjadi kering dan hancur dan tersebar diterbangkan oleh angin ke mana-mana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1696">1696</a><span>]</span><span> Dan Allah Mempunyai Kekuasaan atas segala sesuatu. </span></p><p><span> </span></p><p><span>[c]</span><span> 10:25 ; 57:21.</span></p>');
INSERT INTO `quran` VALUES (8550,2,18,47,'<p><span>[d]</span><span> Harta dan anak-anak laki-laki adalah perhiasan kehidupan dunia. Tetapi, amal shaleh yang kekal lebih baik di sisi Tuhan engkau dalam hal ganjaran, dan lebih baik dalam hal harapan. </span></p><p><span> </span></p><p><span>[d]</span><span> 3:15 ; 57:21.</span></p>');
INSERT INTO `quran` VALUES (8551,2,18,48,'<p><span>Dan pada hari, ketika </span><span>[a]</span><span> Kami akan memperjalankan gunung-gunung dan engkau akan melihat bangsa-bangsa di bumi akan berhadapan </span><span>untuk berperang</span><span> ; dan akan Kami himpun mereka </span><span>semuanya</span><span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1697">1697</a><span>]</span><span> maka tiadalah yang Kami tinggalkan di antara mereka seorang pun. </span></p><p><span> </span></p><p><span>[a]</span><span> 52:11 ; 78:21 ; 81:4.</span></p>');
INSERT INTO `quran` VALUES (8552,2,18,49,'<p><span>[b]</span><span> Dan mereka </span><span>[c]</span><span> akan dihadapkan ke hadirat Tuhan engkau dengan berbaris. Sesungguhnya kamu datang kepada Kami seperti Kami jadikan kamu pada kali pertama. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1698">1698</a><span>]</span><span> Tetapi kamu menyangka bahwa sekali-kali Kami tidak akan menetapkan janji bagi kamu. </span></p><p><span> </span></p><p><span>[b]</span><span> 78:39.</span></p><p><span>[c]</span><span> 6:95.</span></p>');
INSERT INTO `quran` VALUES (8553,2,18,50,'<p><span>[d]</span><span> Dan kitab </span><span>amalannya</span><span> akan diletakkan </span><span>di hadapan mereka,</span><span> maka engkau akan melihat orang-orang yang berdosa itu ketakutan dari apa yang ada di dalamnya itu ; dan mereka akan berkata, “Aduhai, celakalah kami ! Kitab apakah ini ? Tiada ia meninggalkan sesuatu, </span><span>baik</span><span> yang kecil maupun yang besar, melainkan telah mencatatnya.” </span><span>[e]</span><span> Dan mereka menjumpai apa yang telah mereka kerjakan itu berada di hadapan </span><span>mereka,</span><span> dan Tuhan engkau tidak menganiaya seorang pun. </span></p><p><span> </span></p><p><span>[d]</span><span> 39:70.</span></p><p><span>[e]</span><span> 3:31 ; 99:8, 9.</span></p>');
INSERT INTO `quran` VALUES (8554,2,18,51,'<p><span>R. 7</span></p><p><span>[a]</span><span> Dan </span><span>ingatlah</span><span> ketika Kami berkata kepada para malaikat, “Sujudlah bersama Adam” maka bersujudlah mereka, kecuali iblis. Ia adalah dari </span><span>golongan</span><span> jin ; maka ia mendurhakai perintah Tuhannya. Apakah kamu hendak mengambil dia dan keturunan- nya sebagai sahabat-sahabat selain Aku, padahal mereka itu musuh-musuhmu ? Sangat buruklah bagi orang-orang aniaya pertukaran itu. </span></p><p><span> </span></p><p><span>[a]</span><span> 2:35 ; 7:12 ; 15:30, 31 ; 17:62 ; 20:117 ; 38:73, 75.</span></p>');
INSERT INTO `quran` VALUES (8555,2,18,52,'<p><span>Aku tidak membuat mereka menyaksikan penciptaan seluruh langit dan bumi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1699">1699</a><span>]</span><span> dan tidak pula penciptaan mereka sendiri; dan tidak dapat </span><span>aku </span><span>ambil mereka yang menyesatkan </span><span>orang-orang</span><span> sebagai pembantu. </span></p>');
INSERT INTO `quran` VALUES (8556,2,18,53,'<p><span>Dan </span><span>ingatlah </span><span> hari, ketika Dia akan berfirman </span><span>kepada mereka,</span><span> </span><span>[a]</span><span> “Panggillah mereka yang kamu anggap sekutu-sekutu-Ku. “ Lalu mereka akan memanggil mereka itu, tetapi mereka itu tidak akan menjawabnya ; dan Kami jadikan di antara mereka suatu penghalang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1700">1700</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 16:28 ; 28:63, 75 ; 41:48.</span></p>');
INSERT INTO `quran` VALUES (8557,2,18,54,'<p><span>Dan </span><span>[b]</span><span> orang-orang yang berdosa akan melihat Api itu, dan menyadari bahwa mereka akan jatuh ke dalamnya ; dan mereka tidak akan dapat menemukan darinya tempat berpaling. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1701">1701</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 21:40 ; 38:60 ; 52:14.</span></p>');
INSERT INTO `quran` VALUES (8558,2,18,55,'<p><span>R. 8</span></p><p><span>[c]</span><span> Dan sesungguhnya telah Kami jelaskan di dalam Alquran ini untuk manusia, bermacam- macam misal, </span><span>[d]</span><span> tetapi dalam segala sesuatu manusia yang paling banyak membantah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1702">1702</a><span>]</span></p><p><span> </span></p><p><span>[c]</span><span> 17:42, 90.</span></p><p><span>[d]</span><span> 16:5 ; 36:78.</span></p>');
INSERT INTO `quran` VALUES (8559,2,18,56,'<p><span>[a]</span><span> Dan tidak ada yang menghalangi manusia beriman, ketika petunjuk datang kepada mereka, dan dari memohon ampunan dari Tuhan mereka, kecuali datang kepada mereka keadaan orang-orang dahulu, atau azab datang kepada mereka berhadap-hadapan. </span></p><p><span> </span></p><p><span>[a]</span><span> 17:95.</span></p>');
INSERT INTO `quran` VALUES (8560,2,18,57,'<p><span>[b]</span><span> Dan Kami tidak mengutus rasul-rasul, melainkan sebagai pembawa khabar suka dan sebagai pemberi peringatan. Dan orang-orang yang ingkar membantah dengan kebatilan, supaya mereka dapat menghapuskan kebenaran dengan itu. Dan mereka menjadikan Tanda-tanda-Ku dan apa yang telah diperingatkan kepada mereka sebagai olok-olok. </span></p><p><span> </span></p><p><span>[b]</span><span> 2:214 ; 4:166 ; 6:49 ; 17:106.</span></p>');
INSERT INTO `quran` VALUES (8561,2,18,58,'<p><span>Dan siapakah yang lebih aniaya dari orang yang telah diberi peringatan dengan Tanda-tanda Tuhan-nya, malahan berpaling darinya dan lupa apa yang telah dikerjakan oleh kedua tangannya ? </span><span>[c]</span><span> Sesungguhnya telah Kami letakkan tutupan atas hati mereka, supaya mereka tidak memahaminya, dan dalam telinga mereka ada ketulian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1703">1703</a><span>]</span><span> Dan jika engkau penggil mereka kepada petunjuk, maka selamanya mereka tidak akan mau menerima petunjuk. </span></p><p><span> </span></p><p><span>[c]</span><span> 2:8 ; 6:26 ; 17:47 ; 41:6 ; 47:17.</span></p>');
INSERT INTO `quran` VALUES (8562,2,18,59,'<p><span>[a]</span><span> Dan Tuhan engkau Maha Pengampun, Pemilik Rahmat. </span><span>[b]</span><span> Sekiranya Dia berkehendak menghukum mereka disebabkan apa yang telah mereka usahakan, niscaya akan Dia segerakan azab itu bagi mereka. Tetapi bagi mereka ada masa yang telah ditetapkan yang mereka sekali-kali tidak akan memperoleh tempat berlindung selainnya. </span></p><p><span> </span></p><p><span>[a]</span><span> 6:134, 148.</span></p><p><span>[b]</span><span> 10:12 ; 35:46.</span></p>');
INSERT INTO `quran` VALUES (8563,2,18,60,'<p><span>[c]</span><span> Dan negeri-negeri itu telah Kami binasakan tatkala mereka berbuat aniaya. Dan telah Kami tetapkan kebinasaan mereka yang dijanjikan. </span></p><p><span> </span></p><p><span>[c]</span><span> 11:101.</span></p>');
INSERT INTO `quran` VALUES (8564,2,18,61,'<p><span>R. 9</span></p><p><span>Dan </span><span>ingatlah</span><span> ketika Musa berkata kepada </span><span>teman</span><span> yang muda-nya. “Aku tidak akab berhenti </span><span>menempuh perjalanan ini</span><span> sebelum aku sampai ke tempat pertemuan dua lautan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1704">1704</a><span>]</span><span> meskipun aku harus berkelana berabad-abad lamanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1704a">1704a</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8565,2,18,62,'<p><span>Maka tatkala mereka berdua sampai ke tempat di mana kedua </span><span>lautan </span><span> itu bertemu, mereka berdua lupa ikan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1705">1705</a><span>]</span><span> dan </span><span>ikan</span><span> itu cepat-cepat mengambil jalan ke laut. </span></p>');
INSERT INTO `quran` VALUES (8566,2,18,63,'<p><span>Dan ketika mereka berdua telah melewati </span><span>tempat itu,</span><span> berkatalah ia kepada </span><span>teman</span><span> muda nya “Bawalah makanan pagi kita. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1706">1706</a><span>]</span><span> Sesungguhnya kita telah merasa letih disebabkan perjalanan kita. </span></p>');
INSERT INTO `quran` VALUES (8567,2,18,64,'<p><span>Ia berkata, “Apakah engkau melihat, ketika kita berhenti untuk istirahat di batu keras tadi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1707">1707</a><span>]</span><span> dan aku lupa ikan itu, dan tiada yang membuat aku melupakannya untuk mengingatkannya itu kecuali syaitan, dan ia mengambil jalannya ke laut dengan menakjubkan </span></p>');
INSERT INTO `quran` VALUES (8568,2,18,65,'<p><span>Ia berkata. “Itulah </span><span>tempat </span><span> yang kita cari, “Maka mereka berdua kembali sambil mengikuti lagi jejak kaki mereka berdua. </span></p>');
INSERT INTO `quran` VALUES (8569,2,18,66,'<p><span>Maka mereka bertemu dengan seorang hamba dari hamba-hamba Kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1708">1708</a><span>]</span><span> yang telah Kami anugerahi rahmat dari Kami, dan telah Kami ajarkan kepadanya ilmu dari hadirat Kami. </span></p>');
INSERT INTO `quran` VALUES (8570,2,18,67,'<p><span>Musa berkata kepadanya, “Bolehkan aku mengikuti engkau supaya engkau mengajarkan kepadaku petunjuk dari apa-apa yang telah diajarkan kepada engkau ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1709">1709</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8571,2,18,68,'<p><span>Dia berkata, “Sesungguhnya engkau sekali-kali tidak akan sanggup sabar bersamaku, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1710">1710</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8572,2,18,69,'<p><span>“Dan bagaimanakah engkau dapat bersabar tentang sesuatu yang engkau tidak dapat menguasai ilmunya ? “</span></p>');
INSERT INTO `quran` VALUES (8573,2,18,70,'<p><span>Dia berkata, “Engkau akan mendapatiku sebagai seorang yang sabar jika Allah menghendaki dan aku tidak akan menolak </span><span>apa pun</span><span> perintah engkau.” </span></p>');
INSERT INTO `quran` VALUES (8574,2,18,71,'<p><span>Ia berkata, “Baiklah jika engkau mau ikut aku, maka </span><span>[a]</span><span> janganlah engkau menanyakan kepadaku tentang sesuatu sebelum aku menceriterakan kepada engkau.” </span></p><p><span> </span></p><p><span>[a]</span><span> 11:47 ; 17:37.</span></p>');
INSERT INTO `quran` VALUES (8575,2,18,72,'<p><span>R. 10</span></p><p><span>Maka berangkatlah keduanya hingga mereka berdua naik perahu ia melubanginya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1711">1711</a><span>] </span><span>Musa </span><span>berkata, “Apakah engkau melubangi perahu itu untuk menenggelamkan penumpangnya ? Sesungguhnya engkau telah berbuat sesuatu yang tidak disukai.” </span></p>');
INSERT INTO `quran` VALUES (8576,2,18,73,'<p><span>Ia berkata, “Bukankah telah kukatakan bahwa sesungguhnya engkau sekali-kali tidak akan sanggup sabar bersamaku ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1712">1712</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8577,2,18,74,'<p><span>Musa</span><span> berkata, “Janganlah engkau menuntutku karena aku lupa dan janganlah engkau berlaku keras terhadap urusanku.” </span></p>');
INSERT INTO `quran` VALUES (8578,2,18,75,'<p><span>Maka keduanya berangkat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1713">1713</a><span>]</span><span> hingga ketika keduanya bertemu dengan seorang pemuda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1713a">1713a</a><span>]</span><span> lalu ia membunuhnya. </span><span>Musa</span><span> berkata, </span><span>[a]</span><span> “Apakah engkau membunuh seseorang yang tidak berdosa tanpa </span><span>ia pernah membunuh </span><span> orang lain ? Sesungguhnya engkau telah berbuat sesuatu sangat buruk ! “</span></p><p><span> </span></p><p><span>[a]</span><span> 5:33.</span></p>');
INSERT INTO `quran` VALUES (8579,2,18,76,'<p><span>JUZ XVI</span></p><p><span>Ia berkata, “Bukankah telah kukatakan kepada engkau, sesungguhnya engkau sekali-kali tidak akan dapat bersabar bersama aku ? “</span></p>');
INSERT INTO `quran` VALUES (8580,2,18,77,'<p><span>Ia, </span><span>Musa</span><span> berkata, “Jika aku bertanya kepada engkau mengenai sesuatu sesudah ini, maka janganlah engkau mengikutkan aku bersama engkau ; sebab engkau pasti akan mempunyai cukup dalih dari diriku. </span></p>');
INSERT INTO `quran` VALUES (8581,2,18,78,'<p><span>Maka berangkatlah keduanya, hingga ketika mereka sampai kepada penduduk sebuah kota, mereka berdua meminta makanan kepada penduduknya, tetapi mereka menilak untuk menerima kedua orang itu sebagai tamunya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1714">1714</a><span>]</span><span> Maka mereka berdua menjumpai di sana sebuah dinding yang hampir runtuh, maka ia memperbaikinya. Berkatalah ia, </span><span>Musa,</span><span> “Sekiranya engkau menghendaki, niscaya engkau dapat mengambil upah untuk itu.” </span></p>');
INSERT INTO `quran` VALUES (8582,2,18,79,'<p><span>Ia berkata, “Inilah perpisahan antara aku dengan engkau. </span><span>[a]</span><span> Sekarang akan kuberitahukan kepada engkau ta</span><span>’</span><span>wil dari apa yang engkau tidak dapat memikulnya dengan sabar. </span></p><p><span> </span></p><p><span>[a]</span><span> 3:8 ; 12:22.</span></p>');
INSERT INTO `quran` VALUES (8583,2,18,80,'<p><span>“Adapun perahu itu adalah kepunyaan beberapa orang miskin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1715">1715</a><span>]</span><span> yang bekerja di laut ; aku bermaksud merusaknya, karena dibelakang mereka ada seorang raja yang biasa merampas setiap perahu dengan paksa. </span></p>');
INSERT INTO `quran` VALUES (8584,2,18,81,'<p><span>“Dan adapun anak muda itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1716">1716</a><span>]</span><span> maka kedua orang tuanya adalah orang-orang mukmin, lalu kami khawatir bahwa dia akan melibatkan kedua orangtuanya kedalam pelanggaran dan kekufuran. </span></p>');
INSERT INTO `quran` VALUES (8585,2,18,82,'<p><span>“Maka kami menginginkan agar Tuhan mereka akan memberi ganti kepada mereka berdua </span><span>anak</span><span> yang lebih baik dari dia dalam kesucian dan lebih dekat dalam kasih sayang. </span></p>');
INSERT INTO `quran` VALUES (8586,2,18,83,'<p><span>“Dan ada pun dinding itu, maka ia adalah kepunyaan dua anak laki-laki yatim </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1717">1717</a><span>]</span><span> di kota itu, dan di bawah dinding itu terpendam harta kepunyaan mereka berdua, dan ayah keduanya adalah </span><span>seorang </span><span> yang shaleh, maka Tuhan engkau menghendaki, supaya kedua anak itu sampai kepada kedewasaannya dan mereka berdua akan mengeluarkan harta mereka, sebagai suatu rahmat dari Tuhan engkau ; dan aku tidak berbuat atas kemauanku sendiri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1717a">1717a</a><span>]</span><span> Demikianlah ta</span><span>’</span><span>wil dari apa yang engkau tidak dapat memikulnya dengan sabar.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1718">1718</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8587,2,18,84,'<p><span>R. 11</span></p><p><span>Dan mereka menanyakan pula kepada engkau mengenai Zulqanain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1719">1719</a><span>]</span><span> Katakanlah, “Aku akan segera bacakan kepadamu kisah mengenainya.” </span></p>');
INSERT INTO `quran` VALUES (8588,2,18,85,'<p><span>[a]</span><span> Sesungguhnya Kami telah memberikan kekuasaan kepadanya di bumi dan Kami telah berikan kepadanya jalan untuk </span><span>mencapai </span><span> segala sesuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1720">1720</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 12:22, 57.</span></p>');
INSERT INTO `quran` VALUES (8589,2,18,86,'<p><span>Kemudian ia mengikuti jalan </span><span>lain.</span><span> </span></p>');
INSERT INTO `quran` VALUES (8590,2,18,87,'<p><span>Hingga ketika ia sampai ke tempat terbenamnya matahari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1721">1721</a><span>]</span><span> didapatinya matahari </span><span>seolah-olah</span><span> sedang terbenam ke dalam sebuah laut yang hitam airnya, dan mendapati di dekatnya suatu kaum. Kami berkata, “Ya Zulqarnain, engkau boleh menghukum mereka atau memperlakukan mereka dengan baik.” </span></p>');
INSERT INTO `quran` VALUES (8591,2,18,88,'<p><span>Ia berkata, </span><span>[a]</span><span> “Mengenai orang yang aniaya, tentu kami akan menghukumnya ; kemudian ia akan dikembalikan kepada Tuhan-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1722">1722</a><span>] </span><span>maka Dia akan menyiksanya dengan azab yang dashyat.” </span></p><p><span> </span></p><p><span>[a]</span><span> 7:166.</span></p>');
INSERT INTO `quran` VALUES (8592,2,18,89,'<p><span>[b]</span><span> Tetapi mengenai orang beriman dan beramal shaleh, maka baginya ada ganjaran yang baik, dan Kami segera akan berkata kepadanya dari perintah Kamu yang mudah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1723">1723</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 2:26 ; 3:58 ; 6:49 ; 19:61 ; 25:71 ; 36:38.</span></p>');
INSERT INTO `quran` VALUES (8593,2,18,90,'<p><span>Kemudian ia mengikuti jalan </span><span>lain.</span><span> </span></p>');
INSERT INTO `quran` VALUES (8594,2,18,91,'<p><span>Hingga, ketika ia sampai ke tempat terbit matahari, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1724">1724</a><span>]</span><span> didapatinya matahari terbit di atas suatu kaum yang Kami tidak jadikan bagi mereka diantaranya pelindung. </span></p>');
INSERT INTO `quran` VALUES (8595,2,18,92,'<p><span>Demikianlah </span><span>keadaannya.</span><span> Dan sesungguhnya ilmu Kami meliputi segala sesuatu yang ada padanya. </span></p>');
INSERT INTO `quran` VALUES (8596,2,18,93,'<p><span>Kemudian ia mengikuti jalain </span><span>lain.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1725">1725</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8597,2,18,94,'<p><span>Hingga, ketika ia sampai ke tempat di antara dua bukit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1726">1726</a><span>]</span><span> ia dapati di bawah keduanya suatu kaum yang hampir tidak mengerti sepatah kata pun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1727">1727</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8598,2,18,95,'<p><span>Mereka berkata, “Ya Zulqarnain, sesungguhnya Yajuj dan Majuj membuat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1728">1728</a><span>]</span><span> kekacauan di bumi ; maka bolehlah kami membayar upeti kepadamu asalkan engkau membuat di antara kami dengan mereka sebuah penghalang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1729">1729</a><span>]</span><span> “ ? </span></p>');
INSERT INTO `quran` VALUES (8599,2,18,96,'<p><span>Ia berkata, “Kekuasaan yang dianugerahkan kepadaku oleh Tuhan-ku dalam hal ini adalah lebih baik, tetapi kamu boleh membantuku dengan kekuatan ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1730">1730</a><span>]</span><span> aku akan mendirikan di antara kamu dengan mereka sebuah penghalang. </span></p>');
INSERT INTO `quran` VALUES (8600,2,18,97,'<p><span>“Berikanlah kepadaku kepingan-kepingan besi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1731">1731</a><span>]</span><span> Hingga ketika ia telah meratakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1731a">1731a</a><span>]</span><span> di antara kedua bukit itu, ia berkata, </span><span>‘</span><span>Sekarang hembuskanlah.</span><span>’</span><span> Hingga ketika ia telah menjadikannya api ia berkata, “Berikanlah kepadaku cairan tembaga, supaya aku dapat menuangkan di atasnya.” </span></p>');
INSERT INTO `quran` VALUES (8601,2,18,98,'<p><span>Maka mereka, </span><span>Yajuj dan Majuj</span><span> tidak dapat memanjatnya dan tidak dapat melubanginya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1732">1732</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8602,2,18,99,'<p><span>Ia berkata, “Ini rahmat dari Tuhan-ku. Tetapi apabila telah tiba janji Tuhan-ku, Dia akan memecahkannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1733">1733</a><span>]</span><span> berkeping-keping. </span><span>[a]</span><span> Dan janji Tuhan-ku itu </span><span>pasti</span><span> benar.” </span></p><p><span> </span></p><p><span>[a]</span><span> 19:62 ; 46:17 ; 73:19.</span></p>');
INSERT INTO `quran` VALUES (8603,2,18,100,'<p><span>Dan Kami akan membiarkan sebagaian mereka pada hari itu menyerang sebagian lain ; </span><span>[b]</span><span> dan nafiri akan ditiup. Lalu akan Kami himpun mereka itu semuanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1734">1734</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 23:102 ; 36:52 ; 39:69 ; 50:21 ; 69:14.</span></p>');
INSERT INTO `quran` VALUES (8604,2,18,101,'<p><span>Dan pada hari itu Kami hadirkan Jahannam, berhadapan muka dengan orang-orang kafir, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1734a">1734a</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8605,2,18,102,'<p><span>Ialah</span><span> </span><span>[a]</span><span> orang-orang, yang mata mereka tertutup dari peringatan-Ku, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1734b">1734b</a><span>]</span><span> dan mereka tidak mampu mendengar. </span></p><p><span> </span></p><p><span>[a]</span><span> 21:43 ; 39:46.</span></p>');
INSERT INTO `quran` VALUES (8606,2,18,103,'<p><span>R. 12</span></p><p><span>Apakah mengira orang-orang yang ingkar bahwa mereka menjadikan hamba-hamba-Ku selain Aku sebagai penolong-penolong ? Sesungguhnya </span><span>[b]</span><span> Kami sediakan Jahannam bagi orang- orang kafir sebagai tempat tinggal. </span></p><p><span> </span></p><p><span>[b]</span><span> 29:69 ; 33:9 ; 48:14 ; 76:5.</span></p>');
INSERT INTO `quran` VALUES (8607,2,18,104,'<p><span>Katakanlah, “Apakah akan Kami beritahukan kepadamu mengenai orang yang paling rugi amalannya ? </span></p>');
INSERT INTO `quran` VALUES (8608,2,18,105,'<p><span>“Orang-orang yang sia-sia usahanya dalam kehidupan dunia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1735">1735</a><span>]</span><span> sedang mereka menyangka, bahwa mereka mengerjakan perbuatan yang baik.” </span></p>');
INSERT INTO `quran` VALUES (8609,2,18,106,'<p><span>Mereka itu orang-orang yang ingkar kepada Tanda-tanda Tuhan-nya dan mengingkari pertemuan dengan Dia. </span><span>[a]</span><span> Maka sia-sia amalan mereka, dan Kami tidak akan mengadakan bagi mereka penilaian pada Hari Kiamat. </span></p><p><span> </span></p><p><span>[a]</span><span> 2:218 ; 3:23 ; 7:148 ; 9:69.</span></p>');
INSERT INTO `quran` VALUES (8610,2,18,107,'<p><span>Itulah balasan mereka Jahannam disebabkan mereka ingkar, dan menjadikan Tanda- tanda-Ku dan rasul-rasul-Ku sebagai olok-olok. </span></p>');
INSERT INTO `quran` VALUES (8611,2,18,108,'<p><span>Sesungguhnya orang-orang yang beriman dan beramal shaleh, bagi mereka surga Firdaus adalah tempat tinggalnya. </span></p>');
INSERT INTO `quran` VALUES (8612,2,18,109,'<p><span>[b]</span><span> Mereka tetap tinggal di dalamnya, mereka tidak ingin dipindahkan darinya. </span></p><p><span> </span></p><p><span>[b]</span><span> 11:109 ; 15:49. </span></p>');
INSERT INTO `quran` VALUES (8613,2,18,110,'<p><span>Katakanlah, </span><span>[c]</span><span> “Sekiranya lautan menjadi tinta untuk </span><span>menuliskan</span><span> kalimat-kalimat Tuhan-ku, niscaya akan habis lautan itu sebelum kalimat-kalimat Tuhan-ku habis, sekalipun Kami datangkan sebanyak itu lagi sebagai tambahan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1736">1736</a><span>]</span><span> </span></p><p><span> </span></p><p><span> [c]</span><span> 31:28.</span></p>');
INSERT INTO `quran` VALUES (8614,2,18,111,'<p><span>Katakanlah, </span><span>[a]</span><span> “Sesungguhnya aku ini hanyalah seorang manusia seperti kamu, </span><span>tetapi</span><span> telah diwahyukan kepadaku bahwa Tuhan-mu adalah Tuhan Yang Maha Esa. </span><span>[b]</span><span> Maka barangsiapa mengharap akan bertemu dengan Tuhan-nya hendaklah ia beramal shaleh dan janganlah ia mempersekutukan siapa pun dalam beribadah kepada Tuhan-nya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1737">1737</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 14:12 ; 41:7.</span></p><p><span>[b]</span><span> 2:47, 224 ; 11:30 ; 29:6 ; 84:7.</span></p>');
INSERT INTO `quran` VALUES (8615,2,19,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8616,2,19,2,'<p><span>Engkau Maha Mencukupi, Maha Pemberi Petunjuk, ya Allah, Yang Maha Mengetahui, Maha Benar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1738">1738</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8617,2,19,3,'<p><span>Inilah</span><span> penjelasan tentang rahmat Tuhan engkau, kepada hamba-Nya Zakaria, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1739">1739</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8618,2,19,4,'<p><span>[a]</span><span> Ketika ia berseru kepada Tuhan-nya, </span><span>dengan </span><span>doa yang lembut, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1740">1740</a><span>]</span><span> </span></p><p><span> </span><span>[a]</span><span> 3:39 ; 21:90.</span></p>');
INSERT INTO `quran` VALUES (8619,2,19,5,'<p><span>Ia berkata, “Ya, Tuhan-ku, sesungguhnya </span><span>[b]</span><span> tulang-tulangku telah menjadi lemah, dan kepada telah dipenuhi uban. Tetapi ya Tuhan-ku, tidak pernah aku kecewa dalam berdoa kepada Engkau. </span></p><p><span> </span></p><p><span>[b]</span><span> 3:41.</span></p>');
INSERT INTO `quran` VALUES (8620,2,19,6,'<p><span>“Dan sesungguhnya aku khawatir akan kaum-keluargaku sesudahku, sedang </span><span>[c]</span><span> istriku mandul. Maka, </span><span>[d]</span><span> anugerakanlah kepada-ku seorang penerus </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1741">1741</a><span>]</span><span> dari sisi Engkau. </span></p><p><span> </span></p><p><span>[c]</span><span> 3:41 ; 21:91.</span></p><p><span>[d]</span><span> 3:39 ; 21:90.</span></p>');
INSERT INTO `quran` VALUES (8621,2,19,7,'<p><span>“Yang akan menjadi warisku dan pewaris keturunan Ya</span><span>’</span><span>kub. Dan, ya, Tuhan-ku, </span><span>[a]</span><span> jadikanlah dia seorang yang diridhai.” </span></p><p><span> </span></p><p><span>[a]</span><span> 3:39.</span></p>');
INSERT INTO `quran` VALUES (8622,2,19,8,'<p><span>Tuhan berfirman,</span><span> “Ya Zakaria, </span><span>[b]</span><span> Kami berikan khabar suka kepada engkau tentang seorang tentang seorang anak laki-laki namanya Yahya. Tidak perrnah Kami sebut seorangpun sebelum dia </span><span>[c]</span><span> dengan nama itu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1742">1742</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 3:40 ; 21:91.</span></p><p><span>[c]</span><span> 19:66.</span></p>');
INSERT INTO `quran` VALUES (8623,2,19,9,'<p><span>Ia berkata, “Ya Tuhan-ku bagaimana akan ada seorang anak laki-laki begiku, bila </span><span>[d]</span><span> istriku mandul dan aku telah mencapai usia lanjut ? “</span></p><p><span> </span></p><p><span>[d]</span><span> 3:41 ; 21:91.</span></p>');
INSERT INTO `quran` VALUES (8624,2,19,10,'<p><span>Ia berkata, </span><span>[a]</span><span> “Demikianlah.” </span><span>Tetapi,</span><span> Tuhan engkau berfirman, “ Itu mudah bagi-Ku, dan sungguh telah Aku ciptakan engkau sebelum itu, padahal engkau </span><span>tadinya</span><span> sama sekali tidak ada apa-apa.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1743">1743</a><span>]</span></p><p><span> </span></p><p><span>[a]</span><span> 3:41, 48 ; 19:22 ; 51:31.</span></p>');
INSERT INTO `quran` VALUES (8625,2,19,11,'<p><span>Ia, </span><span>Zakaria,</span><span> berkata, “ Ya Tuhan-ku, tetapkanlah bagiku suatu Tanda. “</span><span>Dia </span><span>berfirman, “Tanda bagi engkau, bahwa engkau jangan bicara kepada manusia selama </span><span>[b]</span><span> tiga malam ber turut-turut.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1744">1744</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 3:42 ; 33:43.</span></p>');
INSERT INTO `quran` VALUES (8626,2,19,12,'<p><span>Maka ia keluar kepada kaumnya dari tempat ibadah, lalu ia memberi isyarat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1745">1745</a><span>]</span><span> kepada mereka </span><span>[c]</span><span> supaya bertasbihlah pagi dan petang. </span></p><p><span> </span></p><p><span>[c]</span><span> 3:42 ; 33:43.</span></p>');
INSERT INTO `quran` VALUES (8627,2,19,13,'<p><span>Tuhan berfirman,</span><span> “Ya Yahya, peganglah Kitab itu dengan kuat.” Dan Kami berikan kepadanya kebijaksanaan ketika masa kanak-kanak. </span></p>');
INSERT INTO `quran` VALUES (8628,2,19,14,'<p><span>Dan kelembutan </span><span>hati</span><span> dari sisi Kami dan kesucian. Dan ia adalah seorang yang bertakwa. </span></p>');
INSERT INTO `quran` VALUES (8629,2,19,15,'<p><span>[a]</span><span> Dan seorang yang berbakti kepada kedua orangtuanya. Dan ia tidak zalim, durhaka. </span></p><p><span> </span></p><p><span>[a]</span><span> 6:152 ; 19:33 ; 29:9 ; 31:15 ; 46:16.</span></p>');
INSERT INTO `quran` VALUES (8630,2,19,16,'<p><span>[b]</span><span> Dan keselamatan atasnya pada hari ia dilahirkan dan pada hari ia wafat and pada hari ia akan dibangkitkan hidup </span><span>kembali.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1746">1746</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 19:34.</span></p>');
INSERT INTO `quran` VALUES (8631,2,19,17,'<p><span>R. 2</span></p><p><span>Dan ceriterakanlah di dalam Kitab tentang Maryam, ketika ia mengasingkan diri dari keluarganya ke suatu tempat di sebelah timur ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1747">1747</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8632,2,19,18,'<p><span>Maka ia membuat diantara mereka tabir, lalu Kami utus kepadanya </span><span>[a]</span><span> malaikat Kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1748">1748</a><span>]</span><span> maka ia nampak kepadanya berupa manusia sempurna. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1749">1749</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a] </span><span>3:43.</span></p>');
INSERT INTO `quran` VALUES (8633,2,19,19,'<p><span>Maryam</span><span> berkata, “Aku berlindung kepada </span>Allah<span> Yang Maha Pemurah dari engkau, jika engkau bertakwa.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1750">1750</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8634,2,19,20,'<p><span>Ia, </span><span>malaikat</span><span> menjawab, “Sesungguhnya aku hanyalah seorang utusan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1751">1751</a><span>]</span><span> dari Tuhan engkau supaya </span><span>[a]</span><span> aku memberikan </span><span>menurut wahyu</span><span> kepada engkau, seorang anak laki-laki suci.” </span></p><p><span> </span></p><p><span>[a]</span><span> 3:46.</span></p>');
INSERT INTO `quran` VALUES (8635,2,19,21,'<p><span>Maryam </span><span> berkata, </span><span>[b]</span><span> “Bagaimana aku akan mempunyai seorang anak laki-laki, padahal tidak ada seorang menusia menyentuhku, dan aku tidak berzina” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1752">1752</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8636,2,19,22,'<p><span>[a]</span><span> Ia, </span><span>malaikat</span><span> berkata, “Demikianlah.” Tuhan engkau berfirman. “Itu mudah bagi-Ku. Dan supaya Kami menjadikan dia suatu Tanda bagi manusia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1753">1753</a><span>]</span><span> dan suatu rahmat dari Kami, dan hal itu adalah perkara yang telah diputuskan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1754">1754</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 3:41, 48 ; 19:22 ; 51:31.</span></p>');
INSERT INTO `quran` VALUES (8637,2,19,23,'<p><span>Maka Maryam mengandungnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1755">1755</a><span>]</span><span> lalu ia mengasingkan diri bersamanya ke suatu tempat yang jauh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1756">1756</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8638,2,19,24,'<p><span>Maka datang kepadanya rasa sakit melahirkan </span><span>dan memaksanya pergi</span><span> ke sebatang pohon kurma, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1757">1757</a><span>]</span><span> ia berkata, “Alangkah baiknya jika aku mati sebelum ini dan aku menjadi sesuatu yang dilupakan sama sekali ! “</span></p>');
INSERT INTO `quran` VALUES (8639,2,19,25,'<p><span>Maka ia, </span><span>malaikat, </span><span>berseru kepadanya dari arah bawah dia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1758">1758</a><span>]</span><span> “Janganlah engkau bersedih hati. Sesungguhnya Tuhan engkau telah membuat mata air di arah bawah engkau ; </span></p>');
INSERT INTO `quran` VALUES (8640,2,19,26,'<p><span>“Dan goyangkanlah ke arah engkau </span><span>pelepah</span><span> batang kurma itu ; ia akan menjatuhkan atas engkau buah kurma yang matang lagi segar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1759">1759</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8641,2,19,27,'<p><span>“Maka, makanlah dan minumlah, dan sejukkanlah mata. Dan jika engkau melihat seorang manusia, maka katakanlah, “Sesungguhnya aku telah bernazar kepada </span><span>Tuhan</span><span> Yang Maha Pemurah untuk puasa, maka aku tidak akan bercakap-cakap pada hari ini denga nseorang manusia pun.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1760">1760</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8642,2,19,28,'<p><span>Maka Maryam membawa dia kepada kaumnya, dengan menunggakannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1761">1761</a><span>]</span><span> Mereka berkata, “Hai Maryam, sesungguhnya engkau telah berbuat sesuatu hal yang keji. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1762">1762</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8643,2,19,29,'<p><span>“Hai saudara perempuan Harun, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1763">1763</a><span>]</span><span> ayah engkau bukanlah seorang buruk dan tidak pula ibu engkau seorang pezina ! “</span></p>');
INSERT INTO `quran` VALUES (8644,2,19,30,'<p><span>Maka ia memberi isyarah kepadanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1784">1784</a><span>]</span><span> Mereka berkata, “Bagaimana kami dapat bercakap dengan seorang anak masih dalam buaian ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1765">1765</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8645,2,19,31,'<p><span>Berkatalah ia, </span><span>Ibnu Maryam,</span><span> “Sesungguhnya aku seorang hamba Allah, Dia telah menganugerahkan kepadaku Kitab dan Dia telah menjadikanku seorang nabi ; </span></p>');
INSERT INTO `quran` VALUES (8646,2,19,32,'<p><span>“Dan Dia telah menjadikanku diberkati di mana pun aku berada, dan telah memerintahkan kepadaku shalat dan zakat selama aku hidup ; </span></p>');
INSERT INTO `quran` VALUES (8647,2,19,33,'<p><span>“Dan </span><span>[a]</span><span> berbakti kepada ibuku, dan Dia tidak menjadikanku seorang zalim bernasib buruk ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1766">1766</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a] </span><span>19:15.</span></p>');
INSERT INTO `quran` VALUES (8648,2,19,34,'<p><span>“Dan selamat-sejahtera atasku </span><span>[a]</span><span> pada hari aku dilahirkan dan pada hari aku wafat, dan pada hari aku akan dibangkitkan hidup.” </span></p><p><span> </span></p><p><span>[a]</span><span> 19:16.</span></p>');
INSERT INTO `quran` VALUES (8649,2,19,35,'<p><span>Itulah Isa ibnu Maryam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1767">1767</a><span>]</span><span> Suatu pernyataan benar yang mereka di dalamnya ber bantah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1768">1768</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8650,2,19,36,'<p><span>[a]</span><span> Tidak layak bagi Allah untuk mengambil seorang anak</span><span>, [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1769">1769</a><span>]</span><span> Mahasuci Dia. Apabila Dia memutuskan sesuatu, Dia hanya berfirman, “Jadilah ! “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1770">1770</a><span>]</span><span> Maka jadilah ia. </span></p><p><span> </span></p><p><span>[a] </span><span>10:69 ; 17:112 ; 18:5 ; 19:89 ; 21:27 ; 25:3 ; 39:5.</span></p>');
INSERT INTO `quran` VALUES (8651,2,19,37,'<p><span>Dan </span><span>Isa berkata,</span><span> “Sesungguhnya, </span><span>[b]</span><span> Allah adalah Tuhan-ku dan Tuhan-mu, maka sembahlah Dia. Inilah jalan yang lurus.” </span></p><p><span> </span></p><p><span>[b]</span><span> 3:52 ; 5:73 ; 43:65.</span></p>');
INSERT INTO `quran` VALUES (8652,2,19,38,'<p><span>Tetapi, golongan-golongan itu berselisih di antara mereka ; maka </span><span>[c]</span><span> celaka bagi orang- orang ingkar karena kehadiran pada Hari yang besar. </span></p><p><span> </span></p><p><span>[c]</span><span> 14:3 ; 38:28 ; 51:61.</span></p>');
INSERT INTO `quran` VALUES (8653,2,19,39,'<p><span>Alangkah tajamnya pendengaran dan penglihatan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1771">1771</a><span>]</span><span> pada hari bila mereka akan datang kepada Kami ! Akan tetapi pada hari ini orang-orang aniaya itu ada dalam kesesatan yang nyata. </span></p>');
INSERT INTO `quran` VALUES (8654,2,19,40,'<p><span>Dan peringatkan mereka tentang </span><span>[a]</span><span> hari penyesalan ketika diputuskan segala urusan. Sedangkan mereka dalam keadaan lalai, dan mereka tidak beriman. </span></p><p><span> </span></p><p><span>[a]</span><span> 2:168 ; 6:32 ; 39:57.</span></p>');
INSERT INTO `quran` VALUES (8655,2,19,41,'<p><span>[b]</span><span> Sesungguhnya Kami yang akan jadi waris </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1772">1772</a><span>]</span><span> bumi dan seriap orang yang ada di atasnya ; dan kepada Kami mereka akan dikembalikan. </span></p><p><span> </span></p><p><span>[b]</span><span> 15:24 ; 28:59.</span></p>');
INSERT INTO `quran` VALUES (8656,2,19,42,'<p><span>R. 3</span></p><p><span>[c]</span><span> Dan ceritakan </span><span>kisah </span><span> Ibrahim yang </span><span>ada </span><span> dalam kitab itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1773">1773</a><span>]</span><span> Sesungguhnya, ia benar </span><span>dan seorang </span><span>nabi. </span></p><p><span> </span></p><p><span>[c]</span><span> 38:47 ; 53:38.</span></p>');
INSERT INTO `quran` VALUES (8657,2,19,43,'<p><span>Ketika ia berkata kepada ayahnya, </span><span>[a]</span><span> “Wahai bapakku, mengapakah engkau menyembah sesuatu yang tidak mendengar, dan tidak melihat, dan tidak dapat memberi sesuatu manfaat kepada engkau ? </span></p><p><span> </span></p><p><span>[a]</span><span> 6:75 ; 21:53 ; 26:71 ; 37:86 </span><span>–</span><span> 87.</span></p>');
INSERT INTO `quran` VALUES (8658,2,19,44,'<p><span>“Wahai, bapakku, sesungguhnya telah datang kepadaku ilmu yang belum pernah datang kepada engkau ; maka ikutilah aku, aku akan menunjukkan kepada engkau jalan yang lurus ; </span></p>');
INSERT INTO `quran` VALUES (8659,2,19,45,'<p><span>“Wahai bapakku, janganlah </span><span>[b]</span><span> menyembah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1774">1774</a><span>]</span><span> syaitan. Sesungguhnya syaitan itu durhaka terhadap terhadap Yang Maha Pemurah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1775">1775</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 6:143 ; 24:22 ; 36:61.</span></p>');
INSERT INTO `quran` VALUES (8660,2,19,46,'<p><span>“Wahai bapakku, sesungguhnya aku khawatir bahwa akan menimpa engkau azab dari Yang Maha Pemurah, lalu engkau menjadi kawan syaitan.” </span></p>');
INSERT INTO `quran` VALUES (8661,2,19,47,'<p><span>Ia berkata, “Apakah engkau benci kepada tuhan-tuhanku, wahai Ibrahim ? </span><span>[c]</span><span> Jika engkau tidak berhenti, niscaya aku akan memutuskan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1776">1776</a><span>]</span><span> segala hubungan dengan engkau. Dan tinggalkanlah aku sampai masa yang lama.” </span></p><p><span> </span></p><p><span>[c]</span><span> 21:69 ; 29:25 ; 37:98.</span></p>');
INSERT INTO `quran` VALUES (8662,2,19,48,'<p><span>Ibrahim</span><span> berkata, “Selamat sejahtera atas engkau. </span><span>[a]</span><span> Aku akan memohon ampunan bagi engkau kepada Tuhan-ku. Sesungguhnya Dia sangat baik terhadapku ; </span></p><p><span> </span></p><p><span>[a]</span><span> 9:114 ; 26:87 ; 60:5.</span></p>');
INSERT INTO `quran` VALUES (8663,2,19,49,'<p><span>“Dan </span><span>[b]</span><span> aku akan menjauhkan diri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1777">1777</a><span>]</span><span> dari kamu dan dari apa yang kamu seru selain Allah ; dan aku akan berdoa kepada Tuhan-ku. Mudah-mudahan dalam berdoa kepada Tuhan-ku, aku tidak bernasib buruk.” </span></p><p><span> </span></p><p><span>[b]</span><span> 29:27.</span></p>');
INSERT INTO `quran` VALUES (8664,2,19,50,'<p><span>Maka ketika ia telah memisahkan diri dari mereka dan dari apa yang mereka sembah selain Allah, </span><span>[c]</span><span> Kami anegerahkan kepadanya Ishak dan Ya</span><span>’</span><span>kub. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1778">1778</a><span>]</span><span> Dan semua Kami jadikan nabi. </span></p>');
INSERT INTO `quran` VALUES (8665,2,19,51,'<p><span>Dan Kami menganugerahkan kepada mereka sebagian rahmat Kami ; dan </span><span>[d] </span><span>Kami menjadi kan mereka buah tutur yang benar </span><span>dan </span><span> luhur.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1779">1779</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[d]</span><span> 26:85.</span></p>');
INSERT INTO `quran` VALUES (8666,2,19,52,'<p><span>Dan ceriterakanlah </span><span>kisah </span><span>Musa di dalam Kitab </span><span>Alquran, </span><span>Sesungguhnya </span><span>[a]</span><span> ia seorang pilihan ; serta ia seorang rasul, seorang nabi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1780">1780</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 33:70.</span></p>');
INSERT INTO `quran` VALUES (8667,2,19,53,'<p><span>Dan Kami memanggilnya dari </span><span>[a]</span><span> sebelah kanan gunung, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1781">1781</a><span>]</span><span> dan Kami mendekatkannya untuk bermunajat. </span></p><p><span> </span></p><p><span>[a]</span><span> 20:81 ; 28:31.</span></p>');
INSERT INTO `quran` VALUES (8668,2,19,54,'<p><span>Dan Kami berikan kepadanya dari rahmat Kami </span><span>yakni </span><span> </span><span>[b]</span><span> saudaranya, Harun, </span><span>sebagai</span><span> nabi, </span></p><p><span> </span></p><p><span>[b]</span><span> 28:30, 31 ; 25:36 ; 28:36.</span></p>');
INSERT INTO `quran` VALUES (8669,2,19,55,'<p><span>Dan ceriterakan </span><span>kisah</span><span> Ismail </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1782">1782</a><span>]</span><span> di dalam Kitab </span><span>Alquran</span><span>. Sesungguhnya ia adalah seorang yang benar-benar setia pada janji-janjinya. Dan ia adalah seorang rasul, seorang nabi. </span></p>');
INSERT INTO `quran` VALUES (8670,2,19,56,'<p><span>Dan </span><span>[c]</span><span> ia senantiasa menyuruh keluarganya shalat dan zakat, dan ia diridhai oleh Tuhan-nya. </span></p><p><span> </span></p><p><span>[c]</span><span> 20:133 ; 33:34.</span></p>');
INSERT INTO `quran` VALUES (8671,2,19,57,'<p><span>Dan ceriterakanlah </span><span>kisah </span><span> Idris </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1783">1783</a><span>]</span><span> di dalam Kitab </span><span>Alquran</span><span>. Sesungguhnya ia adalah seorang nabi yang benar. </span></p>');
INSERT INTO `quran` VALUES (8672,2,19,58,'<p><span>Dan </span><span>[a]</span><span> Kami telah mengangkat derajat yang tinggi. </span></p><p><span> </span></p><p><span>[a] </span><span>2:254 ; 4:159.</span></p>');
INSERT INTO `quran` VALUES (8673,2,19,59,'<p><span>Mereka inilah orang-orang </span><span>[b]</span><span> yang Allah telah memberi nikmat atas mereka dari nabi-nabi, dari keturunan Adam, dan dari antara </span><span>keturunan</span><span> orang-orang yang Kami angkut </span><span>dalam bahtera</span><span> bersama Nuh, dan dari keturunan Ibrahim dan Israil, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1784">1784</a><span>]</span><span> dan </span><span>mereka itu</span><span> di antara orang-orang yang telah Kami beri petunjuk dan telah Kami pilih. </span><span>[c]</span><span> Tatkala Ayat-ayat Yang Maha Pemurah dibacakan kepada mereka, mereka tersungkur sujud dan menangis</span></p><p><span> </span></p><p><span>[b]</span><span> 1:7 ; 4:70 ; 5:21 ; 57:20.</span></p><p><span>[c]</span><span> 17:108, 110 ; 32:16.</span></p>');
INSERT INTO `quran` VALUES (8674,2,19,60,'<p><span>Lalu datanglah sesudah mereka </span><span>[d]</span><span> suatu keturunan yang mengabaikan shalat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1785">1785</a><span>]</span><span> dan mengikuti hawa-nafsu. Maka mereka dalam waktu dekata akan menemui kesesatan, </span></p><p><span> </span></p><p><span>[d]</span><span> 7:170.</span></p>');
INSERT INTO `quran` VALUES (8675,2,19,61,'<p><span>Kecuali </span><span>[a]</span><span> orang yang bertobat dan beriman dan beramal shaleh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1786">1786</a><span>]</span><span> Maka mereka itu lah akan masuk surga, dan mereka tidak akan dianiaya sedikit pun. </span></p><p><span> </span></p><p><span>[a] </span><span>6:49 ; 18:89 ; 25:71 ; 34:38.</span></p>');
INSERT INTO `quran` VALUES (8676,2,19,62,'<p><span>[b]</span><span> Surga-surga yang kekal yang telah dijanjikan oleh Yang Maha Pemurah kepada hamba-hamba-Nya dengan ghaib. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1787">1787</a><span>]</span><span> Sesungguhnya janji-Nya pasti akan terjadi. </span></p><p><span> </span></p><p><span>[b]</span><span> 9:72 ; 13:24 ; 61:13.</span></p>');
INSERT INTO `quran` VALUES (8677,2,19,63,'<p><span>[c]</span><span> Tidaklah mereka akan mendengar di dalamnya sesuatu yang sia-sia, kecuali </span><span>ucapan </span><span> selamat, dan mereka akan memperoleh rezeki mereka di dalamnya, pagi dan petang. </span></p><p><span> </span></p><p><span>[c]</span><span> 52:24 ; 56:26 ; 78:36.</span></p>');
INSERT INTO `quran` VALUES (8678,2,19,64,'<p><span>[d]</span><span> Itulah surga yang akan Kami wariskan kepada sebagian hamba-hamba Kami yang bertakwa. </span></p><p><span> </span></p><p><span>[d]</span><span> 7:44 ; 43:73 ; 52:18.</span></p>');
INSERT INTO `quran` VALUES (8679,2,19,65,'<p><span>Dan </span><span>malaikat-malaikat akan berkata,</span><span> “Kami tidak akan turun kecuali dengan perintah Tuhan engkau. Kepunyaan Dia-lah segala yang ada di hadapan kami dan segala yang ada di belakang kami dan segala yang ada di antaranya. Dan tidaklah Tuhan engkau itu pelupa. </span></p>');
INSERT INTO `quran` VALUES (8680,2,19,66,'<p><span>“</span><span>Dia </span><span>[a]</span><span> Tuhan seluruh langit dan bumi, dan segala yang ada di antara keduanya. Maka sembahlah Dia, dan bersiteguhlah dalam beribadah kepada-Nya. Apakah engkau mengerahui ada yang sama dengan Dia ? “</span></p><p><span> </span></p><p><span>[a]</span><span> 37:6 ; 38:67 ; 44:8 ; 78:38.</span></p>');
INSERT INTO `quran` VALUES (8681,2,19,67,'<p><span>R. 5</span></p><p><span>Dan manusia berkata, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1788">1788</a><span>] [b]</span><span> “Apakah jika aku mati, pasti akan dibangkitkan hidup kembali ? “</span></p><p><span> </span></p><p><span>[b]</span><span> 23:38 ; 36:79.</span></p>');
INSERT INTO `quran` VALUES (8682,2,19,68,'<p><span>Tidaklah manusia ingat bahwa </span><span>[c]</span><span> Kami telah menciptakan dia dahulu, padahal dia ketika itu tidak ada sama sekali ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1789">1789</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[c]</span><span> 19:10 ; 76:2.</span></p>');
INSERT INTO `quran` VALUES (8683,2,19,69,'<p><span>Maka demi Tuhan engkau, niscaya akan </span><span>[d]</span><span> Kami himpun mereka dan syaitan-syaitan ; kemudian Kami pasti akan menghadirkan mereka di sekeliling Jahannam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1790">1790</a><span>]</span><span> dalam keadaan berlutut. </span></p><p><span> </span></p><p><span>[d]</span><span> 10:29 ; 17:98 ; 34:41.</span></p>');
INSERT INTO `quran` VALUES (8684,2,19,70,'<p><span>Kemudian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1791">1791</a><span>]</span><span> Kami pasti akan memisahkan dari tiap-tiap golongan, siapa-siapa diantara mereka yang lebih keras terhadap Yang Maha Pemurah dalam kedurhakaan. </span></p>');
INSERT INTO `quran` VALUES (8685,2,19,71,'<p><span>Dan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1792">1792</a><span>]</span><span> sesungguhnya Kami lebih mengetahui orang-orang yang lebih layak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1793">1793</a><span>]</span><span> dibakar di dalamnya. </span></p>');
INSERT INTO `quran` VALUES (8686,2,19,72,'<p><span>Dan </span><span>[a]</span><span> tiada seorang pun dari antara kami </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1793a">1793a</a><span>]</span><span> melainkan akan mendatangi </span><span>neraka </span><span>itu. Inilah ketetapan mutlak Tuhan engkau. </span></p><p><span> </span></p><p><span>[a]</span><span> 21:99.</span></p>');
INSERT INTO `quran` VALUES (8687,2,19,73,'<p><span>[a]</span><span> Kemudian akan Kami selamatkan orang-orang yang bertakwa, dan akan Kami biarkan orang-orang yang aniaya itu berlutut di dalamnya. </span></p><p><span> </span></p><p><span>[a]</span><span> 21:102 ; 39:62.</span></p>');
INSERT INTO `quran` VALUES (8688,2,19,74,'<p><span>Dan apabila dibacakan kepada mereka Ayat-ayat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1794">1794</a><span>]</span><span> Kami yang nyata, berkata orang- orang yang ingkar kepada orang-orang yang beriman, “Siapakah di antara dua golongan yang lebih baik kedudukannya dan lebih baik sebagai kawan seiring ? “</span></p>');
INSERT INTO `quran` VALUES (8689,2,19,75,'<p><span>Dan </span><span>[a]</span><span> berapa banyaknya Kami telah binasakan sebelum mereka dari generasi, yang lebih baik dalam kekayaan dan dalam pandangan ! </span></p><p><span> </span></p><p><span>[a]</span><span> 6:7 ; 17:18 ; 19:99 ; 21:12 ; 36:32 ; 50:37.</span></p>');
INSERT INTO `quran` VALUES (8690,2,19,76,'<p><span>Katakanlah, “Barangsiapa berada dalam kesesatan, maka diberi tangguh baginya oleh Yang Maha Pemurah sampai suatu waktu, </span><span>[b]</span><span> hingga mereka akan melihat apa yang telah dijanjikan kepada mereka, apakah azab, atau apakah Saat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1795">1795</a><span>]</span><span> Maka mereka akan segera mengetahui siapa yang paling buruk kedudukan-</span><span>nya</span><span> dan paling lemah kekuatan pasukan</span><span>nya.</span><span> </span></p><p><span> </span></p><p><span>[b] </span><span>72:25.</span></p>');
INSERT INTO `quran` VALUES (8691,2,19,77,'<p><span>[c]</span><span> Dan Allah menambah petunjuk kepada orang-orang yang mendapat petunjuk. </span><span>[d]</span><span> Dan amal-amal shaleh yang kekal, lebih baik di sisi Tuhan engkau sebagai ganjaran, dan paling baik kesudahan. </span></p><p><span> </span></p><p><span>[c]</span><span> 9:124 ; 47:18 ; 48:5.</span></p><p><span>[d] </span><span>87:18.</span></p>');
INSERT INTO `quran` VALUES (8692,2,19,78,'<p><span>Maka, apakah engkau tidak melihat orang yang ingkar kepada Tanda-tanda Kami, dan ia berkata, </span><span>[a]</span><span> “Sesungguhnya aku akan diberi harta dan anak.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1796">1796</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 18:35 ; 74:13 </span><span>–</span><span> 14.</span></p>');
INSERT INTO `quran` VALUES (8693,2,19,79,'<p><span>Apakah ia mengetahui yang gaib ataukah ia telah membuat janji di sisi Yang Maha Pemurah ? </span></p>');
INSERT INTO `quran` VALUES (8694,2,19,80,'<p><span>Sekali-kali tidak ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1797">1797</a><span>]</span><span> Pasti Kami akan catat apa yang ia katakan dan Kami akan benar-benar memperpanjang azab baginya. </span></p>');
INSERT INTO `quran` VALUES (8695,2,19,81,'<p><span>Dan Kami akan mewarisi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1798">1798</a><span>]</span><span> apa-apa yang ia katakan, dan </span><span>[b]</span><span> ia akan datang kepada Kami seorang diri. </span></p><p><span> </span></p><p><span>[b]</span><span> 6:95 ; 18:49.</span></p>');
INSERT INTO `quran` VALUES (8696,2,19,82,'<p><span>[a]</span><span> Dan mereka telah mengambil tuhan-tuhan selain Allah, supaya mereka akan menjadi </span><span>sumber</span><span> kehormatan bagi mereka. </span></p><p><span> </span></p><p><span>[a]</span><span> 21:25 ; 36:75.</span></p>');
INSERT INTO `quran` VALUES (8697,2,19,83,'<p><span>Sama-sekali tidak ! </span><span>[b]</span><span> Mereka itu akan mengingkari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1799">1799</a><span>]</span><span> penyembahan mereka dan akan menjadi lawan mereka. </span></p><p><span> </span></p><p><span>[b]</span><span> 6:24 ; 10:29.</span></p>');
INSERT INTO `quran` VALUES (8698,2,19,84,'<p><span>R. 6</span></p><p><span>Tidaklah engkau melihat bahwa </span><span>[c]</span><span> Kami telah mengirim syaitan-syaitan kepada orang-orang kafir untuk menghasut mereka dengan hasutan. </span></p><p><span> </span></p><p><span>[c]</span><span> 8:49 ; 47:26 ; 59:17.</span></p>');
INSERT INTO `quran` VALUES (8699,2,19,85,'<p><span>Maka janganlah engkau tergesa-gesa bertindak terhadap mereka ; sesungguhnya Kami akan menghitung dengan teliti bagi mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1800">1800</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8700,2,19,86,'<p><span>Ingatlah</span><span> hari ketika </span><span>[d]</span><span> Kami akan menghimpun orang-orang yang bertakwa di hadapan Yang Maha Pemurah dalam bentuk kelompok ; </span></p><p><span> </span></p><p><span>[d] </span><span>39:74.</span></p>');
INSERT INTO `quran` VALUES (8701,2,19,87,'<p><span>Dan </span><span>[a]</span><span> Kami akan menggiring orang-orang berdosa itu ke Jahannam dalam keadaan dahaga. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1801">1801</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 39:72.</span></p>');
INSERT INTO `quran` VALUES (8702,2,19,88,'<p><span>[b]</span><span> Mereka tidak mampu memberi syafaat, kecuali dia yang telah membuat janji di sisi Yang Maha Pemurah. </span></p><p><span> </span></p><p><span>[b]</span><span> 2:49 ; 20 :100 ; 21:29 ; 34:24 ; 39:45 ; 43:87 ; 53:27 ; 74:49.</span></p>');
INSERT INTO `quran` VALUES (8703,2,19,89,'<p><span>[c]</span><span> Dan mereka itu berkata, “Yang Maha Pemurah telah mengambil seorang anak laki-laki.” </span></p><p><span> </span></p><p><span>[c]</span><span> 2:117 ; 4:172 ; 6:101 </span><span>–</span><span> 102 ; 10:69 ; 17:112 ; 18:5 ; 19:36 ; 21:27 ; 25:3 ; 39:5 ; 43:82.</span></p>');
INSERT INTO `quran` VALUES (8704,2,19,90,'<p><span>Sesungguhnya kamu telah mengucapkan sesuatu yang sangat mengerikan. </span></p>');
INSERT INTO `quran` VALUES (8705,2,19,91,'<p><span>Hampir-hampir seluruh langit pecah oleh karenanya, dan bumi tebelah, dan gunung-gunung runtuh berkeping-keping. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1802">1802</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8706,2,19,92,'<p><span>Karena mereka mengaku bagi Yang Maha Pemurah mempunyai anak laki-laki. </span></p>');
INSERT INTO `quran` VALUES (8707,2,19,93,'<p><span>[a]</span><span> Padahal tidak layak bagi Yang Maha Pemurah, mengambil seorang anak laki-laki. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1803">1803</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 2:117 ; 4:172 ; 10:69 ; 37:152 </span><span>–</span><span> 155.</span></p>');
INSERT INTO `quran` VALUES (8708,2,19,94,'<p><span>[b]</span><span> Tiada seorang pun di seluruh langit dan bumi melainkan ia akan datang kepada Yang Maha Pemurah sebagai hamba.. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1804">1804</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 20:109.</span></p>');
INSERT INTO `quran` VALUES (8709,2,19,95,'<p><span>Sesungguhnya, Dia melingkupi mereka dan menghitung mereka dengan teliti. </span></p>');
INSERT INTO `quran` VALUES (8710,2,19,96,'<p><span>Dan setiap mereka akan datang kepada-Nya pada Hari Kiamat sendiri-sendiri. </span></p>');
INSERT INTO `quran` VALUES (8711,2,19,97,'<p><span>Sesungguhnya orang-orang beriman dan beramal shaleh Yang Maha Pemurah akan menanamkan kecintaan bagi mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1805">1805</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8712,2,19,98,'<p><span>Maka sesungguhnya </span><span>[a]</span><span> Kami telah memudahkan </span><span>Alquran</span><span> itu dalam bahasa engkau supaya dengan itu engkau dapat menyampaikan khabar suka kepada orang-orang bertakwa, dan memberi peringatan dengan itu kepada kaum yang suka berbantah. </span></p><p><span> </span></p><p><span>[a]</span><span> 44:59 ; 54:18.</span></p>');
INSERT INTO `quran` VALUES (8713,2,19,99,'<p><span>[b]</span><span> Dan berapa banyaknya yang telah Kami binasakan generasi sebelum mereka ! Apakah generasi sebelum mereka ! Apakah engkau merasakan seorang saja dari antara mereka, atau kamu mendengar suara sayup-sayup mereka ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1806">1806</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 17:18 ; 19:75 ; 21:12 ; 36:32 ; 50:37.</span></p>');
INSERT INTO `quran` VALUES (8714,2,20,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8715,2,20,2,'<p><span>Hai manusia yang sempurna. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1807">1807</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8716,2,20,3,'<p><span>Kami tidak menurunkan Alquran kepada engkau supaya engkau susah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1808">1808</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8717,2,20,4,'<p><span>Melainkan sebagai suatu </span><span>[b]</span><span> peringatan bagi siapa yang takut.</span></p><p><span>[b]</span><span> 73:20 ; 74:55 ; 76:30 ; 80:12.</span></p>');
INSERT INTO `quran` VALUES (8718,2,20,5,'<p><span>Diturunkan dari Dia Yang menciptakan bumi dan seluruh langit yang tinggi.</span></p>');
INSERT INTO `quran` VALUES (8719,2,20,6,'<p><span>[c]</span><span> Yang Maha Pemurah, bersemayam teguh di atas </span><span>‘</span><span>Arasy. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1809">1809</a><span>]</span></p><p><span>[c]</span><span> 7:55 ; 10:4.</span></p>');
INSERT INTO `quran` VALUES (8720,2,20,7,'<p><span>[a]</span><span> Kepunyaan Dia apa-apa yang ada di seluruh langit dan apa-apa yang ada di bumi, dan apa-apa yang ada di antara keduanya, dan apa-apa yang ada di bawah tanah lembab. </span></p><p><span> </span></p><p><span>[a]</span><span> 2:285 ; 3:130 ; 5:19.</span></p>');
INSERT INTO `quran` VALUES (8721,2,20,8,'<p><span>[b]</span><span> Dan jika engkau berkata dengan suara keras maka sesungguhnya dia mengetahui yang rahasia dan apa yang sangat tersembunyi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1810">1810</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 2:78 ; 6:4 ; 11:6 ; 67:14.</span></p>');
INSERT INTO `quran` VALUES (8722,2,20,9,'<p><span>Allah, tiada tuhan selain Dia </span><span>[c]</span><span> Kepunyaan Dia segala nama yang terbaik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1811">1811</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[c]</span><span> 7:181 ; 59:25.</span></p>');
INSERT INTO `quran` VALUES (8723,2,20,10,'<p><span>[d]</span><span> Dan apakah sudah sampai kepada engkau kisah Musa ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1812">1812</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[d]</span><span> 19:52 ; 79:16.</span></p>');
INSERT INTO `quran` VALUES (8724,2,20,11,'<p><span>[a]</span><span> Ketika ia melihat api, lalu ia berkata kepada keluarganya. “Tunggulah kamu, sesungguhnya aku melihat api ; mudah-mudahan dapat kubawa bagimu bara api darinya ; atau aku memperoleh perunjuk pada api itu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1813">1813</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 27:8 ; 28:30.</span></p>');
INSERT INTO `quran` VALUES (8725,2,20,12,'<p><span>[b]</span><span> Maka, ketika ia datang kepada </span><span>api</span><span> itu, ia dipanggil </span><span>oleh suatu suara, </span><span>“Hai Musa, </span></p><p><span> </span></p><p><span>[b]</span><span> 27:9 ; 28:31 ; 79:17.</span></p>');
INSERT INTO `quran` VALUES (8726,2,20,13,'<p><span>“Sesungguhnya Aku, Tuhan engkau, maka tanggalkanlah kedua sepatumu ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1814">1814</a><span>] [c]</span><span> karena sesungguhnya engkau telah berada di lembah suci Thuwa. </span></p><p><span> </span></p><p><span>[c]</span><span> 20:13 ; 28:31 ; 79:17.</span></p>');
INSERT INTO `quran` VALUES (8727,2,20,14,'<p><span>[d]</span><span> “Dan Aku memilih engkau ; maka dengarkan apa yang diwahyukan, </span></p><p><span> </span></p><p><span>[d]</span><span> 20:42.</span></p>');
INSERT INTO `quran` VALUES (8728,2,20,15,'<p><span>[a]</span><span> “Sesungguhnya, Aku Allah ; tiada tuhan selain Aku, maka sembahlah Aku, dan dirikanlah shalat untuk mengingat-Ku; </span></p><p><span> </span></p><p><span>[a]</span><span> 27:10 ; 28:31.</span></p>');
INSERT INTO `quran` VALUES (8729,2,20,16,'<p><span>[b]</span><span> “Sesungguhnya, saat </span><span>Kiamat</span><span> itu akan datang, dan Aku hampir menampakkannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1815">1815</a><span>]</span><span> supaya setiap jiwa dibalas menurut apa yang ia usahakan ; </span></p><p><span> </span></p><p><span>[b]</span><span> 5:86 ; 40:60.</span></p>');
INSERT INTO `quran` VALUES (8730,2,20,17,'<p><span>“Maka janganlah memalingkan engkau dari </span><span>Kiamat</span><span> itu, orang yang tidak percaya kepadanya dan mengikuti hawa nafsunya, sehingga engkau akan binasa. </span></p>');
INSERT INTO `quran` VALUES (8731,2,20,18,'<p><span>“Dan apa itu di tangan kanan engkau, wahai Musa ? “</span></p>');
INSERT INTO `quran` VALUES (8732,2,20,19,'<p><span>Ia, </span><span>Musa</span><span>, berkata, “Inilah tongkatku, aku bertelekan padanya, dan aku rontokkan dengan-nya daun-daunan untuk kambing-kambingku, dan bagiku di dalamnya ada beberapa manfaat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1816">1816</a><span>]</span><span> lain.” </span></p>');
INSERT INTO `quran` VALUES (8733,2,20,20,'<p><span>Dia berfirman, </span><span>[c]</span><span> “Lemparkanlah </span><span>tongkat</span><span> itu, wahai Musa.” </span></p><p><span> </span></p><p><span>[c]</span><span> 7:118 ; 26:33 ; 27:11 ; 28:32.</span></p>');
INSERT INTO `quran` VALUES (8734,2,20,21,'<p><span>Maka ia melemparkannya, lalu tiba-tiba ia melihat seekor ular yang berlari. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1816a">1816a</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8735,2,20,22,'<p><span>Dan berfirman, “Peganglah dia dan jangan engkau takut. Kami akan mengembalikan kepada keadaannya semula. </span></p>');
INSERT INTO `quran` VALUES (8736,2,20,23,'<p><span>[a]</span><span> “Dan kepitlah tangan engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1817">1817</a><span>]</span><span> ke ketiak engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1818">1818</a><span>]</span><span> ia akan keluar menjadi putih tanpa penyakit, sebagai suatu Tanda lain, </span></p><p><span> </span></p><p><span>[a]</span><span> 7:109 l 27:13 ; 28:33.</span></p>');
INSERT INTO `quran` VALUES (8737,2,20,24,'<p><span>“Supaya dapat Kami memperlihatkan kepada engkau beberapa dari Tanda-tanda Kami yang besar ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1819">1819</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8738,2,20,25,'<p><span>“Pergilah engkau kepada Firaun, sesungguhnya ia telah melanggar batas.” </span></p>');
INSERT INTO `quran` VALUES (8739,2,20,26,'<p><span>R. 2</span></p><p><span>Ia, </span><span>Musa</span><span>, berkata, “Ya Tuhan-ku, lapangkan bagiku dadaku, </span></p>');
INSERT INTO `quran` VALUES (8740,2,20,27,'<p><span>“Dan mudahkan bagiku urusanku ; </span></p>');
INSERT INTO `quran` VALUES (8741,2,20,28,'<p><span>[a]</span><span> “Dan lepaskan kekakuan dari lidahku ; </span></p><p><span> </span></p><p><span>[a]</span><span> 26:14.</span></p>');
INSERT INTO `quran` VALUES (8742,2,20,29,'<p><span>“Supaya mereka dapat memahami bicaraku ; </span></p>');
INSERT INTO `quran` VALUES (8743,2,20,30,'<p><span>“Dan jadikanlah bagiku seorang penolong </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1820">1820</a><span>]</span><span> dari antara keluargaku, </span></p>');
INSERT INTO `quran` VALUES (8744,2,20,31,'<p><span>[b]</span><span> “Harun saudaraku ; </span></p><p><span> </span></p><p><span>[b]</span><span> 28:35.</span></p>');
INSERT INTO `quran` VALUES (8745,2,20,32,'<p><span>“Teguhkanlah dengan dia kekuatanku</span></p>');
INSERT INTO `quran` VALUES (8746,2,20,33,'<p><span>[c]</span><span> “Dan ikut sertakan dia dalam urusanku ; </span></p><p><span> </span></p><p><span>[c]</span><span> 26:16.</span></p>');
INSERT INTO `quran` VALUES (8747,2,20,34,'<p><span>“Supaya kami banyak bertasbih kepada Engkau. </span></p>');
INSERT INTO `quran` VALUES (8748,2,20,35,'<p><span>“Dan kami banyak mengingat Engkau ; </span></p>');
INSERT INTO `quran` VALUES (8749,2,20,36,'<p><span>“Sesungguhnya Engkau Maha Melihat kepada kami.” </span></p>');
INSERT INTO `quran` VALUES (8750,2,20,37,'<p><span>[a]</span><span> Dia berfirman, “Sesungguhnya, telah diberikan permintaan engkau, wahai Musa, </span></p><p><span> </span></p><p><span>[a]</span><span> 26:16.</span></p>');
INSERT INTO `quran` VALUES (8751,2,20,38,'<p><span>“Dan, sesungguhnya, Kami telah berbuat baik atas engkau pada kesempatan lain ; </span></p>');
INSERT INTO `quran` VALUES (8752,2,20,39,'<p><span>“Ketika </span><span>[b]</span><span> Kami wahyukan kepada ibu engkau, apa yang diwahyukan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1821">1821</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 28:8, 9.</span></p>');
INSERT INTO `quran` VALUES (8753,2,20,40,'<p><span>“Supaya letakkan dia dalam peti, dan lemparkan peti itu di sungai, maka sungai itu akan menghanyutkannya ke tepi, di sana </span><span>[c]</span><span> seorang akan mengambilnya, musuh bagi-Ku dan musuh baginya.” Dan Aku menanamkan kecintaan kepada engkau dari-Ku supaya engkau terpelihara di hadapan penglihatan-Ku ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1822">1822</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[c]</span><span> 28:9.</span></p>');
INSERT INTO `quran` VALUES (8754,2,20,41,'<p><span>[a]</span><span> Ketika saudara perempuan engkau berjalan lalu berkata, “Apakah boleh aku tunjukkan kepadamu seseorang yang akan memelihara dia ? “ Maka </span><span>[b]</span><span> Kami kembalikan engkau kepada ibumu, supaya jadi sejuk matanya dan jangan pula ia bersedih. Dan </span><span>[c]</span><span> engkau telah membunuh seseorang, lalu Kami selamatkan engkau dari kedukaan. Dan Kami menguji engkau dengan berbagai ujian. Lalu engkau tinggal beberapa tahun di antara orang-orang Madyan. Kemudian engkau sampai kepada waktu yang ditentukan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1823">1823</a><span>]</span><span> wahai Musa. </span></p><p><span> </span></p><p><span>[a]</span><span> 28:12, 13.</span></p><p><span>[b]</span><span> 28:14.</span></p><p><span>[c]</span><span> 28:16, 34.</span></p>');
INSERT INTO `quran` VALUES (8755,2,20,42,'<p><span>“Dan </span><span>[d]</span><span> Aku telah memilih engkau untuk diri-Ku ; </span></p><p><span> </span></p><p><span>[d]</span><span> 12:55.</span></p>');
INSERT INTO `quran` VALUES (8756,2,20,43,'<p><span>[e]</span><span> Pergilah engkau dan saudara engkau dengan Tanda-tanda-Ku, dan janganlah lalai mengingat Aku ; </span></p><p><span> </span></p><p><span>[e]</span><span> 28:36.</span></p>');
INSERT INTO `quran` VALUES (8757,2,20,44,'<p><span>[f]</span><span> “Pergilah kamu berdua, kepada Firaun, sebab sesungguhnya ia telah melampui batas, </span></p><p><span> </span></p><p><span>[f]</span><span> 79:18.</span></p>');
INSERT INTO `quran` VALUES (8758,2,20,45,'<p><span>“Maka hendaklah kamu berdua katakan kepadanya perkataan lemah-lembut </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1824">1824</a><span>]</span><span> supaya ia dapat nasihat atau takut.” </span></p>');
INSERT INTO `quran` VALUES (8759,2,20,46,'<p><span>Mereka berdua berkata, </span><span>[a]</span><span> “Ya Tuhan kami, sesungguhnya kami takut ia akan bertindak keterlaluan terhadap kami, atau ia melanggar batas.” </span></p><p><span> </span></p><p><span>[a]</span><span> 26:13.</span></p>');
INSERT INTO `quran` VALUES (8760,2,20,47,'<p><span>Dia berfirman, “Janganlah kamu berdua takut karena sesungguhnya Aku berserta kamu berdua, Aku mendengar dan Aku melihat.” </span></p>');
INSERT INTO `quran` VALUES (8761,2,20,48,'<p><span>Maka datanglah kamu berdua kepadanya dan katakanlah, “Kami berdua adalah rasul dari Tuhan engkau ; maka perkenankanlah pergi beserta kami Bani Israil, dan janganlah menyiksa mereka, Sesungguhnya kami telah membawa kepada engkau Tanda dari Tuhan engkau. Dan selamat atas orang yang mengikuti petunjuk. </span></p>');
INSERT INTO `quran` VALUES (8762,2,20,49,'<p><span>Sesungguhnya telah diwahyukan kepada kami, bahwa azab akan diturunkan atas orang yang mendustakan dan berpaling.” </span></p>');
INSERT INTO `quran` VALUES (8763,2,20,50,'<p><span>Ia, </span><span>Firaun,</span><span> berkata, </span><span>[b]</span><span> “Maka siapakah Tuhan kamu berdua, ya Musa ? “</span></p><p><span> </span></p><p><span>[b]</span><span> 26:24.</span></p>');
INSERT INTO `quran` VALUES (8764,2,20,51,'<p><span>Berkata </span><span>Musa,</span><span> “Tuhan kami </span><span>[a]</span><span> Yang memberikan segala sesuatu bentuk yang serasi dan kemudian Dia memberi petunjuk.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1825">1825</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 87:3, 4.</span></p>');
INSERT INTO `quran` VALUES (8765,2,20,52,'<p><span>Ia, </span><span>Firaun,</span><span> berkata, “bagaimanakah nasib generasi-generasi terdahulu ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1826">1826</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8766,2,20,53,'<p><span>Ia, </span><span>Musa,</span><span> berkata, “Ilmu </span><span>mengenai</span><span> itu di sisi Tuhan-ku dalam sebuah Kitab. Tuhan-ku tidak sesat dan </span><span>[b]</span><span> tidak lupa ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1827">1827</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 87:3, 4.</span></p><p><span>[b]</span><span> 19:65.</span></p>');
INSERT INTO `quran` VALUES (8767,2,20,54,'<p><span>[a]</span><span> “</span><span>Dialah </span><span> Yang telah menjadikan bagi kamu bumi sebagai hamparan dan telah mengadakan bagi kamu di dalamnya banyak jalan. Dan Dia yang menurunkan air dari langit. Maka dengan itu Kami jadikan berpasang-pasangan dari bermacam-macam tumbuh-tumbuhan. </span></p><p><span> </span></p><p><span>[a]</span><span> 43:11.</span></p>');
INSERT INTO `quran` VALUES (8768,2,20,55,'<p><span>[b]</span><span> “Kamu makanlah dan gembalakanlah ternakmu. Sesungguhnya dalam hal demikian ada Tanda-tanda bagi orang-orang yang berakal.” </span></p><p><span> </span></p><p><span>[b]</span><span> 10:25 ; 25:50 ; 32:28.</span></p>');
INSERT INTO `quran` VALUES (8769,2,20,56,'<p><span>R. 3</span></p><p><span>[c]</span><span> Dari </span><span>bumi</span><span> ini Kami telah menciptakanmu dan ke dalamnya Kami akan mengembalikan kamu dan dan darinya Kami akan mengeluarkan kamu untuk kedua kalinya. </span></p><p><span> </span></p><p><span>[c]</span><span> 7:26 ; 71:18, 19.</span></p>');
INSERT INTO `quran` VALUES (8770,2,20,57,'<p><span>Dan, sesungguhnya, telah </span><span>[d]</span><span> Kami perlihatkan kepadanya, Tanda-tanda Kami semuanya ; tetapi ia mendustakan dan menolak. </span></p><p><span> </span></p><p><span>[d]</span><span> 27:13 </span><span>–</span><span> 15 ; 43:48, 49 ; 79:21, 22.</span></p>');
INSERT INTO `quran` VALUES (8771,2,20,58,'<p><span>Dia berkata, </span><span>[e]</span><span> “Apakah engkau datang kepada kami, supaya mengusir kami dari negeri kami dengan sihirmu, ya, Musa ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1828">1828</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[e]</span><span> 26:36.</span></p>');
INSERT INTO `quran` VALUES (8772,2,20,59,'<p><span>[f]</span><span> “Maka Kami akan datangkan kepada engkau sihir semacam itu ; maka tetapkanlah di antara kami dengan engkau suatu waktu dan tempat perjanjian yang kita tidak akan melanggarnya, kami tidak dan engkau pun tidak, pada suatu tempat yang sama.” </span></p><p><span> </span></p><p><span>[f]</span><span> 7:112, 113 ; 26:37.</span></p>');
INSERT INTO `quran` VALUES (8773,2,20,60,'<p><span>Ia, </span><span>Musa,</span><span> berkata, </span><span>[a]</span><span> “Waktu perjanjian kamu ialah pada hari raya dan supaya manusia dikumpulkan pada waktu matahari sepenggalah tingginya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1829">1829</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 26:39.</span></p>');
INSERT INTO `quran` VALUES (8774,2,20,61,'<p><span>Maka Firaun berpaling meninggalkan </span><span>tempat itu</span><span> lalu mengatur tipu dayanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1830">1830</a><span>]</span><span> kemudian ia datang </span><span>lagi.</span><span> </span></p>');
INSERT INTO `quran` VALUES (8775,2,20,62,'<p><span>Musa berkata kepada mereka, “Celakalah kamu, janganlah kamu mengada-adakan dusta terhadap Allah, maka Dia membinasakan kamu dengan azab ; dan sesungguhnya telah gagal orang yang mengada-adakan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1831">1831</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8776,2,20,63,'<p><span>Maka mereka mempertengkarkan perkara mereka diantara mereka dan mengadakan perundingan rahasia. </span></p>');
INSERT INTO `quran` VALUES (8777,2,20,64,'<p><span>Mereka berkata, “Sesungguhnya, </span><span>[a]</span><span> kedua orang ini hanyalah tukang sihir yang berusaha mengusir kamu dari negerimu dengan sihir mereka berdua dan menghapuskan kebiasaan kamu yang terbaik ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1831a">1831a</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 7:110, 111 ; 26:35, 36.</span></p>');
INSERT INTO `quran` VALUES (8778,2,20,65,'<p><span>“Maka himpunlah rencanamu, kemudian datanglah berbaris. Dan, sesungguhnya akan beruntunglah siapa yang unggul pada hari ini.” </span></p>');
INSERT INTO `quran` VALUES (8779,2,20,66,'<p><span>[b]</span><span> Mereka itu berkata, “Ya Musa, apakah engkau yang akan melempar, ataukah kami yang pertama akan melempar ? “</span></p><p><span> </span></p><p><span>[b]</span><span> 7:116.</span></p>');
INSERT INTO `quran` VALUES (8780,2,20,67,'<p><span>[c]</span><span> Berkata ia, </span><span>Musa,</span><span> “Bahkan kamu yang melempar.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1832">1832</a><span>]</span><span> Maka tiba-tiba tali-tali mereka dan tongkat-tongkat mereka terbayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1833">1833</a><span>]</span><span> kepadanya seolah-olah berlari karena sihir mereka. </span></p><p><span> </span></p><p><span>[c]</span><span> 7:117 ; 26:44.</span></p>');
INSERT INTO `quran` VALUES (8781,2,20,68,'<p><span>Maka Musa merasa takut pada jiwanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1834">1834</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8782,2,20,69,'<p><span>Kami berkata, “Kamu jangan takut karena sesungguhnya engkaulah yang paling unggul. </span></p>');
INSERT INTO `quran` VALUES (8783,2,20,70,'<p><span>“Dan lemparkanlah apa yang ada di tangan kanan engkau </span><span>[a]</span><span> itu akan menelan apa yang mereka buat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1835">1835</a><span>]</span><span> karena apa yang mereka perbuat hanyalah tipu-daya tukang sihir. Dan tukang sihir tidak akan berhasil dari mana saja ia datang ! “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1835a">1835a</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 7:118 ; 26:46.</span></p>');
INSERT INTO `quran` VALUES (8784,2,20,71,'<p><span>Maka </span><span>[b]</span><span> </span><span>kesadaran akan kebenaran</span><span> membuat semua tukang sihir itu tersungkur sujud. Mereka berkata, </span><span>[c]</span><span> “Kami beriman kepada tuhan Harun dan Musa.” </span></p><p><span> </span></p><p><span>[b]</span><span> 7:121 ; 26:47.</span></p><p><span>[c]</span><span> 7:122, 123 ; 26:28, 49.</span></p>');
INSERT INTO `quran` VALUES (8785,2,20,72,'<p><span>Ia, </span><span>Firaun,</span><span> berkata, </span><span>[d]</span><span> “Apakah kamu percaya kepadanya, sebelum aku beri izin kepadamu ? Pastilah ia pemimpinmu yang telah mengajarkan kepadamu sihir. </span><span>[e]</span><span> Maka, niscaya akan kupotong tanganmu dan kakimu secar bersilangan timbal-balim ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1835b">1835b</a><span>]</span><span> dan tentu akan ku salibkan kamu pada batang-batang kurma ; dan niscaya kamu akan mengetahui siapa di antara kita lebih keras azabnya lebih kekal.” </span></p><p><span> </span></p><p><span>[d]</span><span> 7:124 ; 26:50.</span></p><p><span>[e]</span><span> 7:125 ; 26:50.</span></p>');
INSERT INTO `quran` VALUES (8786,2,20,73,'<p><span>Mereka berkata, “Kami sekali-kali tidak akan mengutamakan engkau atas Tanda-tanda nyata yang telah datang kepada kami, dan tidak juga dari Dzat Yang telah menciptakan kami. </span><span>[a]</span><span> Maka putuskanlah apa yang hendak engkau putuskan. Sesungguhnya engkau hanya dapat memutuskan mengenai kehidupan dunia ini. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1836">1836</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 26:51. </span></p>');
INSERT INTO `quran` VALUES (8787,2,20,74,'<p><span>“Sesungguhnya, </span><span>[b]</span><span> kami telah beriman kepada Tuhan kami ; supaya Dia mengampuni untuk kami kesalahan-kesalahan kami, dan sihir yang telah engkau paksakan kepada kami </span><span>melakukannya.</span><span> Dan Allah lebih baik dan lebih kekal.” </span></p><p><span> </span></p><p><span>[b]</span><span> 7:127 ; 26:52.</span></p>');
INSERT INTO `quran` VALUES (8788,2,20,75,'<p><span>Sesungguhnya, barang siapa datang kepada Tuhan-nya sebagai orang berdosa, maka sesungguhnya Jahannam baginya Ia tidak akan mati di dalamnya dan tidak </span><span>pula</span><span> hidup. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1837">1837</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8789,2,20,76,'<p><span>Dan barangsiapa datang kepada-Nya sebagai orang beriman </span><span>dan</span><span> mengerjakan amal shaleh, maka itulah </span><span>[a]</span><span> bagi mereka derajat yang tertinggi. </span></p><p><span> </span></p><p><span>[a]</span><span> 4:96, 97 ; 8:5.</span></p>');
INSERT INTO `quran` VALUES (8790,2,20,77,'<p><span>[b]</span><span> Kebun-kebun yang kekal, di bawahnya mengalir sungai-sungai ; mereka akan tinggal kekal di dalamnya. Dan demikianlah ganjaran bagi orang-orang yang mensucikan dirinya. </span></p><p><span> </span></p><p><span>[b]</span><span> 9:72 ; 18:32 ; 19:62 ; 61:13.</span></p>');
INSERT INTO `quran` VALUES (8791,2,20,78,'<p><span>R. 4</span></p><p><span>Dan sesungguhnya telah </span><span>[c]</span><span> Kami wahyukan kepada Musa, “Bawalah pada waktu malam hamba-hamba-Ku lalu </span><span>[d]</span><span> tunjukkan kepada mereka suatu jalan di laut yang kering, jangan lah engkau khawatir disergap, dan jangan engkau takut. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1838">1838</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[c]</span><span> 26:53.</span></p><p><span>[d]</span><span> 26:64.</span></p>');
INSERT INTO `quran` VALUES (8792,2,20,79,'<p><span>[a]</span><span> Maka Firaun dengan lasykar-lasykarnya mengejar mereka, maka </span><span>air</span><span> laut sama sekali meliputi mereka. </span></p><p><span> </span></p><p><span>[a]</span><span> 10:91 ; 26:61.</span></p>');
INSERT INTO `quran` VALUES (8793,2,20,80,'<p><span>Dan Firaun telah menyesatkan kaumnya dan tidak memberi petunjuk. </span></p>');
INSERT INTO `quran` VALUES (8794,2,20,81,'<p><span>Hai, Bani Israil, </span><span>[b]</span><span> Kami telah menyelamatkan kamu dari musuhmu, dan Kami telah mengadakan perjanjian denganmu </span><span>[c]</span><span> di sebelah kanan gunung Thur dan Kami telah menurunkan atasmu manna dan salwa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1839">1839</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 2:51 ; 14::7 ; 44:31, 32/</span></p><p><span>[c]</span><span> 19:53 ; 20:13 ; 28:31 ; 79:17.</span></p>');
INSERT INTO `quran` VALUES (8795,2,20,82,'<p><span>[a]</span><span> “Makanlah diantara barang-barang baik yang telah Kami rezekikan kepadamu, dan janganlah kamu melampui batas di dalamnya, maka jangan-jangan nanti kemurkaan-Ku turun atasmu ; dan barangsiapa kepadanya turun kemurkaan-Ku, niscaya akan binasa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1840">1840</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 2:58 ; 7:161.</span></p>');
INSERT INTO `quran` VALUES (8796,2,20,83,'<p><span>“Dan </span><span>[b]</span><span> sesungguhnya Aku Maha Pengampun bagi siapa yang bertobat dan beriman dan beramal shaleh, kemudian tetap berpegang pada petunjuk.” </span></p><p><span> </span></p><p><span>[b]</span><span> 3:136 ; 39:54.</span></p>');
INSERT INTO `quran` VALUES (8797,2,20,84,'<p><span>“Dan, apakah yang membuat engkau tergesa-gesa memdahului kaummu, hai Musa ? “</span></p>');
INSERT INTO `quran` VALUES (8798,2,20,85,'<p><span>Ia, </span><span>Musa,</span><span> berkata, “Mereka itu mengikuti jejakku dan aku segera menghadap Engkau, ya Tuhan-ku, agar kiranya Engkau ridha.” </span></p>');
INSERT INTO `quran` VALUES (8799,2,20,86,'<p><span>Dia berfirman, </span><span>[c]</span><span> “Maka, sesungguhnya, telah Kami uji kaummu sepeninggal kamu dan seorang Samiri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1841">1841</a><span>]</span><span> telah menyesatkan mereka.” </span></p><p><span> </span></p><p><span>[c]</span><span> 7:149.</span></p>');
INSERT INTO `quran` VALUES (8800,2,20,87,'<p><span>Maka </span><span>[a]</span><span> Musa kembali kepada kaumnya, marah dan sedih Ia berkata, “Hai kaumku, bukankah Tuhan-mu telah menjanjikan kepadamu suatu janji yang baik ? Apakah masa </span><span>sempurnanya</span><span> janji itu terlalu lama bagimu, ataukah kamu menghendaki, supaya kemurkaan dari Tuhan-mu menimpamu, karena kamu telah menentang perjanjian dengan aku ? “</span></p><p><span> </span></p><p><span>[a] </span><span>7:151.</span></p>');
INSERT INTO `quran` VALUES (8801,2,20,88,'<p><span>Mereka berkata, “Kami tidak menyalahi perjanjian dengan engkau atas kehendak kami sendiri, melainkan kami memikul beban perhiasan kaum itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1842">1842</a><span>]</span><span> dan kami campakkan semua ; maka demikian pula orang Samiri mencampakkannya. </span></p>');
INSERT INTO `quran` VALUES (8802,2,20,89,'<p><span>Maka ia menyiapkan </span><span>[b]</span><span> seekor anak sapi bagi mereka, suatu jasad belaka yang mempunyai suara lenguhan. Kemudian, mereka berkata, “Inilah Tuhan-mu dan tuhan Musa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1843">1843</a><span>]</span><span> tetapi ia telah lupa.” </span></p><p><span> </span></p><p><span>[b]</span><span> 2:52, 93 ; 4:154 ; 7:149.</span></p>');
INSERT INTO `quran` VALUES (8803,2,20,90,'<p><span>Tidaklah mereka itu melihat bahwa </span><span>anak sapi</span><span> itu tidak memberi jawaban apa-apa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1844">1844</a><span>]</span><span> dan tidak mempunyai daya untuk menyampaikan kemudaratan atau pun kemanfaatan ? </span></p>');
INSERT INTO `quran` VALUES (8804,2,20,91,'<p><span>R. 5</span></p><p><span>Dan sesungguhnya Harun berkata kepada mereka sebelum </span><span>Musa kembali,</span><span> “Hai kaumku, sesungguhnya kamu telah diuji dengan </span><span>anak sapi</span><span> ini. Dan sesungguhnya Tuhan-mu Yang Maha Pemurah, maka ikutilah aku dan taatilah perintah-Ku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1845">1845</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8805,2,20,92,'<p><span>Mereka berkata, “Kami tidak akan berhenti menyembahnya sebelum Musa kembali kepada kami.” </span></p>');
INSERT INTO `quran` VALUES (8806,2,20,93,'<p><span>Ia, </span><span>Musa,</span><span> berkata, “Hai Harun, apakah yang telah menghalangi engkau, ketika engkau melihat mereka telah sesat. </span></p>');
INSERT INTO `quran` VALUES (8807,2,20,94,'<p><span>“Apakah engkau tidak mengikuti aku ? Apa engkau mendurhakai perintah-Ku ?” </span></p>');
INSERT INTO `quran` VALUES (8808,2,20,95,'<p><span>Ia, </span><span>Harun,</span><span> berkata, </span><span>[a]</span><span> “Hai anak bundaku, janganlah memegang janggutku dan jangan pula </span><span>rambut</span><span> kepalaku. Sesungguhnya aku takut bahwa engkau berkata, “Engkau telah berbuat perpecahan di antara Bani Israil, dan tidak menjaga perkataanku</span><span>’</span><span>.” </span></p><p><span> </span></p><p><span>[a]</span><span> 7:151</span></p>');
INSERT INTO `quran` VALUES (8809,2,20,96,'<p><span>Ia, </span><span>Musa, </span><span>berkata, “Apakah elasan engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1846">1846</a><span>]</span><span> hai Samiri ?” </span></p>');
INSERT INTO `quran` VALUES (8810,2,20,97,'<p><span>Ia, </span><span>Samiri,</span><span> berkata, “Aku mengetahui apa yang mereka tidak mengetahui tentang itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1847">1847</a><span>]</span><span> maka aku mengambil </span><span>hanya </span><span> sebagian dari ajaran rasul, tetapi aku telah mencampakkannya. Demikianlah hariku menampakkahn indah kepadaku.” </span></p>');
INSERT INTO `quran` VALUES (8811,2,20,98,'<p><span>Ia, </span><span>Musa,</span><span> berkata, “Maka pergilah engkau ! Sesungguhnya bagi engkau dalam kehidupan ini akan selalu berkata, </span><span>‘</span><span>Jangan sentuh </span><span>aku</span><span>’</span><span> ! “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1848">1848</a><span>]</span><span> Dan, sesungguhnya bagimu ada suatu janji </span><span>hukuman</span><span> yang sekali-kali engkau tidak akan dapat mengelakkannya. Dan lihatlah kepada tuhan engkau yang terhadapnya engkau telah menjadi penyembah. Pasti Kami akan membakarnya, kemudian akan Kami hamburkan </span><span>debu</span><span> nya ke dalam laut. </span></p>');
INSERT INTO `quran` VALUES (8812,2,20,99,'<p><span>Sesungguhnya, Tuhan kamu hanyalah Allah, Yang tiada Tuhan selain Dia. Ilmu-Nya meliputi segala sesuatu. </span></p>');
INSERT INTO `quran` VALUES (8813,2,20,100,'<p><span>Demikianlah Kami menceriterakan kepada engkau beberapa khabar tentang apa yang telah terjadi dahulu. Dan telah Kami berikan kepada engkau dari sisi Kami suatu peringatan, </span><span>Alquran.</span><span> </span></p>');
INSERT INTO `quran` VALUES (8814,2,20,101,'<p><span>Barangsiapa </span><span>[a]</span><span> berpaling dari itu, maka sesungguhnya ia pasti akan memikul beban berat pada Hari Kiamat. </span></p><p><span> </span></p><p><span>[a]</span><span> 18:102 ; 43:37 ; 72:18.</span></p>');
INSERT INTO `quran` VALUES (8815,2,20,102,'<p><span>Mereka akan tinggal lama di dalamnya. Dan buruklah bagi mereka beban pada Hari Kiamat. </span></p>');
INSERT INTO `quran` VALUES (8816,2,20,103,'<p><span>Hari, ketika </span><span>[b]</span><span> nafiri akan ditiup. Dan akan Kami himpun orang-orang yang berdosa, yang bermata biru pada hari itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1849">1849</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b]</span><span> 18:100 ; 27:88 ; 36:52 ; 78:19.</span></p>');
INSERT INTO `quran` VALUES (8817,2,20,104,'<p><span>Mereka saling berbisik-bisik di antara mereka, “Tidak kamu tinggal, melainkan hanya sepuluh.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1850">1850</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8818,2,20,105,'<p><span>Kami lebih mengetahui mengenai apa yang akan mereka katakan ketika akan berkata orang yang paling baik di antara mereka cara hidupnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1850a">1850a</a><span>]</span><span> “Tidaklah kamu tinggal melainkan sehari.” </span></p>');
INSERT INTO `quran` VALUES (8819,2,20,106,'<p><span>Dan </span><span>[a]</span><span> mereka bertanya kepada engkau mengenai gunung-gunung. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1851">1851</a><span>]</span><span> Maka katakanlah, “Tuhan-ku akan menghancurkannya berkeping-keping ; </span></p><p><span> </span></p><p><span>[a]</span><span> 56:6 ; 70:10 l 101:6.</span></p>');
INSERT INTO `quran` VALUES (8820,2,20,107,'<p><span>“Maka Dia akan meninggalkannya sebagai tanah datar yang gersang ; </span></p>');
INSERT INTO `quran` VALUES (8821,2,20,108,'<p><span>“Tidak akan engkau lihat di dalamnya bengkok dan berbukit bergelombang.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1852">1852</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8822,2,20,109,'<p><span>Pada hari itu mereka akan mengikuti Penyeru, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1853">1853</a><span>]</span><span> tiada kebengkokan dalam </span><span>ajaran</span><span>-nya, dan semua suara akan merendah di hadapan Yang Maha Pemurah dan tidaklah engkau akan mendengar kecuali bisikan. </span></p>');
INSERT INTO `quran` VALUES (8823,2,20,110,'<p><span>Pada hari itu </span><span>[a]</span><span> syafaat tidak berfaedah kecuali orang yang telah mendapat izin baginya dari Yang Maha Pemurah dan telah diridhai baginya perkataan-nya. </span></p><p><span> </span></p><p><span>[a]</span><span> 21:29 ; 79:39.</span></p>');
INSERT INTO `quran` VALUES (8824,2,20,111,'<p><span>[b]</span><span> Dia mengetahui apa yang ada, di hadapan mereka dan apa yang ada dibelakang mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1854">1854</a><span>]</span><span> dan tidaklah mereka dapat meliputi-Nya dengan ilmu </span><span>mereka.</span><span> </span></p><p><span> </span></p><p><span>[b] </span><span>2:256 ; 21:29.</span></p>');
INSERT INTO `quran` VALUES (8825,2,20,112,'<p><span>Dan akan tunduk </span><span>semua </span><span> pemuka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1854a">1854a</a><span>]</span><span> di hadapan Yang Mahahidup, Yang Tegak atas Dzat-Nya sendiri dan Penegak </span><span>segala sesuatu.</span><span> Dan sesungguhnya rugilah siapa yang memikul </span><span>beban</span><span> kezaliman. </span></p>');
INSERT INTO `quran` VALUES (8826,2,20,113,'<p><span>Dan </span><span>[c]</span><span> barangsiapa mengerjakan amal shaleh, sedang ia seorang mukmin, maka ia tidak takut akan perlakuan aniaya dan tidak pula menderita kerugian. </span></p><p><span> </span></p><p><span>[c]</span><span> 10:10 ; 16:98 ; 21:95.</span></p>');
INSERT INTO `quran` VALUES (8827,2,20,114,'<p><span>Dan demikianlah </span><span>[d]</span><span> Kami telah menurunkannya Alquran berbahasa Arab dan telah Kami bentangkan di dalamnya berbagai macam ancaman supaya mereka bertakwa atau </span><span>Alquran</span><span> ini akan menyebabkan mereka mengingat-Nya. </span></p><p><span> </span></p><p><span>[d]</span><span> 42:8 ; 43:4 ; 46:13.</span></p>');
INSERT INTO `quran` VALUES (8828,2,20,115,'<p><span>Maka </span><span>[a]</span><span> Mahatinggi Allah, Raja Yang Benar. Dan janganlah engkau tergesa-gesa membaca Alquran sebelum pewahyuannya dilengkapkan kepada engkau, melainkan katakanlah, “Ya, Tuhan-ku tambahkanlah kepadaku ilmu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1855">1855</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[a]</span><span> 23:117.</span></p>');
INSERT INTO `quran` VALUES (8829,2,20,116,'<p><span>Dan, sesungguhnya telah Kami adakan perjanjian dengan Adam sebelum ini, tetapi ia telah lupa dan Kami tidak mendapatkan padanya tekad </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1856">1856</a><span>]</span><span> </span><span>untuk berbuat dosa, ……</span></p>');
INSERT INTO `quran` VALUES (8830,2,20,117,'<p><span>R. 7</span></p><p><span>Dan </span><span>ingatlah</span><span> </span><span>[b]</span><span> ketika Kami berkata kepada para malaikat, “Sujudlah kamu bersama Adam .” maka mereka bersujud kecuali iblis. Ia menolak. </span></p><p><span> </span></p><p><span>[b]</span><span> 2:35 ; 7:12, 13 ; 15:27 </span><span>–</span><span> 34 ; 17:62 ; 18:51 ; 38:72 </span><span>–</span><span> 75.</span></p>');
INSERT INTO `quran` VALUES (8831,2,20,118,'<p><span>Kemudian Kami berkata, “Hai Adam, </span><span>[c]</span><span> sesungguhnya ini adalah musuh bagi engkau, dan bagi istri engkau ; maka janganlah sampai ia mengeluarkan kamu berdua dari surga, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1857">1857</a><span>]</span><span> maka kamu menderita kesusahan. </span></p><p><span> </span></p><p><span>[c]</span><span> 7:23 ; 18:51.</span></p>');
INSERT INTO `quran` VALUES (8832,2,20,119,'<p><span>“Sesungguhnya engkau tidak akan kelaparan di dalamnya, dan tidak </span><span>pula</span><span> engkau akan telanjang, </span></p>');
INSERT INTO `quran` VALUES (8833,2,20,120,'<p><span>“Dan bahwa engkau tidak akan kehausan di dalamnya dan tidak </span><span>pula</span><span> akan disengat panas matahari. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1858">1858</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (8834,2,20,121,'<p><span>Maka </span><span>[a]</span><span> syaitan membisikkan was-was kepadanya. Ia berkata, “Hai Adam, maukah aku tunjukkan kepada engkau pohon kekekalan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1859">1859</a><span>]</span><span> dan kerajaan yang tidak akan binasa ? “</span></p><p><span> </span></p><p><span>[a]</span><span> 2:37 ; 7:21.</span></p>');
INSERT INTO `quran` VALUES (8835,2,20,122,'<p><span>Maka </span><span>[a]</span><span> keduanya telah makan darinya, </span><span>akibatnya</span><span> nampaklah bagi mereka berdua kelemahan-kelemahan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1860">1860</a><span>]</span><span> dan keduanya menutupi badan mereka dengan barang-barang keindahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1861">1861</a><span>]</span><span> surga, </span><span>yakni amal-amal baik.</span><span> Dan Adam telah mendurhakai Tuhan-nya, maka ia menderita. </span></p><p><span> </span></p><p><span>[a]</span><span> 7:23.</span></p>');
INSERT INTO `quran` VALUES (8836,2,20,123,'<p><span>Kemudian Tuhan-nya memilih dia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1862">1862</a><span>] [b]</span><span> maka Dia menerima tobatnya dan memberi petunjuk. </span></p><p><span> </span></p><p><span>[b]</span><span> 2:38.</span></p>');
INSERT INTO `quran` VALUES (8837,2,20,124,'<p><span>Dia, berfirman, </span><span>[a]</span><span> “Enyahlah kamu berdua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1863">1863</a><span>]</span><span> semuanya dari sini ; sebagian kamu musuh bagi sebagaian yang lain. Maka apabila datang kepadamu petunjuk dari-Ku, lalu barangsiapa mengikuti petunjuk-Ku, maka ia tidak akan sesat, dan tidak pula ia akan menderita kesusahan. </span></p><p><span> </span></p><p><span>[a]</span><span> 2:37 </span><span>–</span><span> 39 ; 7:25.</span></p>');
INSERT INTO `quran` VALUES (8838,2,20,125,'<p><span>“Dan </span><span>[b]</span><span> barangsiapa berpaling dari mengingat Aku, maka sesungguhnya baginya ada kehidupan yang sempit, dan Kami akan membangkitkannya pada Hari Kiamat dalam keadaan buta.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1864">1864</a><span>]</span><span> </span></p><p><span> </span></p><p><span>[b] </span><span>18:102.</span></p>');
INSERT INTO `quran` VALUES (8839,2,20,126,'<p><span>Ia berkata, “Ya Tuhan-ku, mengapa Engkau telah membangkitkan aku dalam keadaan buta, padahal sesungguhnya dahulu aku dapat melihat ?” </span></p>');
INSERT INTO `quran` VALUES (8840,2,20,127,'<p><span>Dia, berfirman, “Demikianlah telah datang kepadamu Tanda-tanda Kami, tetapi engkau melupakannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1865">1865</a><span>]</span><span> dan demikian pula engkau dilupakan pada hari ini.” </span></p>');
INSERT INTO `quran` VALUES (8841,2,20,128,'<p><span>Dan demikianlah Kami memberi balasan orang yang melanggar dan ia tidak beriman kepada Tanda-tanda Tuhan-nya. Dan sesungguhnya azab akhirat itu lebih keras dan lebih kekal. </span></p>');
INSERT INTO `quran` VALUES (8842,2,20,129,'<p><span>Maka tidakkah ini memberi petunjuk kepada mereka, </span><span>[a]</span><span> berapa banyak keturunan telah Kami binasakan sebelum mereka, mereka berjalan-jalan di rumah-rumah mereka </span><span>yang telah hancur.</span><span> Sesungguhnya, dalam hal yang demikian itu adalah Tanda-tanda bagi orang-orang yang berakal. </span></p><p><span> </span></p><p><span>[a]</span><span> 17:18 ; 38:32.</span></p>');
INSERT INTO `quran` VALUES (8843,2,20,130,'<p><span>R. 8</span></p><p><span>Dan </span><span>[b]</span><span> sekiranya bukan karena suatu kalimat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1866">1866</a><span>]</span><span> yang telah berlalu dari Tuhan engkau dan suatu batas waktu </span><span>telah </span><span> ditetapkan, niscaya </span><span>azab</span><span> menimpa </span><span>mereka</span><span> dalam waktu yang ditentukan. </span></p><p><span> </span></p><p><span>[b]</span><span> 8:69 ; 10:20.</span></p>');
INSERT INTO `quran` VALUES (8844,2,20,131,'<p><span>Maka bersabarlah engkau atas apa yang mereka katakan, dan </span><span>[a]</span><span> bertasbihlah dengan pujian Tuhan engkau sebelum matahari terbit dan sebelum terbenamnya ; dan bertasbihlah pada bagian malam dan pada waktu bagian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1867">1867</a><span>]</span><span> siang supaya engkau mendapat keridhaan. </span></p><p><span> </span></p><p><span>[a]</span><span> 17:79, 80 ; 30:18, 19 ; 50:40, 41.</span></p>');
INSERT INTO `quran` VALUES (8845,2,20,132,'<p><span>Dan jangan sekali-kali engkau </span><span>[b]</span><span> tujukan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1868">1868</a><span>]</span><span> kedua mata engkau kepada apa yang telah Kami anugerahkan kepada beberapa golongan dari mereka berupa keindahan kehidupan dunia supaya Kami menguji mereka di dalamnya. Dan rezeki Tuhan engkau adalah lebih baik dan lebih kekal. </span></p><p><span> </span></p><p><span>[b]</span><span> 15:89 ; 26:206 </span><span>–</span><span> 208 ; 28:61, 62.</span></p>');
INSERT INTO `quran` VALUES (8846,2,20,133,'<p><span>Dan </span><span>[a]</span><span> perintahkanlah keluargamu untuk shalat dan tetaplah mengamalkannya. Kami tidak meminta kepada engkau rezeki, Kami-lah Yang memberi rezeki engkau. Dan akibat </span><span>yang baik</span><span> bagi mereka yang bertakwa. </span></p><p><span> </span></p><p><span>[a]</span><span> 19:56 ; 33:34.</span></p>');
INSERT INTO `quran` VALUES (8847,2,20,134,'<p><span>Dan mereka berkata, “Mengapakah ia tidak mendatangkan kepada kami suatu tanda dari Tuhan-nya ? “ Bukankah telah datang kepada mereka bukti yang jelas apa yang ada dalam lembaran-lembaran terdahulu ? </span></p>');
INSERT INTO `quran` VALUES (8848,2,20,135,'<p><span>Dan sekiranya Kami binasakan mereka dengan azab sebelum ini, niscaya mereka akan berkata, “Ya Tuhan kami, mengapakah tidak Engkau kirimkan kepada Kami, seorang rasul supaya kami mengikuti Ayat-ayat Engkau sebelum kami direndahkan dan dihinakan ? “</span></p>');
INSERT INTO `quran` VALUES (8849,2,20,136,'<p><span>Katakanlah, “ Setiap orang sedang menunggu, maka kamu tungguhlah, dan segara kamu akan mengetahui siapakah yang ada pada jalan yang lurus dan siapa yang mengikuti petunjuk. </span></p>');
INSERT INTO `quran` VALUES (8850,2,21,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8851,2,21,2,'<p><span>[b]</span><span> Telah dekat kepada manusia perhitungan mereka, dan mereka berpaling dalam kelalaian.</span></p><p><span>[b]</span><span> 54:2, 3.</span></p>');
INSERT INTO `quran` VALUES (8852,2,21,3,'<p><span>[c]</span><span> Tidaklah datang kepada mereka suatu peringatan baru </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1869">1869</a><span>]</span><span> dari Tuhan mereka, melainkan mereka mendengarkannya sambil mereka mempermainkan.</span></p><p><span>[c]</span><span> 21:43 ; 26:6.</span></p>');
INSERT INTO `quran` VALUES (8853,2,21,4,'<p><span>Dan</span><span> dalam keadaan lalai hati mereka. Dan mereka, orang-orang yang aniaya bermusyawarah secara rahasia, </span><span>dan berkata,</span><span> “Bukankah </span><span>orang </span><span> ini tak lain hanya seorang manusia seperti kamu ? Apakah kamu menyerah kepada sihir-</span><span>nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1870">1870</a><span>]</span><span> itu padahal kamu mengerti ? “</span></p>');
INSERT INTO `quran` VALUES (8854,2,21,5,'<p><span>Rasulullah saw.</span><span> bersabda, “Tuhan-ku mengetahui apa yang dikatakan di langit dan di bumi. Dan Dia Maha Mendengar, Maha Mengetahui.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1871">1871</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8855,2,21,6,'<p><span>Bahkan mereka berkata, “</span><span>Perkataan itu hanyalah impian</span><span> yang kacau-balau ; bahkan ia sendiri yang telah mengada-adakannya ; bahkan </span><span>[a]</span><span> ia hanyalah seorang penyair. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1872">1872</a><span>]</span><span> Maka hendaklah ia mendatangkan suatu Tanda kepada kami seperti telah diutus </span><span>rasul-rasul</span><span> yang terdahulu.”</span></p><p><span>[a]</span><span> 52:31.</span></p>');
INSERT INTO `quran` VALUES (8856,2,21,7,'<p><span>Tiada penduduk negeri, sebelum mereka, yang Kami binasakan pernah beriman. Maka apakah mereka akan beriman ? </span></p>');
INSERT INTO `quran` VALUES (8857,2,21,8,'<p><span>[b]</span><span> Dan tiada Kami mengutus </span><span>sebagai rasul</span><span> sebelum engkau melainkan orang laki-laki, yang telah Kami turunkan wahyu kepada mereka, maka tanyakanlah kepada pemberi peringatan itu, jika kamu tidak mengetahui.</span></p><p><span>[b]</span><span> 12:110 ; 16:44.</span></p>');
INSERT INTO `quran` VALUES (8858,2,21,9,'<p><span>[a]</span><span> Dan tidak Kami jadikan mereka jasad yang tidak makan makanan, dan tidak mereka hidup kekal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1873">1873</a><span>]</span></p><p><span>[a]</span><span> 25:21</span></p>');
INSERT INTO `quran` VALUES (8859,2,21,10,'<p><span>Kemudian Kami sempurnakan janji kepada mereka ; maka Kami selamatkan mereka dan orang-orang yang Kami kehendaki ; dan Kami binasakan orang-orang yang melampaui batas.</span></p>');
INSERT INTO `quran` VALUES (8860,2,21,11,'<p><span>Sesungguhnya telah Kami turunkan kepadamu suatu Kitab di dalamnya terdapat kemuliaan-mu ; maka tidakkah kamu menggunakan akal ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1874">1874</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8861,2,21,12,'<p><span>R. 2</span></p><p><span>[a]</span><span> Dan betapa banyaknya yang telah Kami hancurkan kota </span><span>yang penduduknya</span><span> aniaya ; dan Kami bangkitkan sesudahnya suatu kaum lain.</span></p><p><span>[a]</span><span> 7:5 ; 22:46 ; 28:59 ; 50:37 ; 65:9.</span></p>');
INSERT INTO `quran` VALUES (8862,2,21,13,'<p><span>Maka tatkala mereka merasakan azab Kami, tiba-tiba mereka lari darinya.</span></p>');
INSERT INTO `quran` VALUES (8863,2,21,14,'<p><span>Kami berfirman</span><span>, “Janganlah kamu lari, dan kembalilah kamu kepada kehidupan yang senang yang kamu ada di dalamnya, dan kepada tempat-tempat tinggalmu supaya kamu ditanya.”</span></p>');
INSERT INTO `quran` VALUES (8864,2,21,15,'<p><span>Mereka berkata, “Aduhai, celakalah kami, sesungguhnya kami adalah orang-orang aniaya ! “</span></p>');
INSERT INTO `quran` VALUES (8865,2,21,16,'<p><span>Maka tidak henti-hentinya keluhan mereka </span><span>bagaikan</span><span> ladang yang telah diketam, hancur lebur. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1875">1875</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8866,2,21,17,'<p><span>[b]</span><span> Dan tidaklah Kami jadikan langit dan bumi serta segala yang ada di antara keduanya itu sebagai permainan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1875a">1875a</a><span>]</span></p><p><span>[b]</span><span> 15:86 ; 38:28 ; 44:39.</span></p>');
INSERT INTO `quran` VALUES (8867,2,21,18,'<p><span>Sekiranya Kami mau untuk menjadikan suatu hiburan, tentulah Kami akan menjadikannya di hadapan Kami, itu pun seandainya Kami mau berbuat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1876">1876</a><span>]</span><span> </span><span>demikian.</span><span> </span></p>');
INSERT INTO `quran` VALUES (8868,2,21,19,'<p><span>Bahkan </span><span>[a]</span><span> Kami lemparkan kebenaran atas kebatilan, maka </span><span>kebenaran</span><span> itu memecahkan kepalanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1876a">1876a</a><span>]</span><span> maka tiba-tiba, binasalah </span><span>kebatilan</span><span> itu. Dan celakalah kamu disebabkan apa yang kamu terangkan.</span></p><p><span>[a]</span><span> 17:82 ; 34:49, 50.</span></p>');
INSERT INTO `quran` VALUES (8869,2,21,20,'<p><span>Dan kepunyaan Dia segala yang ada di seluruh langit dan bumi. </span><span>[b]</span><span> Dan mereka yang ada di sisi-Nya tidak merasa sombong untuk beribadah kepada-Nya, dan tidak pula mereka merasa letih.</span></p><p><span>[b]</span><span> 7:207 ; 41:39 ; 21:20.</span></p>');
INSERT INTO `quran` VALUES (8870,2,21,21,'<p><span>Mereka bertasbih malam dan siang ; tanpa henti-henti. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1877">1877</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8871,2,21,22,'<p><span>Apakah mereka mengambil tuhan-tuhan dari bumi ini yang dapat menghidupkan </span><span>yang mati ?</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1878">1878</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8872,2,21,23,'<p><span>Dan sekiranya di dalam </span><span>langit dan bumi</span><span> keduanya ada tuhan-tuhan selain Allah, pasti binasalah kedua-duanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1879">1879</a><span>]</span><span> Maka Maha Suci Allah, Tuhan </span><span>‘</span><span>Arasy itu, jauh di atas segala yang mereka terangkan.</span></p>');
INSERT INTO `quran` VALUES (8873,2,21,24,'<p><span>Tidak akan ditanya tentang apa yang Dia kerjakan, </span><span>tetapi</span><span> merekalah yang ditanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1880">1880</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8874,2,21,25,'<p><span>[a] </span><span>Apakah mereka mengambil tuhan-tuhan selain Dia ? Katakanlah, “Kemukankanlah keteranganmu. </span><span>Alquran</span><span> ini adalah </span><span>sumber</span><span> kemuliaan bagi orang-orang yang besertaku, dan </span><span>sumber</span><span> kemulian bagi mereka sebelumku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1880a">1880a</a><span>]</span><span> Bahkan kebanyakan mereka itu tak mengetahui kebenaran, maka mereka berpaling.</span></p><p><span>[a]</span><span> 18:16 ; 23:118 ; 27:65.</span></p>');
INSERT INTO `quran` VALUES (8875,2,21,26,'<p><span>Dan tidak Kami utus seorang rasul sebelummu, melainkan Kami wahyukan kepadanya, “Sesungguhnya tiada Tuhan kecuali Aku, maka sembahlah Aku.”</span></p>');
INSERT INTO `quran` VALUES (8876,2,21,27,'<p><span>[b]</span><span> Dan mereka berkata, “Yang Maha Pemurah telah mengambil seorang anak.” Maha Suci Dia. Bahkan mereka adalah hamba-hamba-</span><span>Nya</span><span> yang dimuliakan.</span></p><p><span>[b]</span><span> 2:117 ; 4:172 ; 10:69 ; 19:89, 90.</span></p>');
INSERT INTO `quran` VALUES (8877,2,21,28,'<p><span>Mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1881">1881</a><span>]</span><span> tidak mendahului-Nya dalam bicara, dan mereka </span><span>hanya</span><span> melaksanakan perintah-Nya.</span></p>');
INSERT INTO `quran` VALUES (8878,2,21,29,'<p><span>[c]</span><span> Dia, </span><span>Allah,</span><span> mengetahui segala yang ada di hadapan mereka, dan apa yang ada di belakang mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1882">1882</a><span>]</span><span> dan tidaklah mereka itu memberi syafaat, melainkan kepada siapa yang Dia ridhai dan karena mereka merasa gemetar takut kepada-Nya.</span></p><p><span>[c]</span><span> 2:256 ; 20:111.</span></p>');
INSERT INTO `quran` VALUES (8879,2,21,30,'<p><span>Dan barangsiapa berkata di antara mereka, “Sesungguhnya akulah tuhan selain Dia,” maka dialah yang akan Kami ganjar dengan Jahannam. Demikianlah Kami balas orang-orang aniaya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1883">1883</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8880,2,21,31,'<p><span>R. 3</span></p><p><span>Tidaklah orang-orang yang ingkar melihat bahwa seluruh langit dan bumi keduanya dahulu suatu </span><span>massa</span><span> yang menggumpal, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1884">1884</a><span>]</span><span> lalu Kami pisahkan keduanya ? Dan Kami jadikan segala sesuatu yang hidup dari air. Apakah mereka tidak mau beriman ? </span></p>');
INSERT INTO `quran` VALUES (8881,2,21,32,'<p><span>[a]</span><span> Dan telah Kami jadikan di bumi gunung-gunung yang kokoh supaya </span><span>bumi</span><span> jangan bergoncang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1885">1885</a><span>]</span><span> bersama mereka ; dan telah Kami jadikan di dalamnya jalan-jalan yang luas supaya mereka mendapat petunjuk.</span></p><p><span>[a]</span><span> 13:4 ; 15:20 ; 16:16 ; 31:11 ; 77:28.</span></p>');
INSERT INTO `quran` VALUES (8882,2,21,33,'<p><span>Dan telah Kami jadikan langit sebagai atap yang terpelihara ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1886">1886</a><span>]</span><span> namun mereka dari Tanda-tandanya berpaling.</span></p>');
INSERT INTO `quran` VALUES (8883,2,21,34,'<p><span>Dan Dia yang telah menciptakan malam dan siang, dan matahari serta bulan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1886a">1886a</a><span>] masing</span><span>–</span><span>masing beredar pada garis peredaran-</span><span>nya</span><span>.</span></p>');
INSERT INTO `quran` VALUES (8884,2,21,35,'<p><span>Dan Kami tidak pernah menjadikan seorang manusia sebelum engkau hidup kekal. Maka jika engkau mati, apakah mereka akan hidup kekal ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1887">1887</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8885,2,21,36,'<p><span>Setiap jiwa pasti akan merasakan maut ; dan Kami uji kamu dengan keburukan dan kebaikan sebagai percobaan. Dan kebaikan sebagai percobaan. Dan kepada Kami kamu akan dikembalikan.</span></p>');
INSERT INTO `quran` VALUES (8886,2,21,37,'<p><span>[a]</span><span> Dan apabila melihat kepada engkau, orang-orang yang ingkar tidak lain mereka membuat engkau hanya sebagai perolokan belaka, </span><span>dan berkata, </span><span>“ Inikah orangnya, yang menyebutkan keburukan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1887a">1887a</a><span>]</span><span> tuhan-tuhan kamu ? “ Padahal mereka sendirilah </span><span>[b]</span><span> yang menolak untuk mengingat Yang Maha Pemurah.</span></p><p><span>[b]</span><span> 13:31</span></p>');
INSERT INTO `quran` VALUES (8887,2,21,38,'<p><span>Manusia dijadikan </span><span>dari sifat</span><span> tergesa-gesa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1888">1888</a><span>]</span><span> niscaya Aku akan memperlihatkan kepadamu tanda-tanda-Ku, maka janganlah kamu minta kepada-Ku dengan segera.</span></p>');
INSERT INTO `quran` VALUES (8888,2,21,39,'<p><span>[c]</span><span> Dan mereka berkata. “Bilakah janji itu </span><span>akan sempurna,</span><span> jika kamu adalah orang-orang yang benar ?”</span></p><p><span>[c]</span><span> 34:30 ; 36:49 ; 67:26.</span></p>');
INSERT INTO `quran` VALUES (8889,2,21,40,'<p><span>Sekiranya orang-orang yang ingkar mengetahui </span><span>saat,</span><span> ketika mereka tidak akan dapat mengelakkan api </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1889">1889</a><span>]</span><span> dari wajah mereka, dan tidak dari punggung mereka, dan mereka tidak akan di tolong.</span></p>');
INSERT INTO `quran` VALUES (8890,2,21,41,'<p><span>Bahkan </span><span>[a]</span><span> </span><span>azab</span><span> itu akan datang dengan tiba-tiba kepada mereka, lalu mengherankan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1890">1890</a><span>]</span><span> mereka ; maka mereka tidak akan mampu menolaknya, dan tidak pula mereka diberi tangguh.</span></p><p><span>[a]</span><span> 36:50 ; 67:28.</span></p>');
INSERT INTO `quran` VALUES (8891,2,21,42,'<p><span>[b]</span><span> Dan sungguh rasul-rasul telah diperolok-perolok sebelum engkau, maka telah dikepung di antara mereka orang-orang yang memperolok-olokkan </span><span>rasul</span><span> dengan </span><span>[c]</span><span> apa yang mereka perolok-olokkan.</span></p><p><span>[b]</span><span> 6:11 ; 13:33.</span></p><p><span>[c]</span><span> 11:9 ; 46:27.</span></p>');
INSERT INTO `quran` VALUES (8892,2,21,43,'<p><span>R. 4</span></p><p><span>Katakanlah, “Siapakah yang dapat melindungi kamu pada waktu malam dan siang terhadap </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1891">1891</a><span>]</span><span> Yang Maha Pemurah ? “ Bahkan </span><span>[d]</span><span> mereka </span><span>masih</span><span> berpaling dari mengingat Tuhan mereka.</span></p><p><span>[d]</span><span> 18:102 ; 21:3 ; 26:6.</span></p>');
INSERT INTO `quran` VALUES (8893,2,21,44,'<p><span>Apakah mereka mempunyai tuhan-tuhan yang dapat melindungi mereka terhadap Kami ? Mereka tidak mampu menolong diri mereka sendiri, dan tidak pula mereka mempunyai sahabat untuk melawan Kami.</span></p>');
INSERT INTO `quran` VALUES (8894,2,21,45,'<p><span>Bahkan, Kami perlengkapi mereka dan orangtua mereka </span><span>dengan harta dunia,</span><span> sehingga telah </span><span>[a]</span><span> panjang umur mereka. </span><span>[b]</span><span> Tidakkah mereka melihat bahwa Kami mendatangi bumi dengan menguranginya dari segala tepinya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1892">1892</a><span>]</span><span> Mungkinkah mereka menjadi orang- orang yang menang ?</span></p><p><span>[a]</span><span> 57:17.</span></p><p><span>[b]</span><span> 13:42.</span></p>');
INSERT INTO `quran` VALUES (8895,2,21,46,'<p><span>Katakanlah, “Sesungguhnya tiada aku memperingati kamu </span><span>hanyalah</span><span> dengan wahyu.” Tetapi </span><span>[c]</span><span> orang-orang tuli tidak dapat mendengar panggilan apabila mereka diperingatkan.</span></p><p><span>[c] </span><span>30:53.</span></p>');
INSERT INTO `quran` VALUES (8896,2,21,47,'<p><span>[d]</span><span> Dan jika mereka ditimpa sedikit saja dari azab Tuhanmu, pastilah mereka akan berkata, “Aduh, celakalah kami ! Sesungguhnya kami adalah orang-orang aniaya.”</span></p><p><span>[d]</span><span> 7:6.</span></p>');
INSERT INTO `quran` VALUES (8897,2,21,48,'<p><span>Dan akan Kami adakan neraca-necara yang adil pada Hari Kiamat supaya </span><span>[a]</span><span> tiada jiwa akan teraniaya sedikit pun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1893">1893</a><span>]</span><span> Dan jika ada amal seberat biji sawi, Kami akan mewujudkannya. Dan cukuplah Kami sebagai Penghisab.</span></p><p><span>[a]</span><span> 4:41 ; 18:50.</span></p>');
INSERT INTO `quran` VALUES (8898,2,21,49,'<p><span>[b]</span><span> Dan sesungguhnya telah Kami berikan tanda pemisah kepada Musa dan Harun, serta penerangan dan peringatan bagi orang-orang yang bertakwa.</span></p><p><span>[b]</span><span> 2:54.</span></p>');
INSERT INTO `quran` VALUES (8899,2,21,50,'<p><span>Yaitu </span><span>[c]</span><span> mereka yang takut kepada Tuhan mereka dengan cara sembunyi, dan mereka yang takut pula kepada waktu </span><span>pembalasan</span><span>.</span></p><p><span>[c]</span><span> 67:13.</span></p>');
INSERT INTO `quran` VALUES (8900,2,21,51,'<p><span>Dan ini adalah peringatan penuh berkat yang telah Kami turunkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1894">1894</a><span>] </span><span>Maka apakah kamu masih mengingkarinya ?</span></p>');
INSERT INTO `quran` VALUES (8901,2,21,52,'<p><span>R. 5</span></p><p><span>Dan sesungguhnya Kami telah memberikan kepada Ibrahim petunjuknya sebelum ini dan Kami mengetahui benar tentang dia.</span></p>');
INSERT INTO `quran` VALUES (8902,2,21,53,'<p><span>[a]</span><span> Ketika ia berkata kepada ayahnya dan kaumnya, “Patung-patung apakah ini </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1895">1895</a><span>]</span><span> yang begitu patuh kalian duduk </span><span>memujanya</span><span> ? “</span></p><p><span>[a]</span><span> 6:75 ; 19:43 ; 26:71.</span></p>');
INSERT INTO `quran` VALUES (8903,2,21,54,'<p><span>[b]</span><span> Mereka menjawab, “Kami dapati bapak-bapak kami menyembahnya.”</span></p><p><span>[b]</span><span> 26:75 ; 43:24.</span></p>');
INSERT INTO `quran` VALUES (8904,2,21,55,'<p><span>[c]</span><span> Ia berkata, “Sesungguhnya kamu, begitu juga bapak-bapakmu, benar-benar dalam kesesatan yang nyata.”</span></p><p><span>[c]</span><span> 60:5.</span></p>');
INSERT INTO `quran` VALUES (8905,2,21,56,'<p><span>Mereka berkata, “Apakah kebenaran yang engkau datangkan kepada kami, ataukah engkau termasuk orang-orang yang bermain-main ?”</span></p>');
INSERT INTO `quran` VALUES (8906,2,21,57,'<p><span>Ia berkata, “Sebenarnya Tuhan-mu itu Tuhan seluruh langit dan bumi. </span><span>Dia-lah</span><span> Yang telah menciptakannya ; dan atas hal itu aku termasuk orang-orang yang menjadi saksi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1896">1896</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8907,2,21,58,'<p><span>“Dan, demi Allah, niscaya aku akan membuat rencana </span><span>melawan</span><span> berhala-berhalamu, setelah kamu terlalu membalikkan punggungmu.”</span></p>');
INSERT INTO `quran` VALUES (8908,2,21,59,'<p><span>[a]</span><span> Maka ia membuat </span><span>berhala-hala</span><span> itu pecah berkeping-keping, kecuali yang besar dari mereka, </span><span>berhala,</span><span> supaya mereka kepadanya kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1897">1897</a><span>]</span></p><p><span>[a]</span><span> 37:94.</span></p>');
INSERT INTO `quran` VALUES (8909,2,21,60,'<p><span>Mereka berkata, “Siapakah yang telah berbuat demikian terhadap tuhan-tuhan kami ? Sesungguhnya ia orang aniaya.”</span></p>');
INSERT INTO `quran` VALUES (8910,2,21,61,'<p><span>Mereka berkata, “Kami mendengar seorang pemuda yang menerangkan </span><span>kelemahannya,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1898">1898</a><span>]</span><span> ia disebut Ibrahim.</span></p>');
INSERT INTO `quran` VALUES (8911,2,21,62,'<p><span>Berkatalah mereka, “Maka bawalah dia ke hadapan mata manusia supaya mereka dapat menjadi saksi.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1899">1899</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8912,2,21,63,'<p><span>Mereka berkata, “Engkaukah yang telah berbuat ini terhadap tuhan-tuhan kami, ya Ibrahim ? “</span></p>');
INSERT INTO `quran` VALUES (8913,2,21,64,'<p><span>Ia menjawab, “Bahkan, </span><span>seseorang </span><span>telah berbuat itu. Di antara mereka yang besat ini, maka tanyakanlah kepada mereka jika mereka dapat berkata-kata.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1900">1900</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8914,2,21,65,'<p><span>Maka mereka berbalik kepada pemimpin mereka lalu berkata, “Sesungguhnya kamu sendirilah orang-orang aniaya.”</span></p>');
INSERT INTO `quran` VALUES (8915,2,21,66,'<p><span>Kemudian mereka menundukkan kepada mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1901">1901</a><span>]</span><span> Sesungguhnya engkau telah mengetatahui bahwa mereka itu tidak dapat berkata-kata.</span></p>');
INSERT INTO `quran` VALUES (8916,2,21,67,'<p><span>Ia berkata, </span><span>[a]</span><span> “Apakah kamu, menyembah selain Allah, yang tidak memberikan manfaat kepadamu sedikit pun, dan tidak mendatangkan mudarat kepadamu ?</span></p><p><span>[a]</span><span> 29:18 ; 37:96.</span></p>');
INSERT INTO `quran` VALUES (8917,2,21,68,'<p><span>“Ah, </span><span>celakalah</span><span> atas kamu atas apa yang kamu dan atas apa yang kamu tidak menggunakan akal ? “</span></p>');
INSERT INTO `quran` VALUES (8918,2,21,69,'<p><span>[a]</span><span> Mereka berkata, “Bakarlah dia dan bantulah tuhan-tuhanmu, jika kamu mau melakukan sesuatu.”</span></p><p><span>[a]</span><span> 29:25 ; 37:98.</span></p>');
INSERT INTO `quran` VALUES (8919,2,21,70,'<p><span>Kami berfirman, “Hai api jadilah kamu dingin dan selamat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1902">1902</a><span>]</span><span> atas Ibrahim ! “</span></p>');
INSERT INTO `quran` VALUES (8920,2,21,71,'<p><span>[b]</span><span> Dan mereka bermaksud akan melakukan tipu-daya terhadap dia, tetapi Kami jadikan mereka orang-orang yang paling rugi.</span></p><p><span>[b]</span><span> 37:99.</span></p>');
INSERT INTO `quran` VALUES (8921,2,21,72,'<p><span>Dan telah Kami selamatkan dia dan Luth ke negeri yang telah Kami berkati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1903">1903</a><span>]</span><span> di dalam-nya untuk seluruh umat manusia.</span></p>');
INSERT INTO `quran` VALUES (8922,2,21,73,'<p><span>[a]</span><span> Dan Kami anugerahkan kepadanya Ishak,dan seorang cucu, Ya</span><span>’</span><span>kub, dan semua, Kami jadikan orang-orang yang shaleh.</span></p><p><span>[a]</span><span> 11:72 ; 19:50 ; 29:28 ; 37:113 ; 51:29.</span></p>');
INSERT INTO `quran` VALUES (8923,2,21,74,'<p><span>[b]</span><span> Dan Kami jadikan mereka imam-imam yang memberi petunjuk dengan perintah Kami, dan Kami wahyukan kepada mereka </span><span>untuk</span><span> berbuat kebaikan-kebaikan, dan mendirikan shalat, dan membayar zakat. Dan hanya kepada Kami mereka menyembah.</span></p><p><span>[b]</span><span> 2:125 ; 32:25.</span></p>');
INSERT INTO `quran` VALUES (8924,2,21,75,'<p><span>Dan kepada Luth, Kami anugerakan kebijaksanaan dan ilmu. </span><span>[c]</span><span> Dan Kami selamatkan dia dari </span><span>penduduk</span><span> kota yang melakukan kekejian. Sesungguhnya mereka itu adalah kaum yang buruk , pendurhaka.</span></p><p><span>[c]</span><span> 7:84 ; 27:58 ; 29:34.</span></p>');
INSERT INTO `quran` VALUES (8925,2,21,76,'<p><span>Dan Kami masukkan dia ke dalam rahmat Kami ; sesungguhnya ia termasuk orang-orang shaleh.</span></p>');
INSERT INTO `quran` VALUES (8926,2,21,77,'<p><span>R. 6</span></p><p><span>[d]</span><span> Dan </span><span>ingatlah</span><span> Nuh, ketika ia berseru sebelum itu, dan Kami kabulkan doanya, dan Kami kabulkan doanya, dan Kami selamatkan dia dan keluarganya dari bencana </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1904">1904</a><span>]</span><span> yang besar.</span></p><p><span>[d]</span><span> 26:118, 120 ; 37:76, 77 ; 54:11.</span></p>');
INSERT INTO `quran` VALUES (8927,2,21,78,'<p><span>Dan Kami tolong dia dari kaum yang mendustakan Tanda-tanda Kami. Sesungguhnya mereka adalah suatu kaum yang buruk maka </span><span>[a]</span><span> Kami tenggelamkan mereka semuanya.</span></p><p><span>[a]</span><span> 26:121 ; 37:83; 54:12, 13 ; 71:26.</span></p>');
INSERT INTO `quran` VALUES (8928,2,21,79,'<p><span>Dan </span><span>ingatlah</span><span> Daud dan Sulaiman, ketika mereka berdua memberikan keputusan mengenai suatu ladang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1905">1905</a><span>]</span><span> ketika kambing-kambing suatu kaum merusak di dalamnya, dan Kami menjadi saksi atas keputusan mereka.</span></p>');
INSERT INTO `quran` VALUES (8929,2,21,80,'<p><span>Maka Kami berikan pengertian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1906">1906</a><span>]</span><span> kepada Sulaiman dan kepada masing-masing dari mereka Kami berikan kebijaksanaan dan ilmu. </span><span>[b]</span><span> Dan Kami tundukkan gunung-gunung dan burung-burung untuk bertasbih bersama Daud. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1907">1907</a><span>]</span><span> Dan Kami Yang mengerjakan</span><span>nya</span><span>.</span></p><p><span>[b] </span><span>34:11 ; 38:19, 20.</span></p>');
INSERT INTO `quran` VALUES (8930,2,21,81,'<p><span>Dan Kami mengajar dia membuat baju besi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1908">1908</a><span>]</span><span> bagimu supaya dapat melindungi dari pertempuranmu. Maka apakah kamu mau bersyukur ?</span></p>');
INSERT INTO `quran` VALUES (8931,2,21,82,'<p><span>[a]</span><span> Dan </span><span>Kami tundukkan </span><span> kepada Sulaiman angin yang kencang ; </span><span>angin </span><span> itu bertiup atas perintahnya ke arah daerah yang telah Kami berkati di dalamnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1909">1909</a><span>]</span><span> Dan Kami Maha Mengetahui segala sesuatu.</span></p><p><span>[a]</span><span> 34:13.</span></p>');
INSERT INTO `quran` VALUES (8932,2,21,83,'<p><span>Dan </span><span>dari golongan</span><span> </span><span>[a]</span><span> pemberontak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1910">1910</a><span>]</span><span> ada yang menyelam untuk dia, dan mereka menjalankan pekerjaan lain selain itu ;dan Kami yang memelihara mereka.</span></p><p><span>[a]</span><span> 34:13 ; 38:37 ; 38:38, 39.</span></p>');
INSERT INTO `quran` VALUES (8933,2,21,84,'<p><span>[b]</span><span> Dan </span><span>ingatlah</span><span> Ayyub, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1911">1911</a><span>]</span><span> tatkala ia berseru kepada Tuhan-nya, “Sesungguhnya aku di timpa kesusahan, dan Engkau yang paling Penyayang di antara semua penyayang.”</span></p><p><span>[b]</span><span> 38:42.</span></p>');
INSERT INTO `quran` VALUES (8934,2,21,85,'<p><span>Maka Kami kabulkan doanya dan Kami jauhkan kesusahan yang dideritanya, </span><span>[c]</span><span> dan Kami memberikan kepadanya keluarganya dan semisalnya bersama mereka sebagai rahmat dari sisi Kami, dan sebagai nasihat bagi orang-orang yang beribadah.</span></p><p><span>[c]</span><span> 38:44.</span></p>');
INSERT INTO `quran` VALUES (8935,2,21,86,'<p><span>Dan </span><span>ingatlah</span><span> </span><span>[a]</span><span> Ismail dan Idris, dan </span><span>[b]</span><span> Dzul-Kifli, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1912">1912</a><span>]</span><span> semua termasuk orang-orang yang sabar.</span></p><p><span>[a]</span><span> 6:87 ; 38:49.</span></p><p><span>[b]</span><span> 38:49.</span></p>');
INSERT INTO `quran` VALUES (8936,2,21,87,'<p><span>Dan Kami masukkan mereka itu ke dalam rahmat Kami. Sesungguhnya mereka itu orang-orang yang shaleh.</span></p>');
INSERT INTO `quran` VALUES (8937,2,21,88,'<p><span>[a]</span><span> Dan </span><span>ingatlah</span><span> Dzun-nun (Yunus) ketika ia pergi dalam keadaan marah, dan ia yakin bahwa Kami tidak akan sekali-kali mendatangkan kesusahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1913">1913</a><span>]</span><span> kepadanya, maka </span><span>[b]</span><span> ia berseru dalam kegelapan, “Tiada Tuhan selain Engkau, Engkau Mahasuci. Sesungguhnya aku termasuk orang yang aniaya.”</span></p><p><span>[a]</span><span> 37:140 , 141 ; 68:49.</span></p><p><span>[b]</span><span> 37:144.</span></p>');
INSERT INTO `quran` VALUES (8938,2,21,89,'<p><span> </span><span>[a]</span><span> Maka Kami kabulkan doanya, dan Kami selamatkan dia dari kesedihan. Dan demikianlah Kami selamatkan orang-orang beriman.</span></p><p><span>[a]</span><span> 68:50, 51.</span></p>');
INSERT INTO `quran` VALUES (8939,2,21,90,'<p><span>[b]</span><span> dan </span><span>ingatlah </span><span> Zakaria, ketika ia berseru kepada Tuhan-nya. “Ya Tuhan, janganlah Engkau tinggalkan aku seorang diri, dan Engkau-lah sebaik-baik waris.”</span></p><p><span>[b]</span><span> 3:39 ; 19:3 </span><span>–</span><span> 7.</span></p>');
INSERT INTO `quran` VALUES (8940,2,21,91,'<p><span>Maka Kami kabulkan doanya dan menganugerahkan kepada Yahya ; dan Kami sembuhkan baginya isterinya </span><span>dari kemandulan.</span><span> Sesungguhnya mereka itu bersegera dalam kebaikan dan </span><span>[c]</span><span> mereka berseru kepada Kami dengan harapan dan ketakutan, dan mereka merendahkan diri di hadapan Kami.</span></p><p><span>[c] </span><span>32:17.</span></p>');
INSERT INTO `quran` VALUES (8941,2,21,92,'<p><span>[d]</span><span> Dan </span><span>ingatlah</span><span> perempuan yang memelihara kesuciannya, maka Kami hembuskan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1914">1914</a><span>]</span><span> kepada ruh Kami dan Kami jadikan dia dan puteranya suatu Tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1914a">1914a</a><span>]</span><span> untuk seluruh dunia.</span></p><p><span>[d]</span><span> 66:13.</span></p>');
INSERT INTO `quran` VALUES (8942,2,21,93,'<p><span>[a]</span><span> Sesungguhnya umatmu ini merupakan satu umat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1915">1915</a><span>]</span><span> dan Aku adalah Tuhan-mu, maka sembahlah Aku.</span></p><p><span>[a]</span><span> 23:53.</span></p>');
INSERT INTO `quran` VALUES (8943,2,21,94,'<p><span>[b]</span><span> Dan mereka telah memecah-mecah urusan agama mereka di antara mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1916">1916</a><span>]</span><span> </span><span>Padahal</span><span> semuanya akan kembali kepada Kami.</span></p><p><span>[b]</span><span> 23:54.</span></p>');
INSERT INTO `quran` VALUES (8944,2,21,95,'<p><span>R. 7</span></p><p><span>[c]</span><span> Maka barangsiapa beramal shaleh dan ia orang mukmin, maka usahanya tidak akan ditolak, dan Kami akan mencatatnya.</span></p><p><span>[c]</span><span> 4:125 ; 10:10 ; 16:98 ; 20:113.</span></p>');
INSERT INTO `quran` VALUES (8945,2,21,96,'<p><span>[a]</span><span> Dan tidak mungkin bagi </span><span>penduduk</span><span> suatu negeri yang telah Kami binasakan, bahwa mereka tidak akan kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1917">1917</a><span>]</span></p><p><span>[a]</span><span> 23:100, 101 ; 36:32.</span></p>');
INSERT INTO `quran` VALUES (8946,2,21,97,'<p><span>Hingga ketika dibukakan </span><span>tembok</span><span> untuk </span><span>[b]</span><span> Yajuj dan Majuj </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1918">1918</a><span>]</span><span> dan mereka dari setiap tempat yang tinggi tersebar luas. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1919">1919</a><span>]</span></p><p><span>[b]</span><span> 18:95.</span></p>');
INSERT INTO `quran` VALUES (8947,2,21,98,'<p><span>Sudah dekat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1920">1920</a><span>]</span><span> janji yang benar ; maka sekonyong-konyong akan terbelalak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1920a">1920a</a><span>] [c]</span><span> mata orang-orang yang ingkar, </span><span>mereka berseru,</span><span> “Aduhai, celakalah kami ! Kami sungguh dalam kelalaian mengenai ini ; bahkan, kami orang yang aniaya ! “</span></p><p><span>[c]</span><span> 14:43.</span></p>');
INSERT INTO `quran` VALUES (8948,2,21,99,'<p><span>“Sesungguhnya kamu dan apa yang kamu sembah selain Allah adalah bahan bakar Jahannam. </span><span>[a]</span><span> Kamu akan masuk ke dalamnya.”</span></p><p><span>[a]</span><span> 19:72. </span></p>');
INSERT INTO `quran` VALUES (8949,2,21,100,'<p><span>Sekiranya semua itu tuhan-tuhan, niscaya mereka tidak akan masuk ke dalamnya ; dan semuanya akan tinggal lama di dalamnya.</span></p>');
INSERT INTO `quran` VALUES (8950,2,21,101,'<p><span>[b]</span><span> Mereka di dalamnya ada jeritan, dan mereka di dalamnya tidak mendengar </span><span>apa-apa.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1921">1921</a><span>]</span></p><p><span>[b]</span><span> 11:107 ; 25:14 ; 67:8.</span></p>');
INSERT INTO `quran` VALUES (8951,2,21,102,'<p><span>[c]</span><span> Sesungguhnya orang-orang yang telah lebih dahulu mendapat ganjaran baik dari Kami, mereka itu akan dijauhkan darinya.</span></p><p><span>[c]</span><span> 19:73.</span></p>');
INSERT INTO `quran` VALUES (8952,2,21,103,'<p><span>Mereka tidak akan mendengar sedikit pun suaranya</span><span>, [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1922">1922</a><span>] [d]</span><span> dan mereka kekal dalam apa yang diri mereka inginkan.</span></p><p><span>[d]</span><span> 41:32.</span></p>');
INSERT INTO `quran` VALUES (8953,2,21,104,'<p><span>Tidak akan menyedihkan mereka kecemasan yang besar, dan </span><span>[a]</span><span> malaikat-malaikat akan bertemu dengan mereka, </span><span>berkata,</span><span> “Inilah harimu yang telah dijanjikan kepadamu.”</span></p><p><span>[a]</span><span> 41:31.</span></p>');
INSERT INTO `quran` VALUES (8954,2,21,105,'<p><span>[b]</span><span> Pada hari ketika langit akan Kami gulung, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1923">1923</a><span>]</span><span> seperti tergulungnya lembaran- lembaran naskah-tertulis.” </span><span>[c]</span><span> Sebagaimana telah kami memulai penciptaan untuk pertama kali </span><span>dan </span><span> Kami akan mengulanginya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1924">1924</a><span>]</span><span> lagi, suatu janji yang menjadi kewajiban atas Kami. Sesungguhnya hal itu pasti akan Kami laksanakan.</span></p><p><span>[b]</span><span> 39:68.</span></p><p><span>[c]</span><span> 20:56 ; 29:20 ; 30:12.</span></p>');
INSERT INTO `quran` VALUES (8955,2,21,106,'<p><span>Dan sesungguhnya telah Kami tulis dalam Kitab Zabur, sesudah nasihat itu, bahwa negeri itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1925">1925</a><span>]</span><span> akan diwarisi oleh hamba-hamba-Ku yang shaleh.</span></p>');
INSERT INTO `quran` VALUES (8956,2,21,107,'<p><span>Sesungguhnya dalam </span><span>hal </span><span> ini ada suatu amanat bagi kaum yang beribadah.</span></p>');
INSERT INTO `quran` VALUES (8957,2,21,108,'<p><span>[a]</span><span> Dan tidaklah Kami mengutus engkau melainkan sebagai rahmat bagi semesta alam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1926">1926</a><span>]</span></p><p><span>[a]</span><span> 34:29.</span></p>');
INSERT INTO `quran` VALUES (8958,2,21,109,'<p><span>[b]</span><span> Katakanlah, “Sesungguhnya diwahyukan kepada-ku, bahwa Tuhan-mu adalah Tuhan Yang Esa. Maka, apakah kamu berserah diri ?”</span></p><p><span>[b]</span><span> 18:111 ; 41:7.</span></p>');
INSERT INTO `quran` VALUES (8959,2,21,110,'<p><span>Maka jika mereka berpaling, maka katakanlah, “Aku telah memperingatkan yang sama kepoda kamu, dan </span><span>[a]</span><span> aku tidak tahu, apakah telah dekat ataukah masih jauh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1927">1927</a><span>]</span><span> apa yang telah dijanjikan kepadamu ;</span></p><p><span>[a]</span><span> 72:26.</span></p>');
INSERT INTO `quran` VALUES (8960,2,21,111,'<p><span>[b]</span><span> “Sesungguhnya Dia mengetahui apa yang telah dizahirkan dalam ucapan, dan Dia mengetahui apa yang kamu sembunyikan ;</span></p><p><span>[b]</span><span> 2:34 ; 20:8 ; 87:8.</span></p>');
INSERT INTO `quran` VALUES (8961,2,21,112,'<p><span>“Dan aku tidak tahu, tetapi boleh jadi hal itu suatu fitnah bagi kamu dan kesenangan untuk sementara waktu.”</span></p>');
INSERT INTO `quran` VALUES (8962,2,21,113,'<p><span>[c]</span><span> </span><span>Dan Rasulullah saw.</span><span> berkata, “Ya Tuhan-ku, hakimilah dengan kebenaran, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1928">1928</a><span>]</span><span> Tuhan Kami adalah Yang Maha Pemurah, yang dimohonkan pertolongan-Nya terhadap apa yang kamu terangkan.”</span></p><p><span>[c]</span><span> 7:90.</span></p>');
INSERT INTO `quran` VALUES (8963,2,22,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (8964,2,22,2,'<p><span>Hai manusia, bertakwalah kepada Tuhan-mu ; sesungguhnya kegoncangan Saat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1929">1929</a><span>]</span><span> itu sesuatu yang sangat dashyat.</span></p>');
INSERT INTO `quran` VALUES (8965,2,22,3,'<p><span>Pada hari ketika engkau melihatnya, setiap wanita yang menyusui akan lupa kepada yang disusuinya dan setiap wanita yang sedang hamil akan menggugurkan kandungannya ; dan engkau akan melihat manusia mabuk, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1930">1930</a><span>]</span><span> padahal mereka itu tidak mabuk, akan tetapi azab Allah sungguh sangat keras.</span></p>');
INSERT INTO `quran` VALUES (8966,2,22,4,'<p><span>[a] </span><span>Dan dari antara manusia ada yang berbantah mengenai Allah tanpa pengetahuan, dan mengikuti setiap syaitan pemberontak.</span></p><p><span>[a]</span><span> 13:14 ; 22:9 ; 31:21 ; 40:70.</span></p>');
INSERT INTO `quran` VALUES (8967,2,22,5,'<p><span>Mengenai dia telah diputuskan, bahwa </span><span>[b]</span><span> barangsiapa yang bersahabat dengan dia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1931">1931</a><span>]</span><span> maka ia menyesatkannya dan menuntunnya kepada azab yang bernyala-nyala.</span></p><p><span>[b]</span><span> 4:39, 120.</span></p>');
INSERT INTO `quran` VALUES (8968,2,22,6,'<p><span>Hai manusia, jika kamu dalam keraguan mengenai kebangkitan </span><span>kembali,</span><span> maka sesungguh nya Kami telah menciptakan kamu dari tanah, kemudian dari setetes mani, kemudian dari segumpal darah, kemudian dari sepotong daging, sebagian telah berbentuk dan sebagian lagi belum berbentuk, supaya Kami dapat menjelaskan kepadamu. </span><span>[c]</span><span> Dan Kami menempat kan di dalam rahim-rahim sebagaimana yang Kami kehendaki sampai masa yang telah ditentukan ; kemudian Kami keluarkan kamu sebagai bayi, lalu kamu mencapai kedewasaan mu. Dan di antara kamu ada yang diwafatkan, </span><span>[a]</span><span> dan sebagian dari kamu ada yang dipanjangkan umurnya hingga pikun, sehingga ia tidak mengetahui sedikit pun setelah ia mengetahui. Dan engkau melihat bumi gersang, </span><span>[b]</span><span> tetapi apabila Kami turunkan air di atasnya, ia bergerak dan berkembang, dan menumbuhkan segala macam tumbuhan yang indah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1932">1932</a><span>]</span></p><p><span>[c]</span><span> 13:9 ; 35:12 ; 41:48.</span></p><p><span>[a]</span><span> 16:71 ; 36:69.</span></p><p><span>[b]</span><span> 16:66 ; 27:61 ; 30:49-51 ; 35:28 ; 45:6.</span></p>');
INSERT INTO `quran` VALUES (8969,2,22,7,'<p><span>Yang demikian itu, karena sesungguhnya Allah, Dia-lah Yang Maha Benar dan sesungguh nya </span><span>[c]</span><span> Dia-lah Yang menghidupkan yang mati, dan sesungguhnya Dia atas segala sesuatu berkuasa.</span></p><p><span>[c] </span><span>2:74 ; 30:51 ; 35:10 ; 41:40 ; 42:10 ; 57:18.</span></p>');
INSERT INTO `quran` VALUES (8970,2,22,8,'<p><span>[d]</span><span> Dan sesungguhnya saat yang ditentukan itu akan datang, tidak sedikit pun keraguan di dalamnya, dan sesungguhnya Allah akan menbangkitkan orang yang ada di dalam kubur.</span></p><p><span>[d]</span><span> 15:86 ; 18:22 ; 20:16 ; 40:60 ; 45:33.</span></p>');
INSERT INTO `quran` VALUES (8971,2,22,9,'<p><span>[a]</span><span> Dan di antara manusia ada yang berbantah tentang Allah tanpa ilmu dan tanpa petunjuk dan tanpa Kitab yang menerangi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1933">1933</a><span>]</span></p><p><span>[a]</span><span> 22:4.</span></p>');
INSERT INTO `quran` VALUES (8972,2,22,10,'<p><span>Ia membalikkan sisinya supaya ia dapat menyesatkan </span><span>manusia </span><span> dari jalan Allah. Baginya di dunia ada kehinaan, dan Kami merasakan kepadanya pada Hari Kiamat azab yang membakar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1934">1934</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8973,2,22,11,'<p><span>[b]</span><span> Yang demikian itu disebabkan apa yang dilakukan kedua tanganmu, dan sesungguhnya Allah tidak akan berlaku aniaya terhadap hamba-hamba-</span><span>Nya.</span></p><p><span>[b]</span><span> 3:183 ; 8:52 ; 41:47.</span></p>');
INSERT INTO `quran` VALUES (8974,2,22,12,'<p><span>R. 2</span></p><p><span>Dan dari antara manusia ada yang menyembah Allah dengan berdiri di tepi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1935">1935</a><span>]</span><span> Apabila </span><span>[a]</span><span> kebaikan sampai kepadanya, ia merasa puas dengan itu ; dan bila menimpanya cobaan, ia berbalik ke jalan </span><span>semula.</span><span> Dia rugi di dunia dan di akhirat. Yang demikian itu adalah kerugian yang nyata.</span></p><p><span>[a]</span><span> 70:21 </span><span>–</span><span> 22.</span></p>');
INSERT INTO `quran` VALUES (8975,2,22,13,'<p><span>[b]</span><span> Ia menyeru kepada selain dari Allah apa yang tidak dapat memudaratkannya dan tidak dapat memberi manfaat kepadanya. Yang demikian itu adalah kesesatan yang jauh.</span></p><p><span>[b]</span><span> 6:72 ; 10:107 ; 21:67 ; 25:56.</span></p>');
INSERT INTO `quran` VALUES (8976,2,22,14,'<p><span>Ia berseru kepada seseorang yang mudaratnya lebih dekat dari manfaatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1936">1936</a><span>]</span><span> Sungguh buruk pelindung itu, dan sungguh buruk teman bergaul itu.</span></p>');
INSERT INTO `quran` VALUES (8977,2,22,15,'<p><span>[a]</span><span> Sesungguhnya, Allah akan memasukkan orang-orang yang beriman dan beramal shaleh ke dalam kebun-kebun yang mengalir di bawahnya sungai-sungai. Sesungguhnya Allah melakukan apa yang Dia kehendaki.</span></p><p><span>[a]</span><span> 2:278 ; 4:176 ; 10:10 ; 13:30 ; 14:24.</span></p>');
INSERT INTO `quran` VALUES (8978,2,22,16,'<p><span>Barangsiapa mengira Allah tidak akan menolongnya di dunia ini dan di akhirat, maka hendaklah ia merentangkan tali ke langit, lalu biarlah ia memutuskan</span><span>nya.</span><span> Maka hendaklah ia melihat apakah tipu-dayanya itu dapat menghilangkan apa yang menyebabkan dia marah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1937">1937</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8979,2,22,17,'<p><span>Dan demikianlah Kami turunkan </span><span>Alquran</span><span> itu, sebagai Tanda-tanda yang nyata, dan sesungguhnya Allah memberi petunjuk kepada siapa yang Dia kehendaki.</span></p>');
INSERT INTO `quran` VALUES (8980,2,22,18,'<p><span>Sesungguhnya orang-orang yang beriman dan orang-orang Yahudi, dan orang-orang Shabi</span><span>’</span><span>in, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1938">1938</a><span>]</span><span> dan orang-orang Nasrani, dan orang-orang Majusi dan orang-orang berbuat syirik ; niscaya Allah akan memberi keputusan di antara mereka pada Hari Kiamat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1939">1939</a><span>]</span><span> Sesungguhnya Allah atas segala sesuatu </span><span>menjadi saksi.</span></p>');
INSERT INTO `quran` VALUES (8981,2,22,19,'<p><span>Apakah engkau tidak melihat, bahwa </span><span>[a]</span><span> kepada Allah sujud segala yang ada di langit dan segala yang ada di bumi, dan matahari, dan bulan, dan bintang-bintang, dan gunung- gunung, dan pohon-pohon, dan hewan-hewan, dan banyak dari manusia ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1940">1940</a><span>]</span><span> Tetapi banyak pula yang patut mendapat azab. Dan siapa yang dihina Allah, maka tiada ada baginya </span><span>pemberi</span><span> kehormatan. Sesungguhnya Allah melakukkan apa-apa yang Dia kehendaki.</span></p><p><span>[a]</span><span> 13:16 ; 16:40 </span><span>–</span><span> 50 ; 55:7.</span></p>');
INSERT INTO `quran` VALUES (8982,2,22,20,'<p><span>Inilah dua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1941">1941</a><span>]</span><span> golongan petengkar yang berbantah tentang Tuhan mereka. Mengenai orang-orang ingkar, akan dipotongkan bagi mereka pakaian-pakaian dari api. Akan dituangkan di atas kepada mereka </span><span>[a]</span><span> air mendidih.</span></p><p><span>[a]</span><span> 44:49 ; 55:45 ; 56:43 </span><span>–</span><span> 44.</span></p>');
INSERT INTO `quran` VALUES (8983,2,22,21,'<p><span>[b]</span><span> Akan dileburnya dengan itu, apa yang ada dalam perut mereka dan juga kulit.</span></p><p><span>[b]</span><span> 44:46.</span></p>');
INSERT INTO `quran` VALUES (8984,2,22,22,'<p><span>Dan bagi mereka ada cambuk-cambuk dari besi.</span></p>');
INSERT INTO `quran` VALUES (8985,2,22,23,'<p><span>[a]</span><span> Setiap kali mereka hendak ke luar dari situ karena sedih, mereka akan dikembalikan ke dalamnya dan, </span><span>dikatakan</span><span> </span><span>[b]</span><span> Rasakanlah azab yang membakar !”</span></p><p><span>[a]</span><span> 5:38 ; 32:21.</span></p><p><span>[b]</span><span> 8:15 ; 34:43.</span></p>');
INSERT INTO `quran` VALUES (8986,2,22,24,'<p><span>R. 3</span></p><p><span>Sesungguhnya Allah akan memasukkan orang-orang yang beriman dan beramal shaleh ke dalam kebun-kebun yang di bawahnya mengalir sungai-sungai. </span><span>[c]</span><span> Mereka akan dihiasi di dalamnya dengan gelang-gelang emas dan mutiara. Dan </span><span>[d]</span><span> pakaian mereka di dalamnya dari sutera. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1942">1942</a><span>]</span></p><p><span>[c] </span><span>18:32 ; 35:34 ; 76:22.</span></p><p><span>[d]</span><span> 76:13.</span></p>');
INSERT INTO `quran` VALUES (8987,2,22,25,'<p><span>Dan mereka akan dibimbing kepada percakapan yang baik, dan mereka akan dibimbing ke jalan terpuji.</span></p>');
INSERT INTO `quran` VALUES (8988,2,22,26,'<p><span>Sesungguhnya orang-orang yang ingkar dan menghalangi dari jalan Allah, dan </span><span>[e]</span><span> Masjidil haram, yang telah Kami jadikan itu bagi manusia, sama bagi orang yang duduk beribadah di dalamnya dan juga bagi orang yang datang dari kampung. Dan barangsiapa bermaksud buruk di dalamnya untuk menyesatkan dengan aniaya, Kami akan merasakan kepadanya azab yang pedih.</span></p><p><span>[e]</span><span> 8:35 ; 16:89 ; 43:38 ; 48:26.</span></p>');
INSERT INTO `quran` VALUES (8989,2,22,27,'<p><span>Dan </span><span>ingatlah</span><span> ketika Kami menempatkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1943">1943</a><span>]</span><span> Ibrahim di tempat rumah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1943a">1943a</a><span>]</span><span> </span><span>Allah dan berfirman,</span><span> “Janganlah mempersekutukan Aku dengan sesuatu ; dan </span><span>[a]</span><span> bersihkanlah rumah-Ku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1944">1944</a><span>]</span><span> bagi mereka yang tawaf, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1945">1945</a><span>]</span><span> dan mereka yang ruku</span><span>’</span><span> </span><span>dan </span><span> sujud </span><span>dalam </span><span> shalat.</span></p><p><span>[a]</span><span> 2:126.</span></p>');
INSERT INTO `quran` VALUES (8990,2,22,28,'<p><span>[a]</span><span> “Dan umumkanlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1946">1946</a><span>]</span><span> kepada manusia untuk naik haji. Mereka akan datang kepada engkau berjalan kaki, dan menunggang unta yang kurus, datang dari segenap penjuru yang jauh-jauh.</span></p><p><span>[a] </span><span>2:198 ; 3:98.</span></p>');
INSERT INTO `quran` VALUES (8991,2,22,29,'<p><span>“Supaya </span><span>[b]</span><span> mereka dapat menyaksikan manfaat-manfaatnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1947">1947</a><span>]</span><span> bagi mereka, dan dapat menyebut nama Allah </span><span>[c]</span><span> selama hari-hari yang ditetapkan, atas apa yang telah Dia rezeki kan kepada mereka dari binatang ternak berkaki empat. Maka makanlah darinya dan berilah makan orang-orang sengsara, </span><span>dan</span><span> fakir.</span></p><p><span>[b]</span><span> 2:199.</span></p><p><span>[c]</span><span> 2:204.</span></p>');
INSERT INTO `quran` VALUES (8992,2,22,30,'<p><span>“Kemudian hendaklah mereka membersihkan kekotoran mereka, dan menyempurnakan segala nazar mereka, dan bertawaflah di sekeliling Rumah Kuno itu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1948">1948</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8993,2,22,31,'<p><span>Demikianlah, </span><span>[a]</span><span> dan barangsiapa mengagungkan tempat-tempat yang telah disucikan Allah, maka hal itu baik baginya di sisi Tuhan-nya. Dan </span><span>[b]</span><span> telah dihalalkan bagimu semua binatang ternak kecuali apa yang diterangkan kepadamu </span><span>diharamkannya,</span><span> maka jauhilah kenajisan berhala, dan jauhilah ucapan-ucapan dusta.</span></p><p><span>[a]</span><span> 5:3.</span></p><p><span>[b]</span><span> 5:2 ; 6:146.</span></p>');
INSERT INTO `quran` VALUES (8994,2,22,32,'<p><span>Beribadalah</span><span> dengan ikhlas kepada Allah, tanpa mempersekutukan sesuatu dengan-Nya. Dan barangsiapa yang mempersekutukan sesuatu dengan Allah, maka seolah-olah ia jatuh dari langit, dan ia di-sambar burung-burung, atau angin menerbangkan ke tempat yang jauh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1949">1949</a><span>]</span></p>');
INSERT INTO `quran` VALUES (8995,2,22,33,'<p><span>Demikianlah, dan bahwa </span><span>[a]</span><span> barangsiapa memuliakan Tanda-tanda suci Allah, maka sesungguhnya itu adalah ketakwaan hati. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1950">1950</a><span>]</span></p><p><span>[a]</span><span> 2:159.</span></p>');
INSERT INTO `quran` VALUES (8996,2,22,34,'<p><span>Dalam pengurbanan binatang ternak</span><span> itu bagi kamu di dalamnya ada manfaat-manfaat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1951">1951</a><span>]</span><span> sampai suatu masa yang ditentukan kemudian </span><span>[a]</span><span> tempat penyembelihannya adalah di Rumah Kuno.</span></p><p><span>[a]</span><span> 2:197 ; 48:26.</span></p>');
INSERT INTO `quran` VALUES (8997,2,22,35,'<p><span>R. 5</span></p><p><span>Dan bagi setiap umat telah Kami tetapkan cara </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1952">1952</a><span>]</span><span> pengurbanan, supaya </span><span>[b]</span><span> mereka menyebut nama Allah atas apa yang telah Dia rezekikan kepada mereka dari binatang ternak berkaki empat. Maka </span><span>[c]</span><span> Tuhan-mu adalah Tuhan Yang Maha Esa ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1953">1953</a><span>]</span><span> maka patuhlah kamu sekalian kepada-Nya. Dan berikanlah khabar suka kepada orang-orang yang berhati rendah.</span></p><p><span>[b]</span><span> 5:5 ; 6:119.</span></p><p><span>[c]</span><span> 5:74 ; 16:23 ; 37:5.</span></p>');
INSERT INTO `quran` VALUES (8998,2,22,36,'<p><span>[a]</span><span> Orang-orang yang apabila disebut nama Allah gemetarlah hati mereka, dan mereka yang bersabar atas apa yang menimpa mereka, dan yang mendirikan shalat dan dari apa-apa yang Kami rezekikan kepada mereka, mereka membelanjakan.</span></p><p><span>[a]</span><span> 23:61.</span></p>');
INSERT INTO `quran` VALUES (8999,2,22,37,'<p><span>Dan unta-unta kurban Kami jadikan itu bagi kamu sebagai Tanda-tanda Allah yang bagimu di dalamnya banyak kebaikan, Maka sebutlah nama Allah atasnya waktu </span><span>binatang- binatang</span><span> itu berdiri berbaris-baris. Dan apabila telah jatuh </span><span>mati</span><span> pada sisinya, maka makanlah darinya dan berilah makan orang yang tidak mau minta-minta, dan yang minta-minta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1954">1954</a><span>]</span><span> Demikianlah Kami tundukkan mereka </span><span>unta</span><span> itu bagi kamu, supaya kamu bersyukur.</span></p>');
INSERT INTO `quran` VALUES (9000,2,22,38,'<p><span>Sekali-kali tidak akan sampai kepada Allah, dagingnya dan tidak pula darahnya, akan tetapi yang sampai kepada-Nya adalah ketakwaan darimu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1955">1955</a><span>]</span><span> Demikianlah Dia menundukkan mereka untuk kamu, supaya kamu mengagungkan Allah sesuai petunjuk kepadamu. Dan berikan khabar suka kepada orang-orang yang berbuat kebaikan.</span></p>');
INSERT INTO `quran` VALUES (9001,2,22,39,'<p><span>Sesungguhnya Allah membela orang-orang yang beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1956">1956</a><span>]</span><span> Sesungguhnya Allah tidak mencintai setiap orang yang berkhianat, lagi ingkar.</span></p>');
INSERT INTO `quran` VALUES (9002,2,22,40,'<p><span>R. 6</span></p><p><span>Telah diizinkan bagi mereka yang telah diperangi, disebabkan mereka telah dianiaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1957">1957</a><span>]</span><span> Dan sesunngguhnya Allah berkuasa menolong mereka. </span></p>');
INSERT INTO `quran` VALUES (9003,2,22,41,'<p><span>Orang-orang yang telah diusir dari rumah-rumah mereka tanpa hak, hanya karena mereka berkata, “Tuhan kami ialah Allah.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1958">1958</a><span>] [a]</span><span> Dam sekiranya tidak ada tangkisan Allah terhadap sebagian manusia oleh sebagian yang lain, maka akan hancurlah biara-biara serta gereja-gereja Nasrani dan rumah-rumah ibadah Yahudi serta masjid-masjid yang banyak disebut nama Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1959">1959</a><span>]</span><span> di dalamnya. </span><span>[b]</span><span> Dan pasti Allah akan menolong siapa yang menolong-Nya. Sesungguhnya Allah Maha Kuasa, Maha Perkasa.</span></p><p><span>[a]</span><span> 2:252.</span></p><p><span>[b] </span><span>47:8.</span></p>');
INSERT INTO `quran` VALUES (9004,2,22,42,'<p><span>Orang-orang yang, jika Kami teguhkan mereka di bumi, mereka mendirikan shalat dan membayar zakat dan menyuruh berbuat kebaikan dan melarang dari keburukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1960">1960</a><span>]</span><span> Dan kepada Allah kembali segala urusan.</span></p>');
INSERT INTO `quran` VALUES (9005,2,22,43,'<p><span>[a]</span><span> Dan jika mereka itu mendustakan engkau, maka sesungguhnya telah mendustakan pula sebelum mereka, kaum Nuh dan </span><span>‘</span><span>Ad dan Tsamud.</span></p><p><span>[a]</span><span> 6:35 ; 35:26 ; 40:6 ; 54:10.</span></p>');
INSERT INTO `quran` VALUES (9006,2,22,44,'<p><span>Dan kaum Ibrahim dan kaum Luth ;</span></p>');
INSERT INTO `quran` VALUES (9007,2,22,45,'<p><span>Dan penduduk Madyan. Dan telah didustakan Musa. Maka Aku berikan tangguh bagi orang-orang ingkar ; kemudian Aku tangkap mereka, maka betapa </span><span>dahsyatnya akibat</span><span> keingkaran kepada-Ku !</span></p>');
INSERT INTO `quran` VALUES (9008,2,22,46,'<p><span>[a]</span><span> Dan berapa banyaknya kota yang telah Kami binasakannya, yang </span><span>penduduknya</span><span> sedang aniaya, maka mereka jatuh pada atapnya, dan </span><span>banyak</span><span> sumur yang telah ditinggalkan dan </span><span>banyak</span><span> istana yang menjulang tinggi.</span></p><p><span>[a]</span><span> 7:51 ; 21:12 ; 28:59 ; 65:9 </span><span>–</span><span> 10.</span></p>');
INSERT INTO `quran` VALUES (9009,2,22,47,'<p><span>[b]</span><span> Maka apakah mereka tidak berjalan </span><span>melihat</span><span> di bumi, supaya mereka mempunyai hati untuk memahami dengan itu, atau </span><span>mempunyai</span><span> telinga untuk mendengar dengan itu ? Maka sesungguhnya bukan mata yang buta, akan tetapi yang buta adalah hati yang ada dalam dada. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1961">1961</a><span>]</span></p><p><span>[b]</span><span> 12:110 ; 30:10 ; 35:45 ; 40:22 ; 47:11.</span></p>');
INSERT INTO `quran` VALUES (9010,2,22,48,'<p><span>[c]</span><span> Dan mereka minta kepada engkau untuk mempercepat azab, tetapi Allah sekali-kali tidak akan mengingkari janji-Nya. Dan sesungguhnya satu hari di sisi Tuhan-mu seperti seribu tahun menurut perhitungan kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1961a">1961a</a><span>]</span></p><p><span>[c]</span><span> 26:205 ; 27:72 ; 29:54 </span><span>–</span><span> 55 ; 37:177 ; 51:15.</span></p>');
INSERT INTO `quran` VALUES (9011,2,22,49,'<p><span>Dan berapa banyaknya kota telah Aku beri tangguh baginya, padahal dia berlaku aniaya. Kemudian Aku menangkapnya, dan kepada Aku-lah kembali.</span></p>');
INSERT INTO `quran` VALUES (9012,2,22,50,'<p><span>R. 7</span></p><p><span>Katakanlah, “Hai manusia, </span><span>[a]</span><span> sesungguhnya aku bagi kamu hanya pemberi ingat yang nyata.</span></p><p><span>[a]</span><span> 26:116 ; 29:51 ; 51:51 ; 67:27.</span></p>');
INSERT INTO `quran` VALUES (9013,2,22,51,'<p><span>“Maka orang-orang yang beriman dan beramal shaleh. </span><span>[b]</span><span> bagi mereka ada ampunan dan rezeki yang mulia.</span></p><p><span>[b]</span><span> 8:75 ; 24:27 ; 34:5.</span></p>');
INSERT INTO `quran` VALUES (9014,2,22,52,'<p><span>“Tetapi </span><span>[c]</span><span> orang-orang yang berusaha menggagalkan Tanda-tanda Kami, mereka itulah penghuni Jahannam.”</span></p><p><span>[c]</span><span> 34:6, 39.</span></p>');
INSERT INTO `quran` VALUES (9015,2,22,53,'<p><span>Dan Kami tidak pernah mengirim sebelum engkau seorang rasul dan tidak pula seorang nabi melainkan apabila ia menginginkan sesuatu, maka syaitan meletakkan </span><span>rintangan</span><span> pada keinginannya. Tetapi Allah melenyapkan </span><span>rintangan</span><span> yang diletakkan oleh syaitan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1962">1962</a><span>]</span><span> Kemudian Allah meneguhkan Tanda-tanda-Nya. Dan Allah itu Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (9016,2,22,54,'<p><span>Supaya Dia menjadikan </span><span>rintangan</span><span> yang diletakkan oleh syaitan sebagai ujian bagi orang-orang yang dalam hatinya ada penyakit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1963">1963</a><span>]</span><span> dan mereka yang hatinya keras. Dan sesungguhnya orang-orang yang aniaya itu benar-benar dalam permusuhan yang keras.</span></p>');
INSERT INTO `quran` VALUES (9017,2,22,55,'<p><span>Dan supaya </span><span>[a]</span><span> diketahui oleh orang-orang yang diberi ilmu, bahwa </span><span>Alquran</span><span> itu adalah kebenaran dari Tuhan engkau, supaya mereka beriman kepadanya. Dan sesungguhnya Allah pasti memberi petunjuk kepada orang-orang yang beriman ke jalan yang lurus.</span></p><p><span>[a]</span><span> 13:20 ; 34:7 ; 35:32 ; 47:3 ; 56:96.</span></p>');
INSERT INTO `quran` VALUES (9018,2,22,56,'<p><span>Dan tidak henti-hentinya orang-orang yang ingkar </span><span>[b]</span><span> dalam keraguan tentang </span><span>Alquran</span><span> itu, hingga datang kepada mereka Saat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1964">1964</a><span>]</span><span> dengan tiba-tiba atau datang kepada mereka azab, pada suatu hari yang membinasakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1965">1965</a><span>]</span></p><p><span>[b]</span><span> 11:18.</span></p>');
INSERT INTO `quran` VALUES (9019,2,22,57,'<p><span>[a]</span><span> Kerajaan pada hari itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1966">1966</a><span>]</span><span> kepunyaan Allah. Maka Dia akan menghakimi di antara mereka. Maka </span><span>[b]</span><span> orang-orang yang beriman dan beramal shaleh akan ada di dalam surga penuh kenikmatan.</span></p><p><span>[a] </span><span>25:27.</span></p><p><span>[b]</span><span> 13:30 ; 14:24 ; 18:31 ; 30:16 ;68:35 ; 78:32 </span><span>–</span><span> 37.</span></p>');
INSERT INTO `quran` VALUES (9020,2,22,58,'<p><span>Tetapi </span><span>[c]</span><span> orang-orang yang ingkar dan mendustakan Tanda-tanda Kami ; maka itulah orang-orang yang bagi mereka ada azab yang hina.</span></p><p><span>[c]</span><span> 2:40 ; 7:37 ; 30:17 ; 57:20 ; 64:11 ; 78:22 </span><span>–</span><span> 27.</span></p>');
INSERT INTO `quran` VALUES (9021,2,22,59,'<p><span>R. 8</span></p><p><span>Dan </span><span>[d]</span><span> orang-orang yang berhijrah di jalan Allah, kemudian mereka terbunuh atau mati, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1967">1967</a><span>]</span><span> niscaya Allah akan memberi rezki kepada mereka satu rezki yang baik. Dan sesungguhnya Allah adalah sebaik-baik pemberi rezeki.</span></p><p><span>[d]</span><span> 3:196 ; 8:75 ; 9:20 -22 ; 16:42.</span></p>');
INSERT INTO `quran` VALUES (9022,2,22,60,'<p><span>Niscaya Dia akan memasukkan mereka ke dalam suatu tempat yang mereka akan menyukai-nya. Dan sesungguhnya Allah Maha Mengetahui, Maha Penyantun.</span></p>');
INSERT INTO `quran` VALUES (9023,2,22,61,'<p><span>Demikianlah, dan barangsiapa membalas setimpal dengan apa yang telah ditimpakan kepadanya, dan kemudian ia diperlakukan aniaya, tentulah Allah akan menolongnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1968">1968</a><span>]</span><span> Sesungguhnya Allah Maha Pemaaf, Maha Pengampun.</span></p>');
INSERT INTO `quran` VALUES (9024,2,22,62,'<p><span>Yang demikian itu, bahwa sesungguhnya </span><span>[a]</span><span> Allah memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1969">1969</a><span>]</span><span> dan sesungguhnya Allah Maha Mendengar, Maha Melihat.</span></p><p><span>[a]</span><span> 3:28 ; 31:30 ; 35:14 ; 57:7.</span></p>');
INSERT INTO `quran` VALUES (9025,2,22,63,'<p><span>Demikian itu disebabkan </span><span>[b]</span><span> sesungguhnya Allah Dzat Yang Hak, dan bahwa apa yang mereka seru selain Dia, itulah yang batil, dan sesungguhnya Allah Maha Tinggi, Maha Besar.</span></p><p><span>[b]</span><span> 20:115 ; 23:117 ; 24:26.</span></p>');
INSERT INTO `quran` VALUES (9026,2,22,64,'<p><span>apakah engkau tidak melihat, bahwa </span><span>[a]</span><span> Allah menurunkan air dari langit maka bumi menjadi hijau ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1970">1970</a><span>]</span><span> Sesungguhnya Allah Mahahalus, Maha Mengetahui.</span></p><p><span>[a]</span><span> 22:6 ; 30:51 ; 35:28 ; 39:22 ; 45:6.</span></p>');
INSERT INTO `quran` VALUES (9027,2,22,65,'<p><span>[b]</span><span> Kepunyaan Dia-lah apa-apa yang ada di seluruh langit dan apa-apa yang ada di bumi. Dan sesungguhnya Allah Dzat Yang Mahakaya, Maha Terpuji.</span></p><p><span>[b]</span><span> 2:256 ; 10:56 ; 31:27.</span></p>');
INSERT INTO `quran` VALUES (9028,2,22,66,'<p><span>R. 9</span></p><p><span>Tidaklah engkau melihat bahwa </span><span>[c]</span><span> Allah telah menundukkan kepadamu segala yang ada di bumi, dan kapal-kapal yang mengarungi lautan atas perintah-Nya ? Dan Dia-lah Yang menahan langit supaya jangan jatuh ke atas bumi, kecuali dengan izin-Nya. Sesungguhnya Allah terhadap manusia Maha Pengasih, Maha Penyayang.</span></p><p><span>[c]</span><span> 16:15.</span></p>');
INSERT INTO `quran` VALUES (9029,2,22,67,'<p><span>[d]</span><span> Dan Dia-lah Yang menghidupkan kamu, kemudian Dia akan mematikan kamu, kemudian Dia akan menghidupkan kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1971">1971</a><span>]</span><span> Sesungguhnya manusia sangat mengingkari </span><span>nikmat.</span></p><p><span>[d] </span><span>2:29 ; 16:71 ; 30:41 ; 40:69.</span></p>');
INSERT INTO `quran` VALUES (9030,2,22,68,'<p><span>Bagi setiap umat telah Kami tetapkan cara beribadah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1972">1972</a><span>]</span><span> sesuai dengan itu mereka melakukannya, maka janganlah hendaknya mereka berbantah dengan engkau dalam urusan </span><span>Islam</span><span> ; dan ajaklah kepada Tuhan engkau. Sesungguhnya engkau berada pada petunjuk yang lurus.</span></p>');
INSERT INTO `quran` VALUES (9031,2,22,69,'<p><span>Dan jika mereka berbantah dengan engkau, maka katakanlah, “Allah lebih mengetahui apa yang kamu kerjakan ;</span></p>');
INSERT INTO `quran` VALUES (9032,2,22,70,'<p><span>[a]</span><span> “Allah akan menghakimi di antara kamu pada Hari Kiamat mengenai apa yang kamu perselisikan di dalamnya.”</span></p><p><span>[a]</span><span> 2:114 ; 4:142.</span></p>');
INSERT INTO `quran` VALUES (9033,2,22,71,'<p><span>Tidaklah engkau mengetahui, bahwa </span><span>[b]</span><span> Allah mengetahui apa-apa yang ada di langit dan bumi ? Sesungguhnya hal itu </span><span>tersimpan</span><span> dalam sebuah kitab. Sesungguhnya itu bagi Allah sangat mudah.</span></p><p><span>[b]</span><span> 20:8 ; 27:66 ; 49:17.</span></p>');
INSERT INTO `quran` VALUES (9034,2,22,72,'<p><span>[a]</span><span> Dan mereka menyembah selain Allah, yang untuk itu Dia tidak menurunkan dalil, dan tidak ada bagi mereka tentang itu ilmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1973">1973</a><span>]</span><span> Dan bagi orang-orang aniaya tidak ada seorang penolong.</span></p><p><span>[a]</span><span> 7:72 ; 12:41 ; 53:24.</span></p>');
INSERT INTO `quran` VALUES (9035,2,22,73,'<p><span>[b]</span><span> Dan apabila dibacakan kepada mereka Ayat-ayat Kami yang jelas, engkau mengenal pada wajah orang-orang yang ingkar </span><span>tanda-tanda</span><span> penolakan. Hampir mereka menyerang orang-orang yang membacakan Ayat-ayat Kami kepada mereka. Katakanlah, </span><span>[c]</span><span> “ Bolehkan aku khabarkan kepada kamu tentang sesuatu yang lebih buruk dari itu ? Ialah Api ! allah telah menjanjikannya kepada orang-orang yang ingkar. Dan alangkah buruknya tempat kembali itu ! “</span></p><p><span>[b]</span><span> 17:47 ; 23:67 </span><span>–</span><span> 68 ; 39:46.</span></p><p><span>[c]</span><span> 5:61.</span></p>');
INSERT INTO `quran` VALUES (9036,2,22,74,'<p><span>R. 10</span></p><p><span>Hai manusia, suatu tamsil telah dikemukakan, maka dengarlah tamsil itu. </span><span>[d]</span><span> Sesungguhnya, mereka yang kamu seru selain Allah tidak dapat menjadikan seekor lalat, walaupun mereka itu bergabung untuk itu. Dan jika sekiranya lalat itu menyambar sesuatu dari mereka, mereka tidak akan dapat merebutnya kembali dari lalat itu. Sangat lemah yang meminta dan yang diminta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1974">1974</a><span>]</span></p><p><span>[d]</span><span> 16:21.</span></p>');
INSERT INTO `quran` VALUES (9037,2,22,75,'<p><span>[a]</span><span> Mereka tidak dapat memahami </span><span>sifat-sifat</span><span> Allah dengan sebenar-benarnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1975">1975</a><span>]</span><span> Sesungguhnya Allah Mahakuat, Maha Perkasa.</span></p><p><span>[a]</span><span> 6:92 ; 39:68.</span></p>');
INSERT INTO `quran` VALUES (9038,2,22,76,'<p><span>Allah memilih dari antara malaikat-malaikat, rasul-rasul dan dari antara manusia. Sesungguhnya allah Maha Mendengar, Maha Melihat.</span></p>');
INSERT INTO `quran` VALUES (9039,2,22,77,'<p><span>[b]</span><span> Dia mengetahui apa-apa yang di hadapan mereka dan apa-apa yang di belakang mereka. Dan kepada Allah akan dikembalikan segala urusan.</span></p><p><span>[b]</span><span> 2:256 ; 27:26 ; 49:17.</span></p>');
INSERT INTO `quran` VALUES (9040,2,22,78,'<p><span>Hai orang-orang yang beriman ! </span><span>[a]</span><span> Ruku</span><span>’</span><span>lah kamu dan sujudlah dan sembahlah Tuhan-mu, dan berbuatlah kebaikan, supaya kamu memperoleh kebahagiaan.</span></p><p><span>[a]</span><span> 3:44 ; 41:38 ; 96:20.</span></p>');
INSERT INTO `quran` VALUES (9041,2,22,79,'<p><span>[b]</span><span> Dan berjihadlah kamu di </span><span>jalan</span><span> Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1976">1976</a><span>]</span><span> dengan sebenar-benarnya jihad. Dia telah memilih kamu, dan Dia tidak menjadikan kesukaran padamu dalam </span><span>urusan</span><span> agama ; </span><span>[c]</span><span> </span><span>Ikuti lah </span><span>agama bapakmu, Ibrahim. Dia telah memberi kamu nama Muslimin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1977">1977</a><span>]</span><span> dahulu dan dalam Kitab ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1977a">1977a</a><span>] [d]</span><span> supaya Rasul itu menjadi saksi atas kamu, dan supaya kamu menjadi saksi atas umat manusia. Maka dirikanlah shalat dan bayarlah zakat, dan berpeganglah teguh kepada Allah. Dia Pemeliharamu, maka Dia-lah sebaik-baik Pemelihara dan sebaik-baik Penolong !</span></p><p><span>[b]</span><span> 9:41.</span></p><p><span>[c]</span><span> 2:136 ; 16:124.</span></p><p><span>[d]</span><span> 2:144 ; 16:90.</span></p>');
INSERT INTO `quran` VALUES (9042,2,23,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9043,2,23,2,'<p><span>Sesungguhnya telah berhasil orang-orang yang beriman, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1978">1978</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9044,2,23,3,'<p><span>Orang-orang yang dalam shalat mereka khusyu</span><span>’</span><span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1979">1979</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9045,2,23,4,'<p><span>Dan </span><span>[b]</span><span> orang-orang yang dari hal sia-sia, mereka berpaling, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1980">1980</a><span>]</span></p><p><span>[b]</span><span> 25:73.</span></p>');
INSERT INTO `quran` VALUES (9046,2,23,5,'<p><span>Dan </span><span>[c]</span><span> orang-orang yang membayar zakat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1981">1981</a><span>]</span></p><p><span>[c]</span><span> 5:56 ; 9:71.</span></p>');
INSERT INTO `quran` VALUES (9047,2,23,6,'<p><span>Dan </span><span>[d]</span><span> orang-orang yang menjaga kemaluannya ;</span></p><p><span>[d]</span><span> 70:30.</span></p>');
INSERT INTO `quran` VALUES (9048,2,23,7,'<p><span>[a]</span><span> Kecuali terhadap istri-istri mereka atau apa yang dimiliki tangan kanan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1981a">1981a</a><span>]</span><span> maka sesungguhnya mereka tidak tercela ;</span></p><p><span>[a]</span><span> 70:31.</span></p>');
INSERT INTO `quran` VALUES (9049,2,23,8,'<p><span>[b]</span><span> Tetapi barangsiapa menghendaki selain dari itu, maka sesungguhnya mereka itulah orang-orang yang melampui batas.</span></p><p><span>[b]</span><span> 70:32.</span></p>');
INSERT INTO `quran` VALUES (9050,2,23,9,'<p><span>[c]</span><span> Dan orang-orang yang memelihara amanat-amanat dan perjanjian-perjanjian mereka.</span></p><p><span>[c]</span><span> 70:33.</span></p>');
INSERT INTO `quran` VALUES (9051,2,23,10,'<p><span>[d]</span><span> Dan orang-orang yang memelihara shalat mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1982">1982</a><span>]</span></p><p><span>[d]</span><span> 6:93 ; 70:35.</span></p>');
INSERT INTO `quran` VALUES (9052,2,23,11,'<p><span>Mereka itulah pewaris,</span></p>');
INSERT INTO `quran` VALUES (9053,2,23,12,'<p><span>Yaitu</span><span> </span><span>[e]</span><span> orang-orang yang akan mewarisi Firdaus. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1983">1983</a><span>]</span><span> Mereka akan tinggal kekal di dalamnya.</span></p><p><span>[e]</span><span> 18:108 ; 70:36.</span></p>');
INSERT INTO `quran` VALUES (9054,2,23,13,'<p><span>Dan sesungguhnya </span><span>[a]</span><span> telah Kami jadikan manusia dari sari tanah liat ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1984">1984</a><span>]</span></p><p><span>[a]</span><span> 32:8 </span><span>–</span><span> 9.</span></p>');
INSERT INTO `quran` VALUES (9055,2,23,14,'<p><span>Kemudian </span><span>[b]</span><span> Kami menjadikan air mani di dalam tempat penyimpanan yang kokoh.</span></p><p><span>[b]</span><span> 22:6.</span></p>');
INSERT INTO `quran` VALUES (9056,2,23,15,'<p><span>Kemudian Kami jadikan air mani menjadi segumpal darah ; maka Kami jadikan segumpal darah itu menjadi segumpal daging, maka Kami jadikan dari segumpal daging itu tulang- tulang, kemudian Kami bungkus tulang-tulang itu dengan daging ; kemudian Kami tumbuhkan dia menjadi makhluk lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1985">1985</a><span>]</span><span> Maka Maha Berkat Allah, sebaik-baik Pencipta.</span></p>');
INSERT INTO `quran` VALUES (9057,2,23,16,'<p><span>Kemudian, sesungguhnya sesudah itu </span><span>[a]</span><span> kamu pasti akan mati. </span><span>{</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1986">1986</a><span>]</span></p><p><span>[a]</span><span> 39:31.</span></p>');
INSERT INTO `quran` VALUES (9058,2,23,17,'<p><span>[b]</span><span> Kemudian, sesungguhnya kamu pada Hari Kiamat akan dibangkitkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1987">1987</a><span>]</span></p><p><span>[b]</span><span> 39:32.</span></p>');
INSERT INTO `quran` VALUES (9059,2,23,18,'<p><span>[c]</span><span> Dan sesungguhnya telah Kami jadikan di atas kamu tujuh jalan ruhani, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1988">1988</a><span>]</span><span> dan Kami tidak pernah lalai dari penciptaan.</span></p><p><span>[c]</span><span> 78:13.</span></p>');
INSERT INTO `quran` VALUES (9060,2,23,19,'<p><span>[d]</span><span> Dan Kami turunkan air dari langit menurut suatu ukuran, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1989">1989</a><span>]</span><span> dan Kami menampungnya dalam bumi, dan sesungguhnya Kami untuk melenyapkannya sangat berkuasa.</span></p><p><span>[d]</span><span> 15:23.</span></p>');
INSERT INTO `quran` VALUES (9061,2,23,20,'<p><span>[a]</span><span> Maka Kami tumbuhkan bagimu dengan itu kebun-kebun kurma dan anggur ; bagimu di dalamnya banyak buah-buahan dan sebagian dari itu kamu makan.</span></p><p><span>[a]</span><span> 16:12, 68 ; 36:35.</span></p>');
INSERT INTO `quran` VALUES (9062,2,23,21,'<p><span>Dan pohon yang keluar dari Gunung Sinai ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1990">1990</a><span>]</span><span> menghasilkan minyak dan bumbu untuk orang-orang yang makan ; </span></p>');
INSERT INTO `quran` VALUES (9063,2,23,22,'<p><span>[b]</span><span> Dan sesungguhnya bagi kamu pada hewan ternak pasti ada pelajaran. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1991">1991</a><span>]</span><span> Kami beri minum kepadamu dari apa yang ada di dalam perutnya, dan kamu mempunyai banyak lagi manfaat di dalamnya, dan dari sebagiannya kamu makan ;</span></p><p><span>[b]</span><span> 6:143 ; 16:6 ; 36:72 </span><span>–</span><span> 73 ; 40:80 </span><span>–</span><span> 81.</span></p>');
INSERT INTO `quran` VALUES (9064,2,23,23,'<p><span>Dan </span><span>[c]</span><span> di atas </span><span>ternak</span><span> itu ; dan di atas perahu-perahu kamu diangkut.</span></p><p><span>[c]</span><span> 16:8 </span><span>–</span><span> 9 ; 36:42 </span><span>–</span><span> 43 ; 43:13.</span></p>');
INSERT INTO `quran` VALUES (9065,2,23,24,'<p><span>R. 2</span></p><p><span>[a]</span><span> Dan sesungguhnya telah Kami utus Nuh kepada kaumnya, maka ia berkata, “Hai kaumku, sembahlah Allah, tiada bagimu Tuhan selain Dia. Apakah kamu tidak akan bertakwa ?”</span></p><p><span>[a]</span><span> 7:60 ; 11:26 ; 71:2.</span></p>');
INSERT INTO `quran` VALUES (9066,2,23,25,'<p><span>[b]</span><span> Maka berkata pemuka-pemuka orang-orang yang ingkar dari kaumnya, “Tidaklah orang ini melainkan seorang manusia seperti kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1992">1992</a><span>]</span><span> ia berusaha memperoleh keunggulan di atas kamu. Dan jika Allah menghendaki, tentulah </span><span>[c]</span><span> Dia akan menurunkan malaikat- malaikat. Tidak pernah kami mendengar semacam ini dari bapak-bapak kami dahulu. ;</span></p><p><span>[b]</span><span> 7:61 ; 11:28 ; 17:95 ; 34:44.</span></p><p><span>[c]</span><span> 17:96.</span></p>');
INSERT INTO `quran` VALUES (9067,2,23,26,'<p><span>[d]</span><span> Tidaklah ia melainkan seorang laki-laki yang berpenyakit gila ; maka tunggulah akibatnya untuk sementara waktu.”</span></p><p><span>[d]</span><span> 54:10.</span></p>');
INSERT INTO `quran` VALUES (9068,2,23,27,'<p><span>[e]</span><span> </span><span>Nuh</span><span> berkata, “Ya Tuhan-ku, tolonglah aku, karena mereka telah mendustakan aku.”</span></p><p><span>[e]</span><span> 26:118 </span><span>–</span><span> 119 ; 54:11.</span></p>');
INSERT INTO `quran` VALUES (9069,2,23,28,'<p><span>Maka Kami telah wahyukan kepadanya, </span><span>[f]</span><span> “Agar buatlah bahtera itu di hadapan mata Kami sesuai wahyu Kami. </span><span>[g]</span><span> Maka apabila datang perintah Kami, dan air bersemburan ke luar, maka muatkanlah ke dalamnya dari setiap </span><span>jenis </span><span> sejodoh, dan keluargamu, kecuali orang dari antara mereka yang telah lebih dulu ditetapkan perintah azab Kami atas mereka. Dan janganlah engkau bicarakan dengan Aku mengenai orang-orang yang aniaya ; sesungguhnya mereka akan ditenggelamkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1993">1993</a><span>]</span></p><p><span>[f]</span><span> 11:38.</span></p><p><span>[g]</span><span> 11:41 ; 54:13 </span><span>–</span><span> 14.</span></p>');
INSERT INTO `quran` VALUES (9070,2,23,29,'<p><span>[a]</span><span> Dan apabila engkau telah duduk tenang dan orang-orang beserta engkau di atas bahtera, maka katakanlah, “Segala puji bagi Allah yang telah menyelamatkan kami dari kaum yang aniaya ! “</span></p><p><span>[a]</span><span> 11:42 ; 43:14.</span></p>');
INSERT INTO `quran` VALUES (9071,2,23,30,'<p><span>“Dan katakanlah, </span><span>[b]</span><span> </span><span>‘</span><span>Ya Tuhan-ku, turunkanlah aku di tempat turun yang diberkati, dan Engkau adalah sebaik-baik yang menurunkan</span><span>’</span><span>.”</span></p><p><span>[b] </span><span>11:49.</span></p>');
INSERT INTO `quran` VALUES (9072,2,23,31,'<p><span>[c]</span><span> Sesungguhnya dalam hal ini adalah Tanda-tanda, dan Kami pasti menguji </span><span>hamba-hamba</span><span> Kami.</span></p><p><span>[c]</span><span> 29:16.</span></p>');
INSERT INTO `quran` VALUES (9073,2,23,32,'<p><span>[d]</span><span> Kemudian Kami bangkitkan sesudah mereka suatu keturunan lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1994">1994</a><span>]</span></p><p><span>[d]</span><span> 23:43 ; 25:39.</span></p>');
INSERT INTO `quran` VALUES (9074,2,23,33,'<p><span>Dan Kami kirimkan pada mereka seorang rasul dari antara mereka supaya sembahlah Allah. Tiada bagimu Tuhan selain Dia. Apakah kamu tidak bertakwa ?</span></p>');
INSERT INTO `quran` VALUES (9075,2,23,34,'<p><span>R. 3</span></p><p><span>Dan berkata pemuka-pemuka dari kaumnya yang ingkar and mendustakan pertemuan di akhirat dan </span><span>[a]</span><span> yang kepada mereka telah Kami berikan kesenangan dalam kehidupan dunia, “Ia tidak lain melainkan manusia seperti kamu. </span><span>[b]</span><span> Ia makan dari apa yang kamu makan, dan ia minum apa yang kamu minum ;</span></p><p><span>[a]</span><span> 17:17. </span></p><p><span>[b]</span><span> 21:9 ; 25:8.</span></p>');
INSERT INTO `quran` VALUES (9076,2,23,35,'<p><span>[c]</span><span> “Dan jika kamu ta</span><span>’</span><span>at kepada seseorang seperti kamu, sesungguhnya kamu jika demikian, niscaya menjadi orang-orang yang rugi.</span></p><p><span>[c]</span><span> 23:48.</span></p>');
INSERT INTO `quran` VALUES (9077,2,23,36,'<p><span>[d]</span><span> “Apakah Dia menjanjikan kepada kamu, bahwa apabila kamu telah mati dan telah menjadi tanah dan tulang-belulang, sesungguhnya kamu akan dikeluarkan ?</span></p><p><span>[d]</span><span> 17:50 ; 36:79 ; 50:4.</span></p>');
INSERT INTO `quran` VALUES (9078,2,23,37,'<p><span>[e]</span><span> “Jauh, jauh sekali </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1995">1995</a><span>]</span><span> apa yang telah dijanjikan kepadamu itu ; </span></p><p><span>[e]</span><span> 50:4.</span></p>');
INSERT INTO `quran` VALUES (9079,2,23,38,'<p><span>[a]</span><span> “Tiada kehidupan lain kecuali kehidupan kita di dunia ; kita mati dan kita hidup, dan kita tidak akan dibangkitkan ;</span></p><p><span>[a]</span><span> 6:20 ; 19:67 ; 36:79 ; 44:36 ; 45:25.</span></p>');
INSERT INTO `quran` VALUES (9080,2,23,39,'<p><span>“Ia hanya seorang laki-laki yang telah mengada-adakan dusta terhadap Allah ; dan kepadanya kami tidak akan beriman.”</span></p>');
INSERT INTO `quran` VALUES (9081,2,23,40,'<p><span>Berkatalah ia, “Ya Tuhan-ku, tolonglah aku, karena mereka telah mendustakanku.”</span></p>');
INSERT INTO `quran` VALUES (9082,2,23,41,'<p><span>Allah</span><span> berfirman, “Dalam sedikit waktu lagi pasti mereka akan menjadi orang yang menyesal .”</span></p>');
INSERT INTO `quran` VALUES (9083,2,23,42,'<p><span>[b]</span><span> Maka azab serta-merta menimpa mereka, dan Kami jadikan mereka sampah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1996">1996</a><span>]</span><span> Maka terkutuklah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1997">1997</a><span>] </span><span>bagi kaum yang aniaya !</span></p><p><span>[b]</span><span> 7:92 ; 11:68.</span></p>');
INSERT INTO `quran` VALUES (9084,2,23,43,'<p><span>[c]</span><span> Kemudian Kami bangkitkan sesudah mereka keturunan lain.</span></p><p><span>[c]</span><span> 23:32.</span></p>');
INSERT INTO `quran` VALUES (9085,2,23,44,'<p><span>[a]</span><span> Tiada satu umat yang mendahului ajalnya, dan tidak dapat pula mereka memperlambatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1998">1998</a><span>]</span></p><p><span>[a]</span><span> 15:6.</span></p>');
INSERT INTO `quran` VALUES (9086,2,23,45,'<p><span>Kemudian Kami kirimkan rasul-rasul Kami berturut-turut. </span><span>[b]</span><span> Setiap kali datang kepada umat rasulnya, mereka mendustakannya. Maka Kami mengikutikan sebagian mereka mengikuti sebagian yang lain, dan Kami jadikan mereka hanya hikayat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=1999">1999</a><span>]</span><span> Maka terkutuklah bagi kaum yang tidak beriman !</span></p><p><span>[b]</span><span> 2:88 ; 36:31.</span></p>');
INSERT INTO `quran` VALUES (9087,2,23,46,'<p><span>Kemudian </span><span>[c]</span><span> Kami kirimkan Musa dan saudaranya, Harun, dengan Tanda-tanda Kami dan dalil yang nyata.</span></p><p><span>[c]</span><span> 20:30 ; 31:43 </span><span>–</span><span> 44.</span></p>');
INSERT INTO `quran` VALUES (9088,2,23,47,'<p><span>Kepada Firaun dan pemuka-pemukanya, tetapi mereka takabur dan mereka adalah suatu kaum yang sombong.</span></p>');
INSERT INTO `quran` VALUES (9089,2,23,48,'<p><span>Maka mereka berkata, “Apakah kami harus beriman kepada dua orang manusia yang seperti kami, padahal kaum keduanya adalah orang-orang yang menhambakan diri kepada kami ? “</span></p>');
INSERT INTO `quran` VALUES (9090,2,23,49,'<p><span>Maka mereka itu mendustakan keduanya, karena itu mereka menjadi diantara orang-orang yang dibinasakan.</span></p>');
INSERT INTO `quran` VALUES (9091,2,23,50,'<p><span>[a]</span><span> Dan sungguh telah Kami berikan kepada Musa Kitab, supaya mereka mendapat petunjuk.</span></p><p><span>[a]</span><span> 2:88 ; 17:3 ; 32:24 ; 40:54</span></p>');
INSERT INTO `quran` VALUES (9092,2,23,51,'<p><span>Dan Kami jadikan Ibnu Maryam dan Ibunya suatu Tanda, dan Kami melindungi keduanya di suatu tempat yang tinggi, layak dihuni dan sumber mata air yang mengalir. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2000">2000</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9093,2,23,52,'<p><span>R. 4</span></p><p><span>“Hai rasul-rasul, </span><span>[a]</span><span> makanlah dari barang-barang yang baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2001">2001</a><span>]</span><span> dan berbuatlah amal shaleh. Sesungguhnya Aku Maha Mengetahui segala yang kamu perbuat.</span></p><p><span>[a]</span><span> 7:33.</span></p>');
INSERT INTO `quran` VALUES (9094,2,23,53,'<p><span>“Dan </span><span>[a]</span><span> sesungguhnya ini umat kamu umat yang satu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2002">2002</a><span>]</span><span> dan Aku-lah Tuhan-</span><span>mu</span><span>. Maka bertakwalah kepada-</span><span>Ku.</span><span> </span></p><p><span>[a]</span><span> 21:93.</span></p>');
INSERT INTO `quran` VALUES (9095,2,23,54,'<p><span>Tetapi mereka, </span><span>orang ingkar,</span><span> telah memotong berkeping-keping perkara </span><span>syariat</span><span> mereka di antara mereka. Masing-masing kelompok bergembira dengan apa yang ada pada mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2003">2003</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9096,2,23,55,'<p><span>Maka </span><span>[b]</span><span> tinggalkanlah mereka dalam kesesatan mereka hingga suatu waktu.</span></p><p><span>[b]</span><span> 70:43 ; 73:12.</span></p>');
INSERT INTO `quran` VALUES (9097,2,23,56,'<p><span>Apakah mereka menyangka, bahwa Kami membantu mereka dengan harta dan anak-anak ?</span></p>');
INSERT INTO `quran` VALUES (9098,2,23,57,'<p><span>Kami akan mempercepat bagi mereka dalam kebaikan-kebaikan ? Bahkan mereka tidak menyadari. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2004">2004</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9099,2,23,58,'<p><span>Sesungguhnya, orang-orang </span><span>[a]</span><span> yang karena takut kepada Tuhan mereka, mereka gemetar.</span></p><p><span>[a] </span><span>79:41.</span></p>');
INSERT INTO `quran` VALUES (9100,2,23,59,'<p><span>Dan orang-orang yang kepada Tanda-tanda dari Tuhan mereka, mereka beriman.</span></p>');
INSERT INTO `quran` VALUES (9101,2,23,60,'<p><span>Dan orang-orang yang kepada Tuhan mereka, mereka tidak mempersekutukan.</span></p>');
INSERT INTO `quran` VALUES (9102,2,23,61,'<p><span>Dan orang-orang yang memberikan apa yang mereka berikan, sedang </span><span>[b]</span><span> hati mereka penuh ketakutan bahwa mereka akan kembali kepada Tuhan mereka.</span></p><p><span>[b]</span><span> 22:36.</span></p>');
INSERT INTO `quran` VALUES (9103,2,23,62,'<p><span>Mereka itulah yang bersegera dalam kebaikan-kebaikan, dan mereka untuk itu saling berlomba.</span></p>');
INSERT INTO `quran` VALUES (9104,2,23,63,'<p><span>Dan tidaklah </span><span>[c]</span><span> Kami bebani suatu jiwa lebih dari kemampuannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2005">2005</a><span>] [d]</span><span> dan pada Kami ada suatu Kitab yang berkata dengan benar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2006">2006</a><span>]</span><span> dan mereka tidak akan dianiaya.</span></p><p><span>[c]</span><span> 2:287 ; 7:43.</span></p><p><span>[d]</span><span> 17:14 </span><span>–</span><span> 15 ; 45:30 ; 69:20.</span></p>');
INSERT INTO `quran` VALUES (9105,2,23,64,'<p><span>Bahkan, </span><span>[a]</span><span> hati mereka dalam kesesatan tentang ini, dan bagi mereka ada amal-amal </span><span>buruk</span><span> selain itu yang mereka mengerjakannya,</span></p><p><span>[a]</span><span> 21:4.</span></p>');
INSERT INTO `quran` VALUES (9106,2,23,65,'<p><span>Hingga, </span><span>[b]</span><span> apabila Kami timpakan kepada orang-orang di antara mereka yang hidup mewah itu azab, tiba-tiba mereka menjerit minta pertolongan.</span></p><p><span>[b]</span><span> 10:23 ; 16:54 ; 30:34 ; 39:9.</span></p>');
INSERT INTO `quran` VALUES (9107,2,23,66,'<p><span>[c]</span><span> “Janganlah kamu menjerit minta pertolongan pada hari ini. Sesungguhnya dari kami, kamu tidak akan mendapat pertolongan.</span></p><p><span>[c]</span><span> 22:73 ; 39:46.</span></p>');
INSERT INTO `quran` VALUES (9108,2,23,67,'<p><span>“Sesungguhnya </span><span>[d]</span><span> Ayat-ayat-Ku telah dibacakan kepadamu, tetapi kamu senantiasa ber-balik atas tumitmu,</span></p><p><span>[d]</span><span> 22:73 ; 39:46.</span></p>');
INSERT INTO `quran` VALUES (9109,2,23,68,'<p><span>“Kamu dengan sombong </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2007">2007</a><span>]</span><span> menghabiskan waktu malam </span><span>[e]</span><span> berbicara sia-sia mengenai itu.”</span></p><p><span>[e]</span><span> 83:14.</span></p>');
INSERT INTO `quran` VALUES (9110,2,23,69,'<p><span>Maka apakah mereka tidak merenungkan firman ini, ataukah telah datang kepada mereka apa yang tidak pernah datang kepada bapak-bapak mereka dahulu ?</span></p>');
INSERT INTO `quran` VALUES (9111,2,23,70,'<p><span>Atau, tidakkah mereka mengenal rasul mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2008">2008</a><span>]</span><span> sehingga mereka mengingkarinya ?</span></p>');
INSERT INTO `quran` VALUES (9112,2,23,71,'<p><span>Atau, apakah </span><span>[a]</span><span> mereka berkata padanya gila ? </span><span>Tidak,</span><span> bahkan ia datang kepada mereka dengan kebenaran, dan kebanyakan mereka membenci kebenaran.</span></p><p><span>[a]</span><span> 7:185 ; 34:47.</span></p>');
INSERT INTO `quran` VALUES (9113,2,23,72,'<p><span>Dan sekiranya kebenaran mengikuti hawa nafsu mereka, niscaya akan rusaklah seluruh langit dan bumi dan siapa pun yang ada di dalamnya. Bahkan telah Kami berikan kepada mereka </span><span>[b]</span><span> kehormatan mereka, tetapi dari kehormatan mereka itu, mereka berpaling.</span></p><p><span>[b]</span><span> 21:3.</span></p>');
INSERT INTO `quran` VALUES (9114,2,23,73,'<p><span>[c]</span><span> Atau, apakah engkau meminta dari mereka upah ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2009">2009</a><span>]</span><span> Maka ganjaran Tuhan engkau lebih baik ; dan Dia-lah sebaik-baik Pemberi rezeki.</span></p><p><span>[c]</span><span> 52:41 ; 68:47.</span></p>');
INSERT INTO `quran` VALUES (9115,2,23,74,'<p><span>Dan sesungguhnya engkau benar-benar mengajak mereka kepada jalan yang lurus.</span></p>');
INSERT INTO `quran` VALUES (9116,2,23,75,'<p><span>Dan sesungguhnya orang-orang yang tidak beriman kepada akhirat, mereka sesungguhnya menympang dari jalan itu.</span></p>');
INSERT INTO `quran` VALUES (9117,2,23,76,'<p><span>[a]</span><span> Dan jika Kami kasihani mereka dan Kami jauhkan kemudaratan mereka, niscaya mereka akan lebih bertambah dalam kedurhakaan, mereka berkelana membuta.</span></p><p><span>[a]</span><span> 7:136 ; 43:51.</span></p>');
INSERT INTO `quran` VALUES (9118,2,23,77,'<p><span>[b]</span><span> Dan sesungguhnya telah Kami sergap mereka dengan azab, tetapi mereka tidak merendahkan diri di hadapan Tuhan mereka, dan tidak pula mereka memohon dengan merendahkan diri.</span></p><p><span>[b]</span><span> 6:44.</span></p>');
INSERT INTO `quran` VALUES (9119,2,23,78,'<p><span>[c]</span><span> Hingga, ketika Kami bukakan untuk mereka pintu yang mempunyai azab keras, tiba-tiba mereka di dalamnya putus-asa. [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2010">2010</a><span>]</span></p><p><span>[c]</span><span> 6:45.</span></p>');
INSERT INTO `quran` VALUES (9120,2,23,79,'<p><span>R. 5</span></p><p><span>Dan Dia-lah yang telah </span><span>[a]</span><span> menciptakan bagimu pendengaran, dan penglihatan, dan hati. Kamu sama sekali tidak bersyukur. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2011">2011</a><span>]</span></p><p><span>[a]</span><span> 16:79 ; 67:24.</span></p>');
INSERT INTO `quran` VALUES (9121,2,23,80,'<p><span>Dan Dia-lah yang mengembang-biakkan kamu di bumi, dan kepada Dia-lah kamu akan dihimpun.</span></p>');
INSERT INTO `quran` VALUES (9122,2,23,81,'<p><span>Dan Dia-lah yang menghidupkan dan mematikan, dan bagi-Nya </span><span>[b]</span><span> mengatur pergantian malam dan siang. Apakah kamu tidak menggunakan akal ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2012">2012</a><span>]</span></p><p><span>[b]</span><span> 2:165 ; 3:191 ; 10:7.</span></p>');
INSERT INTO `quran` VALUES (9123,2,23,82,'<p><span>Bahkan mereka mengatakan seperti yang telah dikatakan orang-orang yang dahulu.</span></p>');
INSERT INTO `quran` VALUES (9124,2,23,83,'<p><span>Mereka berkata, </span><span>[c]</span><span> “Apakah apabila kami telah mati dan kami telah menjadi debu dan </span><span>tulang-tulang</span><span> ; kami sungguh akan dibangkitkan ?</span></p><p><span>[c]</span><span> 17:99 ; 27:68 ; 37:17 ; 56:48.</span></p>');
INSERT INTO `quran` VALUES (9125,2,23,84,'<p><span>[d]</span><span> Inilah yang telah dijanjikan kepada kami dan bapak-bapak kami dari dahulu. Ini tidak lain hanya hikayat-hikayat orang-orang dahulu.”</span></p><p><span>[d]</span><span> 27:69.</span></p>');
INSERT INTO `quran` VALUES (9126,2,23,85,'<p><span>Katakanlah, “Kepunyaan siapakah bumi ini dan segala yang ada di dalamnya, jika kamu mengetahui ? “</span></p>');
INSERT INTO `quran` VALUES (9127,2,23,86,'<p><span>Mereka akan berkata, “Kepunyaan Allah.” Katakanlah, “Apakah kamu tidak mengambil pelajaran ? “</span></p>');
INSERT INTO `quran` VALUES (9128,2,23,87,'<p><span>Katakanlah, “Siapakah Tuhan tujuh langit, dan Tuhan </span><span>‘</span><span>Arasy yang agung ? “</span></p>');
INSERT INTO `quran` VALUES (9129,2,23,88,'<p><span>Mereka akan berkata, “Kepunyaan Allah.” Katakanlah, “Apakah kamu tidak bertakwa ?”</span></p>');
INSERT INTO `quran` VALUES (9130,2,23,89,'<p><span>Katakanlah, </span><span>[a]</span><span> “Siapakah yang ada di tangan-Nya terletak kekuasaan segala sesuatu, dan Dia melindungi </span><span>semuanya, </span><span>dan tidak ada dilindungi yang melawan-Nya, jika kamu mengetahui ?”</span></p><p><span>[a]</span><span> 36:84</span></p>');
INSERT INTO `quran` VALUES (9131,2,23,90,'<p><span>Mereka akan berkata, “Kepunyaan Allah.” Katakanlah, “Maka bagaimana kamu bisa tertipu ? “</span></p>');
INSERT INTO `quran` VALUES (9132,2,23,91,'<p><span>Bahkan sebenarnya Kami telah mendatangkan kepada mereka kebenaran, dan sesungguh nya mereka itu pendusta.</span></p>');
INSERT INTO `quran` VALUES (9133,2,23,92,'<p><span>[a]</span><span> Allah tidak mengambil seorang anak laki-laki, dan tiada tuhan beserta Dia, sekiranya begitu </span><span>[b]</span><span> setiap tuhan akan membawa yang telah ia ciptakan, dan sebagian dari mereka itu akan menguasai sebagian yang lain. Maha Suci Allah, dari apa yang mereka katakan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2013">2013</a><span>]</span><span> </span></p><p><span>[a]</span><span> 18:5 ; 19:36 ; 21:27 ; 25:3 ; 39:5 ; 43:82 ; 72:4.</span></p><p><span>[b]</span><span> 21:23.</span></p>');
INSERT INTO `quran` VALUES (9134,2,23,93,'<p><span>[c]</span><span> Yang Maha Mengetahui yang gaib dan yang nampak ! Maka Maha Luhur Dia di atas apa yang mereka sekutukan.</span></p><p><span>[c]</span><span> 6:74 ; 32:7 ; 34:4 ; 59:23 ; 64:19.</span></p>');
INSERT INTO `quran` VALUES (9135,2,23,94,'<p><span>R. 6</span></p><p><span>Katakanlah, “Ya Tuhan-ku jika Engkau perlihatkan kepadaku apa yang dijanjikan kepada mereka ;</span></p>');
INSERT INTO `quran` VALUES (9136,2,23,95,'<p><span>“Ya Tuhan-ku, maka jangan Engkau jadikan aku di antara kaum yang aniaya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2014">2014</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9137,2,23,96,'<p><span>Dan sesungguhnya </span><span>[d]</span><span> Kami berkuasa untuk memperlihatkan kepada engkau apa yang Kami janjikan kepada mereka.</span></p><p><span>[d]</span><span> 40:78.</span></p>');
INSERT INTO `quran` VALUES (9138,2,23,97,'<p><span>[a]</span><span> Tolaklah keburukan dengan yang lebih baik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2015">2015</a><span>]</span><span> Kami lebih mengetahui apa yang mereka katakan.</span></p><p><span>[a]</span><span> 13:23 ; 16:126 ; 41:35.</span></p>');
INSERT INTO `quran` VALUES (9139,2,23,98,'<p><span>Dan Katakanlah, “Ya Tuhan-ku, aku berlindung kepada Engkau dari hasutan-hasutan syaitan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2016">2016</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9140,2,23,99,'<p><span>“Dan aku berlindung kepada Engkau, ya Tuhan-ku supaya jangan mereka menghampiriku.”</span></p>');
INSERT INTO `quran` VALUES (9141,2,23,100,'<p><span>Hingga, apabila maut datang kepada salah seorang dari mereka, </span><span>[b]</span><span> ia berkata “Ya Tuhan-ku, kembalikanlah aku,</span></p><p><span>[b] </span><span>39:59.</span></p>');
INSERT INTO `quran` VALUES (9142,2,23,101,'<p><span>“Supaya aku dapat mengerjakan amal shaleh yang telah kutinggalkan.” Sekali-kali tidak ! Sesungguhnya ini hanyalah perkataan yang ia ucapkan. </span><span>[c]</span><span> Dan dibelakang mereka ada dinding penghalang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2017">2017</a><span>]</span><span> hingga hari mereka dibangkitkan.</span></p><p><span>[c] </span><span>21:96 ; 36:32.</span></p>');
INSERT INTO `quran` VALUES (9143,2,23,102,'<p><span>Dan apabila </span><span>[a]</span><span> nafiri ditiup, maka tidak akan ada lagi pertalian kekeluargaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2018">2018</a><span>]</span><span> di antara mereka pada hari itu, dan tidak </span><span>pula</span><span> mereka akan bertanya satu sama lain.</span></p><p><span>[a]</span><span> 18:100 ; 36:52 ; 50:21 ; 69:14.</span></p>');
INSERT INTO `quran` VALUES (9144,2,23,103,'<p><span>Maka </span><span>[b]</span><span> barangsiapa yang berat timbangannya, maka itulah orang-orang yang memperoleh kemenangan.</span></p><p><span>[b]</span><span> 7:9 ; 101:7 -8.</span></p>');
INSERT INTO `quran` VALUES (9145,2,23,104,'<p><span>Dan barangsiapa yang ringan </span><span>[c]</span><span> timbangannya, maka mereka itulah orang-orang yang merugikan diri mereka sendiri ; dalam Jahannam mereka akan tinggal lama.</span></p><p><span>[c]</span><span> 7:10 ; 101:9 </span><span>–</span><span> 10.</span></p>');
INSERT INTO `quran` VALUES (9146,2,23,105,'<p><span>[d]</span><span> Wajah mereka dibakar api dan mereka di dalamnya meringis.</span></p><p><span>[d]</span><span> 10:28 ; 14:51 ; 54:49 ; 80:42.</span></p>');
INSERT INTO `quran` VALUES (9147,2,23,106,'<p><span>[e]</span><span> “Bukanlah Ayat-ayat-Ku telah dibacakan kepadamu, tetapi kamu mendustakannya ?”</span></p><p><span>[e]</span><span> 40:51 ; 45:32 ; 67:9.</span></p>');
INSERT INTO `quran` VALUES (9148,2,23,107,'<p><span>Mereka akan berkata, “Ya Tuhan kami, telah menguasai atas kami nasib buruk kami, dan kami menjadi kaum yang sesat.</span></p>');
INSERT INTO `quran` VALUES (9149,2,23,108,'<p><span>“Ya Tuhan kami, </span><span>[a]</span><span> keluarkanlah kami darinya, maka jika kami kembali maka sungguh kami orang aniaya.”</span></p><p><span>[a]</span><span> 6:28.</span></p>');
INSERT INTO `quran` VALUES (9150,2,23,109,'<p><span>Dia berfirman, “Tinggallah dengan hina </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2019">2019</a><span>]</span><span> di dalamnya, dan jangan kamu berbicara dengan Aku.</span></p>');
INSERT INTO `quran` VALUES (9151,2,23,110,'<p><span>“Sesungguhnya ada segolongan di antara hamba-hamba Kami yang berkata, </span><span>[b]</span><span> </span><span>‘</span><span>Ya Tuhan kami, kami telah beriman, maka ampunilah kami dan kasihanilah kami, dan Engkau-lah yang sebaik-baiknya Pemberi rahmat.</span></p><p><span>[b]</span><span> 3:17, 194.</span></p>');
INSERT INTO `quran` VALUES (9152,2,23,111,'<p><span>“Maka kamu jadikan mereka cemoohan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2020">2020</a><span>]</span><span> sehingga mereka membuat kamu lupa mengingat-Ku dan kamu menertawakan mereka ;</span></p>');
INSERT INTO `quran` VALUES (9153,2,23,112,'<p><span>“Sesungguhnya Aku telah memberikan balasan mereka pada hari ini atas kesabaran mereka; sesungguhnya mereka itulah yang memperoleh kemenangan.”</span></p>');
INSERT INTO `quran` VALUES (9154,2,23,113,'<p><span>Dia berfirman, “Berapa tahun lamanya kamu tinggal di bumi ?”</span></p>');
INSERT INTO `quran` VALUES (9155,2,23,114,'<p><span>Mereka berkata, “Kami tingal sehari atau sebagian hari, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2021">2021</a><span>]</span><span> maka tanyakan kepada yang menghitung.”</span></p>');
INSERT INTO `quran` VALUES (9156,2,23,115,'<p><span>Dia berfirman, “Tidaklah kamu tinggal kecuali sebentar, jika kamu sesungguhnya kamu mengetahui.”</span></p>');
INSERT INTO `quran` VALUES (9157,2,23,116,'<p><span>Apakah kamu menyangka bahwa sesungguhnya Kami menciptakan kamu tampa tujuan, dan bahwa kamu tidak akan dikembalikan kepada Kami ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2022">2022</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9158,2,23,117,'<p><span>[a]</span><span> Maka Maha Luhur Allah, Raja yang sebenar-benarnya. Tiada tuhan selain Dia. Tuhan </span><span>‘</span><span>Arasy yang sangat mulia.</span></p><p><span>[a] </span><span>20:115 ; 22:63 ; 24:26.</span></p>');
INSERT INTO `quran` VALUES (9159,2,23,118,'<p><span>Dan barangsiapa yang menyeru bersama Allah tuhan lain, yang tidak mempunyai suatu pun dalil mengenainya, maka sesungguhnya perhitungannya ada di sisi Tuhan-nya. Sesungguhnya tidak akan berhasil orang-orang kafir.</span></p>');
INSERT INTO `quran` VALUES (9160,2,23,119,'<p><span>Dan katakanlah, “Ya Tuhan-ku, ampunilah dan kasihanilah, dan Engkau adalah Pemberi rahmat yang sebaik-baiknya.”</span></p>');
INSERT INTO `quran` VALUES (9161,2,24,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9162,2,24,2,'<p><span>Inilah</span><span> satu Surah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2023">2023</a><span>]</span><span> yang telah Kami turunkan dan telah Kami wajibkan ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2024">2024</a><span>] </span><span>dan telah Kami turunkan di dalamnya Tanda-tanda yang terang, supaya kamu mendapat nasihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2025">2025</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9163,2,24,3,'<p><span>Perempuan yang berzina dan laki-laki yang berzina, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2025a">2025a</a><span>]</span><span> deralah masing-masing di antara keduanya </span><span>dengan </span><span> seratus kali deraan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2026">2026</a><span>]</span><span> Dan janganlah belas-kasihan akan menghalangi kamu kepada keduanya dalam agama Allah, jika kamu beriman kepada Allah dan Hari Akhirat. Dan hendaklah hukuman mereka berdua, disaksikan oleh sekumpulan orang-orang yang beriman.</span></p>');
INSERT INTO `quran` VALUES (9164,2,24,4,'<p><span>Laki-laki berzina tidak bercampur melainkan dengan perempuan berzina atau perempuan musyrik, dan perempuan berzina tidak ada yang mencampurinya kecuali laki-laki berzina atau laki-laki musyrik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2026a">2026a</a><span>]</span><span> Dan hal demikian itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2027">2027</a><span>]</span><span> telah diharamkan atas orang-orang yang beriman.</span></p>');
INSERT INTO `quran` VALUES (9165,2,24,5,'<p><span>Dan </span><span>[a]</span><span> orang-orang yang menuduh wanita-wanita yang memelihara kehormatannya </span><span>berzina,</span><span> tetapi tidak mendatangkan empat orang saksi, maka deralah mereka </span><span>dengan </span><span> delaoan puluh kali deraan, dan janganlah menerima lagi persaksian mereka untuk selama-lamanya, sebab merekalah orang-orang yang durhaka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2028">2028</a><span>]</span></p><p><span>[a]</span><span> 24:24.</span></p>');
INSERT INTO `quran` VALUES (9166,2,24,6,'<p><span>Kecuali </span><span>[a]</span><span> orang-orang yang bertobat sesudah itu dan memperbaiki </span><span>diri,</span><span> maka sesungguhnya Allah Maha Pengampun, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2029">2029</a><span>]</span><span> Maha Penyayang.</span></p><p><span>[a]</span><span> 4:18.</span></p>');
INSERT INTO `quran` VALUES (9167,2,24,7,'<p><span>Dan orang-orang yang menuduh berzina </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2030">2030</a><span>]</span><span> istri-istri mereka, dan tidak ada saksi bagi mereka kecuali diri mereka sendiri, maka persaksian seorang dari mereka empat kali bersumpah dengan nama Allah bahwa ia sesungguhnya termasuk orang-orang yang benar.</span></p>');
INSERT INTO `quran` VALUES (9168,2,24,8,'<p><span>Dan yang kelima </span><span>kalinya, </span><span>bahwa laknat Allah atasnya, jika ia termasuk orang-orang berdusta.</span></p>');
INSERT INTO `quran` VALUES (9169,2,24,9,'<p><span>Dan hukuman dapat dijauhkan dari dia jika ia memberikan persaksian empat kali dengan nama Allah, bahwa </span><span>suaminya </span><span> itu termasuk orang-orang yang berdusta.</span></p>');
INSERT INTO `quran` VALUES (9170,2,24,10,'<p><span>Dan yang kelima </span><span>kalinya,</span><span> bahwa kemurkaan Allah menimpa dirinya, jika </span><span>suaminya </span><span> itu termasuk orang-orang yang berkata benar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2031">2031</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9171,2,24,11,'<p><span>Dan sekiranya tidak ada karunia Allah atas kamu dan rahmat-Nya, dan bahwa sesungguhnya Allah Maha Penerima Tobat, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (9172,2,24,12,'<p><span>Sesungguhnya orang-orang yang melontarkan tuduhan itu adalah golongan dari kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2032">2032</a><span>]</span><span> Janganlah kamu menyangkanya buruk bagimu ; bahkan itu baik bagimu. Tiap-tiap orang di antara mereka akan mendapat </span><span>bagiannya</span><span> dari apa yang ia telah peroleh dari dosa itu ; dan orang yang mengambil peranan besar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2033">2033</a><span>]</span><span> di antara mereka, bagi dia ada azab yang sangat besar.</span></p>');
INSERT INTO `quran` VALUES (9173,2,24,13,'<p><span>Mengapa ketika kamu mendengarnya, orang-orang mukmin laki-laki dan orang-orang mukmin perempuan menyangka baik tentang diri mereka, dan mereka berkata bahwa ini adalah kedustaan yang nyata ?</span></p>');
INSERT INTO `quran` VALUES (9174,2,24,14,'<p><span>Mengapakah mereka tidak membawa atasnya empat orang saksi ? Karena mereka tidak mendatangkan saksi-saksi itu, maka di sisi Allah mereka itu pendusta ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2034">2034</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9175,2,24,15,'<p><span>[a]</span><span> Dan sekiranya tidak ada karunia Allah atas kamu dan rahmat-Nya, di dunia dan di akhirat, niscaya akan menimpa kamu mengenai apa yang kamu lakukan di dalamnya, azab yang besar.</span></p><p><span>[a]</span><span> 2:65 ; 4:84.</span></p>');
INSERT INTO `quran` VALUES (9176,2,24,16,'<p><span>Ketika kamu menerima </span><span>kebohongan </span><span> itu dengan lidah kamu satu sama lain, dan kamu mengatakan dengan mulut kamu hal yang kamu tidak mempunyai ilmu tentang itu, dan kamu menyangkanya kecil, padahal hal itu di sisi Allah sangat besar.</span></p>');
INSERT INTO `quran` VALUES (9177,2,24,17,'<p><span>Dan mengapakah tidak kamu </span><span>katakan</span><span> ketika kamu mendengarnya, “Tidak layak bagi kami berbicara tentang ini. Maha Suci Engkau, ini adalah tuduhan yang sangat besar.”</span></p>');
INSERT INTO `quran` VALUES (9178,2,24,18,'<p><span>Allah menasihatkan kamu, supaya kamu jangan mengulangi lagi hal yang seperti itu selama-lamanya, jika kamu orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (9179,2,24,19,'<p><span>Dan Allah menjelaskan Ayat-ayat-Nya kepadamu ; dan Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (9180,2,24,20,'<p><span>Sesungguhnya orang-orang yang suka, supaya kekejian tersiar di kalangan orang-orang beriman, bagi mereka itu azab yang pedih di dunia dan di akhirat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2035">2035</a><span>]</span><span> Dan Allah mengetahui, dan kamu tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9181,2,24,21,'<p><span>Dan sekiranya tidak ada karunia Allah atasmu dan rahmat-Nya, dan sesungguhnya Allah adalah Maha Penyantun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9182,2,24,22,'<p><span>R. 3</span></p><p><span>Hai orang-orang yang beriman ! </span><span>[a]</span><span> Janganlah mengikuti jejak-jejak syaitan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2036">2036</a><span>]</span><span> Dan barangsiapa yang mengikuti jejak-jejak syaitan, bahwa sesungguhnya ia menyuruh </span><span>berbuat</span><span> kekejian dan keburukan. Dan, sekiranya tidak ada karunia Allah atasmu, dan rahmat-Nya, niscaya tidak ada seorang pun yang suci di antara kamu selama-lamanya. Tetapi Allah mensucikan siapa yang Dia kehendaki, Dan Allah Maha Mendengar, Maha Mengetahui.</span></p><p><span>[a] </span><span>6:143 ; 19:45 ; 36:61.</span></p>');
INSERT INTO `quran` VALUES (9183,2,24,23,'<p><span>Dan janganlah bersumpah orang-orang yang mempunyai kelebihan </span><span>dan</span><span> kelimpahan di antara mu untuk </span><span>tidak</span><span> memberikan kepada kaum kerabat dan kepada orang-orang miskin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2037">2037</a><span>]</span><span> dan kepada orang-orang yang berhijrah di jalan Allah. Hendaklah mereka memaafkan dan berlapang dada. Tidakkah kamu suka agar Allah mengampuni kamu ? Dan Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9184,2,24,24,'<p><span>Sesungguhnya orang-orang yang menuduh perempuan suci, yang lengah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2038">2038</a><span>]</span><span> dan yang beriman, mereka akan dilaknat di dunia dan di akhirat. Dan bagi mereka azab yang sangat besar.</span></p>');
INSERT INTO `quran` VALUES (9185,2,24,25,'<p><span>[a]</span><span> Pada hari apabila akan menjadi saksi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2039">2039</a><span>]</span><span> atas mereka lidah mereka dan tangan mereka dan kaki mereka tentang apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 17:37 ; 36:66 ; 41:21 </span><span>–</span><span> 23.</span></p>');
INSERT INTO `quran` VALUES (9186,2,24,26,'<p><span>Pada hari itulah Allah akan menyempurnakan kepada mereka ganjaran mereka yang sebenarnya, dan mereka akan mengetahui, bahwa sesungguhnya </span><span>[a]</span><span> Allah., Dia-lah Kebenaran yang nyata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2040">2040</a><span>]</span></p><p><span>[a]</span><span> 20:115 ; 22:63 ; 23:117.</span></p>');
INSERT INTO `quran` VALUES (9187,2,24,27,'<p><span>Hal-hal buruk untuk laki-laki buruk, dan laki-laki buruk untuk hal-hal yang buruk. Dan hal-hal baik untuk laki-laki baik, dan laki-laki baik untuk hal-hal yang baik ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2041">2041</a><span>]</span><span> mereka itu bersih dari segala yang dituduhkan. </span><span>[b]</span><span> bagi mereka adalah ampunan dan rezeki yang mulia.</span></p><p><span>[b]</span><span> 8:75 ; 22:51.</span></p>');
INSERT INTO `quran` VALUES (9188,2,24,28,'<p><span>R. 4</span></p><p><span>Hai orang-orang yang beriman ! </span><span>[c]</span><span> Janganlah kamu masuk ke dalam rumah-rumah, yang bukan rumahmu sendiri, sebelum kamu minta izin dan memberi salam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2042">2042</a><span>]</span><span> kepada penghuninya. Hal itu lebih baik bagimu, supaya kamu </span><span>selalu ingat.</span><span> </span></p><p><span>[c]</span><span> 24:62.</span></p>');
INSERT INTO `quran` VALUES (9189,2,24,29,'<p><span>Maka jika kamu tidak menemukan seorang pun di dalamnya, maka janganlah kamu memasukinya, sebelum kamu diberi izin. Dan jika dikatakan kepadamu, “Kembali,” maka kembalilah ; yang demikian itu lebih suci bagimu. Dan Allah mengetahui apa-apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (9190,2,24,30,'<p><span>Tiada dosamu bagimu memasuki rumah-rumah yang tidak didiami, yang di dalamnya ada barang-barang kepunyaanmu. Dan </span><span>[a]</span><span> Allah mengetahui apa yang kamu zahirkan dan apa yang kamu sembunyikan.</span></p><p><span>[a]</span><span> 2:34 ; 21:111 ; 87:8.</span></p>');
INSERT INTO `quran` VALUES (9191,2,24,31,'<p><span>Katakanlah kepada orang-orang mukmin laki-laki, mereka </span><span>hendaklah</span><span> menundukkan mata mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2043">2043</a><span>]</span><span> dan memelihara aurat mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2043a">2043a</a><span>]</span><span> Yang demikian itu lebih suci bagi mereka. Sesungguhnya Allah mengetahui apa yang mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (9192,2,24,32,'<p><span>Dan katakanlah kepada orang-orang mukmin wanita, bahwa mereka </span><span>hendaknya</span><span> menundukkan mata mereka dan memelihara aurat mereka, dan janganlah mereka menampakkan kecantikan mereka, kecuali apa yang dengan sendirinya nampak darinya, dan mereka mengenakan kudungan mereka hingga menutupi dada mereka, dan janganlah mereka menampakkan kecantikan mereka kecuali kepada suaminya, atau kepada bapaknya, atau bapak suaminya, atau anak lelakinya atau anak lelaki suaminya atau saudara lelaki mereka, atau anak lelaki saudara lelaki mereka, atau anak lelaki saudara perempuan mereka, atau perempuan-perempuan </span><span>teman mereka</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2043b">2043b</a><span>]</span><span> atau apa yang dimiliki oleh tangan kanan mereka, atau pelayan-pelayan lelaki yang tidak mempunyai keinginan terhadap wanita, atau anak-anak yang belum mengetahui tentang bagian-bagian aurat wanita. Dan janganlah mereka itu menghentakkan kaki mereka, sehingga dapat diketahui apa yang mereka sembunyikan dari kecantikan mereka. Dan kembalilah kamu sekalian kepada Allah, hai orang-orang yang beriman, supaya kamu mendapat kebahagian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2044">2044</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9193,2,24,33,'<p><span>Dan nikahkahlah janda-janda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2045">2045</a><span>]</span><span> dari antara kamu, dan hamba-sahaya lelaki kamu dan hamba-sahaya perempuan kamu yang patut. Jika mereka itu miskin, Allah akan memberikan kecukupan kepada mereka dari karunia-Nya ; dan Allah Maha Luas Pemberian-nya, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9194,2,24,34,'<p><span>Dan hendaknya memelihara kesucican mereka orang-orang yang belum mampu nikah, sehingga Allah menganugerahkan kekayaan dari karunia-Nya kepada mereka. Dan orang-orang yang menghendaki </span><span>surat pembebasan budak,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2046">2046</a><span>]</span><span> dari apa yang dimiliki oleh tangan kananmu, maka tuliskanlah bagi mereka, jika kamu mengetahui sesuatu kebaikan dalam diri mereka ; dan berikanlah kepada mereka dari harta Allah, yang telah Dia berikan kepadamu. Dan janganlah kamu memaksa pelayan-pelayan perempuanmu untuk melakukan perzinahan karena kamu ingin mencari keuntungan dan kehidupan dunia, sedangkan mereka berkeinginan untuk hidup suci. Dan barangsiapa memaksa mereka, maka sesungguhnya Allah setelah pemaksaan </span><span>terhadap</span><span> mereka itu, Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9195,2,24,35,'<p><span>[a]</span><span> Dan sesungguhnya telah Kami turunkan kepadamu Tanda-tanda yang nyata, dan contoh mengenai orang-orang yang telah berlalu sebelummu, dan juga nasihat bagi orang-orang yang bertakwa.</span></p><p><span>[a]</span><span> 22:17 ; 57:10 ; 58:6.</span></p>');
INSERT INTO `quran` VALUES (9196,2,24,36,'<p><span>R. 5</span></p><p><span>Allah adalah Nur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2046a">2046a</a><span>]</span><span> seluruh langit dan bumi. Perumpamaan nur-Nya adalah seperti sebuah relung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2046b">2046b</a><span>]</span><span> yang didalamnya ada suatu pelita. Pelita itu ada dalam suatu semprong kaca. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2046c">2046c</a><span>] </span><span>Semprong kaca itu seperti bintang yang gemerlapan. </span><span>Pelita itu </span><span> di nyalakan </span><span>dengan minyak</span><span> dari sebatang pohon kayu yang diberkati, ialah pohon zaitun yang bukan di timur dan bukan di barat, yang minyaknya hampir-hampir bercahaya walaupun apitidak menyentuhnya. Nur, di atas nur ! Allah memberi bimbingan menuju nur-Nya kepada siapa yang Dia kehendaki. Dan Allah mengemukakan tamsil-tamsil untuk manusia, dan Allah Maha Mengetahui segala sesuatu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2047">2047</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9197,2,24,37,'<p><span>Di dalam rumah yang Allah telah mengizinkan supaya ditinggikan dan diingat di dalamnya nama-Nya, dia bertasbih kepada-Nya di dalamnya, pada waktu pagi dan petang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2047a">2047a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9198,2,24,38,'<p><span>[a]</span><span> Orang-orang lelaki yang tidak melalaikan mereka perniagaan dan tidak pula jual-beli dari mengingat Allah dan mendirikan shalat dan membayar zakat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2048">2048</a><span>]</span><span> Mereka takut pada hari, ketika terbalik di dalamnya hati dan mata .</span></p><p><span>[a]</span><span> 63:10</span></p>');
INSERT INTO `quran` VALUES (9199,2,24,39,'<p><span>Supaya </span><span>[a]</span><span> Allah memberi mereka ganjaran yang sebaik-baiknya atas apa yang telah mereka kerjakan, dan Allah akan menambah kepada mereka dari karunia-Nya. Dan Allah memberi rezeki kepada siapa yang Dia kehendaki tanpa perhitungan.</span></p><p><span>[a]</span><span> 9:121 ; 16:98.</span></p>');
INSERT INTO `quran` VALUES (9200,2,24,40,'<p><span>Dan orang-orang yang ingkar, </span><span>[b]</span><span> amal-amal mereka bagaikan fatamorgana di padang pasir. Orang yang dahaga menyangka air, sehingga apabila ia datang kepadanya, ia tidak mendapatkan apa-apa. Dan ia dapati Allah dekat kepadanya, Yang membayar penuh perhitungannya ; dan Allah sangat cepat dalam perhitungan.</span></p><p><span>[b]</span><span> 14:19.</span></p>');
INSERT INTO `quran` VALUES (9201,2,24,41,'<p><span>Atau seperti gelap-gulita di laut yang salam yang meliputinya gelombang demi gelombang dari atasnya, dari atasnya ada awan-awan. Lapisan demi lapisan kegelapan sebagiannya di atas sebagian yang lain. Apabila ia mengulurkan tangannya, ia hampir-hampir tidak dapat melihatnya dan barangsiapa tidak Allah jadikan baginya nur, maka bagi dia tidak ada nur. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2049">2049</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9202,2,24,42,'<p><span>R. </span><span>6</span></p><p><span>Apakah engkau tidak melihat, bahwa sesungguhnya </span><span>[a]</span><span> itulah Allah, Yang bertasbih kepada-Nya segala yang ada di seluruh langit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2050">2050</a><span>]</span><span> dan bumi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2050a">2050a</a><span>]</span><span> dan burung-burung yang berbondong-bondong ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2050b">2050b</a><span>]</span><span> Semuanya mengetahui </span><span>cara</span><span> shalat dan tasbihnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2051">2051</a><span>]</span><span> Dan Allah Maha Mengetahui apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 17:45 ; 59:25 ; 61:2 ; 62:2.</span></p>');
INSERT INTO `quran` VALUES (9203,2,24,43,'<p><span>[a]</span><span> Dan kepunyaan Allah kerajaan seluruh langit dan bumi, dan kepada Allah kembalinya.</span></p><p><span>[a]</span><span> 3:190 ; 5:121.</span></p>');
INSERT INTO `quran` VALUES (9204,2,24,44,'<p><span>Apakah engkau tidak melihat betapa </span><span>[b]</span><span> Allah menggiring awan, kemudian mengumpulkannya, kemudian menumpukkannya bersusun-susun sehingga engkau melihat hujan ke luar dari tengah-tengahnya ? Dan Dia turunkan dari awan </span><span>seperti</span><span> gunung-gunung yang di dalamnya ada butiran-butiran es, </span><span>[c]</span><span> maka Dia menimpakan dengannya siapa yang Dia kehendaki, dan menjauhkannya dari siapa yang Dia kehendaki. Cahaya kilatnya hampir menyambar penglihatan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2052">2052</a><span>]</span></p><p><span>[b]</span><span> 30:49.</span></p><p><span>[c]</span><span> 13:14</span></p>');
INSERT INTO `quran` VALUES (9205,2,24,45,'<p><span>Allah mempertukarkan malam dan siang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2053">2053</a><span>]</span><span> Sesungguhnya di dalam itu terdapat pelajaran bagi orang-orang yang mempunyai penglihatan.</span></p>');
INSERT INTO `quran` VALUES (9206,2,24,46,'<p><span>[a]</span><span> Dan Allah telah menciptakan segala hewan dari air. Maka dari antaranya </span><span>sebagian </span><span> ada yang berjalan pada perutnya, dan </span><span>sebagian</span><span> dari antaranya ada yang berjalan dengan dua kaki, dan dari antaranya ada sebagian </span><span>lagi</span><span> yang berjalan pada empat kaki, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2054">2054</a><span>]</span><span> Allah menciptakan apa yang Dia kehendaki. Sesungguhnya, Allah Maha Kuasa atas segala sesuatu.</span></p><p><span>[a]</span><span> 25:55.</span></p>');
INSERT INTO `quran` VALUES (9207,2,24,47,'<p><span>Sesungguhnya Kami telah menurunkan Tanda-tanda yang nyata. Dan Allah memberi petunjuk kepada siapa yang Dia kehendaki ke jalan yang lurus.</span></p>');
INSERT INTO `quran` VALUES (9208,2,24,48,'<p><span>Dan mereka berkata, “Kami beriman kepada Allah dan kepada Rasul, dan kami patuh,” kemudian berpaling segolongan dari mereka sesudah itu. Dan bukanlah mereka itu orang- orang yang beriman.</span></p>');
INSERT INTO `quran` VALUES (9209,2,24,49,'<p><span>[a]</span><span> Dan apabila mereka dipanggil kepada Allah dan Rasul-Nya, supaya Dia memutuskan di antara mereka, tiba-tiba segolongan dari mereka berpaling.</span></p><p><span>[a]</span><span> 3:24.</span></p>');
INSERT INTO `quran` VALUES (9210,2,24,50,'<p><span>Dan jika bagi mereka ada kebenaran, mereka datang kepadanya dengan ketaatan.</span></p>');
INSERT INTO `quran` VALUES (9211,2,24,51,'<p><span>Apakah di dalam hati mereka ada penyakit ? ataukah mereka ragu-ragu, ataukah mereka takut, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2055">2055</a><span>]</span><span> bahwa Allah dan Rasul-Nya akan berlaku aniaya atas mereka ? </span><span>Tidak,</span><span> bahkan mereka lah orang-orang yang aniaya.</span></p>');
INSERT INTO `quran` VALUES (9212,2,24,52,'<p><span>R. 7</span></p><p><span>Sesungguhnya perkataan orang-orang mukmin apabila mereka dipanggil kepada Allah dan Rasul-Nya supaya dia menghikimi di antara mereka itu ialah, mereka berkata, “Kami dengar dan kami taat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2056">2056</a><span>]</span><span> Dan mereka itu orang-orang menang.</span></p>');
INSERT INTO `quran` VALUES (9213,2,24,53,'<p><span>[a]</span><span> Dan barangsiapa yang taat kepada Allah dan Rasul-Nya, dan takut kepada Allah, dan bertakwa kepada-Nya, maka mereka itulah orang-orang yang menang.</span></p><p><span>[a]</span><span> 4:14. </span></p>');
INSERT INTO `quran` VALUES (9214,2,24,54,'<p><span>Dan mereka bersumpah dengan nama Allah dengan sekuat-kuat sumpah mereka, bahwa jika engkau perintahkan kepada mereka, niscaya mereka akan keluar segera. Katakanlah, “Janganlah bersumpah ; </span><span>apa yang dituntut dari kamu</span><span> adalah </span><span>[b]</span><span> taat kepada yang benar. Sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.”</span></p><p><span>[b]</span><span> 5:93 ; 64:13.</span></p>');
INSERT INTO `quran` VALUES (9215,2,24,55,'<p><span>Katakanlah, </span><span>[c]</span><span> “Taatlah kepada Allah, dan taatlah kepada Rasul.” Maka jika kamu berpaling, maka ia bertanggung-jawab tentang apa yang dibebankan kepadanya, dan kamu bertanggung jawab tentang apa yang dibebankan kepadamu. Dan jika kamu taat kepadanya, kamu akan mendapat petunjuk. </span><span>[d]</span><span> Dan tidaklah kewajiban Rasul melainkan menyampaikan secara jelas.</span></p><p><span>[c]</span><span> 4:14 ; 33:72 ; 48:18.</span></p><p><span>[d]</span><span> 16:36 ; 29:19 ; 36:18.</span></p>');
INSERT INTO `quran` VALUES (9216,2,24,56,'<p><span>Allah telah berjanji kepada orang-orang yang beriman dari antara kamu dan berbuat amal shaleh, bahwa Dia pasti akan menjadikan mereka itu khalifah di bumi, sebagaimana Dia telah menjadikan khalifah orang-orang yang sebelum mereka ; dan Dia akan meneguhkan bagi mereka agama mereka, yang telah Dia ridhai bagi mereka ; dan niscaya Dia akan menggantikan mereka sesudah ketakutan mereka </span><span>dengan </span><span> keamanan. Mereka akan menyembah Aku, dan mereka tidak akan mempersekutukan sesuatu dengan Aku. Dan barangsiapa ingkar sesudah itu, mereka itulah orang-orang yang durhaka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2057">2057</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9217,2,24,57,'<p><span>[a]</span><span> Dan dirikanlah shalat, dan bayarlah zakat, dan taatlah kepada Rasul supaya kamu mendapat rahmat.</span></p><p><span>[a]</span><span> 22:78</span></p>');
INSERT INTO `quran` VALUES (9218,2,24,58,'<p><span>Janganlah engkau menyangka, bahwa orang-orang yang ingkar akan dapat melemahkan </span><span>Kami</span><span> di bumi ; dan tempat tinggal mereka adalah Api. Dan sesungguhnya sangat buruklah tempat kembali itu.</span></p>');
INSERT INTO `quran` VALUES (9219,2,24,59,'<p><span>R. 8</span></p><p><span>Hai orang-orang yang beriman, hendaklah minta izin kepada kamu orang-orang yang dimiliki tangan kananmu, dan mereka yang belum baligh di antara kamu, tiga waktu. Sebelum shalat Subuh, dan apabila kamu membuka pakaianmu waktu tengah hari, dan sesudah shalat Isya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2058">2058</a><span>]</span><span> </span><span>Inilah</span><span> tiga </span><span>waktu</span><span> aurat bagimu dan tidak pula bagi mereka, karena beberapa di antara kamu harus melayani yang lainnya dan keluar masuk. Demikianlah Allah menjelaskan bagimu perintah-perintah itu ; karena Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (9220,2,24,60,'<p><span>Dan apabila anak-anak di antara kamu telah mencapai kedewasaan, maka hendaklah mereka meminta izin orang-orang sebelum mereka. Demikianlah Allah menerangkan bagimu perintah-perintah-Nya ; dan Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (9221,2,24,61,'<p><span>Wanita-wanita yang usianya telah lanjut, tidak berkeinginan lagi untuk menikah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2058a">2058a</a><span>]</span><span> maka tiada dosa bagi mereka, jika mereka melepaskan pakaian </span><span>luar</span><span> mereka tanpa memperlihatkan kecantikan. Tetapi jika mereka menjaga diri adalah lebih baik bagi mereka. Dan Allah Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9222,2,24,62,'<p><span>Tiada salahnya bagi orang yang buta dan tiada salahnya bagi orang yang pincang, dan tiada salahnya bagi orang yang sakit dan tidak pula bagi dirimu sendiri, bahwa kamu makan dari rumah-rumahmu, atau dari rumah bapak-bapakmu atau dari rumah ibu-ibumu atau dari rumah saudara-saudara lelaki kamu, atau dari rumah saudara-saudara perempuan kamu, atau dari rumah paman-pamanmu, atau dari rumah saudara-saudara perempuan bapak-mu, atau dari rumah saudara-saudara lelaki ibumu, atau dari rumah saudara-saudara perempuan ibumu, atau dari rumah yang kamu mempunyai kuncinya, atau dari rumah sahabatmu. </span><span>Demikian</span><span> </span><span>pula</span><span> tidak ada dosa bagi kamu biar kamu makan bersama-sama ataupun secara terpisah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2059">2059</a><span>]</span><span> Tetapi </span><span>[a]</span><span> apabila kamu masuk rumah-rumah, berilah salam kepada orang-orang karib-kerabat kamu dengan ucapan selamat dari sisi Allah, yang penuh berkat dan kesucian. Demikianlah Allah menjelaskan bagimu perintah-perintah-Nya, supaya kamu mempergunakan akal.</span></p><p><span>[a]</span><span> 24:28</span></p>');
INSERT INTO `quran` VALUES (9223,2,24,63,'<p><span>R. 9</span></p><p><span>Sesungguhnya orang-orang mukmin ialah orang-orang yang beriman kepada Allah dan Rasul-Nya, dan apabila mereka </span><span>berkumpul</span><span> bersamanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2059a">2059a</a><span>]</span><span> atas suatu utusan penting janganlah mereka pergi sebelum mereka minta izin kepadanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2060">2060</a><span>]</span><span> Sesungguhnya, orang </span><span>–</span><span>orang yang minta izin kepada engkau, mereka itulah orang-orang yang beriman kepada Allah dan Rasul-Nya. Maka, apabila mereka minta izin kepada engkau untuk suatu urusan mereka, berilah izin siapa yang engkau kehendaki dari mereka, dan mintakanlah ampunan bagi mereka dari Allah. Sesungguhnya Allah itu Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9224,2,24,64,'<p><span>Janganlah kamu memperlakukan seruan dari Rasul di antara kamu, seperti seruan seseorang di antara kamu kepada yang lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2061">2061</a><span>]</span><span> Sesungguhnya Allah mengetahui </span><span>[a]</span><span> orang-orang di antara kamu yang meloloskan diri dengan sembunyi-sembunyi. Maka hendaklah hati-hati orang-orang yang menyalahi perintah-Nya supaya </span><span>jangan</span><span> sampai cobaan menimpa mereka atau azab yang pedih menimpa mereka.</span></p><p><span>[a]</span><span> 9:127.</span></p>');
INSERT INTO `quran` VALUES (9225,2,24,65,'<p><span>Dengarkanlah ! </span><span>[b]</span><span> Sesungguhnya kepanyaan Allah apa yang ada di seluruh langit dan bumi. Bahwa Dia mengetahui </span><span>keadaan </span><span> yang kamu ada di atasnya. Dan pada hari ketika mereka akan dikembalikan kepada-Nya, maka Dia memberitahukan kepada mereka apa yang telah mereka kerjakan. Dan Allah Maha Mengetahui atas segala sesuatu.</span></p><p><span>[b]</span><span> 2:285 ; 10:56 ; 31:27.</span></p>');
INSERT INTO `quran` VALUES (9226,2,25,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9227,2,25,2,'<p><span>Maha beberkat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2062">2062</a><span>]</span><span> Dia, Yang telah menurunkan Al-Furqan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2063">2063</a><span>]</span><span> Kepada hamba-Nya, supaya ia menjadi pemberi peringatan bagi sekalian alam.</span></p>');
INSERT INTO `quran` VALUES (9228,2,25,3,'<p><span>Yang kepunyaan-Nya kerajaan seluruh langit dan bumi. </span><span>[b]</span><span> Dan Dia tidak mengambil anak, dan tidak ada sekutu bagi-Nya dalam kerajaan ; dan Dia telah menciptakan segala sesuatu, dan telah menetapkan ukurannya dengan sebaik-baiknya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2064">2064</a><span>]</span></p><p><span>[b]</span><span> 2:117 ; 10:69 ; 17:112 ; 18:5 ; 19:89 ; 21:27 ; 39:5 ; 43:82.</span></p>');
INSERT INTO `quran` VALUES (9229,2,25,4,'<p><span>Dan </span><span>[a]</span><span> mereka telah mengambil tuhan-tuhan selain Dia, </span><span>[b]</span><span> yang tidak menciptakan apa pun, malahan mereka tidak berdaya untuk memberi mudarat ataupun manfaat kepada diri mereka dan tidaklah mereka berkuasa atas maut, atas hidup dan tidak pula atas kebangkitan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2065">2065</a><span>]</span></p><p><span>[a]</span><span> 17:57 ; 18:16 ; 21:25.</span></p><p><span>[b]</span><span> 7:192 ; 16:21.</span></p>');
INSERT INTO `quran` VALUES (9230,2,25,5,'<p><span>Dan berkata orang-orang yang ingkar, “Ini tiada lain melainkan dusta yang telah ia mengada adakannya, </span><span>[c]</span><span> dan membantu atasnya suatu kaum yang lain.” Sesungguhnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2066">2066</a><span>]</span><span> mereka telah berbuat aniaya dan dusta.</span></p><p><span>[c]</span><span> 16:104.</span></p>');
INSERT INTO `quran` VALUES (9231,2,25,6,'<p><span>Dan mereka berkata, </span><span>[d]</span><span> “Kisah-kisah orang-orang terdahulu ; yang ia suruh tulis </span><span>dari seseorang,</span><span> maka </span><span>kisah</span><span> itu dibacakan kepadanya pagi dan petang.”</span></p><p><span>[d]</span><span> 8:32 ; 16:25 ; 68:16 ; 83:14.</span></p>');
INSERT INTO `quran` VALUES (9232,2,25,7,'<p><span>Katakanlah, “Telah diturunkannya </span><span>[a]</span><span> </span><span>Alquran,</span><span> oleh Dzat Yang mengetahui rahasia seluruh langit dan bumi. Sesungguhnya Dia adalah Maha Pengampun, Maha Penyayang.”</span></p><p><span>[a]</span><span> 6:4 ; 11:6 ; 67:14.</span></p>');
INSERT INTO `quran` VALUES (9233,2,25,8,'<p><span>Dan mereka berkata, “Macam rasul apakah ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2066a">2066a</a><span>]</span><span> ia makan makanan, dan berjalan di pasar-pasar ? </span><span>[b]</span><span> Mengapakah tidak diturunkan seorang malaikat kepadanya, supaya ia menjadi seorang pemberi peringatan bersama-sama dengan dia ?</span></p><p><span>[b]</span><span> 11:13 ; 15:8 ; 17:93.</span></p>');
INSERT INTO `quran` VALUES (9234,2,25,9,'<p><span>“Atau hendaknya diturunkan kepadanya </span><span>[c]</span><span> suatu khazanah, atau ada baginya kebun untuk makan darinya.” Dan berkata orang-orang aniaya, </span><span>[d]</span><span> “Kamu tidak mengikuti selain seorang laki-laki yang kena sihir.”</span></p><p><span>[c]</span><span> 11:13 ; 17:94.</span></p><p><span>[d]</span><span> 17:48.</span></p>');
INSERT INTO `quran` VALUES (9235,2,25,10,'<p><span>[e]</span><span> Lihatlah, betapa mereka membuat tamsilan bagi engkau ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2067">2067</a><span>]</span><span> Maka mereka telah sesat dan mereka tidak dapat menemukan jalan.</span></p><p><span>[e]</span><span> 17:49.</span></p>');
INSERT INTO `quran` VALUES (9236,2,25,11,'<p><span>R. 2</span></p><p><span>Maha Berkatlah Dia Yang jika Dia kehendaki, akan menjadikan bagimu yang lebih baik dari itu, </span><span>[a]</span><span> kebun-kebun yang dibawahnya mengalir sungai-sungai dan akan menjadikan bagimu istana-istana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2068">2068</a><span>]</span></p><p><span>[a]</span><span> 17:92.</span></p>');
INSERT INTO `quran` VALUES (9237,2,25,12,'<p><span>Bahkan mereka mendustakan Hari Kiamat dan Kami sediakan bagi orang-orang yang mendutakan Hari Kiamat api yang berkobar-kobar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2069">2069</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9238,2,25,13,'<p><span>Apabila </span><span>Jahannam</span><span> itu melihat mereka dari tempat yang jauh, </span><span>[b]</span><span> mereka akan mendengar gemuruh dan gelagarnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2070">2070</a><span>]</span></p><p><span>[b]</span><span> 11:107 ; 21:101 ; 67:8.</span></p>');
INSERT INTO `quran` VALUES (9239,2,25,14,'<p><span>Dan apabila mereka dilemparkan ke dalam tempat yang sempit darinya, </span><span>Jahannam</span><span>, dengan </span><span>[a]</span><span> terbelenggu, mereka di sana akan memohon kebinasaan.</span></p><p><span>[a]</span><span> 14:50.</span></p>');
INSERT INTO `quran` VALUES (9240,2,25,15,'<p><span>“Janganlah pada hari ini kamu meminta hanya satu kebinasaan, tetapi mintalah banyak- banyak kebinasaan.”</span></p>');
INSERT INTO `quran` VALUES (9241,2,25,16,'<p><span>Katakanlah, “Apakah yang demikian itu lebih baik, ataukah surga abadi yang telah </span><span>[b]</span><span> dijanjikan kepada orang-orang yang bertakwa ? Itulah yang akan menjadi ganjaran mereka dan tempat kembali.”</span></p><p><span>[b]</span><span> 21:104 ; 41:31.</span></p>');
INSERT INTO `quran` VALUES (9242,2,25,17,'<p><span>[c]</span><span> Mereka akan memperoleh di dalamnya apa yang mereka inginkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2071">2071</a><span>]</span><span> dan tinggal selama-lamanya. Itu adalah janji dari Tuhan engkau dan patut dimohonkan.</span></p><p><span>[c]</span><span> 41:32.</span></p>');
INSERT INTO `quran` VALUES (9243,2,25,18,'<p><span>[d]</span><span> Dan pada hari ketika Dia akan menghimpun mereka dan apa-apa yang mereka sembah selain Allah, kemudian Dia akan berkata, “Apakah kamu yang menyesatkan hamba- hamba-Ku ini, ataukah mereka sesat dari jalan yang </span><span>lurus </span><span> ?”</span></p><p><span>[d]</span><span> 10:29 ; 15:26 ; 34:41.</span></p>');
INSERT INTO `quran` VALUES (9244,2,25,19,'<p><span>[a]</span><span> Mereka berkata, “Maha Suci Engkau ! Tidaklah </span><span>layak</span><span> bagi kami untuk mengambil pelindung-pelindung selain Engkau ; tetapi Engkau menganugerahkan kepada mereka dan bapak-bapak mereka kenikmatan </span><span>hidup</span><span> hingga mereka melupakan peringatan </span><span>Engkau</span><span> dan mereka menjadi kaum yang binasa.”</span></p><p><span>[a]</span><span> 34:42.</span></p>');
INSERT INTO `quran` VALUES (9245,2,25,20,'<p><span>Maka sesungguhnya mereka telah mendustakan kamu tentang apa yang kamu katakan, maka kini kamu tidak dapat mengelakkan </span><span>azab</span><span> dan tidak akan mendapat pertolongan.” Dan barangsipa aniaya di antara kamu, Kami akan merasakannya azab yang besar.</span></p>');
INSERT INTO `quran` VALUES (9246,2,25,21,'<p><span>Dan tidak pernah Kami utus seseorang dari rasul-rasul sebelum engkau, malainkan </span><span>[b]</span><span> mereka akan makan makanan dan berjalan di pasar-pasar. Dan Kami jadikan sebagian dari kamu cobaan bagi sebagian yang lain </span><span>untuk melihat</span><span> apakah kamu bersabar. Dan adalah Tuhan engkau Maha Melihat.</span></p><p><span>[b]</span><span> 21:9.</span></p>');
INSERT INTO `quran` VALUES (9247,2,25,22,'<p><span>JUZ XIX</span></p><p><span>R. 3</span></p><p><span>[a]</span><span> Dan berkata orang-orang yang tidak mengharapkan pertemuan dengan Kami, “Mengapakah tidak diturunkan kepada kami malaikat-malaikat ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2071a">2071a</a><span>]</span><span> Atau, mengapakah kami tidak melihat Tuhan kami ? “ Sesungguhnya mereka terlalu sombong mengenai diri mereka dan mereka telah terlampau jauh dalam kedurhakaan.</span></p><p><span>[a]</span><span> 10:8 , 12.</span></p>');
INSERT INTO `quran` VALUES (9248,2,25,23,'<p><span>Pada hari </span><span>[b]</span><span> ketika mereka melihat malaikat-malaikat, tiada kabar suka pada hari itu bagi orang-orang yang berdosa ; dan mereka berkata </span><span>dengan sedih-hati.</span><span> “Semogalah ada dinding penghalang yang kuat ! “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2072">2072</a><span>]</span></p><p><span>[b]</span><span> 6:9, 159.</span></p>');
INSERT INTO `quran` VALUES (9249,2,25,24,'<p><span>Dan Kami akan hadapi segala pekerjaan yang telah mereka laksanakan, maka Kami akan menjadikannya zarrah-zarrah debu yang berhamburan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2073">2073</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9250,2,25,25,'<p><span>Para penghuni surga pada hari itu lebih baik tempat tinggalnya dan lebih indah tempat istirahatnya.</span></p>');
INSERT INTO `quran` VALUES (9251,2,25,26,'<p><span>Dan pada hari itu, </span><span>[a]</span><span> ketika langit akan terpecah-belah dengan awan, dan malaikat- malaikat akan diturunkan berkali-kali.</span></p><p><span>[a]</span><span> 2:211.</span></p>');
INSERT INTO `quran` VALUES (9252,2,25,27,'<p><span>[b]</span><span> Kerajaan yang hak pada hari itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2074">2074</a><span>]</span><span> kepunyaan Yang Maha Pemurah ; dan hari itu atas orang-orang kafir sangat keras.</span></p><p><span>[b]</span><span> 6:74 ; 22:57.</span></p>');
INSERT INTO `quran` VALUES (9253,2,25,28,'<p><span>Dan pada hari itu orang aniaya akan menggigit-gigit kedua tangannya lalu berkata, </span><span>[c]</span><span> “Ah, alangkah baiknya jika aku mengambil jalan bersama Rasul itu.</span></p><p><span>[c]</span><span> 33:67 ; 67:11.</span></p>');
INSERT INTO `quran` VALUES (9254,2,25,29,'<p><span>“Wahai, celakalah aku ! Alangkah baiknya sekiranya tidak pernah aku menjadikan di fulan itu sahabat !</span></p>');
INSERT INTO `quran` VALUES (9255,2,25,30,'<p><span>Sesungguhnya ia telah malalaikanku dari berzikir kepada </span><span>Allah</span><span> sesudah ia datang kepadaku .” Dan syaitan selalu menghinakan manusia.</span></p>');
INSERT INTO `quran` VALUES (9256,2,25,31,'<p><span>Dan berkata Rasul itu, “Ya Tuhan-ku, sesungguhnya kaumku telah menjadikan Alquran ini </span><span>sesuatu</span><span> yang telah ditinggalkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2075">2075</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (9257,2,25,32,'<p><span>[a]</span><span> Dan demikianlah telah Kami jadikan bagi tiap-tiap nabi musuh dari orang-orang yang berdosa. Dan cukuplah Tuhan engkau sebagai pemberi petunjuk dan penolong.</span></p><p><span>[a]</span><span> 6:113.</span></p>');
INSERT INTO `quran` VALUES (9258,2,25,33,'<p><span>[b]</span><span> Dan berkata orang-orang yang ingkar, “Mengapakah Alquran tidak diturunkan kepada-nya seluruhnya sekaligus ? Seperti itulah </span><span>Kami menurunkannya,</span><span> </span><span>[c]</span><span> supaya senantiasa dapat Kami meneguhkan hatimu dengannya. Dan Kami telah menyusunnya dalam bentuk yang sebaik-baiknya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2076">2076</a><span>]</span></p><p><span>[b]</span><span> 17:107 ; 73:5.</span></p><p><span>[c]</span><span> 11:121.</span></p>');
INSERT INTO `quran` VALUES (9259,2,25,34,'<p><span>Dan mereka tidak datang kepada engkau dengan sesuatu keberatan malainkan Kami melengkapi engkau dengan kebenaran dan sebaik-baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2077">2077</a><span>]</span><span> penjelasan.</span></p>');
INSERT INTO `quran` VALUES (9260,2,25,35,'<p><span>[a]</span><span> Mereka yang akan dihimpun wajah mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2077a">2077a</a><span>]</span><span> ke dalam Jahannam, </span><span>[b]</span><span> mereka itulah yang akan paling buruk tempatnya dan paling sesat jalannya.</span></p><p><span>[a]</span><span> 17:98.</span></p><p><span>[b]</span><span> 5:61.</span></p>');
INSERT INTO `quran` VALUES (9261,2,25,36,'<p><span>R. 4</span></p><p><span>Dan sesungguhnya Kami telah memberikan Kitab kepada Musa, dan </span><span>[c]</span><span> telah Kami jadikan bersamanya saudaranya Harun sebagai pembantu.</span></p><p><span>[c]</span><span> 20:30 </span><span>–</span><span> 33 ; 26:14 ; 28:35.</span></p>');
INSERT INTO `quran` VALUES (9262,2,25,37,'<p><span>Dan Kami berkata, </span><span>[d] </span><span>“Pergilah kamu berdua kepada kaum yang telah mendustakan Tanda-tanda Kami.” Maka Kami binasakan mereka sebinasa-binasanya.</span></p><p><span>[d]</span><span> 20:44 ; 28:35 </span><span>–</span><span> 36.</span></p>');
INSERT INTO `quran` VALUES (9263,2,25,38,'<p><span>Dan Kaum Nuh, tatkala mereka mendustakan rasul-rasul itu Kami tenggelamkan mereka, dan Kami jadikan mereka Tanda bagi umat manusia. </span><span>[a]</span><span> Dan telah Kami sediakan bagi orang-orang aniaya azab yang pedih.</span></p><p><span>[a]</span><span> 18:30.</span></p>');
INSERT INTO `quran` VALUES (9264,2,25,39,'<p><span>Dan </span><span>telah Kami binasakan </span><span> </span><span>[b]</span><span> </span><span>‘</span><span>Ad dan Tsamud, dan kaum sumur </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2078">2078</a><span>]</span><span> dan banyak generasi di antara mereka.</span></p><p><span>[b]</span><span> 9:70 ; 38:13 ; 50:13 </span><span>–</span><span> 15.</span></p>');
INSERT INTO `quran` VALUES (9265,2,25,40,'<p><span>Dan kepada masing-masing Kami kemukakan contoh-contoh ; dan semua Kami binasakan sehancur-hancurnya.</span></p>');
INSERT INTO `quran` VALUES (9266,2,25,41,'<p><span>Dan sesungguhnya mereka, </span><span>orang-orang Mekkah </span><span> itu, tentu telah melewati </span><span>[c]</span><span> kota </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2079">2079</a><span>]</span><span> yang telah dihujani hujan yang buruk, </span><span>bangsa Luth.</span><span> Apakah mereka tidak melihatnya ? Bahkan sebenarnya mereka tidak mengharapkan </span><span>hari</span><span> kebangkitan.</span></p><p><span>[c]</span><span> 7:85 ; 27:59.</span></p>');
INSERT INTO `quran` VALUES (9267,2,25,42,'<p><span>[d]</span><span> Dan apabila mereka melihat kepada engkau, mereka menjadikan engkau hanya sebagai perolok-olokan saja dan </span><span>berkata,</span><span> “Apakah orang ini yang telah dibangkitkan Allah sebagai rasul ?</span></p><p><span>[d]</span><span> 21:37.</span></p>');
INSERT INTO `quran` VALUES (9268,2,25,43,'<p><span>“Sesungguhnya ia hampir menyesatkan kami dari sembahan-sembahan kami. sekiranya kami tidak tetap setia kepada mereka.” Dan segera mereka akan mengentahui, bila mereka menyaksikan azab, siapa yang paling sesat jalannya.</span></p>');
INSERT INTO `quran` VALUES (9269,2,25,44,'<p><span>[a]</span><span> Apakah engkau melihat orang yang menjadikan hawa nafsunya sebagai tuhannya ? Maka apakah engkau menjadi pengawas atasnya ?</span></p><p><span>[a]</span><span> 45:24.</span></p>');
INSERT INTO `quran` VALUES (9270,2,25,45,'<p><span>Apakah engkau menyangka, bahwa sesungguhnya kebanyakan dari mereka mendengar atau mengerti ? </span><span>[b]</span><span> Mereka tidak lain melainkan seperti hewan ternak [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2080">2080</a><span>]</span><span> bahkan mereka lebih sesat dari jalannya.</span></p><p><span>[b]</span><span> 7:180.</span></p>');
INSERT INTO `quran` VALUES (9271,2,25,46,'<p><span>R. 5</span></p><p><span>Apakah engkau tidak melihat </span><span>[c]</span><span> bagaimana Tuhan engkau memanjangkan bayangan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2081">2081</a><span>]</span><span> Dan jika sekiranya Dia menghendaki, tentulah Dia menjadikannya tak berubah. Kemudian Kami menjadikan </span><span>kedudukan </span><span> matahari sebagai petunjuk atasnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2082">2082</a><span>]</span></p><p><span>[c]</span><span> 16:49.</span></p>');
INSERT INTO `quran` VALUES (9272,2,25,47,'<p><span>Kemudian Kami menariknya kepada Kami, sedikit demi sedikit. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2082a">2082a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9273,2,25,48,'<p><span>[a]</span><span> Dan Dia-lah, Yang telah menjadikan bagimu malam hari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2083">2083</a><span>]</span><span> sebagai pakaian dan tidur istirahat, dan </span><span>Dia</span><span> telah menjadikan siang untuk berusaha.</span></p><p><span>[a]</span><span> 6:97 ; 78:11.</span></p>');
INSERT INTO `quran` VALUES (9274,2,25,49,'<p><span>[b]</span><span> Dan Dia-lah Dzat yang mengirimkan angin sebagai </span><span>pembawa </span><span> khabar suka sebelum </span><span>kedatangan</span><span> rahmat-Nya, dan Kami turunkan air bersih dari awan.</span></p><p><span>[b]</span><span> 7:58 ; 15:23.</span></p>');
INSERT INTO `quran` VALUES (9275,2,25,50,'<p><span>Supaya dengan itu Kami menghidupkan negeri yang telah mati, dan memberinya minum dari apa yang telah Kami ciptakan, binatang-binatang ternak dan banyak manusia.</span></p>');
INSERT INTO `quran` VALUES (9276,2,25,51,'<p><span>Dan sesungguhnya Kami telah menjelaskan </span><span>Alquran</span><span> ini dengan berbagai cara diantara mereka, supaya mereka mendapat pelajaran tetapi kebanyakan manusia menolak kecuali kekufuran.</span></p>');
INSERT INTO `quran` VALUES (9277,2,25,52,'<p><span>Dan sekiranya Kami menghendaki, niscaya Kami bangkitkan di tiap-tiap negeri seorang pemberi ingat.</span></p>');
INSERT INTO `quran` VALUES (9278,2,25,53,'<p><span>Maka janganlah mengikuti orang-orang kafir dan berjihadlah terhadap mereka dengan </span><span>Alquran </span><span> ini, jihad yang besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2084">2084</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9279,2,25,54,'<p><span>[a]</span><span> Dan Dia-lah Yang mengalirkan dua lautan, yang satu tawar </span><span>dan </span><span> segar dan yang lainnya asin </span><span>lagi </span><span> pahit ; dan di antara keduanya Dia menjadikan penghalang dan pemisah yang tek terlintasi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2085">2085</a><span>]</span></p><p><span>[a]</span><span> 35:13 ; 55:20, 21.</span></p>');
INSERT INTO `quran` VALUES (9280,2,25,55,'<p><span>[b]</span><span> Dan Dia-lah Yang menjadikan manusia dari air, dan menjadikan baginya keluarga melalui pertalian darah dan keluarga melalui perkawinan ; dan Tuhan-mu Maha Kuasa.</span></p><p><span>[b]</span><span> 32:9</span></p>');
INSERT INTO `quran` VALUES (9281,2,25,56,'<p><span>[a]</span><span> Dan mereka menyembah selain Allah apa yang tidak dapat memberi manfaat kepada mereka dan tidak pula dapat mendatangkan mudarat kepada mereka. Dan orang kafir selalu menentang </span><span>rencana</span><span> Tuhan-nya.</span></p><p><span>[a]</span><span> 6:72 ;10:107 ; 21:67 ; 22:13.</span></p>');
INSERT INTO `quran` VALUES (9282,2,25,57,'<p><span>[b]</span><span> Dan tidaklah Kami mengutus engkau melainkan sebagai pembawa khabar suka dan pemberi peringatan.</span></p><p><span>[b]</span><span> 2:120 ; 5:20 ; 11:3 ; 35:25.</span></p>');
INSERT INTO `quran` VALUES (9283,2,25,58,'<p><span>[c]</span><span> Katakanlah, “Aku tidak meminta balasan kepadamu untuk ini, kecuali supaya barang siapa menyukainya, baiklah ia menempuh jalan kepada Tuhan-nya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2086">2086</a><span>]</span></p><p><span>[c]</span><span> 38:87 ; 42:24.</span></p>');
INSERT INTO `quran` VALUES (9284,2,25,59,'<p><span>[d]</span><span> Dan bertawakallah kepada Dia Yang hidup kekal dan Yang tidak mati, dan sanjunglah Dia dengan pujian-Nya. Dan cukuplah Dia sebagai Yang mengetahui dosa-dosa hamba-hamba-Nya.</span></p><p><span>[d]</span><span> 26:218 ; 27:80 ; 33:49.</span></p>');
INSERT INTO `quran` VALUES (9285,2,25,60,'<p><span>[a]</span><span> Dia-lah Yang telah menciptakan seluruh langit dan bumi dan apa-apa di antara keduanya dalam enam masa, kemudian Dia bersemayam di atas </span><span>‘</span><span>Arasy, Yang Maha Pemurah ! Maka tanyakanlah mengenai Dia kepada yang mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2087">2087</a><span>]</span></p><p><span>[a]</span><span> 7:55 ; 11:8 ; 32:5 ; 57:5.</span></p>');
INSERT INTO `quran` VALUES (9286,2,25,61,'<p><span>Dan apabila dikatakan kepada mereka, “Bersujudlah kepada Yang Maha Pemurah.” mereka berkata, “ Dan siapakah Yang Maha Pemurah itu ? Haruskah kami bersujud kepada apa yang engkau suruh kami ? “ Dan hal ini menambah kebencian mereka.</span></p>');
INSERT INTO `quran` VALUES (9287,2,25,62,'<p><span>R. 6</span></p><p><span>Maha beberkatlah Dia, </span><span>[b]</span><span> Yang telah menjadikan gugusan-gugusan bintang di langit dan telah menempatkan di dalamnya matahari yang bersinar dan bulan yang memantulkan cahaya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2087a">2087a</a><span>]</span></p><p><span>[b]</span><span> 15:17 ; 85:2.</span></p>');
INSERT INTO `quran` VALUES (9288,2,25,63,'<p><span>[c]</span><span> Dan Dia-lah, Yang telah menjadikan malam dan siang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2088">2088</a><span>]</span><span> silih berganti, untuk orang yang ingin mengambil nasihat, atau ingin bersyukur.</span></p><p><span>[c]</span><span> 36:38 </span><span>–</span><span> 41.</span></p>');
INSERT INTO `quran` VALUES (9289,2,25,64,'<p><span>Dan hamba-hamba </span><span>Tuhan</span><span> Yang Rahman ialah </span><span>[a]</span><span> mereka yang berjalan di muka bumi dengan merendahkan diri ; dan </span><span>[b]</span><span> apabila orang-orang jahil menegur mereka, mereka mengucapkan “Selamat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2089">2089</a><span>]</span></p><p><span>[a]</span><span> 17:38 ; 31:19.</span></p><p><span>[b]</span><span> 28:56.</span></p>');
INSERT INTO `quran` VALUES (9290,2,25,65,'<p><span>[c]</span><span> Dan orang-orang yang melewatkan malam untuk Tuhan mereka dengan bersujud dan berdiri.</span></p><p><span>[c]</span><span> 41:39 ; 73:21.</span></p>');
INSERT INTO `quran` VALUES (9291,2,25,66,'<p><span>Dan orang-orang yang berkata, “Ya Tuhan kami, jauhkanlah dari kami azab Jahannam ; sesungguhnya azabnya itu adalah kebinasaan yang besar.</span></p>');
INSERT INTO `quran` VALUES (9292,2,25,67,'<p><span>“Sesungguhnya </span><span>Jahannam</span><span> itu seburuk-buruknya tempat tinggal sementara dan tempat tinggal tetap.”</span></p>');
INSERT INTO `quran` VALUES (9293,2,25,68,'<p><span>Dan mereka yang apabila membelanjakan harta </span><span>[d]</span><span> tidaklah boros dan tidak pula kikir, melainkan </span><span>mengambil</span><span> jalan-tengah di antara </span><span>kedua keadaan </span><span> itu.</span></p><p><span>[d]</span><span> 7:32 ; 17:28.</span></p>');
INSERT INTO `quran` VALUES (9294,2,25,69,'<p><span>Dan orang-orang yang tidak berseru beserta </span><span>[a]</span><span> Allah tuhan yang lain, dan tidak membunuh jiwa yang telah dilarang oleh Allah, kecuali dengan alasan yang benar, dan tidak pula berzina, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2090">2090</a><span>]</span><span> dan barangsiapa berbuat demikian, ia akan menemui hukuman dosa.</span></p><p><span>[a]</span><span> 6:152 ; 17:33, 34.</span></p>');
INSERT INTO `quran` VALUES (9295,2,25,70,'<p><span>[b]</span><span> Akan digandakan baginya azab pada Hari Kiamat, dan ia akan tinggal di dalamnya terhina.</span></p><p><span>[b]</span><span> 4:15.</span></p>');
INSERT INTO `quran` VALUES (9296,2,25,71,'<p><span>Kecuali mereka yang bertobat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2091">2091</a><span>]</span><span> dan </span><span>[c]</span><span> beriman dan berbuat amal shaleh ; maka mereka itulah Allah akan mengubah keburukan-keburukan mereka menjadi kebaikan- kebaikan. Dan adalah Allah Maha pengampun, Maha Penyayang.</span></p><p><span>[c]</span><span> 3:58 ; 6:49 ; 18:89 ; 19:61 ; 34:38.</span></p>');
INSERT INTO `quran` VALUES (9297,2,25,72,'<p><span>[d]</span><span> Dan barangsiapa yang bertaubat dan beramal shaleh, maka sesungguhnya ia kembali kepada Allah dengan tobat yang benar.</span></p><p><span>[d]</span><span> 5:40 ; 20:83 ; 28:68.</span></p>');
INSERT INTO `quran` VALUES (9298,2,25,73,'<p><span>Dan orang-orang yang tidak memberikan kesaksian palsu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2092">2092</a><span>]</span><span> dan </span><span>[a]</span><span> apabila mereka melalui sesuatu hal yang sia-sia, mereka berlalu dengan sikap yang mulia ;</span></p><p><span>[a]</span><span> 23:4 ; 28:56.</span></p>');
INSERT INTO `quran` VALUES (9299,2,25,74,'<p><span>Dan orang-orang yang apabila diperingatkan tentang Tanda-tanda Tuhan mereka, tidak akan terjerumus ke dalamnya sebagai orang-orang tuli dan buta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2092a">2092a</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (9300,2,25,75,'<p><span>Dan orang-orang yang bekata, “Ya Tuhan kami, anugerahkanlah kepada kami dari istri- istri kami dan keturunan kami menjadi menyejuk mata kami ; dan jadikanlah kami imama bagi orang-orang yang bertakwa.”</span></p>');
INSERT INTO `quran` VALUES (9301,2,25,76,'<p><span>Orang-orang itulah yang akan dianugerahi </span><span>[b]</span><span> kamar-kamar tinggi </span><span>di surga</span><span>, karena mereka bersabar, dan mereka akan disambut di dalamnya dengan penghormatan dan doa selamat.</span></p><p><span>[b]</span><span> 34:38.</span></p>');
INSERT INTO `quran` VALUES (9302,2,25,77,'<p><span>Mereka akan tinggal kekal di dalamnya. Itulah sebaik-baik tempat istirahat dan tempat menetap.</span></p>');
INSERT INTO `quran` VALUES (9303,2,25,78,'<p><span>Katakanlah “Tuhan-ku tidak akan memperdulikan kamu jika tidak ada doa kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2093">2093</a><span>]</span><span> Maka sungguh kamu telah mendustakah, oleh karena itu </span><span>azab</span><span> akan melekat </span><span>dengan kamu.</span><span> “</span></p>');
INSERT INTO `quran` VALUES (9304,2,26,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9305,2,26,2,'<p><span>Allah </span><span> Yang Maha Suci, Maha Mendengar, Maha Mulia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2094">2094</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9306,2,26,3,'<p><span>[b]</span><span> Inilah ayat-ayat kitab yang menjelaskan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2094a">2094a</a><span>]</span></p><p><span>[b]</span><span> 12:2 ; 15:2 ; 27:2 ; 28:3.</span></p>');
INSERT INTO `quran` VALUES (9307,2,26,4,'<p><span>[c]</span><span> Boleh jadi engkau akan membinasakan dirimu, karena mereka tidak mau beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2094b">2094b</a><span>]</span></p><p><span>[c]</span><span> 18:7.</span></p>');
INSERT INTO `quran` VALUES (9308,2,26,5,'<p><span>Jika Kami kehendaki, Kami dapat menurunkan kepada mereka suatu Tanda dari langit, sehingga leher-leher </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2095">2095</a><span>]</span><span> mereka akan tertunduk di hadapannya.</span></p>');
INSERT INTO `quran` VALUES (9309,2,26,6,'<p><span>[a]</span><span> Dan tidak pernah datang kepada mereka peringatan yang baru </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2096">2096</a><span>]</span><span> dari </span><span>Tuhan</span><span> Yang Maha Pemurah, melainkan mereka selalu berpaling darinya.</span></p><p><span>[a]</span><span> 21:3, 43.</span></p>');
INSERT INTO `quran` VALUES (9310,2,26,7,'<p><span>[b]</span><span> Maka sesungguhnya mereka telah mendustakan, tetapi segera datang kepada mereka khabar-khabar mengenai apa yang pernah mereka perolok-olokan.</span></p><p><span>[b]</span><span> 6:35 ; 22:43 ; 35:26 ; 40:6.</span></p>');
INSERT INTO `quran` VALUES (9311,2,26,8,'<p><span>[c]</span><span> Apakah mereka tidak melihat bumi, berapa banyaknya telah Kami tumbuhkan di dalamnya dari setiap jodoh yang mulia ?</span></p><p><span>[c]</span><span> 36:34 </span><span>–</span><span> 37.</span></p>');
INSERT INTO `quran` VALUES (9312,2,26,9,'<p><span>Sesungguhnya dalam hal itu ada satu Tanda ; tetapi kebanyakan dari mereka tidak beriman.</span></p>');
INSERT INTO `quran` VALUES (9313,2,26,10,'<p><span>Dan sesungguhnya Tuhan-mu, Dia Maha Perkasa, Maha Penyayang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2097">2097</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9314,2,26,11,'<p><span>R. 2</span></p><p><span>[a]</span><span> Dan </span><span>ingatlah</span><span> ketika Tuhan-mu berseru kepada Musa, “Datanglah kepada kaum yang aniaya.</span></p><p><span>[a]</span><span> 20:25 ; 79:17 </span><span>–</span><span> 18.</span></p>');
INSERT INTO `quran` VALUES (9315,2,26,12,'<p><span>Kaum Firaun. Apakah mereka tidak bertakwa ? “</span></p>');
INSERT INTO `quran` VALUES (9316,2,26,13,'<p><span>Ia berkata, “Ya Tuhan-ku, </span><span>[b]</span><span> sesungguhnya aku takut bahwa mereka akan mendustakanku ;</span></p><p><span>[b]</span><span> 20:46 ; 28:35.</span></p>');
INSERT INTO `quran` VALUES (9317,2,26,14,'<p><span>“Dan dadaku menjadi sempit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2098">2098</a><span>] [c]</span><span> dan lidahku tidak lancar ; maka </span><span>[d]</span><span> utuslah kepada Harun </span><span>bersamaku.</span></p><p><span>[c]</span><span> 20:28.</span></p>');
INSERT INTO `quran` VALUES (9318,2,26,15,'<p><span>[e]</span><span> “Dan mereka mempunyai tuduhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2099">2099</a><span>]</span><span> terhadapku, maka aku takut mereka akan membunuhku.”</span></p><p><span>[e]</span><span> 28:34.</span></p>');
INSERT INTO `quran` VALUES (9319,2,26,16,'<p><span>[f]</span><span> </span><span>Tuhan</span><span> berfirman, “sama sekali tidak, maka pergilah kamu berdua dengan Tanda-tanda Kami ; sesungguhnya Kami beserta kamu dan mendengar </span><span>doa-doa-mu.</span></p><p><span>[f]</span><span> 28:36.</span></p>');
INSERT INTO `quran` VALUES (9320,2,26,17,'<p><span>“Maka datangilah kamu berdua kepada Firaun dan katakanlah. “Sesungguhnya kami rasul </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2100">2100</a><span>]</span><span> Tuhan semesta alam.</span></p>');
INSERT INTO `quran` VALUES (9321,2,26,18,'<p><span>supaya kirimlah beserta kami Bani Israil.”</span></p>');
INSERT INTO `quran` VALUES (9322,2,26,19,'<p><span>Firaun</span><span> berkata, “</span><span>Ya Musa,</span><span> bukankah kami telah mendidik kamu di antara kami ketika engkau kanak-kanak ? Dan engkau pernah tinggal di antara kami </span><span>beberapa</span><span> tahun dalam hidupmu.</span></p>');
INSERT INTO `quran` VALUES (9323,2,26,20,'<p><span>“Dan engkau telah melakukan perbuatan yang pernah engkau perbuat itu dan engkau adalah </span><span>salah seorang </span><span> dari yang tidak berterimakasih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2101">2101</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9324,2,26,21,'<p><span>Musa</span><span> berkata, “Aku telah melakukannya ketika itu aku tidak mengetahui hakikatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2102">2102</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9325,2,26,22,'<p><span>[a]</span><span> “Maka aku melarikan diri dari kamu ketika aku takut kepadamu, maka Tuhan-ku menganugerahkan kepadaku keputusan dan menjadikan aku </span><span>seorang </span><span> dari rasul-rasul. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2103">2103</a><span>]</span></p><p><span>[a]</span><span> 28:22.</span></p>');
INSERT INTO `quran` VALUES (9326,2,26,23,'<p><span>“Dan itukah nikmat </span><span>pendidikan waktu kecil</span><span> yang telah engkau melakukannya kepadaku sehingga engkau telah perbudak Bani Israil ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2104">2104</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9327,2,26,24,'<p><span>Firaun berkata, </span><span>[b]</span><span> “Dan apakah Tuhan sekalian alam itu ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2105">2105</a><span>]</span></p><p><span>[b]</span><span> 20:50.</span></p>');
INSERT INTO `quran` VALUES (9328,2,26,25,'<p><span>Musa </span><span> berkata, </span><span>[a]</span><span> “Tuhan Pencipata seluruh langit dan bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2106">2106</a><span>]</span><span> dan apa-apa yang ada di antara keduanya, jikakamu mau meyakini.”</span></p><p><span>[a]</span><span> 44:8.</span></p>');
INSERT INTO `quran` VALUES (9329,2,26,26,'<p><span>Firaun</span><span> berkata kepada orang-orang di sekelilingnya, “Tidaklah kamu mendengar ?” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2107">2107</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9330,2,26,27,'<p><span>Musa </span><span> berkata, “Tuhan-mu, dan Tuhan bapak-bapakmu terdahulu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2108">2108</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9331,2,26,28,'<p><span>Firaun </span><span> berkata, “ </span><span>[b]</span><span> Sesungguhnya rasulmu, yang telah diutus kepadamu adalah gila.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2109">2109</a><span>]</span></p><p><span>[b]</span><span> 44:15.</span></p>');
INSERT INTO `quran` VALUES (9332,2,26,29,'<p><span>Musa </span><span> berkata, </span><span>[c]</span><span> “Tuhan timur dan barat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2110">2110</a><span>]</span><span> dan </span><span>Tuhan</span><span> segala yang ada di antara keduanya, jika sekiranya kamu mau mempergunakan akal.”</span></p><p><span>[c]</span><span> 2:116 ; 55:18.</span></p>');
INSERT INTO `quran` VALUES (9333,2,26,30,'<p><span>[a]</span><span> </span><span>Firaun </span><span> berkata, “Jika kamu menjadikan persembahan lain selainku, tentulah akan aku penjarakan engkau.”</span></p><p><span>[a]</span><span> 28:39.</span></p>');
INSERT INTO `quran` VALUES (9334,2,26,31,'<p><span>Musa</span><span> berkata, “Apa, walaupun aku mendatangkan kepadamu sesuatu </span><span>mujizat</span><span> yang nyata ! ? “</span></p>');
INSERT INTO `quran` VALUES (9335,2,26,32,'<p><span>[b]</span><span> </span><span>Firaun</span><span> berkata, “Maka datangkanlah </span><span>sesuatu </span><span>itu, jika engkau termasuk orang-orang yang berkata benar.”</span></p><p><span>[b]</span><span> 7:107.</span></p>');
INSERT INTO `quran` VALUES (9336,2,26,33,'<p><span>Maka Musa melepaskan tongkatnya, lalu tiba-tiba ia </span><span>[c]</span><span> menjadi seekor ular yang </span><span>nampak </span><span>jelas.</span></p><p><span>[c] </span><span>7:108.</span></p>');
INSERT INTO `quran` VALUES (9337,2,26,34,'<p><span>[d]</span><span> Dan ia mengulurkan tangannya, dan tiba-tiba tangannya itu menjadi putih bagi orang- orang yang menyaksikannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2111">2111</a><span>]</span></p><p><span>[d]</span><span> 7:109 ; 20:23.</span></p>');
INSERT INTO `quran` VALUES (9338,2,26,35,'<p><span>R. 3</span></p><p><span>[e]</span><span> </span><span>Firaun </span><span> berkata kepada pembesar-pembesar di sekelilingnya, “Sesungguhnya ia tukang sihir yang pandai ;</span></p><p><span>[e]</span><span> 7:110.</span></p>');
INSERT INTO `quran` VALUES (9339,2,26,36,'<p><span>[f]</span><span> Dia berniat dengan sihirnya mengeluarkan kamu dari negeri kamu, Maka, apakah yang kamu sarankan ? “</span></p><p><span>[f]</span><span> 7:111 ; 20:58, 64.</span></p>');
INSERT INTO `quran` VALUES (9340,2,26,37,'<p><span>[g]</span><span> Mereka berkata, “Tundalah dia dan saudaranya </span><span>untuk sementara </span><span> dan kirimkanlah para penyeru ke kota-kota,</span></p><p><span>[g]</span><span> 7:112 ; 10:80.</span></p>');
INSERT INTO `quran` VALUES (9341,2,26,38,'<p><span>[h]</span><span> Yang akan mendatangkan kepada engkau ahli sihir yang pandai.”</span></p><p><span>[h]</span><span> 7:113.</span></p>');
INSERT INTO `quran` VALUES (9342,2,26,39,'<p><span>[a] </span><span>Maka dihimpunnyalah ahli-ahli sihir itu pada waktu hari yang ditentukan.</span></p><p><span>[a]</span><span> 7:114 ; 20:59.</span></p>');
INSERT INTO `quran` VALUES (9343,2,26,40,'<p><span>[b]</span><span> Dan dikatakan kepada orang-orang, “Apakah kamu akan berkumpul bersama- sama,</span></p><p><span>[b]</span><span> 20:60.</span></p>');
INSERT INTO `quran` VALUES (9344,2,26,41,'<p><span>Supaya kita dapat mengikuti ahli-ahli sihir itu, jika mereka yang menang ? “</span></p>');
INSERT INTO `quran` VALUES (9345,2,26,42,'<p><span>Dan ketika ahli-ahli sihir itu datang, mereka berkata kepada Firaun, “Apakah kami akan mendapat hadiah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2112">2112</a><span>] </span><span>jika kami jadi pemenang ? “</span></p>');
INSERT INTO `quran` VALUES (9346,2,26,43,'<p><span>[c]</span><span> Ia berkata, “Ya, dan pasti nanti kamu akan termasuk orang-orang yang dekat kepada ku.”</span></p><p><span>[c]</span><span> 7:115.</span></p>');
INSERT INTO `quran` VALUES (9347,2,26,44,'<p><span>[d]</span><span> Berkata kepada mereka Musa, “</span><span>Sekarang </span><span>lemparkanlah apa yang hendak kamu lemparkan.”</span></p><p><span>[d]</span><span> 7:117 ; 10:81 ; 20:67.</span></p>');
INSERT INTO `quran` VALUES (9348,2,26,45,'<p><span>Maka mereka melemparkan tali-talinya dan tongkat-tongkatnya, dan berkata, “Demi kehormatan Firaun, sesungguhnya kami yang pasti akan menang.”</span></p>');
INSERT INTO `quran` VALUES (9349,2,26,46,'<p><span>[e]</span><span> Maka Musa melemparkan tongkatnya, tiba-tiba tongkat itu menelan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2113">2113</a><span>]</span><span> semua yang telah mereka ada-adakan.</span></p><p><span>[e]</span><span> 7:118 ; 20:70.</span></p>');
INSERT INTO `quran` VALUES (9350,2,26,47,'<p><span>[a]</span><span> Maka ahli-ahli sihir itu menjatuhkan diri bersujud </span><span>di hadapan Tuhan.</span></p><p><span>[a]</span><span> 7:121 ; 20:71.</span></p>');
INSERT INTO `quran` VALUES (9351,2,26,48,'<p><span>Mereka berkata, “Kami beriman kepada Tuhan sekalian alam,</span></p>');
INSERT INTO `quran` VALUES (9352,2,26,49,'<p><span>[b]</span><span> Tuhan Musa, dan Harun.”</span></p><p><span>[b]</span><span> 7:123 ; 20:71.</span></p>');
INSERT INTO `quran` VALUES (9353,2,26,50,'<p><span>[c]</span><span> </span><span>Firaun</span><span> berkata, “Apakah kamu beriman kepadanya sebelum aku memberimu izin ? Sesungguhnya ia benar pemimpinmu yang telah mengajarkan sihir kepadamu. Tetapi kamu pasti akan mengetahui </span><span>akibatnya.</span><span> Maka aku akan memotong tanganmu dan kakimu , karena </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2113a">2113a</a><span>]</span><span> kamu menentangku dan niscaya aka akan menyalib kamu sekalian.”</span></p><p><span>[c]</span><span> 7:124 </span><span>–</span><span> 125 ; 20:72.</span></p>');
INSERT INTO `quran` VALUES (9354,2,26,51,'<p><span>[d]</span><span> Mereka berkata, “Tiada kemudaratan </span><span>menimpa kami ;</span><span> sesunguhnya kami kepada Tuhan kami akan kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2114">2114</a><span>]</span></p><p><span>[d]</span><span> 7:126 ; 20:73.</span></p>');
INSERT INTO `quran` VALUES (9355,2,26,52,'<p><span>[e]</span><span> “Sesungguhnya, kami mengharapkan, bahwa Tuhan kami akan mengampuni dosa-dosa kami, karena kami adalah orang-orang mukmin yang pertama.</span></p><p><span>[e]</span><span> 5:85.</span></p>');
INSERT INTO `quran` VALUES (9356,2,26,53,'<p><span>R. 4</span></p><p><span>[a]</span><span> Dan Kami wahyukan kepada Musa, “Bawalah hamba-hamba-ku pada waktu malam hari ; niscaya kamu akan dikejar.”</span></p><p><span>[a]</span><span> 20:74.</span></p>');
INSERT INTO `quran` VALUES (9357,2,26,54,'<p><span>Dan Firaun itu mengirimkan penyeru-penyeru ke kota-kota untuk mengumpulkan,</span></p>');
INSERT INTO `quran` VALUES (9358,2,26,55,'<p><span>Sesungguhnya mereka itu hanyalah segolongan kecil</span></p>');
INSERT INTO `quran` VALUES (9359,2,26,56,'<p><span>Dan sesungguhnya mereka itu telah menimbulkan kemarahan pada kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2115">2115</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9360,2,26,57,'<p><span>Dan kita adalah golongan besar yang bersiaga.</span></p>');
INSERT INTO `quran` VALUES (9361,2,26,58,'<p><span>[b]</span><span> Kemudian Kami keluarkan mereka dari kebun-kebun dan mata air-mata air,</span></p><p><span>[b]</span><span> 44:26, 27.</span></p>');
INSERT INTO `quran` VALUES (9362,2,26,59,'<p><span>Dan khazanah-khazanah dan tempat tinggal yang terhormat,</span></p>');
INSERT INTO `quran` VALUES (9363,2,26,60,'<p><span>[c]</span><span> Demikianlah keadaannya ; dan Kami mewariskannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2116">2116</a><span>]</span><span> kepada Bani Israil,</span></p><p><span>[c]</span><span> 44:29.</span></p>');
INSERT INTO `quran` VALUES (9364,2,26,61,'<p><span>[a]</span><span> Maka </span><span>lasykar-lasykar Firaun</span><span> mengejar dan menyusul mereka pada waktu matahari terbit.</span></p><p><span>[a]</span><span> 10:91 ; 20:79 ; 44:24.</span></p>');
INSERT INTO `quran` VALUES (9365,2,26,62,'<p><span>Dan ketika kedua lasykar itu dapat melihat satu sama lain, berkatalah pengikut-pengikut Musa, “Sesungguhnya kita pasti akan tertangkap.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2117">2117</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9366,2,26,63,'<p><span>Musa </span><span>berkata, “Sekali-kali tidak ! Sesungguhnya Tuhan-ku bersertaku. Dia akan menunjukkan kepadaku jalan kemenangan.”</span></p>');
INSERT INTO `quran` VALUES (9367,2,26,64,'<p><span>[b]</span><span> Maka Kami wahyukan kepada musa, “Pukullah laut dengan tongkatmu,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2117a">2117a</a><span>]</span><span> Maka tersibaklah laut, dan setiap bagiannya seperti gunung besar.</span></p><p><span>[b]</span><span> 20:78.</span></p>');
INSERT INTO `quran` VALUES (9368,2,26,65,'<p><span>Dan Kami dekatkan disana golongan yang lain, </span><span>golongan Firaun.</span></p>');
INSERT INTO `quran` VALUES (9369,2,26,66,'<p><span>[c]</span><span> Dan Kami selamatkan Musa dan orang-orang beserta dia seluruhnya.</span></p><p><span>[c]</span><span> 20:81 ; 44:31 </span><span>–</span><span> 32.</span></p>');
INSERT INTO `quran` VALUES (9370,2,26,67,'<p><span>[d]</span><span> Kemudian Kami tenggelamkan golongan yang lain.</span></p><p><span>[d]</span><span> 2:51 ; 7:137 ; 17:104 ; 20:79.</span></p>');
INSERT INTO `quran` VALUES (9371,2,26,68,'<p><span>Sesungguhnya, dalam hal itu ada Tanda besar ; akan tetapi kebanyakan dari mereka tidak mau beriman.</span></p>');
INSERT INTO `quran` VALUES (9372,2,26,69,'<p><span>Dan sesungguhnya Tuhan-mu, Dia-lah Yang Maha Perkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9373,2,26,70,'<p><span>R. 5</span></p><p><span>Dan bacakanlah kepada mereka kisah Ibrahim.</span></p>');
INSERT INTO `quran` VALUES (9374,2,26,71,'<p><span>[a]</span><span> Ketika ia berkata kepada ayahnya dan kaumnya, “Apakah yang kamu sembah ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2118">2118</a><span>]</span></p><p><span>[a]</span><span> 6:75 ; 19:43 ; 21:53 ; 37:86 </span><span>–</span><span> 87.</span></p>');
INSERT INTO `quran` VALUES (9375,2,26,72,'<p><span>[b]</span><span> Mereka berkata, “Kami menyembah berhala-berhala, dan kami senantiasa duduk di depan mereka.”</span></p><p><span>[b] </span><span>21:54 ; 26:72.</span></p>');
INSERT INTO `quran` VALUES (9376,2,26,73,'<p><span>Berkata Ibrahim, “Apakah mereka mendengar kamu apabila kamu berseru ?</span></p>');
INSERT INTO `quran` VALUES (9377,2,26,74,'<p><span>Ataukah mereka memberi manfaat kepadamu atau menyampaikan kemudaratan ? “</span></p>');
INSERT INTO `quran` VALUES (9378,2,26,75,'<p><span>[c]</span><span> Mereka berkata, “Melainkan kami dapati bapak-bapak kami berbuat demikian.”</span></p><p><span>[c]</span><span> 21:54 ; 43:24.</span></p>');
INSERT INTO `quran` VALUES (9379,2,26,76,'<p><span>[d]</span><span> Berkata </span><span>Ibrahim,</span><span> “Apakah kamu mengetahui apa yang kamu sembah,</span></p><p><span>[d]</span><span> 21:67 ; 37:86 </span><span>–</span><span> 87.</span></p>');
INSERT INTO `quran` VALUES (9380,2,26,77,'<p><span>Kamu dan bapak-bapakmu yang dahulu ?</span></p>');
INSERT INTO `quran` VALUES (9381,2,26,78,'<p><span>Mereka itu semuanya musuh-musuh bagiku, kecuali Tuhan sekalian alam,</span></p>');
INSERT INTO `quran` VALUES (9382,2,26,79,'<p><span>Yang telah menciptakan aku, maka Dia-lah yang akan memberi petunjuk kepadaku ;</span></p>');
INSERT INTO `quran` VALUES (9383,2,26,80,'<p><span>Dan Yang memberiku makan dan memberiku minum,</span></p>');
INSERT INTO `quran` VALUES (9384,2,26,81,'<p><span>Dan apabila aku sakit, maka Dia-lah Yang menyembuhkanku ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2119">2119</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9385,2,26,82,'<p><span>Dan Yang akan mematikan aku, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2120">2120</a><span>]</span><span> dan kemudian menghidupkan </span><span>kembali;</span></p>');
INSERT INTO `quran` VALUES (9386,2,26,83,'<p><span>Dan Yang kuharapkan akan mengampuni kesalahanku pada hari pembalasan.</span></p>');
INSERT INTO `quran` VALUES (9387,2,26,84,'<p><span>Ya Tuhan-ku, anugerahikanlah kepadaku kebijaksanaan dan masukkanlah aku bersama orang-orang shaleh ;</span></p>');
INSERT INTO `quran` VALUES (9388,2,26,85,'<p><span>[a]</span><span> Dan jadikanlah aku buah tutur yang baik diantara orang-orang yang datang kemudian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2121">2121</a><span>]</span></p><p><span>[a]</span><span> 19:51.</span></p>');
INSERT INTO `quran` VALUES (9389,2,26,86,'<p><span>Dan jadikanlah aku dari ahli waris surga kenikmatan,</span></p>');
INSERT INTO `quran` VALUES (9390,2,26,87,'<p><span>[a]</span><span> Dan ampunilah ayahku ; sesungguhnya ia termasuk orang-orang yang sesat,</span></p><p><span>[a]</span><span> 9:144 ; 19:48 ; 60:5.</span></p>');
INSERT INTO `quran` VALUES (9391,2,26,88,'<p><span>Dan janganlah aku dihina pada hari, ketika mereka akan dibangkitkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2122">2122</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9392,2,26,89,'<p><span>Pada hari, ketika tidak bermanfaat harta dan anak laki-laki,</span></p>');
INSERT INTO `quran` VALUES (9393,2,26,90,'<p><span>[b]</span><span> Kecuali orang yang menghadap kepada Allah dengan hari yang sehat.</span></p><p><span>[b]</span><span> 17:85.</span></p>');
INSERT INTO `quran` VALUES (9394,2,26,91,'<p><span>Dan surga akan didekatkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2123">2123</a><span>]</span><span> bagi orang-orang yang bertakwa,</span></p>');
INSERT INTO `quran` VALUES (9395,2,26,92,'<p><span>Dan Jahannam akan ditampakkan dengan jelas kepada orang-orang yang sesat,</span></p>');
INSERT INTO `quran` VALUES (9396,2,26,93,'<p><span>Dan akan dikatakan kepada mereka, Di manakah mereka yang kamu sembah,</span></p>');
INSERT INTO `quran` VALUES (9397,2,26,94,'<p><span>Selain Allah ? Dapatkah mereka menolong kamu atau menolong diri mereka sendiri ? “</span></p>');
INSERT INTO `quran` VALUES (9398,2,26,95,'<p><span>[a]</span><span> Lalu mereka akan dijungkir-balikkan ke dalamnya, mereka dan orang-orang yang sesat,</span></p><p><span>[a]</span><span> 27:91.</span></p>');
INSERT INTO `quran` VALUES (9399,2,26,96,'<p><span>[b]</span><span> Dan lasykar-lasykar iblis semuanya.</span></p><p><span>[b]</span><span> 7:19 ; 38:86.</span></p>');
INSERT INTO `quran` VALUES (9400,2,26,97,'<p><span>Mereka berkata sedang mereka di dalamnya bertengkar satu sama lain,</span></p>');
INSERT INTO `quran` VALUES (9401,2,26,98,'<p><span>“Demi Allah, memang kami ada dalam kesesatan yang nyata,</span></p>');
INSERT INTO `quran` VALUES (9402,2,26,99,'<p><span>Ketika kami mempersamakan kamu dengan Tuhan sekalian alam.</span></p>');
INSERT INTO `quran` VALUES (9403,2,26,100,'<p><span>Dan tiada yang menyesatkan kami melainkan orang-orang yang berdosa.</span></p>');
INSERT INTO `quran` VALUES (9404,2,26,101,'<p><span>Dan kini tiada bagi kami orang-orang yang memberi syafaat,</span></p>');
INSERT INTO `quran` VALUES (9405,2,26,102,'<p><span>Dan tiada pula seorang sahabat karib.</span></p>');
INSERT INTO `quran` VALUES (9406,2,26,103,'<p><span>[c]</span><span> Maka sekiranya kami dapat kembali ke </span><span>dunia</span><span>, niscaya kami akan termasuk orang-orang yang beriman.</span></p><p><span>[c]</span><span> 2:168 ; 6:28 ; 23:100 ; 39:59.</span></p>');
INSERT INTO `quran` VALUES (9407,2,26,104,'<p><span>Sesungguhnya, dalam yang demikian terdapat tanda, tetapi kebanyakan mereka tidak beriman</span></p>');
INSERT INTO `quran` VALUES (9408,2,26,105,'<p><span>Dan sesungguhnya Tuhan-mu, Dia-lah Yang Maha Perkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9409,2,26,106,'<p><span>R. 6</span></p><p><span>Kaum Nuh telah mendustakan rasul-rasul,</span></p>');
INSERT INTO `quran` VALUES (9410,2,26,107,'<p><span>Ketika berkata kepada mereka saudara mereka Nuh, “Tidakkah kamu bertakwa ?</span></p>');
INSERT INTO `quran` VALUES (9411,2,26,108,'<p><span>Sesungguhnya aku bagimu seorang rasul yang terpercaya,</span></p>');
INSERT INTO `quran` VALUES (9412,2,26,109,'<p><span>Maka bertakwalah kepada Allah, dan taatlah kepadaku. </span><span>[2124]</span></p>');
INSERT INTO `quran` VALUES (9413,2,26,110,'<p><span>[a]</span><span> Dan tidaklah aku minta dari kamu upah untuk itu, sesungguhnya ganjaranku hanyalah pada Tuhan sekalian alam.</span></p><p><span>[a]</span><span> 10:73 ; 11:30.</span></p>');
INSERT INTO `quran` VALUES (9414,2,26,111,'<p><span>Maka bertakwalah kepada Allah, dan taatlah kepadaku.”</span></p>');
INSERT INTO `quran` VALUES (9415,2,26,112,'<p><span>Mereka berkata, “Patutkah kami beriman kepada engkau, padahal yang mengikuti engkau adalah orang-orang </span><span>[b]</span><span> yang paling hina.”</span></p><p><span>[b]</span><span> 11:28.</span></p>');
INSERT INTO `quran` VALUES (9416,2,26,113,'<p><span>Berkatalah </span><span>Nuh,</span><span> “Dan bagaimana aku mengetahui tentang apa yang dahulu mereka kerjakan ? </span></p>');
INSERT INTO `quran` VALUES (9417,2,26,114,'<p><span>Perhitungan mereka tiada lain kecuali pada Tuhan-ku, sekitanya kamu menyadari ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2125">2125</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9418,2,26,115,'<p><span>[a]</span><span> Dan aku tidak mengusir orang-orang Mukmin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2125a">2125a</a><span>] </span></p><p><span>[a]</span><span> 11:30.</span></p>');
INSERT INTO `quran` VALUES (9419,2,26,116,'<p><span>Tidak lain aku hanyalah seorang pemberi peringatan yang nyata.”</span></p>');
INSERT INTO `quran` VALUES (9420,2,26,117,'<p><span>Mereka berkata, “Sekiranya engkau tidak berhenti, hai Nuh, niscaya engkau akan termasuk orang-orang yang dirajam.”</span></p>');
INSERT INTO `quran` VALUES (9421,2,26,118,'<p><span>Berkatalah </span><span>Nuh,</span><span> “Ya Tuhan-ku, sesungguhnya kaumku telah mendustakan aku,</span></p>');
INSERT INTO `quran` VALUES (9422,2,26,119,'<p><span>Maka berilah keputusan antara kau dan mereka, keputusan yang pasti, dan selamatkanlah aku dan orang-orang mukmin yang ada besertaku.”</span></p>');
INSERT INTO `quran` VALUES (9423,2,26,120,'<p><span>[b]</span><span> Maka Kami menyelamatkannya dan orang-orang yang ada besertanya dalam bahtera yang penuh muatan.</span></p><p><span>[b]</span><span> 21:77 ; 37:77.</span></p>');
INSERT INTO `quran` VALUES (9424,2,26,121,'<p><span>[c]</span><span> Kemudian Kami tenggelamkan orang-orang yang bersisa di belakang.</span></p><p><span>[c]</span><span> 37:83 ; 54:12 </span><span>–</span><span> 13 ; 71:26.</span></p>');
INSERT INTO `quran` VALUES (9425,2,26,122,'<p><span>Sesungguhnya dalam hal itu ada Tanda, akan tetapi kebanyakan mereka tidak mau beriman.</span></p>');
INSERT INTO `quran` VALUES (9426,2,26,123,'<p><span>Dan sesungguhnya Tuhan-mu itu, Dia-lah Yang Maha Perkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9427,2,26,124,'<p><span>R. 7</span></p><p><span>[a]</span><span> Kaum </span><span>‘</span><span>Ad </span><span> telah mendustakan rasul-rasul.</span></p><p><span>[a]</span><span> 7:66 </span><span>–</span><span> 67.</span></p>');
INSERT INTO `quran` VALUES (9428,2,26,125,'<p><span>Ketika saudara mereka, Hud, berkata kepada mereka, “Tidaklah kamu bertakwa ?</span></p>');
INSERT INTO `quran` VALUES (9429,2,26,126,'<p><span>Sesungguhnya aku bagimu seorang rasul yang terpercaya.</span></p>');
INSERT INTO `quran` VALUES (9430,2,26,127,'<p><span>Maka bertakwalah kepada Allah, dan taatlah kepadaku ;</span></p>');
INSERT INTO `quran` VALUES (9431,2,26,128,'<p><span>[b]</span><span> Dan tidaklah aku minta dari kamu upah untuk itu, sesungguhnya ganjaranku hanyalah pada Tuhan sekalian alam.</span></p><p><span>[b]</span><span> 11:52.</span></p>');
INSERT INTO `quran` VALUES (9432,2,26,129,'<p><span>Apakah kamu mendirikan diatas setiap tanah tinggi bagunan yang sia-sia ?</span></p>');
INSERT INTO `quran` VALUES (9433,2,26,130,'<p><span>“Dan kamu mendirikan istana-istana seakan-akan kamu akan hidup selama-lamanya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2126">2126</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9434,2,26,131,'<p><span>Dan apabila kamu menangkap </span><span>seseorang,</span><span> kamu menangkap seperti orang-orang yang kejam.</span></p>');
INSERT INTO `quran` VALUES (9435,2,26,132,'<p><span>Maka bertakwalah kepada Allah, dan taatlah kepadaku ;</span></p>');
INSERT INTO `quran` VALUES (9436,2,26,133,'<p><span>Dan bertakwalah kepada Dzat Yang telah menolong kamu dengan apa yang kamu ketahui,</span></p>');
INSERT INTO `quran` VALUES (9437,2,26,134,'<p><span>Dia telah menolong kamu dengan binatang ternak dan anak laki-laki.</span></p>');
INSERT INTO `quran` VALUES (9438,2,26,135,'<p><span>“Dan kebun-kebun dan mata air-mata air.</span></p>');
INSERT INTO `quran` VALUES (9439,2,26,136,'<p><span>Sesungguhnya aku khawatirkan atasmu azab Hari yang besar.”</span></p>');
INSERT INTO `quran` VALUES (9440,2,26,137,'<p><span>Mereka berkata, “Sama saja bagi kami apakah engkau menasihati atau engkau tidak termasuk orang-orang yang menasihati,</span></p>');
INSERT INTO `quran` VALUES (9441,2,26,138,'<p><span>“Ini tidak lain melainkan adat-kebiasaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2127">2127</a><span>] </span><span>orang-orang dahulu,</span></p>');
INSERT INTO `quran` VALUES (9442,2,26,139,'<p><span>Dan kami tidak akan di azab.”</span></p>');
INSERT INTO `quran` VALUES (9443,2,26,140,'<p><span>[a]</span><span> Maka orang-orang itu mendustakannya, dan Kami binasakan mereka. Sesungguhnya dalam hal yang demikian itu ada Tanda, tetapi kebanyakan mereka tidak beriman.</span></p><p><span>[a]</span><span> 7:73 ; 50:15.</span></p>');
INSERT INTO `quran` VALUES (9444,2,26,141,'<p><span>Dan sesungguhnya Tuhan-mu, Dia-lah Yang Maha Perkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9445,2,26,142,'<p><span>R. 8</span></p><p><span>[b]</span><span> </span><span>Kaum</span><span> Tsamud </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2128">2128</a><span>]</span><span> telah mendustakan rasul-rasul,</span></p><p><span>[b]</span><span> 7:74 ; 11:62 </span><span>–</span><span> 63 ; 27:46.</span></p>');
INSERT INTO `quran` VALUES (9446,2,26,143,'<p><span>Ketika berkata kepada mereka, saudara mereka, Shaleh, “Tidaklah kamu bertakwa ? </span></p>');
INSERT INTO `quran` VALUES (9447,2,26,144,'<p><span>Sesungguhnya aku bagimu seorang rasul, yang terpercaya,</span></p>');
INSERT INTO `quran` VALUES (9448,2,26,145,'<p><span>Maka bertakwalah kepada Allah, dan taatlah kepadaku.</span></p>');
INSERT INTO `quran` VALUES (9449,2,26,146,'<p><span>[c]</span><span> Dan tidaklah aku minta upah dari kamu untuk itu, sesungguhnya ganjaranku hanyalah pada Tuhan sekalian alam.</span></p><p><span>[c]</span><span> 11:52</span></p>');
INSERT INTO `quran` VALUES (9450,2,26,147,'<p><span>“Apakah kamu akan dibiarkan </span><span>tinggal </span><span> di sini </span><span>dengan aman</span><span>.</span></p>');
INSERT INTO `quran` VALUES (9451,2,26,148,'<p><span>Di tengah kebun-kebun dan mataair-mataair,</span></p>');
INSERT INTO `quran` VALUES (9452,2,26,149,'<p><span>Dan ladang-ladang, dan pohon-pohon kurma dengan mayangnya </span><span>yang </span><span> hampir patah ?</span></p>');
INSERT INTO `quran` VALUES (9453,2,26,150,'<p><span>[a]</span><span> Dan kamu memahat sebagian gunung-gunung, untuk rumah-rumah sebagai kemegahan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2128a">2128a</a><span>]</span></p><p><span>[a]</span><span> 7:75 ; 15:82</span></p>');
INSERT INTO `quran` VALUES (9454,2,26,151,'<p><span>Maka bertakwalah kepada Allah, dan taatlah kepadaku.</span></p>');
INSERT INTO `quran` VALUES (9455,2,26,152,'<p><span>Dan janganlah kamu taati perintah orang-orang yang melampaui batas.</span></p>');
INSERT INTO `quran` VALUES (9456,2,26,153,'<p><span>[b]</span><span> Orang-orang yang mengadakan kerusuhan di bumi, dan mereka tidak mengadakan perbaikan.</span></p><p><span>[b]</span><span> 27:49.</span></p>');
INSERT INTO `quran` VALUES (9457,2,26,154,'<p><span>Mereka berkata, “Engkau hanya diantara orang-orang yang diberi makan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2129">2129</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9458,2,26,155,'<p><span>Engkau tidak lain melainkan seorang laki-laki seperti kami. Maka datangkanlah satu Tanda, jika engkau termasuk orang-orang benar.”</span></p>');
INSERT INTO `quran` VALUES (9459,2,26,156,'<p><span>[a]</span><span> Berkatalah </span><span>Shaleh,</span><span> “Inilah seekor unta betina ; ia mempunyai gilirannya minum, dan kamu pun mempunyai giliran minum pada hari yang telah ditentukan ;</span></p><p><span>[a]</span><span> 7:74 ; 11:65 ; 17:60 ; 54:28 ; 91:14.</span></p>');
INSERT INTO `quran` VALUES (9460,2,26,157,'<p><span>[b]</span><span> Dan janganlah kamu menyentuhnya dengan jahat, jangan-jangan kamu ditimpa azab Hari yang besar.”</span></p><p><span>[b]</span><span> 26:156.</span></p>');
INSERT INTO `quran` VALUES (9461,2,26,158,'<p><span>[c]</span><span> Maka mereka itu memotong urat kakinya, maka mereka menyesal.</span></p><p><span>[c]</span><span> 7:78 ; 11:66 ; 54:30 ; 91:15.</span></p>');
INSERT INTO `quran` VALUES (9462,2,26,159,'<p><span>[d]</span><span> Maka mereka telah ditimpa azab. Sesungguhnya dalam yang demikian itu ada Tanda, tetapi kebanyakan mereka tidak beriman.</span></p><p><span>[d]</span><span> 7:79 ; 11:68 ; 54:32.</span></p>');
INSERT INTO `quran` VALUES (9463,2,26,160,'<p><span>Dan sesungguhnya Tuhan engkau, Dia-lah Yang Maha Perkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9464,2,26,161,'<p><span>R. 9</span></p><p><span>[e]</span><span> Kaum Luth telah mendustakan rasul-rasul.</span></p><p><span>[e]</span><span> 7:81 </span><span>–</span><span> 83 ; 54:34.</span></p>');
INSERT INTO `quran` VALUES (9465,2,26,162,'<p><span>Ketika berkata kepada mereka, saudara mereka. Luth, “Tidaklah kamu bertakwa ?</span></p>');
INSERT INTO `quran` VALUES (9466,2,26,163,'<p><span>“Sesungguhnya aku bagimu seorang rasul, yang terpercaya,</span></p>');
INSERT INTO `quran` VALUES (9467,2,26,164,'<p><span>“Maka bertakwalah kepada Allah, dan taatlah kepadaku.</span></p>');
INSERT INTO `quran` VALUES (9468,2,26,165,'<p><span>Dan aku tidaklah meminta upah dari kamu untuk itu, sesungguhnya ganjaranku hanyalah pada Tuhan sekalian alam.</span></p>');
INSERT INTO `quran` VALUES (9469,2,26,166,'<p><span>[a]</span><span> “Apakah kamu mendatangi laki-laki dari antara sekalian makhluk ?</span></p><p><span>[a]</span><span> 7:82 ; 27:56 ; 29:29 </span><span>–</span><span> 30.</span></p>');
INSERT INTO `quran` VALUES (9470,2,26,167,'<p><span>“Dan kamu meninggalkan apa-apa yang diciptakan bagimu oleh Tuhan kamu dari istri- istrimu. Tidak, bahkan kamu adalah suatu kaum yang melanggar batas.”</span></p>');
INSERT INTO `quran` VALUES (9471,2,26,168,'<p><span>[b]</span><span> Mereka berkata, “Sekiranya engkau tidak berhenti wahai Luth, niscaya engkau akan termasuk orang-orang yang di usir.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2129a">2129a</a><span>]</span></p><p><span>[b]</span><span> 7:83 ; 27:57.</span></p>');
INSERT INTO `quran` VALUES (9472,2,26,169,'<p><span>Berkatalah </span><span>Luth,</span><span> “Sesungguhnya aku benci kepada perbuatanmu.</span></p>');
INSERT INTO `quran` VALUES (9473,2,26,170,'<p><span>“Ya Tuhan-ku, selamatkanlah aku dan keluargaku dari apa yang mereka kerjakan.”</span></p>');
INSERT INTO `quran` VALUES (9474,2,26,171,'<p><span>[a]</span><span> Lalu Kami selamatkan dia dan seluruh keluarganya</span></p><p><span>[a]</span><span> 15:60 ; 29:33 ; 37:135 ; 51:36.</span></p>');
INSERT INTO `quran` VALUES (9475,2,26,172,'<p><span>[b]</span><span> Kecuali seorang perempuan tua yang termasuk orang-orang yang tertinggal di belakang.</span></p><p><span>[b]</span><span> 7:84 ; 11:82 ; 15:61 ; 27:58 ; 37:136.</span></p>');
INSERT INTO `quran` VALUES (9476,2,26,173,'<p><span>[c]</span><span> Kemudian Kami binasakan yang lain.</span></p><p><span>[c] </span><span>37:137.</span></p>');
INSERT INTO `quran` VALUES (9477,2,26,174,'<p><span>[d]</span><span> Dan Kami hujani atas mereka hujan </span><span>batu,</span><span> dan sangat buruk hujan </span><span>batu</span><span> yang </span><span>turun</span><span> atas orang-orang yang telah diperingatkan itu.</span></p><p><span>[d]</span><span> 7:85 ; 25:41 ; 27:59.</span></p>');
INSERT INTO `quran` VALUES (9478,2,26,175,'<p><span>Sesungguhnya dalam hal itu terdapat Tanda, tetapi kebanyakan mereka tidak beriman.</span></p>');
INSERT INTO `quran` VALUES (9479,2,26,176,'<p><span>Dan sesungguhnya Tuhan engkau, Dia-lah Yang Maha Perkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9480,2,26,177,'<p><span>R. 10</span></p><p><span>[e]</span><span> Penghuni hutan Aikah telah mendustakan rasul-rasul.</span></p><p><span>[e]</span><span> 15:79 ; 38:14 ; 50:15.</span></p>');
INSERT INTO `quran` VALUES (9481,2,26,178,'<p><span>[f]</span><span> Ketika Syu</span><span>’</span><span>aib berkata kepada mereka, “Tidaklah kamu akan bertakwa ?</span></p><p><span>[f]</span><span> 7:86 ; 11:85.</span></p>');
INSERT INTO `quran` VALUES (9482,2,26,179,'<p><span>“Sesungguhnya aku bagimu seorang rasul, yang terpercaya,</span></p>');
INSERT INTO `quran` VALUES (9483,2,26,180,'<p><span>“Maka bertakwalah kepada Allah, dan taatlah kepadaku.</span></p>');
INSERT INTO `quran` VALUES (9484,2,26,181,'<p><span>“Dan tidaklah aku minta upah dari kamu untuk itu, sesungguhnya ganjaranku hanyalah pada Tuhan sekalian alam.</span></p>');
INSERT INTO `quran` VALUES (9485,2,26,182,'<p><span>[a]</span><span> Sempurnakanlah sukatan dan janganlah kamu termasuk orang-orang yang merugikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2130">2130</a><span>]</span></p><p><span>[a]</span><span> 11:85 ; 17:36 ; 55:10 ; 83:2 </span><span>–</span><span> 4.</span></p>');
INSERT INTO `quran` VALUES (9486,2,26,183,'<p><span>[b]</span><span> “Dan timbanglah dengan timbangan yang lurus.</span></p><p><span>[b]</span><span> 11:85.</span></p>');
INSERT INTO `quran` VALUES (9487,2,26,184,'<p><span>[c]</span><span> “Dan janganlah kamu merugikan manusia pada barang-barang mereka dan janganlah kamu berjalan di bumi untuk menimbulkan kerusuhan ;</span></p><p><span>[c]</span><span> 7:86 ; 11:86.</span></p>');
INSERT INTO `quran` VALUES (9488,2,26,185,'<p><span>“Dan bertakwalah kepada Yang telah menciptakan kamu dan umat-umat yang terdahulu.”</span></p>');
INSERT INTO `quran` VALUES (9489,2,26,186,'<p><span>Mereka berkata, “Sesungguhnya engkau hanyalah di antara </span><span>[d]</span><span> orang yang diberi makan,”</span></p><p><span>[d]</span><span> 26:154.</span></p>');
INSERT INTO `quran` VALUES (9490,2,26,187,'<p><span>“Dan engkau hanya seorang laki-laki seperti kami, dan kami menganggap engkau dari para pendusta.”</span></p>');
INSERT INTO `quran` VALUES (9491,2,26,188,'<p><span>“Maka jatuhkanlah kepada kami gumpalan-gumpalan awan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2131">2131</a><span>]</span><span> jika engkau termasuk orang-orang yang benar.”</span></p>');
INSERT INTO `quran` VALUES (9492,2,26,189,'<p><span>Berkatalah </span><span>Syu</span><span>’</span><span>aib,</span><span> “Tuhan-ku lebih mengetahui apa yang kamu kerjakan.”</span></p>');
INSERT INTO `quran` VALUES (9493,2,26,190,'<p><span>Maka mereka mendustakannya. </span><span>[a]</span><span> Lalu menangkap mereka azab hari yang menaungi. Sesungguhnya itu adalah azab hari yang sangat besar.</span></p><p><span>[a]</span><span> 7:92 ; 11:95 ; 29:38.</span></p>');
INSERT INTO `quran` VALUES (9494,2,26,191,'<p><span>Sesungguhnya dalam yang demikian itu terdapat Tanda, tetapi kebanyakan mereka tidak beriman.</span></p>');
INSERT INTO `quran` VALUES (9495,2,26,192,'<p><span>Dan sesungguhnya Tuhan nengkau, Dia-lah Yang Maha Perkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9496,2,26,193,'<p><span>R. 11</span></p><p><span>[b]</span><span> Dan sesungguhnya </span><span>Alquran</span><span> ini diturunkan oleh Tuhan sekalian alam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2132">2132</a><span>]</span></p><p><span>[b]</span><span> 20:5 ; 56:81.</span></p>');
INSERT INTO `quran` VALUES (9497,2,26,194,'<p><span>Telah turun bersamanya </span><span>[a]</span><span> ruh yang terpercaya, </span><span>Jibrail.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2133">2133</a><span>]</span></p><p><span>[a]</span><span> 2:98 ; 16:103.</span></p>');
INSERT INTO `quran` VALUES (9498,2,26,195,'<p><span>Atas kalbu engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2134">2134</a><span>]</span><span> supaya engkau termasuk di antara para pemberi peringatan.</span></p>');
INSERT INTO `quran` VALUES (9499,2,26,196,'<p><span>[b]</span><span> Dengan bahasa Arab yang jelas.</span></p><p><span>[b]</span><span> 16:104 ; 41:45 ; 46:13.</span></p>');
INSERT INTO `quran` VALUES (9500,2,26,197,'<p><span>Dan sesungguhnya </span><span>Alquran</span><span> </span><span>telah tercantum </span><span>dalam kitab-kitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2135">2135</a><span>]</span><span> terdahulu.</span></p>');
INSERT INTO `quran` VALUES (9501,2,26,198,'<p><span>Dan tidakkah ini merupakan satu Tanda bagi mereka, bahwa ulama-ulama Bani Israil mengetahuinya ?</span></p>');
INSERT INTO `quran` VALUES (9502,2,26,199,'<p><span>Dan sekiranya Kami menurunkannya kepada salah seorang di antara orang yang bukan Arab.</span></p>');
INSERT INTO `quran` VALUES (9503,2,26,200,'<p><span>Dan ia membacakannya kepada mereka, mereka tidak akan beriman kepadanya.</span></p>');
INSERT INTO `quran` VALUES (9504,2,26,201,'<p><span>Demikianlah telah Kami masukkan hal itu dalam hati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2136">2136</a><span>]</span><span> orang-orang berdosa.</span></p>');
INSERT INTO `quran` VALUES (9505,2,26,202,'<p><span>Mereka tidak akan beriman kepadanya, sebelum mereka melihat azab pedih,</span></p>');
INSERT INTO `quran` VALUES (9506,2,26,203,'<p><span>Yang akan datang kepada mereka dengan tiba-tiba, sedang mereka tidak menyadari,</span></p>');
INSERT INTO `quran` VALUES (9507,2,26,204,'<p><span>Dan mereka akan berkata, “Apakah kami akan diberi tangguh ?”</span></p>');
INSERT INTO `quran` VALUES (9508,2,26,205,'<p><span>[a]</span><span> Apakah azab Kami diminta dengan segera ?</span></p><p><span>[a]</span><span> 22:48 ; 27:72 </span><span>–</span><span> 73 ; 51:15.</span></p>');
INSERT INTO `quran` VALUES (9509,2,26,206,'<p><span>[b]</span><span> Apkah engkau mengetahui bahwa Kami memberikan kesenangan kepada mereka bertahun- tahun,</span></p><p><span>[b] </span><span>20:132 ; 28:62.</span></p>');
INSERT INTO `quran` VALUES (9510,2,26,207,'<p><span>Kemudian datang kepada mereka </span><span>azab,</span><span> yang telah dijanjikan kepada mereka,</span></p>');
INSERT INTO `quran` VALUES (9511,2,26,208,'<p><span>Tidak ada gunanya bagi mereka apa yang mereka telah menikmatinya.</span></p>');
INSERT INTO `quran` VALUES (9512,2,26,209,'<p><span>[a]</span><span> Dan tidak Kami binasakan suatu kota, melainkan telah ada baginya orang-orang yang memberi peringatan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2137">2137</a><span>]</span></p><p><span>[a]</span><span> 6:132 ; 11:118 ; 20:135 ; 28:60.</span></p>');
INSERT INTO `quran` VALUES (9513,2,26,210,'<p><span>Supaya</span><span> mereka mendapat peringatan dan Kami tidak berlaku aniaya.</span></p>');
INSERT INTO `quran` VALUES (9514,2,26,211,'<p><span>Dan bukanlah syaitan-syaitan yang membawanya </span><span>Alquran</span><span> turun ;</span></p>');
INSERT INTO `quran` VALUES (9515,2,26,212,'<p><span>Dan tidak layak untuk mereka dan tidak </span><span>pula </span><span> mereka mempunyai kekuatan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2138">2138</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9516,2,26,213,'<p><span>[b]</span><span> Sesungguhnya mereka itu telah dihalangi dari mendengar </span><span>Kalam Ilahi.</span><span> </span></p><p><span>[b]</span><span> 11:21.</span></p>');
INSERT INTO `quran` VALUES (9517,2,26,214,'<p><span>[a]</span><span> Maka janganlah engkau menyeru bersama Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2139">2139</a><span>]</span><span> tuhan lain, karena engkau akan menjadi orang-orang yang diazab.</span></p><p><span>[a]</span><span> 17:23, 40 ; 28:89.</span></p>');
INSERT INTO `quran` VALUES (9518,2,26,215,'<p><span>Dan berilah peringatan kepada keluargamu yang paling dekat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2140">2140</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9519,2,26,216,'<p><span>[b]</span><span> Dan rendahkanlah sayap kasih-sayangmu kepada orang-orang yang mengikuti engkau di antara orang-orang mukmin.</span></p><p><span>[b]</span><span> 15:89.</span></p>');
INSERT INTO `quran` VALUES (9520,2,26,217,'<p><span>Maka jika mereka mendurhakai engkau, maka katakanlah, “Sesungguhnya aku bebas dari segala apa yang kamu kerjakan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2140a">2140a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9521,2,26,218,'<p><span>[c]</span><span> Dan bertakwakallah kepada Yang Maha Perkasa, Maha Penyayang.</span></p><p><span>[c]</span><span> 25:59 ; 33:49.</span></p>');
INSERT INTO `quran` VALUES (9522,2,26,219,'<p><span>[d]</span><span> Yang melihat engkau bila engkau berdiri </span><span>shalat.</span></p><p><span>[d]</span><span> 73:21.</span></p>');
INSERT INTO `quran` VALUES (9523,2,26,220,'<p><span>Dan </span><span>melihat</span><span> gerakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2141">2141</a><span>]</span><span> engkau di antara orang-orang yang sujud. </span></p>');
INSERT INTO `quran` VALUES (9524,2,26,221,'<p><span>Sesungguhnya Dia-lah Yang Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9525,2,26,222,'<p><span>Maukah Aku beritahukan kamu kepada siapa syaitan-syaitan itu turun ?</span></p>');
INSERT INTO `quran` VALUES (9526,2,26,223,'<p><span>Mereka turun kepada tiap-tiap pendusta yang berdosa.</span></p>');
INSERT INTO `quran` VALUES (9527,2,26,224,'<p><span>Mereka menghadapkan telinga </span><span>ke arah langit,</span><span> dan kebanyakan mereka pendusta.</span></p>');
INSERT INTO `quran` VALUES (9528,2,26,225,'<p><span>Dan penyair-penyair itu mengikuti mereka yang sesat.</span></p>');
INSERT INTO `quran` VALUES (9529,2,26,226,'<p><span>Tidakkah engkau memaklumi bahwa mereka itu di dalam setiap lembah, berjalan kian kemari tanpa tujuan.</span></p>');
INSERT INTO `quran` VALUES (9530,2,26,227,'<p><span>Dan sesungguhnya mereka itu mengatakan apa yang mereka tidak lakukan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2142">2142</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9531,2,26,228,'<p><span>Kecuali orang-orang beriman dan beramal shaleh dan banyak-banyak mengingat Allah, dan mereka hanya membela diri, setelah mereka dianiaya. Dan pasti mengetahui orang-orang yang aniaya itu ke tempat mana mereka akan kembali.</span></p>');
INSERT INTO `quran` VALUES (9532,2,27,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9533,2,27,2,'<p><span>[b]</span><span> Maha Suci, Maha Mendengar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2143">2143</a><span>] [c]</span><span> Inilah ayat-ayat Alquran dan Kitab yang menjelaskan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2144">2144</a><span>]</span></p><p><span>[b]</span><span> 26:2 ; 27:2 ; 28:2.</span></p><p><span>[c]</span><span> 15:2 ; 26:3 ; 28:3.</span></p>');
INSERT INTO `quran` VALUES (9534,2,27,3,'<p><span>[d]</span><span> Petunjuk dan khabar suka bagi orang-orang beriman.</span></p><p><span>[d]</span><span> 2:3 ; 10:58 ; 12:112 ; 31:4.</span></p>');
INSERT INTO `quran` VALUES (9535,2,27,4,'<p><span>[e]</span><span> Orang-orang yang mendirikan shalat dan membayar zakat dan kepada </span><span>apa-apa yang telah dijanjikan </span><span> akan datang, mereka pun yakin.</span></p><p><span>[e]</span><span> 2:4 ; 8:4 ; 14:32 ; 31:5.</span></p>');
INSERT INTO `quran` VALUES (9536,2,27,5,'<p><span>[f]</span><span> Sesungguhnya orang-orang yang tidak beriman kepada akhirat, Kami telah menampak- kan indah bagi mereka amal mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2145">2145</a><span>]</span><span> maka mereka itu berkelana bingung.</span></p><p><span>[f]</span><span> 16:23 ; 17:11 ; 34:9.</span></p>');
INSERT INTO `quran` VALUES (9537,2,27,6,'<p><span>Mereka itulah orang-orang yang bagi mereka ada azab buruk dan mereka di akhirat menjadi orang-orang yang paling rugi.</span></p>');
INSERT INTO `quran` VALUES (9538,2,27,7,'<p><span>Dan sesungguhnya, engkau menerima Alquran dari Yang Maha Bijaksana, Maha Mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2146">2146</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9539,2,27,8,'<p><span>[a]</span><span> </span><span>Ingatlah</span><span> ketika Musa berkata kepada keluarganya, “Sesungguhnya aku telah melihat suatu api, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2147">2147</a><span>]</span><span> Aku segera akan membawa bagimu darinya khabar, atau aku akan membawa bagimu bara yang menyala-nyala, supaya kamu dapat berdiang diri.”</span></p><p><span>[a]</span><span> 20:11 ; 28:30.</span></p>');
INSERT INTO `quran` VALUES (9540,2,27,9,'<p><span>Maka tatkala ia sampai kepadanya, ia dipanggil </span><span>oleh suatu suara,</span><span> “Beberkatlah siapa yang ada dalam api, dan orang yang ada di sekelilingnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2148">2148</a><span>]</span><span> Dan Maha Suci Allah, Tuhan sekalian alam.</span></p>');
INSERT INTO `quran` VALUES (9541,2,27,10,'<p><span>“Ya Musa, </span><span>[a]</span><span> sesungguhnya Akulah Allah, Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a]</span><span> 20:12 </span><span>–</span><span> 13 ; 28:31.</span></p>');
INSERT INTO `quran` VALUES (9542,2,27,11,'<p><span>[b]</span><span> Dan lemparkanlah tongkatmu.” Maka ketika ia melihatnya </span><span>tongkat </span><span> itu bergerak bagaikan seekor ular kecil </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2148a">2148a</a><span>]</span><span> berbaliklah ia kebelakang dan tidak menoleh lagi. </span><span>Kami berfirman,</span><span> “ Hai Musa, jangan takut. Sesungguhnya rasul-rasul tidak takut di hadapan-Ku,</span></p><p><span>[b]</span><span> 7:118 ; 20:28:32.</span></p>');
INSERT INTO `quran` VALUES (9543,2,27,12,'<p><span>“Kecuali siapa yang berbuat aniaya, kemudian menukar kebaikan sesudah keburukan ; maka sesungguhnya Aku Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9544,2,27,13,'<p><span>[a]</span><span> Dan masukkanlah tangan engkau ke dalam kantong baju engkau, ia akan keluar putih bukan karena penyakit. Inilah di antara sembilan Tanda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2149">2149</a><span>]</span><span> untuk Firaun dan kaumnya. Sesungguhnya mereka kaum yang durhaka.”</span></p><p><span>[a]</span><span> 20:23, 24 ; 17:102.</span></p>');
INSERT INTO `quran` VALUES (9545,2,27,14,'<p><span>Tetapi tatkala datang kepada mereka Tanda-tanda Kami tang tampak jelas, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2150">2150</a><span>]</span><span> mereka berkata, </span><span>[b]</span><span> “Ini sihir yang nyata.”</span></p><p><span>[b]</span><span> 43:31 ; 61:7.</span></p><p><span>[c]</span><span> 2:88.</span></p>');
INSERT INTO `quran` VALUES (9546,2,27,15,'<p><span>[c]</span><span> Dan mereka menolaknya dengan aniaya dan sombong sementara hati mereka telah meyakininya. Maka lihatlah, betapa akibatnya orang-orang yang berbuat kerusuhan.</span></p><p><span>[c]</span><span> 2:88.</span></p>');
INSERT INTO `quran` VALUES (9547,2,27,16,'<p><span>R. 2</span></p><p><span>Dan sesungguhnya telah Kami berikan ilmu kepada Daud dan Sulaiman, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2151">2151</a><span>]</span><span> dan keduanya berkata, “Segala pugi bagi Allah, Dzat Yang telah memuliakan kami di atas kebanyakan dari hamba-hamba-Nya yang beriman.”</span></p>');
INSERT INTO `quran` VALUES (9548,2,27,17,'<p><span>Dan Sulaiman telah mewarisi Daud. Dan ia berkata, “Hai manusia, kami telah diajari bahasa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2152">2152</a><span>]</span><span> burung ; dan kami telah dianugerahi segala sesuatu ; Sesungguhnya inilah karunia yang nyata.”</span></p>');
INSERT INTO `quran` VALUES (9549,2,27,18,'<p><span>[a]</span><span> Dan dihimpunkan bagi Sulaiman lasykar-lasykarnya bersama-sama, terdiri dari jin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2153">2153</a><span>]</span><span> dan manusia dan burung-burung, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2154">2154</a><span>]</span><span> dan mereka dijadikan pasukan-pasukan yang tertib. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2155">2155</a><span>]</span></p><p><span>[a]</span><span> 38:19 </span><span>–</span><span> 20.</span></p>');
INSERT INTO `quran` VALUES (9550,2,27,19,'<p><span>Hingga ketika mereka sampai ke lembah An-Naml, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2156">2156</a><span>]</span><span> seorang dari kaum Naml berkata, “Hai bangsa Naml, masuklah kamu ke dalam tempat tinggalmu, jangan-jangan Sulaiman dan lasykarnya akan menghancurkan kamu, sedang mereka tidak menyadari. “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2157">2157</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9551,2,27,20,'<p><span>Maka ia, Sulaiman, tersenyum sambil tertawa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2158">2158</a><span>]</span><span> mendengar perkataannya dan berkata, “Ya Tuhan-ku, anugerahkanlah kepada-ku taufik untuk mensyukuri nikmat Engkau yang telah Engkau anugerahkan kepadaku dan kepada orang-tuaku, dan untuk berbuat amal shaleh yang Engkau ridhai, dan masukkanlah aku dengan rahmat Engkau di antara hamba-hamba Engkau yang shaleh.”</span></p>');
INSERT INTO `quran` VALUES (9552,2,27,21,'<p><span>Dan ia memeriksa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2159">2159</a><span>]</span><span> burung-burung itu, kemudian ia berkata, “Mengapa aku tidak melihat Hud-hud ? Apakah ia </span><span>dengan sengaja tidak hadir ?</span><span> </span></p>');
INSERT INTO `quran` VALUES (9553,2,27,22,'<p><span>‘</span><span>Nicaya akan kuhukum </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2160">2160</a><span>]</span><span> dia dengan azab yang keras, atau akan kubunuh dia, atau ia datang kepadaku dengan alasan yang jelas.”</span></p>');
INSERT INTO `quran` VALUES (9554,2,27,23,'<p><span>Maka tidak lama ia menunggu </span><span>maka, Hud-hud datang </span><span> dan berkata, “Aku telah mengetahui apa yang engkau belum mengetahui ; dan apa telah datang kepada engkau dari </span><span>negeri </span><span> </span><span>kaum </span><span> Saba dengan kabar yang yakin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2161">2161</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9555,2,27,24,'<p><span>“Aku dapati di sana seorang wanita memerintah atas mereka, dan ia telah dianugerahi segala sesuatu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2162">2162</a><span>]</span><span> dan ia mempunyai singgasana besar.</span></p>');
INSERT INTO `quran` VALUES (9556,2,27,25,'<p><span>“Aku dapati dia dan kaumnya sujud kepada matahari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2163">2163</a><span>]</span><span> selain Allah ; dan </span><span>[a]</span><span> syaitan telah menampakkan indah bagi mereka amal-amal mereka, dan dia menghalangi mereka dari jalan </span><span>yang benar</span><span> sehingga mereka tidak mendapat petunjuk.</span></p><p><span>[a]</span><span> 8:49 ; 16:64 ; 35:9.</span></p>');
INSERT INTO `quran` VALUES (9557,2,27,26,'<p><span>“Mereka bersikeras tidak bersujud kepada Allah, Yang mengeluarkan yang tersembunyi di seluruh langit dan bumi, dan </span><span>[b]</span><span> Yang Mengetahui apa-apa yang kamu sembunyikan dan apa-apa yang kamu zahirkan.</span></p><p><span>[b]</span><span> 2:78 ; 16:20 ; 64:5.</span></p>');
INSERT INTO `quran` VALUES (9558,2,27,27,'<p><span>“Allah, tiada Tuhan selain Dia, Tuhan </span><span>‘</span><span>Arasy Yang Maha Agung.”</span></p>');
INSERT INTO `quran` VALUES (9559,2,27,28,'<p><span>Ia, </span><span>Sulaiman,</span><span> berkata, “Kami akan melihat, apakah engkau telah berkata benar atau engkau di antara orang-orang yang dusta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2164">2164</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9560,2,27,29,'<p><span>“Pergilah engkau dengan membawa suratku ini, dan sampaikanlah kepada mereka ; kemudian undurlah dari mereka dan perhatikanlah apa yang mereka jawab.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2165">2165</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9561,2,27,30,'<p><span>Berkatalah ia, </span><span>ratu itu,</span><span> “Hai pembesar-pembesar, sesungguhnya telah disampaikan kepada ku surat yang mulia,</span></p>');
INSERT INTO `quran` VALUES (9562,2,27,31,'<p><span>“Sesungguhnya surat itu dari Sulaiman, dan sesungguhnya surat itu </span><span>berbunyi,</span><span> “Dengan nama Allah, Maha Pemurah, Maha Penyayang.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2166">2166</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9563,2,27,32,'<p><span>“Janganlah berlaku sombong terhadapku, dan datanglah kepadaku dengan menyerahkan diri.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2167">2167</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9564,2,27,33,'<p><span>R. 3</span></p><p><span>Berkatalah ia, </span><span>ratu itu,</span><span> “Hai pembesar-pembesar, berikanlah fatwa kepadaku mengenai urusanku ini. Aku tidak pernah memutuskan sesuatu perkara sebelum kamu hadir dihadapan ku.”</span></p>');
INSERT INTO `quran` VALUES (9565,2,27,34,'<p><span>Mereka berkata, “Kita mempunyai kekuatan dan kita mempunyai keberanian yang hebat dalam peperangan, tetapi memberi perintah itu ada pada engkau ; maka pertimbangkanlah apa yang engkau akan perintahkan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2168">2168</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9566,2,27,35,'<p><span>Berkatalah ia, </span><span>ratu itu,</span><span> “Sesungguhnya raja-raja, apabila mereka memasuki suatu kota, mereka merusakkannya, dan penduduknya yang mulia mereka jadikan orang-orang yang hina. Dan demikianlah selalu mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (9567,2,27,36,'<p><span>“Sesungguhnya aku akan mengirimkan kepada mereka hadiah dan akan menanti jawaban apa yang akan dibawa kembali oleh para utusan itu.</span></p>');
INSERT INTO `quran` VALUES (9568,2,27,37,'<p><span>Maka ketika ia datang </span><span>membawa</span><span> </span><span>hadiah</span><span> ke hadapan Sulaiman, ia berkata, “Apakah kamu menolongku dengan harta ? Tetapi apa yang Allah berikan kepadaku itu lebih baik daripada apa yang Dia berikan kepadamu. Bahkan kamu merasa sangat bangga dengan hadiahmu itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2169">2169</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9569,2,27,38,'<p><span>“</span><span>Hai, Hud-hud </span><span> kembalilah kepada mereka, kami pasti akan datang kepada mereka dengan lasykar-lasykar yang mereka tidak sanggup menghadapinya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2170">2170</a><span>]</span><span> dan kami pasti mengusir mereka darinya dengan terhina dan mereka menjadi orang nista.”</span></p>');
INSERT INTO `quran` VALUES (9570,2,27,39,'<p><span>Ia berkata, “Hai pembesar-pembesar, siapakah dari antara kamu akan membawa kepadaku singgasana </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2171">2171</a><span>]</span><span> sebelum mereka datang kepadaku menyerahkan diri ? “</span></p>');
INSERT INTO `quran` VALUES (9571,2,27,40,'<p><span>Berkata seorang hulubalang yang gagah-perkasa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2172">2172</a><span>]</span><span> di antara para jin, “Aku akan membawanya kepada engkau sebelum engkau berdiri dari tempat engkau ; dan sesungguhnya atas itu aku mempunyai kekuatan, terpercaya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2173">2173</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9572,2,27,41,'<p><span>Berkata orang yang mempunyai pengetahuan tentang buku, “Aku akan mendatangkannya kepada engkau dalam sekejam mata.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2174">2174</a><span>]</span><span> Maka ketika ia, </span><span>Sulaiman, </span><span>melihatnya telah ada di hadapannya, ia berkata, “Ini adalah dari karunia Tuhan-ku, supaya Dia mengujiku, apakah aku bersyukur atau tidak bersyukur. Dan barangsiapa yang bersyukur maka sesungguhnya ia bersyukur untuk dirinya sendiri; dan barangsiapa tidak bersyukur, maka sesungguhnya Tuhan-ku Mahacukup, Mahamulia.”</span></p>');
INSERT INTO `quran` VALUES (9573,2,27,42,'<p><span>Ia berkata, “Buatlah tidak berharga untuk dia singgasana itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2175">2175</a><span>]</span><span> kita lihat apakah ia mendapat petunjuk atau ia termasuk orang-orang yang tidak mendapat petunjuk.”</span></p>');
INSERT INTO `quran` VALUES (9574,2,27,43,'<p><span>Dan ketika ia, </span><span>ratu itu,</span><span> datang, dikatakan </span><span>kepadanya</span><span> “Serupa inikah singgasana engkau ? “ Ia menjawab, “Ini seolah-olah sama seperti itu. Dan kami telah diberi pengetahuan sebelumnya, dan kami adalah orang-orang yang telah menyerahkan diri.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2176">2176</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9575,2,27,44,'<p><span>Dan </span><span>Sulaiman </span><span> telah melarang </span><span>ratu</span><span> menyembah selain dari Allah. Sesungguhnya ia dari kaum kafir.</span></p>');
INSERT INTO `quran` VALUES (9576,2,27,45,'<p><span>Dikatakan kepada dia, “Masuklah ke istana.” Dan ketika ia melihatnya, ia mengira air yang dalam, dan ia menyingkapkan kain dari betisnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2177">2177</a><span>]</span><span> Ia, </span><span>Sulaiman, </span><span>berkata, “Sesungguh nya ini istana yang berlantaikan jubin dari kaca.” Berkatalah </span><span>ratu,</span><span> “Ya Tuhan-ku, sesunggunya aku telah menganiaya diriku sendiri dan aku tunduk bersama Sulaiman kepada Allah, Tuhan sekalian alam.”</span></p>');
INSERT INTO `quran` VALUES (9577,2,27,46,'<p><span>R. 4</span></p><p><span>[a]</span><span> Dan Kami utus kepada Tsamud saudara mereka Shaleh </span><span>yang berkata,</span><span> “Sembahlah Allah.” Tetapi tiba-tiba mereka menjadi dua golongan yang saling berbantah.</span></p><p><span>[a]</span><span> 7:74 ; 11:62 ; 26:142 ; 54:24.</span></p>');
INSERT INTO `quran` VALUES (9578,2,27,47,'<p><span>Ia, </span><span>Shaleh, </span><span>berkata, “Hai kaumku, mengapakah kamu mau bersegera kepada keburukan sebelum datang kebaikan ? </span><span>[a]</span><span> Mengapakah kamu tidak memohon ampun kepada Allah, supaya kamu dikasihani ? “ </span></p><p><span>[a]</span><span> 27:47.</span></p>');
INSERT INTO `quran` VALUES (9579,2,27,48,'<p><span>Mereka berkata , “</span><span>Hai Shaleh !</span><span> Kami telah mendapatkan engkau dan para pengikut engkau bernasib buruk.” Ia, </span><span>Shaleh,</span><span> berkata, “Nasib buruk kamu ada di sisi Allah, bahkan kamu kaum yang diuji.”</span></p>');
INSERT INTO `quran` VALUES (9580,2,27,49,'<p><span>Dan dalam kota itu ada sembilan </span><span>orang</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2178">2178</a><span>] [b]</span><span> yang menimbulkan kerusuhan di bumi, dan tidak mau mengadakan perbaikan.</span></p><p><span>[b]</span><span> 26:153.</span></p>');
INSERT INTO `quran` VALUES (9581,2,27,50,'<p><span>Mereka berkata, “Hendaklah kamu sekalian bersumpah dengan </span><span>nama </span><span> Allah, bahwa kami pasti akan menyerbu pada malam hari kepada dia dan keluarganya, kemudian kami akan berkata kepada walinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2178a">2178a</a><span>]</span><span> “Kami tidak menyaksikan keluarganya menjadi binasa dan sesungguhnya kami orang-orang yang benar.”</span></p>');
INSERT INTO `quran` VALUES (9582,2,27,51,'<p><span>[a]</span><span> Dan mereka membuat suatu rencana, dan Kami </span><span>pun</span><span> membuat suatu rencana, tetapi mereka tidak menyadari. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2179">2179</a><span>]</span></p><p><span>[a]</span><span> 3:55 ; 8:31 ; 13:43 ; 14:47.</span></p>');
INSERT INTO `quran` VALUES (9583,2,27,52,'<p><span>[b]</span><span> Maka lihatlah betapa </span><span>buruknya</span><span> akibat rencana mereka ! Sesungguhnya Kami musnahkan mereka dan kaumnya semua.</span></p><p><span>[b]</span><span> 7:79 ; 26:173 ; 37:137.</span></p>');
INSERT INTO `quran` VALUES (9584,2,27,53,'<p><span>Maka itulah rumah-rumah mereka yang runtuh, disebabkan mereka berbuat aniaya. Sesungguhnya dalam yang demikian itu ada Tanda untuk kaum yang mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9585,2,27,54,'<p><span>Dan Kami selamatkan orang-orang yang beriman dan bertakwa.</span></p>');
INSERT INTO `quran` VALUES (9586,2,27,55,'<p><span>[c]</span><span> Dan </span><span>Kami utus</span><span> Luth </span><span>sebagai rasul,</span><span> ketika ia berkata kepada kaumnya, “Apakah kamu berbuat keji, sedangkan kamu melihat ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2179a">2179a</a><span>]</span></p><p><span>[c]</span><span> 7:81 ; 29:29.</span></p>');
INSERT INTO `quran` VALUES (9587,2,27,56,'<p><span>[a]</span><span> “Apakah kamu mendatangi laki-laki dengan nafsu syahwat selain perempuan-perempuan ? Sebenarnya kamu kaum yang bodoh. “</span></p><p><span>[a]</span><span> 7:82 ; 26:166 </span><span>–</span><span> 167 ; 29:30.</span></p>');
INSERT INTO `quran` VALUES (9588,2,27,57,'<p><span>Tetapi tidak ada jawaban dari kaumnya kecuali mereka berkata, </span><span>[b]</span><span> “Usirlah keluarga Luth dari kotamu. Mereka adalah orang-orang yang menganggap dirinya suci” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2180">2180</a><span>]</span></p><p><span>[b]</span><span> 7:83 ; 26:168.</span></p>');
INSERT INTO `quran` VALUES (9589,2,27,58,'<p><span>[c]</span><span> Maka Kami selamatkan dia dan keluarganya, kecuali istrinya ; Kami Takdirkan dia termasuk orang-orang yang tertinggal di belakang.</span></p><p><span>[c]</span><span> 7:84 ; 29:34.</span></p>');
INSERT INTO `quran` VALUES (9590,2,27,59,'<p><span>[d]</span><span> Dan Kami hujani pada mereka hujan ; dan sangat jahatlah hujan atas orang-orang yang telah diberi peringatan itu.</span></p><p><span>[d]</span><span> 7:85 ; 25:41 ; 26:174.</span></p>');
INSERT INTO `quran` VALUES (9591,2,27,60,'<p><span>R. 5</span></p><p><span>Katakanlah, </span><span>[e]</span><span> “Segala puji bagi Allah, dan selamat sejahtera atas hamba-hamba-Nya yang telah Dia pilih. Apakah Allah lebih baik ataukah apa yang mereka persekutukan ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2181">2181</a><span>]</span></p><p><span>[e]</span><span> 37:182 </span><span>–</span><span> 183.</span></p>');
INSERT INTO `quran` VALUES (9592,2,27,61,'<p><span>JUZ XX</span></p><p><span>Atau, siapakah yang menciptakan seluruh langit dan bumi, dan siapkah </span><span>[a]</span><span> yang menurunkan untuk kamu air dari awan, dengannya Kami tumbuhkan kebun-kebun yang indah ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2182">2182</a><span>]</span><span> Kamu tidak dapat menumbuhkan pohon-pohonnya.Adakah tuhan lain selain Allah ? </span><span>[b]</span><span> Bahkan merekalah kaum yang mempersekutukan-</span><span>Nya.</span></p><p><span>[a]</span><span> 31:11 ; 50:10.</span></p><p><span>[b]</span><span> 6:2.</span></p>');
INSERT INTO `quran` VALUES (9593,2,27,62,'<p><span>Atau, </span><span>[c]</span><span> siapakah yang menjadikan bumi ini tempat tinggal dan menjadikan di tengah-tengahnya sungai-sungai dan </span><span>[d]</span><span> menjadikan padanya gunung-gunung dan menjadikan di antara dua laut penghalang ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2183">2183</a><span>]</span><span> Adakah tuhan lain bersama Allah ? Bahkan kebanyakan mereka tidak mengetahui.</span></p><p><span>[c]</span><span> 20:54 ; 78:7.</span></p><p><span>[d]</span><span> 25:54 ; 55:20 </span><span>–</span><span> 21.</span></p>');
INSERT INTO `quran` VALUES (9594,2,27,63,'<p><span>Atau, </span><span>[e]</span><span> siapakah yang mengabulkan </span><span>doa</span><span> orang yang sengsara apabila ia berdoa kepada-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2184">2184</a><span>]</span><span> dan melenyapkan keburukan, </span><span>[f]</span><span> dan menjadikan kamu khalifah- khalifah di bumi ? Adakah tuhan lain bersama Allah ? Kamu sangat sedikit mendapat pelajaran.</span></p><p><span>[e]</span><span> 2:187 ; 7:56.</span></p><p><span>[f]</span><span> 10:15.</span></p>');
INSERT INTO `quran` VALUES (9595,2,27,64,'<p><span>Atau, siapakah yang memberi petunjuk kepadamu dalam musibah di daratan dan lautan, dan siapakah yang mengirimkan angin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2185">2185</a><span>]</span><span> sebagai khabar suka sebelum rahmat-Nya ? Adakah tuhan lain bersama Allah ? Maha Tinggi Allah di atas apa yang mereka persekutukan.</span></p>');
INSERT INTO `quran` VALUES (9596,2,27,65,'<p><span>Atau siapakah yang </span><span>[a]</span><span> mula-mula menciptakan </span><span>makhluk,</span><span> kemudian mengulanginya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2186">2186</a><span>]</span><span> dan </span><span>[b]</span><span> siapakah yang memberimu rezeki dari awan dan bumi ? Adakah tuhan lain bersama Allah ? Katakanlah, “Tunjukkanlah bukti-buktimu jika kamu orang-orang benar.”</span></p><p><span>[a]</span><span> 10:35 ; 29:20 ; 30:12, 28.</span></p><p><span>[b]</span><span> 10:32 ; 34:25 ; 35:4.</span></p>');
INSERT INTO `quran` VALUES (9597,2,27,66,'<p><span>Katakanlah, </span><span>[c]</span><span> “Tidak ada seorang pun di seluruh langit dan bumi mengetahui yang gaib, kecuali Allah. Dan mereka tidak mengetahui kapan mereka akan dibangkitkan.”</span></p><p><span>[c]</span><span> 11:124 ; 16:78 ; 35:39.</span></p>');
INSERT INTO `quran` VALUES (9598,2,27,67,'<p><span>Bahkan, sebenarnya telah sampai kepada batasnya ilmu mereka mengenai akhirat ; bahkan mereka dalam keragu-raguan mengenai itu ; bahkan mereka mengenainya buta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2187">2187</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9599,2,27,68,'<p><span>R. 6</span></p><p><span>[a]</span><span> Dan berkata, orang-orang yang ingkar, “Apakah jika kami dan bapak-bapak kami telah menjadi debu, apakah kami sungguh-sungguh akan dibangkitkan ? </span></p><p><span>[a]</span><span> 13:6 ; 37:17 ; 50:4.</span></p>');
INSERT INTO `quran` VALUES (9600,2,27,69,'<p><span>[b]</span><span> “Sesungguhnya ini telah dijanjikan kepada kami dan bapak-bapak kami dari dahulu, ini tidak lain melainkan dongeng orang-orang dahulu.”</span></p><p><span>[b]</span><span> 23:84.</span></p>');
INSERT INTO `quran` VALUES (9601,2,27,70,'<p><span>Katakanlah, </span><span>[c]</span><span> “Berjalanlah kamu di bumi dan lihatlah betapa </span><span>buruk</span><span> akibat orang-orang yang berdosa ! “</span></p><p><span>[c]</span><span> 16:37 ; 30:43 ; 40:83.</span></p>');
INSERT INTO `quran` VALUES (9602,2,27,71,'<p><span>[d]</span><span> Dan janganlah engkau bersedih atas mereka, dan jangan engkau merasa sempit dari apa yang mereka rencanakan.</span></p><p><span>[d]</span><span> 15:89 ; 16:128.</span></p>');
INSERT INTO `quran` VALUES (9603,2,27,72,'<p><span>[e]</span><span> Dan mereka berkata, “Kapankah perjanjian itu </span><span>akan terlaksana,</span><span> jika kamu memang orang-orang yang benar ? “</span></p><p><span>[e]</span><span> 10:49 ; 21:39 ; 34:30 ; 36:49.</span></p>');
INSERT INTO `quran` VALUES (9604,2,27,73,'<p><span>Katakanlah, “Mungkin telah hampir datang di belakangmu </span><span>[f]</span><span> sebagian dari </span><span>azab</span><span> yang kamu minta disegerakan itu.”</span></p><p><span>[f]</span><span> 22:48 ; 26:205 ; 29:55.</span></p>');
INSERT INTO `quran` VALUES (9605,2,27,74,'<p><span>Dan sesungguhnya </span><span>[a]</span><span> Tuhan-mu mempunyai karunai yang banyak atas menusia, akan tetapi kebanyakan mereka tidak bersyukur.</span></p><p><span>[a]</span><span> 10:61 ; 40:62.</span></p>');
INSERT INTO `quran` VALUES (9606,2,27,75,'<p><span>[b]</span><span> Dan sesungguhnya Tuhan-mu pasti mengetahui apa yang disembunyikan oleh dada mereka dan apa yang mereka zahirkan.</span></p><p><span>[b]</span><span> 2:78 ; 16:24 ; 28:70 ; 36:77.</span></p>');
INSERT INTO `quran` VALUES (9607,2,27,76,'<p><span>Dan tidak ada sesuatu pun yang tersembunyi di langit dan bumi, melainkan ada </span><span>tercatat</span><span> di dalam kitab yang jelas.</span></p>');
INSERT INTO `quran` VALUES (9608,2,27,77,'<p><span>Sesungguhnya Alquran ini menjelaskan kepada Bani Israil kebayakan dari apa-apa yang mereka di dalamnya berselisih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2188">2188</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9609,2,27,78,'<p><span>Dan sesungguhnya, </span><span>Alquran</span><span> itu termasuk petunjuk dan rahmat bagi orang-orang beriman.</span></p>');
INSERT INTO `quran` VALUES (9610,2,27,79,'<p><span>Sesungguhnya Tuhan-mu akan memutuskan di antara mereka dengan hukum-Nya, dan Dia Maha Perkasa, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9611,2,27,80,'<p><span>[c]</span><span> Maka bertawakallah kepada Allah, Sesungguhnya engkau berada di atas kebenaran yang nyata.</span></p><p><span>[c]</span><span> 11:124 ; 25:59; 33:49.</span></p>');
INSERT INTO `quran` VALUES (9612,2,27,81,'<p><span>Sesungguhnya engkau tidak dapat membuat orang-orang mati mendengar, dan </span><span>[a]</span><span> tidak dapat pula engkau membuat orang-orang tuli mendengar seruan, ketika mereka undur sambil membalikkan punggung mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2189">2189</a><span>]</span></p><p><span>[a]</span><span> 10:43 ; 30:53.</span></p>');
INSERT INTO `quran` VALUES (9613,2,27,82,'<p><span>[b]</span><span> Dan tidak dapat pula engkau memberikan petunjuk kepada orang-orang buta dari kesesatan mereka. Engkau hanya dapat membuat mereka yang beriman mendengar kepada Ayat-ayat Kami, maka mereka itulah orang-orang yang menyerahkan diri.</span></p><p><span>[b]</span><span> 10:44 ; 30:54.</span></p>');
INSERT INTO `quran` VALUES (9614,2,27,83,'<p><span>Dan apabila terjadi nubuatan </span><span>kehancuran</span><span> atas mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2190">2190</a><span>]</span><span> Kami akan mengeluarkan bagi mereka binatang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2191">2191</a><span>]</span><span> dari bumi yang akan melukai mereka, sesungguhnya manusia atas Tanda-tanda Kami tidak yakin.</span></p>');
INSERT INTO `quran` VALUES (9615,2,27,84,'<p><span>R. 7</span></p><p><span>Dan </span><span>ingatlah tentang </span><span>hari </span><span>[a]</span><span> ketika Kami akan menghimpun dari tiap-tiap umat satu golongan dari antara mereka yang mendustakan Tanda-tanda Kami, kemudian mereka akan dibagi dalam beberapa kelompok.</span></p><p><span>[a] </span><span>25:18 ; 67:9.</span></p>');
INSERT INTO `quran` VALUES (9616,2,27,85,'<p><span>Sehingga ketika mereka datang, Dia berfirman, </span><span>[b]</span><span> “Apakah kamu mendustakan Tanda-tanda-Ku ? Sedangkan ilmu kamu belum dapat mengenalnya dengan sempurna . Atau, apakah yang sedang kamu perbuat ? “</span></p><p><span>[b]</span><span> 10:40.</span></p>');
INSERT INTO `quran` VALUES (9617,2,27,86,'<p><span>Dan terjadilah nubuatan </span><span>kehancuran</span><span> atas mereka disebabkan mereka berbuat aniaya, mereka tidak akan dapat berkata-kata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2192">2192</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9618,2,27,87,'<p><span>[c]</span><span> Apakah mereka tidak mengetahui, bahwa Kami telah menjadikan malam supaya mereka beristirahat di dalamnya, dan siang untuk dapat melihat ? Sesungguhnya di dalam yang demikian itu ada Tanda-tanda bagi kaum yang beriman.</span></p><p><span>[c]</span><span> 10:68 ; 17:13 ; 28:74 ; 30:24.</span></p>');
INSERT INTO `quran` VALUES (9619,2,27,88,'<p><span>[d]</span><span> Dan </span><span>ingatlah</span><span> hari ketika terompet akan ditiup, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2193">2193</a><span>]</span><span> maka akan cemaslah siapa yang ada di seluruh langit dan siapa yang ada di bumi, kecuali siapa yang dikehendaki Allah. Dan semuanya akan datang merendahkan diri kepada-Nya.</span></p><p><span>[d]</span><span> 18:100 ; 20:103 ; 36:52 ; 78:19.</span></p>');
INSERT INTO `quran` VALUES (9620,2,27,89,'<p><span>Dan engkau melihat gunung-gunung, yang engkau menganggapnya tetap </span><span>pada tempatnya padahal </span><span> berjalan </span><span>seperti</span><span> berjalannya awan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2194">2194</a><span>] </span><span>itulah</span><span> karya Allah Yang telah membuat sempurna segala sesuatu. Sesungguhnya Dia mengetahui apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (9621,2,27,90,'<p><span>[a]</span><span> Barangsiapa berbuat kebaikan, maka baginya </span><span>pahala</span><span> yang lebih baik darinya, dan mereka dari kecemasan pada hari itu akan aman.</span></p><p><span>[a]</span><span> 4:41 ; 6:161 ; 28:85</span></p>');
INSERT INTO `quran` VALUES (9622,2,27,91,'<p><span>Dan barangsiapa yang datang dengan amal buruk, </span><span>[b]</span><span> maka disungkurkan pemimpin- pemimpin mereka ke dalam api. Tidaklah kamu dibalas, melainkan apa-apa yang kamu kerjakan.</span></p><p><span>[b]</span><span> 26:95.</span></p>');
INSERT INTO `quran` VALUES (9623,2,27,92,'<p><span>Sesungguhnya, “Aku telah diperintahkan hanya menyembah Tuhan kota </span><span>Mekkah</span><span> ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2195">2195</a><span>]</span><span> yang telah dimuliakan oleh-Nya, dan kepunyaan Dia-lah segala sesuatu ; dan aku diperintahkan supaya aku menjadi orang-orang yang berserah diri.</span></p>');
INSERT INTO `quran` VALUES (9624,2,27,93,'<p><span>Dan supaya aku bacakan Alquran. Maka </span><span>[a]</span><span> barangsiapa memdapat petunjuk, maka sesunggguhnya ia mendapat petunjuk itu untuk dirinya sendiri ; dan barangsiapa menjadi sesat, maka katakanlah, “Sesungguhnya aku hanya seorang pemberi peringatan.”</span></p><p><span>[a]</span><span> 10:109 ; 39:42</span></p>');
INSERT INTO `quran` VALUES (9625,2,27,94,'<p><span>Dan katakanlah, “Segala puji bagi Allah, Dia akan segera memperlihatkan kepadamu Tanda-tanda-Nya dan kamu akan mengenalinya.” Dan tidaklah Tuhan engkau lalai mengenai apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (9626,2,28,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9627,2,28,2,'<p><span>[b]</span><span> Maha Suci, Maha Mendengar, Maha Mulia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2195a">2195a</a><span>]</span></p><p><span>[b]</span><span> 26:2 ; 27:2.</span></p>');
INSERT INTO `quran` VALUES (9628,2,28,3,'<p><span>[c]</span><span> Inilah ayat-ayat Kitab yang jelas.</span></p><p><span>[c]</span><span> 12:2 ; 15:2 ; 26:3 ; 27:2.</span></p>');
INSERT INTO `quran` VALUES (9629,2,28,4,'<p><span>Kami bacakan kepadamu kisah Musa dan Firaun dengan sebenarnya untuk kaum yang beriman.</span></p>');
INSERT INTO `quran` VALUES (9630,2,28,5,'<p><span>[d]</span><span> Sesungguhnya Firaun berlaku sombong di atas bumi, dan ia menjadikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2196">2196</a><span>]</span><span> penduduk nya berkelompok-kelompok ; ia berusaha melemahkan sekelompok dari mereka, dengan </span><span>[e]</span><span> membunuh anak-anak laki-laki mereka, dan membiarkan hidup perempuan-perempuan mereka. Sesungguhnya ia termasuk orang-orang berbuat kerusuhan.</span></p><p><span>[d] </span><span>10:84.</span></p><p><span>[e]</span><span> 2:50 ; 7:142 ; 14:7.</span></p>');
INSERT INTO `quran` VALUES (9631,2,28,6,'<p><span>Dan Kami berkehendak memberikan karunia kepada orang-orang yang dianggap lemah di bumi, dan menjadikan mereka pemimpin-pemimpin dan menjadikan mereka ahli waris </span><span>karunia</span><span>–</span><span>karunia Kami.</span><span> </span></p>');
INSERT INTO `quran` VALUES (9632,2,28,7,'<p><span>[a]</span><span> Dan Kami teguhkan mereka di bumi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2197">2197</a><span>]</span><span> dan Kami perlihatkan kepada Firaun dan Haman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2198">2198</a><span>]</span><span> dan lasykar keduanya di antara mereka, apa yang mereka khawatirkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2199">2199</a><span>]</span></p><p><span>[a]</span><span> 7:138 ; 26:60 ; 44:29.</span></p>');
INSERT INTO `quran` VALUES (9633,2,28,8,'<p><span>[a]</span><span> Dan Kami wahyukan kepada ibu Musa, “Supaya dia menyusuinya, maka apabila engkau takut tentang dia, maka letakkanlah dia di sungai dan janganlah engkau takut dan jangan pula engkau bersedih ; sesungguhnya Kami mengembalikannya kepada engkau, dan Kami jadikan dia diantara rasul-rasul.</span></p><p><span>[a]</span><span> 20:39.</span></p>');
INSERT INTO `quran` VALUES (9634,2,28,9,'<p><span>Kemudian </span><span>[b]</span><span> seseorang dari keluaga Firaun mengangkat dia, </span><span>Musa,</span><span> sehingga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2200">2200</a><span>]</span><span> akibatnya bagi mereka menjadi musuh dan kesedihan. Sesungguhnya Firaun dan Jaman dan lasykar-lasykar keduanya itu adalah orang-orang yang bersalah.</span></p><p><span>[b]</span><span> 20:40.</span></p>');
INSERT INTO `quran` VALUES (9635,2,28,10,'<p><span>Dan berkata seorang perempuan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2200a">2200a</a><span>]</span><span> dari </span><span>keluarga</span><span> Firaun, “</span><span>Anak ini akan menjadi penyejuk mata </span><span>bagiku dan bagi engkau. Jangan engkau membunuhnya. Mudah-mudahan ia akan bermanfaat bagi kita atau kita akan menjadikannya anak,” dan mereka tidak menyadari. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2201">2201</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9636,2,28,11,'<p><span>Dan hati ibu Musa menjadi bebas </span><span>dari kesedihan.</span><span> Hampir saja ia menyingkapkan tentang anak itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2202">2202</a><span>]</span><span> seandainya tidak Kami teguhkan hatinya, sehingga ia termasuk orang-orang yang beriman.</span></p>');
INSERT INTO `quran` VALUES (9637,2,28,12,'<p><span>Dan berkata </span><span>ibu Musa</span><span> kepada saudara perempuannya </span><span>Musa,</span><span> “Ikutilah dia.” Maka ia mengawasinyadari jauh. Sedang mereka tidak menyadari.</span></p>');
INSERT INTO `quran` VALUES (9638,2,28,13,'<p><span>Dan Kami mengharamkan atasnya perempuan-perempuan lain menyusui sebelum itu, lalu ia, </span><span>saudara perempuan Musa,</span><span> berkata, </span><span>[a]</span><span> “Maukah aku tunjukkan kepadamu suatu keluarga yang dapat memeliharanya bagi kamu dan akan menjadi orang-orang yang dapat berlaku baik terhadapnya ? “</span></p><p><span>[a]</span><span> 20:41.</span></p>');
INSERT INTO `quran` VALUES (9639,2,28,14,'<p><span>Maka Kami kembalikan dia kepada ibunya, supaya sejuklah matanya dan ia tidak bersedih, dan supaya ia mengetahui, bahwa sesungguhnya janji Allah itu benar, tetapi kebanyakan mereka tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9640,2,28,15,'<p><span>R. 2</span></p><p><span>[a]</span><span> Dan tatkala ia telah sampai pada umur kedewasaannya dan telah mempunyai </span><span>akhlak</span><span> yang sempurna Kami anugerahkan kepadanya hukum dan ilmu. Dan demikianlah Kami balas orang-orang yang berbuat kebaikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2203">2203</a><span>]</span></p><p><span>[a]</span><span> 12:23 ; 46:16.</span></p>');
INSERT INTO `quran` VALUES (9641,2,28,16,'<p><span>Dan </span><span>pada suatu hari</span><span> ia masuk ke kota, ketika penghuni kota itu sedang lengah ; dan ia dapati di dalamnya dua orang sedang berkelahi, seorang dari golongannya sendiri dan seorang lagi dari golongannya itu meminta pertolongan kepadanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2204">2204</a><span>]</span><span> untuk melawan orang dari musuhnya. </span><span>[b]</span><span> Maka Musa meninju dia maka matilah dia, </span><span>Musa </span><span> berkata, “Ini adalah perbuatan syaitan ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2205">2205</a><span>]</span><span> sesungguhnya ia </span><span>syaitan,</span><span> musuh menyesatkan yang nyata.”</span></p><p><span>[b]</span><span> 20:41 ; 26:20.</span></p>');
INSERT INTO `quran` VALUES (9642,2,28,17,'<p><span>Ia berkata, “Ya Tuhan-ku, aku telah menganiaya diriku, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2206">2206</a><span>]</span><span> maka ampunilah aku,” maka Dia mengampuninya. Sesungguhnya Dia Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9643,2,28,18,'<p><span>Ia berkata, “Ya Tuhan-ku, karena nikmat yang Engkau telah anugerahkan kepadaku, aku tidak akan menjadi penolong orang-orang yang berdosa.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2207">2207</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9644,2,28,19,'<p><span>Dan pada waktu pagi ia berada di dalam kota dalam keadaan takut sambil waspada ; maka tiba-tiba orang yang kemarin telah minta pertolongannya berteriak </span><span>minta tolong lagi</span><span> kepadanya. Musa berkata kepadanya, “Sesungguhnya engkau berada </span><span>dalam </span><span> kesesatan yang nyata.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2208">2208</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9645,2,28,20,'<p><span>Dan ketika ia hendak menangkap orang, yang ia adalah musuh bagi mereka berdua, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2209">2209</a><span>]</span><span> berkatalah orang itu, “Hai Musa, apakah engkau berniat membunuhku sebagaimana engkau telah membunuh seseorang kemarin ? Engkau tidak menghendaki </span><span>lain</span><span> kecuali hanya </span><span>ingin</span><span> menjadi seorang zalim di negeri ini, dan tidaklah engkau ingin menjadi seorang dari juru damai.”</span></p>');
INSERT INTO `quran` VALUES (9646,2,28,21,'<p><span>Dan </span><span>pada waktu itu</span><span> datanglah seorang laki-laki dari bagian jauh dari kota, dengan berlari-lari. Berkatalah ia, “Hai Musa, sebenarnya pemuka-pemuka sedang bermusyawa- rah tentang diri engkau untuk membunuh engkau. Maka keluarlah engkau, sesungguhnya aku bagi engkau termasuk orang-orang yang memberi nasihat.”</span></p>');
INSERT INTO `quran` VALUES (9647,2,28,22,'<p><span>[a]</span><span> Maka keluarlah ia dari situ dalam keadaan takut sambil waspada. Berkata ia, “Ya Tuhan-ku, selamatkanlah aku dari kaum yang aniaya.”</span></p><p><span>[a]</span><span> 26:22.</span></p>');
INSERT INTO `quran` VALUES (9648,2,28,23,'<p><span>R. 3</span></p><p><span>Dan tatkala ia menghadap ke arah Madyan, ia berkata, “Mudah-mudahan Tuhan-ku akan membimbingku pada jalan yang lurus.”</span></p>');
INSERT INTO `quran` VALUES (9649,2,28,24,'<p><span>Dan tatkala ia sampai ke </span><span>sumber air</span><span> Midian, ia dapati di sana sekelompok dari manusia sedang memberi minum </span><span>ternaknya.</span><span> Dan ia dapati pula selain mereka itu dua wanita yang menahan </span><span>ternaknya.</span><span> Ia berkata, “Apakah urusan kamu berdua ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2209a">2209a</a><span>]</span><span> Keduanya berkata, “Kami tidak dapat memberi minum ternak kami sebelum gembala-gembala itu pergi , sedang ayah kami sangat tua.”</span></p>');
INSERT INTO `quran` VALUES (9650,2,28,25,'<p><span>Maka ia memberi minum </span><span>ternak</span><span> kedua mereka itu, kemudian ia pergi berteduh dan berkata , “Ya Tuhan-ku, sesungguhnya aku apa yang Engkau turunkan kepadaku dari kebaikan, </span><span>aku selalu</span><span> mengharap.”</span></p>');
INSERT INTO `quran` VALUES (9651,2,28,26,'<p><span> Kemudian datanglah kepadanya salah seorang dari kedua </span><span>wanita</span><span> itu berjalan dengan malu-malu. Ia berkata, “Sesungguhnya ayahku memanggil engkau, supaya ia membalas kepada engkau jasa, yang engkau telah memberi minum bagi </span><span>ternak </span><span>kami.” Maka ketika ia datang kepadanya dan menceriterakan kepadanya seluruh kisah, ia berkata, “Jangan engkau takut, engkau telah selamat dari kaum yang aniaya itu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2210">2210</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9652,2,28,27,'<p><span>Salah seorang dari kedua </span><span>wanita</span><span> itu berkata, “Ya ayahku, jadikanlah dia pekerja, sesungguhnya sebaik-baik orang yang engkau jadikan pekerja ialah orang yang kuat, terpercaya.”</span></p>');
INSERT INTO `quran` VALUES (9653,2,28,28,'<p><span>Ia berkata, “Sesungguhnya aku hendak menikahkan engkau dengan salah seorang dari kedua anak perempuanku ini dengan syarat, bahwa engkau bekerja untukku delapan tahun. Tetapi jika engkau menggenapkannya sepuluh tahun, maka itu </span><span>kebaikan</span><span> dari engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2211">2211</a><span>]</span><span> Dan aku tidak menginginkan bahwa aku memberatkan engkau. Engkau akan mendapatiku, insya Allah, termasuk orang-orang yang shaleh.</span></p>');
INSERT INTO `quran` VALUES (9654,2,28,29,'<p><span>Ia, </span><span>Musa,</span><span> berkata, “Itulah </span><span>perjanjuan </span><span> di antara aku dan engkau. Manapun dari kedua jangka waktu itu yang kesempurnakan, tidak akan ada sesuatu tuduhan pada diriku. Dan Allah menjadi saksi atas apa yang kita katakan.”</span></p>');
INSERT INTO `quran` VALUES (9655,2,28,30,'<p><span>R. 4</span></p><p><span>Dan tatkala Musa telah menyempurnakan jangka waktu itu, dan ia berangkat dengan keluarganya, ia melihat api di lereng gunung Thur. Ia berkata kepada keluarganya, “Tunggulah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2212">2212</a><span>]</span><span> sesungguhnya </span><span>[a]</span><span> aku membawa kepadamu khabar darinya, atau bara dari api, supaya kamu dapat memanaskan diri.”</span></p><p><span>[a]</span><span> 20:11 ; 27:8.</span></p>');
INSERT INTO `quran` VALUES (9656,2,28,31,'<p><span>Maka tatkala ia datang kepadanya, </span><span>[a]</span><span> ia diseru dari pinggir lembah sebelah kanan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2213">2213</a><span>]</span><span> di tempat yang diberkati, di dekat pohon itu, “Hai Musa ! Sesungguhnya Aku, Aku-lah Allah, Tuhan semesta alam ; ;</span></p><p><span>[a]</span><span> </span><span>19:53 ; 20:81 ; 79:17.</span></p>');
INSERT INTO `quran` VALUES (9657,2,28,32,'<p><span>[b]</span><span> “Dan lemparkanlah tongkat engkau.” Maka ketika ia melihatnya bergerak seolah-olah itu ular kecil, berbaliklah ia lari kebelakang dan tidak menoleh lagi. </span><span>Dikatakan,</span><span> “Hai Musa, maju lah dan jangan takut ; sesungguhnya engkau termasuk orang-orang yang aman,</span></p><p><span>[b]</span><span> 7:118 ; 20:20 ; 26:46.</span></p>');
INSERT INTO `quran` VALUES (9658,2,28,33,'<p><span>[c]</span><span> “Masukkan tangan engkau ke leher bajumu ; ia akan keluar putih tanpa cacat, dan dekapkan tanganmu pada </span><span>dadamu</span><span> kuat-kuat karena takut. Maka inilah dua bukti dari Tuhan engkau kepada Firaun dan pembesar-pembesarnya. Sesungguhnya mereka adalah kaum durhaka.”</span></p><p><span>[c]</span><span> 7:109 ; 20:23 ; 27:13.</span></p>');
INSERT INTO `quran` VALUES (9659,2,28,34,'<p><span>[a]</span><span> Ia, </span><span>Musa,</span><span> berkata, “Ya Tuhan-ku, sesungguhnya aku telah membunuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2214">2214</a><span>]</span><span> seseorang dari antara mereka, maka aku takut, mereka akan membunuh aku ;</span></p><p><span>[a]</span><span> 20:41 ; 26:15.</span></p>');
INSERT INTO `quran` VALUES (9660,2,28,35,'<p><span>“Dan saudaraku Harun, ia lebih fasih daripadaku dalam berbicara, maka </span><span>[b]</span><span> kirimkanlah dia besertaku sebagai pembantu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2215">2215</a><span>]</span><span> supaya ia dapat membenarkan aku. Sesungguhnya aku takut mereka akan mendustakan aku.”</span></p><p><span>[b]</span><span> 20:30 </span><span>–</span><span> 33 ; 26:14.</span></p>');
INSERT INTO `quran` VALUES (9661,2,28,36,'<p><span>[c]</span><span> Dia, </span><span>Tuhan,</span><span> berfirman “Kami akan menguatkan lengan engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2216">2216</a><span>]</span><span> dengan saudara engkau, dan Kami akan memberi kamu berdua kekuasaan, sehingga mereka tidak akan dapat sampai kepada kamu berdua. Dengan Tanda-tanda Kami kamu berdua dan orang- orang yang mengikuti kamu berdua akan mendapat kemenangan.”</span></p><p><span>[c]</span><span> 20:43.</span></p>');
INSERT INTO `quran` VALUES (9662,2,28,37,'<p><span>[d]</span><span> Maka tatkala Musa datang kepada mereka dengan Tanda-tanda Kami yang nyata, mereka berkata, “Ini tidak lain melainkan sihir yang dibuat-buat, dan tidaklah kami mendengar semacam ini dari bapak-bapak kami yang dahulu.”</span></p><p><span>[d]</span><span> 29:40.</span></p>');
INSERT INTO `quran` VALUES (9663,2,28,38,'<p><span>Dan Musa berkata, “Tuhan-ku lebih mengetahui siapa yang membawa petunjuk dari sisi-Nya, dan siapa yang akan ada baginya kesudahan yang baik. Sesungguhnya tidak akan berhasil orang-orang yang aniaya.”</span></p>');
INSERT INTO `quran` VALUES (9664,2,28,39,'<p><span>Dan Firaun berkata, “Hai pembesar-pembesar, </span><span>[a]</span><span> aku tidak mengetahu bagimu tuhan lain selain aku ; maka hidupkanlah api bagiku </span><span>[b]</span><span> hai Haman, atas tanah liat </span><span>untuk membuat bata</span><span> ; kemudian buatlah bagiku sebuah bangunan tinggi, supaya aku dapat melihat Tuhan Musa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2217">2217</a><span>]</span><span> dan sesungguhnya aku benar-benar yakin ia termasuk orang-orang pendusta.”</span></p><p><span>[a]</span><span> 26:30.</span></p><p><span>[b]</span><span> 40:37.</span></p>');
INSERT INTO `quran` VALUES (9665,2,28,40,'<p><span>[c]</span><span> Dan ia dan lasykarnya berlaku takabur di bumi tanpa hak. Dan mereka mengira bahwa, mereka tidak akan dibawa kembali kepada Kami.</span></p><p><span>[c]</span><span> 7:134.</span></p>');
INSERT INTO `quran` VALUES (9666,2,28,41,'<p><span>[d]</span><span> Maka Kami menangkapnyadan lasykarnya, lalu Kami campakkan mereka ke dalam laut. Maka lihatlah, betapa </span><span>buruknya </span><span> akibat orang-orang aniaya !</span></p><p><span>[d]</span><span> 2:51 ; 7:137 ; 17:104 ; 20:79 ; 26:67 ; 79:26.</span></p>');
INSERT INTO `quran` VALUES (9667,2,28,42,'<p><span>Dan Kamu jadikan mereka pemimpin-pemimpin </span><span>[a]</span><span> yang mengajak kepada Api ; dan pada Hari Kiamat mereka tidak akan ditolong</span></p><p><span>[a]</span><span> 11:99.</span></p>');
INSERT INTO `quran` VALUES (9668,2,28,43,'<p><span>[b]</span><span> Dan Kami ikuti mereka di dunia ini dengan laknat ; dan pada Hari Kiamat mereka termasuk orang-orang yang bernasib buruk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2217a">2217a</a><span>]</span></p><p><span>[b]</span><span> 11:61, 100.</span></p>');
INSERT INTO `quran` VALUES (9669,2,28,44,'<p><span>R. 5</span></p><p><span>[c]</span><span> Dan sesungguhnya Kami berikan kitab kepada Musa, setelah Kami binasakan generasi yang terdahulu, sebagai </span><span>sumber</span><span> penglihatan ruhani bagi manusia dan petunjuk serta rahmat, supaya mereka mendapat pelajaran.</span></p><p><span>[c]</span><span> 7:155 ; 46:13.</span></p>');
INSERT INTO `quran` VALUES (9670,2,28,45,'<p><span>Dan engkau tidak ada di sebelah barat </span><span>gunung itu,</span><span> ketika Kami serahkan hukum </span><span>risalat </span><span> kepada Musa, dan engkau tidak termasuk orang-orang yang menyaksikan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2218">2218</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9671,2,28,46,'<p><span>Tetapi Kami telah menjadikan generasi-generasi dan berlalulah atas mereka masa yang panjang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2219">2219</a><span>]</span><span> Dan engkau tidak tinggal bersama penduduk Midian, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2219a">2219a</a><span>]</span><span> yang membacakan Tanda-tanda Kami kepada mereka ; melainkan Kami-lah yang mengutus rasul-rasul.</span></p>');
INSERT INTO `quran` VALUES (9672,2,28,47,'<p><span>Dan engkau tidak berada di lereng gunung Thur, </span><span>[a]</span><span> ketika Kami berseru </span><span>kepada Musa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2220">2220</a><span>]</span><span> Tetapi ini adalah rahmat dari Tuhan engkau, </span><span>[b]</span><span> supaya engkau memberikan peringatan kepada kaum yang tidak datang kepada mereka seorang pemberi ingat sebelum engkau supaya mereka mendapat nasihat.</span></p><p><span>[a]</span><span> 20:12 </span><span>–</span><span> 13 ; 79:17.</span></p><p><span>[b]</span><span> 32:4 ; 36:7.</span></p>');
INSERT INTO `quran` VALUES (9673,2,28,48,'<p><span>Dan sekiranya tidak menimpa mereka musibah disebabkan perbuatan tangan mereka, maka mereka akan berkata, </span><span>[a]</span><span> “ Ya Tuhan kami, mengapa Engkau tidak mengutus kepada kami seorang rasul, sehingga kami dapat mengikuti Ayat-ayat Engkau dan kami menjadi orang- orang mukmin.”</span></p><p><span>[a]</span><span> 20:135.</span></p>');
INSERT INTO `quran` VALUES (9674,2,28,49,'<p><span>Apabila telah datang kepada mereka kebenaran dari sisi Kami, mereka berkata, </span><span>[b]</span><span> “Mengapa ia tidak diberi semisal apa yang telah diberikan kepada Musa ? “ Bukankah mereka telah menolak apa yang telah diberikan kepada Musa dari dahulu ? Mereka berkata, “Mereka ini keduanya tukang sihir yang saling membantu.” Dan mereka berkata, “Sesungguhnya kami ingkar kepada setiap </span><span>penda</span><span>’</span><span>waan mereka.</span><span> “ </span></p><p><span>[b]</span><span> 6:125.</span></p>');
INSERT INTO `quran` VALUES (9675,2,28,50,'<p><span>Katakanlah, “Maka datangkanlah sebuah kitab dari sisi Allah sebagai yang lebih baik memberi perunjuk daripada keduanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2221">2221</a><span>]</span><span> supaya aku mengikutinya, jika kamu memang orang-orang yang benar.”</span></p>');
INSERT INTO `quran` VALUES (9676,2,28,51,'<p><span>[a]</span><span> Tetapi jika mereka tidak menjawab engkau, maka ketahuilah, bahwa mereka hanya mengkuti hawa nafsunya. Dan siapakah yang lebih sesat daripada orang yang mengikuti hawa nafsunya tanpa petunjuk dari Allah ? Sesungguhnya Allah tidak memberi petunjuk kepada kaum yang aniaya.</span></p><p><span>[a]</span><span> 11:15</span></p>');
INSERT INTO `quran` VALUES (9677,2,28,52,'<p><span>R. 6</span></p><p><span>Dan sesungguhnya telah Kami turunkan wahyu secara berturut-turut kepada mereka, supaya mereka mendapat nasihat.</span></p>');
INSERT INTO `quran` VALUES (9678,2,28,53,'<p><span>Orang-orang yang telah Kami berikan kepada mereka kitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2222">2222</a><span>]</span><span> sebelumnya, dan mereka beriman kepadanya, </span><span>Alquran,</span></p>');
INSERT INTO `quran` VALUES (9679,2,28,54,'<p><span>Dan Apabila dibacakan </span><span>Alquran</span><span> kepada mereka, mereka berkata, “Kami beriman kepadanya, sesungguhnya </span><span>Alquran </span><span> itu benar dari Tuhan kami, sesungguhnya kami sebelumnya sudah muslim.”</span></p>');
INSERT INTO `quran` VALUES (9680,2,28,55,'<p><span>Mereka itu akan diberi ganjaran mereka dua kali, karena mereka telah bersabar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2223">2223</a><span>]</span><span> dan </span><span>[a]</span><span> mereka menolak keburukan dengan kebaikan, dan dari apa yang Kami rezekikan kepada mereka, </span><span>[b]</span><span> mereka belanjakan.</span></p><p><span>[a]</span><span> 13:23 ; 23:97 ; 41:35.</span></p><p><span>[b]</span><span> 23:5.</span></p>');
INSERT INTO `quran` VALUES (9681,2,28,56,'<p><span>[c]</span><span> Dan apabila mereka mendengarkan percakapan sia-sia, mereka berpaling darinya dan berkata, “Bagi kami amal kami dan bagi kamu amalmu. Selamat sejahtera atas kamu. Kami tidak menghendaki </span><span>bergaul</span><span> dengan orang-orang jahil.”</span></p><p><span>[c]</span><span> 25:64 ; 25:73.</span></p>');
INSERT INTO `quran` VALUES (9682,2,28,57,'<p><span>[d]</span><span> Sesungguhnya engkau tidak dapat memberi petunjuk kepada siapa yang engkau cintai, tetapi Allah memberi petunjuk kepada siapa yang Dia kehendaki ; dan Dia lebih mengetahui orang-orang yang mendapat petunjuk.</span></p><p><span>[d]</span><span> 12:104 ; 16:38.</span></p>');
INSERT INTO `quran` VALUES (9683,2,28,58,'<p><span>Dan mereka berkata, “Jika sekiranya kami mengikuti petunjuk bersama engkau, tentulah kami akan diusir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2224">2224</a><span>]</span><span> dari negeri kami.” </span><span>Katakanlah,</span><span> “Bukankah telah Kami tempatkan mereka pada tempat suci yang aman, </span><span>[e]</span><span> yang didatangkan ke tempat itu dari segala macam buah-buahan, sebagai rezeki dari sisi Kami ? “ Akan tetapi kebanyakan mereka tidak mengetahui.</span></p><p><span>[e]</span><span> 2:127 ;14:38.</span></p>');
INSERT INTO `quran` VALUES (9684,2,28,59,'<p><span>[a]</span><span> Dan berapa banyaknya kota yang telah Kami binasakan, yang telah takabur dikarena- kan kemewahan kehidupannya. Maka itulah tempat kediaman mereka yang tidak sama sekali, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2224a">2224a</a><span>]</span><span> didiami sesudah mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2225">2225</a><span>]</span><span> Dan Kami-lah menjadi pewaris</span><span>nya.</span></p><p><span>[a]</span><span> 7:5 ; 21:12 ; 22:46 ; 65:9.</span></p>');
INSERT INTO `quran` VALUES (9685,2,28,60,'<p><span>[b]</span><span> Dan Tuhan engkau tidak akan membinasakan kota-kota sebelum Dia membangkitkan di ibukota seorang rasul, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2226">2226</a><span>]</span><span> yang membacakan kepada mereka Ayat-ayat Kami, dan tidak pula Kami binasakan kota-kota kecuali penduduknya aniaya.</span></p><p><span>[b]</span><span> 6:132 ; 11:118 ; 20:135 ; 26:209.</span></p>');
INSERT INTO `quran` VALUES (9686,2,28,61,'<p><span>[a]</span><span> Dan apa pun yang diberikan kepadamu adalah kesenangan sementara dari kehidupan duniawi dan perhiasannya ; dan apa yang ada di sisi Allah lebih baik dan lebih kekal. Maka tidaklah kamu menggunakan akal ?</span></p><p><span>[a]</span><span> 3:15 ; 9:38 ; 10:71 ; 16:118 ; 40:40.</span></p>');
INSERT INTO `quran` VALUES (9687,2,28,62,'<p><span>R. 7 </span></p><p><span>Maka apakah sama orang yang Kami janjikan kepadanya suatu janji yang baik, lalu ia memperolehnya, </span><span>[b] </span><span>sama seperti orang yang Kami berikan kesenangan kehidupan di dunia, kemudian kesenangan kehidupan di dunia, kemudian pada Hari Kiamat ia akan termasuk orang-orang yang dihadapkan </span><span>kepada Tuhan untuk mempertanggung- jawabkannya ?</span></p><p><span>[b]</span><span> 20:132 ; 26:206 </span><span>–</span><span> 208.</span></p>');
INSERT INTO `quran` VALUES (9688,2,28,63,'<p><span>Dan pada Hari itu Dia akan berseru kepada mereka, dan akan berkata, </span><span>[c]</span><span> “Di manakah sekutu-sekutu-Ku yang dahulu kamu anggap ? “</span></p><p><span>[c]</span><span> 28:75 ; 41:48.</span></p>');
INSERT INTO `quran` VALUES (9689,2,28,64,'<p><span>Berkata orang-orang yang telah sempurna azab </span><span>Kami </span><span> atas mereka. </span><span>[d]</span><span> “Ya Tuhan kami, itu lah mereka orang-orang yang kami sesatkan, kami telah menyesatkan mereka sebagaimana kami sendiri telah sesat. </span><span>Kini</span><span> kami menyatakan berlepas diri </span><span>dari mereka</span><span> menghadap kepada Engkau. </span><span>[e]</span><span> Mereka dahulu sekali-kali tidak menyembah kami.”</span></p><p><span>[d]</span><span> 7:39, 40 ; 14:22 ; 33:68 </span><span>–</span><span> 69 ; 34:32 </span><span>–</span><span> 33 ; 40:48 </span><span>–</span><span> 49.</span></p><p><span>[e]</span><span> 10:29 ; 16:87.</span></p>');
INSERT INTO `quran` VALUES (9690,2,28,65,'<p><span>Dan dikatakan, </span><span>[a]</span><span> “Serulah sekutu-sekutumu.” Maka mereka menyerunya, tetapi mereka itu tidak menjawab mereka, dan mereka melihat azab, sekiranya mereka dahulu mengikuti petunjuk.</span></p><p><span>[a]</span><span> 10:29 </span><span>–</span><span> 30 ; 16:87.</span></p>');
INSERT INTO `quran` VALUES (9691,2,28,66,'<p><span>Dan pada hari Dia akan memanggil mereka, maka Dia berfirman, </span><span>[b]</span><span> Jawaban apakah yang kamu berikan kepada rasul-rasul ? “</span></p><p><span>[b] </span><span>5:110 ; 7:7.</span></p>');
INSERT INTO `quran` VALUES (9692,2,28,67,'<p><span>Maka gelap </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2227">2227</a><span>]</span><span> atas mereka segala dalih </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2228">2228</a><span>]</span><span> pada hari itu, maka mereka tidak akan saling bertanya.</span></p>');
INSERT INTO `quran` VALUES (9693,2,28,68,'<p><span>[c]</span><span> Maka barangsiapa yang bertobat dan beriman dan beramal shaleh, mungkin ia akan termasuk orang-orang yang memperoleh kebahagian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2229">2229</a><span>]</span></p><p><span>[c]</span><span> 20:83 ; 25:72.</span></p>');
INSERT INTO `quran` VALUES (9694,2,28,69,'<p><span>Dan Tuhan engkau menciptakan apa yang Dia kehendaki dan memilih </span><span>siapa yang Dia kehendaki.</span><span> Tidak ada bagi mereka pilihan. Maha Suci Allah dan Maha Tinggi di atas apa yang mereka sekutukan.</span></p>');
INSERT INTO `quran` VALUES (9695,2,28,70,'<p><span>[a]</span><span> Dan Tuhan engkau mengetahui apa yang disembunyikan oleh dada mereka, dan apa yang mereka tampakkan.</span></p><p><span>[a]</span><span> 2:78 ; 11:6 ; 16:24 ; 36:77.</span></p>');
INSERT INTO `quran` VALUES (9696,2,28,71,'<p><span>Dan Dia-lah Allah, tidak ada tuhan selain Dia. Bagi-Nya segala puji pada permulaan dan akhir. Bagi-Nya segala hukum, dan kepada-Nya kamu akan dikembalikan.</span></p>');
INSERT INTO `quran` VALUES (9697,2,28,72,'<p><span>Katakanlah, “Terangkanlah kepadaku, sekiranya Allah menjadikan atas kamu malam terus-menerus sampai Hari Kiamat, siapakah tuhan selain Allah yang dapat mendatangkan kepadamu cahaya ? Apakah kamu tidak mendengar ? “</span></p>');
INSERT INTO `quran` VALUES (9698,2,28,73,'<p><span>Katakanlah, “Terangkanlah kepadaku, sekiranya Allah menjadikan atas kamu siang terus-menerus sampai hari Kiamat, siapakah tuhan selain Allah yang mendatangkan kepadamu malam </span><span>supaya </span><span> kamu beristirahat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2230">2230</a><span>]</span><span> di dalamnya ? Apakah kamu tidak melihat ? “</span></p>');
INSERT INTO `quran` VALUES (9699,2,28,74,'<p><span>[a]</span><span> Dan dari rahmat-Nya juga Dia telah menjadikan bagimu malam dan siang supaya kamu beristirahat di dalamnya, dan supaya kamu mencari dari karunia-Nya dan supaya kamu bersyukur.</span></p><p><span>[a]</span><span> 10:68 ; 17:13 ; 27:87 ; 30:24.</span></p>');
INSERT INTO `quran` VALUES (9700,2,28,75,'<p><span>Dan pada Hari Dia akan memanggil mereka, dan berfirman, </span><span>[b]</span><span> “Dimanakah sekutu- sekutu-Ku yang dahulu kamu anggap ? “</span></p><p><span>[b]</span><span> 16:28 ; 18:53 ; 28:63 ; 41:48.</span></p>');
INSERT INTO `quran` VALUES (9701,2,28,76,'<p><span>[c]</span><span> Dan akan Kami bangkitkan dari tiap-tiap umat seorang saksi maka Kami berfirman, “Bawalah buktimu.” Kemudian mereka mengetahui, bahwa kebenaran itu kepunyaan Allah. Dan akan lenyap dari mereka apa-apa yang mereka ada-adakan.</span></p><p><span>[c]</span><span> 4:42 ; 16:85.</span></p>');
INSERT INTO `quran` VALUES (9702,2,28,77,'<p><span>R. 8</span></p><p><span>[d]</span><span> Sesungguhnya Karun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2331">2331</a><span>]</span><span> adalah dari kaum Musa, tetapi ia berlaku aniaya terhadap mereka. Dan telah Kami berikan kepadanya begitu banyak khazanah-khazanah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2232">2232</a><span>]</span><span> yang kunci-kuncinya sangat susah diangkat oleh sejumlah orang-orang kuat. Ketika kaumnya berkata kepadanya, “Janganlah engkau begitu sombong, sesungguhnya Allah tidak mencintai orang-orang sombong.</span></p><p><span>[d]</span><span> 29:40 ; 40:25.</span></p>');
INSERT INTO `quran` VALUES (9703,2,28,78,'<p><span>“Dan carilah dalam apa yang telah Allah berikan kepadamu, rumah akhirat itu ; dan janganlah engkau melupakan nasib engkau di dunia. Dan berbuatlah kebaikan sebagaimana Allah telah berbuat baik terhadap engkau ; dan janganlah engkau menimbulkan kekacauan di bumi . Sesungguhnya Allah tidak mencintai orang-orang yang berbuat kekacauan.”</span></p>');
INSERT INTO `quran` VALUES (9704,2,28,79,'<p><span>Ia </span><span>[a]</span><span> berkata, “Sesungguhnya ini telah diberikan kepadaku disebabkan ilmu yang ada pada-ku.” Tidaklah ia mengetahui, bahwa sesungguhnya Allah telah membinasakan banyak generasi sebelumnya, yang lebih besar kekuasaannya daripada dia dan lebih banyak harta kekayaan ? Dan tidak akan ditanya orang-orang yang berdosa tentang dosa-dosa mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2232a">2232a</a><span>]</span></p><p><span>[a]</span><span> 39:50.</span></p>');
INSERT INTO `quran` VALUES (9705,2,28,80,'<p><span>Maka keluarlah ia dihadapan kaumnya dengan kemegahan. Berkata orang-orang yang menghendaki kehidupan dunia, “Alangkah baiknya, apabila kami pun mempunyai seperti apa yang telah diberikan kepada Karun ! Sesungguhnya ia mempunyai bagian </span><span>harta</span><span> yang besar.”</span></p>');
INSERT INTO `quran` VALUES (9706,2,28,81,'<p><span>Tetapi orang-orang yang diberi ilmu berkata, “Celakalah kamu, ganjaran dari Allah adalah lebih baik siapa yang beriman dan beramal shaleh ; dan itu tidak akan diberikan kecuali kepada orang-orang yang sabar.”</span></p>');
INSERT INTO `quran` VALUES (9707,2,28,82,'<p><span>[a]</span><span> Maka karena itu Kami membenamkannya </span><span>Karun,</span><span> beserta rumahnya ke dalam bumi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2233">2233</a><span>]</span><span> dan tidak ada baginya satu golongan pun yang menolongnya selain Allah, dan tidak pula ia termasuk orang-orang yang dapat membela diri.</span></p><p><span>[a]</span><span> 29:41.</span></p>');
INSERT INTO `quran` VALUES (9708,2,28,83,'<p><span>Dan jadilah orang-orang yang ingin mendapat kedudukannya kemarin itu berkata, “Celakalah </span><span>bagimu !</span><span> </span><span>[b]</span><span> Sesungguhnya Allah Yang melapangkan rezeki bagi siapa yang Dia kehendaki dari hamba-hamba-Nya dan menyempitkan. Sekiranya Allah tidak menganugerahkan kemurahan-Nya kepada kami, pasti Dia akan membenamkan kami </span><span>dalam musibah itu.</span><span> Celakalah </span><span>bagi mu !</span><span> Tidak akan berhasil orang-orang ingkar.”</span></p><p><span>[b]</span><span> 13:27 ; 29:63 ; 34:37.</span></p>');
INSERT INTO `quran` VALUES (9709,2,28,84,'<p><span>R. 9 </span></p><p><span>[a]</span><span> Inilah rumah akhirat, Kami jadikan itu bagi orang-orang yang tidak menghendaki kesombongan di bumi, dan tidak pula kekacauan. Dan kesudahan yang </span><span>baik</span><span> adalah bagi orang-orang yang bertakwa.</span></p><p><span>[a]</span><span> 7:170 ; 16:31.</span></p>');
INSERT INTO `quran` VALUES (9710,2,28,85,'<p><span>[b]</span><span> Barangsiapa berbuat kebaikan, maka baginya ada </span><span>balasan</span><span> yang lebih baik dari itu ; dan barangsiapa yang berbuat kejahatan, maka tidak akan dibalas orang-orang yang berbuat kejahatan-kejahatan melainkan apa yang telah mereka kerjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2234">2234</a><span>]</span></p><p><span>[b]</span><span> 4:125 ; 6:161 ; 17:8 ; 41:47 ; 99:8 </span><span>–</span><span> 9.</span></p>');
INSERT INTO `quran` VALUES (9711,2,28,86,'<p><span>Sesungguhnya Dia yang telah mewajibkan Alquran atasmu, pasti akan mengembalikan engkau ke tampat kembali </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2235">2235</a><span>]</span><span> </span><span>yang telah ditetapkan.</span><span> Katakanlah, </span><span>[c]</span><span> “Tuhan-ku lebih mengetahui siapa yang membawa petunjuk, dan siapa yang ada dalam kesesatan yang nyata.”</span></p><p><span>[c]</span><span> 17:85.</span></p>');
INSERT INTO `quran` VALUES (9712,2,28,87,'<p><span>Dan engkau tidak pernah mengharapkan bahwa diturunkan kepada engkau sebuah kitab yang sempurna ; </span><span>[a]</span><span> melainkan </span><span>itu adalah </span><span> rahmat dari Tuhan engkau ; maka engkau jangan sekali-kali menjadi penolong orang-orang ingkar.</span></p><p><span>[a]</span><span> 17:88.</span></p>');
INSERT INTO `quran` VALUES (9713,2,28,88,'<p><span>Dan jangan sampai ada orang yang mencegah engkau dari Ayat-ayat Allah sesudah diturunkan kepada engkau ; dan ajaklah kepada Tuhan engkau dan janganlah engkau termasuk orang-orang musyrik.</span></p>');
INSERT INTO `quran` VALUES (9714,2,28,89,'<p><span>[a]</span><span> Dan janganlah engkau menyeru bersama Allah tuhan lain. tidak ada tuhan selain Dia. Segala sesuatu akan binasa kecuali Wujud-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2236">2236</a><span>]</span><span> Baginya segala hukum, dan kepada-Nya kamu akan dikembalikan.</span></p><p><span>[a]</span><span> 10:107 ; 17:40 ; 26:214.</span></p>');
INSERT INTO `quran` VALUES (9715,2,29,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9716,2,29,2,'<p><span>[b]</span><span> Aku, Allah Yang Maha Mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2236a">2236a</a><span>]</span><span> </span></p><p><span>[b]</span><span> 2:2 ; 3:2 ; 13:2 ; 30:2 ; 31:2 ; 32:2.</span></p>');
INSERT INTO `quran` VALUES (9717,2,29,3,'<p><span>[c]</span><span> Apakah manusia menyangka, bahwa mereka akan dibiarkan berkata, “Kami telah beriman” dan mereka tidak akan diuji ?</span></p><p><span>[c]</span><span> 3:180 ; 9:16.</span></p>');
INSERT INTO `quran` VALUES (9718,2,29,4,'<p><span>Dan sesungguhnya telah Kami uji orang-orang sebelum mereka. Maka Allah pasti mengetahui </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2237">2237</a><span>]</span><span> orang-orang yang berkata benar dan Dia pasti mengetahui orang-orang yang dusta.</span></p>');
INSERT INTO `quran` VALUES (9719,2,29,5,'<p><span>Atau, apakah telah menyangka orang-orang yang berbuat keburukan, mereka akan dapat melepaskan diri dari </span><span>azab</span><span> Kami ? Buruklah apa yang mereka putuskan !</span></p>');
INSERT INTO `quran` VALUES (9720,2,29,6,'<p><span>[a]</span><span> Barangsiapa mengharapkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2238">2238</a><span>]</span><span> pertemuan dengan Allah, maka sesungguhnya waktu yang ditetapkan Allah pasti tiba. Dan Dia Maha Mendengar, Maha Mengetahui.</span></p><p><span>[a]</span><span> 1130 ; 18:111 ; 84:7.</span></p>');
INSERT INTO `quran` VALUES (9721,2,29,7,'<p><span>Barangsiapa telah berjihad, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2239">2239</a><span>]</span><span> maka sesungguhnya ia berjihad untuk dirinya sendiri. Sesungguhnya Allah benar-benar Maha Kaya dari semesta alam.</span></p>');
INSERT INTO `quran` VALUES (9722,2,29,8,'<p><span>Dan orang-orang </span><span>[b]</span><span> yang beriman dan beramal shaleh, niscaya akan Kami jauhkan dari mereka keburukan-keburukan mereka, dan pasti akan Kami berikan pahala kepada mereka yang lebih baik dari yang mereka kerjakan.</span></p><p><span>[b]</span><span> 2:83 ; 3:58 ; 13:30 ; 22:57 ; 30:16 ; 35:8 ; 42:23 ; 47:13.</span></p>');
INSERT INTO `quran` VALUES (9723,2,29,9,'<p><span>[c] </span><span>Dan kami perintahkan kepada manusia berbuat kebaikan terhadap ibu-bapaknya, tetapi jika keduanya memaksa engkau untuk menyekutukan Aku dengan apa yang engkau tidak mempunyai ilmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2240">2240</a><span>]</span><span> tentang itu, maka janganlah engkau mentaati keduanya. Dan kepada Aku akan beritahukan kepadamu tentang apa yang telah kamu kerjakan.</span></p><p><span>[c]</span><span> 2:84 ; 4:37 ; 6:152 ; 17:24 ; 31:15 ; 46:16.</span></p>');
INSERT INTO `quran` VALUES (9724,2,29,10,'<p><span>Dan orang-orang yang beriman dan beramal shaleh, pasti akan Kami masukkan </span><span>[a]</span><span> mereka ke dalam </span><span>golongan</span><span> orang-orang shaleh.</span></p><p><span>[a]</span><span> 14:24.</span></p>');
INSERT INTO `quran` VALUES (9725,2,29,11,'<p><span>Dan dari antara manusia ada yang berkata, “Kami beriman kepada Allah,” tetapi ketika mereka disusahkan pada jalan Allah, mereka menganggap cobaan dari manusia sebagai azab Allah. </span><span>[b]</span><span> Dan jika datang pertolongan dari Tuhan engkau, tentu mereka berkata, “Sesungguhnya kami besertamu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2241">2241</a><span>]</span><span> Bukankah Allah lebih mengetahui apa yang terkandung dalam dada manusia semesta alam ?</span></p><p><span>[b]</span><span> 4:142.</span></p>');
INSERT INTO `quran` VALUES (9726,2,29,12,'<p><span>[c]</span><span> Dan Allah pasti mengetahui orang-orang yang beriman dan niscaya Dia pasti mengetahui orang-orang munafik.</span></p><p><span>[c]</span><span> 3:142 ; 47:32.</span></p>');
INSERT INTO `quran` VALUES (9727,2,29,13,'<p><span>Dan berkata orang-orang ingkar kepada orang-orang beriman, “Ikutilah jalan kami, </span><span>[d]</span><span> maka tentulah kami akan menanggung dosa-dosamu.” Padahal mereka tidak dapat memikul dosa-dosa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2242">2242</a><span>]</span><span> mereka itu sedikit pun. Mereka itu sungguh-sungguh pendusta.</span></p><p><span>[d]</span><span> 14:22 ; 40:48.</span></p>');
INSERT INTO `quran` VALUES (9728,2,29,14,'<p><span>Dan sesungguhnya mereka akan memikul beban mereka dan beban </span><span>orang lain</span><span> beserta beban mereka. Dan sesungguhnya mereka akan ditanya pada Hari Kiamat tentang apa-apa yang mereka ada-adakan.</span></p>');
INSERT INTO `quran` VALUES (9729,2,29,15,'<p><span>R. 2</span></p><p><span>Dan sesungguhnya telah Kami utus Nuh kepada kaumnya, dan ia tinggal di antara mereka seribu tahun kurang lima puluh tahun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2243">2243</a><span>]</span><span> Kemudian mereka disergap oleh taufan sedang mereka dalam keadaan aniaya.</span></p>');
INSERT INTO `quran` VALUES (9730,2,29,16,'<p><span>[a]</span><span> Tetapi Kami selamatkan dia dan penumpang-penumpang bahtera ; dan Kami jadikan </span><span>peristiwa</span><span> itu suatu Tanda untuk segenap manusia.</span></p><p><span>[a]</span><span> 10:74 ; 11:42.</span></p>');
INSERT INTO `quran` VALUES (9731,2,29,17,'<p><span>Dan Ibrahim, ketika ia berkata kepada kaumnya, “Sembahlah Allah dan bertakwalah kepada-Nya. Demikian itu lebih baik bagimu jika kamu mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9732,2,29,18,'<p><span>[a]</span><span> Sesungguhnya apa yang kamu sembah selain Allah adalah berhala-berhala, dan kamu berbuat dusta. Sesungguhnya orang-orang yang kamu sembah selain Allah, tidak mempunyai kekuasaan kepadamu untuk </span><span>memberi</span><span> rezeki. Maka carilah rezeki di sisi Allah dan sembahlah Dia, dan bersyukurlah kepada-Nya. Kepada-Nya kamu akan dikembalikan.</span></p><p><span>[a]</span><span> 22:72.</span></p>');
INSERT INTO `quran` VALUES (9733,2,29,19,'<p><span>Dan jika kamu mendustakan, maka sesungguhnya telah mendustakan umat-umat sebelum kamu. </span><span>[b]</span><span> Dan tidaklah kewajiban Rasul melainkan menyampaikan dengan jelas.</span></p><p><span>[b]</span><span> 16:36 ; 24;55 ; 36:18.</span></p>');
INSERT INTO `quran` VALUES (9734,2,29,20,'<p><span>[c]</span><span> Apakah mereka tidak mengetahui bagaimana Allah mula pertama menciptakan makhluk kemudian mengulang-ulanginya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2344">2344</a><span>]</span><span> Sesungguhnya hal itu sangat mudah bagi Allah.</span></p><p><span>[c]</span><span> 10:35 ; 21:105 ; 27:65 ; 30:12, 28.</span></p>');
INSERT INTO `quran` VALUES (9735,2,29,21,'<p><span>Katakanlah, “Berjalanlah di bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2245">2245</a><span>]</span><span> dan lihatlah, </span><span>[d]</span><span> bagaimana Dia memulai penciptaan makhluk kemudian Allah akan menghidupkan kembali sesudah mati.” Sesungguhnya Allah Maha Kuasa atas segala sesuatu.</span></p><p><span>[d]</span><span> 10:5 ; 30:28.</span></p>');
INSERT INTO `quran` VALUES (9736,2,29,22,'<p><span>[a]</span><span> Dia mengazab siapa yang Dia kehendaki dan Dia mengasihi siapa yang Dia kehendaki, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2245a">2245a</a><span>]</span><span> dan kepada-Nya kamu akan dikembalikan.</span></p><p><span>[a]</span><span> 3:129 ; 5:41 ; 17:55.</span></p>');
INSERT INTO `quran` VALUES (9737,2,29,23,'<p><span>[b]</span><span> Dan kamu tidak dapat menggagalkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2246">2246</a><span>]</span><span>, </span><span>rencana Allah</span><span> di bumi dan tidak pula di langit, dan tidak ada bagimu selain Allah seorang kawan dan tidak pula penolong.</span></p><p><span>[b]</span><span> 10:54 ; 11:34 ; 42:32.</span></p>');
INSERT INTO `quran` VALUES (9738,2,29,24,'<p><span>R. 3</span></p><p><span>[c]</span><span> Dan orang-orang yang mengingkari Tanda-tanda Allah dan pertemuan dengan-Nya, mereka itulah yang telah putus-asa dari rahmat-Ku. Dan mereka itulah bagi mereka azab yang pedih.</span></p><p><span>[c]</span><span> 18:106 ; 30:17 ; 32:11.</span></p>');
INSERT INTO `quran` VALUES (9739,2,29,25,'<p><span>Dan tiada jawaban dari kaumnya melainkan mereka berkata, </span><span>[a]</span><span> “Bunuhlah dia atau bakarlah dia.” Tetapi Allah menyelamatkan dia dari api. Sesungguhnya dalam hal itu adalah Tanda-tanda bagi kaum yang beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2247">2247</a><span>]</span></p><p><span>[a]</span><span> 21:69 ; 37:98.</span></p>');
INSERT INTO `quran` VALUES (9740,2,29,26,'<p><span>Dan ia, </span><span>Ibrahim, </span><span>berkata, “Sesungguhnya kamu telah mengambil selain Allah berhala- berhala </span><span>sebagai sembahan</span><span> atas kecintaan diantara kamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2248">2248</a><span>]</span><span> dalam kehidupan dunia. Kemudian pada Hari Kiamat </span><span>[b]</span><span> sebagian dari kamu akan mengingkari sebagian yang lain. Dan sebagian kamu melaknati sebagian yang lain. Dan tempat tinggalmu adalah Api ; dan tidak akan ada bagimu seorang penolong.”</span></p><p><span>[b]</span><span> 16:87.</span></p>');
INSERT INTO `quran` VALUES (9741,2,29,27,'<p><span>Maka beriman Luth kepadanya, </span><span>Ibrahim,</span><span> dan ia berkata, </span><span>[c]</span><span> “Sesungguhnya aku berhijrah kepada Tuhan-ku. Sesungguhnya Dia-lah Yang Maha perkasa, Maha Bijaksana.”</span></p><p><span>[c] </span><span>19:49.</span></p>');
INSERT INTO `quran` VALUES (9742,2,29,28,'<p><span>[a]</span><span> Dan Kami anugerahkan kepadanya Ishak dan Ya</span><span>’</span><span>kup, dan Kami jadikan dalam keturunannya kenabian dan kitab, dan </span><span>[b]</span><span> Kami berikan kepadanya ganjarannya di dunia ; dan sesungguhnya dia di akhirat termasuk di antara orang-orang shaleh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2248a">2248a</a><span>]</span></p><p><span>[a]</span><span> 19:50 ; 21:73 ; 37:113.</span></p><p><span>[b]</span><span> 2:131 ; 16:123.</span></p>');
INSERT INTO `quran` VALUES (9743,2,29,29,'<p><span>Dan Luth ketika ia berkata kepada kaumnya, </span><span>[c]</span><span> “Sesungguhnya kamu mengerjakan pekerjaan keji yang sebelum kamu tidak ada seorang pun dari manusia sedunia </span><span>melakukannya.</span></p><p><span>[c]</span><span> 7:81 ; 11:79.</span></p>');
INSERT INTO `quran` VALUES (9744,2,29,30,'<p><span>[d]</span><span> “Apakah kamu mendatangi laki-laki dan menyamun di jalan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2249">2249</a><span>]</span><span> Dan kamu pada pertemuan-pertemuanmu melakukan kemungkaran ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2250">2250</a><span>]</span><span> Maka tidak ada jawaban dari kaumnya melainkan mereka berkata, “Datangkanlah kepada kami azab Allah, jika engkau termasuk orang-orang yang benar.”</span></p><p><span>[d]</span><span> 7:82 ; 11:79 ; 26:166.</span></p>');
INSERT INTO `quran` VALUES (9745,2,29,31,'<p><span>Berkatalah ia </span><span>Luth,</span><span> “Ya Tuhan-ku, </span><span>[a]</span><span> tolonglah aku terhadap kaum yang berbuat kekacauan.</span></p><p><span>[a]</span><span> 11:70 </span><span>–</span><span> 71.</span></p>');
INSERT INTO `quran` VALUES (9746,2,29,32,'<p><span>R. 4</span></p><p><span>[b]</span><span> Dan tatkala utusan-utusan Kami datang kepada Ibrahim membawa khabar suka, mereka berkata, “Kami akan membinasakan penduduk kota ini, karena sesungguhnya penduduknya orang-orang aniaya.</span></p><p><span>[b]</span><span> 11:70 </span><span>–</span><span> 71.</span></p>');
INSERT INTO `quran` VALUES (9747,2,29,33,'<p><span>Ia </span><span>Ibrahim,</span><span> berkata, “Sesungguhnya di dalamnya ada Luth.” Mereka berkata, “ Kami lebih mengetahui siapa yang ada di dalamnya. </span><span>[c]</span><span> Kami sesungguhnya akan menyelamatkan dia dan keluarganya, </span><span>[d]</span><span> kecuali istrinya, yang termasuk orang-orang yang tertinggal dibelakang.”</span></p><p><span>[c]</span><span> 15:60 ; 51:36.</span></p><p><span>[d]</span><span> 7:84 ; 15:61 ; 26:172 ; 27:58.</span></p>');
INSERT INTO `quran` VALUES (9748,2,29,34,'<p><span>Dan ketika utusan-utusan Kami datang kepada Luth, </span><span>[e]</span><span> ia merasa susah atas khabar mereka, dan hatinya merasa sempit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2251">2251</a><span>]</span><span> mengenai mereka itu. Dan mereka itu berkata, “Janganlah engkau takut, dan jangan pula bersedih. </span><span>[f]</span><span> “Kami pasti akan menyelamatkan engkau dan keluarga engkau kecuali istri engkau, yang termasuk orang-orang yang tertinggal dibelakang.</span></p><p><span>[e]</span><span> 11:78.</span></p><p><span>[f]</span><span> 7:84 ; 27:58.</span></p>');
INSERT INTO `quran` VALUES (9749,2,29,35,'<p><span>“Sesungguhnya akan </span><span>[a]</span><span> Kami turunkan atas penduduk kota ini siksaan dari langit, disebabkan mereka telah melakukan kedurhakaan.”</span></p><p><span>[a]</span><span> 27:59.</span></p>');
INSERT INTO `quran` VALUES (9750,2,29,36,'<p><span>[b]</span><span> Dan sesungguhnya telah Kami tingalkan darinya suatu Tanda yang nyata bagi kaum yang menggunakan akal.</span></p><p><span>[b]</span><span> 15:76 ; 51:38.</span></p>');
INSERT INTO `quran` VALUES (9751,2,29,37,'<p><span>[c]</span><span> Dan kepada </span><span>kaum </span><span> Madyan </span><span>Kami utus </span><span> saudara mereka Syu</span><span>’</span><span>aib, Maka ia berkata, “Hai kaum ku, sembahlah Allah, dan ingatlah akan Hari Akhir dan janganlah kamu melakukan kerusakan di bumi sambil berbuat kekacauan.”</span></p><p><span>[c]</span><span> 7:86 ; 11:85.</span></p>');
INSERT INTO `quran` VALUES (9752,2,29,38,'<p><span>Maka mereka mendustakannya, </span><span>[d]</span><span> lalu mereka disergap gempa yang dahsyat, dan mereka jatuh bergelimpangan di dalam rumah mereka.</span></p><p><span>[d]</span><span> 7:92 ; 11:95 ; 26:190.</span></p>');
INSERT INTO `quran` VALUES (9753,2,29,39,'<p><span>Dan </span><span>Kami binasakan </span><span> </span><span>[e]</span><span> </span><span>‘</span><span>Ad dan Tsamud, dan telah jelas bagi kamu tempat kediaman mereka. Dan syaitan membuat indah bagi mereka amal mereka, dan menghalangi mereka dari jalan Allah, padahal mereka itu orang-orang cerdik-pandai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2252">2252</a><span>]</span></p><p><span>[e]</span><span> 9:70.</span></p>');
INSERT INTO `quran` VALUES (9754,2,29,40,'<p><span>Dan </span><span>Kami binasakan</span><span> pula Karun, Firaun dan Haman. Dan </span><span>[a]</span><span> Sesungguhnya Musa telah datang kepada mereka dengan Tanda-tanda yang nyata, maka mereka berlaku sombong di bumi dan mereka tidak dapat melepaskan diri </span><span>dari azab Kami.</span></p><p><span>[a]</span><span> 28:37.</span></p>');
INSERT INTO `quran` VALUES (9755,2,29,41,'<p><span>Maka tiap-tiap orang </span><span>dari mereka</span><span> Kami tangkap karena dosanya, jadi di antara mereka ada yang Kami kirim kepadanya taufan kerikil, dan di antara mereka ada yang disambar oleh petir, dan di antara mereka ada </span><span>[b]</span><span> yang Kami benamkan di bumi, dan di antara mereka ada yang Kami tenggelamkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2253">2253</a><span>] [c]</span><span> dan Allah tidak berbuat aniaya terhadap mereka, tetapi merekalah menganiaya diri mereka sendiri.</span></p><p><span>[b]</span><span> 28:82. </span></p><p><span>[c]</span><span> 16:34 ; 30:10</span></p>');
INSERT INTO `quran` VALUES (9756,2,29,42,'<p><span>Perumpamaan orang-orang yang mengambil selain Allah penolong-penolong adalah seperti perumpamaan laba-laba yang membuat rumah. Dan sesungguhnya selemah-lemah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2254">2254</a><span>]</span><span> rumah ialah rumah laba-laba, seandainya mereka itu mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9757,2,29,43,'<p><span>Sesungguhnya Allah mengetahui apa saja yang mereka seru sesuatu selain Dia ; dan Dia Maha Perkasa, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (9758,2,29,44,'<p><span>Dan </span><span>[a]</span><span> itulah perumpamaan-perumpumaan yang Kami jelaskan bagi manusia, tetapi dia tidak dapat memahaminya kecuali orang-orang yang berilmu.</span></p><p><span>[a]</span><span> 13:18 ; 14:26.</span></p>');
INSERT INTO `quran` VALUES (9759,2,29,45,'<p><span>[b]</span><span> Allah menciptakan seluruh langit dan bumi sesuai dengan hak. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2255">2255</a><span>]</span><span> Sesungguhnya dalam yang demikian itu adalah Tanda bagi orang-orang yang beriman.</span></p><p><span>[b]</span><span> 6:74 ; 16:4 ; 39:6.</span></p>');
INSERT INTO `quran` VALUES (9760,2,29,46,'<p><span>JUZ XXI</span></p><p><span>R.5</span></p><p><span>[a]</span><span> Bacakanlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2256">2256</a><span>]</span><span> apa yang diwahyukan kepada engkau dari kitab </span><span>Alquran</span><span> dan dirikanlah shalat. Sesungguhnya shalat mencegah dari kekejian dan kemungkaran. Dan sesungguhnya mengingat Allah adalah </span><span>perkerjaan</span><span> yang lebih besar. Dan Allah mengetahui apa yang kamu kerjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2256a">2256a</a><span>]</span></p><p><span>[a]</span><span> 18:28.</span></p>');
INSERT INTO `quran` VALUES (9761,2,29,47,'<p><span>Dan janganlah kamu berbantah dengan Ahlikitab, melainkan </span><span>[b]</span><span> dengan </span><span>dalil-dalil</span><span> yang paling baik, kecuali dengan orang-orang yang aniaya di antara mereka. Dan katakanlah, “Kami beriman kepada apa yang telah diturunkan kepada kami dan yang telah diturunkan kepada kamu, dan Tuhan kami dan Tuhan kamu itu Esa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2257">2257</a><span>]</span><span> dan kami kepada-Nya berserah diri.</span></p><p><span>[b]</span><span> 16:126 ; 23:97 ; 41:35.</span></p>');
INSERT INTO `quran` VALUES (9762,2,29,48,'<p><span>Dan demikianlah Kami turunkan kepada engkau Kitab yang sempurna. </span><span>[a]</span><span> Maka orang- orang yang telah Kami beri kepada mereka Kitab, mereka beriman kepadanya, dan dari antara orang-orang </span><span>Ahlikitab</span><span> sebagian dari mereka beriman kepadanya. dan tiada yang menolak Tanda-tanda Kami kecuali orang-orang kafir.</span></p><p><span>[a]</span><span> 11:18.</span></p>');
INSERT INTO `quran` VALUES (9763,2,29,49,'<p><span>[b]</span><span> Dan tidak pernah engkau membacakan sebelum </span><span>Alquran</span><span> ini satu pun Kitab, dan tidak pernah engkau menulisnya dengan tangan kanan engkau, sekiranya terjadi demikian, tentulah menjadi ragu orang-orang yang mendustakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2258">2258</a><span>]</span></p><p><span>[b]</span><span> 42:53.</span></p>');
INSERT INTO `quran` VALUES (9764,2,29,50,'<p><span>Bahkan, </span><span>Alquran</span><span> itu adalah Tanda-tanda yang nyata di dalam dada orang-orang yang diberi ilmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2259">2259</a><span>]</span><span> Dan tiada yang akan menolak Tanda-tanda Kami, melainkan orang- orang aniaya.</span></p>');
INSERT INTO `quran` VALUES (9765,2,29,51,'<p><span>Dan mereka berkata, </span><span>[a]</span><span> “Mengapa tidak diturunkan kepadanya Tanda-tanda dari Tuhan-nya ?” katakanlah, “Tanda-tanda itu hanya ada pada Allah. Dan sesungguhnya aku seorang </span><span>[b]</span><span> pemberi peringatan yang nyata.”</span></p><p><span>[a]</span><span> 6:38 ; 13:28.</span></p><p><span>[b]</span><span> 22:50 ; 26:116 ; 51:51 ; 67:27.</span></p>');
INSERT INTO `quran` VALUES (9766,2,29,52,'<p><span>Apakah tidak cukup bagi mereka, bahwa kami telah menurunkan kepada engkau Kitab </span><span>yang sempurna</span><span> yang dibacakan kepada mereka ? Sesungguhnya dalam yang demikian itu ada rahmat dan nasihat bagi kaum yang beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2260">2260</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9767,2,29,53,'<p><span>R. 6</span></p><p><span>Katakanlah, </span><span>[c]</span><span> “Cukuplah Allah antara aku dan kamu sebagai saksi. Dia mengetahui apa yang ada di seluruh langit dan bumi. Dan orang-orang yang percaya kepada yang bathil dan ingkar kepada Allah, mereka itulah orang-orang yang rugi.”</span></p><p><span>[c]</span><span> 4:167 ; 6:20 ; 13:44 ; 48:29.</span></p>');
INSERT INTO `quran` VALUES (9768,2,29,54,'<p><span>Dan </span><span>[a]</span><span> mereka minta kepada engkau disegerakan azab. Dan sekiranya tidak ada waktu yang telah ditentukan, tentulah azab itu telah datang kepada mereka. Dan niscaya </span><span>azab</span><span> itu akan datang kepada mereka dengan tiba-tiba. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2261">2261</a><span>]</span><span> sedang mereka tidak menyadarinya.</span></p><p><span>[a]</span><span> 22:48 ; 26:205 ; 27:72 </span><span>–</span><span> 73 ; 37:117 </span><span>–</span><span> 178.</span></p>');
INSERT INTO `quran` VALUES (9769,2,29,55,'<p><span>Mereka minta kepada engkau disegerakan azab. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2261a">2261a</a><span>]</span><span> Dan sesungguhnya </span><span>[b]</span><span> Jahannam akan mengepung orang-orang ingkar.</span></p><p><span>[b]</span><span> 9:49 ; 13:36 ; 17:9.</span></p>');
INSERT INTO `quran` VALUES (9770,2,29,56,'<p><span>[c]</span><span> Pada hari azab itu akan meliputi mereka dari atas mereka dan dari bawah kaki mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2262">2262</a><span>]</span><span> dan Dia akan berfirman, “Rasakanlah apa yang telah kamu kerjakan.”</span></p><p><span>[c]</span><span> 6:9.</span></p>');
INSERT INTO `quran` VALUES (9771,2,29,57,'<p><span>Hai hamba-hamba-Ku yang beriman ! Sesungguhnya bumi-Ku sangat luas, maka hanya kepada Aku saja kamu menyembah.</span></p>');
INSERT INTO `quran` VALUES (9772,2,29,58,'<p><span>[a]</span><span> Tiap-tiap jiwa akan merasakan maut ; kemudian kepada Kami-lah kamu akan dikembalikan.</span></p><p><span>[a]</span><span> 3:186 ; 21:36.</span></p>');
INSERT INTO `quran` VALUES (9773,2,29,59,'<p><span>Dan orang-orang yang beriman dan beramal shaleh, Kami pasti </span><span>[b]</span><span> menempatkan mereka di surga pada tempat-tempat yang tinggi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2263">2263</a><span>]</span><span> yang di bawahnya mengalir sungai-sungai, mereka akan kekal di dalamnya. Sebaik-baik ganjaran bagi orang-orang yang beramal.</span></p><p><span>[b]</span><span> 25:76 ; 34:38.</span></p>');
INSERT INTO `quran` VALUES (9774,2,29,60,'<p><span>[c]</span><span> Orang-orang yang bersabar, dan kepada Tuhan mereka, mereka bertawakkal.</span></p><p><span>[c]</span><span> 25:76 ; 34:38.</span></p>');
INSERT INTO `quran` VALUES (9775,2,29,61,'<p><span>Dan alangkah banyaknya hewan-hewan, yang tidak membawa perbekalannya ! </span><span>[d]</span><span> Allah yang memberi rezeki kepadanya dan kepada kamu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2264">2264</a><span>]</span><span> Dan Dia Yang Maha Mendengar, Maha Mengetahui.</span></p><p><span>[d]</span><span> 11:7.</span></p>');
INSERT INTO `quran` VALUES (9776,2,29,62,'<p><span>Dan jika engkau bertanya kepada mereka, “Siapakah yang telah menciptakan seluruh langit dan bumi dan </span><span>[e]</span><span> menundukkan matahari dan bulan ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2265">2265</a><span>]</span><span> Mereka pasti akan berkata, “Allah.” Maka kemanakah mereka berpaling ? </span></p><p><span>[e]</span><span> 7:55 ; 13:3 ; 31 30 ; 35:14 ; 39:6.</span></p>');
INSERT INTO `quran` VALUES (9777,2,29,63,'<p><span>[a]</span><span> Allah melapangkan rezeki bagi siapa yang Dia kehendaki dari hamba-hamba-Nya, Dan menyempitkan baginya. Sesungguhnya Allah Maha Mengetahui segala sesuatu.</span></p><p><span>[a]</span><span> 13:27 ; 30:38 ; 34:37 ; 39:53 ; 42:13.</span></p>');
INSERT INTO `quran` VALUES (9778,2,29,64,'<p><span>Dan jika engkau bertanya kepada mereka, “Siapakah yang menurunkan air dan awan, lalu dengannya menghidupkan bumi setalah matinya ? “ Mereka tentu akan berkata, “Allah” Katakanlah. “Segala puji bagi Allah” Tetapi kebanyakan mereka tidak mau mengerti.</span></p>');
INSERT INTO `quran` VALUES (9779,2,29,65,'<p><span>[b]</span><span> Dan tidaklah kehidupan di dunia ini melainkan pengisi waktu dan permainan. Dan sesungguhnya rumah di akhirat itulah kehidupan </span><span>yang hakiki</span><span> andaikata mereka mengetahui ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2266">2266</a><span>]</span></p><p><span>[b]</span><span> 6:33 ; 47:37 ; 57:21.</span></p>');
INSERT INTO `quran` VALUES (9780,2,29,66,'<p><span>[c]</span><span> Maka apabila mereka menaiki bahtera, mereka berdoa kepada Allah dengan mengikhlaskan ketaatan kepada-Nya. Tetapi bila Dia telah menyelamatkan mereka sampai ke darat, tiba-tiba mereka mulai mempersekutukan-</span><span>Nya.</span><span> </span></p><p><span>[c]</span><span> 10:23 ; 31:33</span></p>');
INSERT INTO `quran` VALUES (9781,2,29,67,'<p><span>[a]</span><span> Supaya mereka mengingkari apa-apa yang telah Kami berikan kepada mereka, agar mereka dapat bersenang-senang </span><span>sementara waktu.</span><span> Maka mereka segera akan mengetahui</span></p><p><span>[a]</span><span> 16:56 ; 30:35.</span></p>');
INSERT INTO `quran` VALUES (9782,2,29,68,'<p><span>Apakah mereka tidak memperlihatkan, bahwa Kami telah menjadikan tanah suci </span><span>Mekkah</span><span> aman, dan manusia dibawa secara paksa dari sekeliling mereka </span><span>di luar Mekkah ?</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2267">2267</a><span>] [b]</span><span> Maka apakah mereka akan beriman kepada yang bathil dan akan ingkar kepada karunia Allah ?</span></p><p><span>[b]</span><span> 16:73.</span></p>');
INSERT INTO `quran` VALUES (9783,2,29,69,'<p><span>[c]</span><span> Dan siapakah yang lebih aniaya daropada orang yang mengada-adakan dusta terhadap Allah, atau yang mendustakan kebenaran ketika datang kepadanya ? </span><span>[d]</span><span> Bukankah dalam Jahannam ada tempat tinggal bagi orang-orang kafir ?</span></p><p><span>[c]</span><span> 6:22 ; 10:18 ; 39:33.</span></p><p><span>[d]</span><span> 18:103 ; 33:9 ; 48:14.</span></p>');
INSERT INTO `quran` VALUES (9784,2,29,70,'<p><span>Dan orang-orang yang berjuang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2268">2268</a><span>]</span><span> untuk Kami, sesungguhnya Kami akan memberi petunjuk kepada mereka pada jalan Kami. Dan sesungguhnya Allah beserta orang- orang yang berbuat kebaikan.</span></p>');
INSERT INTO `quran` VALUES (9785,2,30,1,'<p><span>Juz 21</span></p><p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9786,2,30,2,'<p><span>[b]</span><span> Aku, Allah Yang Maha Mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2269">2269</a><span>]</span></p><p><span>[b]</span><span> 29:2.</span></p>');
INSERT INTO `quran` VALUES (9787,2,30,3,'<p><span>Telah dikalahkan bangsa Romawi,</span></p>');
INSERT INTO `quran` VALUES (9788,2,30,4,'<p><span>Di negeri yang dekat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2269a">2269a</a><span>]</span><span> dan mereka sesudah kekalahan mereka, akan memperoleh kemenangan,</span></p>');
INSERT INTO `quran` VALUES (9789,2,30,5,'<p><span>Dalam beberapa tahun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2270">2270</a><span>] [c]</span><span> Bagi Allah kedaulatan sebelum dan sesudah-</span><span>nya.</span><span> Dan pada hari itu akan bergembira orang-orang mukmin, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2271">2271</a><span>]</span></p><p><span>[c]</span><span> 3:155 ; 13:32.</span></p>');
INSERT INTO `quran` VALUES (9790,2,30,6,'<p><span>Dengan pertolongan Allah Dia menolong siapa yang dikehendaki-Nya. Dan Dia Maha perkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9791,2,30,7,'<p><span>Ingatlah</span><span> janji Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2272">2272</a><span>] [a]</span><span> Allah tidak menyalahi janji-Nya, akan tetapi kebanyakan manusia tidak mengetahui.</span></p><p><span>[a]</span><span> 3:195 ; 39:21.</span></p>');
INSERT INTO `quran` VALUES (9792,2,30,8,'<p><span>Mereka mengetahui bagian zahir kehidupan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2273">2273</a><span>]</span><span> dunia ini, dan mereka mengenai akhirat mereka lalai.</span></p>');
INSERT INTO `quran` VALUES (9793,2,30,9,'<p><span>[a]</span><span> Apakah tidak mereka itu merenungkan tentang diri mereka sendiri, </span><span>bahwa</span><span> Allah tidak menciptakan seluruh langit dan bumi dan apa-apa di antara keduanya, melainkan dengan hak, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2274">2274</a><span>]</span><span> dan untuk masa yang telah ditentukan ? </span><span>[b]</span><span> Dan sesungguhnya kebanyakan manusia terhadap pertemuan dengan Tuhan mereka, benar-benar ingkar.</span></p><p><span>[a]</span><span> 7:186.</span></p><p><span>[b]</span><span> 10:46 ; 29:24 ; 32:11.</span></p>');
INSERT INTO `quran` VALUES (9794,2,30,10,'<p><span>[c]</span><span> Apakah mereka itu tidak berjalan di bumi ini, supaya mereka dapat melihat betapa akibatnya orang-orang sebelum mereka ? Mereka itu lebih unggul dalam kekuatan dari pada mereka, mereka itu mengolah tanah dan menghuninya lebih banyak daripada yang telah mereka huni. Dan datang kepada mereka rasul-rasul mereka dengan Tanda-tanda yang nyata. </span><span>[d]</span><span> Dan Allah tidak akan berbuat aniaya terhadap mereka, melainkan mereka sendirilah yang telah berbuat aniaya terhadap diri mereka sendiri.</span></p><p><span>[c]</span><span> 12:110 ; 22:47 ; 35:45 ; 47:11.</span></p><p><span>[d]</span><span> 4:41 ; 10:45.</span></p>');
INSERT INTO `quran` VALUES (9795,2,30,11,'<p><span>Kemudian akibat orang-orang yang berbuat buruk, sangat buruk karena mereka mendustakan Tanda-tanda Allah dan mereka memperolokkannya.</span></p>');
INSERT INTO `quran` VALUES (9796,2,30,12,'<p><span>R. 2</span></p><p><span>[a]</span><span> Allah memulai penciptaan, lalu Dia mengulang-ulanginya ; kemudian kepada-Nya kamu akan dikembalikan.</span></p><p><span>[a]</span><span> 29:20.</span></p>');
INSERT INTO `quran` VALUES (9797,2,30,13,'<p><span>Dan pada hari ketika saat itu tiba, </span><span>[b]</span><span> akan putus-asa orang-orang berdosa.</span></p><p><span>[b]</span><span> 6:45.</span></p>');
INSERT INTO `quran` VALUES (9798,2,30,14,'<p><span>Dan tidak ada bagi mereka dari sekutu-sekutu mereka yang memberi syafaat dan </span><span>[c]</span><span> mereka mengingkari sekutu-sekutu mereka.</span></p><p><span>[c]</span><span> 10:29.</span></p>');
INSERT INTO `quran` VALUES (9799,2,30,15,'<p><span>Dan, pada hari ketika saat itu tiba, pada hari itu mereka akan terpisah-pisah.</span></p>');
INSERT INTO `quran` VALUES (9800,2,30,16,'<p><span>Adapun </span><span>[d]</span><span> orang-orang yang beriman dan beramal shaleh, maka mereka di dalam taman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2275">2275</a><span>] </span><span>akan diberi kegembiraan.</span></p><p><span>[d]</span><span> 4:176 ; 13:30 ; 14:24 ; 22:57 ; 42:23 ; 68:35.</span></p>');
INSERT INTO `quran` VALUES (9801,2,30,17,'<p><span>Namun, </span><span>[a]</span><span> mengenai orang-orang yang telah ingkar dan mendustakan Tanda-tanda Kami dan pertemuan di akhirat, mereka akan dihadapkan kepada azab.</span></p><p><span>[a]</span><span> 2:40 ; 7:37 ; 57:20 ; 64:11 ; 78:22 </span><span>–</span><span> 29.</span></p>');
INSERT INTO `quran` VALUES (9802,2,30,18,'<p><span>[b]</span><span> Maka Maha Suci Allah, </span><span>bertasbihlah</span><span> pada waktu petang dan di waktu pagi.</span></p><p><span>[b]</span><span> 17:79 ; 20:131 ; 50:40.</span></p>');
INSERT INTO `quran` VALUES (9803,2,30,19,'<p><span>Dan, bagi-nya segala puji di seluruh langit dan bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2276">2276</a><span>]</span><span> dan </span><span>bertasbihlah</span><span> pada waktu Isya dan pada waktu Zuhur.</span></p>');
INSERT INTO `quran` VALUES (9804,2,30,20,'<p><span>[c]</span><span> Dia mengeluarkan yang hidup dari yang mati, dan Dia mengeluarkan yang mati dari yang hidup, dan Dia menghidupkan bumi setelah matinya. Dan demikianlah pula kamu pun akan dibangkitkan.</span></p><p><span>[c]</span><span> 10:32.</span></p>');
INSERT INTO `quran` VALUES (9805,2,30,21,'<p><span>R. 3</span></p><p><span>Dan dari antara Tanda-tanda-Nya ialah bahwa Dia menciptakan kamu dari tanah, kemudian kamu tiba-tiba menjadi manusia yang bertebaran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2277">2277</a><span>]</span><span> </span><span>di muka bumi.</span><span> </span></p>');
INSERT INTO `quran` VALUES (9806,2,30,22,'<p><span>Dan, dari antara Tanda-tanda-Nya ialah, bahwa </span><span>[a]</span><span> Dia telah menciptakan bagimu jodoh-jodoh dari jenismu sendiri, supaya kamu memperoleh ketenteraman padanya, dan Dia telah menjadikan di antara kamu kecintaan dan kasih-sayang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2278">2278</a><span>]</span><span> Sesungguhnya di dalam yang demikian itu ada Tanda-tanda bagi kaum yang berfikir.</span></p><p><span>[a]</span><span> 4:2 ; 7:190 ; 16:73 ; 39:7.</span></p>');
INSERT INTO `quran` VALUES (9807,2,30,23,'<p><span>[b]</span><span> Dan dari Tanda-tanda-Nya ialah penciptaan seluruh langit dan bumi, dan perbedaan bahasamu dan warna kulitmu. Sesungguhnya dalam yang demikian itu ada Tanda-tanda bagi mereka yang berilmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2279">2279</a><span>]</span></p><p><span>[b]</span><span> 42:30.</span></p>');
INSERT INTO `quran` VALUES (9808,2,30,24,'<p><span>[a]</span><span> Dan dari antara Tanda-tanda-Nya ialah tidurmu pada malam hari dan siang hari dan usahamu mencari karunia-Nya. Sesungguhnya dalam yang demikian itu adalah Tanda-tanda bagi kaum yang mau mendengar.</span></p><p><span>[a]</span><span> 10:68 ; 27:87 ; 28:74.</span></p>');
INSERT INTO `quran` VALUES (9809,2,30,25,'<p><span>Dan dari antara Tanda-tanda-Nya ialah, </span><span>[b]</span><span> Dia memperlihatkan kepadamu kilat yang menimbulkan ketakutan dan harapan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2280">2280</a><span>]</span><span> dan </span><span>[c]</span><span> Dia menurunkan air dari awan dan dengan </span><span>air</span><span> itu Dia menghidupkan bumi sesudah matinya. Sesungguhnya, dalam yang demikian itu ada Tanda-tanda bagi kaum yang berakal.</span></p><p><span>[b] </span><span>13:13.</span></p><p><span>[c]</span><span> 40:14 ; 42:29.</span></p>');
INSERT INTO `quran` VALUES (9810,2,30,26,'<p><span>[d]</span><span> Dan dari antara Tanda-tanda-Nya ialah, bahwa berdirinya langit dan bumi dengan perintah-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2281">2281</a><span>]</span><span> Kemudian, ketika Dia memanggil kamu dengan satu panggilan saja </span><span>untuk keluar</span><span> dari bumi, tiba-tiba kamu akan keluar.</span></p><p><span>[d]</span><span> 35:42.</span></p>');
INSERT INTO `quran` VALUES (9811,2,30,27,'<p><span>[a]</span><span> Dan kepunyaan Dia-lah siapa pun yang ada di seluruh langit dan bumi. Semuanya patuh kepada-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2282">2282</a><span>]</span></p><p><span>[a]</span><span> 16:53 ; 20:7 ; 21:20 ; 22:65.</span></p>');
INSERT INTO `quran` VALUES (9812,2,30,28,'<p><span>[b]</span><span> Dan Dia-lah Yang memulai penciptaan, kemudian Dia mengulanginya dan hal itu sangat mudah bagi-Nya. Dan kepunyaan Dia-lah kedudukan yang tertinggi di seluruh langit dan bumi, dan Dia Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b]</span><span> 10:35 ; 27:65 ; 29:20.</span></p>');
INSERT INTO `quran` VALUES (9813,2,30,29,'<p><span>Dia mengemukakan bagimu perumpamaan mengenai dirimu. Adakah dari orang-orang yang dimiliki tangan kananmu menjadi milik bersama di dalam yang Kami rezekikan kepadamu yang kamu di dalamnya sama </span><span>bagiannya,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2283">2283</a><span>]</span><span> kamu takut kepada mereka seperti ketakutanmu kepada diri sendiri ? Demikianlah Kami jelaskan Tanda-tanda itu bagi kaum yang berakal.</span></p>');
INSERT INTO `quran` VALUES (9814,2,30,30,'<p><span>Bahkan, orang-orang yang aniaya itu menuruti hawa nafsunya tanpa ilmu. Kemudian </span><span>[a]</span><span> siapakah yang dapat memberi petunjuk kepada orang yang dibiarkan sesat oleh Allah ? Dan, bagi mereka tidak ada penolong-penolong.</span></p><p><span>[a]</span><span> 7:187 ; 13:34 ; 39:37 ; 40:34.</span></p>');
INSERT INTO `quran` VALUES (9815,2,30,31,'<p><span>[b]</span><span> Maka hadapkanlah wajahmu kepada agama yang lurus. </span><span>Turutilah</span><span> fitrat Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2284">2284</a><span>] </span><span>yang dengannya Dia menciptakan manusia. Tiada perubahan dalam penciptaan Allah </span><span>[c]</span><span> Itulah agama yang kekal. tetapi kebanyakan menusia tidak mengetahui.</span></p><p><span>[b]</span><span> 10:106 ; 30:44.</span></p><p><span>[c]</span><span> 98:6.</span></p>');
INSERT INTO `quran` VALUES (9816,2,30,32,'<p><span>Tunduklah kamu kepada-Nya dan bertakwalah kepada-Nya dan dirikanlah shalat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2285">2285</a><span>] </span><span>dan janganlah kami termasuk orang-orang yang musyrik.</span></p>');
INSERT INTO `quran` VALUES (9817,2,30,33,'<p><span>[a]</span><span> Yaitu, orang-orang yang memecah-belah agama mereka dan mereka menjadi golongan-golongan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2286">2286</a><span>]</span><span> Tiap-tiap golongan gembira dengan apa yang ada pada mereka.</span></p><p><span>[a]</span><span> 6:160.</span></p>');
INSERT INTO `quran` VALUES (9818,2,30,34,'<p><span>[b]</span><span> Dan apabila suatu kesusahan menimpa manusia, mereka berseru kepada Tuhan mereka, seraya tunduk kepada-Nya, kemudian apabila dirasakan kepada mereka rahmat dari Dia, tiba-tiba segolongan dari mereka mempersekutukan Tuhan mereka.</span></p><p><span>[b]</span><span> 10:13; 39:9, 50.</span></p>');
INSERT INTO `quran` VALUES (9819,2,30,35,'<p><span>[c]</span><span> Akibatnya mereka mengingkari apa yang telah Kami berikan kepada mereka. Maka bersenang-senang kamu sejenak, lalu segera kamu akan mengetahui.</span></p><p><span>[c]</span><span> 16:56 ; 29:67.</span></p>');
INSERT INTO `quran` VALUES (9820,2,30,36,'<p><span>Apakah Kami menurunkan kepada mereka suatu dalil </span><span>syirik</span><span>, lalu dalil itu membicarakan tentang apa yang mereka persekutukan dengan-Nya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2387">2387</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9821,2,30,37,'<p><span>[a]</span><span> Dan apabila Kami merasakan rahmat kepada manusia, mereka bergembira karenanya. Dan jika suatu keburukan menimpa mereka disebabkan apa yang diperbuat tangan mereka, tiba-tiba mereka menjadi putus asa.</span></p><p><span>[a]</span><span> 10:22 ; 41:51 </span><span>–</span><span> 52 ; 42:49.</span></p>');
INSERT INTO `quran` VALUES (9822,2,30,38,'<p><span>Apakah mereka tidak melihat, bahwa </span><span>[b]</span><span> Allah melapangkan rezeki bagi siapa yang dikehendaki-Nya, dan menyempitkan ? Sesungguhnya dalam yang demikian itu ada Tanda- tanda bagi kaum yang beriman.</span></p><p><span>[b]</span><span> 29:63.</span></p>');
INSERT INTO `quran` VALUES (9823,2,30,39,'<p><span>[c]</span><span> Maka berikanlah kepada kaum kerabat haknya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2288">2288</a><span>]</span><span> dan orang miskin, dan orang musafir, Yang demikian itu paling baik bagi orang-orang yang menginginkan keridhaan Allah, dan mereka itulah orang-orang yang memperoleh kebahagian. </span></p><p><span>[c]</span><span> 16:91 ; 17:27.</span></p>');
INSERT INTO `quran` VALUES (9824,2,30,40,'<p><span>[a]</span><span> Dan apa yang kamu berikan untuk memperoleh riba supaya bertambah banyak pada harta manusia, padahal harta itu tidak bertambah banyak di sisi Allah, tetapi apa-apa yang kamu berikan sebagai zakat dengan menginginkan keridhaan Allah, maka mereka itulah orang-orang yang akan mendapat berlipat-ganda. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2289">2289</a><span>]</span></p><p><span>[a] </span><span>2:276 </span><span>–</span><span> 277.</span></p>');
INSERT INTO `quran` VALUES (9825,2,30,41,'<p><span>Allah Yang telah menciptakan kamu, kemudian memberi rezeki kepadamu, kemudian </span><span>[b]</span><span> Dia mematikan kamu, kemudian Dia menghidupkan kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2290">2290</a><span>]</span><span> Adakah dari antara tuhan-tuhan sekutumu itu, yang dapat berbuat sesuatu dari hal itu ? Maha Suci Dia dan Maha Tinggi daripada apa yang mereka sekutukan.</span></p><p><span>[b]</span><span> 2:29 ; 22:67 ; 40:69 ; 45:27.</span></p>');
INSERT INTO `quran` VALUES (9826,2,30,42,'<p><span>Kerusakan telah meluas di daratan dan di lautan, di sebabkan perbuatan tangan manusia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2291">2291</a><span>]</span><span> supaya dirasakan kepada mereka </span><span>akibat</span><span> sebagian perbuatan yang mereka lakukan, supaya mereka kembali </span><span>dari kedurhakaannya.</span></p>');
INSERT INTO `quran` VALUES (9827,2,30,43,'<p><span>Katakanlah, </span><span>[a]</span><span> “Berjalanlah di bumi dan lihatlah betapa </span><span>buruknya</span><span> akibat bagi orang- orang sebelum </span><span>kamu</span><span> ini. Kebanyakan mereka itu orang-orang musyrik.”</span></p><p><span>[a]</span><span> 16:37 ; 27:70 ; 40:83.</span></p>');
INSERT INTO `quran` VALUES (9828,2,30,44,'<p><span>[b]</span><span> Maka hadapkanlah wajahmu kepada agama yang kekal, sebelum datang hari yang tidak dapat dihindarkan dari Allah, pada hari itu </span><span>orang-orang mukmin dan kafir</span><span> akan terpisah.</span></p><p><span>[b]</span><span> 10:106 ; 30:31.</span></p>');
INSERT INTO `quran` VALUES (9829,2,30,45,'<p><span>Barangsiapa yang ingkar, maka dia menanggung kekufurannya, dan barangsiapa yang beramal shaleh maka mereka menyediakan faedah bagi diri mereka.</span></p>');
INSERT INTO `quran` VALUES (9830,2,30,46,'<p><span>[a]</span><span> Supaya Dia memberi pahala kepada orang-orang yang beriman dan beramal shaleh dari karunia-Nya. Sesungguhnya Dia tidak mencintai orang-orang kafir.</span></p><p><span>[a]</span><span> 10:5 ; 34:5.</span></p>');
INSERT INTO `quran` VALUES (9831,2,30,47,'<p><span>Dan dari Tanda-tanda-Nya, bahwa Dia mengirimkan angin sebagai pembawa khabar suka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2292">2292</a><span>]</span><span> dan supaya Dia merasakan kepadamu sebagian rahmat-Nya, dan supaya </span><span>[b]</span><span> bahtera-bahtera melaju atas perintah-Nya, dan supaya kamu mencari karunia-Nya, dan supaya kamu bersyukur.</span></p><p><span>[b]</span><span> 17:67 ; 31:32 ; 45:13.</span></p>');
INSERT INTO `quran` VALUES (9832,2,30,48,'<p><span>Dan sesungguhnya Kami telah mengutus rasul-rasul sebelummu kepada kaum mereka, maka mereka itu membawa kepada mereka Tanda-tanda yang nyata, maka Kami membalas kepada orang-orang yang berbuat dosa. </span><span>[a]</span><span> Dan telah menjadi kewajiban atas Kami menolong orang-orang yang beriman.</span></p><p><span>[a]</span><span> 10:104 ; 40:52 ; 58:22.</span></p>');
INSERT INTO `quran` VALUES (9833,2,30,49,'<p><span>[b]</span><span> Allah Yang mengirimkan angin, supaya mengangkat </span><span>uap berupa </span><span> awan. Kemudian Dia menyebarkan awan itu di langit sebagaimana awan itu di langit sebagaimana Dia kehendaki, dan Dia menjadikan </span><span>awan</span><span> itu tersebar bergumpal-gumpal, dan engkau melihat hujan keluar dari tengah-tengah </span><span>awan</span><span> itu. Lalu, apabila Dia menurunkan </span><span>hujan</span><span> itu kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya, tiba-tiba mereka bersuka-rialah.</span></p><p><span>[b]</span><span> 28:44.</span></p>');
INSERT INTO `quran` VALUES (9834,2,30,50,'<p><span>Walaupun mereka itu ialah sebelum </span><span>hujan</span><span> itu diturunkan atas mereka, sebelumnya mereka itu pasti telah putus asa.</span></p>');
INSERT INTO `quran` VALUES (9835,2,30,51,'<p><span>Maka lihatlah kepada Tanda-tanda rahmat Allah,bagaimana </span><span>[a]</span><span> Dia menghidupkan bumi setelah matinya. Sesungguhnya Dia Yang menghidupkan yang telah mati, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2293">2293</a><span>]</span><span> sebab Dia berkuasa atas segala sesuatu.</span></p><p><span>[a]</span><span> 16:66 ; 22:6 ; 39:22 ; 45:6.</span></p>');
INSERT INTO `quran` VALUES (9836,2,30,52,'<p><span>Dan sekiranya Kami mengirimkan angin dan </span><span>[b]</span><span> mereka melihatnya </span><span>ladang-ladang mereka</span><span> telah menguning, niscaya selalu mereka sesudah itu tidak bersyukur.</span></p><p><span>[b]</span><span> 56:66 ; 57:21.</span></p>');
INSERT INTO `quran` VALUES (9837,2,30,53,'<p><span>Maka sesungguhnya engkau tidak dapat membuat mendengar orang-orang yang telah mati, dan </span><span>[c]</span><span> tidak dapat membuat orang-orang tuli mendengar seruan, apabila mereka mundur sambil membalikkan punggung mereka ;</span></p><p><span>[c]</span><span> 10:43 ; 21:46 ; 27:81.</span></p>');
INSERT INTO `quran` VALUES (9838,2,30,54,'<p><span>[d]</span><span> Dan tidak pula engkau dapat memberi petunjuk kepada orang-orang buta supaya </span><span>keluar </span><span>dari kesesatan mereka. Engkau tidak dapat membuat mereka mendengar, kecuali mereka yang beriman kepada Tanda-tanda Kami dan berserah diri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2294">2294</a><span>]</span></p><p><span>[d]</span><span> 10:44 ; 27:82.</span></p>');
INSERT INTO `quran` VALUES (9839,2,30,55,'<p><span>[a]</span><span> Allah Yang menciptakan kamu dalam keadaan lemah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2295">2295</a><span>]</span><span> kemudian Dia jadikan setelah kelemahan, kekuatan, kemudian setelah kuat Dia menjadikanmu lemah dan tua. Dia menciptakan apa yang Dia kehendaki, dan Dia Maha Mengetahui, Maha Kuasa.</span></p><p><span>[a]</span><span> 40:68.</span></p>');
INSERT INTO `quran` VALUES (9840,2,30,56,'<p><span>Dan pada hari ketika datang saat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2296">2296</a><span>]</span><span> </span><span>yang telah ditetapkan, </span><span>akan bersumpah orang- orang yang berdosa, bahwa </span><span>[b]</span><span> mereka tidak tinggal </span><span>di dunia</span><span> kecuali sesaat saja. Demikianlah mereka itu telah dipalingkan </span><span>dari jalan lurus.</span></p><p><span>[b]</span><span> 10:46 ; 46:36.</span></p>');
INSERT INTO `quran` VALUES (9841,2,30,57,'<p><span>Dan berkata orang-orang yang diberi ilmu dan iman. “Sesungguhnya kamu telah tinggal menurut Kitab Allah sampai Hari Kebangkitan. Dan inilah Hari Kebangkitan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2297">2297</a><span>]</span><span> akan tetapi kamu tidak </span><span>berusaha</span><span> mengetahuinya.”</span></p>');
INSERT INTO `quran` VALUES (9842,2,30,58,'<p><span>Maka </span><span>[a]</span><span> pada hari itu tidak akan bermanfaat bagi orang-orang yang aniaya alasan mereka, dan mereka tidak akan dikabulkan tobat mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2298">2298</a><span>]</span></p><p><span>[a]</span><span> 16:85 ; 41:25 ; 45:36.</span></p>');
INSERT INTO `quran` VALUES (9843,2,30,59,'<p><span>Dan sesungguhnya </span><span>[b]</span><span> telah Kami jelaskan bagi manusia dalam Alquran ini segala macam tamsil ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2299">2299</a><span>]</span><span> dan apabila engkau membawa kepada mereka suatu Tanda, tentulah berkata orang-orang yang ingkar, “Kamu tidak lain melainkan penipu belaka.”</span></p><p><span>[b]</span><span> 17:90 ; 39:28.</span></p>');
INSERT INTO `quran` VALUES (9844,2,30,60,'<p><span>[c]</span><span> Demikianlah Allah memeterai atas kalbu orang-orang yang tidak mau memahami. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2300">2300</a><span>]</span><span> </span></p><p><span>[c]</span><span> 9:93 ; 16:109 ; 47:17.</span></p>');
INSERT INTO `quran` VALUES (9845,2,30,61,'<p><span>Maka bersabarlah, sesungguhnya janji Allah itu benar, dan janganlah engkau dibuat lemah oleh orang-orang yang tidak mempunyai keyakinan.</span></p>');
INSERT INTO `quran` VALUES (9846,2,31,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9847,2,31,2,'<p><span>[b]</span><span> Aku, Allah Ynag Maha Mengetahui.</span></p><p><span>[b]</span><span> 30:2.</span></p>');
INSERT INTO `quran` VALUES (9848,2,31,3,'<p><span>[c]</span><span> Inilah ayat-ayat Kitab yang sempurna, penuh kebijaksanaan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2301">2301</a><span>]</span></p><p><span>[c]</span><span> 10:2.</span></p>');
INSERT INTO `quran` VALUES (9849,2,31,4,'<p><span>[d]</span><span> Suatu petunjuk dan rahmat bagi mereka yang berbuat kebaikan.</span></p><p><span>[d]</span><span> 16:90 ; 27:3.</span></p>');
INSERT INTO `quran` VALUES (9850,2,31,5,'<p><span>[e]</span><span> Mereka yang mendirikan shalat dan membayar zakat dan kepada </span><span>kehidupan</span><span> akhirat mereka yakin.</span></p><p><span>[e]</span><span> 2:4 ; 5:56 ; 9:71 ; 27:4.</span></p>');
INSERT INTO `quran` VALUES (9851,2,31,6,'<p><span>[f]</span><span> Mereka itulah yang mengikuti petunjuk dari Tuhan mereka, dan mereka itulah orang- orang yang beruntung.</span></p><p><span>[f]</span><span> 2:6.</span></p>');
INSERT INTO `quran` VALUES (9852,2,31,7,'<p><span>Dan di antara manusia ada orang yang membeli dengan </span><span>hartanya </span><span> cerita-cerita kosong, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2302">2302</a><span>]</span><span> supaya mennyesatkan </span><span>orang-orang </span><span> dari jalan Allah tanpa ilmu, dan menjadikannya sebagai olok-olok. Mereka itulah baginya ada azab yang menghinakan.</span></p>');
INSERT INTO `quran` VALUES (9853,2,31,8,'<p><span>Dan ketika dibacakan kepadanya ayat-ayat Kami berpalinglah ia dengan sombong, seolah-olah ia tidak mendengarnya, seakan-akan pada kedua telinganya ada ketulian. Maka berilah khabar suka dia tentang azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (9854,2,31,9,'<p><span>Sesungguhnya, mereka yang beriman dan beramal shaleh, bagi mereka ada Kebun-kebun Kenikmatan,</span></p>');
INSERT INTO `quran` VALUES (9855,2,31,10,'<p><span>Mereka akan menetap di dalamnya. Janji Allah itu benar. Dan Dia Maha Perkasa, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (9856,2,31,11,'<p><span>[a]</span><span> Dia telah menciptakan seluruh langit tanpa tiang sebagaimana kamu melihatnya dan </span><span>[b]</span><span> Dia telah menempatkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2303">2303</a><span>]</span><span> di bumi gunung-gunung, supaya </span><span>bumi</span><span> ini tidak bergoncang bersamamu, dan Dia telah menyebarkan di dalamnya segala macam binatang, dan Kami menurunkan air dari awan, maka Kami </span><span>[c]</span><span> menimbulkan tumbuhan di dalamnya segala jenis yang baik.</span></p><p><span>[a]</span><span> 13:3. </span></p><p><span>[b]</span><span> 13:4 ; 15:20 ; 16:16 ; 77:28.</span></p><p><span>[c]</span><span> 27:61 ; 50:8.</span></p>');
INSERT INTO `quran` VALUES (9857,2,31,12,'<p><span>Inilah makhluk Allah, maka perlihatkanlah kepadaku apa yang telah diciptakan oleh yang lain selain Dia. Tidak, bahkan sebenarnya orang-orang yang aniaya itu dalam kesesatan yang nyata.</span></p>');
INSERT INTO `quran` VALUES (9858,2,31,13,'<p><span>R. 2</span></p><p><span>Dan sesungguhnya Kami telah menganugerahkan kepada Luqman kebijaksanaan </span><span>dan ia berkata,</span><span> “Bersyukurlah kepada Allah,” dan barangsiapa bersyukur maka sesungguhnya dia bersyukur untuk faedah dirinya. Dan barangsiapa tidak bersyukur, maka sesungguhnya Allah itu Maha Kaya, Maha Terpuji.</span></p>');
INSERT INTO `quran` VALUES (9859,2,31,14,'<p><span>Dan </span><span>ingatlah</span><span> tatkala berkata Luqman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2304">2304</a><span>]</span><span> kepada anaknya ketika memberi nasihat kepadanya, “Wahai anakku ! Janganlah kamu mempersekutukan </span><span>sesuatu</span><span> dengan Allah. Sesungguhnya syirik itu pasti keaniayaan besar.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2305">2305</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9860,2,31,15,'<p><span>[a]</span><span> Dan Kami telah memerintahkan kepada manusia </span><span>supaya berbuat baik </span><span>terhadap ibu-bapak-nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2306">2306</a><span>]</span><span> ibunya telah mengandungnya dalam kelemahan di atas kelemahan, dan penyapuhan susunya dalam dua tahun, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2306a">2306a</a><span>]</span><span> supaya bersyukurlah kepada-Ku dan kepada kedua orangtua engkau. Kepada Aku-lah tempat kembali.</span></p><p><span>[a]</span><span> 6:152 ; 29:9 ; 46:16.</span></p>');
INSERT INTO `quran` VALUES (9861,2,31,16,'<p><span>Dan apabila keduanya memaksa engkau supaya engkau mempersekutukan dengan Aku, yang mengenai itu engkau tidak mempunyai pengetahuan, maka janganlah engkau menaati kedua-nya, tetapi bergaullah dengan keduanya dengan baik-baik dalam urusan dunia ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2307">2307</a><span>]</span><span> dan ikutilah jalan orang yang tunduk kepada-Ku. Kemudian kepada-Ku tempat kembalimu, maka Aku akan memberitahukan kepadamu tentang apa yang senantiasa kamu kerjakan ;</span></p>');
INSERT INTO `quran` VALUES (9862,2,31,17,'<p><span>Luqman berkata,</span><span> “Wahai anakku ! Sekali pun </span><span>amal</span><span> itu sebesar biji sawi, dan sekalipun </span><span>amal itu tersembunyi</span><span> di dalam batu karang, atau di dalam seluruh langit atau di dalam bumi, niscaya Allah akan mengeluarkannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2308">2308</a><span>]</span><span> Sesungguhnya Allah Maha Mengetahui rahasia-rahasia paling tersembunyi, Maha Waspada.</span></p>');
INSERT INTO `quran` VALUES (9863,2,31,18,'<p><span>“Wahai anakku ! Dirikanlah shalat dan suruhlah orang mengerjakan kebaikan dan cegahlah orang berbuat kemungkaran, dan bersabarlah atas apa yang menimpamu. Sesungguhnya, yang demikian itu adalah dari perkara-perkara penting. </span></p>');
INSERT INTO `quran` VALUES (9864,2,31,19,'<p><span>“Dan janganlah engkau memalingkan pipimu dari orang-orang dengan angkuh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2309">2309</a><span>]</span><span> dan </span><span>[a] </span><span>jangan berjalan di bumi dengan sombong. Sesungguhnya Allah tidak mencintai orang yang congkak dan sombong ; </span></p><p><span>[a]</span><span> 17:38 ; 25:64.</span></p>');
INSERT INTO `quran` VALUES (9865,2,31,20,'<p><span>“Dan berjalanlah kamu dengan sederhana, dan rendahkanlah suaramu. Sesungguhnya yang paling tidak menyenangkan di antara suara-suara ialah suara keledai.”</span></p>');
INSERT INTO `quran` VALUES (9866,2,31,21,'<p><span>R. 3</span></p><p><span>Apakah kamu tidak melihat bahwa Allah telah menundukkan bagi kamu apa yang ada di seluruh langit dan apa yang ada di bumi, dan Dia telah menyempurnakan atasmu nikmat- nikmat-Nya, baik yang nampak ataupun yang tidak nampak ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2310">2310</a><span>]</span><span> Dan di antara manusia ada orang-orang yang </span><span>[a]</span><span> berbantah mengenai Allah tanpa ilmu dan tanpa petunjuk dan tanpa Kitab yang bersinar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2311">2311</a><span>]</span></p><p><span>[a]</span><span> 13:14 ; 22:4, 9.</span></p>');
INSERT INTO `quran` VALUES (9867,2,31,22,'<p><span>Dan apabila dikatakan kepada mereka, “Ikutilah apa yang telah diturunkan Allah,” </span><span>[b]</span><span> mereka berkata, “Tidak, bahkan kami akan mengikuti apa yang kami dapati atasnya dari bapak-bapak kami.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2312">2312</a><span>]</span><span> Apakah walaupun syaitan mengajak mereka kepada azab yang menyala-nyala ? </span></p><p><span>[b]</span><span> 5:105 ; 10:79 ; 21:54.</span></p>');
INSERT INTO `quran` VALUES (9868,2,31,23,'<p><span>[a]</span><span> Dan barangsiapa menyerahkan perhatiannya kepada Allah dan ia seorang yang berbuat kebaikan, maka sesungguhnya ia telah berpegang pada lingkaran yang kuat. Dan kepada Allah-lah akibat segala perkara. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2313">2313</a><span>]</span><span> </span></p><p><span>[a]</span><span> 2:113.</span></p>');
INSERT INTO `quran` VALUES (9869,2,31,24,'<p><span>Dan, </span><span>[b]</span><span> barangsiapa yang ingkar, maka janganlah menyedihkan engkau keingkarannya. Kepada Kami kembali mereka dan Kami akan memberitahukan kepada mereka apa yang mereka kerjakan. Sesungguhnya Allah Maha Mengetahui segala isi hati.</span></p><p><span>[b]</span><span> 3:177.</span></p>');
INSERT INTO `quran` VALUES (9870,2,31,25,'<p><span>Kami akan memberikan sedikit manfaat duniawi kepada mereka; kemudian Kami akan memaksakan mereka kepada azab yang keras.</span></p>');
INSERT INTO `quran` VALUES (9871,2,31,26,'<p><span>[c]</span><span> Dan, sekiranya engkau menanyakan kepada mereka, “Siapakah yang telah menciptakan seluruh langit dan bumi ? “ Pasti mereka akan berkata “Allah.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2314">2314</a><span>]</span><span> Katakanlah, “Segala puji kepunyaan Allah.” Tetapi kebanyakan mereka tidak mengetahui.</span></p><p><span>[c]</span><span> 29:62 ; 39:39.</span></p>');
INSERT INTO `quran` VALUES (9872,2,31,27,'<p><span>[a]</span><span> Kepunyaan Allah apa yang ada di seluruh langit dan bumi. Sesungguhnya Dia-lah Allah Maha Kaya, Maha Terpuji.</span></p><p><span>[a]</span><span> 2:285 ; 10:56 ; 24:65.</span></p>');
INSERT INTO `quran` VALUES (9873,2,31,28,'<p><span>[b]</span><span> Dan andaikata semua pohon yang ada di bumi ini menjadi pena dan laut ditambah sesudahnya tujuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2315">2315</a><span>]</span><span> samudera </span><span>menjadi tinta,</span><span> niscaya tidak akan habisnya kalimat Allah. Sesungguhnya Allah Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b]</span><span> 18:110.</span></p>');
INSERT INTO `quran` VALUES (9874,2,31,29,'<p><span>Tidaklah penciptaanmu dan kebangkitanmu hanya seperti </span><span>penciptaan </span><span>suatu jiwa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2315a">2315a</a><span>]</span><span> Sesungguhnya Allah Maha Mendengar, Maha Melihat.</span></p>');
INSERT INTO `quran` VALUES (9875,2,31,30,'<p><span>Apakah engkau tidak melihat bahwa </span><span>[c]</span><span> Allah memasukkan malam ke dalam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2316">2316</a><span>]</span><span> siang, dan memasukkan siang ke dalam malam, dan </span><span>[d]</span><span> Dia telah menundukkan matahari dan bulan, masing-masing beredar terus sampai masa yang telah ditentukan, dan sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan ?</span></p><p><span>[c]</span><span> 22:62 ; 35:14 ; 57:7.</span></p><p><span>[d]</span><span> 7:55 ; 13:3 ;35:14 ; 39:6.</span></p>');
INSERT INTO `quran` VALUES (9876,2,31,31,'<p><span>Hal demikian itu sesungguhnya Allah Dia-lah Yang haq dan bahwa apa yang mereka seru selain Dia adalah bathil, dan bahwa sesungguhnya Allah, Dia-lah Dzat Yang Maha Tinggi, Maha Besar.</span></p>');
INSERT INTO `quran` VALUES (9877,2,31,32,'<p><span>R. 4</span></p><p><span>Apakah engkau tidak melihat bahwa </span><span>[a]</span><span> bahtera-bahtera berlayar di lautan dengan nikmat Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2317">2317</a><span>]</span><span> supaya Dia memperlihatkan kepadamu Tanda-tanda-Nya ? Sesungguhnya dalam hal itu adalah Tanda-tanda bagi setiap orang yang sabar dan bersyukur.</span></p><p><span>[a]</span><span> 17:67 ; 30:47 ; 45:13.</span></p>');
INSERT INTO `quran` VALUES (9878,2,31,33,'<p><span>Dan apabila meliputi mereka ombak seperti naungan, </span><span>[b]</span><span> mereka berseru kepada Allah dengan memurnikan ketaatan kepada-Nya, tetapi apabila </span><span>[c]</span><span> Dia telah menyelamatkan mereka kedaratan, kemudian sebagian dari mereka menempuh jalan pertengahan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2318">2318</a><span>]</span><span> Dan tiada yang menolak Tanda-tanda Kami melainkan setiap </span><span>orang</span><span> yang khianat lagi tidak bersyukur.</span></p><p><span>[b]</span><span> 10:23 ; 17:68 ; 29:66.</span></p><p><span>[c]</span><span> 10:24 ; 17:68.</span></p>');
INSERT INTO `quran` VALUES (9879,2,31,34,'<p><span>Wahai sekalian manusia ! Takutlah kepada Tuhan-mu dan takutlah akan </span><span>[a]</span><span> Hari, apabila seorang ayah tidak dapat menolong anaknya dan tidak pula seorang anak dapat menolong ayahnya sedikitpun. Sesungguhnya, janji Allah itu benar. Maka janganlah sampai kehidupan dunia dapat memperdayakan kamu, dan jangan pula si penipu itu dapat menipu kamu mengenai Allah.</span></p><p><span>[a]</span><span> 2:124 ; 82:20.</span></p>');
INSERT INTO `quran` VALUES (9880,2,31,35,'<p><span>Sesungguhnya Allah di sisi-Nya pengetahuan tentang Hari Kiamat. </span><span>[b]</span><span> Dan, Dia menurunkan hujan dan Dia mengetahui apa yang ada di dalam rahim-rahim. Dan, tiada mengetahui sesuatu jiwa apa yang akan diusahakannya esok hari. Dan tiada mengetahui sesuatu jiwa di bumi mana ia akan mati. Sesungguhnya Allah Maha Mengetahui, Dzat Yang memaklumi segala khabar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2319">2319</a><span>]</span></p><p><span>[b]</span><span> 30:25 ; 42:29.</span></p>');
INSERT INTO `quran` VALUES (9881,2,32,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9882,2,32,2,'<p><span>[b]</span><span> Aku, Allah Yang Maha Mengetahui.</span></p><p><span>[b]</span><span> 30:2.</span></p>');
INSERT INTO `quran` VALUES (9883,2,32,3,'<p><span>[c]</span><span> Diturunkannya Kitab ini, tidak ada keraguan di dalamnya dari Tuhan sekalian alam.</span></p><p><span>[c]</span><span> 20:5 ; 40:3 ; 46:3.</span></p>');
INSERT INTO `quran` VALUES (9884,2,32,4,'<p><span>Apakah mereka berkata, “Ia, </span><span>Nabi Muhammad,</span><span> telah mengada-adakannya ? “ Tidak, bahkan </span><span>Kitab</span><span> ini kebenaran dari Tuhan engkau, </span><span>[d]</span><span> supaya dapatlah engkau memperingatkan kaum yang tidak pernah didatangi seorang pemberi peringatan sebelum engkau, agar mereka mendapat petunjuk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2320">2320</a><span>]</span></p><p><span>[d]</span><span> 28:47 ; 36:7.</span></p>');
INSERT INTO `quran` VALUES (9885,2,32,5,'<p><span>[e]</span><span> Allah-lah Dzat Yang telah menciptakan seluruh langit dan bumi dan apa yang ada di antara keduanya itu dalam enam masa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2321">2321</a><span>]</span><span> kemudian Dia bersemayam di atas Singgasana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2322">2322</a><span>]</span><span> Tiada bagimu selain Dia seorang penolong dan tidak ada pemberi syafaat. Apakah kamu tidak memperoleh nasihat ?</span></p><p><span>[e]</span><span> 7:55 ; 11:8 ; 25:60.</span></p>');
INSERT INTO `quran` VALUES (9886,2,32,6,'<p><span>Dia mengatur peraturan dari langit sampai bumi, kemudian </span><span>peraturan </span><span> itu akan naik kepada-Nya dalam satu hari, yang hitungan lamanya seribu tahun dari apa yang kamu hitung. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2323">2323</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9887,2,32,7,'<p><span>[a]</span><span> Demikianlah </span><span>Tuhan</span><span> mengetahui segala yang gaib dan yang nampak, Maha Perkasa, Maha Penyayang.</span></p><p><span>[a]</span><span> 34:4 ; 59:23.</span></p>');
INSERT INTO `quran` VALUES (9888,2,32,8,'<p><span>Yang telah menjadikan sempurna segala sesuatu yang telah diciptakan-Nya, </span><span>[b]</span><span> dan Dia memulai penciptaan manusia dari tanah liat ;</span></p><p><span>[b]</span><span> 6:3 ; 15:27 ; 37:12.</span></p>');
INSERT INTO `quran` VALUES (9889,2,32,9,'<p><span>Kemudian Dia menjadikan keturunannya dari sari </span><span>[c]</span><span> cairan </span><span>nutfah</span><span> yang hina ;</span></p><p><span>[c]</span><span> 77:21.</span></p>');
INSERT INTO `quran` VALUES (9890,2,32,10,'<p><span>[a]</span><span> Kemudian Dia melengkapinya </span><span>dengan kemampuan-kemampuan</span><span> yang sempurna dan Dia meniupkan ke dalamnya ruh-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2324">2324</a><span>]</span><span> dan Dia telah menjadikan bagimu telinga dan mata, dan hati. Sedikit sekali kamu bersyukur !</span></p><p><span>[a]</span><span> 15:30 ; 38:73.</span></p>');
INSERT INTO `quran` VALUES (9891,2,32,11,'<p><span>Dan mereka berkata, “Apakah jika kami telah hilang dibumi, apakah pasti kami akan dibangkitkan dalam ciptaan baru ?” </span><span>[b]</span><span> Bahkan mereka ingkar dengan pertemuan Tuhan mereka.</span></p><p><span>[b]</span><span> 18:106 ; 29:24 ; 30:17.</span></p>');
INSERT INTO `quran` VALUES (9892,2,32,12,'<p><span>Katakanlah. “Akan mematikan kamu malaikal maut yang ditugaskan kepada kamu, kemudian kepada Tuhan-mu, kamu akan dikembalikan.”</span></p>');
INSERT INTO `quran` VALUES (9893,2,32,13,'<p><span>R. 2</span></p><p><span>Dan sekiranya engkau melihat, ketika orang-orang yang berdosa akan menundukkan kepada mereka di hadapan Tuhan mereka, </span><span>dan berkata,</span><span> “Wahai Tuhan kami, kami telah melihat dan kami telah mendengar, maka </span><span>[c]</span><span> kembalikanlah kami, </span><span>supaya kami</span><span> dapat berbuat amal shaleh ; sesungguhnya kami telah yakin.”</span></p><p><span>[c]</span><span> 23:100, 101 ; 35:38 ; 39:59.</span></p>');
INSERT INTO `quran` VALUES (9894,2,32,14,'<p><span>Dan, sekiranya Kami menghendaki, tentulah Kami dapat memberikan kepada setiap jiwa petunjuknya, akan tetapi perkataan dari-Ku telah ternyata benar, ialah, </span><span>[a]</span><span> “Aku pasti memenuhi Jahannam dengan jin dan manusia semua.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2325">2325</a><span>]</span></p><p><span>[a]</span><span> 11:120 ; 15:44 ; 38:86.</span></p>');
INSERT INTO `quran` VALUES (9895,2,32,15,'<p><span>Maka rasakanlah </span><span>azab</span><span> disebabkan kamu telah melupakan pertemuan dengan harimu ini. Kami </span><span>pun</span><span> telah melupakan kamu. Maka rasakanlah olehmu azab yang kekal, disebabkan apa yang senantiasa kamu perbuat.</span></p>');
INSERT INTO `quran` VALUES (9896,2,32,16,'<p><span>Sesungguhnya orang-orang yang beriman kepada ayat-ayat Kami, adalah orang-orang yang apabila mereka diperingatkan dengannya, mereka </span><span>[b]</span><span> menjatuhkan </span><span>diri</span><span> bersujud dan bertasbih dengan memuji Tuhan mereka, dan mereka tidak menyombongkan diri.</span></p><p><span>[b]</span><span> 17:108, 110 ; 19:59. </span></p>');
INSERT INTO `quran` VALUES (9897,2,32,17,'<p><span>Terpisah </span><span>jauh</span><span> lambung mereka dari tempat tidur, </span><span>[c]</span><span> mereka berseru kepada Tuhan mereka dengan rasa takut dan harap, dan mereka membelanjakan dari apa yang telah Kami rezekikan kepada mereka.</span></p><p><span>[c]</span><span> 21:91.</span></p>');
INSERT INTO `quran` VALUES (9898,2,32,18,'<p><span>Maka tiada sesuatu jiwa mengetahui, apa yang tersembunyi bagi mereka dari penyejuk mata sebagai balasan terhadap apa yang telah mereka kerjakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2326">2326</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9899,2,32,19,'<p><span>Maka, apakah seorang yang beriman </span><span>[a]</span><span> sama seperti orang durhaka ? Mereka tidak akan sama.</span></p><p><span>[a]</span><span> 40:49.</span></p>');
INSERT INTO `quran` VALUES (9900,2,32,20,'<p><span>Adapun </span><span>[b]</span><span> orang-orang yang beriman dan beramal shaleh, maka bagi mereka ada surga-surga tempat tinggal, sebagai jamuan untuk apa yang telah mereka kerjakan.</span></p><p><span>[b]</span><span> 30:16 ; 35:8 ; 42:23 ; 45:31.</span></p>');
INSERT INTO `quran` VALUES (9901,2,32,21,'<p><span>Dan mengenai orang-orang yang durhaka, tempat tinggal mereka adalah Api. </span><span>[c]</span><span> Setiap kali mereka berkehendak keluar dari situ, mereka akan dikembalikan lagi ke dalamnya, dan akan dikatakan kepada mereka, “Rasakanlah azab api yang dahulu kamu mendusta kannya.”</span></p><p><span>[c]</span><span> 3:38 ; 22:23.</span></p>');
INSERT INTO `quran` VALUES (9902,2,32,22,'<p><span>Dan, tentu sekali </span><span>[a]</span><span> Kami akan membuat mereka merasakan azab yang lebih ringan sebelum azab yang lebih besar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2327">2327</a><span>]</span><span> supaya mereka kembali </span><span>bertaubat.</span></p><p><span>[a]</span><span> 52:48.</span></p>');
INSERT INTO `quran` VALUES (9903,2,32,23,'<p><span>Dan, siapakah yang lebih aniaya daripada orang yang diperingatkan akan Tanda-tanda Tuhan-nya, kemudian berpaling darinya ? Sesungguhnya Kami akan menghukum orang- orang yang berdosa.</span></p>');
INSERT INTO `quran` VALUES (9904,2,32,24,'<p><span>R. 3</span></p><p><span>[b]</span><span> Dan, sesungguhnya Kami telah memberi Kitab kepada Musa, maka janganlah engkau ragu tentang pertemuan dengannya, dan Kami telah menjadikannya petunjuk bagi Bani Israil.</span></p><p><span>[b]</span><span> 2:88 ; 17:3 ; 23:50.</span></p>');
INSERT INTO `quran` VALUES (9905,2,32,25,'<p><span>[c]</span><span> Dan telah Kami jadikan dari antara mereka imam-imam yang memberikan petunjuk atas perintah Kami, sebab mereka sabar dan mempunyai keyakinan kuat kepada ayat-ayat Kami.</span></p><p><span>[c]</span><span> 21:74.</span></p>');
INSERT INTO `quran` VALUES (9906,2,32,26,'<p><span>Sesungguhnya, Tuhan engkau, Dia-lah yang akan </span><span>[d]</span><span> mengadili di antara mereka pada Hari Kiamat, mengenai apa yang senantiasa di dalamnya mereka berselisih.</span></p><p><span>[d]</span><span> 4:142 ; 22:70 ; 39:4.</span></p>');
INSERT INTO `quran` VALUES (9907,2,32,27,'<p><span>Apakah tidak menjadi petunjuk bagi mereka, betapa banyak keturunan telah Kami binasakan sebelum mereka, sedang mereka berjalan-jalan di tempat tinggal mereka itu ? Sesungguhnya dalam yang demikian itu ada Tanda-tanda. Apakah mereka tidak mau mendengar ?</span></p>');
INSERT INTO `quran` VALUES (9908,2,32,28,'<p><span>Apakah mereka tidak melihat, bahwa Kami mengalirkan air ke bumi yang tandus dan Kami mengeluarkan dengan </span><span>air </span><span>itu </span><span>[a]</span><span> tumbuh-tumbuhan, yang darinya binatang ternak mereka makan, dan diri mereka sendiri ? Apakah mereka tidak melihat ?</span></p><p><span>[a]</span><span> 10:25 ; 20:55 ; 25:50.</span></p>');
INSERT INTO `quran` VALUES (9909,2,32,29,'<p><span>Dan mereka itu berkata, “Bilakah kemenangan itu akan tiba, jika kamu orang-orang yang benar ?”</span></p>');
INSERT INTO `quran` VALUES (9910,2,32,30,'<p><span>Katakanlah, “Pada hari kemenangan itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2328">2328</a><span>]</span><span> bagi orang-orang ingkar tidak bermanfaat iman mereka, dan tidak pula mereka diberi tangguh.”</span></p>');
INSERT INTO `quran` VALUES (9911,2,32,31,'<p><span>Maka berpalinglah dari mereka dan tungguhlah </span><span>akibat mereka,</span><span> sesungguhnya mereka pun sedang menunggu.</span></p>');
INSERT INTO `quran` VALUES (9912,2,33,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9913,2,33,2,'<p><span>Wahai Nabi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2329">2329</a><span>]</span><span> carilah perlindungan kepada Allah, dan janganlah mengikuti orang-orang kafir dan orang-orang munafik. Sesungguhnya Allah Maha Mengetahui , Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (9914,2,33,3,'<p><span>[b]</span><span> Dan, turutilah apa yang diwahyukan kepada engkau dari Tuhan engkau, Sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.</span></p><p><span>[b]</span><span> 10:110.</span></p>');
INSERT INTO `quran` VALUES (9915,2,33,4,'<p><span>[c]</span><span> Dan bertawakallah kepada Allah. Dan cukuplah Allah sebagai Pemelihara.</span></p><p><span>[c]</span><span> 3:160 ; 26:218.</span></p>');
INSERT INTO `quran` VALUES (9916,2,33,5,'<p><span>Tidak Allah jadikan bagi seseorang dua hati dalam dadanya, dan Dia tidak </span><span>pula</span><span> menjadikan istri-istrimu, yang kamu menjauhi mereka dengan menyebut mereka ibu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2330">2330</a><span>]</span><span> ibu-ibumu </span><span>yang hakiki</span><span>, dan Dia tidak </span><span>pula</span><span> menjadikan anak-anak angkatmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2331">2331</a><span>]</span><span> </span><span>sebagai</span><span> anak-anakmu. Yang demikian itu </span><span>hanyalah</span><span> ucapanmu dengan mulutmu. Dan Allah mengatakan yang hak, dan Dia memberi petunjuk kepada jalan </span><span>yang lurus.</span></p>');
INSERT INTO `quran` VALUES (9917,2,33,6,'<p><span>Panggilan mereka, </span><span>anak-anak angkat,</span><span> dengan </span><span>nama</span><span> bapak mereka. Hal itu lebih adil di sisi Allah. Tetapi, jika kamu tidak mengetahui bapak mereka, maka mereka adalah saudara-saudaramu. Dan tiada dosa atasmu </span><span>tentang</span><span> kesalahan yang telah kamu kerjakan dalam urusan ini ; tetapi </span><span>kamu diminta pertanggung-jawaban atas</span><span> apa yang sengaja diinginkan hatimu. Dan Allah adalah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9918,2,33,7,'<p><span>Nabi itu lebih dekat kepada orang-orang mukmin daripada kepada diri mereka sendiri, dan istri-istrinya adalah ibu-ibu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2332">2332</a><span>]</span><span> mereka. </span><span>[a]</span><span> Dan keluarga yang sedarah adalah lebih dekat satu sama lain, menurut Kitab Allah, daripada orang-orang mukmin dan orang-orang Muhajir, kecuali jika kamu berbuat kebaikan terhadap sahabatmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2333">2333</a><span>]</span><span> Yang demikian itu di dalam Kitab </span><span>Alquran</span><span> telah tertulis.</span></p><p><span>[a]</span><span> 8:76.</span></p>');
INSERT INTO `quran` VALUES (9919,2,33,8,'<p><span>Dan ingatlah </span><span>[a]</span><span> ketika Kami </span><span> mengambil janji mereka dari para nabi mereka itu, dan dari engkau dan Nuh, dan Ibrahim, dan Musa dan </span><span>‘</span><span>Isa ibnu Maryam, dan Kami pernah mengambil janji yang kuat dari mereka itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2334">2334</a><span>]</span></p><p><span>[a]</span><span> 3:82.</span></p>');
INSERT INTO `quran` VALUES (9920,2,33,9,'<p><span>Supaya </span><span>Allah</span><span> dapat bertanya kepada orang-orang yang benar tentang kebenaran mereka. </span><span>[b]</span><span> Dan Dia telah menyediakan bagi orang-orang kafir azab pedih.</span></p><p><span>[b]</span><span> 18:103 ; 48:14 ; 76:5.</span></p>');
INSERT INTO `quran` VALUES (9921,2,33,10,'<p><span>R. 2</span></p><p><span>Hai, orang-orang yang beriman ! Ingatlah nikmat Allah atas kamu, ketika datang </span><span>menyerang</span><span> kepadamu lasykar-lasykar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2335">2335</a><span>]</span><span> maka Kami mengirimkan kepada mereka angin taufan dan lasykar-lasykar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2336">2336</a><span>]</span><span> yang kamu tidak melihatnya. Dan Allah Maha Melihat apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (9922,2,33,11,'<p><span>Ketika mereka datang kepadamu dari atasmu dan dari bawahmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2337">2337</a><span>]</span><span> dan ketika matamu melantur dan hati sampai tenggerokan, dan kamu berprasangka terhadap Allah dengan bermacam-macam prasangka.</span></p>');
INSERT INTO `quran` VALUES (9923,2,33,12,'<p><span>Di situlah orang-orang Mukmin diuji, </span><span>[a]</span><span> dan mereka digoncangkan dengan suatu goncangan yang dahsyat.</span></p><p><span>[a]</span><span> 8:18.</span></p>');
INSERT INTO `quran` VALUES (9924,2,33,13,'<p><span>[b]</span><span> Dan </span><span>ingatlah</span><span> ketika orang-orang munafik dan mereka yang di dalamnya ada penyakit berkata, “Tidaklah Allah dan Rasul-Nya menjanjikan kepada kami melainkan janji yang dusta,”</span></p><p><span>[b]</span><span> 8:50.</span></p>');
INSERT INTO `quran` VALUES (9925,2,33,14,'<p><span>Dan ketika segolongan dari mereka berkata, “Hai, orang-orang Yathrib </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2337a">2337a</a><span>]</span><span> kamu mungkin tidak dapat bertahan </span><span>terhadap musuh,</span><span> oleh karena itu kembalilah kamu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2338">2338</a><span>]</span><span> Dan Segolongan dari mereka meminta izin kepada Nabi dengan berkata, “Sesungguhnya rumah kami terbuka </span><span>terhadap serangan musuh,</span><span>” Padahal rumah mereka itu </span><span>sebenarnya</span><span> tidak terbuka. Mereka hanya berusaha melarikan diri.</span></p>');
INSERT INTO `quran` VALUES (9926,2,33,15,'<p><span>Dan sekiranya </span><span>musuh</span><span> memasuki </span><span>kota Medinah</span><span> dari daerah-daerah sekitarnya, dan kemudian mereka diminta bergabung dalam kerusuhan </span><span>terhadap kaum Muslimin,</span><span> tentulah mereka akan melakukannya, dan mereka tidak akan tinggal </span><span>di Medinah</span><span> melainkan sebentar saja. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2339">2339</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9927,2,33,16,'<p><span>Padahal sebenarnya mereka dahulu telah mengikat janji dengan Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2340">2340</a><span>]</span><span> sebelum ini, bahwa mereka sekali-kali tidak akan memalingkan punggung mereka. Dan perjanjian yang telah diadakan dengan Allah niscaya akan ditanya.</span></p>');
INSERT INTO `quran` VALUES (9928,2,33,17,'<p><span>Katakanlah, </span><span>[a]</span><span> “Lari sekali</span><span>–</span><span>kali tidak akan berfaedah bagimu jika kamu lari dari maut atau terbunuh ; dan meskipun demikian kamu tidak akan diberi manfaat kecuali sedikit.”</span></p><p><span>[a]</span><span> 4:79 ; 62:9.</span></p>');
INSERT INTO `quran` VALUES (9929,2,33,18,'<p><span>Katakanlah, </span><span>[b]</span><span> “Siapakah dapat menyelamatkanmu dari Allah, jika Dia berkehendak menimpakan keburukan kepadamu, atau jika Dia berkehendak memberi rahmat kepadamu ?” Dan mereka tidak akan mendapatkan seorang teman hakiki dan tidak pula seorang penolong selain Allah.</span></p><p><span>[b]</span><span> 39:39.</span></p>');
INSERT INTO `quran` VALUES (9930,2,33,19,'<p><span>Sesungguhnya Allah mengetahui orang-orang yang menghalangi-halangi dari antara kamu dan orang-orang yang berkata kepada saudara-saudara mereka, “Datanglah kepada kami,” dan mereka sama sekali tidak datang untuk berperang.</span></p>');
INSERT INTO `quran` VALUES (9931,2,33,20,'<p><span>Mereka bakhil kepadamu. Apabila datang ketakutan, engkau melihat mereka memandang kepada engkau dengan matanya berputar-putar seperti orang yang menjadi pingsan karena </span><span>dihampiri</span><span> maut. Tetapi, apabila ketakutan berlalu, mereka menyerang engkau dengan lidah yang tajam, karena mereka sangat kikir mengenai kebaikan </span><span>yang datang kepada engkau.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2341">2341</a><span>]</span><span> Mereka tidak pernah beriman ; maka Allah telah menjadikan amal mereka sia-sia. Dan yang demikian itu sangat mudah bagi Allah.</span></p>');
INSERT INTO `quran` VALUES (9932,2,33,21,'<p><span>Mereka masih mengharapkan agar lasykar-lasykar persekutuan itu belum pergi ; dan andaikata lasykar-lasykar persekutuan itu datang </span><span>lagi,</span><span> mereka menghendaki berada di antara orang-orang Badui Arab padang pasir, dan menanyakan berita tentang kamu. Dan sekiranya mereka berada di antaramu, mereka sama sekali tidak akan ikut berperang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2342">2342</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9933,2,33,22,'<p><span>R. 3</span></p><p><span>Sesungguhnya kamu dapati dalam diri Rasulullah saw., </span><span>[a]</span><span> suri teladan yang sebaik- baiknya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2343">2343</a><span>]</span><span> bagi orang yang mengharapkan </span><span>bertemu dengan</span><span> Allah dan Hari Kemudian dan yang banyak mengingat Allah.</span></p><p><span>[a]</span><span> 3:32.</span></p>');
INSERT INTO `quran` VALUES (9934,2,33,23,'<p><span>Dan ketika orang-orang mukmin melihat lasykar-lasykar persekutuan, mereka berkata, “Inilah yang telah dijanjikan Allah dan Rasul-Nya kepada kami ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2344">2344</a><span>]</span><span> dan Allah dan Rasul-Nya telah mengatakan yang benar.” Dan hal itu tidak menambah kepada mereka kecuali keimanan dan kepatuhan.</span></p>');
INSERT INTO `quran` VALUES (9935,2,33,24,'<p><span>Di antara orang-orang yang beriman, ada orang-orang yang benar-benar telah menepati apa yang dijanjikan mereka kepada Allah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2345">2345</a><span>]</span><span> Maka sebagian dari mereka telah menyempurnakan niatnya, </span><span>meninggal,</span><span> dan di antara mereka ada yang masih menunggu, dan mereka tidak merubah sedikit pun.</span></p>');
INSERT INTO `quran` VALUES (9936,2,33,25,'<p><span>[a</span><span>] Agar Allah membalas orang-orang yang benar itu atas kebenaran mereka, dan menghukum orang-orang munafik, jika Dia menghendaki, atau memberi ampunan kepada mereka. Sesungguhnya Allah itu Maha Pengampun, Maha Penyayang.</span></p><p><span>[a]</span><span> 48:6, 7.</span></p>');
INSERT INTO `quran` VALUES (9937,2,33,26,'<p><span>Dan Allah telah mengembalikan orang-orang ingkar dalam kemarahan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2346">2346</a><span>]</span><span> dan mereka tidak memperoleh kebaikan apa pun. Dan Allah mencukupi orang-orang mukmin dalam perang itu. Dan Allah Maha Kuat, Maha Perkasa.</span></p>');
INSERT INTO `quran` VALUES (9938,2,33,27,'<p><span>Dan Dia telah menurunkan orang-orang dari antara Ahlikitab yang menolong mereka, </span><span>yakni</span><span> </span><span>orang-orang musyrik,</span><span> </span><span>[a]</span><span> dari benteng-benteng mereka dan memasukkan ketakutan ke dalam hati mereka. Sebagian dari mereka kamu bunuh dan sebagian kamu tawan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2347">2347</a><span>]</span></p><p><span>[a]</span><span> 59:3.</span></p>');
INSERT INTO `quran` VALUES (9939,2,33,28,'<p><span>Dan, Dia mewariskan kepadamu tanah mereka dan rumah-rumah mereka dan harta mereka, dan suatu daerah yang belum kamu menginjaknya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2348">2348</a><span>]</span><span> Dan Allah atas segala sesuatu berkuasa.</span></p>');
INSERT INTO `quran` VALUES (9940,2,33,29,'<p><span>R. 4</span></p><p><span>Wahai Nabi ! Katakanlah kepada istri-istrimu, “Jika kamu menghendaki kehidupan dunia ini dan perhiasannya, maka marilah, aku akan menceraikan dengan cara yang baik ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2349">2349</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9941,2,33,30,'<p><span>“Tetapi, jika kamu menghendaki Allah dan Rasul-Nya dan rumah di akhirat, maka sesungguhnya Allah telah menyediakan bagi mereka diantaramu yang beramal baik, pahala yang besar.”</span></p>');
INSERT INTO `quran` VALUES (9942,2,33,31,'<p><span>Wahai, istri-istri Nabi ! Barangsiapa di antara kamu berbuat kekejian yang nyata, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2349a">2349a</a><span>]</span><span> akan dilipatgandakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2350">2350</a><span>]</span><span> azab baginya dua kali lipat. Dan yang demikian itu mudah bagi Allah.</span></p>');
INSERT INTO `quran` VALUES (9943,2,33,32,'<p><span>JUZ XXII</span></p><p><span>Tetapi, barangsiapa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2351">2351</a><span>]</span><span> dari antara kamu taat kepada Allah dan Rasul-Nya serta beramal shaleh, Kami akan memberi kepadanya ganjarannya dua kali lipat ; dan Kami telah menyediakan baginya rezeki yang mulia.</span></p>');
INSERT INTO `quran` VALUES (9944,2,33,33,'<p><span>Wahai, istri-istri Nabi ! Kamu tidak sama dengan salah seorang dari wanita-wanita </span><span>lain</span><span> jika kamu bertakwa. Maka jangan-</span><span>lah kamu lembut dalam tutur kata,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2352">2352</a><span>]</span><span> jangan-jangan orang yang dalam hatinya ada penyakit, akan tergiur ; dan ucapkanlah perkataan yang baik.</span></p>');
INSERT INTO `quran` VALUES (9945,2,33,34,'<p><span>Dan tinggallah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2353">2353</a><span>]</span><span> di rumah-rumahmu dan janganlah memamerkan kecantikanmu seperti cara pamer kecantikan </span><span>zaman</span><span> Jahiliah dahulu, dan </span><span>[a]</span><span> dirikanlah shalat dan bayarlah zakat, dan taatlah kepada Allah dan Rasul-Nya. Sesungguhnya Allah menghendaki agar dia menghilangkan </span><span>segala</span><span> kekotoran dari dirimu, hai ahlulbait, dan Dia mensucikan kamu sesuci-sucinya.</span></p><p><span>[a]</span><span> 19:56 ; 20:133.</span></p>');
INSERT INTO `quran` VALUES (9946,2,33,35,'<p><span>Dan, ingatlah akan apa yang dibacakan dalam rumah-rumahmu dari Ayat-ayat Allah dan hikmah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2354">2354</a><span>]</span><span> Sesungguhnya Allah Maha Halus, Maha Memaklumi.</span></p>');
INSERT INTO `quran` VALUES (9947,2,33,36,'<p><span>R. 5</span></p><p><span>[a]</span><span> Sesungguhnya laki-laki Muslim dan perempuan Muslim dan mukmin laki-laki dan mukmin perempuan, dan laki-laki yang patuh dan perempuan-perempuan yang patuh, dan laki-laki yang jujur, dan perempuan-perempuan yang jujur, dan laki-laki yang sabar dan perempuan-perempuan yang sabar dan laki-laki yang merendahkan diri, dan perempuan-perempuan yang merendahkan diri, dan laki-laki yang bersedekah dan perempuan yang bersedekah, dan laki-laki yang berpuasa dan perempuan- perempuan yang berpuasa, dan laki-laki yang memelihara kehormatan dan kesucian mereka dan perempuan-perempuan yang memelihara kehormatan dan kesucian mereka, dan laki-laki yang banyak mengingat Allah dan perempuan-perempuan yang </span><span>banyak </span><span> mengingat </span><span>Dia,</span><span> Allah telah menyediakan bagi </span><span>semua </span><span>mereka itu ampunan dan ganjaran yang besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2355">2355</a><span>]</span></p><p><span>[a]</span><span> 9:112.</span></p>');
INSERT INTO `quran` VALUES (9948,2,33,37,'<p><span>Dan tidak layak bagi laki-laki mukmin ataupun perempuan mukmin, </span><span>[a]</span><span> apabila Allah dan Rasul-Nya telah memutuskan sesuatu perkara, lalu mereka </span><span>menentukan</span><span> pilihan sendiri dalam urusan mengenai diri mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2356">2356</a><span>]</span><span> Dan barangsiapa durhaka terhadap Allah dan Rasul-Nya, sesungguhnya ia telah sesat, kesesatan yang nyata.</span></p><p><span>[a]</span><span> 4:66.</span></p>');
INSERT INTO `quran` VALUES (9949,2,33,38,'<p><span>Dan, </span><span>ingatlah</span><span> ketika engkau berkata kepada orang yang Allah telah memberi nikmat dan engkau </span><span>pun</span><span> telah memberi nikmat kepadanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2357">2357</a><span>]</span><span> “Tahanlah istrimu pada dirimu sendiri dan bertakwah kepada Allah,” sedang engkau menyembunyikan dalam hatimu apa yang Allah hendak menampakkannya, dan engkau takut kepada manusia, padahal Allah lebih berhak agar engkau takut kepada-Nya. maka tatkala Zaid menyempurnakan keperluannya terhadap dia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2357a">2357a</a><span>]</span><span> Kami menikahkan engkau dengan dia, supaya tidak akan ada keberatan bagi orang-orang mukmin untuk </span><span>menikahi</span><span> bekas istri anak-anak angkat mereka, apabila mereka telah menyempurnakan kehendak mereka mengenai mereka. Dan keputusan Allah pasti akan terlaksana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2357b">2357b</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9950,2,33,39,'<p><span>Tidak ada suatu keberatan atas Nabi tentang apa yang telah diwajibkan Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2358">2358</a><span>]</span><span> kepadanya. Inilah sunnah Allah yang </span><span>Dia tetapkan</span><span> terhadap orang-orang yang telah berlalu sebelumnya. Dan perintah Allah adalah suatu keputusan yang telah ditetapkan.</span></p>');
INSERT INTO `quran` VALUES (9951,2,33,40,'<p><span>Orang-orang yang menyampaikan amanat Allah dan </span><span>[a]</span><span> takut kepada-Nya, dan tiada mereka takut siapapun selain Allah. Dan cukuplah Allah sebagai penghisap.</span></p><p><span>[a]</span><span> 67:13.</span></p>');
INSERT INTO `quran` VALUES (9952,2,33,41,'<p><span>Muhammad bukanlah bapak, salah seorang diantara laki-lakimu, akan tetapi </span><span>ia adalah </span><span> Rasul Allah dan meterai sekalian nabi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2359">2359</a><span>]</span><span> dan Allah Maha Mengetahui segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (9953,2,33,42,'<p><span>R. 6</span></p><p><span>Hai orang-orang yang beriman ! </span><span>[a]</span><span> Ingatlah kepada Allah sebanyak-banyaknya,</span></p><p><span>[a]</span><span> 4:104 ; 8:46 ; 62:11.</span></p>');
INSERT INTO `quran` VALUES (9954,2,33,43,'<p><span>[b]</span><span> Dan bertasbihlah kepada-Nya pada waktu pagi dan petang.</span></p><p><span>[b]</span><span> 3:42 ; 19:12.</span></p>');
INSERT INTO `quran` VALUES (9955,2,33,44,'<p><span>Dia-lah Yang menganugerahkan rahmat-</span><span>Nya</span><span> kepada kamu, dan malaikat- malaikat-Nya </span><span>pun berdoa bagimu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2359a">2359a</a><span>]</span><span> supaya Dia mengeluarkan kamu dari segala kegelapan kepada </span><span>[c]</span><span> cahaya. Dan Dia Maha Penyayang terhadap orang-orang yang beriman.</span></p><p><span>[c]</span><span> 2:258 ; 14:6 ; 57:10 ; 65:12.</span></p>');
INSERT INTO `quran` VALUES (9956,2,33,45,'<p><span>[d]</span><span> Hadiah mereka, ketika mereka menemui-Nya </span><span>ialah, </span><span>“Salam.” Dan Dia menyediakan bagi mereka ganjaran yang sangat mulia.</span></p><p><span>[d]</span><span> 10:11 ; 36:59.</span></p>');
INSERT INTO `quran` VALUES (9957,2,33,46,'<p><span>Wahai Nabi ! Sesungguhnya Kami telah mengutus engkau sebagai saksi dan pembawa khabar suka dan pemberi peringatan.</span></p>');
INSERT INTO `quran` VALUES (9958,2,33,47,'<p><span>[e]</span><span> Dan juga sebagai penyeru kepada Allah dengan perintah-Nya, dan sebagai matahari memancarkan cahaya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2360">2360</a><span>]</span></p><p><span>[e]</span><span> 25:57 ; 35:25 ; 48:9.</span></p>');
INSERT INTO `quran` VALUES (9959,2,33,48,'<p><span>Dan berikanlah khabar suka kepada orang-orang mukmin, bahwa sesungguhnya bagi mereka ada karunia yang besar dari Allah.</span></p>');
INSERT INTO `quran` VALUES (9960,2,33,49,'<p><span>[a]</span><span> Dan Janganlah mengikuti orang-orang kafir dan orang-orang munafik, dan janganlah menghiraukan gangguan mereka, dan bertawakkallah kepada Allah. Dan cukuplah Allah sebagai pelindung.</span></p><p><span>[a]</span><span> 18:29 ; 25:53.</span></p>');
INSERT INTO `quran` VALUES (9961,2,33,50,'<p><span>Wahai orang-orang yang beriman, apabila kamu menikahi wanita-wanita mukmin, kemudian kamu mentalak mereka </span><span>[b]</span><span> sebelum kamu menyentuh mereka, maka tidak ada bagimu atas mereka batas waktu yang kamu hitung. Maka bekalilah mereka dan suruhlah mereka pergi dengan cara yang sebaik-baiknya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2361">2361</a><span>]</span></p><p><span>[b]</span><span> 2:237.</span></p>');
INSERT INTO `quran` VALUES (9962,2,33,51,'<p><span>Wahai Nabi, sesungguhnya telah Kami halalkan bagi engkau istri-istri engkau yang telah engkau lunasi maskawin mereka, </span><span>demikian pula </span><span> yang dimiliki tangan kanan engkau dari antara mereka yang telah diberikan Allah kepada engkau sebagai tawanan perang, dan demikian pula anak-anak perempuan saudara-saudara lekaki bapak engkau, dan anak-anak perempuan saudara-saudara perempuan bapak engkau, dan anak-anak perempuan saudara-saudara lelaki ibu engkau, dan anak-anak perempuan saudara- saudara perempuan ibu engkau yang telah ber-hijrah beserta engkau, dan wanita-wanita mukmin </span><span>yang lain,</span><span> jika ia menawarkan diri kepada Nabi, jika Nabi sendiri ingin menikahinya ; </span><span>perintah ini</span><span> hanya khusus bagi engkau dan bukan bagi orang-orang mukmin lainnya. Kami mengetahui apa yang telah Kami wajibkan atas mereka dan yang dimiliki tangan kanan mereka, supaya tidak menjadi kesempitan bagi engkau. Dan Allah Maha Pengampun, Maha Penyayang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2362">2362</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9963,2,33,52,'<p><span>Engkau boleh mengesampingkan siapa yang engkau kehendaki diantara mereka, dan engkau boleh menggauli siapa yang engkau kehendaki ; dan siapa yang engkau inginkan </span><span>kembali</span><span> dari perempuan yang telah engkau ceraikan, maka tiada dosa atas engkau. Yang demikian itu lehih dekat untuk kesejukkan mata mereka, dan mereka tidak akan bersedih dan mereka semuanya rela dengan apa yang telah engkau berikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2263">2263</a><span>]</span><span> kepada mereka. Dan Allah mengetahui apa yang ada dalam hatimu. Dan Allah Maha Mengetahui, Maha Penyantun.</span></p>');
INSERT INTO `quran` VALUES (9964,2,33,53,'<p><span>Tidak dihalalkan bagi engkau </span><span>mengawini</span><span> perempuan-perampuan sesudah itu, dan pula tidak </span><span>dihalalkan</span><span> mengganti mereka dengan istri-istri </span><span>yang lain, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2364">2364</a><span>]</span><span> walaupun engkau menakjubi kecantikan mereka, kecuali apa yang telah dimiliki oleh tangan kananmu. Dan Allah adalah Pengawas atas segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (9965,2,33,54,'<p><span>R. 7</span></p><p><span>Wahai orang-orang yang beriman ! Janganlah memasuki rumah-rumah Nabi, kecuali diizinkan kepadamu untuk makan, tanpa menunggu makanannya masak. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2364a">2364a</a><span>]</span><span> Tetapi, apabila kamu diundang masuklah dan apabila kamu telah selesai makan, maka bubarlah, dan janganlah kamu terus duduk mengobrol. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2365">2365</a><span>]</span><span> Sesungguhnya hal demikian itu akan menyusahkan Nabi, dan ia merasa malu kepada kamu, tetapi Allah tidak merasa malu dari </span><span>menyatakan </span><span> yang benar. Dan, apabila kamu meminta sesuatu dari mereka, </span><span>istri-istri Nabi,</span><span> maka mintalah kepada mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2366">2366</a><span>]</span><span> dari belakang tirai. Yang demikian itu lebih suci bagi hatimu dan juga bagi hati mereka. Dan, tidaklah layak bagi kamu untuk menyusahkan Rasulullah, dan janganlah kamu mengawini istri-istrinya sepeninggalnya untuk selama-lamannya. Sesungguhnya yang demikian itu suatu </span><span>keburukan</span><span> yang sangat besar di sisi Allah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2367">2367</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9966,2,33,55,'<p><span>[a]</span><span> Jika kamu menampakkan sesuatu ataupun kamu menyembunyikan, Sesungguhnya Allah Maha Mengetahui segala sesuatu.</span></p><p><span>[a]</span><span> 3:30 ; 4:150.</span></p>');
INSERT INTO `quran` VALUES (9967,2,33,56,'<p><span>Tiada dosa atas mereka </span><span>mengenai hal itu</span><span> berkenaan dengan bapak-bapak mereka atau anak-anak lelaki mereka atau saudara-saudara lelaki mereka, atau anak-anak lelaki saudara-saudara perempuan mereka, atau kaum wanita mereka atau yang dimiliki tangan kanan mereka. Dan bertakwalah kepada Allah, </span><span>hal istri-istri Nabi.</span><span> Sesungguhnya Allah menjadi saksi atas segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (9968,2,33,57,'<p><span>Sesungguhnya Allah dan para malaikat-Nya bershalawat untuk Nabi. Hai orang-orang mukmin, ucapkanlah shalawat untuknya dan mintalah selalu doa keselamatan baginya.</span></p>');
INSERT INTO `quran` VALUES (9969,2,33,58,'<p><span>Sesungguhnya, </span><span>[a]</span><span> orang-orang yang menyakiti Allah dan Rasul-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2368">2368</a><span>]</span><span> Allah mengutuk mereka di dunia dan di akhirat, dan Dia menyediakan bagi mereka azab yang menghinakan.</span></p><p><span>[a]</span><span> 9:61.</span></p>');
INSERT INTO `quran` VALUES (9970,2,33,59,'<p><span>[b]</span><span> Dan, orang-orang yang menyakiti orang-orang mukmin lelaki dan orang-orang mukmin perempuan tanpa apa yang mereka perbuat, maka mereka menanggung kebohongan dan dosa yang nyata.</span></p><p><span>[b]</span><span> 4:113 ; 24:24.</span></p>');
INSERT INTO `quran` VALUES (9971,2,33,60,'<p><span>R. 8</span></p><p><span>Wahai Nabi, katakanlah kepada istri-istri engkau dan anak-anak perempuan engkau dan istri-istri orang mukmin, bahwa mereka harus menarik ke bawah kain selubung mereka </span><span>dari atas kepada sampai ke dada.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2369">2369</a><span>] Yang demikian itu lebih memungkinkan mereka dapat dikenal dan tidak diganggu. Dan, Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (9972,2,33,61,'<p><span>Jika tidak berhenti orang-orang munafik dan orang-orang yang dalam hatinya ada penyakit dan mereka yang menimbulkan keresahan di dalam kota </span><span>dengan menyebarkan desas-desus palsu, </span><span>tentulah Kami akan mendorong engkau terus untuk melawan mereka ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2370">2370</a><span>]</span><span> kemudian mereka tidak akan tinggal bertetangga dengan engkau di dalam </span><span>kota</span><span> itu melainkan hanya sebentar.</span></p>');
INSERT INTO `quran` VALUES (9973,2,33,62,'<p><span>Mereka itu terkutuk, Di mana saja mereka dijumpai mereka ditangkap dan dibunuh dengan sesungguh-sungguhnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2371">2371</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9974,2,33,63,'<p><span>[a]</span><span> </span><span>Demikianlah </span><span> sunnah Allah bertalian dengan orang-orang yang telah lampau, dan engkau sekali-kali tidak akan mendapatkan suatu perubahan dalam sunnah Allah.</span></p><p><span>[a]</span><span> 17:78 ; 35:44; 48:24.</span></p>');
INSERT INTO `quran` VALUES (9975,2,33,64,'<p><span>[b]</span><span> Orang-orang bertanya kepada engkau mengenai Saat itu. Katakanlah, “Ilmunya hanya ada di sisi Allah.” Dan, apakah yang dapat membuat engkau mengetahui bahwa Saat itu mungkin telah dekat ?</span></p><p><span>[b]</span><span> 7:188 ; 78:3.</span></p>');
INSERT INTO `quran` VALUES (9976,2,33,65,'<p><span>Sesungguhnya, </span><span>[a]</span><span> Allah telah mengutuk orang-orang munafik, dan telah menyediakan bagi mereka api yang menyala-nyala.</span></p><p><span>[a]</span><span> 7:45.</span></p>');
INSERT INTO `quran` VALUES (9977,2,33,66,'<p><span>Mereka akan tinggal lama di dalamnya. Mereka tidak akan mendapatkan sahabat dan tidak pula penolong.</span></p>');
INSERT INTO `quran` VALUES (9978,2,33,67,'<p><span>Pada hari itu ketika akan dibolak-balikkan muka mereka di dalam api </span><span>dan</span><span> mereka akan berkata, </span><span>[b]</span><span> “Alangkah baiknya sekiranya kami menaati Allah dan menaati Rasul.”</span></p><p><span>[b]</span><span> 25:28.</span></p>');
INSERT INTO `quran` VALUES (9979,2,33,68,'<p><span>[c]</span><span> Dan, mereka akan berkata, “Hai Tuhan kami, kami telah menaati pemimpin-peminpin kami dan pembesar-pembesar kami, kemudian mereka menyesatkan kami dari jalan </span><span>lurus.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2372">2372</a><span>]</span></p><p><span>[c]</span><span> 7:39 ; 14:22 ; 28:64 ; 34:32 </span><span>–</span><span> 33 ; 40:48 </span><span>–</span><span> 49.</span></p>');
INSERT INTO `quran` VALUES (9980,2,33,69,'<p><span>“Wahai, Tuhan kami, datangkanlah kepada mereka azab dua kali lipat, dan laknatlah mereka dengan laknat yang besar.”</span></p>');
INSERT INTO `quran` VALUES (9981,2,33,70,'<p><span>R. 9</span></p><p><span>Wahai, orang-orang yang beriman, janganlah kamu seperti orang-orang yang telah menyusahkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2373">2373</a><span>]</span><span> Musa ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2373a">2373a</a><span>]</span><span> tetapi Allah membersihkannya dari apa yang mereka katakan. </span><span>[a]</span><span> Dan ia, </span><span>Musa,</span><span> di sisi Allah adalah orang yang terhormat.</span></p><p><span>[a]</span><span> 19:52, 53.</span></p>');
INSERT INTO `quran` VALUES (9982,2,33,71,'<p><span>Wahai, orang-orang yang beriman, bertakwalah kepada Allah dan ucapkanlah perkataan yang jujur.</span></p>');
INSERT INTO `quran` VALUES (9983,2,33,72,'<p><span>Dia akan memperbaiki bagimu amal-amalmu dan akan menampuni bagimu dosa-dosamu. </span><span>[b]</span><span> Dan barangsiapa taat kepada Allah dan Rasul-Nya, maka sesungguhnya ia akan meraih kemenangan besar.</span></p><p><span>[b]</span><span> 4:14 ; 24:53 ; 48:18.</span></p>');
INSERT INTO `quran` VALUES (9984,2,33,73,'<p><span>Sesungguhnya telah Kami tawarkan amanat </span><span>syariat</span><span> kepada sekalian langit dan bumi dan gunung-gunung, namun semuanya enggan memikulnya, dan mereka takut terhadapnya, akan tetapi manusia memikulnya. Sesungguhnya ia </span><span>berbuat </span><span> aniaya dan mengabaikan </span><span>dirinya.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2374">2374</a><span>]</span></p>');
INSERT INTO `quran` VALUES (9985,2,33,74,'<p><span>Supaya Allah akan menghukum orang-orang munafik lelaki dan orang-orang munafik perempuan, dan orang-orang musyrik lelaki dan orang-orang musyrik perempuan ; </span><span>[a]</span><span> dan Allah senantiasa kembali dengan kasih sayang kepada orang-orang lelaki beriman dan orang-orang perempuan yang beriman. Dan Allah adalah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a]</span><span> 4:28 ; 9:104.</span></p>');
INSERT INTO `quran` VALUES (9986,2,34,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (9987,2,34,2,'<p><span>Segala puji bagi Allah, Yang kepunyaan Dia-lah apa yang ada di seluruh langit dan apa yang ada di bumi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2375">2375</a><span>]</span><span> dan kepunyaan-Nya-lah segala puji di akhirat. Dan Dia Maha Bijaksana dan Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (9988,2,34,3,'<p><span>[b]</span><span> Dia mengetahui apa yang masuk ke dalam bumi dan apa yang keluar darinya, dan apa yang turun dari langit dan apa yang naik kepadanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2376">2376</a><span>]</span><span> Dan Dia Maha Penyayang, Maha Pengampun</span></p><p><span>[b]</span><span> 57:5</span></p>');
INSERT INTO `quran` VALUES (9989,2,34,4,'<p><span>Dan berkata orang-orang yang ingkar, “Tidak akan datang kepada kami Saat itu,” Katakanlah, “Mengapa tidak, demi Tuhanku pasti akan datang kepadamu, Dia mengetahui segala yang gaib, </span><span>[a]</span><span> tiada terluput dari-Nya seberat zarah pun di seluruh langit atau di bumi, atau sesuatu yang lebih kecil dari itu dan yang lebih besar, melainkan tercatat dalam Kitab yang nyata, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2377">2377</a><span>]</span></p><p><span>[a]</span><span> 10:62.</span></p>');
INSERT INTO `quran` VALUES (9990,2,34,5,'<p><span>[b]</span><span> Supaya Dia memberi ganjaran orang-orang yang beriman dan beramal shaleh. Mereka itulah yang akan memperoleh ampunan dan rezeki yang mulia.</span></p><p><span>[b]</span><span> 10:5 ; 40:46.</span></p>');
INSERT INTO `quran` VALUES (9991,2,34,6,'<p><span>[c]</span><span> Dan orang-orang berusaha menggagalkan Tanda-tanda Kami, mereka itulah, bagi mereka azab dari siksaan yang sangat pedih.</span></p><p><span>[c]</span><span> 22:52 ; 34:39.</span></p>');
INSERT INTO `quran` VALUES (9992,2,34,7,'<p><span>[d]</span><span> Dan orang-orang yang diberi ilmu, melihat bahwa apa yang diturunkan kepada engkau dari Tuhan engkau itulah benar, dan membimbing kepada jalan Yang Maha Perkasa, Maha Terpuji.</span></p><p><span>[d]</span><span> 13:20 ; 22:55 ; 35:32 ; 56:96.</span></p>');
INSERT INTO `quran` VALUES (9993,2,34,8,'<p><span>Dan berkata orang-orang yang ingkar, “Maukah kami tunjukkan kepadamu seorang laki-laki, yang akan memberitahukan kepadamu, </span><span>bahwa</span><span> apabila kamu telah dihancurkan kamu akan </span><span>di bangkitkan kembali</span><span> menjadi makhluk baru ? “</span></p>');
INSERT INTO `quran` VALUES (9994,2,34,9,'<p><span>Adakah ia mengada-adakan suatu dusta terhadap Allah ataukah ia dihinggapi kegilaan ? Tidak, </span><span>[a]</span><span> bahkan orang-orang yang tidak beriman kepada akhirat itulah yang berada dalam azab dan kesesatan yang jauh.</span></p><p><span>[a]</span><span> 17:11 ; 27:5.</span></p>');
INSERT INTO `quran` VALUES (9995,2,34,10,'<p><span>Tidakkah mereka itu melihat, apa yang ada di hadapan mereka dan apa yang ada di belakang mereka dari langit dan bumi ini ? </span><span>[b]</span><span> Jika Kami menghendaki, Kami dapat menyebabkan bumi tenggelam bersama mereka atau menyebabkan pecahan-pecahan awan menimpa mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2378">2378</a><span>]</span><span> Sesungguhnya dalam yang demikian itu, ada Tanda bagi setiap hamba yang tunduk.</span></p><p><span>[b]</span><span> 6:66 ; 17:69 ; 57:17 </span><span>–</span><span> 18.</span></p>');
INSERT INTO `quran` VALUES (9996,2,34,11,'<p><span>R. 2</span></p><p><span>Dan, sesungguhnya telah Kami anugerakan karunia kepada Daud dari Kami </span><span>dan berfirman,</span><span> </span><span>[c]</span><span> “Hai, gunung-gunung, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2378a">2378a</a><span>]</span><span> sanjunglah bersamanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2378b">2378b</a><span>]</span><span> dan juga hai, burung-burung, </span><span>orang-orang shaleh.</span><span>” Dan, Kami menjadikan besi lunak baginya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2379">2379</a><span>]</span></p><p><span>[c]</span><span> 21:80 ; 38:19 </span><span>–</span><span> 20.</span></p>');
INSERT INTO `quran` VALUES (9997,2,34,12,'<p><span>Dan, Kami berfirman,</span><span> “Buatlah baju besi yang cukup panjang, dan buatlah cincin- cincinnya berukuran tepat. Dan berbuatlah amal shaleh. Sesungguhnya Aku melihat segala apa yang kamu kerjakan.”</span></p>');
INSERT INTO `quran` VALUES (9998,2,34,13,'<p><span>[a]</span><span> Dan, kepada Sulaiman </span><span>Kami tundukkan</span><span> angin ; perjalanan paginya sama dengan sebulan </span><span>perjalanan </span><span> dan perjalanan petangnya sama dengan sebulan </span><span>perjalanan juga.</span><span> Dan, Kami mengalirkan sumber cairan tembaga untuk dia. Dan, dari jin-jin ada </span><span>sebagian </span><span> yang bekerja di bawah perintahnya dengan izin Tuhan-nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2380">2380</a><span>]</span><span> Dan </span><span>Kami katakan </span><span> bahwa barangsiapa dari mereka menyimpang dari perintah Kami, Kami membuat dia merasakan azab api yang menyala-nyala.</span></p><p><span>[a]</span><span> 21:82 ; 38:37.</span></p>');
INSERT INTO `quran` VALUES (9999,2,34,14,'<p><span>Mereka mengerjakan untuknya apa yang dia kehendaki dari tempat-tempat menyembah dan patung-patung, kolam-kolam bagaikan bendungan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2381">2381</a><span>]</span><span> dan periuk-periuk besar yang tetap pada tungkunya. </span><span>Dan Kami berfirman,</span><span> “Hai keluarga Daud, </span><span>[a]</span><span> beramallah sambil bersyukur.” Tetapi sedikit sekali di antara hamba-hamba-Ku bersyukur.</span></p><p><span>[a]</span><span> 21:81.</span></p>');
INSERT INTO `quran` VALUES (10000,2,34,15,'<p><span>Dan ketika Kami menakdirkan kematiannya, </span><span>kematian Sulaiman,</span><span> tiada sesuatu menunjukkan kepada mereka </span><span>perihal </span><span> kematiannya selain rayap bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2382">2382</a><span>]</span><span> yang memakan tongkatnya. Maka tatkala </span><span>tongkat </span><span> itu jatuh, jin-jin (</span><span>orang-orang besar</span><span> ) menyadari dengan jelas bahwa sekiranya mereka itu mengetahui apa-apa yang gaib, tentulah mereka tidak akan tetap dalam azab yang menghinakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2383">2383</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10001,2,34,16,'<p><span>Sesungguhnya bagi kaum Saba</span><span>’</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2383a">2383a</a><span>]</span><span> ada satu Tanda besar di tanah air mereka, dua kebun, di sebelah kanan dan di kiri. </span><span>Dan Kami berfirman,</span><span> “Makanlah rezeki dari Tuhan-mu dan berterima kasihlah kepada-Nya. Suatu negeri yang indah dan Tuhan Yang Maha Pengampun.”</span></p>');
INSERT INTO `quran` VALUES (10002,2,34,17,'<p><span>Tetapi, mereka itu berpaling ; maka Kami kirimkan kepada mereka banjir dahsyat yang membinasakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2384">2384</a><span>]</span><span> Dan Kami tukarkan kebun kebun mereka itu dengan dua kebun yang berisikan buah-buahan pahit dan pohon cemara dan sedikit pohon bidara.</span></p>');
INSERT INTO `quran` VALUES (10003,2,34,18,'<p><span>Demikianlah Kami memberi balasan kepada mereka karena mereka tidak bersyukur. Dan tidaklah Kami membalas </span><span>seperti itu </span><span> kecuali kepada orang-orang yang bersyukur.</span></p>');
INSERT INTO `quran` VALUES (10004,2,34,19,'<p><span>Dan Kami telah jadikan di antara mereka dan diantara kota-kota yang telah Kami beri berkat di dalamnya </span><span>menjadi </span><span> kota-kota yang berdekatan, dan telah Kami tetapkan </span><span>perhentian </span><span> perjalanan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2385">2385</a><span>]</span><span> di antara </span><span>kota-kota</span><span> itu </span><span>dan Kami katakan,</span><span> “Berjalanlah di dalamnya malam dan siang dengan aman.”</span></p>');
INSERT INTO `quran` VALUES (10005,2,34,20,'<p><span>Maka mereka berkata, “Ya, Tuhan kami, jauhkanlah jarak di antara perjalanan kami,” dan mereka menganiaya diri sendiri ; maka Kami jadikan mereka buah tutur dan Kami hancurkan mereka sehancur-hancurnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2386">2386</a><span>]</span><span> Sesungguhnya dalam hal yang demikian itu pasti ada Tanda-tanda bagi setiap </span><span>orang</span><span> yang bersabar </span><span>dan</span><span> bersyukur.</span></p>');
INSERT INTO `quran` VALUES (10006,2,34,21,'<p><span>Dan sesungguhnya iblis telah membenarkan perkiraannya tentang mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2387">2387</a><span>]</span><span> maka </span><span>[a]</span><span> mereka mengikutinya, kecuali segolongan dari orang-orang mukmin.</span></p><p><span>[a]</span><span> 15:43 ; 16:100.</span></p>');
INSERT INTO `quran` VALUES (10007,2,34,22,'<p><span>[b]</span><span> Dan, tidaklah ia mempunyai atas mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2388">2388</a><span>]</span><span> melainkan supaya Kami dapat membedakan orang-orang beriman kepada akhirat dari orang-orang yang masih dalam keraguan tentang itu. Dan Tuhan engkau adalah Pemelihara atas segala sesuatu.</span></p><p><span>[b]</span><span> 34:22.</span></p>');
INSERT INTO `quran` VALUES (10008,2,34,23,'<p><span>R. 3</span></p><p><span>Katakanlah, “Serulah kepada mereka yang kamu anggap </span><span>sebagai tuhan </span><span> selain Allah. Mereka tidak menguasai sebesar zarrah pun di seluruh langit dan tidak </span><span>pula</span><span> di bumi, dan mereka tidak mempunyai bahagian di dalam kedua-duanya, dan tidak ada baginya di antara mereka seorang penolong pun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2389">2389</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10009,2,34,24,'<p><span>[a]</span><span> Dan tiada berguna syafaat di sisi Dia kecuali bagi barangsiapa yang Dia izinkan beginya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2389a">2389a</a><span>]</span><span> Hingga bila telah dihilangkan ketakutan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2390">2390</a><span>]</span><span> dari hati mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2391">2391</a><span>]</span><span> mereka berkata, “Apakah yang telah difirmankan Tuhan-mu ?” Mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2392">2392</a><span>]</span><span> berkata, “Kebenaran.” Dan Dia-lah Yang Maha luhur, Maha Besar.</span></p><p><span>[a]</span><span> 2:255 ; 20:110 ; 78:39.</span></p>');
INSERT INTO `quran` VALUES (10010,2,34,25,'<p><span>Katakanlah, </span><span>[b]</span><span> “Siapakah yang memberi rezeki kepadamu dari seluruh langit dan bumi ?” Katakanlah, “Allah, dan sesungguhnya kami atau kamu pasti berada di atas petunjuk yang benar atau dalam kesesatan yang nyata.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2393">2393</a><span>]</span></p><p><span>[b]</span><span> 10:32 ; 27:65 ; 35:4.</span></p>');
INSERT INTO `quran` VALUES (10011,2,34,26,'<p><span>Katakanlah, “Kamu tidak akan ditanyai tentang dosa apa yang telah kami lakukan dan kami tidak akan ditanyai tentang apa yang kamu kerjakan.”</span></p>');
INSERT INTO `quran` VALUES (10012,2,34,27,'<p><span>Katakanlah, “Tuhan kita akan menghimpun di antara kita, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2394">2394</a><span>]</span><span> kemudian Dia akan memutuskan di antara kita dengan kebenaran, dan Dia-lah Maha Pemberi Keputusan, Maha Mengetahui.”</span></p>');
INSERT INTO `quran` VALUES (10013,2,34,28,'<p><span>Katakanlah, </span><span>[a]</span><span> “Perlihatkanlah kepadaku mereka yang kamu hubungkan dengan Dia sebagai sekutu-sekutu-</span><span>Nya.</span><span>” Tidak ! Sekali-kali tidak, bahkan Dia adalah Allah, Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a]</span><span> 35:41 ; 46:5.</span></p>');
INSERT INTO `quran` VALUES (10014,2,34,29,'<p><span>Dan, Kami tidak mengutus engkau sebagai </span><span>[b]</span><span> pembawa khabar suka dan pemberi peringatan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2395">2395</a><span>]</span><span> akan tetapi kebanyakan manusia tidak mengetahui.</span></p><p><span>[b]</span><span> 21:108.</span></p>');
INSERT INTO `quran` VALUES (10015,2,34,30,'<p><span>[a]</span><span> Dan, mereka akan berkata, “Kapankah perjanjian ini </span><span>akan sempurna,</span><span> jika kamu orang-orang yang benar ? “ </span></p><p><span>[a]</span><span> 21:39 ; 36:49 ; 67:26.</span></p>');
INSERT INTO `quran` VALUES (10016,2,34,31,'<p><span>Katakanlah, “Bagimu ada janji akan suatu hari, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2396">2396</a><span>]</span><span> yang </span><span>[b]</span><span> kamu tidak akan menunda darinya sesaat pun, dan tidak pula kamu dapat mempercepat-</span><span>nya.</span><span>”</span></p><p><span>[b]</span><span> 7:35 ; 10:50.</span></p>');
INSERT INTO `quran` VALUES (10017,2,34,32,'<p><span>R. 4</span></p><p><span>Dan, berkata orang-orang yang ingkar, “Kami sekali-kali tidak akan percaya kepada Alquran ini, dan tidak pula kepada yang sebelumnya. Dan, sekiranya engkau dapat melihat, </span><span>[c]</span><span> ketika mereka yang aniaya itu akan disuruh berdiri di hadapan Tuhan mereka, seraya mereka melemparkan tuduhan kepada satu sama lain, berkatalah orang- orang yang dianggap lemah kepada orang-orang yang menyombongkan diri, “Sekiranya tidak karena kamu, tentulah kami telah menjadi orang-orang yang beriman.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2397">2397</a><span>]</span></p><p><span>[c]</span><span> 7:39 ; 14:22 ; 28:64 ; 33:68 ; 40:48.</span></p>');
INSERT INTO `quran` VALUES (10018,2,34,33,'<p><span>[a]</span><span> Berkatalah orang-orang yang menyombongkan diri kepada orang-orang yang dianggap lemah, “Adakah kami telah menghalangimu dari petunjuk, setelah </span><span>petunjuk</span><span> itu datang kepada mu ? Tidak, bahkan kamu sendirilah yang berdosa.” </span></p><p><span>[a]</span><span> 14:22 ; 28:54 ; 40:48.</span></p>');
INSERT INTO `quran` VALUES (10019,2,34,34,'<p><span>[b]</span><span> Dan, berkatalah orang-orang yang dianggap lemah kepada orang-orang yang menyombongkan diri, “Tidak, bahkan itu adalah rencana-</span><span>mu</span><span> malam dan siang, ketika kamu menyuruh kami supaya kami ingkar kepada Allah dan menjadikan bagi-Nya sekutu- sekutu.” </span><span>[c]</span><span> Dan mereka akan menyembunyikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2398">2398</a><span>]</span><span> penyesalan ketika mereka menyaksikan azab itu. Dan Kami akan mengenakan belenggu pada leher </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2398a">2398a</a><span>]</span><span> orang-orang yang ingkar itu. Mereka tidak akan di balas, melainkan untuk apa yang telah mereka kerjakan.</span></p><p><span>[b]</span><span> 14:22 ; 40:48.</span></p>');
INSERT INTO `quran` VALUES (10020,2,34,35,'<p><span>Dan tidak pernah Kami mengirimkan seorang pemberi peringatan ke suatu negeri, melainkan </span><span>[d]</span><span> orang-orang hartawan negeri itu berkata, “Sesungguhnya kami dengan apa-apa yang kamu diutus, kami mengingkari.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2399">2399</a><span>]</span></p><p><span>[d]</span><span> 6:1234 ; 17:17.</span></p>');
INSERT INTO `quran` VALUES (10021,2,34,36,'<p><span>Dan mereka berkata, “Kami mempunyai lebih harta dan anak ; dan tidaklah kami akan diazab.”</span></p>');
INSERT INTO `quran` VALUES (10022,2,34,37,'<p><span>Katakanlah, “Sesungguhnya </span><span>[a]</span><span> Tuhan-ku melapangkan rezeki bagi siapa yang Dia kehendaki dan menyempitkan, akan tetapi kebanyakan manusia tidak mengetahui.”</span></p><p><span>[a] </span><span>13:27 ; 29:63 ; 39:53 ; 42:13.</span></p>');
INSERT INTO `quran` VALUES (10023,2,34,38,'<p><span>R. 5</span></p><p><span>Dan, bukan hartamu dan bukan pula anak-anakmu yang akan mendekatkan derajat kamu kepada Kami, melainkan </span><span>[b]</span><span> orang yang beriman dan beramal shaleh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2400">2400</a><span>]</span><span> maka, mereka itulah bagi mereka ada ganjaran ayng berlipat ganda disebabkan apa yang telah mereka kerjakan, </span><span>[c]</span><span> dan mereka aman di rumah-rumah yang tinggi.</span></p><p><span>[b]</span><span> 3:58 ; 6:49 ; 18:89 ; 19:61.</span></p><p><span>[c]</span><span> 25:76.</span></p>');
INSERT INTO `quran` VALUES (10024,2,34,39,'<p><span>[a]</span><span> Dan, orang-orang yang berusaha menggagalkan Tanda-tanda Kami, mereka itulah yang akan dihadapkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2401">2401</a><span>]</span><span> kepada azab.</span></p><p><span>[a]</span><span> 22:52.</span></p>');
INSERT INTO `quran` VALUES (10025,2,34,40,'<p><span>Katakanlah, “Sesungguhnya Tuhan-ku melapangkan rezeki bagi siapa yang Dia kehendaki dari antara hamba-hamba-Nya dan menyempitkan baginya. Dan apa yang kamu belanjakan dari sesuatu, tentulah Dia akan menggantikannya ; dan Dia adalah sebaik- baik Pemberi rezeki.”</span></p>');
INSERT INTO `quran` VALUES (10026,2,34,41,'<p><span>Dan, </span><span>ingatlah</span><span> hari </span><span>[b]</span><span> apabila Dia akan menghimpun mereka semuanya, kemudian Dia akan berfirman kepada para malaikat, “Apakah mereka ini dahulu menyembah kamu ? “</span></p><p><span>[b] </span><span>10:29 ; 17:98 ; 19:69.</span></p>');
INSERT INTO `quran` VALUES (10027,2,34,42,'<p><span>Mereka berkata, </span><span>[c]</span><span> “Maha Suci Engkau, Engkau-lah Pelindung kami terhadap mereka. Tidak, bahkan mereka menyembah jin ; kebanyakan mereka beriman kepadanya.”</span></p><p><span>[c]</span><span> 25:19.</span></p>');
INSERT INTO `quran` VALUES (10028,2,34,43,'<p><span>Akan dikatakan kepada mereka,</span><span> “Maka, pada hari ini, sebagian kamu tidak mempunyai kekuasaan memberikan manfaat ataupun mudarat kepada sebagian kamu.” Dan akan Kami katakan kepada mereka yang aniaya, </span><span>[d]</span><span> “Rasakanlah olehmu azab api yang dahulu kamu mendustakannya.”</span></p><p><span>[d]</span><span> 8:15 ; 10:53 ; 22:23.</span></p>');
INSERT INTO `quran` VALUES (10029,2,34,44,'<p><span>Dan, apabila dibacakan kepada mereka Ayat-ayat Kami yang nyata, mereka berkata, </span><span>[a]</span><span> “Ini tak lain melainkan seorang laki-laki yang hendak berusaha menghalangi kamu dari apa yang di sembah bapak-bapakmu.” Dan mereka berkata, “Tidaklah </span><span>Alquran</span><span> ini melainkan suatu kedustaan yang telah diada-adakan.” Dan berkata orang-orang ingkar terhadap kebenaran, ketika datang kepada mereka, “Ini tidak lain melainkan sihir yang nyata.”</span></p><p><span>[a]</span><span> 17:95 ; 23:25.</span></p>');
INSERT INTO `quran` VALUES (10030,2,34,45,'<p><span>Dan tidak pernah Kami memberikan kepada mereka Kitab-kitab yang mereka mempelajarinya, dan tidak pernah pula Kami mengirimkan kepada mereka seorang Pemberi peringatan sebelum engkau.</span></p>');
INSERT INTO `quran` VALUES (10031,2,34,46,'<p><span>Dan orang-orang yang sebelum mereka mendustakan </span><span>rasul-rasul,</span><span> dan mereka tidak mencapai sepersepuluh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2402">2402</a><span>]</span><span> </span><span>kekuatan </span><span> yang pernah Kami berikan kepada mereka, tetapi mereka mendustakan rasul-rasul-Ku. Maka bagaimana akibat penolakan terhadap Aku.</span></p>');
INSERT INTO `quran` VALUES (10032,2,34,47,'<p><span>R. 6</span></p><p><span>Katakanlah. “Sesungguhnya aku menasihatimu satu hal ; supaya kamu berdiri untuk Allah, berdua atau sendiri-sendiri kemudian berfikirlah, </span><span>[a]</span><span> Tiada penyakit gila sedikit pun pada kawanmu itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2403">2403</a><span>]</span><span> Ia tiada lain melainkan seorang Pemberi ingat kepadamu sebelum azab yang dahsyat.”</span></p><p><span>[a] </span><span>7:185 ; 23:71.</span></p>');
INSERT INTO `quran` VALUES (10033,2,34,48,'<p><span>Katakanlah, </span><span>[b]</span><span> “Ganjaran apapun yang aku minta kepadamu, maka itu untuk kamu. Tiadalah ganjaranku melainkan pada Allah ;dan Dia menjadi Saksi atas segala sesuatu.”</span></p><p><span>[b]</span><span> 38:87 ; 42:24 ; 52:41 ; 68:47.</span></p>');
INSERT INTO `quran` VALUES (10034,2,34,49,'<p><span>Katakanlah. “Sesungguhnya Tuhan-ku melempar </span><span>kedustaan </span><span> dengan kebenaran. </span><span>Dia</span><span> </span><span>[c]</span><span> Yang Maha Mengetahui segala yang gaib.”</span></p><p><span>[c]</span><span> 5:117.</span></p>');
INSERT INTO `quran` VALUES (10035,2,34,50,'<p><span>Katakanlah, </span><span>[d]</span><span> “Kebenaran telah datang, dan kebatilan tidak dapat memulai dan tidak pula dapat mengulangi.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2404">2404</a><span>]</span></p><p><span>[d]</span><span> 17:82 ; 21:19.</span></p>');
INSERT INTO `quran` VALUES (10036,2,34,51,'<p><span>Katakanlah, “Sekiranya aku sesat, maka akibat kesesatanku hanyalah bagi diriku sendiri l dan jika aku mendapat perunjuk, maka hal itu disebabkan apa yang telah diwahyukan Tuhan-ku kepadaku. </span><span>[a]</span><span> Sesungguhnya Dia-lah Dzat Yang Maha Mendengar, Maha Dekat.”</span></p><p><span>[a]</span><span> 2:187 ; 11:62.</span></p>');
INSERT INTO `quran` VALUES (10037,2,34,52,'<p><span>Dan, sekiranya engkau dapat melihat ketika mereka dicekam kecemasan. Maka tidak </span><span>dapat mereka </span><span> meloloskan diri dan mereka akan ditangkap dari tempat yang dekat.</span></p>');
INSERT INTO `quran` VALUES (10038,2,34,53,'<p><span>Dan, mereka berkata, “Kami beriman kepadanya.” Tetapi bagaimana mungkin mereka mencapai </span><span>keimanan</span><span> dari tempat jauh ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2405">2405</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (10039,2,34,54,'<p><span>Padahal mereka telah mengingkarinya sebelumnya, mereka hanya menduga-duga belaka mengenai yang gaib itu dari tempat jauh.</span></p>');
INSERT INTO `quran` VALUES (10040,2,34,55,'<p><span>Dan rintangan diletakkan di antara mereka dan apa yang diinginkan mereka, sebagaimana telah dilakukan terhadap orang-orang seperti mereka sebelum mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2406">2406</a><span>] </span><span>Sesungguhnya mereka ada dalam keraguan yang mengelisahkan.</span></p>');
INSERT INTO `quran` VALUES (10041,2,35,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10042,2,35,2,'<p><span>Segala puji kepunyaan Allah, </span><span>[b]</span><span> Yang menciptakan seluruh langit dan bumi, Yang menjadikan malaikat-malaikat sebagai utusan-utusan yang bersayap dua dan tiga dan empat. Dia menambahkan dalam ciptaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2407">2407</a><span>]</span><span> apa yang Dia kehendaki, Sesungguhnya Allah Maha Kuasa atas segala sesuatu.</span></p><p><span>[b]</span><span> 6:15 ; 12:102 ; 14:11 ; 42:12.</span></p>');
INSERT INTO `quran` VALUES (10043,2,35,3,'<p><span>Rahmat </span><span>[c]</span><span> apa pun yang dibukakan Allah bagi umat manusia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2408">2408</a><span>]</span><span> tiada yang dapat manahannya, dan apa pun </span><span>dari</span><span> yang ditahan-Nya, tiada yang dapat melepaskannya sesudah itu. Dan Dia Maha Perkasa, Maha Bijaksana.</span></p><p><span>[c]</span><span> 39:39.</span></p>');
INSERT INTO `quran` VALUES (10044,2,35,4,'<p><span>Hai manusia, ingatlah akan nikmat Allah kepadamu. </span><span>[a]</span><span> Adakah pencipta lain selain Allah, Yang memberi rezeki kepadamu dari langit dan bumi ? Tiada yang patut disembah selain Dia. Kemudian ke mana lagi kamu dipalingkan ?</span></p><p><span>[a]</span><span> 10:32 ; 27:65 ; 34:25.</span></p>');
INSERT INTO `quran` VALUES (10045,2,35,5,'<p><span>[b]</span><span> Dan, jika mereka mendustakan engkau, maka sesungguhnya telah didustakan rasul-rasul </span><span>Tuhan</span><span> sebelum engkau ; dan kepada Allah segala urusan dikembalikan, </span><span>untuk diputuskan.</span></p><p><span>[b]</span><span> 6:35 ; 22:43 ; 40:6 ; 54:10.</span></p>');
INSERT INTO `quran` VALUES (10046,2,35,6,'<p><span>Hai manusia,sesungguhnya janji Allah itu benar, maka janganlah kehidupan dunia ini memperdayakan kamu dan jangan pula si penipu akan menipu kamu mengenai Allah.</span></p>');
INSERT INTO `quran` VALUES (10047,2,35,7,'<p><span>[c]</span><span> Sesungguhnya, syaitan itu masuh bagimu ; maka perlakukanlah dia sebagai musuh. Sesungguhnya ia hanya memanggiil golongannya agar mereka akan menjadi penghuni Api yang menyala</span><span>–</span><span>nyala.</span></p><p><span>[c]</span><span> 2:169 ; 12:6 ; 18:51 ; 20:118.</span></p>');
INSERT INTO `quran` VALUES (10048,2,35,8,'<p><span>Orang-orang yang ingkar bagi mereka ada azab yang keras. Dan orang-orang yang beriman dan berbuat amal shaleh bagi mereka ada ampunan dan ganjaran besar.</span></p>');
INSERT INTO `quran` VALUES (10049,2,35,9,'<p><span>R. 2</span></p><p><span>Maka apakah orang yang keburukan </span><span>[a]</span><span> perbuatannya dibuat </span><span>nampak</span><span> indah baginya, lalu ia memandangnya baik. Maka, sesungguhnya Allah membiarkan sesat siapa yang Dia kehendaki dan memberi petunjuk siapa yang Dia kehendaki. Maka janganlah diri engkau binasa karena kesedihan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2409">2409</a><span>]</span><span> mengenai mereka. Sesungguhnya Allah Maha Mengetahui apa yang mereka perbuat.</span></p><p><span>[a]</span><span> 16:64 ; 27:25 ; 29:39.</span></p>');
INSERT INTO `quran` VALUES (10050,2,35,10,'<p><span>Dan, Allah Yang mengirimkan angin yang membumbungkan awan ; kemudian Kami menggiring awan itu ke suatu negeri yang telah mati, </span><span>[b]</span><span> dan menghidupkan dengannya bumi setelah mati nya. Demikianlah pulalah </span><span>akan terjadi </span><span> kebangkitan itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2410">2410</a><span>]</span></p><p><span>[b]</span><span> 22:7 ; 57:18.</span></p>');
INSERT INTO `quran` VALUES (10051,2,35,11,'<p><span>Barangsiapa menghendaki kehormatan, maka Allah-lah kehormatan itu semuanya. Kepada-Nya naik segala perkataan baik, dan alam shaleh mengangkatnya, </span><span>[a]</span><span> dan orang-orang yang menrencanakan keburukan, bagi mereka ada azab yang sangat keras. Dan rencana mereka itu akan hancur.</span></p><p><span>[a]</span><span> 27:51, 52 ; 35 :44.</span></p>');
INSERT INTO `quran` VALUES (10052,2,35,12,'<p><span>[b]</span><span> Dan, Allah menciptakan kamu dari tanah, kemudian dari setetes nutfah, kemudian Dia menjadikanmu berjodoh-jodoh. Dan, tiada wanita hamil, dan tiada pula ia melahirkan </span><span>anak, </span><span>melainkan dengan sepengetahuan-Nya. Dan, tidak dipanjangkan umur orang yang berumur panjang, dan tiada dikurangkan umurnya melainkan di dalam sebuah kitab. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2411">2411</a><span>]</span><span> Sesungguhnya itu mudah bagi Allah.</span></p><p><span>[b]</span><span> 18:38 ; 22:6 ; 23:13 </span><span>–</span><span> 14 ; 36:78 ; 40:68.</span></p>');
INSERT INTO `quran` VALUES (10053,2,35,13,'<p><span>Dan, tidaklah sama dua lautan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2412">2412</a><span>]</span><span> yang satu enak, tawar, nyaman diminum, dan yang satu lagi asin dan </span><span>pahit.</span><span> </span><span>[a]</span><span> Namun, dari </span><span>laut</span><span> itu masing-masing kamu makan daging segar, dan kamu mengeluarkan perhiasan yang kamu pakai. Dan kamu lihat di situ kapal-kapal membelah </span><span>gelombang, supaya </span><span> kamu dapat mencari karunia-Nya, dan agar kamu bersyukur.</span></p><p><span>[a]</span><span> 16:15 ; 45:13.</span></p>');
INSERT INTO `quran` VALUES (10054,2,35,14,'<p><span>[b]</span><span> Dia memasukkan malam ke dalam siang dan Dia memasukkan siang ke dalam malam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2413">2413</a><span>]</span><span> </span><span>[c}</span><span> Dan, menundukkan matahari dan bulan, masing-masing menempuh jalan tempuhan hingga masa tertentu. Itulah Allah Tuhan-mu ; kepunyaan Dia-lah kerajaan. </span><span>[d]</span><span> Dan mereka yang kamu seru selain Dia, mereka tidak memiliki sebesar alur biji korma. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2413a">2413a</a><span>]</span></p><p><span>[b]</span><span> 22:62 ; 31:30 ; 57:7.</span></p><p><span>[c]</span><span> 7:55 ; 13:3 ; 31:21.</span></p><p><span>[d]</span><span> 13:15 ; 40:21.</span></p>');
INSERT INTO `quran` VALUES (10055,2,35,15,'<p><span>[a]</span><span> Jika kamu memanggil mereka, mereka tidak akan mendengar seruanmu ; dan sekiranya mereka mendengar, mereka tidak akan dapat menjawabmu. Dan pada Hari Kiamat mereka akan mengingkari kemusyrikanmu. Dan tiada yang dapat memberi penerangan kepadamu seperti Dia Yang Maha Mengetahui segala kabar.</span></p><p><span>[a]</span><span> 7:194.</span></p>');
INSERT INTO `quran` VALUES (10056,2,35,16,'<p><span>R. 3</span></p><p><span>Hai manusia, </span><span>[b]</span><span> kamulah yang memerlukan Allah, dan Allah itu Maha Kaya, Maha Terpuji.</span></p><p><span>[b]</span><span> 47:39.</span></p>');
INSERT INTO `quran` VALUES (10057,2,35,17,'<p><span>[c]</span><span> Jika Dia menghendaki, Dia dapat membinasakan kamu dan mendatangkan makhluk baru.</span></p><p><span>[c]</span><span> 4:134 ; 6:134 ; 14:20.</span></p>');
INSERT INTO `quran` VALUES (10058,2,35,18,'<p><span>[d]</span><span> Dan hal itu bagi Allah tidak sulit. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2414">2414</a><span>]</span></p><p><span>[d]</span><span> 14:21.</span></p>');
INSERT INTO `quran` VALUES (10059,2,35,19,'<p><span>[e]</span><span> Dan tiada </span><span>jiwa</span><span> berbeban dapat memikul beban orang lain ; dan jika </span><span>jiwa</span><span> berbeban berat berseru kepada yang lain untuk </span><span>memikul </span><span> bebannya, tidak akan dipikul sedikit pun darinya, walaupun ia kaum kerabat </span><span>sendiri</span><span> . </span><span>[f]</span><span> Engkau hanya dapat memperingatkan orang-orang yang takut kepada Tuhan mereka dalam keadaan menyendiri dan mendirikan shalat. Dan barangsiapa mensucikan untuk dirinya, dan kepada Allah kembali </span><span>segala sesuatu.</span></p><p><span>[e]</span><span> 6:165 ; 39:8 ; 53:39.</span></p><p><span>[f]</span><span> 36:12.</span></p>');
INSERT INTO `quran` VALUES (10060,2,35,20,'<p><span>Dan, tidak sama </span><span>[a]</span><span> orang buta dan orang yang melihat.</span></p><p><span>[a]</span><span> 11:25 ; 13:17 ; 40:59.</span></p>');
INSERT INTO `quran` VALUES (10061,2,35,21,'<p><span>Dan, tidak </span><span>sama</span><span> kegelapan dan cahaya.</span></p>');
INSERT INTO `quran` VALUES (10062,2,35,22,'<p><span>Dan, tidak </span><span>sama</span><span> teduh dan panas.</span></p>');
INSERT INTO `quran` VALUES (10063,2,35,23,'<p><span>Dan, tidak </span><span>sama</span><span> yang hidup dan yang mati. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2415">2415</a><span>]</span><span> Sesungguhnya, Allah membuat mendengar siapa yang disukai-Nya ; dan engkau tidak dapat membuat mendengar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2416">2416</a><span>]</span><span> orang yang ada dalam kubur.</span></p>');
INSERT INTO `quran` VALUES (10064,2,35,24,'<p><span>[b]</span><span> Engkau tidak lain melainkan seorang pemberi peringatan.</span></p><p><span>[b]</span><span> 11:13 ; 13:8.</span></p>');
INSERT INTO `quran` VALUES (10065,2,35,25,'<p><span>Sesungguhnya Kami mengutus engkau dengan kebenaran </span><span>[c]</span><span> sebagai pembawa khabar suka dan pemberi peringatan. Dan </span><span>[d]</span><span> tiada sesuatu kaum pun melainkan telah diutus kepada mereka seorang pemberi ingat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2417">2417</a><span>]</span></p><p><span>[c]</span><span> 2:120 ; 5:20 ; 11:3 ; 25:57 ; 48:9.</span></p><p><span>[d]</span><span> 10:48 ; 13:8 ; 16:37.</span></p>');
INSERT INTO `quran` VALUES (10066,2,35,26,'<p><span>[a]</span><span> Dan, jika mereka mendustakan engkau, maka orang-orang sebelum mereka pun telah mendustakan, telah datang kepada mereka rasul-rasul mereka dengan </span><span>[b]</span><span> Tanda-tanda yang jelas, dan dengan Kitab-kitab suci dan dengan Kitab yang menerangi.</span></p><p><span>[a]</span><span> 6:35 ; 22:43 ; 40:6 ; 54:10.</span></p><p><span>[b]</span><span> 16:45.</span></p>');
INSERT INTO `quran` VALUES (10067,2,35,27,'<p><span>Kemudian Aku tangkap orang-orang yang ingkar, dan betapa </span><span>mengerikannya </span><span> penolakan terhadap-Ku !</span></p>');
INSERT INTO `quran` VALUES (10068,2,35,28,'<p><span>R. 4</span></p><p><span>[c]</span><span> Apakah engkau tidak melihat, bahwa Allah menurunkan air dari awan, dan Kami mengeluarkan dengan air itu buah-buahan yang beraneka warnanya. Dan di gunung- gunung ada garis-garis putih dan merah, dengan beraneka macam warnanya, dan ada yang sehitam burung gagak ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2418">2418</a><span>]</span></p><p><span>[c]</span><span> 14:33 ; 22:6 ; 45:6.</span></p>');
INSERT INTO `quran` VALUES (10069,2,35,29,'<p><span>Dan demikian juga di antara manusia dan hewan berkaki empat dan binatang ternak, bermacam-macam warnanya. Sesungguhnya yang takut kepada Allah dari hamba- hamba-Nya hanyalah para ulama. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2419">2419</a><span>]</span><span> Sesungguhnya Allah Maha Perkasa, Maha Pengampun.</span></p>');
INSERT INTO `quran` VALUES (10070,2,35,30,'<p><span>Sesungguhnya, orang-orang yang membaca Kitab Allah dan mendirikan shalat </span><span>[a]</span><span> dan menbelanjakan sebagian dari apa yang telah Kami rezekikan kepada mereka dengan sembunyi-sembunyi dan terang-terang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2420">2420</a><span>]</span><span> mereka mengharapkan perniagaan yang tidak akan hancur.</span></p><p><span>[a]</span><span> 14:32 ; 16:76.</span></p>');
INSERT INTO `quran` VALUES (10071,2,35,31,'<p><span>[a]</span><span> Agar Dia menyempurnakan kepada mereka ganjaran mereka sepenuhnya dan Dia menambahkan kepada mereka dari karunia-Nya. Sesungguhnya, Dia Maha Pengampun, Maha Menghargai.</span></p><p><span>[a]</span><span> 3:58 ; 39:11.</span></p>');
INSERT INTO `quran` VALUES (10072,2,35,32,'<p><span>Dan apa yang Kami wahyukan kepada engkau dari Kitab ini adalah </span><span>[b]</span><span> kebenaran membenarkan apa yang sebelumnya. Sesungguhnya, Allah terhadap hamba-hambanya sungguh Maha Waspada dan Maha Melihat.</span></p><p><span>[b]</span><span> 22:55 ; 47:3 ; 56:96.</span></p>');
INSERT INTO `quran` VALUES (10073,2,35,33,'<p><span>Kemudian Kami telah mewariskan Kitab itu kepada orang-orang yang telah Kami pilih dari antara hamba-hamba Kami, maka dari antara mereka sangat aniaya terhadap dirinya, dan dari antara mereka ada yang mengambil jalan tengah, dan dari antara mereka ada yang mengungguli dalam kebajikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2421">2421</a><span>]</span><span> dengan izin Allah. Itu adalah merupakan karunia yang sangat besar.</span></p>');
INSERT INTO `quran` VALUES (10074,2,35,34,'<p><span>Ganjaran mereka ialah </span><span> </span><span>[a]</span><span> Kebun-kebun abadi, </span><span>[b]</span><span> mereka akan memasukinya </span><span>dan</span><span> di dalamnya mereka dihiasi dengan gelang-gelang emas dan mutiara, dan pakaian mereka di dalamnya adalah sutera.</span></p><p><span>[a]</span><span> 9:72 ; 13:24 ; 16:32 ; 61:13 ; 98:9.</span></p><p><span>[b]</span><span> 18:32 ; 22:24; 76:22.</span></p>');
INSERT INTO `quran` VALUES (10075,2,35,35,'<p><span>Dan mereka akan berkata, “Segala puji kepunyaan Allah, Yang telah menjauhkan kesedihan dari kami. Sesungguhnya Tuhan kami adalah Maha Pengampun, Maha menghargai,</span></p>');
INSERT INTO `quran` VALUES (10076,2,35,36,'<p><span>“Yang menempatkan kami di rumah abadi dari karunia-Nya, </span><span>[c]</span><span> tidak menyentuh kami di dalam kesulitan dan tidak pula menyentuh kami di dalamnya kelelahan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2422">2422</a><span>]</span></p><p><span>[c]</span><span> 15:49.</span></p>');
INSERT INTO `quran` VALUES (10077,2,35,37,'<p><span>Dan, orang-orang yang ingkar, bagi mereka ada Api Jahannam. </span><span>[d]</span><span> Tidak diputuskan atas mereka agar mereka mati, dan tidak diringankan bagi mereka dari azabnya. Demikian bagi mereka dari azabnya. Demikianlah Kami membalas setiap </span><span>orang</span><span> yang tidak bersyukur.</span></p><p><span>[d]</span><span> 20:75 ; 87:14.</span></p>');
INSERT INTO `quran` VALUES (10078,2,35,38,'<p><span>Dan, mereka akan berteriak </span><span>minta tolong</span><span> di dalamnya, “Wahai, Tuhan kami, keluarkanlah kami, </span><span>[a]</span><span> kami akan beramal shaleh, lain dengan apa yang biasa kami kerjakan.” </span><span>Allah berfir-man,</span><span> “Bukankah telah Kami beri kamu umur yang cukup panjang, agar orang yang hendak mengambil pelajaran akan memperoleh pelajaran di dalamnya ? Dan telah datang kepadamu seorang Pemberi peringatan. Maka rasakanlah </span><span>azab ini,</span><span> karena tiada seorang penolong pun bagi orang-orang zalim.</span></p><p><span>[a]</span><span> 7:54 ; 26:103 ; 32:13 ; 39:59.</span></p>');
INSERT INTO `quran` VALUES (10079,2,35,39,'<p><span>R. 5</span></p><p><span>[b]</span><span> Sesungguhnya, Allah mengetahui segala yang gaib di seluruh langit dan bumi. Sesungguhnya Dia mengetahui benar apa yang di dalam hati.</span></p><p><span>[b]</span><span> 11:124 ; 16:78 ; 27:66.</span></p>');
INSERT INTO `quran` VALUES (10080,2,35,40,'<p><span>Dia-lah Dzat Yang telah menjadikan kamu khalifah-khalifah di bumi. Barangsiapa yang ingkar, maka ia sendiri menanggung akibat keingkarannya. Dan tidak akan menambah orang-orang ingkar itu keingkarannya di sisi Tuhan mereka, melainkan kemurkaan, dan tidak akan menambah kepada orang-orang ingkar itu keingkaran mereka, melainkan kerugian.</span></p>');
INSERT INTO `quran` VALUES (10081,2,35,41,'<p><span>Katakanlah, </span><span>[a]</span><span> “Sudahkah kamu melihat tuhan-tuhan sekutumu yang kamu seru selian Allah ? Perlihatkanlah kepada-Ku apa yang telah diciptakan mereka dari bumi, atau, punyakah mereka bahagian dalam penciptaan seluruh langit ? Atau, telah Kami berikankah kepada mereka suatu Kitab, sehingga mempunyai bukti darinya ? </span><span>Tidaklah demikian ! </span><span> Bahkan orang-orang aniaya itu tidak menjanjikan satu sama lain melainkan tipuan belaka.</span></p><p><span>[a]</span><span> 34:28 ; 46:5.</span></p>');
INSERT INTO `quran` VALUES (10082,2,35,42,'<p><span>[b]</span><span> Sesungguhnya, Allah menahan seluruh langit dan bumi sepaya keduanya jangan sampai menyimpang </span><span>dari posisinya,</span><span> dan andaikata keduanya menyimpang, maka tiada seorang pun dapat menahan keduanya selain Dia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2423">2423</a><span>]</span><span> Sesungguhnya Dia Maha Penyantun, Maha Pengampun.</span></p><p><span>[b]</span><span> 22:66.</span></p>');
INSERT INTO `quran` VALUES (10083,2,35,43,'<p><span>[c]</span><span> Dan mereka bersumpah dengan nama Allah sekuat-kuat persumpahan mereka, bahwa sekiranya seorang Pemberi ingat datang kepada mereka, tentu mereka akan lebih banyak mendapat petunjuk dari umat-umat yang lain, tetapi tatkala telah datang kepada mereka seorang Pemberi ingat, tidak menambah bagi mereka selain kebencian.</span></p><p><span>[c]</span><span> 6:158.</span></p>');
INSERT INTO `quran` VALUES (10084,2,35,44,'<p><span>[a]</span><span> Sebab, mereka bersikap sombong di bumi dan </span><span>merencanakan</span><span> tipu daya jahat. Tetapi tipu daya jahat itu tidak meliputi sesuatu selain </span><span>meliputi </span><span> si perancananya. Maka, tidakkah mereka menantikan </span><span>sesuatu </span><span> yang lain selain kebiasaan </span><span>Allah memperlakukan</span><span> orang-orang yang terdahulu ? Maka sekali-kali tidak akan engkau dapatkan sesuatu </span><span>[b]</span><span> perubahan dalam sunnah Allah ; tidak pula sekali-kali engkau dapatkan sesuatu pergantian dalam sunnah Allah.</span></p><p><span>[a]</span><span> 27:51 </span><span>–</span><span> 52.</span></p><p><span>[b]</span><span> 17:78 ; 33:63 ; 48:24.</span></p>');
INSERT INTO `quran` VALUES (10085,2,35,45,'<p><span>[c]</span><span> Apakah mereka tidak pernah berjalan di muka bumi dan melihat betapa </span><span>buruknya </span><span> kesudahan orang-orang sebelum mereka ? Padahal mereka itu lebih hebat dari mereka dalam kekuatan. Dan, tidak ada sesuatu pun yang menggagalkan Allah di seluruh langit dan di bumi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2424">2424</a><span>]</span><span> Sesungguhnya Dia Maha Mengetahui, Maha Kuasa.</span></p><p><span>[c]</span><span> 12:110 ; 22:46, 47 ; 30:10 ; 40:22 ; 47:11.</span></p>');
INSERT INTO `quran` VALUES (10086,2,35,46,'<p><span>[a]</span><span> Dan sekiranya Allah akan menghukum manusia atas apa yang diusahakan mereka, tentu Dia tidak akan meninggalkan satu makhluk hidup pun di permukaan </span><span>bumi</span><span> ini ; akan tetapi Dia memberi tangguh kepada mereka sampai </span><span>[b]</span><span> suatu masa yang tertentu ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2425">2425</a><span>]</span><span> maka apabila masa yang ditetapkan bagi mereka telah tiba, maka sesungguhnya Allah Maha Melihat kepada hamba-hamba-Nya.</span></p><p><span>[a]</span><span> 10:12 ; 18:59.</span></p><p><span>[b]</span><span> 7:35 ; 10:50 ; 16:62.</span></p>');
INSERT INTO `quran` VALUES (10087,2,36,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10088,2,36,2,'<p><span>Hai manusia sempurna, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2426">2426</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10089,2,36,3,'<p><span>Demi Alquran. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2427">2427</a><span>]</span><span> yang penuh hikmah. </span></p>');
INSERT INTO `quran` VALUES (10090,2,36,4,'<p><span>Sesungguhnya engkau dari </span><span>antara</span><span> rasul-rasul.</span></p>');
INSERT INTO `quran` VALUES (10091,2,36,5,'<p><span>Pada jalan yang lurus, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2427a">2427a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10092,2,36,6,'<p><span>[b]</span><span> </span><span>Inilah Alquran</span><span> yang diturunkan oleh Yang Maha Perkasa, Maha Penyayang,</span></p><p><span>[b]</span><span> 20:5 ; 32:3 ; 40:3 ; 41:3 ; 45:3 ; 46:3.</span></p>');
INSERT INTO `quran` VALUES (10093,2,36,7,'<p><span>[a]</span><span> Supaya engkau memberi peringatan kepada suatu kaum yang bapak-bapaknya belum pernah diberi peringatan. Maka mereka itu lalai.</span></p><p><span>[a]</span><span> 28:47 ; 32:4.</span></p>');
INSERT INTO `quran` VALUES (10094,2,36,8,'<p><span>Sesungguhnya perkataan itu telah terbukti benar atas kebanyakan mereka, maka mereka tidak akan beriman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2427b">2427b</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10095,2,36,9,'<p><span>[b]</span><span> Sesungguhnya telah Kami pasangkan belenggu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2428">2428</a><span>]</span><span> sekeliling leher mereka sampai dagu mereka, maka mereka tertengadah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2428a">2428a</a><span>]</span></p><p><span>[b]</span><span> 13:6 ; 76:5.</span></p>');
INSERT INTO `quran` VALUES (10096,2,36,10,'<p><span>Dan, Kami telah memasang penghalang di hadapan mereka dan penghalang di belakang mereka , dan telah Kami menutupi mereka, maka mereka tidak melihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2429">2429</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10097,2,36,11,'<p><span>[a]</span><span> Dan sama saja bagi mereka, baik engkau memberi peringatan kepada mereka atau tidak memberi peringatan kepada mereka, mereka tidak akan beriman.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2430">2430</a><span>]</span></p><p><span>[a]</span><span> 2:7.</span></p>');
INSERT INTO `quran` VALUES (10098,2,36,12,'<p><span>[b]</span><span> Engkau hanya dapat menasihati orang yang mengikuti Pemberi peringatan dan yang takut kepada </span><span>Tuhan</span><span> Yang Maha Pemurah dalam keadaan tidak tampak, maka berilah dia khabar suka tentang ampunan dan ganjaran yang mulia.</span></p><p><span>[b]</span><span> 35:19.</span></p>');
INSERT INTO `quran` VALUES (10099,2,36,13,'<p><span>Sesungguhnya, Kami menghidupkan yang telah mati dan Kami mencatat apa yang telah mereka dahulukan dan akibat-akibat mereka. </span><span>[c]</span><span> Dan segala sesuatu itu Kami menghitungnya dalam Kitab yang nyata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2431">2431</a><span>]</span></p><p><span>[c]</span><span> 18:50 ; 72:29.</span></p>');
INSERT INTO `quran` VALUES (10100,2,36,14,'<p><span>R. 2</span></p><p><span>Dan, terangkanlah bagi mereka itu misal tentang penduduk suatu negeri</span><span>; [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2432">2432</a><span>]</span><span> ketika datang kepada mereka rasul-rasul.</span></p>');
INSERT INTO `quran` VALUES (10101,2,36,15,'<p><span>Ketika Kami mengirimkan kepada mereka dua </span><span>orang rasul,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2433">2433</a><span>]</span><span> maka mereka mendustakan keduanya ; kemudian Kami memperkuat dengan yang ketiga ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2434">2434</a><span>]</span><span> lalu mereka berkata, “Sesungguhnya, kami diutus kapada kamu.”</span></p>');
INSERT INTO `quran` VALUES (10102,2,36,16,'<p><span>Mereka berkata, </span><span>[a]</span><span> “Kamu tidak lain hanyalah manusia seperti kami dan </span><span>Tuhan </span><span> Yang Maha Pemurah tidak menurunkan sesuatu. Kamu hanya berdusta belaka.”</span></p><p><span>[a]</span><span> 14:11 ; 26:155. </span></p>');
INSERT INTO `quran` VALUES (10103,2,36,17,'<p><span>Mereka berkata, “Tuhan kami mengetahui, bahwa sesungguhnya kami diutus kepada kamu.</span></p>');
INSERT INTO `quran` VALUES (10104,2,36,18,'<p><span>[b]</span><span> “Dan tugas kami tiada lain hanya menyampaikan dengan seterang-terangnya.”</span></p><p><span>[b]</span><span> 13:41 ; 16:36 ; 24:55 ; 29:19.</span></p>');
INSERT INTO `quran` VALUES (10105,2,36,19,'<p><span>Meraka berkata, “Sesungguhnya, kami meramalkan kemalangan kami karena kamu ; jika kamu tidak berhenti, tentulah kami akan merajammu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2435">2435</a><span>]</span><span> dan pasti akan menimpamu dari kami siksaan yang pedih.”</span></p>');
INSERT INTO `quran` VALUES (10106,2,36,20,'<p><span>Mereka, </span><span>para rasul </span><span> berkata, “Kemalanganmu itu bersama diri kamu sendiri. Apakah jika kamu diingatkan ? </span><span>Kemudian kamu tetap ingkar.</span><span> Bahkan kamu adalah suatu kaum yang melanggar batas.”</span></p>');
INSERT INTO `quran` VALUES (10107,2,36,21,'<p><span>[a]</span><span> Maka datang dari bagian terjauh kota itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2436">2436</a><span>]</span><span> seorang laki-laki </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2437">2437</a><span>]</span><span> dengan berlari-lari ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2438">2438</a><span>]</span><span> ia berkata, “Hai kaumku, ikutilah para rasul itu.</span></p><p><span>[a]</span><span> 28:21.</span></p>');
INSERT INTO `quran` VALUES (10108,2,36,22,'<p><span>“Ikutilah mereka yang tidak meminta upah dari kamu dan mereka yang telah mendapat petunjuk.”</span></p>');
INSERT INTO `quran` VALUES (10109,2,36,23,'<p><span>Juz XXIII</span></p><p><span>“Dan mengapakah aku tidak menyembah Yang menciptakan diriku, dan kepada Dia-lah kamu sekalian akan dikembalikan.</span></p>');
INSERT INTO `quran` VALUES (10110,2,36,24,'<p><span>“Apakah aku mengambil sebagai sembahan-sembahan selain Dia ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2439">2439</a><span>] [b]</span><span> Jika Yang Maha Pemurah menghendaki sesuatu kemudaratan bagiku, syafaat mereka tidak akan bermanfaat bagiku sedikit pun, dan mereka tidak dapat menyelamatkanku.</span></p><p><span>[b]</span><span> 22:13 </span><span>–</span><span> 14 ; 39:39.</span></p>');
INSERT INTO `quran` VALUES (10111,2,36,25,'<p><span>“Sesungguhnya jika aku </span><span>berbuat </span><span>demikian, niscaya berada dalam kesesatan yang nyata.</span></p>');
INSERT INTO `quran` VALUES (10112,2,36,26,'<p><span>“Sesungguhnya aku beriman kepada Tuhan-mu ; maka dengarlah aku.”</span></p>');
INSERT INTO `quran` VALUES (10113,2,36,27,'<p><span>Dikatakan </span><span>kepadanya,</span><span> “Masuklah ke dalam surga.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2440">2440</a><span>]</span><span> Ia berkata, “Ah, alangkah baiknya, jika kaumku mengetahui.”</span></p>');
INSERT INTO `quran` VALUES (10114,2,36,28,'<p><span>“Batapa Tuhan-ku telah mengampuniku dan telah menjadikan aku dari antara orang-orang yang dimuliakan.””</span></p>');
INSERT INTO `quran` VALUES (10115,2,36,29,'<p><span>Dan tidaklah Kami menurunkan atas kaumnya sesudah dia suatu lasykar dari langit, dan tidak pernah pula Kami menurunkannya.</span></p>');
INSERT INTO `quran` VALUES (10116,2,36,30,'<p><span>[a]</span><span> Itu tidak lain melainkan suatu ledakan dahsyat, tiba-tiba musnahlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2441">2441</a><span>]</span><span> </span></p><p><span>[a]</span><span> 21:41 ; 36:50.</span></p>');
INSERT INTO `quran` VALUES (10117,2,36,31,'<p><span>Ah, sayang bagi hamba-hamba-Ku ! </span><span>[b]</span><span> Tidak pernah datang kepada mereka seorang rasul, melainkan mereka senantiasa mencemoohkannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2442">2442</a><span>]</span><span> </span></p><p><span>[b]</span><span> 15:12 ; 43:8.</span></p>');
INSERT INTO `quran` VALUES (10118,2,36,32,'<p><span>[c]</span><span> Apakah mereka tidak melihat betapa banyaknya keturunan yang telah Kami binasakan sebelum mereka, bahwa </span><span>[d]</span><span> mereka itu tidak kembali lagi kepada mereka ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2443">2443</a><span>]</span></p><p><span>[c]</span><span> 17:18 ; 19:99 ; 20:129 ; 50:37.</span></p><p><span>[d]</span><span> 21:96 ; 23:100, 101.</span></p>');
INSERT INTO `quran` VALUES (10119,2,36,33,'<p><span>Dan, sesungguhnya mereka semua, akan dihadapkan kepada Kami.</span></p>');
INSERT INTO `quran` VALUES (10120,2,36,34,'<p><span>R. 3</span></p><p><span>[e]</span><span> Dan suatu Tanda bagi mereka adalah bumi yang mati ; Kami menghidupkannya dan Kami tumbuhkan darinya biji-bijian, maka mereka makan darinya.</span></p><p><span>[e]</span><span> 16:66.</span></p>');
INSERT INTO `quran` VALUES (10121,2,36,35,'<p><span>[a]</span><span> Dan Kami jadikan di dalamnya kebun-kebun kurma dan anggur, dan Kami pancarkan di dalamnya mata-mata air. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2444">2444</a><span>]</span></p><p><span>[a]</span><span> 13:5 ; 16:68 ; 23:20.</span></p>');
INSERT INTO `quran` VALUES (10122,2,36,36,'<p><span>Supaya mereka dapat makan buah-buahannya, dan bukanlah tangan mereka yang menjadikannya </span><span>tumbuh</span><span>. Kemudian, tidaklah mereka bersyukur ?</span></p>');
INSERT INTO `quran` VALUES (10123,2,36,37,'<p><span>[b]</span><span> Maha Suci Dzat Yang menciptakan segala sesuatu berjodoh-jodoh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2445">2445</a><span>]</span><span> dari apa yang di tumbuhkan oleh bumi, maupun dari diri mereka sendiri, dan juga dari apa yang mereka tidak mengetahui.</span></p><p><span>[b]</span><span> 13:4 ; 51:50.</span></p>');
INSERT INTO `quran` VALUES (10124,2,36,38,'<p><span>[c]</span><span> Dan suatu Tanda bagi mereka adalah malam, darinya siang hari Kami tanggalkan, dan tiba-tiba mereka berada dalam kegelapan.</span></p><p><span>[c]</span><span> 17:13 ; 40:62.</span></p>');
INSERT INTO `quran` VALUES (10125,2,36,39,'<p><span>[d]</span><span> Dan matahari terus beredar ke arah tujuan yang telah ditetapkan baginya. Itulah takdir </span><span>Tuhan</span><span> Yang Maha Perkasa, Maha Mengetahui.</span></p><p><span>[d]</span><span> 6:97 ; 55:6.</span></p>');
INSERT INTO `quran` VALUES (10126,2,36,40,'<p><span>[a]</span><span> Dan bagi bulan telah Kami tetapkan tingkat-tingkatnya, sehingga ia kembali lagi seperti bentuk tandan </span><span>kurma</span><span> yang tua. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2445a">2445a</a><span>]</span></p><p><span>[a]</span><span> 10:6.</span></p>');
INSERT INTO `quran` VALUES (10127,2,36,41,'<p><span>Matahari tidak kuasa menyusul bulan, </span><span>[b]</span><span> dan tidak pula malam mendahului siang. </span><span>[c]</span><span> Dan semua itu terus beredar pada tempat peredarannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2446">2446</a><span>]</span></p><p><span>[b]</span><span> 25:63.</span></p><p><span>[c]</span><span> 21:34.</span></p>');
INSERT INTO `quran` VALUES (10128,2,36,42,'<p><span>Dan, suatu Tanda bagi mereka ialah, bahwa Kami muatkan anak-cucu mereka dalam bahtera-bahtera yang bermuatan penuh.</span></p>');
INSERT INTO `quran` VALUES (10129,2,36,43,'<p><span>Dan, Kami menciptakan bagi mereka semacam itu juga </span><span>[a]</span><span> yang akan mereka kendarai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2447">2447</a><span>]</span></p><p><span>[a]</span><span> 16:9 ; 43:13.</span></p>');
INSERT INTO `quran` VALUES (10130,2,36,44,'<p><span>Dan jika Kami menghendaki, tentulah dapat Kami menenggelamkan mereka ; kemudian tidak akan ada penolong bagi mereka, dan tidak pula mereka akan diselamatkan.</span></p>');
INSERT INTO `quran` VALUES (10131,2,36,45,'<p><span>Kecuali dengan rahmat dari Kami dan sebagai bekal sampai suatu masa.</span></p>');
INSERT INTO `quran` VALUES (10132,2,36,46,'<p><span>Dan, apabila dikatakan kepada mereka, “Lindungilah dirimu dari apa yang ada di hadapanmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2448">2448</a><span>]</span><span> dan terhadap </span><span>amal yang kamu tinggalkan </span><span> di belakangmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2449">2449</a><span>]</span><span> supaya kamu dikasihani.”</span></p>');
INSERT INTO `quran` VALUES (10133,2,36,47,'<p><span>[b]</span><span> Dan, tidak pernah datang kepada mereka suatu Tanda dari antara Tanda-tanda Tuhan mereka, melainkan mereka berpaling darinya.</span></p><p><span>[b]</span><span> 6:5 ; 21:3 ; 26:6.</span></p>');
INSERT INTO `quran` VALUES (10134,2,36,48,'<p><span>Dan apabila dikatakan kepada mereka, “Belanjakanlah dari apa yang telah direzekikan Allah kepadamu, “ berkatalah orang-orang kafir itu kepada orang-orang yang beriman, </span><span>[a]</span><span> “Apakah kami memberi makan kepada orang yang, jika Allah menghendaki, tentulah Dia akan memberinya makan ? Tidaklah kamu melainkan dalam kesesatan yang nyata.”</span></p><p><span>[a]</span><span> 3:182 ; 5:65.</span></p>');
INSERT INTO `quran` VALUES (10135,2,36,49,'<p><span>[b]</span><span> Dan mereka berkata, “Bilamanakah janju </span><span>azab </span><span>ini </span><span>akan terlaksana,</span><span> jika kamu memang orang-orang benar ? “</span></p><p><span>[b]</span><span> 21:39 ; 34:30 ; 67:26.</span></p>');
INSERT INTO `quran` VALUES (10136,2,36,50,'<p><span>Tidaklah mereka menunggu melainkan satu </span><span>[c]</span><span> ledakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2450">2450</a><span>]</span><span> yang akan menyergap mereka sementara mereka sedang bertengkar.</span></p><p><span>[c]</span><span> 21:41 ; 36:30 ; 38:16.</span></p>');
INSERT INTO `quran` VALUES (10137,2,36,51,'<p><span>Maka mereka tidak akan dapat membuat sebuah wasiat pun dan tidak pula mereka akan kembali kepada keluarga mereka.</span></p>');
INSERT INTO `quran` VALUES (10138,2,36,52,'<p><span>R. 4</span></p><p><span>[d]</span><span> Dan sangkakala akan ditiup, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2451">2451</a><span>]</span><span> maka tiba-tiba mereka akan segera keluar dari kuburan menuju Tuhan mereka.</span></p><p><span>[d]</span><span> 18:100 ; 39:69 ; 50:21 ; 69:14.</span></p>');
INSERT INTO `quran` VALUES (10139,2,36,53,'<p><span>Mereka akan berkata </span><span>kepada satu sama lain,</span><span>” Aduhai, celakalah kami ! Siapakah yang telah membangkitkan kami dari tempat tidur kami ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2452">2452</a><span>]</span><span> Inilah apa yang telah dijanjikan </span><span>Tuhan</span><span> Yang Maha Pemurah, dan benarlah apa yang dikatakan oleh rasul-rasul itu ?</span></p>');
INSERT INTO `quran` VALUES (10140,2,36,54,'<p><span>Ini tidak lain hanya satu ledakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2453">2453</a><span>]</span><span> dan tiba-tiba mereka itu semua akan dihadapkan kepada Kami.</span></p>');
INSERT INTO `quran` VALUES (10141,2,36,55,'<p><span>[a]</span><span> Maka, pada hari itu tiada suatu jiwa pun akan dianiaya barang sezarah pun ; dan kamu tidak akan dibalas melainkan apa yang telah kamu kerjakan.</span></p><p><span>[a</span><span>] 3:26 ; 40:18 ; 45:23.</span></p>');
INSERT INTO `quran` VALUES (10142,2,36,56,'<p><span>Sesungguhnya, para ahli surga pada hari itu akan bergembira dalam kesibukan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2454">2454</a><span>]</span><span> </span><span>zikir Ilahi.</span></p>');
INSERT INTO `quran` VALUES (10143,2,36,57,'<p><span>Mereka dan istri-istri mereka akan berada di tempat-tempat teduh, </span><span>[a]</span><span> sambil bersandar di atas dipan-dipan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2455">2455</a><span>]</span></p><p><span>[a]</span><span> 15:48 ; 18:32 ; 83:24.</span></p>');
INSERT INTO `quran` VALUES (10144,2,36,58,'<p><span>[b]</span><span> Bagi mereka di dalamnya tersedia buah-buahan, dan mereka akan diberi apa pun yang mereka minta.</span></p><p><span>[b]</span><span> 52:23 ; 55:53.</span></p>');
INSERT INTO `quran` VALUES (10145,2,36,59,'<p><span>[c]</span><span> “Salam,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2456">2456</a><span>]</span><span> sebagai ucapan </span><span>selamat</span><span> dari Tuhan Yang Maha Penyayang.</span></p><p><span>[c]</span><span> 10:11 ; 14:24 ; 33:45.</span></p>');
INSERT INTO `quran` VALUES (10146,2,36,60,'<p><span>Dan berpisahlah kamu pada hari ini, hai orang-orang yang berdosa </span><span>dari orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (10147,2,36,61,'<p><span>“Bukanlah telah Aku perintahkan kepadamu, hai Bani Adam, bahwa kamu janganlah menyembah syaitan, sesungguhnya bagimu ia </span><span>[d]</span><span> musuh yang nyata.</span></p><p><span>[d]</span><span> 6:143.</span></p>');
INSERT INTO `quran` VALUES (10148,2,36,62,'<p><span>“Dan supaya kamu menyembah-Ku. Inilah jalan yang lurus.</span></p>');
INSERT INTO `quran` VALUES (10149,2,36,63,'<p><span>“Dan sesungguhnya </span><span>syaitan</span><span> telah menyesatkan sebagian besar dari antara kamu. Maka apakah kamu tidak mau berpikir ?</span></p>');
INSERT INTO `quran` VALUES (10150,2,36,64,'<p><span>[a]</span><span> Inilah Jahannam yang telah dijanjikan kepadamu.</span></p><p><span>[a]</span><span> 52:15 ; 55:44.</span></p>');
INSERT INTO `quran` VALUES (10151,2,36,65,'<p><span>“Masuklah itu pada hari ini, disebabkan kamu dahulu selalu ingkar.”</span></p>');
INSERT INTO `quran` VALUES (10152,2,36,66,'<p><span>Pada hari ini Kami akan mencap pada mulut mereka </span><span>[b]</span><span> dan tangan mereka akan berbicara kepada Kami, dan kaki mereka akan bersaksi tentang apa yang dahulu mereka usahakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2457">2457</a><span>]</span></p><p><span>[b]</span><span> 17:37 ; 24:25 ; 41:21 </span><span>–</span><span> 23.</span></p>');
INSERT INTO `quran` VALUES (10153,2,36,67,'<p><span>Dan, sekiranya Kami menghendaki, niscaya Kami dapat melenyapkan penglihatan mata mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2458">2458</a><span>]</span><span> kemudian mereka akan berlomba-lomba </span><span>mencari </span><span>jalan. Tetapi, bagaimana kah mereka dapat melihat </span><span>jalan yang benar</span><span> ?</span></p>');
INSERT INTO `quran` VALUES (10154,2,36,68,'<p><span>Dan sekiranya Kami menghendaki, niscaya Kami dapat mengubah </span><span>keadaan</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2458a">2458a</a><span>]</span><span> mereka pada tempat mereka ; kemudian mereka tidak dapat maju ke depan dan tidak pula mereka kembali.</span></p>');
INSERT INTO `quran` VALUES (10155,2,36,69,'<p><span>R. 5</span></p><p><span>[a]</span><span> Dan barangsiapa Kami penjangkan umurnya, tentulah Kami melemahkan dalam kejadiannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2459">2459</a><span>]</span><span> Maka tidakkah mereka mau mengerti ?</span></p><p><span>[a]</span><span> 16:71.</span></p>');
INSERT INTO `quran` VALUES (10156,2,36,70,'<p><span>Dan Kami tidak mengajarinya syair dan tidak pula patut baginya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2460">2460</a><span>] [b]</span><span> Itu hanyalah suatu nasihat dan Quran yang memberi penerangan.</span></p><p><span>[b]</span><span> 15:10 ; 65:11.</span></p>');
INSERT INTO `quran` VALUES (10157,2,36,71,'<p><span>Supaya memberi peringatan kepada yang hidup, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2461">2461</a><span>]</span><span> dan supaya menjadi sempurna keputusan </span><span>Allah</span><span> atas orang-orang kafir.</span></p>');
INSERT INTO `quran` VALUES (10158,2,36,72,'<p><span>Apakah mereka tidak melihat, bahwa di antara barang-barang yang telah dibuat oleh tangan Kami, telah Kami ciptakan binatang ternak bagi mereka dan mereka menjadi pemiliknya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2462">2462</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10159,2,36,73,'<p><span>[a]</span><span> Dan Kami telah menundukkan bagi mereka maka sebagaian dari </span><span>binatang-binatang </span><span> itu menjadi tunggangan mereka dan dari sebagiannya mereka makan.</span></p><p><span>[a]</span><span> 6:143 ; 16:6 ; 40:80 </span><span>–</span><span> 81.</span></p>');
INSERT INTO `quran` VALUES (10160,2,36,74,'<p><span>[b]</span><span> Dan, bagi mereka di dalam </span><span>binatang-binatang </span><span> itu terdapat banyak manfaat dan minuman. Apakah mereka tidak bersyukur ?</span></p><p><span>[b]</span><span> 16:6, 67.</span></p>');
INSERT INTO `quran` VALUES (10161,2,36,75,'<p><span>[c]</span><span> Dan, mereka telah menjadikan tuhan-tuhan selain Allah, supaya mereka ditolong.</span></p><p><span>[c]</span><span> 7:193.</span></p>');
INSERT INTO `quran` VALUES (10162,2,36,76,'<p><span>Berhala-berhala</span><span> itu tidak dapat menolong mereka. </span><span>Sebaliknya </span><span> mereka, adalah lasykar yang dihadirkan untuk </span><span>menentang </span><span> mereka.</span></p>');
INSERT INTO `quran` VALUES (10163,2,36,77,'<p><span>[a]</span><span> Maka janganlah menyedihkan engkau ucapan mereka. Sesungguhnya </span><span>[b]</span><span> Kami mengetahui apa yang mereka sembunyikan dan apa yang mereka tampakkan.</span></p><p><span>[a]</span><span> 10:66. </span></p><p><span>[b]</span><span> 11:6 ; 16:24 ; 27:75 ; 28:70.</span></p>');
INSERT INTO `quran` VALUES (10164,2,36,78,'<p><span>Apakah manusia tidak melihat, bahwa </span><span>[c]</span><span> Kami telah menciptakan dia dari setetes air mani ? lalu tiba-tiba ia menjadi pembantah yang nyata !</span></p><p><span>[c]</span><span> 18:38 ; 22:6 ; 23:14 ; 35:12 ; 40:68.</span></p>');
INSERT INTO `quran` VALUES (10165,2,36,79,'<p><span>Dan, ia membuat perumpamaan-perumpamaan mengenai Kami dan melupakan kejadian dirinya sendiri. Berkatalah ia, </span><span>[d]</span><span> “Siapakah yang akan menghidupkan tulang itu setalah hancur-luluh ?”</span></p><p><span>[d]</span><span> 19:67 ; 23:38 ; 45:25.</span></p>');
INSERT INTO `quran` VALUES (10166,2,36,80,'<p><span>[e]</span><span> Katakanlah, “Dia, yang menghidupkannya, yang menciptakannya pertama kali, dan Dia Maha Mengetahui </span><span>keadaan </span><span> setiap makhluk ;</span></p><p><span>[e]</span><span> 17:52 ; 46:34 ; 75:41.</span></p>');
INSERT INTO `quran` VALUES (10167,2,36,81,'<p><span>[f]</span><span> </span><span>Dia</span><span> Yang telah menjadikan bagimu api dari pohon hijau itu ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2463">2463</a><span>]</span><span> maka lihatlah, darinya kamu menyalakan api.</span></p><p><span>[f]</span><span> 56:72, 73.</span></p>');
INSERT INTO `quran` VALUES (10168,2,36,82,'<p><span>[a]</span><span> Tidaklah </span><span>Dia</span><span> Yang telah menciptakan seluruh langit dan bumi itu berkuasa menciptakan lagi makhluk seperti mereka itu ?” Ya, </span><span>Dia berkuasa !</span><span> Dan Dia sungguh Maha Pencipta, Maha Tahu.</span></p><p><span>[a]</span><span> 17:100 ; 46:34 ; 86:9.</span></p>');
INSERT INTO `quran` VALUES (10169,2,36,83,'<p><span>[b]</span><span> Sesungguhnya perintah-Nya, apabila Dia menghendaki sesuatu, Dia </span><span>hanya</span><span> berfirman mengenai itu, “Jadilah,” maka jadilah ia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2664">2664</a><span>]</span></p><p><span>[b]</span><span> 2:118 ; 3:48 ; 40:69.</span></p>');
INSERT INTO `quran` VALUES (10170,2,36,84,'<p><span>Maka , Maha Suci Dia Yang </span><span>[c]</span><span> di tangan-Nya ada kedaulantan atas segala sesuatu. Dan kepada Dia-lah kamu </span><span>semua </span><span> akan dikembalikan.</span></p><p><span>[c]</span><span> 23:89.</span></p>');
INSERT INTO `quran` VALUES (10171,2,37,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10172,2,37,2,'<p><span>Demi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2465">2465</a><span>]</span><span> mereka yang berjajar dalam shaf-shaf yang rapat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2466">2466</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10173,2,37,3,'<p><span>Dan mereka yang menolak </span><span>kejahatan </span><span> dengan giatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2467">2467</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10174,2,37,4,'<p><span>Dan mereka yang membacakan Pemberi peringatan ini, </span><span>ialah alquran.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2468">2468</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10175,2,37,5,'<p><span>[a]</span><span> Sesungguhnya, Tuhan-mu itu adalah Esa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2469">2469</a><span>]</span></p><p><span>[a]</span><span> 5:74 ; 16:23 ; 22:35.</span></p>');
INSERT INTO `quran` VALUES (10176,2,37,6,'<p><span>[b]</span><span> Tuhan seluruh langit dan bumi dan segala sesuatu yang ada di antara keduanya dan Tuhan tempat-tempat yang darinya cahaya memancar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2470">2470</a><span>]</span></p><p><span>[b]</span><span> 19:66 ; 38:67 ; 44:8 ; 78:38.</span></p>');
INSERT INTO `quran` VALUES (10177,2,37,7,'<p><span>Sesungguhnya telah </span><span>[c]</span><span> Kami hiasi langit yang terendah dengan hiasan bintang-bintang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2471">2471</a><span>]</span></p><p><span>[c]</span><span> 15:17 ; 41:13 ; 67:6</span></p>');
INSERT INTO `quran` VALUES (10178,2,37,8,'<p><span>[a]</span><span> Dan, telah memelihara</span><span>nya</span><span> dari segala syaitan durhaka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2472">2472</a><span>]</span></p><p><span>[a]</span><span> 15:18 ; 41:13 ; 67:6.</span></p>');
INSERT INTO `quran` VALUES (10179,2,37,9,'<p><span>Mereka tidak dapat mendengar sesuatu dari Dewan Agung </span><span>malaikat</span><span> dan mereka dilempari dari segala jurusan.</span></p>');
INSERT INTO `quran` VALUES (10180,2,37,10,'<p><span>Terusir dan bagi mereka ada azab yang kekal,</span></p>');
INSERT INTO `quran` VALUES (10181,2,37,11,'<p><span>[b]</span><span> Kecuali barangsiapa menyambar </span><span>sesuatu</span><span> dengan curi-curi, maka ia dikejar oleh nyala api yang cemerlang </span><span>hamba-hamba Allah.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2473">2473</a><span>]</span></p><p><span>[b]</span><span> 15:19.</span></p>');
INSERT INTO `quran` VALUES (10182,2,37,12,'<p><span>Maka tanyakanlah kepada mereka, merekalah yang lebih sukar diciptakan ataukah mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2474">2474</a><span>]</span><span> </span><span>yang lainnya lagi,</span><span> yang telah Kami ciptakan ? Merekalah yang telah Kami ciptakan dari </span><span>[c]</span><span> tanah liat lengket.</span></p><p><span>[c]</span><span> 6:3 ; 23:13 ; 32:8 ; 38:72.</span></p>');
INSERT INTO `quran` VALUES (10183,2,37,13,'<p><span>Bahkan engkau merasa heran, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2475">2475</a><span>]</span><span> sedang mereka berolok-olok.</span></p>');
INSERT INTO `quran` VALUES (10184,2,37,14,'<p><span>Dan apabila mereka diperingatkan, mereka tidak mengindahkan.</span></p>');
INSERT INTO `quran` VALUES (10185,2,37,15,'<p><span>Dan apabila mereka melihat suatu Tanda, mereka memperolok-olok</span><span>nya.</span></p>');
INSERT INTO `quran` VALUES (10186,2,37,16,'<p><span>Dan mereka berkata, </span><span>[a]</span><span> “Ini tiada lain melainkan sihir yang nyata. </span></p><p><span>[a]</span><span> 7:110 ; 61:7.</span></p>');
INSERT INTO `quran` VALUES (10187,2,37,17,'<p><span>[b]</span><span> “Apakah apabila kami telah mati dan sudah menjadi debu dan tulang, apakah kami pasti akan dibankitkan </span><span>lagi ?</span><span> </span></p><p><span>[b]</span><span> 13:6 ; 27:68 ; 50:4.</span></p>');
INSERT INTO `quran` VALUES (10188,2,37,18,'<p><span>“Apakah juga bapak-bapak kami dahulu ? “</span></p>');
INSERT INTO `quran` VALUES (10189,2,37,19,'<p><span>Katakanlah, “Ya benar ! Dan kamu akan menjadi terhina.”</span></p>');
INSERT INTO `quran` VALUES (10190,2,37,20,'<p><span>Maka </span><span>saat</span><span> itu hanya </span><span>seperti</span><span> </span><span>[a]</span><span> sebuah hardikan, dan tiba-tiba mereka akan </span><span>bangkit lagi </span><span> dan mulai dapat melihat.</span></p><p><span>[a]</span><span> 79:14.</span></p>');
INSERT INTO `quran` VALUES (10191,2,37,21,'<p><span>Dan, mereka akan berkata, “Hai, celakalah kami ! Inilah Hari Pembalasan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2476">2476</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10192,2,37,22,'<p><span>Allah berfirman,</span><span> </span><span>[b]</span><span> “Inilah Hari Keputusan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2476">2476</a><span>]</span><span> yang selalu kamu mendustakannya.”</span></p><p><span>[b]</span><span> 46:35 ; 52:14, 15.</span></p>');
INSERT INTO `quran` VALUES (10193,2,37,23,'<p><span>R. 2</span></p><p><span>[c]</span><span> </span><span>Diperintahkan kepada malaikat,</span><span> “Kumpulkanlah orang-orang yang aniaya bersama kawan-kawan mereka dan apa yang biasa mereka sembah,</span></p><p><span>[c]</span><span> 6:23.</span></p>');
INSERT INTO `quran` VALUES (10194,2,37,24,'<p><span>Selain Allah ; dan dan giringlah mereka ke jalan Jahannam.</span></p>');
INSERT INTO `quran` VALUES (10195,2,37,25,'<p><span>“Dan, hentikanlah mereka, karena sesungguhnya mereka akan ditanya.”</span></p>');
INSERT INTO `quran` VALUES (10196,2,37,26,'<p><span>Mereka akan ditanyai,</span><span> “Apa yang terjadi padamu, bahwa kamu tidak tolong menolong ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2477">2477</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10197,2,37,27,'<p><span>Bahkan mereka pada hari itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2478">2478</a><span>]</span><span> akan menyerah sepenuhnya.</span></p>');
INSERT INTO `quran` VALUES (10198,2,37,28,'<p><span>Dan sebagian mereka menghadap sebagian yang lain, </span><span>[a]</span><span> saling bertanya.</span></p><p><span>[a]</span><span> 34:32.</span></p>');
INSERT INTO `quran` VALUES (10199,2,37,29,'<p><span>Mereka akan berkata, “Sesungguhnya, kamu selalu datang kepada kami dari kanan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2478a">2478a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10200,2,37,30,'<p><span>Sembahan mereka </span><span> akan berkata, </span><span>[b]</span><span> “Bahkan kamu sendiri bukan orang beriman.</span></p><p><span>[b]</span><span> 34:33.</span></p>');
INSERT INTO `quran` VALUES (10201,2,37,31,'<p><span>[c]</span><span> “Dan kami tidak mempunyai kekuasaan atas kamu ; tetapi kamu sendirilah kaum yang melampaui batas.</span></p><p><span>[c]</span><span> 14:23 ; 15:43.</span></p>');
INSERT INTO `quran` VALUES (10202,2,37,32,'<p><span>“Maka firman Tuhan kami telah terbukti benar atas kami, sesungguhnya kami pasti merasakan </span><span>azab itu.</span></p>');
INSERT INTO `quran` VALUES (10203,2,37,33,'<p><span>“Dan kami telah menyesatkan kamu, karena sesungguhnya kami orang-orang yang sesat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2479">2479</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10204,2,37,34,'<p><span>Maka sesungguhnya, mereka pada hari itu di dalam azab bersama-sama.</span></p>');
INSERT INTO `quran` VALUES (10205,2,37,35,'<p><span>Sesungguhnya, demikianlah Kami memperlakukan orang-orang berdosa.</span></p>');
INSERT INTO `quran` VALUES (10206,2,37,36,'<p><span>Sesungguhnya dahulu apabila dikatakan kepada mereka, “Tiada tuhan selan Allah,” mereka menyombongkan diri.</span></p>');
INSERT INTO `quran` VALUES (10207,2,37,37,'<p><span>Dan mereka berkata, “Apakah sesungguhnya kami harus meninggalkan tuhan-tuhan kami bagi seorang </span><span>[a]</span><span> penyair gila ?”</span></p><p><span>[a]</span><span> 15:7 ; 44:15 ; 68:52.</span></p>');
INSERT INTO `quran` VALUES (10208,2,37,38,'<p><span>Tidak demikian,</span><span> bahkan ia telah datang dengan kebenaran dan telah membenarkan semua rasul.</span></p>');
INSERT INTO `quran` VALUES (10209,2,37,39,'<p><span>Sesungguhnya, kamu pasti akan merasakan azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (10210,2,37,40,'<p><span>[b]</span><span> Dan kamu tidak akan diberi balasan, kecuali apa yang telah kamu kerjakan,</span></p><p><span>[b]</span><span> 36:55 ; 45:29.</span></p>');
INSERT INTO `quran` VALUES (10211,2,37,41,'<p><span>Kecuali hamba-hamba Allah yang terpilih.</span></p>');
INSERT INTO `quran` VALUES (10212,2,37,42,'<p><span>Merekalah yang akan memperoleh rezeki yang telah ditentukan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2480">2480</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10213,2,37,43,'<p><span>[c]</span><span> Buah-buahan </span><span>‘</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2481">2481</a><span>]</span><span> dan mereka akan dimuliakan,</span></p><p><span>[c]</span><span> 52:23 ; 55:53 ; 56:21.</span></p>');
INSERT INTO `quran` VALUES (10214,2,37,44,'<p><span>[a]</span><span> Dalam kebun-kebun yang penuh nikmat,</span></p><p><span>[a]</span><span> 44:53 ; 68:35 ; 78:32.</span></p>');
INSERT INTO `quran` VALUES (10215,2,37,45,'<p><span>[b]</span><span> </span><span>Duduk</span><span> di atas singgasana, berhadap-hadapan,</span></p><p><span>[b]</span><span> 56:16, 17.</span></p>');
INSERT INTO `quran` VALUES (10216,2,37,46,'<p><span>[c]</span><span> Akan disuguhkan kepada mereka dengan cawan-cawan dari mata air yang mengalir.</span></p><p><span>[c]</span><span> 56:18, 19.</span></p>');
INSERT INTO `quran` VALUES (10217,2,37,47,'<p><span>Putih bersih, dan lezat bagi orang-orang yang minum.</span></p>');
INSERT INTO `quran` VALUES (10218,2,37,48,'<p><span>[d]</span><span> Tidak ada di dalamnya yang memabukkan, dan tidak pula mereka karenanya kehilangan akal.</span></p><p><span>[d]</span><span> 56:20.</span></p>');
INSERT INTO `quran` VALUES (10219,2,37,49,'<p><span>[e]</span><span> Dan beserta mereka ada wanita-wanita yang merundukkan pandangannya bermata jelita, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2482">2482</a><span>]</span><span> </span></p><p><span>[e]</span><span> 55:57.</span></p>');
INSERT INTO `quran` VALUES (10220,2,37,50,'<p><span>[f]</span><span> Bagaikan telur-telur terlindung.</span></p><p><span>[f]</span><span> 55:59.</span></p>');
INSERT INTO `quran` VALUES (10221,2,37,51,'<p><span>Kemudian sebagian mereka akan menghadap sebagian yang lain, saling bertanya.</span></p>');
INSERT INTO `quran` VALUES (10222,2,37,52,'<p><span>Berkatalah seorang pembicara dari antara mereka, “Sesungguhnya, aku pernah mempunyai seorang sahabat.</span></p>');
INSERT INTO `quran` VALUES (10223,2,37,53,'<p><span>“Ia selalu berkata, </span><span>‘</span><span>Apakah engkau sesungguhnya dari antara orang-orang yang membenarkan </span><span>Hari Kebangkitan </span><span>?</span></p>');
INSERT INTO `quran` VALUES (10224,2,37,54,'<p><span>[a]</span><span> “Apakah bila kami mati dan telah menjadi debu dan tulang, apakah sungguh kami memperoleh pembalasan ? “</span></p><p><span>[a] </span><span>13:6 ; 50:4 ; 56:48.</span></p>');
INSERT INTO `quran` VALUES (10225,2,37,55,'<p><span>Ia berkata, “Apakah kamu mau menengok </span><span>mengenai dia ?</span><span> “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2483">2483</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10226,2,37,56,'<p><span>Kemudian dia menengok maka dia melihatnya ada di tengah-tengah Jahannam.</span></p>');
INSERT INTO `quran` VALUES (10227,2,37,57,'<p><span>Dia berkata, “Demi Allah ! Hampir-hampir engkau menyebabkan kebinasaanku.</span></p>');
INSERT INTO `quran` VALUES (10228,2,37,58,'<p><span>“Dan seandainya bukan karena karunia Tuhan-ku, tentulah aku termasuk orang yang diseret </span><span>ke neraka.</span></p>');
INSERT INTO `quran` VALUES (10229,2,37,59,'<p><span>“Maka apakah kami tidak akan mati </span><span>lagi ?</span><span> “</span></p>');
INSERT INTO `quran` VALUES (10230,2,37,60,'<p><span>[a]</span><span> “Kecuali kematian kami yang pertama, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2484">2484</a><span>]</span><span> dan kami tidak akan diazab ?</span></p><p><span>[a]</span><span> 23:38 ; 44:36.</span></p>');
INSERT INTO `quran` VALUES (10231,2,37,61,'<p><span>[b]</span><span> “Sesungguhnya, ini adalah kemenangan yang besar.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2485">2485</a><span>]</span></p><p><span>[b]</span><span> 44:58 ; 61:13.</span></p>');
INSERT INTO `quran` VALUES (10232,2,37,62,'<p><span>Untuk </span><span>mencapai </span><span> seperti ini, maka orang-orang beramal hendaknya beramal.</span></p>');
INSERT INTO `quran` VALUES (10233,2,37,63,'<p><span>Apakah ini lebih baik sebagai jamuan, ataukah </span><span>[c]</span><span> pohon zaqqum ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2486">2486</a><span>]</span></p><p><span>[c]</span><span> 44:44 ; 56:53.</span></p>');
INSERT INTO `quran` VALUES (10234,2,37,64,'<p><span>Sesungguhnya, Kami menjadikannya suatu cobaan bagi orang-orang yang aniaya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2487">2487</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10235,2,37,65,'<p><span>Sesungguhnya pohon itu tumbuh di dasar neraka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2488">2488</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10236,2,37,66,'<p><span>Buahnya seakan-akan seperti kepala ular.</span></p>');
INSERT INTO `quran` VALUES (10237,2,37,67,'<p><span>[a]</span><span> Maka, sesungguhnya mereka pasti akan makan dari </span><span>pohon</span><span> itu dan mereka akan mengisi perut dengan itu.</span></p><p><span>[a]</span><span> 56:54.</span></p>');
INSERT INTO `quran` VALUES (10238,2,37,68,'<p><span>Kemudian, sesungguhnya bagi mereka </span><span>selain dari itu </span><span> di atasnya diberikan campuran air mendidih.</span></p>');
INSERT INTO `quran` VALUES (10239,2,37,69,'<p><span>Kemudian, sesungguhnya tempat kembali mereka pasti ke neraka Jahim.</span></p>');
INSERT INTO `quran` VALUES (10240,2,37,70,'<p><span>[b]</span><span> Sesungguhnya mereka dapati bapak-bapak mereka sesat,</span></p><p><span>[b]</span><span> 7:174.</span></p>');
INSERT INTO `quran` VALUES (10241,2,37,71,'<p><span>[c]</span><span> Lalu mereka bergegas mengikuti jejak mereka itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2489">2489</a><span>]</span></p><p><span>[c]</span><span> 43:24.</span></p>');
INSERT INTO `quran` VALUES (10242,2,37,72,'<p><span>Dan sesungguhnya telah sesat sebelum mereka banyak sekali kaum terdahulu.</span></p>');
INSERT INTO `quran` VALUES (10243,2,37,73,'<p><span>Dan Kami telah mengutus di antara mereka Pemberi-pemberi ingat.</span></p>');
INSERT INTO `quran` VALUES (10244,2,37,74,'<p><span>Maka, lihatlah bagaimana akibat orang-orang yang telah diberi peringatan.</span></p>');
INSERT INTO `quran` VALUES (10245,2,37,75,'<p><span>Kecuali hamba-hamba Allah yang terpilih.</span></p>');
INSERT INTO `quran` VALUES (10246,2,37,76,'<p><span>R. 3</span></p><p><span>Dan, sesungguhnya Nuh telah berseru kepada Kami, maka betapa baiknya Kami selaku Pengabul </span><span>Doa</span><span>. </span></p>');
INSERT INTO `quran` VALUES (10247,2,37,77,'<p><span>[a]</span><span> Dan, Kami menyelamatkan dan keluarganya dari bencana besar.</span></p><p><span>[a]</span><span> 21:77 ; 26:120 ; 54:14.</span></p>');
INSERT INTO `quran` VALUES (10248,2,37,78,'<p><span>Dan, Kami menjadikan keturunannya, mereka yang tinggal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2490">2490</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10249,2,37,79,'<p><span>Dan, Kami tinggalkan </span><span>nama baik </span><span> atasnya pada generasi seterusnya.</span></p>');
INSERT INTO `quran` VALUES (10250,2,37,80,'<p><span>Sejahterahlah atas Nuh di semesta alam.</span></p>');
INSERT INTO `quran` VALUES (10251,2,37,81,'<p><span>Sungguh demikianlah Kami membalas orang-orang yang berbuat kebaikan.</span></p>');
INSERT INTO `quran` VALUES (10252,2,37,82,'<p><span>Sesungguhnya, ia termasuk di antara hamba-hamba Kami yang beriman.</span></p>');
INSERT INTO `quran` VALUES (10253,2,37,83,'<p><span>Kemudian Kami menenggelamkan yang lain.</span></p>');
INSERT INTO `quran` VALUES (10254,2,37,84,'<p><span>Dan sesungguhnya Ibrahim adalah dari golongannya.</span></p>');
INSERT INTO `quran` VALUES (10255,2,37,85,'<p><span>[b]</span><span> Ketika ia datang menghadap Tuhan-nya dengan hati yang suci.</span></p><p><span>[b]</span><span> 26:90.</span></p>');
INSERT INTO `quran` VALUES (10256,2,37,86,'<p><span>[a]</span><span> Tatkala ia berkata kepada ayahnya dan kaumnya, “Apakah yang kamu sembah ?</span></p><p><span>[a]</span><span> 19:43 ; 26:71.</span></p>');
INSERT INTO `quran` VALUES (10257,2,37,87,'<p><span>“Apakah dengan kebohongan sembahan-sembahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2491">2491</a><span>]</span><span> selain Allah kalian kehendaki ?</span></p>');
INSERT INTO `quran` VALUES (10258,2,37,88,'<p><span>“Maka, bagaimanakah pendapatmu tentang Tuhan sekalian alam ? “</span></p>');
INSERT INTO `quran` VALUES (10259,2,37,89,'<p><span>[b]</span><span> Kemudian ia melayangkan pandangan ke bintang-bintang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2492">2492</a><span>]</span></p><p><span>[b]</span><span> 6:77.</span></p>');
INSERT INTO `quran` VALUES (10260,2,37,90,'<p><span>Dan berkata, “Sesungguhnya aku merasa sakit.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2493">2493</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10261,2,37,91,'<p><span>Maka mereka berpaling darinya dengan meninggalkan</span><span>nya.</span></p>');
INSERT INTO `quran` VALUES (10262,2,37,92,'<p><span>Maka ia pergi dengan diam-diam kepada berhala-berhala mereka lalu ia berkata, “Apakah kamu tidak makan ?</span></p>');
INSERT INTO `quran` VALUES (10263,2,37,93,'<p><span>“Gerangan apa yang terjadi atasmu hingga kamu tidak bicara ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2494">2494</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10264,2,37,94,'<p><span>[a]</span><span> Lalu secara diam-diam ia memukul mereka dengan tangan kanan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2495">2495</a><span>]</span><span> </span></p><p><span>[a]</span><span> 21:59.</span></p>');
INSERT INTO `quran` VALUES (10265,2,37,95,'<p><span>Setelah itu mereka datang kepadanya dengan tergesa-gesa.</span></p>');
INSERT INTO `quran` VALUES (10266,2,37,96,'<p><span>Ia, </span><span>Ibrahim</span><span> berkata, </span><span>[b]</span><span> “Apakah kamu menyembah apa yang telah kamu pahat ?</span></p><p><span>[b]</span><span> 21:67, 68.</span></p>');
INSERT INTO `quran` VALUES (10267,2,37,97,'<p><span>“Padahal Allah menciptakan kamu dan apa yang kamu perbuat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2495a">2495a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10268,2,37,98,'<p><span>Mereka berkata, “Buatlah untuk dia suatu bangunan, kemudian </span><span>[c]</span><span> lemparkanlah dia ke dalam api.”</span></p><p><span>[c]</span><span> 21:69 ; 29:25.</span></p>');
INSERT INTO `quran` VALUES (10269,2,37,99,'<p><span>[a]</span><span> Maka mereka merencanakan tipu daya terhadapnya, lalu Kami jadikan mereka orang-orang yang paling hina. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2496">2496</a><span>]</span></p><p><span>[a]</span><span> 21:71.</span></p>');
INSERT INTO `quran` VALUES (10270,2,37,100,'<p><span>[b]</span><span> Dan ia berkata, “Sesungguhnya aku hendak pergi kepada Tuhan-ku, tentulak Dia akan memberiku petunjuk.”</span></p><p><span>[b]</span><span> 19:49 ; 29:27.</span></p>');
INSERT INTO `quran` VALUES (10271,2,37,101,'<p><span>Ia mendoa,</span><span> “Hai Tuhan-ku, anugerahkanlah kepadaku anak-anak yang shaleh.”</span></p>');
INSERT INTO `quran` VALUES (10272,2,37,102,'<p><span>Maka Kami memberikan khabar suka kepadanya tentang seorang anak lelaki yang lembut hati-nya.</span></p>');
INSERT INTO `quran` VALUES (10273,2,37,103,'<p><span>Dan ketika anak itu telah berusia cukup untuk dapat berlari-lari bersama dia, berkatalah ia, “Hai anakku, sesungguhnya aku melihat dalam mimpi bahwa aku melihat dalam mimpi bahwa aku menyembelihmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2497">2497</a><span>]</span><span> </span><span>sebagai kurban.</span><span> Maka pikirkanlah apa pendapatmu ? “ Ia berkata, Hai, bapakku, kerjakanlah apa yang telah diperintahkan kepada engkau ; insya Allah engkau akan mendapatiku, di antara orang-orang yang sabar.”</span></p>');
INSERT INTO `quran` VALUES (10274,2,37,104,'<p><span>Dan, ketika keduanya telah rela berserah diri dan ia, </span><span>Ibrahim,</span><span> telah menelungkupkan </span><span>anak</span><span>-nya pada dahinya.</span></p>');
INSERT INTO `quran` VALUES (10275,2,37,105,'<p><span>Maka Kami berseru kepadanya, “Hai Ibrahim,</span></p>');
INSERT INTO `quran` VALUES (10276,2,37,106,'<p><span>“Sungguh engkau telah menyempurnakan mimpi itu.” Sesungguhnya demikianlah Kami memberi ganjaran orang-orang yang berbuat kebaikan.</span></p>');
INSERT INTO `quran` VALUES (10277,2,37,107,'<p><span>Sesungguhnya ini adalah suatu ujian yang nyata.</span></p>');
INSERT INTO `quran` VALUES (10278,2,37,108,'<p><span>Dan, Kami telah menebus dia, </span><span>Ismail</span><span> dengan pengurbankan yang besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2498">2498</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (10279,2,37,109,'<p><span>Dan, Kami meninggalkan </span><span>nama baik </span><span>baginya, </span><span>Ibrahim,</span><span> di antara umat-umat yang akan datang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2499">2499</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10280,2,37,110,'<p><span>Sejahteralah atas Ibrahim !</span></p>');
INSERT INTO `quran` VALUES (10281,2,37,111,'<p><span>Demikianlah Kami mengganjar orang-orang yang berbuat baik.</span></p>');
INSERT INTO `quran` VALUES (10282,2,37,112,'<p><span>Sesungguhnya ia dari antara hamba-hamba Kami yang beriman.</span></p>');
INSERT INTO `quran` VALUES (10283,2,37,113,'<p><span>[a]</span><span> Dan, Kami telah memberi khabar suka kepadanya tentang Ishak, seorang Nabi dari antara orang-orang shaleh.</span></p><p><span>[a]</span><span> 11:72 ; 19:50 ; 21:73 ; 29:28.</span></p>');
INSERT INTO `quran` VALUES (10284,2,37,114,'<p><span>Dan, Kami melimpahkan berkat atasnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2500">2500</a><span>]</span><span> dan </span><span>juga</span><span> kepada Ishak. </span><span>[b]</span><span> Dan di antara keturunan keduanya ada yang berbuat baik dan ada pula yang nyata-nyata aniaya terhadap dirinya sendiri.</span></p><p><span>[b]</span><span> 57:27.</span></p>');
INSERT INTO `quran` VALUES (10285,2,37,115,'<p><span>R. 4</span></p><p><span>[c]</span><span> Dan, sesungguhnya Kami telah memberikan anugerah kepada Musa dan Harun.</span></p><p><span>[c]</span><span> 20:31 ; 28:35.</span></p>');
INSERT INTO `quran` VALUES (10286,2,37,116,'<p><span>[d]</span><span> Dan, Kami menyelamatkan mereka berdua dan kaumnya dari bencana yang besar.</span></p><p><span>[d]</span><span> 20:81 ; 26:66</span></p>');
INSERT INTO `quran` VALUES (10287,2,37,117,'<p><span>Dan, Kami menolong mereka, dan mereka itulah yang menang.</span></p>');
INSERT INTO `quran` VALUES (10288,2,37,118,'<p><span>Dan, Kami memberikan kepada mereka berdua Kitab yang menjadikan </span><span>segala sesuatu</span><span> jelas.</span></p>');
INSERT INTO `quran` VALUES (10289,2,37,119,'<p><span>Dan, Kami memberi mereka berdua petunjuk kepada jalan lurus.</span></p>');
INSERT INTO `quran` VALUES (10290,2,37,120,'<p><span>Dan Kami meninggalkan nama </span><span>baik</span><span> bagi mereka berdua di antara umat-umat yang akan datang</span></p>');
INSERT INTO `quran` VALUES (10291,2,37,121,'<p><span>Sejahtera atas Musa dan Harun !</span></p>');
INSERT INTO `quran` VALUES (10292,2,37,122,'<p><span>Sesungguhnya, demikian-lah Kami mengganjar orang-orang yang berbuat baik.</span></p>');
INSERT INTO `quran` VALUES (10293,2,37,123,'<p><span>Sesungguhnya, keduanya dari hamba-hamba Kami yang beriman.</span></p>');
INSERT INTO `quran` VALUES (10294,2,37,124,'<p><span>Dan, Sesungguhnya, Ilyas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2501">2501</a><span>]</span><span> adalah seorang dari para rasul.</span></p>');
INSERT INTO `quran` VALUES (10295,2,37,125,'<p><span>Ketika ia berkata kepada kaumnya, “Mengapa kamu tidak bertakwa ?”</span></p>');
INSERT INTO `quran` VALUES (10296,2,37,126,'<p><span>“Apakah kamu menyeru Ba</span><span>’</span><span>al, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2502">2502</a><span>]</span><span> dan kamu tinggalkan sebaik-baik Pencipta ? </span></p>');
INSERT INTO `quran` VALUES (10297,2,37,127,'<p><span>“</span><span>Yakni</span><span> Allah, Tuhan kamu dan Tuhan bapak-bapak kamu dahulu ? “</span></p>');
INSERT INTO `quran` VALUES (10298,2,37,128,'<p><span>Maka mereka mendustakannya, karena itu pasti mereka diseret </span><span>ke neraka.</span></p>');
INSERT INTO `quran` VALUES (10299,2,37,129,'<p><span>Kecuali hamba-hamba Allah yang terpilih.</span></p>');
INSERT INTO `quran` VALUES (10300,2,37,130,'<p><span>Dan Kami meninggalkan </span><span>nama baik</span><span> baginya di antara umat-umat yang akan datang.</span></p>');
INSERT INTO `quran` VALUES (10301,2,37,131,'<p><span>Sejahtera atas Ilyas dan kaumnya ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2503">2503</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10302,2,37,132,'<p><span>Sesungguhnya, demikianlah Kami mengganjar orang-orang yang berbuat baik.</span></p>');
INSERT INTO `quran` VALUES (10303,2,37,133,'<p><span>Sesungguhnya, dia dari antara hamba-hamba Kami yang beriman.</span></p>');
INSERT INTO `quran` VALUES (10304,2,37,134,'<p><span>[a]</span><span> Dan sesungguhnya Luth adalah seorang dari para rasul.</span></p><p><span>[a]</span><span> 7:81 ; 26:161 ; 29:29.</span></p>');
INSERT INTO `quran` VALUES (10305,2,37,135,'<p><span>[b]</span><span> Ketika Kami menyelamatkan dia dan seluruh keluarganya.</span></p><p><span>[b]</span><span> 26:171 ; 29:33 ; 51:36.</span></p>');
INSERT INTO `quran` VALUES (10306,2,37,136,'<p><span>[c]</span><span> Kecuali seorang wanita tua yang berada di antara orang-orang yang tinggal di belakang.</span></p><p><span>[c]</span><span> 7:84 ; 11:82 ; 15:61 ; 27:58.</span></p>');
INSERT INTO `quran` VALUES (10307,2,37,137,'<p><span>[d]</span><span> Kemudian Kami binasakan orang-orang lain.</span></p><p><span>[d]</span><span> 26:173.</span></p>');
INSERT INTO `quran` VALUES (10308,2,37,138,'<p><span>[e]</span><span> Dan sesungguhnya, kamu melewati mereka pada pagi hari,</span></p><p><span>[e]</span><span> 15:77.</span></p>');
INSERT INTO `quran` VALUES (10309,2,37,139,'<p><span>Dan pada malam hari, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2504">2504</a><span>]</span><span> Apakah kamu tidak menggunakan akal ?</span></p>');
INSERT INTO `quran` VALUES (10310,2,37,140,'<p><span>[a]</span><span> Dan, sesungguhnya, Yunus </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2505">2505</a><span>]</span><span> adalah seorang rasul-rasul.</span></p><p><span>[a]</span><span> 21:88 ; 68:49.</span></p>');
INSERT INTO `quran` VALUES (10311,2,37,141,'<p><span>Ketika ia lari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2506">2506</a><span>]</span><span> ke kapal yang penuh muatan.</span></p>');
INSERT INTO `quran` VALUES (10312,2,37,142,'<p><span>Kemudian ia ikut berundi </span><span>dengan orang-orang lain,</span><span> lalu ia termasuk orang-orang yang dilempar </span><span>ke laut.</span></p>');
INSERT INTO `quran` VALUES (10313,2,37,143,'<p><span>Maka seekor ikan menelannya ketika ia sedang menyesali diri.</span></p>');
INSERT INTO `quran` VALUES (10314,2,37,144,'<p><span>Maka jika ia bukan di antara orang-orang yang mensucikan </span><span>Tuhan,</span></p>');
INSERT INTO `quran` VALUES (10315,2,37,145,'<p><span>Niscaya ia akan tetap tinggal di dalam perut ikan itu hingga hari kebangkitan, </span><span>mati.</span></p>');
INSERT INTO `quran` VALUES (10316,2,37,146,'<p><span>Kemudian Kami melemparkannya ke tanah kosong, sedang ia sedang sakit.</span></p>');
INSERT INTO `quran` VALUES (10317,2,37,147,'<p><span>Dan Kami tumbuhkan atas tanah itu sebatang pohon dari pohon labu.</span></p>');
INSERT INTO `quran` VALUES (10318,2,37,148,'<p><span>Dan Kami utus dia kepada seratus ribu orang atau lebih.</span></p>');
INSERT INTO `quran` VALUES (10319,2,37,149,'<p><span>[a]</span><span> Maka mereka beriman ; karena itu Kami memberikan kepada mereka kesejahteraan hidup hingga waktu lama.</span></p><p><span>[a]</span><span> 10:99.</span></p>');
INSERT INTO `quran` VALUES (10320,2,37,150,'<p><span>[b]</span><span> Sekarang tanyailah mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2506a">2506a</a><span>]</span><span> “Apakah Tuhan-mu mempunyai anak perempuan, dan untuk mereka anak laki-laki ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2507">2507</a><span>]</span></p><p><span>[b]</span><span> 6:101 ; 16:58 ; 43:17 ; 52:40 ; 53:22.</span></p>');
INSERT INTO `quran` VALUES (10321,2,37,151,'<p><span>[c]</span><span> Apakah Kami menciptakan malaikat-malaikat itu perempuan, dan mereka menyaksikan</span><span>nya ?</span></p><p><span>[c]</span><span> 17:41 ; 37:151 ; 43:20.</span></p>');
INSERT INTO `quran` VALUES (10322,2,37,152,'<p><span>Ketahuilah, bahwa sesungguhnya itu dari kebohongan mereka dan sungguh mereka berkata,</span></p>');
INSERT INTO `quran` VALUES (10323,2,37,153,'<p><span>Bahwa</span><span> “Allah beranak ;” dan sesungguhnya mereka itu benar-benar pendusta.</span></p>');
INSERT INTO `quran` VALUES (10324,2,37,154,'<p><span>[d]</span><span> Apakah Dia telah memilih anak-anak perempuan dari anak-anak laki-laki ?</span></p><p><span>[d]</span><span> 43:17 ; 53:22.</span></p>');
INSERT INTO `quran` VALUES (10325,2,37,155,'<p><span>Apakah yang terjadi atas dirimu ? Bagaimanakah kamu mengambil keputusan ? </span></p>');
INSERT INTO `quran` VALUES (10326,2,37,156,'<p><span>Apakah kamu tidak mengerti ? </span></p>');
INSERT INTO `quran` VALUES (10327,2,37,157,'<p><span>[a]</span><span> Atau, apakah padamu ada suatu bukti yang nyata ?</span></p><p><span>[a]</span><span> 52:39.</span></p>');
INSERT INTO `quran` VALUES (10328,2,37,158,'<p><span>Maka kemukakanlah Kitabmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2508">2508</a><span>]</span><span> jika kamu memang orang-orang benar.</span></p>');
INSERT INTO `quran` VALUES (10329,2,37,159,'<p><span>[b]</span><span> Dan mereka mengada-ada hubungan keluarga diantara Dia dan kaum jin, sedangkan kaum jin itu mengetahui bahwa mereka pasti akan dihadapkan </span><span>kepada azab.</span></p><p><span>[b]</span><span> 6:101.</span></p>');
INSERT INTO `quran` VALUES (10330,2,37,160,'<p><span>Maha Suci Allah dari segala apa yang mereka sifatkan.</span></p>');
INSERT INTO `quran` VALUES (10331,2,37,161,'<p><span>Kecuali hamba-hamba Allah yang terpilih.</span></p>');
INSERT INTO `quran` VALUES (10332,2,37,162,'<p><span>Sesungguhnya, kamu dan apa yang kamu sembah itu, </span></p>');
INSERT INTO `quran` VALUES (10333,2,37,163,'<p><span>Tidak dapat kamu menyesatkan </span><span>seorang</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2509">2509</a><span>]</span><span> menentang Dia,</span></p>');
INSERT INTO `quran` VALUES (10334,2,37,164,'<p><span>Kecuali orang yang masuk ke neraka Jahim.</span></p>');
INSERT INTO `quran` VALUES (10335,2,37,165,'<p><span>Dan tiada di antara kami, kecuali baginya tempat yang ditentukan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2510">2510</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10336,2,37,166,'<p><span>“Dan sesungguhnya kami niscaya berdiri bershaf-shaf </span><span>di hadapan Tuhan.</span></p>');
INSERT INTO `quran` VALUES (10337,2,37,167,'<p><span>[a]</span><span> “Dan sesungguhnya kami adalah orang-orang yang bertasbih.”</span></p><p><span>[a]</span><span> 2:31 ; 21:21 ; 41:39.</span></p>');
INSERT INTO `quran` VALUES (10338,2,37,168,'<p><span>Dan sesungguhnya mereka, </span><span>orang-orang kafir Mekkah,</span><span> selalu berkata,</span></p>');
INSERT INTO `quran` VALUES (10339,2,37,169,'<p><span>“Sekiranya ada pada kami Pemberi ingat seperti </span><span>telah datang </span><span> kepada orang-orang yang terdahulu,</span></p>');
INSERT INTO `quran` VALUES (10340,2,37,170,'<p><span>“Tentulah kami menjadi hamba-hamba Allah yang terpilih,”</span></p>');
INSERT INTO `quran` VALUES (10341,2,37,171,'<p><span>Maka mereka ingkar kepada-Nya, tetapi mereka akan segera mengetahui.</span></p>');
INSERT INTO `quran` VALUES (10342,2,37,172,'<p><span>Dan sesungguhnya janji Kami telah disampaikan kepada hamba-hamba Kami yang telah diutus.</span></p>');
INSERT INTO `quran` VALUES (10343,2,37,173,'<p><span>[b]</span><span> Sesungguhnya mereka itulah yang akan diberi pertolongan.</span></p><p><span>[b]</span><span> 40:52 ; 58:22.</span></p>');
INSERT INTO `quran` VALUES (10344,2,37,174,'<p><span>Dan sesungguhnya lasykar Kami itulah yang akan menang.</span></p>');
INSERT INTO `quran` VALUES (10345,2,37,175,'<p><span>Maka berpalinglah engkau dari mereka itu untuk sementara waktu.</span></p>');
INSERT INTO `quran` VALUES (10346,2,37,176,'<p><span>Dan amatilah mereka, karena mereka segera akan melihat.</span></p>');
INSERT INTO `quran` VALUES (10347,2,37,177,'<p><span>[a]</span><span> Apakah mereka cepat-cepat meminta azab Kami datang ?</span></p><p><span>[a]</span><span> 22:48 ; 27:72 ; 29:54.</span></p>');
INSERT INTO `quran` VALUES (10348,2,37,178,'<p><span>Tetapi, apabila </span><span>azab itu</span><span> turun ke halaman mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2511">2511</a><span>]</span><span> maka sangat buruklah pagi itu bagi orang-orang yang diperingatkan.</span></p>');
INSERT INTO `quran` VALUES (10349,2,37,179,'<p><span>Maka berpalinglah engkau dari mereka itu untuk sementara waktu.</span></p>');
INSERT INTO `quran` VALUES (10350,2,37,180,'<p><span>Dan amatilah, karena mereka akan segera melihat.</span></p>');
INSERT INTO `quran` VALUES (10351,2,37,181,'<p><span>Maha Suci Tuhan engkau, Tuhan Yang Memiliki Segala Kebesaran dari apa yang mereka lukiskan.</span></p>');
INSERT INTO `quran` VALUES (10352,2,37,182,'<p><span>[b]</span><span> Dan sejahteralah atas para rasul ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2512">2512</a><span>]</span><span> </span></p><p><span>[b] </span><span>27:60.</span></p>');
INSERT INTO `quran` VALUES (10353,2,37,183,'<p><span>[c]</span><span> Dan segala puji bagi Allah, Tuhan semesta alam.</span></p><p><span>[c]</span><span> 1:2 ; 6:46.</span></p>');
INSERT INTO `quran` VALUES (10354,2,38,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10355,2,38,2,'<p><span>Allah Maha Benar </span><span>yang menurunkan Alquran,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2513">2513</a><span>]</span><span> demi </span><span>[b]</span><span> Alquran yang penuh nasihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2514">2514</a><span>]</span></p><p><span>[b]</span><span> 43:45.</span></p>');
INSERT INTO `quran` VALUES (10356,2,38,3,'<p><span>Tetapi, orang-orang yang ingkar berada dalam kesombongan dan permusuhan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2515">2515</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10357,2,38,4,'<p><span>[c]</span><span> Berapa banyaknya kaum sebelum mereka telah Kami binasakan ! Lalu mereka berseru </span><span>meminta pertolongan,</span><span> tetapi tidak ada waktu lagi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2516">2516</a><span>] </span><span>untuk melepaskan diri.</span></p><p><span>[c]</span><span> 6:7 ; 19:75 ; 36:32 ; 50:37.</span></p>');
INSERT INTO `quran` VALUES (10358,2,38,5,'<p><span>[a]</span><span> Dan, mereka takjub bahwa datang kepada mereka seorang pemberi ingat dari antara mereka ; dan berkata orang-orang kafir, “Ini seorang sihir </span><span>dan</span><span> seorang pendusta besar.</span></p><p><span>[a]</span><span> 7:64.</span></p>');
INSERT INTO `quran` VALUES (10359,2,38,6,'<p><span>“Apakah ia telah menjadikan tuhan-tuhan itu satu Tuhan ? Ini sungguh suatu hal yang sangat ajaib.”</span></p>');
INSERT INTO `quran` VALUES (10360,2,38,7,'<p><span>Dan berjalan para pemimpin dari mereka </span><span>sambil berkata,</span><span> “Pergilah dan </span><span>[b]</span><span> berpegang teguhlah kepada tuhan-tuhanmu. Sesungguhnya ini adalah sesuatu yang dikehendaki ;</span></p><p><span>[b]</span><span> 71:24.</span></p>');
INSERT INTO `quran` VALUES (10361,2,38,8,'<p><span>[c]</span><span> “Kami tidak pernah mendengar hal ini dalam agama terdahulu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2517">2517</a><span>]</span><span> Ini tidak lain melainkan penipuan belaka ; </span></p><p><span>[c]</span><span> 23:25.</span></p>');
INSERT INTO `quran` VALUES (10362,2,38,9,'<p><span>[d]</span><span> “Apakah telah diturunkan kepadanya Alquran di antara kita ? “ Tidak, bahkan mereka ada dalam keraguan tentang Alquran-Ku. Bahkan mereka belum merasakan azab-Ku.</span></p><p><span>[d]</span><span> 54:26.</span></p>');
INSERT INTO `quran` VALUES (10363,2,38,10,'<p><span>[a]</span><span> Apakah mereka itu memiliki khazanah-khazanah rahmat Tuhan engkau Yang Maha Perkasa, Maha Pemberi karunia ?</span></p><p><span>[a]</span><span> 17:101 ; 52:38.</span></p>');
INSERT INTO `quran` VALUES (10364,2,38,11,'<p><span>Atau, apakah kepunyaan mereka kerajaan seluruh langit dan bumi dan apa yang ada di antara keduanya itu ? Maka biarlah mereka naik dengan sarana-sarana </span><span>yang ada pada mereka.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2518">2518</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10365,2,38,12,'<p><span>[b]</span><span> </span><span>Mereka itu </span><span> suatu lasykar golongan-golongan perserikatan </span><span>yang akan</span><span> dikalahkan di sini. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2519">2519</a><span>]</span></p><p><span>[b]</span><span> 54:46.</span></p>');
INSERT INTO `quran` VALUES (10366,2,38,13,'<p><span>Telah mendustakan </span><span>[c]</span><span> sebelum mereka kaum Nuh dan suku </span><span>‘</span><span>Ad dan Fir</span><span>’</span><span>aun, yang mempunyai kekuatan besar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2520">2520</a><span>]</span><span> </span></p><p><span>[c]</span><span> 9:70 ; 40:32 ; 50:13.</span></p>');
INSERT INTO `quran` VALUES (10367,2,38,14,'<p><span>Dan </span><span>suku</span><span> Tsamud dan kaum Luth dan </span><span>[d]</span><span> penghuni hutan. Mereka itu golongan perserikatan.</span></p><p><span>[d]</span><span> 15:79 ; 26:177 ; 50:15.</span></p>');
INSERT INTO `quran` VALUES (10368,2,38,15,'<p><span>Semua mereka itu tidak lain kecuali mendustakan rasul-rasul, </span><span>[a]</span><span> maka pasti azab-Ku </span><span>menimpa mereka.</span></p><p><span>[a]</span><span> 15:80 ; 26:190 ; 50:15.</span></p>');
INSERT INTO `quran` VALUES (10369,2,38,16,'<p><span>R. 2</span></p><p><span>Dan tidaklah mereka menunggu, kecuali azab yang datang tiba-tiba, dan tidak akan ada penangguhannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2521">2521</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10370,2,38,17,'<p><span>Dan mereka berkata, “Hai Tuhan kami, </span><span>[b]</span><span> segerakanlah bagi kami bagian kami sebelum Hari Perhitungan.”</span></p><p><span>[b]</span><span> 17:19.</span></p>');
INSERT INTO `quran` VALUES (10371,2,38,18,'<p><span>Bersabarlah atas apa yang mereka katakan, dan ingatlah akan hamba Kami, Daud yang mempunyai kekuatan besar ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2522">2522</a><span>]</span><span> sesungguhnya ia senantiasa kembali </span><span>kepada Tuhan.</span></p>');
INSERT INTO `quran` VALUES (10372,2,38,19,'<p><span>[c]</span><span> Sesungguhnya, Kami menundukkan </span><span>orang-orang </span><span> gunung </span><span>kepadanya.</span><span> Mereka menyanjungkan puji-pujian kepada </span><span>Tuhan</span><span> bersamanya pada waktu petang dan pagi hari.</span></p><p><span>[c]</span><span> 21:80 ; 34:11.</span></p>');
INSERT INTO `quran` VALUES (10373,2,38,20,'<p><span>Dan, </span><span>Kami menundukkan kepadanya </span><span> burung-burung, </span><span>orang-orang tinggi keruhaniannya,</span><span> yang berhimpun bersama-sama ; semuanya patuh kepada </span><span>Tuhan.</span></p>');
INSERT INTO `quran` VALUES (10374,2,38,21,'<p><span>Dan, Kami meneguhkan kerajaannya, </span><span>[a]</span><span> dan Kami memberikan kepadanya kebijaksanaan dan memutuskan perkara.</span></p><p><span>[a]</span><span> 2:252</span></p>');
INSERT INTO `quran` VALUES (10375,2,38,22,'<p><span>Dan, sudahkah datang kepadamu khabar tentang orang-orang yang bermusuhan, tatkala mereka itu memanjat dinding kamar pribadi</span><span>nya </span><span> ?</span></p>');
INSERT INTO `quran` VALUES (10376,2,38,23,'<p><span>Ketika mereka masuk mendatangi Daud, maka ia takut dari mereka itu. Mereka berkata, “Janganlah takut. </span><span>Kami </span><span> dua orang sedang bersengketa ; kami berlaku aniaya terhadap satu sama lain ; maka hakimilah di antara kami dengan keadilan, dan janganlah menganiaya kami dan tunjukilah jalan lurus.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2523">2523</a><span>] </span></p>');
INSERT INTO `quran` VALUES (10377,2,38,24,'<p><span>“Sesungguhnya saudaraku ini mempunyai sembilan puluh sembilan domba betina, dan aku mempunyai seekor domba betina. Namun ia berkata, </span><span>‘</span><span>Serahkanlah itu kepadaku,</span><span>’</span><span> dan ia telah mengungguli diriku dalam pembicaraan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2524">2524</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10378,2,38,25,'<p><span>Ia, </span><span>Daud, </span><span>berkata, “Sesungguhnya ia telah berlaku aniaya terhadapmu dengan meminta domba betinamu untuk </span><span>menambahkannya kepada </span><span> domba-domba betinanya. Dan, sesungguhnya banyak di antara orang-orang yang bersekutu berlaku aniaya, sebagian terhadap sebagian lain, kecuali orang-orang yang beriman dan beramal shaleh ; tetapi mereka itu hanyalah sedikit.” Dan, Daud pun menyangka bahwa Kami telah menguji dia ; maka ia memohon ampun kepada Tuhan-Nya, dan ia merebahkan diri menyatakan kepatuhan dan menghadapkan diri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2525">2525</a><span>]</span><span> </span><span>kepada-Nya.</span><span> </span></p>');
INSERT INTO `quran` VALUES (10379,2,38,26,'<p><span>Maka Kami mengampuni baginya </span><span>kelemahan </span><span> itu ; dan sesungguhnya ia mempunyai kedudukan akrab di sisi Kami dan sebaik-baik tempat kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2226">2226</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10380,2,38,27,'<p><span>“Hai Daud, sesungguhnya Kami telah menjadikan engkau khalifah di bumi ini ; maka hakimilah di antara manusia dengan adil, dan janganlah mengikuti hawa nafsu, jangan- jangan ia menyesatkan engkau dari jalan Allah.” Sesungguhnya, orang-orang yang tersesat dari jalan Allah bagi mereka ada azab yang sangat keras, disebabkan mereka telah lupa Hari Perhitungan.</span></p>');
INSERT INTO `quran` VALUES (10381,2,38,28,'<p><span>R. 3</span></p><p><span>[a]</span><span> Dan tidaklah Kami menjadikan langit dan bumi ini dan apa yang ada di antara keduanya dengan sia-sia. Hal demikian itu adalah anggapan orang-orang yang ingkar. </span><span>[b]</span><span> Maka celakalah bagi orang-orang yang ingkar dari Api.</span></p><p><span>[a]</span><span> 21:17 ; 44:39.</span></p><p><span>[b]</span><span> 14:3 ; 19:38 ; 51:61.</span></p>');
INSERT INTO `quran` VALUES (10382,2,38,29,'<p><span>[a]</span><span> Apakah Kami memperlakukan orang-orang beriman dan beramal shaleh itu sama seperti para pengacau di bumi ? Ataukah Kami memperlakukan orang-orang muttaqi itu sama seperti orang-orang berdosa ?</span></p><p><span>[a]</span><span> 68:36.</span></p>');
INSERT INTO `quran` VALUES (10383,2,38,30,'<p><span>Inilah</span><span> Kitab yang telah Kami turunkan kepada engkau </span><span>[b]</span><span> penuh dengan keberkatan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2527">2527</a><span>]</span><span> supaya mereka dapat merenungkan ayat-ayatnya, dan supaya dapat nasihat orang-orang yang berakal.</span></p><p><span>[b]</span><span> 6:93 ; 21:51.</span></p>');
INSERT INTO `quran` VALUES (10384,2,38,31,'<p><span>[c]</span><span> Dan kepada Daud, Kami anugerahkan Sulaiman, seorang hamba yang sangat baik. Sesungguhnya ia selalu kembali </span><span>kepada Kami.</span></p><p><span>[c]</span><span> 27:17.</span></p>');
INSERT INTO `quran` VALUES (10385,2,38,32,'<p><span>Ketika dihadapkan kepadanya pada waktu petang hari kuda-kuda yang terbaik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2528">2528</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10386,2,38,33,'<p><span>Maka ia berkata, “Sesungguhnya aku mencintai barang-barang indah, sebab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2529">2529</a><span>]</span><span> ingat kepada Tuhan-ku.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2530">2530</a><span>] </span><span>Sehingga </span><span>kuda-kuda</span><span> itu tersembunyi di belakang tabir.</span></p>');
INSERT INTO `quran` VALUES (10387,2,38,34,'<p><span>Berkatalah ia, </span><span>“Bawalah kembali </span><span>kuda-kuda</span><span> itu kepadaku,” Kemudian ia mulai mengusap-usap kaki dan leher </span><span>kuda-kuda </span><span> itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2531">2531</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10388,2,38,35,'<p><span>Dan sesungguhnya, Kami menguji Sulaiman dan Kami mendudukkan di atas singgasana nya jasad </span><span>belaka</span><span>. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2532">2532</a><span>]</span><span> Kemudian ia kembali kepada </span><span>Tuhan-nya.</span></p>');
INSERT INTO `quran` VALUES (10389,2,38,36,'<p><span>Ia berkata, “Hai Tuhan-ku, ampunilah aku dan anugerahkanlah kepadaku suatu kerajaan yang tidak layak </span><span>diwarisi oleh seseorang sesudahku.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2533">2533</a><span>]</span><span> Sesungguhnya, engkau adalah Maha Pemberi anugerah.”</span></p>');
INSERT INTO `quran` VALUES (10390,2,38,37,'<p><span>Maka </span><span>[a]</span><span> Kami tundukkan kepadanya angin, yang berhembus dengan baik sesuai perintahnya kemana saja yang dikehendakinya.</span></p><p><span>[a]</span><span> 21:82 ; 34:13.</span></p>');
INSERT INTO `quran` VALUES (10391,2,38,38,'<p><span>[b]</span><span> Dan syaitan-syaitan, semua ahli bangunan dan penyelam-penyelam.</span></p><p><span>[b]</span><span> 21:83 ; 34:13.</span></p>');
INSERT INTO `quran` VALUES (10392,2,38,39,'<p><span>[c]</span><span> Dan orang-orang lainnya juga, terikat dalam belenggu-belenggu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2534">2534</a><span>]</span></p><p><span>[c]</span><span> 14:50.</span></p>');
INSERT INTO `quran` VALUES (10393,2,38,40,'<p><span>“Inilah anugerah Kami, maka berikanlah sebebasnya atau tahanlah tanpa perhitungan”</span></p>');
INSERT INTO `quran` VALUES (10394,2,38,41,'<p><span>Dan, sesungguhnya ia mempunyai kedudukan akrab di sisi Kami dan sebaik-baik tempat kembali.</span></p>');
INSERT INTO `quran` VALUES (10395,2,38,42,'<p><span>R. 4</span></p><p><span>Dan, ingatlah kepada hamba Kami </span><span>[a]</span><span> Ayyub, ketika ia menyeru kepada Tuhan-nya, “Sesungguhnya syaitan telah menyusahkanku dengan kesusahan dan azab. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2535">2535</a><span>]</span></p><p><span>[a]</span><span> 21:84.</span></p>');
INSERT INTO `quran` VALUES (10396,2,38,43,'<p><span>Pukullah dengan kakimu </span><span>tungganganmu,</span><span> di sini ada tempat mandi yang sejuk dan air minum.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2536">2536</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10397,2,38,44,'<p><span>[a]</span><span> Dan Kami menganugerahkan kepadanya keluarganya dan banyak lagi lainnya beserta mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2537">2537</a><span>]</span><span> sebagai rahmat dari Kami, dan sebagai nasihat bagi orang-orang yang berakal.</span></p><p><span>[a]</span><span> 21:85.</span></p>');
INSERT INTO `quran` VALUES (10398,2,38,45,'<p><span>Dan </span><span>Kami memerintahkan kepada Ayyub,</span><span> “Ambillah dengan tanganmu ranting kering, kemudian pukullah dengan itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2538">2538</a><span>]</span><span> dan jangan cenderung kepada kepalsuan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2539">2539</a><span>]</span><span> Sungguh, Kami dapati dia berhati sabar. Seorang hamba yang sangat baik. Sesungguh nya ia senantiasa kembali </span><span>kepada Tuhan.</span></p>');
INSERT INTO `quran` VALUES (10399,2,38,46,'<p><span>Dan, ingatlah kepada hamba-hamba Kami, Ibrahim dan Ishak dan Ya</span><span>’</span><span>kub, </span><span>orang-orang </span><span> yang mempunyai kekuatan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2540">2540</a><span>]</span><span> dan berpandangan jauh.</span></p>');
INSERT INTO `quran` VALUES (10400,2,38,47,'<p><span>Kami memilih mereka untuk </span><span>tujuan</span><span> khusus, guna memperingatkan </span><span>orang-orang </span><span> tentang tempat tinggal </span><span>di akhirat.</span></p>');
INSERT INTO `quran` VALUES (10401,2,38,48,'<p><span>Dan, sesungguhnya mereka itu di sisi Kami adalah dari orang-orang terpilih, terbaik.</span></p>');
INSERT INTO `quran` VALUES (10402,2,38,49,'<p><span>[a]</span><span> Dan ingatlah kepada Ismail dan Al-Yasa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2541">2541</a><span>]</span><span> dan Dzu</span><span>’</span><span>l-Kifli ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2542">2542</a><span>]</span><span> dan mereka itu semuanya dari orang-orang yang terbaik.</span></p><p><span>[a]</span><span> 6:87 ; 21:86 </span><span>–</span><span> 87.</span></p>');
INSERT INTO `quran` VALUES (10403,2,38,50,'<p><span>Inilah suatu peringatan. Dan, sesungguhnya bagi orang-orang muttaki pasti </span><span>disediakan</span><span> sebaik</span><span>–</span><span>baik tempat kembali.</span></p>');
INSERT INTO `quran` VALUES (10404,2,38,51,'<p><span>Kebun-kebun yang kekal, yang selalu terbuka untuk mereka pintu-pintunya.</span></p>');
INSERT INTO `quran` VALUES (10405,2,38,52,'<p><span>Mereka itu </span><span> duduk di dalamnya </span><span>[a]</span><span> sambil bersandar </span><span>pada bantal-bantal ;</span><span> mereka di dalamnya meminta berbagai buah-buahan yang banyak dan minuman.</span></p><p><span>[a]</span><span> 18:32 ; 36:57 ; 83:24.</span></p>');
INSERT INTO `quran` VALUES (10406,2,38,53,'<p><span>Dan di sisi mereka akan ada </span><span>[b]</span><span> </span><span>wanita-wanita</span><span> dengan pondangan mereka tertunduk, yang sebaya umurnya.</span></p><p><span>[b]</span><span> 55:57.</span></p>');
INSERT INTO `quran` VALUES (10407,2,38,54,'<p><span>Inilah apa yang telah dijanjikan kepadamu untuk </span><span>diberikan </span><span> pada Hari Perhitungan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2543">2543</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10408,2,38,55,'<p><span>Sesungguhnya, inilah rezeki Kami yang tiada habis-habisnya.</span></p>');
INSERT INTO `quran` VALUES (10409,2,38,56,'<p><span>Inilah </span><span>untuk orang-orang beriman. </span><span>{c]</span><span> Dan, sesungguhnya untuk orang-orang durhaka ada sebuah tempat kembali yang sangat buruk.</span></p><p><span>[c]</span><span> 78:22 </span><span>–</span><span> 23.</span></p>');
INSERT INTO `quran` VALUES (10410,2,38,57,'<p><span>Jahannam, yang mereka akan masuk ke dalamnya. Maka, alangkah buruknya tempat tinggal itu !</span></p>');
INSERT INTO `quran` VALUES (10411,2,38,58,'<p><span>Inilah </span><span>yang mereka akan peroleh.</span><span> Maka, biarlah mereka merasakannya, </span><span>[d]</span><span> cairan mendidih, dan minuman sangat dingin yang berbau busuk. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2544">2544</a><span>]</span></p><p><span>[d]</span><span> 78:26.</span></p>');
INSERT INTO `quran` VALUES (10412,2,38,59,'<p><span>Dan akan ada serupa dengan itu golongan lain </span><span>yang sama amalnya.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2545">2545</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10413,2,38,60,'<p><span>Inilah suatu pasukan yang </span><span>[a]</span><span> masuk berdesakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2546">2546</a><span>]</span><span> besertamu. Tiada sambutan selamat datang bagi mereka. Sesungguhnya mereka masuk ke dalam Api.</span></p><p><span>[a]</span><span> 52:14.</span></p>');
INSERT INTO `quran` VALUES (10414,2,38,61,'<p><span>Berkata mereka, “Tidak ! Bahkan kamulah yang demikian. Tiada sambutan selamat datang bagimu </span><span>juga.</span><span> Kamulah yang telah menyiapkannya bagi kami </span><span>dengan membawa kami sesat.</span><span>” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2547">2547</a><span>]</span><span> Maka, alangkah buruknya tempat tinggal itu ! </span></p>');
INSERT INTO `quran` VALUES (10415,2,38,62,'<p><span>Mereka berkata, “Hai Tuhan kami, barangsiapa yang telah menyediakan ini bagi kami, maka tambahkanlah kepadanya azab yang </span><span>[a]</span><span> berlipat-ganda dalam Api.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2548">2548</a><span>]</span></p><p><span>[a]</span><span> 7:39.</span></p>');
INSERT INTO `quran` VALUES (10416,2,38,63,'<p><span>Dan </span><span>ahli neraka</span><span> berkata, “Gerangan apa yang terjadi dengan kami sehingga kami tidak melihat orang-orang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2549">2549</a><span>]</span><span> yang biasa kami kira dari orang-orang buruk ?</span></p>');
INSERT INTO `quran` VALUES (10417,2,38,64,'<p><span>“Apakah karena kami menganggap mereka hina, ataukah mata </span><span>kami</span><span> bengkok </span><span>tidak dapat melihat</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2550">2550</a><span>]</span><span> mereka ? “</span></p>');
INSERT INTO `quran` VALUES (10418,2,38,65,'<p><span>Sesungguhnya, yang demikian itu pasti terjadi, </span><span>yaitu</span><span> </span><span>[b]</span><span> pertengkeran ahli neraka.</span></p><p><span>[b]</span><span> 34:32 ; 40:48.</span></p>');
INSERT INTO `quran` VALUES (10419,2,38,66,'<p><span>R. 5</span></p><p><span>Katakanlah, “Aku hanyalah seorang pemberi peringatan ; dan tiada tuhan selain Allah Yang Maha Esa, Yang Maha Unggul.</span></p>');
INSERT INTO `quran` VALUES (10420,2,38,67,'<p><span>“Tuhan seluruh langit dan bumi, dan </span><span>segala</span><span> yang ada di antara keduanya, Yang Maha Perkasa, Yang Maha Pengampun.”</span></p>');
INSERT INTO `quran` VALUES (10421,2,38,68,'<p><span>Katakanlah, “Itu adalah berita besar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2551">2551</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (10422,2,38,69,'<p><span>“Yang darinya kamu berpaling ;</span></p>');
INSERT INTO `quran` VALUES (10423,2,38,70,'<p><span>“Aku tidak mempunyai pengetahuan apa pun tentang Dewan Malaikat Agung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2552">2552</a><span>]</span><span> ketika mereka sedang berbahas.</span></p>');
INSERT INTO `quran` VALUES (10424,2,38,71,'<p><span>“Tidak diwahyukan kepadaku, melainkan bahwa sesungguhnya aku adalah seorang pemberi ingat yang nyata.”</span></p>');
INSERT INTO `quran` VALUES (10425,2,38,72,'<p><span>[a]</span><span> </span><span>Ingatlah</span><span> ketika Tuhan-mu berkata kepada malaikat, “Sesungguhnya Aku Pencipta manusia dari tanah liat ;</span></p><p><span>[a]</span><span> 15:29 </span><span>–</span><span> 33 ; 17:62.</span></p>');
INSERT INTO `quran` VALUES (10426,2,38,73,'<p><span>[b]</span><span> “Dan tatkala Aku menyempurnakannya dan telah Aku tiupkan ke dalamnya wahyu-Ku, maka hendaklah kamu bersungkur kepadanya dengan bersujud.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2553">2553</a><span>]</span></p><p><span>[b]</span><span> 15:30 ; 32:10.</span></p>');
INSERT INTO `quran` VALUES (10427,2,38,74,'<p><span>Maka para malaikat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2554">2554</a><span>]</span><span> </span><span>pun</span><span> taat, mereka taat semuanya,</span></p>');
INSERT INTO `quran` VALUES (10428,2,38,75,'<p><span>Kecuali Iblis, Ia sombong, dan ia termasuk orang-orang kafir.</span></p>');
INSERT INTO `quran` VALUES (10429,2,38,76,'<p><span>[a]</span><span> Dia, </span><span>Allah,</span><span> berfirman, “Hai iblis, apakah yang telah melarang engkau taat kepada apa yang telah Kuciptakan dengan kedua tangan-Ku ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2555">2555</a><span>]</span><span> Apakah engkau sombong atau engkau termasuk orang-orang yang </span><span>lebih </span><span> tinggi </span><span>untuk menaati perintah-Ku ?</span><span> “</span></p><p><span>[a]</span><span> 7:13 ; 15:33.</span></p>');
INSERT INTO `quran` VALUES (10430,2,38,77,'<p><span>Ia berkata, “Aku lebih baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2556">2556</a><span>]</span><span> daripadanya. </span><span>[b]</span><span> Engkau telah menciptakan aku dari api dan dia telah Engkau ciptakan dari tanah liat,”</span></p><p><span>[b]</span><span> 7:13 ; 15:28 ; 55:16.</span></p>');
INSERT INTO `quran` VALUES (10431,2,38,78,'<p><span>[c]</span><span> Dia, </span><span>Allah,</span><span> berfirman, “Maka, keluarlah engkau dari sini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2557">2557</a><span>]</span><span> karena sesungguhnya engkau terusir ;</span></p><p><span>[c]</span><span> 7:14 ; 15:35.</span></p>');
INSERT INTO `quran` VALUES (10432,2,38,79,'<p><span>[a]</span><span> Dan sesungguhnya, laknat-Ku menimpa atas engkau sampai Hari Pembalasan.”</span></p><p><span>[a]</span><span> 15:36.</span></p>');
INSERT INTO `quran` VALUES (10433,2,38,80,'<p><span>Ia berkata, “Tuhan-ku, </span><span>[b]</span><span> jika demikian berilah aku tangguh hingga hari mereka dibangkitkan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2558">2558</a><span>]</span><span> </span></p><p><span>[b]</span><span> 7:15 ; 15:37 ; 17:63.</span></p>');
INSERT INTO `quran` VALUES (10434,2,38,81,'<p><span>Dia, </span><span>Allah, </span><span>berfirman, </span><span>[c]</span><span> “Pastilah engkau termasuk orang-orang yang diberi tangguh,</span></p><p><span>[c]</span><span> 7:16 ; 15:38.</span></p>');
INSERT INTO `quran` VALUES (10435,2,38,82,'<p><span>[d]</span><span> “Hingga hari yang waktunya telah ditentukan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2559">2559</a><span>]</span></p><p><span>[d]</span><span> 15:39</span></p>');
INSERT INTO `quran` VALUES (10436,2,38,83,'<p><span>Ia berkata, “Maka demi kemuliaan Engkau, niscaya aku akan sesatkan semuanya,</span></p>');
INSERT INTO `quran` VALUES (10437,2,38,84,'<p><span>“Kecuali hamba-hamba Engkau yang terpilih di antara mereka.”</span></p>');
INSERT INTO `quran` VALUES (10438,2,38,85,'<p><span>Dia, </span><span>Allah,</span><span> berfirman, “Maka hakikatnya kebenaranlah yang Aku firmankan.</span></p>');
INSERT INTO `quran` VALUES (10439,2,38,86,'<p><span>“</span><span>Bahwa</span><span> tentulah Aku akan memenuhi Jahannam dengan engkau dan dengan semua mereka yang mengikuti engkau.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2560">2560</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10440,2,38,87,'<p><span>Katakanlah, “Aku tidak meminta kepadamu upah apa pun atas </span><span>da</span><span>’</span><span>wah</span><span> itu, dan bukanlah aku termasuk orang-orang yang berpura-pura.</span></p>');
INSERT INTO `quran` VALUES (10441,2,38,88,'<p><span>“Tidaklah </span><span>[a]</span><span> </span><span>Alquran</span><span> itu melainkan suatu peringatan untuk semesta alam.</span></p><p><span>[a]</span><span> 7:17 </span><span>–</span><span> 18 ; 15:40.</span></p>');
INSERT INTO `quran` VALUES (10442,2,38,89,'<p><span>“Dan sesungguhnya kamu pasti akan mengetahui beritanya sesudah suatu masa.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2561">2561</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10443,2,39,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10444,2,39,2,'<p><span>[b]</span><span> Kitab ini diturunkan dari Allah Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b]</span><span> 32:3 ; 36:6 ; 40:3 ; 41:3 ; 46:3.</span></p>');
INSERT INTO `quran` VALUES (10445,2,39,3,'<p><span>[c]</span><span> Sesungguhnya Kami Yang telah menurunkan kepada engkau Kitab ini dengan kebenaran ; maka sembahlah Allah dengan seikhlas-ikhlas ketaatan kepada-Nya.</span></p><p><span>[c]</span><span> 5:49 ; 6:107.</span></p>');
INSERT INTO `quran` VALUES (10446,2,39,4,'<p><span>Ingatlah, hanya untuk Allah ketaatan yang murni. Dan mereka yang mengambil selain Dia pelindung-pelindung </span><span>mengatakan,</span><span> “Tidaklah kami menyembah mereka itum malainkan supaya mereka akan mendekatkan kami kepada Allah sedekat-dekatnya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2562">2562</a><span>]</span><span> Sesungguhnya, </span><span>[d]</span><span> Allah akan menghakimi di antara mereka tentang apa yang di dalamnya mereka berselisih. Sesungguhnya Allah tidak memberi petunjuk kepada siapa yang berdusta, tidak bersyukur.</span></p><p><span>[d]</span><span> 4:142 ; 22:70 ; 32:26.</span></p>');
INSERT INTO `quran` VALUES (10447,2,39,5,'<p><span>[a]</span><span> Sekiranya Allah berkehendak mengambil seorang putra, tentulah Dia memilih dari apa yang diciptakan-Nya </span><span>sesuai </span><span> apa yang dikehendaki-Nya. Maha Suci Dia, Dia-lah Allah Yang Maha Esa, Maha Unggul.</span></p><p><span>[a]</span><span> 2:117 ; 10:69 ; 17:112 ; 19:89 </span><span>–</span><span> 93.</span></p>');
INSERT INTO `quran` VALUES (10448,2,39,6,'<p><span>[b]</span><span> Dia telah menciptakan seluruh langit dan bumi sesuai dengan hak. Dia menutupkan malam ke atas siang dan Dia menutupkan siang ke atas malam ; </span><span>[c]</span><span> dan Dia menundukkan matahari dan bulan. Masing-masing menempuh jalan peredaran hingga suatu masa yang tertentu. Ketahuilah, Dia Yang Maha Perkasa, Maha Pengampun.</span></p><p><span>[b]</span><span> 6:74 ; 14:20 ; 16:4 ; 29:45.</span></p>');
INSERT INTO `quran` VALUES (10449,2,39,7,'<p><span>[d]</span><span> Dia telah menciptakan kamu dari satu jiwa ; kemudian Dia menjadikan darinya jodohnya ; dan Dia telah menurunkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2563">2563</a><span>]</span><span> bagimu dari binatang ternak delapan jodoh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2564">2564</a><span>]</span><span> Dia menciptakan kamu dalam perut ibu-ibumu, kejadian sesudah kejadian, melalui tiga kegelapan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2565">2565</a><span>]</span><span> Demikianlah Allah, Tuhan-mu, kepunyaan-Nya-lah kerajaan. Tiada Tuhan selain Dia, maka kemanakah kamu dipalingkan ?</span></p><p><span>[d] </span><span>4:2 ; 7:190 ; 16:73.</span></p>');
INSERT INTO `quran` VALUES (10450,2,39,8,'<p><span>Jika kamu tidak bersyukur, maka sesungguhnya Allah Maha Kaya </span><span>tidak tergantung </span><span> pada kamu. Dan Dia tidak menyukai pada hamba-hamba-Nya yang tidak bersyukur. Tetapi, jika kamu bersyukur, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2566">2566</a><span>]</span><span> Dia menyukai kamu. </span><span>[a]</span><span> Dan tiada pemikul beban akan memikul beban orang lain. Kemudian kepada Tuhan-mu tempat kembalimu ; dan Dia akan membritahukan kepadamu tentang apa yang telah kamu kerjakan. Sesungguhnya Dia Maha Mengetahu segala yang tersembunyi dalam dada.</span></p><p><span>[a] </span><span>6:165 ; 35:19 ; 53:39.</span></p>');
INSERT INTO `quran` VALUES (10451,2,39,9,'<p><span>[b]</span><span> Dan, apabila menimpa manusia kemudaratan, ia berseru kepada Tuhan-nya seraya kembali kepada-Nya, kemudian, apabila Dia memberikan kepadanya kenikmatan dari hadirat-Nya, ia lupa akan apa yang dimohonkan kepada-Nya sebelum itu, dan ia mengada-adakan bagi Allah sekutu-sekutu, supaya ia menyesatkan </span><span>manusia</span><span> dari jalan-Nya. Katakanlah, “Ambillah sedikit faedah dengan kekufuran engkau ; sesungguhnya engkau termasuk penghuni Api.”</span></p><p><span>[b]</span><span> 17:68 ; 30:34 ; 39:50.</span></p>');
INSERT INTO `quran` VALUES (10452,2,39,10,'<p><span>Apakah orang yang berdoa dengan khusuk pada waktu-waktu malam dengan bersujud dan berdiri, takut kepada akhirat dan mengharapkan rahmat Tuhan-nya. Katakanlah, </span><span>[a]</span><span> “Apakah sama orang-orang yang tidak mengetahui ? “ Sesungguhnya, yang mengambil nasihat hanya orang-orang berakal.</span></p><p><span>[a] </span><span>6:165 ; 35:19 ; 53:39.</span></p>');
INSERT INTO `quran` VALUES (10453,2,39,11,'<p><span>R. 2</span></p><p><span>Katakanlah, “Hai, hamba-hamba-Ku yang beriman, bertakwalah kepada Tuhan-mu. Bagi orang-orang yang mengerjakan kebaikan di dunia ini </span><span>[b]</span><span> mereka </span><span>mendapat ganjaran </span><span> yang baik. Dan bumi Allah itu luas. Sesungguhnya, akan dicukupkan ganjaran </span><span>[c]</span><span> orang-orang yang sabar tanpa perhitungan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2567">2567</a><span>]</span></p><p><span>[b]</span><span> 16:31.</span></p><p><span>[c]</span><span> 3:58 ; 11:112 ; 16:97.</span></p>');
INSERT INTO `quran` VALUES (10454,2,39,12,'<p><span>[d]</span><span> Katakanlah, “Sesungguhnya aku diperintahkan supaya beribadah kepada Allah dengan mengikhlaskan ketaatan hanya kepada-Nya.</span></p><p><span>[d]</span><span> 13:37.</span></p>');
INSERT INTO `quran` VALUES (10455,2,39,13,'<p><span>“Dan, aku diperintahkan supaya aku menjadi orang pertama dari orang-orang yang berserah diri.”</span></p>');
INSERT INTO `quran` VALUES (10456,2,39,14,'<p><span>[a]</span><span> Katakanlah, “Sesungguhnya aku takut jika aku durhaka terhadap Tuhan-ku, pada azab Hari yang besar.”</span></p><p><span>[a]</span><span> 6:16 ; 10:16.</span></p>');
INSERT INTO `quran` VALUES (10457,2,39,15,'<p><span>[b]</span><span> Katakanlah, “Allah-lah yang kusembah dengan mengikhlaskan ketaatanku hanya kepada-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2568">2568</a><span>]</span></p><p><span>[b]</span><span> 40:66 ; 98:6.</span></p>');
INSERT INTO `quran` VALUES (10458,2,39,16,'<p><span>“Maka sembahlah apa pun yang kamu kehendaki selain dari Dia.” Katakanlah, “Sesungguhnya orang-orang yang rugi ialah mereka yang telah merugikan diri mereka sendiri dan keluarga mereka pada Hari Kiamat.” Ketahuilah, yang demikian itu adalah kerugian yang nyata.</span></p>');
INSERT INTO `quran` VALUES (10459,2,39,17,'<p><span>[c]</span><span> Bagi mereka itu ada bayangan api di atas mereka dan di bawah mereka pun ada bayangan. Itulah yang terhadapnya Allah mempertakut hamba-hamba-Nya. Hai hamba- hamba-Ku, maka bertakwalah kepada-Ku.</span></p><p><span>[c]</span><span> 7:42 ; 18:30.</span></p>');
INSERT INTO `quran` VALUES (10460,2,39,18,'<p><span>Dan, orang-orang yang menjauhi tuhan-tuhan palsu </span><span>dari</span><span> menyembahnya dan kembali kepada Allah, bagi mereka ada khabar suka. Maka berilah khabar suka kepada hamba- hamba-Ku.</span></p>');
INSERT INTO `quran` VALUES (10461,2,39,19,'<p><span>[a]</span><span> Orang-orang yang mendengarkan firman </span><span>Kami,</span><span> kemudian mengikuti yang terbaik darinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2569">2569</a><span>]</span><span> Mereka itulah yang Allah telah membimbing mereka, dan mereka itulah orang-orang yang mempunyai akal.</span></p><p><span>[a]</span><span> 7:205.</span></p>');
INSERT INTO `quran` VALUES (10462,2,39,20,'<p><span>Maka, apakah orang yang telah sempurna ketentuan azab atasnya </span><span>dapat diselamatkan ? </span><span> Apakah engkau dapat menyelamatkan orang yang ada di dalam Api ? </span></p>');
INSERT INTO `quran` VALUES (10463,2,39,21,'<p><span>Tetapi, orang-orang yang bertakwa kepada Tuhan mereka, bagi mereka </span><span>[b]</span><span> ada rumah-rumah beringkat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2570">2570</a><span>]</span><span> dan di atasnya ada pula dibangun kamar-kamar yang kukuh, yang di bawahnya mengalir sungai-sungai. Inilah satu janji teguh Allah ; </span><span>dan </span><span> Allah tidak pernah menyalakan janji.</span></p><p><span>[b]</span><span> 25:76 ; 29:59 ; 34:38.</span></p>');
INSERT INTO `quran` VALUES (10464,2,39,22,'<p><span>Apakah engkau tidak melihat bahwa </span><span>[a]</span><span> Allah menurunkan air dari langit, kemudian Dia mengalirkannya lewat sumber-sumber di dalam bumi ; kemudian Dia tumbuhkan dengannya </span><span>[b]</span><span> tumbuh-tumbuhan yang bermacam-macam warnanya ? Kemudian </span><span>tananman</span><span> itu menjadi kering , lalu engkau melihatnya menjadi kuning ; kemudian Dia menjadikannya jerami terpotong-potong. Sesungguhnya dalam hal demikian itu adalah nasihat bagi orang-orang yang berakal.</span></p><p><span>[a]</span><span> 35:28.</span></p><p><span>[b]</span><span> 13:5 ; 16:14.</span></p>');
INSERT INTO `quran` VALUES (10465,2,39,23,'<p><span>R. 3</span></p><p><span>[c]</span><span> Apakah orang yang Allah telah membukakan dadanya untuk </span><span>menerima </span><span> Islam, maka ia mendapat cahaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2571">2571</a><span>]</span><span> dari Tuhan-nya </span><span>sama dengan orang-orang yang keras hatinya ? </span><span>Maka celakalah orang-orang yang hatinya keras dari mengingat Allah. Mereka itulah berada dalam kesesatan yang nyata.</span></p><p><span>[c]</span><span> 6:126.</span></p>');
INSERT INTO `quran` VALUES (10466,2,39,24,'<p><span>Allah telah menurunkan sebaik-baik firman, sebuah Kitab </span><span>[d]</span><span> </span><span>yang ayat-ayatnya </span><span> saling menguatkan dan diulang-ulangi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2572">2572</a><span>]</span><span> Dengan itu membangkitkan bulu roma orang-orang yang takut kepada Tuhan mereka, kemudian kulit dan kalbu mereka menjadi lembut karena ber-zikir kepada Allah. Demikianlah petunjuk Allah ; dengannya Dia memberi petunjuk kepada siapa yang Dia kehendaki. </span><span>[e]</span><span> Dan barangsiapa disesatkan Allah maka baginya tiada seorang pemberi petunjuk.</span></p><p><span>[d]</span><span> 15:88</span></p><p><span>[e]</span><span> 17:98.</span></p>');
INSERT INTO `quran` VALUES (10467,2,39,25,'<p><span>Maka apakah orang yang menjadikan mukanya sendiri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2573">2573</a><span>]</span><span> sebagai perisai untuk melindungi diri dari keburukan azab pada Hari kiamat, </span><span>sama dengan orang-orang yang selamat ?</span><span> Dan akan dikatakan kepada orang-orang yang aniaya, “Rasakanlah oleh mu apa yang dahulu biasa kamu usahakan.”</span></p>');
INSERT INTO `quran` VALUES (10468,2,39,26,'<p><span>Orang-orang yang sebelum mereka pun telah mendustakan </span><span>rasul-rasul Kami ;</span><span> maka datanglah atas mereka </span><span>[a]</span><span> azab dari arah yang tidak mereka sadari.</span></p><p><span>[a]</span><span> 16:27 ; 59:3.</span></p>');
INSERT INTO `quran` VALUES (10469,2,39,27,'<p><span>Maka, Allah telah membuat mereka merasakan kehinaan dalam kehidupan dunia ini dan </span><span>[b] </span><span>azab di akhirat pasti akan lebih besar lagi, sekiranya mereka mengetahui.</span></p><p><span>[b]</span><span> 13:35 ; 68:34.</span></p>');
INSERT INTO `quran` VALUES (10470,2,39,28,'<p><span>[c]</span><span> Dan, sesungguhnya Kami telah memberi penjelasan kepada manusia di dalam Alquran ini dengan berbagai tamsil, supaya mereka dapat </span><span>nasihat.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2574">2574</a><span>]</span></p><p><span>[c]</span><span> 17:90 ; 30:59.</span></p>');
INSERT INTO `quran` VALUES (10471,2,39,29,'<p><span>[d]</span><span> </span><span>Kami telah mewahyukan </span><span> Alquran ini dalam bahasa Arab, yang di dalamnya tidak ada kebengkokan, supaya mereka bertakwa.</span></p><p><span>[d]</span><span> 12:3 ; 42:8 ; 43:4.</span></p>');
INSERT INTO `quran` VALUES (10472,2,39,30,'<p><span>Allah menampilkan suatu misal seorang laki-laki dimiliki oleh beberapa majikan yang dalamnya berselisih antara satu sama lain dan seorang laki-laki yang sepenuhnya dimiliki oleh seorang saja. Apakah sama kedua orang itu keadaannya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2575">2575</a><span>]</span><span> Segala puji kepunyaan Allah, tetapi kebanyakan mereka tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (10473,2,39,31,'<p><span>[a]</span><span> Sesungguhnya, engkau akan mati, dan sesungguhnya mereka </span><span>pun </span><span> akan mati.</span></p><p><span>[a]</span><span> 23:16.</span></p>');
INSERT INTO `quran` VALUES (10474,2,39,32,'<p><span>[b]</span><span> Kemudian sesungguhnya, kamu pada Hari Kiamat akan berbantah satu sama lain di hadapan Tuhan-mu.</span></p><p><span>[b]</span><span> 23:17.</span></p>');
INSERT INTO `quran` VALUES (10475,2,39,33,'<p><span>JUZ XXIV</span></p><p><span>R. 4</span></p><p><span>[c]</span><span> Maka, siapakah lebih aniaya daripada orang yang mengadakan dusta terhadap Allah dan mendustakan kebenaran apobila datang kepadanya ? Tidak adakah di Jahannam itu tempat tinggal bagi orang-orang kafir ?</span></p><p><span>[c]</span><span> 6:22 ; 10:18 ; 29:69.</span></p>');
INSERT INTO `quran` VALUES (10476,2,39,34,'<p><span>Dan, orang yang telah membawa kebenaran serta membenarkannya, mereka itulah orang-orang muttaki.</span></p>');
INSERT INTO `quran` VALUES (10477,2,39,35,'<p><span>[d]</span><span> Bagi mereka apa yang mereka inginkan di sisi Tuhan Mereka. Itulah ganjaran mereka yang berbuat kebaikan.</span></p><p><span>[d]</span><span> 16:32 ; 50:36.</span></p>');
INSERT INTO `quran` VALUES (10478,2,39,36,'<p><span>[e]</span><span> Supaya Allah akan menutupi dari mereka seburuk-buruk yang telah mereka amalkan, dan akan menganugerahi mereka ganjaran mereka dengan sebaik-baik yang mereka amalkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2576">2576</a><span>]</span></p><p><span>[e]</span><span> 16:98 ; 29:8.</span></p>');
INSERT INTO `quran` VALUES (10479,2,39,37,'<p><span>Apakah Allah tidak cukup bagi hamba-Nya ? Dan mereka, menakut-nakuti engkau dengan orang-orang yang selain Dia. </span><span>[a]</span><span> Dan barangsiapa disesatkan Allah, maka baginya tiada seorang pun pemberi petunjuk.</span></p><p><span>[a]</span><span> 39:24.</span></p>');
INSERT INTO `quran` VALUES (10480,2,39,38,'<p><span>[b]</span><span> Dan, barangsiapa yang Allah memberi petunjuk, tiada seorang pun dapat menyesatkannya. Bukankah Allah itu Maha Perkasa, Empunya Pembalasan ?</span></p><p><span>[b]</span><span> 18:18</span></p>');
INSERT INTO `quran` VALUES (10481,2,39,39,'<p><span>[c]</span><span> Dan jika engkau menanyakan kepada mereka, “Siapakah yang menciptakan seluruh langit dan bumi ? “ Pasti mereka akan berkata, “Allah.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2377">2377</a><span>]</span><span> Katakanlah, “Apakah kamu mengetahui apa yang kamu seru selain Allah, jika Allah menghendaki kepada kemudaratannya ? Atau, jika Dia berkehendak memberi rahmat, dapatkah mereka mencegah rahmat-Nya itu?” </span><span>[d</span><span>] Katakanlah, “Allah sudah cukup bagiku. Kepada-Nya bertawakal orang-orang yang tawakal. ”</span></p><p><span>[c]</span><span> 29:62 ; 31:26.</span></p><p><span>[d]</span><span> 9:129.</span></p>');
INSERT INTO `quran` VALUES (10482,2,39,40,'<p><span>Katakanlah, “Hai, kaumku, </span><span>[a]</span><span> beramallah kamu di tempatmu masing-masing, sesungguhnya aku </span><span>pun</span><span> beramal ; maka kamu pasti akan mengetahui, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2578">2578</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10483,2,39,41,'<p><span>[b]</span><span> “Siapakah yang datang kepadanya azab yang menghinakannya dan yang kepadanya turun azab yang kekal ?”</span></p><p><span>[b]</span><span> 11:40.</span></p>');
INSERT INTO `quran` VALUES (10484,2,39,42,'<p><span>Sesungguhnya, Kami telah menurunkan Kitab ini kepadamu dengan kebenaran untuk manfaat manusia, </span><span>[c]</span><span> barangsiapa mendapat petunjuk, maka untuk dirinya sendiri ; dan barangsiapa sesat, maka kesesatannya untuk dirinya sendiri, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2579">2579</a><span>]</span><span> dan engkau bukanlah penjaga atas mereka.</span></p><p><span>[c]</span><span> 10:109 ; 17:16 ; 27:93.</span></p>');
INSERT INTO `quran` VALUES (10485,2,39,43,'<p><span>R. 5</span></p><p><span>[d</span><span>] Allah mengambil ruh manusia ketika matinya, dan yang tidak mati di waktu tidurnya. Maka Dia menahan </span><span>ruh</span><span> yang Dia menetapkan atasnya mati dan mengirimkan yang lain hingga masa yang telah ditetapkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2580">2580</a><span>]</span><span> Sesungguhnya dalam yang demikian itu ada Tanda-tanda bagi kaum yang merenungkan.</span></p><p><span>[d]</span><span> 6:61.</span></p>');
INSERT INTO `quran` VALUES (10486,2,39,44,'<p><span>[a]</span><span> Apakah mereka telah mengambil pemberi-pemberi syafaat selain Allah ? Katakanlah, “Apakah walaupun mereka tidak memiliki kekuasaan sedikit pun dan mereka tidak berakal ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2581">2581</a><span>]</span></p><p><span>[a]</span><span> 17:57</span></p>');
INSERT INTO `quran` VALUES (10487,2,39,45,'<p><span>Katakanlah, “Milik Allah-lah syafaat itu semuanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2582">2582</a><span>]</span><span> Bagi Dia kerajaan seluruh langit dan bumi. Kemudian kepada-Nya-lah kamu akan dikembalikan.”</span></p>');
INSERT INTO `quran` VALUES (10488,2,39,46,'<p><span>[b]</span><span> Dan, apabila hanya Allah yang disebut, mulai kesallah hati orang-orang yang tidak beriman kepada akhirat ; tetapi apabila disebut orang-orang lain selain Dia, tiba-tiba mereka bergirang hati.</span></p><p><span>[b]</span><span> 17:47 ; 22:73 ; 40:13.</span></p>');
INSERT INTO `quran` VALUES (10489,2,39,47,'<p><span>[c]</span><span> Katakanlah, “Ya, Allah ! Pencipta seluruh langit dan bumi ; Maha Mengetahui yang ghaib dan yang nyata ; dan Engkau-lah yang akan menghakimi di antara hamba-hamba Engkau mengenai apa yang mereka berselisih di dalamnya.”</span></p><p><span>[c]</span><span> 6:15 ; 12:102 ; 14:11 ; 35:2.</span></p>');
INSERT INTO `quran` VALUES (10490,2,39,48,'<p><span>[a]</span><span> Dan, sekiranya orang-orang aniaya itu memiliki segala yang ada di bumi ini, dan sebanyak itu pula sebagai tambahan padanya, tentu mereka akan </span><span>berusaha </span><span> menebus diri dengan </span><span>semuanya</span><span> itu </span><span>untuk menyelamatkan diri</span><span> dari azab yang ganas pada Hari Kiamat. Tetapi, akan nyata kepada mereka dari Allah apa yang tidak pernah mereka sangka.</span></p><p><span>[a]</span><span> 5:37 ; 10:55 ; 13:19.</span></p>');
INSERT INTO `quran` VALUES (10491,2,39,49,'<p><span>[b]</span><span> Dan akan nyata kepada mereka keburukan-keburukan apa yang telah diusahakan mereka; dan akan mengepung mereka apa yang dengannya mereka perolok-olokan.</span></p><p><span>[b]</span><span> 21:42 ; 45:34.</span></p>');
INSERT INTO `quran` VALUES (10492,2,39,50,'<p><span>[c]</span><span> Maka apabila kemudaratan menimpa manusia, ia berseru kepada Kami, tetapi apabila Kami menganugerahkan kepadanya suatu kenikmatan dari Kami, ia berkata, “Itu telah diberikan kepadaku karena ilmu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2583">2583</a><span>]</span><span> Tidak, bahkan hal itu suatu ujian, akan tetapi kebanyakan mereka tidak mengetahui.</span></p><p><span>[c]</span><span> 11:10, 11 ; 17:68 ; 30:34 ; 39:9.</span></p>');
INSERT INTO `quran` VALUES (10493,2,39,51,'<p><span>Orang-orang sebelum mereka pun telah mengatakan serupa itu, tetapi segala yang telah mereka usahakan itu tidak berguna bagi mereka.</span></p>');
INSERT INTO `quran` VALUES (10494,2,39,52,'<p><span>Maka telah menimpa mereka keburukan-keburukan apa yag telah mereka kerjakan. Dan orang-orang yang aniaya di antara mereka ini, pasti akan menimpa mereka keburukan- keburukan yang mereka kerjakan dan mereka tidak akan dapat mencegah </span><span>Allah dari kehendak-Nya.</span></p>');
INSERT INTO `quran` VALUES (10495,2,39,53,'<p><span>Apakah mereka tidak mengetahui, sesungguhnya </span><span>[a]</span><span> Allah melapangkan rezeki bagi siapa yang Dia kehendaki dan menyempitkan ? Sesungguhnya, dalam yang demikian itu ada Tanda-tanda bagi kaum yang beriman.</span></p><p><span>[a]</span><span> 13:27 ; 29:63 ; 30:38 ; 34:37 ; 42:13.</span></p>');
INSERT INTO `quran` VALUES (10496,2,39,54,'<p><span>R. 6</span></p><p><span>[b]</span><span> Katakanlah, “Hai hamba-hamba-Ku yang telah melampaui batas terhadap diri mereka sendiri, janganlah kamu berputus asa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2584">2584</a><span>]</span><span> dari rahmat Allah. Sesungguhnya Allah mengampuni segala dosa. Sesungguhnya Dia Maha Pengampun, Maha Penyayang.</span></p><p><span>[b]</span><span> 12:88 ; 15:57.</span></p>');
INSERT INTO `quran` VALUES (10497,2,39,55,'<p><span>“Dan, kembalilah kepada Tuhan-mu dan berserah-dirilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2585">2585</a><span>]</span><span> kepada-Nya sebelum azab datang kepadamu ; kemudian kamu tidak akan ditolong.</span></p>');
INSERT INTO `quran` VALUES (10498,2,39,56,'<p><span>“Dan, ikutilah </span><span>ajaran </span><span> terbaik yang telah diturunkan kapadamu dari Tuhan-mu, sebelum datang kepadamu azab yang tiba-tiba, sedang kamu tidak menyadari.”</span></p>');
INSERT INTO `quran` VALUES (10499,2,39,57,'<p><span>Supaya jangan </span><span> ada orang yang mengatakan, </span><span>[a]</span><span> “Ah, sungguh suatu penyesalan atas kelalaianku terhadap Allah, sedangkan aku termasuk orang-orang yang menghinakan </span><span>wahyu Ilahi.</span><span>”</span></p><p><span>[a]</span><span> 6:32 ; 23:100 ; 26:103 ; 35:38.</span></p>');
INSERT INTO `quran` VALUES (10500,2,39,58,'<p><span>Atau, ia berkata, “Sekiranya Allah memberi petunjuk kepadaku, niscaya aku berada di antara orang-orang yang muttaki.”</span></p>');
INSERT INTO `quran` VALUES (10501,2,39,59,'<p><span>Atau, ia berkata ketika ia melihat azab, “Sekiranya bagiku ada </span><span>kesempatan</span><span> kembali </span><span>ke dunia,</span><span> niscaya aku akan berada di antara orang-orang yang berbuat kebaikan.”</span></p>');
INSERT INTO `quran` VALUES (10502,2,39,60,'<p><span>Mengapa tidak, sesungguhnya telah datang kepada engkau Tanda-tanda-Ku, tetapi engkau telah mendustakan dan berlaku sombong dan engkau termasuk di antara orang-orang kafir.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2586">2586</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10503,2,39,61,'<p><span>Dan, pada Hari Kiamat engkau akan melihat orang-orang yang akan melihat orang-orang yang telah berdusta terhadap Allah </span><span>[a]</span><span> muka mereka menjadi hitam. Bukankah dalam Jahannam itu ada tempat tinggal bagi orang-orang yang sombong ? </span></p><p><span>[a]</span><span> 3:107 ; 10:28.</span></p>');
INSERT INTO `quran` VALUES (10504,2,39,62,'<p><span>[b]</span><span> Dan, Allah akan menyelamatkan orang-orang bertakwa karena keberhasilan mereka ; mereka tidak akan disentuh kesusahan dan mereka tidak akan bersedih.</span></p><p><span>[b]</span><span> 19:73 ; 21:102.</span></p>');
INSERT INTO `quran` VALUES (10505,2,39,63,'<p><span>[c]</span><span> Allah Pencipta segala sesuatu, dan Dia-lah Pemelihara segala sesuatu.</span></p><p><span>[c]</span><span> 6:103 ; 13:17.</span></p>');
INSERT INTO `quran` VALUES (10506,2,39,64,'<p><span>[d]</span><span> Dia-lah Yang mempunyai kunci-kunci seluruh langit dan bumi. Dan orang-orang yang ingkar kepada Tanda-tanda Allah, mereka itulah orang-orang yang rugi.</span></p><p><span>[d]</span><span> 42:13.</span></p>');
INSERT INTO `quran` VALUES (10507,2,39,65,'<p><span>R. 7</span></p><p><span>[e]</span><span> “Katakanlah, “Apakah selain Allah kamu menyuruhku </span><span>supaya</span><span> aku menyembah, hai orang-orang bodoh ? “</span></p><p><span>[e]</span><span> 6:15.</span></p>');
INSERT INTO `quran` VALUES (10508,2,39,66,'<p><span>Dan, sesungguhnya telah diwahyukan kepada engkau dan kepada mereka sebelum engkau, </span><span>[f]</span><span> “Jika engkau mempersekutukan </span><span>Allah,</span><span> niscaya akan hapulah amal engkau, dan pastilah engkau termasuk orang-orang yang rugi.”</span></p><p><span>[f]</span><span> 6:89.</span></p>');
INSERT INTO `quran` VALUES (10509,2,39,67,'<p><span>Bahkan beribadahlah kepada Allah dan jadilah engkau di antara orang-orang yang bersyukur.</span></p>');
INSERT INTO `quran` VALUES (10510,2,39,68,'<p><span>[a]</span><span> Dan mereka tidak memuliakan Allah sebenar-benar kemulian-Nya. </span><span>[b]</span><span> Padahal bumi seluruhnya akan berada di bawah kekuasaan-Nya pada Hari Kiamat, dan seluruh langit akan tergulung di tangan kanan-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2587">2587</a><span>]</span><span> Maha Suci Dia dan Maha Tinggi di atas apa yang mereka persekutukan.</span></p><p><span>[a]</span><span> 6:92 ; 22:75.</span></p><p><span>[b]</span><span> 21:105.</span></p>');
INSERT INTO `quran` VALUES (10511,2,39,69,'<p><span>[c]</span><span> Dan nafiri akan ditiup, lalu akan </span><span>jatuh </span><span> pingsan semua yang ada di seluruh langit dan semua yang ada di bumi, kecuali siapa yang dikehendaki Allah. Kemudian </span><span>nafiri</span><span> itu akan ditiup kedua kalinya, maka tiba-tiba mereka berdiri menantikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2588">2588</a><span>]</span><span> </span><span>keputusan.</span></p><p><span>[c]</span><span> 18:100 ; 23:102 ; 36:52 ; 50:21 ; 69:14.</span></p>');
INSERT INTO `quran` VALUES (10512,2,39,70,'<p><span>Dan, bumi akan bersinar dengan nur Tuhan-nya, </span><span>[d]</span><span> dan Kitab itu akan diletakkan </span><span>terbuka di hadapan mereka,</span><span> dan akan didatangkan nabi-nabi dan saksi-saksi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2589">2589</a><span>]</span><span> dan diputuskan di antara mereka dengan adil, dan mereka tidak akan di aniaya.</span></p><p><span>[d]</span><span> 18:50.</span></p>');
INSERT INTO `quran` VALUES (10513,2,39,71,'<p><span>[a]</span><span> Dan, setiap jika akan diberikan sempurna apa yang ia kerjakan. Dan Dia mengetahui apa yang mereka kerjakan.</span></p><p><span>[a]</span><span> 2:282 ; 3:26.</span></p>');
INSERT INTO `quran` VALUES (10514,2,39,72,'<p><span>R. 8</span></p><p><span>[b]</span><span> Dan orang-orang ingkar akan digiring ke Jahannam berbondong-bondong. Hingga apabila mereka sampai kepada, pintu-pintunya akan dibukakan, </span><span>[c]</span><span> dan penjaga- penjaganya akan berkata kepada mereka, “Bukankah telah datang kepadamu rasul-rasul dari antara kamu sendiri membacakan kepadamu Ayat-ayat Tuhan-mu, dan memberi peringatan kepadamu tentang pertemuan pada harimu ini ? “ Mereka akan berkata, “Memang benar, tetapi sudah pasti sempurna ketetapan azab terhadap orang-orang kafir.”</span></p><p><span>[b]</span><span> 19:87.</span></p><p><span>[c]</span><span> 40:51 ; 67:9 </span><span>–</span><span> 10.</span></p>');
INSERT INTO `quran` VALUES (10515,2,39,73,'<p><span>Akan dikatakan, </span><span>[a]</span><span> “Kalian masukilah pintu-pintu Jahannam, kalian akan tinggal lama di dalam nya. Maka sangat buruklah tempat tinggal orang-orang sombong.”</span></p><p><span>[a]</span><span> 16:30 ; 40:77.</span></p>');
INSERT INTO `quran` VALUES (10516,2,39,74,'<p><span>Dan digiring orang-orang bertakwa kepada Tuhan mereka ke dalam surga dalam rombongan-rombongan. Hingga apabila mereka sampai padanya dan dibukakan pintu-pintunya, dan berkata kepada mereka penjaga-penjaganya, </span><span>[b]</span><span> “Selamat sejahtera atas kamu ! Dan kamu sampai dalam keadaan baik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2590">2590</a><span>]</span><span> maka kamu masuklah kedalamnya untuk selama-lamanya.</span></p><p><span>[b]</span><span> 13:25.</span></p>');
INSERT INTO `quran` VALUES (10517,2,39,75,'<p><span>Dan, mereka akan berkata, </span><span>[c]</span><span> “Segala puji bagi Allah, Yang telah membenarkan kepada kami janji-Nya dan telah mewariskan kepada kami bumi, kami akan bertempat tinggal di surga di mana pun kami menghendaki.” Maka alangkah baiknya ganjaran orang-orang yang beramal.</span></p><p><span>[c]</span><span> 1:2 ; 7:44 ; 37:183 ; 40:8.</span></p>');
INSERT INTO `quran` VALUES (10518,2,39,76,'<p><span>Dan, engkau akan melihat malaikat-malaikat berkeliling di sekitar </span><span>‘</span><span>arasy, seraya bertasbih dengan menyanjungkan puji-pujian kepada Tuhan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2591">2591</a><span>]</span><span> Dan akan diputuskan di antara mereka dengan adil, dan akan dikatakan, “Segala puji bagi Allah, Tuhan sekalian alam.”</span></p>');
INSERT INTO `quran` VALUES (10519,2,40,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10520,2,40,2,'<p><span>[b]</span><span> Maha Terpuji, Maha Mulia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2592">2592</a><span>]</span></p><p><span>[b]</span><span> 41:2 ; 42:2 ; 43:2 ; 44:2 ; 45:2 ; 46:2.</span></p>');
INSERT INTO `quran` VALUES (10521,2,40,3,'<p><span>[c]</span><span> Diturunkan kitab ini dari Allah, Yang Maha Perkasa, Maha Mengetahui.</span></p><p><span>[c]</span><span> 20:5 ; 32:3 ; 41:3 ; 45:3 ; 46:3.</span></p>');
INSERT INTO `quran` VALUES (10522,2,40,4,'<p><span>Pengampun dosa dan Penerima taubat, keras menghukum Yang mempunyai kelimpahan karunai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2593">2593</a><span>]</span><span> Tiada Tuhan selain dia. Kepada-Nya-lah tempat kembali.</span></p>');
INSERT INTO `quran` VALUES (10523,2,40,5,'<p><span>[d]</span><span> Tiada yang bertengkar tentang Tanda-tanda Allah, kecuali orang-orang yang ingkar. </span><span>[e] </span><span>Maka, hendaknya jangan memperdayakan engkau lalu-lalang mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2594">2594</a><span>]</span><span> di kota-kota.</span></p><p><span>[d]</span><span> 22:4 ; 42:36.</span></p><p><span>[e]</span><span> 3:197.</span></p>');
INSERT INTO `quran` VALUES (10524,2,40,6,'<p><span>[a]</span><span> Telah mendustakan sebelum mereka, kaum Nuh dan golongan-golongan sesudah mereka, dan telah berusaha setiap umat kepada rasul mereka supaya mereka menangkapnya dan mereka berbantah dengan </span><span>dalil-dalil</span><span> batil agar dengan itu mereka dapat menolak kebenaran, kemudian Aku tangkap mereka, dan betapa </span><span>hebatnya </span><span> hukuman-Ku !</span></p><p><span>[a]</span><span> 6:35 ; 22:43 ; 35:26 ; 54:10.</span></p>');
INSERT INTO `quran` VALUES (10525,2,40,7,'<p><span>[b]</span><span> Dan, demikianlah telah sempurna keputusan Tuhan engkau terhadap orang-orang yang ingkar, bahwa sesungguhnya mereka itu penghuni Api.</span></p><p><span>[b]</span><span> 10:34, 97.</span></p>');
INSERT INTO `quran` VALUES (10526,2,40,8,'<p><span>[c]</span><span> Orang-orang yang memikul </span><span>‘</span><span>Arasy </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2595">2595</a><span>]</span><span> dan yang di sekitarnya, mereka mensucikan dengan pujian Tuhan mereka, dan mereka beriman kepada-Nya dan mereka memohon ampunan bagi orang-orang yang beriman, “Hai Tuhan kami, engkau meliputi segala sesuatu dengan rahmat dan ilmu. Maka ampunilah bagi orang-orang yang bertaubat dan mengikuti jalan Engkau, dan lindungilah mereka dari azab Jahannam.</span></p><p><span>[c]</span><span> 39:76 ; 69:18.</span></p>');
INSERT INTO `quran` VALUES (10527,2,40,9,'<p><span>“Hai Tuhan kami dan masukkanlah mereka ke dalam surga-surga abadi yang telah Engkau janjikan kepada mereka, dan begitu pun </span><span>[a] </span><span>orang-orang yang berbuat baik dari bapak-bapak mereka dan istri-istri mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2596">2596</a><span>]</span><span> dan keturunan-keturunan mereka. Sesungguhnya, Engkau adalah Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[a]</span><span> 13:24 ; 52:22.</span></p>');
INSERT INTO `quran` VALUES (10528,2,40,10,'<p><span>“Dan lindungilah mereka dari segala keburukan. </span><span>[b]</span><span> Dan barangsiapa Engkau pelihara dari keburukan-keburukan pada hari itu, sesungguhnya Engkau telah mengasihinya. Dan yang demikian itu suatu kemenangan besar.”</span></p><p><span>[b]</span><span> 6:17.</span></p>');
INSERT INTO `quran` VALUES (10529,2,40,11,'<p><span>R. 2</span></p><p><span>Sesungguhnya orang-orang yang ingkar itu akan dipanggil, “Pasti kemurkaan Allah lebih besar dari kemukaan kamu terhadap dirimu tatkala kamu diseru kepada iman, lalu kamu ingkar.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2597">2597</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10530,2,40,12,'<p><span>Mereka berkata, “Hai Tuhan kami, </span><span>[a]</span><span> Engkau telah mematikan kami dua kali, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2598">2598</a><span>]</span><span> dan Engkau telah menghidupkan kami dua kali ; maka kini kami mengakui dosa-dosa kami. Kemudian apakah ada jalan keluar </span><span>dari musibah ini ? </span><span> “</span></p><p><span>[a]</span><span> 30:41.</span></p>');
INSERT INTO `quran` VALUES (10531,2,40,13,'<p><span>Yang demikian itu disebabkan </span><span>[b]</span><span> apabila hanya Allah yang diseru kamu mengingkari, dan jika Dia dipersekutukan dengan sesuatu kamu mengimani. Maka keputusan hanya pada Allah, Yang Maha Tinggi, Yang Maha Besar.</span></p><p><span>[b]</span><span> 39:46.</span></p>');
INSERT INTO `quran` VALUES (10532,2,40,14,'<p><span>[c]</span><span> Dia-lah Yang memperlihatkan kepadamu Tanda-tanda-Nya, dan menurunkan bagi kamu dari langit rezeki. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2599">2599</a><span>]</span><span> Dan tidak ada yang memperoleh nasihat kecuali orang yang tunduk </span><span>kepada-Nya.</span></p><p><span>[c]</span><span> 30:25.</span></p>');
INSERT INTO `quran` VALUES (10533,2,40,15,'<p><span>Maka serulah Allah, </span><span>[a]</span><span> dengan mengikhlaskan ketaatan kepada-Nya, walaupun orang-orang kafir tidak menyukai</span><span>nya.</span></p><p><span>[a]</span><span> 29:66 ; 31:33 ; 98:6.</span></p>');
INSERT INTO `quran` VALUES (10534,2,40,16,'<p><span>Dia</span><span> Yang Maha Tinggi derajat-Nya, Yang Empunya </span><span>‘</span><span>Arasy, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2600">2600</a><span>] [b]</span><span> Dia menurunkan kalam dengan perintah-Nya kepada siapa yang dikehendaki di antara hamba-hamba-Nya supaya Dia memperingatkan tentang Hari Pertemuan.</span></p><p><span>[b]</span><span> 16:3 ; 97:5.</span></p>');
INSERT INTO `quran` VALUES (10535,2,40,17,'<p><span>Pada Hari ketika mereka akan hadir di hadapan </span><span>Allah,</span><span> </span><span>[c]</span><span> tiada tersembunyi di hadapan </span><span>Allah,</span><span> dari mereka sesuatu pun. </span><span>[d]</span><span> “Kepunyaan siapakah Kerajaan pada Hari itu ? “ Kepunyaan Allah, Yang Maha Esa dan Yang Maha Unggul.</span></p><p><span>[c]</span><span> 3:6 ; 14:39.</span></p><p><span>[d]</span><span> 18:45 ; 48:15 ; 82:20.</span></p>');
INSERT INTO `quran` VALUES (10536,2,40,18,'<p><span>[e]</span><span> Pada hari itu setiap jiwa akan diganjar dengan apa yang telah diusahakannya. Tidak ada keaniayaan pada hari itu ! Sesungguhnya Allah sangat cepat dalam menghisab.</span></p><p><span>[e]</span><span> 14:52 ; 45:23 ; 74:39.</span></p>');
INSERT INTO `quran` VALUES (10537,2,40,19,'<p><span>[f]</span><span> Dan, peringatkanlah mereka tentang hari yang kian mendekat dengan cepatnya, ketika setiap hari akan sampai ke tenggorokan dengan penuh kedukaan. Bagi orang-orang aniaya tidak ada sahabat setia dan tidak ada pemberi syafaat yang akan diterima.</span></p><p><span>[f]</span><span> 19:40.</span></p>');
INSERT INTO `quran` VALUES (10538,2,40,20,'<p><span>Dia mengetahui pengkhianatan mata </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2600a">2600a</a><span>]</span><span> dan apa yang disembunyikan oleh </span><span>[a]</span><span> dada.</span></p><p><span>[a]</span><span> 27:75 ; 28:70.</span></p>');
INSERT INTO `quran` VALUES (10539,2,40,21,'<p><span>Dan, Allah memutuskan dengan benar. Dan orang-orang yang menyeru selain Dia, mereka tidak memutuskan sesuatu. Sesungguhnya Allah itu Dia Yang Maha Mendengar, Maha Melihat.</span></p>');
INSERT INTO `quran` VALUES (10540,2,40,22,'<p><span>R. 3</span></p><p><span>[b]</span><span> apakah mereka tidak bepergian di bumi supaya dapat melihat bagaimana kesudahan orang-orang sebelum mereka ? Mereka itu lebih hebat kekuatannya daripada mereka dan peninggalan-peninggalan mereka di bumi, maka Allah membinasakan mereka disebabkan dosa-dosa mereka. Dan tidak ada penyelamat bagi mereka dari </span><span>azab</span><span> Allah.</span></p><p><span>[b]</span><span> 12:110 ; 22:47 ; 35:45 ; 47:11.</span></p>');
INSERT INTO `quran` VALUES (10541,2,40,23,'<p><span>Yang demikian itu disebabkan </span><span>[c]</span><span> rasul-rasul mereka telah datang kepada mereka dengan Tanda-tanda nyata, tetapi mereka ingkar ; maka Allah membinasakan mereka. Sesungguhnya, Dia Maha Kuat, keras dalam mengazab.</span></p><p><span>[c]</span><span> 23:45 ; 41:15.</span></p>');
INSERT INTO `quran` VALUES (10542,2,40,24,'<p><span>[d]</span><span> Dan, sesungguhnya telah Kami kirimkan Musa dengan Tanda-tanda Kami dan dalil yang nyata.</span></p><p><span>[d]</span><span> 23:46.</span></p>');
INSERT INTO `quran` VALUES (10543,2,40,25,'<p><span>Kepada Fir</span><span>’</span><span>aun dan Haman dan Karun, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2601">2601</a><span>]</span><span> tetapi mereka berkata, “Ia tukang sihir </span><span>dan</span><span> pendusta besar ! “ </span></p>');
INSERT INTO `quran` VALUES (10544,2,40,26,'<p><span>[a]</span><span> Dan, ketika ia datang kepada mereka dengan kebenaran dari sisi Kami, mereka berkata, </span><span>[b]</span><span> “Bunuhlah semua anak laki-laki mereka yang telah beriman besertanya, dan biarkanlah hidup perempuan-perempuan mereka.” Dan tidaklah tipu-daya orang-orang kafir itu kecuali sia-sia.</span></p><p><span>[a]</span><span> 29:40.</span></p><p><span>[b]</span><span> 7:128.</span></p>');
INSERT INTO `quran` VALUES (10545,2,40,27,'<p><span>Dan berkata Fir</span><span>’</span><span>aun, “Biarkalah aku membunuh Musa dan supaya dia menyeru Tuhan-nya, </span><span>[c]</span><span> sesungguhnya aku khawatir jangan-jangan ia mengubah agamamu atau menimbulkan kekacauan di muka bumi.”</span></p><p><span>[c]</span><span> 20:64 ; 26:36.</span></p>');
INSERT INTO `quran` VALUES (10546,2,40,28,'<p><span>Dan, Musa berkata, </span><span>[d]</span><span> “Aku berlindung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2602">2602</a><span>]</span><span> kepada Tuhan-ku dan Tuhan-mu dari setiap orang-orang yang sombong, yang tidak beriman kepada Hari Perhitungan.” </span></p><p><span>[d] </span><span>44:21.</span></p>');
INSERT INTO `quran` VALUES (10547,2,40,29,'<p><span>R. 4</span></p><p><span>Dan, berkata seorang laki-laki yang beriman dari kaum Fir</span><span>’</span><span>aun yang menyembunyikan imannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2603">2603</a><span>]</span><span> “Apakah kamu akan membunuh seorang laki-laki karena ia mengatakan, </span><span>‘</span><span>Tuhan-ku ialah Allah,</span><span>’</span><span> padahal ia telah datang kepadamu dengan Tanda-tanda nyata dari Tuhan-mu ? </span><span>[a]</span><span> Dan, sekiranya ia seorang pendusta maka atas dialah kedustaannya, dan jika ia benar, maka akan menimpa kamu sebagian dari apa yang dijanjikan kepadamu. Sesungguhnya Allah tidak memberi petunjuk kepada siapa yang melampaui batas </span><span>dan</span><span> pembohong besar.”</span></p><p><span>[a] </span><span>69:45, 47.</span></p>');
INSERT INTO `quran` VALUES (10548,2,40,30,'<p><span>“Hai, kaumku ! Bagi kamu ada kerajaan hari ini sebagai penguasa di bumi, tetapi siapakah yang akan menolong kami dari azab Allah, jika menimpa kami ? “ Fir</span><span>’</span><span>aun berkata, “Aku hanya menunjukkan kepada kamu apa yang telah aku lihat, dan aku tidak memberi petunjuk kepada kamu, kecuali kepada jalan yang benar.”</span></p>');
INSERT INTO `quran` VALUES (10549,2,40,31,'<p><span>Dan, berkata orang yang beriman, “Hai, kaumku, sesungguhnya aku takut atas kamu seperti </span><span>terjadi</span><span> hari </span><span>kebinasaan</span><span> bangsa-bangsa.</span></p>');
INSERT INTO `quran` VALUES (10550,2,40,32,'<p><span>[a]</span><span> “Seperti apa yang terjadi </span><span>kepada </span><span> kaum Nuh dan </span><span>‘</span><span>Ad dan Tsamud dan orang-orang yang sesudah mereka. Dan Allah tidak menghendaki keaniayaan terhadap hamba-hamba-</span><span>Nya ;</span></p><p><span>[a]</span><span> 9:70 ; 14:10 ; 50:13 </span><span>–</span><span> 15.</span></p>');
INSERT INTO `quran` VALUES (10551,2,40,33,'<p><span>“Dan, hai kaumku, sesungguhnya aku takutkan bagimu hari ketika orang-orang saling memanggil </span><span>meminta pertolongan,</span><span> </span><span>{</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2604">2604</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10552,2,40,34,'<p><span>“Hari itu ketika kamu akan berbalik ke belakang melarikan diri ; tiada bagi kamu seorang pun penyelamat dari allah. Dan, siapa yang Allah nyatakan sesat, maka tidak ada baginya pemberi petunjuk.”</span></p>');
INSERT INTO `quran` VALUES (10553,2,40,35,'<p><span>Dan, sesungguhnya telah datang kepadamu Yusuf sebelum ini dengan bukti-bukti yang nyata, tetapi kamu selalu dalam keraguan dari apa yang dibawanya kepada kamu. Sehingga tatkala ia telah mati, kamu berkata, “Allah sekali-kali tidak akan mengutus sesudah dia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2605">2605</a><span>]</span><span> seorang rasul.” Demikianlah Allah menetapkan sesat barangsiapa yang melampaui batas, yang ragu-ragu.</span></p>');
INSERT INTO `quran` VALUES (10554,2,40,36,'<p><span>Mereka yang bertengkar tentang Tanda-tanda Allah tanpa dalil yang datang kepada mereka. Besar kemurkaan di sisi Allah dan di sisi orang-orang yang beriman. Demikian Allah mencap setiap hati orang sombong yang angkuh.</span></p>');
INSERT INTO `quran` VALUES (10555,2,40,37,'<p><span>Dan Fir</span><span>’</span><span>aun berkata, </span><span>[a]</span><span> “Hai Haman, dirikanlah bagiku suatu bangunan tinggi supaya aku dapat mencapai sarana </span><span>untuk naik,</span></p><p><span>[a]</span><span> 28:39.</span></p>');
INSERT INTO `quran` VALUES (10556,2,40,38,'<p><span>“Sarana </span><span>untuk mencapai</span><span> langit, supaya </span><span>[b]</span><span> aku dapat memandang Tuhan Musa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2606">2606</a><span>] </span><span>dan sesungguhnya aku menganggapnya sebagai seorang pendusta ! “ Dan demikianlah ditampakkan indah bagi Fir</span><span>’</span><span>aun keburukan amalnya, dan ia dihalangi dari jalan </span><span>yang benar.</span><span> Dan rencana Fir</span><span>’</span><span>aun </span><span>pasti </span><span> berakhir dalam kehancuran.</span></p><p><span>[b]</span><span> 28:39.</span></p>');
INSERT INTO `quran` VALUES (10557,2,40,39,'<p><span>R. 5</span></p><p><span>Dan orang yang beriman itu berkata, “Hai kaumku, ikutilah aku. Aku akan menunjukkan kamu jalan yang benar.</span></p>');
INSERT INTO `quran` VALUES (10558,2,40,40,'<p><span>“Hai kaumku, sesungguhnya </span><span>[c]</span><span> kehidupan dunia ini hanya kesenangan </span><span>sementara ;</span><span> dan sesungguhnya </span><span>kehidupan akhirat</span><span> itu tempat tinggal yang kekal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2607">2607</a><span>]</span></p><p><span>[c]</span><span> 3:15, 198, 199 ; 9:38 ; 16:118 ; 28:61.</span></p>');
INSERT INTO `quran` VALUES (10559,2,40,41,'<p><span>[a]</span><span> “Barangsiapa berbuat keburukan tidak akan dibalas kecuali dengan semisalnya; </span><span>[b]</span><span> namun barangsiapa beramal shaleh, dari laki-laki ataupun perempuan, sedang ia orang yang beriman, maka mereka akan masuk surga, mereka akan diberi rezeki di dalamnya tanpa perhitungan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2608">2608</a><span>]</span></p><p><span>[a]</span><span> 10:28 ; 4:124.</span></p><p><span>[b]</span><span> 4:125.</span></p>');
INSERT INTO `quran` VALUES (10560,2,40,42,'<p><span>“Dan, hai kaumku, betapa anehnya keadaanku, aku mengajakmu kepada keselamatan, dan kamu mengajak aku kepada Api.</span></p>');
INSERT INTO `quran` VALUES (10561,2,40,43,'<p><span>“Kamu menyeru aku supaya aku ingkar kepada Allah dan menyekutukan-Nya yang tidak ada padaku ilmu mengenai itu, sedang aku mengajak kamu kepada Dzat Yang Maha Perkasa, Maha Pengampun.</span></p>');
INSERT INTO `quran` VALUES (10562,2,40,44,'<p><span>“Tidak ragu lagi bahwa apa yang kamu mengajakku kepadanya tidak mempunyai seruan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2608a">2608a</a><span>]</span><span> </span><span>yang berpengaruh</span><span> di dunia dan tidak pula di akhirat ; dan bahwa </span><span>tempat </span><span> kembali kami kepada Allah ; dan bahwa orang-orang yang melampaui batas akan menjadi penghuni Api.</span></p>');
INSERT INTO `quran` VALUES (10563,2,40,45,'<p><span>“Maka kamu segera akan ingat apa yang aku katakan kepada kamu. dan, aku menyerahkan urusanku kepada Allah. Sesungguhnya allah Maha Melihat hamba-hamba-</span><span>Nya.</span><span>”</span></p>');
INSERT INTO `quran` VALUES (10564,2,40,46,'<p><span>Maka Allah memeliharanya dari keburukan apa yang telah mereka rencanakan dan kaum Fir</span><span>’</span><span>aun dikepung oleh azab yang buruk.</span></p>');
INSERT INTO `quran` VALUES (10565,2,40,47,'<p><span>Yakni</span><span> Api. Mereka dihadapkan kepadanya pagi dan petang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2609">2609</a><span>]</span><span> dan pada hari ketika waktu yang ditentukan itu datang, </span><span>akan dikatakan kepada para malaikat,</span><span> “Masukkanlah kaum Fir</span><span>’</span><span>aun itu </span><span>ke dalam </span><span> azab yang amat keras.”</span></p>');
INSERT INTO `quran` VALUES (10566,2,40,48,'<p><span>[a]</span><span> Dan, ketika mereka akan berbantah satu sama lain dalam Api, orang-orang yang lemah akan berkata kepada orang-orang yang menyombongkan diri, “Sesungguhnya kami pengikut kamu ; </span><span>[b]</span><span> maka dapatkah kamu melepaskan dari kami sebagian </span><span>siksaan </span><span> dari Api ? “</span></p><p><span>[a]</span><span> 7:39 ; 14:22 ; 34:32.</span></p><p><span>[b]</span><span> 14:22.</span></p>');
INSERT INTO `quran` VALUES (10567,2,40,49,'<p><span>[c]</span><span> Orang-orang yang menyombongkan diri mereka, “Sekarang, kita semua berada di dalamnya, sesungguhnya Allah telah menghakimi di antara hamba-hamba-</span><span>Nya.</span><span>”</span></p><p><span>[c]</span><span> 7:40 ; 34:33.</span></p>');
INSERT INTO `quran` VALUES (10568,2,40,50,'<p><span>Dan berkata orang-orang yang ada dalam Api kepada para penjaga Jahannam, “Mohonkanlah kepada Tuhan-mu, supaya Dia berkenan meringankan azab bagi kami barang sehari.”</span></p>');
INSERT INTO `quran` VALUES (10569,2,40,51,'<p><span>Mereka </span><span>para penjaga itu,</span><span> berkata, </span><span>[a]</span><span> “Bukankah telah datang kepadamu rasul-rasulmu dengan Tanda-tanda nyata ?” Mereka berkata, “Ya, benar.” </span><span>Para penjaga itu</span><span> berkata, “Maka berdoalah kamu.” </span><span>[b]</span><span> Tetapi, doa orang-orang kafir itu sia-sia belaka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2610">2610</a><span>] </span></p><p><span>[a]</span><span> 23:106 ; 39:72 ; 67:9 </span><span>–</span><span> 10.</span></p><p><span>[b]</span><span> 13:15.</span></p>');
INSERT INTO `quran` VALUES (10570,2,40,52,'<p><span>R. 6</span></p><p><span>[c]</span><span> Sesungguhnya, tentu Kami akan menolong para rasul Kami dan orang-orang yang beriman, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2611">2611</a><span>]</span><span> di dalam kehidupan dunia dan para hari ketika saksi-saksi akan berdiri.</span></p><p><span>[c]</span><span> 10:104 ; 30:48 ; 58:22.</span></p>');
INSERT INTO `quran` VALUES (10571,2,40,53,'<p><span>Pada hari itu tidak akan bermanfaat bagi orang-orang aniaya alasan mereka, dan </span><span>[d]</span><span> bagi mereka ada laknat dan ada tempat tinggal yang buruk.</span></p><p><span>[d]</span><span> 13:26</span></p>');
INSERT INTO `quran` VALUES (10572,2,40,54,'<p><span>[a]</span><span> Dan sungguh, Kami telah memberi kepada Musa petunjuk, dan Kami telah mewariskan kepada Bani Israil Kitab </span><span>Taurat.</span></p><p><span>[a]</span><span> 2:88 ; 17:3 ; 23:50 ; 32:24.</span></p>');
INSERT INTO `quran` VALUES (10573,2,40,55,'<p><span>Suatu petunjuk dan nasihat bagi orang-orang yang berakal.</span></p>');
INSERT INTO `quran` VALUES (10574,2,40,56,'<p><span>[b]</span><span> Maka bersabarlah, sesungguhnya, janji Allah itu benar, dan mintalah ampunan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2612">2612</a><span>]</span><span> </span><span>bagi mereka </span><span> atas dosa </span><span>yang diperbuat mereka </span><span> terhadap engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2612a">2612a</a><span>]</span><span> dan sanjunglah dengan pujian Tuhan engkau pada waktu petang dan pagi.</span></p><p><span>[b]</span><span> 30:61.</span></p>');
INSERT INTO `quran` VALUES (10575,2,40,57,'<p><span>Sesungguhnya, </span><span>[c] </span><span>orang-orang yang berbantah tentang Tanda-tanda Allah tanpa suatu dalil yang telah datang kepada mereka, tiada sesuatu dalam dada mereka kecuali </span><span>angan-angan </span><span> besar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2613">2613</a><span>]</span><span> yang mereka tidak akan pernah mencapainya. Maka mohonlah perlindungan kepada Allah. Sesungguhnya, Dia Maha Mendengar, Maha Melihat.</span></p><p><span>[c]</span><span> 40:36.</span></p>');
INSERT INTO `quran` VALUES (10576,2,40,58,'<p><span>Pastilah, pencipataan seluruh langit dan bumi itu lebih besar daripada penciptaan manusia ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2614">2614</a><span>]</span><span> tetapi kebanyakan manusia tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (10577,2,40,59,'<p><span>[a]</span><span> Dan tidak sama orang buta dan orang melihat ; dan tidak </span><span>pula </span><span> orang-orang yang beriman dan beramal shaleh </span><span>sama </span><span> dengan orang-orang yang berbuat keburukan. Sedikit pun kamu tidak mendapat nasihat.</span></p><p><span>[a]</span><span> 13:17 ; 35:20 ; 39:10.</span></p>');
INSERT INTO `quran` VALUES (10578,2,40,60,'<p><span>[b] </span><span>Sesungguhnya saat </span><span>kehancuran</span><span> itu pasti akan datang ; tidak ada keraguan di dalamnya, akan tetapi kebanyakan manusia tidak beriman.</span></p><p><span>[b]</span><span> 15:86 ; 20:16.</span></p>');
INSERT INTO `quran` VALUES (10579,2,40,61,'<p><span>[c]</span><span> Dan Tuhan-mu berfirman, “Berdoalah kepada-Ku ; Aku akan mengabulkan bagi kamu. Akan tetapi orang-orang yang menyombongkan diri untuk beribadah kepada-Ku, mereka niscaya akan masuk ke dalam Jahannam dalam keadaan terhina.”</span></p><p><span>[c]</span><span> 2:187 ; 6:42 ; 25:78 ; 27:63.</span></p>');
INSERT INTO `quran` VALUES (10580,2,40,62,'<p><span>R. 7</span></p><p><span>[a]</span><span> Allah yang menjadikan bagi kamu malam, supaya kamu beristirahat di dalamnya, dan siang untuk melihat. Sesungguhnya Allah mempunyai karunai atas manusia, tetapi kebanyakan manusia tidak bersyukur.</span></p><p><span>[a]</span><span> 17:13 ; 41:38.</span></p>');
INSERT INTO `quran` VALUES (10581,2,40,63,'<p><span>[b]</span><span> Demikianlah Allah, Tuhan-mu. Pencipta segala sesuatu, tiada Tuhan selain Dia, maka kemanakah kamu dipalingkan ? </span></p><p><span>[b]</span><span> 6:103.</span></p>');
INSERT INTO `quran` VALUES (10582,2,40,64,'<p><span>Demikianlah pulalah telah dipalingkan orang-orang yang mengingkari Tanda-tanda Allah.</span></p>');
INSERT INTO `quran` VALUES (10583,2,40,65,'<p><span>Allah adalah Dia, Yang telah menjadikan bagimu bumi sebagai tempat tinggal, dan langit sebagai bangunan </span><span>untuk perlindungan,</span><span> </span><span>[c]</span><span> dan telah memberi kamu bentuk dan menjadikan bentukmu sempurna, dan memberi kamu rezeki dengan barang-barang baik. Demikianlah Allah, Tuhan-mu. Maha Berberkatlah Allah Tuhan semesta alam.</span></p><p><span>[c]</span><span> 7:12 ; 23:15 ; 64:4.</span></p>');
INSERT INTO `quran` VALUES (10584,2,40,66,'<p><span>Dia-lah Yang Hidup, tiada Tuhan selain Dia. </span><span>[a]</span><span> Maka berdoalah kepada-Nya dengan mengikhlaskan ketaatan kepada-Nya Segala puji bagi Allah, Tuhan semesta alam.</span></p><p><span>[a]</span><span> 39:12 ; 98:6.</span></p>');
INSERT INTO `quran` VALUES (10585,2,40,67,'<p><span>[b]</span><span> Katakanlah. “Sesungguhnya aku telah dilarang menyembah mereka yang kamu seru selain Allah, sejak telah datang kepadaku Tanda-tanda yang nyata dari Tuhan-ku dan aku telah diperintahkan menyerahkan diri hanya kepada Tuhan semesta alam.”</span></p><p><span>[b]</span><span> 6:57 ; 39:65.</span></p>');
INSERT INTO `quran` VALUES (10586,2,40,68,'<p><span>Dia-lah </span><span>[c]</span><span> Yang telah menciptakan kamu dari tanah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2615">2615</a><span>]</span><span> kemudian dari air mani, kemudian dari segumpal darah, kemudian Dia mengeluarkan kamu sebagai seorang anak, kemudian supaya kamu mencapai kedewasaanmu, kemudian kamu menjadi tua, dan sebagian dari antara mu ada yang diwafatkan sebelum itu, dan supaya kamu sampai kepada batas waktu yang telah ditetapkan dan supaya kamu menggunakan akal.</span></p><p><span>[c]</span><span> 22:6 ; 23:13 </span><span>–</span><span> 15 ; 35:12.</span></p>');
INSERT INTO `quran` VALUES (10587,2,40,69,'<p><span>[d]</span><span> Dia-lah Yang menghidupkan dan Yang mematikan, </span><span>[e]</span><span> dan apabila Dia memutuskan suatu hal, maka Dia berfirman tentang hal itu, “Jadilah !” Maka jadilah itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2616">2616</a><span>]</span></p><p><span>[d]</span><span> 2:29 ; 22:67 ; 30:41.</span></p><p><span>[e]</span><span> 2:118 ; 3:48 ; 16:41 ; 36:83.</span></p>');
INSERT INTO `quran` VALUES (10588,2,40,70,'<p><span>R. 8</span></p><p><span>[a]</span><span> Tidaklah engkau melihat orang-ornag yang bertengkar tentang Tanda-tanda Allah ? Kemanakah mereka dipalingkan !</span></p><p><span>[a]</span><span> 13:14 ; 22:9 ; 31:21.</span></p>');
INSERT INTO `quran` VALUES (10589,2,40,71,'<p><span>Mereka yang mendustakan Kitab dan apa-apa yang Kami kirim bersama rasul-rasul Kami, maka segera mereka akan mengetahui </span><span>akibatnya,</span></p>');
INSERT INTO `quran` VALUES (10590,2,40,72,'<p><span>[b] </span><span>Tatkala belenggu melingkari leher mereka, dan </span><span>begitu pula </span><span> rantai-rantai. Mereka akan diseret.</span></p><p><span>[b]</span><span> 36:9 ; 76:5.</span></p>');
INSERT INTO `quran` VALUES (10591,2,40,73,'<p><span>[c]</span><span> Ke dalam air mendidih, kemudian di dalam Api mereka akan dibakar.</span></p><p><span>[c]</span><span> 55:45 ; 78:26.</span></p>');
INSERT INTO `quran` VALUES (10592,2,40,74,'<p><span>Kemudian akan dikatakan kepada mereka, “Dimanakah </span><span>tuhan-tuhan</span><span> yang dahulu kamu persekutukan ?</span></p>');
INSERT INTO `quran` VALUES (10593,2,40,75,'<p><span>“Selain Allah ? “ Mereka berkata, </span><span>[d]</span><span> “Mereka itu telah lenyap dari kami. Bahkan bukan itu saja tidak pernah sebelum itu kami memohon kepada sesuatu </span><span>selain Allah.</span><span>” Demikianlah Allah akan menyesatkan orang-orang kafir.</span></p><p><span>[d]</span><span> 41:49.</span></p>');
INSERT INTO `quran` VALUES (10594,2,40,76,'<p><span>Hal demikian adalah disebabkan kamu dahulu bersuka-ria di bumi tanpa hak, dan oleh karena kamu berlaku sombong.</span></p>');
INSERT INTO `quran` VALUES (10595,2,40,77,'<p><span>[a]</span><span> Masukilah pintu-pintu Jahannam untuk tinggal lama di dalamnya, maka sangat buruklah tempat tinggal orang-orang sombong.</span></p><p><span>[a]</span><span> 16:30 ; 39:73.</span></p>');
INSERT INTO `quran` VALUES (10596,2,40,78,'<p><span>Maka bersabarlah engkau. Sesungguhnya, janji Allah pasti sempurna, </span><span>[b]</span><span> maka jika Kami memperlihatkan kepada engkau sebagaian yang Kami janjikan kepada mereka atau Kami mewafatkan engkau, maka kepada Kami mereka akan dikembalikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2617">2617</a><span>]</span></p><p><span>[b]</span><span> 10:47 ; 13:41 ; 43:43.</span></p>');
INSERT INTO `quran` VALUES (10597,2,40,79,'<p><span>Dan sesungguhnya Kami telah mengutus rasul-rasul sebelum engkau, </span><span>[c]</span><span> dari antara mereka ada sebagian yang Kami ceriterakan kepada engkau, dan dari antara mereka ada sebagian yang tidak Kami ceriterakan kepada engkau. Dan </span><span>[d]</span><span> tidak mungkin bagi seorang rasul membawa suatu Tanda, kecuali seizin Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2618">2618</a><span>]</span><span> tetapi, apabila perintah Allah datang, diputuskannyalah dengan benar, dan merugilah ketika itu orang-orang yang berpegang kepada kebatilan.</span></p><p><span>[c]</span><span> 4:165.</span></p><p><span>[d]</span><span> 13:39 ; 14:12.</span></p>');
INSERT INTO `quran` VALUES (10598,2,40,80,'<p><span>R. 9</span></p><p><span>[a]</span><span> Allah Yang telah menjadikan bagimu binatang berkaki empat, supaya kamu menunggangi sebagian darinya dan sebagiannya kamu makan.</span></p><p><span>[a]</span><span> 6:143 ; 16:6 ; 23:22 ; 36:72 </span><span>–</span><span> 74.</span></p>');
INSERT INTO `quran` VALUES (10599,2,40,81,'<p><span>[b]</span><span> Dan bagi kamu di dalamnya ada manfaat-manfaat, dan supaya dengan perantaraannya kamu dapat memenuhi hasrat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2619">2619</a><span>]</span><span> yang ada di dalam dadamu. Dan di atasnya dan di atas bahtera-bahtera kamu diangkut.</span></p><p><span>[b]</span><span> 16:6 </span><span>–</span><span> 8 ; 23:22 </span><span>–</span><span> 23 ; 36:73 </span><span>–</span><span> 74.</span></p>');
INSERT INTO `quran` VALUES (10600,2,40,82,'<p><span>Dan Dia memperlihatkan kepadamu Tanda-tanda-Nya, kemudian, manakah Tanda-tanda Allah akan kamu ingkari ?</span></p>');
INSERT INTO `quran` VALUES (10601,2,40,83,'<p><span>apakah mereka </span><span>[c]</span><span> tidak bepergian di bumi supaya mereka dapat melihat bagaimana kesudahan orang-orang sebelum mereka ? Mereka itu lebih banyak daripada mereka, dan lebih hebat peninggalan-peninggalan mereka di bumi, maka tidak berguna kepada mereka apa-apa yang telah mereka usahakan.</span></p><p><span>[c]</span><span> 16:37 ; 27:70 ; 30:43.</span></p>');
INSERT INTO `quran` VALUES (10602,2,40,84,'<p><span>Maka ketika datang kepada mereka rasul-rasul mereka dengan Tanda-tanda yang nyata, mereka bersuka ria dengan apa yang ada pada mereka dari ilmu, dan mengepung mereka dengan apa yang dahulu mereka perolok-olokkan.</span></p>');
INSERT INTO `quran` VALUES (10603,2,40,85,'<p><span>[a]</span><span> Kemudian ketika mereka menyaksikan azab Kami, mereka berkata, “Kami beriman kepada Allah Yang Esa dan kami mengingkari apa yang pernah kami persekutukan dengan Dia.”</span></p><p><span>[a]</span><span> 10:52, 91.</span></p>');
INSERT INTO `quran` VALUES (10604,2,40,86,'<p><span>[b]</span><span> Maka tidak bermanfaat bagi mereka iman mereka apabila mereka melihat azab Kami. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2620">2620</a><span>]</span><span> Itulah sunnah Allah yang telah berlalu terhadap hamba-hamba-Nya, dan rugilah disana orang-orang kafir.</span></p><p><span>[b]</span><span> 10:92.</span></p>');
INSERT INTO `quran` VALUES (10605,2,41,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10606,2,41,2,'<p><span>[b]</span><span> Aku, Allah Yang Maha Mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2620a">2620a</a><span>]</span></p><p><span>[b]</span><span> 40:2 ; 42:2 ; 43:2 ; 44:2 ; 45:2 ; 46:2</span></p>');
INSERT INTO `quran` VALUES (10607,2,41,3,'<p><span>[c]</span><span> </span><span>Alquran diturunkan dari Tuhan, Maha Pemurah, Maha Penyayang.</span></p><p><span>[c]</span><span> 32:3 ; 40:3 ; 45:3 ; 46:3.</span></p>');
INSERT INTO `quran` VALUES (10608,2,41,4,'<p><span>[d]</span><span> </span><span>Sebuah Kitab yang telah dijelaskan Ayat-ayatnya, yang dibaca berulang-ulang, berbahasa Arab bagi kaum yang mengetahui,</span></p><p><span>[d]</span><span> 11:2.</span></p>');
INSERT INTO `quran` VALUES (10609,2,41,5,'<p><span>[e]</span><span> </span><span>Pemberi khabar suka dan pemberi peringatan. Tetapi kebanyakan mereka yang berpaling dan mereka tidak mendengar.</span></p><p><span>[e]</span><span> 5:20 ; 25:57 ; 35:25 ; 48:9.</span></p>');
INSERT INTO `quran` VALUES (10610,2,41,6,'<p><span>[f]</span><span> </span><span>Dan mereka berkata, “Hati kami dalam tutupan terhadap apa yang kamu panggil kami kepadanya, dan dalam telinga kamu ada sumbatan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2621">2621</a><span>]</span><span> dan di antara kami dan engkau ada pembatas. Maka berbuatlah engkau, sesungguhnya kami pun berbuat.”</span></p><p><span>[f]</span><span> 6:26 ; 17:47 ; 18:58.</span></p>');
INSERT INTO `quran` VALUES (10611,2,41,7,'<p><span>[a]</span><span> </span><span>katakanlah, “Aku hanyalah seorang manusia seperti kamu diwahyukan kepadaku, bahwa Tuhan kamu adalah Tuhan Yang Maha Esa; maka tetaplah di jalan yang lurus kepada-Nya.” Dan mintalah ampun kepada-Nya.” Dan celakalah bagi orang-orang musyrik,</span></p><p><span>[a</span><span>] 13:12 ; 18:111 ; 21:109.</span></p>');
INSERT INTO `quran` VALUES (10612,2,41,8,'<p><span>Orang-orang yang tidak membayar zakat dan mereka itulah orang yang ingkar kepada akhirat.</span></p>');
INSERT INTO `quran` VALUES (10613,2,41,9,'<p><span>[b]</span><span> </span><span>Sesungguhnya orang-orang yang beriman dan beramal shaleh. bagi mereka ganjaran yang tidak putus-putusnya.</span></p><p><span>[b]</span><span> 11:12 ; 84:26 ; 95:7.</span></p>');
INSERT INTO `quran` VALUES (10614,2,41,10,'<p><span>R. 2</span></p><p><span>Katakanlah, “Apakah kamu sungguh ingkar kepada Dzat Yang menciptakan bumi dalam dua hari ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2622">2622</a><span>]</span><span> Dan, kamu menjadikan bagi-nya sekutu-sekutu ?” Itulah Tuhan seluruh alam.</span></p>');
INSERT INTO `quran` VALUES (10615,2,41,11,'<p><span>[c]</span><span> </span><span>Dan Dia menjadikan di dalamnya gunung-gunung di atasnya, dan memberkati di dalamnya, dan Dia menentukan di dalamnya, kadar makanan-makanannya dalam empat hari. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2623">2623</a><span>]</span><span> Sama rata bagi semua peminta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2624">2624</a><span>]</span></p><p><span>[c]</span><span> 13:4 ; 15:20 ; 77:28.</span></p>');
INSERT INTO `quran` VALUES (10616,2,41,12,'<p><span>Kemudian Dia mengarahkan perhatian ke langit, ketika itu </span><span>masih </span><span>merupakan asap, lalu Dia berfirman kepadanya dan kepada bumi, “datanglah kamu berdua dengan rela ataupun terpaksa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2625">2625</a><span>]</span><span> Keduanya menjawab, ”Kami berdua datang dengan rela.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2626">2626</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10617,2,41,13,'<p><span>Maka Dia menciptakanya tujuh langit dalam dua hari, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2627">2627</a><span>]</span><span> dan Dia mewahyukan kepada tiap-tiap langit tugasnya. </span><span>[a]</span><span> </span><span>Dan, Kami menghiasi langit dari Yang Maha Perkasa, Maha Mengetahui.</span></p><p><span>[a]</span><span> 15:17 ; 37:7 ; 67:6.</span></p>');
INSERT INTO `quran` VALUES (10618,2,41,14,'<p><span>Tetapi sekiranya mereka itu berpaling, maka katakanlah, </span><span>[b]</span><span> </span><span>”Aku memperingatkan kamu tentang suatu azab yang membinasakan seperti azab kaum </span><span>‘</span><span>Ad dan Tsamud.”</span></p><p><span>[b]</span><span> 40:31-32.</span></p>');
INSERT INTO `quran` VALUES (10619,2,41,15,'<p><span>Ketika datang kepada mereka rasul-rasul dari depan mereka dan dari belakang mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2627a">2627a</a><span>]</span><span> “Janganlah kamu menyembah selain Allah.” Mereka berkata. “Sekiranya Tuhan kami menghendaki, tentulah Dia telah menurunkan malaikat-malaikat. Maka Sesungguhnya kami ingkar dengan apa yang kamu diutus.”</span></p>');
INSERT INTO `quran` VALUES (10620,2,41,16,'<p><span>Adapun tentang kaum </span><span>‘</span><span>Ad, mereka berlaku sombong di bumi tanpa hak, dan mereka berkata, “Siapakah lebih hebat dari kami dalam kekuatan?” Apakah mereka tidak melihat bahwa Allah, Yang menciptakan mereka, Dia lebih hebat dari mereka dalam kekuatan ? Tetapi mereka menolak Tanda-tanda Kami.</span></p>');
INSERT INTO `quran` VALUES (10621,2,41,17,'<p><span>Maka Kami mengirimkan kepada mereka angin sangat kencang dalam beberapa hari yang naas itu, supaya Kami membuat mereka merasakan azab kehinaan dalam kehidupan di dunia. Dan tentukanlah azab alam akhirat itu lebih hina, dan mereka tidak akan ditolong.</span></p>');
INSERT INTO `quran` VALUES (10622,2,41,18,'<p><span>Adapun tentang kaum Tsamud telah Kami beri mereka petunjuk, tetapi mereka lebih menyukai kebutaan daripada petunjuk; maka menimpa mereka malapetaka azab yang hina, disebabkan apa yang telah mereka usahakan.</span></p>');
INSERT INTO `quran` VALUES (10623,2,41,19,'<p><span>Dan Kami selamatkan orang-orang yang beriman dan yang bertakwa.</span></p>');
INSERT INTO `quran` VALUES (10624,2,41,20,'<p><span>R. 3</span></p><p><span>Dan </span><span>ingatlah</span><span> hari ketika dihimpun </span><span>[a]</span><span> </span><span>musuh-musuh Allah ke pada Api, dan mereka akan dibagi dalam kelompok-kelompok.</span></p><p><span>[a]</span><span> 27:84</span></p>');
INSERT INTO `quran` VALUES (10625,2,41,21,'<p><span>Hingga, apabila mereka sampai kepadanya, menjadi saksi atas mereka </span><span>[b]</span><span> </span><span>telinga mereka dan mata mereka dan kulit mereka mengenai apa yang telah mereka kerjakan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2628">2628</a><span>]</span></p><p><span>[b] </span><span>24:25 ; 36:66.</span></p>');
INSERT INTO `quran` VALUES (10626,2,41,22,'<p><span>Dan mereka berkata kepada kulit mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2629">2629</a><span>]</span><span> “Mengapa kamu telah memberikan kesaksian terhadap kami?” Kulit mereka akan menjawab, “Allah-lah Yang telah membuat kami berbicara seperti Dia telah membuat berbicara segala sesuatu, dan Dia-lah Yang pertama kali telah menciptakan kamu dan kepada Dia-lah kamu dikembalikan.</span></p>');
INSERT INTO `quran` VALUES (10627,2,41,23,'<p><span>“Dan, kamu tidak dapat menutupi </span><span>aib </span><span>bahwa, telingamu dan matamu dan kulitmu menjadi saksi melawan kamu, tetapi kamu menyangka, bahwa Allah tidak mengetahui kebanyakan dari apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (10628,2,41,24,'<p><span>“Dan itulah sangkaanmu yang kamu sangkakan kepada Tuhan-mu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2630">2630</a><span>]</span><span> yang telah membinasakanmu, maka kamu telah menjadi di antara orang-orang yang rugi.”</span></p>');
INSERT INTO `quran` VALUES (10629,2,41,25,'<p><span>[a]</span><span> Jika mereka bersabar, maka Api tempat tinggal bagi mereka; dan jika mereka mengemukakan alasan, maka mereka tidak termasuk orang-orang </span><span>[b]</span><span> </span><span>yang diterima alasan-alasannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2631">2631</a><span>]</span></p><p><span>[a]</span><span> 14:22</span></p><p><span>[b]</span><span> 16:85 ; 30:58</span></p>');
INSERT INTO `quran` VALUES (10630,2,41,26,'<p><span>Dan kami menetapkan bagi mereka teman-teman yang menampakkan indah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2632">2632</a><span>]</span><span> bagi mereka apa yang ada di hadapan mereka dan apa yang di belakanag mereka sempurnalah atas mereka firman Allah di antara umat-umat </span><span>[c]</span><span> </span><span>yang telah berlalu sebelum mereka dari jin dan manusia, Sesungguhnya mereka itu orang-orang rugi.</span></p><p><span>[c]</span><span> 3:138 ; 7:39 ; 13:31 ; 46:19.</span></p>');
INSERT INTO `quran` VALUES (10631,2,41,27,'<p><span>R .4</span></p><p><span>Dan berkata orang-orang yang ingkar, “Janganlah kamu mendengarkan Alquran ini, melainkan berbuat gaduhlah pada waktu </span><span>pembacaan-nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2633">2633</a><span>]</span><span> supaya kamu menang,”</span></p>');
INSERT INTO `quran` VALUES (10632,2,41,28,'<p><span>[a]</span><span> </span><span>Maka, pasti Kami akan merasakan kepada orang-orang yang ingkar azab yang keras, dan niscaya Kami akan membalas mereka seburuk-buruk yang mereka telah kerjakan.</span></p><p><span>[a] </span><span>27:91 ; 32:22.</span></p>');
INSERT INTO `quran` VALUES (10633,2,41,29,'<p><span>Demikianlah balasan bagi musuh-musuh Allah, yakni Api, bagi mereka di dalamnya ada tempat tinggal yang lama. Sebagai balasan karena mereka menolak Tanda-tanda Kami.</span></p>');
INSERT INTO `quran` VALUES (10634,2,41,30,'<p><span>Dan berkata orang-orang yang ingkar, “Hai, Tuhan kami, perlihatkanlah kepada kami </span><span>[b</span><span>] </span><span>orang-orang yang telah menyesatkan kami di antara jin dan manusia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2634">2634</a><span>]</span><span> supaya kami menjadikan keduanya di bawah kaki kami supaya mereka menjadi orang terhina,”</span></p><p><span>[b]</span><span> 33:69 ; 38:62.</span></p>');
INSERT INTO `quran` VALUES (10635,2,41,31,'<p><span>[c]</span><span> </span><span>Sesungguhnya orang-orang yang berkata, “Tuhan kami Allah,” kemudian mereka istiqamah, turun kepada mereka istiqamah, turun kepada mereka malaikat-malaikat, “Janganlah kamu takut, dan jangan </span><span>pula</span><span> bersedih; dan berilah khabar suka tentang surga yang telah di janjikan kepadamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2635">2635</a><span>]</span></p><p><span>[c]</span><span> 21:104 ; 46:14.</span></p>');
INSERT INTO `quran` VALUES (10636,2,41,32,'<p><span>“Kami adalah teman-temanmu di dalam kehidupan dunia dan di akhirat. Dan bagi </span><span>[a]</span><span> </span><span>kamu di dalamnya apa yang diinginkan diri kamu dan bagi kamu di dalamnya apa yang kamu minta.</span></p><p><span>[a]</span><span> 25:17.</span></p>');
INSERT INTO `quran` VALUES (10637,2,41,33,'<p><span>“Sebagai hidangan dari </span><span>Tuhan</span><span> Yang Maya Pengampun, Maha Penyayang.”</span></p>');
INSERT INTO `quran` VALUES (10638,2,41,34,'<p><span>R. 5</span></p><p><span>Dan, siapakah yang lebih baik pembicaraanya dari orang yang mengajak </span><span>manusia</span><span> kepada Allah dan beramal shaleh serta berkata, “Sesungguhnya aku termasuk orang-orang yang berserah diri.”</span></p>');
INSERT INTO `quran` VALUES (10639,2,41,35,'<p><span>Dan tidaklah sama kebaikan dan keburukan. </span><span>[b]</span><span> </span><span>Tolaklah </span><span>keburukan</span><span> itu dengan cara yang sebaik-baiknya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2636">2636</a><span>]</span><span> maka tiba-tiba ia, yang di antara engkau dan dirinya ada permusuhan, akan menjadi seperti seorang sahabat yang setia.</span></p><p><span>[b]</span><span> 13:23 ; 28:55.</span></p>');
INSERT INTO `quran` VALUES (10640,2,41,36,'<p><span>Dan, tiada yang dianugerahi </span><span>taufik</span><span> itu selain orang-orang yang sabar, dan tiada yang dianugerahi </span><span>taufik</span><span> itu selain orang yang mempunyai bagian besar dalam kebaikan.</span></p>');
INSERT INTO `quran` VALUES (10641,2,41,37,'<p><span>[c]</span><span> </span><span>Dan, jika suatu godaan dari syaitan menggoda engkau, maka mohonlah perlindungan kepada Allah. Sesungguhnya Dia adalah Yang maha Mendengar, Maha Mengetahui.</span></p><p><span>[c]</span><span> 12:101</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10642,2,41,38,'<p><span>[a]</span><span> </span><span>Dan, dari antara Tanda-tanda-Nya ialah malam dan siang, dan matahari, dan bulan. Janganlah kamu bersujud kepada matahari dan janganlah pula kepada bulan, dan berseujudlah kepada Allah, Yang telah menciptakan mereka, jika kamu hanya kepada-Nya menyembah.</span></p><p><span>[a]</span><span> 17:13 ; 40:62.</span></p>');
INSERT INTO `quran` VALUES (10643,2,41,39,'<p><span>Tetapi, jika mereka berlaku sombong, sedang orang-orang yang berada disisi Tuhan engkau bertasbih kepada-Nya pada malam dan siang, dan mereka tidak pernah merasa lelah.</span></p>');
INSERT INTO `quran` VALUES (10644,2,41,40,'<p><span>Dan, dari antara Tanda-tanda-Nya ialah engkau melihat bumi menjadi gersang, </span><span>[b]</span><span> </span><span>tetapi apabila Kami menurunkan air ke atasnya, bergerak dan berkembanglah ia. Sesungguhnya Dia, Yang telah menghidupkan </span><span>juga</span><span> yang mati. Sesungguhnya Dia berkuasa atas segala sesuatu.</span></p><p><span>[b]</span><span> 22:6 ; 30:51 ; 35:28.</span></p>');
INSERT INTO `quran` VALUES (10645,2,41,41,'<p><span>Sesungguhnya, orang-orang yang memutar-balikkan di antara Ayat-ayat Kami, mereka tidak tersembunyi dari Kami. Apakah </span><span>[c]</span><span> </span><span>orang yang dilemparkan ke dalam Api itu lebih baik, atau orang yang datang </span><span>kepada Kami </span><span> dengan aman pada Hari Kiamat ? Berbuatlah apa yang kamu kehendaki, Sesungguhnya apa yang kamu lakukan, Dia Maha Melihat.</span></p><p><span>[c]</span><span> 38:29.</span></p>');
INSERT INTO `quran` VALUES (10646,2,41,42,'<p><span>Sesungguhnya orang-orang yang ingkar kepada Zikir, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2637">2637</a><span>]</span><span> </span><span>Alquran</span><span>, ketika itu datang kepada mereka, dan Sesungguhnya itu adalah Kitab yang mulia.</span></p>');
INSERT INTO `quran` VALUES (10647,2,41,43,'<p><span>[a</span><span>] </span><span>Kebatilan tidak dapat mendekatinya, baik dari depannya maupun dari belakangnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2638">2638</a><span>]</span><span> Diturunkan dari </span><span>Tuhan</span><span> Yang Maha Bijaksana, Maha Terpuji.</span></p><p><span>[a]</span><span> 15:11</span></p>');
INSERT INTO `quran` VALUES (10648,2,41,44,'<p><span>Tiada sesuatu yang dikatakan terhadap engkau melainkan apa yang telah dikatakan terhadap rasul-rasul sebelum engkau. Sesungguhnya </span><span>[b</span><span>] </span><span>Tuhan engkau Yang Empunya pengampunan dan juga Yang Empunya azab yang pedih.</span></p><p><span>[b]</span><span> 13:7 ; 53:33</span></p>');
INSERT INTO `quran` VALUES (10649,2,41,45,'<p><span>Dan, sekiranya Kami menjadikannya </span><span>[c]</span><span> </span><span>Alquran dalam bahasa asing, niscaya mereka akan berkata, “Mengapa tidak dijelaskan Ayat-ayatnya? Apakah </span><span>patut Alquran</span><span> dalam bahasa asing sedang rasul orang Arab ?” Katakanlah, “Itu bagi orang-orang yang beriman sebagai petunjuk dan penyembuh.” Dan orang-orang yang tidak beriman, dalam telinga mereka ada sumbatan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2638A">2638A</a><span>]</span><span> dan Alquran itu kabur bagi mereka. Mereka akan dipanggil dari suatu tempat yang amat jauh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2639">2639</a><span>]</span></p><p><span>[c]</span><span> 16:104 ; 26:196 ; 46:13</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10650,2,41,46,'<p><span>R. 6</span></p><p><span>Dan, Sesungguhnya kami telah memberikan kepada Musa Kitab, maka timbullah perselisihan di dalamnya. </span><span>[a]</span><span> </span><span>Dan sekiranya karena tiada firman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2639A">2639A</a><span>]</span><span> yang terdahulu dari Tuhan engkau, tentukanlah </span><span>perkara</span><span> itu telah diputuskan di antara mereka. Dan Sesungguhnya mereka pasti dalam keraguan yang menggelisahkan mengenainya.</span></p><p><span>[a]</span><span> 10:20 ; 11:111 ; 20:130 ; 42:15.</span></p>');
INSERT INTO `quran` VALUES (10651,2,41,47,'<p><span>[b]</span><span> </span><span>Barangsiapa beramal shaleh, maka itu untuk </span><span>manfaat </span><span> dirinya sendiri; dan barangsiapa berbuat keburukan, maka </span><span>azabnya</span><span> akan </span><span>menimpa</span><span> atas dirinya. Dan, Tuhan engkau sekali-kali tidak aniaya terhadap hamba-hamba-Nya.</span></p><p><span>[b]</span><span> 3:183 ; 8:52 ; 17:8 ; 22:111</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10652,2,41,48,'<p><span>JUZ XXV</span></p><p><span>Kepada Dia-lah dikembalikan ilmu Kiamat itu. Dan tidak ada buah-buahan yang keluar dari kelopaknya dan </span><span>[a]</span><span> </span><span>tidak ada pula perempuan mengandung dan tidak pula melahirkan, melainkan dengan sepengetahuan-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2640">2640</a><span>]</span><span> Dan pada hari ketika Dia akan berseru kepada mereka </span><span>seraya berfirman,</span><span> </span><span>[b]</span><span> </span><span>”Dimanakah sekutu-sekutu-Ku ?” Mereka berkata, “Kami memberitahukan kepada Engkau, tiada seorang pun di antara kami menjadi saksi atas itu.”</span></p><p><span>[a]</span><span> 13:9 ; 35:12.</span></p><p><span>[b]</span><span> 18:53 ; 28:65.</span></p>');
INSERT INTO `quran` VALUES (10653,2,41,49,'<p><span>[c]</span><span> </span><span>Dan, lenyaplah dari mereka apa yang mereka seru dahulu dan mereka akan mengetahui dengan yakni bahwa bagi mereka tidak ada tempat melepaskan diri.</span></p><p><span>[c]</span><span> 40:75.</span></p>');
INSERT INTO `quran` VALUES (10654,2,41,50,'<p><span>[d] </span><span>Manusia tidak jemu-jemu mendoa untuk kebaikan; tetapi jika keburukan menyentuhnya, ia berputus asa </span><span>dan</span><span> melepaskan </span><span>segala</span><span> harapan.</span></p><p><span>[d]</span><span> 11:10-11 : 17:84.</span></p>');
INSERT INTO `quran` VALUES (10655,2,41,51,'<p><span>[e]</span><span> </span><span>Dan, jika Kami membuat dia merasakan rahmat dari Kami sesudah suatu kemalangan menimpanya, tentulah ia akan berkata, “ini </span><span>memang</span><span> hak bagiku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2641">2641</a><span>]</span><span> dan aku tidak mengira Saat ini akan jadi datang. Dan sekiranya aku dikembalikan juga kepada Tuhan-ku, niscaya bagiku di sisi-Nya ada yang sebaik-baiknya.” Kemudian tentu Kami akan memberitahukan kepada orang-orang ingkar tentang apa yang mereka telah kerjakan, dan niscaya Kami akan membuat mereka merasakan azab yang keras.</span></p><p><span>[e]</span><span> 10:22 ; 11:11.</span></p>');
INSERT INTO `quran` VALUES (10656,2,41,52,'<p><span>[a]</span><span> </span><span>Dan, apabila Kami memberikan nikmat kepada manusia, ia berpaling dan menjauhkan diri; tetapi apabila malapetaka menimpanya, tiba-tiba ia </span><span>mulai</span><span> berdoa panjang-panjang.</span></p><p><span>[a]</span><span> 11:10 ; 17:84.</span></p>');
INSERT INTO `quran` VALUES (10657,2,41,53,'<p><span>Katakanlah, “Bagaimana pendapatmu, jika Alquran ini memang dari Allah, kemudian kamu mengingkarinya; siapakah yang lebih sesat dari orang yang telah melantur jauh dari kebenaran ?”</span></p>');
INSERT INTO `quran` VALUES (10658,2,41,54,'<p><span>[b]</span><span> </span><span>Segera akan Kami perlihatkan kepada mereka Tanda-tanda Kami di wilayah-wilayah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2642">2642</a><span>] </span><span>dunia</span><span> ini dan dalam diri mereka sendiri, sehingga akan nyata kepada mereka, bahwa </span><span>Alquran</span><span> itu benar. Tidak cukuplah Tuhan engkau bahwa Dia sebagai Saksi atas segala sesuatu?</span></p><p><span>[b]</span><span> 51:21-22</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10659,2,41,55,'<p><span>Camkanlah, Sesungguhnya mereka itu ada dalam keragu-raguan tentang pertemuan dengan Tuhan mereka. Sadarilah, Sesungguhnya Dia meliputi segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (10660,2,42,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10661,2,42,2,'<p><span>[b] </span><span>Maha Terpuji, Maha Mulia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2643">2643</a><span>]</span></p><p><span>[b]</span><span> 41:2 ; 43:2 ; 45:2 ; 46:2.</span></p>');
INSERT INTO `quran` VALUES (10662,2,42,3,'<p><span>Maha Mengetahui, Maha Mendengar, Maha Kuasa</span><span>.[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2643A">2643A</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10663,2,42,4,'<p><span>Demikianlah Dia mewahyukan kepada engkau dan kepada orang-orang sebelum engkau, Allah Yang Maha Perkasa, Maha Bijaksana</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10664,2,42,5,'<p><span>[c] </span><span>Kepunyaan Dia apa yang ada di seluruh langit dan apa yang ada di bumi. Dan Dia-lah Yang Mahaluhur, Mahabesar.</span></p><p><span>[c]</span><span> 16:53 ; 22:65 ; 31:27.</span></p>');
INSERT INTO `quran` VALUES (10665,2,42,6,'<p><span>Hampir saja seluruh langit pecah dari atas mereka; </span><span>[d] </span><span>dan para malaikat bertasbih dengan pujian Tuhan mereka dan memohonkan ampunan bagi mereka di bumi</span><span>. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2643b">2643b</a><span>]</span><span> </span><span>Ketahuilah, Sesungguhnya Allah itu Dzat Yang Maha Pengampun, Maha Penyayang.</span></p><p><span>[d] 13:14</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10666,2,42,7,'<p><span>Dan orang-orang yang mengambil perlindung-pelindung selain Dia, Allah Pengawas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2644">2644</a><span>]</span><span> atas mereka,</span><span> </span><span>[a] </span><span>dan engkau bukanlah penjaga atas mereka.</span></p><p><span>[a</span><span>] 6:108 ; 88:23.</span></p>');
INSERT INTO `quran` VALUES (10667,2,42,8,'<p><span>Dan, demikianlah</span><span> </span><span>[b] </span><span>Kami telah mewahyukan kepada engkau Alquran dalam bahasa Arab, </span><span>[c]</span><span> </span><span>supaya engkau dapat memberi peringatan kepada </span><span>penduduk</span><span> Ibukota </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2645">2645</a><span>]</span><span> </span><span>dan orang-orang sekitarnya, dan engkau memberi peringatan tentang Hari Berkumpul, yang mengenainya tidak ada keraguan. Segolongan akan </span><span>berada</span><span> di surga, dan segolongan lagi dalam Api yang menyala-nyala.</span></p><p><span>[b</span><span>] 20:114 ; 39:29 ; 43:4 ;46:13</span></p><p><span>[c]</span><span> 6:93.</span></p>');
INSERT INTO `quran` VALUES (10668,2,42,9,'<p><span>[d] </span><span>Dan, sekiranya Allah menghendaki, tentulah Dia membuat mereka satu umat; tetapi Dia memasukkan siapa yang Dia kehendaki ke dalam rahmat-Nya. Dan orang-orang yang zalim tidak ada bagi mereka pelindung dan penolong</span><span>.</span></p><p><span>[d] 11:119</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10669,2,42,10,'<p><span>[a] </span><span>Apakah mereka telah mengambil pelindung-pelindung selain-Nya? Tetapi Allah, Dia-lah Pelindung, dan Dia menghidupkan yang mati, dan Dia berkuasa atas segala sesuatu</span><span>.</span></p><p><span>[a]</span><span> 13:17 ; 39:44.</span></p>');
INSERT INTO `quran` VALUES (10670,2,42,11,'<p><span>R. 2</span></p><p><span>Dan apa pun yang kamu perselisihkan di dalamnya tentang sesuatu, maka keputusannya ada pada Allah. </span><span>Katakanlah</span><span>, “Inilah Allah Tuhan-ku; kepada-Nya aku bertawakkal,dan kepada-Nya aku kembali.”</span></p>');
INSERT INTO `quran` VALUES (10671,2,42,12,'<p><span>Dia </span><span>[b] </span><span>Pencipta seluruh langit dan bumi. Dia menjadikan bagi kamu dari jenismu jodoh-jodoh; dan dari binatang ternak </span><span>pun</span><span> pasangan-pasangan, Dia mengembang biakkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2646">2646</a><span>]</span><span> kamu di dalamnya. Tidak ada semisal-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2647">2647</a><span>]</span><span> sesuatu pun dan Dia-lah Yang Maha Mendengar, Maha Melihat.</span></p><p><span>[b]</span><span> 6:15 ; 14:11 ; 35:2.</span></p>');
INSERT INTO `quran` VALUES (10672,2,42,13,'<p><span>[c] </span><span>Kepunyaan</span><span>-Nya kunci-kunci seluruh langit dan bumi, </span><span>[d] </span><span>Dia melapangkan rezeki bagi siapa yang Dia kehendaki dan Dia menyempitkan. Sesungguhnya, tentang segala sesuatu Dia Maha Mengetahui.</span></p><p><span>[c] </span><span>39:64.</span></p><p><span>[d]</span><span> 13:27 ; 29:63 ; 34:37 ; 39:53.</span></p>');
INSERT INTO `quran` VALUES (10673,2,42,14,'<p><span>Dia telah menetapkan kepadamu agama yang telah diwasiatkan-Nya kepada Nuh, dan yang telah Kami wahyukan kepada engkau, dan apa yang telah Kami wasiatkan kepada Ibrahim, Musa, dan </span><span>‘</span><span>Isa, </span><span>ialah</span><span>, “Tegakkanlah agama, dan janganlah kamu bercerai-berai di dalamnya. Sungguh berat bagi orang-orang musyrik yang engkau seru mereka kepadanya. Allah memilih bagi Dia sendiri siapa yang Dia kehendaki dan memberi petunjuk kepadanya siapa yang selalu tunduk.”</span></p>');
INSERT INTO `quran` VALUES (10674,2,42,15,'<p><span>[a]</span><span> </span><span>Dan, mereka tidak bercerai-berai, melainkan setelah datang kepada mereka ilmu, </span><span>Alquran,</span><span> karena iri hati di antara mereka. </span><span>[b]</span><span> </span><span>Dan, sekiranya tidak karena suatu ketetapan yang terdahulu dari Tuhan engkau untuk masa tertentu, </span><span>hal</span><span> itu niscaya telah diputuskan di antara mereka. Dan, Sesungguhnya orang-orang yang diwariskan Kitab sesudah mereka, pasti dalam keraguan yang menggelisahkan mengenainya.</span></p><p><span>[a]</span><span> 45:18 ; 98:5.</span></p><p><span>[b] </span><span>10:20 ; 20:130 ; 41:46.</span></p>');
INSERT INTO `quran` VALUES (10675,2,42,16,'<p><span>Maka oleh karena itu serulah </span><span>Manusia</span><span> kepada agama ini. </span><span>[a]</span><span> </span><span>Dan bersiteguhlah engkau seperti apa yang telah diperintahkan kepada engkau, </span><span>[b]</span><span> </span><span>dan janganlah engkau mengikuti hawa nafsu mereka, dan katakanlah, “Aku beriman kepada apa yang diturunkan Allah dari Kitab, dan aku diperintahkan untuk adil di antara kamu, Allah adalah Tuhan kami dan Tuhan kamu. </span><span>[c]</span><span> </span><span>Bagi kami amal kami dan bagi kamu amal kamu. Tidak ada perselisihan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2648">2648</a><span>]</span><span> di antara kami dan kamu. Allah akan menghimpunkan kita dan kepada Dia-lah tempat kembali.”</span></p><p><span>[a]</span><span> 11:113.</span></p><p><span>[b]</span><span> 5:50.</span></p><p><span>[c]</span><span> 2:140 ; 10:42.</span></p>');
INSERT INTO `quran` VALUES (10676,2,42,17,'<p><span>Dan orang-orang yang berbantah mengenai Allah setelah </span><span>orang</span><span> banyak menerima seruan-Nya, bantahan mereka ditolak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2649">2649</a><span>]</span><span> di sisi Tuhan mereka; dan atas mereka kemurkaan dan bagi mereka azab yang keras.</span></p>');
INSERT INTO `quran` VALUES (10677,2,42,18,'<p><span>Allah-lah </span><span>[d]</span><span> </span><span>Yang menurunkan Kitab ini dengan benar dan timbangan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2650">2650</a><span>]</span><span> Dan, apakah yang akan memberitahukan kepada engkau, bahwa Saat itu telah dekat?</span></p><p><span>[d]</span><span> 55:8 ; 57:26</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10678,2,42,19,'<p><span>[a]</span><span> </span><span>Ingin cepat didatangkan </span><span>Saat</span><span> itu oleh mereka yang tidak beriman kepadanya, dan orang-orang yang beriman takut </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2651">2651</a><span>]</span><span> darinya, dan mereka mengetahui bahwa Sesungguhnya itu benar. Ingatlah Sesungguhnya orang-orang yang meragukan tentang Saat itu pasti ada dalam kesesatan yang jauh.</span></p><p><span>[a] </span><span>13:7.</span></p>');
INSERT INTO `quran` VALUES (10679,2,42,20,'<p><span>[b]</span><span> </span><span>Allah Maha Lembut terhadap hamba-hamba-Nya. Dia memberi rezeki kepada siapa yang Dia kehendaki, dan Dia adalah Yang Mahakuat, Mahaperkasa.</span></p><p><span>[b]</span><span> 6:104 ; 22:64.</span></p>');
INSERT INTO `quran` VALUES (10680,2,42,21,'<p><span>R. 3</span></p><p><span>[c]</span><span> </span><span>Barangsiapa menghendaki ladang akhirat, Kami meningkatkan baginya hasil ladangnya; dan barangsiapa menghendaki ladang dunia, Kami memberikan kepadanya bagiannya, tetapi di akhirat ia tidak akan mempunyai bagian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2652">2652</a><span>]</span></p><p><span>[c]</span><span> 3:146 ; 17:20 ; 3:146 ; 17:19.</span></p>');
INSERT INTO `quran` VALUES (10681,2,42,22,'<p><span>Apakah bagi mereka ada sekutu-sekutu yang menetapkan bagi mereka dari agama yang tidak diizinkan Allah? Dan sekiranya tidak ada keputusan akhir </span><span>dari</span><span> Allah, tentu telah diputuskan di antara mereka. Dan, Sesungguhnya, orang-orang aniaya bagi mereka ada azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (10682,2,42,23,'<p><span>Engkau akan melihat orang-orang aniaya merasa takut karena apa yang telah mereka usahakan, dan </span><span>azab</span><span> itu tentu akan menimpa mereka. </span><span>[a]</span><span> </span><span>Tetapi orang-orang yang beriman dan berbuat shaleh, mereka berada di kebun-kebun surga. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2653">2653</a><span>]</span><span> Mereka memperoleh apa juga yang mereka inginkan di sisi Tuhan mereka. Itu adalah karunia besar.</span></p><p><span>[a]</span><span> 2:83 ; 13:30 ; 22:57 ; 68:35</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10683,2,42,24,'<p><span>Itulah yang mengenainya Allah memberi kabar suka kepada hamba-hamba-Nya yang beriman dan beramal shaleh. </span><span>[a]</span><span> </span><span>Katakanlah, “Aku tidak meminta kepadamu atasnya upah, melainkan kecintaan di antara kaum kerabat.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2654">2654</a><span>]</span><span> Dan barangsiapa berbuat kebaikan, Kami menambahkan kebaikan baginya di dalamnya. Sesungguhnya, Allah itu Maha Pengampun, Maha Menghargai.</span></p><p><span>[a]</span><span> 25:58 ; 38:87.</span></p>');
INSERT INTO `quran` VALUES (10684,2,42,25,'<p><span>Apakah mereka itu berkata. “Ia, </span><span>Muhammad,</span><span> telah mengada-adakan dusta terhadap Allah?” Maka, sekiranya Allah menghendaki, niscaya Dia akan mencap hati engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2655">2655</a><span>]</span><span> </span><span>[b]</span><span> </span><span>Dan Allah menghapus kebatilan dan menegakkan kebenaran dengan tanda-tanda-Nya. Sesungguhnya, Dia Maha Mengetahui segala isi hati.</span></p><p><span>[b]</span><span> 13:40</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10685,2,42,26,'<p><span>[a]</span><span> </span><span>Dan, Dia-lah Yang menerima taubat dari hamba-hamba-Nya, dan memaafkan kesalahan-kesalahan, dan Dia mengetahui apa yang kamu kerjakan,</span></p><p><span>[a]</span><span> 9:104 ; 33:74.</span></p>');
INSERT INTO `quran` VALUES (10686,2,42,27,'<p><span>[b]</span><span> </span><span>Dan Dia mengabulkan doa-doa orang-orang yang beriman dan beramal shaleh, dan Dia menambahkan kepada mereka sebagian karunia-Nya. Dan orang-orang kafir bagi mereka ada azab yang keras.</span></p><p><span>[b]</span><span> 2:187.</span></p>');
INSERT INTO `quran` VALUES (10687,2,42,28,'<p><span>Dan, Sekiranya Allah melapangkan rezeki bagi hamba-hamba-Nya, tentulah mereka akan memberontak di bumi; </span><span>[c]</span><span> </span><span>akan tetapi, Dia menurunkan menurut ukuran yang Dia kehendaki, Sesungguhnya mengenai hamba-hamba-Nya Dia maha Mengetahui, Maha Melihat.</span></p><p><span>[c]</span><span> 15:22.</span></p>');
INSERT INTO `quran` VALUES (10688,2,42,29,'<p><span>[d]</span><span> </span><span>Dan. Dia-lah Yang menurunkan hujan sesudah mereka berputus-asa dan menyebarkan rahmat-Nya. Dan Dia-lah Maha Pelindung, Maha Terpuji.</span></p><p><span>[d]</span><span> 31:35</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10689,2,42,30,'<p><span>[a]</span><span> </span><span>Dan, dari antara Tanda-tanda-Nya adalah penciptaan seluruh langit dan bumi, dan apa yang telah disebarkan di dalam keduanya dari hewan-hewan. Dan, Dia berkuasa menghimpunkan mereka apabila Dia menghendaki. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2656">2656</a><span>]</span></p><p><span>[a]</span><span> 30:23.</span></p>');
INSERT INTO `quran` VALUES (10690,2,42,31,'<p><span>R. 4</span></p><p><span>[b]</span><span> </span><span>Dan apa saja yang menimpa kamu dari musibah, maka itu disebabkan usaha tanganmu. Dan Dia memaafkan banyak </span><span>dosa.</span></p><p><span>[b]</span><span> 4:80.</span></p>');
INSERT INTO `quran` VALUES (10691,2,42,32,'<p><span>[c]</span><span> </span><span>Dan kamu tidak dapat menggagalkan </span><span>rencana</span><span> </span><span>Allah</span><span> di bumi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2657">2657</a><span>]</span><span> dan tidak ada bagi kamu selain Allah seorang pelindung dan penolong.</span></p><p><span>[c]</span><span> 6:135 ; 11:34 ; 29:23</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10692,2,42,33,'<p><span>[a]</span><span> </span><span>Dan dari antara Tanda-tanda-Nya adalah kapal-kapal </span><span>berlayar</span><span> di lautan, bagaikan gunung-gunung. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2658">2658</a><span>]</span></p><p><span>[a]</span><span> 31:32 ; 55:25.</span></p>');
INSERT INTO `quran` VALUES (10693,2,42,34,'<p><span>Sekiranya Dia berkehendak, Dia menghentikan angin, sehingga mereka diam tidak bergerak di atas permukaannya. Sesungguhnya, dalam hal demikian itu terdapat Tanda- tanda bagi tiap orang yang sabar, banyak bersyukur,</span></p>');
INSERT INTO `quran` VALUES (10694,2,42,35,'<p><span>Atau, Dia dapat membinasakan mereka disebabkan oleh apa yang telah mereka usahakan. Dan Dia memaafkan banyak </span><span>dosa</span></p>');
INSERT INTO `quran` VALUES (10695,2,42,36,'<p><span>[b]</span><span> </span><span>Dan Dia mengetahui orang-orang yang berbantah tentang Ayat-ayat Kami. Tidak ada bagi mereka tempat pelarian.</span></p><p><span>[b]</span><span> 22:4 ; 40:5.</span></p>');
INSERT INTO `quran` VALUES (10696,2,42,37,'<p><span>[c</span><span>] </span><span>Dan apa saja yang telah diberikan kepadamu dari sesuatu hanyalah perbekalan sementara kehidupan dunia, tetapi apa yang ada di sisi Allah adalah lebih baik dan lebih kekal bagi mereka yang beriman dan bertawakkal kepada Tuhan mereka.</span></p><p><span>[c]</span><span> 28:61.</span></p>');
INSERT INTO `quran` VALUES (10697,2,42,38,'<p><span>[d]</span><span> </span><span>Dan orang-orang yang menjauhi dosa-dosa besar dan kekejian-kekejian, dan apabila mereka marah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2659">2659</a><span>]</span><span> mereka memaafkan.</span></p><p><span>[d]</span><span> 4:32 ; 53:33</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10698,2,42,39,'<p><span>Dan orang-orang yang menerima </span><span>seruan</span><span> Tuhan mereka, dan mendirikan shalat, </span><span>[a] </span><span>dan setiap urusan mereka, mereka bermusyawarah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2660">2660</a><span>]</span><span> di antara mereka, dan dari apa yang telah Kami rezekikan kepada mereka, mereka belanjakan.</span></p>');
INSERT INTO `quran` VALUES (10699,2,42,40,'<p><span>Dan orang-orang yang apabila menimpa mereka keaniayaan. mereka membela diri.</span></p>');
INSERT INTO `quran` VALUES (10700,2,42,41,'<p><span>[b] </span><span>Dan pembalasan terhadap suatu keburukan adalah keburukan semisalnya, tetapi barang siapa memaafkan dan memperbaiki, maka ganjarannya ada pada Allah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2661">2661</a><span>]</span><span> Sesungguhnya, Dia tidak menyukai orang-orang aniaya.</span></p><p><span>[b] 2 ; 195 ; 16:127</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10701,2,42,42,'<p><span>Dan barangsiapa membela diri sesudah dianiaya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2662">2662</a><span>]</span><span> maka mereka itulah tidak ada keberatan atas mereka.</span></p>');
INSERT INTO `quran` VALUES (10702,2,42,43,'<p><span>Sesungguhnya hanya ada keberatan pada orang-orang yang berbuat aniaya terhadap manusia dan berontak di bumi tanpa hak. Itulah orang-orang yang bagi mereka ada azab pedih</span></p>');
INSERT INTO `quran` VALUES (10703,2,42,44,'<p><span>[a] </span><span>Dan, barangsiapa bersabar dan memaafkan, Sesungguhnya perkara-perkara itu </span><span>memerlukan</span><span> tekad yang kuat.</span></p><p><span>[a]</span><span> 16:127.</span></p>');
INSERT INTO `quran` VALUES (10704,2,42,45,'<p><span>R. 5</span></p><p><span>[b] </span><span>Dan, barangsiapa dinyatakan sesat oleh Allah, maka baginya tidak ada seorang pelindung selain-Nya. Dan, engkau akan melihat orang-orang aniaya, ketika mereka melihat azab, mereka akan berkata, “Apakah ada jalan untuk kembali ?”</span></p><p><span>[b]</span><span> 4:144 ; 17:98 ; 18:18</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10705,2,42,46,'<p><span>Dan, Engkau akan melihat mereka dihadapkan kepadanya, dengan menundukan kepala mereka karena hina, sambil melihat dengan pandangan mencuri-curi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2663">2663</a><span>]</span><span> Dan, mereka yang beriman akan berkata, “Sesungguhnya,orang-orang yang merugi adalah orang-orang yang merugikan diri sendiri dan keluarga mereka pada Hari Kiamat.” Ketahuilah, bahwa Sesungguhnya orang-orang aniaya berada dalam azab yang tetap.</span></p>');
INSERT INTO `quran` VALUES (10706,2,42,47,'<p><span>Dan, mereka tidak mempunyai pelindung-pelindung yang akan menolong mereka selain Allah. Dan barangsiapa yang dinyatakan sesat oleh Allah, maka tidak ada baginya jalan.</span></p>');
INSERT INTO `quran` VALUES (10707,2,42,48,'<p><span>Jawablah seruan Tuhan-mu sebelum datang suatu hari yang tidak dapat menghindarinya menentang Allah. Tidak ada bagimu tempat perlindungan pada hari itu, dan tidak ada bagimu </span><span>kesempatan</span><span> menyangkal.</span></p>');
INSERT INTO `quran` VALUES (10708,2,42,49,'<p><span>Tetapi, sekiranya mereka berpaling, maka Kami tidak mengutus engkau atas mereka sebagai penjaga. Tidaklah kewajiban engkau melainkan menyampaikan. </span><span>[a] </span><span>Dan Sesungguhnya apabila Kami merasakan kepada manusia rahmat dari Kami, maka ia bergembira dengan itu. Dan, jika menimpa mereka suatu keburukan disebabkan perbuatan tangan mereka sendiri; maka Sesungguhnya manusia tidak tahu berterima kasih.</span></p><p><span>[a] 11:11</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10709,2,42,50,'<p><span>[a] </span><span>Kepunyaan Allah kerajaan seluruh langit dan bumi. Dia menciptakan apa yang Dia kehendaki. Dia menganugerahkan anak-anak perempuan kepada siapa yang Dia kehendaki, dan menganugerahkan anak-anak lelaki kepada siapa yang Dia kehendaki.</span></p><p><span>[a]</span><span> 5:4 ; 39:45 ; 57:3</span></p>');
INSERT INTO `quran` VALUES (10710,2,42,51,'<p><span>Atau, Dia membaurkan mereka, laki-laki dan perempuan; dan Dia menjadikan siapa yang Dia kehendaki mandul. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2664">2664</a><span>]</span><span> Sesungguhnya Dia Maha Mengetahui, Mahakuasa.</span></p>');
INSERT INTO `quran` VALUES (10711,2,42,52,'<p><span>Dan tidak ada bagi manusia bahwa Allah berbicara kepadanya, kecuali dengan wahyu atau dari belakang tabir atau dengan mengirimkan seorang utusan guna mewahyukan dengan seizin-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2665">2665</a><span>]</span><span> apa yang Dia kehendaki. Sesungguhnya, Dia Mahaluhur, Mahabijaksana.</span></p>');
INSERT INTO `quran` VALUES (10712,2,42,53,'<p><span>Dan Demikianlah Kami telah mewahyukan kepada engkau Firman ini </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2666">2666</a><span>]</span><span> dengan perintah Kami. Engkau tidak mengetahui apa Kitab itu, dan tidak </span><span>pula</span><span> apa iman itu. Akan tetapi, Kami telah menjadikan </span><span>wahyu itu nur</span><span>, yang dengan itu Kami memberi petunjuk kepada siapa yang Kami kehendaki dari antara hamba-hamba Kami. Dan, Sesungguhnya, engkau pasti memberi petunjuk ke jalan lurus,</span></p>');
INSERT INTO `quran` VALUES (10713,2,42,54,'<p><span>Jalan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2667">2667</a><span>]</span><span> Allah, Yang kepunyaan-Nya apa yang ada di seluruh langit dan apa yang ada di bumi. Ketahuilah, kepada Allah segala perkara kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2668">2668</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10714,2,43,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10715,2,43,2,'<p><span>[b] </span><span>Maha Terpuji. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2668A">2668A</a><span>]</span><span> Maha Mulia</span><span>.</span></p><p><span>[b]</span><span> 44:2 ; 45:2.</span></p>');
INSERT INTO `quran` VALUES (10716,2,43,3,'<p><span>Demi Kitab yang menerangkan dengan jelas.</span></p>');
INSERT INTO `quran` VALUES (10717,2,43,4,'<p><span>[c] </span><span>Sesungguhnya Kami telah menjadikannya </span><span>Kitab ini</span><span> sebagai Alquran dalam bahasa Arab, Supaya kamu memahami.</span></p><p><span>[c]</span><span> 39:29 ; 42:8 ; 46:13.</span></p>');
INSERT INTO `quran` VALUES (10718,2,43,5,'<p><span>Dan, Sesungguhnya </span><span>Alquran</span><span> ini dalam Induk Kitab, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2669">2669</a><span>]</span><span> benar-benar di sisi Kami sangat luhur, sangat bijaksana.</span></p>');
INSERT INTO `quran` VALUES (10719,2,43,6,'<p><span>Maka apakah kami melepaskan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2670">2670</a><span>]</span><span> dari kamu untuk menerangkan Zikir, </span><span>Alquran</span><span>, karena kamu kaum yang melampaui batas? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2671">2671</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10720,2,43,7,'<p><span>[a] </span><span>Dan, berapa banyak nabi telah Kami utus kepada kaum terdahulu!</span></p><p><span>[a]</span><span> 15:11.</span></p>');
INSERT INTO `quran` VALUES (10721,2,43,8,'<p><span>[b] </span><span>Dan, tidak pernah datang kepada mereka seorang nabi, melainkan mereka selalu memperolok-olokannya.</span></p><p><span>[b]</span><span> 15:12 ; 36:31.</span></p>');
INSERT INTO `quran` VALUES (10722,2,43,9,'<p><span>Maka Kami membinasakan yang terkuat dari mereka, dan telah berlalu pula contoh orang-orang terdahulu.</span></p>');
INSERT INTO `quran` VALUES (10723,2,43,10,'<p><span>Dan, jika engkau menanyakan kepada mereka, “Siapakah yang menciptakan seluruh langit dan bumi ?” Tentukanlah mereka akan berkata, “Dia menciptakan mereka, Yang Maha Perkasa, Maha Mengetahui.”</span></p>');
INSERT INTO `quran` VALUES (10724,2,43,11,'<p><span>[c] </span><span>Dia, Yang telah menjadikan bumi bagimu hamparan, dan telah membuat bagimu di dalamnya jalan-jalan, supaya kamu mendapat petunjuk.</span></p><p><span>[c]</span><span> 20:54.</span></p>');
INSERT INTO `quran` VALUES (10725,2,43,12,'<p><span>Dan Yang menurunkan air dari awan dengan kadar tertentu, dan dengan itu Kami menghidupkan negeri yang mati, seperti itulah kamu akan dikeluarkan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2672">2672</a><span>]</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10726,2,43,13,'<p><span>Dan, Yang telah menciptakan segala sesuatu berpasang-pasangan, dan menjadikan bagimu kapal-kapal dan binatang-binatang ternak yang kamu tunggangi</span></p>');
INSERT INTO `quran` VALUES (10727,2,43,14,'<p><span>Supaya kamu dapat duduk dengan teguh di atas punggungnya, kemudian kamu mengingat nikmat Tuhan-mu apabila kamu telah duduk di atasnya dan kamu berkata , “Maha Suci Yang telah menundukkan ini kepada kami, dan kami tidak mampu menguasainya,</span></p>');
INSERT INTO `quran` VALUES (10728,2,43,15,'<p><span>“Dan, Sesungguhnya kami kepada Tuhan kami pasti akan kembali,”</span></p>');
INSERT INTO `quran` VALUES (10729,2,43,16,'<p><span>Dan, mereka menjadikan bagi Dia sebagian </span><span>perempuan</span><span> dari hamba-hamba-Nya. Sesungguh nya, manusia itu benar-benar pengingkar yang nyata.</span></p>');
INSERT INTO `quran` VALUES (10730,2,43,17,'<p><span>R. 2</span></p><p><span>[a] </span><span>Apakah Dia mengambil dari apa yang Dia ciptakan anak-anak perempuan, dan Dia telah mengkhususkan kamu dengan anak-anak lelaki ?</span></p><p><span>[a]</span><span> 6:101 ; 16:56 ; 58:40 ; 53:22.</span></p>');
INSERT INTO `quran` VALUES (10731,2,43,18,'<p><span>[b] </span><span>Dan apabila disampaikan khabar suka kepada salah seorang dari mereka tentang apa yang dijadikannya tamsil bagi </span><span>Tuhan</span><span> Yang Maha Pemurah, </span><span>berubahlah</span><span> wajahnya menjadi hitam dan ia penuh dengan kemarahan.</span></p><p><span>[b] 16:59</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10732,2,43,19,'<p><span>Apakah orang yang dibesarkan di dalam perhiasan-perhiasan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2673">2673</a><span>]</span><span> dan dia tidak mampu memberikan penjelasan dalam pertengkaran, </span><span>masuk bagian Tuhan?</span></p>');
INSERT INTO `quran` VALUES (10733,2,43,20,'<p><span>[a] </span><span>Dan, mereka menjadikan malaikat-malaikat yang adalah hamba-hamba </span><span>Allah</span><span> Yang Maha Pemurah sebagai perempuan-perempuan. Apakah mereka menyaksikan kejadian mereka akan dicatat dan mereka akan ditanyai.</span></p><p><span>[a]</span><span> 17:41 ; 37:151 ; 52:40.</span></p>');
INSERT INTO `quran` VALUES (10734,2,43,21,'<p><span>Dan mereka berkata, </span><span>[b] </span><span>”Jika </span><span>Tuhan</span><span> Yang Maha Pemurah menghendaki demikian, tentulah kami tidak akan menyembah mereka.” Mereka tidak mempunyai ilmu sedikit pun mengenai hal itu, mereka tidak berbuat sesuatu kecuali menerka-nerka belaka.</span></p><p><span>[b]</span><span> 6:149 ; 16:36.</span></p>');
INSERT INTO `quran` VALUES (10735,2,43,22,'<p><span>[c] </span><span>Apakah pernah Kami memberi mereka suatu Kitab sebelumnya sehingga mereka berpegang teguh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2674">2674</a><span>]</span><span> kepadanya ?</span></p><p><span>[c] 37:157-158 ; 68:38</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10736,2,43,23,'<p><span>Tidak, bahwa mereka berkata, </span><span>[a] </span><span>”Sesungguhnya kami mendapatkan bapak-bapak kami mengikuti cara tertentu, dan Sesungguhnya kami berjalan di atas jejak mereka.”</span></p><p><span>[a]</span><span> 2:171 ; 7:29.</span></p>');
INSERT INTO `quran` VALUES (10737,2,43,24,'<p><span>Dan demikianlah Kami tidak pernah mengirimkan sebelum engkau ke sesuatu negeri seorang pemberi ingat, melainkan berkata orang-orang kaya mereka, </span><span>[b] </span><span>”Sesungguh nya kami mendapatkan bapak-bapak kami mengikuti cara tertentu, dan Sesungguhn ya kami berjalan di atas jejak mereka.”</span></p><p><span>[b]</span><span> 21:54 ; 26:75.</span></p>');
INSERT INTO `quran` VALUES (10738,2,43,25,'<p><span>Rasul</span><span> berkata, “Apakah walaupun aku mendatangkan kepadamu suatu petunjuk yang lebih baik dari apa yang telah kamu dapati atasnya bapak-bapakmu ? ” Mereka berkata, “Sesungguhnya kami ingkar kepada apa yang dengannya kamu diutus.”</span></p>');
INSERT INTO `quran` VALUES (10739,2,43,26,'<p><span>[c] </span><span>Maka Kami menuntut ganti rugi dari mereka, maka lihatlah bagaimana akibat orang-orang yang mendustakan.</span></p><p><span>[c]</span><span> 7:137 ; 43:56.</span></p>');
INSERT INTO `quran` VALUES (10740,2,43,27,'<p><span>R. 3</span></p><p><span>Dan, </span><span>ingatlah</span><span>, ketika berkata Ibrahim kepada bapaknya dan kaumnya, </span><span>[d] </span><span>”Sesungguhnya aku berlepas diri dari apa yang kamu sembah,</span></p><p><span>[d]</span><span> 6:79 ; 9:114 ; 60:5.</span></p>');
INSERT INTO `quran` VALUES (10741,2,43,28,'<p><span>“Selain Dia Yang menciptakan aku, maka Sesungguhnya Dia akan memberi petunjuk kepadaku.”</span></p>');
INSERT INTO `quran` VALUES (10742,2,43,29,'<p><span>Dan, Dia menjadikannya ajaran </span><span>Tauhid</span><span> itu tetap di antara keturunannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2675">2675</a><span>]</span><span> supaya mereka kembali kepada Tuhan.</span></p>');
INSERT INTO `quran` VALUES (10743,2,43,30,'<p><span>Tidak, bahkan aku memberikan perbekalan kepada orang-orang ini dan bapak- bapak mereka hingga datang kepada mereka kebenaran dan seorang rasul yang memberikan penjelasan.</span></p>');
INSERT INTO `quran` VALUES (10744,2,43,31,'<p><span>Tetapi, ketika datang kepada mereka kebenaran, mereka berkata, </span><span>[a] </span><span>”Ini adalah sihir, dan Sesungguhnya kami mengingkarinya.”</span></p><p><span>[a]</span><span> 27:14.</span></p>');
INSERT INTO `quran` VALUES (10745,2,43,32,'<p><span>Dan mereka berkata, “Mengapakah Alquran ini tidak diturunkan kepada seseorang yang besar dari kedua kota besar itu” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2676">2676</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10746,2,43,33,'<p><span>Apakah mereka membagikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2677">2677</a><span>]</span><span> rahmat Tuhan engkau ? Kami Yang membagikan di antara mereka bekal hidup mereka dalam kehidupan dunia dan Kami mengangkat sebagian mereka di atas sebagian lain dalam sederajat, supaya sebagian dari mereka membuat yang lainnya perolokkan. Dan rahmat Tuhan engkau adalah lebih baik dari apa yang mereka kumpulkan.</span></p>');
INSERT INTO `quran` VALUES (10747,2,43,34,'<p><span>Dan, sekiranya tidak </span><span>takut</span><span> bahwa manusia akan menjadi berada dalam satu cara, maka pasti Kami menjadikan bagi orang yang ingkar kepada Tuhan Yang Maha Pemurah itu atap-atap rumah mereka dari </span><span>perak,</span><span> yang diatasnya mereka naik,</span></p>');
INSERT INTO `quran` VALUES (10748,2,43,35,'<p><span>Dan, </span><span>Kami menjadikan</span><span> pintu-pintu rumah mereka dan dipan-dipan </span><span>dari perak</span><span> yang padanya mereka bersandar,</span></p>');
INSERT INTO `quran` VALUES (10749,2,43,36,'<p><span>Dan, </span><span>bahkan</span><span> dari emas, Dan tidak lain kesemuanya itu hanyalah perbekalan sementara kehidupan dunia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2678">2678</a><span>]</span><span> Sedangkan </span><span>kesenangan</span><span> akhirat di sisi Tuhan engkau adalah untuk orang-orang bertakwa.</span></p>');
INSERT INTO `quran` VALUES (10750,2,43,37,'<p><span>R. 4</span></p><p><span>[a] </span><span>Dan, barangsiapa berpaling dari berzikir kepada Tuhan Yang Maha Pemurah, Kami menetapkan baginya satu syaitan, maka dia baginya menjadi teman.</span></p><p><span>[a] 20:100 ; 101 ; 72:18</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10751,2,43,38,'<p><span>[a] </span><span>Dan, Sesungguhnya mereka, </span><span>syaitan-syaitan,</span><span> itu pasti menghalangi mereka dari jalan </span><span>lurus</span><span>, dan mereka menyangka bahwa mereka mendapat petunjuk.</span></p><p><span>[a]</span><span> 8:35 ; 16:89.</span></p>');
INSERT INTO `quran` VALUES (10752,2,43,39,'<p><span>Hingga, ketika orang demikian datang kepada Kami, ia berkata, </span><span>[b] </span><span>”Alangkah baiknya jika di antara aku dan engkau ada jarak sejauh timur dan barat!” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2679">2679</a><span>]</span><span> Maka ia seburuk-buruk teman.</span></p><p><span>[b]</span><span> 3:31.</span></p>');
INSERT INTO `quran` VALUES (10753,2,43,40,'<p><span>[c] </span><span>Dan, hari itu sekali-kali tidak bermanfaat bagi kamu, ketika kamu telah berbuat aniaya, Sesungguhnya kamu bersama-sama azab itu.”</span></p><p><span>[c]</span><span> 37:34.</span></p>');
INSERT INTO `quran` VALUES (10754,2,43,41,'<p><span>Maka </span><span>[d] </span><span>apakah engkau membuat orang tuli mendengar, atau menuntun orang buta dan orang yang ada dalam kesesatan yang nyata ? ” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2680">2680</a><span>]</span></p><p><span>[d] </span><span>10:43 ; 27:81.</span></p>');
INSERT INTO `quran` VALUES (10755,2,43,42,'<p><span>[e] </span><span>Maka jika Kami membawa engkau, </span><span>mewafatkan,</span><span> niscaya Kami akan menuntut balas dari mereka;</span></p><p><span>[e]</span><span> 13:41 ; 40:78.</span></p>');
INSERT INTO `quran` VALUES (10756,2,43,43,'<p><span>Atau, </span><span>[f] </span><span>Kami akan memperlihatkan kepada engkau yang telah Kami janjikan kepada mereka, karena Sesungguhnya, Kami berkuasa penuh atas mereka.</span></p><p><span>[f] 10:47 ; 13:41 ; 40:78</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10757,2,43,44,'<p><span>Maka </span><span>[a] </span><span>berpegangan teguhlah engkau kepada yang telah diwahyukan kepada engkau. Sesungguhnya engkau berada di atas jalan lurus.</span></p><p><span>[a]</span><span> 11:113.</span></p>');
INSERT INTO `quran` VALUES (10758,2,43,45,'<p><span>Dan Sesungguhnya </span><span>[b] </span><span>Alquran</span><span> ini adalah kemuliaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2681">2681</a><span>]</span><span> bagi engkau dan bagi kaum engkau; dan kamu pasti akan dimintai pertanggungjawaban.</span></p><p><span>[b]</span><span> 21:11 ; 38:2.</span></p>');
INSERT INTO `quran` VALUES (10759,2,43,46,'<p><span>[c] </span><span>Dan tanyakanlah kepada siapa yang Kami utus sebelum engkau dari rasul-rasul Kami, “Pernahkah Kami menetapkan tuhan-tuhan untuk disembah sebagai sembahan selain Tuhan Yang Maha Pemurah ? ”</span></p><p><span>[c]</span><span> 21:26.</span></p>');
INSERT INTO `quran` VALUES (10760,2,43,47,'<p><span>R. 5</span></p><p><span>Dan, Sesungguhnya, </span><span>[d] </span><span>Kami telah mengirimkan Musa dengan Tanda-tanda Kami kepada Firaun dan para pembesarnya, maka ia berkata, “Sesungguhnya aku seorang rasul Tuhan semesta alam.”</span></p><p><span>[d]</span><span> 11:97 ; 14:6 ; 23:46 ; 40:24.</span></p>');
INSERT INTO `quran` VALUES (10761,2,43,48,'<p><span>Maka, apabila ia datang kepada mereka dengan Tanda-tanda Kami, tiba-tiba mereka menertawakannya.</span></p>');
INSERT INTO `quran` VALUES (10762,2,43,49,'<p><span>Dan, Kami tidak memperlihatkan kepada mereka sesuatu Tanda, melainkan itu lebih besar dari </span><span>Tanda sejenisnya,</span><span> dan Kami menyerang mereka dengan azab, supaya mereka kembali dari amal buruk.</span></p>');
INSERT INTO `quran` VALUES (10763,2,43,50,'<p><span>Dan mereka berkata “Hai ahli sihir, </span><span>[a] </span><span>berdoalah bagi kami kepada Tuhan engkau, sesuai janji yang dilakukan dengan engkau, Sesungguhnya kami pasti akan mendapat petunjuk.”</span></p><p><span>[a]</span><span> 7:135.</span></p>');
INSERT INTO `quran` VALUES (10764,2,43,51,'<p><span>Tetapi, </span><span>[b] </span><span>apabila Kami menjauhkan dari mereka azab, tiba-tiba mereka melanggar janji mereka.</span></p><p><span>[b]</span><span> 7:136.</span></p>');
INSERT INTO `quran` VALUES (10765,2,43,52,'<p><span>Dan Firaun mengumumkan kepada kaumnya dengan berkata “Hai, kaumku! Bukanlah kerajaan Mesir ini kepunyaanku dan sungai-sungai ini mengalir dibawahku ? Maka apakah kamu tidak melihat ?</span></p>');
INSERT INTO `quran` VALUES (10766,2,43,53,'<p><span>“Apakah aku lebih baik daripada orang yang hina dan ia tidak dapat menjelaskan ?</span></p>');
INSERT INTO `quran` VALUES (10767,2,43,54,'<p><span>“Mengapa tidak dianugerahkan kepadanya gelang-gelang dari emas, atau datang bersamanya </span><span>[c] </span><span>malaikat-malaikat yang berkumpul di sekelilingnnya ? ”</span></p><p><span>[c]</span><span> 6:9 ; 11:13 ; 25:8.</span></p>');
INSERT INTO `quran` VALUES (10768,2,43,55,'<p><span>Demikianlah ia memperbodohkan kaumnya, lalu mereka patuh kepadanya, Sesungguhnya mereka adalah kaum durhaka.</span></p>');
INSERT INTO `quran` VALUES (10769,2,43,56,'<p><span>Maka, ketika mereka membuat Kami murka, </span><span>[d] </span><span>Kami menuntut balas dari mereka, dan Kami menenggelamkan mereka semua,</span></p><p><span>[d]</span><span> 43:26.</span></p>');
INSERT INTO `quran` VALUES (10770,2,43,57,'<p><span>Dan Kami menjadikan mereka kisah yang lalu dan misal bagi yang akan datang.</span></p>');
INSERT INTO `quran` VALUES (10771,2,43,58,'<p><span>Dan, apabila dijelaskan Ibnu Maryam sebagai misal, tiba-tiba kaum engkau meneriakkan suara protes </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2682">2682</a><span>]</span><span> terhadapnya;</span></p>');
INSERT INTO `quran` VALUES (10772,2,43,59,'<p><span>Dan mereka berkata, “Apakah tuhan-tuhan kami lebih baik, ataukah dia ?” Mereka tidak menyebutkan hal itu kepada engkau melainkan perbantahan semata. Bahkan mereka adalah kaum yang biasa berbantah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2683">2683</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10773,2,43,60,'<p><span>Tidaklah dia, kecuali seorang hamba, yang Kami telah anugerahkan karunia kepadanya, dan Kami menjadikan dia suatu misal bagi Bani Israil.</span></p>');
INSERT INTO `quran` VALUES (10774,2,43,61,'<p><span>Dan, sekiranya Kami menghendaki, tentu Kami dapat menjadi dari antaramu malaikat sebagai penerus di bumi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2684">2684</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10775,2,43,62,'<p><span>Tetapi, Sesungguhnya ia, Alquran, memberi ilmu tentang Saat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2685">2685</a><span>]</span><span> Maka janganlah ragu-ragu tentang itu, melainkan ikutilah aku. Inilah jalan lurus.</span></p>');
INSERT INTO `quran` VALUES (10776,2,43,63,'<p><span>Dan, janganlah syaitan menghalangi kamu, Sesungguhnya, ia bagimu adalah musuh yang nyata.</span></p>');
INSERT INTO `quran` VALUES (10777,2,43,64,'<p><span>Dan, ketika Isa datang dengan Tanda-tanda yang nyata ia berkata, “Sesungguhnya, aku datang kepadamu dengan hikmah, dan untuk menjelaskan beberapa hal kepadamu yang mengenainya kami berselisih, maka bertakwalah kepada Allah dan taatlah kepadaku.</span></p>');
INSERT INTO `quran` VALUES (10778,2,43,65,'<p><span>“Sesungguhnya </span><span>[a]</span><span> </span><span>Allah, Dia-lah Tuhan-ku dan Tuhan-mu, maka sembahlah Dia. Inilah jalan yang lurus.”</span></p><p><span>[a]</span><span> 3:52 ; 19:37.</span></p>');
INSERT INTO `quran` VALUES (10779,2,43,66,'<p><span>Tetapi, berselisihlah</span><span> </span><span>[b] </span><span>golong-golongan di antara mereka, maka celakalah bagi orang-orang aniaya disebabkan azab hari yang pedih.</span></p><p><span>[b]</span><span> 19:38</span></p>');
INSERT INTO `quran` VALUES (10780,2,43,67,'<p><span>[c] </span><span>Tidaklah yang mereka nantikan selain Saat yang akan datang kepada mereka secara tiba-tiba, sedangkan mereka tidak menyadari.</span></p><p><span>[c]</span><span> 10:51 ; 12:108 ; 22:56 ; 47:19.</span></p>');
INSERT INTO `quran` VALUES (10781,2,43,68,'<p><span>Kawan-kawan pada hari itu sebagian akan bermusuhan dengan sebagian lain, </span><span>[a] </span><span>kecuali orang-orang bertakwa.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2686">2686</a><span>]</span></p><p><span>[a]</span><span> 2:256.</span></p>');
INSERT INTO `quran` VALUES (10782,2,43,69,'<p><span>R. 7</span></p><p><span>Allah berfirman</span><span>, “Hai, hamba-hamba-Ku </span><span>[b]</span><span> </span><span>Tiada ketakutan atas kamu pada hari ini, dan tidak pula kamu akan bersedih hati,</span></p><p><span>[b]</span><span> 10:63 ; 39:62.</span></p>');
INSERT INTO `quran` VALUES (10783,2,43,70,'<p><span>“Orang-orang yang beriman kepada Tanda-tanda Kami dan mereka selalu menyerahkan diri,</span></p>');
INSERT INTO `quran` VALUES (10784,2,43,71,'<p><span>[c]</span><span> </span><span>”Masuklah ke dalam surga, kamu dan istri-istrimu, kamu akan dibuat gembira!”</span></p><p><span>[c]</span><span> 30:16.</span></p>');
INSERT INTO `quran` VALUES (10785,2,43,72,'<p><span>Akan diedarkan kepada mereka </span><span>[d]</span><span> </span><span>piring-piring dari emas dan piala-piala, dan di dalamnnya akan terdapat segala yang diinginkan oleh jiwa dan mata akan merasakan nikmat. Dan kamu di dalamnnya akan tinggal selama-lamanya.</span></p><p><span>[d]</span><span> 59:19 ; 76:16.</span></p>');
INSERT INTO `quran` VALUES (10786,2,43,73,'<p><span>“Dan </span><span>[e]</span><span> </span><span>inilah surga itu yang telah diwariskannya kepadamu disebabkan apa yang telah kamu kerjakan.</span></p><p><span>[e]</span><span> 7:44 ; 19:64.</span></p>');
INSERT INTO `quran` VALUES (10787,2,43,74,'<p><span>[f] </span><span>”Bagi kamu di dalamnya banyak buah-buahan yang darinya kamu makan.</span></p><p><span>[f] 55:53 ; 77:43</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10788,2,43,75,'<p><span>[a] </span><span>Sesungguhnya, orang-orang yang berdosa itu di dalam azab Jahannam akan tinggal lama.</span></p><p><span>[a]</span><span> 20:75.</span></p>');
INSERT INTO `quran` VALUES (10789,2,43,76,'<p><span>[b] </span><span>Tidak akan diringankan </span><span>azab</span><span> dari mereka, dan mereka di dalamnnya akan berputus-asa.</span></p><p><span>[b]</span><span> 2:87 ; 40:50-51.</span></p>');
INSERT INTO `quran` VALUES (10790,2,43,77,'<p><span>Dan, Kami tidak berlaku aniaya terhadap mereka, akan tetapi mereka sendirilah orang-orang aniaya.</span></p>');
INSERT INTO `quran` VALUES (10791,2,43,78,'<p><span>Dan, mereka akan berseru, “Hai Malik,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2687">2687</a><span>] </span><span>hendaknya Tuhan engkau mematikan kami.” Ia berkata, Sesungguhnya kamu akan tinggal lama.”</span></p>');
INSERT INTO `quran` VALUES (10792,2,43,79,'<p><span>Allah berfirman</span><span>, “Sesungguhnya Kami membawa kepadamu kebenaran, akan tetapi kebanyakan kamu membenci kebenaran itu.”</span></p>');
INSERT INTO `quran` VALUES (10793,2,43,80,'<p><span>Apakah mereka telah memutuskan suatu perkara </span><span>menyerang nabi ?</span><span> Maka Kami pun telah memutuskan </span><span>untuk kehancuran mereka.</span></p>');
INSERT INTO `quran` VALUES (10794,2,43,81,'<p><span>Apakah mereka menyangka bahwa Kami tidak mendengar rahasia-rahasia mereka dan musyawarah-musyawarah rahasia mereka ? Tidak demikian, </span><span>[c]</span><span> </span><span>bahkan utusan-utusan Kami di dekat mereka mencatatnya.</span></p><p><span>[c] 50:19 ; 82:11-12</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10795,2,43,82,'<p><span>Katakanlah, “Sekiranya Tuhan Yang Maha Pemurah mempunyai seorang anak, niscaya akulah yang pertama di antara para penyembah.”</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2688">2688</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10796,2,43,83,'<p><span>Maha Suci Tuhan seluruh langit dan bumi, Tuhan Arasy, </span><span>jauh </span><span>dari apa yang mereka sifatkan.</span></p>');
INSERT INTO `quran` VALUES (10797,2,43,84,'<p><span>[a] </span><span>Maka biarkanlah mereka bercakap kotor dan bermain-main sampai mereka bertemu dengan Hari mereka yang telah dijanjikan.</span></p><p><span>[a]</span><span> 23:55 ; 52:46 ; 70:43.</span></p>');
INSERT INTO `quran` VALUES (10798,2,43,85,'<p><span>Dan, </span><span>[b] </span><span>Dia-lah Tuhan Yang di langit dan Yang di bumi, dan Dia-lah Yang Maha Bijaksana, Maha Mengetahui.</span></p><p><span>[b]</span><span> 6:4.</span></p>');
INSERT INTO `quran` VALUES (10799,2,43,86,'<p><span>Dan, Maha Berbekatlah Dzat Yang kepunyaan-Nya kerajaan seluruh langit dan bumi dan segala yang ada di antara keduanya, dan pada Dia semata ada ilmu mengenai Saat itu, dan kepada-Nya kamu akan dikembalikan.</span></p>');
INSERT INTO `quran` VALUES (10800,2,43,87,'<p><span>Dan, </span><span>[a] </span><span>tidaklah memiliki syafaat orang-orang yang menyeru selain Dia, kecuali orang yang menjadi saksi atas kebenaran, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2689">2689</a><span>]</span><span> dan mereka mengetahui.</span></p><p><span>[a]</span><span> 19:88.</span></p>');
INSERT INTO `quran` VALUES (10801,2,43,88,'<p><span>Dan, </span><span>[b] </span><span>jika engkau bertanya kepada mereka, “Siapakah yang menciptakan mereka ?” Pastilah mereka akan berkata, “Allah” kemudian, kemanakah mereka dipalingkan ?</span></p><p><span>[b] </span><span>29:62 ; 31:26 ; 39:39.</span></p>');
INSERT INTO `quran` VALUES (10802,2,43,89,'<p><span>Dan ucapannya. “Hai Tuhan-ku, </span><span>[c] </span><span>Sesungguhnya mereka ini kaum yang tidak beriman,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2690">2690</a><span>]</span></p><p><span>[c] </span><span>84:21.</span></p>');
INSERT INTO `quran` VALUES (10803,2,43,90,'<p><span>Maka maafkanlah mereka, dan ucapkanlah, “Selamat sejahtera.” Maka mereka segera akan mengetahui.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2691">2691</a><span>]</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10804,2,44,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10805,2,44,2,'<p><span>[b] </span><span>Tuhan Maha Terpuji, Maha Mulia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2691A">2691A</a><span>]</span></p><p><span>[b]</span><span> 40:2</span></p>');
INSERT INTO `quran` VALUES (10806,2,44,3,'<p><span>Demi Kitab yang menjelaskan.</span></p>');
INSERT INTO `quran` VALUES (10807,2,44,4,'<p><span>Sesungguhnya, </span><span>[c] </span><span>Kami menurunkannya dalam suatu malam yang diberkati, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2692">2692</a><span>]</span><span> Sesungguhnya, Kami selalu memberi peringatan.</span></p><p><span>[c]</span><span> 97:2.</span></p>');
INSERT INTO `quran` VALUES (10808,2,44,5,'<p><span>[d] </span><span>Di dalamnya diputuskan semua perkara yang bijaksana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2693">2693</a><span>]</span></p><p><span>[d]</span><span> 97:5</span></p>');
INSERT INTO `quran` VALUES (10809,2,44,6,'<p><span>Dengan perintah dari sisi Kami. Sesungguhnya, Kami selalu mengutus rasul-rasul.</span></p>');
INSERT INTO `quran` VALUES (10810,2,44,7,'<p><span>Suatu</span><span> rahmat dari Tuhan engkau. Sesungguhnya, Dia Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (10811,2,44,8,'<p><span>[a] </span><span>Tuhan seluruh langit dan bumi dan segala yang ada di antara keduanya, jika kamu </span><span>mau</span><span> yakin.</span></p><p><span>[a]</span><span> 19:16 ; 37:6 ; 44:8.</span></p>');
INSERT INTO `quran` VALUES (10812,2,44,9,'<p><span>Tiada Tuhan melainkan Dia. </span><span>[b] </span><span>Dia, menghidupkan dan Dia mematikan. Tuhan-mu dan Tuhan bapak-bapakmu dahulu.</span></p><p><span>[b]</span><span> 10:57 ; 57:3.</span></p>');
INSERT INTO `quran` VALUES (10813,2,44,10,'<p><span>Bahkan mereka dalam keraguan bermain-main.</span></p>');
INSERT INTO `quran` VALUES (10814,2,44,11,'<p><span>maka Tunggulah Hari itu, ketika langit akan membawa asap yang nyata, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2694">2694</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10815,2,44,12,'<p><span>Yang akan meliputi segenap manusia. Ini adalah suatu azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (10816,2,44,13,'<p><span>Mereka berseru</span><span>, </span><span>[c] </span><span>”Hai, Tuhan kami, jauhkanlah azab ini dari kami; Sesungguhnya kami orang-orang yang beriman.”</span></p><p><span>[c]</span><span> 7:135 ; 43:51.</span></p>');
INSERT INTO `quran` VALUES (10817,2,44,14,'<p><span>Dari mana mereka akan memperoleh peringatan itu, padahal telah datang kepada mereka seorang rasul yang menerangkan dengan jelas, </span><span>yang mereka tidak percayai.</span></p>');
INSERT INTO `quran` VALUES (10818,2,44,15,'<p><span>Kemudian, mereka berpaling darinya dan berkata, “ia telah </span><span>[a] </span><span>diajari; ia orang gila.”</span></p><p><span>[a]</span><span> 37:37 ; 68:52.</span></p>');
INSERT INTO `quran` VALUES (10819,2,44,16,'<p><span>[b] </span><span>Kami akan menjauhkan azab itu untuk sementara waktu, </span><span>tetapi</span><span> tentulah kamu akan kembali kepada keburukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2695">2695</a><span>]</span></p><p><span>[b]</span><span> 7:136 ; 43:51.</span></p>');
INSERT INTO `quran` VALUES (10820,2,44,17,'<p><span>Pada hari ketika Kami akan mencengkram dengan cengkraman dahsyat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2696">2696</a><span>]</span><span> Sesungguhnya Kami </span><span>berkuasa</span><span> untuk menuntut balas.</span></p>');
INSERT INTO `quran` VALUES (10821,2,44,18,'<p><span>Dan, Sesungguhnya Kami telah menguji sebelum mereka kaum Firaun, dan telah datang kepada mereka seorang rasul yang mulia.</span></p>');
INSERT INTO `quran` VALUES (10822,2,44,19,'<p><span>Yang berkata kepada mereka</span><span>, “Serakanlah kepadaku hamba-hamba Allah itu. Sesungguhnya, aku untukmu seorang rasul yang jujur.”</span></p>');
INSERT INTO `quran` VALUES (10823,2,44,20,'<p><span>“Dan, janganlah kamu menyombongkan diri terhadap Allah. Sesungguhnya aku datang kepadamu dengan dalil yang nyata.</span></p>');
INSERT INTO `quran` VALUES (10824,2,44,21,'<p><span>[a] </span><span>”Dan, Sesungguhnya aku berlindung kepada Tuhan-ku dan Tuhan-mu, jangan-jangan kamu akan merajamku.</span></p><p><span>[a]</span><span> 40:28.</span></p>');
INSERT INTO `quran` VALUES (10825,2,44,22,'<p><span>“Dan, jika kamu tidak beriman kepadaku, maka tinggalkanlah aku seorang diri.”</span></p>');
INSERT INTO `quran` VALUES (10826,2,44,23,'<p><span>Kemudian ia, Musa, berseru kepada Tuhan-nya, “Sesungguhnya mereka ini kaum berdosa.”</span></p>');
INSERT INTO `quran` VALUES (10827,2,44,24,'<p><span>Allah berfirman, “Bawalah hamba-hamba-Ku pada waktu malam; </span><span>[b] </span><span>sebab kamu tentu akan ditenggelamkan.”</span></p><p><span>[b]</span><span> 10:91 ; 20:79 ; 26:61.</span></p>');
INSERT INTO `quran` VALUES (10828,2,44,25,'<p><span>“Dan tinggalkanlah laut itu ketika tenang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2697">2697</a><span>]</span><span> Sesungguhnya mereka itu lasykar yang akan ditenggelamkan.”</span></p>');
INSERT INTO `quran` VALUES (10829,2,44,26,'<p><span>[c] </span><span>Beberapa banyaknya mereka tinggalkan dari kebun-kebun dan sumber-sumber mata air,</span></p><p><span>[c]</span><span> 26:59.</span></p>');
INSERT INTO `quran` VALUES (10830,2,44,27,'<p><span>Dan, ladang-ladang dan tempat-tempat mulia,</span></p>');
INSERT INTO `quran` VALUES (10831,2,44,28,'<p><span>Dan, nikmat-nikmat yang dahulu mereka di dalamnya bersenang-senang,</span></p>');
INSERT INTO `quran` VALUES (10832,2,44,29,'<p><span>Demikianlah, </span><span>[a] </span><span>dan Kami mewariskannya kepada kaum lain.</span></p><p><span>[a]</span><span> 7:138 ; 26:60 ; 28:7.</span></p>');
INSERT INTO `quran` VALUES (10833,2,44,30,'<p><span>Dan, tidaklah menangisi mereka langit dan bumi, dan tidak pula mereka diberi tangguh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2698">2698</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10834,2,44,31,'<p><span>R .2</span></p><p><span>[b] </span><span>Dan Sesungguhnya Kami menyelamatkan Bani Israil dari azab yang menghinakan,</span></p><p><span>[b]</span><span> 2:50 ; 14:7 ; 20:81.</span></p>');
INSERT INTO `quran` VALUES (10835,2,44,32,'<p><span>Dari Firaun. Sesungguhnya, ia adalah orang yang sombong, dari orang-orang yang melampaui batas.</span></p>');
INSERT INTO `quran` VALUES (10836,2,44,33,'<p><span>Dan, Sesungguhnya Kami melebihkan mereka dengan ilmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2699">2699</a><span>]</span><span> atas sekalian bangsa pada zamannya.</span></p>');
INSERT INTO `quran` VALUES (10837,2,44,34,'<p><span>Dan, Kami memberikan kepada mereka Tanda-tanda, yang di dalamnya ada cobaan yang nyata.</span></p>');
INSERT INTO `quran` VALUES (10838,2,44,35,'<p><span>Sesungguhnya orang-orang </span><span>Mekkah</span><span> ini tentu berkata,</span></p>');
INSERT INTO `quran` VALUES (10839,2,44,36,'<p><span>[a] </span><span>”Hanya ada satu kematian bagi kami, dan kami tidak akan dibangkitkan kembali,</span></p><p><span>[a]</span><span> 6:30 ; 23:38 ; 37:60 ; 45;25.</span></p>');
INSERT INTO `quran` VALUES (10840,2,44,37,'<p><span>“Maka datangkanlah bapak-bapak kami. jika kamu berkata benar.”</span></p>');
INSERT INTO `quran` VALUES (10841,2,44,38,'<p><span>Apakah mereka lebih baik ataukah kaum Tubba</span><span>’</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2700">2700</a><span>]</span><span> dan orang-orang sebelum mereka ? Kami telah membinasakan mereka karena Sesungguhnya mereka orang-orang yang berdosa.</span></p>');
INSERT INTO `quran` VALUES (10842,2,44,39,'<p><span>[b] </span><span>Dan, tidaklah Kami menciptakan seluruh langit dan bumi dan segala yang ada di antara keduanya dengan main-main. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2701">2701</a><span>]</span></p><p><span>[b]</span><span> 21:17 ; 38:28.</span></p>');
INSERT INTO `quran` VALUES (10843,2,44,40,'<p><span>Tidaklah Kami menjadikan keduanya itu melainkan dengan kebenaran, akan tetapi kebanyakan mereka tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (10844,2,44,41,'<p><span>Sesungguhnya, Hari Keputusan itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2702">2702</a><span>]</span><span> waktu yang telah ditetapkan untuk mereka semua,</span></p>');
INSERT INTO `quran` VALUES (10845,2,44,42,'<p><span>[a] </span><span>Hari yang tidak bermanfaat seorang teman dari teman lainnya sesuatu pun, dan mereka tidak akan ditolong,</span></p><p><span>[a]</span><span> 2:124 ; 70:11 ; 80:35-37.</span></p>');
INSERT INTO `quran` VALUES (10846,2,44,43,'<p><span>Kecuali orang yang dikasihani Allah. Sesungguhnya, Dia Mahaperkasa, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (10847,2,44,44,'<p><span>R.2</span></p><p><span>Sesungguhnya, </span><span>[b] </span><span>pohon Zaqqum itu,</span></p><p><span>[b]</span><span> 37:63 ; 56:53.</span></p>');
INSERT INTO `quran` VALUES (10848,2,44,45,'<p><span>Adalah makanan orang yang banyak berdosa,</span></p>');
INSERT INTO `quran` VALUES (10849,2,44,46,'<p><span>Seperti cairan tembaga, </span><span>[c] </span><span>mendidih dalam perut </span><span>mereka</span><span>,</span></p><p><span>[c]</span><span> 22:21.</span></p>');
INSERT INTO `quran` VALUES (10850,2,44,47,'<p><span>Seperti mendidihnya air panas.</span></p>');
INSERT INTO `quran` VALUES (10851,2,44,48,'<p><span>Kami perintah para malaikat,</span><span> “Tangkaplah dia dan seretlah dia ke dalam Api Jahannam,</span></p>');
INSERT INTO `quran` VALUES (10852,2,44,49,'<p><span>“Kemudian </span><span>[d] </span><span>tuangkanlah ke atas kepalanya dari azab air mendidih.”</span></p><p><span>[d]</span><span> 22:20 ; 55:45.</span></p>');
INSERT INTO `quran` VALUES (10853,2,44,50,'<p><span>“Rasakanlah azab, Sesungguhnya engkau </span><span>telah menganggap diri engkau perkasa</span><span>, dan mulia; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2703">2703</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10854,2,44,51,'<p><span>“Sesungguhnya, inilah apa yang kamu ragukan mengenainya.”</span></p>');
INSERT INTO `quran` VALUES (10855,2,44,52,'<p><span>Sesungguhnya, </span><span>[a] </span><span>orang-orang muttaqi </span><span>akan tinggal</span><span> di tempat yang aman,</span></p><p><span>[a]</span><span> 36:16 ; 52:18.</span></p>');
INSERT INTO `quran` VALUES (10856,2,44,53,'<p><span>[b] </span><span>Di tengah kebun-kebun di mata-mata air,</span></p><p><span>[b]</span><span> 68:35 ; 78:32.</span></p>');
INSERT INTO `quran` VALUES (10857,2,44,54,'<p><span>[c] </span><span>Mereka berpakaian sutera halus dan brokat tebal, </span><span>duduk</span><span> berhadap-hadapan,</span></p><p><span>[c]</span><span> 18:32 ; 76:22.</span></p>');
INSERT INTO `quran` VALUES (10858,2,44,55,'<p><span>Demikianlah </span><span>akan terjadi</span><span>. Dan </span><span>[d] </span><span>Kami memberi mereka pasangan-pasangan bidadari dengan mata jeli.</span></p><p><span>[d]</span><span> 55:71-73; 56:23.</span></p>');
INSERT INTO `quran` VALUES (10859,2,44,56,'<p><span>Di dalamnya mereka memesan </span><span>[e] </span><span>segala </span><span>macam</span><span> buah-buahan, dengan aman sentosa.</span></p><p><span>[e]</span><span> 55:53 ; 56:21.</span></p>');
INSERT INTO `quran` VALUES (10860,2,44,57,'<p><span>Mereka tidak akan mengalami di dalamnya kematian, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2704">2704</a><span>]</span><span> kecuali kematian pertama, </span><span>[f] </span><span>dan Dia akan melindungi mereka dari azab Api yang berkobar-kobar.</span></p><p><span>[f]</span><span> 52:28.</span></p>');
INSERT INTO `quran` VALUES (10861,2,44,58,'<p><span>Suatu Karunia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2705">2705</a><span>]</span><span> dari Tuhan engkau. </span><span>[g] </span><span>Itulah kemenangan yang besar.</span></p><p><span>[g]</span><span> 37:61.</span></p>');
INSERT INTO `quran` VALUES (10862,2,44,59,'<p><span>[h] </span><span>Dan, Sesungguhnya Kami telah memudahkannya </span><span>Alquran</span><span> dengan bahasa engkau, supaya mereka mengambil nasihat.</span></p><p><span>[h]</span><span> 19:98 ; 54:18.</span></p>');
INSERT INTO `quran` VALUES (10863,2,44,60,'<p><span>Maka tunggulah; Sesungguhnya, mereka pun sedang menunggu.</span></p>');
INSERT INTO `quran` VALUES (10864,2,45,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10865,2,45,2,'<p><span>[b]</span><span>Tuhan Maha Terpuji, Maha Mulia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2705a">2705a</a><span>]</span></p><p><span>[b]</span><span> 41:2</span></p>');
INSERT INTO `quran` VALUES (10866,2,45,3,'<p><span>[c]</span><span>Turunnya Kitab ini dari Allah, Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[c]</span><span> 32:3 ; 36:6 ; 40:3 ; 41:3.</span></p>');
INSERT INTO `quran` VALUES (10867,2,45,4,'<p><span>[d]</span><span> Sesungguhnya, di seluruh langit dan bumi adalah Tanda-tanda bagi orang-orang yang beriman.</span></p><p><span>[d]</span><span> 2:165 ; 43:30.</span></p>');
INSERT INTO `quran` VALUES (10868,2,45,5,'<p><span>Dan, dalam kejadian kamu dan apa yang Dia sebarkan dari hewan-hewan, ada Tanda-tanda bagi kaum yang yakin,</span></p>');
INSERT INTO `quran` VALUES (10869,2,45,6,'<p><span>Dan </span><span>[e] </span><span>pergantian malam dan siang, dan apa yang diturunkan Allah dari awan sebagai rezeki, yang menajadi itu </span><span>[f] </span><span>Dia menghidupkan bumi sesudah matinya, dan dalam perubahan angin, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2706">2706</a><span>]</span><span> ada Tanda-tanda bagi kaum yang berakal.</span></p><p><span>[e]</span><span> 2:165 ; 3:191 ; 10:7.</span></p><p><span>[f]</span><span> 16:66 ; 30:51.</span></p>');
INSERT INTO `quran` VALUES (10870,2,45,7,'<p><span>Itulah Tanda-tanda Allah, yang Kami membacakannya kepada engkau dengan benar, kemudian kepada perkataan manakah, setelah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2707">2707</a><span>]</span><span> </span><span>menolak Firman</span><span> Allah dan Tanda-tanda-Nya, mereka akan beriman?</span></p>');
INSERT INTO `quran` VALUES (10871,2,45,8,'<p><span>Celakalah setiap pendusta lagi berdosa,</span></p>');
INSERT INTO `quran` VALUES (10872,2,45,9,'<p><span>Yang mendengar ayat-ayat Allah ketika dibacakan kepadanya, dan kemudian ia bersikeras dengan kesombongan seolah-olah ia tidak mendengarkannya, maka berikanlah kepadanya khabar tentang azab pedih.</span></p>');
INSERT INTO `quran` VALUES (10873,2,45,10,'<p><span>Dan, apabila ia mengetahui sesuatu tentang Tanda-tanda Kami, </span><span>[a] </span><span>ia menjadikannya perolokan. Bagi orang-orang demikian ada azab yang menghinakan.</span></p><p><span>[a]</span><span> 31:7</span></p>');
INSERT INTO `quran` VALUES (10874,2,45,11,'<p><span>[b] </span><span>Di hadapan mereka ada Jahannam; dan tidak bermanfaat bagi mereka sedikitpun apa yang telah diusahakan mereka, dan tidak </span><span>bermanfaat</span><span> apa yang telah mereka jadikan pelindung-pelindung selain Allah, dan bagi mereka ada azab yang besar.</span></p><p><span>[b]</span><span> 14:17-18.</span></p>');
INSERT INTO `quran` VALUES (10875,2,45,12,'<p><span>Inilah petunjuk </span><span>yang benar.</span><span> </span><span>[c] </span><span>Dan, bagi orang-orang yang ingkar kepada Tanda- tanda Tuhan mereka, bagi mereka ada azab yang sangat pedih.</span></p><p><span>[c]</span><span> 2:40 ; 22:58.</span></p>');
INSERT INTO `quran` VALUES (10876,2,45,13,'<p><span>R. 2</span></p><p><span>Allah </span><span>[a] </span><span>Yang menundukkan laut kepadamu, supaya kapal-kapal dapat berlayar di dalamnya dengan perintah-Nya, dan supaya kamu dapat mencari karunia-Nya, dan supaya kamu bersyukur.</span></p><p><span>[a]</span><span> 6:15 ; 17:67 ; 35:13.</span></p>');
INSERT INTO `quran` VALUES (10877,2,45,14,'<p><span>[b] </span><span>Dan, Dia telah menundukkan bagimu apa-apa yang ada di langit dan di bumi, kesemuanya itu dari Dia. Sesungguhnya, dalam hal itu adalah Tanda-tanda bagi kaum yang mau berfikir. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2708">2708</a><span>]</span></p><p><span>[b]</span><span> 22:66.</span></p>');
INSERT INTO `quran` VALUES (10878,2,45,15,'<p><span>Katakanlah kepada orang-orang beriman, </span><span>supaya </span><span>mereka memaafkan orang-orang yang tidak takut kepada Hari-hari Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2709">2709</a><span>]</span><span> supaya Dia akan membalas suatu kaum menurut apa yang telah mereka usahakan.</span></p>');
INSERT INTO `quran` VALUES (10879,2,45,16,'<p><span>[c]</span><span> Barangsiapa beramal shaleh, tentu bagi dirinya sendiri; dan barangsiapa berbuat buruk, maka </span><span>kerugian</span><span> atas dirinya, kemudian kepada Tuhan-mu kamu akan dikembalikan.</span></p><p><span>[c]</span><span> 29:7.</span></p>');
INSERT INTO `quran` VALUES (10880,2,45,17,'<p><span>Dan, Sesungguhnya, </span><span>[d] </span><span>Kami telah memberikan kepada Bani Israil Alkitab dan kekuasaan dan kenabian; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2710">2710</a><span>]</span><span> dan </span><span>[e] </span><span>Kami merezekikan kepada mereka barang- barang yang baik, dan Kami lebihkan mereka di atas sekalian umat di </span><span>zamannya.</span></p><p><span>[d]</span><span> 6:90.</span></p><p><span>[e] </span><span>10:94</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10881,2,45,18,'<p><span>Dan, Kami memberikan kepada mereka Tanda-tanda yang jelas mengenai urusan ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2711">2711</a><span>]</span><span> </span><span>[a] </span><span>dan tidaklah mereka berselisih melainkan setelah datang kepada mereka ilmu, disebabkan kedengkian di antara mereka. Sesungguhnya, Tuhan engkau akan memutuskan di antara mereka pada Hari Kiamat tentang apa yang di dalamnya mereka berselisih.</span></p><p><span>[a]</span><span> 42:15 ; 98:5.</span></p>');
INSERT INTO `quran` VALUES (10882,2,45,19,'<p><span>Kemudian Kami menetapkan engkau di atas satu cara syariat, maka ikutilah itu, </span><span> </span><span>dan janganlah mengikuti hawa nafsu orang-orang yang tidak mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2712">2712</a><span>]</span></p><p><span>[b]</span><span> 5:49 ; 6:151.</span></p>');
INSERT INTO `quran` VALUES (10883,2,45,20,'<p><span>Sesungguhnya mereka sekali-kali tidak berguna sedikitpun bagimu terhadap Allah. Dan Sesungguhnya, orang-orang aniaya itu sebagian mereka pelindung sebagian yang lain; tetapi Allah itu Pelindung bagi orang-orang yang bertakwa.</span></p>');
INSERT INTO `quran` VALUES (10884,2,45,21,'<p><span>[a] </span><span>Alquran</span><span> ini adalah dalil-dalil yang diterima akal bagi umat manusia dan merupakan petunjuk dan rahmat bagi kaum yang yakin.</span></p><p><span>[a]</span><span> 7:204</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10885,2,45,22,'<p><span>[b] </span><span>Apakah menyangka orang-orang berbuat keburukan, bahwa Kami akan menjadikan mereka sama seperti orang-orang yang beriman dan beramal shaleh, sehingga kehidupan mereka dan kematian mereka akan sama juga ? </span><span>Sungguh</span><span> buruklah apa yang mereka putuskan.</span></p><p><span>[b] </span><span>32:19 ; 38:29</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10886,2,45,23,'<p><span>R. 3</span></p><p><span>Dan, Allah telah menciptakan seluruh langit dan bumi dengan benar, supaya dibalas </span><span>[c] </span><span>setiap jiwa sesuai apa yang dia usahakan; dan mereka tidak akan dianiaya</span></p><p><span>[c]</span><span> 14:52 ; 40:18</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10887,2,45,24,'<p><span>Apakah pernah engkau merenungkan </span><span>[d] </span><span>orang yang menjadikan hawa nafsunya sebagai tuhannya, dan Allah menyesatkan, menurut ilmu-Nya </span><span>[e] </span><span>dan dia memeterai atas telinganya dan hatinya dan Dia telah meletakkan tutupan di atas matanya? Maka siapakah yang akan dapat memberi petunjuk kepadanya sesudah Allah ? Apakah kamu tidak mengambil nasihat ?</span></p><p><span>[d] </span><span>25:44</span><span>.</span></p><p><span>[e] </span><span>2:8 ; 6:47 : 16:109</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10888,2,45,25,'<p><span>Dan, mereka berkata, </span><span>[f] </span><span>“ Tidak ada bagi kami selain kehidupan di dunia ini; kami mati dan kami hidup; dan tidak ada sesuatu yang membinasakan kami selain waktu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2713">2713</a><span>]</span><span> Tetapi, mereka tidak memiliki ilmu mengenai hal itu; tidaklah mereka kecuali mengira-ngira.</span></p><p><span>[f]</span><span> 6:30 ; 23:38</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10889,2,45,26,'<p><span>Dan, apabila dibacakan kepada mereka Tanda-tanda Kami yang jelas, tiada perbantahan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2714">2714</a><span>]</span><span> mereka selain berkata, “Datangkanlah bapak-bapak kami, jika kamu orang benar.”</span></p>');
INSERT INTO `quran` VALUES (10890,2,45,27,'<p><span>Katakanlah, “Allah Yang menghidupkan kamu, kemudian Dia </span><span>[a] </span><span>mematikan kamu. kemudian Dia menhimpunkan kamu hingga Hari Kiamat, yang tidak ada keraguan di dalamnya, akan tetapi, kebanyakan manusia tidak mengetahui.</span></p><p><span>[a]</span><span> 2:29 ; 22:67</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10891,2,45,28,'<p><span>R. 4</span></p><p><span>Dan kepunyaan Allah kerajaan seluruh langit dan bumi. Dan pada hari ketika Saat itu datang, pada hari itu sangat rugi orang-orang yang mengerjakan kebatilan.</span></p>');
INSERT INTO `quran` VALUES (10892,2,45,29,'<p><span>Dan, engkau akan melihat tiap-tiap umat bertekuk lutut, tiap-tiap umat akan </span><span>[b] </span><span>dipanggil kepada Kitabnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2714A">2714A</a><span>]</span><span> pada hari itu kamu akan dibalas atas apa yang telah kamu kerjakan.</span></p><p><span>[b] </span><span>17:15</span></p>');
INSERT INTO `quran` VALUES (10893,2,45,30,'<p><span>[a] </span><span>“Inilah Kitab Kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2715">2715</a><span>]</span><span> yang berbicara menentang kamu dengan benar. Sesungguhnya, Kami mencatat apa yang kamu kerjakan.</span></p><p><span>[a] </span><span>17:15 ; 83:21</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10894,2,45,31,'<p><span>[b] </span><span>Adapun orang-orang yang beriman dan beramal shaleh, maka Tuhan mereka akan memasukkan mereka dalam rahmat-Nya. Itulah kemenangan yang nyata.</span></p><p><span>[b] </span><span>83:23</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10895,2,45,32,'<p><span>Dan, orang-orang yang ingkar, </span><span>dikatakan kepada mereka</span><span>, </span><span>[c] </span><span>”Tidaklah Tanda- tanda-Ku telah dibacakan kepadamu ? Namun, kamu berlaku sombong dan kamu adalah kaum yang berdosa.”</span></p><p><span>[c]</span><span> 23:106 ; 67:9-10</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10896,2,45,33,'<p><span>Dan, apabila dikatakan, “Sesungguhnya janji Allah itu pasti benar dan </span><span>[d] </span><span>saat itu tidak ada keraguan di dalamnya.” Kamu berkata, “Kami tidak mengetahui saat itu apa; tidaklah kami menduga selain khayalan belaka dan kami tidak yakin.”</span></p><p><span>[d] </span><span>18:22 ; 20:16 ; 22:8</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10897,2,45,34,'<p><span>[e] </span><span>Dan sudah nampak bagi mereka keburukan-keburukan yang mereka lakukan, dan mengepung mereka apa yang mengenainya mereka perolok-olok.</span></p><p><span>[e] </span><span>15:35 ; 21:42 ; 39:49</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10898,2,45,35,'<p><span>Dan, dikatakan </span><span>kepada mereka</span><span>, </span><span>[a] </span><span>”Pada hari ini Kami akan melupakan kamu sebagaimana kamu telah melupakan pertemuan pada harimu ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2716">2716</a><span>]</span><span> dan tempat tinggalmu adalah Api, dan tidak ada bagimu penolong;</span></p><p><span>[a] </span><span>7:52</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10899,2,45,36,'<p><span>“Hal itu disebabkan </span><span>[b] </span><span>kamu telah menjadikan Tanda-tanda Allah perolokan, dan kamu telah diperdayakan oleh kehidupan dunia ini, maka pada hari itu mereka tidak akan dikeluarkan darinya, dan tidak akan diterima alasan mereka.”</span></p><p><span>[b] </span><span>5:58-59</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10900,2,45,37,'<p><span>Maka segala puji kepunyaan Allah, Tuhan seluruh langit dan Tuhan bumi, Tuhan semesta alam.</span></p>');
INSERT INTO `quran` VALUES (10901,2,45,38,'<p><span>[c] </span><span>Dan, kepunyaan-Nya segala keagungan di seluruh langit dan bumi dan Dia-lah Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[c] </span><span>30:28</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10902,2,46,1,'<p><span>JUZ XXVI</span></p><p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10903,2,46,2,'<p><span>[b]</span><span> Tuhan Maha Terpuji, Maha Mulia.</span></p><p><span>[b]</span><span> 40:2 ; 41:2 ; 42:2 ; 43:2 ; 44:2 ; 45:2.</span></p>');
INSERT INTO `quran` VALUES (10904,2,46,3,'<p><span>[c]</span><span> Diturunkannya Kitab ini dari Allah, Maha Perkasa, Maha Bijaksana.</span></p><p><span>[c]</span><span> 20:5 ; 32:3 ; 36:6 ; 40:3 ; 45:3.</span></p>');
INSERT INTO `quran` VALUES (10905,2,46,4,'<p><span>[d]</span><span> ”Tidaklah Kami menciptakan seluruh langit dan bumi dan apa yang ada di antara keduanya, melainkan dengan benar, dan untuk masa tertentu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2717">2717</a><span>]</span><span> Tetapi orang-orang yang ingkar dari apa yang mereka diperingatkan, mereka berpaling.”</span></p><p><span>[d]</span><span> 21:17 ; 38:28 ; 44:39.</span></p>');
INSERT INTO `quran` VALUES (10906,2,46,5,'<p><span>Katakanlah, </span><span>[e]</span><span> ”Apakah kamu mengetahui apa yang kamu seru selain dari Allah? Tunjukkanlah kepadaku apa yang telah diciptakan mereka dari bumi ini, atau mereka mempunyai bagian dalam </span><span>penciptaan</span><span> seluruh langit? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2718">2718</a><span>]</span><span> Bawalah kepadaku sebuah Kitab sebelum ini atau peninggalan dari ilmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2719">2719</a><span>]</span><span> jika kamu benar.”</span></p><p><span>[e]</span><span> 35:41.</span></p>');
INSERT INTO `quran` VALUES (10907,2,46,6,'<p><span>Dan, Siapakah yang leibh sesat dari pada orang yang menyeru selain Allah, yang tidak dapat mengabulkannya sampai Hari Kiamat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2720">2720</a><span>]</span><span> dan </span><span>[a]</span><span> mereka lalai dari doa mereka?</span></p><p><span>[a]</span><span> 10:30.</span></p>');
INSERT INTO `quran` VALUES (10908,2,46,7,'<p><span>[b]</span><span> Dan, ketika dibangkitkan umat manusia, mereka, </span><span>tuhan-tuhan palsu</span><span>, akan menjadi musuh-musuh bagi mereka dan akan mengingkari ibadah mereka.</span></p><p><span>[b]</span><span> 6:23 ; 10:29.</span></p>');
INSERT INTO `quran` VALUES (10909,2,46,8,'<p><span>[c]</span><span> Dan, apabila dibacakan kepada mereka Ayat-ayat Kami yang jelas, berkatalah orang-orang yang ingkar kepada kebenaran ketika datang kepada mereka, “Ini adalah sihir yang nyata.”</span></p><p><span>[c]</span><span> 34:44 ; 61:7.</span></p>');
INSERT INTO `quran` VALUES (10910,2,46,9,'<p><span>Apakah mereka berkata,”ia telah mengada-adakannya, </span><span>Alquran?</span><span> </span><span>[d]</span><span> Katakanlah, “Sekiranya aku telah mengada-adakannya, kamu tidak memiliki sesuatu pun melawan Allah untuk membela aku. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2721">2721</a><span>]</span><span> Dan Dia lebih mengetahui apa yang kamu katakan tanpa tujuan di dalamnya. Cukuplah Dia sebagai saksi antara aku dan kamu, Dan, Dia-lah Maha Pengampun, Maha Penyayang.”</span></p><p><span>[d]</span><span> 11:36.</span></p>');
INSERT INTO `quran` VALUES (10911,2,46,10,'<p><span>Katakanlah, “Aku bukan </span><span>rasul</span><span> baru di antara rasul-rasul, dan tidak pula aku mengetahui apa yan akan diperbuat terhadapku atau pun terhadapmu. </span><span>[a]</span><span> Aku hanyalah mengikuti apa yang diwahyukan kepadaku; dan aku tidak lain melainkan seorang pemberi peringatan yang nyata.”</span></p><p><span>[a]</span><span> 6:51 ; 7:204.</span></p>');
INSERT INTO `quran` VALUES (10912,2,46,11,'<p><span>Katakanlah, “Terangkanlah kepadaku jika </span><span>Alquran</span><span> ini dari Allah dan kamu tidak percaya kepadanya dan memberi kesaksian </span><span>[b]</span><span> seorang saksi dari antara Bani Israil </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2722">2722</a><span>]</span><span> terhadap </span><span>kedatangan</span><span> seseorang semisalnya, kemudian ia beriman, tetapi kamu berlaku sombong?” Sesungguhnya Allah tidak memberi petunjuk kepada kaum yang aniaya.</span></p><p><span>[b]</span><span> 11:18 ; 61:7.</span></p>');
INSERT INTO `quran` VALUES (10913,2,46,12,'<p><span>R. 2</span></p><p><span>Dan berkata orang-orang yang ingkar kepada orang-orang yang beriman, </span><span>[c]</span><span> ”Seandainya </span><span>Alquran </span><span>itu baik, tentulah mereka tidak dapat mendahului kami </span><span>untuk beriman</span><span> kepadanya,” Dan karena mereka tidak mendapat petunjuk dengan itu, maka mereka akan berkata, “Ini adalah dusta yang lama.”</span></p><p><span>[c]</span><span> 11:28.</span></p>');
INSERT INTO `quran` VALUES (10914,2,46,13,'<p><span>Dan, sebelum ini telah ada </span><span>[a]</span><span> Kitab Musa, sebagai petunjuk dan rahmat; dan </span><span>[b]</span><span> ini adalah Kitab dalam bahasa Arab yang membenarkan </span><span>nubuatan-nubuatan terdahulu,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2723">2723</a><span>]</span><span> supaya memberi peringatan kepada orang-orang yang berlaku aniaya dan khabar suka untuk orang-orang yang berbuat baik.</span></p><p><span>[a]</span><span> 28:44.</span></p><p><span>[b]</span><span> 20:114 ; 42:8 ; 43:4.</span></p>');
INSERT INTO `quran` VALUES (10915,2,46,14,'<p><span>[c]</span><span> Sesungguhnya orang-orang yang berkata, “Tuhan kami adalah Allah,” kemudian mereka tetap bersiteguh, maka tiada ketakutan atas mereka, dan tidak pula mereka bersedih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2724">2724</a><span>]</span></p><p><span>[c]</span><span> 29:70 ; 41:31.</span></p>');
INSERT INTO `quran` VALUES (10916,2,46,15,'<p><span>Mereka itulah penghuni surga; mereka akan menetap di dalamnya, sebagai ganjaran atas apa yang telah mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (10917,2,46,16,'<p><span>[a]</span><span> Dan Kami diperintahkan kepada manusia supaya berbuat baik terhadap orangtuanya. Ibunya mengandungnya dengan susah-payah, dan melahirkannya dengan susah-payah. Dan mengandungnya dan menyapihnya selama tiga puluh bulan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2725">2725</a><span>]</span><span> Hingga apabila ia mencapai </span><span>usia</span><span> dewasanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2726">2726</a><span>]</span><span> dan mencapai usia empat puluh tahun, ia berkata, </span><span>[b]</span><span> ”Hai Tuhan-ku, limpahkanlah taufik kepadaku supaya aku dapat bersyukur atas nikmat Engkau yang telah Engkau anugerahkan kepadaku dan kepada orangtuaku, dan supaya aku dapat beramal shaleh yang Engkau meridhainya, dan perbaikilah bagiku dalam keturunanku. Sesungguhnya, aku kembali kepada Engkau; dan Sesungguhnya aku termasuk orang-orang yang berserah diri.</span></p><p><span>[a]</span><span> 6:152 ; 17:24 ; 29:9.</span></p><p><span>[b]</span><span> 27:20.</span></p>');
INSERT INTO `quran` VALUES (10918,2,46,17,'<p><span>Mereka itulah orang-orang Yang Kami terima dari mereka yang terbaik apa yang mereka amalkan dan akan Kami ampuni keburukan-keburukan mereka di antara penghuni surga. Janji yang benar yang telah dijanjikan kepada </span><span>[a]</span><span> mereka.</span></p><p><span>[a]</span><span> 17:109 ; 19:62 ; 73 :19.</span></p>');
INSERT INTO `quran` VALUES (10919,2,46,18,'<p><span>Dan, orang yang berkata kepada kedua Ibu-bapaknya, “Cislah untuk kamu berdua; kamu mengancamku bahwa aku akan dibangkitkan, padahal keturunan demi keturunan telah berlaku sebelumku?” Dan mereka berdua meratap kepada Allah mohon pertolongan </span><span>dan berkata kepada anaknya,</span><span> “Celakalah engkau , berimanlah! Sesungguhnya janji Allah itu benar.” Tetapi ia berkata,” Ini tidak lain selain dongengan orang-orang dahulu.”</span></p>');
INSERT INTO `quran` VALUES (10920,2,46,19,'<p><span>Mereka itulah orang-orang yang telah sempurna atas mereka khabar ghaib tentang </span><span>azab</span><span> dalam </span><span>[b]</span><span> umat-umat yang telah berlalu sebelum mereka dari jin dan manusia. Sesungguhnya mereka itulah orang-orang yang rugi.</span></p><p><span>[b]</span><span> 7:39 ; 41:26.</span></p>');
INSERT INTO `quran` VALUES (10921,2,46,20,'<p><span>Dan, bagi setiap orang ada derajat sesuai apa yang mereka </span><span>[c]</span><span> amalkan dan supaya Dia menyempurnakan kepada mereka ganjaran amal-amal mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2727">2727</a><span>]</span><span> dan mereka tidak akan dianiaya.</span></p><p><span>[c]</span><span> 61:133.</span></p>');
INSERT INTO `quran` VALUES (10922,2,46,21,'<p><span>Dan, pada hari ketika dihadapkan orang-orang yang ingkar kepada Api, </span><span>dikatakan kepada mereka,</span><span> “Kamu telah menghabiskan segala barang yang baik di dalam kehidupan duniamu, dan kamu telah menikmatinya, </span><span>[a]</span><span> maka pada hari ini kamu akan dibalas dengan azab yang hina, sebab kamu berlaku sombong di bumi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2728">2728</a><span>]</span><span> tanpa hak, dan karena kamu telah berbuat durhaka.”</span></p><p><span>[a]</span><span> 6:94.</span></p>');
INSERT INTO `quran` VALUES (10923,2,46,22,'<p><span>R. 3</span></p><p><span>Dan ingatlah </span><span>[b]</span><span> saudaraku </span><span>‘</span><span>Ad, ketika ia memperingatkan kaumnya di sebelah bukit-bukit pasir, dan Sesungguhnya telah berlalu pemberi-pemberi ingat sebelum dia dan juga sesudahnya, mengatakan, “Jangan-lah kamu menyembah selain Allah. Sesungguhnya aku takut atasmu azab hari yang besar.”</span></p><p><span>[b]</span><span> 7:66 ; 11:51.</span></p>');
INSERT INTO `quran` VALUES (10924,2,46,23,'<p><span>Mereka berkata, “Apakah engkau datang kepada kami supaya engkau memalingkan kami dari tuhan-tuhan kami? Maka bawalah kepada kami apa yang engkau janjikan kepada kami, jika engkau termasuk </span><span>[c]</span><span> orang-orang yang benar.”</span></p><p><span>[c]</span><span> 7:71.</span></p>');
INSERT INTO `quran` VALUES (10925,2,46,24,'<p><span>Ia, </span><span>Hud</span><span>, berkata, “Sesungguhnya ilmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2729">2729</a><span>]</span><span> yang benar hanya di sisi Allah, dan aku menyampaikan kepadamu dengan apa yang aku diutus, akan tetapi aku melihat kamu adalah kaum yang bodoh.”</span></p>');
INSERT INTO `quran` VALUES (10926,2,46,25,'<p><span>Maka apabila mereka melihatnya </span><span>azab</span><span> </span><span>berupa</span><span> awan menuju ke lembah-lembah mereka, mereka berkata, “Inilah awan yang akan memberi hujan kepada kami.” </span><span>Kami berfirman,</span><span> “Tidak, bahkan itulah yang kamu minta supaya disegerakannya, </span><span>[a]</span><span> Angin yang di dalamnya ada azab yang pedih,</span></p><p><span>[a]</span><span> 41:17.</span></p>');
INSERT INTO `quran` VALUES (10927,2,46,26,'<p><span>“Ia, angin, akan membinasakan segala sesuatu dengan perintah Tuhan-Nya.” Maka pada waktu subuh tiada sesuatu yang kelihatan selain rumah-rumah mereka. Demikianlah Kami membalas kaum yang berdosa.</span></p>');
INSERT INTO `quran` VALUES (10928,2,46,27,'<p><span>Dan Sesungguhnya </span><span>[a]</span><span> Kami telah memperkuat kedudukan mereka di dalam hal-hal yang Kami tidak memperkuat kedudukan kamu di dalamnya; dan Kami memberikan kepada mereka pendengaran, penglihatan, dan hati. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2730">2730</a><span>]</span><span> Tetapi, tidak bermanfaat sedikit pun bagi mereka pendengaran mereka dan penglihatan mereka dan tidak pula hati mereka, karena mereka mengingkari Tanda-tanda Allah; dan mengepung mereka </span><span>[b]</span><span> apa yang mereka selalu perolok-olok.</span></p><p><span>[a]</span><span> 6:7.</span></p><p><span>[b] </span><span>21:42</span><span>.</span></p>');
INSERT INTO `quran` VALUES (10929,2,46,28,'<p><span>R. 4</span></p><p><span>Dan Sesungguhnya, Kami membinasakan kota-kota di sekitarmu; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2731">2731</a><span>]</span><span> dan Kami telah menjelaskan Tanda-tanda supaya mereka kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2732">2732</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10930,2,46,29,'<p><span>[a]</span><span> Maka, mengapa tidak menolong mereka orang-orang yang mereka jadikan teman tuhan yang mendekatkan mereka, selain Allah? Tidak, bahkan mereka itu lenyap dari </span><span>mata </span><span>mereka. Itulah kedustaan mereka dan apa yang mereka ada-adakan.</span></p><p><span>[a]</span><span> 42:47.</span></p>');
INSERT INTO `quran` VALUES (10931,2,46,30,'<p><span>Dan ingatlah ketika </span><span>[b]</span><span> Kami hadapkan kepada engkau segolongan dari jin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2733">2733</a><span>]</span><span> yang ingin mendengarkan Alquran, maka ketika mereka hadir kepadanya mereka berkata, “Diamlah </span><span>dan dengarkanlah!”</span><span> Dan tatkala telah selesai, mereka kembali kepada kaum mereka </span><span>untuk</span><span> memberi peringatan.</span></p><p><span>[b]</span><span> 72:2.</span></p>');
INSERT INTO `quran` VALUES (10932,2,46,31,'<p><span>Mereka berkata, “Hai kaum kami, Sesungguhnya </span><span>[c]</span><span> kami telah mendengar suatu Kitab, yang telah diturunkan sesudah Musa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2734">2734</a><span>]</span><span> membenarkan apa yang ada sebelumnya, serta memimpin kepada kebenaran dan kepada jalan yang lurus.</span></p><p><span>[c] </span><span>72:2-3.</span></p>');
INSERT INTO `quran` VALUES (10933,2,46,32,'<p><span>“Hai, kaum kami, sambutlah penyeru Allah, </span><span>Muhammad,</span><span> dan berimanlah kepadanya, Dia akan mengampuni dosa-dosamu, dan akan melindungi kamu dari azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (10934,2,46,33,'<p><span>“Dan, Barangsiapa tidak menyambut penyeru Allah, </span><span>Muhammad</span><span>, maka ia tidak dapat melemahkannya di bumi dan tidak ada baginya pelindung-pelindung selaih Dia. Mereka itu dalam kesesatan yang nyata.”</span></p>');
INSERT INTO `quran` VALUES (10935,2,46,34,'<p><span>Apakah mereka tidak melihat bahwa Sesungguhnya Allah, Yang telah menciptakan seluruh langit dan bumi dan tidak lelah karena menciptakan segalanya itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2735">2735</a><span>]</span><span> </span><span>[a]</span><span> berkuasa pula menghidupkan yang telah mati? Bahkan Sesungguhnya, Dia berkuasa atas segala sesuatu.</span></p><p><span>[a]</span><span> 17:100 ; 36:82 ; 86:9.</span></p>');
INSERT INTO `quran` VALUES (10936,2,46,35,'<p><span>Dan pada hari dihadapkan orang-orang ingkar kepada Api. </span><span>Dikatakan kepada mereka,</span><span> “Bukankah ini benar? Mereka berkata, “Ya, benar, demi Tuhan kami.” Dia berfirman, “Maka rasakanlah azab itu, disebabkan kamu selalu ingkar.”</span></p>');
INSERT INTO `quran` VALUES (10937,2,46,36,'<p><span>Maka sabarlah engkau seperti telah bersabar orang-orang yang memiliki keteguhan hati dari antara rasul-rasul; dan janganlah engkau minta azab itu dipercepat bagi mereka. Pada hari ketika mereka melihat apa yang telah diancamkan kepada mereka, </span><span>keadaan mereka </span><span>[a]</span><span> seolah-olah tidak pernah tinggal kecuali hanya sesaat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2736">2736</a><span>]</span><span> pada siang hari. </span><span>peringatan ini </span><span> telah disampaikan, dan tidak ada yang akan dibinasakan selain orang-orang durhaka.</span></p><p><span>[a]</span><span> 10:46 ; 30:56 ; 79:47.</span></p>');
INSERT INTO `quran` VALUES (10938,2,47,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10939,2,47,2,'<p><span>[b] </span><span>Orang-orang yang ingkar dan menghalangi dari jalan Allah, Dia memusnahkan amal-amal mereka.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2737">2737</a><span>]</span></p><p><span>[b] </span><span>4:168 ; 16:89.</span></p>');
INSERT INTO `quran` VALUES (10940,2,47,3,'<p><span>Tetapi, orang-orang yang beriman dan beramal shaleh dan beriman kepada apa yang telah diturunkan kepada Muhammad, bahwa itu adalah kebenaran dari Tuhan mereka, Dia menghapuskan dari mereka dosa-dosa mereka dan memperbaiki keadaan mereka.</span></p>');
INSERT INTO `quran` VALUES (10941,2,47,4,'<p><span>Demikian itu disebabkan orang-orang yang ingkar mengikuti yang bathil, sedangkan orang-orang yang beriman mengikuti kebenaran dari Tuhan mereka. Demikianlah Allah menerangkan kepada manusia perumpamaan-perumpamaan mereka.</span></p>');
INSERT INTO `quran` VALUES (10942,2,47,5,'<p><span>[c]</span><span> ”Dan apabila kamu bertemu dengan orang-orang yang ingkar, maka pukullah leher-leher </span><span>mereka,</span><span> hingga apabila kamu telah mengalahkan mereka,</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2738">2738</a><span>]</span><span> maka perkuatlah belenggu mereka, kemudian sesudah itu </span><span>melepaskan</span><span> mereka sebagai suatu kebaikan atau dengan tebusan hingga perang meletakkan senjatanya. Demikianlah berlaku </span><span>segala peraturan menurut keadaan.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2739">2739</a><span>]</span><span> Dan andaikata Allah menghendaki, tentu Dia mengambil balasan dari mereka, tetapi supaya Dia menguji </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2740">2740</a><span>]</span><span> sebagian dari kamu dengan sebagian yang lain. Dan orang-orang yang terbunuh di jalan Allah, Dia sekali-kali tidak menyia-nyiakan amal-amal mereka.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2741">2741</a><span>]</span></p><p><span>[c] </span><span>8:48, 68.</span></p>');
INSERT INTO `quran` VALUES (10943,2,47,6,'<p><span>Dia akan memberi mereka petunjuk</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2742">2742</a><span>]</span><span> dan akan memperbaiki keadaan mereka.</span></p>');
INSERT INTO `quran` VALUES (10944,2,47,7,'<p><span>[a]</span><span> Dan, Dia akan memasukkan mereka ke surga yang Dia telah memperkenalkannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2743">2743</a><span>] </span><span>kepada mereka.</span></p><p><span>[a]</span><span> 3:196 ; 9:111.</span></p>');
INSERT INTO `quran` VALUES (10945,2,47,8,'<p><span>Hai, orang-orang yang beriman, jika kamu menolong Allah, Dia akan menolong kamu dan akan meneguhkan langkah-langkahmu.</span></p>');
INSERT INTO `quran` VALUES (10946,2,47,9,'<p><span>Dan orang-orang yang ingkar, celakalah bagi mereka; dan Dia menyia-nyiakan amal-amal mereka.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2744">2744</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10947,2,47,10,'<p><span>Demikian itu disebabkan mereka membenci apa yang telah diturunkan Allah, maka Dia telah menghapus amal-amal mereka.</span></p>');
INSERT INTO `quran` VALUES (10948,2,47,11,'<p><span>[a]</span><span> Apakah mereka tidak berpergian di bumi dan melihat bagaimana kesudahan orang-orang yang sebelum mereka?</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2745">2745</a><span>]</span><span> Allah telah membinasakan mereka, dan bagi orang-orang kafir sama seperti mereka.</span></p><p><span>[a]</span><span> 12:110 ; 22:47 ; 30:10 ; 35:45 ; 40:22.</span></p>');
INSERT INTO `quran` VALUES (10949,2,47,12,'<p><span>Demikian itu karena sesungguhnya </span><span>[b]</span><span> Allah Penolong orang-orang yang beriman, dan Sesungguhnya orang-orang kafir tidak ada penolong bagi mereka.</span></p><p><span>[b]</span><span> 3:151 ; 8;41.</span></p>');
INSERT INTO `quran` VALUES (10950,2,47,13,'<p><span>R. 2</span></p><p><span>Sesungguhnya, Allah akan memasukkan orang-orang yang beriman dan beramal shaleh ke dalam kebun-kebun yang mengalir di bawahnya sungai-sungai; </span><span>[c]</span><span> sedang orang-orang yang ingkar akan mendapat sedikit manfaat duniawi dan mereka makan sebagaimana binatang-binatang ternak</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2746">2746</a><span>]</span><span> makan, dan Api akan menjadi tempat tinggal bagi mereka.</span></p><p><span>[c]</span><span> 14:31 ; 77:47.</span></p>');
INSERT INTO `quran` VALUES (10951,2,47,14,'<p><span>Dan, berapa banyak negeri yang lebih kuat daripada negeri engkau yang telah mengusir engkau,</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2747">2747</a><span>]</span><span> Kami telah membinasakan mereka, maka tidak ada penolong bagi mereka.</span></p>');
INSERT INTO `quran` VALUES (10952,2,47,15,'<p><span>Maka </span><span>[a]</span><span> apakah orang yang berpegang teguh pada dalil yang nyata dari Tuhan-nya, sama seperti orang yang ditampakkan indah baginya amal buruknya dan mereka mengikuti hawa nafsu mereka?</span></p><p><span>[a]</span><span> 11:29.</span></p>');
INSERT INTO `quran` VALUES (10953,2,47,16,'<p><span>[b]</span><span> Perumpamaan surga yang dijanjikan kepada orang-orang yang bertakwa; di dalamnya terdapat sungai-sungai yang airnya tidak akan rusak; dan sungai-sungai susu yang rasanya tidak akan berubah; dan sungai-sungai arak yang lezat rasanya bagi orang-orang yang meminum; dan sungai-sungai madu yang dijernihkan.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2748">2748</a><span>]</span><span> Dan bagi mereka di dalamnya ada segala macam buah-buahan, dan pengampunan dari Tuhan mereka. </span><span>Apakah</span><span> sama orang yang tinggal lama di dalam Api dan diberi minum air mendidih, sehingga akan merobek-robek usus mereka?</span></p><p><span>[b] </span><span>13:36.</span></p>');
INSERT INTO `quran` VALUES (10954,2,47,17,'<p><span>Dan di antara mereka ada yang mendengarkan kepada engkau hingga apabila mereka berlalu dari sisi engkau, mereka berkata kepada orang-orang yang telah diberi ilmu, “Apa yang telah dikatakannya tadi?”</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2749">2749</a><span>]</span><span> </span><span>[a]</span><span> Mereka itulah orang-orang yang Allah telah memeterai hati mereka dan mereka mengikuti hawa nafsu mereka.</span></p><p><span>[a]</span><span> 16:109 ; 63:4.</span></p>');
INSERT INTO `quran` VALUES (10955,2,47,18,'<p><span>Dan, orang-orang yang mendapat petunjuk, </span><span>[a] </span><span>Dia menambahkan petunjuk kepada mereka, dan Dia memberikan kepada mereka ketakwaan mereka.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2750">2750</a><span>]</span></p><p><span>[a]</span><span> 8:3.</span></p>');
INSERT INTO `quran` VALUES (10956,2,47,19,'<p><span>Maka tidaklah mereka menunggu selain Saat yang akan datang kepada mereka </span><span>[b]</span><span>dengan tiba-tiba, maka Sesungguhnya telah datang Tanda-tandanya.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2751">2751</a><span>]</span><span> Apakah faedah bagi mereka, apabila telah datang peringatan itu kepada mereka?</span></p><p><span>[b]</span><span> 22:56 ; 43:67.</span></p>');
INSERT INTO `quran` VALUES (10957,2,47,20,'<p><span>Maka ketahuilah, bahwa tiada tuhan selain Allah, dan mohon ampunlah untuk kelemahan-kelemahan engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2752">2752</a><span>]</span><span> dan untuk orang-orang mukmin laki-laki dan perempuan. Dan Allah mengetahui tempat kamu berkeliaran dan tempat kamu tinggal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2753">2753</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10958,2,47,21,'<p><span>R. 3</span></p><p><span>Dan berkata orang-orang yang beriman, “Mengapa tidak diturunkan </span><span>kepadanya </span><span>suatu Surah?” Tetapi apabila suatu Surah yang jelas diturunkan dan disebutkan di dalamnya </span><span>perintah</span><span> berperang, engkau akan melihat orang-orang yang di dalam hati mereka ada penyakit, mereka melihat kepada engkau dengan pandangan seperti orang pingsan karena takut mati. Maka celaka bagi mereka itu !</span></p>');
INSERT INTO `quran` VALUES (10959,2,47,22,'<p><span>Pekerjaan kami</span><span> hanya taat </span><span>[a]</span><span> dan ucapan yang baik, maka apabila urusan itu telah ditetapkan jika mereka belaku benar kepada Alalh adalah baik bagi mereka.</span></p><p><span>[a]</span><span> 2:264.</span></p>');
INSERT INTO `quran` VALUES (10960,2,47,23,'<p><span>Maka apakah jika kamu berkuasa, akan membuat kerusakan di bumi dan memutuskan hubungan kekeluargaanmu.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2754">2754</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10961,2,47,24,'<p><span>Itulah orang-orang yang dilaknat Allah, maka Dia telah menjadikan mereka tuli dan menjadikan buta penglihatan mereka.</span></p>');
INSERT INTO `quran` VALUES (10962,2,47,25,'<p><span>[a]</span><span> Apakah mereka tidak merenungkan Alquran, ataukah di hari mereka ada kunci- kuncinya?</span></p><p><span>[a]</span><span> 4:83.</span></p>');
INSERT INTO `quran` VALUES (10963,2,47,26,'<p><span>Sesungguhnya, </span><span>[b]</span><span> orang-orang yang berpaling atas punggung mereka sesudah jelas bagi mereka petunjuk, syaitan telah menampakkan indah </span><span>amal</span><span> bagi mereka. Dan memberikan harapan-harapan palsu kepada mereka.</span></p><p><span>[b] </span><span> 3:87.</span></p>');
INSERT INTO `quran` VALUES (10964,2,47,27,'<p><span>Yang demikian itu disebabkan mereka berkata kepada orang-orang yang membenci apa yang telah diturunkan Allah, “Kami akan mentaati kamu dalam beberapa perkara,”</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2755">2755</a><span>]</span><span> dan Allah mengetahui rahasia-rahasia mereka.</span></p>');
INSERT INTO `quran` VALUES (10965,2,47,28,'<p><span>Maka bagaimanakah apabila malaikat-malaikat akan </span><span>[c]</span><span> mematikan mereka dengan memukul muka mereka dan punggung mereka?</span></p><p><span>[c]</span><span> 4:98 ; 8:51 ; 16:29.</span></p>');
INSERT INTO `quran` VALUES (10966,2,47,29,'<p><span>Yang demikian itu disebabkan mereka mengikuti apa yang tidak disukai Allah, dan mereka membenci keridhaan-Nya, maka Dia menyia-nyiakan amal mereka.</span></p>');
INSERT INTO `quran` VALUES (10967,2,47,30,'<p><span>R. 4</span></p><p><span>Apakah mengira orang-orang yang di dalam hati mereka ada penyakit bahwa Allah tidak akan menampakkan kedengkian mereka?</span></p>');
INSERT INTO `quran` VALUES (10968,2,47,31,'<p><span>Dan sekiranya Kami menghendaki, niscaya Kami dapat memperlihatkan mereka kepada engkau, sehingga engkau pasti mengenal mereka dari tanda </span><span>muka</span><span> mereka. Dan, engkau pasti akan mengenal mereka dari nada ucapan merka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2756">2756</a><span>]</span><span> Dan Allah mengetahui segala amal kamu.</span></p>');
INSERT INTO `quran` VALUES (10969,2,47,32,'<p><span>Dan niscaya Kami akan menguji kamu sehingga Kami mengetahui orang-orang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2757">2757</a><span>]</span><span> yang berjihad dari antara kamu dan orang-orang yang </span><span>[a]</span><span> bersabar, dan Kami akan menguji keadaan kamu.</span></p><p><span>[a]</span><span> 3:141-143 ; 29:4, 12.</span></p>');
INSERT INTO `quran` VALUES (10970,2,47,33,'<p><span>Sesungguhnya, orang-orang yang ingkar dan menghalangi dari jalan Allah dan menentang rasul itu setelah jelas bagi mereka petunjuk, mereka sekali-kali tidak akan memudaratkan Allah sedikitpun. Dan Dia segera akan menghapuskan amal mereka.</span></p>');
INSERT INTO `quran` VALUES (10971,2,47,34,'<p><span>Hai, orang-orang yang beriman ! Taatlah kepada Allah dan taatlah kepada Rasul dan janganlah menyia-nyiakan amal kamu.</span></p>');
INSERT INTO `quran` VALUES (10972,2,47,35,'<p><span>[a]</span><span> Sesungguhnya, orang-orang yang ingkar dan menghalangi dari jalan Allah, kemudian mereka mati selagi mereka itu kafir, maka sekali-kali Allah tidak akan mengampuni mereka.</span></p><p><span>[a]</span><span> 3:92 ; 4:19.</span></p>');
INSERT INTO `quran` VALUES (10973,2,47,36,'<p><span>[b]</span><span> Maka janganlah kamu lemah sehingga kamu menuntut damai, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2758">2758</a><span>] </span><span>padahal kamu lebih unggul, dan Allah beserta kamu dan sekali-kali Dia tidak akan mengurangi amal-amalmu.</span></p><p><span>[b] </span><span>3:140.</span></p>');
INSERT INTO `quran` VALUES (10974,2,47,37,'<p><span>[c] </span><span>Sesungguhnya kehidupan dunia ini hanya permainan dan sendagurau, dan jika kamu beriman dan bertakwa, Dia akan memberi ganjaranmu kepadamu dan Dia tidak akan meminta kepadamu harta kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2759">2759</a><span>]</span></p><p><span>[c]</span><span> 6:33 ; 29:65 ; 57:21.</span></p>');
INSERT INTO `quran` VALUES (10975,2,47,38,'<p><span>Sekiranya Dia akan memintanya kepadamu dan mendesakmu, kamu akan berlaku bakhil, dan niscaya Dia akan menampakkan kedengkianmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2760">2760</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10976,2,47,39,'<p><span>Ingatlah, kamu adalah orang-orang yang dipanggil supaya membelanjakan di jalan Allah, tetapi di antara kamu ada orang yang bakhil. Dan barangsiapa yang bakhil, maka Sesungguhnya ia bakhil terhadap dirinya sendiri.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2761">2761</a><span>]</span><span> Dan, Alalh Yang Maha Kaya; dan kamulah orang-orang fakir, </span><span>[a]</span><span> dan jika kamu berpaling, Dia akan menggantikan dengan suatu kaum selain kamu, kemudian mereka tidak akan menjadi seperti kamu.</span><span> [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2762">2762</a><span>]</span></p><p><span>[a]</span><span> 5:55.</span></p>');
INSERT INTO `quran` VALUES (10977,2,48,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (10978,2,48,2,'<p><span>Sesungguhnya Kami telah memberi kepada engkau satu kemenangan nyata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2763">2763</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10979,2,48,3,'<p><span>Supaya Allah melindungi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2764">2764</a><span>]</span><span> engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2765">2765</a><span>]</span><span> di masa lalu dari dosa-dosa yang dibuat terhadap engkau dan di masa yang akan datang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2766">2766</a><span>]</span><span> dan Dia menyempurnakan nikmat-Nya atas engkau; dan memberi petunjuk kepada engkau pada jalan yang lurus;</span></p>');
INSERT INTO `quran` VALUES (10980,2,48,4,'<p><span>Dan Allah akan menolong engkau dengan pertolongan yang perkasa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2767">2767</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10981,2,48,5,'<p><span>Dia-lah Dzat Yang telah menurunkan ketenteraman </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2768">2768</a><span>]</span><span> kedalam hati orang-orang mukmin; supaya mereka dapat menambah lagi iman pada keimanan mereka. Dan kepunyaan Alalh-lah lasykar-lasykar seluruh langit dan bumi. Dan Allah adalah Yang Maha Mengetahui; Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (10982,2,48,6,'<p><span>Supaya Dia akan memasukkan orang-orang mukmin laki-laki dan orang-orang mukmin perempuan ke dalam surga yang di bawahnya mengalir sungai-sungai, mereka akan kekal di dalamnya; dan </span><span>[a]</span><span> Dia akan menghapus dari mereka. Dan yang demikian itu, di sisi Allah adalah kemenangan yang besar.</span></p><p><span>[a]</span><span> 8:30 ; 64:10 ; 66:9.</span></p>');
INSERT INTO `quran` VALUES (10983,2,48,7,'<p><span>Dan </span><span>[b]</span><span> Dia akan mengazab orang-orang munafik laki-laki dan orang-orang munafik perempuan, dan orang-orang musyrik laki-laki dan orang-orang musyrik perempuan. yang berprasangka buruk. </span><span>[c]</span><span> Atas mereka </span><span>menimpa</span><span> malapetaka buruk; dan Allah murka atas mereka, dan Dia mengutuk mereka, dan Dia menyediakan bagi mereka Jahannam. Dan itulah seburuk-buruk tempat tinggal.</span></p><p><span>[b]</span><span> 33:25</span></p><p><span>[c]</span><span> 9:98.</span></p>');
INSERT INTO `quran` VALUES (10984,2,48,8,'<p><span>Dan, kepunyaan Allah lasykar-lasykar seluruh langit dan bumi. Dan Allah adalah Maha Perkasa, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (10985,2,48,9,'<p><span>Sesungguhnya Kami mengutus engkau sebagai saksi dan </span><span>[a]</span><span> pemberi khabar suka dan pemberi peringatan,</span></p><p><span>[a]</span><span> 25:57 ; 33:46 ; 35:25.</span></p>');
INSERT INTO `quran` VALUES (10986,2,48,10,'<p><span>Supaya kamu beriman kepada Allah dan Rasul-Nya, dan menolongnya dan </span><span>[b]</span><span> memuliakannya. Dan </span><span>supaya</span><span> kamu mensucikan-Nya pada pagi dan petang hari.</span></p><p><span>[b]</span><span> 5:13.</span></p>');
INSERT INTO `quran` VALUES (10987,2,48,11,'<p><span>Sesungguhnya orang-orang yang baiat kepada engkau </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2769">2769</a><span>]</span><span> sebenarnya mereka baiat kepada Allah. Tangan Allah ada di atas tangan mereka, maka barangsiapa melanggar </span><span>janjinya</span><span>, maka ia memutuskannya untuk kerugian dirinya sendiri; dan barangsiapa menyempurnakan apa yang dia telah janjikan kepada Allah, maka Dia segera akan memberinya ganjaran yang besar.</span></p>');
INSERT INTO `quran` VALUES (10988,2,48,12,'<p><span>Akan berkata kepada engkau orang-orang yang tertinggal di belakang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2770">2770</a><span>]</span><span> dari orang-orang Arab gurun, “Telah membuat kami sibuk harta kami dan keluarga kami, oleh karena itu mohonkanlah ampunan bagi kami.” </span><span>[a]</span><span> Mereka mengucapkan dengan lidah mereka apa yang tidak ada dalam hati mereka. Katakanlah, “Siapakah yang mampu berbuat barang sedikitpun bagimu menentang Allah, sekiranya Dia menghendaki suatu mudarat atau suatu manfaat bagimu ? Bahkan Allah itu Maha Mengetahui apa yang kamu kerjakan.”</span></p><p><span>[a]</span><span> 3:168.</span></p>');
INSERT INTO `quran` VALUES (10989,2,48,13,'<p><span>“Bahkan kamu menyangka bahwa sekali-kali tidak akan kembali Rasul dan orang-orang mukmin kepada keluarga mereka selamanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2771">2771</a><span>]</span><span> dan hal itu dinampakkan indah dalam hatimu, dan kamu menyangka dengan sangkaan buruk, dan kamu adalah kaum yang binasa.”</span></p>');
INSERT INTO `quran` VALUES (10990,2,48,14,'<p><span>Dan, barangsiapa yang tidak beriman kepada Allah dan Rasul-Nya, </span><span>[a]</span><span> Sesungguhnya Kami telah menyediakan bagi orang-orang kafir Api yang menyala-nyala.</span></p><p><span>[a]</span><span> 18:103 ; 29:69 ; 33:9 ; 76:5.</span></p>');
INSERT INTO `quran` VALUES (10991,2,48,15,'<p><span>[b]</span><span> Dan kepunyaan Allah kerajaan seluruh langit dan bumi. </span><span>[c]</span><span> Dia mengampuni bagi siapa yang Dia kehendaki dan mengazab siapa yang Dia kehendaki. Dan Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[b]</span><span> 40:17.</span></p><p><span>[c]</span><span> 3:130 ; 5:19.</span></p>');
INSERT INTO `quran` VALUES (10992,2,48,16,'<p><span>Akan berkata orang-orang yang tertinggal di belakang, ketika kamu berangkat untuk mengambil harta-harta rampasan dengan membawanya, “Biarkan kami mengikuti kamu,” Mereka bermaksud untuk mengubah kalam Allah. Katakanlah “Kamu tidak boleh ikut kami.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2772">2772</a><span>]</span><span> Demikianlah Allah telah berfirman sebelumnya, kemudian mereka berkata, “Bahkan kamu iri hati terhadap kami.” Bahkan mereka tidak mengerti kecuali sedikit.</span></p>');
INSERT INTO `quran` VALUES (10993,2,48,17,'<p><span>Katakanlah kepada orang-orang gurun yang tertinggal di belakang, “Kamu akan diajak berperang terhadap suatu kaum yang gagah-perkasa; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2773">2773</a><span>]</span><span> kamu akan memerangi mereka atau mereka masuk Islam, kemudian jika kamu taat, Allah akan memberimu ganjaran yang baik; tetapi jika kamu berpaling sebagaimana kamu pernah berpaling sebelum itu, Dia akan mengazab kamu dengan azab yang pedih.”</span></p>');
INSERT INTO `quran` VALUES (10994,2,48,18,'<p><span>[a]</span><span> Tiada keberatan atas orang buta, dan tiada keberatan atas orang lumpuh, dan tiada keberatan atas orang sakit, jika mereka tidak berangkat untuk berperang. </span><span>[b]</span><span> Dan, barangsiapa mentaati Allah dan Rasul-Nya, Dia akan memasukkannya ke kebun-kebun, yang dibawahnya mengalir sungai-sungai; tetapi barangsiapa berpaling, Dia akan mengazabnya dengan azab yang pedih.</span></p><p><span>[a]</span><span> 9:91.</span></p><p><span>[b]</span><span> 4;14 ; 24:53 ; 33:72.</span></p>');
INSERT INTO `quran` VALUES (10995,2,48,19,'<p><span>Sesungguhnya, Allah telah ridha terhadap orang-orang mukmin ketika mereka baiat kepada engkau di bawah pohon itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2774">2774</a><span>]</span><span> dan Dia mengetahui apa yang ada di dalam hati mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2775">2775</a><span>]</span><span> maka Dia menurunkan ketenteraman kepada mereka, dan Dia mengganjar mereka dengan kemenangan yang dekat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2776">2776</a><span>]</span></p>');
INSERT INTO `quran` VALUES (10996,2,48,20,'<p><span>Dan harta rampasan perang yang banyak, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2777">2777</a><span>]</span><span> yang mereka akan memperolehnya. Dan Allah Maha Perkasa; Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (10997,2,48,21,'<p><span>Allah telah menjanjikan kepadamu harta rampasan perang yang banyak </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2778">2778</a><span>]</span><span> yang kamu akan memperolehnya, dan Dia mempercepat bagi kamu </span><span>harta rampasan</span><span> ini, dan Dia telah </span><span>[a]</span><span> mencegah tangan manusia dari kamu, supaya menjadi Tanda bagi orang-orang yang beriman, dan supaya Dia akan memberi kamu petunjuk pada jalan lurus;</span></p><p><span>[a]</span><span> 5:12.</span></p>');
INSERT INTO `quran` VALUES (10998,2,48,22,'<p><span>Dan, </span><span>Dia telah menjanjikan kepadamu kemenangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2779">2779</a><span>]</span><span> lain yang kamu belum mampu mencapainya, tetapi Sesungguhnya Allah telah meliputinya. Dan Allah berkuasa atas segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (10999,2,48,23,'<p><span>Dan, seandainya memerangi kamu orang-orang yang ingkar, mereka tentu membalikkan punggung mereka; kemudian mereka tidak akan mendapat seorang pun pelindung dan tidak pula seorang penolong.</span></p>');
INSERT INTO `quran` VALUES (11000,2,48,24,'<p><span>[a]</span><span> </span><span>Demikianlah</span><span> sunnah Allah telah berlaku sebelum ini; dan engkau tidak akan mendapati sesuatu perubahan pada sunnah Allah.</span></p><p><span>[a]</span><span> 17:78 ; 33:63 ; 35:44.</span></p>');
INSERT INTO `quran` VALUES (11001,2,48,25,'<p><span>Dan, Dia-Lah Yang telah mencegah tangan mereka dari kamu dan tanganmu dari mereka di Lembah Mekkah; sesudah Dia memberikan kemenangan kepadamu atas mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2780">2780</a><span>]</span><span> Dan Allah Melihat apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11002,2,48,26,'<p><span>Mereka itulah orang-orang yang ingkar dan </span><span>[a]</span><span> menghalangi kamu dari Masjidil Haram dan juga mencegah hewan-hewan kurban sampai ke tempat penyembelihannya. Dan sekiranya tidak karena beberapa orang mukmin laki-laki dan orang mukmin perempuan, yang kamu tidak mengetahui mereka, dan dengan demikian dia menimpakan kepada kamu suatu aib dari mereka tanpa ilmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2781">2781</a><span>]</span><span> supaya Allah akan memasukkan ke dalam rahmat-Nya siapa yang Dia kehendaki, sekiranya mereka terpisah, pastilah Kami akan mengazab orang-orang yang ingkar di antara mereka dengan azab yang pedih.</span></p><p><span>[a]</span><span> 8:35 ; 22:26.</span></p>');
INSERT INTO `quran` VALUES (11003,2,48,27,'<p><span>Ketika orang-orang yang ingkar menyimpan dalam hati mereka kesombongan, kesombongan Jahiliah maka </span><span>[b]</span><span> Allah menurunkan ketenteraman-Nya kepada Rasul-Nya dan kepada orang-orang mukmin, dan membuat mereka berpegang teguh kepada asas takwa itu dan mereka lebih berhak memilikinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2782">2782</a><span>]</span><span> Dan Allah Maha Mengetahui segala sesuatu.</span></p><p><span>[b]</span><span> 9:26.</span></p>');
INSERT INTO `quran` VALUES (11004,2,48,28,'<p><span>Sesungguhnya Allah telah menyempurnakan kepada Rasulullah saw. rukya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2783">2783</a><span>]</span><span> dengan benar, kamu pasti akan masuk Masjidil Haram jika Allah menghendaki dengan aman, dengan mencukur habis rambut kepalamu atau memotong pendek tanpa rasa takut. Tetapi Dia mengetahui apa yang kamu tidak ketahui, Dia sebenarnya telah menetapkan bagimu selain itu satu kemenangan yang dekat.</span></p>');
INSERT INTO `quran` VALUES (11005,2,48,29,'<p><span>[a]</span><span> Dia-lah Yang telah mengutus Rasul-Nya dengan petunjuk dan agama yang benar, supaya Dia memenangkannya atas semua agama. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2784">2784</a><span>]</span><span> Dan </span><span>[b]</span><span> cukuplah Allah sebagai saksi.</span></p><p><span>[a]</span><span> 61:10.</span></p><p><span>[b]</span><span> 4:167 ; 13:44 ; 29:53.</span></p>');
INSERT INTO `quran` VALUES (11006,2,48,30,'<p><span>Muhammad adalah Rasul Allah. Dan orang-orang besertanya sangat </span><span>[a]</span><span> keras terhadap orang-orang kafir, tetapi kasih-sayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2785">2785</a><span>]</span><span> di antara mereka, engkau melihat mereka rukuk, sujud </span><span>[b]</span><span> mencari karunia Allah dan kerihdaan-Nya, ciri-ciri pengenal mereka terdapat pada wajah mereka, dari bekas-bekas sujud. Demikianlah perumpamaan mereka dalam Taurat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2786">2786</a><span>]</span><span> dan perumpaman mereka adalah Injil adalah laksana tanaman yang mengeluarkan tunasnya, kemudian menjadi kuat; kemudian menjadi kokoh, dan berdiri mantap pada batangnya, menyenangkan penanam-penanamnya supaya Dia menbangkitkan amarah orang-orang kafir dengan perantara itu. Allah telah menjanjikan kepada orang-orang yang beriman dan berbuat amal shaleh di antara mereka ampunan dan ganjaran yang besar.</span></p><p><span>[a]</span><span> 9:123.</span></p><p><span>[b]</span><span> 59:9.</span></p>');
INSERT INTO `quran` VALUES (11007,2,49,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11008,2,49,2,'<p><span>Hai. Orang-orang yang beriman, janganlah kamu mendahului </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2787">2787</a><span>]</span><span> di hadapan Allah dan Rasul-Nya, tetapi bertakwalah kepada Allah. Sesungguhnya, Allah Maha Mendengar, Maha Mengetahui.</span></p>');
INSERT INTO `quran` VALUES (11009,2,49,3,'<p><span>Hai. Orang-orang yang beriman janganlah kamu meninggikan suaramu di atas suara Nabi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2788">2788</a><span>]</span><span> dan janganlah kamu meninggikan suara kepadanya seperti meninggikan suara kamu kepada sebagian yang lain supaya jangan menjadi sia-sia amal-amalmu, sedangkan kamu tidak menyadari.</span></p>');
INSERT INTO `quran` VALUES (11010,2,49,4,'<p><span>Sesungguhnya, orang-orang yang merendahkan suaranya di hadapan Rasulullah, mereka itulah orang-orang yang telah diuji Allah hati mereka untuk ketakwaan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2789">2789</a><span>]</span><span> Bagi mereka ada ampunan dan ganjaran yang besar.</span></p>');
INSERT INTO `quran` VALUES (11011,2,49,5,'<p><span>Sesungguhnya, orang-orang yang memanggil-manggil engkau dari belakang </span><span>dinding</span><span> kamar-kamar, kebanyakan mereka tidak menggunakan akal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2790">2790</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11012,2,49,6,'<p><span>Dan, seandainya mereka bersabar hingga engkau keluar menemui mereka, tentulah akan lebih baik bagi mereka. Dan Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11013,2,49,7,'<p><span>Hai, orang-orang yang beriman, jika datang kepadamu seorang durhaka dengan membawa suatu kabar, </span><span>[a]</span><span> selidikilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2791">2791</a><span>]</span><span> dengan teliti, supaya jangan kamu mendatangkan musibah terhadap suatu kaum karena kebodohan, maka kamu menjadi menyesal atas apa yang telah kamu kerjakan.</span></p><p><span>[a]</span><span> 4:95.</span></p>');
INSERT INTO `quran` VALUES (11014,2,49,8,'<p><span>Dan ketahuilah bahwa di antara kamu ada Rasul Allah. Sekiranya ia harus mengikuti kehendakmu dalam banyak urusan, tentulah kamu akan mendapat kesusahan; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2792">2792</a><span>]</span><span> akan tetapi Allah telah menjadikan kamu cinta kepada keimanan dan telah menampakkannya indah dalam hatimu, dan Dia telah menjadikan kamu benci kepada kekufuran. Mereka itulah orang-orang yang mengikuti jalan yang benar.</span></p>');
INSERT INTO `quran` VALUES (11015,2,49,9,'<p><span>Sebagai karunia dan nikmat dari Allah. Dan Allah Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (11016,2,49,10,'<p><span>Dan, jika dua golongan dari orang-orang mukmin berperang, maka </span><span>[a]</span><span> damaikanlah di antara keduanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2793">2793</a><span>]</span><span> maka jika salah satu dari kedua mereka menyerang yang lain, maka perangilah pihak yang menyerang, hingga ia kembali kepada perintah Allah, kemudian jika ia kembali, damaikanlah antara keduanya dengan adil dan berbuat adillah. Sesungguhnya Allah mencintai orang-orang yang berbuat adil.</span></p><p><span>[a]</span><span> 8:2</span></p>');
INSERT INTO `quran` VALUES (11017,2,49,11,'<p><span>Sesungguhnya, orang-orang mukmin itu bersaudara. Maka damaikanlah di antara kedua saudaramu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2794">2794</a><span>]</span><span> dan bertakwalah kepada Allah supaya kamu dikasihani.</span></p>');
INSERT INTO `quran` VALUES (11018,2,49,12,'<p><span>R. 2</span></p><p><span>Hai, orang-orang yang beriman, janganlah suatu kaum mencemoohkan kaum </span><span>lain</span><span>, mungkin mereka itu lebih baik daripada mereka, dan janganlah wanita </span><span>mencemoohkan </span><span>wanita </span><span>lain</span><span>, mungkin mereka itu lebih baik daripada mereka, </span><span>[a]</span><span> dan janganlah kamu memburuk-burukkan dia antara kamu, begitu pula jangan panggil-memanggil dengan nama buruk. Seburuk-buruknya nama adalah fasik sesudah beriman, dan barangsiapa tidak bertaubat, mereka itulah orang yang aniaya.</span></p><p><span>[a]</span><span> 68:12 ; 104:2</span></p>');
INSERT INTO `quran` VALUES (11019,2,49,13,'<p><span>Hai, orang-orang yang beriman, hindarilah banyak prasangka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2795">2795</a><span>] [a]</span><span> karena sebagian prasangka dosa. Dan, jangan kamu saling memata-matai, dan jangan pula sebagian kamu mengumpat sebagian yang lain. Apakah salah seorang kamu suka memakan daging saudaranya yang mati ? Tentulah kamu merasa jijik kepadanya. Dan bertakwalah kepada Allah. Sesungguhnya, Allah berulang-ulang menerima taubat dan Maha Penyayang.</span></p><p><span>[a]</span><span> 53:29</span></p>');
INSERT INTO `quran` VALUES (11020,2,49,14,'<p><span>Hai manusia, Sesungguhnya Kami telah menciptakan kamu dari laki-laki dan perempuan; dan Kami telah menjadikan kamu bangsa-bangsa dan bersuku-suku, supaya kamu dapat saling mengenal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2796">2796</a><span>]</span><span> Sesungguhnya, yang paling mulia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2797">2797</a><span>]</span><span> di antara kamu di sisi Allah ialah yang paling bertakwa di antara kamu. Sesungguhnya Allah Maha Mengetahui, Maha Waspada.</span></p>');
INSERT INTO `quran` VALUES (11021,2,49,15,'<p><span>Orang-orang Arab gurun berkata, “Kami telah beriman.” Katankanlah, “Kamu belum beriman,” akan tetapi hendaknya kamu berkata, “kami telah menyerahkan diri, karena iman belum masuk ke dalam hatimu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2798">2798</a><span>]</span><span> Tetapi, jika kamu menaati Allah dan Rasul-Nya, Dia tidak mengurangi sesuatu dari amal-amalmu. Sesungguhnya, Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11022,2,49,16,'<p><span>[a]</span><span> Sesungguhnya orang mukmin adalah orang-orang yang beriman kepada Allah dan Rasul-Nya, kemudian tidak ragu-ragu dan terus berjihad dengan harta dan jiwa mereka di jalan Allah. Mereka itulah orang-orang yang benar.</span></p><p><span>[a]</span><span> 9:20 ; 61:12.</span></p>');
INSERT INTO `quran` VALUES (11023,2,49,17,'<p><span>Katakanlah, “Apakah kamu mengajarkan kepada Allah tentang agamamu? Padahal </span><span>[b]</span><span> </span><span>Allah mengetahui apa yang ada di seluruh langit dan bumi. Dan Allah Maha Mengetahui segala sesuatu.”</span></p><p><span>[b]</span><span> 20:8 ; 22:71 ; 27:66.</span></p>');
INSERT INTO `quran` VALUES (11024,2,49,18,'<p><span>Mereka </span><span>mengira </span><span>telah memberi hutang budi kepada engkau, karena mereka telah masuk Islam. Katakanlah, “Janganlah kamu merasa memberi hutang budi kepadamu karena ke-Islamanmu, bahkan </span><span>[a]</span><span> Allah-lah Yang berbuat baik terhadap kamu, karena Dia telah memberi kamu petunjuk kepada iman, jika kamu orang-orang yang benar.”</span></p><p><span>[a]</span><span> 3: 165.</span></p>');
INSERT INTO `quran` VALUES (11025,2,49,19,'<p><span>Sesungguhnya, Allah mengetahui yang gaib di seluruh langit dan bumi. Dan Allah Maha Melihat apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11026,2,50,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11027,2,50,2,'<p><span>Tuhan</span><span> Yang Maha Kuasa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2799">2799</a><span>]</span><span> demi Alquran yang mulia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2800">2800</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11028,2,50,3,'<p><span>Bahkan mereka merasa heran, bahwa telah datang kepada mereka seorang pemberi peringatan dari antara mereka, maka berkata orang-orang kafir, “Ini adalah sesuatu yang sangat ajaib.”</span></p>');
INSERT INTO `quran` VALUES (11029,2,50,4,'<p><span>“Apakah apabila kami telah mati dan kami telah menjadi tanah </span><span>akan dibangkitkan lagi ?</span><span> </span><span>[b]</span><span> Hal demikian itu </span><span>cara </span><span> kembali yang sangat jauh </span><span>dari akal.</span><span> “</span></p><p><span>[b]</span><span> 13:6 ; 23:37.</span></p>');
INSERT INTO `quran` VALUES (11030,2,50,5,'<p><span>Sesungguhnya Kami mengetahui apa yang bumi sedang kurangi dari mereka, dan pada sisi Kami ada sebuah Kitab yang memelihara </span><span>segala sesuatu.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2801">2801</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11031,2,50,6,'<p><span>Bahkan mereka mendustakan kebenaran ketika datang kepada mereka ; maka </span><span>jadilah</span><span> mereka dalam keadaan kacau-balau.</span></p>');
INSERT INTO `quran` VALUES (11032,2,50,7,'<p><span>Apakah mereka tidak melihat ke langit di atas mereka, bagaimana Kami telah membuatnya dan </span><span>[a]</span><span> menghiasinya, dan tidak ada padanya lubang-lubang ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2802">2802</a><span>]</span></p><p><span>[a]</span><span> 37:7 ; 41:13 ; 67:7.</span></p>');
INSERT INTO `quran` VALUES (11033,2,50,8,'<p><span>Dan bumi ini Kami telah menbentangkannya, dan Kami menepatkan di dalamnya gunung-gunung ; dan Kami menumbuhkan di dalamnya </span><span>[b]</span><span> segala macam pasangan yang indah,</span></p><p><span>[b]</span><span> 31:11.</span></p>');
INSERT INTO `quran` VALUES (11034,2,50,9,'<p><span>Sebagai penjelasan dan nasihat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2803">2803</a><span>]</span><span> bagi setiap hamba yang tunduk </span><span>kepada Kami.</span><span> </span></p>');
INSERT INTO `quran` VALUES (11035,2,50,10,'<p><span>[a]</span><span> Dan Kami menurunkan dari awan air yang berbarkat, dan dengan </span><span>air</span><span> itu Kami menumbuhkan kebun-kebun dan bijian yang diketam.</span></p><p><span>[a]</span><span> 25:49.</span></p>');
INSERT INTO `quran` VALUES (11036,2,50,11,'<p><span>Dan pohon-pohon kurma yang tinggi dengan tandan-tandannya bersusun-susun.</span></p>');
INSERT INTO `quran` VALUES (11037,2,50,12,'<p><span>Sebagai</span><span> rezeki bagi hamba-hamba-Ku ; </span><span>[b]</span><span> dan dengan itu Kami menghidupkan negeri yang mati. Demikianlah kebangkitan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2804">2804</a><span>]</span></p><p><span>[b]</span><span> 25:50 ; 43:12.</span></p>');
INSERT INTO `quran` VALUES (11038,2,50,13,'<p><span>Telah mendustakan sebelum mereka </span><span>[c]</span><span> kaum Nuh dan menghuni-penghuni sumur dan </span><span>kaum </span><span> Tsamud.</span></p><p><span>[c]</span><span> 9:70 ; 14:10 ; 38:13.</span></p>');
INSERT INTO `quran` VALUES (11039,2,50,14,'<p><span>Dan </span><span>kaum </span><span> </span><span>‘</span><span>Ad, dan Firaun dan saudara-saudara Luth.</span></p>');
INSERT INTO `quran` VALUES (11040,2,50,15,'<p><span>[a]</span><span> Dan, penghuni-penghuni hutan, dan kaum Tubba</span><span>’</span><span>. Semuanya mendustakan rasul-rasul, maka sempurnalah janji azab-Ku.</span></p><p><span>[a]</span><span> 15:79 ; 26:177 ; 38:14.</span></p>');
INSERT INTO `quran` VALUES (11041,2,50,16,'<p><span>[b]</span><span> Apakah Kami menjadi lelah dengan penciptaan yang pertama kali ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2805">2805</a><span>]</span><span> Bahkan mereka dalam keraguan tentang penciptaan yang baru.</span></p><p><span>[b]</span><span> 50:39.</span></p>');
INSERT INTO `quran` VALUES (11042,2,50,17,'<p><span>Dan sesungguhnya Kami telah menciptakan manusia dan Kami mengetahui apa yang dibisikkan oleh jiwanya kepadanya, dan Kami lebih dekat kepadanya daripada urat leher-</span><span>nya.</span></p>');
INSERT INTO `quran` VALUES (11043,2,50,18,'<p><span>Ketika kedua </span><span>malaikat</span><span> pencatat mencatat duduk di sebelah kanan dan di sebelah diri-</span><span>nya.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2806">2806</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11044,2,50,19,'<p><span>Ia tidak mengucapkan sepatah kata pun, melainkan </span><span>[c]</span><span> di dekatnya sudah siap pengawas.</span></p><p><span>[c]</span><span> 43:81 ; 82:11 </span><span>–</span><span> 12 ; 86:5</span></p>');
INSERT INTO `quran` VALUES (11045,2,50,20,'<p><span>[a]</span><span> Dan sakratulmaut pasti akan datang. Itulah apa yang selalu engkau menghindar darinya.</span></p><p><span>[a]</span><span> 6:94 ; 23:100.</span></p>');
INSERT INTO `quran` VALUES (11046,2,50,21,'<p><span>[b]</span><span> Dan nafiri akan ditiup. Itulah Hari azab yang dijanjikan.</span></p><p><span>[b] </span><span>18:100 ; 23:100.</span></p>');
INSERT INTO `quran` VALUES (11047,2,50,22,'<p><span>Dan, datanglah setiap jiwa besertanya ada </span><span>malaikat</span><span> penggiring dan </span><span>malaikat</span><span> pemberi kesaksian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2807">2807</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11048,2,50,23,'<p><span>Kami berfirman,</span><span> “Sesungguhnya dahulu engkau lalai mengenai </span><span>hari </span><span> ini ; maka Kami telah menyingkapkan dari engkau tabur engkau, maka pada hari ini penglihatan engkau sangat tajam. “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2808">2808</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11049,2,50,24,'<p><span>Dan temannya akan berkata, “Inilah yang tersedia padaku </span><span>catatan amalmu</span><span> “ </span></p>');
INSERT INTO `quran` VALUES (11050,2,50,25,'<p><span>Kami berfirman kepada keduanya,</span><span> “Campakkanlah oleh kamu berdua ke dalam Jahanam setiap yang ingkar musuh kebenaran. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2909">2909</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11051,2,50,26,'<p><span>[a]</span><span> “Penghalang bagi kebaikan, pelanggar batas, peragu ;</span></p><p><span>[a]</span><span> 68:13.</span></p>');
INSERT INTO `quran` VALUES (11052,2,50,27,'<p><span>“Yang menjadikan sama dengan Allah tuhan lain, maka campakkanlah dia oleh kamu berdua ke dalam azab yang sangat keras.”</span></p>');
INSERT INTO `quran` VALUES (11053,2,50,28,'<p><span>[b]</span><span> Dan berkata teman-temannya, “Hai, Tuhan kami, bukanlah aku yang menyebabkan dia berontak, melainkan ia </span><span>sendirilah,</span><span> yang berada dalam kesesatan yang jauh.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2810">2810</a><span>]</span></p><p><span>[b]</span><span> 14:23.</span></p>');
INSERT INTO `quran` VALUES (11054,2,50,29,'<p><span>Allah </span><span> berfirman, “Janganlah kamu bertengkar di hadapan-Ku, dan sesungguhnya Aku telah memberi peringatan lebih dahulu kepadamu tentang </span><span>azab.</span></p>');
INSERT INTO `quran` VALUES (11055,2,50,30,'<p><span>“Tidak akan dapat diubah keputusan di sisi-Ku </span><span>[c]</span><span> dan Aku tidak aniaya terhadap hamba-hamba-Ku.”</span></p><p><span>[c]</span><span> 3:183 ; 8:52 ; 22:11 ; 41:47.</span></p>');
INSERT INTO `quran` VALUES (11056,2,50,31,'<p><span>Pada hari </span><span>itu</span><span> Kami akan berkata kepada Jahannam, “Apakah engkau sudah penuh ? “ Dan Jahannam itu akan menjawab, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2811">2811</a><span>]</span><span> “Apakah masih ada tambahan lagi ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2812">2812</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11057,2,50,32,'<p><span>[a]</span><span> Dan, surga akan didekatkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2813">2813</a><span>]</span><span> kepada orang-orang bertakwa, </span><span>mereka merasa</span><span> tidak jauh lagi.</span></p><p><span>[a]</span><span> 26:19 ; 81:14.</span></p>');
INSERT INTO `quran` VALUES (11058,2,50,33,'<p><span>Inilah yang telah dijanjikan kepada setiap orang yang tunduk </span><span>kepada Tuhan</span><span> dan memelihara </span><span>amalnya</span><span>.</span></p>');
INSERT INTO `quran` VALUES (11059,2,50,34,'<p><span>Orang yang takut kepada </span><span>Tuhan</span><span> Yang Maha Pemurah dalam keadaan menyendiri dan datang </span><span>kepada-Nya</span><span> dengan hati yang tunduk.</span></p>');
INSERT INTO `quran` VALUES (11060,2,50,35,'<p><span>[a]</span><span> “Masuklah ke dalamnya dengan damai. Inilah Hari yang kekal abadi.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2814">2814</a><span>]</span></p><p><span>[a]</span><span> 14:24 ; 15:47 ; 36:59.</span></p>');
INSERT INTO `quran` VALUES (11061,2,50,36,'<p><span>Bagi mereka tersedia apa yang mereka inginkan di dalamnya dan di sisi Kami </span><span>[b]</span><span> ada tambahan lagi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2815">2815</a><span>]</span></p><p><span>[b]</span><span> 10:27.</span></p>');
INSERT INTO `quran` VALUES (11062,2,50,37,'<p><span>[c]</span><span> Dan, berapa banyak yang telah Kami binasakan generasi sebelum mereka, mereka itu lebih kuat daripada mereka dari segi kekuasaan, maka mereka membuat lubang-lubang perlindungan di negeri-negeri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2815a">2815a</a><span>]</span><span> </span><span>Tetapi</span><span> masih adakah tempat perlindungan ?</span></p><p><span>[c]</span><span> 19:75 ; 47:14.</span></p>');
INSERT INTO `quran` VALUES (11063,2,50,38,'<p><span>Sesungguhnya, dalam yang demikian itu ada nasihat bagi siapa yang mempunyai hati, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2816">2816</a><span>]</span><span> atau yang memasang telinga untuk mendengar dan dia adalah saksi.</span></p>');
INSERT INTO `quran` VALUES (11064,2,50,39,'<p><span>Dan sesungguhnya, Kami telah mencipatakan seluruh langit dan bumi dan apa yang ada di antara keduanya </span><span>[a]</span><span> dalam enam masa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2817">2817</a><span>]</span><span> dan Kami </span><span>[b]</span><span> tidak ditimpa keletihan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2818">2818</a><span>]</span></p><p><span>[a]</span><span> 7:55 ; 10:4 ; 11:8 ; 25:60.</span></p><p><span>[b]</span><span> 50:16.</span></p>');
INSERT INTO `quran` VALUES (11065,2,50,40,'<p><span>Maka bersabarlah atas apa yang mereka katakan, dan sucikanlah dengan memuji Tuhan engkau, sebelum matahari terbenam.</span></p>');
INSERT INTO `quran` VALUES (11066,2,50,41,'<p><span>Dan pada sebagian malam bertasbihlah engkau kepada-Nya dan sesudah shalat.</span></p>');
INSERT INTO `quran` VALUES (11067,2,50,42,'<p><span>Dan dengarkanlah ! Pada hari ketika seorang penyeru </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2819">2819</a><span>]</span><span> memanggil dari tempat yang dekat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2820">2820</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11068,2,50,43,'<p><span>Pada hari mereka mendengar teriakan azab dengan benar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2821">2821</a><span>]</span><span> Itulah hari keluar.</span></p>');
INSERT INTO `quran` VALUES (11069,2,50,44,'<p><span>Sesungguhnya, Kami Yang menghidupkan dan mematikan, dan kepada Kami tempat kembali.</span></p>');
INSERT INTO `quran` VALUES (11070,2,50,45,'<p><span>Pada hari ketika bumi akan terbelah dari atas mereka dengan cepat. Yang demikian itu adalah pengumpulan yang mudah bagi Kami.</span></p>');
INSERT INTO `quran` VALUES (11071,2,50,46,'<p><span>Kami lebih mengetahui apa yang mereka katakan ; dan bukanlah </span><span>[a]</span><span> engkau atas mereka seorang pemaksa, maka nasihatilah dengan Alquran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2822">2822</a><span>]</span><span> siapa yang takut kepada janji azab Aku.</span></p><p><span>[a]</span><span> </span><span>39:42 ; 42:7</span><span>.</span></p>');
INSERT INTO `quran` VALUES (11072,2,51,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11073,2,51,2,'<p><span>Demi </span><span>angin</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2823">2823</a><span>]</span><span> yang menyebarkan </span><span>awan-awan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2823a">2823a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11074,2,51,3,'<p><span>Kemudian memikul beban yang mengandung </span><span>hujan,</span><span> </span></p>');
INSERT INTO `quran` VALUES (11075,2,51,4,'<p><span>Kemudian berjalan perlahan-lahan.</span></p>');
INSERT INTO `quran` VALUES (11076,2,51,5,'<p><span>Kemudian membagi-bagikan perintah Kami, </span><span>tentang hujan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2824">2824</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11077,2,51,6,'<p><span>Sesungguhnya, apa yang telah dijanjikan kepadamu itu sungguh </span><span>[a]</span><span> benar.</span></p><p><span>[a]</span><span> 52:8</span></p>');
INSERT INTO `quran` VALUES (11078,2,51,7,'<p><span>Dan sesungguhnya pembalasan itu pasti akan terjadi.</span></p>');
INSERT INTO `quran` VALUES (11079,2,51,8,'<p><span>Dan demi langit </span><span>penuh </span><span> dengan jalur-jalur. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2825">2825</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11080,2,51,9,'<p><span>Sesungguhnya kamu, ada dalam perkataan yang berbeda. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2826">2826</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11081,2,51,10,'<p><span>Dipalingkan dari </span><span>kebenaran.</span><span> Siapa saja yang diputuskan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2827">2827</a><span>]</span><span> </span><span>untuk</span><span> dipalingkan</span></p>');
INSERT INTO `quran` VALUES (11082,2,51,11,'<p><span>Terkutuklah orang-orang yang banyak berdusta.</span></p>');
INSERT INTO `quran` VALUES (11083,2,51,12,'<p><span>Yaitu </span><span> orang-orang yang ada dalam jurang kesesatan, mereka melupakan </span><span>kebenaran,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2828">2828</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11084,2,51,13,'<p><span>Mereka bertanya, </span><span>[a]</span><span> “Kapankah hari Pembalasan itu ? “</span></p><p><span>[a]</span><span> 7:188 ; 79:43.</span></p>');
INSERT INTO `quran` VALUES (11085,2,51,14,'<p><span>“Pada hari </span><span>ketika</span><span> mereka di atas Api akan diazab.”</span></p>');
INSERT INTO `quran` VALUES (11086,2,51,15,'<p><span>Dikatakan kepada mereka,</span><span> “Rasakanlah azabmu. </span><span>[b]</span><span> Inilah apa yang telah kamu minta disegerakan.”</span></p><p><span>[b]</span><span> 26:205 ; 27:72 </span><span>–</span><span> 73 ; 29:54 </span><span>–</span><span> 55.</span></p>');
INSERT INTO `quran` VALUES (11087,2,51,16,'<p><span>Sesungguhnya, orang-orang muttaki akan berada di </span><span>[c]</span><span> kebun-kebun dan mata-mata air.</span></p><p><span>[c]</span><span> 15:146 ; 52:18 ; 68:35 ; 77:42 ; 78:32.</span></p>');
INSERT INTO `quran` VALUES (11088,2,51,17,'<p><span>Mereka mengambil apa yang akan dianugerahkan kepada mereka oleh Tuhan mereka. Karena sebelum itu mereka berbuat kebaikan ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2829">2829</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11089,2,51,18,'<p><span>[d]</span><span> Mereka sedikit sekali tidur di waktu malam.</span></p><p><span>[d]</span><span> 32:17.</span></p>');
INSERT INTO `quran` VALUES (11090,2,51,19,'<p><span>Dan di waktu subuh mereka </span><span>[e]</span><span> memohon ampunan.</span></p><p><span>[e]</span><span> 3:18.</span></p>');
INSERT INTO `quran` VALUES (11091,2,51,20,'<p><span>Dan dalam harta benda mereka ada </span><span>[a]</span><span> hak bagi mereka yang meminta dan bagi mereka yang tidak meminta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2830">2830</a><span>]</span></p><p><span>[a]</span><span> 70:25 </span><span>–</span><span> 26.</span></p>');
INSERT INTO `quran` VALUES (11092,2,51,21,'<p><span>Dan di bumi ada Tanda-tanda bagi orang-orang yang yakin.</span></p>');
INSERT INTO `quran` VALUES (11093,2,51,22,'<p><span>Dan </span><span>bagitu pula</span><span> dalam dirimu sendiri. Apakah kamu tidak melihat ? </span></p>');
INSERT INTO `quran` VALUES (11094,2,51,23,'<p><span>Dan di langit ada </span><span>[b]</span><span> rezeki kamu, dan apa yang dijanjikan kepada kamu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2831">2831</a><span>]</span></p><p><span>[b]</span><span> 40:14 ; 45:6.</span></p>');
INSERT INTO `quran` VALUES (11095,2,51,24,'<p><span>Maka demi Tuhan langit dan bumi, sesungguhnya ini adalah kebenaran, seperti apa yang sesungguhnya kamu ucapkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2832">2832</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11096,2,51,25,'<p><span>R. 2</span></p><p><span>Apakah telah sampai kepada engkau kabar para </span><span>[c]</span><span> tamu terhormat Ibrahim ? </span></p><p><span>[c]</span><span> 11:70 </span><span>–</span><span> 71 ; 15:52.</span></p>');
INSERT INTO `quran` VALUES (11097,2,51,26,'<p><span>Ketika mereka datang kepadanya, maka mereka berkata, </span><span>[a]</span><span> “Selamat sejahtera !” Ia berkata , “Selamat sejahtera !” </span><span>Ia mengira</span><span> mereka itu orang-orang asing. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2833">2833</a><span>]</span></p><p><span>[a]</span><span> 11:70.</span></p>');
INSERT INTO `quran` VALUES (11098,2,51,27,'<p><span>Maka ia pergi dengan diam-diam kepada keluarganya, lalu ia membawa </span><span>[b]</span><span> anak sapi gemuk </span><span>yang sudah dimasak</span><span>,</span></p><p><span>[b]</span><span> 11:70.</span></p>');
INSERT INTO `quran` VALUES (11099,2,51,28,'<p><span>Kemudian ia menyajikannya di hadapan mereka dan berkata, “Apakah kamu tidak mau makan ?”</span></p>');
INSERT INTO `quran` VALUES (11100,2,51,29,'<p><span>[c]</span><span> Dan ia merasa takut terhadap mereka, </span><span>[d]</span><span> Mereka berkata, “Jangan takut.” Dan mereka memberikan khabar suka kepadanya tentang </span><span>kelahiran </span><span> seorang anak laki-laki yang berilmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2834">2834</a><span>]</span></p><p><span>[c]</span><span> 11:71 ; 15:53.</span></p><p><span>[d]</span><span> 11:71 </span><span>–</span><span> 72 ; 15:54.</span></p>');
INSERT INTO `quran` VALUES (11101,2,51,30,'<p><span>Kemudian istrinya datang dengan malu-malu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2835">2835</a><span>]</span><span> maka ia memukul mukanya sendiri dan berkata, “</span><span>Aku hanyalah </span><span> perempuan tua mandul.”</span></p>');
INSERT INTO `quran` VALUES (11102,2,51,31,'<p><span>Mereka berkata, “Memang demikianlah Tuhan engkau berfirman. Sesungguhnya, Dia Maha Bijaksana, Maha Mengetahui.”</span></p>');
INSERT INTO `quran` VALUES (11103,2,51,32,'<p><span>Ibrahim</span><span> berkata, </span><span>[a]</span><span> “Apakah urusanmu, hai orang-orang yang diutus ? “</span></p><p><span>[a]</span><span> 15:57.</span></p>');
INSERT INTO `quran` VALUES (11104,2,51,33,'<p><span>Mereka berkata, “Kami diutus kepada </span><span>[b]</span><span> suatu kaum yang berdosa.</span></p><p><span>[b]</span><span> 15:58.</span></p>');
INSERT INTO `quran` VALUES (11105,2,51,34,'<p><span>Supaya kami menghujani atas mereka </span><span>[c]</span><span> batu-batu dari tanah liat.</span></p><p><span>[c]</span><span> 11:83.</span></p>');
INSERT INTO `quran` VALUES (11106,2,51,35,'<p><span>[d] </span><span>Yang telah ditandai, di sisi Tuhan engkau, untuk orang-orang pelampau batas.”</span></p><p><span>[d]</span><span> 11:84.</span></p>');
INSERT INTO `quran` VALUES (11107,2,51,36,'<p><span>Dan Kami mengeluarkan mereka yang ada di dalamnya </span><span>di kota itu</span><span> dari orang-orang Mukmin.</span></p>');
INSERT INTO `quran` VALUES (11108,2,51,37,'<p><span>Dan Kami tidak mendapatkan di dalamnya selain sebuah rumah dari orang-orang yang menyerahkan diri.</span></p>');
INSERT INTO `quran` VALUES (11109,2,51,38,'<p><span>Dan Kami meninggalkan di dalamnya suatu Tanda </span><span>[a]</span><span> bagi orang-orang yang takut akan azab yang pedih.</span></p><p><span>[a]</span><span> 15:76 ; 29:36.</span></p>');
INSERT INTO `quran` VALUES (11110,2,51,39,'<p><span>Dan dalam </span><span>kisah</span><span> Musa </span><span>ada Tanda,</span><span> ketika Kami mengirim dia kepada Firaun dengan dalil yang terang.</span></p>');
INSERT INTO `quran` VALUES (11111,2,51,40,'<p><span>Tetapi ia, </span><span>Firaun,</span><span> berpaling kepada sumber kekuatannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2836">2836</a><span>]</span><span> dan berkata, “Dia tukang sihir, atau orang gila.”</span></p>');
INSERT INTO `quran` VALUES (11112,2,51,41,'<p><span>[b]</span><span> Maka Kami menangkap dia dan lasykar-lasykarnya dan Kami mencampakkan mereka ke dalam laut ; dan ia adalah orang tercela.</span></p><p><span>[b]</span><span> 10:91 ; 28:41.</span></p>');
INSERT INTO `quran` VALUES (11113,2,51,42,'<p><span>Dan dalam </span><span>kisah kaum </span><span> </span><span>‘</span><span>Ad, ketika Kami mengirimkan kepada mereka </span><span>[c]</span><span> angin taufan yang membinasakan.</span></p><p><span>[c]</span><span> 46:26.</span></p>');
INSERT INTO `quran` VALUES (11114,2,51,43,'<p><span>[d]</span><span> </span><span>Angin itu </span><span> tidak membiarkan segala sesuatu yang dilandanya, melainkan dijadikan nya seperti tulang remuk.</span></p><p><span>[d]</span><span> 46:26.</span></p>');
INSERT INTO `quran` VALUES (11115,2,51,44,'<p><span>Dan dalam </span><span>kisah kaum </span><span> Tsamud, ketika dikatakan kepada mereka, “Bersenang- senanglah sampai suatu masa.”</span></p>');
INSERT INTO `quran` VALUES (11116,2,51,45,'<p><span>Tetapi mereka membangkang terhadap perintah Tuhan mereka. Maka mereka disambar petir, </span><span>[a]</span><span> sedang mereka melihatnya ;</span></p><p><span>[a]</span><span> 11:68.</span></p>');
INSERT INTO `quran` VALUES (11117,2,51,46,'<p><span>Dan mereka tidak mampu berdiri </span><span>untuk menghindar</span><span> dan tidak pula mereka mendapat pertolongan.</span></p>');
INSERT INTO `quran` VALUES (11118,2,51,47,'<p><span>Dan </span><span>Kami membinasakan</span><span> kaum Nuh sebelumnya. Mereka adalah kaum pembangkang.</span></p>');
INSERT INTO `quran` VALUES (11119,2,51,48,'<p><span>R. 3</span></p><p><span>Dan Kami menjadikan langit dengan beberapa sifat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2837">2837</a><span>]</span><span> dan sesungguhnya Kami terus memperluas, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2837a">2837a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11120,2,51,49,'<p><span>[b]</span><span> Dan bumi telah Kami hamparkan, maka alangkah sempurnanya Kami menghamparkan.</span></p><p><span>[b]</span><span> 2:23 ; 20:54 ; 78:7.</span></p>');
INSERT INTO `quran` VALUES (11121,2,51,50,'<p><span>Dan segala sesuatu telah Kami ciptakan </span><span>[c]</span><span> berpasangan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2838">2838</a><span>]</span><span> supaya kamu mendapat nasihat.</span></p><p><span>[c]</span><span> 13:4 ; 36:37.</span></p>');
INSERT INTO `quran` VALUES (11122,2,51,51,'<p><span>Maka, larilah kepada Allah. Sesungguhnya, aku bagimu seorang pemberi ingat yang nyata dari-Nya.</span></p>');
INSERT INTO `quran` VALUES (11123,2,51,52,'<p><span>Dan janganlah kamu menjadikan beserta Allah tuhan lain. Sesungguhnya, aku bagimu seorang pemberi ingat yang nyata dari-Nya.</span></p>');
INSERT INTO `quran` VALUES (11124,2,51,53,'<p><span>Demikianlah tidak pernah datang kepada orang-orang sebelum mereka seorang rasul. melainkan mereka berkata, “Dia tukang sihir, atau orang gila.”</span></p>');
INSERT INTO `quran` VALUES (11125,2,51,54,'<p><span>Adakah mereka saling mewasiatkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2839">2839</a><span>]</span><span> mengenai itu ? Tidak, bahkan mereka itu semua kaum pendurhaka.</span></p>');
INSERT INTO `quran` VALUES (11126,2,51,55,'<p><span>Maka, berpalinglah dari mereka, dan engkau tidak akan tercela.</span></p>');
INSERT INTO `quran` VALUES (11127,2,51,56,'<p><span>Dan, berilah selalu nasihat ; karena sesungguhnya nasihat itu bermanfaat bagi orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (11128,2,51,57,'<p><span>Dan, tidaklah Aku menciptakan jin dan manusia melainkan supaya mereka menyembah-Ku. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2840">2840</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11129,2,51,58,'<p><span>[a]</span><span> Aku tidak menghendaki rezeki dari mereka, dan tidak </span><span>pula</span><span> Aku menghendaki supaya mereka memberi makan kepada-Ku. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2841">2841</a><span>]</span></p><p><span>[a]</span><span> 6:15 ; 20:133.</span></p>');
INSERT INTO `quran` VALUES (11130,2,51,59,'<p><span>Sesungguhnya, Allah Dia-lah Pemberi rezeki, Yang Mempunyai Kekuatan dahsyat.</span></p>');
INSERT INTO `quran` VALUES (11131,2,51,60,'<p><span>Maka sesungguhnya, bagi orang-orang yang berbuat aniaya ada balasan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2842">2842</a><span>]</span><span> seperti balasan sahabat-sahabat mereka, maka janganlah mereka meminta kepada-Ku menyegerakan </span><span>azab </span><span> itu.</span></p>');
INSERT INTO `quran` VALUES (11132,2,51,61,'<p><span>Maka </span><span>[b]</span><span> celakalah bagi orang-orang yang ingkar pada hari mereka yang dijanjikan.</span></p><p><span>[b]</span><span> 14:3 ; 19:38 ; 8:28.</span></p>');
INSERT INTO `quran` VALUES (11133,2,52,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11134,2,52,2,'<p><span>Demi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2843">2843</a><span>] [b]</span><span> Thur,</span></p><p><span>[b]</span><span> 95:3.</span></p>');
INSERT INTO `quran` VALUES (11135,2,52,3,'<p><span>Dan </span><span>demi</span><span> Kitab yang ditulis </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2844">2844</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11136,2,52,4,'<p><span>Pada lembaran kulit terbuka ;</span></p>');
INSERT INTO `quran` VALUES (11137,2,52,5,'<p><span>Dan </span><span>demi</span><span> Rumah yang selalu dikunjungi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2845">2845</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11138,2,52,6,'<p><span>Dan </span><span>demi</span><span> Atap yang ditinggikan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2846">2846</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11139,2,52,7,'<p><span>Dan </span><span>demi</span><span> </span><span>[a]</span><span> Laut yang bergelombang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2847">2847</a><span>]</span></p><p><span>[a]</span><span> 81:7.</span></p>');
INSERT INTO `quran` VALUES (11140,2,52,8,'<p><span>Sesungguhnya azab Tuhan engkau niscaya akan </span><span>[b]</span><span> terjadi</span></p><p><span>[b]</span><span> 51:6.</span></p>');
INSERT INTO `quran` VALUES (11141,2,52,9,'<p><span>Tidak ada yang dapat mengelakkannya,</span></p>');
INSERT INTO `quran` VALUES (11142,2,52,10,'<p><span>Pada Hari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2848">2848</a><span>]</span><span> ketika awan sangat bergoncang,</span></p>');
INSERT INTO `quran` VALUES (11143,2,52,11,'<p><span>[a]</span><span> Dan gunung-gunung bergerak dengan cepat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2849">2849</a><span>]</span></p><p><span>[a]</span><span> 18:48 ; 78:21 ; 81:4.</span></p>');
INSERT INTO `quran` VALUES (11144,2,52,12,'<p><span>Maka celakalah pada Hari itu bagi orang-orang yang mendustakan.</span></p>');
INSERT INTO `quran` VALUES (11145,2,52,13,'<p><span>Orang-orang yang bermain dalam percakapan kosong.</span></p>');
INSERT INTO `quran` VALUES (11146,2,52,14,'<p><span>Hari, ketika mereka akan didorong ke dalam Api Jahannam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2850">2850</a><span>]</span><span> dengan paksa.</span></p>');
INSERT INTO `quran` VALUES (11147,2,52,15,'<p><span>Dan dikatakan, </span><span>“Inilah Api yang kamu pernah mendustakannya.</span></p>');
INSERT INTO `quran` VALUES (11148,2,52,16,'<p><span>Maka apakah ini sihir ataukah kamu tidak melihat ?</span></p>');
INSERT INTO `quran` VALUES (11149,2,52,17,'<p><span>Masuklah kamu di dalamnya, dan kamu bersabar atau tidak bersabar, sama saja </span><span>[b]</span><span> bagimu. Sesungguhnya kamu dibalas sesuai dengan apa yang kamu kerjakan.”</span></p><p><span>[b]</span><span> 14:22 ; 41:25.</span></p>');
INSERT INTO `quran` VALUES (11150,2,52,18,'<p><span>[c]</span><span> Sesungguhnya, orang-orang muttaki di dalam surga dan kenikmatan.</span></p><p><span>[c]</span><span> 15:46 ; 77:42 </span><span>–</span><span> 43 ; 78:32 </span><span>–</span><span> 33.</span></p>');
INSERT INTO `quran` VALUES (11151,2,52,19,'<p><span>Mereka bersenang-senang dengan apa yang diberikan kepada mereka oleh Tuhan mereka; dan menyelamatkan mereka Tuhan mereka dari azab Jahannam.</span></p>');
INSERT INTO `quran` VALUES (11152,2,52,20,'<p><span>Dan Dia berfirman,</span><span> “Makan dan minumlah dengan senang disebabkan oleh apa yang kamu kerjakan.”</span></p>');
INSERT INTO `quran` VALUES (11153,2,52,21,'<p><span>Mereka </span><span>[a] </span><span>duduk-duduk bersandar pada dipan-dipan yang berderet-deret teratur, </span><span>[b]</span><span> dan Kami akan menjodohkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2851">2851</a><span>]</span><span> mereka dengan bidadari-bidadari cantik bermata jeli. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2851a">2851a</a><span>]</span></p><p><span>[a]</span><span> 18:32 ; 55:55 ; 76:14.</span></p><p><span>[b]</span><span> 44:55 ; 56:23.</span></p>');
INSERT INTO `quran` VALUES (11154,2,52,22,'<p><span>Dan orang-orang yang beriman dan keturunan mereka pun mengikuti mereka dalam keimanan, dengan mereka akan Kami </span><span>[a]</span><span> pertemukan keturunan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2882">2882</a><span>]</span><span> dan Kami tidak mengurangi dari amal mereka sedikit pun. Tiap-tiap orang terikat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2853">2853</a><span>] </span><span>pada apa yang telah diusahakannya.</span></p><p><span>[a]</span><span> 40:9.</span></p>');
INSERT INTO `quran` VALUES (11155,2,52,23,'<p><span>Dan, Kami membantu mereka dengan </span><span>[a]</span><span> buah-buahan dan daging yang mereka inginkan.</span></p><p><span>[a] </span><span>55:12 ; 56:21.</span></p>');
INSERT INTO `quran` VALUES (11156,2,52,24,'<p><span>Di sana mereka saling memperebutkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2854">2854</a><span>]</span><span> piala yang di dalamnya tidak berisikan sesuatu yang sia-sia dan tidak pula </span><span>[b]</span><span> dosa.</span></p><p><span>[b]</span><span> 19:63 ; 56:26 ; 78:36.</span></p>');
INSERT INTO `quran` VALUES (11157,2,52,25,'<p><span>[c]</span><span> Dan, berkeliling di sekitar mereka pemuda-pemuda </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2855">2855</a><span>]</span><span> untuk mereka, bagaikan mutiara-mutiara yang tersimpan baik.</span></p><p><span>[c]</span><span> 56:18 ; 76:20.</span></p>');
INSERT INTO `quran` VALUES (11158,2,52,26,'<p><span>Dan, sebagian mereka berhadapan dengan sebagian lain, </span><span>dan </span><span> akan saling bertanya.</span></p>');
INSERT INTO `quran` VALUES (11159,2,52,27,'<p><span>Mereka berkata, “Sesungguhnya, dahulu ketika kami di tengah-tengah keluarga kami, kami takut </span><span>pembalasan atas diri kami. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2856">2856</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11160,2,52,28,'<p><span>“Tetapi, Allah telah berbuat baik atas kami dan telah memelihara kami </span><span>dari </span><span> azab angin panas.</span></p>');
INSERT INTO `quran` VALUES (11161,2,52,29,'<p><span>“Kami dahulu biasa mendoa kepada-Nya. Sesungguhnya Dia adalah Pelimpah kebaikan, Maha Penyayang.”</span></p>');
INSERT INTO `quran` VALUES (11162,2,52,30,'<p><span>R. 2</span></p><p><span>Maka nasihatilah selalu dengan karunia Tuhan engkau, engkau bukanlah </span><span>[a]</span><span> seorang tukang tenung dan bukan pula gila.</span></p>');
INSERT INTO `quran` VALUES (11163,2,52,31,'<p><span>Ataukah mereka berkata, “</span><span>Ia </span><span> </span><span>[b]</span><span> seorang penyair, kami sedang menunggu untuknya masa kehancuran.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2857">2857</a><span>]</span></p><p><span>[b]</span><span> 21:6 ; 69:42. </span></p>');
INSERT INTO `quran` VALUES (11164,2,52,32,'<p><span>Katakanlah, </span><span>[c] </span><span>“Tunggulah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2858">2858</a><span>]</span><span> olehmu, maka sesungguhnya aku bersama kamu termasuk orang yang menunggu.”</span></p><p><span>[c]</span><span> 9:52 ; 32:31.</span></p>');
INSERT INTO `quran` VALUES (11165,2,52,33,'<p><span>Apakah aksi mereka menyuruh mereka hal ini, ataukah mereka kaum pembangkang ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2859">2859</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11166,2,52,34,'<p><span>Apakah mereka berkata “Ia telah mengada-adakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2860">2860</a><span>]</span><span> hal itu ?” Tidak, bahkan mereka tidak beriman.</span></p>');
INSERT INTO `quran` VALUES (11167,2,52,35,'<p><span>Maka biarlah mereka mendatangkan suatu firman lain seperti ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2861">2861</a><span>]</span><span> jika mereka orang-orang benar.</span></p>');
INSERT INTO `quran` VALUES (11168,2,52,36,'<p><span>Apakah mereka diciptakan tanpa suatu maksud ataukah mereka sendiri pencipta- pencipta ?</span></p>');
INSERT INTO `quran` VALUES (11169,2,52,37,'<p><span>Apokah mereka menciptakan seluruh langit dan bumi ? </span><span>Tidak</span><span>, bahkan mereka tidak yakin.</span></p>');
INSERT INTO `quran` VALUES (11170,2,52,38,'<p><span>Apakah mereka mempunyai khazanah-khazanan Tuhan engkau, ataukah mereka penjaga-penjaga ?</span></p>');
INSERT INTO `quran` VALUES (11171,2,52,39,'<p><span>Apakah mereka mempunyai tangga yang dengan itu mereka </span><span>naik ke langit</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2862">2862</a><span>]</span><span> mendengarkan </span><span>firman Ilahi ?</span><span> Maka hendaklah membawa bukti yang nyata dari mereka orang yang mendengarkan.</span></p>');
INSERT INTO `quran` VALUES (11172,2,52,40,'<p><span>Apakah Dia mempunyai anak-anak perempuan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2863">2863</a><span>]</span><span> dan kamu mempunyai anak-anak laki-laki ?</span></p>');
INSERT INTO `quran` VALUES (11173,2,52,41,'<p><span>[a]</span><span> Apakah engkau meminta upah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2864">2864</a><span>]</span><span> dari mereka, sehingga mereka dibebani hutang ?</span></p><p><span>[a]</span><span> 68:47.</span></p>');
INSERT INTO `quran` VALUES (11174,2,52,42,'<p><span>Apakah mereka mempunyai </span><span>ilmu</span><span> yang gaib sehingga mereka dapat menulisnya ?</span></p>');
INSERT INTO `quran` VALUES (11175,2,52,43,'<p><span>Apakah mereka menghendaki tipu daya </span><span>menentang engkau </span><span> ? Tetapi, orang-orang yang ingkar, mereka sendiri </span><span>menjadi korban </span><span> tipu daya mereka.</span></p>');
INSERT INTO `quran` VALUES (11176,2,52,44,'<p><span>Apakah mereka mempunyai tuhan selain Allah ? Maha Suci Allah dari apa yang mereka persekutukan.</span></p>');
INSERT INTO `quran` VALUES (11177,2,52,45,'<p><span>Dan andaikata mereka melihat sebagian awan jatuh, mereka berkata, “</span><span>Itu hanyalah</span><span> awan yang berlapis-lapis.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2865">2865</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11178,2,52,46,'<p><span>Maka biarkanlah mereka hingga mereka bertemu dengan hari mereka yang di dalamnya mereka akan dipingsankan.</span></p>');
INSERT INTO `quran` VALUES (11179,2,52,47,'<p><span>Pada hari ketika rencana mereka tidak bermanfaat bagi mereka sedikit pun, dan mereka tidak akan ditolong.</span></p>');
INSERT INTO `quran` VALUES (11180,2,52,48,'<p><span>Dan, sesungguhnya, bagi orang-orang yang aniaya, ada azab selain itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2866">2866</a><span>]</span><span> akan tetapi kebanyakan mereka tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (11181,2,52,49,'<p><span>Dan bersabarlah untuk keputusan Tuhan engkau, karena sesungguhnya engkau berada di hadapan mata Kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2867">2867</a><span>]</span><span> dan hendaknya engkau bertasbihlah dengan pujian Tuhan engkau, ketika engkau berdiri,</span></p>');
INSERT INTO `quran` VALUES (11182,2,52,50,'<p><span>[a]</span><span> Dan selama beberapa waktu di malam hari </span><span>juga </span><span> bertasbihlah kepada-Nya, dan </span><span>demikian pula </span><span> pada waktu bintang-bintang terbenam.</span></p><p><span>[a]</span><span> 73:3 </span><span>–</span><span> 5 ; 76:27.</span></p>');
INSERT INTO `quran` VALUES (11183,2,53,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11184,2,53,2,'<p><span>Demi bintang </span><span>Suraa</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2868">2868</a><span>]</span><span> tatkala jatuh.</span></p>');
INSERT INTO `quran` VALUES (11185,2,53,3,'<p><span>Tidaklah sesat sahabatmu dan tidak pula keliru. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2869">2869</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11186,2,53,4,'<p><span>Dan, ia tidak berkata-kata menurut kehendak nafsu-</span><span>nya.</span></p>');
INSERT INTO `quran` VALUES (11187,2,53,5,'<p><span>Itu tidak lain melainkan wahyu yang diwahyukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2870">2870</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11188,2,53,6,'<p><span>Telah mengajarkan </span><span>Tuhan </span><span> Yang Maha Perkasa , </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2871">2871</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11189,2,53,7,'<p><span>Yang Empunya Kekuatan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2872">2872</a><span>]</span><span> </span><span>yang menapak berulang-ulang.</span><span> Maka Dia bersemayam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2873">2873</a><span>]</span><span> </span><span>di atas </span><span>‘</span><span>Arasy.</span></p>');
INSERT INTO `quran` VALUES (11190,2,53,8,'<p><span>Dan, </span><span>Dia mewahyukan kalam-Nya</span><span> ketika ia berada di atas </span><span>[a]</span><span> ufuk tertinggi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2874">2874</a><span>]</span></p><p><span>[a]</span><span> 81:24.</span></p>');
INSERT INTO `quran` VALUES (11191,2,53,9,'<p><span>Kemudian ia, </span><span>Rasulullah,</span><span> mendekati </span><span>Allah,</span><span> lalu Dia, </span><span>Allah,</span><span> kian dekat </span><span>kepadanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2875">2875</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11192,2,53,10,'<p><span>Maka jadilah ia, </span><span>seakan-akan, </span><span>seutas tali </span><span>dari</span><span> dua buah busur, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2876">2876</a><span>]</span><span> atau lebih dekat lagi.</span></p>');
INSERT INTO `quran` VALUES (11193,2,53,11,'<p><span>Kemudian Dia mewahyukan kepada hamba-Nya apa yang telah Dia wahyukan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2877">2877</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11194,2,53,12,'<p><span>Hati </span><span>Rasulullah</span><span> tidak berdusta apa yang dia lihat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2878">2878</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11195,2,53,13,'<p><span>Maka, apakah kamu membantah tentang apa yang telah dia lihat ?</span></p>');
INSERT INTO `quran` VALUES (11196,2,53,14,'<p><span>Dan, sesungguhnya, dia melihat-Nya kedua kali, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2879">2879</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11197,2,53,15,'<p><span>Dekat pohon Sidrah tertinggi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2880">2880</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11198,2,53,16,'<p><span>Yang didekatnya ada surga, tempat tinggal.</span></p>');
INSERT INTO `quran` VALUES (11199,2,53,17,'<p><span>Ketika pohon Sidrah ditutupi oleh sesuatu yang menutupi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2881">2881</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11200,2,53,18,'<p><span>Penglihatannya tidak menyimpang dan tidak pula melantur.</span></p>');
INSERT INTO `quran` VALUES (11201,2,53,19,'<p><span>Sesungguhnya, ia melihat satu Tanda besar dari Tanda-tanda Tuhan-Nya.</span></p>');
INSERT INTO `quran` VALUES (11202,2,53,20,'<p><span>Apakah kamu memperhatikan </span><span>keadaan </span><span> Lata dan </span><span>‘</span><span>Uzza ;</span></p>');
INSERT INTO `quran` VALUES (11203,2,53,21,'<p><span>Dan Manat, ketiga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2882">2882</a><span>]</span><span> yang lain.</span></p>');
INSERT INTO `quran` VALUES (11204,2,53,22,'<p><span>‘</span><span>Apakah bagi kamu yang laki-laki dan bagi Dia </span><span>[a]</span><span> yang perempuan ?”</span></p><p><span>[a]</span><span> 6:101 ; 43:17 ; 52:40.</span></p>');
INSERT INTO `quran` VALUES (11205,2,53,23,'<p><span>Yang demikian itu sungguh pembagian yang curang.</span></p>');
INSERT INTO `quran` VALUES (11206,2,53,24,'<p><span>[b]</span><span> Ini tidak lain melainkan nama-nama yang telah kamu beri namanya, kamu dan bapak-bapakmu yang untuk itu Allah tidak pernah menurunkan keterangan. Mereka tidak mengikuti sesuatu selain dugaan-dugaan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2883">2883</a><span>]</span><span> dan apa yang diinginkan diri mereka, padahal sesungguhnya telah datang kepada petunjuk dari Tuhan mereka.</span></p><p><span>[b]</span><span> 7:72 ; 12:41.</span></p>');
INSERT INTO `quran` VALUES (11207,2,53,25,'<p><span>Dapatkan manusia memperoleh apa yang dia inginkan ?</span></p>');
INSERT INTO `quran` VALUES (11208,2,53,26,'<p><span>Maka bagi Allah-lah </span><span>nikmat-nikmat </span><span>akhirat dan dunia.</span></p>');
INSERT INTO `quran` VALUES (11209,2,53,27,'<p><span>Dan, betapa banyak malaikat di seluruh langit, </span><span>tetapi</span><span> syafaat mereka tidak akan berfaedah sedikit pun, kecuali sesudah Allah mengizinkan bagi siapa yang Dia kehendaki dan Dia ridhai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2884">2884</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11210,2,53,28,'<p><span>Sesungguhnya, orang-orang yang tidak beriman kepada akhirat, niscaya mereka memberikan nama-nama perempuan kepada para malaikat.</span></p>');
INSERT INTO `quran` VALUES (11211,2,53,29,'<p><span>Mereka tidak mempunyai pengetahuan tentang itu. Mereka tidak mengikuti sesuatu selain prasangka belaka ; dan sesungguhnya </span><span>[a]</span><span> prasangka itu tidak berfaedah sedikit pun terhadap kebenaran.</span></p><p><span>[a]</span><span> 6:117 ; 10:37.</span></p>');
INSERT INTO `quran` VALUES (11212,2,53,30,'<p><span>Maka berpalinglah dari orang yang berpaling dari peringatan Kami, dan yang tidak menghendaki sesuatu selain dari kehidupan dunia.</span></p>');
INSERT INTO `quran` VALUES (11213,2,53,31,'<p><span>Itulah puncak akhir dari ilmu mereka. Sesungguhnya, Tuhan engkau Maha Mengetahui siapa yang tersesat dari jalan-Nya dan Dia Maha Mengetahui </span><span>[b]</span><span> siapa yang mengikuti petunjuk.</span></p><p><span>[b]</span><span> 16:126 ; 28:57 ; 68:8.</span></p>');
INSERT INTO `quran` VALUES (11214,2,53,32,'<p><span>Dan kepunyaan Allah apa yang ada di seluruh langit dan apa yang ada di bumi, supaya Dia memberikan balasan kepada orang-orang yang berbuat keburukan </span><span>sesuai</span><span> dengan apa yang telah dikerjakan mereka dam memberi ganjaran kepada orang-orang yang berbuat baik dengan </span><span>ganjaran</span><span> yang sebaik-baiknya.</span></p>');
INSERT INTO `quran` VALUES (11215,2,53,33,'<p><span>Orang-orang yang </span><span>[a]</span><span> menjauhi dosa-dosa besar dan kekejian, kecuali kesalahan-kesalahan kecil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2885">2885</a><span>]</span><span> Sesungguhnya, Tuhan engkau Maha Luas pengampunan-</span><span>Nya</span><span>. </span><span>[b]</span><span> Dia lebih mengetahui tentang dirimu ketika Dia menciptakan kamu dari tanah, dan ketika kamu berupa janin dalam perut ibumu, maka janganlah kamu menganggap suci dirimu sendiri. Dia Maha Mengatahui, siapa yang bertakwa.</span></p><p><span>[a]</span><span> 4:32 ; 42:38.</span></p><p><span>[b]</span><span> 13:6.</span></p>');
INSERT INTO `quran` VALUES (11216,2,53,34,'<p><span>R. 3</span></p><p><span>Apakah engkau melihat orang yang berpaling </span><span>dari petunjuk,</span></p>');
INSERT INTO `quran` VALUES (11217,2,53,35,'<p><span>Dan dia memberi sedikit, dan memberikannya dengan kebakhilan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2886">2886</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11218,2,53,36,'<p><span>Apakah ia memiliki ilmu gaib sehingga ia dapat melihat </span><span>nasib sendiri ?</span><span> </span></p>');
INSERT INTO `quran` VALUES (11219,2,53,37,'<p><span>Tidakkah ia diberitahu tentang apa yang ada dalam Kitab-kitab Suci Musa ?</span></p>');
INSERT INTO `quran` VALUES (11220,2,53,38,'<p><span>Dan, tentang Ibrahim yang memenuhi </span><span>perintah Allah</span><span> ?</span></p>');
INSERT INTO `quran` VALUES (11221,2,53,39,'<p><span>[a]</span><span> Bahwa tiada pemikul beban akan memikul beban orang lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2887">2887</a><span>]</span></p><p><span>[a]</span><span> 6:165 ; 17:16 ; 35:19 ; 39:8.</span></p>');
INSERT INTO `quran` VALUES (11222,2,53,40,'<p><span>Dan tiadalah bagi manusia melainkan apa yang ia usahakan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2888">2888</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11223,2,53,41,'<p><span>Dan bahwa usahanya akan segera diketahui </span><span>hasilnya.</span></p>');
INSERT INTO `quran` VALUES (11224,2,53,42,'<p><span>Kemudian ia akan diberi balasan dengan ganjaran yang paling sempurna.</span></p>');
INSERT INTO `quran` VALUES (11225,2,53,43,'<p><span>Dan, bahwa pada Tuhan engkau </span><span>terletak</span><span> keputusan terakhir. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2889">2889</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11226,2,53,44,'<p><span>Dan bahwa Dia-lah Yang membuat </span><span>orang-orang </span><span> tertawa dan membuat </span><span>mereka</span><span> menangis;</span></p>');
INSERT INTO `quran` VALUES (11227,2,53,45,'<p><span>Dan bahwa Dia-lah Yang </span><span>[a]</span><span> mematikan dan menghidupkan,</span></p><p><span>[a]</span><span> 2:29 ; 30:41.</span></p>');
INSERT INTO `quran` VALUES (11228,2,53,46,'<p><span>Dan bahwa Dia-lah Yang menciptakan berpasang-pasangan </span><span>[b]</span><span> laki-laki dan perempuan,</span></p><p><span>[b]</span><span> 4:2 ; 7:190 ; 30:22.</span></p>');
INSERT INTO `quran` VALUES (11229,2,53,47,'<p><span>[c]</span><span> Dari nuftah, apabila dijatuhkan </span><span>ke dalam rahim,</span></p><p><span>[c]</span><span> 56:59 </span><span>–</span><span> 60 ; 75:38 ; 86:7.</span></p>');
INSERT INTO `quran` VALUES (11230,2,53,48,'<p><span>Dan bahwa bagi Dia-lah penciptaan kejadian yang kedua kali,</span></p>');
INSERT INTO `quran` VALUES (11231,2,53,49,'<p><span>Dan bahwa Dia-lah Yang menganugerahkan kekayaan dan kecukupan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2889a">2889a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11232,2,53,50,'<p><span>Dan bahwa Dia-lah Tuhan </span><span>Bintang</span><span> Syi</span><span>’</span><span>raa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2890">2890</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11233,2,53,51,'<p><span>Dan bahwa Dia-lah Yang telah membinasakan </span><span>kaum</span><span> </span><span>‘</span><span>Ad pertama, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2891">2891</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11234,2,53,52,'<p><span>Dan </span><span>kaum</span><span> Tsamud, maka Dia tidak menyisakan seorang </span><span>pun,</span><span> </span></p>');
INSERT INTO `quran` VALUES (11235,2,53,53,'<p><span>Dan, </span><span>Dia membinasakan</span><span> kaum Nuh sebelum mereka. Sesungguhnya mereka itu paling aniaya dan paling durhaka.</span></p>');
INSERT INTO `quran` VALUES (11236,2,53,54,'<p><span>Dan, kota-kota yang telah dibalikkan, Dia-lah Yang telah meruntuhkan.</span></p>');
INSERT INTO `quran` VALUES (11237,2,53,55,'<p><span>maka telah menutupinya sesuatu yagn menutupi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2892">2892</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11238,2,53,56,'<p><span>Maka dari antara nikmat-nikmat Tuhan engkau, yang manakah, akan engkau ragukan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2893">2893</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11239,2,53,57,'<p><span>Rasul</span><span> Kami ini adalah Pemberi ingat dari Pemberi-pemberi ingat yang terdahulu.</span></p>');
INSERT INTO `quran` VALUES (11240,2,53,58,'<p><span>Saat pembalasan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2894">2894</a><span>]</span><span> itu telah kian mendekat.</span></p>');
INSERT INTO `quran` VALUES (11241,2,53,59,'<p><span>Tiada baginya selain Allah, yang dapat mengelakkan.</span></p>');
INSERT INTO `quran` VALUES (11242,2,53,60,'<p><span>Maka apakah kamu heran atas pemberitaan ini ?</span></p>');
INSERT INTO `quran` VALUES (11243,2,53,61,'<p><span>Dan kamu tertawa dan tidak menangis ?</span></p>');
INSERT INTO `quran` VALUES (11244,2,53,62,'<p><span>Dan sedangkan kamu keheran-keranan ?</span></p>');
INSERT INTO `quran` VALUES (11245,2,53,63,'<p><span>Maka bersujudlah kepada Allah dan </span><span>[a]</span><span> beribadalah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2895">2895</a><span>] </span><span>kepada-Nya.</span></p><p><span>[a]</span><span> 7:206 ; 22:78 ; 41;38 ; 96:20.</span></p>');
INSERT INTO `quran` VALUES (11246,2,54,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11247,2,54,2,'<p><span>Telah dekat </span><span>[b]</span><span> saat </span><span>kehancuran Arab </span><span> dan bulan terbelah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2896">2896</a><span>]</span></p><p><span>[b]</span><span> 21:2.</span></p>');
INSERT INTO `quran` VALUES (11248,2,54,3,'<p><span>[a]</span><span> Dan jika mereka melihat suatu Tanda, mereka berpaling dan berkata, “Sihir yang terus berulang.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2897">2897</a><span>]</span></p><p><span>[a]</span><span> 21:3.</span></p>');
INSERT INTO `quran` VALUES (11249,2,54,4,'<p><span>Dan mereka mendustakan </span><span>kebenaran</span><span> dan mengikuti hawa nafsu mereka dan setiap perkara ada ketetapan waktu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2898">2898</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11250,2,54,5,'<p><span>Dan sesungguhnya telah datang kepada mereka berita-berita yag didalamnya ada peringatan.</span></p>');
INSERT INTO `quran` VALUES (11251,2,54,6,'<p><span>Hikmah yang sempurna maka </span><span>[a]</span><span> pemberi ingat itu tidak berfaedah </span><span>bagi mereka.</span></p><p><span>[a]</span><span> 10:102.</span></p>');
INSERT INTO `quran` VALUES (11252,2,54,7,'<p><span>Maka berpalinglah engkau dari mereka pada hari, ketika Sang Penyeru akan mamanggil </span><span>mereka</span><span> kepada sesuatu yang tidak disenangi, </span><span>azab.</span></p>');
INSERT INTO `quran` VALUES (11253,2,54,8,'<p><span>[b]</span><span> Sambil pandangan mereka menunduk, mereka akan keluar dari kuburan </span><span>mereka</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2899">2899</a><span>]</span><span> seperti mereka belalang yang bertebaran.</span></p><p><span>[b]</span><span> 70:45.</span></p>');
INSERT INTO `quran` VALUES (11254,2,54,9,'<p><span>[c]</span><span> Bergegas-gegas menuju Sang Penyeru itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2900">2900</a><span>]</span><span> Akan berkata orang-orang kafir, “Inilah hari yang sangat keras.”</span></p><p><span>[c]</span><span> 14:44 ; 36:52.</span></p>');
INSERT INTO `quran` VALUES (11255,2,54,10,'<p><span>Telah </span><span>[a]</span><span> mendustakan sebelum mereka kaum Nuh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2901">2901</a><span>]</span><span> maka mereka mendustakan hamba Kami dan mereka berkata. “</span><span>Ia</span><span> </span><span>[b]</span><span> orang gila dan terusir.”</span></p><p><span>[a]</span><span> 6:35 ; 22:43 ; 35:26 ; 40:6.</span></p><p><span>[b]</span><span> 23:26.</span></p>');
INSERT INTO `quran` VALUES (11256,2,54,11,'<p><span>Maka ia berdoa kepada Tuhan-nya, “Sesungguhnya aku dikalahkan, maka </span><span>[c]</span><span> tolonglah </span><span>aku.</span><span>”</span></p><p><span>[c]</span><span> 23:27 ; 26:118 </span><span>–</span><span> 119.</span></p>');
INSERT INTO `quran` VALUES (11257,2,54,12,'<p><span>Maka Kami membukakan pintu-pintu awan dengan air yang tercurah deras.</span></p>');
INSERT INTO `quran` VALUES (11258,2,54,13,'<p><span>[d]</span><span> Dan, Kami pancarkan di bumi, sumber-sumber air, lalu </span><span>kedua</span><span> air itu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2902">2902</a><span>]</span><span> bertemu untuk suatu maksud yag telah ditentukan.</span></p><p><span>[d]</span><span> 11:41.</span></p>');
INSERT INTO `quran` VALUES (11259,2,54,14,'<p><span>Dan, </span><span>[e]</span><span> Kami mengangkut dia di atas sesuatu yang terbuat dari papan dan paku,</span></p><p><span>[e]</span><span> 26:120 ; 29:16.</span></p>');
INSERT INTO `quran` VALUES (11260,2,54,15,'<p><span>Yang berlayar di bawah </span><span>[a]</span><span> pengawasan Kami, sebagai ganjaran bagi orang yang diingkari.</span></p><p><span>[a]</span><span> 11:42 </span><span>–</span><span> 43.</span></p>');
INSERT INTO `quran` VALUES (11261,2,54,16,'<p><span>[b]</span><span> Dan sesungguhnya Kami telah meninggalkan </span><span>peristiwa</span><span> itu </span><span>sebagai</span><span> Tanda, maka apakah ada yang mengambil nasihat ?</span></p><p><span>[b]</span><span> 29:16.</span></p>');
INSERT INTO `quran` VALUES (11262,2,54,17,'<p><span>Maka betapa </span><span>dahsyatnya </span><span> azab-Ku dan peringatan-Ku !</span></p>');
INSERT INTO `quran` VALUES (11263,2,54,18,'<p><span>Dan, sesungguhnya Kami telah </span><span>[c]</span><span> mempermudah Alquran untuk diingat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2903">2903</a><span>]</span><span> Maka, apakah ada orang yang mengambil pelajaran ?</span></p><p><span>[c]</span><span> 19:98 ; 44:59.</span></p>');
INSERT INTO `quran` VALUES (11264,2,54,19,'<p><span>Telah </span><span>[d]</span><span> mendustakan </span><span>kaum </span><span> </span><span>‘</span><span>Ad, maka betapa dahsyatnya azab-Ku dan peringatan- Ku !</span></p><p><span>[d]</span><span> 26:124.</span></p>');
INSERT INTO `quran` VALUES (11265,2,54,20,'<p><span>Sesungguhnya, Kami </span><span>[e]</span><span> mengirimkan kepada mereka angin kencang pada hari naas yang tiada henti-hentinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2904">2904</a><span>]</span></p><p><span>[e]</span><span> 41:17 ; 69:7.</span></p>');
INSERT INTO `quran` VALUES (11266,2,54,21,'<p><span>Merenggut manusia, seolah-olah mereka itu </span><span>[a]</span><span> batang-batang pohon kurma yang lapuk.</span></p><p><span>[a]</span><span> 69:8.</span></p>');
INSERT INTO `quran` VALUES (11267,2,54,22,'<p><span>Maka, betapa </span><span>dahsyatnya </span><span>azab-ku dan peringatan-Ku !</span></p>');
INSERT INTO `quran` VALUES (11268,2,54,23,'<p><span>Dan, sesungguhnya Kami telah memudahkan Alquran untuk dimengerti. Maka apakah ada yang mengambil pelajaran ?</span></p>');
INSERT INTO `quran` VALUES (11269,2,54,24,'<p><span>R. 2</span></p><p><span>Telah </span><span>[b]</span><span> mendustakan </span><span>kaum</span><span> Tsamud kepada para Pemberi ingat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2905">2905</a><span>]</span></p><p><span>[b]</span><span> 69:5. </span></p>');
INSERT INTO `quran` VALUES (11270,2,54,25,'<p><span>Maka mereka berkata, “Apakah seorang manusia dari antara kami, </span><span>harus</span><span> kami mengikutinya ? Jika demikian, sesungguhnya kami berada dalam kesesatan, dan </span><span>mengidap </span><span> penyakit gila. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2906">2906</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11271,2,54,26,'<p><span>[c]</span><span> Apakah peringatan itu </span><span>hanya </span><span> diturunkan kepadanya dari antara kami ? Tidak, bahkan ia seorang pendusta besar lagi amat takabur.”</span></p><p><span>[c]</span><span> 38:9.</span></p>');
INSERT INTO `quran` VALUES (11272,2,54,27,'<p><span>Segera mereka akan mengetahui besok siapa pendusta besar yang amat sombong itu.</span></p>');
INSERT INTO `quran` VALUES (11273,2,54,28,'<p><span>Sesungguhnya, Kami akan mengirimkan </span><span>[a]</span><span> unta betina sebagai cobaan untuk mereka, </span><span>hai Shaleh,</span><span> maka tunggulah akibat mereka dan bersabarlah.</span></p><p><span>[a]</span><span> 7:74 ; 11:65 ; 17:60.</span></p>');
INSERT INTO `quran` VALUES (11274,2,54,29,'<p><span>“Dan, beritahukanlah kepada mereka bahwa air itu terbagi di antara mereka, setiap golongan hadir pada waktu giliran meminumnya.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2907">2907</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11275,2,54,30,'<p><span>Maka, mereka memanggil kawan mereka dan menangkap dan </span><span>[b]</span><span> memotong urat keting </span><span>unta betina.</span></p><p><span>[b]</span><span> 7:78 ; 11:66 ; 26:158 ; 91:15.</span></p>');
INSERT INTO `quran` VALUES (11276,2,54,31,'<p><span>Maka betapa </span><span>dahsyatnya </span><span> azab-Ku dan peringatkan-Ku !</span></p>');
INSERT INTO `quran` VALUES (11277,2,54,32,'<p><span>Sesungguhnya, Kami telah mengirimkan kepada mereka satu suara dahsyat, maka jadilah mereka seperti jerami kering yang dipotong-potong oleh di pembuat pagar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2908">2908</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11278,2,54,33,'<p><span>Dan, sesungguhnya Kami telah memudahkan Alquran untuk dimengerti. Maka apakah ada yang mengambil pelajaran ?</span></p>');
INSERT INTO `quran` VALUES (11279,2,54,34,'<p><span>[a]</span><span> Telah mendustakan kaum Luth kepada para Pemberi ingat.</span></p><p><span>[a]</span><span> 26:161.</span></p>');
INSERT INTO `quran` VALUES (11280,2,54,35,'<p><span>[b]</span><span> Sesungguhnya Kami telah mengirimkan kepada mereka taufan yagn membawa batu kerikil, kecuali keluarga Luth. Kami selamatkan mereka pada waktu sebelum fajar.</span></p><p><span>[b]</span><span> 25:41 ; 26:174.</span></p>');
INSERT INTO `quran` VALUES (11281,2,54,36,'<p><span>Sebagai</span><span> nikmat dari sisi Kami. Demikianlah Kami memberi ganjaran orang yang bersyukur.</span></p>');
INSERT INTO `quran` VALUES (11282,2,54,37,'<p><span>Dan, Sesungguhnya ia telah memperingatkan mereka tentang azab Kami yang keras, tetapi mereka membantah peringatan itu.</span></p>');
INSERT INTO `quran` VALUES (11283,2,54,38,'<p><span>Dan, sesungguhnya mereka telah berusaha membujuknya berpaling dari tamu- tamunya, maka Kami membutakan mata mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2909">2909</a><span>]</span><span> </span><span>dan berfirman,</span><span> “Rasakanlah azab-Ku dan peringatan-Ku”</span></p>');
INSERT INTO `quran` VALUES (11284,2,54,39,'<p><span>Dan, sesungguhnya pada waktu subuh telah menimpa mereka azab yang tetap.</span></p>');
INSERT INTO `quran` VALUES (11285,2,54,40,'<p><span>“Maka rasakanlah azab-Ku dan peringatan-Ku.”</span></p>');
INSERT INTO `quran` VALUES (11286,2,54,41,'<p><span>Dan, sesungguhnya Kami telah memudahkan Alquran untuk dimengerti, Maka apakah ada yang mengambil pelajaran ?</span></p>');
INSERT INTO `quran` VALUES (11287,2,54,42,'<p><span>Dan, sesungguhnya telah datang kepada kaum Firaun, para Pemberi peringatan.</span></p>');
INSERT INTO `quran` VALUES (11288,2,54,43,'<p><span>[a]</span><span> Mereka mendustakan Tanda-tanda Kami semuanya, maka Kami sergap mereka dengan sergapan Dzat Yang Maha Perkasa, Maka Kuasa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2910">2910</a><span>]</span></p><p><span>[a]</span><span> 20:57.</span></p>');
INSERT INTO `quran` VALUES (11289,2,54,44,'<p><span>Apakah orang-orang kafir kamu lebih baik daripada orang-orang sebelum kamu ? Atau apakah ada bagimu jaminan </span><span>[b]</span><span> kebebasan di dalam kitab-kitab terdahulu ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2911">2911</a><span>]</span></p><p><span>[b]</span><span> 2:81.</span></p>');
INSERT INTO `quran` VALUES (11290,2,54,45,'<p><span>Atau apakah mereka berkata, “Kami golongan yang bersatu, yang menang ?”</span></p>');
INSERT INTO `quran` VALUES (11291,2,54,46,'<p><span>[c]</span><span> Golongan itu akan segera dikalahkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2912">2912</a><span>]</span><span> dan akan membalikkan punggung mereka, </span><span>melarikan diri.</span></p><p><span>[c]</span><span> 3:13 ; 8:37 ; 38:12.</span></p>');
INSERT INTO `quran` VALUES (11292,2,54,47,'<p><span>Bahkan Saat itu telah dijanjikan kepada mereka ; dan Saat itu paling mengerikan dan paling pahit.</span></p>');
INSERT INTO `quran` VALUES (11293,2,54,48,'<p><span>Sesungguhnya, orang-orang yang berdosa berada dalam kesesatan dan </span><span>mengidap </span><span> penyakit gila.</span></p>');
INSERT INTO `quran` VALUES (11294,2,54,49,'<p><span>Pada hari ketika mereka akan diseret di dalam Api bersama pemuka-pemuka mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2913">2913</a><span>]</span><span> </span><span>Dikatakan kepada mereka,</span><span> “Rasakanlah sentuhan azab neraka.”</span></p>');
INSERT INTO `quran` VALUES (11295,2,54,50,'<p><span>Sesungguhnya, segala sesuatu Kami telah menciptakannya </span><span>[a]</span><span> dengan ukuran. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2914">2914</a><span>]</span></p><p><span>[a]</span><span> 15:22 ; 25:3.</span></p>');
INSERT INTO `quran` VALUES (11296,2,54,51,'<p><span>Dan tidak ada perintah Kami, kecuali satu </span><span>kata</span><span> bagaikan </span><span>[b]</span><span> sekejap mata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2915">2915</a><span>]</span></p><p><span>[b]</span><span> 7:188 ; 16:78.</span></p>');
INSERT INTO `quran` VALUES (11297,2,54,52,'<p><span>Dan, sesungguhnya Kami telah membinasakan golongan-golongan seperti kamu, tetapi adakah orang yang mengambil pelajaran ?</span></p>');
INSERT INTO `quran` VALUES (11298,2,54,53,'<p><span>[a]</span><span> Dan segala sesuatu yang mereka telah lakukan </span><span>tercantum</span><span> dalam Kitab-kitab. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2916">2916</a><span>]</span></p><p><span>[a]</span><span> 18:50 ; 45:30.</span></p>');
INSERT INTO `quran` VALUES (11299,2,54,54,'<p><span>Dan segala perkara kecil dan besar, telah dicatat.</span></p>');
INSERT INTO `quran` VALUES (11300,2,54,55,'<p><span>Sesungguhnya, orang-orang yang bertakwa berada dalam kebun-kebun dan sungai- sungai. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2917">2917</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11301,2,54,56,'<p><span>Dalam tempat tinggal yang kekal lagi mulia di sisi Raja Yang Maha Kuasa.</span></p>');
INSERT INTO `quran` VALUES (11302,2,55,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11303,2,55,2,'<p><span>Tuhan </span><span> Yang Maha Pemurah,</span></p>');
INSERT INTO `quran` VALUES (11304,2,55,3,'<p><span>Dia mengajarkan Alquran. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2918">2918</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11305,2,55,4,'<p><span>[b]</span><span> Dia menciptakan manusia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2919">2919</a><span>]</span></p><p><span>[b]</span><span> 96:3.</span></p>');
INSERT INTO `quran` VALUES (11306,2,55,5,'<p><span>Dia mengajarkannya kefasihan bicara.</span></p>');
INSERT INTO `quran` VALUES (11307,2,55,6,'<p><span>[c]</span><span> Matahari dan bulan </span><span>beredar </span><span> menurut perhitungan.</span></p><p><span>[c]</span><span> 6:97 ; 36:39 </span><span>–</span><span> 40.</span></p>');
INSERT INTO `quran` VALUES (11308,2,55,7,'<p><span>Dan tumbuh-tumbuhan dan pohon-pohon tunduk </span><span>kepada-Nya.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2920">2920</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11309,2,55,8,'<p><span>Dan langit Dia meninggikannya dan Dia menetapkan </span><span>[a]</span><span> neraca </span><span>pertimbangan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2921">2921</a><span>]</span></p><p><span>[a]</span><span> 42:18 ; 57:26.</span></p>');
INSERT INTO `quran` VALUES (11310,2,55,9,'<p><span>Supaya kamu jangan melampuai dalam neraca itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2922">2922</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11311,2,55,10,'<p><span>Tegakkanlah timbangan dengan adil dan </span><span>[b]</span><span> jangan mengurangi timbangan.</span></p><p><span>[b]</span><span> 11:85 </span><span>–</span><span> 86 ; 17:36 ; 26:182.</span></p>');
INSERT INTO `quran` VALUES (11312,2,55,11,'<p><span>Dan bumi, Dia telah menjadikan bagi manfaat semua makhluk.</span></p>');
INSERT INTO `quran` VALUES (11313,2,55,12,'<p><span>Di dalamnya terdapat </span><span>[c]</span><span> buah-buahan serta pohon-pohon kurma berkelopak mayang.</span></p><p><span>[c]</span><span> 50:10 </span><span>–</span><span> 11.</span></p>');
INSERT INTO `quran` VALUES (11314,2,55,13,'<p><span>Dan biji-bijian yang berkulit serta </span><span>bunga-bunga </span><span> yang harum,</span></p>');
INSERT INTO `quran` VALUES (11315,2,55,14,'<p><span>Maka yang manakah di antara nikmat-nikmat Tuhan kamu berdua yang kamu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2923">2923</a><span>]</span><span> dustakan, hai </span><span>jin </span><span>dan </span><span>manusia </span><span>?</span></p>');
INSERT INTO `quran` VALUES (11316,2,55,15,'<p><span>Dia menciptakan manusia dari tanah liat kering </span><span>[a]</span><span> seperti tembikar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2924">2924</a><span>]</span></p><p><span>[a]</span><span> 15:27, 29.</span></p>');
INSERT INTO `quran` VALUES (11317,2,55,16,'<p><span>[b]</span><span> Dan, Dia menciptakan jin-jin dari nyala api. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2925">2925</a><span>]</span></p><p><span>[b]</span><span> 7:13 ; 15:28 ; 38:77.</span></p>');
INSERT INTO `quran` VALUES (11318,2,55,17,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11319,2,55,18,'<p><span>[a]</span><span> Tuhan dua timur dan Tuhan dua barat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2926">2926</a><span>]</span></p><p><span>[a]</span><span> 2:16 ; 26:29.</span></p>');
INSERT INTO `quran` VALUES (11320,2,55,19,'<p><span>Maka yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11321,2,55,20,'<p><span>Dia telah membuat kedua lautan mengalir. Keduanya akan bertemu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2927">2927</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11322,2,55,21,'<p><span>Di antara keduanya ada </span><span>[b]</span><span> pembatas, keduanya tidak saling melampaui.</span></p><p><span>[b]</span><span> 25:54 ; 27:62.</span></p>');
INSERT INTO `quran` VALUES (11323,2,55,22,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11324,2,55,23,'<p><span>Dari keduanya, </span><span>laut, </span><span>keluar mutiara dan batu marjan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2928">2928</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11325,2,55,24,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11326,2,55,25,'<p><span>Dan kepunyaan-Nya </span><span>[a]</span><span> bahtera-bahtera yang ditinggikan di lautan seperti gunung- gunung. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2929">2929</a><span>]</span></p><p><span>[a]</span><span> 42:33.</span></p>');
INSERT INTO `quran` VALUES (11327,2,55,26,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11328,2,55,27,'<p><span>R. 2</span></p><p><span>[b]</span><span> Segala sesuatu yang ada di atasnya </span><span>bumi,</span><span> itu akan binasa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2930">2930</a><span>]</span></p><p><span>[b]</span><span> 28:89.</span></p>');
INSERT INTO `quran` VALUES (11329,2,55,28,'<p><span>Dan, akan kekal hanyalah Wujud </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2931">2931</a><span>]</span><span> Tuhan engkau, Pemilik segala kemegahan dan kemuliaan.</span></p>');
INSERT INTO `quran` VALUES (11330,2,55,29,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11331,2,55,30,'<p><span>Memohon kepada-Nya segala yang ada di seluruh langit dan bumi. Setiap hari Dia </span><span>menampakkan sifat-Nya</span><span> dalam keadaan yang berlainan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2932">2932</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11332,2,55,31,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11333,2,55,32,'<p><span>Segera Kami akan memperhatikan kamu, hai dua golongan yang kuat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2933">2933</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11334,2,55,33,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11335,2,55,34,'<p><span>Hai golongan jin dan manusia ! Andaikata kalian memiliki kekuatan untuk menembus batas-batas seluruh langit dan bumi, maka tembuslah. Namun kamu tidak dapat menembus</span><span>nya</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2934">2934</a><span>]</span><span> kecuali dengan kekuatan.</span></p>');
INSERT INTO `quran` VALUES (11336,2,55,35,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11337,2,55,36,'<p><span>Akan dikirimkan kepada kamu berdua nyala api, dan leburan tembaga, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2935">2935</a><span>]</span><span> maka kamu berdua tidak akan dapat menolong diri sendiri.</span></p>');
INSERT INTO `quran` VALUES (11338,2,55,37,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11339,2,55,38,'<p><span>[a]</span><span> Dan ketika langit terbelah dan menjadi merah bagaikan kulit merah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2936">2936</a><span>]</span><span> </span></p><p><span>[a]</span><span> 69:17, 84:2.</span></p>');
INSERT INTO `quran` VALUES (11340,2,55,39,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11341,2,55,40,'<p><span>Pada hari itu tidak akan ditanya dosanya baik manusia ataupun jin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2937">2937</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11342,2,55,41,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11343,2,55,42,'<p><span>Akan dikenal orang-orang berdosa dengan ciri-ciri muka mereka, maka mereka akan dipegang pada rambut dahi dan kaki.</span></p>');
INSERT INTO `quran` VALUES (11344,2,55,43,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11345,2,55,44,'<p><span>[b]</span><span> Inilah Jahannam yang orang-orang mendustakannya.</span></p><p><span>[b]</span><span> 52:15.</span></p>');
INSERT INTO `quran` VALUES (11346,2,55,45,'<p><span>Mereka akan berkeliling-keliling di antara </span><span>Jahannam</span><span> itu dan air panas mendidih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2938">2938</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11347,2,55,46,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11348,2,55,47,'<p><span>R. 3</span></p><p><span>Dan bagi orang yang takut pada Keagungan Tuhan-nya ada dua syurga. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2939">2939</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11349,2,55,48,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11350,2,55,49,'<p><span>Yang mempunyai berbagai perpohonan yang rimbun daunnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2940">2940</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11351,2,55,50,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11352,2,55,51,'<p><span>Di dalamnya keduanya ada dua mata air </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2941">2941</a><span>]</span><span> yang mengalir.</span></p>');
INSERT INTO `quran` VALUES (11353,2,55,52,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11354,2,55,53,'<p><span>Di dalam keduanya terdapat </span><span>[a]</span><span> segala macam buah-buahan yang berpasangan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2942">2942</a><span>]</span></p><p><span>[a]</span><span> 44:56 ; 52:23 ; 56:21.</span></p>');
INSERT INTO `quran` VALUES (11355,2,55,54,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11356,2,55,55,'<p><span>Mereka </span><span>[b]</span><span> bersandar </span><span>pada bantal guling</span><span> atas permadani-permadani yang lapisannya dari sutera. Dan buah-buahan dari kedua surga itu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2943">2943</a><span>]</span><span> rendah terjangkau.</span></p><p><span>[b]</span><span> 38:52.</span></p>');
INSERT INTO `quran` VALUES (11357,2,55,56,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11358,2,55,57,'<p><span>Dalam </span><span>kebun-kebun surga</span><span> itu akan terdapat pula bidadari-bidadari yang </span><span>[a]</span><span> menundukkan pandangan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2944">2944</a><span>]</span><span> tidak pernah menyentuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2945">2945</a><span>]</span><span> mereka manusia sebelum mereka dan tidak </span><span>pula</span><span> jin.</span></p><p><span>[a]</span><span> 37:49 ; 38:53.</span></p>');
INSERT INTO `quran` VALUES (11359,2,55,58,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11360,2,55,59,'<p><span>Seolah-olah mereka itu permata-permata yakut dan </span><span>[b]</span><span> marjan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2946">2946</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11361,2,55,60,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11362,2,55,61,'<p><span>Bukankah ganjaran kebaikan itu tidak lain melainkan kebaikan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2947">2947</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11363,2,55,62,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11364,2,55,63,'<p><span>Dan disamping kedua ini ada dua surga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2948">2948</a><span>]</span><span> lagi.</span></p>');
INSERT INTO `quran` VALUES (11365,2,55,64,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11366,2,55,65,'<p><span>Kedua </span><span>surga</span><span> itu sangat hijau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2949">2949</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11367,2,55,66,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11368,2,55,67,'<p><span>Di dalam kedua </span><span>kebun surga</span><span> itu terdapat dua mata air </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2950">2950</a><span>]</span><span> yang memancar dengan deras.</span></p>');
INSERT INTO `quran` VALUES (11369,2,55,68,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11370,2,55,69,'<p><span>[a]</span><span> Di dalam keduanya terdapat buah-buahan dan kurma serta delima.</span></p><p><span>[a]</span><span> 36:58 ; 38:52 ; 43:74.</span></p>');
INSERT INTO `quran` VALUES (11371,2,55,70,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11372,2,55,71,'<p><span>Di dalamnya terdapat </span><span>bidadari-bidadari</span><span> yang baik </span><span>lagi</span><span> cantik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2951">2951</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11373,2,55,72,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11374,2,55,73,'<p><span>Bidadari-bidadari jelita ditempatkan di dalam kemah-kemah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2952">2952</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11375,2,55,74,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11376,2,55,75,'<p><span>Yang tidak pernah menyentuh mereka manusia sebelum mereka dan tidak </span><span>pula</span><span> jin.</span></p>');
INSERT INTO `quran` VALUES (11377,2,55,76,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ?</span></p>');
INSERT INTO `quran` VALUES (11378,2,55,77,'<p><span>[a]</span><span> Bersandarlah pada bantal guling dan permadani-permadani indah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2953">2953</a><span>]</span></p><p><span>[a]</span><span> 55:55.</span></p>');
INSERT INTO `quran` VALUES (11379,2,55,78,'<p><span>Maka, yang manakah di antara nikmat-nikmat Tuhan kamu berdua, yang kamu dustakan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2954">2954</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11380,2,55,79,'<p><span>Maha beberkatlah nama Tuhan engkau, Pemilik segala kemegahan dan kemuliaan.</span></p>');
INSERT INTO `quran` VALUES (11381,2,56,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2955">2955</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11382,2,56,2,'<p><span>Apabila </span><span>[b]</span><span> peristiwa yang tidak dapat dielakkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2956">2956</a><span>] </span><span>itu terjadi.</span></p><p><span>[b]</span><span> 52:8.</span></p>');
INSERT INTO `quran` VALUES (11383,2,56,3,'<p><span>[c]</span><span> Tidak ada seorang pun mendustakan kejadian itu.</span></p><p><span>[c]</span><span> 52:9 ; 70:3.</span></p>');
INSERT INTO `quran` VALUES (11384,2,56,4,'<p><span>Peristiwa itu</span><span> akan merendahkan </span><span>sebagian,</span><span> dan akan meninggikan </span><span>sebagian lain.</span><span> </span><span>{</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2957">2957</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (11385,2,56,5,'<p><span>[d]</span><span> Apabila bumi akan digoncangkan dengan sehebat-hebatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2958">2958</a><span>]</span></p><p><span>[d]</span><span> 50:45 ; 99:2.</span></p>');
INSERT INTO `quran` VALUES (11386,2,56,6,'<p><span>Dan gunung-gunung itu akan </span><span>[e]</span><span> dihancur-leburkan.</span></p><p><span>[e]</span><span> 20:106 ; 70:10 ; 101:6.</span></p>');
INSERT INTO `quran` VALUES (11387,2,56,7,'<p><span>Semua gunung</span><span> itu akan menjadi seperti zarah-zarah debu yang beterbangan.</span></p>');
INSERT INTO `quran` VALUES (11388,2,56,8,'<p><span>Dan, kamu akan menjadi tiga golongan.</span></p>');
INSERT INTO `quran` VALUES (11389,2,56,9,'<p><span>Maka mereka yang sebelah kanan, alangkah </span><span>bahagianya </span><span> mereka yang di sebelah kanan itu ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2959">2959</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11390,2,56,10,'<p><span>Dan mereka yang di sebelah kiri, alangkah </span><span>celakanya </span><span> mereka yang di sebelah kiri itu ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2960">2960</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11391,2,56,11,'<p><span>Dan yang paling dahulu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2961">2961</a><span>]</span><span> mereka </span><span>benar-benar</span><span> yang paling dahulu ;</span></p>');
INSERT INTO `quran` VALUES (11392,2,56,12,'<p><span>Mereka itulah orang-orang yang dekat </span><span>kepada Tuhan.</span></p>');
INSERT INTO `quran` VALUES (11393,2,56,13,'<p><span>Mereka akan berada </span><span> di dalam surga-surga kenikmatan.</span></p>');
INSERT INTO `quran` VALUES (11394,2,56,14,'<p><span>Segalongan besar dari orang-orang terdahulu </span><span>dalam keimanan.</span></p>');
INSERT INTO `quran` VALUES (11395,2,56,15,'<p><span>Dan segalongan kecil dari orang-orang kemudian </span><span>dalam keimanan.</span></p>');
INSERT INTO `quran` VALUES (11396,2,56,16,'<p><span>Mereka di atas dipan bertatahkan </span><span>emas dan permata,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2962">2962</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11397,2,56,17,'<p><span>[a]</span><span> Bersandar padanya, sambil berhadap-hadapan.</span></p><p><span>[a]</span><span> 37:45 ; 55:55 ; 76:14.</span></p>');
INSERT INTO `quran` VALUES (11398,2,56,18,'<p><span>[b]</span><span> Akan melayani mereka pemuda-pemuda yang dikekalkan</span><span> dalam kebaikan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2963">2963</a><span>]</span></p><p><span>[b]</span><span> 76:20.</span></p>');
INSERT INTO `quran` VALUES (11399,2,56,19,'<p><span>Dengan </span><span>membawa</span><span> </span><span>[c]</span><span> gelas, cerek dan cangkir </span><span>yang diisi</span><span> dari mata air.</span></p><p><span>[c]</span><span> 43:72 ; 76:16.</span></p>');
INSERT INTO `quran` VALUES (11400,2,56,20,'<p><span>Mereka tidak akan sakit kepala karenanya, </span><span>[d]</span><span> tidak pula mereka akan mabuk.</span></p><p><span>[d]</span><span> 37:48.</span></p>');
INSERT INTO `quran` VALUES (11401,2,56,21,'<p><span>Dan </span><span>membawa </span><span> buah-buahan </span><span>[e]</span><span> yang dipilih mereka.</span></p><p><span>[e]</span><span> 52:23,</span></p>');
INSERT INTO `quran` VALUES (11402,2,56,22,'<p><span>Dan daging burung-burung yang digemari mereka.</span></p>');
INSERT INTO `quran` VALUES (11403,2,56,23,'<p><span>Dan bidadari-bidadari </span><span>[f]</span><span> yang bermata jeli.</span></p><p><span>[f]</span><span> 44:55 ; 52:21.</span></p>');
INSERT INTO `quran` VALUES (11404,2,56,24,'<p><span>Laksana mutiara yang tersimpan baik.</span></p>');
INSERT INTO `quran` VALUES (11405,2,56,25,'<p><span>Sebagai ganjaran atas apa yang telah diamalkan mereka.</span></p>');
INSERT INTO `quran` VALUES (11406,2,56,26,'<p><span>Di dalamnya mereka tidak akan mendengar </span><span>[g]</span><span> ucapan sia-sia dan tidak pula ucapan yang menjurus kepada dosa.</span></p><p><span>[g]</span><span> 19:63 ; 78:36 ; 88:12.</span></p>');
INSERT INTO `quran` VALUES (11407,2,56,27,'<p><span>Melainkan hanya ucapan “Selamat sejahtera, selamat sejahtera.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2964">2964</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11408,2,56,28,'<p><span>Dan mereka yang di sebelah kanan, alangkah </span><span>bahagianya </span><span> mereka yang di sebalah kanan itu !</span></p>');
INSERT INTO `quran` VALUES (11409,2,56,29,'<p><span>Mereka akan berada </span><span> di antara pohon-pohon Sidrah yang tidak berduri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2965">2965</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (11410,2,56,30,'<p><span>Dan buah-buah pisang yang bersusun-susun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2966">2966</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11411,2,56,31,'<p><span>Dan </span><span>[a]</span><span> keteduhan yang </span><span>membentang </span><span> luas,</span></p><p><span>[a]</span><span> 4:58 ; 13:36.</span></p>');
INSERT INTO `quran` VALUES (11412,2,56,32,'<p><span>Dan, air yang tercurah,</span></p>');
INSERT INTO `quran` VALUES (11413,2,56,33,'<p><span>Dan, buah-buahan yang berlimpah-limpah,</span></p>');
INSERT INTO `quran` VALUES (11414,2,56,34,'<p><span>Yang tidak kunjung putus, dan tidak terlarang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2967">2967</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11415,2,56,35,'<p><span>Dan </span><span>mereka akan mempunyai </span><span> istri-istri mulia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2967a">2967a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11416,2,56,36,'<p><span>Sesungguhnya, Kami telah menciptakan mereka suatu penciptaan</span><span> yang baik,</span></p>');
INSERT INTO `quran` VALUES (11417,2,56,37,'<p><span>Dan Kami menjadikan mereka gadis-gadis perawan,</span></p>');
INSERT INTO `quran` VALUES (11418,2,56,38,'<p><span>Yang cantik jelita, </span><span>[b]</span><span> sebaya dalam usia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2968">2968</a><span>]</span></p><p><span>[b]</span><span> 78:34.</span></p>');
INSERT INTO `quran` VALUES (11419,2,56,39,'<p><span>Untuk mereka yang di sebelah kanan.</span></p>');
INSERT INTO `quran` VALUES (11420,2,56,40,'<p><span>R. 2</span></p><p><span>Segolongan besar dari antara orang-orang terdahulu </span><span>dalam keimanan,</span></p>');
INSERT INTO `quran` VALUES (11421,2,56,41,'<p><span>Dan segolongan besari dari orang-orang kemudian </span><span>dalam keimananan.</span></p>');
INSERT INTO `quran` VALUES (11422,2,56,42,'<p><span>Dan, mereka yang disebelah kiri, alangkah </span><span>celakanya </span><span> yang di sebelah kiri itu !</span></p>');
INSERT INTO `quran` VALUES (11423,2,56,43,'<p><span>Mereka akan berada</span><span> di tengah-tengah angin panas membawa air panas mendidih, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2969">2969</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11424,2,56,44,'<p><span>Dan, di bawah naungan asap hitam,</span></p>');
INSERT INTO `quran` VALUES (11425,2,56,45,'<p><span>Tidak sejuk, dan tidak menyenangkan.</span></p>');
INSERT INTO `quran` VALUES (11426,2,56,46,'<p><span>Sesungguhnya, mereka sebelum itu hidup dalam kemewahan,</span></p>');
INSERT INTO `quran` VALUES (11427,2,56,47,'<p><span>Dan, mereka terus menerus berbuat dosa besar, </span><span>syirik.</span></p>');
INSERT INTO `quran` VALUES (11428,2,56,48,'<p><span>Dan mereka </span><span>dahulu </span><span> mengatakan, “Apa ! Apabila kami sudah mati dan menjadi tanah dan tulang-belulang, apakah sungguh kami </span><span>[a]</span><span> akan dibangkitkan kembali ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2970">2970</a><span>]</span></p><p><span>[a]</span><span> 17:50 ; 23:83 ; 37:17 ; 56:48.</span></p>');
INSERT INTO `quran` VALUES (11429,2,56,49,'<p><span>[a]</span><span> “Atau apakah </span><span>demikian pula </span><span> bapak-bapak kami terdahulu ? “</span></p><p><span>[a]</span><span> 37:18.</span></p>');
INSERT INTO `quran` VALUES (11430,2,56,50,'<p><span>Katakanlah, “Sesungguhnya orang-orang terdahulu dan orang-orang kemudian,</span></p>');
INSERT INTO `quran` VALUES (11431,2,56,51,'<p><span>“Pasti akan dihimpun semuanya di saat tertentu pada hari yang telah ditetapkan.</span></p>');
INSERT INTO `quran` VALUES (11432,2,56,52,'<p><span>“Kemudian, sesungguhnya kamu hai orang-orang yang telah sesat dan mendustakan </span><span>kebenaran,</span></p>');
INSERT INTO `quran` VALUES (11433,2,56,53,'<p><span>“Kamu pasti akan makan pohon </span><span>[b]</span><span> zaqqum,</span></p><p><span>[b]</span><span> 37:63 ; 44:44 </span><span>–</span><span> 45.</span></p>');
INSERT INTO `quran` VALUES (11434,2,56,54,'<p><span>[c]</span><span> Maka mereka akan memenuhi perus dengannya.</span></p><p><span>[c]</span><span> 37:67.</span></p>');
INSERT INTO `quran` VALUES (11435,2,56,55,'<p><span>“Maka mereka akan minum di atasnya dari air mendidih.</span></p>');
INSERT INTO `quran` VALUES (11436,2,56,56,'<p><span>“Maka mereka akan minum seperti unta kehausan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2971">2971</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11437,2,56,57,'<p><span>“Inilah jamuan mereka pada Hari Pembalasan.”</span></p>');
INSERT INTO `quran` VALUES (11438,2,56,58,'<p><span>Kami telah menciptakan kamu, maka mengapa kah kamu tidak membenarkan </span><span>kebenaran</span><span> ?</span></p>');
INSERT INTO `quran` VALUES (11439,2,56,59,'<p><span>Apakah yang kamu pikirkan </span><span>tentang nutfah </span><span> </span><span>[a]</span><span> yang kamu masukkan ?</span></p><p><span>[a]</span><span> 75:38. </span></p>');
INSERT INTO `quran` VALUES (11440,2,56,60,'<p><span>Apakah kamu yang menciptakan, ataukah Kami, Sang Pencipta ?</span></p>');
INSERT INTO `quran` VALUES (11441,2,56,61,'<p><span>Kami telah menakdirkan kematian di antara kamu ; dan tidak ada yang mendahului </span><span>[b]</span><span> Kami.</span></p><p><span>[b]</span><span> 71:5.</span></p>');
INSERT INTO `quran` VALUES (11442,2,56,62,'<p><span>Dalam hal Kami menggantikan kamu </span><span>dengan yang lain</span><span> seperti kamu, dan Kami mengembang kan kamu ke dalam suatu </span><span>bentuk </span><span> yang tidak kamu ketahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2972">2972</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11443,2,56,63,'<p><span>Dan sesungguhnya, kamu telah mengetahui kejadian pertama, maka mengapakah kamu tidak mengambil pelajaran ?</span></p>');
INSERT INTO `quran` VALUES (11444,2,56,64,'<p><span>Apakah kamu mengetahui apa yang kamu tanam ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2973">2973</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11445,2,56,65,'<p><span>Apakah kamu yang menumbuhkannya, atau Kami yang menumbuhkan ?</span></p>');
INSERT INTO `quran` VALUES (11446,2,56,66,'<p><span>Sekiranya Kami menghendaki, niscaya Kami menjadikannya </span><span>[a]</span><span> kering dan hancur, maka jadilah kamu terheran-heran.</span></p><p><span>[a]</span><span> 57:21.</span></p>');
INSERT INTO `quran` VALUES (11447,2,56,67,'<p><span>Sesungguhnya kami dibebani denda !</span></p>');
INSERT INTO `quran` VALUES (11448,2,56,68,'<p><span>Bahkan kami orang-orang yang dijauhkan </span><span>dari segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (11449,2,56,69,'<p><span>Apakah kamu memperhatikan air yang kamu minum ?</span></p>');
INSERT INTO `quran` VALUES (11450,2,56,70,'<p><span>Apakah kamu yang menurunkannya dari awan, ataukah Kami yang menurunkan ?</span></p>');
INSERT INTO `quran` VALUES (11451,2,56,71,'<p><span>Sekiranya Kami kehendaki, niscaya Kami menjadikannya pahit, maka mengapakah kamu tidak bersyukur ?</span></p>');
INSERT INTO `quran` VALUES (11452,2,56,72,'<p><span>Apakah kamu memperhatikan </span><span>[b]</span><span> api </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2974">2974</a><span>]</span><span> yang kamu nyalakan ?</span></p><p><span>[b]</span><span> 36:81.</span></p>');
INSERT INTO `quran` VALUES (11453,2,56,73,'<p><span>Apakah kamu yang menumbuhkan pohonnya, ataukah Kami yang menumbuhkan ?</span></p>');
INSERT INTO `quran` VALUES (11454,2,56,74,'<p><span>Kami menjadikannya sebagai peringatan dan manfaat bagi orang-orang musafir. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2975">2975</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11455,2,56,75,'<p><span>[a]</span><span> Maka sanjunglah nama Tuhan engkau, Yang Maha Agung.</span></p><p><span>[a]</span><span> 69:53 ; 87:2.</span></p>');
INSERT INTO `quran` VALUES (11456,2,56,76,'<p><span>R. 3</span></p><p><span>Maka, pasti </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2976">2976</a><span>]</span><span> Aku bersumpah demi bintang-bintang berjatuhan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2977">2977</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11457,2,56,77,'<p><span>Dan sesungguhnya, itu adalah kesaksian agung, seandainya kamu mengetahui.</span></p>');
INSERT INTO `quran` VALUES (11458,2,56,78,'<p><span>Sesungguhnya itu adalah </span><span>[b]</span><span> Alquran yang mulia,</span></p><p><span>[b]</span><span> 50:2.</span></p>');
INSERT INTO `quran` VALUES (11459,2,56,79,'<p><span>Dalam </span><span>[c]</span><span> suatu kitabb terpelihara dengan baik. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2978">2978</a><span>]</span></p><p><span>[c]</span><span> 85:23.</span></p>');
INSERT INTO `quran` VALUES (11460,2,56,80,'<p><span>Yang tiada orang dapat menyentuhnya kecuali mereka yang disucikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2979">2979</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (11461,2,56,81,'<p><span>[a]</span><span> </span><span>Itu adalah wahyu</span><span> yang diturunkan dari Tuhan semesta alam.</span></p><p><span>[a]</span><span> 20:5 ; 26:193.</span></p>');
INSERT INTO `quran` VALUES (11462,2,56,82,'<p><span>Apakah tentang kalam ini kamu anggap remeh ?</span></p>');
INSERT INTO `quran` VALUES (11463,2,56,83,'<p><span>Dan kamu menjadikan rezekimu dengan mendustakan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2980">2980</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11464,2,56,84,'<p><span>Maka, mengapa tidak, ketika </span><span>ruh </span><span> sampai di kerongkongan ?</span></p>');
INSERT INTO `quran` VALUES (11465,2,56,85,'<p><span>Dan kamu pada waktu itu terus memperhatikan.</span></p>');
INSERT INTO `quran` VALUES (11466,2,56,86,'<p><span>[a]</span><span> Dan, Kami lebih dekat kepadanya dari kamu, akan tetapi kamu tidak melihat.</span></p><p><span>[a] </span><span>50:17.</span></p>');
INSERT INTO `quran` VALUES (11467,2,56,87,'<p><span>Maka, mengapa sekiranya kamu tidak akan dituntut,</span></p>');
INSERT INTO `quran` VALUES (11468,2,56,88,'<p><span>Maka mengapa jika kamu tidak dibalas, kamu mengambalikannya, jika kamu orang benar ?</span></p>');
INSERT INTO `quran` VALUES (11469,2,56,89,'<p><span>Maka, jika ia dari antara mereka yang dekat </span><span>kepada Tuhan,</span></p>');
INSERT INTO `quran` VALUES (11470,2,56,90,'<p><span>Maka </span><span>baginya </span><span> ada kesenangan dan keharuman dan surga kenikmatan.</span></p>');
INSERT INTO `quran` VALUES (11471,2,56,91,'<p><span>Dan adapun jika ia dari golongan kanan.</span></p>');
INSERT INTO `quran` VALUES (11472,2,56,92,'<p><span>Maka, selamatlah bagi engkau, dari golongan kanan !</span></p>');
INSERT INTO `quran` VALUES (11473,2,56,93,'<p><span>Dan adapun jika ia dari golongan yang mendustakan </span><span>lagi</span><span> sesat.</span></p>');
INSERT INTO `quran` VALUES (11474,2,56,94,'<p><span>Maka </span><span>baginya ada</span><span> jamuan air mendidih.</span></p>');
INSERT INTO `quran` VALUES (11475,2,56,95,'<p><span>Dan dibakar dalam neraka,</span></p>');
INSERT INTO `quran` VALUES (11476,2,56,96,'<p><span>Sesungguhnya, </span><span>[b]</span><span> ini adalah kebenaran yang diyakini.</span></p><p><span>[b]</span><span> 35:32.</span></p>');
INSERT INTO `quran` VALUES (11477,2,56,97,'<p><span>Maka sanjunglah nama Tuhan engkau, </span><span>[c]</span><span> Yang Maha Agung.</span></p><p><span>[c]</span><span> 56:75.</span></p>');
INSERT INTO `quran` VALUES (11478,2,57,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11479,2,57,2,'<p><span>[b]</span><span> Mensucikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2981">2981</a><span>]</span><span> bagi Allah segala yang ada di seluruh langit dan bumi, dan Dia Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b]</span><span> 17:45 ; 24:42 ; 61:2 ; 62:2 ; 64:2.</span></p>');
INSERT INTO `quran` VALUES (11480,2,57,3,'<p><span>Kepunyaan-Nya kerajaan seluruh langit dan bumi ; Dia menghidupkan dan </span><span>[c]</span><span> Dia mematikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2982">2982</a><span>]</span><span> , dan Dia berkuasa atas segala sesuatu.</span></p><p><span>[c]</span><span> 3:157 ; 7:159 ; 44:9.</span></p>');
INSERT INTO `quran` VALUES (11481,2,57,4,'<p><span>Dia-lah Yang Awal ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2983">2983</a><span>]</span><span> dan Yang Akhir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2984">2984</a><span>]</span><span> dan Yang Nyata </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2985">2985</a><span>]</span><span> dan Yang Ter-sembunyi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2986">2986</a><span>]</span><span> dan Dia Maha Mengetahui segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (11482,2,57,5,'<p><span>Dialah Yang menciptakan seluruh langit dan bumi </span><span>[a]</span><span> dalam enam masa, kemudian Dia bersemayam di atas </span><span>‘</span><span>Arasy. Dia mengetahui </span><span>[b]</span><span> apa yang masuk di bumi dan apa yang keluar darinya dan apa yang turun dari langit dan apa yang naik kepadanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2987">2987</a><span>]</span><span> Dan Dia beserta kamu di mana pun kamu berada. Dan Allah Maha Melihat segala yang kamu buat.</span></p><p><span>[a]</span><span> 7:55 ; 11:8 ; 25:60 ; 32:5.</span></p>');
INSERT INTO `quran` VALUES (11483,2,57,6,'<p><span>Kepunyaan Dia-lah </span><span>[c]</span><span> kerajaan seluruh langit dan bumi. Dan kepada Allah dikembalikan segala perkara.</span></p><p><span>[c]</span><span> 2:108 ; 7:159.</span></p>');
INSERT INTO `quran` VALUES (11484,2,57,7,'<p><span>[a]</span><span> Dia memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam. Dan Dia Maha Mengetahui segala yang terkandung di dalam dada.</span></p><p><span>[a] </span><span>22:62 ; 31:30 ; 35:14.</span></p>');
INSERT INTO `quran` VALUES (11485,2,57,8,'<p><span>Berimanlah kepada Allah dan Rasul-Nya, dan belanjakanlah dari apa yang Dia telah menjadikan kamu pewaris di dalamnya. Maka orang-orang yang beriman dari antara kamu dan menafkahkan </span><span>harta</span><span> bagi mereka ganjaran besar.</span></p>');
INSERT INTO `quran` VALUES (11486,2,57,9,'<p><span>Dan, mengapakah kamu tidak beriman kepada Allah, sedang Rasul itu memanggil kamu agar kamu beriman kepada Tuhan-mu, dan sesungguhnya Dia telah mengambil perjanjianmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2988">2988</a><span>]</span><span> jika kamu orang beriman ?</span></p>');
INSERT INTO `quran` VALUES (11487,2,57,10,'<p><span>Dia-lah Yang telah </span><span>[b]</span><span> menurunkan kepada hamba-Nya Tanda-tanda yang terang, supaya Dia </span><span>[c]</span><span> mengeluarkan kamu dari setiap kegelapan ke dalam cahaya. Dan, sesungguhnya Allah terhadapmu, Maha Penyantun dan Maha Penyayang.</span></p><p><span>[b]</span><span> 22:17 ; 24:35 ; 58:6.</span></p><p><span>[c]</span><span> 14:6 ; 33:44.</span></p>');
INSERT INTO `quran` VALUES (11488,2,57,11,'<p><span>Dan, mengapakah kamu tidak membelanjakan </span><span>harta</span><span> di jalan Allah, padahal bagi Allah warisan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2989">2989</a><span>]</span><span> seluruh langit dan bumi ? Tidak sama di antara kamu orang yang membelanjakan dan berperang sebelum kemenangan itu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2990">2990</a><span>]</span><span> Mereka lebih besar derajatnya daripada orang-orang yang membelanjakan dan berperang </span><span>[a]</span><span> kemudian. Dan semua yang Allah janjikan adalah yang terbaik. Dan Allah Maha Mengetahui apa yang kamu perbuat.</span></p><p><span>[a]</span><span> 4:96 ; 9:20.</span></p>');
INSERT INTO `quran` VALUES (11489,2,57,12,'<p><span>R. 2</span></p><p><span>[b]</span><span> Siapakah yang hendak meminjamkan kepada Allah pinjaman yang baik ? Maka Dia akan melipat-gandakannya baginya, dan baginya ganjaran yang mulia.</span></p><p><span>[b]</span><span> 2:246 ; 64:18 ; 73:21.</span></p>');
INSERT INTO `quran` VALUES (11490,2,57,13,'<p><span>Pada hari ketika engkau melihat laki-laki mukmin dan perempuan mukmin, cahaya mereka akan berlari-lari di hadapan </span><span>[c]</span><span> mereka dan di sebelah kanan mereka, </span><span>Tuhan dan malaikat akan berkata,</span><span> “Khabar suka bagimu pada hari ini </span><span>tentang</span><span> kebun-kebun yang di bawahnya mengalir sungai-sungai, mereka akan menetap di dalamnya. Itulah kemenangan yang sangat besar.”</span></p><p><span>[c]</span><span> 66:9.</span></p>');
INSERT INTO `quran` VALUES (11491,2,57,14,'<p><span>Pada hari ketika orang-orang munafik laki-laki dan orang-orang munafik perempuan akan berkata kepada orang-orang beriman, “Tunggulah kami, supaya kami memperoleh sebagian cahayamu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2991">2991</a><span>] </span><span>Akan dikatakan </span><span>kepada mereka, </span><span>“Kembalilah ke belakangmu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2992">2992</a><span>]</span><span> dan carilah cahaya,” Kemudian akan didirikan di antara mereka dinding </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2993">2993</a><span>]</span><span> yang berpintu. Di dalamnya ada rahmat dan di luarnya ada azab.</span></p>');
INSERT INTO `quran` VALUES (11492,2,57,15,'<p><span>Orang-orang munafik itu</span><span> akan berseru kepada mereka </span><span>yang beriman,</span><span> “Bukankah kami beserta kamu ?” Mereka </span><span>yang beriman </span><span>akan berkata, “Ya, akan tetapi kamu membiarkan dirimu jatuh ke dalam godaan dan kamu menunggu </span><span>kehancuran </span><span> kami dan kamu ragu, dan keinginanmu yang sia-sia memperdayakan kamu hingga datanglah keputusan Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2994">2994</a><span>]</span><span> dan menipu kamu tentang Allah </span><span>syaitan </span><span> yang sangat penipu.</span></p>');
INSERT INTO `quran` VALUES (11493,2,57,16,'<p><span>“Maka pada hari ini tidak akan diterima dari kamu tebusan, dan tidak </span><span>pula </span><span> dari orang-orang yang ingkar. Tempat tinggal kamu adalah Api. Itulah sahabatmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2995">2995</a><span>]</span><span> Dan seburuk-buruknya tempat kembali.</span></p>');
INSERT INTO `quran` VALUES (11494,2,57,17,'<p><span>Apakah belum sampai waktu bagi orang-orang yang beriman, bahwa hati mereka tunduk untuk mengingat Allah dan apa yang telah turun dari kebenaran, dan janganlah mereka menjadi seperti orang-orang yang diberi kitab sebelumnya, maka menjadi panjang atas </span><span>[a]</span><span> mereka zaman </span><span>aman,</span><span> maka jadilah keras </span><span>[b]</span><span> hati mereka. Dan kebanyakan dari mereka menjadi durhaka ?</span></p><p><span>[a]</span><span> 21:45.</span></p><p><span>[b]</span><span> 2:75 ; 6:44.</span></p>');
INSERT INTO `quran` VALUES (11495,2,57,18,'<p><span>Ketahuilah, bahwa Allah </span><span>[c]</span><span> menghidupkan bumi sesudah matinya. Sesungguhnya Kami telah menjelaskan Tanda-tanda kepadamu, supaya kamu mengerti.</span></p><p><span>[c]</span><span> 35:10.</span></p>');
INSERT INTO `quran` VALUES (11496,2,57,19,'<p><span>Sesungguhnya, orang-orang laki-laki yang memberi sedekah, dan orang-orang perempuan yang memberi sedekah, dan mereka yang telah meminjamkan kepada Allah </span><span>[d]</span><span> pinjaman yang baik akan dilipatgandakan bagi mereka, dan bagi mereka ada ganjaran yang sangat mulia.</span></p><p><span>[d]</span><span> 2:246.</span></p>');
INSERT INTO `quran` VALUES (11497,2,57,20,'<p><span>Dan orang-orang yang beriman kepada Allah dan para Rasul-Nya, mereka adalah orang-orang yang benar dan menjadi saksi di sisi Tuhan mereka. Bagi mereka ada ganjaran mereka dan cahaya mereka . Tetapi mereka yang ingkar dan mendustakan Tanda-tanda Kami, mereka adalah penghuni-penghuni Jahannam.</span></p>');
INSERT INTO `quran` VALUES (11498,2,57,21,'<p><span>Ketahuilah, bahwa sesungguhnya </span><span>[a]</span><span> kehidupan dunia ini hanyalah permainan dan pengisi waktu dan perhiasan dan saling berbangga di antara kamu, dan bersaing dalam banyaknya harta dan anak. </span><span>Kehidupan</span><span> </span><span>ini</span><span> seperti hujan, tanaman-tanamannya mengagumkan para pananamnya, kemudian </span><span>tanaman</span><span> itu bergerak dan engkau melihatnya menjadi kuning ; lalu </span><span>[b]</span><span> menjadi hancur. Dan di akhirat ada azab sangat keras dan ada ampunan dan keridhaan dari Allah. Dan tidak lain kehidupan di dunia ini melainkan kesenangan sementara yang menipu.</span></p><p><span>[a]</span><span> 6:33 ; 29:65 ; 47:37.</span></p><p><span>[b]</span><span> 56:66.</span></p>');
INSERT INTO `quran` VALUES (11499,2,57,22,'<p><span>[c]</span><span> Berlomba-lombalah kamu dalam </span><span>mencari</span><span> ampunan Tuhan-mu dan surga yang nilainya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2996">2996</a><span>]</span><span> setara dengan nilai langit dan bumi yang telah disediakan bagi orang-orang yang beriman kepada Allah dan para </span><span>[a]</span><span> rasul-Nya. Demikianlah karunia Allah ; Dia menganugerahkannya kepada siapa yang Dia kehendaki, dan Allah itu Yang Empunya karunia yang besar.</span></p><p><span>[c]</span><span> 3:134.</span></p>');
INSERT INTO `quran` VALUES (11500,2,57,23,'<p><span>Tidak ada musibah menimpa di bumi dan tidak pula pada dirimu, melainkan </span><span>sudah tercatat</span><span> dalam sebuah kitab </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2996a">2996a</a><span>]</span><span> sebelum Kami menciptakannya. Sesungguhnya yang demikian itu sangat mudah bagi Allah.</span></p>');
INSERT INTO `quran` VALUES (11501,2,57,24,'<p><span>[a]</span><span> Supaya kamu jangan bersedih atas apa yang luput dari kamu, dan jangan </span><span>pula</span><span> kamu terlampau gembira atas apa yang telah dianugerahkan kepadamu. Dan Allah tidak mencintai setiap pembual, sombong.</span></p><p><span>[a]</span><span> 3:154.</span></p>');
INSERT INTO `quran` VALUES (11502,2,57,25,'<p><span>[b]</span><span> Orang-orang yang bakhil dan menyuruh manusia berbuat bakhil. Dan barangsiapa berpaling maka sesungguhnya Allah, Dia-lah Maha Kaya dan Maha Terpuji.</span></p><p><span>[b]</span><span> 4:38.</span></p>');
INSERT INTO `quran` VALUES (11503,2,57,26,'<p><span>Sesungguhnya, Kami telah mengirimkan rasul-rasul Kami dengan </span><span>[a]</span><span> Tanda-tanda yang nyata dan Kami menurunkan beserta mereka Kitab </span><span>[b]</span><span> dan neraca </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2997">2997</a><span>]</span><span> supaya manusia dapat menegakkan keadilan ; dan Kami menurunkan besi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2998">2998</a><span>]</span><span> yang di dalamnya ada </span><span>bahan-bahan</span><span> untuk peperangan dahsyat dan berbagai manfaat bagi manusia ; dan supaya Allah mengetahui siapa yang menolong-Nya dan rasul- rasul-Nya dalam keadaan gaib. Sesungguh nya Allah itu Maha Kuat, Maha Perkasa.</span></p><p><span>[a]</span><span> 7:102 ; 14:10 ; 35:26.</span></p><p><span>[b]</span><span> 42:18 ; 55:8.</span></p>');
INSERT INTO `quran` VALUES (11504,2,57,27,'<p><span>R. 4</span></p><p><span>Dan sesungguhnya Kami telah mengirimkan Nuh dan Ibrahim, dan Kami meletakkan di antara benih keturunan mereka berdua kenabian dan </span><span>[c]</span><span> Kitab. Maka sebagian mereka mengikuti petunjuk, namun kebanyakan dari mereka itu pendurhaka.</span></p><p><span>[c]</span><span> 29:28.</span></p>');
INSERT INTO `quran` VALUES (11505,2,57,28,'<p><span>Kemudian </span><span>[a]</span><span> Kami mengikutkan di atas jejak-jejak mereka rasul-rasul Kami ; dan Kami mengikutikan di </span><span>atas jejak mereka </span><span> Isa Ibnu Maryam, dan Kami memberikan kepadanya Injil, </span><span>[b]</span><span> dan Kami jadikan dalam hati orang-orang yang mengikutinya rasa santun dan kasih sayang. Dan cara hidup merahib yang dibuat-buat mereka, Kami tidak mewajibkannya atas mereka, kecuali untuk mencari keridhaan Allah ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2999">2999</a><span>]</span><span> tetapi mereka tidak melaksanakannya sebagaimana seharusnya dilaksanakan. Maka Kami menganugerahkan kepada orang-orang yang beriman di antara mereka ganjaran mereka, namun kebanyakan dari mereka durhaka.</span></p><p><span>[a]</span><span> 2:88 ; 5:47.</span></p><p><span>[b]</span><span> 5:83.</span></p>');
INSERT INTO `quran` VALUES (11506,2,57,29,'<p><span>Hai orang-orang yang beriman, bertakwalah kepada Allah dan berimanlah kepada Rasul-Nya ; Dia akan menganugerahkan kepadamu dua bagian dari rahmat-Nya, dan akan mengadakan bagimu cahaya, yang dengannya kamu akan berjalan, dan Dia akan mengampuni kamu. Dan Allah itu Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11507,2,57,30,'<p><span>Hal demikian ialah,</span><span> supaya Ahlikitab jangan menganggap bahwa mereka </span><span>orang-orang mukmin </span><span> tidak mampu mendapatkan sesuatu karunia Allah ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3000">3000</a><span>] [a]</span><span> dan karunia itu </span><span>semuanya</span><span> ada di tangan Allah ; Dia menganugerahkannya kepada siapa yang Dia kehendaki. Dan Allah adalah Yang Empunya karunia yang besar.</span></p><p><span>[a]</span><span> 2:106 ; 3:74.</span></p>');
INSERT INTO `quran` VALUES (11508,2,58,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11509,2,58,2,'<p><span>Sesungguhnya Allah telah mendengar ucapan </span><span>perempuan</span><span> yang menyampaikan gugatan kepada engkau tentang suaminya dan mengadu kepada Allah, dan Allah telah mendengar percakapan kamu berdua. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3001">3001</a><span>]</span><span> Sesungguhnya Allah Maha Mendengar, Maha Melihat.</span></p>');
INSERT INTO `quran` VALUES (11510,2,58,3,'<p><span>Orang-orang di antara kamu yang menyatakan ibu kepada istri-istri mereka, bukanlah mereka itu ibu mereka. Tiadalah ibu-ibu mereka selain yang melahirkan mereka. Dan sesungguhnya mereka pasti mengucapkan perkataan yang tidak disukai dan dusta. Dan sesungguhnya Allah Maha Pemaaf, Maha Pengampun.</span></p>');
INSERT INTO `quran` VALUES (11511,2,58,4,'<p><span>Orang-orang yang menyatakan ibu terhadap istri-istri mereka, kemudian mereka hendak menarik kembali </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3002">3002</a><span>]</span><span> apa yang pernah dikatakan mereka, maka mereka harus memerdekakan seorang sahaya sebelum mereka berdua bercampur. Itulah yang dinasehatkan kepadamu. Dan Allah Maha Mengetahui apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11512,2,58,5,'<p><span>Maka, barangsiapa tidak mendapatkan </span><span>seorang sahaya,</span><span> ia harus berpuasa dua bulan berturut-turut, sebelum keduanya bercampur, dan barangsiapa tidak mampu berbuat demikian, ia harus memberi makan kepada enam puluh orang miskin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3003">3003</a><span>]</span><span> Dan demikianlah supaya kamu beriman kepada Allah dan Rasul-Nya. Dan demikian batas-batas </span><span>peraturan</span><span> Allah ; dan bagi orang-orang kafir ada azab yang sangat pedih.</span></p>');
INSERT INTO `quran` VALUES (11513,2,58,6,'<p><span>[a]</span><span> Sesungguhnya orang-orang yang menentang </span><span>perintah</span><span> Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3004">3004</a><span>]</span><span> dan Rasul-Nya dihinakan ; dan sesungguhnya Kami telah menurunkan Tanda-tanda yang nyata. Dan bagi orang-orang kafir ada azab yang menghinakan.</span></p><p><span>[a]</span><span> 9:63</span></p>');
INSERT INTO `quran` VALUES (11514,2,58,7,'<p><span>Pada Hari ketika Allah akan membangkitkan mereka semua bersama-sama, maka Dia akan memberitahukan kepada mereka tentang apa yang mereka berbuat. Allah telah menghitung semua itu, tetapi mereka telah melupakannya. Dan Allah itu Pengawas atas segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (11515,2,58,8,'<p><span>R. 2</span></p><p><span>Apakah engkau tidak melihat bahwa sesungguhnya Allah mengetahui segala sesuatu di seluruh langit dan semua yang ada di bumi ? Tiada permusyawaratan rahasia antara tiga orang, melainkan Dia yang keempatnya dan tidak pula antara lima orang, melainkan Dia yang keenam nya dan tidak pula </span><span>antara bilangan</span><span> yang kurang dari itu, dan tidak pula yang lebih, melainkan Dia ada beserta mereka di mana pun mereka berada, kemudian Dia akan memberitahukan </span><span>kepada</span><span> mereka tentang apa yang telah dikerjakan mereka pada Hari Kiamat. Sesungguhnya Allah Maha Mengetahui segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (11516,2,58,9,'<p><span>Apakah engkau tidak melihat orang-orang yang dilarang </span><span>mengadakan</span><span> musyawarah rahasia, kemudian mereka kembali kepada apa yang mengenainya mereka dilarang dan mereka bermusyawarah secara rahasia tentang dosa dan pelanggaran dan kedurhakaan </span><span>terhadap </span><span> rasul itu ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3005">3005</a><span>]</span><span> Dan apabila mereka datang kepada engkau, mereka mengucapkan salam kepada engkau dengan </span><span>[a]</span><span> </span><span>ucapan salam</span><span> yang tidak pernah diucapkan Allah kepada engkau, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3006">3006</a><span>]</span><span> dan mereka berkata kepada diri mereka sendiri, “Mengapakah Allah tidak mengazab kami atas apa yang kami ucapkan ? “ Maka, cukuplah Jahannam bagi mereka yang di dalamnya mereka akan dibakar ; dan itulah seburuk-buruk tempat kembali !</span></p><p><span>[a]</span><span> 4:47.</span></p>');
INSERT INTO `quran` VALUES (11517,2,58,10,'<p><span>Hai, orang-orang yang beriman ! Apabila kamu mengadakan musyawarah- musyawarah rahasia, janganlah kamu bermusyawarah tentang dosa dan pelanggaran dan kedurhakaan terhadap Rasul, tetapi hendaklah kamu bermusyawarah tentang kebaikan dan ketakwaan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3007">3007</a><span>]</span><span> dan bertakwalah kepada Allah, Yang kepada Dia-lah kamu akan dihimpun.</span></p>');
INSERT INTO `quran` VALUES (11518,2,58,11,'<p><span>Sesungguhnya musyawarah rahasia itu dari syaitan, supaya orang-orang yang beriman menjadi sedih, dan ia tidak dapat memudaratkan mereka sedikit pun, kecuali dengan izin Allah. Dan hanya kepada Allah hendaknya orang-orang mukmin bertawakkal.</span></p>');
INSERT INTO `quran` VALUES (11519,2,58,12,'<p><span>Hai, orang-orang yang beriman ! Apabila dikatakan kepadamu, “Lapangkanlah tempat di dalam majelis.” Maka hendaklah kamu melapangkan bagimu, dan apabila dikatakan, “Berdirilah,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3008">3008</a><span>]</span><span> maka hendaklah kamu berdiri, Allah akan mengangkat orang-orang yang beriman di antara kamu dan orang-orang yang diberi ilmu pengetahuan, beberapa derajat. Dan Allah Maha Mengetahui tentang apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11520,2,58,13,'<p><span>Hai, orang-orang yang beriman ! Apabila kamu bermusyawarah dengan Rasul, maka berikanlah sedekah lebih dahulu sebelum musyawarahmu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3009">3009</a><span>]</span><span> Hal demikian itu lebih baik bagimu dan lebih suci. Namun, apabila kamu tidak mendapat </span><span>sesuatu,</span><span> maka Allah itu Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11521,2,58,14,'<p><span>Apakah kamu takut memberi sedekah sebelum kamu mengambil musyawarah ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3010">3010</a><span>] Maka, apabila kamu tidak berbuat demikian dan Allah telah bermurah hati kepadamu, maka dirikanlah shalat dan tunaikanlah zakat dan patuhilah Allah dan Rasul-Nya. Dan Allah Maha Mengetahui tentang apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11522,2,58,15,'<p><span>Apakah engkau tidak melihat orang-orang yang bersahabat dengan kaum yang Allah murka </span><span>[a]</span><span> terhadap mereka ? Orang-orang itu bukan dari kamu dan bukan dari mereka, dan mereka itu bersumpah atas kedustaan padahal mereka itu mengetahui.</span></p><p><span>[a]</span><span> 60:14</span></p>');
INSERT INTO `quran` VALUES (11523,2,58,16,'<p><span>Allah telah menyediakan bagi mereka azab yang sangat keras. Sesungguhnya sangat buruklah apa yang biasa mereka kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11524,2,58,17,'<p><span>Mereka telah menjadikan sumpah-sumpah mereka sebagai perisai </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3011">3011</a><span>]</span><span> dan mereka berusaha menghalangi </span><span>manusia </span><span> dari jalan Allah ; maka bagi mereka itu ada azab yang menghinakan.</span></p>');
INSERT INTO `quran` VALUES (11525,2,58,18,'<p><span>[a]</span><span> Sekali-kali tidak akan bermanfaat bagi mereka harta mereka dan tidak pula anak-anak mereka terhadap Allah sedikit pun. Mereka adalah penghuni Api. Di dalamnya mereka akan menetap.</span></p><p><span>[a]</span><span> 3:11 ; 92:12 ; 111:3.</span></p>');
INSERT INTO `quran` VALUES (11526,2,58,19,'<p><span>Pada hari ketika Allah akan membangkitkan mereka semua maka mereka akan bersumpah kepada-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3012">3012</a><span>]</span><span> sebagaimana mereka bersumpah kepadamu, dan mereka menyangka bahwa mereka mempunyai sesuatu. Ketahuilah sesungguhnya mereka adalah orang-orang pendusta.</span></p>');
INSERT INTO `quran` VALUES (11527,2,58,20,'<p><span>Syaitan telah berkuasa atas mereka, dan telah menjadikan mereka lupa berzikir kepada Allah. Mereka itu golongan syaitan. Ketahuilah, sesungguhnya golongan syaitan itu merekalah yang rugi.</span></p>');
INSERT INTO `quran` VALUES (11528,2,58,21,'<p><span>[a]</span><span> Sesungguhnya orang-orang yang menentang Allah dan Rasul-Nya mereka itu termasuk orang-orang yang sangat hina.</span></p><p><span>[a]</span><span> 9:63.</span></p>');
INSERT INTO `quran` VALUES (11529,2,58,22,'<p><span>Allah telah menetapkan “Aku dan rasul-rasul-Ku </span><span>[b]</span><span> pasti akan menang.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3013">3013</a><span>]</span><span> Sesungguhnya Allah itu Maha Kuat, Maha Perkasa.</span></p><p><span>[b]</span><span> 5:57 ; 37:172 </span><span>–</span><span> 173.</span></p>');
INSERT INTO `quran` VALUES (11530,2,58,23,'<p><span>[c]</span><span> Engkau tidak akan mendapatkan suatu kaum yang beriman kepada Allah dan Hari Kemudian , mereka mencintai orang-orang yang memusuhi Allah dan Rasul-Nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3014">3014</a><span>]</span><span> dan walaupun mereka itu bapak-bapak mereka atau anak-anak mereka atau saudara-saudara mereka ataupun keluarga mereka. Mereka itulah orang-orang yang di dalam hati mereka Dia telah menanamkan iman dan Dia telah meneguhkan mereka dengan ilham dari Dia sendiri. Dan Dia akan memasukkan mereka ke dalam kebun-kebun yang mengalir di bawahnya sungai-sungai. Mereka akan menetap di dalamnya. </span><span>[d]</span><span> Allah ridha kepada mereka dan mereka ridha kepada-Nya. Itulah golongan Allah. Ketahuilah, sesungguhnya golongan Allah, mereka itulah orang-orang yang menang.</span></p><p><span>[c]</span><span> 3:29 ; 4:145 ; 9:23.</span></p><p><span>[d]</span><span> 5:120 ; 9:100 ; 98:9.</span></p>');
INSERT INTO `quran` VALUES (11531,2,59,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11532,2,59,2,'<p><span>[b]</span><span> Menyanjung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3015">3015</a><span>]</span><span> kepada Allah segala yang ada di seluruh langit dan segala yang ada di bumi, dan Dia-lah Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b]</span><span> 17:45 ; 24:42 ; 61:2 ; 62:2 ; 64:2.</span></p>');
INSERT INTO `quran` VALUES (11533,2,59,3,'<p><span>Dia-lah Yang mengeluarkan orang-orang ingkar di antara Ahlikitab dari rumah-rumah mereka pada pengusiran pertama. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3016">3016</a><span>]</span><span> Kamu tidak menyangka bahwa mereka akan keluar, dan mereka menyangka bahwa benteng-benteng akan melindungi mereka terhadap Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3017">3017</a><span>]</span><span> tetapi </span><span>[c]</span><span> Allah datang kepada mereka dari mana mereka tidak menyangka, dan Dia </span><span>[d]</span><span> melemparkan kecemasan dalam kalbu mereka, sehingga mereka merusakkan rumah mereka dengan tangan sendiri </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3018">3018</a><span>]</span><span> dan </span><span>dengan </span><span> tangan orang-orang mukmin. Maka ambillah pelajaran, hai orang-orang yang mempunyai pandangan.</span></p><p><span>[c]</span><span> 16:27 ; 39:26.</span></p><p><span>[d]</span><span> 3:152 ; 8:13.</span></p>');
INSERT INTO `quran` VALUES (11534,2,59,4,'<p><span>Dan, jika tidak karena Allah telah menetapkan pengusiran terhadap mereka, niscaya </span><span>Allah</span><span> telah mengazab mereka di dunia ini juga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3019">3019</a><span>]</span><span> Dan bagi mereka di akhirat ada azab Api.</span></p>');
INSERT INTO `quran` VALUES (11535,2,59,5,'<p><span>Hal demikian itu karena mereka menentang Allah dan Rasul-Nya ; dan </span><span>[a]</span><span> barangsiapa menentang Allah, maka sesungguhnya Allah sangat keras azab-Nya.</span></p><p><span>[a]</span><span> 4:116 ; 8:14 ; 47:33.</span></p>');
INSERT INTO `quran` VALUES (11536,2,59,6,'<p><span>Apa saja yang kamu tebang dari pohon kurma, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3020">3020</a><span>]</span><span> atau kamu membiarkannya berdiri pada akar-akarnya, maka itu dengan izin Allah, supaya Dia menghinakan orang-orang durhaka.</span></p>');
INSERT INTO `quran` VALUES (11537,2,59,7,'<p><span>Dan apapun harta yang Allah berikan kepada Rasul-Nya sebagai ghanimah dari mereka, </span><span>adalah karunia Allah.</span><span> Kamu tidak mengerahkan kuda maupun unta untuk </span><span>harta</span><span> itu ; akan tetapi Allah memberikan kewenangan kepada rasul-rasul-Nya atas siapa pun yang Dia kehendaki. Dan Allah Maha Kuasa atas segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (11538,2,59,8,'<p><span>Apapun harta </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3021">3021</a><span>]</span><span> yang Allah berikan kepada Rasul-Nya sebagai ghanimah dari warga kota, itu bagi Allah dan bagi Rasul dan bagi kaum kerabat dan anak yatim dan orang miskin dan orang musafir, supaya harta itu tidak </span><span>hanya </span><span> beredar di antara orang-orang kaya dari kamu. Dan apa yang diberikan Rasul kepadamu, maka ambillah itu ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3022">3022</a><span>]</span><span> dan apa yang dia melarang kamu darinya, maka hindarlah, dan bertakwalah kepada Allah. Sesungguhnya Allah sangat keras hukuman-</span><span>Nya.</span></p>');
INSERT INTO `quran` VALUES (11539,2,59,9,'<p><span>Harta ghanimah</span><span> itu untuk orang-orang miskin yang berhijrah yang terusir dari rumah mereka dan dari harta mereka ; mereka mencari karunia Allah dan keridhaan-</span><span>Nya,</span><span> dan mereka menolong Allah dan Rasul-Nya. Mereka itulah orang- orang yang benar.</span></p>');
INSERT INTO `quran` VALUES (11540,2,59,10,'<p><span>Dan </span><span>untuk</span><span> mereka yang telah mendirikan rumah </span><span>di Medinah</span><span> dan sudah beriman sebelum mereka, mereka mencintai orang-orang yang datang berhijrah kepada mereka, dan mereka tidak mendapati suatu keinginan dalam dada mereka mengenai apa yang diberikan kepada mereka itu, tetapi mereka mengutamakan di atas diri mereka sendiri, walaupun kemiskinan menyertai mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3023">3023</a><span>]</span><span> Dan barangsiapa dapat mengatasi keserakahan dirinya, maka mereka itu </span><span>[a]</span><span> yang akan berhasil.</span></p><p><span>[a]</span><span> 64:17.</span></p>');
INSERT INTO `quran` VALUES (11541,2,59,11,'<p><span>Dan orang-orang yang datang sesudah mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3024">3024</a><span>]</span><span> mereka berkata, “Hai, Tuhan kami, ampunilah kami dan saudara-saudara kami yang mendahului kami dalam keimanan, dan janganlah Engkau membiarkan kedengkian tinggal dalam hati kami untuk orang-orang yang beriman. Hai Tuhan kami ! Sesungguhnya Engkau Maha Pengantun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11542,2,59,12,'<p><span>R. 2</span></p><p><span>Apakah engkau tidak melihat orang-orang munafik yang berkata saudara-saudara mereka yang ingkar dari antara Ahlikitab, “Sekiranya kamu diusir </span><span>dari Medinah,</span><span> niscayalah kami akan ikut keluar bersama kamu, dan kami sekali-kali tidak akan tunduk kepada siapa pun untuk melawan kamu selama-lamanya, dan jika kamu diperangi, tentulah kami akan menolong kamu.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3025">3025</a><span>]</span><span> Dan Allah menyaksikan bahwa sesungguhnya mereka itu pendusta.</span></p>');
INSERT INTO `quran` VALUES (11543,2,59,13,'<p><span>Jika mereka diusir, mereka tidak akan ikut keluar bersama mereka ; dan jika mereka diperangi, mereka tidak akan menolong mereka, dan sekiranya mereka menolong mereka, niscaya mereka akan </span><span>[a]</span><span> membalikkan punggung </span><span>mereka,</span><span> kemudian mereka tidak akan ditolong.</span></p><p><span>[a]</span><span> 3:112.</span></p>');
INSERT INTO `quran` VALUES (11544,2,59,14,'<p><span>Sesungguhnya kamu lebih </span><span>[b]</span><span> ditakuti dalam hati mereka daripada Allah. Yang demikian itu di sebabkan mereka adalah kaum yang tidak mengerti.</span></p><p><span>[b]</span><span> 4:78.</span></p>');
INSERT INTO `quran` VALUES (11545,2,59,15,'<p><span>Mereka tidak akan memerangi kamu bersatu-padu kecuali dalam kota-kota berbenteng atau dari belakang tembok. Peperangan mereka di antara mereka sendiri sengit. Engkau mengira mereka bersatu-padu, padaha hati mereka terpecah-belah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3026">3026</a><span>]</span><span> Yang demikian itu disebabkan mereka adalah kaum yang tidak berakal.</span></p>');
INSERT INTO `quran` VALUES (11546,2,59,16,'<p><span>Keadaan mereka seperti orang-orang </span><span>yang sudah lewat</span><span> sebelum mereka dalam waktu dekat. Mereka merasakan akibat buruk perbuatan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3027">3027</a><span>]</span><span> dan bagi mereka ada azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (11547,2,59,17,'<p><span>[a]</span><span> Seperti keadaan syaitan ketika ia berkata kepada manusia, “Ingkarlah”, maka ketika ia ingkar, syaitan berkata, “Aku berlepas diri dari engkau ; sesungguhnya aku takut kepada Allah, Tuhan semesta Alam.”</span></p><p><span>[a]</span><span> 8:49 ; 14:23.</span></p>');
INSERT INTO `quran` VALUES (11548,2,59,18,'<p><span>Kesudahan kedua mereka itu ialah, sesungguhnya kedua mereka itu </span><span>masuk</span><span> ke dalam Api ; mereka akan menetap di dalamnya. Dan demikian pembalasan orang-orang aniaya.</span></p>');
INSERT INTO `quran` VALUES (11549,2,59,19,'<p><span>R. 3</span></p><p><span>Hai, orang-orang yang beriman, bertakwalah kepada Allah ; dan hendaklah setiap jiwa memperhatikan apa yang didahulukan untuk esok hari, dan bertakwalah kepada Allah. Sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11550,2,59,20,'<p><span>Dan, janganlah kamu menjadi seperti orang-orang yang telah </span><span>[a]</span><span> melupakan Allah ; maka Dia pun menjadikan mereka lupa terhadap diri mereka sendiri. Mereka itulah orang-orang yang fasik.</span></p><p><span>[a]</span><span> 9:67.</span></p>');
INSERT INTO `quran` VALUES (11551,2,59,21,'<p><span>Tidaklah sama penghuni neraka dengan penghuni surga. Ahli surgalah yang akan memperoleh kemenangan.</span></p>');
INSERT INTO `quran` VALUES (11552,2,59,22,'<p><span>[b]</span><span> Seandainya Kami menurunkan Alquran ini kepada gunung, niscaya engkau akan melihatnya tunduk dan menjadi berkeping-keping </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3027a">3027a</a><span>]</span><span> karena takut kepada Allah. Dan inilah tamsil-tamsil yang Kami kemukakan untuk manusia, supaya mereka berpikir.</span></p><p><span>[b]</span><span> 13:32.</span></p>');
INSERT INTO `quran` VALUES (11553,2,59,23,'<p><span>Dia-lah Allah, Yang tiada tuhan selain Dia, </span><span>[a]</span><span> Mengetahui yang ghaib dan yang nampak, Dia-lah Maha Pemurah, Maha Penyayang.</span></p><p><span>[a]</span><span> 6:74 ; 9:94 ; 13:10.</span></p>');
INSERT INTO `quran` VALUES (11554,2,59,24,'<p><span>Dia-lah Allah yang tiada tuhan selain Dia, Maha berdaulat, Yang Maha Suci, Sumber segala kedamaian, Pelimpahan keamanan, Maha Pelindung, Maha Perkasa, Maha Penakluk, Maha Agung, Maha Suci Allah, dari apa yang mereka persekutukan.</span></p>');
INSERT INTO `quran` VALUES (11555,2,59,25,'<p><span>Dia-lah Allah, Yang Maha Pencipta, Pembuat segala sesuatu, Pemberi bentuk, </span><span>[b]</span><span> kepunyaan Dia-lah segala nama yang terindah. </span><span>[c]</span><span> Bertasbihlah kepada-Nya segala yang ada di seluruh langit dan bumi dan Dia-lah Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b]</span><span> 7:181. </span></p><p><span>[c]</span><span> 17:45 ; 24:42 ; 61:2 ; 62:2 ; 64:2.</span></p>');
INSERT INTO `quran` VALUES (11556,2,60,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11557,2,60,2,'<p><span>Hai, orang-orang yang beriman ! janganlah kamu mengambil musuh-musuh-Ku dan musuh-musuh-mu </span><span>sebagai</span><span> </span><span>[b]</span><span> sahabat, kamu akan menyampaikan kepada mereka amanat kecintaan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3028">3028</a><span>]</span><span> padahal mereka telah mengingkari kebenaran yang telah datang kepadamu </span><span>dan</span><span> mereka </span><span>[c]</span><span> telah mengusir Rasul dan kamu sendiri karena kamu beriman kepada Allah, Tuhan-mu ? Jika kamu keluar berjihad di jalan-Ku, </span><span>sebagian</span><span> kamu dengan sembunyi-sembunyi menyampaikan kepada mereka amanat kecintaan, sedang Aku mengetahui benar apa yang kamu sembunyikan dan apa yang kamu tampakkan. Dan barangsiapa dari antara kamu berbuat demikian, maka ia sesungguhnya telah sesat dari jalan lurus.</span></p><p><span>[b]</span><span> 3:119 ; 4:145 ; 5:58.</span></p><p><span>[c]</span><span> 17:77.</span></p>');
INSERT INTO `quran` VALUES (11558,2,60,3,'<p><span>Jika mereka menangkap kamu, mereka akan menjadi bagi kamu musuh-musuh dan akan menjangkau tangan mereka dan lidah mereka terhadap kamu dengan </span><span>maksud</span><span> buruk ; dan mereka selalu ingin supaya kamu menjadi ingkar.</span></p>');
INSERT INTO `quran` VALUES (11559,2,60,4,'<p><span>[a]</span><span> Sekali-kali tidak memberi manfaat kepadamu kerabat-kerabatmu dan tidak pula anak-anakmu pada Hari Kiamat, </span><span>Dia</span><span> akan memutuskan di antara kamu. Dan Allah Maha Melihat apa yang kamu kerjakan.</span></p><p><span>[a]</span><span> 3:11 ; 31:34.</span></p>');
INSERT INTO `quran` VALUES (11560,2,60,5,'<p><span>[b]</span><span> Sesungguhnya bagimu ada contoh yang baik dalam </span><span>diri </span><span> Ibrahim </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3029">3029</a><span>]</span><span> dan orang-orang yang besertanya, ketika mereka berkata kepada kaum mereka, </span><span>[c]</span><span> “Sesungguhnya kami berlepas diri dari kamu dan dari apa yang kamu sembah selain Allah, kami mengingkari </span><span>ucapan</span><span> kamu. Dan telah nyata permusuhan dan kebencian di antara kami dan kamu untuk selama-lamanya hingga kamu beriman kepada Allah semata, kecuali yang dikatakan Ibrahim kepada bapaknya, </span><span>[d]</span><span> “Tentulah aku akan memohonkan ampunan bagi engkau, meskipun aku tidak berdaya menolong engkau sedikit pun terhadap Allah.” </span><span>Ibrahim </span><span> berkata, “Hai Tuhan kami ! Kepada Engkau kami bertawakkal, dan kepada Engkau kami tunduk dan kepada Engkau kami akan kembali.</span></p><p><span>[b]</span><span> 60:7.</span></p><p><span>[c]</span><span> 6:79 ; 43:27.</span></p><p><span>[d]</span><span> 19:48.</span></p>');
INSERT INTO `quran` VALUES (11561,2,60,6,'<p><span>“Hai, Tuhan kami, janganlah Engkau menjadikan kami suatu </span><span>[a]</span><span> fitnah bagi orang-orang yang ingkar, dan ampunilah kami, hai Tuhan kami ; sesungguhnya Engkau Maha Perkasa, Maha Bijaksana.”</span></p><p><span>[a]</span><span> 10:86.</span></p>');
INSERT INTO `quran` VALUES (11562,2,60,7,'<p><span>[b]</span><span> Sesungguhnya, bagi kamu dalam diri mereka ada contoh yang baik bagi orang yang mengharapkan bertemu </span><span>dengan Allah </span><span> dan Hari Kemudian. dan barangsiapa berpaling, maka sesungguhnya Allah, Dia Maha Kaya, Maha Terpuji.</span></p><p><span>[b]</span><span> 60:5.</span></p>');
INSERT INTO `quran` VALUES (11563,2,60,8,'<p><span>Mudah-mudahan Allah menjadikan kecintaan di antara kamu dan di antara orang-orang yang kamu bermusuhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3030">3030</a><span>]</span><span> dengan mereka. dan Allah Maha Kuasa. Dan Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11564,2,60,9,'<p><span>Allah tidak melarang kamu </span><span>berbuat baik</span><span> terhadap orang-orang yang tidak memerangi kamu karena agama, dan yang tidak mengusir kamu dari rumah-rumahmu untuk berbuat baik dan berlaku adil terhadap mereka. Sesungguhnya Allah mencintai orang-orang yang berlaku adil.</span></p>');
INSERT INTO `quran` VALUES (11565,2,60,10,'<p><span>Sesungguhnya Allah melarang kamu menjadikan mereka sahabat orang-orang yang memerangi kamu karena agama dan telah mengusir kamu dari rumah-rumahmu dan telah membantu untuk mengusir kamu l dan barangsiapa bersahabat dengan mereka itulah orang-orang aniaya.</span></p>');
INSERT INTO `quran` VALUES (11566,2,60,11,'<p><span>Hai, orang-orang yang beriman ! Apabila datang kepadamu perempuan-perempuan mukmin sebagai muhajir, maka ujilah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3031">3031</a><span>]</span><span> mereka. Allah Maha Mengetahui keimanan mereka, kemudian jika kamu ketahui mereka </span><span>benar-benar</span><span> beriman, maka janganlah kamu mengembalikan mereka kepada orang-orang kafir. </span><span>Perempuan-perempuan</span><span> itu tidaklah halal bagi mereka, demikian pula mereka tidak halal bagi perempuan- perempuan itu. Tetapi, berikanlah kepada </span><span>suami </span><span> mereka apa yang telah mereka belanjakan. Dan tidak ada dosa bagimu untuk menikahi mereka, apabila kamu telah memberikan kepada mereka </span><span>[a]</span><span> maskawin mereka. Dan janganlah kamu menahan tali pernikahan dengan </span><span>[b]</span><span> perempuan-perempuan kafir, dan mintalah apa yang telah kamu nafkahkan ; dan hendaklah mereka meminta apa yang telah mereka belanjakan. Demikianlah keputusan Allah. Dia-lah Yang menghakimi di antara kamu. Dan Allah, Maha Mengetahu, Maha Bijaksana.</span></p><p><span>[a]</span><span> 4:5, 25.</span></p><p><span>[b]</span><span> 2:222.</span></p>');
INSERT INTO `quran` VALUES (11567,2,60,12,'<p><span>Dan, jika seorang dari istri-istrimu lari dari kamu kepada orang-orang kafir, kemudian kamu membalas mereka, maka berikanlah kepada orang-orang mukmin yang istri-istrinya telah melarikan diri sebanyak yang telah dibelanjakan oleh mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3032">3032</a><span>]</span><span> Dan bertakwalah kepada Allah, Yang kepada-Nya kamu beriman.</span></p>');
INSERT INTO `quran` VALUES (11568,2,60,13,'<p><span>Hai, Nabi ! Jika datang kepadamu perempuan-perempuan mukmin dan hendak bai</span><span>’</span><span>at kepada engkau bahwa mereka tidak akan menyekutukan sesuatu pun dengan Allah, dan mereka tidak akan mencuri, dan tidak akan berzina, dan tidak akan membunuh anak-anak mereka, dan tidak akan melemparkan suatu tuduhan yang sengaja dibuat-buat antara tangan dan kaki mereka dan tidak akan mendurhakai engkau dalam hal-hal kebaikan, maka terimalah bai</span><span>’</span><span>at mereka dan mintalah ampunan bagi mereka dari Allah. Sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11569,2,60,14,'<p><span>Hai orang-orang yang beriman ! Janganlah kamu bersahabat dengan suatu kaum yang </span><span>[a]</span><span> Allah telah murka atas mereka, sesungguhnya mereka telah berputus-asa tentang alam ukhrawi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3033">3033</a><span>]</span><span> sebagaimana orang-orang kafir telah berputus-asa tentang orang-orang yang ada di dalam kubur.</span></p><p><span>[a]</span><span> 58:15.</span></p>');
INSERT INTO `quran` VALUES (11570,2,61,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11571,2,61,2,'<p><span>[b]</span><span> Bertasbih kepada Allah apa ayng ada di seluruh langit dan apa yang ada di bumi, dan Dia-lah Yang Maha Perkasa, Maha Bijaksana.</span></p><p><span>[b]</span><span> 17:45 ; 24:42 ; 57:2 ; 62:2 ; 64:2.</span></p>');
INSERT INTO `quran` VALUES (11572,2,61,3,'<p><span>Hai, orang-orang yang beriman ! Mengapakah kamu mengatakan apa-apa yang kamu tidak kerjakan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3034">3034</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11573,2,61,4,'<p><span>Adalah sesuatu yang paling dibenci di sisi Allah bahwa kamu mengatakan apa yang tidak kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11574,2,61,5,'<p><span>Sesungguhnya Allah mencintai orang-orang yang berperang di jalan-Nya dalam barisan, mereka itu seolah-olah suatu bangunan yang tersusun kokoh. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3035">3035</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11575,2,61,6,'<p><span>Dan, </span><span>ingatlah </span><span> ketika Musa berkata kepada kaumnya, “Hai kaumku, mengapa kamu menyakitiku, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3036">3036</a><span>] </span><span>padahal kamu mengetahui bahwa aku Rasul Allah yang diutus kepada kamu sekalian ?” Maka apabila mereka menyimpang </span><span>dari jalan benar,</span><span> Allah pun menyebabkan hati mereka menyimpang . Dan Allah tidak memberi petunjuk kepada kaum yang durhaka.</span></p>');
INSERT INTO `quran` VALUES (11576,2,61,7,'<p><span>Dan </span><span>ingatlah</span><span> ketika Isa ibnu Maryam berkata, “Hai, Bani Israil, sesungguhnya aku Rasul Allah kepadamu membenarkan apa yang ada sebelumku yaitu Taurat, dan memberi khabar suka tentang seorang rasul yang datang sesudahku namanya Ahmad.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3037">3037</a><span>]</span><span> Maka tatkala ia datang kepada mereka dengan bukti-bukti jelas, mereka berkata, “Ini adalah </span><span>[a]</span><span> sihir yang nyata.”</span></p><p><span>[a]</span><span> 27:14 ; 43:31.</span></p>');
INSERT INTO `quran` VALUES (11577,2,61,8,'<p><span>[a]</span><span> Dan, siapakah yang lebih aniaya daripada orang-orang yang mengada-adakan dusta terhadap Allah, padahal ia diajak kepada Islam ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3038">3038</a><span>]</span><span> Dan Allah tidak akan memberi petunjuk kepada kaum yang aniaya.</span></p><p><span>[a]</span><span> 6:22 ; 10:18 ; 11:19.</span></p>');
INSERT INTO `quran` VALUES (11578,2,61,9,'<p><span>[b]</span><span> Mereka berkehendak memadamkan Cahaya Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3039">3039</a><span>]</span><span> dengan mulut mereka, tetapi Allah akan menyempurnakan Cahaya-Nya, walaupun orang-orang kafir tidak menyukai.</span></p><p><span>[b]</span><span> 9:22.</span></p>');
INSERT INTO `quran` VALUES (11579,2,61,10,'<p><span>[a]</span><span> Dia-lah Yang mengirimkan Rasul-Nya dengan petunjuk dan dengan agama yang benar supaya Dia memenangkannya di atas semua agama, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3040">3040</a><span>]</span><span> walaupun orang musyrik tidak menyukai.</span></p><p><span>[a]</span><span> 9:33 ; 48:29.</span></p>');
INSERT INTO `quran` VALUES (11580,2,61,11,'<p><span>R. 2</span></p><p><span>Hai, orang-orang yang beriman ! Apakah Aku tunjukkan kepadamu suatu perdagangan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3041">3041</a><span>]</span><span> yang akan menyelamatkan kamu dari azab yang pedih ?</span></p>');
INSERT INTO `quran` VALUES (11581,2,61,12,'<p><span>[b]</span><span> Kamu beriman kepada Allah dan Rasul-Nya, dan kamu berjihad di jalan Allah dengan hartamu dan jiwamu. Yang demikian itu lebih baik bagimu, jika kamu mengetahui.</span></p><p><span>[b] </span><span>9:20, 41.</span></p>');
INSERT INTO `quran` VALUES (11582,2,61,13,'<p><span>Dia akan memaafkan kamu dosa-dosamu, dan Dia akan memasukkan kamu ke kebun-kebun yang di bawahnya mengalir sungai-sungai, dan ke tempat-tempat tinggal suci lagi </span><span>menyenang kan</span><span> di dalam </span><span>[c]</span><span> surga yang kekal. </span><span>Demikian</span><span> itulah kemenangan yang besar.</span></p><p><span>[c]</span><span> 9:72 ; 19:62 ; 20:77.</span></p>');
INSERT INTO `quran` VALUES (11583,2,61,14,'<p><span>Dan, ada lagi </span><span>karunia</span><span> lain yang kamu mencintainya. Pertolongan dari Allah dan kemenangan yang dekat. Maka berilah khabar suka kepada orang-orang yang beriman.</span></p>');
INSERT INTO `quran` VALUES (11584,2,61,15,'<p><span>Hai orang-orang yang beriman ! Jadilah kamu penolong Allah sebagaimana Isa ibnu Maryam berkata kepada </span><span>[a]</span><span> pengikut-pengikutnya, “Siapakah penolong-penolongku di </span><span>jalan</span><span> Allah ? “ Pengikut-pengikut yang setia itu berkata, “Kamilah penolong-penolong Allah.” Maka berimanlah segolongan dari Bani Israil, dan ingkarlah segolongan </span><span>lagi,</span><span> kemudian Kami membantu orang-orang yang beriman terhadap musuh-musuh mereka, maka mereka menjadi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3042">3042</a><span>]</span><span> orang-orang yang menang.</span></p><p><span>[a]</span><span> 3:53 ; 5:112.</span></p>');
INSERT INTO `quran` VALUES (11585,2,62,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11586,2,62,2,'<p><span>[b]</span><span> Menyanjung kepada Allah segala yang ada di seluruh langit dan segala yang ada di bumi, Yang Maha Berdaulat, Maha Suci, Maha Perkasa, Maha Bijaksana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3043">3043</a><span>]</span></p><p><span>[b]</span><span> 61:2.</span></p>');
INSERT INTO `quran` VALUES (11587,2,62,3,'<p><span>Dia-lah Yang telah membangkitkan di tengah-tengah </span><span>bangsa</span><span> yang buta huruf </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3044">3044</a><span>] </span><span>seorang </span><span>[c]</span><span> rasul dari antara mereka, yang membacakan kepada mereka Tanda- tanda-Nya, dan mensucikan mereka, dan mengajarkan kepada mereka Kitab dan Hikmah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3045">3045</a><span>]</span><span> walaupun sebelumnya mereka berada dalam kesesatan yang nyata.</span></p><p><span>[c]</span><span> 3:165 ; 7:158 ; 9:128.</span></p>');
INSERT INTO `quran` VALUES (11588,2,62,4,'<p><span>Dan, </span><span>Dia akan membangkitkannya </span><span> pada kaum lain dari antara mereka, yang belum bertemu dengan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3046">3046</a><span>]</span><span> Dan, Dia-lah Yang Maha Perkasa, Maha bijaksana.</span></p>');
INSERT INTO `quran` VALUES (11589,2,62,5,'<p><span>Itulah karunia Allah ; Dia menganugerahkannya kepada siapa yang Dia kehendaki. Dan Allah mempunyai karunia yang besar.</span></p>');
INSERT INTO `quran` VALUES (11590,2,62,6,'<p><span>Misal orang-orang yang dipikulnya kepada mereka Taurat, kemudian mereka tidak memikulnya , adalah semisal keledai yang memikul kitab-kitab. Sangatlah buruk misal kaum yang mendustakan Tanda-tanda Allah, Dan Allah tidak akan memberi petunjuk kaum yang aniaya.</span></p>');
INSERT INTO `quran` VALUES (11591,2,62,7,'<p><span>Katakanlah, “Hai orang-orang Yahudi, sekiranya kamu mengaku bahwa sesungguhnya kamu sahabat Allah selain manusia, maka inginkanlah kematian, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3047">3047</a><span>] [a]</span><span> jika kamu orang-orang benar.</span></p><p><span>[a] </span><span>2:95.</span></p>');
INSERT INTO `quran` VALUES (11592,2,62,8,'<p><span>[b]</span><span> Dan mereka tidak menginginkan selama-lamanya disebabkan oleh apa yang telah diperbuat tangan mereka. Dan Allah Maha Mengetahui orang-orang yang aniaya.</span></p><p><span>[b]</span><span> 2:96.</span></p>');
INSERT INTO `quran` VALUES (11593,2,62,9,'<p><span>Katakanlah, </span><span>[c]</span><span> “Sesungguhnya kematian, yang kamu lari darinya, maka sesungguhnya itu akan menemui kamu, kemudian kamu akan dikembalikan kepada Yang Maha Mengetahui yang gaib dan yang nampak, maka Dia akan memberitahukan kepadamu tentang apa yang telah kamu perbuat.</span></p><p><span>[c]</span><span> 2:97 ; 4:79 ; 33:17.</span></p>');
INSERT INTO `quran` VALUES (11594,2,62,10,'<p><span>R. 2</span></p><p><span>Hai, orang-orang yang beriman ! Apabila dipanggil untuk shalat pada hari Jum</span><span>’</span><span>at. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3047a">3047a</a><span>]</span><span> maka bersegeralah untuk mengingat Allah dan tinggalkanlah jual-beli. Hal demikian adalah lebih baik bagimu, jika kamu mengetahui.</span></p>');
INSERT INTO `quran` VALUES (11595,2,62,11,'<p><span>Dan, apabila telah diselesaikan shalat, maka bertebaranlah kamu di bumi dan carilah karunia Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3048">3048</a><span>]</span><span> dan ingatlah kepada Allah banyak-banyak, supaya kamu mendapat kebahagiaan.</span></p>');
INSERT INTO `quran` VALUES (11596,2,62,12,'<p><span>Dan, apabila mereka melihat sesuatu perniagaan atau hiburan, berhamburanlah mereka menuju kepadanya, dan meninggalkan engkau berdiri sendirian. Katakanlah, “Apa yang ada di sisi Allah itu lebih baik daripada hiburan dan perniagaan. Dan Allah adalah sebaik-baik Pemberi rezeki.”</span></p>');
INSERT INTO `quran` VALUES (11597,2,63,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11598,2,63,2,'<p><span>Apabila orang-orang munafik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3049">3049</a><span>]</span><span> datang kepada engkau, mereka berkata, “Kami menyaksikan sesungguhnya engkau Rasul Allah.” Dan Allah telah mengetahui sesungguhnya engkau benar-benar Rasul-Nya. Dan Allah menyaksikan sesungguhnya orang-orang munafik itu benar-benar pendusta.</span></p>');
INSERT INTO `quran` VALUES (11599,2,63,3,'<p><span>Mereka menjadikan sumpah-sumpah mereka perisai ; maka </span><span>[b]</span><span> mereka menghalangi </span><span>orang-orang </span><span> dari jalan Allah. Sesungguhnya sangat buruk apa yang telah mereka kerjakan.</span></p><p><span>[b]</span><span> 9:9.</span></p>');
INSERT INTO `quran` VALUES (11600,2,63,4,'<p><span>Yang demikian itu disebabkan mereka beriman kemudian mereka </span><span>[c]</span><span> ingkar, maka meterai dikenakan pada kalbu mereka, karena itu mereka tidak dapat mengerti. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3050">3050</a><span>]</span></p><p><span>[c]</span><span> 3:91 ; 4:138 ; 16:107.</span></p>');
INSERT INTO `quran` VALUES (11601,2,63,5,'<p><span>Dan apabila engkau melihat mereka, tubuh mereka membuat engkau kagum. </span><span>[a]</span><span> Dan jika mereka berkata, engkau mendengarkan ucapan mereka. Mereka seolah-olah kayu tersandar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3051">3051</a><span>]</span><span> mereka menyangka, bahwa tiap-tiap teriakan adalah terhadap mereka. Mereka adalah musuh, maka waspasdalah </span><span>terhadap</span><span> mereka. Semoga Allah membinasakan mereka, bagaimana mereka dipalingkan </span><span>dari kebenaran !</span><span> </span></p><p><span>[a]</span><span> 2:205.</span></p>');
INSERT INTO `quran` VALUES (11602,2,63,6,'<p><span>Dan apabila dikatakan kepada mereka, “Marilah, </span><span>[b]</span><span> supaya Rasulullah saw. memohonkan ampunan bagimu,” mereka memalingkan kepada mereka dan engkau melihat mereka menghalangi-halangi </span><span>kebenaran</span><span> dan mereka menyombongkan diri.</span></p><p><span>[b]</span><span> 4:62.</span></p>');
INSERT INTO `quran` VALUES (11603,2,63,7,'<p><span>Sama saja bagi mereka, apakah engkau memohonkan ampunan bagi mereka atau engkau tidak memohonkan ampunan bagi mereka. </span><span>[c]</span><span> Allah sekali-kali tidak akan mengampuni mereka. Sesungguhnya Allah tidak akan memberi petunjuk kepada kaum yang durhaka.</span></p><p><span>[c]</span><span> 9:80.</span></p>');
INSERT INTO `quran` VALUES (11604,2,63,8,'<p><span>Merekalah orang-orang yang berkata, “Janganlah kamu membelanjakan </span><span>harta</span><span> bagi orang yang bersama Rasul Allah, supaya mereka lari </span><span>karena kelaparan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3052">3052</a><span>]</span><span> Padahal kepunyaan Allah khazanah-khazanah seluruh langit dan bumi ; akan tetapi, orang-orang munafik itu tidak mengerti.</span></p>');
INSERT INTO `quran` VALUES (11605,2,63,9,'<p><span>Mereka berkata, “Jika kita kembali ke Medinah, tentulah orang yang paling mulia akan mengeluarkan orang yang paling hina </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3053">3053</a><span>]</span><span> darinya.” Padahal kemuliaan </span><span>hakiki</span><span> itu kepunyaan Allah dan Rasul-Nya dan orang-orang Mukmin ; akan tetapi orang-orang munafik itu tidak mengetahui.</span></p>');
INSERT INTO `quran` VALUES (11606,2,63,10,'<p><span>R. 2</span></p><p><span>[a]</span><span> Hai, orang-orang yang beriman ! Janganlah melalaikan kamu hartamu dan anak-anakmu dari berzikir kepada Allah, dan barangsiapa yang berbuat demikian, maka mereka itulah orang-orang yang rugi.</span></p><p><span>[a]</span><span> 8:29 ; 24:38 ; 64:16 ; 102:2.</span></p>');
INSERT INTO `quran` VALUES (11607,2,63,11,'<p><span>[b]</span><span> Dan, nafkahkanlah dari apa yang telah Kami rezekikan kepadamu sebelum kamatian menimpa seseorang dari antara kamu, lalu ia berkata, </span><span>[c]</span><span> “ Hai Tuhan-ku ! Andaikata Engkau memberi tenggang waktu kepadaku barang sejenak, niscaya aku akan bersedekah dan menjadi termasuk orang-orang yang shaleh.”</span></p><p><span>[b]</span><span> 2:196 ; 9:34.</span></p><p><span>[c]</span><span> 14:45.</span></p>');
INSERT INTO `quran` VALUES (11608,2,63,12,'<p><span>[d]</span><span> Dan Allah sekali-kali tidak akan memberi tangguh kepada suatu jiwa pun, apabila telah tiba ajalnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3054">3054</a><span>]</span><span> Dan Allah Maha Mengetahui apa yang kamu kerjakan.</span></p><p><span>[d]</span><span> 71:5.</span></p>');
INSERT INTO `quran` VALUES (11609,2,64,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11610,2,64,2,'<p><span>Bertasbih </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3055">3055</a><span>]</span><span> kepada Allah apa </span><span>[b]</span><span> yang ada di seluruh langit dan apa yang ada di bumi ; kepunyaan Dia kerajaan dan kepunyaan Dia segala puji, dan Dia Maha Kuasa atas segala sesuatu.</span></p><p><span>[b]</span><span> 17:45 ; 24:42 ; 59:25 ; 61:2.</span></p>');
INSERT INTO `quran` VALUES (11611,2,64,3,'<p><span>Dia-lah Yang menciptakan kamu, maka di antaramu ada yang kafir dan di antaramu ada yang mukmin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3056">3056</a><span>]</span><span> Dan Allah Maha Melihat apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (11612,2,64,4,'<p><span>Dia menciptakan seluruh langit dan bumi dengan hak, dan </span><span>[c]</span><span> Dia memberi kamu bentuk, maka Dia menjadikan indah rupamu, dan kepada-Nya tempat kembali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3057">3057</a><span>]</span></p><p><span>[c]</span><span> 3:7 ; 7:12.</span></p>');
INSERT INTO `quran` VALUES (11613,2,64,5,'<p><span>Dia mengetahui apa pun yang ada di seluruh langit dan bumi, dan Dia mengetahui </span><span>[a]</span><span> apa yang kamu sembunyikan dan apa yang kamu tampakkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3058">3058</a><span>]</span><span> Dan Allah Maha Mengetahui yang ada di dalam dada.</span></p><p><span>[a]</span><span> 2:78 ; 16:20 ; 27:26.</span></p>');
INSERT INTO `quran` VALUES (11614,2,64,6,'<p><span>[b]</span><span> Apakah tidak datang kepadamu khabar orang-orang yang ingkar sebelumnya ? Mereka merasakan akibat buruk perbuatan mereka, dan bagi mereka ada azab pedih.</span></p><p><span>[b]</span><span> 40:22 </span><span>–</span><span> 23.</span></p>');
INSERT INTO `quran` VALUES (11615,2,64,7,'<p><span>Yang demikian itu disebabkan rasul-rasul mereka datang kepada mereka dengan Tanda-tanda nyata, tetapi mereka berkata, “Apakah manusia memberi petunjuk kepada kami ?” Maka mereka ingkar dan berpaling, dan Allah tidak memerlukan mereka. Dan Allah itu Maha Kaya, Maha Terpuji.</span></p>');
INSERT INTO `quran` VALUES (11616,2,64,8,'<p><span>Orang-orang yang ingkar menyangka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3059">3059</a><span>]</span><span> bahwa </span><span>[c]</span><span> mereka sekali-kali tidak akan dibangkitkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3059a">3059a</a><span>]</span><span> Katakanlah. “</span><span>Tidak demikian, </span><span>bahkan demi Tuhan-ku, kamu pasti akan dibangkitkan, kemudian kamu pasti akan diberitahu tentang apa yang telah kamu kerjakan. Dan, yang demikian itu mudah bagi Allah.”</span></p><p><span>[c]</span><span> 36:70 </span><span>–</span><span> 80 ; 46:18 ; 50:4.</span></p>');
INSERT INTO `quran` VALUES (11617,2,64,9,'<p><span>Maka berimanlah kepada Allah dan Rasul-Nya, dan kepada </span><span>[a]</span><span> Cahaya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3060">3060</a><span>]</span><span> yang telah Kami turunkan. Dan Allah Maha Mengetahui apa yang kamu kerjakan.</span></p><p><span>[a]</span><span> 4:175 ; 7:158.</span></p>');
INSERT INTO `quran` VALUES (11618,2,64,10,'<p><span>Pada hari Dia akan mengumpulkan kamu pada Hari Berhimpun ; itulah Hari kerugian dan keungungan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3061">3061</a><span>]</span><span> Dan barangsiapa beriman kepada Allah dan beramal shaleh. </span><span>[b]</span><span> Dia akan menghapuskan darinya keburukan-keburukannya dan Dia akan memasukkannya ke dalam kebun-kebun yang di bawahnya mengalir sungai-sungai, mereka akan tinggal di dalamnya selama-lamanya. Itulah kemenangan yang besar.</span></p><p><span>[b]</span><span> 8:30 ; 48:6 ; 66:9.</span></p>');
INSERT INTO `quran` VALUES (11619,2,64,11,'<p><span>[a]</span><span> Dan, orang-orang yang ingkar dan mendustakan Tanda-tanda Kami, mereka itu adalah penghuni Api, mereka akan menetap di dalamnya. Dan itu seburuk-buruk tempat kembali.</span></p><p><span>[a]</span><span> 2:40 ; 7:37 ; 22:58.</span></p>');
INSERT INTO `quran` VALUES (11620,2,64,12,'<p><span>R. 2</span></p><p><span>[b]</span><span> Tidaklah menimpa sesuatu musibah kecuali dengan izin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3062">3062</a><span>]</span><span> Allah. Dan barangsiapa beriman kepada Allah, Dia memberi petunjuk kepada hatinya. Dan Allah Maha Mengetahui segala sesuatu.</span></p><p><span>[b]</span><span> 30:17 ; 78:29 ; 4:79.</span></p>');
INSERT INTO `quran` VALUES (11621,2,64,13,'<p><span>[c]</span><span> Dan, taatlah kepada Allah dan taatlah kepada Rasul. tetapi jika kamu berpaling maka sesungguhnya bagi Rasul Kami hanyalah menyampaikan </span><span>amanat </span><span> dengan jelas.</span></p><p><span>[c]</span><span> 5:93 ; 24:55.</span></p>');
INSERT INTO `quran` VALUES (11622,2,64,14,'<p><span>Allah tidak ada tuhan selain Dia. Dan kepada Allah hendaknya bertawakkal orang-orang mukmin.</span></p>');
INSERT INTO `quran` VALUES (11623,2,64,15,'<p><span>Hai, orang-orang yang beriman ! Sesungguhnya, di antara istri-istrimu dan anak-anakmu adalah musuh bagi kamu, maka waspadalah terhadap mereka, dan jika kamu memaafkan dan tidak memarahi dan mengampuni, maka sesungguhnya Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11624,2,64,16,'<p><span>Sesungguhnya, hartamu dan </span><span>[a]</span><span> anak-anakmu adalah fitnah. Dan Allah di sisi-Nya ganjaran yang besar.</span></p><p><span>[a]</span><span> 8:29 ; 63:10.</span></p>');
INSERT INTO `quran` VALUES (11625,2,64,17,'<p><span>Maka bertakwalah kepada Allah sejauh kesanggupanmu, dan dengarlah serta taatlah, dan belanjakanlah </span><span>hartamu,</span><span> hal itu baik bagi dirimu. </span><span>[b]</span><span> Dan barangsiapa diperlihara dari kebakhilan dirinya, maka mereka itulah orang-orang yang berhasil.</span></p><p><span>[b]</span><span> 59:10.</span></p>');
INSERT INTO `quran` VALUES (11626,2,64,18,'<p><span>[c]</span><span> Jika kamu meminjamkan kepada Allah suatu pinjaman yang baik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3063">3063</a><span>]</span><span> niscaya Dia akan melipat-gandakan bagimu dan akan mengampuni kamu. Dan Allah Maha Menghargai, Maha Penyantun.</span></p><p><span>[c]</span><span> 2:246 ; 57:12 ; 73:21.</span></p>');
INSERT INTO `quran` VALUES (11627,2,64,19,'<p><span>[d]</span><span> Dia Maha Mengetahui yang gaib dan yang nampak. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3063a">3063a</a><span>]</span><span> Maha Perkasa, Maha Bijaksana.</span></p><p><span>[d]</span><span> 6:74 ; 9:94 ; 13:10 ; 59:23.</span></p>');
INSERT INTO `quran` VALUES (11628,2,65,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11629,2,65,2,'<p><span>Hai Nabi, </span><span>[b]</span><span> apabila kamu menceraikan perempuan-perempuan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3064">3064</a><span>]</span><span> maka hendaklah kamu menceraikan mereka menurut </span><span>‘</span><span>iddah mereka (waktu yang telah ditentukan), dan hitunglah </span><span>‘</span><span>iddah itu, dan bertakwalah kepada Allah, Tuhan-mu ; janganlah kamu mengusir mereka dari rumah mereka, [</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3064a">3064a</a><span>]</span><span> dan janganlah mereka keluar, kecuali kalai mereka berbuat kekejian yang nyata. Dan </span><span>[c]</span><span> itulah batas-batas Allah. Dan barangsiapa melampaui batas-batas Allah, maka sesungguhnya ia menganiaya dirinya sendiri. Engkau tidak mengetahui, semoga Allah menzahirkan sesudah itu suatu hal </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3065">3065</a><span>]</span><span> yang lain.</span></p><p><span>[b]</span><span> 2:232 </span><span>–</span><span> 233.</span></p><p><span>[c]</span><span> 2:230.</span></p>');
INSERT INTO `quran` VALUES (11630,2,65,3,'<p><span>Kemudian </span><span>[a]</span><span> apabila mereka sampai akhir </span><span>‘</span><span>iddah mereka, maka tahanlah mereka dengan baik atau pisahkan mereka dengan cara yang wajar, dan jadikanlah saksi dua orang yang adil dari antara kamu ; dan berikanlah kesaksian karena Allah. Demikianlah diberi nasihat dengan itu orang yang beriman kepada Allah dan Hari Akhirat. Dan barangsiapa bertakwa kepada Allah, Dia akan membuat baginya suatu jalan keluar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3066">3066</a><span>]</span></p><p><span>[a]</span><span> 2:232.</span></p>');
INSERT INTO `quran` VALUES (11631,2,65,4,'<p><span>Dan, Dia akan memberikan rezeki kepadanya dari mana tidak pernah ia menyangka. Dan, barangsiapa bertawakkal kepada Allah, niscaya Dia memadai baginya. Sesungguhnya Allah menyempurnakan urusan-Nya. Sesungguhnya Allah telah menetapkan ketentuan bagi segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (11632,2,65,5,'<p><span>Dan perempuan-perempuan yang putus asa tentang haid dari perempuan-perempuan kamu, jika kamu ragu </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3067">3067</a><span>]</span><span> </span><span>tentang </span><span>‘</span><span>iddah,</span><span> maka </span><span>‘</span><span>iddah mereka adalah </span><span>[b]</span><span> tiga bulan, dan </span><span>demikian pula bagi </span><span>perempuan-perempuan hamil, </span><span>‘</span><span>iddah mereka adalah sampai melahirkan kandungan mereka, Dan barangsiapa bertakwa kepada Allah, Dia menjadikan baginya kemudahan dalam urusannya.</span></p><p><span>[b]</span><span> 2:229.</span></p>');
INSERT INTO `quran` VALUES (11633,2,65,6,'<p><span>Demikianlah perintah Allah, yang diturunkan-Nya kepada kamu. Dan barangsiapa bertakwa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3068">3068</a><span>]</span><span> kepada Allah, Dia menghapuskan darinya keburukan-keburukannya dan memperbasar ganjaran baginya.</span></p>');
INSERT INTO `quran` VALUES (11634,2,65,7,'<p><span>Tempatkanlah mereka di mana kamu tinggal menurut kemampuanmu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3068a">3068a</a><span>]</span><span> dan janganlah kamu menyusahkan mereka sehingga menimbulkan kesempitan pada mereka. Dan, jika mereka hamil, maka berikanlah nafkah kepada mereka hingga mereka melahirkan kandungan mereka, </span><span>[a]</span><span> dan jika mereka menyusui </span><span>anak</span><span> bagi kamu, berikanlah kepada mereka upah mereka, dan bermusyawarahlah di antaramu dengan cara yang baik ; tetapi, jika di antara kamu menemui kesulitan satu sama lain, maka biarlah seorang wanita lain menyusui baginya.</span></p><p><span>[a]</span><span> 2:234.</span></p>');
INSERT INTO `quran` VALUES (11635,2,65,8,'<p><span>[a]</span><span> Hendaklah orang yang mempunyai kelapangan </span><span>rezeki</span><span> menafkahkan sebagaian dari kelimpahannya. Dan hendaklah orang yang disempitkan atasnya rezekinya menafkahkan sebagian dari apa yang Allah berikan kepadanya. Allah tidak membebani suatu jiwa melainkan apa yang telah Dia berikan kepadanya. Allah akan segera menjadikan sesudah kesempitan kelapangan.</span></p><p><span>[a]</span><span> 2:234.</span></p>');
INSERT INTO `quran` VALUES (11636,2,65,9,'<p><span>R. 2</span></p><p><span>[b]</span><span> Berapa banyak kota yang telah mendurhakai perintah Tuhan-nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3069">3069</a><span>]</span><span> dan rasul-rasul-Nya, lalu Kami membuat perhitungan dengan mereka perhitungan keras, dan Kami azab mereka dengan azab yang mengerikan.</span></p><p><span>[b]</span><span> 7:5, 6 ; 17:18 ; 21:12 ; 22:46.</span></p>');
INSERT INTO `quran` VALUES (11637,2,65,10,'<p><span>Maka mereka merasakan akibat buruk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3070">3070</a><span>]</span><span> amal perbuatannya dan kesudahan perbuatannya itu adalah kerugian.</span></p>');
INSERT INTO `quran` VALUES (11638,2,65,11,'<p><span>Allah telah menyediakan bagi mereka azab yang sangat keras ; maka bertakwalah kepada Allah, hai orang-orang yang berakal dan yang beriman. </span><span>[a]</span><span> Sesungguhnya Allah telah menurunkan kepadamu seorang Pemberi ingat.</span></p><p><span>[a]</span><span> 15:10 ; 36:70.</span></p>');
INSERT INTO `quran` VALUES (11639,2,65,12,'<p><span>Seorang rasul yang membacakan kepadamu Tanda-tanda Allah yang menerangkan </span><span>[b]</span><span> supaya ia mengeluarkan orang-orang yang beriman dan berbuat amal shaleh dari kegelapan kepada cahaya. Dan barangsiapa beriman kepada Allah dan berbuat amal shaleh, Dia akan memasukkannya ke dalam kebun-kebun yang mengalir di bawahnya sungai-sungai, tinggal di dalamnya selama-lamanya. Sesungguhnya Allah telah memberikan rezeki yang baik kepadanya.</span></p><p><span>[b]</span><span> 2:258 ; 5:17.</span></p>');
INSERT INTO `quran` VALUES (11640,2,65,13,'<p><span>Allah Adalah Dia Yang menciptakan </span><span>[c]</span><span> tujuh langit dan tujuh bumi sepertinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3070a">3070a</a><span>]</span><span> Turun perintah di antaranya, supaya kamu mengetahui, bahwa Allah Maha Kuasa atas segala sesuatu ; dan sesungguhnya Allah benar-benar ilmu-</span><span>Nya</span><span> meliputi segala sesuatu.</span></p><p><span>[c]</span><span> 67:4 ; 71:16.</span></p>');
INSERT INTO `quran` VALUES (11641,2,66,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11642,2,66,2,'<p><span>Hai, Nabi, mengapa engkau mengharamkan apa yang telah di halalkan Allah bagimu </span><span>hanya karena </span><span> engkau mencari kesenangan istri-istrimu ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3071">3071</a><span>]</span><span> Dan Allah Maha Pengampun, Maha Penyayang.</span></p>');
INSERT INTO `quran` VALUES (11643,2,66,3,'<p><span>Sungguh Allah mewajibkan kepada kamu membebaskan </span><span>diri</span><span> dari sumpah-sumpahmu </span><span>yang menimbulkan fitnah,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3072">3072</a><span>]</span><span> dan Allah adalah Pelindungmu, dan Dia Maha Mengetahui, Maha Bijaksana.</span></p>');
INSERT INTO `quran` VALUES (11644,2,66,4,'<p><span>Dan ketika Nabi menceritakan suatu hal secara rahasia kepada salah seorang istri-istrinya; ketika istrinya itu membocorkannya, dan Allah memberitahukan kepada dia (Nabi) tentang itu, dia memberitahukan sebagian darinya </span><span>kepada istrinya</span><span> dan sebagian lagi dia menyembunyikannya, </span><span>kemudian,</span><span> tatkala dia memberitahukan hal itu kepada istrinya, </span><span>istrinya </span><span> berkata, “Siapakah memberitahukan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3073">3073</a><span>]</span><span> kepada engkau perihal itu ?” </span><span>Nabi</span><span> berkata, “Telah memberitahukan kepadaku Yang Maha Mengetahui, Maha Mengenal.”</span></p>');
INSERT INTO `quran` VALUES (11645,2,66,5,'<p><span>Jika kamu berdua </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3074">3074</a><span>]</span><span> bertobat kepada Allah, maka sesungguhnya hati kamu berdua telah cenderung </span><span>kepada-Nya,</span><span> tetapi jika kamu berdua saling mendukung terhadap dia, maka sesungguhnya Allah adalah Pelindungnya, begitu pula Jibril dan orang shaleh dari orang-orang yang beriman, dan malikat sesudah itu adalah pendukungnya.</span></p>');
INSERT INTO `quran` VALUES (11646,2,66,6,'<p><span>Boleh jadi Tuhan-nya, jika Nabi menceraikan kamu, maka Dia akan menggantikan baginya istri-istri yang lebih baik daripada kamu, muslimah, beriman, taat selamanya menghadap </span><span>ke hadirat Tuhan,</span><span> tekun beribadah, rajin berpuasa, janda dan gadis.”</span></p>');
INSERT INTO `quran` VALUES (11647,2,66,7,'<p><span>Hai, orang-orang yang beriman ! Peliharalah dirimu dan keluargamu dari </span><span>[a]</span><span> Api, yang bahan bakarnya manusia dan batu, penjaganya malaikat-malaikat yang kasar, keras, tidak mendurhakai Allah apa yang dia perintahkan kepada mereka dan mereka mengerjakan apa yang diperintahkan.</span></p><p><span>[a]</span><span> 2:25.</span></p>');
INSERT INTO `quran` VALUES (11648,2,66,8,'<p><span>[b]</span><span> Hai, orang-orang yang ingkar ! Janganlah kamu mengemukakan dalih pada hari ini, Sesungguhnya kamu dibalas menurut apa yang kamu kerjakan.</span></p><p><span>[b]</span><span> 9:66.</span></p>');
INSERT INTO `quran` VALUES (11649,2,66,9,'<p><span>R. 2</span></p><p><span>Hai, orang-orang yang beriman ! Bertobatlah kepada Allah dengan seikhlas-ikhlas tobat. Semoga Tuhan-mu akan menghapuskan dari kamu keburukan-keburukanmu dan akan memasukkan kamu di dalam </span><span>[c]</span><span> kebun-kebun yang mengalir di bawahnya sungai-sungai, pada hari ketika Allah tidak akan menghinakan Nabi maupun orang-orang yang beriman besertanya, cahaya mereka akan berlari-lari di hadapan mereka dan di sebelah kanan mereka, mereka akan berkata, “Hai, Tuhan kami, sempurnakanlah bagi kami cahaya kami ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3075">3075</a><span>]</span><span> dan maafkanlah kami ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3076">3076</a><span>]</span><span> sesungguhnya Engkau Maha Kuasa atas segala sesuatu.”</span></p><p><span>[c]</span><span> 48:6 ; 64:10.</span></p>');
INSERT INTO `quran` VALUES (11650,2,66,10,'<p><span>Hai Nabi ! Berjihadlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3077">3077</a><span>]</span><span> terhadap orang-orang kafir dan orang-orang munafik, dan bersikeraslah terhadap mereka. Tempat tinggal mereka adalah Jahannam, dan seburuk-buruk tempat kembali.</span></p>');
INSERT INTO `quran` VALUES (11651,2,66,11,'<p><span>Allah membuat misal bagi orang-orang kafir </span><span>seperti</span><span> istri Nuh dan istri Luth. Keduanya di bawah dua hamba dari hamba-hamba Kami yang shaleh, tetapi keduanya berbuat khianat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3078">3078</a><span>]</span><span> kepada kedua </span><span>suami</span><span> mereka, maka mereka berdua itu sedikit pun tidak dapat membela kedua istri mereka di hadapan Allah, dan dikatakan </span><span>kepada mereka,</span><span> “Masuklah kamu berdua ke dalam Api beserta orang-orang yang masuk.”</span></p>');
INSERT INTO `quran` VALUES (11652,2,66,12,'<p><span>Dan. Allah membuat misal bagi orang-orang mukmin, </span><span>seperti</span><span> istri Firaun ketika ia berkata, “Hai Tuhan, buatkanlah bagiku di sisi Engkau sebuah rumah di surga ; dan selamatkanlah aku dari Firaun dan perbuatannya, dan selamatkanlah aku dari kaum yang aniaya.</span></p>');
INSERT INTO `quran` VALUES (11653,2,66,13,'<p><span>Dan, </span><span>seperti </span><span> Maryam putri </span><span>‘</span><span>Imran, </span><span>[a]</span><span> yang telah memelihara kesuciannya, maka Kami meniupkan ke dalamnya Ruh Kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3078a">3078a</a><span>]</span><span> dan ia membenarkan firman Tuhan-nya dan Kitab-kitab-Nya, dan ia termasuk orang-orang yang patuh.</span></p><p><span>[a]</span><span> 21:92.</span></p>');
INSERT INTO `quran` VALUES (11654,2,67,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11655,2,67,2,'<p><span>[b]</span><span> Maha berberkatlah Dia, Yang di tangan-Nya kerajaan dan Dia Maha Kuasa atas segala sesuatu ;</span></p><p><span>[b]</span><span> 25:2 </span><span>–</span><span> 3.</span></p>');
INSERT INTO `quran` VALUES (11656,2,67,3,'<p><span>Yang menciptakan kematian </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3079">3079</a><span>]</span><span> dan kehidupan, </span><span>[c]</span><span> supaya Dia menguji kamu, siapakah di antara kamu yang terbaik amalnya. Dan Dia Maha Perkasa, Maha Pengampun ;</span></p><p><span>[c]</span><span> 5:49 ; 11:8 ; 18:8.</span></p>');
INSERT INTO `quran` VALUES (11657,2,67,4,'<p><span>[d]</span><span> Yang telah menciptakan tujuh langit dengan serasi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3079a">3079a</a><span>]</span><span> Engkau tidak akan melihat di dalam ciptaan </span><span>Tuhan</span><span> Yang Maha Pemurah sesuatu keganjilan. Maka lihatlah berulang-ulang, apakah engkau melihat sesuatu kekurangan ?</span></p><p><span>[d]</span><span> 65:13 ; 67:4 ; 71:16.</span></p>');
INSERT INTO `quran` VALUES (11658,2,67,5,'<p><span>Kemudian, pandanglah untuk kedua kali, niscaya penglihatan engkau akan kembali kepada engkau dengan gagal dan dia letih, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3080">3080</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11659,2,67,6,'<p><span>Dan, sesungguhnya </span><span>[a]</span><span> Kami telah menghiasi langit yang terdekat dengan pelita-pelita, dan </span><span>[b]</span><span> Kami telah menjadikannya untuk mengusir syaitan-syaitan, dan Kami telah menyediakan bagi mereka azab Api yang berkobar-kobar.</span></p><p><span>[a]</span><span> 15:17 ; 37:7 ; 41:13 ; 50:7.</span></p><p><span>[b]</span><span> 15:18 ; 37:11.</span></p>');
INSERT INTO `quran` VALUES (11660,2,67,7,'<p><span>Dan, bagi orang-orang yang ingkar kepada Tuhan mereka ada azab jahannam. Dan seburuk-buruk tempat kembali.</span></p>');
INSERT INTO `quran` VALUES (11661,2,67,8,'<p><span>Apabila mereka dilemparkan ke dalamnya, </span><span>[c]</span><span> mereka akan mendengarnya gemuruh, sedangkan </span><span>neraka</span><span> itu menggelegar.</span></p><p><span>[c]</span><span> 11:107 ; 21:101 ; 25:13.</span></p>');
INSERT INTO `quran` VALUES (11662,2,67,9,'<p><span>Hampir-hampir </span><span>neraka </span><span> itu pecah karena marah. </span><span>[a]</span><span> Setiap kali dilemparkan ke dalamnya suatu rombongan </span><span>orang berdosa</span><span> akan bertanya kepada mereka penjaga-penjaganya, “Apakah tidak pernah datang kepada kamu seorang Pemberi ingat ? “</span></p><p><span>[a]</span><span> 39:72 ; 40:51.</span></p>');
INSERT INTO `quran` VALUES (11663,2,67,10,'<p><span>Mereka berkata, “Ya, sesungguhnya telah datang kepada kami seorang Pemberi ingat, tetapi kami mendustakan</span><span>nya</span><span> dan kami berkata, </span><span>‘</span><span>Allah tidak menurunkan sesuatu pun</span><span>’</span><span> ; tidaklah kamu melainkan di dalam kesesatan yang besar.”</span></p>');
INSERT INTO `quran` VALUES (11664,2,67,11,'<p><span>Dan mereka berkata, “Seandainya kami mendengarkan atau mempergunakan akal, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3080a">3080a</a><span>]</span><span> niscaya kami tidak akan termasuk di antara penghuni Api yang menyala-nyala.</span></p>');
INSERT INTO `quran` VALUES (11665,2,67,12,'<p><span>Maka mereka mengakui dosa-dosa mereka; maka laknatlah bagi para penghuni Api yang menyala-nyala.</span></p>');
INSERT INTO `quran` VALUES (11666,2,67,13,'<p><span>[b]</span><span> Sesungguhnya orang-orang yang takut kepada Tuhan mereka dalam keadaan ghaib, bagi mereka ada ampunan dan ganjaran besar.</span></p><p><span>[b] </span><span>21:50 ; 55:47 ; 79:41 </span><span>–</span><span> 43.</span></p>');
INSERT INTO `quran` VALUES (11667,2,67,14,'<p><span>Dan </span><span>[a]</span><span> sembunyikanlah ucapanmu atau zahirkanlah itu. Sesungguhnya Dia Maha Mengetahui segala isi hati.</span></p><p><span>[a]</span><span> 2:78 ; 6:4 ; 11:6 ; 20:8.</span></p>');
INSERT INTO `quran` VALUES (11668,2,67,15,'<p><span>Apakah Dia Yang menciptakan tidak mengetahui ? Dan Dia Maha Halus, Yang Maha Waspada.</span></p>');
INSERT INTO `quran` VALUES (11669,2,67,16,'<p><span>Dia-lah Yang telah menjadikan </span><span>[b]</span><span> bagi kamu bumi sebagai tempat tinggal ; maka berjalanlah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3081">3081</a><span>]</span><span> ke segala penjurunya, dan makanlah dari rezeki-Nya. Dan kepada-Nya-lah dibangkitkan.</span></p><p><span>[b]</span><span> 2:23 ; 20:54.</span></p>');
INSERT INTO `quran` VALUES (11670,2,67,17,'<p><span>[c]</span><span> Apakah kamu merasa aman dari Yang ada di langit </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3082">3082</a><span>]</span><span> bahwa Dia akan menenggelamkan bumi bersama kamu ? Maka tiba-tiba </span><span>bumi</span><span> itu bergoncang.</span></p><p><span>[c]</span><span> 16:46 ; 17:69 ; 34:10.</span></p>');
INSERT INTO `quran` VALUES (11671,2,67,18,'<p><span>Apakah kamu merasa aman dari Yang ada di langit bahwa Dia akan mengirimkan kepada kamu hujan batu ? Maka kamu akan segera mengetahui betapa </span><span>dahsyatnya</span><span> peringatan-Ku !</span></p>');
INSERT INTO `quran` VALUES (11672,2,67,19,'<p><span>Dan, sesungguhnya telah mendustakan orang-orang sebelum mereka, maka betapa kerasnya azab-Ku !</span></p>');
INSERT INTO `quran` VALUES (11673,2,67,20,'<p><span>[a]</span><span> Apakah mereka tidak melihat burung-burung di atas mereka berbaris mengembangkan dan mengatupkan sayap</span><span>nya</span><span> ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3083">3083</a><span>]</span><span> Tiada yang dapat menahan mereka selain </span><span>Tuhan</span><span> Yang Maha Pemurah. Sesungguhnya Dia Maha Melihat segala sesuatu.</span></p><p><span>[a]</span><span> 16:80.</span></p>');
INSERT INTO `quran` VALUES (11674,2,67,21,'<p><span>Atau siapakah dia yang menjadi lasykar bagi kamu yang akan menolong kamu selain </span><span>Tuhan</span><span> Yang Maha Pemurah ? Tidaklah orang-orang kafir melainkan dalam </span><span>keadaan </span><span> tertipu.</span></p>');
INSERT INTO `quran` VALUES (11675,2,67,22,'<p><span>Atau, </span><span>[b]</span><span> siapakah dia ini yang akan memberi rezeki kepadamu, seandainya Dia menahan rezeki-Nya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3084">3084</a><span>]</span><span> Bahkan mereka bersekeras dalam kesombongan dan menjauhkan diri.</span></p><p><span>[b]</span><span> 10:32 ; 34:25.</span></p>');
INSERT INTO `quran` VALUES (11676,2,67,23,'<p><span>Apakah orang yang berjalan terjungkal atas mukanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3085">3085</a><span>]</span><span> mendapat petunjuk lebih baik ataukah orang yang berjalan tegak pada jalan yang lurus ?</span></p>');
INSERT INTO `quran` VALUES (11677,2,67,24,'<p><span>Katakanlah, “Dia-lah Yang telah menjadikan kamu dan </span><span>[c]</span><span> menjadikan bagimu telinga dan mata dan hati. </span><span>Tetapi</span><span> sedikit sekali kamu bersyukur.”</span></p><p><span>[c]</span><span> 16:79 ; 23:79.</span></p>');
INSERT INTO `quran` VALUES (11678,2,67,25,'<p><span>Katakanlah, </span><span>[a]</span><span> “Dia-lah Yang telah mengembang-biakkan kamu di bumi ini dan kepada Dia-lah kamu sekalian akan dihimpun.”</span></p><p><span>[a]</span><span> 23:80.</span></p>');
INSERT INTO `quran` VALUES (11679,2,67,26,'<p><span>[b]</span><span> “Dan, mereka berkata, “Kapankah perjanjian ini </span><span>akan sempurna,</span><span> jika kamu orang-orang yang benar ? “</span></p><p><span>[b]</span><span> 21:39 ; 34:30 ; 36:49.</span></p>');
INSERT INTO `quran` VALUES (11680,2,67,27,'<p><span>Katakanlah, “Sesungguhnya ilmu </span><span>tentang itu </span><span> hanyalah di sisi Allah, dan sesungguhnya </span><span>[c]</span><span> aku hanyalah seorang pemberi ingat yang menjelaskan.”</span></p><p><span>[c]</span><span> 22:50 ; 26:116 ; 29:51.</span></p>');
INSERT INTO `quran` VALUES (11681,2,67,28,'<p><span>Tetapi, ketika mereka melihat </span><span>azab</span><span> itu sudah dekat, menjadi suramlah muka orang-orang yang kafir </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3086">3086</a><span>]</span><span> dan dikatakan, “Inilah yang dahulu kamu minta berulang-kali.”</span></p>');
INSERT INTO `quran` VALUES (11682,2,67,29,'<p><span>Katakanlah, “Terangkanlah kepadaku, jika Allah akan membinasakan aku dan orang-orang besertaku atau </span><span>Dia</span><span> menaruh kasihan kepada kami, maka siapakah dapat melindungi orang-orang kafir dari azab yang sangat pedih ? “</span></p>');
INSERT INTO `quran` VALUES (11683,2,67,30,'<p><span>katakanlah, “Dia-lah </span><span>Tuhan</span><span> Yang Maha Pemurah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3087">3087</a><span>]</span><span> kepada Dia kami beriman dan kepada-Nya kami bertawakkal, maka segera kamu akan mengetahui siapa yang ada dalam kesesatan yang nyata.”</span></p>');
INSERT INTO `quran` VALUES (11684,2,67,31,'<p><span>Katakanlah, “Beritahukanlah kepadaku, jika air kamu meresap ke dalam tanah, maka siapakah yang akan mendatangkan kepadamu air yang mengalir ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3088">3088</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11685,2,68,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11686,2,68,2,'<p><span>Demi tempat tinta dan pena dan apa yang mereka tulis. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3089">3089</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11687,2,68,3,'<p><span>Dengan nikmat Tuhan engkau, </span><span>[b]</span><span> engkau bukan orang gila. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3089a">3089a</a><span>]</span></p><p><span>[b]</span><span> 34:47 ; 52:30.</span></p>');
INSERT INTO `quran` VALUES (11688,2,68,4,'<p><span>Dan, sesungguhnya, bagi engkau pasti ada ganjaran tanpa putus-putusnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3090">3090</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11689,2,68,5,'<p><span>Dan, sesungguhnya, engkau benar-benar memiliki akhlak yang agung. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3091">3091</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11690,2,68,6,'<p><span>Maka engkau akan segera melihat, dan mereka </span><span>pun</span><span> akan melihat,</span></p>');
INSERT INTO `quran` VALUES (11691,2,68,7,'<p><span>Siapa di antara kamu yang sesat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3092">3092</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11692,2,68,8,'<p><span>Sesungguhnya, Tuhan engkau Dia-lah Paling Mengetahui </span><span>[a]</span><span> siapa yang sesat dari jalan-Nya. dan Dia-lah Paling mengetahui mereka yang mendapat petunjuk.</span></p><p><span>[a]</span><span> 16:126.</span></p>');
INSERT INTO `quran` VALUES (11693,2,68,9,'<p><span>Maka janganlah engkau ikuti mereka yang mendustakan.</span></p>');
INSERT INTO `quran` VALUES (11694,2,68,10,'<p><span>[b]</span><span> Mereka menghendaki engkau bersikap lunak, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3093">3093</a><span>]</span><span> supaya mereka </span><span>pun</span><span> bersikap lunak.</span></p><p><span>[b]</span><span> 17:74.</span></p>');
INSERT INTO `quran` VALUES (11695,2,68,11,'<p><span>Janganlah engkau ikuti setiap orang yang banyak bersumpah lagi hina.</span></p>');
INSERT INTO `quran` VALUES (11696,2,68,12,'<p><span>[a]</span><span> Pengumpat yang kesana kemari dengan </span><span>menyebar </span><span> fitnah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3094">3094</a><span>]</span></p><p><span>[a]</span><span> 104:2.</span></p>');
INSERT INTO `quran` VALUES (11697,2,68,13,'<p><span>[b]</span><span> Penghalang bagi kebaikan, pelampau batas, bergelimang dosa,</span></p><p><span>[b]</span><span> 50:26.</span></p>');
INSERT INTO `quran` VALUES (11698,2,68,14,'<p><span>Berbudi kasar dan selain dari itu terkenal kejahatannya,</span></p>');
INSERT INTO `quran` VALUES (11699,2,68,15,'<p><span>[c]</span><span> Karena ia mempunyai harta dan anak-anak. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3095">3095</a><span>]</span></p><p><span>[c]</span><span> 23:56 ; 74:13 </span><span>–</span><span> 14.</span></p>');
INSERT INTO `quran` VALUES (11700,2,68,16,'<p><span>[d]</span><span> Apabila dibacakan kepadanya ayat-ayat Kami, ia berkata, “</span><span>Ini </span><span> dongeng-dongeng orang-orang dahulu ! “</span></p><p><span>[d]</span><span> 8:32 ; 16:25 ; 83:14.</span></p>');
INSERT INTO `quran` VALUES (11701,2,68,17,'<p><span>Kami segera menandainya pada belalainya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3096">3096</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11702,2,68,18,'<p><span>Sesungguhnya Kami mencobai mereka sebagaimana Kami telah mencobai pemilik-pemilik kebun, ketika mereka bersumpah bahwa mereka pasti akan memetik </span><span>buah</span><span>-nya di waktu pagi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3097">3097</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11703,2,68,19,'<p><span>Dan mereka tidak mengecualikan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3098">3098</a><span>] </span><span>dan tidak mengucapkan</span><span> “</span><span>insya Allah</span><span> “.</span></p>');
INSERT INTO `quran` VALUES (11704,2,68,20,'<p><span>[a]</span><span> Maka menimpa atas </span><span>kebun</span><span> itu azab dari Tuhan engkau ketika mereka sedang tidur ;</span></p><p><span>[a]</span><span> 3:118 ; 18:43.</span></p>');
INSERT INTO `quran` VALUES (11705,2,68,21,'<p><span>Maka jadilah </span><span>kebun </span><span> itu seperti telah terpotong,</span></p>');
INSERT INTO `quran` VALUES (11706,2,68,22,'<p><span>maka mereka saling memanggil di waktu pagi,</span></p>');
INSERT INTO `quran` VALUES (11707,2,68,23,'<p><span>Pergilah waktu pagi-pagi ke kebunmu, jika kamu hendak memetik </span><span>buahnya.</span></p>');
INSERT INTO `quran` VALUES (11708,2,68,24,'<p><span>Maka berangkatlah mereka sambil bercakap-cakap satu sama lain dengan suara rendah.</span></p>');
INSERT INTO `quran` VALUES (11709,2,68,25,'<p><span>Jangan sekali-kali memasukinya orang miskin hari ini, sedang kamu berada di sana. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3099">3099</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11710,2,68,26,'<p><span>Mereka berangkat pagi-pagi dengan niat bakhil padahal mereka mampu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3100">3100</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11711,2,68,27,'<p><span>Maka, ketika mereka melihat </span><span>kebun</span><span> itu, berkatalah mereka, “Sesungguhnya kita telah sesat jalan.”</span></p>');
INSERT INTO `quran` VALUES (11712,2,68,28,'<p><span>“Bahkan kami telah bernasib buruk,”</span></p>');
INSERT INTO `quran` VALUES (11713,2,68,29,'<p><span>Berkata yang terbaik di antara mereka, “Bukankah aku telah mengatakan kepadamu, mengapa kamu tidak mensucikan </span><span>Tuhan</span><span> ?”</span></p>');
INSERT INTO `quran` VALUES (11714,2,68,30,'<p><span>Berkata mereka, “Maha Suci Tuhan kami ! Sesungguhnya, kami telah berbuat aniaya.</span></p>');
INSERT INTO `quran` VALUES (11715,2,68,31,'<p><span>maka berhadapanlah sebagaian mereka atas sebagian yang lain saling mencela.</span></p>');
INSERT INTO `quran` VALUES (11716,2,68,32,'<p><span>Mereka berkata, “Celakalah kami ! Sesungguhnya kami telah menjadi orang yang melampaui batas.</span></p>');
INSERT INTO `quran` VALUES (11717,2,68,33,'<p><span>Mudah-mudahan </span><span>jika kami bertobat,</span><span> Tuhan kami akan mengganti lebih baik dari itu. Sesungguhnya kami kepada Tuhan kami bermohon dengan rendah hati.</span></p>');
INSERT INTO `quran` VALUES (11718,2,68,34,'<p><span>Demikianlah azab </span><span>di dunia ini.</span><span> Dan, sesungguhnya, </span><span>[a]</span><span> azab akhirat itu lebih besar </span><span>‘</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3101">3101</a><span>]</span><span> jika mereka mengetahui !</span></p><p><span>[a]</span><span> 13:35 ; 39:27.</span></p>');
INSERT INTO `quran` VALUES (11719,2,68,35,'<p><span>R. 2</span></p><p><span>Sesungguhnya </span><span>[b]</span><span> bagi orang-orang muttaki, di sisi Tuhan mereka ada kebun-kebun kenikmatan.</span></p><p><span>[b]</span><span> 30:16 ; 68:35 ; 78:32.</span></p>');
INSERT INTO `quran` VALUES (11720,2,68,36,'<p><span>[c]</span><span> Maka apakah Kami menjadikan orang-orang muslim sama seperti orang-orang yang berdosa ?</span></p><p><span>[c]</span><span> 32:19 ; 38:29 ; 45:22.</span></p>');
INSERT INTO `quran` VALUES (11721,2,68,37,'<p><span>Ada apakah dengan kamu ? Bagaimana kamu memutuskan ?</span></p>');
INSERT INTO `quran` VALUES (11722,2,68,38,'<p><span>Atau apakah kamu memiliki sebauh Kitab yang di dalamnya kamu baca,</span></p>');
INSERT INTO `quran` VALUES (11723,2,68,39,'<p><span>Sesungguhnya bagi kamu di dalamnya pasti ada yang kamu sukai.</span></p>');
INSERT INTO `quran` VALUES (11724,2,68,40,'<p><span>Atau, apakah bagi kamu ada janji-janji sumpah atas Kami yang berlaku sampai Hari Kiamat, sesungguhnya bagi kamu apa yang kamu putuskan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3102">3102</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11725,2,68,41,'<p><span>Tanyakanlah kepada mereka, siapa di antara mereka akan bertanggung-jawab tentang hal itu.</span></p>');
INSERT INTO `quran` VALUES (11726,2,68,42,'<p><span>Atau, apakah bagi mereka ada sekutu-sekutu ? Maka hendaklah mereka membawa sekutu-sekutu mereka, jika mereka orang-orang benar.</span></p>');
INSERT INTO `quran` VALUES (11727,2,68,43,'<p><span>Pada hari ketika timbul malapetaka yang dahsyat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3103">3103</a><span>]</span><span> dan mereka dipanggil untuk sujud, maka mereka tidak mampu ;</span></p>');
INSERT INTO `quran` VALUES (11728,2,68,44,'<p><span>Akan merunduk </span><span>[a</span><span>]</span><span> pandangan mereka </span><span>karena malu</span><span>, akan meliputi mereka kehinaan. Dan sesungguhnya dahulu mereka diseru untuk sujud, sedangkan mereka dalam keadaan selamat </span><span>dari syirik.</span></p><p><span>[a]</span><span> 75:25 ; 88:3 </span><span>–</span><span> 4.</span></p>');
INSERT INTO `quran` VALUES (11729,2,68,45,'<p><span>[a]</span><span> Maka biarkanlah Aku dan yang mendustakan perkataan </span><span>Alquran</span><span> ini. </span><span>[b]</span><span> Kami segera akan menghela mereka selangkah demi selangkah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3104">3104</a><span>]</span><span> dari arah mana yang mereka tidak mengetahui.</span></p><p><span>[a]</span><span> 73:12 ; 74:12.</span></p>');
INSERT INTO `quran` VALUES (11730,2,68,46,'<p><span>[c]</span><span> Dan, Aku memberi tangguh kepada mereka. Sesungguhnya rencana-Ku sangat kuat.</span></p><p><span>[c]</span><span> 7:184.</span></p>');
INSERT INTO `quran` VALUES (11731,2,68,47,'<p><span>[d]</span><span> Atau apakah engkau meminta kepada mereka upah sehingga mereka karena hutang itu merasa berat ?</span></p><p><span>[d]</span><span> 23:73 ; 52:41.</span></p>');
INSERT INTO `quran` VALUES (11732,2,68,48,'<p><span>[e]</span><span> Atau apakah ada pada meereka </span><span>ilmu</span><span> gaib, lalu mereka menulis ?</span></p><p><span>[e]</span><span> 52:42.</span></p>');
INSERT INTO `quran` VALUES (11733,2,68,49,'<p><span>Maka bersabarlah terhadap keputusan Tuhan engkau dan </span><span>[f]</span><span> janganlah engkau menjadi seperti sahabat ikan, </span><span>Yunus,</span><span> ketika ia berseru </span><span>kepada Tuhan-nya</span><span> dalam keadaan penuh dengan duka.</span></p><p><span>[f]</span><span> 21:88.</span></p>');
INSERT INTO `quran` VALUES (11734,2,68,50,'<p><span>[g]</span><span> Seandainya tidak datang kepadanya nikmat dari Tuhan-nya, niscaya akan dicampakkah di atas tanah tandus </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3105">3105</a><span>]</span><span> dan dia dalam keadaan tercela.</span></p><p><span>[g]</span><span> 37:144 </span><span>–</span><span> 146.</span></p>');
INSERT INTO `quran` VALUES (11735,2,68,51,'<p><span>Tetapi, Tuhan-nya telah memilihnya dan menjadikan dari orang-orang shaleh.</span></p>');
INSERT INTO `quran` VALUES (11736,2,68,52,'<p><span>Dan hampir orang-orang ingkar benar-benar menggelincirkan engkau dengan pandangan mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3106">3106</a><span>]</span><span> ketika mereka mendengar Alquran </span><span>dari engkau</span><span> dan mereka berkata, “Sesungguhnya dia pasti orang gila.”</span></p>');
INSERT INTO `quran` VALUES (11737,2,68,53,'<p><span>Dan tidaklah </span><span>Alquran</span><span> itu malainkan peringatan bagi semesta alam.</span></p>');
INSERT INTO `quran` VALUES (11738,2,69,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11739,2,69,2,'<p><span>Kebenaran yang pasti. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3107">3107</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11740,2,69,3,'<p><span>Apakah kebenaran yang pasti itu ?</span></p>');
INSERT INTO `quran` VALUES (11741,2,69,4,'<p><span>Dan, tahukah engkau apakah kebenaran yang pasti itu ?</span></p>');
INSERT INTO `quran` VALUES (11742,2,69,5,'<p><span>Telah mendustakan kaum Tsamud dan </span><span>‘</span><span>Ad pada malapetaka itu.</span></p>');
INSERT INTO `quran` VALUES (11743,2,69,6,'<p><span>[b]</span><span> Adapun kaum Tsamud, maka mereka telah dibinasakan oleh azab yang dahsyat.</span></p><p><span>[b]</span><span> 41:18 ; 54:32.</span></p>');
INSERT INTO `quran` VALUES (11744,2,69,7,'<p><span>[c]</span><span> Dan adapun kaum </span><span>‘</span><span>Ad, mereka telah dibinasakan oleh angin yang sangat dingin dan amat kencang.</span></p><p><span>[c]</span><span> 41:17 ; 54:20.</span></p>');
INSERT INTO `quran` VALUES (11745,2,69,8,'<p><span>Yang Dia telah tiupkan </span><span>angin</span><span> itu atas mereka tujuh malam dan delapan hari terus-menerus, sehingga engkau melihat kaum itu jatuh bergelimpangan, seolah-olah </span><span>[d]</span><span> mereka tunggul-tunggul pohon kurma yang kosong.</span></p><p><span>[d]</span><span> 54:21.</span></p>');
INSERT INTO `quran` VALUES (11746,2,69,9,'<p><span>Maka apakah engkau melihat dari mereka ada yang tersisa ?</span></p>');
INSERT INTO `quran` VALUES (11747,2,69,10,'<p><span>[a]</span><span> Dan, telah datang Firaun serta orang-orang sebelumnya, dan </span><span>kota-kota kaum Luth</span><span> yang telah dijungkir-balikkan karena berbuat dosa.</span></p><p><span>[a]</span><span> 28:9.</span></p>');
INSERT INTO `quran` VALUES (11748,2,69,11,'<p><span>Mereka mendurhakai rasul Tuhan mereka, oleh karena itu Dia </span><span>[b]</span><span> menangkap mereka dengan cengkeraman yang terus bertambah.</span></p><p><span>[b]</span><span> 73:17.</span></p>');
INSERT INTO `quran` VALUES (11749,2,69,12,'<p><span>[c]</span><span> Sesungguhnya, ketika air </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3108">3108</a><span>]</span><span> bertambah tinggi, Kami mengangkut kamu dalam bahtera.</span></p><p><span>[c]</span><span> 11:41 ; 54:14.</span></p>');
INSERT INTO `quran` VALUES (11750,2,69,13,'<p><span>Supaya Kami menjadikan </span><span>kejadian</span><span> itu bagimu peringatan, dan supaya telinga yang mendengar, mau mendengar.</span></p>');
INSERT INTO `quran` VALUES (11751,2,69,14,'<p><span>[d]</span><span> Maka apabila ditiupkan ke dalam nafiri satu tiupan dahsyat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3109">3109</a><span>]</span></p><p><span>[d]</span><span> 18:100 ; 23:102 ; 36:52 ; 39:69 ; 50:21.</span></p>');
INSERT INTO `quran` VALUES (11752,2,69,15,'<p><span>Dan bumi serta gunung-gunung diangkat, maka keduanya dihancurkan dengan sekali benturan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3110">3110</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11753,2,69,16,'<p><span>Maka pada hari itu terjadilah peristiwa itu.</span></p>');
INSERT INTO `quran` VALUES (11754,2,69,17,'<p><span>Dan terbelahlah langit, </span><span>[a]</span><span> maka hari itu </span><span>langit</span><span> menjadi lemah, </span><span>agama orang Mekkah.</span></p><p><span>[a]</span><span> 55:38 ; 84:2.</span></p>');
INSERT INTO `quran` VALUES (11755,2,69,18,'<p><span>[b]</span><span> Dan malaikat-malaikat berada di tepi-tepinya. Dan akan memikul </span><span>‘</span><span>Arasy Tuhan Engkau di atas mereka pada hari itu delapan </span><span>malaikat,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3111">3111</a><span>]</span></p><p><span>[b]</span><span> 39:76 ; 40:8.</span></p>');
INSERT INTO `quran` VALUES (11756,2,69,19,'<p><span>Pada hari itu kamu akan dihadapkan </span><span>kepada Tuhan </span><span>; dan </span><span>[a]</span><span> tidak ada rahasiamu yang tersembunyi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3112">3112</a><span>]</span></p><p><span>[a]</span><span> 4:43 ; 41:21.</span></p>');
INSERT INTO `quran` VALUES (11757,2,69,20,'<p><span>[b]</span><span> Maka barangsiapa diberikan kitabnya di tangan kanannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3113">3113</a><span>]</span><span> maka ia berkata, “Marilah baca kitabku ! “</span></p><p><span>[b] </span><span>17:72 ; 84:8 </span><span>–</span><span> 9.</span></p>');
INSERT INTO `quran` VALUES (11758,2,69,21,'<p><span>“Sesungguhnya, aku yakin bahwa aku akan menemui perhitunganku.”</span></p>');
INSERT INTO `quran` VALUES (11759,2,69,22,'<p><span>[c]</span><span> Maka ia berada dalam kehidupan yang diridhai.</span></p><p><span>[c]</span><span> 88:10 ; 101:8.</span></p>');
INSERT INTO `quran` VALUES (11760,2,69,23,'<p><span>[d]</span><span> Dalam kebun yang tinggi,</span></p><p><span>[d]</span><span> 43:73 ; 88:11.</span></p>');
INSERT INTO `quran` VALUES (11761,2,69,24,'<p><span>[e]</span><span> Buah-buahnya yang dekat.</span></p><p><span>[e]</span><span> 55:55 ; 76:15.</span></p>');
INSERT INTO `quran` VALUES (11762,2,69,25,'<p><span>Dikatakan kepadanya,</span><span> </span><span>[f] </span><span>“Makahlah dan minumlah sesuka hati disebabkan perbuatan kamu yang dahulu pada hari-hari yang telah lampau.”</span></p><p><span>[f]</span><span> 77:44.</span></p>');
INSERT INTO `quran` VALUES (11763,2,69,26,'<p><span>[a]</span><span> Namun, barangsiapa diberikan kitabnya di tangan kirinya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3114">3114</a><span>]</span><span> maka ia berkata, “Aduhai sekiranya aku tidak diberi kitabku ! “</span></p><p><span>[a]</span><span> 56:42 </span><span>–</span><span> 43 ; 84:11 </span><span>–</span><span> 13.</span></p>');
INSERT INTO `quran` VALUES (11764,2,69,27,'<p><span>Dan aku tidak mengetahui apa perhitunganku itu ! “</span></p>');
INSERT INTO `quran` VALUES (11765,2,69,28,'<p><span>“Aduhai, sekiranya </span><span>kematianku</span><span> mengakhiri</span><span> hidupku </span><span>! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3115">3115</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11766,2,69,29,'<p><span>“Tidak bermanfaat bagiku hartaku ;”</span></p>');
INSERT INTO `quran` VALUES (11767,2,69,30,'<p><span>“Hilang lenyap dariku kekuasaanku.”</span></p>');
INSERT INTO `quran` VALUES (11768,2,69,31,'<p><span>[b] </span><span>“Tangkaplah dia dan belenggulah dia.”</span></p><p><span>[b]</span><span> 76:5.</span></p>');
INSERT INTO `quran` VALUES (11769,2,69,32,'<p><span>“Kemudian masukkanlah dia ke dalam Jahannam ;”</span></p>');
INSERT INTO `quran` VALUES (11770,2,69,33,'<p><span>“Lalu dengan rantai yang panjangnya tujuh puluh hasta ikatlah dia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3116">3116</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11771,2,69,34,'<p><span>“Sesungguhnya, ia tidak beriman kepada Allah, Yang Maha Besar.</span></p>');
INSERT INTO `quran` VALUES (11772,2,69,35,'<p><span>[a]</span><span> “Dan ia tidak menganjurkan untuk memberi makan kepada orang miskin.</span></p><p><span>[a]</span><span> 74:45 ; 89:19 ; 107:4.</span></p>');
INSERT INTO `quran` VALUES (11773,2,69,36,'<p><span>[b]</span><span> “Maka tidak ada baginya pada hari ini di sana seorang sahabat karib.</span></p><p><span>[b]</span><span> 43:68 ; 70:11 ; 80:38.</span></p>');
INSERT INTO `quran` VALUES (11774,2,69,37,'<p><span>“Dan tidak ada makanan kecuali bekas </span><span>[c]</span><span> cucian </span><span>luka,</span></p><p><span>[c]</span><span> 14:17 ; 78:25 </span><span>–</span><span> 26.</span></p>');
INSERT INTO `quran` VALUES (11775,2,69,38,'<p><span>“Tidak ada yang memakannya, kecuali orang-orang berdosa.”</span></p>');
INSERT INTO `quran` VALUES (11776,2,69,39,'<p><span>R. 2</span></p><p><span>Maka Aku bersumpah dengan apa yang kamu lihat,</span></p>');
INSERT INTO `quran` VALUES (11777,2,69,40,'<p><span>Dan apa yang tidak kamu lihat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3117">3117</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11778,2,69,41,'<p><span>Sesungguhnya </span><span>Alquran</span><span> itu firman </span><span>yang disampaikan</span><span> seorang Rasul mulia,</span></p>');
INSERT INTO `quran` VALUES (11779,2,69,42,'<p><span>[a]</span><span> Dan, bukanlah </span><span>Alquran </span><span> itu perkataan seorang penyair. Sedikit sekali apa yang kamu percayai</span></p><p><span>[a]</span><span> 36:70 ; 52:31.</span></p>');
INSERT INTO `quran` VALUES (11780,2,69,43,'<p><span>[b]</span><span> Dan bukanlah ini perkataan ahlinujum. Sedikit sekali kamu mengambil nasihat !</span></p><p><span>[b]</span><span> 52:30.</span></p>');
INSERT INTO `quran` VALUES (11781,2,69,44,'<p><span>Ini adalah </span><span> wahyu yang diturunkan dari Tuhan semesta alam.</span></p>');
INSERT INTO `quran` VALUES (11782,2,69,45,'<p><span>[c]</span><span> Dan sekiranya ia mengada-adakan atas </span><span>nama</span><span> Kami sebagian perkataan,</span></p><p><span>[c]</span><span> 40:29.</span></p>');
INSERT INTO `quran` VALUES (11783,2,69,46,'<p><span>Niscaya Kami akan menangkap dia dengan tangan kanan.</span></p>');
INSERT INTO `quran` VALUES (11784,2,69,47,'<p><span>Kemudian, tentulah Kami memotong urat nadinya ;</span></p>');
INSERT INTO `quran` VALUES (11785,2,69,48,'<p><span>Dan tiada seorang pun di antaramu dapat mencegah darinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3118">3118</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11786,2,69,49,'<p><span>Dan sesungguhnya Alquran itu nasihat bagi orang-orang muttaki.</span></p>');
INSERT INTO `quran` VALUES (11787,2,69,50,'<p><span>Dan sesungguhnya, Kami pasti mengetahui bahwa di antara kamu </span><span>ada </span><span> orang-orang yang mendustakan </span><span>Alquran.</span></p>');
INSERT INTO `quran` VALUES (11788,2,69,51,'<p><span>Dan sesungguhnya, </span><span>Alquran </span><span> itu akan menjadi </span><span>sumber</span><span> penyesalan bagi orang-orang kafir.</span></p>');
INSERT INTO `quran` VALUES (11789,2,69,52,'<p><span>Dan sesungguhnya. </span><span>Alquran</span><span> itu adalah kebenaran yang diyakini.</span></p>');
INSERT INTO `quran` VALUES (11790,2,69,53,'<p><span>[a]</span><span> Maka sucikanlah nama Tuhan engkau, Yang Maha Besar.</span></p><p><span>[a]</span><span> 56:75 ; 87:2.</span></p>');
INSERT INTO `quran` VALUES (11791,2,70,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11792,2,70,2,'<p><span>Seorang penanya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3119">3119</a><span>]</span><span> menanyakan tentang </span><span>[b]</span><span> azab yang akan terjadi,</span></p><p><span>[b]</span><span> 52:8 ; 56:2.</span></p>');
INSERT INTO `quran` VALUES (11793,2,70,3,'<p><span>Untuk orang-orang kafir, tidak ada </span><span>[c]</span><span> baginya yang menghindarkan,</span></p><p><span>[c]</span><span> 52:9 ; 56:3.</span></p>');
INSERT INTO `quran` VALUES (11794,2,70,4,'<p><span>Azab itu</span><span> dari Allah, Yang mempunyai tangga-tangga derajat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3119a">3119a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11795,2,70,5,'<p><span>Malaikat-malaikat dan ruh itu naik kepada-Nya dalam satu hari yang ukurannya lima puluh ribu tahun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3120">3120</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11796,2,70,6,'<p><span>[d]</span><span> Maka bersabarlah dengan sabar yang baik.</span></p><p><span>[d]</span><span> 15:86.</span></p>');
INSERT INTO `quran` VALUES (11797,2,70,7,'<p><span>Sesungguhnya mereka memandang </span><span>hari</span><span> itu sangat jauh.</span></p>');
INSERT INTO `quran` VALUES (11798,2,70,8,'<p><span>Dan Kami melihatnya dekat.</span></p>');
INSERT INTO `quran` VALUES (11799,2,70,9,'<p><span>Pada hari langit akan menjadi seperti cairan tembaga,</span></p>');
INSERT INTO `quran` VALUES (11800,2,70,10,'<p><span>[a]</span><span> Dan gunung-gunung akan menjadi seperti bulu domba </span><span>yang beterbagan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3121">3121</a><span>]</span></p><p><span>[a]</span><span> 20:106 ; 70:10 ; 101:6.</span></p>');
INSERT INTO `quran` VALUES (11801,2,70,11,'<p><span>Dan tidak akan bertanya </span><span>[b]</span><span> sahabat karib kepada sahabat karib lainnya.</span></p><p><span>[b]</span><span> 44:42 ; 69:36.</span></p>');
INSERT INTO `quran` VALUES (11802,2,70,12,'<p><span>Hari itu</span><span> akan diperlihatkan dengan jelas kepada mereka. </span><span>[c]</span><span> Orang berdosa ingin jika dia menebus dirinya dari azab hari itu dengan anak-anaknya,</span></p><p><span>[c]</span><span> 5:37 ; 13:19 ; 39:48.</span></p>');
INSERT INTO `quran` VALUES (11803,2,70,13,'<p><span>[d]</span><span> Dan isterinya dan saudaranya,</span></p><p><span>[d]</span><span> 31:34 ; 80:37.</span></p>');
INSERT INTO `quran` VALUES (11804,2,70,14,'<p><span>Dan kaum kerabatnya yang melindunginya.</span></p>');
INSERT INTO `quran` VALUES (11805,2,70,15,'<p><span>Dan semua yang ada di bumi, kemudian dia menyelamatkannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3122">3122</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11806,2,70,16,'<p><span>Tetapi sekali-kali tidak ! Sesungguhnya azab itu Api yang menyala.</span></p>');
INSERT INTO `quran` VALUES (11807,2,70,17,'<p><span>[a]</span><span> Yang mengelupaskan hingga kulit kepada.</span></p><p><span>[a]</span><span> 74:30.</span></p>');
INSERT INTO `quran` VALUES (11808,2,70,18,'<p><span>Dia memanggil orang yang membelakangi dan yang berpaling.</span></p>');
INSERT INTO `quran` VALUES (11809,2,70,19,'<p><span>[b]</span><span> Dan menimbun harta dan menahan-</span><span>nya.</span></p><p><span>[b]</span><span> 9:34 ; 53:35 ; 104:3.</span></p>');
INSERT INTO `quran` VALUES (11810,2,70,20,'<p><span>Sesungguhnya, manusia diciptakan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3123">3123</a><span>] </span><span>ia berkeluh kesah.</span></p>');
INSERT INTO `quran` VALUES (11811,2,70,21,'<p><span>[c]</span><span> Apabila menyentuhnya keburukan, ia berkeluh-kesah,</span></p><p><span>[c]</span><span> 11:10.</span></p>');
INSERT INTO `quran` VALUES (11812,2,70,22,'<p><span>Tetapi apabila menyentuhnya kebaikan, ia bakhil,</span></p>');
INSERT INTO `quran` VALUES (11813,2,70,23,'<p><span>Kecuali orang-orang yang shalat,</span></p>');
INSERT INTO `quran` VALUES (11814,2,70,24,'<p><span>[d]</span><span> Yang mereka atas shalatnya dawam,</span></p><p><span>[d]</span><span> 23:10.</span></p>');
INSERT INTO `quran` VALUES (11815,2,70,25,'<p><span>[e]</span><span> Dan orang-orang yang dalam harta mereka ada hak yang ditentukan </span><span>untuk orang miskin.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3124">3124</a><span>]</span></p><p><span>[e]</span><span> 51:20.</span></p>');
INSERT INTO `quran` VALUES (11816,2,70,26,'<p><span>Untuk yang meminta dan yang tidak meminta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3125">3125</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (11817,2,70,27,'<p><span>Dan orang-orang yang membenarkan Hari Pembalasan ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3125a">3125a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11818,2,70,28,'<p><span>Dan orang-orang yang takut dari azab Tuhan mereka.</span></p>');
INSERT INTO `quran` VALUES (11819,2,70,29,'<p><span>Sesungguhnya</span><span> dari</span><span> azab Tuhan mereka tidak ada yang merasa aman.</span></p>');
INSERT INTO `quran` VALUES (11820,2,70,30,'<p><span>[a]</span><span> Dan orang-orang yang menjaga kemaluan mereka.</span></p><p><span>[a]</span><span> 23:7.</span></p>');
INSERT INTO `quran` VALUES (11821,2,70,31,'<p><span>[b]</span><span> Kecuali terhadap istri-istri mereka atau yang dimiliki tangan kanan mereka ; maka sesungguhnya mereka dalam hal itu tidak tercela.</span></p><p><span>[b]</span><span> 23:7.</span></p>');
INSERT INTO `quran` VALUES (11822,2,70,32,'<p><span>[c]</span><span> Maka barangsiapa mencari selain itu, mereka itulah yang melampaui batas.</span></p><p><span>[c]</span><span> 23:8.</span></p>');
INSERT INTO `quran` VALUES (11823,2,70,33,'<p><span>[d]</span><span> Dan orang-orang yang menjaga amanat-amanatnya dan janji-janjinya,</span></p><p><span>[d]</span><span> 23:9.</span></p>');
INSERT INTO `quran` VALUES (11824,2,70,34,'<p><span>Dan orang-orang yang berdiri teguh pada kesaksian-kesaksiannya.</span></p>');
INSERT INTO `quran` VALUES (11825,2,70,35,'<p><span>Dan mereka yang benar-benar menjaga shalatnya.</span></p>');
INSERT INTO `quran` VALUES (11826,2,70,36,'<p><span>[a]</span><span> Mereka itulah di dalam surga-surga, dimuliakan.</span></p><p><span>[a]</span><span> 18:108 ; 23:12.</span></p>');
INSERT INTO `quran` VALUES (11827,2,70,37,'<p><span>R. 2</span></p><p><span>Tetapi, apa gerangan terjadi dengan orang-orang yang ingkar, </span><span>[b]</span><span> datang kepada engkau bergegas-gegas,</span></p><p><span>[b]</span><span> 14:43 </span><span>–</span><span> 44.</span></p>');
INSERT INTO `quran` VALUES (11828,2,70,38,'<p><span>Dari kanan dan dari kiri, dalam rombongan-rombongan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3126">3126</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11829,2,70,39,'<p><span>Apakah setiap orang dari mereka mengharapkan untuk di masukkan dalam surga penuh kenikmatan ?</span></p>');
INSERT INTO `quran` VALUES (11830,2,70,40,'<p><span>Sekali-kali tidak mungkin ! Sesungguhnya Kami telah menciptakan mereka dari apa </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3127">3127</a><span>]</span><span> yang mereka ketahui.</span></p>');
INSERT INTO `quran` VALUES (11831,2,70,41,'<p><span>Maka Aku bersumpah dengan Tuhan yang mempunyai timur dan barat, sesungguhnya pasti Kami memiliki kekuasaan.</span></p>');
INSERT INTO `quran` VALUES (11832,2,70,42,'<p><span>Untuk menggatikan </span><span>mereka</span><span> dengan </span><span>kaum</span><span> yang lebih baik daripada mereka </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3128">3128</a><span>]</span><span> dan tidaklah ada yang dapat mencegah Kami </span><span>dari maksud itu.</span></p>');
INSERT INTO `quran` VALUES (11833,2,70,43,'<p><span>Maka </span><span>[a]</span><span> tinggalkanlah mereka tenggelam dalam pembicaraan kosong dan bermain-main hingga mereka bertemu dengan hari mereka yang dijanjikan kepada mereka.</span></p><p><span>[a]</span><span> 23:55 ; 43:84 ; 52:46.</span></p>');
INSERT INTO `quran` VALUES (11834,2,70,44,'<p><span>[b]</span><span> Hari ketika mereka akan keluar dari kuburan dengan cepat-cepat, seolah-olah mereka lari menuju tempat-tempat tertentu.</span></p><p><span>[b]</span><span> 36:52 ; 54:8 </span><span>–</span><span> 9.</span></p>');
INSERT INTO `quran` VALUES (11835,2,70,45,'<p><span>Mata mereka tertunduk ; kehinaan meliputi </span><span>[c]</span><span> mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3129">3129</a><span>]</span><span> Itulah hari yang kepada mereka telah dijanjikan.</span></p><p><span>[c]</span><span> 10:28 ; 54:8.</span></p>');
INSERT INTO `quran` VALUES (11836,2,71,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11837,2,71,2,'<p><span>Sesungguhnya Kami telah mengutus Nuh kepada kaumnya </span><span>dengan perintah,</span><span> “Berilah peringatan kaum engkau sebelum datang kepada mereka azab yang pedih.”</span></p>');
INSERT INTO `quran` VALUES (11838,2,71,3,'<p><span>Dia berkata, “Hai, kaum-ku ! Sesungguhnya, aku bagimu seorang pemberi ingat yang nyata.</span></p>');
INSERT INTO `quran` VALUES (11839,2,71,4,'<p><span>“Agar kamu beribadah kepada Allah dan bertakwalah kepada-Nya dan taatlah kepadaku,</span></p>');
INSERT INTO `quran` VALUES (11840,2,71,5,'<p><span>“Dia akan mengampuni kamu atas dosa-dosamu dan akan memberikan tangguh kepadamu hingga suatu saat tertentu. Sesungguhnya, </span><span>[a]</span><span> waktu yang ditetapkan Allah tidak dapat diundur lagi, apabila </span><span>waktu itu</span><span> datang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3130">3130</a><span>]</span><span> jika kamu mengetahui.”</span></p><p><span>[a]</span><span> 63:12.</span></p>');
INSERT INTO `quran` VALUES (11841,2,71,6,'<p><span>Ia berkata, “Hai, Tuhan-ku, sesungguhnya aku telah mengajak kaumku malam dan siang.</span></p>');
INSERT INTO `quran` VALUES (11842,2,71,7,'<p><span>“Maka seruanku tidak menambah mereka melainkan lari </span><span>menjauh.</span></p>');
INSERT INTO `quran` VALUES (11843,2,71,8,'<p><span>“Dan, sesungguhnya setiap kali aku berseru kepada mereka agar Engkau memaafkan mereka, mereka memasukkan jari-jarinya ke dalam telinganya </span><span>[a]</span><span> dan menutupi </span><span>mukanya dengan </span><span>pakaian mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3131">3131</a><span>]</span><span> dan mereka gigih </span><span>dalam keingkaran</span><span> dan mereka sangat menyombongkan diri.</span></p><p><span>[a]</span><span> 11:6.</span></p>');
INSERT INTO `quran` VALUES (11844,2,71,9,'<p><span>“Kemudian, sesungguhnya aku menyeru mereka secara terang-terangan,</span></p>');
INSERT INTO `quran` VALUES (11845,2,71,10,'<p><span>“Kemudian, sesungguhnya aku telah mengumumkan secara terbuka kepada mereka dan menghimbau mereka secara sembunyi-sembunyi,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3132">3132</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11846,2,71,11,'<p><span>[b]</span><span> Maka aku berkata, “Mohonlah ampun kepada Tuhan-mu. Sesungguhnya Dia Maha Pengampun.</span></p><p><span>[b]</span><span> 11:4, 53.</span></p>');
INSERT INTO `quran` VALUES (11847,2,71,12,'<p><span>“Dia akan mengirimkan atasmu hujan dengan lebat,</span></p>');
INSERT INTO `quran` VALUES (11848,2,71,13,'<p><span>“Dan Dia akan membantu kamu dengan harta dan anak-anak dan Dia akan menjadikan bagimu kebun-kebun dan akan menjadikan bagimu sungai-sungai.”</span></p>');
INSERT INTO `quran` VALUES (11849,2,71,14,'<p><span>“Apakah yang terjadi dengan dirimu, </span><span>bahwa</span><span> kamu tidak mengharapkan kemuliaan dari Allah ?</span></p>');
INSERT INTO `quran` VALUES (11850,2,71,15,'<p><span>[a]</span><span> “Dan, sesungguhnya, Dia telah menciptakan kamu dengan berbagai tingkatan </span><span>kejadian.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3133">3133</a><span>]</span></p><p><span>[a] </span><span>23:13 </span><span>–</span><span> 15.</span></p>');
INSERT INTO `quran` VALUES (11851,2,71,16,'<p><span>[b]</span><span> “Apakah tidak kamu melihat, bagaimana Allah telah menciptakan tujuh langit bertingkat-tingkat.</span></p><p><span>[b]</span><span> 65:13 ; 67:4.</span></p>');
INSERT INTO `quran` VALUES (11852,2,71,17,'<p><span>[c]</span><span> “Dan Dia telah menjadikan bulan, di dalamnya sebagai cahaya, dan menjadikan matahari sebagai pelita ?</span></p><p><span>[c]</span><span> 10:6 ; 25:62.</span></p>');
INSERT INTO `quran` VALUES (11853,2,71,18,'<p><span>[d]</span><span> “ Dan Allah telah menumbuhkan kamu dari bumi dengan pertumbuhan yang baik,</span></p><p><span>[d]</span><span> 7:26 ; 20:56.</span></p>');
INSERT INTO `quran` VALUES (11854,2,71,19,'<p><span>[e]</span><span> “Kemudian Dia mengembalikan kamu ke dalamnya, dan Dia mengeluarkan kamu dengan sebenar-benarnya.</span></p><p><span>[e]</span><span> 7:26 ; 20:56.</span></p>');
INSERT INTO `quran` VALUES (11855,2,71,20,'<p><span>[f]</span><span> “Dan, Allah telah menjadikan bagimu bumi sebagai hamparan,</span></p><p><span>[f]</span><span> 67:16 ; 78:7</span></p>');
INSERT INTO `quran` VALUES (11856,2,71,21,'<p><span>[a]</span><span> “Supaya kamu berjalan di jalan-jalannya yang luas.”</span></p><p><span>[a]</span><span> 67:16.</span></p>');
INSERT INTO `quran` VALUES (11857,2,71,22,'<p><span>R. 2</span></p><p><span>Nuh berkata, “Hai, Tuhan-ku ! Mereka sesungguhnya telah mendurhakai aku, dan mengikuti orang-orang yang tidak menambah kepadanya hartanya dan keturunannya selain kerugian.</span></p>');
INSERT INTO `quran` VALUES (11858,2,71,23,'<p><span>“Dan, mereka telah merencanakan tipu daya yang besar,</span></p>');
INSERT INTO `quran` VALUES (11859,2,71,24,'<p><span>“Dan, mereka berkata </span><span>kepada kaumnya,</span><span> </span><span>[b]</span><span> </span><span>‘</span><span> Janganlah sekali-kali meninggalkan tuhan-tuhan-mu, dan janganlah sekali-kali meninggalkan Wadd dan jangan pula Suwa</span><span>’</span><span>, dan jalan pula Yaghuts dan Ya</span><span>’</span><span>uq dan Nasr</span><span>’</span><span>, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3134">3134</a><span>]</span></p><p><span>[b]</span><span> 38:7.</span></p>');
INSERT INTO `quran` VALUES (11860,2,71,25,'<p><span>[a]</span><span> “Dan, sesungguhnya, mereka telah menyesatkan banyak orang ; dan Engkau tidak akan menambah orang-orang aniaya kecuali kesesatan.”</span></p><p><span>[a]</span><span> 14:37.</span></p>');
INSERT INTO `quran` VALUES (11861,2,71,26,'<p><span>[b]</span><span> Disebabkan dosa-dosa mereka, mereka ditenggelamkan dan dimasukkan ke dalam Api, dan mereka tidak mendapati bagi mereka penolong-penolong selain Allah.</span></p><p><span>[b]</span><span> 21:78 ; 26:121 ; 37:83.</span></p>');
INSERT INTO `quran` VALUES (11862,2,71,27,'<p><span>Dan Nuh berkata, “Hai Tuhan-ku, janganlah Engkau membiarkan di atas bumi penghuni dari orang-orang kafir.</span></p>');
INSERT INTO `quran` VALUES (11863,2,71,28,'<p><span>“Sesungguhnya, jika Engkau membiarkan juga mereka, mereka akan menyesatkan hamba-hamba Engkau dan mereka tidak akan melahirkan melainkan orang-orang berdosa, kafir.</span></p>');
INSERT INTO `quran` VALUES (11864,2,71,29,'<p><span>[c]</span><span> “Hai, Tuhan-ku ! Ampunilah aku dan ibu-bapakku, dan bagi yang memasuki rumahku sebagai orang mukmin, dan bagi mukmin laki-laki dan mukmin perempuan. Dan Engkau tidak menambahkan kepada orang-orang aniaya kecuali kebinasaan.” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3135">3135</a><span>]</span></p><p><span>[c]</span><span> 14:42.</span></p>');
INSERT INTO `quran` VALUES (11865,2,72,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11866,2,72,2,'<p><span>Katakanlah, “Telah diwahyukan kepadaku, bahwa serombongan jin </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3126">3126</a><span>]</span><span> mendengar </span><span>Alquran</span><span>,” maka mereka berkata, “Sesungguhnya, kami telah mendengar Alquran yang </span><span>[b]</span><span> ajaib sekali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3137">3137</a><span>]</span></p><p><span>[b]</span><span> 46:31.</span></p>');
INSERT INTO `quran` VALUES (11867,2,72,3,'<p><span>[c]</span><span> “</span><span>Alquran </span><span> itu memberi petunjuk kepada kebebaran ; maka kami telah beriman kepadanya. Dan kami sekali-kali tidak akan menyekutukan seseorang dengan Tuhan kami, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3138">3138</a><span>]</span></p><p><span>[c]</span><span> 46:32.</span></p>');
INSERT INTO `quran` VALUES (11868,2,72,4,'<p><span>“Dan, sesungguhnya Maha Luhur Keagungan Tuhan kami. </span><span>[d]</span><span> Dia tidak beristri dan tidak pula beranak,</span></p><p><span>[d]</span><span> 17:112 ; 18:5 ; 25:3.</span></p>');
INSERT INTO `quran` VALUES (11869,2,72,5,'<p><span>“Dan sesungguhnya orang-orang bodoh di antara kami berkata dusta berlebihan terhadap Allah,</span></p>');
INSERT INTO `quran` VALUES (11870,2,72,6,'<p><span>“Dan sesungguhnya, kami menyangka manusia dan jin sekali-kali tidak akan berkata dusta terhadap Allah,</span></p>');
INSERT INTO `quran` VALUES (11871,2,72,7,'<p><span>[a]</span><span> “Dan, sesungguhnya ada beberapa orang dari manusia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3139">3139</a><span>]</span><span> yang meminta perlindungan kepada beberapa orang dari jin, maka dengan demikian meningkatkan mereka dalam kesombongan,</span></p><p><span>[a]</span><span> 6:129.</span></p>');
INSERT INTO `quran` VALUES (11872,2,72,8,'<p><span>“Dan, sesungguhnya mereka yakin, sebagaimana kamu juga yakin, bahwa sekali-kali Allah tidak akan membangkitkan seorang </span><span>rasul,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3140">3140</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11873,2,72,9,'<p><span>“Dan, sesungguhnya, kami telah berusaha menyentuh langit, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3141">3141</a><span>]</span><span> namun kami mendapatkannya penuh dengan para penjaga yang kuat dan </span><span>[b]</span><span> nyala api.</span></p><p><span>[b]</span><span> 15:17 </span><span>–</span><span> 19 ; 37:7 </span><span>–</span><span> 9.</span></p>');
INSERT INTO `quran` VALUES (11874,2,72,10,'<p><span>“Dan sesungguhnya kami biasa menduduki beberapa tempat duduknya untuk mendengarkan. Tetapi barangsiapa sekarang berusaha mendengarkan, niscaya ia akan mendapatkan disana bintang menyala yang mengintai, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3142">3142</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11875,2,72,11,'<p><span>“Dan, sesungguhnya kami tidak mengetahui apakah keburukan dikehendaki untuk orang di bumi, atau apakah Tuhan mereka menghendaki petunjuk kepada mereka,</span></p>');
INSERT INTO `quran` VALUES (11876,2,72,12,'<p><span>“Dan, sesungguhnya di antara kami sebagaian ada orang-orang shaleh dan sebagian dari kami sebaliknya. Kami mengikuti jalan-jalan yang berbeda,</span></p>');
INSERT INTO `quran` VALUES (11877,2,72,13,'<p><span>“Dan, sesungguhnya kami yakin bahwa </span><span>[a]</span><span> kami pasti tidak dapat menggagalkan Allah di bumi, dan kami tidak akan dapat menggagalkannya dengan melarikan diri.</span></p><p><span>[a]</span><span> 55:34.</span></p>');
INSERT INTO `quran` VALUES (11878,2,72,14,'<p><span>[a]</span><span> “Dan sesungguhnya ketika kami mendengar petunjuk, kami beriman kepadanya. Dan barangsiapa beriman kepada Tuhan-nya, maka ia tidak takut kerugian dan tidak </span><span>pula </span><span> takut keaniayaan,</span></p><p><span>[a]</span><span> 46:32.</span></p>');
INSERT INTO `quran` VALUES (11879,2,72,15,'<p><span>“Dan di antara kami sebagian ada yang menyerahkan diri dan sebagian dari kami ada yang menyimpang dari kebenaran.” Dan, barangsiapa menyerahkan diri, maka mereka itulah yang mencari jalan lurus.”</span></p>');
INSERT INTO `quran` VALUES (11880,2,72,16,'<p><span>Dan, adapun orang-orang yang menyimpang dari kebenaran, maka mereka itu menjadi bahan bakar Jahannam,</span></p>');
INSERT INTO `quran` VALUES (11881,2,72,17,'<p><span>Dan sekiranya mereka, </span><span>kaum kafir Mekkah,</span><span> tetap teguh di atas jalan </span><span>lurus,</span><span> niscaya Kami akan memberi mereka minum air yang berlimpah-limpah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3143">3143</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11882,2,72,18,'<p><span>Supaya Kami menguji mereka didalamnya. </span><span>[b]</span><span> Dan barangsiapa berpaling dari mengingat Tuhan-nya, Dia akan menghalaunya kepada azab yang terus berkobar.</span></p><p><span>[b]</span><span> 20:101 ; 43:37.</span></p>');
INSERT INTO `quran` VALUES (11883,2,72,19,'<p><span>[c]</span><span> Dan sesungguhnya masjid-masjid itu kepunyaan Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3144">3144</a><span>]</span><span> maka janganlah kamu menyeru kepada siapa pun selain Allah,</span></p><p><span>[c]</span><span> 2:115 ; 22:41.</span></p>');
INSERT INTO `quran` VALUES (11884,2,72,20,'<p><span>[a]</span><span> Dan sesungguhnya apabila hamba Allah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3145">3145</a><span>]</span><span> berdiri untuk berdo</span><span>’</span><span>a kepada-Nya, mereka mengililinginya sehingga hampir mencekiknya.</span></p><p><span>[a]</span><span> 96:10 </span><span>–</span><span> 11.</span></p>');
INSERT INTO `quran` VALUES (11885,2,72,21,'<p><span>R. 2</span></p><p><span>Katakanlah, “Sesungguhnya aku hanya berdo</span><span>’</span><span>a kepada Tuhan-ku, </span><span>[b]</span><span> dan aku tidak menyekutukan seorang pun dengan Dia.”</span></p><p><span>[b]</span><span> 13:37 ; 16:29.</span></p>');
INSERT INTO `quran` VALUES (11886,2,72,22,'<p><span>Katakanlah, “Sesungguhnya aku tidak berkuasa untuk mendatang kemudaratan kepadamu dan tidak pula petunjuk.”</span></p>');
INSERT INTO `quran` VALUES (11887,2,72,23,'<p><span>Katakanlah, “Sesungguhnya, tidak ada seorang pun dapat melindungi aku dari </span><span>azab</span><span> Allah, </span><span>[c]</span><span> dan sekali-kali aku tidak akan mendapat tempat berlindung selain Dia,</span></p><p><span>[c]</span><span> 18:28.</span></p>');
INSERT INTO `quran` VALUES (11888,2,72,24,'<p><span>“</span><span>Tanggung jawabku</span><span> hanya menyampaikan </span><span>apa yang diwahyukan </span><span> dari Allah dan Risalat-Nya.” Dan, barangsiapa mendurhakai Allah dan Rasul-Nya, niscaya baginya ada Api Jahannam ; mereka akan menetap di dalamnya untuk masa yang panjang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3146">3146</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11889,2,72,25,'<p><span>[a]</span><span> Sehingga apabila mereka melihatat apa yang telah dijanjikan </span><span>azab</span><span> kepada mereka, maka segera mereka akan mengetahui siapakah yang lebih lemah penolong</span><span>nya</span><span> dan lebih sedikit jumlah</span><span>nya</span><span>.</span></p><p><span>[a]</span><span> 19:76.</span></p>');
INSERT INTO `quran` VALUES (11890,2,72,26,'<p><span>[b]</span><span> Katakanlah, “Aku tidak mengetahui apakah yang dijanjikan kepadamu itu telah dekat, atau apakah Tuhan-ku telah menetapkan baginya masa yang panjang.”</span></p><p><span>[b]</span><span> 21:110.</span></p>');
INSERT INTO `quran` VALUES (11891,2,72,27,'<p><span>Dia-lah Yang mengetahui yang gaib ; maka Dia tidak menzahirkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3147">3147</a><span>]</span><span> rahasia gaib-Nya kepada siapa pun,</span></p>');
INSERT INTO `quran` VALUES (11892,2,72,28,'<p><span>Kecuali kepada Rasul yang Dia ridhai, maka sesungguhnya barisan pengawal berjalan di hadapannya dan di belakangnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3148">3148</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11893,2,72,29,'<p><span>Supaya Dia mengetahui, bahwa sesungguhnya mereka telah menyampaikan Amanat- amanat Tuhan Mereka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3149">3149</a><span>]</span><span> dan Dia meliputi semua yang ada pada mereka dan Dia membuat perhitungan tentang segala sesuatu.</span></p>');
INSERT INTO `quran` VALUES (11894,2,73,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11895,2,73,2,'<p><span>Wahai, orang yang berselimut, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3150">3150</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11896,2,73,3,'<p><span>Berdirilah </span><span>untuk</span><span> shalat waktu malam, kecuali sedikit,</span></p>');
INSERT INTO `quran` VALUES (11897,2,73,4,'<p><span>Setengahnya, atau kurangi sedikit darinya,</span></p>');
INSERT INTO `quran` VALUES (11898,2,73,5,'<p><span>Atau tambakan atasnya dan </span><span>[a]</span><span> bacalah Alquran dengan pembacaan yang baik.</span></p><p><span>[a]</span><span> 17:107 ; 25:33.</span></p>');
INSERT INTO `quran` VALUES (11899,2,73,6,'<p><span>Sesungguhnya, Kami akan segera melimpahkan kepada engkau, firman yang berbobot. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3151">3151</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11900,2,73,7,'<p><span>Sesungguhnya, bangun di waktu malam </span><span>untuk shalat</span><span> adalah lebih kuat untuk menguasai </span><span>diri</span><span> dan lebih ampuh berbicara. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3152">3152</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11901,2,73,8,'<p><span>Sesungguhnya bagi engkau di waktu siang mempunyai kesibukan </span><span>yang </span><span> panjang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3153">3153</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11902,2,73,9,'<p><span>Maka ingatlah selalu nama Tuhan engkau dan baktikanlah dirimu kepada-Nya dengan sepenuh kebaktian.</span></p>');
INSERT INTO `quran` VALUES (11903,2,73,10,'<p><span>[a]</span><span> </span><span>Dia-lah</span><span> Tuhan timur dan barat ; tiada tuhan melainkan Dia ; maka jadikanlah Dia sebagai Pelindung.</span></p><p><span>[a]</span><span> 26:29 ; 37:6.</span></p>');
INSERT INTO `quran` VALUES (11904,2,73,11,'<p><span>Dan bersabarlah atas apa yang mereka katakan ; dan jauhilah mereka dengan cara yang baik.</span></p>');
INSERT INTO `quran` VALUES (11905,2,73,12,'<p><span>[b]</span><span> Dan tinggalkanlah Aku dan orang-orang yang mendustakan yang memiliki nikmat kemewahan, dan berilah mereka tenggang waktu sedikit.</span></p><p><span>[b]</span><span> 68:45 ; 74:12.</span></p>');
INSERT INTO `quran` VALUES (11906,2,73,13,'<p><span>Sesungguhnya, pada Kami tersedia belenggu-belenggu dan Api yang menyala-nyala.</span></p>');
INSERT INTO `quran` VALUES (11907,2,73,14,'<p><span>Dan makanan yang menyumbat kerongkongan, dan azab yang pedih.</span></p>');
INSERT INTO `quran` VALUES (11908,2,73,15,'<p><span>Pada hari bergoncang bumi </span><span>[c]</span><span> dan gunung-gunung, dan jadilah gunung-gunung seperti bukit pasir yang longsor.</span></p><p><span>[c]</span><span> 56:5 </span><span>–</span><span> 6 ; 79:7.</span></p>');
INSERT INTO `quran` VALUES (11909,2,73,16,'<p><span>Sesungguhnya, Kami telah mengirimkan kepada kamu seorang rasul, </span><span>[a]</span><span> yang menjadi saksi atasmu, sebagaimana Kami telah mengirimkan seorang rasul kepada Firaun, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3154">3154</a><span>]</span></p><p><span>[a]</span><span> 33:46 ; 48:9.</span></p>');
INSERT INTO `quran` VALUES (11910,2,73,17,'<p><span>Tetapi, Firaun mendurhakai rasul itu, </span><span>[b]</span><span> maka Kami menyergap dia dengan sergapan yang sangat dahsyat.</span></p><p><span>[b]</span><span> 20:79 ; 26:67 ; 28:41.</span></p>');
INSERT INTO `quran` VALUES (11911,2,73,18,'<p><span>Lalu, bagaimanakah kamu akan memelihara dirimu jika kamu ingkar terhadap hari yang akan menjadikan anak-anak beruban ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3155">3155</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11912,2,73,19,'<p><span>[c]</span><span> Ketika langit pecah pada hari itu ; dan janji-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3156">3156</a><span>]</span><span> menjadi sempurna.</span></p><p><span>[c]</span><span> 82:2.</span></p>');
INSERT INTO `quran` VALUES (11913,2,73,20,'<p><span>Sesungguhnya, ini </span><span>Alquran</span><span> adalah suatu </span><span>[d]</span><span> peringatan, maka barangsiapa yang menghendaki, dia akan mengambil jalan kepada Tuhan-nya.</span></p><p><span>[d]</span><span> 20:4 ; 74:55 ; 76:30 ; 80:12.</span></p>');
INSERT INTO `quran` VALUES (11914,2,73,21,'<p><span>R. 2</span></p><p><span>Sesungguhnya, Tuhan engkau mengetahui bahwa </span><span>[a]</span><span> engkau berdiri </span><span>untuk shalat </span><span> hampir dua pertiga malam, dan </span><span>kadangkala </span><span> setengahnya atau sepertiganya </span><span>[b]</span><span> dan begitu pun segolongan orang yang beserta engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3157">3157</a><span>]</span><span> Dan Allah menetapkan ukuran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3158">3158</a><span>]</span><span> malam dan siang. Dia mengetahui bahwa kamu tidak dapat mengukur waktu dengan cermat, maka Dia mengasihani atas kamu, maka bacalah apa yang mudah dari Alquran. Dia mengetahui bahwa akan ada dari antara kamu yang sakit, dan beberapa lainnya yang sedang bepergian di bumi mencari karunia Allah, dan beberapa lainnya lagi berperang di jalan Allah, maka bacalah kamu apa yang mudah dari </span><span>Alquran</span><span> itu, dan dirikanlah shalat, dan bayarlah zakat </span><span>[c]</span><span> dan pinjamkanlah kepada Allah pinjaman yang baik. Dan apa yang kamu dahulukan untuk dirimu dari kebaikan, kamu akan mendapatkannya di sisi Allah, itu lebih baik dan lebih besar ganjaran-</span><span>nya.</span><span> Dan mohonlah ampunan kepada Allah. Sesungguhnya, Allah Maha Pengampun, Maha Penyayang.</span></p><p><span>[a]</span><span> 26:219.</span></p><p><span>[b]</span><span> 25:65 ; 41:39.</span></p><p><span>[c]</span><span> 2:246 ; 57:12 ; 64:18.</span></p>');
INSERT INTO `quran` VALUES (11915,2,74,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11916,2,74,2,'<p><span>Wahai orang yang memakai jubah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3159">3159</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11917,2,74,3,'<p><span>Bangkitlah dan peringatkanlah,</span></p>');
INSERT INTO `quran` VALUES (11918,2,74,4,'<p><span>Dan Tuhan engkau hendaknya diagungkan.</span></p>');
INSERT INTO `quran` VALUES (11919,2,74,5,'<p><span>Dan sucikanlah orang-orang yang tinggal bersama engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3160">3160</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11920,2,74,6,'<p><span>Dan syirik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3161">3161</a><span>]</span><span> hendaknya dihapuskan,</span></p>');
INSERT INTO `quran` VALUES (11921,2,74,7,'<p><span>Dan janganlah engkau melakukan kebaikan </span><span>dengan niat </span><span> meraih keuntungan lebih banyak.</span></p>');
INSERT INTO `quran` VALUES (11922,2,74,8,'<p><span>Dan untuk Tuhan engkau, maka bersabarlah.</span></p>');
INSERT INTO `quran` VALUES (11923,2,74,9,'<p><span>[a]</span><span> Dan, apabila nafiri ditiup, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3162">3162</a><span>]</span><span> </span></p><p><span>[a] </span><span>23:102 ; 50:21 ; 69:14.</span></p>');
INSERT INTO `quran` VALUES (11924,2,74,10,'<p><span>[b]</span><span> Maka hari itu adalah hari yang sulit. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3163">3163</a><span>]</span></p><p><span>[b] </span><span>25:27.</span></p>');
INSERT INTO `quran` VALUES (11925,2,74,11,'<p><span>Bagi orang-orang kafir tidak akan mudah.</span></p>');
INSERT INTO `quran` VALUES (11926,2,74,12,'<p><span>[c]</span><span> Biarlah Aku </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3164">3164</a><span>] </span><span>berurusan</span><span> dengan orang yang telah Aku ciptakan Sendiri.</span></p><p><span>[c]</span><span> 68:45 ; 73:12.</span></p>');
INSERT INTO `quran` VALUES (11927,2,74,13,'<p><span>[a]</span><span> Dan Aku jadikan baginya harta berlimpah-limpah,</span></p><p><span>[a]</span><span> 68:15.</span></p>');
INSERT INTO `quran` VALUES (11928,2,74,14,'<p><span>[b]</span><span> Dan anak-anak yang hadir </span><span>bersamanya,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3165">3165</a><span>]</span></p><p><span>[b]</span><span> 68:15.</span></p>');
INSERT INTO `quran` VALUES (11929,2,74,15,'<p><span>Dan Aku mudahkan baginya semudah-mudahnya.</span></p>');
INSERT INTO `quran` VALUES (11930,2,74,16,'<p><span>Kemudian ia ingin sekali supaya Aku menambah</span><span>nya.</span></p>');
INSERT INTO `quran` VALUES (11931,2,74,17,'<p><span>Sekali-kali tidak ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3166">3166</a><span>]</span><span> Karena, sesungguhnya dia menentang Tanda-tanda Kami.</span></p>');
INSERT INTO `quran` VALUES (11932,2,74,18,'<p><span>Aku akan menimpakan kepadanya </span><span>azab </span><span> yang terus meningkat.</span></p>');
INSERT INTO `quran` VALUES (11933,2,74,19,'<p><span>Sesungguhnya, ia memikirkan dan menetapkan.</span></p>');
INSERT INTO `quran` VALUES (11934,2,74,20,'<p><span>Maka kebinasaan menyergapnya. Bagaimana ia telah menetapkan.</span></p>');
INSERT INTO `quran` VALUES (11935,2,74,21,'<p><span>Kemudian kebinasaan menyergapnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3167">3167</a><span>]</span><span> Bagaimana ia telah menetapkan.</span></p>');
INSERT INTO `quran` VALUES (11936,2,74,22,'<p><span>Kemudian ia memandang.</span></p>');
INSERT INTO `quran` VALUES (11937,2,74,23,'<p><span>[a]</span><span> Kemudian ia bermasam muka dan merengut, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3168">3168</a><span>]</span></p><p><span>[a]</span><span> 80:2.</span></p>');
INSERT INTO `quran` VALUES (11938,2,74,24,'<p><span>Kemudian ia berpaling dan menyombongkan diri,</span></p>');
INSERT INTO `quran` VALUES (11939,2,74,25,'<p><span>Lalu ia berkata, </span><span>[b]</span><span> “Tidaklah ini melainkan sihir yang diwariskan,</span></p><p><span>[b]</span><span> 34:44 ; 37:16.</span></p>');
INSERT INTO `quran` VALUES (11940,2,74,26,'<p><span>“Ini bukan apa-apa melainkan perkataan manusia.”</span></p>');
INSERT INTO `quran` VALUES (11941,2,74,27,'<p><span>Segera Aku memasukkannya ke </span><span>neraka </span><span> </span><span>[c]</span><span> Saqar.</span></p><p><span>[c]</span><span> 37:24.</span></p>');
INSERT INTO `quran` VALUES (11942,2,74,28,'<p><span>Dan apakah yang engkau ketahui apa Saqar itu ? </span></p>');
INSERT INTO `quran` VALUES (11943,2,74,29,'<p><span>Tidak ada yang dia sisakan dan tidak ada yang dia tinggalkan.</span></p>');
INSERT INTO `quran` VALUES (11944,2,74,30,'<p><span>[d]</span><span> Api itu menghanguskan bagi kulit manusia.</span></p><p><span>[d]</span><span> 70:17.</span></p>');
INSERT INTO `quran` VALUES (11945,2,74,31,'<p><span>Di atasnya ada sembilan belas </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3169">3169</a><span>]</span><span> </span><span>malaikat.</span></p>');
INSERT INTO `quran` VALUES (11946,2,74,32,'<p><span>Dan tidaklah Kami jadikan penjaga-penjaga Api selain malaikat, dan Kami tidak menetapkan bilangan mereka kecuali sebagai cobaan bagi orang-orang yang ingkar, supaya yakin orang-orang yang telah diberi Kitab dan bertambah keimanan orang-orang yang beriman, dan janganlah ragu-ragu orang-orang yang diberi Kitab dan orang-orang Mukmin, dan supaya berkata orang-orang yang dalam hati mereka ada penyakit dan orang-orang kafir, “Apakah yang dikehendaki Allah dengan </span><span>[a]</span><span> misal semacam ini ? “ Demikianlah Allah membiarkan sesat siapa yang Dia kehendaki, dan Dia memberi petunjuk kepada siapa yang Dia kehendaki . </span><span>[b]</span><span> Dan tidak ada yang mengetahui lasykar-lasykar Tuhan engkau selain Dia. Dan tidaklah </span><span>Alquran</span><span> itu melainkan nasihat bagi manusia.</span></p><p><span>[a]</span><span> 2:27.</span></p><p><span>[b]</span><span> 33:10 ; 48:5.</span></p>');
INSERT INTO `quran` VALUES (11947,2,74,33,'<p><span>R. 2</span></p><p><span>Sekali-kali tidak, demi bulan,</span></p>');
INSERT INTO `quran` VALUES (11948,2,74,34,'<p><span>Dan demi malam ketika telah berlalu,</span></p>');
INSERT INTO `quran` VALUES (11949,2,74,35,'<p><span>[c]</span><span> Dan demi subuh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3170">3170</a><span>]</span><span> ketika mulai bersinar,</span></p><p><span>[c]</span><span> 81:19.</span></p>');
INSERT INTO `quran` VALUES (11950,2,74,36,'<p><span>[d]</span><span> Sesungguhnya, itu adalah salah satu </span><span>bencana </span><span> yang amat besar.</span></p><p><span>[d]</span><span> 79:35.</span></p>');
INSERT INTO `quran` VALUES (11951,2,74,37,'<p><span>Suatu peringatan bagi manusia,</span></p>');
INSERT INTO `quran` VALUES (11952,2,74,38,'<p><span>Bagi siapa di antara kamu yang menghendaki maju atau mundur.</span></p>');
INSERT INTO `quran` VALUES (11953,2,74,39,'<p><span>[a]</span><span> Setiap jiwa jaminan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3171">3171</a><span>]</span><span> bagi apa yang dia usahakan.</span></p><p><span>[a] </span><span>14:52 ; 40:18 ; 45:23.</span></p>');
INSERT INTO `quran` VALUES (11954,2,74,40,'<p><span>Kecuali </span><span>[b]</span><span> orang-orang sebelah kanan.</span></p><p><span>[b]</span><span> 56:28 ; 90:19.</span></p>');
INSERT INTO `quran` VALUES (11955,2,74,41,'<p><span>Di dalam surga-surga. Mereka saling bertanya.</span></p>');
INSERT INTO `quran` VALUES (11956,2,74,42,'<p><span>Tentang orang-orang berdosa.</span></p>');
INSERT INTO `quran` VALUES (11957,2,74,43,'<p><span>“Apakah yang menyebabkan kamu masuk ke dalam neraka ?”</span></p>');
INSERT INTO `quran` VALUES (11958,2,74,44,'<p><span>Mereka menjawab, </span><span>[c]</span><span> “Kami tidak termasuk orang-orang yang mengerjakan shalat,</span></p><p><span>[c]</span><span> 75:32.</span></p>');
INSERT INTO `quran` VALUES (11959,2,74,45,'<p><span>[d]</span><span> “Dan kami tidak memberi makan orang-orang miskin,</span></p><p><span>[d]</span><span> 69:35 ; 89:19 ; 107:4.</span></p>');
INSERT INTO `quran` VALUES (11960,2,74,46,'<p><span>“Dan kami berbicara kosong bersama orang-orang berbicara kosong.</span></p>');
INSERT INTO `quran` VALUES (11961,2,74,47,'<p><span>“Dan kami selalu mendustakan </span><span>[e]</span><span> hari pembalasan.</span></p><p><span>[e]</span><span> 75:33.</span></p>');
INSERT INTO `quran` VALUES (11962,2,74,48,'<p><span>[f]</span><span> “Hingga datang kepada kami kematian,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3172">3172</a><span>]</span></p><p><span>[f]</span><span> 15:100.</span></p>');
INSERT INTO `quran` VALUES (11963,2,74,49,'<p><span>Maka tidak berguna kepada mereka </span><span>[a]</span><span> syafaat dari orang-orang yang memberi syafaat.</span></p><p><span>[a]</span><span> 20:110 ; 34:24.</span></p>');
INSERT INTO `quran` VALUES (11964,2,74,50,'<p><span>Maka apakah yang terjadi dengan mereka hingga mereka berpaling dari nasihat,</span></p>');
INSERT INTO `quran` VALUES (11965,2,74,51,'<p><span>Seolah-olah mereka itu keledai-keledai yang ketakutan,</span></p>');
INSERT INTO `quran` VALUES (11966,2,74,52,'<p><span>Lari dari singa ?</span></p>');
INSERT INTO `quran` VALUES (11967,2,74,53,'<p><span>Bahkan, setiap orang dari mereka menghendaki supaya dia diberi lembaran- lembaran terbuka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3173">3173</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11968,2,74,54,'<p><span>Sekali-kali tidak ! Sesungguhnya mereka tidak takut pada akhirat.</span></p>');
INSERT INTO `quran` VALUES (11969,2,74,55,'<p><span>Sekali-kali tidak ! Sesungguhnya </span><span>Alquran</span><span> itu adalah nasihat.</span></p>');
INSERT INTO `quran` VALUES (11970,2,74,56,'<p><span>Maka barangsiapa menghendaki, hendakalah ia memperhatikannya.</span></p>');
INSERT INTO `quran` VALUES (11971,2,74,57,'<p><span>Dan mereka tidak akan memperhatikan kecuali jika Allah </span><span>[b]</span><span> menghendaki. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3174">3174</a><span>]</span><span> Dia memberi ketakwaan dan Dia memberi ampunan.</span></p><p><span>[b]</span><span> 76:31 ; 84:30.</span></p>');
INSERT INTO `quran` VALUES (11972,2,75,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3175">3175</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (11973,2,75,2,'<p><span>Aku bersumpah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3176">3176</a><span>]</span><span> dengan Hari Kiamat,</span></p>');
INSERT INTO `quran` VALUES (11974,2,75,3,'<p><span>Dan Aku bersumpah dengan jiwa yang menyesali, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3177">3177</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11975,2,75,4,'<p><span>Apakah manusia menyangka bahwa Kami tidak akan </span><span>[b]</span><span> mengumpulkan tulang-tulang nya ?</span></p><p><span>[b]</span><span> 23:83 ; 37:54 ; 56:48 ; 79:11 </span><span>–</span><span> 13.</span></p>');
INSERT INTO `quran` VALUES (11976,2,75,5,'<p><span>Mengapa tidak, sebenarnya Kami Yang kuasa menyusun kembali jari-jarinya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3178">3178</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11977,2,75,6,'<p><span>Namun, manusia ingin supaya berbuat buruk terus-menerus.</span></p>');
INSERT INTO `quran` VALUES (11978,2,75,7,'<p><span>Ia </span><span>[a]</span><span> bertanya, “Kapankah Hari Kiamat itu ? “</span></p><p><span>[a]</span><span> 78:2 ; 79:43.</span></p>');
INSERT INTO `quran` VALUES (11979,2,75,8,'<p><span>Maka apabila penglihatan silau,</span></p>');
INSERT INTO `quran` VALUES (11980,2,75,9,'<p><span>Dan terjadi gerhana bulan,</span></p>');
INSERT INTO `quran` VALUES (11981,2,75,10,'<p><span>Dan dikumpulkan matahari dan bulan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3179">3179</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11982,2,75,11,'<p><span>Akan berkata manusia pada hari itu, “Kemanakah </span><span>[b]</span><span> tempat berlari ? “</span></p><p><span>[b]</span><span> 80:35.</span></p>');
INSERT INTO `quran` VALUES (11983,2,75,12,'<p><span>Tidak ada tempat perlindungan </span><span>dari azab.</span></p>');
INSERT INTO `quran` VALUES (11984,2,75,13,'<p><span>Pada Tuhan engkaulah pada hari itu tempat istirahat.</span></p>');
INSERT INTO `quran` VALUES (11985,2,75,14,'<p><span>Akan diberitahukan kepada manusia pada hari itu tentang apa yang dia dahulukan dan dia belakangkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3180">3180</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11986,2,75,15,'<p><span>Bahkan manusia terhadap dirinya menjadi saksi.</span></p>');
INSERT INTO `quran` VALUES (11987,2,75,16,'<p><span>Walaupun ia mengemukakan alasan-alasannya.</span></p>');
INSERT INTO `quran` VALUES (11988,2,75,17,'<p><span>Janganlah engkau gerakkan lidahmu </span><span>hai nabi,</span><span> supaya </span><span>Alquran</span><span> cepat turun.</span></p>');
INSERT INTO `quran` VALUES (11989,2,75,18,'<p><span>[a]</span><span> Sesungguhnya, tanggungjawab Kami mengumpulkannya dan membacakannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3181">3181</a><span>]</span></p><p><span>[a]</span><span> 15:10.</span></p>');
INSERT INTO `quran` VALUES (11990,2,75,19,'<p><span>Maka apabila Kami membacakannya, kemudian engkau ikutilah bacaannya.</span></p>');
INSERT INTO `quran` VALUES (11991,2,75,20,'<p><span>Kemudian, sesungguhnya tanggung-jawab Kami menjelaskannya.</span></p>');
INSERT INTO `quran` VALUES (11992,2,75,21,'<p><span>Sekali-kali tidak, bahkan </span><span>[a] </span><span>kamu mencintai kehidupan dunia,</span></p><p><span>[a]</span><span> 87:17.</span></p>');
INSERT INTO `quran` VALUES (11993,2,75,22,'<p><span>Dan kamu mengabaikan akhirat.</span></p>');
INSERT INTO `quran` VALUES (11994,2,75,23,'<p><span>[b]</span><span> wajah-wajah pada hari itu berseri-seri.</span></p><p><span>[b]</span><span> 88:9.</span></p>');
INSERT INTO `quran` VALUES (11995,2,75,24,'<p><span>Kepada Tuhan-nya mereka memandang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3182">3182</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11996,2,75,25,'<p><span>[c]</span><span> Dan wajah-wajah pada hari itu bermuram,</span></p><p><span>[c]</span><span> 68:44 ; 80:41 ; 88:3 </span><span>–</span><span> 4.</span></p>');
INSERT INTO `quran` VALUES (11997,2,75,26,'<p><span>Mereka mengira bahwa akan ditmpakan </span><span>azab </span><span> yang mematahkan tulang punggung. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3183">3183</a><span>]</span></p>');
INSERT INTO `quran` VALUES (11998,2,75,27,'<p><span>Sekali-kali tidak, </span><span>[a]</span><span> apabila </span><span>ruh</span><span> sudah sampai di tenggorokan,</span></p><p><span>[a]</span><span> 56:84.</span></p>');
INSERT INTO `quran` VALUES (11999,2,75,28,'<p><span>Dan dikatakan, “Siapakah tukang tenung </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3184">3184</a><span>]</span><span> </span><span>yang dapat menyelamatkannya ?</span><span> “</span></p>');
INSERT INTO `quran` VALUES (12000,2,75,29,'<p><span>Dan, ia yakin bahwa sesungguhnya itu saat perpisahaan,</span></p>');
INSERT INTO `quran` VALUES (12001,2,75,30,'<p><span>Dan, betis bertemu dengan betis </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3185">3185</a><span>]</span><span> yang lain </span><span>dalam sakratul-maut ;</span></p>');
INSERT INTO `quran` VALUES (12002,2,75,31,'<p><span>Kepada Tuhan engkaulah pada hari itu digiring.</span></p>');
INSERT INTO `quran` VALUES (12003,2,75,32,'<p><span>R. 2</span></p><p><span>Maka ia tidak membenarkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3186">3186</a><span>]</span><span> </span><span>kebenaran</span><span> dan </span><span>[b]</span><span> tidak shalat,</span></p><p><span>[b]</span><span> 74:44.</span></p>');
INSERT INTO `quran` VALUES (12004,2,75,33,'<p><span>[c]</span><span> Akan tetapi ia mendustakan </span><span>kebenaran</span><span> dan berpaling.</span></p><p><span>[c]</span><span> 74:47.</span></p>');
INSERT INTO `quran` VALUES (12005,2,75,34,'<p><span>Kemudian ia pergi kepada keluarganya dengan berlagak sombong.</span></p>');
INSERT INTO `quran` VALUES (12006,2,75,35,'<p><span>Celakalah bagimu, maka celakalah !</span></p>');
INSERT INTO `quran` VALUES (12007,2,75,36,'<p><span>Kemudian, celakalah bagimu, maka celakalah ! </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3187">3187</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12008,2,75,37,'<p><span>Apakah manusia menyangka bahwa ia dibiarkan tanpa tujuan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3188">3188</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12009,2,75,38,'<p><span>[a]</span><span> Bukankah ia dahulu setetes air mani yang dimasukkan ?</span></p><p><span>[a]</span><span> 18:38 ; 36:78 ; 80:20.</span></p>');
INSERT INTO `quran` VALUES (12010,2,75,39,'<p><span>[b]</span><span> Kemudian, ia menjadi segumpal darah, lalu Dia menciptakan dan menyempurna kannya,</span></p><p><span>[b]</span><span> 23:15 ; 40:68 ; 96:3.</span></p>');
INSERT INTO `quran` VALUES (12011,2,75,40,'<p><span>[c]</span><span> Kemudian, Dia menjadikannya berpasangan, lelaki dan perempuan.</span></p><p><span>[c]</span><span> 92:4.</span></p>');
INSERT INTO `quran` VALUES (12012,2,75,41,'<p><span>Bukankah Dia berkuasa </span><span>[d]</span><span> menghidupkan yang mati ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3189">3189</a><span>]</span></p><p><span>[d]</span><span> 17:51 </span><span>–</span><span> 52 ; 36:80 ; 46:34.</span></p>');
INSERT INTO `quran` VALUES (12013,2,76,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12014,2,76,2,'<p><span>Apakah tidak datang kepada manusia suatu waktu dari masa, ketika ia belum menjadi </span><span>[b]</span><span> sesuatu yang layak disebut ?</span></p><p><span>[b]</span><span> 19:68.</span></p>');
INSERT INTO `quran` VALUES (12015,2,76,3,'<p><span>[c]</span><span> Sesungguhnya Kami telah menciptakan manusia dari nutfah campuran </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3190">3190</a><span>]</span><span> supaya Kami dapat mengujinya ; maka Kami telah membuat dia mendengar, melihat.</span></p><p><span>[c]</span><span> 18:38 ; 35:12 ; 36:78 ; 40:68 ; 80:20</span></p>');
INSERT INTO `quran` VALUES (12016,2,76,4,'<p><span>[d]</span><span> Sesungguhnya Kami telah menunjukinya jalan, apakah ia bersyukur atau pun tidak bersyukur ?</span></p><p><span>[d]</span><span> 90:11.</span></p>');
INSERT INTO `quran` VALUES (12017,2,76,5,'<p><span>[e]</span><span> Sesungguhnya Kami telah menyediakan bagi orang-orang kafir rantai dan belenggu dan Api yang menyala-nyala. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3191">3191</a><span>]</span></p><p><span>[e]</span><span> 18:103 ; 29:69 ; 33:9 ; 48:14.</span></p>');
INSERT INTO `quran` VALUES (12018,2,76,6,'<p><span>Sesungguhnya orang-orang baik, mereka akan minum dari piala yang campurannya adalah kapur barus. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3192">3192</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12019,2,76,7,'<p><span>Mata air </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3192a">3192a</a><span>]</span><span> yang darinya akan minum hamba-hamba Allah, mereka memancarkan nya dengan pancaran yang deras.</span></p>');
INSERT INTO `quran` VALUES (12020,2,76,8,'<p><span>Mereka menyempurnakan nazar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3193">3193</a><span>]</span><span> dan takut pada suatu hari keburukannya tersebar luas.</span></p>');
INSERT INTO `quran` VALUES (12021,2,76,9,'<p><span>[a]</span><span> Dan mereka memberi makan karena cinta-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3194">3194</a><span>]</span><span> kepada orang miskin, anak yatim, dan tawanan ;</span></p><p><span>[a]</span><span> 90:15 </span><span>–</span><span> 17.</span></p>');
INSERT INTO `quran` VALUES (12022,2,76,10,'<p><span>Sesungguhnya kami memberi makan kepadamu karena mengharapkan keridhaan Allah. Kami tidak mengharapkan darimu balasan dan tidak pula terima kasih.</span></p>');
INSERT INTO `quran` VALUES (12023,2,76,11,'<p><span>Sesungguhnya kami takut </span><span>azab</span><span> dari Tuhan kami pada suatu hari yang muka menjadi masam </span><span>dan</span><span> penuh kesulitan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3195">3195</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12024,2,76,12,'<p><span>Maka Allah memelihara mereka dari keburukan hari itu, dan menganugerahkan kepada mereka kesenangan dan kebahagiaan.</span></p>');
INSERT INTO `quran` VALUES (12025,2,76,13,'<p><span>[a]</span><span> Dan, Dia akan membalas mereka disebabkan kesabaran mereka, dengan kebun dan sutera.</span></p><p><span>[a]</span><span> 22:24.</span></p>');
INSERT INTO `quran` VALUES (12026,2,76,14,'<p><span>[b]</span><span> Duduk bersandar di dalamnya atas dipan-dipan ; mereka tidak akan melihat di dalamnya </span><span>[c]</span><span> terik matahari dan tidak pula sangat dingin.</span></p><p><span>[b]</span><span> 18:32 ; 36:57 ; 83:24.</span></p><p><span>[c]</span><span> 20:120.</span></p>');
INSERT INTO `quran` VALUES (12027,2,76,15,'<p><span>Dan didekatkan atas mereka keteduhannya dan tandan-tandan buahnya direndah kan serendah-rendahnya.</span></p>');
INSERT INTO `quran` VALUES (12028,2,76,16,'<p><span>[a]</span><span> Dan diedarkan kepada mereka bejana-bejana dari perak dan kendi-kendi seperti kaca.</span></p><p><span>[a]</span><span> 43:72.</span></p>');
INSERT INTO `quran` VALUES (12029,2,76,17,'<p><span>Bejana-bejana seperti</span><span> kaca, </span><span>terbuat</span><span> dari perak, mereka mengukurnya sesuai dengan ukuran.</span></p>');
INSERT INTO `quran` VALUES (12030,2,76,18,'<p><span>Dan mereka diberi minuman di dalanya segelas yang campurannya jahe. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3196">3196</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12031,2,76,19,'<p><span>Dari mata air di dalamnya yang disebut Salsabil. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3197">3197</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12032,2,76,20,'<p><span>[a]</span><span> Dan mereka dikelilingi pemuda-pemuda yang tetap muda. Apabila engkau melihat mereka, engkau menyangka mereka itu mutiara-mutiara bertaburan.</span></p><p><span>[a]</span><span> 52:25 ; 56:18.</span></p>');
INSERT INTO `quran` VALUES (12033,2,76,21,'<p><span>Dan apabila engkau melihat, niscaya engkau akan melihat kenikmatan dan kerajaan besar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3198">3198</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12034,2,76,22,'<p><span>Pada mereka ada pakaian-pakaian dari </span><span>[b]</span><span> sutera halus hijau dan sutera tebal, dan mereka dipakaikan </span><span>[c]</span><span> gelang-gelang perak, dan Tuhan mereka memberi mereka minum-minuman murni. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3199">3199</a><span>]</span></p><p><span>[b]</span><span> 18:32 ; 44:54.</span></p><p><span>[c]</span><span> 18:32 ; 22:24 ; 35:34.</span></p>');
INSERT INTO `quran` VALUES (12035,2,76,23,'<p><span>[d]</span><span> Sesungguhnya ini adalah ganjaran bagimu, dan usahamu akan dihargai.</span></p><p><span>[d]</span><span> 32:18 ; 43:73.</span></p>');
INSERT INTO `quran` VALUES (12036,2,76,24,'<p><span>R. 2</span></p><p><span>Sesungguhnya Kami telah menurunkan Alquran kepada engkau dengan berangsur- angsur. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3200">3200</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12037,2,76,25,'<p><span>Maka sabarlah pada keputusan Tuhan engkau, dan janganlah taat kepada orang yang berdosa dari antara mereka atau yang tidak bersyukur.</span></p>');
INSERT INTO `quran` VALUES (12038,2,76,26,'<p><span>[a]</span><span> Dan ingatlah nama Tuhan engkau pada waktu pagi dan petang.</span></p><p><span>[a]</span><span> 3:42 ; 48:10.</span></p>');
INSERT INTO `quran` VALUES (12039,2,76,27,'<p><span>[b]</span><span> Dan, pada sebagaian malam bersujudlah kepada-Nya, dan sanjunglah Dia di malam yang panjang.</span></p><p><span>[b]</span><span> 17:80 ; 50:41 ; 52:50.</span></p>');
INSERT INTO `quran` VALUES (12040,2,76,28,'<p><span>[c]</span><span> Sesungguhnya mereka ini mencintai kehidupan dunia, dan mereka meninggalkan di belakang mereka hari yang sangat berat.</span></p><p><span>[c]</span><span> 17:19.</span></p>');
INSERT INTO `quran` VALUES (12041,2,76,29,'<p><span>Kami telah menciptakan mereka dan menguatkan segala bagian tubuh mereka, dan apabila Kami menghendaki, </span><span>[d]</span><span> Kami sungguh-sungguh menukar mereka dengan yang lain seperti mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3201">3201</a><span>]</span></p><p><span>[d]</span><span> 56:62.</span></p>');
INSERT INTO `quran` VALUES (12042,2,76,30,'<p><span>[a]</span><span> Sesungguhnya, ini adalah suatu peringatan, maka barangsiapa menghendaki, ia mengabil jalan kepada Tuhan-nya.</span></p><p><span>[a]</span><span> 73:20 ; 74:55 ; 80:12.</span></p>');
INSERT INTO `quran` VALUES (12043,2,76,31,'<p><span>[b]</span><span> Dan, kamu tidak menghendaki, kecuali Allah menghendaki. Sesungguhnya, Allah Maha Mengetahui, Maha Bijaksana.</span></p><p><span>[b]</span><span> 18:25 ; 74:57 ; 81:30.</span></p>');
INSERT INTO `quran` VALUES (12044,2,76,32,'<p><span>[c]</span><span> Dia memasukkan siapa yang Dia kehendaki </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3202">3202</a><span>]</span><span> dalam rahmat-Nya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3203">3203</a><span>]</span><span> dan orang-orang aniaya Dia sediakan bagi mereka azab yang pedih.</span></p><p><span>[c]</span><span> 48:26.</span></p>');
INSERT INTO `quran` VALUES (12045,2,77,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12046,2,77,2,'<p><span>Demi mereka yang diutus </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3204">3204</a><span>]</span><span> menyiarkan kebaikan.</span></p>');
INSERT INTO `quran` VALUES (12047,2,77,3,'<p><span>Kemudian mereka bergerak maju secepat-cepatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3205">3205</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12048,2,77,4,'<p><span>Demi mereka yang menyiarkan </span><span>kebenaran</span><span> sebaik-baiknya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3206">3206</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12049,2,77,5,'<p><span>Maka mereka membedakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3207">3207</a><span>]</span><span> </span><span>hak dan batil</span><span> dengan sejelas-jelasnya.</span></p>');
INSERT INTO `quran` VALUES (12050,2,77,6,'<p><span>Kemudian mereka menyampaikan peringatan </span><span>Allah.</span></p>');
INSERT INTO `quran` VALUES (12051,2,77,7,'<p><span>Sebagai alasan atau sebagai peringatan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3208">3208</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12052,2,77,8,'<p><span>[a]</span><span> Sesungguhnya, apa yang telah dijanjikan kepada kamu pasti akan terjadi.</span></p><p><span>[a]</span><span> 51:6.</span></p>');
INSERT INTO `quran` VALUES (12053,2,77,9,'<p><span>[b]</span><span> Maka apabila bintang-bintang telah pudar cahayanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3209">3209</a><span>]</span></p><p><span>[b]</span><span> 82:3.</span></p>');
INSERT INTO `quran` VALUES (12054,2,77,10,'<p><span>[c]</span><span> Dan apabila langit terbelah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3210">3210</a><span>]</span></p><p><span>[c]</span><span> 78:21 ; 82:2.</span></p>');
INSERT INTO `quran` VALUES (12055,2,77,11,'<p><span>Dan apabila gunung-gunung dihancurkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3211">3211</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12056,2,77,12,'<p><span>Dan apabila rasul-rasul </span><span>didatangkan</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3212">3212</a><span>]</span><span> pada waktu yang ditentukan.</span></p>');
INSERT INTO `quran` VALUES (12057,2,77,13,'<p><span>Hingga hari manakah ditangguhkan ?</span></p>');
INSERT INTO `quran` VALUES (12058,2,77,14,'<p><span>Hingga Hari Keputusan.</span></p>');
INSERT INTO `quran` VALUES (12059,2,77,15,'<p><span>Dan apa yang engkau ketahui tentang Hari Keputusan itu ?</span></p>');
INSERT INTO `quran` VALUES (12060,2,77,16,'<p><span>Celakalah pada hari itu bagi orang-orang yang mendustakan.</span></p>');
INSERT INTO `quran` VALUES (12061,2,77,17,'<p><span>Apakah Kami tidak membinasakan kaum-kaum dahulu ?</span></p>');
INSERT INTO `quran` VALUES (12062,2,77,18,'<p><span>[a]</span><span> Lalu Kami mengikutkan mereka orang-orang yang datang belakangan.</span></p><p><span>[a]</span><span> 6:134.</span></p>');
INSERT INTO `quran` VALUES (12063,2,77,19,'<p><span>Demikianlah Kami memperlakukan kepada orang-orang berdosa.</span></p>');
INSERT INTO `quran` VALUES (12064,2,77,20,'<p><span>Celakalah pada hari itu bagi orang-orang yang mendustakan.</span></p>');
INSERT INTO `quran` VALUES (12065,2,77,21,'<p><span>Bukankah Kami menciptakan kamu dari </span><span>[b]</span><span> air yang hina,</span></p><p><span>[b]</span><span> 32:9.</span></p>');
INSERT INTO `quran` VALUES (12066,2,77,22,'<p><span>Dan menempatkan pada tempat yang terpelihara,</span></p>');
