INSERT INTO `quran` VALUES (12072,2,77,28,'<p><span>[b]</span><span> Dan Kami jadikan di dalamnya gunung-gunung yang tinggi, dan Kami memberi minum kamu dengan air tawar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3215">3215</a><span>]</span></p><p><span>[b]</span><span> 13:4 ; 15:20 ; 32:32 ; 31:11.</span></p>');
