INSERT INTO `quran` VALUES (12076,2,77,32,'<p><span>Yang tidak memberi keteduhan dan tidak pula berguna melawan nyala api. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3217">3217</a><span>]</span></p>');
