INSERT INTO `quran` VALUES (12078,2,77,34,'<p><span>Seakan-akan mereka itu unta-unta berwarna kuning. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3218a">3218a</a><span>]</span></p>');
