INSERT INTO `quran` VALUES (12080,2,77,36,'<p><span>Inilah hari ketika mereka </span><span>[a]</span><span> tidak akan berkata-kata. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3219">3219</a><span>]</span></p><p><span>[a]</span><span> 78:39.</span></p>');
INSERT INTO `quran` VALUES (12081,2,77,37,'<p><span>[b]</span><span> Dan, tidak diizinkan bagi mereka mengemukakan alasan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3220">3220</a><span>]</span></p><p><span>[b]</span><span> 9:66 ; 66:8.</span></p>');
INSERT INTO `quran` VALUES (12082,2,77,38,'<p><span>Celakalah pada hari itu bagi orang-orang yang mendustakan.</span></p>');
INSERT INTO `quran` VALUES (12083,2,77,39,'<p><span>[c]</span><span> Inilah Hari keputusan, Kami mengumpulkan kamu dan bangsa-bangsa terdahulu.</span></p><p><span>[c]</span><span> 37:22.</span></p>');
INSERT INTO `quran` VALUES (12084,2,77,40,'<p><span>“Maka jika kamu mempunyai tipu daya, lakukanlah tipu daya itu terhadapku. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3221">3221</a><span>]</span></p>');
