INSERT INTO `quran` VALUES (12086,2,77,42,'<p><span>R. 2</span></p><p><span>Sesungguhnya, </span><span>[a]</span><span> orang-orang yang bertakwa berada di tempat-tempat teduh dan mata air-mata air.</span></p><p><span>[a]</span><span> 56:31.</span></p>');
INSERT INTO `quran` VALUES (12087,2,77,43,'<p><span>Dan </span><span>[b]</span><span> buah-buah, dari apa yang mereka inginkan.</span></p><p><span>[b]</span><span> 52:23 ; 55:53 ; 56:33.</span></p>');
INSERT INTO `quran` VALUES (12088,2,77,44,'<p><span>Makanlah dan minumlah dengan sepuas-puasnya karena apa yang telah kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (12089,2,77,45,'<p><span>Sesungguhnya, demikianlah Kami membalas mereka yang berbuat kebaikan.</span></p>');
INSERT INTO `quran` VALUES (12090,2,77,46,'<p><span>Celakalah pada hari itu bagi orang-orang yang mendustakan.</span></p>');
INSERT INTO `quran` VALUES (12091,2,77,47,'<p><span>[c]</span><span> Makan dan bersenang-senanglah sebentar, sesungguhnya kamu adalah orang- orang berdosa.</span></p><p><span>[c]</span><span> 14:31.</span></p>');
INSERT INTO `quran` VALUES (12092,2,77,48,'<p><span>Celakalah pada hari itu bagi orang-orang yang mendustakan.</span></p>');
INSERT INTO `quran` VALUES (12093,2,77,49,'<p><span>Dan apabila dikatakan kepada mereka, “Rukuklah kamu,” mereka tidak mau rukuk.</span></p>');
INSERT INTO `quran` VALUES (12094,2,77,50,'<p><span>Celakalah pada hari itu bagi orang-orang yang mendustakan.</span></p>');
