INSERT INTO `quran` VALUES (12096,2,78,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12097,2,78,2,'<p><span>Tentang apa mereka saling bertanya ?</span></p>');
