INSERT INTO `quran` VALUES (12099,2,78,4,'<p><span>Yang mereka di dalamnya berselisih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3224">3224</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12100,2,78,5,'<p><span>Sekali-kali tidak, </span><span>[a]</span><span> segera mereka akan mengetahui,</span></p><p><span>[a]</span><span> 102:4 </span><span>–</span><span> 5 ; 2:23 ; 20:54 ; 51:49.</span></p>');
INSERT INTO `quran` VALUES (12101,2,78,6,'<p><span>Kemudian sekali-kali tidak, mereka segera akan mengetahui.</span></p>');
INSERT INTO `quran` VALUES (12102,2,78,7,'<p><span>[b]</span><span> Apakah tidak Kami jadikan bumi sebagai haparan.</span></p><p><span>[b]</span><span> 2:23 ; 20:54 ; 51:49.</span></p>');
