INSERT INTO `quran` VALUES (12104,2,78,9,'<p><span>[c]</span><span> Dan, Kami telah ciptakan kamu berpasang-pasangan, </span></p><p><span>[c]</span><span> 36:37 ; 51:50 ; 75:40 ; 92:4.</span></p>');
