INSERT INTO `quran` VALUES (12107,2,78,12,'<p><span>[e]</span><span> Dan, Kami telah jadikan siang untuk mencari penghidupan.</span></p><p><span>[e]</span><span> 17:13 ; 28:74.</span></p>');
INSERT INTO `quran` VALUES (12108,2,78,13,'<p><span>[a]</span><span> Dan Kami telah buat di atasmu tujuh </span><span>langit</span><span> yang kuat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3225">3225</a><span>]</span></p><p><span>[a]</span><span> 23:18.</span></p>');
INSERT INTO `quran` VALUES (12109,2,78,14,'<p><span>Dan Kami telah jadikan matahari yang bersinar,</span></p>');
INSERT INTO `quran` VALUES (12110,2,78,15,'<p><span>[b]</span><span> Dan Kami turunkan dari awan-awan yang tebal air </span><span>hujan</span><span> yang lebat,</span></p><p><span>[b]</span><span> 6:7 ; 71:12 ; 78:15 ; 80:26.</span></p>');
INSERT INTO `quran` VALUES (12111,2,78,16,'<p><span>[c]</span><span> Supaya Kami keluarkan dengan itu biji-bijian dan tumbuh-tumbuhan.</span></p><p><span>[c]</span><span> 80:28 </span><span>–</span><span> 29.</span></p>');
INSERT INTO `quran` VALUES (12112,2,78,17,'<p><span>[d]</span><span> Dan kebun-kebun yang rimbun. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3226">3226</a><span>]</span></p><p><span>[d]</span><span> 80:31.</span></p>');
INSERT INTO `quran` VALUES (12113,2,78,18,'<p><span>Sesungguhnya, Hari keputusan itu telah ditetapkan.</span></p>');
INSERT INTO `quran` VALUES (12114,2,78,19,'<p><span>[e]</span><span> Pada hari akan ditiup nafiri maka kamu akan datang berkelompok-kelompok, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3227">3227</a><span>]</span></p><p><span>[e]</span><span> 18:100 ; 20:103 ; 27:88 ; 36:52</span></p>');
INSERT INTO `quran` VALUES (12115,2,78,20,'<p><span>Dan langit akan dibuka, maka jadilah pintu-pintu. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3228">3228</a><span>]</span></p>');
