INSERT INTO `quran` VALUES (12117,2,78,22,'<p><span>Sesungguhnya, Jahannam itu adalah tempat mengintai,</span></p>');
INSERT INTO `quran` VALUES (12118,2,78,23,'<p><span>Tempat kembali bagi orang-orang yang melampaui batas,</span></p>');
INSERT INTO `quran` VALUES (12119,2,78,24,'<p><span>[b]</span><span> Yang akan tinggal di dalamnya berabad-abad lamanya.</span></p><p><span>[b]</span><span> 11:108.</span></p>');
INSERT INTO `quran` VALUES (12120,2,78,25,'<p><span>Mereka tidak merasa di dalamnya kesejukan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3230">3230</a><span>]</span><span> dan tidak pula ada minuman.</span></p>');
INSERT INTO `quran` VALUES (12121,2,78,26,'<p><span>[c]</span><span> Kecuali air mendidih dan cairan busuk yang dingin sekali. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3230a">3230a</a><span>]</span></p><p><span>[c]</span><span> 6:71 ; 69:37.</span></p>');
INSERT INTO `quran` VALUES (12122,2,78,27,'<p><span>Pembalasan yang setimpal.</span></p>');
INSERT INTO `quran` VALUES (12123,2,78,28,'<p><span>Sesungguhnya, mereka tidak takut kepada penghisaban,</span></p>');
INSERT INTO `quran` VALUES (12124,2,78,29,'<p><span>[d]</span><span> Dan mereka telah mendustakan Tanda-tanda Kami dengan sesungguh-sungguhnya.</span></p><p><span>[d]</span><span> 2:40 ; 7:37.</span></p>');
INSERT INTO `quran` VALUES (12125,2,78,30,'<p><span>[a]</span><span> Dan segala sesuatu telah Kami mencatatnya dalam sebuah kitab. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3231">3231</a><span>]</span></p><p><span>[a]</span><span> 36:13.</span></p>');
INSERT INTO `quran` VALUES (12126,2,78,31,'<p><span>Maka rasakanlah olehmu ; dan sekali-kali Kami tidak akan menambahkan bagimu kecuali azab.</span></p>');
INSERT INTO `quran` VALUES (12127,2,78,32,'<p><span>R. 2</span></p><p><span>Sesungguhnya, bagi orang-orang yang bertakwa mendapat kemenangan.</span></p>');
INSERT INTO `quran` VALUES (12128,2,78,33,'<p><span>Kebun-kebun dan anggur, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3232">3232</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12129,2,78,34,'<p><span>[b]</span><span> Dan gadis-gadis remaja yang sebaya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3233">3233</a><span>]</span></p><p><span>[b]</span><span> 56:38.</span></p>');
INSERT INTO `quran` VALUES (12130,2,78,35,'<p><span>Dan piala-piala yang terisi penuh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3234">3234</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12131,2,78,36,'<p><span>[a]</span><span> Mereka tidak akan mendengar di dalamnya perkataan sia-sia dan tidak dusta.</span></p><p><span>[a]</span><span> 19:63 ; 52:24 ; 56:26.</span></p>');
INSERT INTO `quran` VALUES (12132,2,78,37,'<p><span>Balasan dari Tuhan engkau, pemberian yang cukup.</span></p>');
INSERT INTO `quran` VALUES (12133,2,78,38,'<p><span>[b]</span><span> Tuhan seluruh langit dan bumi dan semua yang ada di antara keduanya, Yang Maha Pemurah, mereka tidak mempunyai kekuatan </span><span>untuk</span><span> berbicara dengan Dia </span><span>tanpa izin.</span></p><p><span>[b]</span><span> 19:66.</span></p>');
INSERT INTO `quran` VALUES (12134,2,78,39,'<p><span>Pada hari berdirinya ruh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3234a">3234a</a><span>]</span><span> dan malaikat-malaikat bershaf-shaf, </span><span>[c]</span><span> mereka tidak akan berbicara, kecuali siapa yang kepadanya diizinkan Yang Maha Pemurah dan dia berkata benar.</span></p><p><span>[c]</span><span> 11:106.</span></p>');
INSERT INTO `quran` VALUES (12135,2,78,40,'<p><span>Itulah hari yang benar, maka barangsiapa yang menghendaki, ia boleh mengambil perlindungan pada Tuhan-nya.</span></p>');
INSERT INTO `quran` VALUES (12136,2,78,41,'<p><span>Sesungguhnya Kami telah memperingatkan kamu tentang azab yang dekat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3235">3235</a><span>]</span><span> suatu hari ketika orang akan melihat apa yang dahulu telah diperbuat oleh kedua tangannya, dan orang kafir akan berkata, </span><span>[a]</span><span> “Alangkah baiknya, aku dahulu jadi tanah ! “</span></p><p><span>[a]</span><span> 4:43.</span></p>');
INSERT INTO `quran` VALUES (12137,2,79,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12138,2,79,2,'<p><span>Demi mereka yang manarik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3236">3236</a><span>]</span><span> </span><span>ilmu agama</span><span> sekuat-kuatnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3237">3237</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12139,2,79,3,'<p><span>Dan demi mereka yang mengikatkan simpul seerat-eratnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3238">3238</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12140,2,79,4,'<p><span>Dan demi mereka yang berlari dengan cepat,</span></p>');
INSERT INTO `quran` VALUES (12141,2,79,5,'<p><span>Maka mereka maju dan jauh mendahului </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3239">3239</a><span>]</span><span> </span><span>yang lain.</span></p>');
INSERT INTO `quran` VALUES (12142,2,79,6,'<p><span>Kemudian mereka mengelola urusan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3240">3240</a><span>]</span><span> </span><span>dunia.</span></p>');
INSERT INTO `quran` VALUES (12143,2,79,7,'<p><span>Pada hari ketika </span><span>[a]</span><span> goncangan </span><span>bumi</span><span> akan bergoncang dengan dahsyatnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3241">3241</a><span>]</span><span> </span><span>untuk berperang,</span></p><p><span>[a]</span><span> 56:5 </span><span>–</span><span> 6.</span></p>');
INSERT INTO `quran` VALUES (12144,2,79,8,'<p><span>Dan akan mengikuti </span><span>goncangan</span><span> berikutnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3242">3242</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12145,2,79,9,'<p><span>Hati pada hari itu berdebar-debar,</span></p>');
INSERT INTO `quran` VALUES (12146,2,79,10,'<p><span>[b]</span><span> Pandangannya tunduk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3243">3243</a><span>]</span><span> </span><span>karena takut.</span></p><p><span>[b]</span><span> 70:45.</span></p>');
INSERT INTO `quran` VALUES (12147,2,79,11,'<p><span>Mereka berkata, “Benarkah kami akan dikembalikan kepada keadaan semula ? “</span></p>');
INSERT INTO `quran` VALUES (12148,2,79,12,'<p><span>[c]</span><span> “Apakah bilamana kami telah menjadi tulang yang rapuh ? “</span></p><p><span>[c]</span><span> 17:50 ; 36:79.</span></p>');
INSERT INTO `quran` VALUES (12149,2,79,13,'<p><span>Mereka berkata, “Hal demikian itu, sungguh merupakan suatu pengembalian yang merugikan</span></p>');
INSERT INTO `quran` VALUES (12150,2,79,14,'<p><span>Maka itu hanya berupa satu gertakan,</span></p>');
INSERT INTO `quran` VALUES (12151,2,79,15,'<p><span>Dan tiba-tiba mereka </span><span>keluar </span><span> di tempat terbuka.</span></p>');
INSERT INTO `quran` VALUES (12152,2,79,16,'<p><span>Apakah sudah sampai kepada engkau kisah Musa ?</span></p>');
INSERT INTO `quran` VALUES (12153,2,79,17,'<p><span>Ketika memanggil dia Tuhan-nya di lembah suci Thuwaa,</span></p>');
INSERT INTO `quran` VALUES (12154,2,79,18,'<p><span>Allah berfirman,</span><span> “Pergilah engkau kepada Firaun ; sesungguhnya ia telah melampaui batas,</span></p>');
INSERT INTO `quran` VALUES (12155,2,79,19,'<p><span>Maka katakanlah, “Apakah ada padamu </span><span>keinginan</span><span> untuk mensucikan diri ? “</span></p>');
INSERT INTO `quran` VALUES (12156,2,79,20,'<p><span>Dan aku akan menunjuki engkau kepada Tuhan engkau, supaya engkau takut.</span></p>');
INSERT INTO `quran` VALUES (12157,2,79,21,'<p><span>[a]</span><span> Maka dia memperlihatkan kepadanya Tanda yang besar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3244">3244</a><span>]</span></p><p><span>[a]</span><span> 20:57.</span></p>');
INSERT INTO `quran` VALUES (12158,2,79,22,'<p><span>Tetapi ia mendustakan dan membangkang,</span></p>');
INSERT INTO `quran` VALUES (12159,2,79,23,'<p><span>Kemudian ia berpaling dan berusaha menantang,</span></p>');
INSERT INTO `quran` VALUES (12160,2,79,24,'<p><span>Maka ia menghimpunkan </span><span>kaumnya </span><span> dan berseru,</span></p>');
INSERT INTO `quran` VALUES (12161,2,79,25,'<p><span>Lalu berkata, “Akulah </span><span>[a]</span><span> tuhanmu yang paling tinggi.”</span></p><p><span>[a]</span><span> 26:30 ; 28:39.</span></p>');
INSERT INTO `quran` VALUES (12162,2,79,26,'<p><span>Maka Allah menyergapnya dengan azab di akhirat dan di dunia.</span></p>');
INSERT INTO `quran` VALUES (12163,2,79,27,'<p><span>Sesungguhnya, dalam hal itu adalah pelajaran bagi orang yang takut.</span></p>');
INSERT INTO `quran` VALUES (12164,2,79,28,'<p><span>R. 2</span></p><p><span>Apakah kamu lebih sukar diciptakan ataukah langit yang </span><span>Allah</span><span> telah membuatnya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3245">3245</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12165,2,79,29,'<p><span>[b]</span><span> Dia telah meninggikan ketinggiannya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3245a">3245a</a><span>]</span><span> dan telah menyempurnakannya.</span></p><p><span>[b]</span><span> 21:33.</span></p>');
INSERT INTO `quran` VALUES (12166,2,79,30,'<p><span>Dan </span><span>[c]</span><span> Dia telah menjadikan gelap malamnya dan telah mengeluarkan sinar terangnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3246">3246</a><span>]</span></p><p><span>[c]</span><span> 78:11 </span><span>–</span><span> 12.</span></p>');
INSERT INTO `quran` VALUES (12167,2,79,31,'<p><span>[a]</span><span> Dan bumi sesudah itu menghamparkannya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3247">3247</a><span>]</span></p><p><span>[a]</span><span> 20:54 ; 51:49.</span></p>');
INSERT INTO `quran` VALUES (12168,2,79,32,'<p><span>[b]</span><span> Dia mengeluarkan darinya airnya dan padang rumputnya,</span></p><p><span>[b]</span><span> 20:54 ; 50:8.</span></p>');
INSERT INTO `quran` VALUES (12169,2,79,33,'<p><span>[c]</span><span> Dan gunung-gunung, Dia meneguhkannya,</span></p><p><span>[c]</span><span> 50:8.</span></p>');
INSERT INTO `quran` VALUES (12170,2,79,34,'<p><span>[d]</span><span> Manfaat hidup bagi kamu dan ternakmu.</span></p><p><span>[d]</span><span> 80:33.</span></p>');
INSERT INTO `quran` VALUES (12171,2,79,35,'<p><span>[e]</span><span> Maka apabila datang malapetaka besar,</span></p><p><span>[e] 74:36 ; 80:34.</span></p>');
INSERT INTO `quran` VALUES (12172,2,79,36,'<p><span>[f]</span><span> Pada hari ketika manusia akan mengingat apa yang ia telah usahakan,</span></p><p><span>[f]</span><span> 89:24.</span></p>');
INSERT INTO `quran` VALUES (12173,2,79,37,'<p><span>[g]</span><span> Dan akan ditampakkan Jahannam kepada orang yang melihat.</span></p><p><span>[g]</span><span> 26:92.</span></p>');
INSERT INTO `quran` VALUES (12174,2,79,38,'<p><span>Maka adapun orang yang melampaui batas.</span></p>');
INSERT INTO `quran` VALUES (12175,2,79,39,'<p><span>Dan mengutamakan kehidupan dunia,</span></p>');
INSERT INTO `quran` VALUES (12176,2,79,40,'<p><span>Maka sesungguhnya, Jahannam menjadi tempat tinggal.</span></p>');
INSERT INTO `quran` VALUES (12177,2,79,41,'<p><span>[h]</span><span> Dan adapun orang yang takut kepada keagungan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3248">3248</a><span>]</span><span> Tuhan-nya dan mencegah diri dari hawa nafsu,</span></p><p><span>[h]</span><span> 55:47.</span></p>');
INSERT INTO `quran` VALUES (12178,2,79,42,'<p><span>Maka, sesungguhnya, surga adalah tempat tinggal.</span></p>');
INSERT INTO `quran` VALUES (12179,2,79,43,'<p><span>[a]</span><span> Mereka bertanya kepada engkau mengenai Saat, “Bilakah itu akan terjadinya ?”</span></p><p><span>[a]</span><span> 7:118 ; 33:64 ; 51:13.</span></p>');
INSERT INTO `quran` VALUES (12180,2,79,44,'<p><span>Dalam hubungan apakah engkau mengingatnya ?</span></p>');
INSERT INTO `quran` VALUES (12181,2,79,45,'<p><span>Kepada Tuhan engkau berakhir </span><span>waktu</span><span>-nya.</span></p>');
INSERT INTO `quran` VALUES (12182,2,79,46,'<p><span>Sesungguhnya, engkau hanyalah seorang pemberi ingat bagi siapa yang takut kepadanya.</span></p>');
INSERT INTO `quran` VALUES (12183,2,79,47,'<p><span>[b]</span><span> Seolah-olah mereka pada hari melihatnya tidak tinggal </span><span>di dunia,</span><span> melainkan hanya pada sore atau paginya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3249">3249</a><span>]</span></p><p><span>[b]</span><span> 10:46 ; 30:56 ; 46:36.</span></p>');
INSERT INTO `quran` VALUES (12184,2,80,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12185,2,80,2,'<p><span>Ia bermuka masam </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3250">3250</a><span>]</span><span> dan berpaling,</span></p>');
INSERT INTO `quran` VALUES (12186,2,80,3,'<p><span>Karena telah datang kepadanya seorang buta.</span></p>');
INSERT INTO `quran` VALUES (12187,2,80,4,'<p><span>Dan apakah yang membuat engkau mengetahui bahwa ia akan mensucikan diri, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3251">3251</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12188,2,80,5,'<p><span>Atau, ia mengingat sehingga bermanfaat baginya nasihat itu ?</span></p>');
INSERT INTO `quran` VALUES (12189,2,80,6,'<p><span>Adapun orang yang menganggap dirnya cukup,</span></p>');
INSERT INTO `quran` VALUES (12190,2,80,7,'<p><span>Lalu kepadanya engkau menaruh perhatian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3252">3252</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12191,2,80,8,'<p><span>Meskipun engkau tidak bertanggung-jawab bahwa ia tidak mensucikan diri. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3253">3253</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12192,2,80,9,'<p><span>Dan adapun orang yang datang kepada engkau dengan bergegas.</span></p>');
INSERT INTO `quran` VALUES (12193,2,80,10,'<p><span>Dan ia takut </span><span>kepada Tuhan,</span></p>');
INSERT INTO `quran` VALUES (12194,2,80,11,'<p><span>Maka kepadanya engkau tidak menaruh perhatian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3254">3254</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12195,2,80,12,'<p><span>Sekali-kali tidak ! Sesungguhnya, </span><span>Alquran</span><span> itu </span><span>[a]</span><span> pemberi nasihat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3255">3255</a><span>]</span></p><p><span>[a]</span><span> 20:4 ; 73:20 ; 74:55.</span></p>');
INSERT INTO `quran` VALUES (12196,2,80,13,'<p><span>Maka barangsiapa menghendaki, ia dapat mengingatnya.</span></p>');
INSERT INTO `quran` VALUES (12197,2,80,14,'<p><span>Yang tercantum</span><span> di dalam lembaran-lembaran yang dimuliakan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3256">3256</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12198,2,80,15,'<p><span>Yang dijunjung tinggi, yang disucikan,</span></p>');
INSERT INTO `quran` VALUES (12199,2,80,16,'<p><span>Di tangan para penulis,</span></p>');
INSERT INTO `quran` VALUES (12200,2,80,17,'<p><span>Yang mulia </span><span>lagi</span><span> baik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3257">3257</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12201,2,80,18,'<p><span>Binasalah manusia, betapa tidak berterima kasihnya dia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3258">3258</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12202,2,80,19,'<p><span>Dari apakah Dia telah menciptakannya ?</span></p>');
INSERT INTO `quran` VALUES (12203,2,80,20,'<p><span>[a]</span><span> Dari setetes mani ! Dia menciptakannya, kemudian menetapkan kadarnya ;</span></p><p><span>[a]</span><span> 18:38 ; 35:12 ; 36:78 ; 40:68.</span></p>');
INSERT INTO `quran` VALUES (12204,2,80,21,'<p><span>Kemudian Dia memudahkan untuknya jalan,</span></p>');
INSERT INTO `quran` VALUES (12205,2,80,22,'<p><span>Kemudian Dia mematikannya lalu menguburkannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3259">3259</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12206,2,80,23,'<p><span>Kemudian, apabila Dia menghendaki, Dia akan membangkitkannya.</span></p>');
INSERT INTO `quran` VALUES (12207,2,80,24,'<p><span>Sekali-kali tidak, ia belum melaksanakan apa yang Dia perintahkan kepadanya.</span></p>');
INSERT INTO `quran` VALUES (12208,2,80,25,'<p><span>Maka hendaklah manusia melihat kepada makanannya ;</span></p>');
INSERT INTO `quran` VALUES (12209,2,80,26,'<p><span>[a]</span><span> Sesungguhnya Kami mencurahkan air dengan berlimpah-limpah.</span></p><p><span>[a]</span><span> 71:12 ; 78:15.</span></p>');
INSERT INTO `quran` VALUES (12210,2,80,27,'<p><span>Kemudian Kami membelah bumi dengan sebaik-baiknya.</span></p>');
INSERT INTO `quran` VALUES (12211,2,80,28,'<p><span>[b]</span><span> Lalu Kami menumbuhkan di dalamnya biji-bijian,</span></p><p><span>[b]</span><span> 78:16.</span></p>');
INSERT INTO `quran` VALUES (12212,2,80,29,'<p><span>Dan anggur serta sayur mayur,</span></p>');
INSERT INTO `quran` VALUES (12213,2,80,30,'<p><span>Dan zaitum serta pohon kurma,</span></p>');
INSERT INTO `quran` VALUES (12214,2,80,31,'<p><span>[c]</span><span> Dan kebun-kebun yang rimbun,</span></p><p><span>[c]</span><span> 78:17.</span></p>');
INSERT INTO `quran` VALUES (12215,2,80,32,'<p><span>Dan buah-buahan serta rumput-rumputan.</span></p>');
INSERT INTO `quran` VALUES (12216,2,80,33,'<p><span>[d]</span><span> Manfaat hidup bagi kamu dan ternakmu.</span></p><p><span>[d]</span><span> 79:34.</span></p>');
INSERT INTO `quran` VALUES (12217,2,80,34,'<p><span>[e]</span><span> Tetapi, apabila datang teriakan memekakkan </span><span>telinga.</span></p><p><span>[e]</span><span> 79:35.</span></p>');
INSERT INTO `quran` VALUES (12218,2,80,35,'<p><span>[f]</span><span> Pada hari ketika orang akan melarikan diri dari saudaranya.</span></p><p><span>[f]</span><span> 44:42.</span></p>');
INSERT INTO `quran` VALUES (12219,2,80,36,'<p><span>Dan </span><span>dari</span><span> ibunya serta bapaknya,</span></p>');
INSERT INTO `quran` VALUES (12220,2,80,37,'<p><span>Dan dari </span><span>[a]</span><span> istrinya serta anak-anak lelakinya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3259a">3259a</a><span>]</span></p><p><span>[a]</span><span> 70:13.</span></p>');
INSERT INTO `quran` VALUES (12221,2,80,38,'<p><span>Bagi setiap orang diantara mereka pada hari itu mempunyai urusan yang menyibukkan dirinya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3260">3260</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12222,2,80,39,'<p><span>[b]</span><span> Wajah-wajah pada hari itu bersinar.</span></p><p><span>[b]</span><span> 3:107 ; 10:27.</span></p>');
INSERT INTO `quran` VALUES (12223,2,80,40,'<p><span>Tertawa dan gembira.</span></p>');
INSERT INTO `quran` VALUES (12224,2,80,41,'<p><span>[c]</span><span> Dan wajah-wajah pada hari itu di atasnya penuh debu.</span></p><p><span>[c]</span><span> 68:44 ; 75:25 ; 88:3 </span><span>–</span><span> 4.</span></p>');
INSERT INTO `quran` VALUES (12225,2,80,42,'<p><span>[d]</span><span> Kegelapan menutupinya.</span></p><p><span>[d]</span><span> 14:51 ; 23:105.</span></p>');
INSERT INTO `quran` VALUES (12226,2,80,43,'<p><span>Mereka itulah orang-orang ingkar lagi jahat.</span></p>');
INSERT INTO `quran` VALUES (12227,2,81,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12228,2,81,2,'<p><span>Apabila matahari digulung, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3261">3261</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12229,2,81,3,'<p><span>Dan apabila bintang-bintang menjadi suram, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3262">3262</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12230,2,81,4,'<p><span>Dan </span><span>[b]</span><span> apabila gunung-gunung dijalankan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3263">3263</a><span>]</span></p><p><span>[b]</span><span> 18:48 ; 52:11 ; 78:21.</span></p>');
INSERT INTO `quran` VALUES (12231,2,81,5,'<p><span>Dan apabila unta-unta bunting sepuluh bulan ditinggalkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3264">3264</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12232,2,81,6,'<p><span>Dan apabila binatang-binatang liar dikumpulkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3265">3265</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12233,2,81,7,'<p><span>[a]</span><span> Dan apabila sungai-sungai disalurkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3266">3266</a><span>]</span></p><p><span>[a]</span><span> 52:7 ; 82:4.</span></p>');
INSERT INTO `quran` VALUES (12234,2,81,8,'<p><span>Dan apabila bermacam-macam manusia dikumpulkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3267">3267</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12235,2,81,9,'<p><span>Dan apabila bayi perempuan dikubur hidup-hidup akan ditanya,</span></p>');
INSERT INTO `quran` VALUES (12236,2,81,10,'<p><span>“Karena dosa apa, ia dibunuh ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3268">3268</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12237,2,81,11,'<p><span>Dan apabila buku-buku akan disebar-luaskan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3269">3269</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12238,2,81,12,'<p><span>Dan apabila langit dibuka, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3270">3270</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12239,2,81,13,'<p><span>Dan apabila neraka dinyalakan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3271">3271</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12240,2,81,14,'<p><span>[a]</span><span> Dan apabila surga di dekatkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3272">3272</a><span>]</span></p><p><span>[a]</span><span> 50:32.</span></p>');
INSERT INTO `quran` VALUES (12241,2,81,15,'<p><span>[a]</span><span> Akan mengetahui setiap jiwa apa yang ia hadirkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3273">3273</a><span>]</span></p><p><span>[a]</span><span> 3:31 ; 82:6.</span></p>');
INSERT INTO `quran` VALUES (12242,2,81,16,'<p><span>Sungguh Aku bersumpah dengan yang tinggal di belakang ketika bahaya datang,</span></p>');
INSERT INTO `quran` VALUES (12243,2,81,17,'<p><span>Yang berjalan dan sembunyi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3274">3274</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12244,2,81,18,'<p><span>Demi malam ketika hampir meninggalkan gelapnya,</span></p>');
INSERT INTO `quran` VALUES (12245,2,81,19,'<p><span>[b]</span><span> Dan demi subuh ketika mulai bernafas, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3275">3275</a><span>]</span></p><p><span>[b]</span><span> 74:35.</span></p>');
INSERT INTO `quran` VALUES (12246,2,81,20,'<p><span>[c]</span><span> Bahwa sesungguhnya </span><span>Alquran</span><span> itu adalah ucapan dari seorang Rasul yang mulia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3276">3276</a><span>]</span></p><p><span>[c]</span><span> 69:41.</span></p>');
INSERT INTO `quran` VALUES (12247,2,81,21,'<p><span>Yang mempunyai kekuatan, yang mempunyai kedudukan tinggi di sisi </span><span>Tuhan</span><span> Yang Mempunyai </span><span>‘</span><span>Arasy,</span></p>');
INSERT INTO `quran` VALUES (12248,2,81,22,'<p><span>Yang ditaati kemudian yang dipercaya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3277">3277</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12249,2,81,23,'<p><span>[a]</span><span> Dan, tidaklah temanmu itu orang gila,</span></p><p><span>[a]</span><span> 52:30 ; 68:3.</span></p>');
INSERT INTO `quran` VALUES (12250,2,81,24,'<p><span>[b]</span><span> Dan, sesungguhnya, ia melihat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3278">3278</a><span>]</span><span> di ufuk yang terbuka.</span></p><p><span>[b]</span><span> 53:13.</span></p>');
INSERT INTO `quran` VALUES (12251,2,81,25,'<p><span>Dan, ia sekali-kali tidak bakhil mengenai hal gaib. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3279">3279</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12252,2,81,26,'<p><span>[c]</span><span> Dan, bukanlah </span><span>Alquran</span><span> itu ucapan syaitan yang terkutuk</span></p><p><span>[c]</span><span> 26:211.</span></p>');
INSERT INTO `quran` VALUES (12253,2,81,27,'<p><span>Maka kemanakah kamu akan pergi ?</span></p>');
INSERT INTO `quran` VALUES (12254,2,81,28,'<p><span>[d]</span><span> Tidak lain </span><span>Alquran</span><span> itu melainkan peringatan bagi semesta alam,</span></p><p><span>[d]</span><span> 12:105 ; 38:88.</span></p>');
INSERT INTO `quran` VALUES (12255,2,81,29,'<p><span>Bagi siapa yang menghendaki di antaramu untuk berjalan lurus.</span></p>');
INSERT INTO `quran` VALUES (12256,2,81,30,'<p><span>[e]</span><span> Dan kamu tidak menhendaki kecuali yang dikehendaki Allah, Tuhan semesta alam. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3280">3280</a><span>]</span></p><p><span>[e]</span><span> 74:57 ; 76:31.</span></p>');
INSERT INTO `quran` VALUES (12257,2,82,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12258,2,82,2,'<p><span>[b]</span><span> Apabila langit terbelah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3281">3281</a><span>]</span></p><p><span>[b]</span><span> 73:19. </span></p>');
INSERT INTO `quran` VALUES (12259,2,82,3,'<p><span>Dan apabila bintang-bintang berserakan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3282">3282</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12260,2,82,4,'<p><span>[c]</span><span> Dan apabila lautan-lautan dialirkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3283">3283</a><span>]</span><span> </span><span>dan dipertemukan.</span></p><p><span>[c]</span><span> 81:7.</span></p>');
INSERT INTO `quran` VALUES (12261,2,82,5,'<p><span>[d]</span><span> Dan apabila kuburan-kuburan dibongkar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3284">3284</a><span>]</span></p><p><span>[d]</span><span> 100:10.</span></p>');
INSERT INTO `quran` VALUES (12262,2,82,6,'<p><span>[a]</span><span> Akan mengetahui setiap jiwa apa yang mereka dahulukan dan apa yang ia tinggalkan di belakang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3285">3285</a><span>]</span></p><p><span>[a]</span><span> 3:31 ; 81:15.</span></p>');
INSERT INTO `quran` VALUES (12263,2,82,7,'<p><span>Hai, manusia ! Apa yang telah memperdayai engkau ihwal Tuhan engkau Yang Maha Mulia.</span></p>');
INSERT INTO `quran` VALUES (12264,2,82,8,'<p><span>yang telah menciptakan engkau, kemudian menyempurnakan engkau, lalu menata </span><span>tubuh</span><span> engkau dengan </span><span>[b]</span><span> serasi ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3286">3286</a><span>]</span></p><p><span>[b]</span><span> 87:3 ; 91:8.</span></p>');
INSERT INTO `quran` VALUES (12265,2,82,9,'<p><span>Di dalam bentuk apa yang Dia kehendaki, Dia menyusun </span><span>tubuh</span><span> engkau.</span></p>');
INSERT INTO `quran` VALUES (12266,2,82,10,'<p><span>Tidak sekali-kali, bahkan kamu mendustakan pembalasan.</span></p>');
INSERT INTO `quran` VALUES (12267,2,82,11,'<p><span>[c]</span><span> Dan sesungguhnya atas kamu ada pengawas-pengawas,</span></p><p><span>[c]</span><span> 6:62.</span></p>');
INSERT INTO `quran` VALUES (12268,2,82,12,'<p><span>[d]</span><span> Pencatat-pencatat, mulia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3287">3287</a><span>]</span></p><p><span>[d]</span><span> 43:81 ; 50:19.</span></p>');
INSERT INTO `quran` VALUES (12269,2,82,13,'<p><span>Mereka mengetahui apa yang kamu kerjakan.</span></p>');
INSERT INTO `quran` VALUES (12270,2,82,14,'<p><span>[a]</span><span> Sesungguhnya, orang-orang yang baik niscaya dalam kenikmatan.</span></p><p><span>[a]</span><span> 45:31 ; 83:23.</span></p>');
INSERT INTO `quran` VALUES (12271,2,82,15,'<p><span>[b]</span><span> Dan sesungguhnya, orang-orang berdosa pasti tinggal di dalam Jahannam.</span></p><p><span>[b]</span><span> 83:8.</span></p>');
INSERT INTO `quran` VALUES (12272,2,82,16,'<p><span>[c]</span><span> Mereka akan masuk kedalamnya pada Hari Pembalasan.</span></p><p><span>[c]</span><span> 23:104 ; 83:17.</span></p>');
INSERT INTO `quran` VALUES (12273,2,82,17,'<p><span>Dan mereka tidak lolos darinya.</span></p>');
INSERT INTO `quran` VALUES (12274,2,82,18,'<p><span>Dan apakah yang engkau tahu, apa Hari Pembalasan itu ?</span></p>');
INSERT INTO `quran` VALUES (12275,2,82,19,'<p><span>Lagi, apakah yang membuat engkau tahu apa Hari Pemalasan itu ?</span></p>');
INSERT INTO `quran` VALUES (12276,2,82,20,'<p><span>[d]</span><span> Pada hari itu, tiada jiwa mempunyai kekuatan sedikitpun untuk memberi manfaat bagi jiwa lain ! </span><span>[e]</span><span> Dan segala keputusan pada hari itu bagi Allah.</span></p><p><span>[d]</span><span> 2:124 ; 31:34.</span></p><p><span>[e]</span><span> 18:45 ; 40:17.</span></p>');
INSERT INTO `quran` VALUES (12277,2,83,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12278,2,83,2,'<p><span>[b]</span><span> Celakalah bagi orang-orang yang mengurangi timbangan ;</span></p><p><span>[b]</span><span> 11:85 ; 26:182 </span><span>–</span><span> 184 ; 55:9.</span></p>');
INSERT INTO `quran` VALUES (12279,2,83,3,'<p><span>Orang-orang yang, apabila menerima takaran dari orang </span><span>lain</span><span> mereka meminta dengan penuh</span></p>');
INSERT INTO `quran` VALUES (12280,2,83,4,'<p><span>[c]</span><span> Dan apabila mereka menakar atau menimbang untuk orang lain mereka mengurangi.</span></p><p><span>[c]</span><span> 55:10.</span></p>');
INSERT INTO `quran` VALUES (12281,2,83,5,'<p><span>Apakah mereka tidak yakin, bahwa sesungguhnya mereka akan dibangkitkan.</span></p>');
INSERT INTO `quran` VALUES (12282,2,83,6,'<p><span>Untuk suatu Hari yang besar ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3288">3288</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12283,2,83,7,'<p><span>Pada hari, ketika umat manusia akan berdiri di hadapan Tuhan semesta alam.</span></p>');
INSERT INTO `quran` VALUES (12284,2,83,8,'<p><span>Sekali-kali tidak, sesungguhnya, kitab para pendurhaka adalah di dalam sijjilin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3289">3289</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12285,2,83,9,'<p><span>Dan apakah yang engkau ketahui, apa sijjilin itu ?</span></p>');
INSERT INTO `quran` VALUES (12286,2,83,10,'<p><span>Ialah</span><span> sebuah kitab tertulis. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3290">3290</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12287,2,83,11,'<p><span>Celakalah pada hari itu bagi orang-orang mendustakan,</span></p>');
INSERT INTO `quran` VALUES (12288,2,83,12,'<p><span>Orang-orang yang mendustakan Hari Pembalasan.</span></p>');
INSERT INTO `quran` VALUES (12289,2,83,13,'<p><span>Dan tiada yang mendustakannya kecuali setiap pelanggar batas dan sangat berdosa.</span></p>');
INSERT INTO `quran` VALUES (12290,2,83,14,'<p><span>[a]</span><span> Apabila dibacakan kepadanya Tanda-tanda Kami, ia berkata, “Inilah dongeng orang-orang dahulu ! “</span></p><p><span>[a]</span><span> 8:32 ; 16:25 ; 68:16.</span></p>');
INSERT INTO `quran` VALUES (12291,2,83,15,'<p><span>Sekali-kali tidak, bahkan karat pada hati mereka </span><span>disebabkan</span><span> apa yang mereka usahakan.</span></p>');
INSERT INTO `quran` VALUES (12292,2,83,16,'<p><span>[a]</span><span> Sekali-kali tidak, bahkan sesungguhnya, pada hari itu mereka pasti terhalang dari </span><span>melihat</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3291">3291</a><span>] </span><span>Tuhan mereka.</span></p><p><span>[a]</span><span> 3:78.</span></p>');
INSERT INTO `quran` VALUES (12293,2,83,17,'<p><span>[b]</span><span> Kemudian, sesungguhnya, mereka pasti masuk ke dalam Jahannam.</span></p><p><span>[b]</span><span> 23:104 ; 82:16.</span></p>');
INSERT INTO `quran` VALUES (12294,2,83,18,'<p><span>Kemudian akan dikatakan, “Inilah apa yang senantiasa kamu </span><span>[c]</span><span> dustakan.”</span></p><p><span>[c]</span><span> 52:15.</span></p>');
INSERT INTO `quran` VALUES (12295,2,83,19,'<p><span>Sekali-kali tidak, sesungguhnya rekaman orang-orang yang baik itu niscaya ada di dalam </span><span>‘</span><span>illiyyuun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3292">3292</a><span>]</span><span> </span></p>');
INSERT INTO `quran` VALUES (12296,2,83,20,'<p><span>dan apakah engkau mengetahui apa </span><span>‘</span><span>illiyyuun </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3293">3293</a><span>]</span><span> itu ?</span></p>');
INSERT INTO `quran` VALUES (12297,2,83,21,'<p><span>Adalah</span><span> sebuah Kitab tertulis.</span></p>');
INSERT INTO `quran` VALUES (12298,2,83,22,'<p><span>Akan menyaksikannya orang-orang terpilih.</span></p>');
INSERT INTO `quran` VALUES (12299,2,83,23,'<p><span>[a]</span><span> Sesungguhnya, orang-orang yang baik niscaya dalam kenikmatan,</span></p><p><span>[a]</span><span> 45:31 ; 82:14.</span></p>');
INSERT INTO `quran` VALUES (12300,2,83,24,'<p><span>[b]</span><span> </span><span>Duduk</span><span> di atas dipan-dipan mereka memandang.</span></p><p><span>[b]</span><span> 15:48 ; 18:32 ; 36:57 ; 76:14.</span></p>');
INSERT INTO `quran` VALUES (12301,2,83,25,'<p><span>Engkau dapat mengenal pada wajah mereka kesegaran nikmat itu.</span></p>');
INSERT INTO `quran` VALUES (12302,2,83,26,'<p><span>Mereka akan diberi minum minuman murni </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3294">3294</a><span>]</span><span> yang bermeterai.</span></p>');
INSERT INTO `quran` VALUES (12303,2,83,27,'<p><span>Meterainya adalah kesturi. Dan untuk mereka yang menginginkan hendaknya menginginkan itu.</span></p>');
INSERT INTO `quran` VALUES (12304,2,83,28,'<p><span>Dan </span><span>minuman</span><span> itu dicampur dengan air tasniin,</span></p>');
INSERT INTO `quran` VALUES (12305,2,83,29,'<p><span>Mata air yang akan meminumnya orang-orang terpilih.</span></p>');
INSERT INTO `quran` VALUES (12306,2,83,30,'<p><span>[c]</span><span> Sesungguhnya, orang-orang berdosa biasa menertawakan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3295">3295</a><span>]</span><span> orang-orang beriman,</span></p><p><span>[c]</span><span> 23:111.</span></p>');
INSERT INTO `quran` VALUES (12307,2,83,31,'<p><span>Dan apabila mereka lewat di dekat mereka itu, mereka saling mengedipkan mata.</span></p>');
INSERT INTO `quran` VALUES (12308,2,83,32,'<p><span>[a]</span><span> Dan, apabila mereka kembali kepada sanak-saudara mereka, mereka kembali dengan gembira.</span></p><p><span>[a]</span><span> 84:14.</span></p>');
INSERT INTO `quran` VALUES (12309,2,83,33,'<p><span>Dan, apabila mereka melihat mereka itu, mereka berkata, “Sesungguhnya, mereka itu pasti sesat ! ”</span></p>');
INSERT INTO `quran` VALUES (12310,2,83,34,'<p><span>Dan, mereka tidak diutus kepada mereka itu sebagai penjaga.</span></p>');
INSERT INTO `quran` VALUES (12311,2,83,35,'<p><span>Maka, pada hari itu orang-orang mukmin terhadap orang-orang kafir akan menertawakan.</span></p>');
INSERT INTO `quran` VALUES (12312,2,83,36,'<p><span>Duduk</span><span> di atas dipan-dipan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3296">3296</a><span>]</span><span> mereka memandang.</span></p>');
INSERT INTO `quran` VALUES (12313,2,83,37,'<p><span>“Apakah orang-orang kafir dibalas untuk apa yang dahulu mereka kerjakan ?</span></p>');
INSERT INTO `quran` VALUES (12314,2,84,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12315,2,84,2,'<p><span>Apabila </span><span>[b]</span><span> langit pecah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3297">3297</a><span>]</span></p><p><span>[b]</span><span> 55:38 ; 69:17.</span></p>');
INSERT INTO `quran` VALUES (12316,2,84,3,'<p><span>Dan </span><span>[c]</span><span> mendengarkan kepada Tuhan-nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3298">3298</a><span>]</span><span> dan </span><span>itu </span><span> wajib baginya,</span></p><p><span>[c]</span><span> 41:12.</span></p>');
INSERT INTO `quran` VALUES (12317,2,84,4,'<p><span>Dan apabila </span><span>[d]</span><span> bumi di bentangkan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3299">3299</a><span>]</span></p><p><span>[d]</span><span> 78:7.</span></p>');
INSERT INTO `quran` VALUES (12318,2,84,5,'<p><span>Dan mengeluarkan apa yang terkandung di dalamnya, dan menjadi kosong, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3300">3300</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12319,2,84,6,'<p><span>Dan mendengarkan kepada Tuhan-nya, dan </span><span>itu</span><span> wajib baginya.</span></p>');
INSERT INTO `quran` VALUES (12320,2,84,7,'<p><span>Hai manusia, sesungguhnya engkau bekerja keras dengan sungguh-sungguh menuju Tuhan engkau, maka </span><span>[a]</span><span> engkau akan bertemu dengan-Nya.</span></p><p><span>[a]</span><span> 2:224 ; 11:30 ; 18:111.</span></p>');
INSERT INTO `quran` VALUES (12321,2,84,8,'<p><span>Maka adapun orang </span><span>[b]</span><span> yang diberikan kitabnya di tangan kanannya,</span></p><p><span>[b]</span><span> 17:72 ; 69:20.</span></p>');
INSERT INTO `quran` VALUES (12322,2,84,9,'<p><span>Niscaya ia segera akan dihisab dengan perhitungan yang mudah,</span></p>');
INSERT INTO `quran` VALUES (12323,2,84,10,'<p><span>Dan ia akan kembali kepada keluarganya dengan gembira.</span></p>');
INSERT INTO `quran` VALUES (12324,2,84,11,'<p><span>Dan adapun orang yang diberikan </span><span>[c]</span><span> kitabnya dari belakang punggungnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3301">3301</a><span>]</span></p><p><span>[c]</span><span> 69:26.</span></p>');
INSERT INTO `quran` VALUES (12325,2,84,12,'<p><span>Maka ia segera akan memanggil kebinasaan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3302">3302</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12326,2,84,13,'<p><span>Dan ia akan masuk ke dalam Api yang menyala-nyala.</span></p>');
INSERT INTO `quran` VALUES (12327,2,84,14,'<p><span>Sesungguhnya, ia dahulu </span><span>[d]</span><span> gembira di tengah keluarganya.</span></p><p><span>[d]</span><span> 83:32.</span></p>');
INSERT INTO `quran` VALUES (12328,2,84,15,'<p><span>Sesungguhnya, ia menyangka bahwa sekali-kali ia tidak akan kembali.</span></p>');
INSERT INTO `quran` VALUES (12329,2,84,16,'<p><span>Memang ! Sesungguhnya Tuhan-nya, selalu melihatnya.</span></p>');
INSERT INTO `quran` VALUES (12330,2,84,17,'<p><span>Maka sesungguhnya aku bersumpah dengan cahaya senja,</span></p>');
INSERT INTO `quran` VALUES (12331,2,84,18,'<p><span>Dan demi malam serta apa yang diliputinya,</span></p>');
INSERT INTO `quran` VALUES (12332,2,84,19,'<p><span>Dan demi bulan apabila jadi purnama, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3303">3303</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12333,2,84,20,'<p><span>Tentulah kamu akan naik satu tingkat ke tingkat lain. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3304">3304</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12334,2,84,21,'<p><span>Maka </span><span>[a]</span><span> apa yang terjadi atas mereka, </span><span>sehingga</span><span> mereka tidak beriman ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3305">3305</a><span>]</span></p><p><span>[a]</span><span> 43:89.</span></p>');
INSERT INTO `quran` VALUES (12335,2,84,22,'<p><span>Dan apabila Alquran dibacakan kepada mereka, mereka tidak bersujud.</span></p>');
INSERT INTO `quran` VALUES (12336,2,84,23,'<p><span>Bahkan orang-orang kafir mereka </span><span>[b]</span><span> mendustakan,</span></p><p><span>[b]</span><span> 85:20.</span></p>');
INSERT INTO `quran` VALUES (12337,2,84,24,'<p><span>Dan, Allah Maha Mengetahui apa yang mereka sembunyikan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3306">3306</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12338,2,84,25,'<p><span>[a]</span><span> Maka kabarkanlah kepada mereka tentang azab yang pedih,</span></p><p><span>[a]</span><span> 9:34.</span></p>');
INSERT INTO `quran` VALUES (12339,2,84,26,'<p><span>Kecuali </span><span>terhadap</span><span> </span><span>[b]</span><span> orang-orang yang beriman dan beramal shaleh, bagi mereka pahala yang tiada putus-putusnya.</span></p><p><span>[b]</span><span> 11:12 ; 41:9 ; 95:7.</span></p>');
INSERT INTO `quran` VALUES (12340,2,85,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12341,2,85,2,'<p><span>Demi langit yang mempunyai </span><span>[b]</span><span> gugusan-gugusan bintang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3307">3307</a><span>]</span></p><p><span>[b]</span><span> 15:17 ; 25:62.</span></p>');
INSERT INTO `quran` VALUES (12342,2,85,3,'<p><span>Dan demi Hari yang dijanjikan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3308">3308</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12343,2,85,4,'<p><span>Dan demi saksi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3309">3309</a><span>]</span><span> dan yang disaksikan,</span></p>');
INSERT INTO `quran` VALUES (12344,2,85,5,'<p><span>Binasalah para pemilik parit, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3310">3310</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12345,2,85,6,'<p><span>Api yang </span><span>dinyalakan</span><span> dengan bahan bakar,</span></p>');
INSERT INTO `quran` VALUES (12346,2,85,7,'<p><span>Ketika mereka duduk </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3311">3311</a><span>]</span><span> di sekitarnya.</span></p>');
INSERT INTO `quran` VALUES (12347,2,85,8,'<p><span>Dan mereka menjadi saksi atas apa yang dilakukan mereka terhadap orang-orang miskin. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3312">3312</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12348,2,85,9,'<p><span>[a]</span><span> Dan, mereka tidak menaruh dendam terhadap mereka itu, melainkan </span><span>hanya</span><span> karena mereka beriman kepada Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3313">3313</a><span>]</span><span> Yang Maha Perkasa, Maha Terpuji,</span></p><p><span>[a]</span><span> 7:27.</span></p>');
INSERT INTO `quran` VALUES (12349,2,85,10,'<p><span>[b]</span><span> Yang kepunyaan-Nya kerajaan seluruh langit dan bumi. Dan Allah menjadi Saksi atas segala sesuatu.</span></p><p><span>[b]</span><span> 14:3.</span></p>');
INSERT INTO `quran` VALUES (12350,2,85,11,'<p><span>Sesungguhnya orang-orang yang memfitnah orang-orang mukmin laki-laki dan orang-orang mukmin perempuan, kemudian mereka tidak bertaubat, maka bagi mereka azab Jahannam dan bagi mereka azab yang membakar.</span></p>');
INSERT INTO `quran` VALUES (12351,2,85,12,'<p><span>Sesungguhnya orang-orang yang beriman dan beramal shaleh, bagi mereka ada kebun-kebun yang di bawahnya mengalir sungai-sungai. Hal demikian itu merupakan keberhasilan besar.</span></p>');
INSERT INTO `quran` VALUES (12352,2,85,13,'<p><span>Sesungguhnya </span><span>[c]</span><span> cengkeraman Tuhan engkau sangat keras.</span></p><p><span>[c]</span><span> 11:103 ; 22:3.</span></p>');
INSERT INTO `quran` VALUES (12353,2,85,14,'<p><span>Sesungguhnya </span><span>[d]</span><span> Dia-lah Yang memulai azab dan mengulangi, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3314">3314</a><span>]</span></p><p><span>[d]</span><span> 29:20 ; 30:12.</span></p>');
INSERT INTO `quran` VALUES (12354,2,85,15,'<p><span>Dan Dia Maha Pengampun, Maha Pencinta.</span></p>');
INSERT INTO `quran` VALUES (12355,2,85,16,'<p><span>Yang Memiliki </span><span>‘</span><span>Arasy, Yang Maha Mulia.</span></p>');
INSERT INTO `quran` VALUES (12356,2,85,17,'<p><span>Maha Kuasa mengerjakan apa yang Dia kehendaki.</span></p>');
INSERT INTO `quran` VALUES (12357,2,85,18,'<p><span>Apakah telah datang kepada engkau riwayat lasykar-lasykar,</span></p>');
INSERT INTO `quran` VALUES (12358,2,85,19,'<p><span>Lasykar</span><span> Firaun dan Tsamud.</span></p>');
INSERT INTO `quran` VALUES (12359,2,85,20,'<p><span>Bahkan orang-orang yang ingkar, selalu mendustakan,</span></p>');
INSERT INTO `quran` VALUES (12360,2,85,21,'<p><span>Dan Allah mengepung </span><span>[a]</span><span> dari belakang mereka.</span></p><p><span>[a]</span><span> 17:60.</span></p>');
INSERT INTO `quran` VALUES (12361,2,85,22,'<p><span>[b]</span><span> Bahkan ia adalah Alquran yang sangat mulia, </span></p><p><span>[b]</span><span> 50:2 ; 56:78.</span></p>');
INSERT INTO `quran` VALUES (12362,2,85,23,'<p><span>[c]</span><span> Dalam sebuah batu tulis yang terjaga ketat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3315">3315</a><span>]</span></p><p><span>[c]</span><span> 41:43 ; 56:79.</span></p>');
INSERT INTO `quran` VALUES (12363,2,86,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12364,2,86,2,'<p><span>Demi langit dan demi Bintang Fajar </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3316">3316</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12365,2,86,3,'<p><span>Dan apakah yang engkau tahu, apa Bintang Fajar itu ?</span></p>');
INSERT INTO `quran` VALUES (12366,2,86,4,'<p><span>Ialah</span><span> bintang yang bercahaya sangat menembus.</span></p>');
INSERT INTO `quran` VALUES (12367,2,86,5,'<p><span>Tiada suatu jiwa pun melainkan mempunyai penjaga </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3317">3317</a><span>]</span><span> atas dirinya.</span></p>');
INSERT INTO `quran` VALUES (12368,2,86,6,'<p><span>Maka hendaknya manusia memperhatikan, dari apa ia diciptakan.</span></p>');
INSERT INTO `quran` VALUES (12369,2,86,7,'<p><span>Ia diciptakan dari cairan yang memancar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3318">3318</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12370,2,86,8,'<p><span>Keluar dari antara tulang-tulang punggung dan tulang-tulang dada. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3318a">3318a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12371,2,86,9,'<p><span>[a]</span><span> Sesungguhnya Dia Maha Kuasa untuk mengembalikannya.</span></p><p><span>[a]</span><span> 46:34.</span></p>');
INSERT INTO `quran` VALUES (12372,2,86,10,'<p><span>[b]</span><span> </span><span>Pada </span><span> hari itu, ketika segala rahasia akan dizahirkan.</span></p><p><span>[b]</span><span> 10:31</span></p>');
INSERT INTO `quran` VALUES (12373,2,86,11,'<p><span>Maka tidak ada baginya kekuatan dan tidak pula penolong.</span></p>');
INSERT INTO `quran` VALUES (12374,2,86,12,'<p><span>Demi awan yang berulang-ulang menurunkan hujan,</span></p>');
INSERT INTO `quran` VALUES (12375,2,86,13,'<p><span>Dan demi bumi yang mekar </span><span>dengan tumbuh-tumbuhan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3319">3319</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12376,2,86,14,'<p><span>Sesungguhnya, </span><span>Alquran</span><span> itu adalah firman yang menentukan,</span></p>');
INSERT INTO `quran` VALUES (12377,2,86,15,'<p><span>Dan bukanlah </span><span>Alquran</span><span> itu perkataan yang lemah.</span></p>');
INSERT INTO `quran` VALUES (12378,2,86,16,'<p><span>Sesungguhnya </span><span>[a]</span><span> mereka merencanakan suatu rencana,</span></p><p><span>[a]</span><span> 52:43.</span></p>');
INSERT INTO `quran` VALUES (12379,2,86,17,'<p><span>Dan Aku </span><span>pun</span><span> merencanakan suatu rencana.</span></p>');
INSERT INTO `quran` VALUES (12380,2,86,18,'<p><span>[b]</span><span> Maka berilah tangguh orang-orang kafir itu, berilah tangguh mereka sebentar. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3319a">3319a</a><span>]</span></p><p><span>[b]</span><span> 68:46 ; 73:12.</span></p>');
INSERT INTO `quran` VALUES (12381,2,87,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12382,2,87,2,'<p><span>[b]</span><span> Sucikanlah nama Tuhan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3320">3320</a><span>]</span><span> engkau, Yang Maha Tinggi,</span></p><p><span>[b]</span><span> 56:75 ; 69:53.</span></p>');
INSERT INTO `quran` VALUES (12383,2,87,3,'<p><span>[c]</span><span> Yang telah menciptakan serta menyempurnakan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3321">3321</a><span>]</span></p><p><span>[c]</span><span> 82:8 ; 91:8.</span></p>');
INSERT INTO `quran` VALUES (12384,2,87,4,'<p><span>[d]</span><span> Dan Yang menetapkan kadar dan memberi petunjuk.</span></p><p><span>[d]</span><span> 80:20.</span></p>');
INSERT INTO `quran` VALUES (12385,2,87,5,'<p><span>Dan Yang menumbuhkan rerumputan,</span></p>');
INSERT INTO `quran` VALUES (12386,2,87,6,'<p><span>[e]</span><span> Kemudian menjadikannya sampah kering kehitam-hitaman. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3322">3322</a><span>]</span></p><p><span>[e]</span><span> 18:46 ; 57:21.</span></p>');
INSERT INTO `quran` VALUES (12387,2,87,7,'<p><span>Kami akan mengajari engkau </span><span>Alquran,</span><span> maka engkau tidak akan melupakan-</span><span>nya,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3323">3323</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12388,2,87,8,'<p><span>Kecuali apa yang dikehendaki Allah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3324">3324</a><span>]</span><span> Sesungguhnya, Dia </span><span>[a]</span><span> mengetahui yang zahir dan yang tersembunyi.</span></p><p><span>[a]</span><span> 2:34 ; 20:8 ; 21:111 ; 24:30.</span></p>');
INSERT INTO `quran` VALUES (12389,2,87,9,'<p><span>[a]</span><span> Dan Kami akan memudahkan engkau untuk </span><span>mencapai</span><span> kemudahan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3325">3325</a><span>]</span></p><p><span>[a]</span><span> 92;8 .</span></p>');
INSERT INTO `quran` VALUES (12390,2,87,10,'<p><span>Maka berilah nasihat, sesungguhnya nasihat itu bermanfaat.</span></p>');
INSERT INTO `quran` VALUES (12391,2,87,11,'<p><span>Tentu akan mengambil pelajaran </span><span>[b]</span><span> orang yang takut </span><span>kepada Tuhan.</span></p><p><span>[b]</span><span> 51:56.</span></p>');
INSERT INTO `quran` VALUES (12392,2,87,12,'<p><span>Namun akan menjauhinya orang yang sangat malang,</span></p>');
INSERT INTO `quran` VALUES (12393,2,87,13,'<p><span>Yang akam memasuki </span><span>[c]</span><span> Api yang besar.</span></p><p><span>[c]</span><span> 88:5.</span></p>');
INSERT INTO `quran` VALUES (12394,2,87,14,'<p><span>[d]</span><span> Kemudian, ia tidak akan mati di dalamnya dan tidak pula hidup.</span></p><p><span>[d]</span><span> 14:18 ; 20:75.</span></p>');
INSERT INTO `quran` VALUES (12395,2,87,15,'<p><span>[e]</span><span> Sesungguhnya, berbahagialah orang yang mensucikan diri,</span></p><p><span>[e]</span><span> 91:10.</span></p>');
INSERT INTO `quran` VALUES (12396,2,87,16,'<p><span>Dan mengingat nama Tuhan-nya lalu mendirikan shalat.</span></p>');
INSERT INTO `quran` VALUES (12397,2,87,17,'<p><span>[f]</span><span> Tetapi, kamu mendahulukan kehidupan dunia,</span></p><p><span>[f]</span><span> 75:21.</span></p>');
INSERT INTO `quran` VALUES (12398,2,87,18,'<p><span>[a]</span><span> Padahal akhirat itu lebih baik dan lebih kekal.</span></p><p><span>[a]</span><span> 93:5.</span></p>');
INSERT INTO `quran` VALUES (12399,2,87,19,'<p><span>[b]</span><span> Sesungguhnya inilah </span><span>yang diajarkan</span><span> dalam Kitab-kitab terdahulu,</span></p><p><span>[b]</span><span> 20:134.</span></p>');
INSERT INTO `quran` VALUES (12400,2,87,20,'<p><span>Kitab-kitab Ibrahim dan Musa. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3326">3326</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12401,2,88,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12402,2,88,2,'<p><span>Apakah sudah sampai kepada engkau </span><span>[b]</span><span> berita malapetaka yang dahsyat ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3327">3327</a><span>]</span></p><p><span>[b]</span><span> 12:108.</span></p>');
INSERT INTO `quran` VALUES (12403,2,88,3,'<p><span>Beberapa</span><span> </span><span>[c]</span><span> wajah pada hari itu tunduk </span><span>terhina ;</span></p><p><span>[c]</span><span> 68:44 ; 75:25 ; 80:41 </span><span>–</span><span> 42.</span></p>');
INSERT INTO `quran` VALUES (12404,2,88,4,'<p><span>Bekerja keras, kepayahan.</span></p>');
INSERT INTO `quran` VALUES (12405,2,88,5,'<p><span>Akan memasuki </span><span>[d]</span><span> Api yang menyala-nyala ; </span></p><p><span>[d]</span><span> 87:13 ; 101:12.</span></p>');
INSERT INTO `quran` VALUES (12406,2,88,6,'<p><span>Akan diberi minum dari </span><span>[e]</span><span> mata air mendidih.</span></p><p><span>[e]</span><span> 55:45.</span></p>');
INSERT INTO `quran` VALUES (12407,2,88,7,'<p><span>Tiada makanan bagi mereka selain rumput kering.</span></p>');
INSERT INTO `quran` VALUES (12408,2,88,8,'<p><span>Yang tidak akan menggemukkan dan tidak pula menghilangkan lapar.</span></p>');
INSERT INTO `quran` VALUES (12409,2,88,9,'<p><span>Dan beberapa </span><span> </span><span>[f]</span><span> wajah pada hari itu akan berseri-seri,</span></p><p><span>[f]</span><span> 75:23.</span></p>');
INSERT INTO `quran` VALUES (12410,2,88,10,'<p><span>Merasa senang karena usahanya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3328">3328</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12411,2,88,11,'<p><span>Di dalam </span><span>[a]</span><span> surga yang tinggi,</span></p><p><span>[a]</span><span> 69:23.</span></p>');
INSERT INTO `quran` VALUES (12412,2,88,12,'<p><span>Engkau tidak akan mendengar di dalamnya percakapan sia-sia.</span></p>');
INSERT INTO `quran` VALUES (12413,2,88,13,'<p><span>Di dalamnya ada mata air yang mengalir. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3329">3329</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12414,2,88,14,'<p><span>Di dalamnya ada dipan-dipan yang ditinggikan,</span></p>');
INSERT INTO `quran` VALUES (12415,2,88,15,'<p><span>[b]</span><span> Dan piala-piala yang terletak rapi,</span></p><p><span>[b]</span><span> 43:72.</span></p>');
INSERT INTO `quran` VALUES (12416,2,88,16,'<p><span>Dan bantal-bantal yang dideretkan,</span></p>');
INSERT INTO `quran` VALUES (12417,2,88,17,'<p><span>Dan permadani-permadani yang dihamparkan.</span></p>');
INSERT INTO `quran` VALUES (12418,2,88,18,'<p><span>Apakah mereka tidak melihat unta-unta, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3330">3330</a><span>]</span><span> bagaimana diciptakan ?</span></p>');
INSERT INTO `quran` VALUES (12419,2,88,19,'<p><span>[c]</span><span> Dan </span><span>memandang</span><span> ke langit, bagaimana ditinggikan ?</span></p><p><span>[c]</span><span> 13:3 ; 55:8.</span></p>');
INSERT INTO `quran` VALUES (12420,2,88,20,'<p><span>[a]</span><span> Dan kepada gunung-gunung, bagaimana ditegakkan ?</span></p><p><span>[a] </span><span>50:8.</span></p>');
INSERT INTO `quran` VALUES (12421,2,88,21,'<p><span>[b]</span><span> Dan kepada bumi, bagaimana dihamparkan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3331">3331</a><span>]</span></p><p><span>[b]</span><span> 50:8 ; 79:31.</span></p>');
INSERT INTO `quran` VALUES (12422,2,88,22,'<p><span>Maka nasihatilah, sesungguhnya engkau hanya seorang pemberi nasihat.</span></p>');
INSERT INTO `quran` VALUES (12423,2,88,23,'<p><span>[c]</span><span> Engkau bukan penjaga atas mereka,</span></p><p><span>[c]</span><span> 6:108 ; 39:42 ; 42:7.</span></p>');
INSERT INTO `quran` VALUES (12424,2,88,24,'<p><span>Tetapi, barangsiapa berpaling dan ingkar,</span></p>');
INSERT INTO `quran` VALUES (12425,2,88,25,'<p><span>Maka Allah akan mengazabnya dengan azab yang paling besar.</span></p>');
INSERT INTO `quran` VALUES (12426,2,88,26,'<p><span>Sesungguhnya kepada Kami-lah mereka akan kembali.</span></p>');
INSERT INTO `quran` VALUES (12427,2,88,27,'<p><span>Kemudian, sesungguhnya atas Kami-lah menghisab mereka.</span></p>');
INSERT INTO `quran` VALUES (12428,2,89,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12429,2,89,2,'<p><span>Demi fajar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3332">3332</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12430,2,89,3,'<p><span>Dan sepuluh malam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3333">3333</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12431,2,89,4,'<p><span>Dan yang genap serta yang ganjil, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3334">3334</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12432,2,89,5,'<p><span>Dan malam itu ketika ia berjalan </span><span>mengakhiri waktunya.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3335">3335</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12433,2,89,6,'<p><span>Apakah dalam hal itu ada sumpah </span><span>saksi</span><span> bagi orang berakal ?</span></p>');
INSERT INTO `quran` VALUES (12434,2,89,7,'<p><span>Apakah engkau tidak mengetahui bagaimana Tuhan engkau telah memperlakukan kaum </span><span>‘</span><span>Ad, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3336">3336</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12435,2,89,8,'<p><span>Suku</span><span> Iram, pemilik gedung-gedung yang megah itu ?</span></p>');
INSERT INTO `quran` VALUES (12436,2,89,9,'<p><span>Yang tidak pernah diciptakan seperti itu di negeri-negeri lain,</span></p>');
INSERT INTO `quran` VALUES (12437,2,89,10,'<p><span>[a]</span><span> Dan kaum Tsamud yang memahat batu di lembah itu,</span></p><p><span>[a]</span><span> 7:75 ; 26:150.</span></p>');
INSERT INTO `quran` VALUES (12438,2,89,11,'<p><span>Dan kaum Firuan yang mempunyai pasak-pasak </span><span>lasykar.</span></p>');
INSERT INTO `quran` VALUES (12439,2,89,12,'<p><span>[b]</span><span> Yang berlaku sewenang-wenang di negeri-negeri itu,</span></p><p><span>[b]</span><span> 28:5.</span></p>');
INSERT INTO `quran` VALUES (12440,2,89,13,'<p><span>[c]</span><span> Dan melakukan sangat banyak kerusakan di negeri-negeri itu ?</span></p><p><span>[c]</span><span> 28:5.</span></p>');
INSERT INTO `quran` VALUES (12441,2,89,14,'<p><span>Maka, Tuhan engkau menimpakan atas mereka cambuk azab, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3337">3337</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12442,2,89,15,'<p><span>Sesungguhnya, Tuhan engkau pasti berjaga-jaga.</span></p>');
INSERT INTO `quran` VALUES (12443,2,89,16,'<p><span>Adapun mengenai manusia, apabila Tuhan-nya mencobai dia dan memuliakannya dan </span><span>[a]</span><span> menganugerahkan nikmat-nikmat </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3338">3338</a><span>]</span><span> kepadanya, maka ia berkata, “Tuhan-ku telah memuliakan aku.”</span></p>');
INSERT INTO `quran` VALUES (12444,2,89,17,'<p><span>Adapun apabila Allah mencobai dia dan menyempitkan atasnya </span><span>[b]</span><span> rezekinya, maka ia berkata, “Tuhan-ku menghinakan aku.”</span></p><p><span>[b]</span><span> 17:84.</span></p>');
INSERT INTO `quran` VALUES (12445,2,89,18,'<p><span>[c]</span><span> Sekali-kali tidak, bahkan kamu tidak memuliakan anak yatim,</span></p><p><span>[c]</span><span> 107:3.</span></p>');
INSERT INTO `quran` VALUES (12446,2,89,19,'<p><span>[d]</span><span> Dan kamu tidak saling menganjurkan memberi makan kepada orang miskin,</span></p><p><span>[d]</span><span> 69:35.</span></p>');
INSERT INTO `quran` VALUES (12447,2,89,20,'<p><span>Dan kamu memakan harta warisan dengan rakus semuanya.</span></p>');
INSERT INTO `quran` VALUES (12448,2,89,21,'<p><span>[a]</span><span> Dan kamu mencintai harta dengan kecintaan berlebihan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3339">3339</a><span>]</span></p><p><span>[a]</span><span> 104:3.</span></p>');
INSERT INTO `quran` VALUES (12449,2,89,22,'<p><span>Dengarlah ! Apabila bumi dihancurkan sehancur-hancurnya ;</span></p>');
INSERT INTO `quran` VALUES (12450,2,89,23,'<p><span>[b]</span><span> Dan, Tuhan engkau datang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3340">3340</a><span>]</span><span> dan para malaikat bersaf-saf.</span></p><p><span>[b]</span><span> 2:110 ; 6:159 ; 16:34.</span></p>');
INSERT INTO `quran` VALUES (12451,2,89,24,'<p><span>Dan pada hari itu Jahannam </span><span>[c]</span><span> didatangkan, pada hari itu manusia ingin memperoleh nasihat, </span><span>[d]</span><span> tetapi apa gunanya nasihat itu bagi dia ?</span></p><p><span>[c]</span><span> 26:92.</span></p><p><span>[d]</span><span> 79:36.</span></p>');
INSERT INTO `quran` VALUES (12452,2,89,25,'<p><span>Ia akan berkata, “Alangkah baiknya jika aku mendahulukan </span><span>amal baik</span><span> untuk kehidupanku ini ! “</span></p>');
INSERT INTO `quran` VALUES (12453,2,89,26,'<p><span>Maka pada hari itu tidak akan memberi azab seorang pun seperti azab-Nya.</span></p>');
INSERT INTO `quran` VALUES (12454,2,89,27,'<p><span>Dan tidak ada seorang pun dapat mengikat seperti ikatan-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3341">3341</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12455,2,89,28,'<p><span>Hai, jiwa yang tenteram !</span></p>');
INSERT INTO `quran` VALUES (12456,2,89,29,'<p><span>Kembalilah kepada Tuhan engkau, engkau ridha </span><span>kepada-Nya dan </span><span> Dia pun ridha </span><span>kepada engkau,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3342">3342</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12457,2,89,30,'<p><span>Maka masuklah dalam hamba-hamba-Ku,</span></p>');
INSERT INTO `quran` VALUES (12458,2,89,31,'<p><span>Dan, masuklah ke dalam surga-Ku.</span></p>');
INSERT INTO `quran` VALUES (12459,2,90,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12460,2,90,2,'<p><span>[b]</span><span> Sungguh, Aku bersumpah dengan kota ini, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3343">3343</a><span>]</span></p><p><span>[b]</span><span> 52:5 ; 95:4.</span></p>');
INSERT INTO `quran` VALUES (12461,2,90,3,'<p><span>Dan engkau akan singgah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3344">3344</a><span>]</span><span> di kota ini,</span></p>');
INSERT INTO `quran` VALUES (12462,2,90,4,'<p><span>Dan demi ayah dan anak, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3345">3345</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12463,2,90,5,'<p><span>Sesungguhnya, Kami telah menciptakan manusia </span><span>[a]</span><span> supaya bekerja keras. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3346">3346</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12464,2,90,6,'<p><span>[b]</span><span> Apakah ia menyangka bahwa tiada seorang pun berkuasa atasnya ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3347">3347</a><span>]</span></p><p><span>[b]</span><span> 96:15.</span></p>');
INSERT INTO `quran` VALUES (12465,2,90,7,'<p><span>Ia berkata, “Aku telah menghabiskan harta yang banyak,” </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3348">3348</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12466,2,90,8,'<p><span>Apakah ia menyangka, bahwa tidak ada seorang pun melihatnya ?</span></p>');
INSERT INTO `quran` VALUES (12467,2,90,9,'<p><span>Apakah tidak Kami menjadikan sepasang mata ?</span></p>');
INSERT INTO `quran` VALUES (12468,2,90,10,'<p><span>Dan sebuah lidah dan dua buah bibir ?</span></p>');
INSERT INTO `quran` VALUES (12469,2,90,11,'<p><span>[a]</span><span> dan Kami telah menunjukkan kepadanya kedua jalan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3349">3349</a><span>]</span></p><p><span>[a]</span><span> 76:4.</span></p>');
INSERT INTO `quran` VALUES (12470,2,90,12,'<p><span>Tetapi ia tidak berusaha mendaki pendakian terjal ; </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3350">3350</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12471,2,90,13,'<p><span>Dan apakah yang engkau ketahui apa pendakian terjal itu ?</span></p>');
INSERT INTO `quran` VALUES (12472,2,90,14,'<p><span>Yaitu</span><span> memerdekakan budak,</span></p>');
INSERT INTO `quran` VALUES (12473,2,90,15,'<p><span>[b]</span><span> Atau memberi makan pada hari kelaparan,</span></p><p><span>[b]</span><span> 76:9.</span></p>');
INSERT INTO `quran` VALUES (12474,2,90,16,'<p><span>Kepada anak yatim kerabat,</span></p>');
INSERT INTO `quran` VALUES (12475,2,90,17,'<p><span>Atau, kepada orang miskin yang terbaring di atas tanah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3351">3351</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12476,2,90,18,'<p><span>Kemudian, dia menjadi di antara orang-orang beriman dan menasihati satu sama lain supaya </span><span>[a]</span><span> bersabar dan mengajak satu sama lain berbelas kasih. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3352">3352</a><span>]</span></p><p><span>[a]</span><span> 103:4.</span></p>');
INSERT INTO `quran` VALUES (12477,2,90,19,'<p><span>Mereka ini </span><span>[b]</span><span> golongan kanan.</span></p><p><span>[b]</span><span> 56:28.</span></p>');
INSERT INTO `quran` VALUES (12478,2,90,20,'<p><span>Dan orang-orang yang mengingkari Tanda-tanda Kami, mereka itu </span><span>[c]</span><span> golongan kiri.</span></p><p><span>[c]</span><span> 56:42.</span></p>');
INSERT INTO `quran` VALUES (12479,2,90,21,'<p><span>Atas mereka akan ada Api yang </span><span>[d]</span><span> tertutup. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3353">3353</a><span>]</span></p><p><span>[d]</span><span> 104:9.</span></p>');
INSERT INTO `quran` VALUES (12480,2,91,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12481,2,91,2,'<p><span>Demi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3354">3354</a><span>]</span><span> matahari </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3355">3355</a><span>]</span><span> dan sinarnya di pagi hari,</span></p>');
INSERT INTO `quran` VALUES (12482,2,91,3,'<p><span>Dan </span><span> demi bulan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3356">3356</a><span>]</span><span> apabila ia mengikutinya,</span></p>');
INSERT INTO `quran` VALUES (12483,2,91,4,'<p><span>Dan </span><span> demi siang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3357">3357</a><span>]</span><span> apabila ia menzahirkannya, </span></p>');
INSERT INTO `quran` VALUES (12484,2,91,5,'<p><span>Dan</span><span> demi malam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3358">3358</a><span>]</span><span> apabila ia menutupinya,</span></p>');
INSERT INTO `quran` VALUES (12485,2,91,6,'<p><span>Dan</span><span> demi langit dan binaannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3359">3359</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12486,2,91,7,'<p><span>Dan</span><span> demi bumi dan hamparannya,</span></p>');
INSERT INTO `quran` VALUES (12487,2,91,8,'<p><span>Dan</span><span> demi jiwa dan penyempurnaannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3359a">3359a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12488,2,91,9,'<p><span>Maka Dia mengilhamkan kepadanya keburukan-keburukannya dan ketakwaannya,</span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3360">3360</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12489,2,91,10,'<p><span>Sungguh, beruntunglah orang yang mensucikannya,</span></p>');
INSERT INTO `quran` VALUES (12490,2,91,11,'<p><span>Dan tentu binasalah orang yang mengotorinya.</span></p>');
INSERT INTO `quran` VALUES (12491,2,91,12,'<p><span>Kaum</span><span> Tsamud mendustakan disebabkan kedurhakaannya,</span></p>');
INSERT INTO `quran` VALUES (12492,2,91,13,'<p><span>Ketika bangkit orang yang paling buruk nasibnya di antara mereka,</span></p>');
INSERT INTO `quran` VALUES (12493,2,91,14,'<p><span>Maka berkata kepada mereka rasul Allah, “Biarlah unta betina Allah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3361">3361</a><span>]</span><span> dan </span><span>jangan merintangi </span><span>minumnya</span><span>.</span><span>”</span></p>');
INSERT INTO `quran` VALUES (12494,2,91,15,'<p><span>Tetapi mereka mendustakannya dan memotong urat keting unta itu, maka Tuhan mereka membinasakannya mereka karena dosa mereka, kemudian Dia menjadikannya sama rata,</span></p>');
INSERT INTO `quran` VALUES (12495,2,91,16,'<p><span>Dan Dia tidak takut akan akibatnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3361a">3361a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12496,2,92,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12497,2,92,2,'<p><span>[b]</span><span> Demi </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3362">3362</a><span>]</span><span> malam apabila menutupi,</span></p><p><span>[b]</span><span> 91:5. </span></p>');
INSERT INTO `quran` VALUES (12498,2,92,3,'<p><span>[c]</span><span> Dan demi siang apabila terang-benderang, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3363">3363</a><span>]</span></p><p><span>[c]</span><span> 91:4.</span></p>');
INSERT INTO `quran` VALUES (12499,2,92,4,'<p><span>[d]</span><span> Dan demi penciptaan laki-laki dan perempuan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3363a">3363a</a><span>]</span></p><p><span>[d]</span><span> 36:37 ; 51:50 ; 78:9.</span></p>');
INSERT INTO `quran` VALUES (12500,2,92,5,'<p><span>Sesungguhnya usaha kamu pasti berbeda. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3364">3364</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12501,2,92,6,'<p><span>Maka adapun orang yang memberi dan bertawa,</span></p>');
INSERT INTO `quran` VALUES (12502,2,92,7,'<p><span>Dan membenarkan kebaikan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3365">3365</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12503,2,92,8,'<p><span>[a]</span><span> Kami akan memudahkan kepadanya untuk </span><span>mencapai </span><span> kemudahan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3366">3366</a><span>]</span></p><p><span>[a]</span><span> 87:9.</span></p>');
INSERT INTO `quran` VALUES (12504,2,92,9,'<p><span>Dan adapun orang yang bakhil serta bersikap tidak acuh,</span></p>');
INSERT INTO `quran` VALUES (12505,2,92,10,'<p><span>Dan mendustakan apa yang baik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3367">3367</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12506,2,92,11,'<p><span>Maka Kami akan menyiapkan baginya </span><span>jalan </span><span> kesukaran, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3368">3368</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12507,2,92,12,'<p><span>[a]</span><span> Dan tidak akan berguna baginya hartanya apabila ia binasa.</span></p><p><span>[a]</span><span> 3:11 ; 58:18 ; 113:3.</span></p>');
INSERT INTO `quran` VALUES (12508,2,92,13,'<p><span>[b]</span><span> Sesungguhnya kewajiban Kami memberi petunjuk,</span></p><p><span>[b]</span><span> 2:273 ; 28:57.</span></p>');
INSERT INTO `quran` VALUES (12509,2,92,14,'<p><span>Dan sesungguhnya kepunyaan Kami alam akhirat dan alam dunia. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3369">3369</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12510,2,92,15,'<p><span>Maka Aku memberi peringatan kepada kamu tentang Api yang menyala-nyala.</span></p>');
INSERT INTO `quran` VALUES (12511,2,92,16,'<p><span>[c]</span><span> Tiada seorang pun memasukinya selain orang yang paling celaka,</span></p><p><span>[c]</span><span> 20:75 ; 87:12 </span><span>–</span><span> 13.</span></p>');
INSERT INTO `quran` VALUES (12512,2,92,17,'<p><span>[d]</span><span> Yang mendustakan dan berpaling. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3370">3370</a><span>]</span></p><p><span>[d]</span><span> 20:49.</span></p>');
INSERT INTO `quran` VALUES (12513,2,92,18,'<p><span>Dan orang yang paling bertakwa segera akan dijauhkan darinya,</span></p>');
INSERT INTO `quran` VALUES (12514,2,92,19,'<p><span>Ialah</span><span> yang memberikan hartanya, supaya ia memperoleh kesucian.</span></p>');
INSERT INTO `quran` VALUES (12515,2,92,20,'<p><span>Dan tidak bagi seorang pun kebaikan yang ada di sisinya yang harus dibalas.</span></p>');
INSERT INTO `quran` VALUES (12516,2,92,21,'<p><span>kecuali mencari keridhaan Tuhan-Nya, Yang Maha Tinggi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3371">3371</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12517,2,92,22,'<p><span>Dan tentulah Dia akan ridha</span><span> kepadanya.</span></p>');
INSERT INTO `quran` VALUES (12518,2,93,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12519,2,93,2,'<p><span>Demi terangnya sinar pagi ketika sedang naik, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3372">3372</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12520,2,93,3,'<p><span>[a]</span><span> Dan demi malam apabila kegelapannya menyebar, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3373">3373</a><span>]</span></p><p><span>[a]</span><span> 81:18.</span></p>');
INSERT INTO `quran` VALUES (12521,2,93,4,'<p><span>Tidak meninggalkan engkau Tuhan engkau dan Dia tidak pula murka </span><span>atas engkau.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3374">3374</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12522,2,93,5,'<p><span>Dan sesungguhnya, keadaan dikemudian hari lebih baik bagi engkau daripada keadaan semula. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3375">3375</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12523,2,93,6,'<p><span>Dan Tuhan engkau pasti akan segera memberikan kepada engkau sehingga engkau menjadi puas.</span></p>');
INSERT INTO `quran` VALUES (12524,2,93,7,'<p><span>Apakah Dia tidak mendapati engkau yatim, lalu Dia memberikan perlindungan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3376">3376</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12525,2,93,8,'<p><span>Dan Dia mendapati engkau fana dalam kecintaan </span><span>kepada kaum engkau,</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3377">3377</a><span>]</span><span> dan Dia memberi engkau petunjuk.</span></p>');
INSERT INTO `quran` VALUES (12526,2,93,9,'<p><span>Dan Dia menadapi engkau berkekurangan, lalu Dia memperkaya engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3378">3378</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12527,2,93,10,'<p><span>Maka terhadap anak yatim, janganlah engkau berlaku sewenang-wenang.</span></p>');
INSERT INTO `quran` VALUES (12528,2,93,11,'<p><span>Dan terhadap orang yang meminta-minta, janganlah engkau menghardik.</span></p>');
INSERT INTO `quran` VALUES (12529,2,93,12,'<p><span>Dan terhadap nikmat Tuhan engkau, hendaknya zahirkanlah. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3379">3379</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12530,2,94,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12531,2,94,2,'<p><span>Apakah Kami tidak melapangkan bagi engkau dada engkau, </span></p>');
INSERT INTO `quran` VALUES (12532,2,94,3,'<p><span>Dan Kami menjauhkan dari engkau beban engkau,</span></p>');
INSERT INTO `quran` VALUES (12533,2,94,4,'<p><span>Yang mematahkan punggung engkau ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3380">3380</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12534,2,94,5,'<p><span>Dan Kami meninggikan untuk engkau nama engkau. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3381">3381</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12535,2,94,6,'<p><span>Sesungguhnya bersama kesukaran dan kemudahan, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3382">3382</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12536,2,94,7,'<p><span>Sesungguhnya bersama kesukaran ada kemudahan.</span></p>');
INSERT INTO `quran` VALUES (12537,2,94,8,'<p><span>Maka apabila engkau telah selesai </span><span>tugas,</span><span> maka berusaha keraslah </span><span>mencari keridhaan Allah.</span></p>');
INSERT INTO `quran` VALUES (12538,2,94,9,'<p><span>[a]</span><span> Dan kepada Tuhan engkaulah </span><span>hendaknya </span><span> engkau mengarahkan perhatian. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3382a">3382a</a><span>]</span></p><p><span>[a]</span><span> 73:9 ; 110:4.</span></p>');
INSERT INTO `quran` VALUES (12539,2,95,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12540,2,95,2,'<p><span>Demi pohon ara dan zaitun, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3383">3383</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12541,2,95,3,'<p><span>Dan </span><span>[a]</span><span> Gunung Sinai, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3383a">3383a</a><span>]</span><span> </span></p><p><span>[a]</span><span> 52:2.</span></p>');
INSERT INTO `quran` VALUES (12542,2,95,4,'<p><span>Dan </span><span>[b]</span><span> Kota yang aman ini,</span></p><p><span>[b]</span><span> 90:2.</span></p>');
INSERT INTO `quran` VALUES (12543,2,95,5,'<p><span>Sesungguhnya, Kami telah menciptakan manusia dalam sebaik-baik </span><span>[c] </span><span>bentuk.</span></p><p><span>[c]</span><span> 23:12 </span><span>–</span><span> 15.</span></p>');
INSERT INTO `quran` VALUES (12544,2,95,6,'<p><span>Kemudian, Kami mengembalikannya kepada tingkat paling rendah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3384">3384</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12545,2,95,7,'<p><span>[d]</span><span> Kecuali orang-orang yang beriman dan beramal shaleh ; maka bagi mereka ganjaran yang tiada putus-putusnya.</span></p><p><span>[d]</span><span> 11:12 ; 41:9 ; 84:26.</span></p>');
INSERT INTO `quran` VALUES (12546,2,95,8,'<p><span>Maka apakah yang menyebabkan engkau mendustakan hari pembalasan sesudah itu ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3385">3385</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12547,2,95,9,'<p><span>Bukankah Allah itu Hakim Yang Maha Adil di antara para hakim ?</span></p>');
INSERT INTO `quran` VALUES (12548,2,96,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12549,2,96,2,'<p><span>Bacalah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3386">3386</a><span>]</span><span> dengan nama Tuhan engkau yang telah menciptakan,</span></p>');
INSERT INTO `quran` VALUES (12550,2,96,3,'<p><span>Menciptakan manusia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3387">3387</a><span>]</span><span> dari </span><span>[b]</span><span> segumpal darah.</span></p><p><span>[b]</span><span> 23:15 ; 40:68 ; 75:39.</span></p>');
INSERT INTO `quran` VALUES (12551,2,96,4,'<p><span>Bacalah ! Dia Tuhan engkau adalah Maha Mulia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3388">3388</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12552,2,96,5,'<p><span>Yang mengajar dengan pena, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3389">3389</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12553,2,96,6,'<p><span>[a]</span><span> Mengajar manusia apa yang tidak diketahuinya.</span></p><p><span>[a]</span><span> 4:114 ; 55:5.</span></p>');
INSERT INTO `quran` VALUES (12554,2,96,7,'<p><span>Sekali-kali tidak, sesungguhnya manusia itu pelampau batas.</span></p>');
INSERT INTO `quran` VALUES (12555,2,96,8,'<p><span>Karena ia menganggap dirinya berkecukupan.</span></p>');
INSERT INTO `quran` VALUES (12556,2,96,9,'<p><span>Sesungguhnya </span><span>[b]</span><span> kepada Tuhan engkaulah tempat kembali.</span></p><p><span>[b]</span><span> 21:36 ; 53:43.</span></p>');
INSERT INTO `quran` VALUES (12557,2,96,10,'<p><span>Apakah engkau melihat orang yang </span><span>[c]</span><span> melarang,</span></p><p><span>[c]</span><span> 2:115 ; 72:20.</span></p>');
INSERT INTO `quran` VALUES (12558,2,96,11,'<p><span>Seorang hamba </span><span>Kami</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3390">3390</a><span>]</span><span> ketika ia shalat ?</span></p>');
INSERT INTO `quran` VALUES (12559,2,96,12,'<p><span>Apakah engkau melihat jika ia mengikuti petunjuk,</span></p>');
INSERT INTO `quran` VALUES (12560,2,96,13,'<p><span>Atau, ia menyuruh bertakwa,</span></p>');
INSERT INTO `quran` VALUES (12561,2,96,14,'<p><span>Apakah engkau melihat jika ia mendutakan dan berpaling ?</span></p>');
INSERT INTO `quran` VALUES (12562,2,96,15,'<p><span>Apakah ia tidak mengetahui bahwa sesungguhnya Allah melihat ?</span></p>');
INSERT INTO `quran` VALUES (12563,2,96,16,'<p><span>Sekali-kali tidak, jika ia tidak berhenti, niscaya Kami akan menarik dia pada jambulnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3391">3391</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12564,2,96,17,'<p><span>Dahi </span><span>orang</span><span> yang berdusta dan berdosa.</span></p>');
INSERT INTO `quran` VALUES (12565,2,96,18,'<p><span>Maka hendaklah ia memanggil teman-temannya,</span></p>');
INSERT INTO `quran` VALUES (12566,2,96,19,'<p><span>Segera Kami akan memanggil para malaikat pelaksana hukuman, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3392">3392</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12567,2,96,20,'<p><span>Sekali-kali tidak, janganlah engkau taat kepadanya, melainkan bersujudlah dan mendekatlah </span><span>kepada Allah.</span></p>');
INSERT INTO `quran` VALUES (12568,2,97,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12569,2,97,2,'<p><span>Sesungguhnya Kami telah menurunkan pada Lailatul </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3393">3393</a><span>]</span><span> Qadr. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3394">3394</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12570,2,97,3,'<p><span>Dan apakah engkau mengetahui apa Lailatul Qadr itu ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3395">3395</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12571,2,97,4,'<p><span>Lailatul Qadr itu lebih baik daripada seribu bulan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3396">3396</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12572,2,97,5,'<p><span>Di dalamnya turun </span><span>[a]</span><span> malaikat-malaikat dan ruh </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3397">3397</a><span>]</span><span> dengan izin Tuhan mereka </span><span>[b]</span><span> mengenai segala perintah,</span></p><p><span>[a]</span><span> 16:3 ; 40:16.</span></p><p><span>[b]</span><span> 44:5.</span></p>');
INSERT INTO `quran` VALUES (12573,2,97,6,'<p><span>Damai </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3398">3398</a><span>]</span><span> hingga fajar terbit. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3398a">3398a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12574,2,98,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12575,2,98,2,'<p><span>Tidak akan berhenti orang-orang yang ingkar dari Ahlikitab dan orang-orang musyrik </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3399">3399</a><span>]</span><span> hingga engkau membawa kepada mereka bukti yang nyata.</span></p>');
INSERT INTO `quran` VALUES (12576,2,98,3,'<p><span>[a]</span><span> Seorang rasul dari Allah yang membacakan lembaran-lembaran suci.</span></p><p><span>[a]</span><span> 3:165 ; 62:3.</span></p>');
INSERT INTO `quran` VALUES (12577,2,98,4,'<p><span>Yang di dalamnya ada perintah-perintah abadi. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3400">3400</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12578,2,98,5,'<p><span>Dan </span><span>[b]</span><span> tidak bercerai-berai orang-orang yang diberi Kitab kecuali setelah datang kepada mereka bukti yang nyata.</span></p><p><span>[b]</span><span> 42:15 ; 45:18.</span></p>');
INSERT INTO `quran` VALUES (12579,2,98,6,'<p><span>Padahal mereka tidak diperintahkan melainkan supaya beribadah kepada Allah dengan tulus ikhlas dalam ketaatan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3400a">3400a</a><span>]</span><span> kepada-Nya </span><span>[a]</span><span> </span><span>dan</span><span> dengan lurus, serta mendirikan shalat dan membayar zakat, dan itulah agama yang teguh.</span></p><p><span>[a]</span><span> 40:15.</span></p>');
INSERT INTO `quran` VALUES (12580,2,98,7,'<p><span>Sesungguhnya orang-orang yang ingkar dari antara Ahlikitab dan orang-orang musyrik, akan berada dalam Api Jahannam ; mereka akan tinggal lama di dalamnya. Mereka itulah seburuk-buruknya makhluk.</span></p>');
INSERT INTO `quran` VALUES (12581,2,98,8,'<p><span>Sesungguhnya, orang-orang yang beriman dan beramal shaleh, mereka itu sebaik- baik makhluk.</span></p>');
INSERT INTO `quran` VALUES (12582,2,98,9,'<p><span>Pahala mereka ada di sisi Tuhan mereka, </span><span>[b]</span><span> kebun-kebun abadi, yang di bawahnya mengalir sungai-sungai ; mereka akan menetap di dalamnya untuk selama-lamanya. Allah ridha kepada mereka dan mereka pun ridha kepada-Nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3401">3401</a><span>] [c]</span><span> Itulah </span><span>balasan</span><span> bagi orang yang takut kepada Tuhan-nya.</span></p><p><span>[b]</span><span> 9:72 ; 13:24 ; 16:32 ; 35:34.</span></p><p><span>[c]</span><span> 36:12 ; 55:47.</span></p>');
INSERT INTO `quran` VALUES (12583,2,99,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12584,2,99,2,'<p><span>Apabila bumi digoncangkan segoncang-goncangnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3402">3402</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12585,2,99,3,'<p><span>Dan bumi akan mengeluarkan bebannya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3403">3403</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12586,2,99,4,'<p><span>Dan manusia akan berkata, “Apakah yang telah terjadi dengannya ? “ </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3404">3404</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12587,2,99,5,'<p><span>Pada hari itu bumi akan menceritakan kabarnya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3405">3405</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12588,2,99,6,'<p><span>Karena sesungguhnya Tuhan engkau telah mewahyukan kepadanya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3406">3406</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12589,2,99,7,'<p><span>Pada hari itu manusia akan keluar dalam golongan-golongan terpisah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3407">3407</a><span>]</span><span> supaya kepada mereka dapat diperlihatkan amal mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3408">3408</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12590,2,99,8,'<p><span>[a]</span><span> Maka barangsiapa berbuat kebaikan seberat zarah, ia akan melihat </span><span>hasil</span><span>-nya,</span></p><p><span>[a]</span><span> 4:124 </span><span>–</span><span> 125 ; 17:8 ; 28:85 ; 41:47.</span></p>');
INSERT INTO `quran` VALUES (12591,2,99,9,'<p><span>Dan barangsiapa berbuat keburukan seberat zarah, ia akan melihat </span><span>hasil</span><span>-nya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3409">3409</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12592,2,100,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12593,2,100,2,'<p><span>Demi kuda-kuda yang berlari kencang denga mendengus-dengus. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3410">3410</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12594,2,100,3,'<p><span>Yang memantikkan percikan-percikan bunga api </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3411">3411</a><span>]</span><span> </span><span>dengan telapak kakinya.</span></p>');
INSERT INTO `quran` VALUES (12595,2,100,4,'<p><span>Melancarkan serbuat-serbuan pada waktu subuh, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3412">3412</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12596,2,100,5,'<p><span>Dan dengan itu menerbangkan debu, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3413">3413</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12597,2,100,6,'<p><span>Lalu mereka menyerbu ke tengah-tengah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3414">3414</a><span>]</span><span> barisan-barisan </span><span>musuh,</span></p>');
INSERT INTO `quran` VALUES (12598,2,100,7,'<p><span>Sesungguhnya, manusia tidak bersyukur kepada Tuhan-nya.</span></p>');
INSERT INTO `quran` VALUES (12599,2,100,8,'<p><span>Dan, sesungguhnya ia memberi kesaksian terhadap hal itu.</span></p>');
INSERT INTO `quran` VALUES (12600,2,100,9,'<p><span>Dan, sesungguhnya ia sangat </span><span>[a]</span><span> cinta kepada harta.</span></p><p><span>[a]</span><span> 89:21.</span></p>');
INSERT INTO `quran` VALUES (12601,2,100,10,'<p><span>Apakah ia tidak mengetahui apabila dibangkitkan orang-orang yang ada di dalam kuburan-kuburan ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3415">3415</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12602,2,100,11,'<p><span>Dan dikeluarkan apa yang ada dalam dada ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3416">3416</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12603,2,100,12,'<p><span>Sesungguhnya Tuhan Mereka, pada hari itu pasti mengetahui keadaan mereka. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3417">3417</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12604,2,101,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12605,2,101,2,'<p><span>Bencana besar !</span></p>');
INSERT INTO `quran` VALUES (12606,2,101,3,'<p><span>Apakah </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3418">3418</a><span>]</span><span> bencana besar itu ?</span></p>');
INSERT INTO `quran` VALUES (12607,2,101,4,'<p><span>Dan apakah engkau mengetahui apa Bencana Besar itu ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3419">3419</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12608,2,101,5,'<p><span>Hari itu ketika manusia akan menjadi seperti laron-laron berserakan.</span></p>');
INSERT INTO `quran` VALUES (12609,2,101,6,'<p><span>Dan gunung-gunung akan menjadi seperti bulu-bulu domba dihambur-hamburkan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3420">3420</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12610,2,101,7,'<p><span>Maka adapun </span><span>[a]</span><span> orang yang berat timbangan </span><span>amal-</span><span>nya, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3421">3421</a><span>]</span></p><p><span>[a]</span><span> 7:9 ; 23:103.</span></p>');
INSERT INTO `quran` VALUES (12611,2,101,8,'<p><span>Maka ia di dalam kehidupan yang menyenangkan.</span></p>');
INSERT INTO `quran` VALUES (12612,2,101,9,'<p><span>Dan adapun orang yang ringan timbangan </span><span>amal-</span><span>nya,</span></p>');
INSERT INTO `quran` VALUES (12613,2,101,10,'<p><span>Maka ibunya, </span><span>tempat tinggalnya</span><span> , adalah Haawiyah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3422">3422</a><span>]</span><span> </span><span>jahannam.</span></p>');
INSERT INTO `quran` VALUES (12614,2,101,11,'<p><span>Dan apakah engkau mengetahui apa Haawiyah itu ?</span></p>');
INSERT INTO `quran` VALUES (12615,2,101,12,'<p><span>Ialah</span><span> Api yang menyala-nyala.</span></p>');
INSERT INTO `quran` VALUES (12616,2,102,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12617,2,102,2,'<p><span>Telah melalaikan kamu persaingan satu sama lain dalam mengumpulkan harta. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3423">3423</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12618,2,102,3,'<p><span>Hingga kamu sampai di kuburan.</span></p>');
INSERT INTO `quran` VALUES (12619,2,102,4,'<p><span>Sekali-kali tidak demikian ! Segera kamu akan mengetahui.</span></p>');
INSERT INTO `quran` VALUES (12620,2,102,5,'<p><span>Lagi, sekali-kali tidak demikian ! Segera kamu akan mengetahui. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3424">3424</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12621,2,102,6,'<p><span>Sekali-kali tidak ! Jika kamu mengetahui </span><span>hakikat itu dengan </span><span> ilmu yakin.</span></p>');
INSERT INTO `quran` VALUES (12622,2,102,7,'<p><span>Pasti kamu akan melihat Jahannam, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3425">3425</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12623,2,102,8,'<p><span>Kemudian kamu pasti akan melihatnya </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3426">3426</a><span>]</span><span> dengan mata yakin.</span></p>');
INSERT INTO `quran` VALUES (12624,2,102,9,'<p><span>Kemudian kamu pasti akan ditanya pada hari itu tentang setiap nikmat yang besar.</span></p>');
INSERT INTO `quran` VALUES (12625,2,103,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12626,2,103,2,'<p><span>Demi masa, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3427">3427</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12627,2,103,3,'<p><span>Sesungguhnya manusia </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3428">3428</a><span>]</span><span> itu pasti dalam </span><span>[b]</span><span> kerugian, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3429">3429</a><span>]</span></p><p><span>[b]</span><span> 10:46.</span></p>');
INSERT INTO `quran` VALUES (12628,2,103,4,'<p><span>Kecuali orang-orang yang beriman dan beramal shaleh dan </span><span>[c]</span><span> saling menasihati mengenai kebenaran, dan saling menasihati </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3430">3430</a><span>]</span><span> mengenai kesabaran.</span></p><p><span>[c]</span><span> 90:18.</span></p>');
INSERT INTO `quran` VALUES (12629,2,104,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12630,2,104,2,'<p><span>[b]</span><span> Celakalah bagi setiap pengumpat, dan pemfitnah, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3431">3431</a><span>]</span></p><p><span>[b]</span><span> 49:13 ; 68:12.</span></p>');
INSERT INTO `quran` VALUES (12631,2,104,3,'<p><span>Yang menimbun harta dan </span><span>[c]</span><span> menghitung-hitungnya. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3432">3432</a><span>]</span></p><p><span>[c]</span><span> 9:34 ; 89:21.</span></p>');
INSERT INTO `quran` VALUES (12632,2,104,4,'<p><span>Ia menyangka bahwa hartanya akan menjadikannya kekal. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3433">3433</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12633,2,104,5,'<p><span>Sekali-kali tidak ! Pasti dia akan dicampakkan ke dalam Hutama. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3434">3434</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12634,2,104,6,'<p><span>Dan apakah engkau mengetahui apa Hutama itu ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3434a">3434a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12635,2,104,7,'<p><span>Yaitu</span><span> Api Allah yang dinyalakan,</span></p>');
INSERT INTO `quran` VALUES (12636,2,104,8,'<p><span>Yang sampat ke dalam hati.</span></p>');
INSERT INTO `quran` VALUES (12637,2,104,9,'<p><span>Sesungguhnya itu atas mereka akan </span><span>[a]</span><span> ditutup rapat. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3435">3435</a><span>]</span></p><p><span>[a]</span><span> 90:21.</span></p>');
INSERT INTO `quran` VALUES (12638,2,104,10,'<p><span>Diikat</span><span> pada tiang-tiang yang panjang. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3435a">3435a</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12639,2,105,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12640,2,105,2,'<p><span>Apakah engkau tidak memperhatikan bagaimana Tuhan engkau memperlakukan terhadap para pemilik gajah ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3436">3436</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12641,2,105,3,'<p><span>Apakah Dia tidak menjadikan </span><span>[a]</span><span> rencana mereka dalam kegagalan ?</span></p><p><span>[a]</span><span> 27: 51 </span><span>–</span><span> 52.</span></p>');
INSERT INTO `quran` VALUES (12642,2,105,4,'<p><span>Dan Dia mengirimkan atas mereka sekawanan burung, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3437">3437</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12643,2,105,5,'<p><span>Yang memakan bangkai mereka,</span><span> sambil memukul-mukulkan </span><span>bangkai</span><span> mereka di atas batu-batu dari tanah keras. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3438">3438</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12644,2,105,6,'<p><span>Maka Dia menjadikan mereka seperti racikan jerami yang dimakan.</span></p>');
INSERT INTO `quran` VALUES (12645,2,106,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12646,2,106,2,'<p><span>Tuhan engkau membinasakan para pemilik gajah </span><span> untuk melekatkan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3439">3439</a><span>]</span><span> </span><span>hati </span><span> orang-orang Quraisy. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3440">3440</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12647,2,106,3,'<p><span>Untuk menanamkan kecintaan pada mereka selama perjalanan </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3441">3441</a><span>]</span><span> di musim dingin dan musim panas.</span></p>');
INSERT INTO `quran` VALUES (12648,2,106,4,'<p><span>Maka hendaklah mereka meyembah Tuhan </span><span>pemilik</span><span> </span><span>[a]</span><span> Rumah itu.</span></p><p><span>[a]</span><span> 3:97 ; 27:92.</span></p>');
INSERT INTO `quran` VALUES (12649,2,106,5,'<p><span>Yang telah memberi mereka makan di waktu lapar dan telah memberi mereka keamanan di waktu ketakutan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3442">3442</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12650,2,107,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12651,2,107,2,'<p><span>Apakah engkau melihat orang yang mendustakan </span><span>[b] </span><span>agama ? </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3443">3443</a><span>]</span></p><p><span>[b]</span><span> 82:10.</span></p>');
INSERT INTO `quran` VALUES (12652,2,107,3,'<p><span>Maka itulah orang yang mengusir anak yatim, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3444">3444</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12653,2,107,4,'<p><span>Dan </span><span>[c]</span><span> tidak mengajurkan </span><span>memberi</span><span> makan orang miskin.</span></p><p><span>[c]</span><span> 69:35 ; 89:19.</span></p>');
INSERT INTO `quran` VALUES (12654,2,107,5,'<p><span>Maka celakalah bagi orang-orang yang shalat, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3445">3445</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12655,2,107,6,'<p><span>Orang-orang yang dari shalat mereka lalai,</span></p>');
INSERT INTO `quran` VALUES (12656,2,107,7,'<p><span>Orang-orang yang berbuat </span><span>[a]</span><span> ria. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3466">3466</a><span>]</span></p><p><span>[a]</span><span> 4:143.</span></p>');
INSERT INTO `quran` VALUES (12657,2,107,8,'<p><span>Dan mencegah diri mereka unutk memberi barang-barang kecil </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3447">3447</a><span>]</span><span> </span><span>kepada orang miskin.</span></p>');
