INSERT INTO `quran` VALUES (12659,2,108,2,'<p><span>Sesungguhnya, Kami </span><span>[a]</span><span> telah menganugerakan kepada engkau berlimpah-limpah </span><span>kebaikan.</span><span> </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3448">3448</a><span>]</span></p><p><span>[a]</span><span> 93:6.</span></p>');
