INSERT INTO `quran` VALUES (12674,2,111,2,'<p><span>[a]</span><span> Binasalah kedua tangan Abu Lahab, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3458">3458</a><span>]</span><span> dan binasalah dia !</span></p><p><span>[a]</span><span> 108:4.</span></p>');
INSERT INTO `quran` VALUES (12675,2,111,3,'<p><span>Tidak memberi manfaat kepadanya </span><span>[b]</span><span> hartanya dan apa yang dia usahakan. </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3459">3459</a><span>]</span></p><p><span>[b]</span><span> 3:11 ; 58:18.</span></p>');
