INSERT INTO `quran` VALUES (12685,2,113,2,'<p><span>Katakanlah, “Aku berlindung kepada </span><span>[a]</span><span> Tuhan seluruh makhluk, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3470">3470</a><span>]</span></p><p><span>[a]</span><span> 6:97.</span></p>');
INSERT INTO `quran` VALUES (12686,2,113,3,'<p><span>“Dari keburukan </span><span>makhluk</span><span> yang Dia ciptakan,</span></p>');
INSERT INTO `quran` VALUES (12687,2,113,4,'<p><span>“Dan dari keburukan yang menjadikan gelap </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3471">3471</a><span>]</span><span> apabila meliputi,</span></p>');
INSERT INTO `quran` VALUES (12688,2,113,5,'<p><span>“Dan dari keburukan orang-orang yang meniupkan ke dalam buhul, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3472">3472</a><span>]</span></p>');
