INSERT INTO `quran` VALUES (12690,2,114,1,'<p><span>[a]</span><span> </span><span>Aku baca</span><span> dengan </span><a>[</a><a href="web+quran://quran.ibrohim.me/tafsir?id=1">1</a><a>] </a><span>nama </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=2">2</a><span>] </span>Allah <span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3">3</a><span>]</span><span>, Maha Pemurah, Maha Penyayang </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=4">4</a><span>]</span></p><p><span>[a] </span><span>Ditempatkan di permulaan tiap surah kecuali Surah 9; juga dalam 27:31. Lihat juga 96:2.</span></p>');
INSERT INTO `quran` VALUES (12691,2,114,2,'<p><span>Katakanlah, “Aku berlindung kepada Tuhan manusia,</span></p>');
INSERT INTO `quran` VALUES (12692,2,114,3,'<p><span>[a]</span><span> “Raja manusia.</span></p><p><span>[a]</span><span> 59:24 ; 62:2.</span></p>');
INSERT INTO `quran` VALUES (12693,2,114,4,'<p><span>“Sembahan manusia, </span><span>[</span><a href="web+quran://quran.ibrohim.me/tafsir?id=3473">3473</a><span>]</span></p>');
INSERT INTO `quran` VALUES (12694,2,114,5,'<p><span>“Dari keburukan bisikan-bisikan syaitan yang tersembunyi,</span></p>');
